INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(1, 'Vợ nhặt', 'Văn học Việt Nam', 'Kim Lân', 1962, 'vo-nhat.png', 'Đã hoàn', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Giữa l&uacute;c nạn đ&oacute;i đang ho&agrave;nh h&agrave;nh, Tr&agrave;ng (một ch&agrave;ng trai ngh&egrave;o đ&oacute;i, lại l&agrave; d&acirc;n x&oacute;m ngụ cư) dẫn một người đ&agrave;n b&agrave; lạ về nh&agrave; khiến mọi người đều ngạc nhi&ecirc;n. Trước đ&oacute;, chỉ hai lần gặp gỡ, với mấy c&acirc;u đ&ugrave;a vu vơ, v&agrave;i b&aacute;t b&aacute;nh đ&uacute;c, Thị đ&atilde; chấp nhận theo Tr&agrave;ng về l&agrave;m vợ. Về đến nh&agrave;, Tr&agrave;ng vẫn c&ograve;n ngỡ ng&agrave;ng. Mẹ Tr&agrave;ng từ ngạc nhi&ecirc;n đến lo lắng, x&oacute;t thương, rồi cũng mừng l&ograve;ng đ&oacute;n nhận n&agrave;ng con d&acirc;u. S&aacute;ng h&ocirc;m sau, vợ v&agrave; mẹ Tr&agrave;ng dậy sớm thu dọn nh&agrave; cửa. Tr&ocirc;ng thấy cảnh tượng ấy, Tr&agrave;ng thấy thương y&ecirc;u v&agrave; gắn b&oacute; với gia đ&igrave;nh của m&igrave;nh. Trong bữa cơm ng&agrave;y đ&oacute;i, nghe tiếng trống th&uacute;c thuế, h&igrave;nh ảnh đo&agrave;n người đi ph&aacute; kho th&oacute;c v&agrave; l&aacute; cờ đỏ hiện l&ecirc;n trong &oacute;c Tr&agrave;ng.</span></span></span></p>\r\n', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:red\">CHƯƠNG 1</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Trước kia mỗi chiều, cứ v&agrave;o l&uacute;c chạng vạng mặt người th&igrave; Tr&agrave;ng đi l&agrave;m về. Hắn bước ngật ngưỡng tr&ecirc;n con đường khẳng khiu luồn qua c&aacute;i x&oacute;m chợ của những người ngụ cư v&agrave;o trong bến.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn vừa đi vừa tủm tỉm cười, hai con mất nhỏ t&iacute;, g&agrave; g&agrave; đắm v&agrave;o b&oacute;ng chiều, hai b&ecirc;n quai h&agrave;m bạch ra, rung rung l&agrave;m cho c&aacute;i bộ mặt th&ocirc; kệch của hắn l&uacute;c n&agrave;o cũng nhấp nh&iacute;nh những &yacute; nghĩ g&igrave; vừa l&iacute; th&uacute; vừa dữ tợn. Hắn c&oacute; tật vừa đi vừa n&oacute;i. Hắn lảm nhảm than thở những điều hắn nghĩ.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Trẻ con trong x&oacute;m, cứ thấy c&aacute;i th&acirc;n h&igrave;nh to lớn, vập vạp của hắn từ dốc chợ đi xuống l&agrave; &ugrave;a cả ra v&acirc;y lấy hắn, reo cười v&aacute;ng l&ecirc;n:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- A a a... Anh Tr&agrave;ng! Anh Tr&agrave;ng đ&atilde; về ch&uacute;ng m&agrave;y ơi!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Anh Tr&agrave;ng ơi bế em mấy...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Anh Tr&agrave;ng ơi đ&atilde; uống rượu chưa?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Anh Tr&agrave;ng ơi!...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Đứa t&uacute;m đằng trước, đứa t&uacute;m đằng sau, đứa c&ugrave;, đứa k&eacute;o, đứa l&ocirc;i ch&acirc;n kh&ocirc;ng cho đi. Tr&agrave;ng chỉ ngửa mặt l&ecirc;n cười hềnh hệch.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">C&aacute;i x&oacute;m ngụ cư tồi t&agrave;n ấy mỗi chiều lại x&ocirc;n xao l&ecirc;n được một l&uacute;c.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Nhưng độ n&agrave;y th&igrave; trẻ con kh&ocirc;ng đứa n&agrave;o buồn ra đ&oacute;n Tr&agrave;ng nữa, ch&uacute;ng n&oacute; ngồi ủ rũ dưới những x&oacute; đường kh&ocirc;ng buồn nh&uacute;c nh&iacute;ch. Trong b&oacute;ng chiều nh&aacute; nhem, Tr&agrave;ng đi từng bước mệt mỏi, chiếc &aacute;o n&acirc;u t&agrave;ng vắt sang một b&ecirc;n c&aacute;nh tay, c&aacute;i đầu trọc nhẵn ch&uacute;i về đằng trước. H&igrave;nh như những lo lắng, chật vật trong một ng&agrave;y đ&egrave; xuống c&aacute;i lưng to rộng như lưng gấu của hắn.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">C&aacute;i đ&oacute;i đ&atilde; tr&agrave;n đến x&oacute;m n&agrave;y tự l&uacute;c n&agrave;o. Những gia đ&igrave;nh từ những v&ugrave;ng Nam Định, Th&aacute;i B&igrave;nh, đội chiếu lũ lượt bồng bế, dắt d&iacute;u nhau l&ecirc;n xanh x&aacute;m như những b&oacute;ng ma, v&agrave; nằm ngổn ngang khắp lều chợ. Người chết như ngả rạ. Kh&ocirc;ng buổi s&aacute;ng n&agrave;o người trong l&agrave;ng đi chợ, đi l&agrave;m đồng kh&ocirc;ng gặp ba bốn c&aacute;i th&acirc;y nằm c&ograve;ng queo b&ecirc;n đường. Kh&ocirc;ng kh&iacute; vẩn l&ecirc;n m&ugrave;i ẩm thối của r&aacute;c rưởi v&agrave; m&ugrave;i g&acirc;y của x&aacute;c người.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Giữa c&aacute;i cảnh tối sầm lại v&igrave; đ&oacute;i kh&aacute;t ấy, một buổi chiều người trong x&oacute;m bỗng thấy Tr&agrave;ng về với một người đ&agrave;n b&agrave; nữa. Mặt hắn c&oacute; một vẻ g&igrave; phởn phơ kh&aacute;c thường. Hắn tủm tỉm cười nụ một m&igrave;nh v&agrave; hai mắt th&igrave; s&aacute;ng l&ecirc;n lấp l&aacute;nh. Người đ&agrave;n b&agrave; đi sau hắn chừng ba bốn bước. Thị cắp c&aacute;i th&uacute;ng con, đầu hơi c&uacute;i xuống, c&aacute;i n&oacute;n r&aacute;ch t&agrave;ng nghi&ecirc;ng nghi&ecirc;ng che khuất đi nửa mặt. Thị c&oacute; vẻ r&oacute;n r&eacute;n, e thẹn. Mấy đứa trẻ con thấy lạ vội chạy ra đ&oacute;n xem. Sợ ch&uacute;ng n&oacute; đ&ugrave;a như trước, Tr&agrave;ng vội v&agrave;ng nghi&ecirc;m n&eacute;t mặt, lắc đầu ra hiệu kh&ocirc;ng bằng l&ograve;ng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Mấy đứa trẻ đứng dừng lại, nh&igrave;n Tr&agrave;ng, đột nhi&ecirc;n c&oacute; đứa g&agrave;o l&ecirc;n:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Anh Tr&agrave;ng ơi! - Tr&agrave;ng quay đầu lại. N&oacute; cong cổ g&agrave;o l&ecirc;n lần nữa - Chồng vợ h&agrave;i.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Tr&agrave;ng bật cười:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Bố ranh!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Người đ&agrave;n b&agrave; c&oacute; vẻ kh&oacute; chịu lắm. Thị nh&iacute;u đ&ocirc;i l&ocirc;ng m&agrave;y lại, đưa tay l&ecirc;n x&oacute;c x&oacute;c lại t&agrave; &aacute;o. Ng&atilde; tư x&oacute;m chợ về chiều c&agrave;ng x&aacute;c xơ, heo h&uacute;t. Từng trận gi&oacute; từ c&aacute;nh đồng thổi v&agrave;o, ngăn ngắt. Hai b&ecirc;n d&atilde;y phố, &uacute;p s&uacute;p, tối om, kh&ocirc;ng nh&agrave; n&agrave;o c&oacute; &aacute;nh đ&egrave;n, lửa. Dưới những gốc đa, gốc gạo x&ugrave; x&igrave; b&oacute;ng những người đ&oacute;i dật dờ đi lại lặng lẽ như những b&oacute;ng ma. Tiếng quạ tr&ecirc;n mấy c&acirc;y gạo ngo&agrave;i b&atilde;i chợ cứ g&agrave;o l&ecirc;n từng hồi th&ecirc; thiết.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Nh&igrave;n theo b&oacute;ng Tr&agrave;ng v&agrave; b&oacute;ng người đ&agrave;n b&agrave; lủi thủi đi về bến, người trong x&oacute;m lạ lắm. Họ đứng cả trong ngưỡng cửa nh&igrave;n ra b&agrave;n t&aacute;n. H&igrave;nh như họ cũng hiểu được đ&ocirc;i phần. Những khu&ocirc;n mặt hốc h&aacute;c u tối của họ bỗng dưng rạng rỡ hẳn l&ecirc;n. C&oacute; c&aacute;i g&igrave; lạ l&ugrave;ng v&agrave; tươi m&aacute;t thổi v&agrave;o cuộc sống đ&oacute;i kh&aacute;t, tăm tối ấy của họ. Một người thở d&agrave;i. Người kh&aacute;c khẽ th&igrave; thầm hỏi:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Ai đấy nhỉ? ... Hay l&agrave; người dưới qu&ecirc; b&agrave; cụ Tứ mới l&ecirc;n?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Chả phải, từ ng&agrave;y c&ograve;n mồ ma &ocirc;ng cụ Tứ c&oacute; thấy họ mạc n&agrave;o l&ecirc;n thăm đ&acirc;u.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Qu&aacute;i nhỉ?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Im một l&uacute;c, c&oacute; người bỗng lại cười l&ecirc;n rung r&uacute;c:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Hay l&agrave; vợ anh cu Tr&agrave;ng? &Ugrave; kh&eacute;o m&agrave; vợ anh cu Tr&agrave;ng thật anh em ạ, tr&ocirc;ng chị ta th&egrave;n thẹn hay đ&aacute;o để.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- &Ocirc;i chao! Giời đất n&agrave;y c&ograve;n rước c&aacute;i của nợ đời về. Biết c&oacute; nu&ocirc;i nổi nhau sống qua được c&aacute;i th&igrave; n&agrave;y kh&ocirc;ng?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Họ c&ugrave;ng n&iacute;n lặng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Người đ&agrave;n b&agrave; như cũng biết xung quanh người ta đang nh&igrave;n dồn cả về ph&iacute;a m&igrave;nh, thị c&agrave;ng ngượng nghịu, ch&acirc;n nọ bước d&iacute;u cả v&agrave;o ch&acirc;n kia. Hắn cũng biết thế, nhưng hắn lại lấy vậy l&agrave;m th&iacute;ch &yacute; lắm, c&aacute;i mặt cứ v&ecirc;nh l&ecirc;n tự đắc với m&igrave;nh. Người đ&agrave;n b&agrave; c&agrave;u nh&agrave;u c&acirc;u g&igrave; trong miệng. Hắn quay lại hỏi:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- G&igrave; hả?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Kh&ocirc;ng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn cũng c&agrave;u nh&agrave;u:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- L&agrave;m g&igrave; m&agrave; họ khỏe nh&igrave;n thế kh&ocirc;ng biết? - Hắn bỗng đứng dừng lại nh&igrave;n ngang nh&igrave;n ngửa.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- N&agrave;y b&aacute;c Tr&agrave;ng! B&aacute;c Tr&agrave;ng! ...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Sau một khu&ocirc;n cửa tối, một c&aacute;i đầu trọc th&ograve; ra gọi giật giọng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Tr&agrave;ng lật đật quay lại.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Về muộn mấy? Hẵng v&agrave;o chơi c&aacute;i đ&atilde; n&agrave;o.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Tr&agrave;ng đứng lại tho&aacute;i th&aacute;c:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Th&ocirc;i &ocirc;ng để cho đến h&ocirc;m kh&aacute;c.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">C&aacute;i đầu trọc nh&aacute;y nh&aacute;y một con mắt, hất hất về ph&iacute;a người đ&agrave;n b&agrave; h&oacute;m hỉnh:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- C&aacute;nh n&agrave;o đấy?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- &Agrave; h&agrave;... người quen. Th&ocirc;i để h&ocirc;m kh&aacute;c &ocirc;ng nh&aacute;.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn quay ngoắt trở ra, lật đật chạy theo người đ&agrave;n b&agrave;, như người xấu hổ chạy trốn. Người đ&agrave;n b&agrave; đ&atilde; đi đến trước c&aacute;i miếu cũ. Tr&agrave;ng gọi với:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Ấy rẽ lối n&agrave;y cơ m&agrave;.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Đằng n&agrave;y &agrave;?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Ừ.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hai người lặng lẽ rẽ xuống một con đường nhỏ. Con đường s&acirc;u thăm thẳm, luồn giữa hai bờ tre cao v&uacute;t. Ở đ&acirc;y vắng vẻ, thoải m&aacute;i. Hắn định n&oacute;i với thị một v&agrave;i c&acirc;u r&otilde; t&igrave;nh tứ m&agrave; chẳng biết n&oacute;i thế n&agrave;o. Hắn cứ l&uacute;ng ta l&uacute;ng t&uacute;ng, tay nọ xoa xoa v&agrave;o vai kia đi b&ecirc;n người đ&agrave;n b&agrave;. Thị cũng kh&ocirc;ng n&oacute;i g&igrave;, hai con mắt tư lự nh&igrave;n ra ph&iacute;a trước. Tiếng gi&oacute; tr&ecirc;n bờ tre r&igrave; r&agrave;o v&agrave; tiếng l&aacute; kh&ocirc; k&ecirc;u x&agrave;o xạc dưới b&agrave;n ch&acirc;n.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Trong một l&uacute;c Tr&agrave;ng h&igrave;nh như qu&ecirc;n hết những cảnh sống &ecirc; chề, tăm tối hằng ng&agrave;y, qu&ecirc;n cả c&aacute;i đ&oacute;i kh&aacute;t gh&ecirc; gớm đang đe dọa, qu&ecirc;n cả những th&aacute;ng ng&agrave;y trước mặt. Trong l&ograve;ng hắn b&acirc;y giờ chỉ c&ograve;n t&igrave;nh nghĩa giữa hắn với người đ&agrave;n b&agrave; đi b&ecirc;n. Một c&aacute;i g&igrave; mới mẻ, lạ lắm, chưa từng thấy ở người đ&agrave;n &ocirc;ng ngh&egrave;o khổ ấy, n&oacute; &ocirc;m ấp, mơn man khắp da thịt Tr&agrave;ng, tựa hồ như c&oacute; b&agrave;n tay vuốt nhẹ tr&ecirc;n sống lưng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Sắp đến chưa? - Người đ&agrave;n b&agrave; chợt hỏi.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Sắp!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Nh&agrave; c&oacute; ai kh&ocirc;ng?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- C&oacute; một m&igrave;nh t&ocirc;i mấy u.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thị tủm tỉm cười:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Đ&atilde; một m&igrave;nh lại c&ograve;n mấy u. B&eacute; lắm đấy!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn bật cười:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- &Agrave; nhỉ.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">C&acirc;u chuyện xem chừng đ&atilde; th&acirc;n th&acirc;n. Hắn đi s&aacute;t gần b&ecirc;n thị hơn, ngẫm nghĩ một l&uacute;c, chợt hắn giơ c&aacute;i chai con vẫn cầm lăm lăm một b&ecirc;n tay l&ecirc;n khoe:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Dầu tối thắp đ&acirc;y n&agrave;y.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Sang nhỉ.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Kh&aacute; th&ocirc;i. Hai h&agrave;o đấy, đắt qu&aacute;, cơ m&agrave; th&ocirc;i chả cần.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Hoang n&oacute; vừa vừa chứ.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn chặc lưỡi:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Vợ mới vợ miếc cũng phải cho n&oacute; s&aacute;ng sủa một t&iacute; chứ, chả nhẽ chưa tối đ&atilde; r&uacute;c v&agrave;o ngay, h&igrave; h&igrave;...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Khỉ gi&oacute;.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thị ph&aacute;t đ&aacute;nh đ&eacute;t v&agrave;o lưng hắn, khoặm mặt lại.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn th&iacute;ch ch&iacute; ngửa cổ cười khanh kh&aacute;ch.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Mấy con ch&oacute; giật m&igrave;nh chạy thọt v&agrave;o trong lũy th&ograve; m&otilde;m ra sủa v&aacute;ng l&ecirc;n. Tr&agrave;ng nhặt một h&ograve;n gạch vung tay n&eacute;m mạnh một c&aacute;i:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Mẹ bố ch&uacute;ng m&agrave;y cắn g&igrave; thế!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Vẫn chưa đến &agrave;?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- C&ograve;n ch&aacute;n.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Khiếp.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thị cau mặt lại, gi&atilde;y l&ecirc;n. Hắn ph&igrave; ra cười, c&uacute;i xuống nhấc c&agrave;nh dong dấp cổng ra reo l&ecirc;n:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Đ&acirc;y rồi!...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thị lẳng lặng theo hắn v&agrave;o trong nh&agrave;, c&aacute;i nh&agrave; vắng teo đứng r&uacute;m r&oacute; tr&ecirc;n mảnh vườn mọc lổn nhổn những b&uacute;i cỏ dại. Thị đảo mắt nh&igrave;n xung quanh, c&aacute;i ngực gầy l&eacute;p nh&ocirc; hẳn l&ecirc;n, n&eacute;n một tiếng thở d&agrave;i. Tr&agrave;ng xăm xăm bước v&agrave;o trong nh&agrave;, nhấc tấm ph&ecirc;n r&aacute;ch sang một b&ecirc;n, thu dọn những ni&ecirc;u b&aacute;t, xống &aacute;o vứt bừa bộn cả tr&ecirc;n giường, dưới đất. Hắn quay lại nh&igrave;n thị cười cười:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Kh&ocirc;ng c&oacute; người đ&agrave;n b&agrave;, nh&agrave; cửa thế đấy!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thị nhếch m&eacute;p cười nhạt nhẽo. Tr&agrave;ng vỗ vỗ xuống giường đon đả:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Ngồi đ&acirc;y!... Ngồi xuống đ&acirc;y, tự nhi&ecirc;n...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Người đ&agrave;n b&agrave; theo lời hắn ngồi mớm xuống m&eacute;p giường. Cả hai bỗng c&ugrave;ng ngượng nghịu. Tr&agrave;ng đứng t&acirc;y ng&acirc;y ra giữa nh&agrave; một l&uacute;c, chợt hắn thấy sờ sợ. Ch&iacute;nh hắn cũng kh&ocirc;ng hiểu sao hắn sợ, hắn lấm l&eacute;t bước vội mấy bước ra s&acirc;n gắt l&ecirc;n:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Sao h&ocirc;m nay b&agrave; l&atilde;o về muộn thế kh&ocirc;ng biết!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn loanh quanh hết chạy ra ng&otilde; đứng ng&oacute;ng, lại chạy v&agrave;o s&acirc;n nh&igrave;n trộm v&agrave;o trong nh&agrave;. Thị vẫn ngồi mớm ở m&eacute;p giường, hai tay &ocirc;m khư khư c&aacute;i th&uacute;ng, mặt bần thần.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn nghĩ bụng: &ldquo;Qu&aacute;i sao n&oacute; lại buồn thế nhỉ?... Ồ sao n&oacute; lại buồn thế nhỉ?...&rdquo;. Hắn nhổ vu vơ một b&atilde;i nước bọt, tủm tỉm cười một m&igrave;nh. Nh&igrave;n thị ngồi ngay giữa nh&agrave;, đến b&acirc;y giờ hắn vẫn c&ograve;n ngờ ngợ như kh&ocirc;ng phải thế. Ra hắn đ&atilde; c&oacute; vợ rồi đấy ư? H&agrave;! Việc xảy ra thật hắn cũng kh&ocirc;ng ngờ, hắn cũng chỉ tầm phơ tầm ph&agrave;o đ&acirc;u c&oacute; hai bận ấy thế m&agrave; th&agrave;nh vợ th&agrave;nh chồng&hellip;</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">&Iacute;t l&acirc;u nay hắn xe th&oacute;c Li&ecirc;n đo&agrave;n l&ecirc;n tỉnh. Mỗi bận qua cửa nh&agrave; kho lại thấy mấy chị con g&aacute;i ngồi v&ecirc;u ra ở đ&acirc;y. Hắn đo&aacute;n họ ngồi đấy nhặt hạt rơi v&atilde;i, hay ai c&oacute; c&ocirc;ng việc g&igrave; gọi đến th&igrave; l&agrave;m. Một lần hắn đang g&ograve; lưng k&eacute;o c&aacute;i xe b&ograve; th&oacute;c v&agrave;o dốc tỉnh, hắn h&ograve; một c&acirc;u chơi cho đỡ nhọc. Hắn h&ograve; rằng:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">&ldquo;Muốn ăn cơm trắng mấy gi&ograve; n&agrave;y!<br />\r\nLại đ&acirc;y m&agrave; đẩy xe b&ograve; với anh, n&igrave;!&rdquo;</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Chủ t&acirc;m hắn cũng chẳng c&oacute; &yacute; ch&ograve;ng ghẹo c&ocirc; n&agrave;o, nhưng mấy c&ocirc; g&aacute;i lại cứ đẩy vai c&ocirc; ả n&agrave;y ra với hắn, cười như nắc nẻ:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- K&igrave;a anh ấy gọi! C&oacute; muốn ăn cơm trắng mấy gi&ograve; th&igrave; ra đẩy xe b&ograve; với anh ấy!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thị cong cớn:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- C&oacute; khối cơm trắng mấy gi&ograve; đấy! N&agrave;y, nh&agrave; t&ocirc;i ơi, n&oacute;i thật hay n&oacute;i kho&aacute;c đấy?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Tr&agrave;ng ngo&aacute;i cổ lại vuốt mồ h&ocirc;i tr&ecirc;n mặt cười:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Thật đấy, c&oacute; đẩy th&igrave; ra mau l&ecirc;n!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thị v&ugrave;ng đứng dậy, ton ton chạy lại đẩy xe cho Tr&agrave;ng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Đ&atilde; thật th&igrave; đẩy chứ sợ g&igrave;, đằng ấy nhỉ! - Thị liếc mắt, cười t&iacute;t.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Tr&agrave;ng th&iacute;ch lắm. Từ cha sinh mẹ đẻ đến giờ, chưa c&oacute; người con g&aacute;i n&agrave;o cười với hắn t&igrave;nh tứ như thế.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Lần thứ hai, Tr&agrave;ng vừa trả h&agrave;ng xong, ngồi uống nước ở ngo&agrave;i cổng chợ tỉnh th&igrave; thị ở đ&acirc;u sầm sập chạy đến. Thị đứng trước mặt hắn sưng sỉa n&oacute;i:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Đi&ecirc;u! Người thế m&agrave; đi&ecirc;u!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn giương mắt nh&igrave;n thị, kh&ocirc;ng hiểu. Thật ra l&uacute;c ấy hắn cũng chưa nhận ra thị l&agrave; ai. H&ocirc;m nay thị r&aacute;ch qu&aacute;, &aacute;o quần tả tơi như tổ đỉa, thị gầy sọp hẳn đi, tr&ecirc;n c&aacute;i khu&ocirc;n mặt lưỡi c&agrave;y x&aacute;m xịt chỉ c&ograve;n thấy hai con mắt.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- H&ocirc;m ấy leo lẻo c&aacute;i mồm hẹn xuống, thế m&agrave; mất mặt. &agrave;, hắn nhớ ra rồi, hắn to&eacute;t miệng cười:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Chả h&ocirc;m ấy th&igrave; h&ocirc;m nay vậy. N&agrave;y hẵng ngồi xuống ăn miếng giầu đ&atilde;.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- C&oacute; ăn g&igrave; th&igrave; ăn, chả ăn giầu.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thị vẫn đứng cong cớn trước mặt hắn.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Đ&acirc;y, muốn ăn g&igrave; th&igrave; ăn.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn vỗ vỗ v&agrave;o t&uacute;i:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- R&iacute;ch bố cu, hở!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hai con mắt trũng ho&aacute;y của thị tức th&igrave; s&aacute;ng l&ecirc;n, thị đon đả:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Ăn thật nh&aacute;! Ừ ăn th&igrave; ăn sợ g&igrave;.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thế l&agrave; thị ngồi s&agrave; xuống ăn thật. Thị cắm đầu ăn một chặp bốn b&aacute;t b&aacute;nh đ&uacute;c liền chẳng chuyện tr&ograve; g&igrave;. Ăn xong thị cầm dọc đ&ocirc;i đũa quệt ngang miệng, thở:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- H&agrave;, ngon! Về chị ấy thấy hụt tiền th&igrave; bỏ bố.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn cười:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- L&agrave;m đếch g&igrave; c&oacute; vợ. N&agrave;y n&oacute;i đ&ugrave;a chứ c&oacute; về với tớ th&igrave; ra khu&acirc;n h&agrave;ng l&ecirc;n xe rồi c&ugrave;ng về.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">N&oacute;i thế Tr&agrave;ng cũng tưởng l&agrave; n&oacute;i đ&ugrave;a, ai ngờ thị về thật. Mới đầu anh ch&agrave;ng cũng chợn, nghĩ: th&oacute;c gạo n&agrave;y đến c&aacute;i th&acirc;n m&igrave;nh cũng chả biết c&oacute; nu&ocirc;i nổi kh&ocirc;ng, lại c&ograve;n đ&egrave;o b&ograve;ng. Sau kh&ocirc;ng biết nghĩ thế n&agrave;o hắn chặc lưỡi một c&aacute;i:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Chặc, kệ!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">H&ocirc;m ấy hắn đưa thị v&agrave;o chợ tỉnh bỏ tiền ra mua cho thị c&aacute;i th&uacute;ng con đựng v&agrave;i thứ lặt vặt v&agrave; ra h&agrave;ng cơm đ&aacute;nh một bữa thật no n&ecirc; rồi c&ugrave;ng đẩy xe b&ograve; về...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Tr&agrave;ng chợt đứng dừng lại, lắng tai nghe. Ngo&agrave;i ng&otilde; c&oacute; tiếng người h&uacute;ng hắng ho, một b&agrave; l&atilde;o từ ngo&agrave;i rặng tre lọng khọng đi v&agrave;o ng&otilde;. B&agrave; l&atilde;o vừa đi vừa lẩm bẩm t&iacute;nh to&aacute;n g&igrave; trong miệng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thấy mẹ, Tr&agrave;ng reo l&ecirc;n như một đứa trẻ, v&agrave; gọi với v&agrave;o trong nh&agrave;:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- U đ&atilde; về đấy!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn lật đật chạy ra đ&oacute;n:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- H&ocirc;m nay sao u về muộn thế! L&agrave;m t&ocirc;i đợi n&oacute;ng cả ruột.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">B&agrave; cụ Tứ nhấp nh&aacute;y hai con mắt nh&igrave;n Tr&agrave;ng, chậm chạp hỏi:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- C&oacute; việc g&igrave; thế vậy?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Th&igrave; u hẵng cứ v&agrave;o trong nh&agrave; đ&atilde; n&agrave;o.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">B&agrave; l&atilde;o phấp phỏng bước theo con v&agrave;o trong nh&agrave;. Đến giữa s&acirc;n b&agrave; l&atilde;o đứng sững lại, b&agrave; l&atilde;o c&agrave;ng ngạc nhi&ecirc;n hơn. Qu&aacute;i, sao lại c&oacute; người đ&agrave;n b&agrave; n&agrave;o ở trong ấy nhỉ? Người đ&agrave;n b&agrave; n&agrave;o lại đứng ngay đầu giường thằng con m&igrave;nh thế kia? Sao lại ch&agrave;o m&igrave;nh bằng u? Kh&ocirc;ng phải con c&aacute;i Đục m&agrave;. Ai thế nhỉ? B&agrave; l&atilde;o hấp h&aacute;y cặp mắt cho đỡ nho&egrave;n v&igrave; tự dưng b&agrave; l&atilde;o thấy mắt m&igrave;nh nho&egrave;n ra th&igrave; phải. B&agrave; l&atilde;o nh&igrave;n kỹ người đ&agrave;n b&agrave; lẫn nữa, vẫn chưa nhận ra người n&agrave;o. B&agrave; l&atilde;o quay lại nh&igrave;n con tỏ &yacute; kh&ocirc;ng hiểu.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Tr&agrave;ng tươi cười:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Th&igrave; u hẵng v&agrave;o ngồi l&ecirc;n giường l&ecirc;n diếc chĩnh chện c&aacute;i đ&atilde; n&agrave;o.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">B&agrave; l&atilde;o lập cập bước v&agrave;o. Người đ&agrave;n b&agrave; tưởng b&agrave; l&atilde;o gi&agrave; cả, điếc l&aacute;c, thị cất tiếng ch&agrave;o lần nữa:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- U đ&atilde; về ạ!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">&Ocirc; hay, thế l&agrave; thế n&agrave;o nhỉ? B&agrave; l&atilde;o băn khoăn ngồi xuống giường.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Tr&agrave;ng nhắc mẹ:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- K&igrave;a nh&agrave; t&ocirc;i n&oacute; ch&agrave;o u.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thấy mẹ vẫn chưa hiểu, hắn bước lại gần n&oacute;i tiếp:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Nh&agrave; t&ocirc;i n&oacute; mới về l&agrave;m bạn với t&ocirc;i đấy u ạ! Ch&uacute;ng t&ocirc;i phải duy&ecirc;n phải kiếp với nhau... Chẳng qua n&oacute; cũng l&agrave; c&aacute;i số cả...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">B&agrave; l&atilde;o c&uacute;i đầu n&iacute;n lặng. B&agrave; l&atilde;o hiểu rồi. L&ograve;ng người mẹ ngh&egrave;o khổ ấy c&ograve;n hiểu ra biết bao nhi&ecirc;u cơ sự, vừa ai o&aacute;n vừa x&oacute;t thương cho số kiếp đứa con m&igrave;nh. Chao &ocirc;i, người ta dựng vợ gả chồng cho con l&agrave; l&uacute;c trong nh&agrave; ăn n&ecirc;n l&agrave;m nổi, những mong sinh con đẻ c&aacute;i mở mặt sau n&agrave;y. C&ograve;n m&igrave;nh th&igrave;... Trong kẽ mắt k&egrave;m nh&egrave;m của b&agrave; rủ xuống hai d&ograve;ng nước mắt... Biết rằng ch&uacute;ng n&oacute; c&oacute; nu&ocirc;i nổi nhau sống qua được cơn đ&oacute;i kh&aacute;t n&agrave;y kh&ocirc;ng?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">B&agrave; l&atilde;o khẽ thở d&agrave;i đứng l&ecirc;n, đăm đăm nh&igrave;n người đ&agrave;n b&agrave;. Thị c&uacute;i mặt xuống, tay v&acirc;n v&ecirc; t&agrave; &aacute;o đ&atilde; r&aacute;ch bợt. B&agrave; l&atilde;o nh&igrave;n thị v&agrave; b&agrave; nghĩ: Người ta c&oacute; gặp bước kh&oacute; khăn, đ&oacute;i khổ n&agrave;y, người ta mới lấy đến con m&igrave;nh. M&agrave; con m&igrave;nh mới c&oacute; vợ được... Th&ocirc;i th&igrave; bổn phận b&agrave; l&agrave; mẹ, b&agrave; đ&atilde; chẳng lo lắng được cho con... May ra m&agrave; qua khỏi được c&aacute;i tao đoạn n&agrave;y th&igrave; thằng con b&agrave; cũng c&oacute; vợ, n&oacute; y&ecirc;n bề n&oacute;, chẳng may ra &ocirc;ng giời bắt chết cũng phải chịu chứ biết thế n&agrave;o m&agrave; lo cho hết được?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">B&agrave; l&atilde;o khẽ dặng hắng một tiếng, nhẹ nh&agrave;ng n&oacute;i với &ldquo;n&agrave;ng d&acirc;u mới&rdquo;:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Ừ, th&ocirc;i th&igrave; c&aacute;c con đ&atilde; phải duy&ecirc;n phải kiếp với nhau, u cũng mừng l&ograve;ng...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Tr&agrave;ng thở đ&aacute;nh ph&agrave;o một c&aacute;i, ngực nhẹ hẳn đi. Hắn ho khẽ một tiếng, bước từng bước d&agrave;i ra s&acirc;n. B&agrave; cụ Tứ vẫn từ tốn tiếp lời:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Nh&agrave; ta ngh&egrave;o con ạ. Vợ chồng ch&uacute;ng m&agrave;y liệu m&agrave; bảo nhau l&agrave;m ăn. Rồi ra may m&agrave; &ocirc;ng giời cho kh&aacute;... Biết thế n&agrave;o hở con, ai gi&agrave;u ba họ, ai kh&oacute; ba đời? C&oacute; ra th&igrave; rồi con c&aacute;i ch&uacute;ng m&agrave;y về sau.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">B&agrave; l&atilde;o đăm đăm nh&igrave;n ra ngo&agrave;i. B&oacute;ng tối tr&ugrave;m lấy hai con mắt. Ngo&agrave;i xa d&ograve;ng s&ocirc;ng s&aacute;ng trắng uốn kh&uacute;c trong c&aacute;nh đồng tối. M&ugrave;i đốt đống rấm ở những nh&agrave; c&oacute; người chết theo gi&oacute; thoảng v&agrave;o kh&eacute;t lẹt. B&agrave; l&atilde;o thở nhẹ ra một hơi d&agrave;i. B&agrave; l&atilde;o nghĩ đến &ocirc;ng l&atilde;o, nghĩ đến đưa con g&aacute;i &uacute;t. B&agrave; l&atilde;o nghĩ đến cuộc đời cực khổ d&agrave;i dằng dặc của m&igrave;nh. Vợ chồng ch&uacute;ng n&oacute; lấy nhau, cuộc đời ch&uacute;ng n&oacute; liệu c&oacute; hơn bố mẹ trước kia kh&ocirc;ng?...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Con ngồi xuống đ&acirc;y. Ngồi xuống đ&acirc;y cho đỡ mỏi ch&acirc;n.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">B&agrave; l&atilde;o nh&igrave;n người đ&agrave;n b&agrave;, l&ograve;ng đầy thương x&oacute;t. N&oacute; b&acirc;y giờ l&agrave; d&acirc;u l&agrave; con trong nh&agrave; rồi. Người đ&agrave;n b&agrave; khẽ nh&uacute;c nh&iacute;ch, thị vẫn kh&eacute;p n&eacute;p đứng nguy&ecirc;n chỗ cũ. B&agrave; l&atilde;o hạ thấp giọng xuống th&acirc;n mật:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Kể c&oacute; ra l&agrave;m được dăm ba m&acirc;m th&igrave; phải đấy, nhưng nh&agrave; m&igrave;nh ngh&egrave;o, cũng chả ai người ta chấp nhặt chỉ c&aacute;i l&uacute;c n&agrave;y. Cốt l&agrave;m sao ch&uacute;ng m&agrave;y h&ograve;a thuận l&agrave; u mừng rồi. Năm nay th&igrave; đ&oacute;i to đấy. Ch&uacute;ng m&agrave;y lấy nhau l&uacute;c n&agrave;y, u thương qu&aacute;...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">B&agrave; cụ nghẹn lời kh&ocirc;ng n&oacute;i được nữa, nước mắt cứ chảy xuống r&ograve;ng r&ograve;ng. Tr&agrave;ng đứng ngo&agrave;i thấy vậy, hắn bực m&igrave;nh qu&aacute; hầm hầm bước v&agrave;o trong nh&agrave;, đ&aacute;nh di&ecirc;m đốt đ&egrave;n. Thấy s&aacute;ng, b&agrave; l&atilde;o vội v&agrave;ng lau nước mắt ngửng l&ecirc;n:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- C&oacute; đ&egrave;n đấy &agrave;? Ừ thắp l&ecirc;n một t&iacute; cho s&aacute;ng sủa... Dầu b&acirc;y giờ đắt gớm l&ecirc;n ấy m&agrave;y ạ.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">B&agrave; l&atilde;o đứng dậy uể oải sang giường b&ecirc;n kia nằm. Tr&agrave;ng l&agrave;u b&agrave;u trong miệng: &ldquo;Ch&aacute;n qu&aacute;, chẳng đ&acirc;u v&agrave;o đ&acirc;u tự nhi&ecirc;n cũng kh&oacute;c!&rdquo;</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Tiếng b&agrave; cụ Tứ b&ecirc;n kia n&oacute;i với sang:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- H&ocirc;m n&agrave;o nghỉ ở nh&agrave; kiếm lấy &iacute;t nứa về đan c&aacute;i ph&ecirc;n m&agrave; ngăn ra m&agrave;y ạ.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- V&acirc;ng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Tr&agrave;ng ngoan ngo&atilde;n đ&aacute;p lời mẹ. Hắn toan quay lại bảo thị cởi &aacute;o đi nằm, nhưng thấy thị ngồi bần thần dưới ch&acirc;n giường tự nhi&ecirc;n hắn lại kh&ocirc;ng d&aacute;m. Hắn lẳng lặng ngồi xuống c&aacute;i ghế b&ecirc;n cạnh, cả hai c&ugrave;ng sượng s&ugrave;ng chả biết n&oacute;i g&igrave;. &Aacute;nh đ&egrave;n v&agrave;ng đục ở g&oacute;c nh&agrave; tỏa ra ấm &aacute;p v&agrave; k&eacute;o d&agrave;i hai c&aacute;i b&oacute;ng tr&ecirc;n v&aacute;ch. B&ecirc;n ngo&agrave;i gi&oacute; từ mặt s&ocirc;ng thổi l&ecirc;n r&igrave; r&agrave;o như tiếng người thầm th&igrave; n&oacute;i chuyện. Giữa sự im lặng của đ&ocirc;i vợ chồng mới, c&oacute; tiếng ai hờ kh&oacute;c ngo&agrave;i x&oacute;m lọt v&agrave;o tỉ t&ecirc; l&uacute;c to l&uacute;c nhỏ. Người đ&agrave;n b&agrave; bỗng thở d&agrave;i. Tr&agrave;ng quay lại khẽ hỏi:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Buồn &agrave;?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Kh&ocirc;ng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Gớm sao l&uacute;c n&agrave;y n&oacute;i chuyện dai thế, đợi sốt cả ruột.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thị lườm hắn kh&ocirc;ng trả lời. Hắn x&iacute;ch lại cười cười:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Th&ocirc;i khuya rồi đấy, ngủ đi.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thị giơ tay củng v&agrave;o tr&aacute;n hắn:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Chỉ được c&aacute;i thế l&agrave; nhanh. Dơ!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn cười kh&igrave; kh&igrave;, vươn cổ thổi tắt phụt ngọn đ&egrave;n. Trong đ&ecirc;m khuya tiếng hờ kh&oacute;c tỉ t&ecirc; nghe c&agrave;ng r&otilde;...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">S&aacute;ng h&ocirc;m sau, mặt trời l&ecirc;n bằng con s&agrave;o, Tr&agrave;ng mới trở dậy. Trong người &ecirc;m &aacute;i lửng lơ như người vừa ở trong giấc mơ đi ra. Việc hắn c&oacute; vợ đến h&ocirc;m nay hắn vẫn c&ograve;n ngỡ ng&agrave;ng như kh&ocirc;ng phải.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn chắp hai tay sau lưng lững thững bước ra s&acirc;n. &Aacute;nh nắng buổi s&aacute;ng m&ugrave;a h&egrave; s&aacute;ng l&oacute;a x&oacute;i v&agrave;o hai con mắt c&ograve;n cay x&egrave; của hắn. Hắn chớp chớp li&ecirc;n hồi mấy c&aacute;i, v&agrave; bỗng vừa chợt nhận ra, xung quanh m&igrave;nh c&oacute; c&aacute;i g&igrave; vừa thay đổi mới mẻ, kh&aacute;c lạ. Nh&agrave; cửa, s&acirc;n vườn h&ocirc;m nay đều được qu&eacute;t tước, thu dọn sạch sẽ gọn g&agrave;ng. Mấy chiếc quần &aacute;o r&aacute;ch như tổ đỉa vẫn vắt khươm mươi ni&ecirc;n ở một g&oacute;c nh&agrave; đ&atilde; thấy đem ra s&acirc;n hong. Hai c&aacute;i ang nước vẫn để kh&ocirc; cong ở dưới gốc ổi đ&atilde; k&iacute;n nước đầy ăm ắp. Đống r&aacute;c m&ugrave;n tung ho&agrave;nh ngay lối đi đ&atilde; h&oacute;t sạch.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Ngo&agrave;i vườn người mẹ đang l&uacute;i h&uacute;i giẫy những b&uacute;i cỏ mọc nham nhở. Vợ hắn qu&eacute;t lại c&aacute;i s&acirc;n, tiếng chổi từng nh&aacute;t k&ecirc;u s&agrave;n sạt tr&ecirc;n mặt đất. Cảnh tượng thật đơn giản, b&igrave;nh thường nhưng đối với hắn lại rất thấm th&iacute;a cảm động. Bỗng nhi&ecirc;n hắn thấy hắn thương y&ecirc;u gắn b&oacute; với c&aacute;i nh&agrave; của hắn lạ l&ugrave;ng. Hắn đ&atilde; c&oacute; một gia đ&igrave;nh. Hắn sẽ c&ugrave;ng vợ sinh con đẻ c&aacute;i ở đấy. C&aacute;i nh&agrave; như c&aacute;i tổ ấm che mưa che nắng. Một nguồn vui sướng, phấn chấn đột ngột tr&agrave;n ngập trong l&ograve;ng. B&acirc;y giờ hắn mới thấy hắn n&ecirc;n người, hắn thấy hắn c&oacute; bổn phận phải lo lắng cho vợ con sau n&agrave;y. Hắn xăm xăm chạy ra giữa s&acirc;n, hắn cũng muốn l&agrave;m một việc g&igrave; để dự phần tu sửa lại căn nh&agrave;.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">B&agrave; cụ Tứ chợt thấy con giai đ&atilde; dậy, b&agrave; l&atilde;o nhẹ nh&agrave;ng bảo n&agrave;ng d&acirc;u:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Anh ấy dậy rồi đấy. Con đi dọn cơm ăn chẳng muộn.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- V&acirc;ng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Người đ&agrave;n b&agrave; lẳng lặng đi v&agrave;o trong bếp. Tr&agrave;ng nom thị h&ocirc;m nay kh&aacute;c lắm, r&otilde; r&agrave;ng l&agrave; người đ&agrave;n b&agrave; hiền hậu đ&uacute;ng mực kh&ocirc;ng c&ograve;n vẻ g&igrave; chao ch&aacute;t chỏng lỏn như mấy lần Tr&agrave;ng gặp ở ngo&agrave;i tỉnh. Kh&ocirc;ng biết c&oacute; phải v&igrave; mới l&agrave;m d&acirc;u m&agrave; thị tu ch&iacute; l&agrave;m ăn kh&ocirc;ng? B&agrave; mẹ Tr&agrave;ng cũng nhẹ nh&otilde;m, tươi tỉnh kh&aacute;c ng&agrave;y thường, c&aacute;i mặt bủng beo u &aacute;m của b&agrave; rạng rỡ hẳn l&ecirc;n. B&agrave; l&atilde;o xăm xắn thu dọn, qu&eacute;t tước nh&agrave; cửa. H&igrave;nh như ai nấy đều c&oacute; &yacute; nghĩ rằng thu xếp cửa nh&agrave; cho quang quẻ, nề nếp th&igrave; cuộc đời họ c&oacute; thể kh&aacute;c đi, l&agrave;m ăn c&oacute; cơ khấm kh&aacute; hơn.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Bữa cơm ng&agrave;y đ&oacute;i tr&ocirc;ng thật thảm hại. Giữa c&aacute;i mẹt r&aacute;ch c&oacute; độc một l&ugrave;m rau chuối th&aacute;i rối, v&agrave; một đĩa muối ăn với ch&aacute;o, nhưng cả nh&agrave; đều ăn rất ngon l&agrave;nh. B&agrave; cụ vừa ăn vừa kể chuyện l&agrave;m ăn, gia cảnh với con d&acirc;u. B&agrave; l&atilde;o n&oacute;i to&agrave;n chuyện vui, to&agrave;n chuyện sung sướng về sau n&agrave;y:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Tr&agrave;ng ạ. Khi n&agrave;o c&oacute; tiền ta mua lấy đ&ocirc;i g&agrave;. Tao tin rằng c&aacute;i chỗ đầu bếp kia l&agrave;m c&aacute;i chuồng g&agrave; th&igrave; tiện qu&aacute;. N&agrave;y ngoảnh đi ngoảnh lại chả mấy m&agrave; c&oacute; ngay đ&agrave;n g&agrave; cho m&agrave; xem...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Tr&agrave;ng chỉ v&acirc;ng. Tr&agrave;ng v&acirc;ng rất ngoan ngo&atilde;n. Chưa bao giờ trong nh&agrave; n&agrave;y mẹ con lại đầm ấm, h&ograve;a hợp như thế. C&acirc;u chuyện trong bữa ăn đang đ&agrave; vui bỗng ngừng lại. Ni&ecirc;u ch&aacute;o l&otilde;ng b&otilde;ng, mỗi người được c&oacute; lưng lưng hai b&aacute;t đ&atilde; hết nhẵn.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">B&agrave; l&atilde;o đặt đũa b&aacute;t xuống, nh&igrave;n hai con vui vẻ:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Ch&uacute;ng m&agrave;y đợi u nh&aacute;. Tao c&oacute; c&aacute;i n&agrave;y hay lắm cơ.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">B&agrave; lật đật chạy xuống bếp, lễ mễ bưng ra một c&aacute;i nồi kh&oacute;i bốc l&ecirc;n nghi ng&uacute;t. B&agrave; l&atilde;o đặt c&aacute;i nồi xuống b&ecirc;n cạnh mẹt cơm, cầm c&aacute;i m&ocirc;i vừa khuấy khuấy vừa cười:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Ch&egrave; đ&acirc;y. - B&agrave; l&atilde;o m&uacute;c ra một b&aacute;t - Ch&egrave; kho&aacute;n đ&acirc;y, ngon đ&aacute;o để cơ.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Người con d&acirc;u đ&oacute;n lấy c&aacute;i b&aacute;t, đưa l&ecirc;n mắt nh&igrave;n, hai con mắt thị tối lại. Thị điềm nhi&ecirc;n v&agrave; v&agrave;o miệng. Tr&agrave;ng cầm c&aacute;i b&aacute;t thứ hai mẹ đưa cho, người mẹ vẫn tươi cười, đon đả:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- C&aacute;m đấy m&agrave;y ạ, h&igrave;. Ngon đ&aacute;o để, cứ thử ăn m&agrave; xem. X&oacute;m ta khối nh&agrave; c&ograve;n chả c&oacute; c&aacute;m m&agrave; ăn đấy.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Tr&agrave;ng cầm đ&ocirc;i đũa, gợt một miếng bỏ vội v&agrave;o miệng. Mặt hắn chum ngay lại, miếng c&aacute;m đắng ch&aacute;t v&agrave; nghẹn bứ trong cổ. Bữa cơm từ đấy kh&ocirc;ng ai n&oacute;i c&acirc;u g&igrave;, họ cắm đầu ăn cho xong lần, họ tr&aacute;nh nh&igrave;n mặt nhau. Một nỗi tủi hờn len v&agrave;o t&acirc;m tr&iacute; mọi người.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Ngo&agrave;i đ&igrave;nh bỗng dội l&ecirc;n một hồi trống, dồn dập, vội v&atilde;. Đ&agrave;n quạ tr&ecirc;n những c&acirc;y gạo cao ch&oacute;t v&oacute;t ngo&agrave;i b&atilde;i chợ hốt hoảng bay v&ugrave; l&ecirc;n, lượn th&agrave;nh từng đ&aacute;m bay vần tr&ecirc;n nền trời như những đ&aacute;m m&acirc;y đen.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Người con d&acirc;u khẽ thở d&agrave;i, thị n&oacute;i l&iacute; nh&iacute; trong miệng:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Trống g&igrave; đấy, u nhỉ?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Trống th&uacute;c thuế đấy. Đằng th&igrave; n&oacute; bắt gồng đay, đằng th&igrave; n&oacute; bắt đ&oacute;ng thuế. Giời đất n&agrave;y kh&ocirc;ng chắc đ&atilde; sống qua được đ&acirc;u c&aacute;c con ạ... - B&agrave; l&atilde;o ngoảnh vội ra ngo&agrave;i. B&agrave; l&atilde;o kh&ocirc;ng d&aacute;m để con d&acirc;u nh&igrave;n thấy b&agrave; kh&oacute;c.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Người con d&acirc;u c&oacute; vẻ lạ lắm, thị lầm bầm:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Ở đ&acirc;y vẫn phải đ&oacute;ng thuế cơ &agrave;?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Im lặng một l&uacute;c thị lại tiếp:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Tr&ecirc;n mạn Th&aacute;i Nguy&ecirc;n, Bắc Giang người ta kh&ocirc;ng chịu đ&oacute;ng thuế nữa đ&acirc;u. Người ta c&ograve;n ph&aacute; cả kho th&oacute;c của Nhật, chia cho người đ&oacute;i nữa đấy.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Tr&agrave;ng thần mặt ra nghĩ ngợi. C&aacute;i mặt to lớn bặm lại, kh&oacute; đăm đăm. Miếng c&aacute;m ngậm trong miệng hắn đ&atilde; b&atilde; ra ch&aacute;t x&iacute;t... Hắn đang nghĩ đến những người ph&aacute; kho th&oacute;c Nhật.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Tr&agrave;ng hỏi vội trong miếng ăn:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Việt Minh phải kh&ocirc;ng?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Ừ, sao nh&agrave; biết?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Tr&agrave;ng kh&ocirc;ng trả lời. Trong &yacute; nghĩ của hắn vụt hiện ra cảnh những người ngh&egrave;o đ&oacute;i ầm ầm k&eacute;o nhau đi tr&ecirc;n đ&ecirc; Sộp. Đằng trước c&oacute; l&aacute; cờ đỏ to lắm.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">H&ocirc;m ấy hắn l&aacute;ng m&aacute;ng nghe người ta n&oacute;i họ l&agrave; Việt Minh đấy. Họ đi cướp th&oacute;c đấy. Tr&agrave;ng kh&ocirc;ng hiểu g&igrave; sợ qu&aacute;, k&eacute;o vội xe th&oacute;c của Li&ecirc;n đo&agrave;n tắt c&aacute;nh động đi lối kh&aacute;c. &Agrave; ra họ đi ph&aacute; kho th&oacute;c chia cho người đ&oacute;i. Tự dưng hắn thấy &acirc;n hận, tiếc rẻ vẩn vơ, kh&oacute; hiểu.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Ngo&agrave;i đ&igrave;nh tiếng trống th&uacute;c thuế vẫn dồn dập. Mẹ v&agrave; vợ Tr&agrave;ng đ&atilde; bu&ocirc;ng đũa đứng dậy.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Trong &oacute;c Tr&agrave;ng vẫn thấy đ&aacute;m người đ&oacute;i v&agrave; l&aacute; cờ đỏ bay phấp phới&hellip;</span></span></span></span></p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(2, 'Truyện Kiều', 'Thơ', 'Nguyễn Du', 1814, 'truyen-kieu.jpg', 'Đã hoàn', '<p style=\"text-align:justify\"><strong>Truyện Kiều</strong>&nbsp;xoay quanh cuộc đời v&agrave; số phận của nh&acirc;n vật ch&iacute;nh t&ecirc;n l&agrave; Vương Th&uacute;y Kiều &ndash; một người con g&aacute;i t&agrave;i sắc vẹn to&agrave;n. Th&uacute;y Kiều sinh ra trong một gia đ&igrave;nh trung lưu, c&oacute; em g&aacute;i l&agrave; Th&uacute;y V&acirc;n v&agrave; em trai l&agrave; Vương Quan. Trong tiết Thanh Minh th&aacute;ng ba, Th&uacute;y Kiều du xu&acirc;n gặp Kim Trọng. Họ thề nguyền v&agrave; đ&iacute;nh ước với nhau. Trong khi Kim Trọng phải trở về Li&ecirc;u Dương chịu tang ch&uacute; th&igrave; gia đ&igrave;nh Kiều gặp tai họa do thằng b&aacute;n tơ vu oan. Kiều phải b&aacute;n m&igrave;nh chuộc cha. Trước khi theo M&atilde; Gi&aacute;m Sinh v&agrave; T&uacute; B&agrave; Th&uacute;y Kiều đ&atilde; trao duy&ecirc;n cho Th&uacute;y V&acirc;n. Khi biết m&igrave;nh bị lừa v&agrave; đưa v&agrave;o lầu xanh, Kiều tự tử nhưng kh&ocirc;ng th&agrave;nh, T&uacute; B&agrave; đưa Kiều ra lầu Ngưng B&iacute;ch. Tại đ&oacute;, Kiều bị Sở Khanh lừa v&agrave; n&agrave;ng phải tiếp kh&aacute;ch ở lầu xanh. Kiều được Th&uacute;c Sinh chuộc ra l&agrave;m vợ lẽ nhưng bị Hoạn Thư &ndash; vợ cả của Th&uacute;c Sinh ghen tu&ocirc;ng v&agrave; h&agrave;nh hạ. Kiều bỏ trốn v&agrave; nhờ sư Gi&aacute;c Duy&ecirc;n nương nhờ cửa Phật. Bị Bạc H&agrave;, Bạc Hạnh ph&aacute;t hiện, Kiều lại v&agrave;o lầu xanh lần thứ hai. Tai đ&acirc;y, Kiều được Từ Hải chuộc ra v&agrave; gi&uacute;p n&agrave;ng b&aacute;o &acirc;n b&aacute;o o&aacute;n. V&igrave; bị mắc lừa Hồ T&ocirc;n Hiến, Từ Hải chết đứng, Th&uacute;y Kiều bị &eacute;p gả cho t&ecirc;n Thổ quan. Kiều tự tử ở s&ocirc;ng Tiền Đường nhưng lại được sư Gi&aacute;c Duy&ecirc;n cứu.</p>\r\n', '<p style=\"text-align:center\"><span style=\"color:#000000\"><strong><u>Kiều Thăm Mộ Đạm Ti&ecirc;n (C&acirc;u 1-244)</u></strong></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">1.Trăm năm trong c&otilde;i người ta,</span><br />\r\n<span style=\"color:#000000\">Chữ t&agrave;i chữ mệnh kh&eacute;o l&agrave; gh&eacute;t nhau.</span><br />\r\n<span style=\"color:#000000\">Trải qua một cuộc bể d&acirc;u,</span><br />\r\n<span style=\"color:#000000\">Những điều tr&ocirc;ng thấy m&agrave; đau đớn l&ograve;ng.</span><br />\r\n<span style=\"color:#000000\">5.Lạ g&igrave; bỉ sắc tư phong,</span><br />\r\n<span style=\"color:#000000\">Trời xanh quen th&oacute;i m&aacute; hồng đ&aacute;nh ghen.</span><br />\r\n<span style=\"color:#000000\">Cảo thơm lần giở trước đ&egrave;n,</span><br />\r\n<span style=\"color:#000000\">Phong t&igrave;nh cổ lục c&ograve;n truyền sử xanh.</span><br />\r\n<span style=\"color:#000000\">Rằng năm Gia Tĩnh triều Minh,</span><br />\r\n<span style=\"color:#000000\">10.Bốn phương phẳng lặng, hai kinh vững v&agrave;ng.</span><br />\r\n<span style=\"color:#000000\">C&oacute; nh&agrave; vi&ecirc;n ngoại họ Vương,</span><br />\r\n<span style=\"color:#000000\">Gia tư nghĩ cũng thường thường bực trung.</span><br />\r\n<span style=\"color:#000000\">Một trai con thứ rốt l&ograve;ng,</span><br />\r\n<span style=\"color:#000000\">Vương Quan l&agrave; chữ, nối d&ograve;ng nho gia.</span><br />\r\n<span style=\"color:#000000\">15.Đầu l&ograve;ng hai ả tố nga,</span><br />\r\n<span style=\"color:#000000\">Th&uacute;y Kiều l&agrave; chị, em l&agrave; Th&uacute;y V&acirc;n.</span><br />\r\n<span style=\"color:#000000\">Mai cốt c&aacute;ch, tuyết tinh thần,</span><br />\r\n<span style=\"color:#000000\">Mỗi người một vẻ, mười ph&acirc;n vẹn mười.</span><br />\r\n<span style=\"color:#000000\">V&acirc;n xem trang trọng kh&aacute;c vời,</span><br />\r\n<span style=\"color:#000000\">20.Khu&ocirc;n trăng đầy đặn, n&eacute;t ng&agrave;i nở nang.</span><br />\r\n<span style=\"color:#000000\">Hoa cười ngọc thốt đoan trang,</span><br />\r\n<span style=\"color:#000000\">M&acirc;y thua nước t&oacute;c, tuyết nhường m&agrave;u da.</span><br />\r\n<span style=\"color:#000000\">Kiều c&agrave;ng sắc sảo, mặn m&agrave;,</span><br />\r\n<span style=\"color:#000000\">So bề t&agrave;i, sắc, lại l&agrave; phần hơn.</span><br />\r\n<span style=\"color:#000000\">25.L&agrave;n thu thủy, n&eacute;t xu&acirc;n sơn,</span><br />\r\n<span style=\"color:#000000\">Hoa ghen thua thắm, liễu hờn k&eacute;m xanh.</span><br />\r\n<span style=\"color:#000000\">Một, hai nghi&ecirc;ng nước nghi&ecirc;ng th&agrave;nh,</span><br />\r\n<span style=\"color:#000000\">Sắc đ&agrave;nh đ&ograve;i một, t&agrave;i đ&agrave;nh họa hai.</span><br />\r\n<span style=\"color:#000000\">Th&ocirc;ng minh vốn sẵn tư trời,</span><br />\r\n<span style=\"color:#000000\">30.Pha nghề thi họa, đủ m&ugrave;i ca ng&acirc;m.</span><br />\r\n<span style=\"color:#000000\">Cung thương l&agrave;u bậc ngũ &acirc;m,</span><br />\r\n<span style=\"color:#000000\">Nghề ri&ecirc;ng ăn đứt Hồ cầm một trương.</span><br />\r\n<span style=\"color:#000000\">Kh&uacute;c nh&agrave; tay lựa n&ecirc;n chương,</span><br />\r\n<span style=\"color:#000000\">Một thi&ecirc;n bạc mệnh, lại c&agrave;ng n&atilde;o nh&acirc;n.</span><br />\r\n<span style=\"color:#000000\">35.Phong lưu rất mực hồng quần,</span><br />\r\n<span style=\"color:#000000\">Xu&acirc;n xanh sấp xỉ tới tuần cập k&ecirc;</span><br />\r\n<span style=\"color:#000000\">&Ecirc;m đềm trướng rủ m&agrave;n che,</span><br />\r\n<span style=\"color:#000000\">Tường đ&ocirc;ng ong bướm đi về mặc ai.</span><br />\r\n<span style=\"color:#000000\">Ng&agrave;y xu&acirc;n con &eacute;n đưa thoi,</span><br />\r\n<span style=\"color:#000000\">40.Thiều quang ch&iacute;n chục đ&atilde; ngo&agrave;i s&aacute;u mươi.</span><br />\r\n<span style=\"color:#000000\">Cỏ non xanh tận ch&acirc;n trời,</span><br />\r\n<span style=\"color:#000000\">C&agrave;nh l&ecirc; trắng điểm một v&agrave;i b&ocirc;ng hoa.</span><br />\r\n<span style=\"color:#000000\">Thanh minh trong tiết th&aacute;ng ba,</span><br />\r\n<span style=\"color:#000000\">Lễ l&agrave; tảo mộ, hội l&agrave; đạp Thanh.</span><br />\r\n<span style=\"color:#000000\">45.Gần xa n&ocirc; nức yến anh,</span><br />\r\n<span style=\"color:#000000\">Chị em sắm sửa bộ h&agrave;nh chơi xu&acirc;n.</span><br />\r\n<span style=\"color:#000000\">Dập d&igrave;u t&agrave;i tử, giai nh&acirc;n,</span><br />\r\n<span style=\"color:#000000\">Ngựa xe như nước &aacute;o quần như n&ecirc;m.</span><br />\r\n<span style=\"color:#000000\">Ngổn ngang g&ograve; đống k&eacute;o l&ecirc;n,</span><br />\r\n<span style=\"color:#000000\">50.Thoi v&agrave;ng v&oacute; rắc tro tiền giấy bay.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&agrave; t&agrave; b&oacute;ng ngả về t&acirc;y,</span><br />\r\n<span style=\"color:#000000\">Chị em thơ thẩn dan tay ra về.</span><br />\r\n<span style=\"color:#000000\">Bước dần theo ngọn tiểu kh&ecirc;,</span><br />\r\n<span style=\"color:#000000\">Lần xem phong cảnh c&oacute; bề thanh thanh.</span><br />\r\n<span style=\"color:#000000\">55.Nao nao d&ograve;ng nước uốn quanh,</span><br />\r\n<span style=\"color:#000000\">Dịp cầu nho nhỏ cuối ghềnh bắc ngang.</span><br />\r\n<span style=\"color:#000000\">S&egrave; s&egrave; nấm đất b&ecirc;n đ&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">D&agrave;u d&agrave;u ngọn cỏ nửa v&agrave;ng nửa xanh.</span><br />\r\n<span style=\"color:#000000\">Rằng: Sao trong tiết thanh minh,</span><br />\r\n<span style=\"color:#000000\">60.M&agrave; đ&acirc;y hương kh&oacute;i vắng tanh thế m&agrave;?</span><br />\r\n<span style=\"color:#000000\">Vương Quan mới dẫn gần xa:</span><br />\r\n<span style=\"color:#000000\">Đạm Ti&ecirc;n n&agrave;ng ấy xưa l&agrave; ca nhi.</span><br />\r\n<span style=\"color:#000000\">Nổi danh t&agrave;i sắc một th&igrave;,</span><br />\r\n<span style=\"color:#000000\">X&ocirc;n xao ngo&agrave;i cửa hiếm g&igrave; yến anh.</span><br />\r\n<span style=\"color:#000000\">65.Kiếp hồng nhan c&oacute; mong manh,</span><br />\r\n<span style=\"color:#000000\">Nửa chừng xu&acirc;n thoắt g&atilde;y c&agrave;nh thi&ecirc;n hương.</span><br />\r\n<span style=\"color:#000000\">C&oacute; người kh&aacute;ch ở viễn phương,</span><br />\r\n<span style=\"color:#000000\">Xa nghe cũng nức tiếng n&agrave;ng t&igrave;m chơi.</span><br />\r\n<span style=\"color:#000000\">Thuyền t&igrave;nh vừa gh&eacute; tới nơi,</span><br />\r\n<span style=\"color:#000000\">70.Th&igrave; đ&agrave; tr&acirc;m gẫy b&igrave;nh rơi bao giờ.</span><br />\r\n<span style=\"color:#000000\">Buồng kh&ocirc;ng lạnh ngắt như tờ,</span><br />\r\n<span style=\"color:#000000\">Dấu xe ngựa đ&atilde; r&ecirc;u lờ mờ xanh.</span><br />\r\n<span style=\"color:#000000\">Kh&oacute;c than kh&ocirc;n xiết sự t&igrave;nh,</span><br />\r\n<span style=\"color:#000000\">Kh&eacute;o v&ocirc; duy&ecirc;n ấy l&agrave; m&igrave;nh với ta.</span><br />\r\n<span style=\"color:#000000\">75.Đ&atilde; kh&ocirc;ng duy&ecirc;n trước chăng m&agrave;,</span><br />\r\n<span style=\"color:#000000\">Th&igrave; chi ch&uacute;t ước gọi l&agrave; duy&ecirc;n sau.</span><br />\r\n<span style=\"color:#000000\">Sắm xanh nếp tử xe ch&acirc;u,</span><br />\r\n<span style=\"color:#000000\">V&ugrave;i n&ocirc;ng một nấm mặc dầu cỏ hoa.</span><br />\r\n<span style=\"color:#000000\">Trải bao thỏ lặn &aacute;c t&agrave;,</span><br />\r\n<span style=\"color:#000000\">80.Ấy mồ v&ocirc; chủ, ai m&agrave; viếng thăm!</span><br />\r\n<span style=\"color:#000000\">L&ograve;ng đ&acirc;u sẵn mối thương t&acirc;m,</span><br />\r\n<span style=\"color:#000000\">Thoắt nghe Kiều đ&atilde; đầm đầm ch&acirc;u sa.</span><br />\r\n<span style=\"color:#000000\">Đau đớn thay phận đ&agrave;n b&agrave;!</span><br />\r\n<span style=\"color:#000000\">Lời rằng bạc mệnh cũng l&agrave; lời chung.</span><br />\r\n<span style=\"color:#000000\">85.Phũ ph&agrave;ng chi bấy ho&aacute; c&ocirc;ng,</span><br />\r\n<span style=\"color:#000000\">Ng&agrave;y xanh m&ograve;n mỏi m&aacute; hồng ph&ocirc;i pha.</span><br />\r\n<span style=\"color:#000000\">Sống l&agrave;m vợ khắp người ta,</span><br />\r\n<span style=\"color:#000000\">Kh&eacute;o thay th&aacute;c xuống l&agrave;m ma kh&ocirc;ng chồng.</span><br />\r\n<span style=\"color:#000000\">N&agrave;o người phượng chạ loan chung,</span><br />\r\n<span style=\"color:#000000\">90.N&agrave;o người t&iacute;ch lục tham hồng l&agrave; ai?</span><br />\r\n<span style=\"color:#000000\">Đ&atilde; kh&ocirc;ng kẻ đo&aacute;i người ho&agrave;i,</span><br />\r\n<span style=\"color:#000000\">Sẵn đ&acirc;y ta kiếm một v&agrave;i n&eacute;n hương.</span><br />\r\n<span style=\"color:#000000\">Gọi l&agrave; gặp gỡ giữa đường,</span><br />\r\n<span style=\"color:#000000\">Họa l&agrave; người dưới suối v&agrave;ng biết cho.</span><br />\r\n<span style=\"color:#000000\">95.Lầm rầm khấn khứa nhỏ to,</span><br />\r\n<span style=\"color:#000000\">Sụp ngồi v&agrave;i gật trước mồ bước ra.</span><br />\r\n<span style=\"color:#000000\">Một v&ugrave;ng cỏ &aacute;y b&oacute;ng t&agrave;,</span><br />\r\n<span style=\"color:#000000\">Gi&oacute; hiu hiu thổi một v&agrave;i b&ocirc;ng lau.</span><br />\r\n<span style=\"color:#000000\">R&uacute;t tr&acirc;m sẵn giắt m&aacute;i đầu,</span><br />\r\n<span style=\"color:#000000\">100.Vạch da c&acirc;y vịnh bốn c&acirc;u ba vần.</span><br />\r\n<span style=\"color:#000000\">Lại c&agrave;ng m&ecirc; mẩn t&acirc;m thần</span><br />\r\n<span style=\"color:#000000\">Lại c&agrave;ng đứng lặng tần ngần chẳng ra.</span><br />\r\n<span style=\"color:#000000\">Lại c&agrave;ng ủ dột n&eacute;t hoa,</span><br />\r\n<span style=\"color:#000000\">Sầu tu&ocirc;n đứt nối, ch&acirc;u sa vắn d&agrave;i.</span><br />\r\n<span style=\"color:#000000\">105.V&acirc;n rằng: Chị cũng nực cười,</span><br />\r\n<span style=\"color:#000000\">Kh&eacute;o dư nước mắt kh&oacute;c người đời xưa.</span><br />\r\n<span style=\"color:#000000\">Rằng: Hồng nhan tự thuở xưa,</span><br />\r\n<span style=\"color:#000000\">C&aacute;i điều bạc mệnh c&oacute; chừa ai đ&acirc;u?</span><br />\r\n<span style=\"color:#000000\">Nỗi niềm tưởng đến m&agrave; đau,</span><br />\r\n<span style=\"color:#000000\">110.Thấy người nằm đ&oacute; biết sau thế n&agrave;o?</span><br />\r\n<span style=\"color:#000000\">Quan rằng: Chị n&oacute;i hay sao,</span><br />\r\n<span style=\"color:#000000\">Một lời l&agrave; một vận v&agrave;o kh&oacute; nghe.</span><br />\r\n<span style=\"color:#000000\">Ở đ&acirc;y &acirc;m kh&iacute; nặng nề,</span><br />\r\n<span style=\"color:#000000\">B&oacute;ng chiều đ&atilde; ngả dậm về c&ograve;n xa.</span><br />\r\n<span style=\"color:#000000\">115.Kiều rằng: Những đấng t&agrave;i hoa,</span><br />\r\n<span style=\"color:#000000\">Th&aacute;c l&agrave; thể ph&aacute;ch, c&ograve;n l&agrave; tinh anh,</span><br />\r\n<span style=\"color:#000000\">Dễ hay t&igrave;nh lại gặp t&igrave;nh,</span><br />\r\n<span style=\"color:#000000\">Chờ xem ắt thấy hiển linh b&acirc;y giờ.</span><br />\r\n<span style=\"color:#000000\">Một lời n&oacute;i chửa kịp thưa,</span><br />\r\n<span style=\"color:#000000\">120.Ph&uacute;t đ&acirc;u trận gi&oacute; cuốn cờ đến ngay.</span><br />\r\n<span style=\"color:#000000\">&Agrave;o &agrave;o đổ lộc rung c&acirc;y,</span><br />\r\n<span style=\"color:#000000\">Ở trong dường c&oacute; hương bay &iacute;t nhiều.</span><br />\r\n<span style=\"color:#000000\">Đ&egrave; chừng ngọn gi&oacute; lần theo,</span><br />\r\n<span style=\"color:#000000\">Dấu gi&agrave;y từng bước in r&ecirc;u r&agrave;nh r&agrave;nh.</span><br />\r\n<span style=\"color:#000000\">125.Mắt nh&igrave;n ai nấy đều kinh,</span><br />\r\n<span style=\"color:#000000\">N&agrave;ng rằng: N&agrave;y thực tinh th&agrave;nh chẳng xa.</span><br />\r\n<span style=\"color:#000000\">Hữu t&igrave;nh ta lại gặp ta,</span><br />\r\n<span style=\"color:#000000\">Chớ nề u hiển mới l&agrave; chị em.</span><br />\r\n<span style=\"color:#000000\">Đ&atilde; l&ograve;ng hiển hiện cho xem,</span><br />\r\n<span style=\"color:#000000\">Tạ l&ograve;ng n&agrave;ng lại nối th&ecirc;m v&agrave;i lời.</span><br />\r\n<span style=\"color:#000000\">130.L&ograve;ng thơ lai l&aacute;ng bồi hồi,</span><br />\r\n<span style=\"color:#000000\">Gốc c&acirc;y lại vạch một b&agrave;i cổ thi.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">D&ugrave;ng dằng nửa ở nửa về,</span><br />\r\n<span style=\"color:#000000\">Nhạc v&agrave;ng đ&acirc;u đ&atilde; tiếng nghe gần gần.</span><br />\r\n<span style=\"color:#000000\">135.Tr&ocirc;ng chừng thấy một văn nh&acirc;n,</span><br />\r\n<span style=\"color:#000000\">Lỏng bu&ocirc;ng tay khấu bước lần dặm băng.</span><br />\r\n<span style=\"color:#000000\">Đề huề lưng t&uacute;i gi&oacute; trăng,</span><br />\r\n<span style=\"color:#000000\">Sau ch&acirc;n theo một v&agrave;i thằng con con.</span><br />\r\n<span style=\"color:#000000\">Tuyết in sắc ngựa c&acirc;u gi&ograve;n,</span><br />\r\n<span style=\"color:#000000\">140.Cỏ pha m&agrave;u &aacute;o nhuộm non da trời.</span><br />\r\n<span style=\"color:#000000\">Nẻo xa mới tỏ mặt người,</span><br />\r\n<span style=\"color:#000000\">Kh&aacute;ch đ&agrave; xuống ngựa tới nơi tự t&igrave;nh.</span><br />\r\n<span style=\"color:#000000\">H&agrave;i văn lần bước dặm xanh,</span><br />\r\n<span style=\"color:#000000\">Một v&ugrave;ng như thể c&acirc;y quỳnh c&agrave;nh dao.</span><br />\r\n<span style=\"color:#000000\">145.Ch&agrave;ng Vương quen mặt ra ch&agrave;o,</span><br />\r\n<span style=\"color:#000000\">Hai Kiều e lệ n&eacute;p v&agrave;o dưới hoa.</span><br />\r\n<span style=\"color:#000000\">Nguy&ecirc;n người quanh quất đ&acirc;u xa,</span><br />\r\n<span style=\"color:#000000\">Họ Kim t&ecirc;n Trọng vốn nh&agrave; tr&acirc;m anh.</span><br />\r\n<span style=\"color:#000000\">Nền ph&uacute; hậu, bậc t&agrave;i danh,</span><br />\r\n<span style=\"color:#000000\">150.Văn chương nết đất, th&ocirc;ng minh t&iacute;nh trời.</span><br />\r\n<span style=\"color:#000000\">Phong tư t&agrave;i mạo t&oacute;t vời,</span><br />\r\n<span style=\"color:#000000\">V&agrave;o trong phong nh&atilde;, ra ngo&agrave;i h&agrave;o hoa.</span><br />\r\n<span style=\"color:#000000\">Chung quanh vẫn đất nước nh&agrave;,</span><br />\r\n<span style=\"color:#000000\">Với Vương Quan trước vẫn l&agrave; đồng th&acirc;n.</span><br />\r\n<span style=\"color:#000000\">155.Vẫn nghe thơm nức hương l&acirc;n,</span><br />\r\n<span style=\"color:#000000\">Một nền đồng Tước kho&aacute; xu&acirc;n hai Kiều.</span><br />\r\n<span style=\"color:#000000\">Nước non c&aacute;ch mấy buồng th&ecirc;u,</span><br />\r\n<span style=\"color:#000000\">Những l&agrave; trộm nhớ thầm y&ecirc;u chốc m&ograve;ng.</span><br />\r\n<span style=\"color:#000000\">May thay giải cấu tương ph&ugrave;ng,</span><br />\r\n<span style=\"color:#000000\">160.Gặp tuần đố l&aacute; thoả l&ograve;ng t&igrave;m hoa.</span><br />\r\n<span style=\"color:#000000\">B&oacute;ng hồng nh&aacute;c thấy nẻo xa,</span><br />\r\n<span style=\"color:#000000\">Xu&acirc;n lan thu c&uacute;c mặn m&agrave; cả hai.</span><br />\r\n<span style=\"color:#000000\">Người quốc sắc, kẻ thi&ecirc;n t&agrave;i,</span><br />\r\n<span style=\"color:#000000\">T&igrave;nh trong như đ&atilde;, mặt ngo&agrave;i c&ograve;n e.</span><br />\r\n<span style=\"color:#000000\">165.Chập chờn cơn tỉnh cơn m&ecirc;.</span><br />\r\n<span style=\"color:#000000\">Rốn ngồi chẳng tiện, dứt về chỉn kh&ocirc;n.</span><br />\r\n<span style=\"color:#000000\">B&oacute;ng t&agrave; như giục cơn buồn,</span><br />\r\n<span style=\"color:#000000\">Kh&aacute;ch đ&agrave; l&ecirc;n ngựa, người c&ograve;n ngh&eacute; theo.</span><br />\r\n<span style=\"color:#000000\">Dưới cầu nước chảy trong veo,</span><br />\r\n<span style=\"color:#000000\">170.B&ecirc;n cầu tơ liễu b&oacute;ng chiều thướt tha.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Kiều từ trở g&oacute;t trướng hoa,</span><br />\r\n<span style=\"color:#000000\">Mặt trời g&aacute;c n&uacute;i chi&ecirc;ng đ&agrave; thu kh&ocirc;ng.</span><br />\r\n<span style=\"color:#000000\">Mảnh trăng ch&ecirc;nh chếch d&ograve;m song,</span><br />\r\n<span style=\"color:#000000\">V&agrave;ng gieo ngấn nước, c&acirc;y lồng b&oacute;ng s&acirc;n.</span><br />\r\n<span style=\"color:#000000\">175.Hải đường lả ngọn đ&ocirc;ng l&acirc;n,</span><br />\r\n<span style=\"color:#000000\">Giọt sương gieo nặng c&agrave;nh xu&acirc;n la đ&agrave;.</span><br />\r\n<span style=\"color:#000000\">Một m&igrave;nh lặng ngắm b&oacute;ng nga,</span><br />\r\n<span style=\"color:#000000\">Rộn đường gần với nỗi xa bời bời:</span><br />\r\n<span style=\"color:#000000\">Người m&agrave; đến thế th&igrave; th&ocirc;i,</span><br />\r\n<span style=\"color:#000000\">180.Đời phồn hoa cũng l&agrave; đời bỏ đi!</span><br />\r\n<span style=\"color:#000000\">Người đ&acirc;u gặp gỡ l&agrave;m chi,</span><br />\r\n<span style=\"color:#000000\">Trăm năm biết c&oacute; duy&ecirc;n g&igrave; hay kh&ocirc;ng?</span><br />\r\n<span style=\"color:#000000\">Ngổn ngang trăm mối b&ecirc;n l&ograve;ng,</span><br />\r\n<span style=\"color:#000000\">N&ecirc;n c&acirc;u tuyệt diệu ngụ trong t&iacute;nh t&igrave;nh.</span><br />\r\n<span style=\"color:#000000\">185.Ch&ecirc;nh ch&ecirc;nh b&oacute;ng nguyệt xế m&agrave;nh,</span><br />\r\n<span style=\"color:#000000\">Tựa nương b&ecirc;n triện một m&igrave;nh thiu thiu.</span><br />\r\n<span style=\"color:#000000\">Thoắt đ&acirc;u thấy một tiểu kiều,</span><br />\r\n<span style=\"color:#000000\">C&oacute; chiều thanh vận, c&oacute; chiều thanh t&acirc;n.</span><br />\r\n<span style=\"color:#000000\">Sương in mặt, tuyết pha th&acirc;n,</span><br />\r\n<span style=\"color:#000000\">190.Sen v&agrave;ng l&atilde;ng đ&atilde;ng như gần như xa.</span><br />\r\n<span style=\"color:#000000\">Ch&agrave;o mừng đ&oacute;n hỏi d&ograve; la:</span><br />\r\n<span style=\"color:#000000\">Đ&agrave;o nguy&ecirc;n lạc lối đ&acirc;u m&agrave; đến đ&acirc;y?</span><br />\r\n<span style=\"color:#000000\">Thưa rằng: Thanh kh&iacute; xưa nay,</span><br />\r\n<span style=\"color:#000000\">Mới c&ugrave;ng nhau l&uacute;c ban ng&agrave;y đ&atilde; qu&ecirc;n.</span><br />\r\n<span style=\"color:#000000\">195.H&agrave;n gia ở m&eacute; t&acirc;y thi&ecirc;n,</span><br />\r\n<span style=\"color:#000000\">Dưới d&ograve;ng nước chảy b&ecirc;n tr&ecirc;n c&oacute; cầu.</span><br />\r\n<span style=\"color:#000000\">Mấy l&ograve;ng hạ cố đến nhau,</span><br />\r\n<span style=\"color:#000000\">Mấy lời hạ tứ n&eacute;m ch&acirc;u gieo v&agrave;ng.</span><br />\r\n<span style=\"color:#000000\">V&acirc;ng tr&igrave;nh hội chủ xem tường,</span><br />\r\n<span style=\"color:#000000\">200.M&agrave; sao trong sổ đoạn trường c&oacute; t&ecirc;n.</span><br />\r\n<span style=\"color:#000000\">&Acirc;u đ&agrave;nh quả kiếp nh&acirc;n duy&ecirc;n,</span><br />\r\n<span style=\"color:#000000\">C&ugrave;ng người một hội, một thuyền đ&acirc;u xa!</span><br />\r\n<span style=\"color:#000000\">N&agrave;y mười b&agrave;i mới mới ra,</span><br />\r\n<span style=\"color:#000000\">C&acirc;u thần lại mượn b&uacute;t hoa vẽ vời.</span><br />\r\n<span style=\"color:#000000\">205.Kiều v&acirc;ng lĩnh &yacute; đề b&agrave;i,</span><br />\r\n<span style=\"color:#000000\">Tay ti&ecirc;n một vẫy đủ mười kh&uacute;c ng&acirc;m.</span><br />\r\n<span style=\"color:#000000\">Xem thơ nức nở khen thầm:</span><br />\r\n<span style=\"color:#000000\">Gi&aacute; đ&agrave;nh t&uacute; khẩu cẩm t&acirc;m kh&aacute;c thường</span><br />\r\n<span style=\"color:#000000\">V&iacute; đem v&agrave;o tập Đoạn Trường</span><br />\r\n<span style=\"color:#000000\">210.Th&igrave; treo giải nhất chi nhường cho ai.</span><br />\r\n<span style=\"color:#000000\">Thềm hoa kh&aacute;ch đ&atilde; trở h&agrave;i,</span><br />\r\n<span style=\"color:#000000\">N&agrave;ng c&ograve;n cầm lại một hai tự t&igrave;nh.</span><br />\r\n<span style=\"color:#000000\">Gi&oacute; đ&acirc;u xịch bức m&agrave;nh m&agrave;nh,</span><br />\r\n<span style=\"color:#000000\">Tỉnh ra mới biết rằng m&igrave;nh chi&ecirc;m bao.</span><br />\r\n<span style=\"color:#000000\">215.Tr&ocirc;ng theo n&agrave;o thấy đ&acirc;u n&agrave;o</span><br />\r\n<span style=\"color:#000000\">Hương thừa dường h&atilde;y ra v&agrave;o đ&acirc;u đ&acirc;y.</span><br />\r\n<span style=\"color:#000000\">Một m&igrave;nh lưỡng lự canh chầy,</span><br />\r\n<span style=\"color:#000000\">Đường xa nghĩ nỗi sau n&agrave;y m&agrave; kinh.</span><br />\r\n<span style=\"color:#000000\">Hoa tr&ocirc;i b&egrave;o dạt đ&atilde; đ&agrave;nh,</span><br />\r\n<span style=\"color:#000000\">220.Biết duy&ecirc;n m&igrave;nh, biết phận m&igrave;nh thế th&ocirc;i!</span><br />\r\n<span style=\"color:#000000\">Nỗi ri&ecirc;ng lớp lớp s&oacute;ng dồi,</span><br />\r\n<span style=\"color:#000000\">Nghĩ đ&ograve;i cơn lại sụt s&ugrave;i đ&ograve;i cơn.</span><br />\r\n<span style=\"color:#000000\">Giọng Kiều rền rĩ trướng loan,</span><br />\r\n<span style=\"color:#000000\">Nh&agrave; Huy&ecirc;n chợt tỉnh hỏi: Cơn cớ g&igrave;?</span><br />\r\n<span style=\"color:#000000\">225.Cớ sao trằn trọc canh khuya,</span><br />\r\n<span style=\"color:#000000\">M&agrave;u hoa l&ecirc; h&atilde;y dầm dề giọt mưa?</span><br />\r\n<span style=\"color:#000000\">Thưa rằng: Ch&uacute;t phận ng&acirc;y thơ,</span><br />\r\n<span style=\"color:#000000\">Dưỡng sinh đ&ocirc;i nợ t&oacute;c tơ chưa đền.</span><br />\r\n<span style=\"color:#000000\">Buổi ng&agrave;y chơi mả Đạm Ti&ecirc;n,</span><br />\r\n<span style=\"color:#000000\">230.Nhắp đi thoắt thấy ứng liền chi&ecirc;m bao.</span><br />\r\n<span style=\"color:#000000\">Đoạn trường l&agrave; số thế n&agrave;o,</span><br />\r\n<span style=\"color:#000000\">B&agrave;i ra thế ấy, vịnh v&agrave;o thế kia.</span><br />\r\n<span style=\"color:#000000\">Cứ trong mộng triệu m&agrave; suy,</span><br />\r\n<span style=\"color:#000000\">Phận con th&ocirc;i c&oacute; ra g&igrave; mai sau!</span><br />\r\n<span style=\"color:#000000\">235.Dạy rằng: Mộng triệu cớ đ&acirc;u,</span><br />\r\n<span style=\"color:#000000\">Bỗng kh&ocirc;ng mua n&atilde;o chuốc sầu nghĩ nao.</span><br />\r\n<span style=\"color:#000000\">V&acirc;ng lời khuy&ecirc;n giải thấp cao,</span><br />\r\n<span style=\"color:#000000\">Chưa xong điều nghĩ đ&atilde; d&agrave;o mạch Tương.</span><br />\r\n<span style=\"color:#000000\">Ngo&agrave;i song thỏ thẻ oanh v&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">240.N&aacute;ch tường b&ocirc;ng liễu bay ngang trước m&agrave;nh.</span><br />\r\n<span style=\"color:#000000\">Hi&ecirc;n t&agrave; g&aacute;c b&oacute;ng ch&ecirc;nh ch&ecirc;nh,</span><br />\r\n<span style=\"color:#000000\">Nỗi ri&ecirc;ng, ri&ecirc;ng trạnh tấc ri&ecirc;ng một m&igrave;nh.</span><br />\r\n<span style=\"color:#000000\">Cho hay l&agrave; th&oacute;i hữu t&igrave;nh,</span><br />\r\n<span style=\"color:#000000\">Đố ai gỡ mối tơ m&agrave;nh cho xong.</span></span></span></p>\r\n\r\n<h3 style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\"><strong><u>Kiều Gặp Kim Trọng (C&acirc;u 245-572)</u></strong></span></span></span></h3>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">245.Ch&agrave;ng Kim từ lại thư song,</span><br />\r\n<span style=\"color:#000000\">Nỗi n&agrave;ng canh c&aacute;nh b&ecirc;n l&ograve;ng biếng khu&acirc;y.</span><br />\r\n<span style=\"color:#000000\">Sầu đong c&agrave;ng lắc c&agrave;ng đầy,</span><br />\r\n<span style=\"color:#000000\">Ba thu dồn lại một ng&agrave;y d&agrave;i gh&ecirc;.</span><br />\r\n<span style=\"color:#000000\">M&acirc;y Tần kh&oacute;a k&iacute;n song the,</span><br />\r\n<span style=\"color:#000000\">250.Bụi hồng lẽo đẽo đi về chi&ecirc;m bao.</span><br />\r\n<span style=\"color:#000000\">Tuần trăng khuyết, đĩa dầu hao,</span><br />\r\n<span style=\"color:#000000\">Mặt mơ tưởng mặt, l&ograve;ng ngao ng&aacute;n l&ograve;ng.</span><br />\r\n<span style=\"color:#000000\">Buồng văn hơi gi&aacute; như đồng,</span><br />\r\n<span style=\"color:#000000\">Tr&uacute;c se ngọn thỏ, tơ ch&ugrave;ng ph&iacute;m loan.</span><br />\r\n<span style=\"color:#000000\">255.M&agrave;nh Tương phất phất gi&oacute; đ&agrave;n,</span><br />\r\n<span style=\"color:#000000\">Hương g&acirc;y m&ugrave;i nhớ, tr&agrave; khan giọng t&igrave;nh.</span><br />\r\n<span style=\"color:#000000\">V&igrave; chăng duy&ecirc;n nợ ba sinh,</span><br />\r\n<span style=\"color:#000000\">Th&igrave; chi đem th&oacute;i khuynh th&agrave;nh tr&ecirc;u ngươi.</span><br />\r\n<span style=\"color:#000000\">B&acirc;ng khu&acirc;ng nhớ cảnh, nhớ người,</span><br />\r\n<span style=\"color:#000000\">260.Nhớ nơi kỳ ngộ vội dời ch&acirc;n đi.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Một v&ugrave;ng cỏ mọc xanh r&igrave;,</span><br />\r\n<span style=\"color:#000000\">Nước ng&acirc;m trong vắt, thấy g&igrave; nữa đ&acirc;u!</span><br />\r\n<span style=\"color:#000000\">Gi&oacute; chiều như gợi cơn sầu,</span><br />\r\n<span style=\"color:#000000\">Vi l&ocirc; hiu hắt như m&agrave;u khảy tr&ecirc;u.</span><br />\r\n<span style=\"color:#000000\">265.Nghề ri&ecirc;ng nhớ &iacute;t tưởng nhiều,</span><br />\r\n<span style=\"color:#000000\">Xăm xăm đ&egrave; nẻo Lam Kiều lần sang.</span><br />\r\n<span style=\"color:#000000\">Th&acirc;m nghi&ecirc;m k&iacute;n cổng cao tường,</span><br />\r\n<span style=\"color:#000000\">Cạn d&ograve;ng l&aacute; thắm dứt đường chim xanh.</span><br />\r\n<span style=\"color:#000000\">Lơ thơ tơ liễu bu&ocirc;ng m&agrave;nh,</span><br />\r\n<span style=\"color:#000000\">270.Con oanh học n&oacute;i tr&ecirc;n c&agrave;nh mỉa mai.</span><br />\r\n<span style=\"color:#000000\">Mấy lần cửa đ&oacute;ng then c&agrave;i,</span><br />\r\n<span style=\"color:#000000\">Đầy thềm hoa rụng, biết người ở đ&acirc;u?</span><br />\r\n<span style=\"color:#000000\">Tần ngần đứng suốt giờ l&acirc;u,</span><br />\r\n<span style=\"color:#000000\">Dạo quanh chợt thấy m&aacute;i sau c&oacute; nh&agrave;.</span><br />\r\n<span style=\"color:#000000\">275.L&agrave; nh&agrave; Ng&ocirc; Việt thương gia,</span><br />\r\n<span style=\"color:#000000\">Buồng kh&ocirc;ng để đ&oacute; người xa chưa về.</span><br />\r\n<span style=\"color:#000000\">Lấy điều du học hỏi thu&ecirc;,</span><br />\r\n<span style=\"color:#000000\">T&uacute;i đ&agrave;n cặp s&aacute;ch đề huề dọn sang.</span><br />\r\n<span style=\"color:#000000\">C&oacute; c&acirc;y, c&oacute; đ&aacute; sẵn s&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">280.C&oacute; hi&ecirc;n L&atilde;m th&uacute;y, n&eacute;t v&agrave;ng chưa phai.</span><br />\r\n<span style=\"color:#000000\">Mừng thầm chốn ấy chữ b&agrave;i,</span><br />\r\n<span style=\"color:#000000\">Ba sinh &acirc;u hẳn duy&ecirc;n trời chi đ&acirc;y.</span><br />\r\n<span style=\"color:#000000\">Song hồ nửa kh&eacute;p c&aacute;nh m&acirc;y,</span><br />\r\n<span style=\"color:#000000\">Tường đ&ocirc;ng gh&eacute; mắt ng&agrave;y ng&agrave;y hằng tr&ocirc;ng.</span><br />\r\n<span style=\"color:#000000\">285.Tấc gang đồng tỏa nguy&ecirc;n phong,</span><br />\r\n<span style=\"color:#000000\">Tuyệt m&ugrave; n&agrave;o thấy b&oacute;ng hồng v&agrave;o ra.</span><br />\r\n<span style=\"color:#000000\">Nhẫn từ qu&aacute;n kh&aacute;ch l&acirc;n la,</span><br />\r\n<span style=\"color:#000000\">Tuần trăng thấm thoắt nay đ&agrave; th&egrave;m hai.</span><br />\r\n<span style=\"color:#000000\">C&aacute;ch tường phải buổi &ecirc;m trời,</span><br />\r\n<span style=\"color:#000000\">290.Dưới đ&agrave;o dường c&oacute; b&oacute;ng người thướt tha.</span><br />\r\n<span style=\"color:#000000\">Bu&ocirc;ng cầm xốc &aacute;o vội ra,</span><br />\r\n<span style=\"color:#000000\">Hương c&ograve;n thơm nức, người đ&agrave; vắng tanh.</span><br />\r\n<span style=\"color:#000000\">Lần theo tường gấm dạo quanh,</span><br />\r\n<span style=\"color:#000000\">Tr&ecirc;n đ&agrave;o nh&aacute;c thấy một c&agrave;nh kim thoa.</span><br />\r\n<span style=\"color:#000000\">295.Giơ tay với lấy về nh&agrave;:</span><br />\r\n<span style=\"color:#000000\">N&agrave;y trong khu&ecirc; c&aacute;c đ&acirc;u m&agrave; đến đ&acirc;y?</span><br />\r\n<span style=\"color:#000000\">Ngẫm &acirc;u người ấy b&aacute;u n&agrave;y,</span><br />\r\n<span style=\"color:#000000\">Chẳng duy&ecirc;n chưa dễ v&agrave;o tay ai cầm!</span><br />\r\n<span style=\"color:#000000\">Liền tay ngắm ngh&iacute;a biếng nằm,</span><br />\r\n<span style=\"color:#000000\">300.H&atilde;y c&ograve;n thoang thoảng hương trầm chưa phai.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Tan sương đ&atilde; thấy b&oacute;ng người,</span><br />\r\n<span style=\"color:#000000\">Quanh tường ra &yacute; t&igrave;m t&ograve;i ngẩn ngơ.</span><br />\r\n<span style=\"color:#000000\">Sinh đ&agrave; c&oacute; &yacute; đợi chờ,</span><br />\r\n<span style=\"color:#000000\">C&aacute;ch tường l&ecirc;n tiếng xa đưa ướm l&ograve;ng:</span><br />\r\n<span style=\"color:#000000\">305.Thoa n&agrave;y bắt được hư kh&ocirc;ng,</span><br />\r\n<span style=\"color:#000000\">Biết đ&acirc;u Hợp Phố m&agrave; mong ch&acirc;u về?</span><br />\r\n<span style=\"color:#000000\">Tiếng Kiều nghe lọt b&ecirc;n kia:</span><br />\r\n<span style=\"color:#000000\">Ơn l&ograve;ng qu&acirc;n tử s&aacute; g&igrave; của rơi.</span><br />\r\n<span style=\"color:#000000\">Chiếc thoa n&agrave;o của mấy mươi,</span><br />\r\n<span style=\"color:#000000\">310.M&agrave; l&ograve;ng trọng nghĩa khinh t&agrave;i xiết bao!</span><br />\r\n<span style=\"color:#000000\">Sinh rằng: L&acirc;n l&yacute; ra v&agrave;o,</span><br />\r\n<span style=\"color:#000000\">Gần đ&acirc;y n&agrave;o phải người n&agrave;o xa x&ocirc;i.</span><br />\r\n<span style=\"color:#000000\">Được r&agrave;y nhờ ch&uacute;t thơm rơi,</span><br />\r\n<span style=\"color:#000000\">Kể đ&agrave; thiểu n&atilde;o l&ograve;ng người bấy nay!</span><br />\r\n<span style=\"color:#000000\">315.Bấy l&acirc;u mới được một ng&agrave;y,</span><br />\r\n<span style=\"color:#000000\">Dừng ch&acirc;n gạn ch&uacute;t niềm t&acirc;y gọi l&agrave;.</span><br />\r\n<span style=\"color:#000000\">Vội về th&ecirc;m lấy của nh&agrave;,</span><br />\r\n<span style=\"color:#000000\">Xuyến v&agrave;ng đ&ocirc;i chiếc khăn l&agrave; một vu&ocirc;ng.</span><br />\r\n<span style=\"color:#000000\">Bậc m&acirc;y r&oacute;n bước ngọn tường,</span><br />\r\n<span style=\"color:#000000\">320.Phải người h&ocirc;m nọ r&otilde; r&agrave;ng chẳng nhe?</span><br />\r\n<span style=\"color:#000000\">Sượng s&ugrave;ng giữ &yacute; rụt r&egrave;,</span><br />\r\n<span style=\"color:#000000\">Kẻ nh&igrave;n r&otilde; mặt người e c&uacute;i đầu.</span><br />\r\n<span style=\"color:#000000\">Rằng: Từ ngẫu nhĩ gặp nhau.</span><br />\r\n<span style=\"color:#000000\">Thầm tr&ocirc;ng trộm nhớ bấy l&acirc;u đ&atilde; chồn.</span><br />\r\n<span style=\"color:#000000\">325.Xương mai t&iacute;nh đ&atilde; rũ m&ograve;n,</span><br />\r\n<span style=\"color:#000000\">Lần lừa ai biết h&atilde;y c&ograve;n h&ocirc;m nay!</span><br />\r\n<span style=\"color:#000000\">Th&aacute;ng tr&ograve;n như gởi cung m&acirc;y,</span><br />\r\n<span style=\"color:#000000\">Trần trần một phận ấp c&acirc;y đ&atilde; liều!</span><br />\r\n<span style=\"color:#000000\">Tiện đ&acirc;y xin một hai điều,</span><br />\r\n<span style=\"color:#000000\">330.Đ&agrave;i gương soi đến dấu b&egrave;o cho chăng?</span><br />\r\n<span style=\"color:#000000\">Ngần ngừ n&agrave;ng mới thưa rằng:</span><br />\r\n<span style=\"color:#000000\">Th&oacute;i nh&agrave; băng tuyết chất hằng phỉ phong,</span><br />\r\n<span style=\"color:#000000\">D&ugrave; khi l&aacute; thắm chỉ hồng,</span><br />\r\n<span style=\"color:#000000\">N&ecirc;n chăng th&igrave; cũng tại l&ograve;ng mẹ cha.</span><br />\r\n<span style=\"color:#000000\">335.Nặng l&ograve; x&oacute;t liễu v&igrave; hoa,</span><br />\r\n<span style=\"color:#000000\">Trẻ thơ đ&atilde; biết đ&acirc;u m&agrave; d&aacute;m thưa!</span><br />\r\n<span style=\"color:#000000\">Sinh rằng: R&agrave;y gi&oacute; mai mưa,</span><br />\r\n<span style=\"color:#000000\">Ng&agrave;y xu&acirc;n đ&atilde; dễ t&igrave;nh cờ mấy khi!</span><br />\r\n<span style=\"color:#000000\">D&ugrave; chăng x&eacute;t tấm t&igrave;nh si,</span><br />\r\n<span style=\"color:#000000\">340.Thiệt đ&acirc;y m&agrave; c&oacute; &iacute;ch g&igrave; đến ai?</span><br />\r\n<span style=\"color:#000000\">Ch&uacute;t chi gắn b&oacute; một hai,</span><br />\r\n<span style=\"color:#000000\">Cho đ&agrave;nh rồi sẽ liệu b&agrave;i mối manh.</span><br />\r\n<span style=\"color:#000000\">Khu&ocirc;n thi&ecirc;ng d&ugrave; phụ tấc th&agrave;nh,</span><br />\r\n<span style=\"color:#000000\">Cũng liều bỏ qu&aacute; xu&acirc;n xanh một đời.</span><br />\r\n<span style=\"color:#000000\">345.Lượng xu&acirc;n d&ugrave; quyết hẹp h&ograve;i,</span><br />\r\n<span style=\"color:#000000\">C&ocirc;ng đeo đuổi chẳng thiệt th&ograve;i lắm ru!</span><br />\r\n<span style=\"color:#000000\">Lặng nghe lời n&oacute;i như ru,</span><br />\r\n<span style=\"color:#000000\">Chiều xu&acirc;n dễ khiến n&eacute;t thu ngại ng&ugrave;ng.</span><br />\r\n<span style=\"color:#000000\">Rằng: Trong buổi mới lạ l&ugrave;ng,</span><br />\r\n<span style=\"color:#000000\">350.Nể l&ograve;ng c&oacute; lẽ cầm l&ograve;ng cho đang!</span><br />\r\n<span style=\"color:#000000\">Đ&atilde; l&ograve;ng qu&acirc;n tử đa mang,</span><br />\r\n<span style=\"color:#000000\">Một lời v&agrave;ng tạc đ&aacute; v&agrave;ng thủy chung.</span><br />\r\n<span style=\"color:#000000\">Được lời như cởi tấm l&ograve;ng,</span><br />\r\n<span style=\"color:#000000\">Giờ kim ho&agrave;n với khăn hồng trao tay.</span><br />\r\n<span style=\"color:#000000\">355.Rằng: Trăm năm cũng từ đ&acirc;y,</span><br />\r\n<span style=\"color:#000000\">Của tin gọi một ch&uacute;t n&agrave;y l&agrave;m ghi.</span><br />\r\n<span style=\"color:#000000\">Sẵn tay khăn gấm quạt quỳ,</span><br />\r\n<span style=\"color:#000000\">Với c&agrave;nh thoa ấy tức th&igrave; đổi trao.</span><br />\r\n<span style=\"color:#000000\">Một lời vừa gắn tất giao,</span><br />\r\n<span style=\"color:#000000\">360.M&aacute;i sau dường c&oacute; x&ocirc;n xao tiếng người.</span><br />\r\n<span style=\"color:#000000\">Vội v&agrave;ng l&aacute; rụng hoa rơi,</span><br />\r\n<span style=\"color:#000000\">Ch&agrave;ng về viện s&aacute;ch n&agrave;ng dời lầu trang.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Từ phen đ&aacute; biết tuổi v&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">T&igrave;nh c&agrave;ng thấm th&iacute;a dạ c&agrave;ng ngẩn ngơ.</span><br />\r\n<span style=\"color:#000000\">365.S&ocirc;ng Tương một dải n&ocirc;ng sờ,</span><br />\r\n<span style=\"color:#000000\">B&ecirc;n tr&ocirc;ng đầu nọ b&ecirc;n chờ suối kia.</span><br />\r\n<span style=\"color:#000000\">Một tường tuyết trở sương che.</span><br />\r\n<span style=\"color:#000000\">Tin xu&acirc;n đ&acirc;u dễ đi về cho năng.</span><br />\r\n<span style=\"color:#000000\">Lần lần ng&agrave;y gi&oacute; đ&ecirc;m trăng,</span><br />\r\n<span style=\"color:#000000\">370.Thưa hồng rậm lục đ&atilde; chừng xu&acirc;n qua.</span><br />\r\n<span style=\"color:#000000\">Ng&agrave;y vừa sinh nhật ngoại gia,</span><br />\r\n<span style=\"color:#000000\">Tr&ecirc;n hai đường dưới nữa l&agrave; hai em.</span><br />\r\n<span style=\"color:#000000\">Tưng bừng sắm sửa &aacute;o xi&ecirc;m,</span><br />\r\n<span style=\"color:#000000\">Biện d&acirc;ng một lễ xa đem tấc th&agrave;nh.</span><br />\r\n<span style=\"color:#000000\">375.Nh&agrave; lan thanh vắng một m&igrave;nh,</span><br />\r\n<span style=\"color:#000000\">Ngẫm cơ hội ngộ đ&atilde; d&agrave;nh h&ocirc;m nay.</span><br />\r\n<span style=\"color:#000000\">Thời tr&acirc;n thức thức sẵn b&agrave;y,</span><br />\r\n<span style=\"color:#000000\">G&oacute;t sen thoăn thoắt dạo ngay m&aacute;i tường.</span><br />\r\n<span style=\"color:#000000\">C&aacute;ch hoa sẽ dặng tiếng v&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">380.Dưới hoa đ&atilde; thấy c&oacute; ch&agrave;ng đứng tr&ocirc;ng:</span><br />\r\n<span style=\"color:#000000\">Tr&aacute;ch l&ograve;ng hờ hững với l&ograve;ng,</span><br />\r\n<span style=\"color:#000000\">Lửa hương chốc để lạnh l&ugrave;ng bấy l&acirc;u.</span><br />\r\n<span style=\"color:#000000\">Những l&agrave; đắp nhớ đổi sầu,</span><br />\r\n<span style=\"color:#000000\">Tuyết sương nhuốm nửa m&aacute;i đầu hoa r&acirc;m.</span><br />\r\n<span style=\"color:#000000\">385.N&agrave;ng rằng: Gi&oacute; bắt mưa cầm,</span><br />\r\n<span style=\"color:#000000\">Đ&atilde; cam tệ với tri &acirc;m bấy chầy.</span><br />\r\n<span style=\"color:#000000\">Vắng nh&agrave; được buổi h&ocirc;m nay,</span><br />\r\n<span style=\"color:#000000\">Lấy l&ograve;ng gọi ch&uacute;t ra đ&acirc;y tạ l&ograve;ng!</span><br />\r\n<span style=\"color:#000000\">Lần theo n&uacute;i giả đi v&ograve;ng,</span><br />\r\n<span style=\"color:#000000\">390.Cuối tường dường c&oacute; nẻo th&ocirc;ng mới r&agrave;o.</span><br />\r\n<span style=\"color:#000000\">Xắn tay mở kh&oacute;a động đ&agrave;o,</span><br />\r\n<span style=\"color:#000000\">Rẽ m&acirc;y tr&ocirc;ng tỏ lối v&agrave;o Thi&ecirc;n-thai.</span><br />\r\n<span style=\"color:#000000\">Mặt nh&igrave;n mặt c&agrave;ng th&ecirc;m tươi,</span><br />\r\n<span style=\"color:#000000\">B&ecirc;n lời vạn ph&uacute;c b&ecirc;n lời h&agrave;n huy&ecirc;n.</span><br />\r\n<span style=\"color:#000000\">395.S&aacute;nh vai về chốn thư hi&ecirc;n,</span><br />\r\n<span style=\"color:#000000\">G&oacute;p lời phong nguyệt nặng nguyền non s&ocirc;ng.</span><br />\r\n<span style=\"color:#000000\">Tr&ecirc;n y&ecirc;n b&uacute;t gi&aacute; thi đồng,</span><br />\r\n<span style=\"color:#000000\">Đạm thanh một bức tranh t&ugrave;ng treo tr&ecirc;n.</span><br />\r\n<span style=\"color:#000000\">Phong sương được vẻ thi&ecirc;n nhi&ecirc;n,</span><br />\r\n<span style=\"color:#000000\">400.Mặt khen n&eacute;t b&uacute;t c&agrave;ng nh&igrave;n c&agrave;ng tươi.</span><br />\r\n<span style=\"color:#000000\">Sinh rằng: Ph&aacute;c họa vừa rồi,</span><br />\r\n<span style=\"color:#000000\">Phẩm đề xin một v&agrave;i lời th&ecirc;m hoa.</span><br />\r\n<span style=\"color:#000000\">Tay ti&ecirc;n gi&oacute; t&aacute;p mưa sa,</span><br />\r\n<span style=\"color:#000000\">Khoảng tr&ecirc;n dừng b&uacute;t thảo v&agrave; bốn c&acirc;u.</span><br />\r\n<span style=\"color:#000000\">405.Khen: T&agrave;i nhả ngọc phun ch&acirc;u,</span><br />\r\n<span style=\"color:#000000\">N&agrave;ng Ban ả Tạ cũng đ&acirc;u thế n&agrave;y!</span><br />\r\n<span style=\"color:#000000\">Kiếp tu xưa v&iacute; chưa d&agrave;y,</span><br />\r\n<span style=\"color:#000000\">Ph&uacute;c n&agrave;o nhắc được gi&aacute; n&agrave;y cho ngang!</span><br />\r\n<span style=\"color:#000000\">N&agrave;ng rằng: Trộm liếc dung quang,</span><br />\r\n<span style=\"color:#000000\">410.Chẳng s&acirc;n bội ngọc cũng phường kim m&ocirc;n.</span><br />\r\n<span style=\"color:#000000\">Nghĩ m&igrave;nh phận mỏng c&aacute;nh chuồn,</span><br />\r\n<span style=\"color:#000000\">Khu&ocirc;n xanh biết c&oacute; vu&ocirc;ng tr&ograve;n m&agrave; hay?</span><br />\r\n<span style=\"color:#000000\">Nhớ từ năm h&atilde;y thơ ng&acirc;y,</span><br />\r\n<span style=\"color:#000000\">C&oacute; người tướng sĩ đo&aacute;n ngay một lời:</span><br />\r\n<span style=\"color:#000000\">415.Anh hoa ph&aacute;t tiết ra ngo&agrave;i,</span><br />\r\n<span style=\"color:#000000\">Ngh&igrave;n thu bạc mệnh một đời t&agrave;i hoa.</span><br />\r\n<span style=\"color:#000000\">Tr&ocirc;ng người lại ngẫm đến ta,</span><br />\r\n<span style=\"color:#000000\">Một dầy một mỏng biết l&agrave; c&oacute; n&ecirc;n?</span><br />\r\n<span style=\"color:#000000\">Sinh rằng: Giải cấu l&agrave; duy&ecirc;n,</span><br />\r\n<span style=\"color:#000000\">420.Xưa nay nh&acirc;n định thắng thi&ecirc;n cũng nhiều.</span><br />\r\n<span style=\"color:#000000\">V&iacute; d&ugrave; giải kết đến điều,</span><br />\r\n<span style=\"color:#000000\">Th&igrave; đem v&agrave;ng đ&aacute; m&agrave; liều với th&acirc;n!</span><br />\r\n<span style=\"color:#000000\">Đủ điều trung kh&uacute;c &acirc;n cần,</span><br />\r\n<span style=\"color:#000000\">L&ograve;ng xu&acirc;n phơi phới ch&eacute;n xu&acirc;n t&agrave;ng t&agrave;ng.</span><br />\r\n<span style=\"color:#000000\">425.Ng&agrave;y vui ngắn chẳng đầy gang,</span><br />\r\n<span style=\"color:#000000\">Tr&ocirc;ng ra &aacute;c đ&atilde; ngậm gương non đo&agrave;i.</span><br />\r\n<span style=\"color:#000000\">Vắng nh&agrave; chẳng tiện ngồi dai,</span><br />\r\n<span style=\"color:#000000\">Gi&atilde; ch&agrave;ng n&agrave;ng mới k&iacute;p dời song sa.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Đến nh&agrave; vừa thấy tin nh&agrave;,</span><br />\r\n<span style=\"color:#000000\">430.Hai th&acirc;n c&ograve;n dở tiệc hoa chưa về.</span><br />\r\n<span style=\"color:#000000\">Cửa ngo&agrave;i vội rủ r&egrave;m the,</span><br />\r\n<span style=\"color:#000000\">Xăm xăm băng lối vườn khuya một m&igrave;nh.</span><br />\r\n<span style=\"color:#000000\">Nhặt thưa gương giọi đầu c&agrave;nh,</span><br />\r\n<span style=\"color:#000000\">Ngọn đ&egrave;n tr&ocirc;ng lọt trướng huỳnh hắt hiu.</span><br />\r\n<span style=\"color:#000000\">435.Sinh vừa tựa &aacute;n thiu thiu,</span><br />\r\n<span style=\"color:#000000\">Dở chiều như tỉnh dở chiều như m&ecirc;.</span><br />\r\n<span style=\"color:#000000\">Tiếng sen sẽ động giấc h&ograve;e,</span><br />\r\n<span style=\"color:#000000\">B&oacute;ng trăng đ&atilde; xế hoa l&ecirc; lại gần.</span><br />\r\n<span style=\"color:#000000\">B&acirc;ng khu&acirc;ng đỉnh Gi&aacute;p non Thần,</span><br />\r\n<span style=\"color:#000000\">440.C&ograve;n ngờ giấc mộng đ&ecirc;m xu&acirc;n mơ m&agrave;ng.</span><br />\r\n<span style=\"color:#000000\">N&agrave;ng rằng: Khoảng vắng đ&ecirc;m trường,</span><br />\r\n<span style=\"color:#000000\">V&igrave; hoa n&ecirc;n phải đ&aacute;nh đường t&igrave;m hoa.</span><br />\r\n<span style=\"color:#000000\">B&acirc;y giờ r&otilde; mặt đ&ocirc;i ta,</span><br />\r\n<span style=\"color:#000000\">Biết đ&acirc;u rồi nữa chẳng l&agrave; chi&ecirc;m bao?</span><br />\r\n<span style=\"color:#000000\">445.Vội mừng l&agrave;m lễ rước v&agrave;o,</span><br />\r\n<span style=\"color:#000000\">Đ&agrave;i sen nối s&aacute;p song đ&agrave;o th&ecirc;m hương.</span><br />\r\n<span style=\"color:#000000\">Ti&ecirc;n thề c&ugrave;ng thảo một chương,</span><br />\r\n<span style=\"color:#000000\">T&oacute;c m&acirc;y một m&oacute;n dao v&agrave;ng chia đ&ocirc;i.</span><br />\r\n<span style=\"color:#000000\">Vầng trăng vằng vặc giữa trời,</span><br />\r\n<span style=\"color:#000000\">450.Đinh ninh hai mặt một lời song song.</span><br />\r\n<span style=\"color:#000000\">T&oacute;c tơ căn vặn tấc l&ograve;ng,</span><br />\r\n<span style=\"color:#000000\">Trăm năm tạc một chữ đồng đến xương.</span><br />\r\n<span style=\"color:#000000\">Ch&eacute;n h&agrave; s&aacute;nh giọng quỳnh tương,</span><br />\r\n<span style=\"color:#000000\">Dải l&agrave; hương lộn b&igrave;nh gương b&oacute;ng lồng.</span><br />\r\n<span style=\"color:#000000\">455.Sinh rằng: Gi&oacute; m&aacute;t trăng trong,</span><br />\r\n<span style=\"color:#000000\">Bấy l&acirc;u nay một ch&uacute;t l&ograve;ng chưa cam.</span><br />\r\n<span style=\"color:#000000\">Ch&agrave;y sương chưa nện cầu Lam,</span><br />\r\n<span style=\"color:#000000\">Sợ lần kh&acirc;n qu&aacute; ra s&agrave;m sỡ chăng?</span><br />\r\n<span style=\"color:#000000\">N&agrave;ng rằng: Hồng diệp x&iacute;ch thằng,</span><br />\r\n<span style=\"color:#000000\">460.Một lời cũng đ&atilde; tiếng rằng tương tri.</span><br />\r\n<span style=\"color:#000000\">Đừng điều nguyệt nọ hoa kia.</span><br />\r\n<span style=\"color:#000000\">Ngo&agrave;i ra ai lại tiếc g&igrave; với ai.</span><br />\r\n<span style=\"color:#000000\">Rằng: Nghe nổi tiếng cầm đ&agrave;i,</span><br />\r\n<span style=\"color:#000000\">Nước non luống những lắng tai Chung Kỳ.</span><br />\r\n<span style=\"color:#000000\">465.Thưa rằng: Tiện kỹ s&aacute; chi,</span><br />\r\n<span style=\"color:#000000\">Đ&atilde; l&ograve;ng dạy đến dạy th&igrave; phải v&acirc;ng.</span><br />\r\n<span style=\"color:#000000\">Hi&ecirc;n sau treo sẵn cầm trăng,</span><br />\r\n<span style=\"color:#000000\">Vội v&agrave;ng Sinh đ&atilde; tay n&acirc;ng ngang m&agrave;y.</span><br />\r\n<span style=\"color:#000000\">N&agrave;ng rằng: Nghề mọn ri&ecirc;ng tay,</span><br />\r\n<span style=\"color:#000000\">470.L&agrave;m chi cho bận l&ograve;ng n&agrave;y lắm th&acirc;n!</span><br />\r\n<span style=\"color:#000000\">So dần d&acirc;y vũ d&acirc;y văn,</span><br />\r\n<span style=\"color:#000000\">Bốn d&acirc;y to nhỏ theo vần cung thương.</span><br />\r\n<span style=\"color:#000000\">Kh&uacute;c đ&acirc;u H&aacute;n Sở chiến trường,</span><br />\r\n<span style=\"color:#000000\">Nghe ra tiếng sắt tiếng v&agrave;ng chen nhau.</span><br />\r\n<span style=\"color:#000000\">475.Kh&uacute;c đ&acirc;u Tư m&atilde; Phượng cầu,</span><br />\r\n<span style=\"color:#000000\">Nghe ra như o&aacute;n như sầu phải chăng!</span><br />\r\n<span style=\"color:#000000\">K&ecirc; Khang n&agrave;y kh&uacute;c Quảng lăng,</span><br />\r\n<span style=\"color:#000000\">Một rằng lưu thủy hai rằng h&agrave;nh v&acirc;n.</span><br />\r\n<span style=\"color:#000000\">Qua quan n&agrave;y kh&uacute;c Chi&ecirc;u Qu&acirc;n,</span><br />\r\n<span style=\"color:#000000\">480.Nửa phần luyến ch&uacute;a nửa phần tư gia.</span><br />\r\n<span style=\"color:#000000\">Trong như tiếng hạc bay qua,</span><br />\r\n<span style=\"color:#000000\">Đục như tiếng suối mới sa nửa vời.</span><br />\r\n<span style=\"color:#000000\">Tiếng khoan như gi&oacute; thoảng ngo&agrave;i,</span><br />\r\n<span style=\"color:#000000\">Tiếng mau sầm sập như trời đổ mưa.</span><br />\r\n<span style=\"color:#000000\">485.Ngọn đ&egrave;n khi tỏ khi mờ,</span><br />\r\n<span style=\"color:#000000\">Khiến người ngồi đ&oacute; cũng ngơ ngẩn sầu.</span><br />\r\n<span style=\"color:#000000\">Khi tựa gối khi c&uacute;i đầu,</span><br />\r\n<span style=\"color:#000000\">Khi v&ograve; ch&iacute;n kh&uacute;c khi chau đ&ocirc;i m&agrave;y.</span><br />\r\n<span style=\"color:#000000\">Rằng: Hay th&igrave; thật l&agrave; hay,</span><br />\r\n<span style=\"color:#000000\">490.Nghe ra ngậm đắng nuốt cay thế n&agrave;o!</span><br />\r\n<span style=\"color:#000000\">Lựa chi những bậc ti&ecirc;u tao,</span><br />\r\n<span style=\"color:#000000\">Dột l&ograve;ng m&igrave;nh cũng nao nao l&ograve;ng người?</span><br />\r\n<span style=\"color:#000000\">Rằng: Quen mất nết đi rồi,</span><br />\r\n<span style=\"color:#000000\">Tẻ vui th&ocirc;i cũng t&iacute;nh trời biết sao!</span><br />\r\n<span style=\"color:#000000\">495.Lời v&agrave;ng &acirc;m lĩnh &yacute; cao,</span><br />\r\n<span style=\"color:#000000\">Họa dần dần bớt ch&uacute;t n&agrave;o được kh&ocirc;ng.</span><br />\r\n<span style=\"color:#000000\">Hoa hương c&agrave;ng tỏ thức hồng,</span><br />\r\n<span style=\"color:#000000\">Đầu m&agrave;y cuối mắt c&agrave;ng nồng tấm y&ecirc;u.</span><br />\r\n<span style=\"color:#000000\">S&oacute;ng t&igrave;nh dường đ&atilde; xi&ecirc;u xi&ecirc;u,</span><br />\r\n<span style=\"color:#000000\">500.Xem trong &acirc;u yếm c&oacute; chiều lả lơi.</span><br />\r\n<span style=\"color:#000000\">Thưa rằng: đừng lấy l&agrave;m chơi,</span><br />\r\n<span style=\"color:#000000\">Dẽ cho thưa hết một lời đ&atilde; nao!</span><br />\r\n<span style=\"color:#000000\">Vẻ chi một đ&oacute;a y&ecirc;u đ&agrave;o,</span><br />\r\n<span style=\"color:#000000\">Vườn hồng chi d&aacute;m ngăn r&agrave;o chim xanh.</span><br />\r\n<span style=\"color:#000000\">505.Đ&atilde; cho v&agrave;o bậc bố kinh,</span><br />\r\n<span style=\"color:#000000\">Đạo t&ograve;ng phu lấy chữ trinh l&agrave;m đầu</span><br />\r\n<span style=\"color:#000000\">Ra tuồng tr&ecirc;n Bộc trong d&acirc;u,</span><br />\r\n<span style=\"color:#000000\">Th&igrave; con người ấy ai cầu l&agrave;m chi!</span><br />\r\n<span style=\"color:#000000\">Phải điều ăn xổi ở th&igrave;,</span><br />\r\n<span style=\"color:#000000\">510.Tiết trăm năm nỡ bỏ đi một ng&agrave;y!</span><br />\r\n<span style=\"color:#000000\">Ngẫm duy&ecirc;n kỳ ngộ xưa nay,</span><br />\r\n<span style=\"color:#000000\">Lứa đ&ocirc;i ai đẹp lại t&agrave;y Th&ocirc;i Trương.</span><br />\r\n<span style=\"color:#000000\">M&acirc;y mưa đ&aacute;nh đổ đ&aacute; v&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">Qu&aacute; chiều n&ecirc;n đ&atilde; ch&aacute;n chường yến anh.</span><br />\r\n<span style=\"color:#000000\">505.Trong khi chắp c&aacute;nh liền c&agrave;nh,</span><br />\r\n<span style=\"color:#000000\">M&agrave; l&ograve;ng rẻ r&uacute;ng đ&atilde; d&agrave;nh một b&ecirc;n.</span><br />\r\n<span style=\"color:#000000\">M&aacute;i t&acirc;y để lạnh hương nguyền,</span><br />\r\n<span style=\"color:#000000\">Cho duy&ecirc;n đằm thắm ra duy&ecirc;n bẽ b&agrave;ng.</span><br />\r\n<span style=\"color:#000000\">Gieo thoi trước chẳng giữ gi&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">520.Để sau n&ecirc;n thẹn c&ugrave;ng ch&agrave;ng bởi ai?</span><br />\r\n<span style=\"color:#000000\">Vội chi liễu &eacute;p hoa n&agrave;i,</span><br />\r\n<span style=\"color:#000000\">C&ograve;n th&acirc;n ắt lại đền bồi c&oacute; khi!</span><br />\r\n<span style=\"color:#000000\">Thấy lời đoan ch&iacute;nh dễ nghe,</span><br />\r\n<span style=\"color:#000000\">Ch&agrave;ng c&agrave;ng th&ecirc;m nể th&ecirc;m v&igrave; mười ph&acirc;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">525.B&oacute;ng t&agrave;u vừa lạt vẻ ng&acirc;n,</span><br />\r\n<span style=\"color:#000000\">Tin đ&acirc;u đ&atilde; thấy cửa ngăn gọi v&agrave;o.</span><br />\r\n<span style=\"color:#000000\">N&agrave;ng th&igrave; vội trở buồng th&ecirc;u,</span><br />\r\n<span style=\"color:#000000\">Sinh th&igrave; dạo g&oacute;t s&acirc;n đ&agrave;o bước ra.</span><br />\r\n<span style=\"color:#000000\">Cửa s&agrave;i vừa ngỏ then hoa,</span><br />\r\n<span style=\"color:#000000\">530.Gia đồng v&agrave;o gởi thư nh&agrave; mới sang.</span><br />\r\n<span style=\"color:#000000\">Đem tin th&uacute;c phụ từ đường,</span><br />\r\n<span style=\"color:#000000\">Bơ vơ lữ thấn tha hương đề huề.</span><br />\r\n<span style=\"color:#000000\">Li&ecirc;u dương c&aacute;ch trở sơn kh&ecirc;,</span><br />\r\n<span style=\"color:#000000\">Xu&acirc;n đường k&iacute;p gọi sinh về hộ tang.</span><br />\r\n<span style=\"color:#000000\">535.Mảng tin xiết nỗi kinh ho&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">Băng m&igrave;nh lẻn trước đ&agrave;i trang tự t&igrave;nh.</span><br />\r\n<span style=\"color:#000000\">G&oacute;t đầu mọi nỗi đinh ninh,</span><br />\r\n<span style=\"color:#000000\">Nỗi nh&agrave; tang t&oacute;c nỗi m&igrave;nh xa x&ocirc;i:</span><br />\r\n<span style=\"color:#000000\">Sự đ&acirc;u chưa kịp đ&ocirc;i hồi,</span><br />\r\n<span style=\"color:#000000\">540.Duy&ecirc;n đ&acirc;u chưa kịp một lời trao tơ,</span><br />\r\n<span style=\"color:#000000\">Trăng thề c&ograve;n đ&oacute; trơ trơ,</span><br />\r\n<span style=\"color:#000000\">D&aacute;m xa x&ocirc;i mặt m&agrave; thưa thớt l&ograve;ng.</span><br />\r\n<span style=\"color:#000000\">Ngo&agrave;i ngh&igrave;n dặm chốc ba đ&ocirc;ng,</span><br />\r\n<span style=\"color:#000000\">Mối sầu khi gỡ cho xong c&ograve;n chầy!</span><br />\r\n<span style=\"color:#000000\">545.G&igrave;n v&agrave;ng giữ ngọc cho hay,</span><br />\r\n<span style=\"color:#000000\">Cho đ&agrave;nh l&ograve;ng kẻ ch&acirc;n m&acirc;y cuối trời.</span><br />\r\n<span style=\"color:#000000\">Tai nghe ruột rối bời bời,</span><br />\r\n<span style=\"color:#000000\">Ngập ngừng n&agrave;ng mới gi&atilde;i lời trước sau:</span><br />\r\n<span style=\"color:#000000\">&Ocirc;ng tơ gh&eacute;t bỏ chi nhau,</span><br />\r\n<span style=\"color:#000000\">550.Chưa vui sum họp đ&atilde; sầu chia ph&ocirc;i!</span><br />\r\n<span style=\"color:#000000\">C&ugrave;ng nhau tr&oacute;t đ&atilde; nặng lời,</span><br />\r\n<span style=\"color:#000000\">Dẫu thay m&aacute;i t&oacute;c d&aacute;m dời l&ograve;ng tơ!</span><br />\r\n<span style=\"color:#000000\">Quản bao th&aacute;ng đợi năm chờ,</span><br />\r\n<span style=\"color:#000000\">Nghĩ người ăn gi&oacute; nằm mưa x&oacute;t thầm.</span><br />\r\n<span style=\"color:#000000\">555.Đ&atilde; nguyền hai chữ đồng t&acirc;m,</span><br />\r\n<span style=\"color:#000000\">Trăm năm thề chẳng &ocirc;m cầm thuyền ai.</span><br />\r\n<span style=\"color:#000000\">C&ograve;n non c&ograve;n nước c&ograve;n d&agrave;i,</span><br />\r\n<span style=\"color:#000000\">C&ograve;n về c&ograve;n nhớ đến người h&ocirc;m nay!</span><br />\r\n<span style=\"color:#000000\">D&ugrave;ng dằng chưa nỡ rời tay,</span><br />\r\n<span style=\"color:#000000\">560.Vầng đ&ocirc;ng tr&ocirc;ng đ&atilde; đứng ngay n&oacute;c nh&agrave;.</span><br />\r\n<span style=\"color:#000000\">Ngại ng&ugrave;ng một bước một xa,</span><br />\r\n<span style=\"color:#000000\">Một lời tr&acirc;n trọng ch&acirc;u sa mấy h&agrave;ng.</span><br />\r\n<span style=\"color:#000000\">Buộc y&ecirc;n quảy g&aacute;nh vội v&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">Mối sầu xẻ nửa bước đường chia hai.</span><br />\r\n<span style=\"color:#000000\">565.Buồn tr&ocirc;ng phong cảnh qu&ecirc; người,</span><br />\r\n<span style=\"color:#000000\">Đầu c&agrave;nh quy&ecirc;n nhặt cuối trời nhạn thưa.</span><br />\r\n<span style=\"color:#000000\">N&atilde;o người cữ gi&oacute; tuần mưa,</span><br />\r\n<span style=\"color:#000000\">Một ng&agrave;y nặng g&aacute;nh tương tư một ng&agrave;y.</span><br />\r\n<span style=\"color:#000000\">N&agrave;ng c&ograve;n đứng tựa hi&ecirc;n t&acirc;y,</span><br />\r\n<span style=\"color:#000000\">570.Ch&iacute;n hồi vấn v&iacute;t như vầy mối tơ.</span><br />\r\n<span style=\"color:#000000\">Tr&ocirc;ng chừng kh&oacute;i ngất song thưa,</span><br />\r\n<span style=\"color:#000000\">Hoa tr&ocirc;i tr&aacute;c thắm, liễu xơ x&aacute;c v&agrave;ng.</span></span></span></p>\r\n\r\n<h3 style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\"><strong><u>Kiều B&aacute;n M&igrave;nh Chuộc Cha (C&acirc;u 573-804)</u></strong></span></span></span></h3>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Tần ngần dạo g&oacute;t lầu trang,</span><br />\r\n<span style=\"color:#000000\">Một đo&agrave;n mừng thọ ngoại hương mới về,</span><br />\r\n<span style=\"color:#000000\">575.H&agrave;n huy&ecirc;n chưa kịp gi&atilde;i dề,</span><br />\r\n<span style=\"color:#000000\">Sai nha bỗng thấy bốn bề x&ocirc;n xao.</span><br />\r\n<span style=\"color:#000000\">Người n&aacute;ch thước, kẻ tay đao;</span><br />\r\n<span style=\"color:#000000\">Đầu tr&acirc;u mặt ngựa &agrave;o &agrave;o như s&ocirc;i.</span><br />\r\n<span style=\"color:#000000\">Gi&agrave; giang một l&atilde;o một trai,</span><br />\r\n<span style=\"color:#000000\">580.Một d&acirc;y v&ocirc; lại buộc hai th&acirc;m t&igrave;nh.</span><br />\r\n<span style=\"color:#000000\">Đầy nh&agrave; vang tiếng ruồi xanh,</span><br />\r\n<span style=\"color:#000000\">Rụng rời khung dệt, tan t&agrave;nh g&oacute;i may.</span><br />\r\n<span style=\"color:#000000\">Đồ tế nhuyễn, của ri&ecirc;ng t&acirc;y,</span><br />\r\n<span style=\"color:#000000\">Sạch s&agrave;nh sanh v&eacute;t cho đầy t&uacute;i tham.</span><br />\r\n<span style=\"color:#000000\">585.Điều đ&acirc;u bay buộc ai l&agrave;m?</span><br />\r\n<span style=\"color:#000000\">N&agrave;y ai dan dậm, giật gi&agrave;m bỗng dưng?</span><br />\r\n<span style=\"color:#000000\">Hỏi ra sau mới biết rằng:</span><br />\r\n<span style=\"color:#000000\">Phải t&ecirc;n xưng xuất l&agrave; thằng b&aacute;n tơ.</span><br />\r\n<span style=\"color:#000000\">Một nh&agrave; hoảng hốt ngẩn ngơ,</span><br />\r\n<span style=\"color:#000000\">590.Tiếng oan dậy đất, &aacute;n ngờ l&ograve;a m&acirc;y.</span><br />\r\n<span style=\"color:#000000\">Hạ từ van lạy suốt ng&agrave;y,</span><br />\r\n<span style=\"color:#000000\">Điếc tai l&acirc;n tuất, phũ tay tồi t&agrave;n.</span><br />\r\n<span style=\"color:#000000\">Rường cao r&uacute;t ngược d&acirc;y oan,</span><br />\r\n<span style=\"color:#000000\">Dẫu l&agrave; đ&aacute; cũng n&aacute;t gan, lọ người.</span><br />\r\n<span style=\"color:#000000\">595.Mặt tr&ocirc;ng đau đớn rụng rời,</span><br />\r\n<span style=\"color:#000000\">Oan n&agrave;y c&ograve;n một k&ecirc;u trời, nhưng xa.</span><br />\r\n<span style=\"color:#000000\">Một ng&agrave;y lạ th&oacute;i sai nha,</span><br />\r\n<span style=\"color:#000000\">L&agrave;m cho khốc hại chẳng qua v&igrave; tiền.</span><br />\r\n<span style=\"color:#000000\">Sao cho cốt nhục vẹn tuyền,</span><br />\r\n<span style=\"color:#000000\">600.Trong khi ngộ biến t&ograve;ng quyền biết sao?</span><br />\r\n<span style=\"color:#000000\">Duy&ecirc;n hội ngộ, đức c&ugrave; lao,</span><br />\r\n<span style=\"color:#000000\">B&ecirc;n t&igrave;nh b&ecirc;n hiếu, b&ecirc;n n&agrave;o nặng hơn?</span><br />\r\n<span style=\"color:#000000\">Để lời thệ hải minh sơn,</span><br />\r\n<span style=\"color:#000000\">L&agrave;m con trước phải đền ơn sinh th&agrave;nh.</span><br />\r\n<span style=\"color:#000000\">605.Quyết t&igrave;nh n&agrave;ng mới hạ t&igrave;nh:</span><br />\r\n<span style=\"color:#000000\">Dẽ cho để thiếp b&aacute;n m&igrave;nh chuộc cha!</span><br />\r\n<span style=\"color:#000000\">Họ Chung c&oacute; kẻ lại gi&agrave;,</span><br />\r\n<span style=\"color:#000000\">Cũng trong nha dịch lại l&agrave; từ t&acirc;m.</span><br />\r\n<span style=\"color:#000000\">Thấy n&agrave;ng hiếu trọng t&igrave;nh th&acirc;m,</span><br />\r\n<span style=\"color:#000000\">610.V&igrave; n&agrave;ng nghĩ cũng thương thầm x&oacute;t vay.</span><br />\r\n<span style=\"color:#000000\">T&iacute;nh b&agrave;i l&oacute;t đ&oacute; luồn đ&acirc;y,</span><br />\r\n<span style=\"color:#000000\">C&oacute; ba trăm lạng việc n&agrave;y mới xu&ocirc;i.</span><br />\r\n<span style=\"color:#000000\">H&atilde;y về tạm ph&oacute; giam ngo&agrave;i,</span><br />\r\n<span style=\"color:#000000\">Dặn n&agrave;ng qui liệu trong đ&ocirc;i ba ng&agrave;y.</span><br />\r\n<span style=\"color:#000000\">615.Thương t&igrave;nh con trẻ thơ ng&acirc;y,</span><br />\r\n<span style=\"color:#000000\">Gặp cơn vạ gi&oacute; tai bay bất kỳ!</span><br />\r\n<span style=\"color:#000000\">Đau l&ograve;ng tử biệt sinh ly,</span><br />\r\n<span style=\"color:#000000\">Th&acirc;n c&ograve;n chẳng tiếc, tiếc g&igrave; đến duy&ecirc;n!</span><br />\r\n<span style=\"color:#000000\">Hạt mưa s&aacute; nghĩ phận h&egrave;n,</span><br />\r\n<span style=\"color:#000000\">620.Liều đem tấc cỏ quyết đền ba xu&acirc;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Sự l&ograve;ng ngỏ với băng nh&acirc;n,</span><br />\r\n<span style=\"color:#000000\">Tin sương đồn đại xa gần x&ocirc;n xao.</span><br />\r\n<span style=\"color:#000000\">Gần miền c&oacute; một mụ n&agrave;o,</span><br />\r\n<span style=\"color:#000000\">Đưa người viễn kh&aacute;ch t&igrave;m v&agrave;o vấn danh.</span><br />\r\n<span style=\"color:#000000\">625.Hỏi t&ecirc;n rằng: M&atilde; Gi&aacute;m sinh.</span><br />\r\n<span style=\"color:#000000\">Hỏi qu&ecirc;, rằng: Huyện L&acirc;m Thanh cũng gần.</span><br />\r\n<span style=\"color:#000000\">Qu&aacute; ni&ecirc;n trạc ngoại tứ tuần,</span><br />\r\n<span style=\"color:#000000\">M&agrave;y r&acirc;u nhẵn nhụi, &aacute;o quần bảnh bao.</span><br />\r\n<span style=\"color:#000000\">Trước thầy sau tớ lao xao</span><br />\r\n<span style=\"color:#000000\">630.Nh&agrave; băng đưa mối rước v&agrave;o lầu trang.</span><br />\r\n<span style=\"color:#000000\">Ghế tr&ecirc;n ngồi t&oacute;t sỗ s&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">Buồng trong mối đ&atilde; giục n&agrave;ng k&iacute;p ra.</span><br />\r\n<span style=\"color:#000000\">Nỗi m&igrave;nh th&ecirc;m tức nỗi nh&agrave;,</span><br />\r\n<span style=\"color:#000000\">Thềm hoa một bước, lệ hoa mấy h&agrave;ng!</span><br />\r\n<span style=\"color:#000000\">635.Ngại ng&ugrave;ng giợn gi&oacute; e sương,</span><br />\r\n<span style=\"color:#000000\">Nh&igrave;n hoa b&oacute;ng thẹn, tr&ocirc;ng gương mặt d&agrave;y.</span><br />\r\n<span style=\"color:#000000\">Mối c&agrave;ng v&eacute;n t&oacute;c bắt tay,</span><br />\r\n<span style=\"color:#000000\">N&eacute;t buồn như c&uacute;c, điệu gầy như mai.</span><br />\r\n<span style=\"color:#000000\">Đắn đo c&acirc;n sắc c&acirc;n t&agrave;i,</span><br />\r\n<span style=\"color:#000000\">640.&Eacute;p cung cầm nguyệt, thử b&agrave;i quạt thơ.</span><br />\r\n<span style=\"color:#000000\">Mặn nồng một vẻ một ưa,</span><br />\r\n<span style=\"color:#000000\">Bằng l&ograve;ng kh&aacute;ch mới t&ugrave;y cơ dặt d&igrave;u.</span><br />\r\n<span style=\"color:#000000\">Rằng: Mua ngọc đến Lam Kiều,</span><br />\r\n<span style=\"color:#000000\">S&iacute;nh nghi xin dạy bao nhi&ecirc;u cho tường?</span><br />\r\n<span style=\"color:#000000\">645.Mối rằng: đ&aacute;ng gi&aacute; ngh&igrave;n v&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">Gấp nh&agrave; nhờ lượng người thương d&aacute;m n&agrave;i.</span><br />\r\n<span style=\"color:#000000\">C&ograve; k&egrave; bớt một th&ecirc;m hai,</span><br />\r\n<span style=\"color:#000000\">Giờ l&acirc;u ng&atilde; gi&aacute; v&agrave;ng ngo&agrave;i bốn trăm.</span><br />\r\n<span style=\"color:#000000\">Một lời thuyền đ&atilde; &ecirc;m dằm</span><br />\r\n<span style=\"color:#000000\">650.H&atilde;y đưa canh thiếp trước cầm l&agrave;m ghi.</span><br />\r\n<span style=\"color:#000000\">Định ng&agrave;y nạp th&aacute;i vu qui,</span><br />\r\n<span style=\"color:#000000\">Tiền lưng đ&atilde; sẵn việc g&igrave; chẳng xong!</span><br />\r\n<span style=\"color:#000000\">Một lời cậy với Chung c&ocirc;ng,</span><br />\r\n<span style=\"color:#000000\">Khất từ tạm lĩnh Vương &ocirc;ng về nh&agrave;.</span><br />\r\n<span style=\"color:#000000\">655.Thương t&igrave;nh con trẻ cha gi&agrave;,</span><br />\r\n<span style=\"color:#000000\">Nh&igrave;n n&agrave;ng &ocirc;ng những m&aacute;u sa ruột d&agrave;u:</span><br />\r\n<span style=\"color:#000000\">Nu&ocirc;i con những ước về sau,</span><br />\r\n<span style=\"color:#000000\">Trao tơ phải lứa, gieo cầu đ&aacute;ng nơi.</span><br />\r\n<span style=\"color:#000000\">Trời l&agrave;m chi cực bấy trời,</span><br />\r\n<span style=\"color:#000000\">660.N&agrave;y ai vu th&aacute;c cho người hợp tan!</span><br />\r\n<span style=\"color:#000000\">B&uacute;a r&igrave;u bao quản th&acirc;n t&agrave;n,</span><br />\r\n<span style=\"color:#000000\">Nỡ đầy đọa trẻ, c&agrave;ng oan khốc gi&agrave;.</span><br />\r\n<span style=\"color:#000000\">Một lần sau trước cũng l&agrave;,</span><br />\r\n<span style=\"color:#000000\">Th&ocirc;i th&igrave; mặt khuất chẳng th&agrave; l&ograve;ng đau!</span><br />\r\n<span style=\"color:#000000\">665.Theo lời c&agrave;ng chảy d&ograve;ng ch&acirc;u,</span><br />\r\n<span style=\"color:#000000\">Liều m&igrave;nh &ocirc;ng rắp gieo đầu tường v&ocirc;i.</span><br />\r\n<span style=\"color:#000000\">Vội v&agrave;ng kẻ giữ người coi,</span><br />\r\n<span style=\"color:#000000\">Nhỏ to n&agrave;ng lại t&igrave;m lời khuy&ecirc;n can:</span><br />\r\n<span style=\"color:#000000\">Vẻ chi một mảnh hồng nhan,</span><br />\r\n<span style=\"color:#000000\">670.T&oacute;c tơ chưa ch&uacute;t đền ơn sinh th&agrave;nh.</span><br />\r\n<span style=\"color:#000000\">D&acirc;ng thư đ&atilde; thẹn n&agrave;ng Oanh,</span><br />\r\n<span style=\"color:#000000\">Lại thua ả L&yacute; b&aacute;n m&igrave;nh hay sao?</span><br />\r\n<span style=\"color:#000000\">Cỗi xu&acirc;n tuổi hạc c&agrave;ng cao,</span><br />\r\n<span style=\"color:#000000\">Một c&acirc;y g&aacute;nh v&aacute;c biết bao nhi&ecirc;u c&agrave;nh.</span><br />\r\n<span style=\"color:#000000\">675.L&ograve;ng tơ d&ugrave; chẳng dứt t&igrave;nh,</span><br />\r\n<span style=\"color:#000000\">Gi&oacute; mưa &acirc;u hẳn tan t&agrave;nh nư&oacute;c non.</span><br />\r\n<span style=\"color:#000000\">Th&agrave; rằng liều một th&acirc;n con,</span><br />\r\n<span style=\"color:#000000\">Hoa d&ugrave; r&atilde; c&aacute;nh, l&aacute; c&ograve;n xanh c&acirc;y.</span><br />\r\n<span style=\"color:#000000\">Phận sao đ&agrave;nh vậy cũng vầy,</span><br />\r\n<span style=\"color:#000000\">680.Cầm như chẳng đậu những ng&agrave;y c&ograve;n xanh.</span><br />\r\n<span style=\"color:#000000\">Cũng đừng t&iacute;nh quẩn lo quanh,</span><br />\r\n<span style=\"color:#000000\">Tan nh&agrave; l&agrave; một thiệt m&igrave;nh l&agrave; hai.</span><br />\r\n<span style=\"color:#000000\">Phải lời &ocirc;ng cũng &ecirc;m tai,</span><br />\r\n<span style=\"color:#000000\">Nh&igrave;n nhau giọt vắn giọt d&agrave;i ngổn ngang.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">685.M&aacute;i ngo&agrave;i họ M&atilde; vừa sang,</span><br />\r\n<span style=\"color:#000000\">Tờ hoa đ&atilde; k&yacute;, c&acirc;n v&agrave;ng mới trao.</span><br />\r\n<span style=\"color:#000000\">Trăng gi&agrave; độc địa l&agrave;m sao?</span><br />\r\n<span style=\"color:#000000\">Cầm d&acirc;y chẳng lựa buộc v&agrave;o tự nhi&ecirc;n.</span><br />\r\n<span style=\"color:#000000\">Trong tay đ&atilde; sẵn đồng tiền,</span><br />\r\n<span style=\"color:#000000\">690.Dầu l&ograve;ng đổi trắng thay đen kh&oacute; g&igrave;!</span><br />\r\n<span style=\"color:#000000\">Họ Chung ra sức gi&uacute;p v&igrave;,</span><br />\r\n<span style=\"color:#000000\">Lễ t&acirc;m đ&atilde; đặt, tụng kỳ cũng xong.</span><br />\r\n<span style=\"color:#000000\">Việc nh&agrave; đ&atilde; tạm thong dong,</span><br />\r\n<span style=\"color:#000000\">Tinh kỳ giục gi&atilde; đ&atilde; mong độ về.</span><br />\r\n<span style=\"color:#000000\">695.Một m&igrave;nh n&agrave;ng ngọn đ&egrave;n khuya,</span><br />\r\n<span style=\"color:#000000\">&Aacute;o dầm giọt lệ, t&oacute;c xe mối sầu.</span><br />\r\n<span style=\"color:#000000\">Phận dầu, dầu vậy cũng dầu,</span><br />\r\n<span style=\"color:#000000\">X&oacute;t l&ograve;ng đeo đẳng bấy l&acirc;u một lời!</span><br />\r\n<span style=\"color:#000000\">C&ocirc;ng tr&igrave;nh kể biết mấy mươi.</span><br />\r\n<span style=\"color:#000000\">700.V&igrave; ta khăng kh&iacute;t, cho người dở dang.</span><br />\r\n<span style=\"color:#000000\">Thề hoa chưa r&aacute;o ch&eacute;n v&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">Lỗi thề th&ocirc;i đ&atilde; phụ ph&agrave;ng với hoa.</span><br />\r\n<span style=\"color:#000000\">Trời Li&ecirc;u non nước bao xa.</span><br />\r\n<span style=\"color:#000000\">Nghĩ đ&acirc;u rẽ cửa chia nh&agrave; tự t&ocirc;i.</span><br />\r\n<span style=\"color:#000000\">705.Biết bao duy&ecirc;n nợ thề bồi.</span><br />\r\n<span style=\"color:#000000\">Kiếp n&agrave;y th&ocirc;i thế th&igrave; th&ocirc;i c&ograve;n g&igrave;.</span><br />\r\n<span style=\"color:#000000\">T&aacute;i sinh chưa dứt hương thề.</span><br />\r\n<span style=\"color:#000000\">L&agrave;m th&acirc;n tr&acirc;u ngựa đền ngh&igrave; tr&uacute;c mai.</span><br />\r\n<span style=\"color:#000000\">Nợ t&igrave;nh chưa trả cho ai,</span><br />\r\n<span style=\"color:#000000\">710.Khối t&igrave;nh mang xuống tuyền đ&agrave;i chưa tan.</span><br />\r\n<span style=\"color:#000000\">Nỗi ri&ecirc;ng ri&ecirc;ng những b&agrave;ng ho&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">Dầu chong trắng đĩa lệ tr&agrave;n thấm khăn.</span><br />\r\n<span style=\"color:#000000\">Th&uacute;y V&acirc;n chợt tỉnh giấc xu&acirc;n,</span><br />\r\n<span style=\"color:#000000\">Dưới đ&egrave;n gh&eacute; đến &acirc;n cần hỏi han:</span><br />\r\n<span style=\"color:#000000\">715.Cơ trời d&acirc;u bể đa đoan,</span><br />\r\n<span style=\"color:#000000\">Một nh&agrave; để chị ri&ecirc;ng oan một m&igrave;nh,</span><br />\r\n<span style=\"color:#000000\">Cớ chi ngồi nhẫn t&agrave;n canh?</span><br />\r\n<span style=\"color:#000000\">Nỗi ri&ecirc;ng c&ograve;n mắc mối t&igrave;nh chi đ&acirc;y?</span><br />\r\n<span style=\"color:#000000\">Rằng: L&ograve;ng đương thổn thức đầy,</span><br />\r\n<span style=\"color:#000000\">720.Tơ duy&ecirc;n c&ograve;n vướng mối n&agrave;y chưa xong.</span><br />\r\n<span style=\"color:#000000\">Hở m&ocirc;i ra cũng thẹn th&ugrave;ng,</span><br />\r\n<span style=\"color:#000000\">Để l&ograve;ng th&igrave; phụ tấm l&ograve;ng với ai.</span><br />\r\n<span style=\"color:#000000\">Cậy em, em c&oacute; chịu lời,</span><br />\r\n<span style=\"color:#000000\">Ngồi l&ecirc;n cho chị lạy rồi sẽ thưa.</span><br />\r\n<span style=\"color:#000000\">725.Giữa đường đứt g&aacute;nh tương tư,</span><br />\r\n<span style=\"color:#000000\">Loan giao chắp mối tơ thừa mặc em.</span><br />\r\n<span style=\"color:#000000\">Kể từ khi gặp ch&agrave;ng Kim,</span><br />\r\n<span style=\"color:#000000\">Khi ng&agrave;y quạt ước, khi đ&ecirc;m ch&eacute;n thề.</span><br />\r\n<span style=\"color:#000000\">Sự đ&acirc;u s&oacute;ng gi&oacute; bất kỳ,</span><br />\r\n<span style=\"color:#000000\">730.Hiếu t&igrave;nh kh&ocirc;n lẽ hai bề vẹn hai!</span><br />\r\n<span style=\"color:#000000\">Ng&agrave;y xu&acirc;n em h&atilde;y c&ograve;n d&agrave;i,</span><br />\r\n<span style=\"color:#000000\">X&oacute;t t&igrave;nh m&aacute;u mủ, thay lời nước non.</span><br />\r\n<span style=\"color:#000000\">Chị d&ugrave; thịt n&aacute;t xương m&ograve;n,</span><br />\r\n<span style=\"color:#000000\">Ngậm cười ch&iacute;n suối h&atilde;y c&ograve;n thơm l&acirc;y.</span><br />\r\n<span style=\"color:#000000\">735.Chiếc thoa với bức tờ m&acirc;y,</span><br />\r\n<span style=\"color:#000000\">Duy&ecirc;n n&agrave;y th&igrave; giữ vật n&agrave;y của chung.</span><br />\r\n<span style=\"color:#000000\">D&ugrave; em n&ecirc;n vợ n&ecirc;n chồng,</span><br />\r\n<span style=\"color:#000000\">X&oacute;t người mệnh bạc, ắt l&ograve;ng chẳng qu&ecirc;n.</span><br />\r\n<span style=\"color:#000000\">Mất người c&ograve;n ch&uacute;t của tin,</span><br />\r\n<span style=\"color:#000000\">740.Ph&iacute;m đ&agrave;n với mảnh hương nguyền ng&agrave;y xưa.</span><br />\r\n<span style=\"color:#000000\">Mai sao dầu c&oacute; bao giờ.</span><br />\r\n<span style=\"color:#000000\">Đốt l&ograve; hương ấy, so tơ ph&iacute;m n&agrave;y.</span><br />\r\n<span style=\"color:#000000\">Tr&ocirc;ng ra ngọn cỏ l&aacute; c&acirc;y,</span><br />\r\n<span style=\"color:#000000\">Thấy hiu hiu gi&oacute; th&igrave; hay chị về.</span><br />\r\n<span style=\"color:#000000\">745.Hồn c&ograve;n mang nặng lời thề,</span><br />\r\n<span style=\"color:#000000\">N&aacute;t th&acirc;n bồ liễu, đền ngh&igrave; tr&uacute;c mai;</span><br />\r\n<span style=\"color:#000000\">Dạ đ&agrave;i c&aacute;ch mặt khuất lời,</span><br />\r\n<span style=\"color:#000000\">Rẩy xin ch&eacute;n nước cho người th&aacute;c oan.</span><br />\r\n<span style=\"color:#000000\">B&acirc;y giờ tr&acirc;m gẫy b&igrave;nh tan,</span><br />\r\n<span style=\"color:#000000\">750.Kể l&agrave;m sao xiết mu&ocirc;n v&agrave;n &aacute;i &acirc;n.</span><br />\r\n<span style=\"color:#000000\">Trăm ngh&igrave;n gửi lại t&igrave;nh qu&acirc;n,</span><br />\r\n<span style=\"color:#000000\">Tơ duy&ecirc;n ngắn ngủi c&oacute; ngần ấy th&ocirc;i.</span><br />\r\n<span style=\"color:#000000\">Phận sao phận bạc như v&ocirc;i,</span><br />\r\n<span style=\"color:#000000\">Đ&atilde; đ&agrave;nh nước chẩy hoa tr&ocirc;i lỡ l&agrave;ng.</span><br />\r\n<span style=\"color:#000000\">755.&Ocirc;i Kim lang! Hỡi Kim lang!</span><br />\r\n<span style=\"color:#000000\">Th&ocirc;i th&ocirc;i thiếp đ&atilde; phụ ch&agrave;ng từ đ&acirc;y!</span><br />\r\n<span style=\"color:#000000\">Cạn lời hồn ngất m&aacute;u say,</span><br />\r\n<span style=\"color:#000000\">Một hơi lặng ngắt đ&ocirc;i tay gi&aacute; đồng.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Xu&acirc;n Huy&ecirc;n chợt tỉnh giấc nồng,</span><br />\r\n<span style=\"color:#000000\">760.Một nh&agrave; tấp nập, kẻ trong người ngo&agrave;i.</span><br />\r\n<span style=\"color:#000000\">Kẻ thang người thuốc bời bời,</span><br />\r\n<span style=\"color:#000000\">Mới dằn cơn vựng, chưa phai giọt hồng.</span><br />\r\n<span style=\"color:#000000\">Hỏi: Sao ra sự lạ l&ugrave;ng?</span><br />\r\n<span style=\"color:#000000\">Kiều c&agrave;ng nức nở mở kh&ocirc;ng ra lời.</span><br />\r\n<span style=\"color:#000000\">765.Nỗi n&agrave;ng V&acirc;n mới rỉ tai,</span><br />\r\n<span style=\"color:#000000\">Chiếc thoa n&agrave;y với tờ bồi ở đ&acirc;y..</span><br />\r\n<span style=\"color:#000000\">N&agrave;y cha l&agrave;m lỗi duy&ecirc;n m&agrave;y,</span><br />\r\n<span style=\"color:#000000\">Th&ocirc;i th&igrave; nỗi ấy sau n&agrave;y đ&atilde; em.</span><br />\r\n<span style=\"color:#000000\">V&igrave; ai rụng cải rơi kim,</span><br />\r\n<span style=\"color:#000000\">770.Để con b&egrave;o nổi m&acirc;y ch&igrave;m v&igrave; ai.</span><br />\r\n<span style=\"color:#000000\">Lời con dặn lại một hai,</span><br />\r\n<span style=\"color:#000000\">Dẫu m&ograve;n bia đ&aacute;, d&aacute;m sai tấc v&agrave;ng.</span><br />\r\n<span style=\"color:#000000\">Lậy th&ocirc;i, n&agrave;ng lại thưa chiềng,</span><br />\r\n<span style=\"color:#000000\">Nhờ cha trả được nghĩa ch&agrave;ng cho xu&ocirc;i.</span><br />\r\n<span style=\"color:#000000\">775.S&aacute; chi th&acirc;n phận t&ocirc;i đ&ograve;i,</span><br />\r\n<span style=\"color:#000000\">Dẫu rằng xương trắng qu&ecirc; người quản đ&acirc;u.</span><br />\r\n<span style=\"color:#000000\">Xiết bao kể nỗi thảm sầu!</span><br />\r\n<span style=\"color:#000000\">Khắc canh đ&atilde; giục nam l&acirc;u mấy hồi.</span><br />\r\n<span style=\"color:#000000\">Kiệu hoa đ&acirc;u đ&atilde; đến ngo&agrave;i,</span><br />\r\n<span style=\"color:#000000\">780.Quản huyền đ&acirc;u đ&atilde; giục người sinh ly.</span><br />\r\n<span style=\"color:#000000\">Đau l&ograve;ng kẻ ở người đi,</span><br />\r\n<span style=\"color:#000000\">Lệ rơi thấm đ&aacute; tơ chia rũ tằm.</span><br />\r\n<span style=\"color:#000000\">Trời h&ocirc;m m&acirc;y k&eacute;o tối rầm,</span><br />\r\n<span style=\"color:#000000\">Rầu rầu ngọn cỏ đầm đầm c&agrave;nh sương.</span><br />\r\n<span style=\"color:#000000\">785.Rước n&agrave;ng về đến tr&uacute; phường,</span><br />\r\n<span style=\"color:#000000\">Bốn bề xu&acirc;n kh&oacute;a một n&agrave;ng ở trong.</span><br />\r\n<span style=\"color:#000000\">Ngập ngừng thẹn lục e hồng,</span><br />\r\n<span style=\"color:#000000\">Nghĩ l&ograve;ng lại x&oacute;t xa l&ograve;ng đ&ograve;i phen.</span><br />\r\n<span style=\"color:#000000\">Phẩm ti&ecirc;n rơi đến tay h&egrave;n,</span><br />\r\n<span style=\"color:#000000\">790.Ho&agrave;i c&ocirc;ng nắng giữ mưa g&igrave;n với ai:</span><br />\r\n<span style=\"color:#000000\">Biết th&acirc;n đến bước lạc lo&agrave;i,</span><br />\r\n<span style=\"color:#000000\">Nhị đ&agrave;o th&agrave; bẻ cho người t&igrave;nh chung.</span><br />\r\n<span style=\"color:#000000\">V&igrave; ai ngăn đ&oacute;n gi&oacute; đ&ocirc;ng,</span><br />\r\n<span style=\"color:#000000\">Thiệt l&ograve;ng khi ở đau l&ograve;ng khi đi.</span><br />\r\n<span style=\"color:#000000\">795.Tr&ugrave;ng ph&ugrave;ng dầu họa c&oacute; khi,</span><br />\r\n<span style=\"color:#000000\">Th&acirc;n n&agrave;y th&ocirc;i c&oacute; c&ograve;n g&igrave; m&agrave; mong.</span><br />\r\n<span style=\"color:#000000\">Đ&atilde; sinh ra số long đong,</span><br />\r\n<span style=\"color:#000000\">C&ograve;n mang lấy kiếp m&aacute; hồng được sao?</span><br />\r\n<span style=\"color:#000000\">Tr&ecirc;n y&ecirc;n sẵn c&oacute; con dao,</span><br />\r\n<span style=\"color:#000000\">800.Giấu cầm n&agrave;ng đ&atilde; g&oacute;i v&agrave;o ch&eacute;o khăn:</span><br />\r\n<span style=\"color:#000000\">Ph&ograve;ng khi nước đ&atilde; đến ch&acirc;n,</span><br />\r\n<span style=\"color:#000000\">Dao n&agrave;y th&igrave; liệu với th&acirc;n sau n&agrave;y.</span><br />\r\n<span style=\"color:#000000\">Đ&ecirc;m thu một khắc một chầy,</span><br />\r\n<span style=\"color:#000000\">B&acirc;ng khu&acirc;ng như tỉnh như say một m&igrave;nh.</span></span></span></p>\r\n\r\n<h3 style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\"><u>Kiều Rơi V&agrave;o Tay T&uacute; B&agrave; &amp; M&atilde; Gi&aacute;m Sinh (C&acirc;u 805-1056)</u></span></span></span></h3>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">805.Chẳng ngờ g&atilde; M&atilde; Gi&aacute;m Sinh,</span><br />\r\n<span style=\"color:#000000\">Vẫn l&agrave; một đứa phong t&igrave;nh đ&atilde; quen.</span><br />\r\n<span style=\"color:#000000\">Qu&aacute; chơi lại gặp hồi đen,</span><br />\r\n<span style=\"color:#000000\">Quen m&ugrave;i lại kiếm ăn miền nguyệt hoa.</span><br />\r\n<span style=\"color:#000000\">Lầu xanh c&oacute; mụ T&uacute; B&agrave;,</span><br />\r\n<span style=\"color:#000000\">810.L&agrave;ng chơi đ&atilde; trở về gi&agrave; hết duy&ecirc;n.</span><br />\r\n<span style=\"color:#000000\">T&igrave;nh cờ chẳng hẹn m&agrave; n&ecirc;n,</span><br />\r\n<span style=\"color:#000000\">Mạt cưa mướp đắng đ&ocirc;i b&ecirc;n một phường.</span><br />\r\n<span style=\"color:#000000\">Chung lưng mở một ng&ocirc;i h&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">Quanh năm bu&ocirc;n phấn b&aacute;n hương đ&atilde; lề.</span><br />\r\n<span style=\"color:#000000\">815.Dạo t&igrave;m khắp chợ th&igrave; qu&ecirc;,</span><br />\r\n<span style=\"color:#000000\">Giả danh hầu hạ dạy nghề ăn chơi.</span><br />\r\n<span style=\"color:#000000\">Rủi may &acirc;u cũng tại trời,</span><br />\r\n<span style=\"color:#000000\">Đoạn trường lại chọn mặt người v&ocirc; duy&ecirc;n.</span><br />\r\n<span style=\"color:#000000\">X&oacute;t n&agrave;ng ch&uacute;t phận thuyền quy&ecirc;n,</span><br />\r\n<span style=\"color:#000000\">820.C&agrave;nh hoa đem b&aacute;n v&agrave;o thuyền l&aacute;i bu&ocirc;n.</span><br />\r\n<span style=\"color:#000000\">Mẹo lừa đ&atilde; mắc v&agrave;o khu&ocirc;n,</span><br />\r\n<span style=\"color:#000000\">S&iacute;nh nghi rẻ gi&aacute; ngh&ecirc;nh h&ocirc;n sẵn ng&agrave;y.</span><br />\r\n<span style=\"color:#000000\">Mừng thầm: Cờ đ&atilde; đến tay!</span><br />\r\n<span style=\"color:#000000\">C&agrave;ng nh&igrave;n vẻ ngọc c&agrave;ng say kh&uacute;c v&agrave;ng.</span><br />\r\n<span style=\"color:#000000\">825.Đ&atilde; n&ecirc;n quốc sắc thi&ecirc;n hương,</span><br />\r\n<span style=\"color:#000000\">Một cười n&agrave;y hẳn ngh&igrave;n v&agrave;ng chẳng ngoa.</span><br />\r\n<span style=\"color:#000000\">Về đ&acirc;y nước trước bẻ hoa,</span><br />\r\n<span style=\"color:#000000\">Vương t&ocirc;n qu&yacute; kh&aacute;ch ắt l&agrave; đua nhau.</span><br />\r\n<span style=\"color:#000000\">Hẳn ba trăm lạng k&eacute;m đ&acirc;u,</span><br />\r\n<span style=\"color:#000000\">830.Cũng đ&agrave; vừa vốn c&ograve;n sau th&igrave; lời,</span><br />\r\n<span style=\"color:#000000\">Miếng ngon kề đến tận nơi,</span><br />\r\n<span style=\"color:#000000\">Vốn nh&agrave; cũng tiếc của trời cũng tham.</span><br />\r\n<span style=\"color:#000000\">Đ&agrave;o ti&ecirc;n đ&atilde; b&eacute;n tay ph&agrave;m,</span><br />\r\n<span style=\"color:#000000\">Th&igrave; vin c&agrave;nh qu&iacute;t cho cam sự đời!</span><br />\r\n<span style=\"color:#000000\">835.Dưới trần mấy mặt l&agrave;ng chơi,</span><br />\r\n<span style=\"color:#000000\">Chơi hoa đ&atilde; dễ mấy người biết hoa.</span><br />\r\n<span style=\"color:#000000\">Nước vỏ lựu m&aacute;u m&agrave;o g&agrave;,</span><br />\r\n<span style=\"color:#000000\">Mượn m&agrave;u chi&ecirc;u tập lại l&agrave; c&ograve;n nguy&ecirc;n.</span><br />\r\n<span style=\"color:#000000\">Mập mờ đ&aacute;nh lận con đen,</span><br />\r\n<span style=\"color:#000000\">840.Bao nhi&ecirc;u cũng bấy nhi&ecirc;u tiền mất chi?</span><br />\r\n<span style=\"color:#000000\">Mụ gi&agrave; hoặc c&oacute; điều g&igrave;,</span><br />\r\n<span style=\"color:#000000\">Liều c&ocirc;ng mất một buổi quỳ m&agrave; th&ocirc;i.</span><br />\r\n<span style=\"color:#000000\">Vả đ&acirc;y đường x&aacute; xa x&ocirc;i,</span><br />\r\n<span style=\"color:#000000\">M&agrave; ta bất động nữa người sinh nghi.</span><br />\r\n<span style=\"color:#000000\">845.Tiếc thay một đ&oacute;a tr&agrave; mi,</span><br />\r\n<span style=\"color:#000000\">Con ong đ&atilde; tỏ đường đi lối về.</span><br />\r\n<span style=\"color:#000000\">Một cơn mưa gi&oacute; nặng nề,</span><br />\r\n<span style=\"color:#000000\">Thương g&igrave; đến ngọc tiếc g&igrave; đến hương.</span><br />\r\n<span style=\"color:#000000\">Đ&ecirc;m xu&acirc;n một giấc mơ m&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">850.Đuốc hoa để đ&oacute;, mặc n&agrave;ng nằm trơ.</span><br />\r\n<span style=\"color:#000000\">Giọt ri&ecirc;ng tầm t&atilde; tu&ocirc;n mưa,</span><br />\r\n<span style=\"color:#000000\">Phần căm nỗi kh&aacute;ch phần dơ nỗi m&igrave;nh:</span><br />\r\n<span style=\"color:#000000\">Tuồng chi l&agrave; giống h&ocirc;i tanh,</span><br />\r\n<span style=\"color:#000000\">Th&acirc;n ngh&igrave;n v&agrave;ng để &ocirc; danh m&aacute; hồng.</span><br />\r\n<span style=\"color:#000000\">855.Th&ocirc;i c&ograve;n chi nữa m&agrave; mong?</span><br />\r\n<span style=\"color:#000000\">Đời người th&ocirc;i thế l&agrave; xong một đời.</span><br />\r\n<span style=\"color:#000000\">Giận duy&ecirc;n tủi phận bời bời,</span><br />\r\n<span style=\"color:#000000\">Cầm dao n&agrave;ng đ&atilde; toan b&agrave;i quy&ecirc;n sinh.</span><br />\r\n<span style=\"color:#000000\">Nghĩ đi nghĩ lại một m&igrave;nh:</span><br />\r\n<span style=\"color:#000000\">860.Một m&igrave;nh th&igrave; chớ hai t&igrave;nh th&igrave; sao?</span><br />\r\n<span style=\"color:#000000\">Sao dầu sinh sự thế n&agrave;o,</span><br />\r\n<span style=\"color:#000000\">Truy nguy&ecirc;n chẳng kẻo lụy v&agrave;o song th&acirc;n.</span><br />\r\n<span style=\"color:#000000\">Nỗi m&igrave;nh &acirc;u cũng gi&atilde;n dần,</span><br />\r\n<span style=\"color:#000000\">K&iacute;p chầy th&ocirc;i cũng một lần m&agrave; th&ocirc;i.</span><br />\r\n<span style=\"color:#000000\">865.Những l&agrave; đo đắn ngược xu&ocirc;i,</span><br />\r\n<span style=\"color:#000000\">Tiếng g&agrave; nghe đ&atilde; g&aacute;y s&ocirc;i m&aacute;i tường.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Lầu mai vừa r&uacute;c c&ograve;i sương,</span><br />\r\n<span style=\"color:#000000\">M&atilde; Sinh giục gi&atilde; vội v&agrave;ng ra đi.</span><br />\r\n<span style=\"color:#000000\">Đoạn trường thay l&uacute;c ph&acirc;n kỳ!</span><br />\r\n<span style=\"color:#000000\">870.V&oacute; c&acirc;u khấp khểnh b&oacute;ng xe gập ghềnh.</span><br />\r\n<span style=\"color:#000000\">Bề ngo&agrave;i mười dặm trường đ&igrave;nh,</span><br />\r\n<span style=\"color:#000000\">Vương &ocirc;ng mở tiệc tiễn h&agrave;nh đưa theo.</span><br />\r\n<span style=\"color:#000000\">Ngo&agrave;i th&igrave; chủ kh&aacute;ch dập d&igrave;u,</span><br />\r\n<span style=\"color:#000000\">Một nh&agrave; huy&ecirc;n với một Kiều ở trong.</span><br />\r\n<span style=\"color:#000000\">875.Nh&igrave;n c&agrave;ng l&atilde; ch&atilde; giọt hồng,</span><br />\r\n<span style=\"color:#000000\">Rỉ tai n&agrave;ng mới gi&atilde;i l&ograve;ng thấp cao:</span><br />\r\n<span style=\"color:#000000\">Hổ sinh ra phận thơ đ&agrave;o,</span><br />\r\n<span style=\"color:#000000\">C&ocirc;ng cha nghĩa mẹ kiếp n&agrave;o trả xong?</span><br />\r\n<span style=\"color:#000000\">Lỡ l&agrave;ng nước đục bụi trong,</span><br />\r\n<span style=\"color:#000000\">880.Trăm năm để một tấm l&ograve;ng từ đ&acirc;y.</span><br />\r\n<span style=\"color:#000000\">Xem gương trong bấy nhi&ecirc;u ng&agrave;y,</span><br />\r\n<span style=\"color:#000000\">Th&acirc;n con chẳng kẻo mắc tay bợm gi&agrave;!</span><br />\r\n<span style=\"color:#000000\">Khi về bỏ vắng trong nh&agrave;,</span><br />\r\n<span style=\"color:#000000\">Khi v&agrave;o d&ugrave;ng dắng khi ra vội v&agrave;ng.</span><br />\r\n<span style=\"color:#000000\">885.Khi ăn khi n&oacute;i lỡ l&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">Khi thầy khi tớ xem thường xem khinh.</span><br />\r\n<span style=\"color:#000000\">Kh&aacute;c m&agrave;u kẻ qu&yacute; người thanh,</span><br />\r\n<span style=\"color:#000000\">Ngẫm ra cho kỹ như h&igrave;nh con bu&ocirc;n.</span><br />\r\n<span style=\"color:#000000\">Th&ocirc;i con c&ograve;n n&oacute;i chi con?</span><br />\r\n<span style=\"color:#000000\">890.Sống nhờ đất kh&aacute;ch th&aacute;c ch&ocirc;n qu&ecirc; người!</span><br />\r\n<span style=\"color:#000000\">Vương b&agrave; nghe bấy nhi&ecirc;u lời,</span><br />\r\n<span style=\"color:#000000\">Tiếng oan đ&atilde; muốn vạch trời k&ecirc;u l&ecirc;n.</span><br />\r\n<span style=\"color:#000000\">V&agrave;i tuần chưa cạn ch&eacute;n khuy&ecirc;n.</span><br />\r\n<span style=\"color:#000000\">M&aacute;i ngo&agrave;i nghỉ đ&atilde; giục liền ruổi xe.</span><br />\r\n<span style=\"color:#000000\">895.X&oacute;t con l&ograve;ng nặng tr&igrave; tr&igrave;,</span><br />\r\n<span style=\"color:#000000\">Trước y&ecirc;n &ocirc;ng đ&atilde; nằn n&igrave; thấp cao:</span><br />\r\n<span style=\"color:#000000\">Ch&uacute;t th&acirc;n yếu liễu tơ đ&agrave;o,</span><br />\r\n<span style=\"color:#000000\">Rớp nh&agrave; đến nỗi giấn v&agrave;o t&ocirc;i ngươi.</span><br />\r\n<span style=\"color:#000000\">Từ đ&acirc;y g&oacute;c bể b&ecirc;n trời,</span><br />\r\n<span style=\"color:#000000\">900.Nắng mưa thui thủi qu&ecirc; người một th&acirc;n.</span><br />\r\n<span style=\"color:#000000\">Ngh&igrave;n tầm nhờ b&oacute;ng t&ugrave;ng qu&acirc;n,</span><br />\r\n<span style=\"color:#000000\">Tuyết sương che chở cho th&acirc;n c&aacute;t đằng.</span><br />\r\n<span style=\"color:#000000\">Cạn lời kh&aacute;ch mới thưa rằng:</span><br />\r\n<span style=\"color:#000000\">Buộc ch&acirc;n th&ocirc;i cũng x&iacute;ch thằng nhiệm trao.</span><br />\r\n<span style=\"color:#000000\">905.Mai sau dầu đến thế n&agrave;o,</span><br />\r\n<span style=\"color:#000000\">K&igrave;a gương nhật nguyệt nọ dao quỉ thần!</span><br />\r\n<span style=\"color:#000000\">Đ&ugrave;ng đ&ugrave;ng gi&oacute; giục m&acirc;y vần,</span><br />\r\n<span style=\"color:#000000\">Một xe trong c&otilde;i hồng trần như bay.</span><br />\r\n<span style=\"color:#000000\">Tr&ocirc;ng vời gạt lệ chia tay,</span><br />\r\n<span style=\"color:#000000\">910.G&oacute;c trời thăm thẳm đ&ecirc;m ng&agrave;y đăm đăm.</span><br />\r\n<span style=\"color:#000000\">N&agrave;ng th&igrave; dặm kh&aacute;ch xa xăm,</span><br />\r\n<span style=\"color:#000000\">Bạc phau cầu gi&aacute; đen rầm ng&agrave;n m&acirc;y.</span><br />\r\n<span style=\"color:#000000\">Vi l&ocirc; san s&aacute;t hơi may,</span><br />\r\n<span style=\"color:#000000\">Một trời thu để ri&ecirc;ng ai một người.</span><br />\r\n<span style=\"color:#000000\">915.Dặm khuya ngất tạnh m&ugrave; khơi,</span><br />\r\n<span style=\"color:#000000\">Thấy trăng m&agrave; thẹn những lời non s&ocirc;ng.</span><br />\r\n<span style=\"color:#000000\">Rừng thu từng biếc xen hồng,</span><br />\r\n<span style=\"color:#000000\">Nghe chim như nhắc tấm l&ograve;ng thần h&ocirc;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Những l&agrave; lạ nước lạ non,</span><br />\r\n<span style=\"color:#000000\">920.L&acirc;m Truy vừa một th&aacute;ng tr&ograve;n tới nơi.</span><br />\r\n<span style=\"color:#000000\">Xe ch&acirc;u dừng b&aacute;nh cửa ngo&agrave;i,</span><br />\r\n<span style=\"color:#000000\">R&egrave;m trong đ&atilde; thấy một người bước ra.</span><br />\r\n<span style=\"color:#000000\">Thoắt tr&ocirc;ng nhờn nhợt m&agrave;u da,</span><br />\r\n<span style=\"color:#000000\">Ăn g&igrave; cao lớn đẫy đ&agrave; l&agrave;m sao!</span><br />\r\n<span style=\"color:#000000\">925.Trước xe lơi lả han ch&agrave;o,</span><br />\r\n<span style=\"color:#000000\">V&acirc;ng lời n&agrave;ng mới bước v&agrave;o tận nơi.</span><br />\r\n<span style=\"color:#000000\">B&ecirc;n th&igrave; mấy ả m&agrave;y ng&agrave;i,</span><br />\r\n<span style=\"color:#000000\">B&ecirc;n th&igrave; ngồi bốn năm người l&agrave;ng chơi.</span><br />\r\n<span style=\"color:#000000\">Giữa th&igrave; hương &aacute;n hẳn hoi,</span><br />\r\n<span style=\"color:#000000\">930.Tr&ecirc;n treo một tượng trắng đ&ocirc;i l&ocirc;ng m&agrave;y.</span><br />\r\n<span style=\"color:#000000\">Lầu xanh quen lối xưa nay,</span><br />\r\n<span style=\"color:#000000\">Nghề n&agrave;y th&igrave; lấy &ocirc;ng n&agrave;y ti&ecirc;n sư,</span><br />\r\n<span style=\"color:#000000\">Hương h&ocirc;m hoa sớm phụng thờ.</span><br />\r\n<span style=\"color:#000000\">C&ocirc; n&agrave;o xấu v&iacute;a c&oacute; thưa mối h&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">935.Cởi xi&ecirc;m lột &aacute;o sỗ s&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">Trước thần sẽ nguyện mảnh hương lầm rầm.</span><br />\r\n<span style=\"color:#000000\">Đổi hoa l&oacute;t xuống chiếu nằm,</span><br />\r\n<span style=\"color:#000000\">Bướm hoa bay lại ầm ầm tứ vi!</span><br />\r\n<span style=\"color:#000000\">Kiều c&ograve;n ngơ ngẩn biết g&igrave;,</span><br />\r\n<span style=\"color:#000000\">940.Cứ lời lạy xuống mụ th&igrave; khấn ngay:</span><br />\r\n<span style=\"color:#000000\">Cửa h&agrave;ng bu&ocirc;n b&aacute;n cho may,</span><br />\r\n<span style=\"color:#000000\">Đ&ecirc;m đ&ecirc;m H&agrave;n thực ng&agrave;y ng&agrave;y Nguy&ecirc;n ti&ecirc;u.</span><br />\r\n<span style=\"color:#000000\">Mu&ocirc;n ngh&igrave;n người thấy cũng y&ecirc;u,</span><br />\r\n<span style=\"color:#000000\">X&ocirc;n xao oanh yến rập r&igrave;u tr&uacute;c mai.</span><br />\r\n<span style=\"color:#000000\">945.Tin nhạn vẩn l&aacute; thư b&agrave;i,</span><br />\r\n<span style=\"color:#000000\">Đưa người cửa trước rước người cửa sau.</span><br />\r\n<span style=\"color:#000000\">Lạ tai nghe chửa biết đ&acirc;u,</span><br />\r\n<span style=\"color:#000000\">Xem t&igrave;nh ra cũng những m&agrave;u dở dang.</span><br />\r\n<span style=\"color:#000000\">Lễ xong hương hỏa gia đường,</span><br />\r\n<span style=\"color:#000000\">950.T&uacute; B&agrave; vắt n&oacute;c l&ecirc;n giường ngồi ngay.</span><br />\r\n<span style=\"color:#000000\">Dạy rằng: Con lạy mẹ đ&acirc;y,</span><br />\r\n<span style=\"color:#000000\">Lạy rồi sang lạy cậu m&agrave;y b&ecirc;n kia.</span><br />\r\n<span style=\"color:#000000\">N&agrave;ng rằng: Phải bước lưu ly,</span><br />\r\n<span style=\"color:#000000\">Phận h&egrave;n v&acirc;ng đ&atilde; cam bề tiểu tinh.</span><br />\r\n<span style=\"color:#000000\">955.Điều đ&acirc;u lấy yến l&agrave;m oanh,</span><br />\r\n<span style=\"color:#000000\">Ng&acirc;y thơ chẳng biết l&agrave; danh phận g&igrave;?</span><br />\r\n<span style=\"color:#000000\">Đủ điều nạp th&aacute;i vu qui,</span><br />\r\n<span style=\"color:#000000\">Đ&atilde; khi chung chạ lại khi đứng ngồi.</span><br />\r\n<span style=\"color:#000000\">Giờ ra thay mặt đổi ng&ocirc;i,</span><br />\r\n<span style=\"color:#000000\">960.D&aacute;m xin gửi lại một lời cho minh.</span><br />\r\n<span style=\"color:#000000\">Mụ nghe n&agrave;ng n&oacute;i hay t&igrave;nh,</span><br />\r\n<span style=\"color:#000000\">Bấy giờ mới nổi tam b&agrave;nh mụ l&ecirc;n:</span><br />\r\n<span style=\"color:#000000\">N&agrave;y n&agrave;y sự đ&atilde; quả nhi&ecirc;n,</span><br />\r\n<span style=\"color:#000000\">Th&ocirc;i đ&agrave; cướp sống chồng min đi rồi.</span><br />\r\n<span style=\"color:#000000\">965.Bảo rằng đi dạo lấy người,</span><br />\r\n<span style=\"color:#000000\">Đem về rước kh&aacute;ch kiếm lời m&agrave; ăn.</span><br />\r\n<span style=\"color:#000000\">Tuồng v&ocirc; nghĩa ở bất nh&acirc;n,</span><br />\r\n<span style=\"color:#000000\">Buồn m&igrave;nh trước đ&atilde; tần mần thử chơi.</span><br />\r\n<span style=\"color:#000000\">M&agrave;u hồ đ&atilde; mất đi rồi,</span><br />\r\n<span style=\"color:#000000\">970.Th&ocirc;i th&ocirc;i vốn liếng đi đời nh&agrave; ma!</span><br />\r\n<span style=\"color:#000000\">Con kia đ&atilde; b&aacute;n cho ta,</span><br />\r\n<span style=\"color:#000000\">Nhập ra phải cứ ph&eacute;p nh&agrave; tao đ&acirc;y.</span><br />\r\n<span style=\"color:#000000\">L&atilde;o kia c&oacute; giở b&agrave;i b&acirc;y,</span><br />\r\n<span style=\"color:#000000\">Chẳng văng v&agrave;o mặt m&agrave; m&agrave;y lại nghe.</span><br />\r\n<span style=\"color:#000000\">975.Cớ sao chịu tốt một bề,</span><br />\r\n<span style=\"color:#000000\">G&aacute;i tơ m&agrave; đ&atilde; ngứa nghề sớm sao?</span><br />\r\n<span style=\"color:#000000\">Phải l&agrave;m cho biết ph&eacute;p tao!</span><br />\r\n<span style=\"color:#000000\">Chập b&igrave; ti&ecirc;n rắp sấn v&agrave;o ra tay.</span><br />\r\n<span style=\"color:#000000\">N&agrave;ng rằng: Trời thẳm đất d&agrave;y!</span><br />\r\n<span style=\"color:#000000\">980.Th&acirc;n n&agrave;y đ&atilde; bỏ những ng&agrave;y ra đi.</span><br />\r\n<span style=\"color:#000000\">Th&ocirc;i th&igrave; th&ocirc;i c&oacute; tiếc g&igrave;!</span><br />\r\n<span style=\"color:#000000\">Sẵn dao tay &aacute;o tức th&igrave; giở ra.</span><br />\r\n<span style=\"color:#000000\">Sợ gan n&aacute;t ngọc liều hoa!</span><br />\r\n<span style=\"color:#000000\">Mụ c&ograve;n tr&ocirc;ng mặt n&agrave;ng đ&agrave; qu&aacute; tay.</span><br />\r\n<span style=\"color:#000000\">985.Thương &ocirc;i t&agrave;i sắc bậc n&agrave;y,</span><br />\r\n<span style=\"color:#000000\">Một dao oan nghiệt đứt d&acirc;y phong trần.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Nỗi oan vỡ lở xa gần,</span><br />\r\n<span style=\"color:#000000\">Trong nh&agrave; người chật một lần như n&ecirc;m.</span><br />\r\n<span style=\"color:#000000\">N&agrave;ng th&igrave; bằn bặt giấc ti&ecirc;n,</span><br />\r\n<span style=\"color:#000000\">990.Mụ th&igrave; cầm cập mặt nh&igrave;n hồn bay.</span><br />\r\n<span style=\"color:#000000\">Vực n&agrave;ng v&agrave;o chốn hi&ecirc;n t&acirc;y,</span><br />\r\n<span style=\"color:#000000\">Cắt người coi s&oacute;c chạy thầy thuốc thang.</span><br />\r\n<span style=\"color:#000000\">N&agrave;o hay chưa hết trần duy&ecirc;n,</span><br />\r\n<span style=\"color:#000000\">Trong m&ecirc; dường đ&atilde; đứng b&ecirc;n một n&agrave;ng.</span><br />\r\n<span style=\"color:#000000\">995.Rỉ rằng: Nh&acirc;n quả dở dang,</span><br />\r\n<span style=\"color:#000000\">Đ&atilde; toan trốn nợ đoạn trường được sao?</span><br />\r\n<span style=\"color:#000000\">Số c&ograve;n nặng nợ m&aacute; đ&agrave;o,</span><br />\r\n<span style=\"color:#000000\">Người dầu muốn quyết trời n&agrave;o đ&atilde; cho.</span><br />\r\n<span style=\"color:#000000\">H&atilde;y xin hết kiếp liễu bồ,</span><br />\r\n<span style=\"color:#000000\">1000.S&ocirc;ng Tiền đường sẽ hẹn h&ograve; về sau.</span><br />\r\n<span style=\"color:#000000\">Thuốc thang suốt một ng&agrave;y th&acirc;u,</span><br />\r\n<span style=\"color:#000000\">Giấc m&ecirc; nghe đ&atilde; d&agrave;u d&agrave;u vừa tan.</span><br />\r\n<span style=\"color:#000000\">T&uacute; b&agrave; chực sẵn b&ecirc;n m&agrave;n,</span><br />\r\n<span style=\"color:#000000\">Lựa lời khuy&ecirc;n giải mơn man gỡ dần:</span><br />\r\n<span style=\"color:#000000\">1005.Một người dễ c&oacute; mấy th&acirc;n!</span><br />\r\n<span style=\"color:#000000\">Hoa xu&acirc;n đương nhụy, ng&agrave;y xu&acirc;n c&ograve;n d&agrave;i.</span><br />\r\n<span style=\"color:#000000\">Cũng l&agrave; lỡ một lầm hai,</span><br />\r\n<span style=\"color:#000000\">Đ&aacute; v&agrave;ng sao nỡ &eacute;p n&agrave;i mưa m&acirc;y!</span><br />\r\n<span style=\"color:#000000\">Lỡ chưn tr&oacute;t đ&atilde; v&agrave;o đ&acirc;y,</span><br />\r\n<span style=\"color:#000000\">1010.Kh&oacute;a buồng xu&acirc;n để đợi ng&agrave;y đ&agrave;o non.</span><br />\r\n<span style=\"color:#000000\">Người c&ograve;n th&igrave; của h&atilde;y c&ograve;n,</span><br />\r\n<span style=\"color:#000000\">T&igrave;m nơi xứng đ&aacute;ng l&agrave; con c&aacute;i nh&agrave;.</span><br />\r\n<span style=\"color:#000000\">L&agrave;m chi tội b&aacute;o o&aacute;n gia,</span><br />\r\n<span style=\"color:#000000\">Thiệt m&igrave;nh m&agrave; hại đến ta hay g&igrave;?</span><br />\r\n<span style=\"color:#000000\">1015.Kề tai mấy mỗi nằn n&igrave;,</span><br />\r\n<span style=\"color:#000000\">N&agrave;ng nghe dường cũng thị phi rạch r&ograve;i.</span><br />\r\n<span style=\"color:#000000\">Vả suy thần mộng mấy lời,</span><br />\r\n<span style=\"color:#000000\">T&uacute;c nh&acirc;n &acirc;u cũng c&oacute; trời ở trong.</span><br />\r\n<span style=\"color:#000000\">Kiếp n&agrave;y nợ trả chưa xong,</span><br />\r\n<span style=\"color:#000000\">1020.L&agrave;m chi th&ecirc;m một nợ chồng kiếp sau!</span><br />\r\n<span style=\"color:#000000\">Lặng nghe, thấm th&iacute;a g&oacute;t đầu,</span><br />\r\n<span style=\"color:#000000\">Thưa rằng: Ai c&oacute; muốn đ&acirc;u thế n&agrave;y?</span><br />\r\n<span style=\"color:#000000\">Được như lời, thế l&agrave; may,</span><br />\r\n<span style=\"color:#000000\">Hẳn rằng mai c&oacute; như r&agrave;y cho chăng!</span><br />\r\n<span style=\"color:#000000\">1025.Sợ khi ong bướm đ&atilde;i đằng,</span><br />\r\n<span style=\"color:#000000\">Đến điều sống đục, sao bằng th&aacute;c trong!</span><br />\r\n<span style=\"color:#000000\">Mụ rằng: Con h&atilde;y thong dong,</span><br />\r\n<span style=\"color:#000000\">Phải điều l&ograve;ng lại dối l&ograve;ng m&agrave; chơi!</span><br />\r\n<span style=\"color:#000000\">Mai sau ở chẳng như lời,</span><br />\r\n<span style=\"color:#000000\">1030.Tr&ecirc;n đầu c&oacute; b&oacute;ng mặt trời rạng soi.</span><br />\r\n<span style=\"color:#000000\">Thấy lời quyết đo&aacute;n hẳn hoi,</span><br />\r\n<span style=\"color:#000000\">Đ&agrave;nh l&ograve;ng, n&agrave;ng cũng sẽ ngu&ocirc;i ngu&ocirc;i dần.</span><br />\r\n<span style=\"color:#000000\">Trước lầu Ngưng B&iacute;ch kh&oacute;a xu&acirc;n,</span><br />\r\n<span style=\"color:#000000\">Vẻ non xa, tấm trăng gần, ở chung.</span><br />\r\n<span style=\"color:#000000\">1035.Bốn bề b&aacute;t ng&aacute;t xa tr&ocirc;ng,</span><br />\r\n<span style=\"color:#000000\">C&aacute;t v&agrave;ng cồn nọ, bụi hồng dặm kia.</span><br />\r\n<span style=\"color:#000000\">Bẽ b&agrave;ng m&acirc;y sớm đ&egrave;n khuya,</span><br />\r\n<span style=\"color:#000000\">Nửa t&igrave;nh, nửa cảnh như chia tấm l&ograve;ng.</span><br />\r\n<span style=\"color:#000000\">Tưởng người dưới nguyệt ch&eacute;n đồng,</span><br />\r\n<span style=\"color:#000000\">1040.Tin sương luống những r&agrave;y tr&ocirc;ng mai chờ.</span><br />\r\n<span style=\"color:#000000\">B&ecirc;n trời g&oacute;c bể bơ vơ,</span><br />\r\n<span style=\"color:#000000\">Tấm son gột rửa bao giờ cho phai.</span><br />\r\n<span style=\"color:#000000\">X&oacute;t người tựa cửa h&ocirc;m mai,</span><br />\r\n<span style=\"color:#000000\">Quạt nồng ấp lạnh, những ai đ&oacute; giờ?</span><br />\r\n<span style=\"color:#000000\">1045.S&acirc;n Lai c&aacute;ch mấy nắng mưa,</span><br />\r\n<span style=\"color:#000000\">C&oacute; khi gốc tử đ&atilde; vừa người &ocirc;m?</span><br />\r\n<span style=\"color:#000000\">Buồn tr&ocirc;ng cửa bể chiều h&ocirc;m,</span><br />\r\n<span style=\"color:#000000\">Thuyền ai thấp tho&aacute;ng c&aacute;nh buồm xa xa?</span><br />\r\n<span style=\"color:#000000\">Buồn tr&ocirc;ng ngọn nước mới sa,</span><br />\r\n<span style=\"color:#000000\">1050.Hoa tr&ocirc;i man m&aacute;c, biết l&agrave; về đ&acirc;u?</span><br />\r\n<span style=\"color:#000000\">Buồn tr&ocirc;ng nội cỏ d&agrave;u d&agrave;u,</span><br />\r\n<span style=\"color:#000000\">Ch&acirc;n m&acirc;y mặt đất một m&agrave;u xanh xanh.</span><br />\r\n<span style=\"color:#000000\">Buồn tr&ocirc;ng gi&oacute; cuốn mặt ghềnh</span><br />\r\n<span style=\"color:#000000\">Ầm ầm tiếng s&oacute;ng k&ecirc;u quanh ghế ngồị</span><br />\r\n<span style=\"color:#000000\">1055.Chung quanh những nước non người,</span><br />\r\n<span style=\"color:#000000\">Đau l&ograve;ng lưu lạc, n&ecirc;n v&agrave;i bốn c&acirc;u.</span></span></span></p>\r\n\r\n<h3 style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\"><strong><u>Kiều Mắc Lừa Sở Khanh (C&acirc;u 1057-1274)</u></strong></span></span></span></h3>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Ngậm ng&ugrave;i rủ bước r&egrave;m ch&acirc;u,</span><br />\r\n<span style=\"color:#000000\">C&aacute;ch tường, nghe c&oacute; tiếng đ&acirc;u họa vần.</span><br />\r\n<span style=\"color:#000000\">Một ch&agrave;ng vừa trạc thanh xu&acirc;n,</span><br />\r\n<span style=\"color:#000000\">1060.H&igrave;nh dong chải chuốt, &aacute;o khăn dịu d&agrave;ng.</span><br />\r\n<span style=\"color:#000000\">Nghĩ rằng cũng mạch thư hương,</span><br />\r\n<span style=\"color:#000000\">Hỏi ra mới biết rằng ch&agrave;ng Sở Khanh.</span><br />\r\n<span style=\"color:#000000\">B&oacute;ng Nga thấp tho&aacute;ng dưới m&agrave;nh,</span><br />\r\n<span style=\"color:#000000\">Tr&ocirc;ng n&agrave;ng, ch&agrave;ng cũng ra t&igrave;nh đeo đai.</span><br />\r\n<span style=\"color:#000000\">1065.Than &ocirc;i! sắc nước hương trời,</span><br />\r\n<span style=\"color:#000000\">Tiếc cho đ&acirc;u bỗng lạc lo&agrave;i đến đ&acirc;y?</span><br />\r\n<span style=\"color:#000000\">Gi&aacute; đ&agrave;nh trong nguyệt tr&ecirc;n m&acirc;y,</span><br />\r\n<span style=\"color:#000000\">Hoa sao, hoa kh&eacute;o đọa đ&agrave;y bấy hoa?</span><br />\r\n<span style=\"color:#000000\">Tức gan ri&ecirc;ng giận trời gi&agrave;,</span><br />\r\n<span style=\"color:#000000\">1070.L&ograve;ng n&agrave;y ai tỏ cho ta, hỡi l&ograve;ng?</span><br />\r\n<span style=\"color:#000000\">Thuyền quy&ecirc;n v&iacute; biết anh h&ugrave;ng,</span><br />\r\n<span style=\"color:#000000\">Ra tay th&aacute;o cũi, sổ lồng như chơi!</span><br />\r\n<span style=\"color:#000000\">Song thu đ&atilde; kh&eacute;p c&aacute;nh ngo&agrave;i,</span><br />\r\n<span style=\"color:#000000\">Tai c&ograve;n đồng vọng mấy lời sắt đanh.</span><br />\r\n<span style=\"color:#000000\">1075.Nghĩ người th&ocirc;i lại nghĩ m&igrave;nh,</span><br />\r\n<span style=\"color:#000000\">C&aacute;m l&ograve;ng chua x&oacute;t, nhạt t&igrave;nh chơ vơ.</span><br />\r\n<span style=\"color:#000000\">Những l&agrave; lần lữa nắng mưa,</span><br />\r\n<span style=\"color:#000000\">Kiếp phong trần biết bao giờ mới th&ocirc;i?</span><br />\r\n<span style=\"color:#000000\">Đ&aacute;nh liều nhắn một hai lời,</span><br />\r\n<span style=\"color:#000000\">1080.Nhờ tay tế độ vớt người trầm lu&acirc;n.</span><br />\r\n<span style=\"color:#000000\">Mảnh ti&ecirc;n kể hết xa gần,</span><br />\r\n<span style=\"color:#000000\">Nỗi nh&agrave; b&aacute;o đ&aacute;p, nỗi th&acirc;n lạc lo&agrave;i.</span><br />\r\n<span style=\"color:#000000\">Tan sương vừa rạng ng&agrave;y mai,</span><br />\r\n<span style=\"color:#000000\">Tiện hồng n&agrave;ng mới nhắn lời gửi sang.</span><br />\r\n<span style=\"color:#000000\">1085.Trời t&acirc;y l&atilde;ng đ&atilde;ng b&oacute;ng v&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">Ph&uacute;c thư đ&atilde; thấy tin ch&agrave;ng đến nơi.</span><br />\r\n<span style=\"color:#000000\">Mở xem một bức ti&ecirc;n mai,</span><br />\r\n<span style=\"color:#000000\">R&agrave;nh r&agrave;nh t&iacute;ch việt c&oacute; hai chữ đề.</span><br />\r\n<span style=\"color:#000000\">Lấy trong &yacute; tứ m&agrave; suy:</span><br />\r\n<span style=\"color:#000000\">1090.Ng&agrave;y hai mươi mốt, tuất th&igrave; phải chăng?</span><br />\r\n<span style=\"color:#000000\">Chim h&ocirc;m thoi th&oacute;t về rừng,</span><br />\r\n<span style=\"color:#000000\">Đ&oacute;a tr&agrave; mi đ&atilde; ngậm trăng nửa v&agrave;nh.</span><br />\r\n<span style=\"color:#000000\">Tường đ&ocirc;ng lay động b&oacute;ng c&agrave;nh,</span><br />\r\n<span style=\"color:#000000\">Rẽ song, đ&atilde; thấy Sở Khanh lẻn v&agrave;o.</span><br />\r\n<span style=\"color:#000000\">1095.Sượng s&ugrave;ng đ&aacute;nh dạn ra ch&agrave;o,</span><br />\r\n<span style=\"color:#000000\">Lạy th&ocirc;i, n&agrave;ng mới rỉ tai &acirc;n cần.</span><br />\r\n<span style=\"color:#000000\">Rằng: T&ocirc;i b&egrave;o bọt ch&uacute;t th&acirc;n,</span><br />\r\n<span style=\"color:#000000\">Lạc đ&agrave;n mang lấy nợ nần yến anh.</span><br />\r\n<span style=\"color:#000000\">D&aacute;m nhờ cốt nhục tử sinh,</span><br />\r\n<span style=\"color:#000000\">1100.C&ograve;n nhiều kết cỏ ngậm v&agrave;nh về sau!</span><br />\r\n<span style=\"color:#000000\">Lặng nghe, lẩm nhẩm gật đầu:</span><br />\r\n<span style=\"color:#000000\">Ta đ&acirc;y phải mượn ai đ&acirc;u m&agrave; rằng!</span><br />\r\n<span style=\"color:#000000\">N&agrave;ng đ&agrave; biết đến ta chăng,</span><br />\r\n<span style=\"color:#000000\">Bể trầm lu&acirc;n, lấp cho bằng mới th&ocirc;i!</span><br />\r\n<span style=\"color:#000000\">1105.N&agrave;ng rằng: Mu&ocirc;n sự ơn người,</span><br />\r\n<span style=\"color:#000000\">Thế n&agrave;o xin quyết một b&agrave;i cho xong.</span><br />\r\n<span style=\"color:#000000\">Rằng: Ta c&oacute; ngựa truy phong,</span><br />\r\n<span style=\"color:#000000\">C&oacute; t&ecirc;n dưới trướng, vốn d&ograve;ng kiện nhi.</span><br />\r\n<span style=\"color:#000000\">Thừa cơ lẻn bước ra đi,</span><br />\r\n<span style=\"color:#000000\">1110.Ba mươi s&aacute;u chước, chước g&igrave; l&agrave; hơn.</span><br />\r\n<span style=\"color:#000000\">D&ugrave; khi gi&oacute; k&eacute;p, mưa đơn,</span><br />\r\n<span style=\"color:#000000\">C&oacute; ta đ&acirc;y cũng chẳng cơn cớ g&igrave;!</span><br />\r\n<span style=\"color:#000000\">Nghe lời n&agrave;ng đ&atilde; sinh nghi,</span><br />\r\n<span style=\"color:#000000\">Song đ&agrave; qu&aacute; đỗi, quản g&igrave; được th&acirc;n.</span><br />\r\n<span style=\"color:#000000\">1115.Cũng liều nhắm mắt đưa ch&acirc;n,</span><br />\r\n<span style=\"color:#000000\">M&agrave; xem con Tạo xoay vần đến đ&acirc;u!</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">C&ugrave;ng nhau lẻn bước xuống lầu,</span><br />\r\n<span style=\"color:#000000\">Song song ngựa trước, ngựa sau một đo&agrave;n.</span><br />\r\n<span style=\"color:#000000\">Đ&ecirc;m th&acirc;u khắc lậu canh t&agrave;n,</span><br />\r\n<span style=\"color:#000000\">1120.Gi&oacute; c&acirc;y tr&uacute;t l&aacute;, trăng ng&agrave;n ngậm gương.</span><br />\r\n<span style=\"color:#000000\">Lối m&ograve;n cỏ nhợt m&ugrave; sương,</span><br />\r\n<span style=\"color:#000000\">L&ograve;ng qu&ecirc; đi một bước đường, một đau.</span><br />\r\n<span style=\"color:#000000\">Tiếng g&agrave; xao x&aacute;c g&aacute;y mau,</span><br />\r\n<span style=\"color:#000000\">Tiếng người đ&acirc;u đ&atilde; m&aacute;i sau dậy d&agrave;ng.</span><br />\r\n<span style=\"color:#000000\">N&agrave;ng c&agrave;ng thổn thức gan v&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">Sở Khanh đ&atilde; rẽ d&acirc;y cương lối n&agrave;o!</span><br />\r\n<span style=\"color:#000000\">Một m&igrave;nh kh&ocirc;n biết l&agrave;m sao,</span><br />\r\n<span style=\"color:#000000\">Dặm rừng bước thấp, bước cao h&atilde;i h&ugrave;ng.</span><br />\r\n<span style=\"color:#000000\">H&oacute;a nhi thật c&oacute; nỡ l&ograve;ng,</span><br />\r\n<span style=\"color:#000000\">1130.L&agrave;m chi d&agrave;y t&iacute;a, v&ograve; hồng, lắm nau!</span><br />\r\n<span style=\"color:#000000\">Một đo&agrave;n đổ đến trước sau,</span><br />\r\n<span style=\"color:#000000\">Vuốt đ&acirc;u xuống đất, c&aacute;nh đ&acirc;u l&ecirc;n trời.</span><br />\r\n<span style=\"color:#000000\">T&uacute; b&agrave; tốc thẳng đến nơi,</span><br />\r\n<span style=\"color:#000000\">Hầm hầm &aacute;p điệu một hơi lại nh&agrave;.</span><br />\r\n<span style=\"color:#000000\">1135.Hung hăng chẳng n&oacute;i chẳng tra,</span><br />\r\n<span style=\"color:#000000\">Đang tay v&ugrave;i liễu, giập hoa tơi bời.</span><br />\r\n<span style=\"color:#000000\">Thịt da ai cũng l&agrave; người,</span><br />\r\n<span style=\"color:#000000\">L&ograve;ng n&agrave;o hồng rụng, thắm rời chẳng đau.</span><br />\r\n<span style=\"color:#000000\">Hết lời th&uacute; phục, khẩn cầu,</span><br />\r\n<span style=\"color:#000000\">1140.Uốn lưng thịt đổ, cất đầu m&aacute;u sa.</span><br />\r\n<span style=\"color:#000000\">Rằng: T&ocirc;i ch&uacute;t phận đ&agrave;n b&agrave;,</span><br />\r\n<span style=\"color:#000000\">Nước non l&igrave;a cửa, l&igrave;a nh&agrave;, đến đ&acirc;y.</span><br />\r\n<span style=\"color:#000000\">B&acirc;y giờ sống chết ở tay,</span><br />\r\n<span style=\"color:#000000\">Th&acirc;n n&agrave;y đ&atilde; đến thế n&agrave;y th&igrave; th&ocirc;i!</span><br />\r\n<span style=\"color:#000000\">1145.Nhưng t&ocirc;i c&oacute; s&aacute; chi t&ocirc;i,</span><br />\r\n<span style=\"color:#000000\">Phận t&ocirc;i đ&agrave;nh vậy, vốn người để đ&acirc;u?</span><br />\r\n<span style=\"color:#000000\">Th&acirc;n lươn bao quản lấm đầu,</span><br />\r\n<span style=\"color:#000000\">Ch&uacute;t l&ograve;ng trinh bạch từ sau xin chừa!</span><br />\r\n<span style=\"color:#000000\">Được lời mụ mới t&ugrave;y cơ,</span><br />\r\n<span style=\"color:#000000\">1150.Bắt người bảo lĩnh l&agrave;m tờ cung chi&ecirc;u.</span><br />\r\n<span style=\"color:#000000\">B&agrave;y vai c&oacute; ả M&atilde; Kiều,</span><br />\r\n<span style=\"color:#000000\">X&oacute;t n&agrave;ng, ra mới đ&aacute;nh liều chịu đoan.</span><br />\r\n<span style=\"color:#000000\">Mụ c&agrave;ng kể nhặt, kể khoan,</span><br />\r\n<span style=\"color:#000000\">Gạn g&ugrave;ng đến mực, nồng n&agrave;n mới tha.</span><br />\r\n<span style=\"color:#000000\">1155.Vực n&agrave;ng v&agrave;o nghỉ trong nh&agrave;,</span><br />\r\n<span style=\"color:#000000\">M&atilde; Kiều lại ngỏ &yacute; ra dặn lời:</span><br />\r\n<span style=\"color:#000000\">Th&ocirc;i đ&agrave; mắc lận th&igrave; th&ocirc;i!</span><br />\r\n<span style=\"color:#000000\">Đi đ&acirc;u chẳng biết con người Sở Khanh?</span><br />\r\n<span style=\"color:#000000\">Bạc t&igrave;nh, nổi tiếng lầu xanh,</span><br />\r\n<span style=\"color:#000000\">1160.Một tay ch&ocirc;n biết mấy c&agrave;nh ph&ugrave; dung!</span><br />\r\n<span style=\"color:#000000\">Đ&agrave; đao lập sẵn chước d&ugrave;ng,</span><br />\r\n<span style=\"color:#000000\">Lạ g&igrave; một cốt một đồng xưa nay!</span><br />\r\n<span style=\"color:#000000\">C&oacute; ba mươi lạng trao tay,</span><br />\r\n<span style=\"color:#000000\">Kh&ocirc;ng dưng chi c&oacute; chuyện n&agrave;y, tr&ograve; kia!</span><br />\r\n<span style=\"color:#000000\">1165.Rồi ra trở mặt tức th&igrave;,</span><br />\r\n<span style=\"color:#000000\">Bớt lời, liệu chớ s&acirc;n si, thiệt đời!</span><br />\r\n<span style=\"color:#000000\">N&agrave;ng rằng: Thề thốt nặng lời,</span><br />\r\n<span style=\"color:#000000\">C&oacute; đ&acirc;u m&agrave; lại ra người hiểm s&acirc;u!</span><br />\r\n<span style=\"color:#000000\">C&ograve;n đương suy trước, nghĩ sau,</span><br />\r\n<span style=\"color:#000000\">1170.Mặt mo đ&atilde; thấy ở đ&acirc;u dẫn v&agrave;o.</span><br />\r\n<span style=\"color:#000000\">Sở Khanh l&ecirc;n tiếng r&ecirc;u rao:</span><br />\r\n<span style=\"color:#000000\">Rằng nghe mới c&oacute; con n&agrave;o ở đ&acirc;y.</span><br />\r\n<span style=\"color:#000000\">Phao cho quyến gi&oacute; rủ m&acirc;y,</span><br />\r\n<span style=\"color:#000000\">H&atilde;y xem c&oacute; biết mặt n&agrave;y l&agrave; ai</span><br />\r\n<span style=\"color:#000000\">1175.N&agrave;ng rằng: Th&ocirc;i thế th&igrave; th&ocirc;i!</span><br />\r\n<span style=\"color:#000000\">Rằng kh&ocirc;ng, th&igrave; cũng v&acirc;ng lời l&agrave; kh&ocirc;ng!</span><br />\r\n<span style=\"color:#000000\">Sở Khanh qu&aacute;t mắng đ&ugrave;ng đ&ugrave;ng,</span><br />\r\n<span style=\"color:#000000\">Sấn v&agrave;o, vừa rắp thị h&ugrave;ng ra tay,</span><br />\r\n<span style=\"color:#000000\">N&agrave;ng rằng: Trời nh&eacute; c&oacute; hay!</span><br />\r\n<span style=\"color:#000000\">1180.Quyến anh, rủ yến, sự n&agrave;y tại ai?</span><br />\r\n<span style=\"color:#000000\">Đem người giẩy xuống giếng khơi,</span><br />\r\n<span style=\"color:#000000\">N&oacute;i rồi, rồi lại ăn lời được ngay!</span><br />\r\n<span style=\"color:#000000\">C&ograve;n ti&ecirc;n t&iacute;ch việt ở tay,</span><br />\r\n<span style=\"color:#000000\">R&otilde; r&agrave;ng mặt ấy, mặt n&agrave;y chứ ai?</span><br />\r\n<span style=\"color:#000000\">1185.Lời ngay, đ&ocirc;ng mặt trong ngo&agrave;i,</span><br />\r\n<span style=\"color:#000000\">Kẻ ch&ecirc; bất nghĩa, người cười v&ocirc; lương!</span><br />\r\n<span style=\"color:#000000\">Phụ t&igrave;nh &aacute;n đ&atilde; r&otilde; r&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">Dơ tuồng, nghỉ mới kiếm đường th&aacute;o lui.</span><br />\r\n<span style=\"color:#000000\">Buồng ri&ecirc;ng, ri&ecirc;ng những sụt s&ugrave;i:</span><br />\r\n<span style=\"color:#000000\">1190.Nghĩ th&acirc;n, m&agrave; lại ngậm ng&ugrave;i cho th&acirc;n.</span><br />\r\n<span style=\"color:#000000\">Tiếc thay trong gi&aacute; trắng ngần,</span><br />\r\n<span style=\"color:#000000\">Đến phong trần, cũng phong trần như ai!</span><br />\r\n<span style=\"color:#000000\">Tẻ vui cũng một kiếp người,</span><br />\r\n<span style=\"color:#000000\">Hồng nhan phải giống ở đời m&atilde;i ru!</span><br />\r\n<span style=\"color:#000000\">1195.Kiếp xưa đ&atilde; vụng đường tu,</span><br />\r\n<span style=\"color:#000000\">Kiếp n&agrave;y chẳng kẻo đền b&ugrave; mới xu&ocirc;i!</span><br />\r\n<span style=\"color:#000000\">D&ugrave; sao b&igrave;nh đ&atilde; vỡ rồi,</span><br />\r\n<span style=\"color:#000000\">Lấy th&acirc;n m&agrave; trả nợ đời cho xong!</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Vừa tuần nguyệt s&aacute;ng, gương trong,</span><br />\r\n<span style=\"color:#000000\">1200.T&uacute; b&agrave; gh&eacute; lại thong dong dặn d&ograve;:</span><br />\r\n<span style=\"color:#000000\">Nghề chơi cũng lắm c&ocirc;ng phu,</span><br />\r\n<span style=\"color:#000000\">L&agrave;ng chơi ta phải biết cho đủ điều.</span><br />\r\n<span style=\"color:#000000\">N&agrave;ng rằng: Mưa gi&oacute; dập d&igrave;u,</span><br />\r\n<span style=\"color:#000000\">Liều th&acirc;n, th&igrave; cũng phải liều thế th&ocirc;i!</span><br />\r\n<span style=\"color:#000000\">1205.Mụ rằng: Ai cũng như ai,</span><br />\r\n<span style=\"color:#000000\">Người ta ai mất tiền ho&agrave;i đến đ&acirc;y?</span><br />\r\n<span style=\"color:#000000\">Ở trong c&ograve;n lắm điều hay,</span><br />\r\n<span style=\"color:#000000\">Nỗi đ&ecirc;m kh&eacute;p mở, nỗi ng&agrave;y ri&ecirc;ng chung.</span><br />\r\n<span style=\"color:#000000\">N&agrave;y con thuộc lấy nằm l&ograve;ng,</span><br />\r\n<span style=\"color:#000000\">1210.V&agrave;nh ngo&agrave;i bảy chữ, v&agrave;nh trong t&aacute;m nghề.</span><br />\r\n<span style=\"color:#000000\">Chơi cho liễu ch&aacute;n, hoa ch&ecirc;,</span><br />\r\n<span style=\"color:#000000\">Cho lăn l&oacute;c đ&aacute;, cho m&ecirc; mẩn đời.</span><br />\r\n<span style=\"color:#000000\">Khi kh&oacute;e hạnh, khi n&eacute;t ng&agrave;i,</span><br />\r\n<span style=\"color:#000000\">Khi ng&acirc;m ngợi nguyệt, khi cười cợt hoa.</span><br />\r\n<span style=\"color:#000000\">1215.Đều l&agrave; nghề nghiệp trong nh&agrave;,</span><br />\r\n<span style=\"color:#000000\">Đủ ngần ấy nết, mới l&agrave; người soi.</span><br />\r\n<span style=\"color:#000000\">G&oacute;t đầu v&acirc;ng dạy mấy lời,</span><br />\r\n<span style=\"color:#000000\">Dường chau n&eacute;t nguyệt, dường phai vẻ hồng.</span><br />\r\n<span style=\"color:#000000\">Những nghe n&oacute;i, đ&atilde; thẹn th&ugrave;ng,</span><br />\r\n<span style=\"color:#000000\">1220.Nước đời lắm nỗi lạ l&ugrave;ng, khắt khe!</span><br />\r\n<span style=\"color:#000000\">X&oacute;t m&igrave;nh cửa c&aacute;c, buồng khu&ecirc;,</span><br />\r\n<span style=\"color:#000000\">Vỡ l&ograve;ng, học lấy những nghề nghiệp hay!</span><br />\r\n<span style=\"color:#000000\">Kh&eacute;o l&agrave; mặt dạn, m&agrave;y d&agrave;y,</span><br />\r\n<span style=\"color:#000000\">Kiếp người đ&atilde; đến thế n&agrave;y th&igrave; th&ocirc;i!</span><br />\r\n<span style=\"color:#000000\">1225.Thương thay th&acirc;n phận lạc lo&agrave;i,</span><br />\r\n<span style=\"color:#000000\">Dẫu sao cũng ở tay người biết sao?</span><br />\r\n<span style=\"color:#000000\">Lầu xanh, mới rủ trướng đ&agrave;o,</span><br />\r\n<span style=\"color:#000000\">C&agrave;ng treo gi&aacute; ngọc, c&agrave;ng cao phẩm người.</span><br />\r\n<span style=\"color:#000000\">Biết bao bướm lả, ong lơi,</span><br />\r\n<span style=\"color:#000000\">1230.Cuộc say đầy th&aacute;ng, trận cười suốt đ&ecirc;m.</span><br />\r\n<span style=\"color:#000000\">Dập d&igrave;u l&aacute; gi&oacute;, c&agrave;nh chim,</span><br />\r\n<span style=\"color:#000000\">Sớm đưa Tống Ngọc, tối t&igrave;m Tr&agrave;ng Khanh.</span><br />\r\n<span style=\"color:#000000\">Khi tỉnh rượu, l&uacute;c t&agrave;n canh,</span><br />\r\n<span style=\"color:#000000\">Giật m&igrave;nh, m&igrave;nh lại thương m&igrave;nh x&oacute;t xa.</span><br />\r\n<span style=\"color:#000000\">1235.Khi sao phong gấm rủ l&agrave;,</span><br />\r\n<span style=\"color:#000000\">Giờ sao tan t&aacute;c như hoa giữa đường?</span><br />\r\n<span style=\"color:#000000\">Mặt sao d&agrave;y gi&oacute; dạn sương,</span><br />\r\n<span style=\"color:#000000\">Th&acirc;n sao bướm ch&aacute;n, ong chường bấy th&acirc;n?</span><br />\r\n<span style=\"color:#000000\">Mặc người mưa Sở, m&acirc;y Tần,</span><br />\r\n<span style=\"color:#000000\">1240.Những m&igrave;nh n&agrave;o biết c&oacute; xu&acirc;n l&agrave; g&igrave;!</span><br />\r\n<span style=\"color:#000000\">Đ&ocirc;i phen gi&oacute; tựa, hoa kề,</span><br />\r\n<span style=\"color:#000000\">Nửa r&egrave;m tuyết ngậm, bốn bề trăng th&acirc;u.</span><br />\r\n<span style=\"color:#000000\">Cảnh n&agrave;o cảnh chẳng đeo sầu,</span><br />\r\n<span style=\"color:#000000\">Người buồn, cảnh c&oacute; vui đ&acirc;u bao giờ!</span><br />\r\n<span style=\"color:#000000\">1245.Đ&ograve;i phen n&eacute;t vẽ, c&acirc;u thơ,</span><br />\r\n<span style=\"color:#000000\">Cung cầm trong nguyệt, nước cờ dưới hoa,</span><br />\r\n<span style=\"color:#000000\">Vui l&agrave; vui gượng kẻo m&agrave;,</span><br />\r\n<span style=\"color:#000000\">Ai tri &acirc;m đ&oacute;, mặn m&agrave; với ai?</span><br />\r\n<span style=\"color:#000000\">Thờ ơ gi&oacute; tr&uacute;c, mưa mai,</span><br />\r\n<span style=\"color:#000000\">1250.Ngẩn ngơ trăm nỗi, gi&ugrave;i m&agrave;i một th&acirc;n.</span><br />\r\n<span style=\"color:#000000\">&Ocirc;m l&ograve;ng đ&ograve;i đoạn xa gần,</span><br />\r\n<span style=\"color:#000000\">Chẳng v&ograve; m&agrave; rối, chẳng dần m&agrave; đau!</span><br />\r\n<span style=\"color:#000000\">Nhớ ơn ch&iacute;n chữ cao s&acirc;u,</span><br />\r\n<span style=\"color:#000000\">Một ng&agrave;y một ngả b&oacute;ng d&acirc;u t&agrave; t&agrave;.</span><br />\r\n<span style=\"color:#000000\">1255.Dặm ng&agrave;n, nước thẳm, non xa,</span><br />\r\n<span style=\"color:#000000\">Nghĩ đ&acirc;u th&acirc;n phận con ra thế n&agrave;y!</span><br />\r\n<span style=\"color:#000000\">S&acirc;n h&ograve;e đ&ocirc;i ch&uacute;t thơ ng&acirc;y</span><br />\r\n<span style=\"color:#000000\">Tr&acirc;n cam, ai kẻ đỡ thay việc m&igrave;nh?</span><br />\r\n<span style=\"color:#000000\">Nhớ lời nguyện ước ba sinh,</span><br />\r\n<span style=\"color:#000000\">1260.Xa x&ocirc;i ai c&oacute; thấu t&igrave;nh chăng ai?</span><br />\r\n<span style=\"color:#000000\">Khi về hỏi liễu Chương đ&agrave;i,</span><br />\r\n<span style=\"color:#000000\">C&agrave;nh xu&acirc;n đ&atilde; bẻ cho người chuy&ecirc;n tay.</span><br />\r\n<span style=\"color:#000000\">T&igrave;nh s&acirc;u mong trả nghĩa d&agrave;y,</span><br />\r\n<span style=\"color:#000000\">Hoa kia đ&atilde; chắp c&agrave;nh n&agrave;y cho chưa?</span><br />\r\n<span style=\"color:#000000\">1265.Mối t&igrave;nh đ&ograve;i đoạn v&ograve; tơ,</span><br />\r\n<span style=\"color:#000000\">Giấc hương quan luống lần mơ canh d&agrave;i.</span><br />\r\n<span style=\"color:#000000\">Song sa v&ograve; v&otilde; phương trời,</span><br />\r\n<span style=\"color:#000000\">Nay ho&agrave;ng h&ocirc;n, đ&atilde; lại mai h&ocirc;n ho&agrave;ng.</span><br />\r\n<span style=\"color:#000000\">Lần lần thỏ bạc &aacute;c v&agrave;ng,</span><br />\r\n<span style=\"color:#000000\">1270.X&oacute;t người trong hội đoạn tr&agrave;ng đ&ograve;i cơn!</span><br />\r\n<span style=\"color:#000000\">Đ&atilde; cho lấy chữ hồng nhan,</span><br />\r\n<span style=\"color:#000000\">L&agrave;m cho, cho hại, cho t&agrave;n, cho c&acirc;n!</span><br />\r\n<span style=\"color:#000000\">Đ&atilde; đ&agrave;y v&agrave;o kiếp phong trần,</span><br />\r\n<span style=\"color:#000000\">Sao cho sỉ nhục một lần mới th&ocirc;i!</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#ff0000\">...</span></span></span></p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(3, 'Người lái đò sông đà', 'Văn học Việt Nam', 'Nguyễn Tuân', 1960, 'nguoi-lai-do-song-da.jpg', 'Đã hoàn', '<p style=\"text-align:justify\">Thi&ecirc;n nhi&ecirc;n T&acirc;y Bắc được t&ocirc; điểm bởi con s&ocirc;ng Đ&agrave; vừa hung bạo vừa trữ t&igrave;nh. S&ocirc;ng Đ&agrave; c&oacute; l&uacute;c dịu d&agrave;ng như người phụ nữ kiều diễm. Nước s&ocirc;ng Đ&agrave; thay đổi theo m&ugrave;a, phản chiếu trời xu&acirc;n nắng thu: &quot;M&ugrave;a xu&acirc;n d&ograve;ng xanh ngọc b&iacute;ch, m&ugrave;a thu lừ lừ ch&iacute;n đỏ như da mặt người bầm đi v&igrave; rượu bữa&quot;. Dọc theo s&ocirc;ng Đ&agrave;, c&oacute; lắm th&aacute;c nhiều ghềnh, c&oacute; đ&aacute; dựng v&aacute;ch th&agrave;nh, c&oacute; đ&aacute; tảng, đ&aacute; h&ograve;n b&agrave;y thế thạch trận, tạo n&ecirc;n cửa sinh, cửa tử. Nổi bật tr&ecirc;n bức tranh thi&ecirc;n nhi&ecirc;n h&ugrave;ng vĩ, đầy sức sống đ&oacute; l&agrave; h&igrave;nh ảnh &ocirc;ng l&aacute;i đ&ograve; s&ocirc;ng Đ&agrave;. Đ&oacute; l&agrave; một người mang vẻ đẹp khỏe khoắn của người d&acirc;n lao động v&ugrave;ng s&ocirc;ng nước với th&acirc;n h&igrave;nh cao to, nước da r&aacute;m nắng. &Ocirc;ng l&agrave;m nghề l&aacute;i đ&ograve; đ&atilde; nhiều năm, từng gắn b&oacute; với d&ograve;ng s&ocirc;ng Đ&agrave;, hiểu được t&iacute;nh kh&iacute; của n&oacute;. &Ocirc;ng thuộc nằm l&ograve;ng từng con th&aacute;c lớn, th&aacute;c nhỏ, từng v&aacute;ch đ&aacute;, luồng nước, từng cửa sinh, cửa tử do thế thạch trận tạo n&ecirc;n. &Ocirc;ng đ&atilde; d&ugrave;ng kinh nghiệm nghề nghiệp cộng với sự cần c&ugrave; gan dạ đưa con thuyền vượt th&aacute;c nước s&ocirc;ng Đ&agrave; đầy nguy hiểm. &Ocirc;ng đ&atilde; đưa nhiều chuyến h&agrave;ng về xu&ocirc;i an to&agrave;n để g&oacute;p phần v&agrave;o cuộc sống. Sau khi vượt s&ocirc;ng Đ&agrave;, &ocirc;ng l&aacute;i đ&ograve; trở về cuộc sống đời thường thanh thản của m&igrave;nh, &ocirc;ng neo thuyền chỗ kh&uacute;c s&ocirc;ng b&igrave;nh lặng, nấu ống cơm lam v&agrave; b&agrave;n t&aacute;n về c&aacute; anh vũ, c&aacute; dầm xanh.<br />\r\n&nbsp;</p>\r\n', '<p style=\"text-align:justify\">&quot;Đẹp vậy thay, tiếng h&aacute;t tr&ecirc;n d&ograve;ng s&ocirc;ng&quot;<br />\r\nWladyslaw Broniewski<br />\r\n&quot;Ch&uacute;ng thuỷ giai đ&ocirc;ng tẩu - Đ&agrave; giang độc bắc lưu&quot;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;(Lượt đoạn đầu: T&aacute;c giả tr&igrave;nh b&agrave;y mục đ&iacute;ch chuyến đi thực tế T&acirc;y Bắc l&agrave; s&ocirc;ng Đ&agrave; của m&igrave;nh chủ yếu l&agrave; để t&igrave;m hiểu nhũng con người ở đ&acirc;y m&agrave; &ocirc;ng gọi &quot;thứ l&agrave;ng mười đ&atilde; qua thứ lửa của c&aacute;ch mạng l&agrave; kh&aacute;ng chiến, hiện nay đang g&oacute;p phần v&agrave;o việc đến thiết T&acirc;y Bắc. Tiếp đ&oacute;, t&aacute;c giả giới thiệu t&agrave;i tứ tuyệt vời của những người l&aacute;i đ&ograve; s&ocirc;ng Đ&agrave; trong nghệ thuật vượt th&aacute;c leo ghềnh v&agrave; liệt k&ecirc; một loạt con th&aacute;c s&ocirc;ng Đ&agrave; từ Vạn Y&ecirc;n về xu&ocirc;i, trong đ&oacute; c&oacute; những con th&aacute;c v&ocirc; c&ugrave;ng độc dữ, nham hiểm&quot;. Đ&aacute;ng sợ nhất l&agrave; những c&aacute;i h&uacute;t nước &quot;xo&aacute;y t&iacute;t đ&aacute;y&quot; giũa l&ograve;ng s&ocirc;ng từng d&igrave;m xuống v&agrave; x&eacute; tan t&aacute;c những b&egrave; gỗ, những con thuyền v&ocirc; &yacute; bị n&oacute; l&ocirc;i tuột xuống&hellip;)</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; &nbsp; &nbsp; H&ugrave;ng vĩ của S&ocirc;ng Đ&agrave; kh&ocirc;ng phải chỉ c&oacute; th&aacute;c đ&aacute;. M&agrave; n&oacute; c&ograve;n l&agrave; những cảnh đ&aacute; bờ s&ocirc;ng, dựng v&aacute;ch th&agrave;nh, mặt s&ocirc;ng chỗ ấy chỉ l&uacute;c đ&uacute;ng ngọ mới c&oacute; mặt trời. C&oacute; v&aacute;ch đ&aacute; th&agrave;nh chẹt l&ograve;ng S&ocirc;ng Đ&agrave; như một c&aacute;i yết hầu. Đứng b&ecirc;n n&agrave;y bờ nhẹ tay n&eacute;m h&ograve;n đ&aacute; qua b&ecirc;n kia v&aacute;ch. C&oacute; qu&atilde;ng con nai con hổ đ&atilde; c&oacute; lần vọt từ bờ n&agrave;y sang bờ kia. Ngồi trong khoang đ&ograve; qua qu&atilde;ng ấy, đang m&ugrave;a h&egrave; m&agrave; cũng thấy lạnh, cảm thấy m&igrave;nh như đứng ở h&egrave; một c&aacute;i ng&otilde; m&agrave; ng&oacute;ng vọng l&ecirc;n một khung cửa sổ n&agrave;o tr&ecirc;n c&aacute;i tầng nh&agrave; thứ mấy n&agrave;o vừa tắt phụt đ&egrave;n điện.<br />\r\n&nbsp; &nbsp; &nbsp;Lại như quăng mặt ghềnh H&aacute;t Lo&oacute;ng, d&agrave;i hang c&acirc;y số nước x&ocirc; đ&aacute;, đ&aacute; x&ocirc; s&oacute;ng, s&oacute;ng x&ocirc; gi&oacute;. Cuốn cuộn luồng gi&oacute; g&ugrave;n gh&egrave; suốt năm như l&uacute;c n&agrave;o cũng đ&ograve;i nợ xu&yacute;t tay l&aacute;i th&igrave; cũng dễ lật ngửa bụng thuyền ra.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; &nbsp; &nbsp;Lại như qu&atilde;ng T&agrave; Mường V&aacute;t ph&iacute;a dưới Sơn La.Tr&ecirc;n song bỗng c&oacute; những c&aacute;i h&uacute;t nước giống như c&aacute;i giếng b&ecirc; t&ocirc;ng thả xuống s&ocirc;ng để chuẩn bị l&agrave;m m&oacute;ng cầu. Nước ở đ&acirc;y thở v&agrave; k&ecirc;u như cửa cống c&aacute;i bị sặc. Tr&ecirc;n mặt c&aacute;i h&uacute;t xo&aacute;y t&iacute;t đ&aacute;y; cũng đang quay lừ lừ những c&aacute;nh quạ đ&agrave;n. Kh&ocirc;ng thuyền n&agrave;o d&aacute;m men gần những c&aacute;i h&uacute;t nước ấy, thuyền n&agrave;o qua cũng ch&egrave;o nhanh để lướt qu&atilde;ng s&ocirc;ng, y như l&agrave; &ocirc; t&ocirc; sang số ấn ga cho nhanh để v&uacute;t qua một qu&atilde;ng đường mượn cạp ra ngo&agrave;i bờ vực. Ch&egrave;o nhanh v&agrave; tay l&aacute;i cho vững m&agrave; ph&oacute;ng qua c&aacute;i giếng s&acirc;u, những c&aacute;i giếng s&acirc;u nước &aacute;c ặc l&ecirc;n như vừa r&oacute;t dầu s&ocirc;i v&agrave;o. Nhiều b&egrave; gỗ rừng đi ngh&ecirc;nh ngang v&ocirc; &yacute; l&agrave; những c&aacute;i giếng h&uacute;t ấy n&oacute; l&ocirc;i tuột xuống. C&oacute; những thuyền đ&atilde; bị c&aacute;i h&uacute;t n&oacute; h&uacute;t xuống, thuyền trồng ngay c&acirc;y chuối ngược rồi vụt biến đi, bị d&igrave;m v&agrave; đi ngầm dưới l&ograve;ng s&ocirc;ng đến mươi ph&uacute;t sau mới thấy tan x&aacute;c ở khuỷnh s&ocirc;ng dưới. T&ocirc;i sợ h&atilde;i m&agrave; nghĩ đến một anh bạn quay phim t&aacute;o tợn n&agrave;o muốn truyền cảm gi&aacute;c lạ cho kh&aacute;n giả, đ&atilde; dũng cảm d&aacute;m ngồi v&agrave;o một c&aacute;i thuyền th&uacute;ng tr&ograve;n v&agrave;nh rồi cho cả thuyền cả m&igrave;nh cả m&aacute;y quay xuống đ&aacute;y c&aacute;i h&uacute;t S&ocirc;ng Đ&agrave; - từ đ&aacute;y c&aacute;i h&uacute;t nh&igrave;n ngược l&ecirc;n v&aacute;ch th&agrave;nh h&uacute;t mặt s&ocirc;ng ch&ecirc;nh nhau tới một cột nước cao đến v&agrave;i sải. Thế rồi thu ảnh. C&aacute;i thuyền xoay t&iacute;t, những thước phim m&agrave;u cũng quay t&iacute;t, c&aacute;i m&aacute;y lia ngược contre-plong&eacute;e l&ecirc;n một c&aacute;i mặt giếng m&agrave; th&agrave;nh giếng x&acirc;y to&agrave;n bằng nước s&ocirc;ng xanh ve một &aacute;ng thuỷ tinh khối đ&uacute;c d&agrave;y, khối pha l&ecirc; xanh như sắp vỡ tan ụp v&agrave;o cả m&aacute;y cả người quay phim cả người đang xem. C&aacute;i phim ảnh thu được trong l&ograve;ng giếng xo&aacute;y t&iacute;t đ&aacute;y, truyền cảm lại cho người xem phim k&iacute; sự thấy m&igrave;nh đang lấy g&acirc;n ngồi giữ chặt ghế như gh&igrave; lấy m&eacute;p một chiếc l&aacute; rừng bị vứt v&agrave;o một c&aacute;i cốc pha l&ecirc; nước khổng lồ vừa r&uacute;t l&ecirc;n c&aacute;i gậy đ&aacute;nh ph&egrave;n.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; &nbsp; &nbsp;Cuộc sống của người l&aacute;i đ&ograve; S&ocirc;ng Đ&agrave; quả l&agrave; một cuộc chiến đấu hắng ng&agrave;y với thi&ecirc;n nhi&ecirc;n, một thứ thi&ecirc;n nhi&ecirc;n T&acirc;y Bắc c&oacute; nhiều l&uacute;c trọng n&oacute; th&agrave;nh ra diện mạo v&agrave; t&acirc;m địa một thứ kẻ th&ugrave; số một. Nh&igrave;n c&aacute;i thi&ecirc;n nhi&ecirc;n ấy, c&oacute; những l&uacute;c thấy n&oacute; kh&ocirc;ng &quot;thơ đời Đường&quot; nh&agrave;n hạ, m&agrave; thấy n&oacute; ch&iacute;nh l&agrave; một cuộc đấu tranh với thi&ecirc;n nhi&ecirc;n để gi&agrave;nh sự sống từ tay n&oacute; về tay m&igrave;nh. T&ocirc;i xin ghi ở đoạn n&agrave;y c&aacute;i h&igrave;nh ảnh chiến đấu gian lao của người l&aacute;i đ&ograve; tr&ecirc;n chiến trường S&ocirc;ng Đ&agrave;, tr&ecirc;n một qu&atilde;ng thuỷ chiến ở mặt trận S&ocirc;ng Đ&agrave;.<br />\r\n&nbsp; &nbsp;&hellip;C&ograve;n xa lắm mới đến c&aacute;i th&aacute;c dưới. Nhưng đ&atilde; thấy tiếng nước r&eacute;o gần m&atilde;i lại r&eacute;o to m&atilde;i l&ecirc;n. Tiếng nước th&aacute;c nghe như l&agrave; o&aacute;n tr&aacute;ch g&igrave;, rồi lại như l&agrave; van xin, rồi lại như l&agrave; khi&ecirc;u kh&iacute;ch, giọng gằn m&agrave; chế nhạo. Thế rồi n&oacute; rống l&ecirc;n như tiếng một ng&agrave;n con tr&acirc;u mộng đang lồng lộn giữa rừng vầu rừng tre nứa nổ lửa, đang ph&aacute; tu&ocirc;ng rừng lửa, rừng lửa c&ugrave;ng gầm th&eacute;t với đ&agrave;n tr&acirc;u da ch&aacute;y b&ugrave;ng b&ugrave;ng. Tới c&aacute;i th&aacute;c rồi. Ngoặt kh&uacute;c s&ocirc;ng lượn, thấy s&oacute;ng bọt đ&atilde; trắng xo&aacute; cả một ch&acirc;n trời đ&aacute;. Đ&aacute; ở đ&acirc;y từ ng&agrave;n năm vẫn mai phục hết trong l&ograve;ng s&ocirc;ng, h&igrave;nh như mỗi lần c&oacute; chiếc thuyền n&agrave;o xuất hiện ở qu&atilde;ng ầm ầm m&agrave; quạnh hiu n&agrave;y, mỗi lần c&oacute; chiếc n&agrave;o nh&ocirc; v&agrave;o đường ngoặt s&ocirc;ng l&agrave; một số h&ograve;n b&egrave;n nhổm cả dậy để vồ lấy thuyền. Mặt h&ograve;n đ&aacute; n&agrave;o tr&ocirc;ng cũng ngỗ ngược, h&ograve;n n&agrave;o cũng nhăn nh&uacute;m m&eacute;o m&oacute; hơn cả c&aacute;i mặt nước chỗ n&agrave;y. Mặt s&ocirc;ng rung r&iacute;t l&ecirc;n như tuyếc-bin thuỷ điện nơi đ&aacute;y hầm đập. Mặt s&ocirc;ng trắng xo&aacute; c&agrave;ng l&agrave;m bật r&otilde; l&ecirc;n những h&ograve;n những tảng mới tr&ocirc;ng tưởng như n&oacute; đứng n&oacute; ngồi n&oacute; nằm tuỳ theo sở th&iacute;ch tự động của đ&aacute; to đ&aacute; b&eacute;. Nhưng h&igrave;nh như S&ocirc;ng Đ&agrave; đ&atilde; giao việc cho mỗi h&ograve;n. Mới thấy rằng đ&acirc;y l&agrave; n&oacute; b&agrave;y thạch trận tr&ecirc;n song. Đ&aacute;m tảng đ&aacute;m h&ograve;n chia l&agrave;m ba h&agrave;ng chặn ngang tr&ecirc;n s&ocirc;ng đ&ograve;i ăn chết c&aacute;i thuyền một c&aacute;i thuyền đơn độc kh&ocirc;ng c&ograve;n biết l&ugrave;i đi đ&acirc;u để tr&aacute;nh một cuộc gi&aacute;p l&aacute; c&agrave; c&oacute; đ&aacute; d&agrave;n trận địa sẵn. H&agrave;ng tiền vệ, c&oacute; hai h&ograve;n canh một cửa đ&aacute; tr&ocirc;ng như l&agrave; sơ hở nhưng ch&iacute;nh hai đứa giữ vai tr&ograve; dụ c&aacute;i thuyền đối phương đi v&agrave;o s&acirc;u nữa, v&agrave;o tận tuyến giữa rồi nước s&oacute;ng luồng với đ&aacute;nh khuỷu quật vu hồi lại Nếu lọt v&agrave;o đ&acirc;y rồi m&agrave; c&aacute;i thuyền du k&iacute;ch ấy vẫn chọc thủng được tuyến hai, th&igrave; nhiệm vụ của những boong-ke ch&igrave;m v&agrave; ph&aacute;o đ&agrave;i đ&aacute; nổi ở tuyến ba phải đ&aacute;nh tan c&aacute;i thuyền lọt lưới đ&aacute; tuyến tr&ecirc;n, phải ti&ecirc;u diệt tất cả thuyền trưởng thuỷ thủ ngay ở ch&acirc;n th&aacute;c. Thạch trận d&agrave;n b&agrave;y vừa xong th&igrave; c&aacute;i thuyền vụt tới. Phối hớp với đ&aacute;, nước th&aacute;c reo h&ograve; l&agrave;m thanh viện cho đ&aacute;, những h&ograve;n bệ vệ oai phong lẫm liệt. Một h&ograve;n ấy tr&ocirc;ng nghi&ecirc;ng th&igrave; y như l&agrave; đang hất h&agrave;m hỏi c&aacute;i thuyền phải xưng t&ecirc;n tuổi trước khi giao chiến. Một h&ograve;n kh&aacute;c l&ugrave;i lại một ch&uacute;t v&agrave; th&aacute;ch thức c&aacute;i thuyền c&oacute; giỏi th&igrave; tiến gần v&agrave;o. &Ocirc;ng đ&ograve; hai tay giữ m&aacute;i ch&egrave;o khỏi bị hất l&ecirc;n khỏi s&oacute;ng trận địa ph&oacute;ng thẳng v&agrave;o m&igrave;nh. Mặt nước h&ograve; la vang dậy quanh m&igrave;nh, &ugrave;a v&agrave;o m&agrave; bẻ g&atilde;y c&aacute;n ch&egrave;o v&otilde; kh&iacute; tr&ecirc;n c&aacute;nh tay m&igrave;nh. S&oacute;ng nước như thể qu&acirc;n liều mạng v&agrave;o s&aacute;t n&aacute;ch m&agrave; đ&aacute; tr&aacute;i m&agrave; th&uacute;c gối v&agrave;o bụng v&agrave; h&ocirc;ng thuyền. C&oacute; l&uacute;c ch&uacute;ng đội cả thuyền l&ecirc;n. Nước b&aacute;m lấy thuyền như đổ vật t&uacute;m thắt lưng &ocirc;ng đ&ograve; đ&ograve;i lật ngửa m&igrave;nh ra giữa trận nước vang trời thanh la n&atilde;o bạt. S&oacute;ng th&aacute;c đ&atilde; đ&aacute;nh đến miếng đ&ograve;n hiểm độc nhất, cả c&aacute;i luồng nước v&ocirc; sở bất ch&iacute; ấy b6p Chặt lấy hạ bộ người l&aacute;i đ&ograve; [&hellip;]. Mặt s&ocirc;ng trong t&iacute;ch tắc lo&agrave; s&aacute;ng l&ecirc;n như một cửa bể đom đ&oacute;m rừng &ugrave;a xuống m&agrave; ch&acirc;m lửa v&agrave;o đầu s&oacute;ng. Nhưng &ocirc;ng đ&ograve; cố n&eacute;n vết thương, hai ch&acirc;n vẫn kẹp chặt lấy cuống l&aacute;i, mặt m&eacute;o bệch đi như c&aacute;i luồng s&oacute;ng đ&aacute;nh hồi lung, đ&aacute;nh đ&ograve;n tỉa, đ&aacute;nh đ&ograve;n &acirc;m v&agrave;o chỗ hiểm. Tăng th&ecirc;m m&atilde;i l&ecirc;n tiếng hỗn chiến của nước của đ&aacute; th&aacute;c. Nhưng tr&ecirc;n c&aacute;i thuyền s&aacute;u bơi ch&egrave;o, vẫn nghe r&otilde; tiếng chỉ huy ngắn gọn tỉnh t&aacute;o của người cầm l&aacute;i. Vậy l&agrave; ph&aacute; xong c&aacute;i tr&ugrave;ng vi thạch trận v&ograve;ng thứ nhất. Kh&ocirc;ng một ph&uacute;t nghỉ tay nghỉ mắt, phải ph&aacute; lu&ocirc;n v&ograve;ng v&acirc;y thứ hai v&agrave; đổi lu&ocirc;n chiến thuật. &Ocirc;ng l&aacute;i đ&atilde; nắm chắc binh ph&aacute;p của thần s&ocirc;ng thần đ&aacute;. &Ocirc;ng đ&atilde; thuộc quy luật phục k&iacute;ch của lũ đ&aacute; nơi ải nước hiểm trở n&agrave;y. V&ograve;ng đầu vừa rồi, n&oacute; mở ra năm cửa trận, c&oacute; bốn cửa tử một cửa sinh cửa, sinh nằm lập lờ ph&iacute;a tả ngạn s&ocirc;ng. V&ograve;ng thứ hai n&agrave;y tăng th&ecirc;m nhiều cửa tử để đ&aacute;nh lừa con thuyền v&agrave;o, v&agrave; cửa sinh lại bố tr&iacute; lệch qua ph&iacute;a bờ hữu ngạn. Cưỡi l&ecirc;n th&aacute;c S&ocirc;ng Đ&agrave;, phải cưỡi đến c&ugrave;ng như l&agrave; cưỡi hổ.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; &nbsp; &nbsp;D&ograve;ng th&aacute;c h&ugrave;m beo đang hồng hộc tế mạnh tr&ecirc;n s&ocirc;ng đ&aacute;. Nắm chặt lấy được c&aacute;i bờm s&oacute;ng đ&uacute;ng luồng rồi, &ocirc;ng đ&ograve; gh&igrave; cương l&aacute;i, băm chắc lấy luồng nước đ&uacute;ng m&agrave; ph&oacute;ng nhanh v&agrave;o cửa sinh, m&agrave; l&aacute;i miết một đường ch&eacute;o về ph&iacute;a cửa đ&aacute; ấy. Bốn năm bọn thuỷ qu&acirc;n cửa ải nước b&ecirc;n bờ tr&aacute;i liền x&ocirc; ra cảnh n&iacute;u thuyền l&ocirc;i v&agrave;o tập đo&agrave;n cửa tử. &Ocirc;ng đ&ograve; vẫn nhớ mặt bọn n&agrave;y, đứa th&igrave; &ocirc;ng tr&aacute;nh m&agrave; rảo bơi ch&egrave;o l&ecirc;n, đứa th&igrave; &ocirc;ng đ&egrave; sấn l&ecirc;n m&agrave; chặt đ&ocirc;i ra để mở đường tiến. Những luồng tử đ&atilde; bỏ hết lại sau thuyền. Chỉ c&ograve;n vẳng reo tiếng h&ograve; của s&oacute;ng th&aacute;c luồng sinh. Ch&uacute;ng vẫn kh&ocirc;ng ngớt khi&ecirc;u kh&iacute;ch, mặc dầu c&aacute;i thằng đ&aacute; tướnng đứng chiến ở cửa v&agrave;o đ&atilde; tiu nghỉu c&aacute;i mặt xanh l&egrave; thất vọng thua c&aacute;i thuyền đ&atilde; đ&aacute;nh tr&uacute;ng v&agrave;o cửa sinh n&oacute; trấn lấy. C&ograve;n một tr&ugrave;ng v&acirc;y thứ b&agrave; nữa. &iacute;t cửa hơn, b&ecirc;n phải b&ecirc;n tr&aacute;i đều l&agrave; luồng chết cả. C&aacute;i luồng sống ở chặng ba n&agrave;y lại ở ngay giữa bọn đ&aacute; hậu vệ của con th&aacute;c. Cứ ph&oacute;ng thẳng thuyền, chọc thủng cửa giữa đ&oacute;. Thuyền v&uacute;t qua cổng đ&aacute; c&aacute;nh mở c&aacute;nh kh&eacute;p. V&uacute;t, v&uacute;t, cửa ngo&agrave;i, cửa trong, lại cửa trong c&ugrave;ng, thuyền như một mũi t&ecirc;n tre xuy&ecirc;n nhanh qua hơi nước, vừa xuy&ecirc;n v&agrave;o từ động l&aacute;i được lượn được. Thế l&agrave; hết th&aacute;c. D&ograve;ng s&ocirc;ng vặn m&igrave;nh v&agrave;o một c&aacute;i bến c&aacute;t c&oacute; hang lạnh. S&oacute;ng th&aacute;c x&egrave;o x&egrave;o tan trong tr&iacute; nhớ. S&ocirc;ng nước lại thanh b&igrave;nh. Đ&ecirc;m ấy nh&agrave; đ&ograve; đốt lửa trong hang đ&aacute;, nướng ống cơm lam v&agrave; to&agrave;n b&agrave;n t&aacute;n về c&aacute; anh vũ c&aacute; dầm xanh, về những c&aacute;i hầm c&aacute; hang c&aacute; m&ugrave;a kh&ocirc; nổ những tiếng to như m&igrave;n bộc ph&aacute; rồi c&aacute; t&uacute;a ra đầy tr&agrave;n ruộng. Cũng chả thấy ai b&agrave;n th&ecirc;m một lời n&agrave;o về cuộc chiến thắng vừa qua nơi cửa ải nước đủ tướng dữ qu&acirc;n tợn vừa rồi. Cuộc sống của họ l&agrave; ng&agrave;y n&agrave;o cũng chiến đấu với S&ocirc;ng Đ&agrave; dữ dội, ng&agrave;y n&agrave;o cũng gi&agrave;nh lấy c&aacute;i sống từ tay những c&aacute;i th&aacute;c, n&ecirc;n n&oacute; cũng kh&ocirc;ng c&oacute; g&igrave; l&agrave; hồi hộp đ&aacute;ng nhớ... Họ nghĩ thế, l&uacute;c ngừng ch&egrave;o.<br />\r\n&nbsp; &nbsp; &nbsp; Tr&ecirc;n S&ocirc;ng Đ&agrave;, thỉnh thoảng c&oacute; t&agrave;u bay lượn v&ograve;ng ở qu&atilde;ng n&agrave;y ở qu&atilde;ng kh&aacute;c, c&oacute; lẽ đề chỉnh l&iacute; về bản đồ đất nước Tổ quốc. T&ocirc;i nghĩ nếu sau n&agrave;y l&agrave;m phim truyện hoặc phim k&iacute; sự (t&ocirc;i kh&ocirc;ng muốn d&ugrave;ng mấy chữ phim t&agrave;i liệu) m&agrave;u về S&ocirc;ng Đ&agrave;, nếu muốn phản &aacute;nh l&ecirc;n c&aacute;i dữ tợn v&agrave; c&aacute;i lớn của S&ocirc;ng Đ&agrave; của th&aacute;c S&ocirc;ng Đ&agrave;, cũng phải đưa ống quay phim l&ecirc;n t&agrave;u bay. Cho bay l&agrave; l&agrave; tr&ecirc;n th&aacute;c m&agrave; g&iacute; m&aacute;y xuống m&agrave; lượn ống m&aacute;y theo những luồng sinh của th&aacute;c, tr&ecirc;n th&aacute;c hi&ecirc;n ngang một người l&aacute;i đ&ograve; S&ocirc;ng Đ&agrave; c&oacute; tự do, v&igrave; người l&aacute;i đ&ograve; ấy đ&atilde; nắm được c&aacute;i quy luật tất yếu của d&ograve;ng nước S&ocirc;ng Đ&agrave;.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; &nbsp; &nbsp; T&ocirc;i c&oacute; bay tạt ngang qua S&ocirc;ng Đ&agrave; mấy lần, v&agrave; thấy đ&oacute; cũng l&agrave; th&ecirc;m cho m&igrave;nh một g&oacute;c độ nh&igrave;n một c&aacute;ch nh&igrave;n về con s&ocirc;ng T&acirc;y Bắc hung bạo v&agrave; trữ t&igrave;nh. Từ tr&ecirc;n t&agrave;u bay m&agrave; nh&igrave;n xuống S&ocirc;ng Đ&agrave;, kh&ocirc;ng ai trong t&agrave;u bay nghĩ rằng c&aacute;i d&acirc;y thừng ngoằn ngo&egrave;o dưới ch&acirc;n m&igrave;nh kia lại ch&iacute;nh l&agrave; c&aacute;i con s&ocirc;ng hằng năm v&agrave; đời đời kiếp kiếp l&agrave;m m&igrave;nh l&agrave;m mẩy với con người T&acirc;y Bắc v&agrave; phản ứng giận dỗi v&ocirc; tội vạ với người l&aacute;i đ&ograve; S&ocirc;ng Đ&agrave;. Cũng kh&ocirc;ng ai nghĩ rằng đ&oacute; l&agrave; con s&ocirc;ng của c&acirc;u đồng dao thần thoại Sơn Tinh Thuỷ Tinh &quot;N&uacute;i cao s&ocirc;ng h&atilde;y c&ograve;n d&agrave;i - Năm năm b&aacute;o o&aacute;n đời đời đ&aacute;nh ghen&quot;. H&igrave;nh như khi m&agrave; ta đ&atilde; quen đọc bản đồ s&ocirc;ng n&uacute;i, th&igrave; mỗi l&uacute;c ngồi t&agrave;u bay tr&ecirc;n chiều cao m&agrave; nh&igrave;n xuống đất nước Tổ quốc bao la, c&agrave;ng thấy quen thuộc với từng n&eacute;t s&ocirc;ng t&atilde;i ra tr&ecirc;n đại dương đ&aacute; lờ lờ b&oacute;ng m&acirc;y dưới ch&acirc;n m&igrave;nh. Con S&ocirc;ng Đ&agrave; tu&ocirc;n d&agrave;i tu&ocirc;n d&agrave;i như một &aacute;ng t&oacute;c trữ t&igrave;nh, đầu t&oacute;c ch&acirc;n t&oacute;c ẩn hiện trong m&acirc;y trời t&acirc;y Bắc bung nở hoa ban hoa gạo th&aacute;ng hai va cuồn cuộn m&ugrave; kh&oacute;i M&egrave;o đốt nương xu&acirc;n. T&ocirc;i đ&atilde; nh&igrave;n say sưa l&agrave;n m&acirc;y m&ugrave;a xu&acirc;n bay tr&ecirc;n S&ocirc;ng Đ&agrave;, t&ocirc;i đ&atilde; xuy&ecirc;n qua đ&aacute;m m&acirc;y m&ugrave;a thu m&agrave; nh&igrave;n xuống d&ograve;ng nước S&ocirc;ng Đ&agrave;. M&ugrave;a xu&acirc;n d&ograve;ng xanh ngọc b&iacute;ch, chứ nước S&ocirc;ng Đ&agrave; kh&ocirc;ng xanh m&agrave;u xanh canh hến của S&ocirc;ng G&acirc;m S&ocirc;ng L&ocirc;. M&ugrave;a thu nước S&ocirc;ng đ&agrave; lừ lừ ch&iacute;n đỏ như da mặt một nguời bầm đi v&igrave; rượu bữa, lừ lừ c&aacute;i m&agrave;u đỏ giận dữ ở một người bất m&atilde;n bực bội g&igrave; mỗi độ thu về. Chưa hề bao giờ t&ocirc;i thấy d&ograve;ng S&ocirc;ng Đ&agrave; l&agrave; đen như thực d&acirc;n Ph&aacute;p đ&atilde; đ&egrave; ngửa con s&ocirc;ng ta ra đổ mực T&acirc;y v&agrave;o m&agrave; gọi bằng một c&aacute;i t&ecirc;n T&acirc;y l&aacute;o lếu, rồi cứ thế m&agrave; phiết v&agrave;o bản đồ lai chữ.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; &nbsp; &nbsp;Con S&ocirc;ng Đ&agrave; gợi cảm. Đối với mỗi người, S&ocirc;ng Đ&agrave; lại gợi một c&aacute;ch. Đ&atilde; c&oacute; lần t&ocirc;i nh&igrave;n S&ocirc;ng Đ&agrave; như một cố nhận. Chuyến ấy ở rừng đi n&uacute;i cũng đ&atilde; hơi l&acirc;u đ&atilde; thấy th&egrave;m chỗ tho&aacute;ng. Mải b&aacute;m g&oacute;t anh li&ecirc;n lạc, qu&ecirc;n đi mất l&agrave; m&igrave;nh sắp đổ ra S&ocirc;ng Đ&agrave;. Xuống một c&aacute;i dốc n&uacute;i, trước mắt thấy loang lo&aacute;ng như trẻ con nghịch chiếu gương v&agrave;o mắt m&igrave;nh rồi bỏ chạy. T&ocirc;i nh&igrave;n c&aacute;i miếng s&aacute;ng lo&eacute; l&ecirc;n một m&agrave;u nắng th&aacute;ng ba Đường thi &quot;Y&ecirc;n hoa tam nguyệt h&aacute; Dương Ch&acirc;u&quot;. Bờ s&ocirc;ng Đ&agrave;, bải S&ocirc;ng Đ&agrave;, chuồn chuồn bươm bướm tr&ecirc;n S&ocirc;ng Đ&agrave;. Chao &ocirc;i, tr&ocirc;ng con s&ocirc;ng, vui như thấy nắng gi&ograve;n tan sau k&igrave; mưa dầm, vui như nới lại chi&ecirc;m bao đứt qu&atilde;ng. Đi rừng d&agrave;i ng&agrave;y rồi lại bắt ra S&ocirc;ng Đ&agrave;, đ&uacute;ng thế, n&oacute; đằm đằm ấm ấm như gặp lại cố nh&acirc;n, mặc dầu người cố nh&acirc;n ấ m&igrave;nh biết l&agrave; lắm bệnh m&agrave; chứng, chốc dịu d&agrave;ng đấy, rồi lại bẳn t&iacute;nh v&agrave; gắt gỏng th&aacute;c lũ ngay đấy.<br />\r\n&nbsp; &nbsp; &nbsp;Thuyền t&ocirc;i tr&ocirc;i tr&ecirc;n S&ocirc;ng Đ&agrave;. Cảnh ven song ở đ&acirc;y lặng tờ. H&igrave;nh như từ đời Trần đời L&ecirc;, qu&atilde;ng s&ocirc;ng n&agrave;y cũng lặng tờ đến thế m&agrave; th&ocirc;i. Thuyền t&ocirc;i tr&ocirc;i qua một nương ng&ocirc; nh&uacute; l&ecirc;n mấy l&aacute; ng&ocirc; non đầu m&ugrave;a. M&agrave; tinh kh&ocirc;ng một b&oacute;ng người. Cỏ gianh đồi n&uacute;i đang ra những n&otilde;n b&uacute;p. Một đ&agrave;n hưu c&uacute;i đầu ngốn b&uacute;p cỏ gianh đẫm sương đ&ecirc;m. Bờ song hoang dại như một bờ tiền sử. Bờ song hồn nhi&ecirc;n như một nỗi niềm cổ t&iacute;ch tuổi xưa. Chao &ocirc;i, thấy th&egrave;m được giật m&igrave;nh v&igrave; một tiếng c&ograve;i x&uacute;p-l&ecirc; của một chuyến xe lửa đầu ti&ecirc;n đường sắt Ph&uacute; Thọ - Y&ecirc;n B&aacute;i - Lai Ch&acirc;u. Con hưu thơ ngộ ngẩng đầu nhung khỏi &aacute;ng cỏ sương, chăm chăm nh&igrave;n t&ocirc;i kh&ocirc;ng chớp mắt lừ lừ tr&ocirc;i tr&ecirc;n một mũi đ&ograve;. Hưu vễnh tai, nh&igrave;n t&ocirc;i kh&ocirc;ng ch&oacute;p mắt m&agrave; như hỏi t&ocirc;i bằng c&aacute;i tiếng n&oacute;i ri&ecirc;ng của con vật l&agrave;nh: hỏi &ocirc;ng kh&aacute;ch S&ocirc;ng Đ&agrave;, c&oacute; phải &ocirc;ng cũng vừa nghe thấy một tiếng c&ograve;i sương?&quot;. Đ&agrave;n c&aacute; dầm xanh quẫy vọt l&ecirc;n mặt song bung trắng như bạc rơi thoi. Tiếng c&aacute; đập nước s&ocirc;ng đuổi mất đ&agrave;n hươu vụt biến. Thuyền t&ocirc;i tr&ocirc;i tr&ecirc;n &quot;Dải S&ocirc;ng Đ&agrave; bọt nước l&ecirc;nh b&ecirc;nh &ndash; Bao nhi&ecirc;u cảnh bấy nhi&ecirc;u t&igrave;nh&quot; của &quot;một người t&igrave;nh nh&acirc;n chưa quen biết&quot; (Tản Đ&agrave;). D&ograve;ng song qu&atilde;ng n&agrave;y lững lờ như nhớ thương những h&ograve;n đ&aacute; th&aacute;c xa x&ocirc;i để lại tr&ecirc;n thượng nguồn T&acirc;y Bắc. V&agrave; con s&ocirc;ng như đang lắng nghe những giọng n&oacute;i &ecirc;m &ecirc;m của người xu&ocirc;i, v&agrave; con s&ocirc;ng đang tr&ocirc;i những con đ&ograve; m&igrave;nh nở chạy buồm vải n&oacute; kh&aacute;c hẳn những con đ&ograve; đu&ocirc;i &eacute;n thắt m&igrave;nh d&acirc;y cổ điển tr&ecirc;n d&ograve;ng tr&ecirc;n.<br />\r\n&nbsp; &nbsp;(Lược đoạn cuối: T&aacute;c giả cung cấp nhũng th&ocirc;ng tin về lịch sử, địa l&iacute; của s&ocirc;ng Đ&agrave; đ&agrave; lịch sử đấu tranh chống x&acirc;m lược của nh&acirc;n d&acirc;n T&acirc;y Bắc, từ cuộc chiến đấu dưới l&aacute; c&oacute; Cần vương của Ch&aacute;nh sứ sơn ph&ograve;ng Nguyễn Quang B&iacute;ch &quot;L&ograve;ng trung kh&ocirc;ng nỡ bỏ T&acirc;y Ch&acirc;u - Giữ lấy Thao, Đ&agrave; dải thượng lưu, từ cuộc nổi dậy rua năm trăm phu vận tải người Th&aacute;i chở th&oacute;c kho cho thực d&acirc;n Ph&aacute;p đ&atilde; d&ugrave;ng m&aacute;i ch&egrave;o đ&aacute;nh l&iacute;nh &aacute;p tải, cướp th&oacute;c kho v&agrave; để theo Việt Minh, đến phong tr&agrave;o đấu tranh trong l&ograve;ng địch của đồng b&agrave;o c&aacute;c d&acirc;n tộc T&acirc;y Bắc thời kh&aacute;ng chiến ch&iacute;n năm... Cuối c&ugrave;ng, t&aacute;c giả trở về hiện tại, b&aacute;o tin vui về kế hoạch cải tạo s&ocirc;ng Đ&agrave; sắp được khởi c&ocirc;ng, bắt con s&ocirc;ng hung dữ phải phục vụ cho cuộc sống của nh&acirc;n d&acirc;n T&acirc;y Bắc.)</p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(4, 'Vợ chồng a phủ', 'Văn học Việt Nam', 'Tô Hoài', 1952, 'vo-chong-a-phu.jpg', 'Đã hoàn', '<p style=\"text-align:justify\"><span style=\"display:none\">&nbsp;</span><span style=\"font-size:16px\"><span style=\"color:#000000\">&nbsp;Mị l&agrave; c&ocirc; g&aacute;i trẻ đẹp, nh&agrave; ngh&egrave;o, sống ở Hồng Ng&agrave;i. C&ocirc; bị bắt c&oacute;c về l&agrave;m vợ A Sử, l&agrave;m con d&acirc;u gạt nợ cho nh&agrave; thống l&iacute; P&aacute; Tra. C&ocirc; phải lao động quần quật, sống kh&ocirc;ng kh&aacute;c g&igrave; con tr&acirc;u, con ngựa. Khi m&ugrave;a xu&acirc;n đến, c&ocirc; cũng muốn đi chơi liền bị A Sử tr&oacute;i đ&aacute;nh đứng trong buồng. Chỉ đến khi A Sử bị đ&aacute;nh, c&ocirc; mới được cởi tr&oacute;i để đi lấy l&aacute; thuốc, xoa dầu cho chồng.&nbsp;A Phủ l&agrave; một ch&agrave;ng trai ngh&egrave;o, mồ c&ocirc;i, khỏe mạnh, gan g&oacute;c v&agrave; giỏi lao động. V&igrave; đ&aacute;nh A Sử đến ph&aacute; rối cuộc chơi n&ecirc;n bị bắt, bị đ&aacute;nh đập, bị phạt vạ, rồi trở th&agrave;nh người ở đợ trừ nợ cho nh&agrave; thống l&iacute;. Một lần để hổ ăn mất một con b&ograve;, A Phủ bị tr&oacute;i đứng, bị bỏ đ&oacute;i suốt mấy ng&agrave;y đ&ecirc;m. Một đ&ecirc;m, khi trở dậy thổi lửa để sưởi, Mị bắt gặp d&ograve;ng nước mắt chảy tr&ecirc;n g&ograve; m&aacute; đen sạm của A Phủ. Mị nghĩ về th&acirc;n phận m&igrave;nh, đồng cảm về cảnh ngộ của A Phủ. C&ocirc; đ&atilde; cắt dậy tr&oacute;i giải tho&aacute;t cho A Phủ v&agrave; bỏ trốn khỏi nh&agrave; thống l&iacute; P&aacute; Tra.&nbsp;Hai người đến Phiềng Sa, th&agrave;nh vợ th&agrave;nh chồng, tạo dựng một cuộc sống mới. A Phủ được sự gi&aacute;c ngộ của c&aacute;n bộ c&aacute;ch mạng A Ch&acirc;u trở th&agrave;nh tiểu đội trưởng du k&iacute;ch. Họ c&ugrave;ng mọi người cầm s&uacute;ng để g&igrave;n giữ bản l&agrave;ng.</span></span><span style=\"display:none\">&nbsp;</span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><strong>CHƯƠNG 1:</strong><br />\r\nAi ở xa về, c&oacute; việc v&agrave;o nh&agrave; thống l&yacute; (1) P&aacute; Tra thường tr&ocirc;ng thấy c&oacute; một c&ocirc; g&aacute;i ngồi quay sợi gai b&ecirc;n tầng đ&aacute; trước cửa, cạnh tầu ngựa.<br />\r\nL&uacute;c n&agrave;o cũng vậy, d&ugrave; quay sợi, th&aacute;i cỏ ngựa, dệt vải, chẻ củi hay đi c&otilde;ng nước dưới khe suối l&ecirc;n, c&ocirc; ấy cũng c&uacute;i mặt, mặt buồn rười rượi. Người ta n&oacute;i: nh&agrave; P&aacute; Tra l&agrave;m thống l&yacute;, ăn của d&acirc;n nhiều , đồn T&acirc;y lại cho muối về b&aacute;n, giầu lắm, nh&agrave; c&oacute; nhiều nương, nhiều bạc, nhiều thuộc phiện nhất l&agrave;ng. Thế th&igrave; con g&aacute;i n&oacute; c&ograve;n bao giờ phải xem c&aacute;i khổ m&agrave; biết khổ, m&agrave; buồn. Nhưng rồi hỏi ra mới r&otilde; c&ocirc; ấy kh&ocirc;ng phải con g&aacute;i thống l&yacute;: c&ocirc; ấy l&agrave; vợ A Sử, con trai thống l&yacute;.<br />\r\nMỵ về l&agrave;m d&acirc;u nh&agrave; P&aacute; Tra đ&atilde; mấy năm. Từ năm n&agrave;o, kh&ocirc;ng nhớ, cũng kh&ocirc;ng ai nhớ. Những người ngh&egrave;o ở Hồng Ng&agrave;i th&igrave; vẫn c&ograve;n kể lại c&acirc;u chuyện Mỵ về l&agrave;m người nh&agrave; quan thống l&yacute;.<br />\r\n(1) Tổ chức cai trị của đế quốc Ph&aacute;p trước đặt chức thống l&yacute; cho bọn chức việc người M&ocirc;ng, cũng như ch&aacute;nh thổng, l&yacute; trưởng ở xu&ocirc;i, ph&igrave;a ở l&agrave;ng Th&aacute;i.<br />\r\nNg&agrave;y xưa bố Mỵ lấy mẹ Mỵ kh&ocirc;ng đủ tiền cưới, phải đến vay nh&agrave; thống l&yacute;, bố của thống l&yacute; P&aacute; Tra b&acirc;y giờ. Mỗi năm đem nộp l&atilde;i cho nh&agrave; chủ nợ một nương ng&ocirc;. &ETH;ến tận khi hai vợ chồng về gi&agrave; m&agrave; cũng chưa xong nợ. Người vợ chết, cũng chưa trả hết nợ.<br />\r\nCho tới năm ấy Mỵ đ&atilde; lớn, Mỵ l&agrave; con g&aacute;i đầu l&ograve;ng. Thống l&yacute; đến bảo bố Mỵ:</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\">- Cho tao đứa con g&aacute;i n&agrave;y về l&agrave;m d&acirc;u th&igrave; tao xo&aacute; hết nợ tho.<br />\r\n&Ocirc;ng l&atilde;o nghĩ năm n&agrave;o cũng phải trả một nương ng&ocirc; cho người ta, tiếc ng&ocirc;, nhưng cũng lại thương con qu&aacute;. &Ocirc;ng chưa biết n&oacute;i thế n&agrave;o th&igrave; Mỵ bảo bố rằng:<br />\r\n- Con đ&atilde; biết cuốc nương l&agrave;m ng&ocirc;, con l&agrave;m nương ng&ocirc; trả nợ thay cho bố. Bố đừng b&aacute;n con cho nh&agrave; gi&agrave;u.<br />\r\nTết năm ấy, tết vui chơi, trai g&aacute;i đ&aacute;nh pao, đ&aacute;nh quay rồi đ&ecirc;m đ&ecirc;m rủ nhau đi chơi. Những nh&agrave; c&oacute; con g&aacute;i, bố mẹ kh&ocirc;ng thể ngủ được v&igrave; tiếng ch&oacute; sủa. Suốt đ&ecirc;m, con trai đến nh&agrave; người m&igrave;nh y&ecirc;u, đứng thổi s&aacute;o xung quanh v&aacute;ch.<br />\r\nTrai đứng nhắn cả ch&acirc;n v&aacute;ch đầu buồng Mỵ.<br />\r\nMột đ&ecirc;m, khuya Mỵ nghe tiếng g&otilde; v&aacute;ch. Tiếng g&otilde; v&aacute;ch hẹn của người y&ecirc;u. Mỵ hồi hộp lặng lẽ quờ tay l&ecirc;n, gặp hai ng&oacute;n tay l&aacute;ch v&agrave;o khe gỗ. Một ng&oacute;n đeo nhẫn. Người y&ecirc;u Mỵ đeo nhẫn ng&oacute;n tay ấy. Mỵ nhấc tấm v&aacute;ch gỗ. Một b&agrave;n tay bắt Mỵ bước ra.<br />\r\nMỵ vừa bước ra, lập tức c&oacute; mấy người cho&agrave;ng đến, nh&eacute;t &aacute;o v&agrave;o miệng Mỵ rồi bịt mắt c&otilde;ng Mỵ đi.<br />\r\nS&aacute;ng h&ocirc;m sau, Mỵ mới biết m&igrave;nh đang ngồi trong nh&agrave; thống l&yacute;. Họ nh&oacute;t Mỵ v&agrave;o buồng.<br />\r\nNgo&agrave;i v&aacute;ch kia, tiếng nhạc sinh tiền c&uacute;ng ma rập rờn nhảy m&uacute;a.<br />\r\nA Sử đến nh&agrave; bố Mỵ.<br />\r\nA Sử n&oacute;i:<br />\r\n- T&ocirc;i đ&atilde; đem con g&aacute;i bố về c&uacute;ng tr&igrave;nh ma nh&agrave; t&ocirc;i. B&acirc;y giờ t&ocirc;i đến cho bố biết. Tiền bạc để cưới, bố t&ocirc;i bảo đ&atilde; đưa cả cho bố rồi (1)<br />\r\nRồi A Sử về. &Ocirc;ng l&atilde;o nhớ c&acirc;u n&oacute;i của thống l&yacute; dạo trước; cho con g&aacute;i về nh&agrave; thống l&yacute; th&igrave; trừ được nợ. Thế l&agrave; cha mẹ ăn bạc nh&agrave; gi&agrave;u kiếp trước, b&acirc;y giờ người ta bắt con trừ nự. Kh&ocirc;ng l&agrave;m thế n&agrave;o kh&aacute;c được rồi.<br />\r\nC&oacute; đến mấy th&aacute;ng, đ&ecirc;m n&agrave;o Mỵ cũng kh&oacute;c.<br />\r\nMột h&ocirc;m, Mỵ trốn về nh&agrave;, hai tr&ograve;ng mắt c&ograve;n đỏ hoe. Tr&ocirc;ng thấy bố, Mỵ qu&igrave;, &uacute;p mặt xuống đất, nức nở. Bố Mỵ cũng kh&oacute;c, đo&aacute;n biết l&ograve;ng con g&aacute;i:<br />\r\n- M&agrave;y về ch&agrave;o lậy tao để m&agrave;y đi chết đấy &agrave;? M&agrave;y chết nhưng nợ tao vẫn c&ograve;n quan lại bắt trả nợ. M&agrave;y chết rồi, kh&ocirc;ng lấy ai l&agrave;m nương ng&ocirc;, trả được nợ, tao th&igrave; ốm yếu qu&aacute; rồi. Kh&ocirc;ng được, con ơi!<br />\r\n(1) người M&ocirc;ng c&oacute; tục cướp vợ trai g&aacute;i y&ecirc;u nhau, bằng l&ograve;ng nhau (c&oacute; khi chỉ anh con trai muốn lấy người con g&aacute;i giữa đ&ecirc;m c&ugrave;ng một số bạn trai kh&aacute;c, đến nh&agrave; hiếm). Người con trai giữa đ&ecirc;m c&ugrave;ng một số bạn trai kh&aacute;c, đến nh&agrave; người y&ecirc;u, b&iacute; mật &quot;cướp&quot; đi. H&ocirc;m sau, người trai trở lại b&aacute;o tin cho bố người g&aacute;i ấy l&agrave; t&ocirc;i đ&atilde; cướp được con g&aacute;i &ocirc;ng l&agrave;m vợ. Thế l&agrave; phong tục bắt bố phải nhận lời. Cưới lối &quot;cướp&quot; như thế, người tai mất rất &iacute;t tiền.<br />\r\nThường m&ugrave;a xu&acirc;n ăn Tết, con trai hay đi &quot;cướp&quot; vợ. &ETH;&oacute; l&agrave; phong tục vui, thanh ni&ecirc;n rất th&iacute;ch. B&acirc;y giờ vẫn thường xảy ra.<br />\r\nMỵ chỉ bưng mặt kh&oacute;c. Mỵ n&eacute;m nắm l&aacute; ng&oacute;n(1) xuống đất. Nắm l&aacute; ng&oacute;n Mỵ đ&atilde; đi t&igrave;m hai trong rừng. Mỵ vẫn giấu trong &aacute;o. Thế l&agrave; Mỵ kh&ocirc;ng đ&agrave;nh l&ograve;ng chết. Mỵ chết th&igrave; bố Mỵ c&ograve;n khổ hơn bao nhi&ecirc;u lần b&acirc;y giờ.<br />\r\nMỵ lại trở lại nh&agrave; thống l&yacute;.<br />\r\nLần lần, mấy năm qua, mấy năm sau, bố Mỵ chết. Nhưng Mỵ cũng kh&ocirc;ng c&ograve;n nghĩ đến Mỵ c&oacute; thể ăn l&aacute; ng&oacute;n tự tử. ở l&acirc;u trong c&aacute;i khổ, Mỵ cũng quen khổ rồi. B&acirc;y giờ Mỵ tưởng m&igrave;nh cũng l&agrave; con tr&acirc;u, m&igrave;nh cũng l&agrave; con ngựa. Con ngựa chỉ biết ăn cỏ, biết đi l&agrave;m m&agrave; th&ocirc;i.<br />\r\nMỵ c&uacute;i mặt, kh&ocirc;ng nghĩ ngợi nữa, l&uacute;c n&agrave;o cũng nhớ lại những việc giống nhau, mỗi năm một m&ugrave;a, mỗi th&aacute;ng lại l&agrave;m đi l&agrave;m lại: tết xong l&ecirc;n n&uacute;i h&aacute;i thuốc phiện; giữa năm th&igrave; giặt đay; đến m&ugrave;a đi nương bẻ bắp. V&agrave; d&ugrave; đi h&aacute;i củi, bung ng&ocirc;, l&uacute;c n&agrave;o cũng g&agrave;i một b&oacute; đay trong c&aacute;ch tay để tước sợi. Bao giờ cũng thế, suốt năm, suốt đời thế. Con ngựa, con tr&acirc;u l&agrave;m c&oacute; l&uacute;c, đ&ecirc;m c&ograve;n được đứng g&atilde;i ch&acirc;n, nhai cỏ, đ&agrave;n b&agrave; con g&aacute;i nh&agrave; n&agrave;y v&ugrave;i v&agrave;o việc cả đ&ecirc;m cả ng&agrave;y.<br />\r\nMỗi ng&agrave;y Mỵ c&agrave;ng kh&ocirc;ng n&oacute;i, l&ugrave;i lũi như con r&ugrave;a nu&ocirc;i trong x&oacute; cửa. ở<br />\r\nbuồng Mỵ nằm k&iacute;n m&iacute;t, c&oacute; một chiếc cửa sổ một lỗ vu&ocirc;ng ấy m&agrave; tr&ocirc;ng ra. &ETH;ến bao giờ chết th&igrave; th&ocirc;i.<br />\r\nTr&ecirc;n đầu n&uacute;i, c&aacute;c nương ng&ocirc;, nương l&uacute;a gặt xong, ng&ocirc; l&uacute;a đ&atilde; xếp y&ecirc;n c&aacute;c nh&agrave; kho.<br />\r\nTrẻ em đi h&aacute;i b&iacute; đỏ, tinh nghịch, đốt những những lều quanh nương để<br />\r\nsưởi lửa. ở Hồng Ng&agrave;i, người ta th&agrave;nh lệ, cứ ăn tết th&igrave; gặt h&aacute;i vừa đoạn, kh&ocirc;ng kể ng&agrave;y th&aacute;ng. &iexcl;n tết th&igrave; gặt h&aacute;i vừa đoạn, kh&ocirc;ng kể ng&agrave;y th&aacute;ng. &iexcl;n tết thế cho kịp mưa xu&acirc;n xuống, đi vỡ nương mới.<br />\r\n(1) một thứ thuốc độc<br />\r\nHồng Ng&agrave;i năm ấy ăn tết giữa l&uacute;c gi&oacute; thổi v&agrave;o cỏ gianh v&agrave;ng ửng, r&eacute;t<br />\r\nc&agrave;ng dữ.<br />\r\nNhưng trong c&aacute;c l&agrave;ng M&ocirc;ng &ETH;ỏ, những chiếc v&aacute;y hoa đ&atilde; được phơi ra mỏm đ&aacute;, xo&egrave; như con bướm sặc sỡ. Hoa thuốc phiện nở trắng lại nở m&agrave;u đỏ hau, đỏ thậm, rồi nở mầu t&igrave;m man m&aacute;t. &ETH;&aacute;m trẻ đợi tết, chơi quay, cười ầm tr&ecirc;n s&acirc;n chơi trước nh&agrave;.<br />\r\nNgo&agrave;i đầu n&uacute;i, đ&atilde; c&oacute; tiếng ai thổi s&aacute;o rủ bạn đi chơi. Mỵ nghe tiếng s&aacute;o vọng lại, thiết tha bồi hồi.<br />\r\n&quot;M&agrave;y c&oacute; con trai con g&aacute;i<br />\r\nM&agrave;y đi nương<br />\r\nTa kh&ocirc;ng c&oacute; con trai con g&aacute;i<br />\r\nTa đi t&igrave;m người y&ecirc;u&quot;<br />\r\nTiếng ch&oacute; sủa xa xa. Những đ&ecirc;m t&igrave;nh m&ugrave;a xu&acirc;n đ&atilde; tới.<br />\r\nở mỗi đầu l&agrave;ng đều c&oacute; một mỏm đất phẳng l&agrave;m s&acirc;n chơi chung ng&agrave;y tết. Trai g&aacute;i, trẻ con ra s&acirc;n ấy tụ tập đ&aacute;nh pao, d&aacute;nh quay, thổi s&aacute;o, thổi k&egrave;n v&agrave; nhảy.<br />\r\nCả nh&agrave; thống l&yacute; ăn xong bữa cơm tết c&uacute;ng ma. Xung quanh chi&ecirc;ng đ&aacute;nh ầm ỹ, người ốp đồng vẫn nhảy l&ecirc;n xuống, rung bần bật. Vừa hết bữa cơm tiếp ngay cuộc rượu b&ecirc;n bếp lửa.<br />\r\nNg&agrave;y tết, Mỵ cũng uống rượu. Mỵ l&eacute;n lấy hũ rượu, uống ực từng b&aacute;t. Rồi say, Mỵ lịm mặt đấy nh&igrave;n người nhảy đồng, người h&aacute;t. Nhưng l&ograve;ng Mỵ đang sống về ng&agrave;y trước, tai văng vẳng tiếng s&aacute;o gọi bạn đầu l&agrave;ng. Ng&agrave;y trước Mỵ thổi s&aacute;o giỏi. M&ugrave;a xu&acirc;n đến, Mỵ uống rượu b&ecirc;n bếp v&agrave; thổi s&aacute;o. Mỵ uốn chiếc l&aacute; tr&ecirc;n m&ocirc;i, thổi l&aacute; cũng hay như thổi s&aacute;o. C&oacute; biết bao nhi&ecirc;u người m&ecirc;, cứ ng&agrave;y đ&ecirc;m thổi s&aacute;o đi theo Mỵ hết n&uacute;i n&agrave;y sang n&uacute;i kh&aacute;c.<br />\r\nRượu tan l&uacute;c n&agrave;o. Người về, người đi chơi đ&atilde; v&atilde;n cả, Mỵ kh&ocirc;ng biết. Mỵ vẫn ngồi trơ một m&igrave;nh giữa nh&agrave;. M&atilde;i sau Mỵ mới đứng dậy. Nhưng Mỵ kh&ocirc;ng bước ra đường. Mỵ từ từ v&agrave;o buồng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#c0392b\"><strong>CHƯƠNG 2:</strong></span><br />\r\nChẳng năm n&agrave;o A Sử cho Mỵ đi chơi hết.<br />\r\nBấy giờ Mỵ ngồi xuống giường, tr&ocirc;ng ra cửa sổ lỗ vu&ocirc;ng mờ mờ trăng trắng. Từ nay Mỵ thấy phơi phới trở lại, trong l&ograve;ng đột nhi&ecirc;n vui như những đ&ecirc;m Tết ng&agrave;y trước. Mỵ trẻ, Mỵ vẫn c&ograve;n trẻ. Mỵ muốn đi chơi. Bao nhi&ecirc;u người c&oacute; chồng cũng đi chơi Tết. Huống chi A Sử với Mỵ kh&ocirc;ng c&oacute; l&ograve;ng với nhau m&agrave; vẫn phải ở với nhau. Nếu c&oacute; nắm l&aacute; ng&oacute;n trong tay l&uacute;c n&agrave;y, Mỵ sẽ ăn cho chết ngay, chứ kh&ocirc;ng buồn nhớ lại nữa. Nhớ lại, ch&igrave; thấy nước mặt ứa ra. M&agrave; tiếng s&aacute;o gọi bạn vẫn lửng lơ bay ngo&agrave;i đường.<br />\r\n&quot; Anh n&eacute;m pao<br />\r\nEm kh&ocirc;ng bắt<br />\r\nEm kh&ocirc;ng y&ecirc;u<br />\r\nQuả pao rơi rồi...&quot;<br />\r\nA Sử vừa ở đ&acirc;u về, lại sửa soạn đi chơi. A Sử thay &aacute;o mới, kho&aacute;c th&ecirc;m v&ograve;ng bạc v&agrave;o cổ rồi bịt c&aacute;i khăn trằng l&ecirc;n đầu. C&oacute; khi n&oacute; đi mấy ng&agrave;y mấy đ&ecirc;m. N&oacute; c&ograve;n đương r&igrave;nh bắt nhiều người con g&aacute;i nữa về l&agrave;m vợ. Cũng chẳng bao giờ Mỵ n&oacute;i.<br />\r\nB&acirc;y giờ Mỵ cũng kh&ocirc;ng n&oacute;i. Mỵ đến g&oacute;c nh&agrave;, lấy &ocirc;ng mỡ, sắn một miếng, bỏ th&ecirc;m vo&agrave; đĩa đ&egrave;n cho s&aacute;ng.<br />\r\nTrong đầu Mỵ đang rập rờn tiếng s&aacute;o. Mỵ muốn đi chơi. Mỵ cũng sắp đi chơi. Mỵ quấn lại t&oacute;c. Mỵ với tay lấy c&aacute;i v&aacute;y hoa vắt ph&iacute;a trong v&aacute;ch. A Sử sắp bước ra, bỗng quay lại, lấy l&agrave;m lạ. A Sử nh&igrave;n quanh thấy Mỵ r&uacute;t th&ecirc;m c&aacute;i &aacute;o.<br />\r\nA Sử hỏi:<br />\r\n- M&agrave;y muốn đi chơi &agrave;?<br />\r\nMỵ kh&ocirc;ng n&oacute;i. A Sử cũng kh&ocirc;ng hỏi th&ecirc;m. A Sử bước lại, nắm Mỵ, lấy thắt lưng tr&oacute;i tay Mỵ. N&oacute; x&aacute;ch cả một th&uacute;ng sợi đay ra trới đứng Mỵ v&agrave;o cột nh&agrave;. T&oacute;c Mỹ xo&atilde; xuống. A Sử quấn lu&ocirc;n t&oacute;c l&ecirc;n cột. Mỵ kh&ocirc;ng c&uacute;i, kh&ocirc;ng nghi&ecirc;ng được đầu nữa. Tr&oacute;i xong. A Sử thắt c&aacute;i thắt lưng xanh ra ngo&agrave;i &aacute;o rồi phẩy tay tắt đ&egrave;n, đi ra kh&eacute;p cửa buồng lại.<br />\r\nTrong b&oacute;ng tối, Mỵ đứng im như kh&ocirc;ng biết m&igrave;nh đang bị tr&oacute;i. Hơi rượu c&ograve;n nồng n&agrave;n. Mỵ vẫn nghe thấy tiếng s&aacute;o đưa Mỵ đi theo những cuộc chơi. &quot;Em kh&ocirc;ng y&ecirc;u, quả pao rơi rồi. Em y&ecirc;u người n&agrave;o, em bắt pao n&agrave;o!&quot; Mỵ v&ugrave;ng bước đi. Nhưng ch&acirc;n đau kh&ocirc;ng cựa được. Mỵ kh&ocirc;ng nghe tiếng s&aacute;o nữa. Chỉ c&ograve;n nghe tiếng ch&acirc;n ngựa đạp v&agrave;o v&aacute;ch. Ngựa vẫn đứng y&ecirc;n, g&atilde;i ch&acirc;n, nhai cỏ. Mỵ thổn thức nghĩ m&igrave;nh kh&ocirc;ng bằng con ngựa.<br />\r\nCh&oacute; sủa xa xa. Chừng đ&atilde; khuya. L&uacute;c n&agrave;y, l&uacute;c trai đang đến g&ograve; v&aacute;ch l&agrave;m hiệu, rủ người y&ecirc;u dỡ v&aacute;ch ra rừng chơi. Mỵ n&iacute;n kh&oacute;c, Mỵ lại bồi hồi.<br />\r\nCả đ&ecirc;m Mỵ phải tr&oacute;i đứng như thế. L&uacute;c th&igrave; khắp người bị d&acirc;y tr&oacute;i th&iacute;t lại, đau nhức. L&uacute;c lại tr&agrave;n trề tha thiết nhớ. Hơi rượu toả. Tiếng s&aacute;o. Tiếng ch&oacute; sửa xa xa. Mỵ l&uacute;c m&ecirc;, l&uacute;c t&igrave;nh. Cho tới khi trời tang tảng rồi kh&ocirc;ng biết s&aacute;ng từ bao giờ.<br />\r\nMỵ b&agrave;ng ho&agrave;ng tỉnh. Buổi s&aacute;ng &acirc;m &acirc;m trong c&aacute;i nh&agrave; gỗ rộng. V&aacute;ch b&ecirc;n cũng im ắng. Kh&ocirc;ng nghe tiếng lửa r&eacute;o trong l&ograve; nấu lợn. Kh&ocirc;ng một tiếng động. Kh&ocirc;ng biết b&ecirc;n bu&ocirc;ng quanh đấy, c&aacute;c chị vợ anh, vợ ch&uacute; của A Sử c&oacute; c&ograve;n ở nh&agrave;, kh&ocirc;ng biết tất cả những người đ&agrave;n b&agrave; khốn khổ sa v&agrave;o nh&agrave; quan đ&atilde; được đi chơi hay cũng đang phải tr&oacute;i như Mỵ. Mỵ kh&ocirc;ng thể biết.<br />\r\n&ETH;ời người đ&agrave;n b&agrave; lấy chồng nh&agrave; gi&agrave;u ở Hồng Ng&agrave;i, một đời người chỉ biết đi theo đuổi con ngựa của chồng. Mỵ chợt nhớ lại c&acirc;u chuyện người ta vẫn kể: đời trước, ở nh&agrave; thống l&yacute; P&aacute; Tra c&oacute; người tr&oacute;i vợ trong nh&agrave; ba ng&agrave;y rồi đi chơi, khi về nh&igrave;n đến, vợ chết rồi. Mỵ sợ qu&aacute;, Mỵ cựa quậy. Xem m&igrave;nh c&ograve;n sống hay chết. Cổ tay, đầu, bắp ch&acirc;n bị d&acirc;y ch&oacute;i xiết lại, đ&acirc;u đứt từng mảnh thịt.<br />\r\nC&oacute; tiếng x&ocirc;n xao ph&iacute;a ngo&agrave;i. Rồi một đ&aacute;m đ&ocirc;ng v&agrave;o nh&agrave;. Thống l&yacute; P&aacute; Tra xuống ngựa vứt cương cho &quot;thị sống&quot; (một chức việc đi hầu thống l&yacute; như người l&agrave;m m&otilde; thời trước) dắt ngựa v&agrave;o t&agrave;u. Nghe như bọn họ khi&ecirc;ng theo con lợn, hoặc một người phải tr&oacute;i, vừa vứt huỵnh xuống đất, cứ thở ph&ograve; ph&ograve;. A Sử chệnh choạng v&agrave;o buồng. &aacute;o r&aacute;ch toạch một mảnh vai. C&aacute;i khăn x&eacute;o trắng loang lổ m&aacute;u, xụp xuống quanh tr&aacute;n. A Sử nằm lăn ra giường. Thống l&yacute; P&aacute; Tra bước v&agrave;o. Theo sau thống l&yacute;, một lũ &quot;thống qu&aacute;n&quot; (Một chức việc như ph&oacute; l&yacute;) &quot;x&eacute;o phải&quot; (như trưởng th&ocirc;n) v&agrave; bọn thị sống vẫn thường ra v&agrave;o hầu hạ, ăn thịt uống rượu, h&uacute;t thuốc phiện nh&agrave; thống l&yacute;.<br />\r\nC&oacute; người bấy giờ mới nh&igrave;n thấy Mỵ phải tr&oacute;i đứng trong cột. Nhưng cũng kh&ocirc;ng ai để &yacute;. Họ x&uacute;m lại quanh giường A Sử.<br />\r\nP&aacute; Tra, tay vẫn cầm c&aacute;i roi ngựa, lại từ từ đi ra. Mỵ nhắm mặt lại, kh&ocirc;ng d&aacute;m nh&igrave;n. Mỵ chỉ nghe h&igrave;nh như c&oacute; tiếng &ocirc;ng thống l&yacute; gọi người ra ngo&agrave;i. Mỵ h&eacute; nh&igrave;n ra, thấy chị d&acirc;u bước tới. Người chị d&acirc;u ấy chưa gi&agrave;, nhưng c&aacute;i lưng quanh năm phải đeo thồ nặng qu&aacute;, đ&atilde; c&ograve;ng rạp xuống. Người chị d&acirc;u đến cởi ch&oacute;i cho Mỵ. Sợi d&acirc;y gai bắp ch&acirc;n vừa lỏng ra, Mỵ ng&atilde; sụp xuống. Chị d&acirc;u khẽ n&oacute;i v&agrave;o tai Mỵ:<br />\r\n- Mỵ! đi h&aacute;i thuốc cho chồng m&agrave;y.<br />\r\nMỵ qu&ecirc;n cả đau đứng l&ecirc;n. Nhưng kh&ocirc;ng nh&iacute;ch ch&acirc;n l&ecirc;n được. Mỵ phải &ocirc;m vai chị d&acirc;u. hai người khổ sở d&igrave;u nhau bước ra. V&agrave;o rừng t&igrave;m l&aacute; thuốc, Mỵ nghe n&oacute;i lại, mới biết A Sử đi chơi bị đ&aacute;nh vỡ đầu.<br />\r\nNửa đ&ecirc;m qua, A Sử v&agrave;o l&agrave;ng t&igrave;m đến đ&aacute;m tiếng s&aacute;o, tiếng kh&egrave;n. Nhiều<br />\r\nch&agrave;ng trai l&agrave;ng ấy v&agrave;o c&aacute;c l&agrave;ng kh&aacute;c, chơi quay, thổi s&aacute;o suốt ng&agrave;y, chập tối vừa tan xong chầu rượu trong nh&agrave;, b&acirc;y giờ vẫn c&ograve;n chưa chịu tan về. L&uacute;c A Sử v&agrave; ch&uacute;ng bạn k&eacute;o đến, kh&ocirc;ng c&ograve;n ai chơi trong nh&agrave;. Nhưng người ra người v&agrave;o c&ograve;n dập d&igrave;u quanh ng&otilde;.<br />\r\nA Sử đứng ngo&agrave;i, tức lắm. N&oacute; b&agrave;n với lũ kh&aacute;c, doạ đ&aacute;nh bọn trai lạ b&aacute;m quanh nh&agrave;, khiến bọn A Sử bị vương kh&ocirc;ng thể v&agrave;o được.<br />\r\nBọn A Sử n&eacute;m v&agrave;o v&aacute;ch. &Ocirc;ng bố trong nh&agrave; ra chửi. Vẫn n&eacute;m. &Ocirc;ng l&atilde;o v&agrave;o trong cửa, bắn ra hai ph&aacute;t s&uacute;ng. Thế l&agrave; tan những đ&aacute;m hẹn.<br />\r\nNhưng cũng chưa người trai n&agrave;o vội về. Họ tản v&agrave;o c&aacute;c nh&agrave; quen trong x&oacute;m. &ETH;ợi s&aacute;ng mai lại l&ecirc;n s&acirc;n đ&aacute;nh pao với con g&aacute;i trong x&oacute;m.<br />\r\nBọn A Sử cũng kh&ocirc;ng chịu để c&aacute;nh kia y&ecirc;n. S&aacute;ng sớm, khi họ vừa ra đầu ng&otilde;, bọn A Sử đ&atilde; k&eacute;o đến g&acirc;y sự. A Sử đi trước, nạm v&ograve;ng bạc rủ xuống tua chỉ xanh đỏ, chỉ ri&ecirc;ng con c&aacute;i nh&agrave; quan trong l&agrave;ng mới được đeo. A Sử h&ugrave;ng hổ bước ra. Bọn kia đứng dồn cả lại, x&ocirc;n xao.<br />\r\n- Lũ ph&aacute; đ&aacute;m ta h&ocirc;m qua đ&acirc;y rồi.<br />\r\n- A Phủ đ&acirc;u! A phủ đ&aacute;nh chết n&oacute; đi!<br />\r\nMột người to lớn chạy vụt ra, vung tay n&eacute;m con quay thẳng v&agrave;o mặt A Sử. Con quay gỗ ng&aacute;t lăng v&agrave;o giữa mặt. N&oacute; vừa kịp bưng tay l&ecirc;n, A Phủ đ&atilde; xộc tới, nắm v&ograve;ng cổ, k&eacute;o dập đầu xuống, x&eacute; vai &aacute;o, đ&aacute;nh tới tấp. Người l&agrave;ng nghe tiếng h&ograve; h&eacute;t đổ ra. Bọn trai l&agrave;ng lạ tản hết l&ecirc;n rừng. Mấy người đuổi đ&oacute;n đầu A Phủ.<br />\r\nA Phủ bị bắt sống, tr&oacute;i g&ocirc; ch&acirc;n tay lại. Vừa l&uacute;c thồng s l&yacute; P&aacute; Tra tới. Ch&uacute;ng n&oacute; xọc ngang c&aacute;i gậy, khi&ecirc;ng A Phủ mang về n&eacute;m xuống giữa nh&agrave; thống l&yacute;.<br />\r\nMỵ đi h&aacute;i được l&aacute; thuốc về, thấy trong nh&agrave; c&agrave;ng đ&ocirc;ng hơn l&uacute;c n&atilde;y. Ngo&agrave;i s&acirc;n, dưới gốc đ&agrave;o lại buộc th&ecirc;m mấy con ngựa lạ.<br />\r\nMỵ đi cửa sau v&agrave;o, l&eacute; mặt nh&igrave;n thấy một người to lớn quỳ trong g&oacute;c nh&agrave;. Mỵ đo&aacute;n đấy l&agrave; A Phủ.<br />\r\nBọn chức việc cả v&ugrave;ng Hồng Ng&agrave;i đến nh&agrave; thống l&yacute; dự đ&aacute;m kiện.<br />\r\nC&aacute;c l&yacute; dịch, quan l&agrave;ng thống qu&aacute;n, x&eacute;o phải, đội mũ quấn khăn, x&aacute;ch gậy, cưỡi ngựa k&eacute;o đến xử kiện v&agrave; ăn cỗ.<br />\r\nTrong nh&agrave; thống l&yacute; đ&atilde; b&agrave;y ra năm b&agrave;n đ&egrave;n. Kh&oacute;i thuốc phiện tu&ocirc;n ra c&aacute;c lỗ cửa sổ tun h&uacute;t xanh như kh&oacute;i bếp. Cả những người chức việc b&ecirc;n l&agrave;ng A Phủ cũng tới. Chỉ bọn trai l&agrave;ng ấy phải ngồi khoanh tay cạnh A Phủ, v&igrave; họ bị gọi sang hầu kiện. Bọn chức việc nằm d&agrave;i cả tr&ecirc;n khay đ&egrave;n.<br />\r\nSuốt từ trưa cho tới hết đ&ecirc;m, mấy chục người h&uacute;t. Tr&ecirc;n nhất l&agrave; thống l&yacute; P&aacute; Tra. Thống l&yacute; h&uacute;t xong một đợt năm điếu, đến người kh&aacute;c, lại người kh&aacute;c, cứ thế lần lượt xuống tới bọn đi gọi người về dự kiện.<br />\r\nChỉ c&oacute; đ&agrave;n b&agrave; ngồi trong buồng v&agrave; đi b&ecirc;n ngo&agrave;i d&ograve;m ng&oacute; đ&aacute;m xử kiện v&agrave; A Phủ quỳ chịu tội ở x&oacute; nh&agrave;, kh&ocirc;ng được dự tiệc h&uacute;t ấy.<br />\r\nMột loạt người vừa h&uacute;t xong, Pa Tra ngồi dậy, vuốt ngược c&aacute;i đầu trọc d&agrave;i, k&eacute;o đu&ocirc;i t&oacute;c ra đằng trước, cất gọng l&egrave; nh&egrave; gọi:<br />\r\n- Thằng A Phủ ra đ&acirc;y.<br />\r\nA Phủ ra quỳ giữa nh&agrave;. Lập tức, bọn trai l&agrave;ng x&ocirc; đến, trước nhất, chắp tay lạy lia lịa l&ecirc;n thống l&yacute; rồi quay lại đ&aacute;nh A Phủ.<br />\r\nA Phủ quỳ chịu đ&ograve;n, chỉ im như c&aacute;i tượng đ&aacute;...<br />\r\nCứ mỗi đợt bọn chức việc h&uacute;t thuốc phiện xong, A Phủ lại phải ra quỳ giữa nh&agrave;, lại bị người x&ocirc; đến đ&aacute;nh. Mặt A Phủ sưng l&ecirc;n, m&ocirc;i v&agrave; đu&ocirc;i mắt dập chảy m&aacute;u. Người đ&aacute;nh, kể, chửi , lại h&uacute;t. Kh&oacute;i thuốc phiện ng&agrave;o ngạt tu&ocirc;n qua c&aacute;c lỗ cửa sổ. Rồi P&aacute; Tra lại ng&oacute;c cổ l&ecirc;n, vuốt t&oacute;c, gọi A Phủ... Cứ như thế, suốt chiều, suốt đ&ecirc;m, c&agrave;ng h&uacute;t, c&agrave;ng tỉnh, c&agrave;ng đ&aacute;nh, c&agrave;ng chửi, c&agrave;ng h&uacute;t.<br />\r\nTrong buồng b&ecirc;n cạnh, Mỵ cũng thức suốt đ&ecirc;m, im lặng ngồi xoa thuốc dấu cho A Sử. L&uacute;c n&agrave;o Mỵ mỏi qu&aacute;, cựa m&igrave;nh, những chỗ lằn tr&oacute;i trong người lại đau &ecirc; ẩm. Mỵ lại gục đầu nằm thiếp. A Sử đạp ch&acirc;n v&agrave;o mặt Mỵ. Mỵ cho&agrave;ng thức, lại nhặt nắm l&aacute; thuốc xoa đều đều tr&ecirc;n lưng chồng.<br />\r\nNgo&agrave;i nh&agrave; vẫn r&ecirc;n l&ecirc;n từng cơn k&eacute;o thuốc phiện, như những con mọt nghiến ghỗ k&eacute;o d&agrave;i, giữa tiếng người kh&oacute;c, tiếng người kể l&agrave;o x&agrave;o, v&agrave; tiếng đấm đ&aacute;nh huỳnh huỵch.<br />\r\nS&aacute;ng h&ocirc;m sau, đ&aacute;m kiện đ&atilde; xong. Mấy người chẳng biết từ bao giờ, ngủ<br />\r\nng&aacute;y ngay b&ecirc;n khay đ&egrave;n. Bọn x&eacute;o phải đang bắc c&aacute;i chảo đồng v&agrave; x&aacute;ch ấm nước ra nấu th&ecirc;m lạng thuốc để h&uacute;t ban ng&agrave;y cho c&aacute;c quan l&agrave;ng thật t&igrave;nh, c&aacute;c quan l&agrave;ng c&ograve;n một tiệc ăn cỗ nữa.<br />\r\nThống l&yacute; mở tr&aacute;p, lấy ra một trăm đồng bạc hoa xo&egrave; b&agrave;y l&ecirc;n mặt tr&aacute;p, rồi n&oacute;i:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#c0392b\"><strong>CHƯƠNG 3:</strong></span><br />\r\n- Thằng A Phủ kia, m&agrave;y đ&agrave;nh người th&igrave; l&agrave;ng xử m&agrave;y phải nộp vạ cho người phải m&agrave;y đ&aacute;nh l&agrave; hai mươi đồng, nộp cho thống qu&aacute;n năm đ&ocirc;ng, mỗi x&eacute;o phải hai đồng, mỗi người đi gọi c&aacute;c quan l&agrave;ng về hầu kiện năm h&agrave;o. M&agrave;y phải mất tiền mời c&aacute;c quan h&uacute;t thuốc từ h&ocirc;m qua tới nay. Lại mất con lợn hai mươi c&acirc;n, chốc nữa mổ để c&aacute;c quan l&agrave;ng ăn vạ m&agrave;y. A Phủ, m&agrave;y đ&aacute;nh con quan l&agrave;ng, đ&aacute;ng lẽ l&agrave;ng xử m&agrave;y tội chết, nhưng l&agrave;ng tha cho m&agrave;y được sống m&agrave; nộp vạ. Cả tiền phạt, tiền thuốc, tiền lợn, m&agrave;y phải chịu một trăm bạc trắng. M&agrave;y kh&ocirc;ng c&oacute; trăm bạc th&igrave; tao cho m&agrave;y vay để m&agrave;y ở nợ. Bao giờ c&oacute; tiền trả th&igrave; tao cho m&agrave;y về, chưa c&oacute; tiền trả th&igrave; tao bắt m&agrave;y l&agrave;m con tr&acirc;u cho nh&agrave; tao. &ETH;ời m&agrave;y, đời con, đời ch&aacute;u m&agrave;y tao cũng bắt thế, bao giờ hết nợ tao mới th&ocirc;i. A Phủ! Lại đ&acirc;y nhận tiền quan cho vay.<br />\r\nA Phủ l&ecirc; hai c&aacute;i đầu gối sưng bạnh l&ecirc;n như mặt hổ ph&ugrave;. A Phủ c&uacute;i sờ l&ecirc;n đồng bạc tr&ecirc;n tr&aacute;p, trong khi P&aacute; Tra đốt hương, lầm rầm khấn gọi m&agrave; về nhận mặt người vay nợ. P&aacute; Tra khấn xong, A Phủ cũng nhặt xong bạc, nhưng chỉ nhặt l&agrave;m ph&eacute;p l&ecirc;n như thế rồi lại để ngay xuống mặt tr&aacute;p. Rồi P&aacute; Tra lại tr&uacute;t cả bạc v&agrave;o trong tr&aacute;p.<br />\r\nCon lợn vừa bắt về cho A Phủ thết l&agrave;ng ăn vạ đ&atilde; k&ecirc;u eng &eacute;c ngo&agrave;i s&acirc;n. &ETH;ếm tiền rồi, A Phủ kh&ocirc;ng phải quỳ, phải đ&aacute;nh nữa.<br />\r\nA Phủ đứng l&ecirc;n cầm con dao, ch&acirc;n đau bước tập tễnh, c&ugrave;ng với trai l&agrave;ng ra chọc tiết l&agrave;m thịt lợn hầu l&agrave;ng.<br />\r\nTrong nh&agrave;, thuốc phiện vẫn h&uacute;t r&agrave;o r&agrave;o.<br />\r\nThế l&agrave; từ đấy A Phủ phải ở trừ nợ cho nh&agrave; quan thống l&yacute;. &ETH;ốt rừng, c&agrave;y nương, cuốc nương, săn b&ograve; t&oacute;t, bẫy hổ, chăn b&ograve; chăn ngựa, quanh năm một th&acirc;n một m&igrave;nh rong ruổi ngo&agrave;i g&ograve; rừng. A Phủ đương tuổi sức lực. &ETH;i l&agrave;m hay đi săn c&aacute;i g&igrave; cũng phăng phăng. Kh&ocirc;ng c&ograve;n c&oacute; l&uacute;c n&agrave;o trở về l&agrave;ng b&ecirc;n. Nhưng A Phủ cũng chẳng muốn trở về l&agrave;m g&igrave; b&ecirc;n ấy.<br />\r\nA Phủ cũng kh&ocirc;ng phải người b&ecirc;n ấy. Bố mẹ đẻ A Phủ ở Hắng B&igrave;a. Năm xưa, l&agrave;ng Hắn B&igrave;a phải một trận bệnh đậu m&ugrave;a, nhiều trẻ con, cả người lớn chết, c&oacute; nơi chết cả nh&agrave;. C&ograve;n s&oacute;t lại c&oacute; một m&igrave;nh A Phủ. C&oacute; người l&agrave;ng đ&oacute;i bắt A Phủ đem xuống b&aacute;n đổi lấy th&oacute;c của người Th&aacute;i dưới c&aacute;nh đồng. A Phủ mới mười một tuổi, nhưng A Phủ gan bướng, kh&ocirc;ng chịu ở c&aacute;nh đồng thấp.<br />\r\nA Phủ trốn l&ecirc;n n&uacute;i, lưu lạc đến Hồng Ng&agrave;i. &ETH;i l&agrave;m cho nh&agrave; người, lần nữa m&ugrave;a n&agrave;y sang m&ugrave;a kh&aacute;c. Chẳng bao l&acirc;u A Phủ đ&atilde; lớn, biết đ&uacute;c lưỡi c&agrave;y, biết đục cuốc, lại c&agrave;y giỏi v&agrave; đi săn b&ograve; t&oacute;t rất bạo.<br />\r\nA Phủ khoẻ, chạy nhanh như ngựa. Con g&aacute;i trong l&agrave;ng nhiều c&ocirc; m&ecirc;. Nhiều người n&oacute;i: &quot;&ETH;ứa n&agrave;o được A Phủ cũng bằng được con tr&acirc;u tốt trong nh&agrave;.<br />\r\nChẳng mấy l&uacute;c m&agrave; gi&agrave;u&quot;. Người ta ao ước đ&ugrave;a thế th&ocirc;i chứ ph&eacute;p rượu cũng chẳng to hơn ph&eacute;p l&agrave;ng, c&ograve;n tục lệ cưới xin, m&agrave; A Phủ kh&ocirc;ng c&oacute; bố mẹ, kh&ocirc;ng c&oacute; ruộng, kh&ocirc;ng c&oacute; bạc, A Phủ kh&ocirc;ng thể lấy nổi vợ.<br />\r\nTuy nhi&ecirc;n, đang tuổi chơi, trong ng&agrave;y tết đến, A Phủ chẳng c&oacute; quần &aacute;o mới như nhiều trai kh&aacute;c, A Phủ chỉ c&oacute; độc một chiếc v&ograve;ng bằng sợi d&acirc;y đồng v&iacute;a lằn tr&ecirc;n cổ. A Phủ cứ c&ugrave;ng trai l&agrave;ng đem s&aacute;o, kh&egrave;n, đem con quay v&agrave; quả pao, quả yến đi t&igrave;m người y&ecirc;u ở c&aacute;c l&agrave;ng trong v&ugrave;ng.<br />\r\nV&igrave; thế sinh sự đ&aacute;nh nhau ở Hồng Ng&agrave;i.<br />\r\nMột năm kia, phải khi đang đốt rừng. Hổ gấu từng đ&agrave;n ra ph&aacute; nương, bắt mất nhiều b&ograve; ngựa. Nh&agrave; thống l&yacute; l&uacute;c n&agrave;o cũng đầy ngựa trong t&agrave;u trước cửa, đầy lưng b&ograve; đứng chen ch&acirc;n trong cột cửa, v&agrave; d&ecirc;, ch&oacute;, lợn nằm quanh nh&agrave;. Ng&agrave;y n&agrave;o cũng lũ lượt h&agrave;ng mấy chục con đi nương ăn. B&acirc;y giờ gặp khi rừng đ&oacute;i, mỗi lần b&ograve; ngựa đi nương, A Phủ phải ở lại trong rừng.<br />\r\nA Phủ ở lều h&agrave;ng th&aacute;ng ngo&agrave;i nương. &ETH;&ecirc;m đến, dồn b&ograve; ngựa về nằm chầu nhau ngủ quanh lều.<br />\r\nMấy ng&agrave;y A Phủ m&ecirc; mải đi bẫy d&iacute;m, kh&ocirc;ng đếm được ngựa. H&ocirc;m ấy v&agrave;o rừng thấy vết ch&acirc;n hổ. A Phủ vội ph&oacute;ng ngựa chạy vờn quanh đ&agrave;n, dồn ch&uacute;ng quấn lại để đếm. A Phủ đếm lại mấy lần. Thiếu một con b&ograve;. A Phủ nh&agrave;o v&agrave;o rừng, lần theo lốt ch&acirc;n hổ, t&igrave;m được con b&ograve; đ&atilde; bị hổ ăn thịt, chỉ c&ograve;n lại một nửa m&igrave;nh nằm ngay dưới c&acirc;y th&ocirc;ng cụt.<br />\r\nA Phủ nhặt mấy miếng thịt rơi quanh đấy rồi v&aacute;c nốt nửa con b&ograve; về. Nghĩ bụng: &quot;Con hổ n&agrave;y to lắm. Hay c&ograve;n ngưởi thấy m&ugrave;i h&ocirc;i quanh đ&acirc;y. Ta về lấy s&uacute;ng đi t&igrave;m, thế n&agrave;o cũng bắn được&quot;.<br />\r\nVề đến nh&agrave;, A Phủ lẳng vai n&eacute;m nửa con b&ograve; xuống gốc đ&agrave;o trước cửa. P&aacute; Tra bước ra hỏi:<br />\r\n- Mất mấy con b&ograve;?<br />\r\nA Phủ trả lời tự nhi&ecirc;n:<br />\r\n- T&ocirc;i về lấy s&uacute;ng. Thế n&agrave;o cũng bắn được. Con hổ n&agrave;y to lắm.<br />\r\n- P&aacute; Tra hất tay, n&oacute;i:<br />\r\n- Qu&acirc;n ăn cướp mất b&ograve; tao. A Sử đ&acirc;u! &ETH;em s&uacute;ng đi lấy con hổ về.<br />\r\nRồi P&aacute; Tra quay lại, bảo A Phủ:<br />\r\n- M&agrave;y ra ngo&agrave;i kia, lấy v&agrave;o đ&acirc;y một c&aacute;i cọc, một cuộn d&acirc;y m&acirc;y. Tao tr&oacute;i m&agrave;y chỗ kia. Bao giờ ch&uacute;ng n&oacute; bắn được con hổ về th&igrave; m&agrave;y khỏi phải chết. Nếu kh&ocirc;ng bắn được con hổ về th&igrave; tao cho m&agrave;y đứng chết ở đấy.<br />\r\nA Phủ c&atilde;i:<br />\r\n- T&ocirc;i được con hổ ấy c&ograve;n nhiều tiền hơn con b&ograve;.<br />\r\nP&aacute; Tra cười:<br />\r\n- Lấy cọc d&acirc;y m&acirc;y v&agrave;o đ&acirc;y!ư<br />\r\nKh&ocirc;ng n&oacute;i nữa, như con tr&acirc;u đ&atilde; đ&oacute;ng l&ecirc;n tr&ograve;ng. A Phủ lẳng lặng ra v&aacute;c chiếc cọc gỗ rồi lấy cuộn d&acirc;y m&acirc;y tr&ecirc;n g&aacute;c bếp xuống. Tự tay A Phủ đ&oacute;ng cọc xuống b&ecirc;n cột, P&aacute; Tra đẩy A Phủ v&agrave;o ch&acirc;n cột, hai tay bắt &ocirc;m quặt l&ecirc;n. Rồi d&acirc;y m&acirc;y cuốn từ ch&acirc;n l&ecirc;n vai, chỉ c&ograve;n cổ v&agrave; đ&acirc;u l&uacute;c lắc được.<br />\r\n&ETH;&agrave;n b&agrave; trong nh&agrave;, mỗi khi đi qua đều c&uacute;i mặt. Kh&ocirc;ng một ai d&aacute;m hỏi. Cũng kh&ocirc;ng một ai d&aacute;m nh&igrave;n ngang mặt.<br />\r\n&ETH;ến đ&ecirc;m, A Phủ c&uacute;i xuống, nhay đứt hai v&ograve;ng m&acirc;y, nh&iacute;ch d&atilde;n d&acirc;y tr&oacute;i một b&ecirc;n ta. Nhưng trời cũng vừa s&aacute;ng. P&aacute; Tra quảng th&ecirc;m một v&ograve;ng tr&ograve;ng lọng v&agrave;o cổ. Thế l&agrave; A Phủ kh&ocirc;ng c&uacute;i, kh&ocirc;ng c&ograve;n lắc được nữa.<br />\r\nA Sử v&agrave; lĩnh d&otilde;ng của thống l&yacute; đi đuổi, mấy ng&agrave;y kh&ocirc;ng l&ugrave;ng bắt được con hổ. Th&igrave; cũng mấy ng&agrave;y A Phủ phải tr&oacute;i đứng g&oacute;c nh&agrave;. &ETH;ằng kia, bếp l&ograve; bung ng&ocirc; cao ngang đầu người vẫn hừng hực đỏ rực. M&ocirc;i h&ocirc;m hai buổi, người ta ra người v&agrave;o ăn uống tấp nập. A Phủ đứng nhắm mắt, cho tới đ&ecirc;m khuya.<br />\r\nNhững đ&ecirc;m m&ugrave;a đ&ocirc;ng tr&ecirc;n n&uacute;i cao d&agrave;i v&agrave; buồn. Nếu kh&ocirc;ng c&oacute; bếp lửa sưởi kia th&igrave; Mỵ cũng đến chết h&eacute;o. Mỗi đ&ecirc;m, Mỵ dậy ra thổi lửa hơ tay, hơ lưng, kh&ocirc;ng biết bao nhi&ecirc;u lần.<br />\r\nThường khi đến g&agrave; g&aacute;y, Mỵ dậy ra bếp sưởi một l&uacute;c thật l&acirc;u, c&aacute;c chị em trong nh&agrave; mới bắt đầu ra d&oacute;m l&ograve; bung ng&ocirc;, nấu ch&aacute;o lợn.<br />\r\nMỗi đ&ecirc;m, nghe tiếng ph&ugrave; ph&ugrave; thổi bếp, a Phủ lại mở mắt. Ngọn lửa b&ugrave;ng l&ecirc;n, c&ugrave;ng l&uacute;c ấy Mỵ cũng nh&igrave;n sang, thấy mắt A Phủ trừng trừng. Mới biết n&oacute; c&ograve;n sống. Mấy đ&ecirc;m nay như thế.<br />\r\nNhưng Mỵ vẫn thản nhi&ecirc;n thổi lửa, hơ tay. nếu A Phủ l&agrave; c&aacute;i x&aacute;c chết đứng chết đấy, cũng thế th&ocirc;i. Mỵ vẫn trở dậy, vẫn sưởi, Mỵ chỉ biết, chỉ c&ograve;n ở với ngọn lửa. C&oacute; đ&ecirc;m A Sử chợt về thấy Mỵ ngồi đấy, A Sử ngứa tay đ&aacute;nh Mỵ ng&atilde; xuống cửa bếp. Nhưng đ&ecirc;m sau Mỵ vẫn ra sưởi như đ&ecirc;m trước.<br />\r\nL&uacute;c ấy đ&atilde; khuya. Trong nh&agrave; ngủ y&ecirc;n. Mỵ trở dậy thổi lửa, ngọn lửa bập b&ugrave;ng s&aacute;ng l&ecirc;n. Mỵ tr&ocirc;ng sang thấy hai mắt A Phủ cũng vừa mở. D&ograve;ng nước mắt lấp l&aacute;nh b&ograve; xuống hai h&oacute;m m&aacute; đ&atilde; x&aacute;m đen. Thấy t&igrave;nh cảnh thế, Mỵ chợt nhớ đ&ecirc;m năm trước, A Sử tr&oacute;i Mỵ, Mỵ cũng phải tr&oacute;i đứng thế kia. Nước mắt chảy xuống miệng, xuống cổ, kh&ocirc;ng biết lau đi được. Trời ơi n&oacute; bắt tr&oacute;i đứng người ta đến chết. N&oacute; bắt m&igrave;nh chết cũng th&ocirc;i. N&oacute; đ&atilde; bắt tr&oacute;i đến chết người đ&agrave;n b&agrave; ng&agrave;y trước ở c&aacute;i nh&agrave; n&agrave;y. Ch&uacute;ng n&oacute; thật độc &aacute;c. Chỉ đ&ecirc;m mai l&agrave; người ta chết, chết đau, chết đối, chết r&eacute;t, phải chết. Ta l&agrave; th&acirc;n phận đ&agrave;n b&agrave;, n&oacute; đ&atilde; bắt về tr&igrave;nh ma rồi, chỉ c&ograve;n biết đợi ng&agrave;y rũ xương ở đ&acirc;y th&ocirc;i... Người kia việc g&igrave; m&agrave; phải chết. A Phủ ... Mỵ phảng phất nghĩ như vậy.<br />\r\n&ETH;&aacute;m than đ&atilde; vạc hẳn lửa. Mỵ kh&ocirc;ng thổi cũng kh&ocirc;ng đứng l&ecirc;n. Mỵ nhớ lại đời m&igrave;nh. Mỵ tưởng tượng như c&oacute; thể một l&uacute;c n&agrave;o, biết đ&acirc;u A Phủ chẳng trốn được rồi, l&uacute;c đ&oacute; bố con thống l&yacute; sẽ đổ l&agrave; Mỵ đ&atilde; cởi tr&oacute;i cho n&oacute;, Mỵ liền phải tr&oacute;i thay v&agrave;o đấy. Mỵ chết tr&ecirc;n c&aacute;i cọc ấy. Nghĩ thế, nhưng l&agrave;m sao Mỵ cũng kh&ocirc;ng thấy sợ...<br />\r\nTrong nh&agrave; tối bưng, Mỵ r&oacute;n r&eacute;n bước lại, A Phủ vẫn nhắm mắt. Nhưng Mỵ tưởng như A Phủ biết c&oacute; người bước lại... Mỵ r&uacute;t con dao nhỏ cắt l&uacute;a, cắt n&uacute;t d&acirc;y m&acirc;y. A Phủ thở ph&egrave; từng hơi như rắn thở, kh&ocirc;ng biết m&ecirc; hay tỉnh. Lần lần, đến l&uacute;c gỡ được hết d&acirc;y tr&oacute;i ở người A Phủ th&igrave; Mỵ cũng hốt<br />\r\nhoảng. Mỵ chỉ th&igrave; th&agrave;o được một tiếng &quot;&ETH;i đi...&quot; rồi Mỵ nghẹn lại. A Phủ khuỵu xuống kh&ocirc;ng bước nổi.<br />\r\nNhưng trước c&aacute;i chết c&oacute; thể đến nơi ngay, A Phủ lại quật sức vung l&ecirc;n, chạy.<br />\r\nMỵ đứng lặng trong b&oacute;ng tối.<br />\r\nTrời tối lắm. Mỵ vẫn băng đi. Mỵ đuổi kịp A Phủ, đ&atilde; lăn, chạy xuống tới lưng dốc.<br />\r\nMỵ thở trong hơi gi&oacute; thốc lạnh buốt:<br />\r\n- A Phủ cho...<br />\r\nA Phủ chưa kịp n&oacute;i, Mỵ lại vừa thở vừa n&oacute;i:<br />\r\n- ở đ&acirc;y chết nất,<br />\r\nA Phủ chợt hiểu.<br />\r\nHai người đỡ nhau lao xuống dốc n&uacute;i.<br />\r\nHai người đi r&ograve;ng r&atilde; hơn một th&aacute;ng. Họ truyền tr&ecirc;n những triền n&uacute;i cao ngất, lốm đốm nh&agrave;, thấp tho&aacute;ng ruộng, đất đỏ, suối trắng tinh, tr&ocirc;ng thấy trước mặt m&agrave; đi mấy ng&agrave;y chưa tới. Từ Hồng Ng&agrave;i xuống qua v&ugrave;ng ruộng ở Mường Qu&agrave;i của người th&aacute;i, từ Nậm Cất sang Chống Chia, từ Chống Chia qua dốc L&ugrave;ng Ch&ugrave;ng Phủng lại trở về bờ s&ocirc;ng &ETH;&agrave; ph&iacute;a giữa ch&acirc;u Ph&ugrave; Y&ecirc;n sang ch&acirc;u Mai Sơn, chỗ đầu mối giao th&ocirc;ng của ngo&agrave;i v&ugrave;ng tự do v&agrave;o khu du k&iacute;ch của c&aacute;c d&acirc;n tộc Th&aacute;i, Dao, M&egrave;o b&ecirc;n kia sống. Rồi họ về trong những l&agrave;ng M&ocirc;ng &ETH;ỏ hẻo l&aacute;nh v&ugrave;ng Ph&igrave;a Sa. Xa lắm rồi, thống l&yacute; kh&ocirc;ng đuổi được nữa... họ nghĩ thế.<br />\r\nR&ograve;ng r&atilde;, ăn rau rừng, củ n&acirc;u, mộc nhĩ, vừa hết m&ugrave;a mưa, tới Ph&igrave;n Sa.<br />\r\nHai người tới Ph&igrave;n Sa, ở đấy kh&ocirc;ng ai biết đấy l&agrave; A Phủ, người ở nợ nh&agrave; thống l&yacute;. Người ta ngỡ đấy l&agrave; hai vợ chồng trong một nh&agrave; đ&ocirc;ng anh em ở b&ecirc;n kia dốc L&ugrave;ng Ch&ugrave;ng Phủng, nương vỡ được &iacute;t m&agrave; miệng ăn th&igrave; nhiều, anh em, vợ chồng phải chia ra, đem nhau đi t&igrave;m ăn nơi kh&aacute;c.<br />\r\nHai người nhận l&agrave; vợ chồng. M&agrave; thật th&igrave; A Phủ v&agrave; Mỵ đ&atilde; th&agrave;nh vợ chồng.</span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\">&quot;&gt;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\">&quot;&gt;</span></span></p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(5, 'Lão Hạc', 'Văn học Việt Nam', 'Nam Cao', 1943, 'lao-hac.jpg', 'Đã hoàn', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Truyện kể về l&atilde;o Hạc - một người n&ocirc;ng d&acirc;n ngh&egrave;o, sống c&ocirc; độc chỉ c&oacute; con ch&oacute; v&agrave;ng l&agrave;m bạn. Con trai l&atilde;o v&igrave; ngh&egrave;o kh&ocirc;ng lấy được vợ đ&atilde; phẫn ch&iacute; bỏ l&agrave;ng đi l&agrave;m ăn xa. L&atilde;o Hạc ở nh&agrave; chờ con về, l&agrave;m thu&ecirc; để sống. D&ugrave; đ&oacute;i l&atilde;o vẫn quyết kh&ocirc;ng b&aacute;n mảnh vườn v&agrave; kh&ocirc;ng ăn v&agrave;o số tiền d&agrave;nh dụm được do thu hoạch từ mảnh vườn; l&atilde;o giữ cả cho con trai. Sau một trận ốm dai dẳng l&atilde;o kh&ocirc;ng c&ograve;n sức đi l&agrave;m thu&ecirc; nữa. Kh&ocirc;ng c&ograve;n đường sinh sống, l&atilde;o Hạc lặng lẽ đi đến một quyết định quan trọng. L&atilde;o b&aacute;n con ch&oacute; v&agrave;ng l&atilde;o rất mực y&ecirc;u thương, mang hết số tiền d&agrave;nh dụm được v&agrave; mảnh vườn gửi cho &ocirc;ng gi&aacute;o tr&ocirc;ng coi hộ đặng sau n&agrave;y con trai trở về c&ograve;n c&oacute; c&aacute;i sinh sống. L&atilde;o chịu đ&oacute;i ăn khoai v&agrave; sau đ&oacute; chế được m&oacute;n g&igrave; ăn m&oacute;n ấy. Cuối c&ugrave;ng l&atilde;o ăn bả ch&oacute; để tự tử. &Ocirc;ng gi&aacute;o hiểu ra tất cả, v&ocirc; c&ugrave;ng đau đớn nghĩ về c&aacute;i chết của l&atilde;o Hạc v&agrave; chi&ecirc;m nghiệm về cuộc đời.</span></span></p>\r\n', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">L&atilde;o Hạc thổi c&aacute;i mồi rơm, ch&acirc;m đ&oacute;m. T&ocirc;i đ&atilde; th&ocirc;ng điếu v&agrave; bỏ thuốc rồi. T&ocirc;i mời l&atilde;o h&uacute;t trước. Nhưng l&atilde;o kh&ocirc;ng nghe... - &Ocirc;ng gi&aacute;o h&uacute;t trước đi. L&atilde;o đưa đ&oacute;m cho t&ocirc;i... - T&ocirc;i xin cụ... V&agrave; t&ocirc;i cầm lấy đ&oacute;m, vo vi&ecirc;n một điếu. T&ocirc;i r&iacute;t một hơi xong, th&ocirc;ng điếu rồi mới đặt v&agrave;o l&ograve;ng l&atilde;o. L&atilde;o bỏ thuốc, nhưng chưa h&uacute;t vội. L&atilde;o cầm lấy đ&oacute;m, gạt t&agrave;n, v&agrave; bảo: - C&oacute; lẽ t&ocirc;i b&aacute;n con ch&oacute; đấy, &ocirc;ng gi&aacute;o ạ! L&atilde;o đặt xe điếu, h&uacute;t. T&ocirc;i vừa thở kh&oacute;i, vừa g&agrave; g&agrave; đ&ocirc;i mắt của người say, nh&igrave;n l&atilde;o, nh&igrave;n để l&agrave;m ra vẻ ch&uacute; &yacute; đến c&acirc;u n&oacute;i của l&atilde;o đ&oacute; th&ocirc;i. Thật ra th&igrave; trong l&ograve;ng t&ocirc;i rất dửng dưng. T&ocirc;i nghe c&acirc;u ấy đ&atilde; nh&agrave;m rồi. T&ocirc;i lại biết rằng: l&atilde;o n&oacute;i l&agrave; n&oacute;i để c&oacute; đấy th&ocirc;i; chẳng bao giờ l&atilde;o b&aacute;n đ&acirc;u. Vả lại, c&oacute; b&aacute;n thật nữa th&igrave; đ&atilde; sao? L&agrave;m qu&aacute;i g&igrave; một con ch&oacute; m&agrave; l&atilde;o c&oacute; vẻ băn khoăn qu&aacute; thế... L&atilde;o h&uacute;t xong, đặt xe điếu cuống, quay ra ngo&agrave;i, thở kh&oacute;i. Sau một điếu thuốc l&agrave;o, &oacute;c người ta t&ecirc; dại đi trong một nỗi đ&ecirc; m&ecirc; nhẹ nh&otilde;m. L&atilde;o Hạc ngồi lặng lẽ, hưởng ch&uacute;t kho&aacute;i lạc con con ấy. T&ocirc;i cũng ngồi lặng lẽ. T&ocirc;i nghĩ đến mấy quyển s&aacute;ch qu&yacute; của t&ocirc;i. Hồi bị ốm nặng ở S&agrave;i G&ograve;n t&ocirc;i b&aacute;n gần hết cả &aacute;o quần, nhưng vẫn kh&ocirc;ng chịu b&aacute;n cho ai một quyển. Ốm dậy, t&ocirc;i về qu&ecirc;, h&agrave;nh l&yacute; chỉ vẻn vẹn c&oacute; một c&aacute;i va-ly đựng to&agrave;n những s&aacute;ch. &Ocirc;i những quyển s&aacute;ch rất n&acirc;ng niu! T&ocirc;i đ&atilde; nguyện giữ ch&uacute;ng suốt đời, để lưu lại c&aacute;i kỷ niệm một thời chăm chỉ, hăng h&aacute;i v&agrave; tin tưởng đầy những say m&ecirc; đẹp v&agrave; cao vọng: mỗi lần mở một quyển ra, chưa kịp đọc d&ograve;ng n&agrave;o, t&ocirc;i đ&atilde; thấy bừng l&ecirc;n trong l&ograve;ng t&ocirc;i như một rạng đ&ocirc;ng, c&aacute;i h&igrave;nh ảnh tuổi hai mươi trong trẻo, biết y&ecirc;u v&agrave; biết gh&eacute;t... Nhưng đời người ta kh&ocirc;ng chỉ khổ một lần. Mỗi lần c&ugrave;ng đường đất sinh nhai, v&agrave; b&aacute;n hết mọi thứ rồi, t&ocirc;i lại phải b&aacute;n đi một &iacute;t s&aacute;ch của t&ocirc;i. Sau c&ugrave;ng chỉ c&ograve;n c&oacute; năm quyển, t&ocirc;i nhất định, d&ugrave; c&oacute; phải chết cũng kh&ocirc;ng chịu b&aacute;n. Ấy thế m&agrave; t&ocirc;i cũng b&aacute;n! Mới c&aacute;ch đ&acirc;y c&oacute; hơn một th&aacute;ng th&ocirc;i, đứa con nhỏ của t&ocirc;i bị chứng lỵ gần kiệt sức... Kh&ocirc;ng! L&atilde;o Hạc ơi! Ta c&oacute; quyền giữ cho ta một t&iacute; g&igrave; đ&acirc;u? L&atilde;o qu&yacute; con ch&oacute; v&agrave;ng của l&atilde;o đ&atilde; thấm v&agrave;o đ&acirc;u với t&ocirc;i qu&yacute; năm quyển s&aacute;ch của t&ocirc;i... T&ocirc;i nghĩ thầm trong bụng thế. C&ograve;n l&atilde;o Hạc, l&atilde;o nghĩ g&igrave;? Đột nhi&ecirc;n l&atilde;o bảo t&ocirc;i: - N&agrave;y! Thằng ch&aacute;u nh&agrave; t&ocirc;i, đến một năm nay, chẳng c&oacute; giấy m&aacute; g&igrave; đấy, &ocirc;ng gi&aacute;o ạ! &Agrave;! Th&igrave; ra l&atilde;o đang nghĩ đến thằng con l&atilde;o. N&oacute; đi cao su năm s&aacute;u năm rồi. Hồi t&ocirc;i mới về, n&oacute; đ&atilde; hết một hạn c&ocirc;ng-ta. L&atilde;o Hạc, đem thư của n&oacute; sang, mượn t&ocirc;i xem. Nhưng n&oacute; xin đăng th&ecirc;m một hạn nữa... L&atilde;o vội cắt nghĩa cho t&ocirc;i hiểu tại sao l&atilde;o đang n&oacute;i chuyện con ch&oacute;, lại nhảy vọt sang chuyện thằng con như vậy: - Con ch&oacute; l&agrave; của ch&aacute;u n&oacute; mua đấy chứ!... N&oacute; mua về nu&ocirc;i, định để đến l&uacute;c cưới vợ th&igrave; giết thịt... Ấy! Sự đời lại cứ thường như vậy đấy. Người ta đ&atilde; định rồi chẳng bao giờ người ta l&agrave;m được. Hai đứa m&ecirc; nhau lắm. Bố mẹ đứa con g&aacute;i biết vậy, n&ecirc;n cũng bằng l&ograve;ng gả. Nhưng họ th&aacute;ch nặng qu&aacute;: nguy&ecirc;n tiền mặt phải một trăm đồng bạc, lại c&ograve;n cau, c&ograve;n rượu... cả cưới nữa th&igrave; mất đến cứng hai trăm bạc. L&atilde;o Hạc kh&ocirc;ng lo được. &Yacute; thằng con l&atilde;o, th&igrave; n&oacute; muốn b&aacute;n vườn, cố lo cho bằng được. Nhưng l&atilde;o kh&ocirc;ng cho b&aacute;n. Ai lại b&aacute;n vườn đi m&agrave; lấy vợ? Vả lại b&aacute;n vườn đi, th&igrave; cưới vợ về, ở đ&acirc;u? Với lại, n&oacute;i cho c&ugrave;ng nữa, nếu đằng nh&agrave; g&aacute;i họ cứ khăng khăng đ&ograve;i như vậy, th&igrave; dẫu c&oacute; b&aacute;n vườn đi cũng kh&ocirc;ng đủ cưới. L&atilde;o Hạc biết vậy đấy, nhưng cũng kh&ocirc;ng d&aacute;m xẵng. L&atilde;o t&igrave;m lời lẽ giảng giải cho con trai hiểu. L&atilde;o khuy&ecirc;n n&oacute; h&atilde;y dằn l&ograve;ng bỏ đ&aacute;m n&agrave;y, để rồi gắng lại &iacute;t l&acirc;u, xem c&oacute; đ&aacute;m n&agrave;o kh&aacute;c m&agrave; nhẹ tiền hơn sẽ liệu; chẳng lấy đứa n&agrave;y th&igrave; lấy đứa kh&aacute;c; l&agrave;ng n&agrave;y đ&atilde; chết hết con g&aacute;i đ&acirc;u m&agrave; sợ?... Lạy trời lạy đất! N&oacute; cũng l&agrave; thằng kh&aacute;, n&oacute; thấy bố n&oacute;i thế th&igrave; n&oacute; th&ocirc;i ngay, n&oacute; kh&ocirc;ng đả động đến việc cưới xin nữa. Nhưng n&oacute; c&oacute; vẻ buồn. V&agrave; l&atilde;o biết n&oacute; vẫn theo đuổi con kia m&atilde;i. L&atilde;o thương con lắm. Nhưng biết l&agrave;m sao được?... Th&aacute;ng mười năm ấy, con kia đi lấy chồng; n&oacute; lấy con trai một &ocirc;ng ph&oacute; l&yacute;, nh&agrave; c&oacute; của. Thằng con l&atilde;o sinh phẫn ch&iacute;. Ngay mấy h&ocirc;m sau, n&oacute; ra tỉnh đến sở mộ phu, đưa thẻ, k&yacute; giấy xin đi l&agrave;m đồn điền cao su... L&atilde;o r&acirc;n rấn nước mắt, bảo t&ocirc;i: - Trước khi đi, n&oacute; c&ograve;n cho t&ocirc;i ba đồng bạc, &ocirc;ng gi&aacute;o ạ! Chả biết n&oacute; gửi thẻ xong, vay trước được mấy đồng, m&agrave; đưa về cho t&ocirc;i ba đồng. N&oacute; đưa cho t&ocirc;i ba đồng m&agrave; bảo: &ldquo;Con biếu thầy ba đồng để thỉnh thoảng thầy ăn qu&agrave;; xưa nay con ở nh&agrave; m&atilde;i cũng kh&ocirc;ng nu&ocirc;i được bữa n&agrave;o, th&igrave; con đi cũng chẳng phải lo; thầy b&ograve;n vườn đất với l&agrave;m thu&ecirc; l&agrave;m mướn th&ecirc;m cho người ta thế n&agrave;o cũng đủ ăn; con đi chuyến n&agrave;y cố ch&iacute; l&agrave;m ăn, bao giờ c&oacute; bạc trăm con mới về, kh&ocirc;ng c&oacute; tiền, sống khổ sống sở ở c&aacute;i l&agrave;ng n&agrave;y, nhục lắm!...&rdquo;. T&ocirc;i chỉ c&ograve;n biết kh&oacute;c, chứ c&ograve;n biết l&agrave;m sao được nữa? Thẻ của n&oacute;, người ta giữ. H&igrave;nh của n&oacute;, người ta chụp rồi. N&oacute; lại đ&atilde; lấy tiền của người ta. N&oacute; l&agrave; người của người ta rồi, chứ đ&acirc;u c&ograve;n l&agrave; con t&ocirc;i?... * * * L&atilde;o Hạc ơi! B&acirc;y giờ th&igrave; t&ocirc;i hiểu tại sao l&atilde;o kh&ocirc;ng muốn b&aacute;n con ch&oacute; v&agrave;ng của l&atilde;o. L&atilde;o chỉ c&ograve;n một m&igrave;nh n&oacute; để l&agrave;m khu&acirc;y. Vợ l&atilde;o chết rồi. Con l&atilde;o đi bằn bặt. Gi&agrave; rồi m&agrave; ng&agrave;y cũng như đ&ecirc;m, chỉ thui thủi một m&igrave;nh th&igrave; ai m&agrave; chả phải buồn? Những l&uacute;c buồn, c&oacute; con ch&oacute; l&agrave;m bạn th&igrave; cũng đỡ buồn một ch&uacute;t. L&atilde;o gọi n&oacute; l&agrave; cậu V&agrave;ng như một b&agrave; hiếm hoi gọi đứa con cầu tự. Thỉnh thoảng kh&ocirc;ng c&oacute; việc g&igrave; l&agrave;m, l&atilde;o lại bắt rận cho n&oacute; hay đem n&oacute; ra ao tắm. L&atilde;o cho n&oacute; ăn cơm trong một c&aacute;i b&aacute;t như một nh&agrave; gi&agrave;u. L&atilde;o ăn g&igrave; l&atilde;o cũng chia cho n&oacute; c&ugrave;ng ăn. Những buổi tối, khi l&atilde;o uống rượu, th&igrave; n&oacute; ngồi ở dưới ch&acirc;n. L&atilde;o cứ nhắm v&agrave;i miếng lại gắp cho n&oacute; một miếng như người ta gắp thức ăn cho con trẻ. Rồi l&atilde;o chửi y&ecirc;u n&oacute;, l&atilde;o n&oacute;i với n&oacute; như n&oacute;i với một đứa ch&aacute;u b&eacute; về bố n&oacute;. L&atilde;o bảo n&oacute; thế n&agrave;y: - Cậu c&oacute; nhớ bố cậu kh&ocirc;ng? Hả cậu V&agrave;ng? Bố cậu l&acirc;u lắm kh&ocirc;ng c&oacute; thư về. Bố cậu đi c&oacute; lẽ được đến ba năm rồi đấy... Hơn ba năm... C&oacute; đến ng&oacute;t bốn năm... Kh&ocirc;ng biết cuối năm nay bố cậu c&oacute; về kh&ocirc;ng? N&oacute; m&agrave; về, n&oacute; cưới vợ, th&igrave; n&oacute; giết cậu. Liệu hồn cậu đấy! Con ch&oacute; vẫn hếch m&otilde;m l&ecirc;n nh&igrave;n, chẳng lộ một vẻ g&igrave;; l&atilde;o lừ mắt nh&igrave;n trừng trừng v&agrave;o mắt n&oacute;, to tiếng dọa: - N&oacute; giết m&agrave;y đấy! M&agrave;y c&oacute; biết kh&ocirc;ng? &Ocirc;ng cho th&igrave; bỏ bố! Con ch&oacute; tưởng chủ mắng, vẫy đu&ocirc;i mừng, để lấy lại l&ograve;ng chủ. L&atilde;o Hạc nạt to hơn nữa: - Mừng &agrave;? Vẫy đu&ocirc;i &agrave;? Vẫy đu&ocirc;i th&igrave; cũng giết! Cho cậu chết! Thấy l&atilde;o sừng sộ qu&aacute;, con ch&oacute; vừa vẫy đu&ocirc;i, vừa chực lảng. Nhưng l&atilde;o vội nắm lấy n&oacute;, &ocirc;m đầu n&oacute;, đập nh&egrave; nhẹ v&agrave;o lưng n&oacute; v&agrave; dấu d&iacute;: - &Agrave; kh&ocirc;ng! &Agrave; kh&ocirc;ng! Kh&ocirc;ng giết cậu V&agrave;ng đ&acirc;u nhỉ!... Cậu V&agrave;ng của &ocirc;ng ngoan lắm! &Ocirc;ng kh&ocirc;ng cho giết... &Ocirc;ng để cậu V&agrave;ng &ocirc;ng nu&ocirc;i... L&atilde;o bu&ocirc;ng n&oacute; ra để nhấc ch&eacute;n, gh&eacute; l&ecirc;n m&ocirc;i uống. L&atilde;o ngẩn mặt ra một ch&uacute;t, rồi bỗng nhi&ecirc;n thở d&agrave;i. Rồi l&atilde;o lẩm bẩm t&iacute;nh. Đấy l&agrave; l&atilde;o t&iacute;nh tiền b&ograve;n vườn của con... Sau khi thằng con đi, l&atilde;o tự hỏi rằng: &ldquo;C&aacute;i vườn l&agrave; của con ta. Hồi c&ograve;n mồ ma mẹ n&oacute;, mẹ n&oacute; cố thắt lưng buộc bụng, d&egrave; sẻn m&atilde;i, mới để ra được năm mươi đồng bạc tậu. Hồi ấy, mọi thức c&ograve;n rẻ cả... Của mẹ n&oacute; tậu, th&igrave; n&oacute; hưởng. Lớp trước n&oacute; đ&ograve;i b&aacute;n, ta kh&ocirc;ng cho b&aacute;n l&agrave; ta c&oacute; &yacute; giữ cho n&oacute;, chứ c&oacute; phải giữ để ta ăn đ&acirc;u? N&oacute; kh&ocirc;ng c&oacute; tiền cưới vợ, phẫn ch&iacute; bước ra đi, th&igrave; đến l&uacute;c c&oacute; tiền để lấy vợ, mới chịu về. Ta b&ograve;n vườn của n&oacute;, cũng n&ecirc;n để ra cho n&oacute;; đến l&uacute;c n&oacute; về, nếu n&oacute; kh&ocirc;ng đủ tiền cưới vợ th&igrave; ta th&ecirc;m v&agrave;o với n&oacute;, nếu n&oacute; c&oacute; đủ tiền cưới vợ, th&igrave; ta cho vợ chồng n&oacute; để c&oacute; ch&uacute;t vốn m&agrave; l&agrave;m ăn...&rdquo;. L&atilde;o tự bảo l&atilde;o như thế, v&agrave; l&atilde;o l&agrave;m đ&uacute;ng như thế. L&atilde;o l&agrave;m thu&ecirc; kiếm ăn. Hoa lợi của khu vườn được bao nhi&ecirc;u, l&atilde;o để ri&ecirc;ng ra. L&atilde;o chắc mẩm thế n&agrave;o đến l&uacute;c con l&atilde;o về, l&atilde;o cũng c&oacute; được một trăm đồng bạc... L&atilde;o lắc đầu ch&aacute;n nản, bảo t&ocirc;i: - Ấy thế m&agrave; b&acirc;y giờ hết nhẵn, &ocirc;ng gi&aacute;o ạ! T&ocirc;i chỉ ốm c&oacute; một trận đấy th&ocirc;i. Một trận đ&uacute;ng hai th&aacute;ng, mười t&aacute;m ng&agrave;y, &ocirc;ng gi&aacute;o ạ! Hai th&aacute;ng mười t&aacute;m ng&agrave;y đ&atilde; kh&ocirc;ng l&agrave;m ra được một xu, lại c&ograve;n thuốc, lại c&ograve;n ăn... &Ocirc;ng thử t&iacute;nh ra xem bao nhi&ecirc;u tiền v&agrave;o đấy?... Sau trận ốm, l&atilde;o yếu người đi gh&ecirc; lắm. Những c&ocirc;ng việc nặng kh&ocirc;ng l&agrave;m được nữa. L&agrave;ng mất v&egrave; sợi, nghề vải đảnh phải bỏ. Đ&agrave;n b&agrave; rỗi r&atilde;i nhiều. C&ograve;n t&iacute; việc nhẹ n&agrave;o, họ tranh nhau l&agrave;m mất cả. L&atilde;o Hạc kh&ocirc;ng c&oacute; việc. Rồi lại b&atilde;o. Hoa mầu bị ph&aacute; sạch s&agrave;nh sanh. Từ ng&agrave;y b&atilde;o đến nay, vườn l&atilde;o chưa c&oacute; một t&iacute; g&igrave; b&aacute;n. Gạo th&igrave; cứ k&eacute;m m&atilde;i đi. Một l&atilde;o với một con ch&oacute; mỗi ng&agrave;y ba h&agrave;o gạo, m&agrave; gia sự vẫn c&ograve;n đ&oacute;i deo đ&oacute;i dắt... - Th&igrave; ra cậu V&agrave;ng cậu ấy ăn khỏe hơn cả t&ocirc;i &ocirc;ng gi&aacute;o ạ. Mỗi ng&agrave;y cậu ấy ăn thế, bỏ rẻ cũng mất h&agrave;o rưỡi, hai h&agrave;o đấy. Cứ m&atilde;i thế n&agrave;y th&igrave; t&ocirc;i lấy tiền đ&acirc;u m&agrave; nu&ocirc;i được? M&agrave; cho cậu ấy ăn &iacute;t th&igrave; cậu ấy g&agrave;y đi, b&aacute;n hụt tiền, c&oacute; phải ho&agrave;i kh&ocirc;ng? B&acirc;y giờ cậu ấy b&eacute;o tr&ugrave;ng trục, mua đắt, người ta cũng th&iacute;ch... L&atilde;o ngắt lại một ch&uacute;t, rồi tắc lưỡi: - Th&ocirc;i th&igrave; b&aacute;n phắt đi! Đỡ được đồng n&agrave;o, hay đồng ấy. B&acirc;y giờ ti&ecirc;u một xu cũng l&agrave; ti&ecirc;u v&agrave;o tiền của ch&aacute;u. Ti&ecirc;u lắm chỉ chết n&oacute;. T&ocirc;i b&acirc;y giờ c&oacute; l&agrave;m g&igrave; được đ&acirc;u? * * * H&ocirc;m sau l&atilde;o Hạc sang nh&agrave; t&ocirc;i. Vừa thấy t&ocirc;i, l&atilde;o b&aacute;o ngay: - Cậu V&agrave;ng đi đời rồi, &ocirc;ng gi&aacute;o ạ! - Cụ b&aacute;n rồi? - B&aacute;n rồi? Họ vừa bắt xong. L&atilde;o cố l&agrave;m ra vẻ vui vẻ. Nhưng tr&ocirc;ng l&atilde;o cười như mếu v&agrave; đ&ocirc;i mắt l&atilde;o ầng ậng nước, t&ocirc;i muốn &ocirc;m cho&agrave;ng lấy l&atilde;o m&agrave; &ograve;a l&ecirc;n kh&oacute;c. B&acirc;y giờ th&igrave; t&ocirc;i kh&ocirc;ng x&oacute;t năm quyển s&aacute;ch của t&ocirc;i qu&aacute; như trước nữa. T&ocirc;i chỉ &aacute;i ngại cho l&atilde;o Hạc. T&ocirc;i hỏi cho c&oacute; chuyện: - Thế n&oacute; cho bắt &agrave;? Mặt l&atilde;o đột nhi&ecirc;n co d&uacute;m lại. Những vết nhăn x&ocirc; lại với nhau, &eacute;p cho nước mắt chảy ra. C&aacute;i đầu l&atilde;o ngoẹo về một b&ecirc;n v&agrave; c&aacute;i miệng m&oacute;m m&eacute;m của l&atilde;o mếu như con n&iacute;t. L&atilde;o hu hu kh&oacute;c... - Khốn nạn... &Ocirc;ng gi&aacute;o ơi! N&oacute; c&oacute; biết g&igrave; đ&acirc;u! N&oacute; thấy t&ocirc;i gọi về th&igrave; chạy ngay về, vẫy đu&ocirc;i mừng. T&ocirc;i cho n&oacute; ăn cơm. N&oacute; đang ăn th&igrave; thằng Mục nấp trong nh&agrave;, ngay đằng sau n&oacute;, t&oacute;m lấy hai cẳng sau n&oacute; dốc ngược n&oacute; l&ecirc;n. Cứ thế l&agrave; thằng Mục với thằng Xi&ecirc;n, hai thằng ch&uacute;ng n&oacute; chỉ loay hoay một l&uacute;c đ&atilde; tr&oacute;i chặt cả bốn ch&acirc;n n&oacute; lại. Bấy giờ cu cậu mới biết l&agrave; cu cậu chết! N&agrave;y! &Ocirc;ng gi&aacute;o ạ! C&aacute;i giống n&oacute; cũng kh&ocirc;n! N&oacute; cứ l&agrave;m in như n&oacute; tr&aacute;ch t&ocirc;i; n&oacute; k&ecirc;u ư ử, nh&igrave;n t&ocirc;i như muốn bảo t&ocirc;i rằng: &ldquo;A! L&atilde;o gi&agrave; tệ lắm! T&ocirc;i ăn ở với l&atilde;o như thế m&agrave; l&atilde;o xử với t&ocirc;i như thế n&agrave;y?&rdquo;. Th&igrave; ra t&ocirc;i gi&agrave; bằng n&agrave;y tuổi đầu rồi c&ograve;n đ&aacute;nh lừa một con ch&oacute;, n&oacute; kh&ocirc;ng ngờ t&ocirc;i nỡ t&acirc;m lừa n&oacute;! T&ocirc;i an ủi l&atilde;o: - Cụ cứ tưởng thế đấy chứ n&oacute; chả hiểu đ&acirc;u! Vả lại ai nu&ocirc;i ch&oacute; m&agrave; chả b&aacute;n hay giết thịt? Ta giết n&oacute; ch&iacute;nh l&agrave; h&oacute;a kiếp cho n&oacute; đấy, h&oacute;a kiếp để cho n&oacute; l&agrave;m kiếp kh&aacute;c. L&atilde;o chua ch&aacute;t bảo: - &Ocirc;ng gi&aacute;o n&oacute;i phải! Kiếp con ch&oacute; l&agrave; kiếp khổ th&igrave; ta h&oacute;a kiếp cho n&oacute; để n&oacute; l&agrave;m kiếp người, may ra c&oacute; sung sướng hơn một ch&uacute;t... kiếp người như kiếp t&ocirc;i chẳng hạn!... T&ocirc;i b&ugrave;i ng&ugrave;i nh&igrave;n l&atilde;o bảo: - Kiếp ai cũng thế th&ocirc;i, cụ ạ! Cụ tưởng t&ocirc;i sung sướng hơn chăng? - Thế th&igrave; kh&ocirc;ng biết nếu kiếp người cũng khổ nốt th&igrave; ta n&ecirc;n l&agrave;m g&igrave; cho thật sướng? L&atilde;o cười v&agrave; ho s&ograve;ng sọc. T&ocirc;i nắm lấy c&aacute;i vai gầy của l&atilde;o, &ocirc;n tồn bảo: - Chẳng kiếp g&igrave; sung sướng thật, nhưng c&oacute; c&aacute;i n&agrave;y l&agrave; sung sướng: B&acirc;y giờ cụ ngồi xuống phản n&agrave;y chơi, t&ocirc;i đi luộc mấy củ khoai lang, nấu một ấm nước ch&egrave; tươi thật đặc; &ocirc;ng con m&igrave;nh ăn khoai, uống nước ch&egrave;, rồi h&uacute;t thuốc l&agrave;o... Thế l&agrave; sướng. - V&acirc;ng! &Ocirc;ng gi&aacute;o dạy phải! Đối với ch&uacute;ng m&igrave;nh th&igrave; thế l&agrave; sung sướng. L&atilde;o n&oacute;i xong lại cười đưa đ&agrave;. Tiếng cười gượng nhưng nghe đ&atilde; hiền hậu lại. T&ocirc;i vui vẻ bảo: - Thế l&agrave; được, chứ g&igrave;? Vậy cụ ngồi xuống đ&acirc;y, t&ocirc;i đi luộc khoai, nấu nước. - N&oacute;i đ&ugrave;a thế, chứ &ocirc;ng gi&aacute;o cho để khi kh&aacute;c?... - Việc g&igrave; c&ograve;n phải chờ khi kh&aacute;c?... Kh&ocirc;ng bao giờ n&ecirc;n ho&atilde;n sự sung sướng lại. Cụ cứ ngồi xuống đ&acirc;y! T&ocirc;i l&agrave;m nhanh lắm... - Đ&atilde; biết, nhưng t&ocirc;i c&ograve;n muốn nhờ &ocirc;ng một việc... Mặt l&atilde;o nghi&ecirc;m trang lại... - Việc g&igrave; thế, cụ? - &Ocirc;ng gi&aacute;o để t&ocirc;i n&oacute;i... N&oacute; hơi d&agrave;i d&ograve;ng một t&iacute;. - V&acirc;ng, cụ n&oacute;i. - N&oacute; thế n&agrave;y, &ocirc;ng gi&aacute;o ạ! V&agrave; l&atilde;o kể. L&atilde;o kể nhỏ nhẹ v&agrave; d&agrave;i d&ograve;ng thật. Nhưng đại kh&aacute;i c&oacute; thể r&uacute;t v&agrave;o hai việc. Việc thứ nhất: L&atilde;o th&igrave; gi&agrave;, con đi vắng, vả lại n&oacute; cũng c&ograve;n dại lắm, nếu kh&ocirc;ng c&oacute; người tr&ocirc;ng nom cho th&igrave; kh&oacute; m&agrave; giữ được vườn đất để l&agrave;m ăn ở l&agrave;ng n&agrave;y. T&ocirc;i l&agrave; người nhiều chữ nghĩa, nhiều l&yacute; luận, người ta ki&ecirc;ng nể, vậy l&atilde;o muốn nhờ t&ocirc;i cho l&atilde;o gửi ba s&agrave;o vườn của thằng con l&atilde;o, l&atilde;o viết văn tự nhượng cho t&ocirc;i để kh&ocirc;ng ai c&ograve;n tơ tưởng d&ograve;m ng&oacute; đến; khi n&agrave;o con l&atilde;o về th&igrave; n&oacute; sẽ nhận vườn l&agrave;m, nhưng văn tự cứ để t&ecirc;n t&ocirc;i cũng được, để thế để t&ocirc;i tr&ocirc;ng coi cho n&oacute;... Việc thứ hai: L&atilde;o gi&agrave; yếu lắm rồi, kh&ocirc;ng biết sống chết l&uacute;c n&agrave;o: con kh&ocirc;ng c&oacute; nh&agrave;, lỡ chết kh&ocirc;ng biết ai đứng ra lo cho được; để phiền cho h&agrave;ng x&oacute;m th&igrave; chết kh&ocirc;ng nhắm mắt: l&atilde;o c&ograve;n được hăm nhăm đồng bạc với năm đồng vừa b&aacute;n ch&oacute; l&agrave; ba mươi đồng bạc, muốn gửi t&ocirc;i để lỡ c&oacute; chết th&igrave; t&ocirc;i đem ra, n&oacute;i với h&agrave;ng x&oacute;m gi&uacute;p, gọi l&agrave; của l&atilde;o c&oacute; t&iacute; ch&uacute;t, c&ograve;n bao nhi&ecirc;u đ&agrave;nh nhờ h&agrave;ng x&oacute;m cả... T&ocirc;i bật cười bảo l&atilde;o: - Sao cụ lo xa qu&aacute; thế? Cụ c&ograve;n khỏe lắm, chưa chết đ&acirc;u m&agrave; sợ! Cụ cứ để tiền ấy m&agrave; ăn, l&uacute;c chết h&atilde;y hay! Tội g&igrave; b&acirc;y giờ nhịn đ&oacute;i m&agrave; tiền để lại? - Kh&ocirc;ng, &ocirc;ng gi&aacute;o ạ! Ăn m&atilde;i hết đi th&igrave; đến l&uacute;c chết lấy g&igrave; m&agrave; lo liệu? Đ&atilde; đ&agrave;nh rằng l&agrave; thế, nhưng t&ocirc;i b&ograve;n vườn của n&oacute; bao nhi&ecirc;u, ti&ecirc;u hết cả. N&oacute; vợ con chưa c&oacute;. Ngộ n&oacute; kh&ocirc;ng lấy g&igrave; lo được, lại b&aacute;n vườn th&igrave; sao?... T&ocirc;i cắn rơm, cắn cỏ t&ocirc;i lạy &ocirc;ng gi&aacute;o! &Ocirc;ng gi&aacute;o c&oacute; nghĩ c&aacute;i t&igrave;nh t&ocirc;i gi&agrave; nua tuổi t&aacute;c m&agrave; thương th&igrave; &ocirc;ng gi&aacute;o cứ cho t&ocirc;i gửi. Thấy l&atilde;o nằn n&igrave; m&atilde;i, t&ocirc;i đ&agrave;nh nhận vậy. L&uacute;c l&atilde;o ra về, t&ocirc;i c&ograve;n hỏi: - C&oacute; đồng n&agrave;o, cụ nhặt nhạnh đưa cho t&ocirc;i cả th&igrave; cụ lấy g&igrave; m&agrave; ăn? L&atilde;o cười nhạt bảo: - Được ạ! T&ocirc;i đ&atilde; liệu đ&acirc;u v&agrave;o đấy... Thế n&agrave;o rồi cũng xong. Lu&ocirc;n mấy h&ocirc;m, t&ocirc;i thấy l&atilde;o Hạc chỉ ăn khoai. Rồi th&igrave; khoai cũng hết. Bắt đầu từ đấy, l&atilde;o chế tạo được m&oacute;n g&igrave;, ăn m&oacute;n ấy. H&ocirc;m th&igrave; l&atilde;o ăn củ chuối, h&ocirc;m th&igrave; l&atilde;o ăn sung luộc, h&ocirc;m th&igrave; ăn rau m&aacute;, với thỉnh thoảng một v&agrave;i củ r&aacute;y, hay bữa trai, bữa ốc. T&ocirc;i n&oacute;i chuyện l&atilde;o với vợ t&ocirc;i. Thị gạt phắt đi: - Cho l&atilde;o chết! Ai bảo l&atilde;o c&oacute; tiền m&agrave; chịu khổ! L&atilde;o l&agrave;m l&atilde;o khổ chứ ai l&agrave;m l&atilde;o khổ! Nh&agrave; m&igrave;nh sung sướng g&igrave; m&agrave; gi&uacute;p l&atilde;o? Ch&iacute;nh con m&igrave;nh cũng đ&oacute;i... Chao &ocirc;i! Đối với những người ở quanh ta, nếu ta kh&ocirc;ng cố t&igrave;m m&agrave; hiểu họ, th&igrave; ta chỉ thấy họ g&agrave;n dở, ngu ngốc, bần tiện, xấu xa, bỉ ổi... to&agrave;n những cớ để cho ta t&agrave;n nhẫn; kh&ocirc;ng bao giờ ta thấy họ l&agrave; những người đ&aacute;ng thương: kh&ocirc;ng bao giờ ta thương... Vợ t&ocirc;i kh&ocirc;ng &aacute;c, nhưng thị khổ qu&aacute; rồi. Một người đau ch&acirc;n c&oacute; l&uacute;c n&agrave;o qu&ecirc;n được c&aacute;i ch&acirc;n đau của m&igrave;nh để nghĩ đến một c&aacute;i g&igrave; kh&aacute;c đ&acirc;u? Khi người ta khổ qu&aacute; th&igrave; người ta chẳng c&ograve;n nghĩ g&igrave; đến ai được nữa. C&aacute;i bản t&iacute;nh tốt của người ta bị những nỗi lo lắng, buồn đau &iacute;ch kỷ che lấp mất. T&ocirc;i biết vậy, n&ecirc;n t&ocirc;i chỉ buồn chứ kh&ocirc;ng nỡ giận. T&ocirc;i giấu giếm vợ t&ocirc;i, thỉnh thoảng gi&uacute;p ngấm ngầm l&atilde;o Hạc. Nhưng h&igrave;nh như l&atilde;o cũng biết vợ t&ocirc;i kh&ocirc;ng ưng gi&uacute;p l&atilde;o. L&atilde;o từ chối tất cả những c&aacute;i g&igrave; t&ocirc;i cho l&atilde;o. L&atilde;o từ chối một c&aacute;ch gần như l&agrave; h&aacute;ch dịch. V&agrave; l&atilde;o cứ xa t&ocirc;i dần dần... L&atilde;o kh&ocirc;ng hiểu t&ocirc;i, t&ocirc;i nghĩ vậy, v&agrave; t&ocirc;i c&agrave;ng buồn lắm. Những người ngh&egrave;o nhiều tự &aacute;i vẫn thường như thế. Họ dễ tủi th&acirc;n n&ecirc;n rất hay chạnh l&ograve;ng. Ta kh&oacute; m&agrave; ở cho vừa &yacute; họ... Một h&ocirc;m, t&ocirc;i ph&agrave;n n&agrave;n về việc ấy với binh Tư. Binh Tư l&agrave; một người l&aacute;ng giềng kh&aacute;c của t&ocirc;i: Hắn l&agrave;m nghề ăn trộm n&ecirc;n vốn kh&ocirc;ng ưa l&atilde;o Hạc bởi v&igrave; l&atilde;o lương thiện qu&aacute;. Hắn bĩu m&ocirc;i v&agrave; bảo: - L&atilde;o l&agrave;m bộ đ&acirc;y! Thật ra th&igrave; l&atilde;o chỉ t&acirc;m ngẩm thế, nhưng cũng ra phết chứ chả vừa đ&acirc;u. L&atilde;o vừa xin t&ocirc;i một &iacute;t bả ch&oacute;... T&ocirc;i trố to đ&ocirc;i mắt, ngạc nhi&ecirc;n. Hắn th&igrave; thầm: - L&atilde;o bảo c&oacute; con ch&oacute; nh&agrave; n&agrave;o cứ đến vườn nh&agrave; l&atilde;o... L&atilde;o định cho n&oacute; xơi một bữa. Nếu tr&uacute;ng, l&atilde;o với t&ocirc;i uống rượu. Hỡi ơi l&atilde;o Hạc! Th&igrave; ra đến l&uacute;c c&ugrave;ng l&atilde;o cũng c&oacute; thể l&agrave;m liều như ai hết. Một người như thế ấy!... Một người đ&atilde; kh&oacute;c v&igrave; tr&oacute;t lừa một con ch&oacute;!... Một người nhịn ăn để tiền lại l&agrave;m ma, bởi kh&ocirc;ng muốn li&ecirc;n lụy đến h&agrave;ng x&oacute;m, l&aacute;ng giềng... Con người đ&aacute;ng k&iacute;nh ấy b&acirc;y giờ cũng theo g&oacute;t binh Tư để c&oacute; ăn ư? Cuộc đời quả thật cứ một ng&agrave;y một th&ecirc;m đ&aacute;ng buồn... * * * Kh&ocirc;ng! Cuộc đời chưa hẳn đ&atilde; đ&aacute;ng buồn, hay vẫn đ&aacute;ng buồn nhưng lại đ&aacute;ng buồn theo một nghĩa kh&aacute;c. T&ocirc;i ở nh&agrave; binh Tư về được một l&uacute;c l&acirc;u th&igrave; thấy những tiếng nhốn nh&aacute;o ở b&ecirc;n nh&agrave; l&atilde;o Hạc. T&ocirc;i mải mốt chạy sang. Mấy người h&agrave;ng x&oacute;m đến trước t&ocirc;i đang x&ocirc;n xao ở trong nh&agrave;. T&ocirc;i xồng xộc chạy v&agrave;o. L&atilde;o Hạc đang vật v&atilde; ở tr&ecirc;n giường, đầu t&oacute;c rũ rượi, quần &aacute;o xộc xệch, hai mắt long s&ograve;ng sọc. L&atilde;o tru tr&eacute;o, bọt m&eacute;p s&ugrave;i ra, khắp người chốc chốc lại bị giật mạnh một c&aacute;i, nẩy l&ecirc;n. Hai người đ&agrave;n &ocirc;ng lực lưỡng phải ngồi đ&egrave; l&ecirc;n người l&atilde;o. L&atilde;o vật v&atilde; đến hai giờ đồng hồ rồi mới chết. C&aacute;i chết thật l&agrave; dữ dội. Chẳng ai hiểu l&atilde;o chết v&igrave; bệnh g&igrave; m&agrave; đau đớn v&agrave; bất th&igrave;nh l&igrave;nh như vậy. Chỉ c&oacute; t&ocirc;i với binh Tư hiểu. Nhưng n&oacute;i ra l&agrave;m g&igrave; nữa! L&atilde;o Hạc ơi! L&atilde;o h&atilde;y y&ecirc;n l&ograve;ng m&agrave; nhắm mắt! L&atilde;o đừng lo g&igrave; cho c&aacute;i vườn của l&atilde;o. T&ocirc;i sẽ cố giữ g&igrave;n cho l&atilde;o. Đến khi con trai l&atilde;o về, t&ocirc;i sẽ trao lại cho hắn v&agrave; bảo hắn: &ldquo;Đ&acirc;y l&agrave; c&aacute;i vườn m&agrave; &ocirc;ng cụ th&acirc;n sinh ra anh đ&atilde; cố để lại cho anh trọn vẹn: cụ th&agrave; chết chứ kh&ocirc;ng chịu b&aacute;n đi một s&agrave;o...&rdquo;.</span></span></p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(6, 'Nhà giả kim', 'Văn học nước ngoài', 'Paulo Coelho', 1988, 'nha-gia-kim.jpg', 'Đang cập nhật', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Một c&acirc;u chuyện đơn giản về cậu b&eacute; chăn cừu vốn mộng mơ về những chuyến phi&ecirc;u lưu định mệnh, v&ocirc; t&igrave;nh gặp được một vị thầy b&oacute;i c&oacute; năng lực đặc biệt, v&agrave; sau đ&oacute; l&agrave; h&agrave;nh tr&igrave;nh từ giấc mơ trở th&agrave;nh đời thực của cậu ch&agrave;ng trong chuyến đi t&igrave;m kho b&aacute;u truyền thuyết.</span></span></p>\r\n', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nh&agrave; luyện kim đan cầm lấy quyển s&aacute;ch m&agrave; một người trong đo&agrave;n lữ h&agrave;nh mang theo. Quyển s&aacute;ch đ&atilde; cũ v&agrave; long g&aacute;y nhưng &ocirc;ng vẫn đọc được t&ecirc;n t&aacute;c giả: Oscar Wilde. Mở ra đọc, &ocirc;ng thấy c&oacute; một c&acirc;u chuyện về hoa thủy ti&ecirc;n. Dĩ nhi&ecirc;n, &ocirc;ng kh&ocirc;ng lạ g&igrave; truyền thuyết về ch&agrave;ng Narziss xinh trai, ng&agrave;y ng&agrave;y soi mặt tr&ecirc;n hồ nước để tự chi&ecirc;m ngưỡng sắc đẹp của m&igrave;nh. Ch&agrave;ng say m&ecirc; ch&iacute;nh m&igrave;nh đến nỗi một ng&agrave;y kia nghi&ecirc;ng qu&aacute; đ&agrave;, ng&atilde; xuống hồ v&agrave; chết đuối. Thế l&agrave; từ nơi đ&oacute; mọc l&ecirc;n một b&ocirc;ng hoa đẹp, mang t&ecirc;n ch&agrave;ng Narziss nọ. Nhưng Oscar Wilde kh&ocirc;ng kết th&uacute;c c&acirc;u chuyện như thế m&agrave; kể rằng sau khi ch&agrave;ng chết, những n&agrave;ng ti&ecirc;n trong rừng hiện ra, thấy hồ nước ngọt kia giờ đ&atilde; biến th&agrave;nh một đầm lầy mặn v&igrave; nước mắt. &quot;V&igrave; sao em kh&oacute;c?&quot; &ndash; c&aacute;c n&agrave;ng ti&ecirc;n hỏi. &quot;V&igrave; em thương tiếc ch&agrave;ng Narziss&quot;, hồ nước đ&aacute;p. &quot;Phải rồi. C&aacute;c chị chẳng ngạc nhi&ecirc;n t&iacute; n&agrave;o. V&agrave; tuy tất cả ch&uacute;ng ta đều theo đuổi ch&agrave;ng nhưng chỉ m&igrave;nh em được chi&ecirc;m ngưỡng sắc đẹp tuyệt vời ấy&quot;. &quot;Ch&agrave;ng xinh trai đến thế ư?&quot;, hồ nước ngơ ng&aacute;c hỏi. &quot;C&ograve;n ai biết điều n&agrave;y r&otilde; hơn l&agrave; em chứ?&quot; &ndash; c&aacute;c n&agrave;ng ti&ecirc;n ngạc nhi&ecirc;n &ndash; &quot;ng&agrave;y n&agrave;o m&agrave; ch&agrave;ng chẳng c&uacute;i người soi m&igrave;nh tr&ecirc;n mặt hồ&quot;. Nghe thế, hồ nước im lăng hồi l&acirc;u rồi mới đ&aacute;p: &quot;Đ&uacute;ng l&agrave; em kh&oacute;c ch&agrave;ng Narziss, nhưng em chưa bao giờ để &yacute; rằng ch&agrave;ng đẹp trai đến thế. Em kh&oacute;c ch&agrave;ng v&igrave; mỗi lần ch&agrave;ng soi người tr&ecirc;n mặt hồ th&igrave; em mới thấy được sắc đẹp của ch&iacute;nh em hiện l&ecirc;n r&otilde; trong đ&ocirc;i mắt ch&agrave;ng&quot;. &quot;Quả l&agrave; một c&acirc;u chuyện tuyệt vời&quot;, nh&agrave; luyện kim đan n&oacute;i. *** Khi cậu chăn cừu Santiago xua được đ&agrave;n cừu về đến ng&ocirc;i nh&agrave; thờ cổ v&agrave; hoang phế th&igrave; trời đ&atilde; sẩm tối. Ng&ocirc;i nh&agrave; thờ n&agrave;y đ&atilde; bị sập m&aacute;i từ kh&aacute; l&acirc;u rồi v&agrave; nơi xưa kia l&agrave; ph&ograve;ng thay &aacute;o lễ nay sừng sững một c&acirc;y d&acirc;u tằm to tướng. Cậu quyết định ngủ qua đ&ecirc;m tại đấy. Thế l&agrave; cậu l&ugrave;a lũ cừu qua khung cửa đ&atilde; hư hại rồi chắn lại bằng v&agrave;i thanh gỗ để đ&ecirc;m đến lũ vật khỏi chui ra. Tuy v&ugrave;ng n&agrave;y kh&ocirc;ng c&oacute; ch&oacute; s&oacute;i nhưng đ&atilde; c&oacute; đ&ecirc;m một con cừu chui ra ngo&agrave;i khiến h&ocirc;m sau cậu mất cả ng&agrave;y đi t&igrave;m. Rồi cậu trải &aacute;o kho&aacute;c tr&ecirc;n nền đất, ngả lưng &agrave; d&ugrave;ng quyển s&aacute;ch đang dọc dở l&agrave;m gối. Trước khi ngủ, cậu tự nhủ sau n&agrave;y phải t&igrave;m những s&aacute;ch d&agrave;y hơn để vừa c&oacute; thể đọc được l&acirc;u, vừa c&oacute; thể d&ugrave;ng l&agrave;m gối tốt hơn. Khi cậu thức giấc th&igrave; trời c&ograve;n tối mịt. Nh&igrave;n l&ecirc;n trời cao, cậu thấy sao lấp l&aacute;nh giữa những x&agrave; ngang. &quot;M&igrave;nh c&ograve;n muốn ngủ tiếp m&agrave;&quot;, cậu thầm nghĩ. Cậu lại vừa mơ giấc mơ y hệt c&aacute;ch đ&acirc;y một tuần v&agrave; lần n&agrave;y cũng thức giấc giữa cơn mơ. Cậu ngồi dậy, uống một hớp vang rồi d&ugrave;ng loại gậy của người chăn cừu đ&aacute;nh thức từng con một. C&agrave;ng ng&agrave;y cậu c&agrave;ng c&oacute; cảm tưởng l&agrave; lũ vật c&ugrave;ng thức dậy một lượt với m&igrave;nh như thể c&oacute; một sự h&ograve;a điệu thần b&iacute; giữa đời cậu v&agrave; những con vật từ hai năm nay đi theo cậu, nay đ&acirc;y mai đ&oacute;, t&igrave;m thức ăn v&agrave; nước uống. &quot;Ch&uacute;ng đ&atilde; qu&aacute; quen với m&igrave;nh n&ecirc;n biết lu&ocirc;n cả giờ giấc của m&igrave;nh&quot;, cậu nghĩ. Nhưng sau một l&uacute;c suy ngẫm cậu lại thấy c&oacute; thể ngược lại lắm, rằng cậu đ&atilde; quen với giờ giấc của bầy cừu. Một v&agrave;i con chưa chịu dậy ngay. Cậu lấy gậy đ&aacute;nh thức, gọi t&ecirc;n từng con một. Cậu lu&ocirc;n lu&ocirc;n c&oacute; cảm tưởng lũ cừu hiểu biết những g&igrave; m&igrave;nh n&oacute;i. Cho n&ecirc;n đ&ocirc;i l&uacute;c cậu đọc cho ch&uacute;ng nghe v&agrave;i đoạn trong những quyển s&aacute;ch m&agrave; cậu đặc biệt th&iacute;ch, hoặc cậu triết l&iacute; về cuộc sống đơn độc v&agrave; niềm vui của người chăn cừu, hay l&agrave; b&igrave;nh phẩm về những tin mới biết được nơi những th&agrave;nh phố c&acirc;u thường đi qua. Nhưng từ hai ng&agrave;y nay cậu hầu như chỉ n&oacute;i về một đề t&agrave;i: c&ocirc; con g&aacute;i của một nh&agrave; bu&ocirc;n ở c&aacute;i th&agrave;nh phố nhỏ m&agrave; bốn ng&agrave;y nữa cậu v&agrave; lũ cừu sẽ đến. Năm ngo&aacute;i l&agrave; lần đầu ti&ecirc;n cậu đến nh&agrave; người l&aacute;i bu&ocirc;n n&agrave;y, chủ một cửa h&agrave;ng vải v&oacute;c. &Ocirc;ng ta đ&oacute;i phải x&eacute;n l&ocirc;ng cừu ngay trước cửa h&agrave;ng để khỏi bị lọc lừa. Lần đ&oacute; một người quen đ&atilde; giới thiệu với cậu cửa h&agrave;ng n&agrave;y v&agrave; giờ đ&acirc;y cậu lại dẫn bầy cừu đến đ&oacute;. &quot;T&ocirc;i muốn b&aacute;n l&ocirc;ng cừu&quot;, lần đ&oacute; cậu đ&atilde; n&oacute;i với &ocirc;ng nh&agrave; bu&ocirc;n kia như thế. Cửa tiệm đang đầy kh&aacute;ch h&agrave;ng n&ecirc;n &ocirc;ng ta y&ecirc;u cầu ch&agrave;ng chăn cừu đợi đến xế trưa. Thế l&agrave; cậu ngồi ngay xuống lề đường trước cửa hiệu, l&ocirc;i trong bị ra một quyển s&aacute;ch. &quot;M&igrave;nh kh&ocirc;ng biết l&agrave; chăn cừu m&agrave; cũng đọc được s&aacute;ch đấy&quot;, một giọng con g&aacute;i cất l&ecirc;n ngay cạnh cậu. Đ&uacute;ng l&agrave; một c&ocirc; g&aacute;i đất Andalusia (Andalusia: v&ugrave;ng ph&iacute;a nam T&acirc;y Ban Nha, s&aacute;t Đại T&acirc;y Dương v&agrave; Địa Trung Hải), với m&aacute;i t&oacute;c đen d&agrave;i v&agrave; đ&ocirc;i mắt phảng phất n&eacute;t người Mauren (Người Mauren (tiếng Anh: Moor): một sắc d&acirc;n du mục ở Bắc Phi, đ&atilde; đ&ocirc; hộ T&acirc;y Ban Nha từ thế kỉ VIII đến thế kỉ XV) x&acirc;m lược xưa kia. &quot;Bởi v&igrave; giống cừu c&ograve;n dạy t&ocirc;i biết được nhiều điều hơn l&agrave; s&aacute;ch vở&quot;, cậu đ&aacute;p. Hai người tr&ograve; chuyện t&iacute;u t&iacute;t suốt hơn hai tiếng. C&ocirc; g&aacute;i cho biết m&igrave;nh l&agrave; con người chủ tiệm kia v&agrave; kể về cuộc sống đơn điệu, ng&agrave;y n&agrave;o như ng&agrave;y nấy ở đ&oacute;. Về phần m&igrave;nh, cậu chăn cừu kể về phong cảnh v&ugrave;ng Andalusia v&agrave; về những tin mới lạ ở những nơi cậu đ&atilde; đi qua. Cậu lấy l&agrave;m vui sướng v&igrave; c&oacute; người lắng nghe chuyện m&igrave;nh. &quot;Anh học đọc s&aacute;ch như thế n&agrave;o?&quot;, c&ocirc; muốn biết. &quot;Trong trường học, như mọi người kh&aacute;c&quot;, cậu đ&aacute;p. &quot;Nhưng nếu anh đọc s&aacute;ch được th&igrave; sao anh chỉ th&agrave;nh một người chăn cừu b&igrave;nh thường th&ocirc;i?&quot;. Cậu bối rối v&igrave; tin chắc rằng c&ocirc; sẽ kh&ocirc;ng hiểu nổi cậu. Để tr&aacute;nh n&eacute; trả lời, cậu tiếp tục kể về h&agrave;nh tr&igrave;nh của m&igrave;nh v&agrave; đ&ocirc;i mắt nhỏ phảng phất mắt người Mauren của c&ocirc; g&aacute;i khi tr&ograve;n xoe, l&uacute;c nheo lại v&igrave; kinh ngạc. Thời gian tr&ocirc;i qua, c&ograve;n cậu thầm ước ng&agrave;y h&ocirc;m ấy đừng bao giờ chấm dứt, hoặc l&agrave; bố c&ocirc; cứ tiếp tục để cậu chờ th&ecirc;m ba ng&agrave;y nữa. Cậu thấy một cảm gi&aacute;c kh&aacute;c lạ chưa từng biết đến: đ&oacute; l&agrave; mơ ước được sống ổn định một nơi. C&oacute; c&ocirc; g&aacute;i n&agrave;y b&ecirc;n cạnh th&igrave; chẳng c&ograve;n ng&agrave;y n&agrave;o l&agrave; nh&agrave;m ch&aacute;n nữa. Nhưng rồi &ocirc;ng nh&agrave; bu&ocirc;n đến, bảo x&eacute;n cho l&ocirc;ng bốn con cừu, trả tiền ngay rồi bảo cậu sang năm trở lại. B&acirc;y giờ chỉ c&ograve;n c&oacute; bốn ng&agrave;y đường nữa l&agrave; đến th&agrave;nh phố kia. Cậu nao nức nhưng l&ograve;ng lại bồn chồn: biết đ&acirc;u c&ocirc; g&aacute;i đ&atilde; qu&ecirc;n cậu l&acirc;u rồi. Thiếu g&igrave; người chăn cừu đi qua đấy để b&aacute;n l&ocirc;ng cừu. &quot;Chẳng cần&quot;, cậu n&oacute;i to với bầy cừu, &quot;bề g&igrave; tao cũng quen khối con g&aacute;i ở bao th&agrave;nh phố kh&aacute;c&quot;. Nhưng trong th&acirc;m t&acirc;m cậu biết m&igrave;nh kh&ocirc;ng thể chẳng cần, v&igrave; rằng bất cứ người chăn cừu, người thủy thủ hay người kh&aacute;ch thương nay đ&acirc;y mai đ&oacute; n&agrave;o cũng c&oacute; ở đ&acirc;u đ&oacute; một kẻ khiến cho họ qu&ecirc;n mất th&uacute; vui được tự do giang hồ đi c&ugrave;ng trời cuối đất. Trời hửng s&aacute;ng. Cậu chăn cừu l&ugrave;a lũ vật đi về hướng mặt trời mọc. &#39;Lo&agrave;i vật chẳng bao giờ phải tự quyết định về bất cứ chuyện g&igrave;&#39;, cậu nghĩ. &#39;C&oacute; lẽ v&igrave; vậy m&agrave; ch&uacute;ng quấn qu&yacute;t với m&igrave;nh&#39;. Nhu cầu duy nhất của lũ cừu l&agrave; ăn v&agrave; uống. Bao l&acirc;u c&ograve;n dẫn ch&uacute;ng đến được những đồng cỏ mượt m&agrave; của v&ugrave;ng Andalusia th&igrave; ch&uacute;ng vẫn m&atilde;i l&agrave; những người bạn th&acirc;n thiết, d&ugrave; cho một ng&agrave;y như mọi ng&agrave;y với những giờ tẻ nhạt tr&ocirc;i qua từ l&uacute;c hừng đ&ocirc;ng đến khi mặt trời lặn, d&ugrave; cho c&uacute;ng kh&ocirc;ng hề đọc một quyển s&aacute;ch trong cuộc sống ngắn ngủi v&agrave; kh&ocirc;ng bao giờ hiểu được tiếng người ta kh&aacute;o nhau về những chuyện mới lạ ở nơi n&agrave;y nơi nọ. Được cho ăn v&agrave; cho uống l&agrave; ch&uacute;ng h&agrave;i l&ograve;ng. Với ch&uacute;ng th&igrave; thế l&agrave; đủ. Đổi lại, ch&uacute;ng l&agrave; những người bạn đồng h&agrave;nh đem lại niềm vui, cống hiến nhiều len v&agrave; thỉnh thoảng cả thịt nữa. &#39;Nếu bất chợt m&igrave;nh biến th&agrave;nh một kẻ hung &aacute;c, giết hết con n&agrave;y đến con kh&aacute;c th&igrave; chắc khi cả lũ chết gần hết rồi ch&uacute;ng mới biết&#39;, cậu thầm nghĩ. &#39;V&igrave; ch&uacute;ng m&ugrave; qu&aacute;ng tin v&agrave;o m&igrave;nh, chứ kh&ocirc;ng c&ograve;n tin ở bản năng của ch&uacute;ng nữa. Chỉ bởi v&igrave; m&igrave;nh l&agrave; kẻ dẫn ch&uacute;ng đến những đồng cỏ xanh v&agrave; nguồn nước m&aacute;t.&#39; Cậu chợt ngạc nhi&ecirc;n trước những suy nghĩ n&agrave;y của ch&iacute;nh m&igrave;nh. C&oacute; lẽ ng&ocirc;i nh&agrave; thờ cổ n&agrave;y c&ugrave;ng với c&acirc;y d&acirc;u tằm bị ma &aacute;m rồi chăng. D&ugrave; sao đi nữa th&igrave; cũng tại hai thứ n&agrave;y m&agrave; cậu mơ giấc mơ kia th&ecirc;m lần thứ hai v&agrave; v&ocirc; cớ tức bực với những người bạn đồng h&agrave;nh trung th&agrave;nh của m&igrave;nh.Cậu uống một ngum vang c&ograve;n lại của chiều h&ocirc;m trước v&agrave; k&eacute;o &aacute;o kho&aacute;c s&aacute;t m&igrave;nh hơn. Cậu biết chỉ &iacute;t giờ nữa, khi mặt trời đứng b&oacute;ng th&igrave; sẽ qu&aacute; n&oacute;ng, kh&ocirc;ng l&ugrave;a cừu đi tr&ecirc;n đồng cỏ được nữa. L&uacute;c đ&oacute; cả nước T&acirc;y Ban Nha sẽ ngủ giấc trưa h&egrave;. N&oacute;ng tới tận chiều, thế m&agrave; cậu vẫn phải tha theo người chiếc &aacute;o kho&aacute;c. Nhưng mỗi khi sắp c&agrave;u nh&agrave;u về c&aacute;i &aacute;o nặng như c&ugrave;m kia th&igrave; cậu lai thấy biết ơn n&oacute; v&igrave; nhờ n&oacute; m&agrave; s&aacute;ng ra cậu kh&ocirc;ng bị r&eacute;t run người. &#39;M&igrave;nh lu&ocirc;n lu&ocirc;n phải ph&ograve;ng khi thời tiết giở chứng bất thường&#39;, cậu nghĩ v&agrave; thấy sung sướng c&oacute; c&aacute;i &aacute;o kho&aacute;c nặng. C&aacute;i &aacute;o, cũng như cuộc đời cậu, c&oacute; &yacute; nghĩa của n&oacute;. Sau hai năm rong ruổi, cậu biết tường tận mọi th&agrave;nh phố v&ugrave;ng Andalusia v&agrave; cả mục đ&iacute;ch của đời m&igrave;nh, đ&oacute; l&agrave;: đi du h&agrave;nh. Cậu định lần n&agrave;y sẽ giải th&iacute;ch cho c&ocirc; g&aacute;i biết v&igrave; sao m&agrave; cậu, một g&atilde; chăn cừu b&igrave;nh thường, lại biết đọc. Cho tới năm mười s&aacute;u tuổi cậu c&ograve;n theo học trong một chủng viện. Cha mẹ cậu mong cậu sẽ trở th&agrave;nh linh mục; được như thế th&igrave; mọi gia đ&igrave;nh n&ocirc;ng d&acirc;n b&igrave;nh thường như gia đ&igrave;nh cậu sẽ rất tự h&agrave;o. V&igrave; ch&iacute;nh những người n&ocirc;ng d&acirc;n n&agrave;y đến nay cũng chỉ sống nhờ ăn v&agrave; uống, chẳng kh&aacute;c g&igrave; bầy cừu của cậu. Thế l&agrave; cậu được học tiếng Latinh, tiếng T&acirc;y Ban Nha v&agrave; thần học. Nhưng từ nhỏ cậu đ&atilde; mơ ước đi c&ugrave;ng khắp thế giới bao la v&agrave; điều n&agrave;y đối với cậu quan trọng hơn l&agrave; biết về Ch&uacute;a v&agrave; tội lỗi của lo&agrave;i người. Rồi v&agrave;o một buổi xế trưa, nh&acirc;n dịp về nh&agrave; thăm cha mẹ, cậu thu hết can đảm n&oacute;i với bố rằng m&igrave;nh kh&ocirc;ng muốn trở th&agrave;nh linh mục, m&agrave; muốn được đi đ&acirc;y đ&oacute;. &quot;Từ bốn phường trời thi&ecirc;n hạ đ&atilde; đến nơi đ&acirc;y rồi, con ạ&quot;, lần ấy bố cậu n&oacute;i. &quot;Họ đến để t&igrave;m sự mới lạ, nhưng rồi th&igrave; họ vẫn m&atilde;i l&agrave; ch&iacute;nh họ. Họ leo l&ecirc;n đồi để xem th&agrave;nh qu&aacute;ch rồi cho rằng qu&aacute; khứ hay ho hơn hiện tại. D&ugrave; t&oacute;c họ v&agrave;ng hay da họ ngăm, n&oacute;i chung họ cũng giống như d&acirc;n th&agrave;nh phố n&agrave;y cả th&ocirc;i&quot;. &quot;Nhưng con chưa biết c&aacute;c th&agrave;nh qu&aacute;ch ở đất nước họ&quot;, cậu đ&aacute;p. &quot;Khi đ&atilde; quen phong thổ v&agrave; phụ nữ v&ugrave;ng n&agrave;y rồi th&igrave; những người đ&agrave;n &ocirc;ng nọ n&oacute;i rằng họ muốn vĩnh viễn ở lại đ&acirc;y&quot;, &ocirc;ng bố n&oacute;i tiếp. &quot;Con cũng rất muốn biết về phụ nữ v&agrave; đất nước của họ&quot;, cậu th&uacute; nhận. &quot;V&igrave; thật ra họ c&oacute; hề ở lại đ&acirc;y m&atilde;i đ&acirc;u&quot;. &quot;Họ thừa tiền, thừa bạc con ạ&quot;, &ocirc;ng bố đ&aacute;p. &quot;C&ograve;n v&ugrave;ng m&igrave;nh chỉ c&oacute; người chăn cừu mới phải nay đ&acirc;y mai đ&oacute; th&ocirc;i&quot;. &quot;Thế th&igrave; con sẽ l&agrave;m kẻ chăn cừu&quot;. &Ocirc;ng bố kh&ocirc;ng n&oacute;i g&igrave; nữa cả. H&ocirc;m sau &ocirc;ng cho cậu một t&uacute;i tiền đựng ba đồng tiền v&agrave;ng cổ T&acirc;y Ban Nha. &quot;Bố t&igrave;nh cờ t&igrave;m thấy trong ruộng nh&agrave; ta c&aacute;ch đ&acirc;y l&acirc;u rồi. Lẽ ra l&agrave; tiền để cho con được nhận v&agrave;o nh&agrave; thờ đấy. Con lấy m&agrave; mua một đ&agrave;n cừu, rồi đi khắp nơi khắp chốn cho đến khi con hiểu ra rằng l&agrave;ng mạc, th&agrave;nh qu&aacute;ch v&ugrave;ng m&igrave;nh tuyệt vời nhất, phụ nữ v&ugrave;ng m&igrave;nh xinh đẹp nhất.&quot; Rồi &ocirc;ng ban ph&uacute;c ch&uacute;c l&agrave;nh cho cậu. Cậu đọc thấy trong mắt bố cũng ch&iacute;nh niềm mơ ước được phi&ecirc;u du. N&oacute; vẫn sống m&atilde;i trong &ocirc;ng sau h&agrave;ng chục năm t&igrave;m c&aacute;ch l&atilde;ng qu&ecirc;n qua những lo toan sao cho c&oacute; c&aacute;i ăn, c&aacute;i uống hằng ng&agrave;y v&agrave; một chốn nương th&acirc;n. Ch&acirc;n trời đỏ ối, mặt trời dần l&ecirc;n. Cậu chăn cừu nhớ đến lần chuyện tr&ograve; với bố v&agrave; cảm thấy sung sướng; từ đ&oacute; đến nay cậu đ&atilde; qua nhiều th&agrave;nh qu&aacute;ch v&agrave; quen biết nhiều phụ nữ nhưng kh&ocirc;ng c&oacute; ai giống như c&ocirc; g&aacute;i cậu sẽ gặp lại trong &iacute;t ng&agrave;y tới. Cậu c&oacute; c&aacute;i &aacute;o kho&aacute;c, một quyển s&aacute;ch m&agrave; cậu c&oacute; thể đổi lấy một quyển kh&aacute;c v&agrave; một đ&agrave;n cừu. Nhưng c&aacute;i ch&iacute;nh l&agrave; mỗi ng&agrave;y cậu đều thực hiện được ước mơ của m&igrave;nh: đi khắp đ&oacute; đ&acirc;y. Khi n&agrave;o ch&aacute;n v&ugrave;ng Andalusia bao la, cậu c&oacute; thể b&aacute;n bầy cừu để trở th&agrave;nh thủy thủ. Rồi khi ch&aacute;n biển cả, cậu c&oacute; thể thăm th&uacute; mọi th&agrave;nh phố, l&agrave;m quen với bao phụ nữ v&agrave; thử mọi phương c&aacute;ch để đạt hạnh ph&uacute;c. &quot;M&igrave;nh thật kh&ocirc;ng hiểu nổi l&agrave;m sao người ta c&oacute; thể t&igrave;m thấy Ch&uacute;a trong lớp học cho ch&uacute;ng sinh được&quot;, cậu nghĩ trong l&uacute;c ngắm nh&igrave;n mặt trời l&ecirc;n. V&igrave; cậu lu&ocirc;n t&igrave;m những con đường mới để đi nếu điều kiện cho ph&eacute;p, n&ecirc;n d&ugrave; đ&atilde; nhiều lần qua lại v&ugrave;ng n&agrave;y nhưng trước đ&acirc;y cậu chưa từng v&agrave;o ng&ocirc;i nh&agrave; thờ hoang t&agrave;n nọ. Thế giới bao la v&ocirc; c&ugrave;ng tận; giả thử cứ để cho bầy cừu dẫn đi th&igrave; chắc hẳn cậu sẽ c&ograve;n ph&aacute;t hiện được nhiều điều hay ho nữa. &quot;Ch&uacute;ng kh&ocirc;ng nhận ra rằng ng&agrave;y ng&agrave;y ch&uacute;ng đi đường mới. Ch&uacute;ng kh&ocirc;ng biết đồng cỏ kh&aacute;c nhau v&agrave; bốn m&ugrave;a thay đổi, v&igrave; ch&uacute;ng chỉ lo c&oacute; mỗi chuyện ăn v&agrave; uống. Nhưng biết đ&acirc;u con người cũng y như thế&quot;, cậu nghĩ. &quot;Ngay ch&iacute;nh m&igrave;nh cũng kh&ocirc;ng nghĩ đến c&ocirc; g&aacute;i n&agrave;o kh&aacute;c từ khi quen con g&aacute;i chủ tiệm vải nọ.&quot; Cậu nh&igrave;n trời v&agrave; &aacute;ng chừng sẽ đến Tarifa trước giữa trưa. Ở đấy cậu c&oacute; thể đổi quyển s&aacute;ch lấy quyển dầy hơn, mua đầy b&igrave;nh rượu vang, cạo r&acirc;u, cắt t&oacute;c. Cậu định chuẩn bị cho bảnh để đi gặp c&ocirc; g&aacute;i chứ kh&ocirc;ng muốn thắc mắc rằng biết đ&acirc;u một g&atilde; chăn cừu kh&aacute;c với một bầy cừu lớn hơn đ&atilde; đến đ&oacute; trước cậu v&agrave; hỏi cưới n&agrave;ng rồi. &quot;Chỉ khi n&agrave;o c&oacute; khả năng thực hiện được giấc mơ th&igrave; cuộc sống mới đ&aacute;ng sống&quot;, cậu c&acirc;n nhắc trong l&uacute;c nh&igrave;n trời lần nữa v&agrave; rảo bước. Cậu chợt nhớ ra rằng ở Tarifa c&oacute; một b&agrave; gi&agrave; giải đo&aacute;n được mộng mị. M&agrave; tối h&ocirc;m qua cậu lại mơ y như lần trước. B&agrave; l&atilde;o dẫn kh&aacute;ch v&agrave;o ph&ograve;ng trong của ng&ocirc;i nh&agrave;, ngăn với ph&ograve;ng kh&aacute;ch bởi tấm m&agrave;nh l&agrave;m bằng c&aacute;c dải nylon sặc sỡ. Ở đấy c&oacute; một c&aacute;i b&agrave;n, hai c&aacute;i ghế v&agrave; một h&igrave;nh Ch&uacute;a Jesu. B&agrave; l&atilde;o ngồi rồi bảo cậu ngồi theo. Xong, b&agrave; nắm lấy hai b&agrave;n tay cậu, khẽ lẩm bẩm đọc kinh. Nghe như kinh của người Zigeuner (Zigeuner: (tiếng Anh: gypspy) một sắc d&acirc;n &quot;du cư&quot;, gốc từ Ấn Độ, sang ch&acirc;u &nbsp;u từ thế kỉ XV). Trong cuộc h&agrave;nh tr&igrave;nh cậu đ&atilde; từng gặp kh&ocirc;ng &iacute;t người Zigeuner rồi; họ cũng đi khắp nơi n&agrave;y nơi kh&aacute;c tuy kh&ocirc;ng chăn cừu như cậu. Người ta bảo rằng d&acirc;n Zigeuner chuy&ecirc;n lọc lừa. Người ta c&ograve;n n&oacute;i th&ecirc;m rằng họ li&ecirc;n minh với ma quỷ, rằng họ cướp trẻ thơ, bắt ch&uacute;ng l&agrave;m n&ocirc; lệ trong c&aacute;c khu lều &acirc;m u của họ. Thuở nhỏ cậu rất sợ bị người Zigeuner bắt c&oacute;c, th&agrave;nh ra khi b&agrave; l&atilde;o nắm tay th&igrave; nỗi sợ xưa lại ập đến. &quot;Nhưng b&agrave; ta treo h&igrave;ng Ch&uacute;a Jesu tr&ecirc;n tường m&agrave;&quot;, cậu t&igrave;m c&aacute;ch tự trấn an. Cậu kh&ocirc;ng muốn tay m&igrave;nh run l&ecirc;n để b&agrave; l&atilde;o kh&ocirc;ng nhận ra được rằng m&igrave;nh đang sợ. Cậu thầm đọc kinh lậy Cha. &quot;Lạ thật&quot;, b&agrave; l&atilde;o n&oacute;i, mắt vẫn nh&igrave;n chăm chắm đ&ocirc;i b&agrave;n tay cậu; rồi im bặt. Cậu c&agrave;ng th&ecirc;m lo. B&agrave; l&atilde;o nhận thấy tay cậu bỗng dưng run l&ecirc;n. Cậu vội rụt tay về. &quot;T&ocirc;i đến đ&acirc;y kh&ocirc;ng phải để nhờ xem chỉ tay&quot;, cậu n&oacute;i v&agrave; &acirc;n hận đ&atilde; đến chốn n&agrave;y. Trong một khoảnh khắc cậu nghĩ tốt hơn cả n&ecirc;n trả tiền rồi chuồn thẳng. Đ&uacute;ng l&agrave; cậu đ&atilde; qu&aacute; quan trọng h&oacute;a giấc mơ của m&igrave;nh. &quot;Cậu đến đ&acirc;y v&igrave; muốn t&igrave;m hiểu về giấc mơ&quot;, b&agrave; l&atilde;o đ&aacute;p. &quot;M&agrave; mơ l&agrave; ng&ocirc;n ngữ của Ch&uacute;a. Nếu người n&oacute;i bằng ng&ocirc;n ngữ thế gian th&igrave; ta giải đo&aacute;n được. Nhưng nếu người n&oacute;i bằng ng&ocirc;n ngữ của t&acirc;m hồn th&igrave; chỉ m&igrave;nh cậu c&oacute; thể hiểu được th&ocirc;i. Tuy thế ta cũng thử xem sao&quot;. &quot;Lại một kế mọn&quot;, cậu chăn cừu nghĩ. Tuy thế cậu muốn thử một lần xem sao. Chẳng g&igrave; người chăn cừu cũng liều đối ph&oacute; với ch&oacute; s&oacute;i v&agrave; kh&ocirc; hạn, v&igrave; thế m&agrave; nghề n&agrave;y mới hứng th&uacute;. &quot;T&ocirc;i nằm mộng hai lần li&ecirc;n tiếp&quot;, cậu n&oacute;i. &quot;Mơ thấy m&igrave;nh v&agrave; bầy cừu đang tr&ecirc;n đồng cỏ, chợt một đứa trẻ kh&ocirc;ng biết từ đ&acirc;u đến, chơi với bầy cừu. Đ&uacute;ng ra t&ocirc;i kh&ocirc;ng th&iacute;ch c&oacute; ai quấy rầy lũ cừu của t&ocirc;i v&igrave; ch&uacute;ng vốn sợ người lạ. Nhưng trẻ con lu&ocirc;n n&ocirc; đ&ugrave;a với ch&uacute;ng được m&agrave; kh&ocirc;ng l&agrave;m ch&uacute;ng hoảng. T&ocirc;i kh&ocirc;ng hiểu tại sao. Do đ&acirc;u m&agrave; cừu biết được tuổi t&aacute;c của người nhỉ?&quot;. &quot;V&agrave;o chuyện ch&iacute;nh đi th&ocirc;i&quot;, b&agrave; l&atilde;o ngắt lời. &quot;Ta c&ograve;n một c&aacute;i nồi to đang đặt tr&ecirc;n bếp cần phải tr&ocirc;ng chừng. Hơn nữa cậu chỉ c&oacute; ch&uacute;t tiền c&ograve;m th&igrave; kh&ocirc;ng thể l&agrave;m ta mất nhiều thời giờ được.&quot; &quot;Đứa trẻ chơi với bầy cừu một l&uacute;c&quot;, cậu ngượng nghịu kể tiếp. &quot;Rồi chợt n&oacute; nắm tay t&ocirc;i dẫn đến Kim Tự Th&aacute;p b&ecirc;n Ai Cập.&quot; Cậu chờ một ch&uacute;t để chờ phản ứng của b&agrave; l&atilde;o nhưng b&agrave; chẳng n&oacute;i g&igrave;. &quot;Rồi ở Kim Tự Th&aacute;p b&ecirc;n Ai Cập&quot;, cậu nhấn mạnh s&aacute;u chữ sau để b&agrave; l&atilde;o r&otilde;, &quot;đứa b&eacute; n&oacute;i với t&ocirc;i: &quot;nếu anh đến đ&acirc;y th&igrave; anh sẽ t&igrave;m thấy một kho t&agrave;ng ch&ocirc;n giấu b&iacute; mật, kh&ocirc;ng ai biết.&quot; Rồi khi n&oacute; định chỉ cho t&ocirc;i chỗ ch&iacute;nh x&aacute;c của kho t&agrave;ng th&igrave; t&ocirc;i thức giấc. Cả hai lần đều y như vậy.&quot; B&agrave; l&atilde;o im lặng th&ecirc;m một l&uacute;c rồi lại cầm lấy hai b&agrave;n tay cậu chăn cừu, chăm ch&uacute; nh&igrave;n. &quot;B&acirc;y giờ ta kh&ocirc;ng đ&ograve;i cậu ch&uacute;t th&ugrave; lao n&agrave;o hết&quot;, b&agrave; l&atilde;o n&oacute;i. &quot;Nhưng ta muốn được hưởng một phần mười kho t&agrave;ng khi cậu t&igrave;m thấy n&oacute;.&quot; Cậu chăn cừu cười vui th&iacute;ch. Nhờ một kho t&agrave;ng trong mơ m&agrave; cậu tiết kiệm đươc ch&uacute;t tiền c&ograve;m hiện c&oacute;. Đ&uacute;ng l&agrave; một b&agrave; l&atilde;o Zigeuner c&oacute; kh&aacute;c, ngờ nghệch hết sức. &quot;Được th&ocirc;i, b&agrave; giải đo&aacute;n đi&quot;, cậu đ&aacute;p. &quot;Trước đ&oacute; cậu phải thề sẽ trả ta một phần mười kho t&agrave;ng l&agrave; tiền c&ocirc;n cho điều ta sắp giải đo&aacute;n.&quot; Cậu thề v&agrave; b&agrave; l&atilde;o y&ecirc;u cầu cậu lặp lại lời thề trước h&igrave;nh Ch&uacute;a. &quot;Đ&acirc;y l&agrave; một giấc mơ thuộc ng&ocirc;n ngữ thế gian&quot;, b&agrave; l&atilde;o n&oacute;i, &quot;do đ&oacute; ta c&oacute; thể giải đo&aacute;n được. Nhưng việc giải đo&aacute;n n&agrave;y rất kh&oacute;. Th&agrave;nh ra ta được hưởng một phần kho t&agrave;ng của cậu cũng đ&aacute;ng th&ocirc;i. &Yacute; nghĩa n&oacute; như thế n&agrave;y: cậu h&atilde;y đi đến Kim Tự Th&aacute;p Ai Cập. Tuy ta chưa từng nghe n&oacute;i đến c&aacute;i th&aacute;p đ&oacute; bao giờ nhưng một khi đứa trẻ đ&atilde; chỉ đường cho cậu th&igrave; nhất định phải c&oacute;. Cậu sẽ t&igrave;m thấy ở đấy c&aacute;i kho t&agrave;ng khiến cậu gi&agrave;u to.&quot; Cậu chăn cừu sửng sốt rồi thất vọng. Nếu chỉ c&oacute; thế th&igrave; cậu chẳng cần mất c&ocirc;ng tới đ&acirc;y l&agrave;m g&igrave;. Song dẫu sao cậu cũng chưa phải trả g&igrave; hết. &quot;Nếu chỉ để biết c&oacute; bấy nhi&ecirc;u th&igrave; lẽ ra t&ocirc;i kh&ocirc;ng cần mất th&igrave; giờ như thế n&agrave;y&quot;, cậu n&oacute;i. &quot;Ch&iacute;nh v&igrave; thế m&agrave; ta đ&atilde; n&oacute;i rồi, rằng đ&acirc;y l&agrave; một giấc mơ kh&oacute; đo&aacute;n. Những việc tưởng chừng như đơn giản trong cuộc sống th&igrave; thật ra l&agrave; những việc hết sức phi thường, chỉ những bậc tr&iacute; giả mới thấu hiểu nổi. Ta kh&ocirc;ng phải l&agrave; bậc tr&iacute; giả n&ecirc;n phải d&ugrave;ng c&aacute;ch kh&aacute;c, chẳng hạn phải xem chỉ tay&quot;. &quot;Thế t&ocirc;i phải đến Ai Cập bằng c&aacute;ch n&agrave;o đ&acirc;y?&quot; &quot;Ta chỉ c&oacute; thể giải đo&aacute;n mộng th&ocirc;i chứ kh&ocirc;ng biết phải l&agrave;m sao để biến ch&uacute;ng th&agrave;nh thật được. Ch&iacute;nh v&igrave; thế m&agrave; ta phải sống nhờ v&agrave;o th&ugrave; lao kh&aacute;ch trả.&quot; &quot;C&ograve;n nếu t&ocirc;i kh&ocirc;ng bao giờ tới đươc Ai Cập th&igrave; sao?&quot; &quot;Th&igrave; ta kh&ocirc;ng được trả th&ugrave; lao. Kh&ocirc;ng phải l&agrave; lần đầu ti&ecirc;n đ&acirc;u.&quot; Rồi b&agrave; l&atilde;o kh&ocirc;ng n&oacute;i g&igrave; th&ecirc;m nữa m&agrave; chỉ bảo cậu về đi v&igrave; b&agrave; đ&atilde; mất qu&aacute; nhiều th&igrave; giờ cho cậu rồi. Cậu chăn cừu thấ vọng ra về v&agrave; nhất quyết kh&ocirc;ng bao giờ tin v&agrave;o mộng mị nữa. Rồi cậu chợt nhớ c&ograve;n phải giải quyết một v&agrave;i việc: mua thức ăn, đổi s&aacute;ch lấy một quyển dầy hơn. Sau đ&oacute; cậu ra ngo&agrave;i b&atilde;i chợ, ngồi tr&ecirc;n ghế băng thưởng thức rượu vang mới mua. H&ocirc;m ấy trời rất n&oacute;ng v&agrave; chẳng hiểu sao vang lại l&agrave;m cậu phấn chấn. Lũ cừu được gửi gắm chăm s&oacute;c cẩn thận nơi chuồng của một người bạn cậu mới quen ở lối v&agrave;o th&agrave;nh phố. Cậu quen biết nhiều người ở v&ugrave;ng n&agrave;y. Cậu th&iacute;ch đi nhiều cũng v&igrave; thế. Lu&ocirc;n lu&ocirc;n kết được bạn mới m&agrave; kh&ocirc;ng cần mất trọn th&igrave; giờ để sống b&ecirc;n họ. Nếu l&uacute;c n&agrave;o cũng chỉ quen một số người th&ocirc;i, như ở trường đạo, th&igrave; họ sẽ trở th&agrave;nh một phần kh&ocirc;ng thể t&aacute;ch rời khỏi cuộc đời m&igrave;nh. Khi đ&atilde; như thế th&igrave; họ muốn thay đổi cuộc đời m&igrave;nh. Rồi khi ta kh&ocirc;ng muốn thay đổi như họ muốn th&igrave; họ sẽ thất vọng. V&igrave; h&igrave;nh như ai cũng tưởng m&igrave;nh biết rất r&otilde; người kh&aacute;c phải sống như thế n&agrave;o cho đ&uacute;ng, trong khi lại m&ugrave; mờ về cuộc sống của ch&iacute;nh bản th&acirc;n m&igrave;nh. Giống như b&agrave; l&atilde;o giải mộng kia kh&ocirc;ng biến nổi giấc mơ th&agrave;nh hiện thực vậy. Cậu muốn chờ cho đến khi mặt trời xuống thấp nữa mới dẫn đ&agrave;n cừu đi tiếp. Chỉ c&ograve;n hơn ba ng&agrave;y nữa l&agrave; cậu sẽ gặp lại c&ocirc; con g&aacute;i của nh&agrave; bu&ocirc;n nọ. Cậu bắt đầu đọc quyển s&aacute;ch mới nhận được của linh mục th&agrave;nh Tarifa. S&aacute;ch dầy lắm v&agrave; ngay trang đầu đ&atilde; n&oacute;i về một đ&aacute;m tang m&agrave; t&ecirc;n c&aacute;c nh&acirc;n vật phức tạp qu&aacute;. Nếu một ng&agrave;y n&agrave;o đ&oacute; m&igrave;nh viết s&aacute;ch, cậu nghĩ, m&igrave;nh sẽ chỉ cho nh&acirc;n vật n&agrave;y xuất hiện tiếp nh&acirc;n vật kh&aacute;c để người đọc khỏi rối tr&iacute;. Tập trung đọc một l&uacute;c cậu mới thấy hay v&igrave; truyện viết về một đ&aacute;m tang tr&ecirc;n tuyết trắng đ&atilde; đem đến cho cậu một cảm gi&aacute;c tươi m&aacute;t dưới c&aacute;i nắng trưa gay gắt n&agrave;y. Cậu đang m&ecirc; mải đọc th&igrave; một &ocirc;ng gi&agrave; đến ngồi cạnh v&agrave; bắt chuyện. &quot;Họ l&agrave;m g&igrave; đấy?&quot; &ocirc;ng gi&agrave; hỏi, tay chỉ những người đi lại hối hả tr&ecirc;n b&atilde;i chợ. &quot;L&agrave;m việc&quot;, cậu đ&aacute;p cụt ngủn chủ &yacute; cho thấy m&igrave;nh đang say m&ecirc; đọc. Nhưng thật ra cậu đang nghĩ đến việc sẽ x&eacute;n l&ocirc;ng cừu trước mắt c&ocirc; con g&aacute;i chủ tiệm để c&ocirc; thấy cậu giỏi giang, l&agrave;m được nhiều việc kh&ocirc;ng dễ t&iacute; n&agrave;o. Cậu đ&atilde; nhiều lần tưởng tượng cảnh ấy v&agrave; lần n&agrave;o c&ocirc; g&aacute;i cũng sửng sốt khi cậu giải th&iacute;ch cho c&ocirc; biết rằng phải x&eacute;n l&ocirc;ng cừu từ sau tới trước. Cậu cũng r&aacute;ng nhớ v&agrave;i ba giai thoại để c&oacute; thể vừa l&agrave;m vừa kể c&ocirc; nghe. Phần lớn những giai thoại n&agrave;y cậu đọc trong mấy quyển s&aacute;ch n&agrave;o đ&oacute; nhưng lại muốn kể như thể ch&iacute;nh m&igrave;nh đ&atilde; chứng kiến. Đ&agrave;ng n&agrave;o th&igrave; c&ocirc; cũng kh&ocirc;ng biết thực hư được v&igrave; c&ocirc; kh&ocirc;ng biết đọc. Nhưng &ocirc;ng gi&agrave; kh&ocirc;ng chịu bu&ocirc;ng. &Ocirc;ng k&ecirc;u rằng mệt, kh&aacute;t v&agrave; xin một hớp vang. Cậu đưa &ocirc;ng chai rượu, hi vọng rồi sẽ được để y&ecirc;n. Nhưng &ocirc;ng gi&agrave; cứ nhất định bắt chuyện. &Ocirc;ng hỏi cậu đang đọc g&igrave; đấy. Cậu muốn tỏ ra bất lịch sự v&agrave; đổi sang ghế kh&aacute;c qu&aacute; nhưng bố cậu đ&atilde; dạy phải k&iacute;nh trọng người gi&agrave; cả. Th&agrave;nh ra cậu đưa quyển s&aacute;ch cho &ocirc;ng, v&igrave; hai l&iacute; do: một l&agrave; cậu kh&ocirc;ng đọc được đ&uacute;ng t&ecirc;n s&aacute;ch, hai l&agrave; nếu &ocirc;ng gi&agrave; kh&ocirc;ng biết đọc th&igrave; c&oacute; lẽ &ocirc;ng sẽ xấu hổ v&agrave; tự sang ngồi ghế kh&aacute;c. &quot;Hừm&hellip;&quot;, &ocirc;ng gi&agrave; ậm ừ rồi săm soi nh&igrave;n quyển s&aacute;ch như thể n&oacute; l&agrave; một vật lạ. &quot;Quyển n&agrave;y tuy l&agrave; một t&aacute;c phẩm lớn nhưng rất nh&agrave;m ch&aacute;n&quot;. Cậu chăn cừu sửng sốt. Kh&ocirc;ng những &ocirc;ng gi&agrave; biết chữ m&agrave; c&ograve;n đ&atilde; đọc quyển s&aacute;ch đ&oacute; rồi nữa. Nếu đ&uacute;ng l&agrave; nh&agrave;m ch&aacute;n như &ocirc;ng gi&agrave; n&oacute;i th&igrave; c&ograve;n kịp để đổi lấy quyển kh&aacute;c. &quot;N&oacute; cũng n&oacute;i về c&ugrave;ng một vấn đề như mọi quyển s&aacute;ch kh&aacute;c th&ocirc;i&quot;, &ocirc;ng gi&agrave; n&oacute;i tiếp. &quot;Rằng con người kh&ocirc;ng c&oacute; khả năng lựa chọn lấy vận mệnh của m&igrave;nh. V&agrave; kết th&uacute;c rằng ai cũng tin v&agrave;o c&aacute;i điều bịp bợm nhất thế gian&quot;. &quot;Điều bịp bợm ấy l&agrave; g&igrave;?&quot; cậu chăn cừu ngơ ng&aacute;c hỏi. &quot;Đ&oacute; l&agrave;: v&agrave;o một l&uacute;c nhất định trong đời, ch&uacute;ng ta kh&ocirc;ng l&agrave;m chủ được vận mệnh của m&igrave;nh nữa v&agrave; rồi đời m&igrave;nh sẽ do định mệnh đưa đẩy. Đ&oacute; ch&iacute;nh l&agrave; điều dối tr&aacute; nhất thế gian!&quot; &quot;Ch&aacute;u th&igrave; kh&ocirc;ng thế&quot;, cậu đ&aacute;p. &quot;Người ta muốn ch&aacute;u trở th&agrave;nh thầy tu nhưng ch&aacute;u đ&atilde; quyết định l&agrave;m kẻ chăn cừu.&quot; &quot;Thế l&agrave; phải lắm,&quot; &ocirc;ng gi&agrave; n&oacute;i. &quot;Bởi cậu th&iacute;ch đi đ&acirc;y đi đ&oacute; nhiều m&agrave;.&quot; &quot;&Ocirc;ng n&agrave;y đọc được &yacute; nghĩ của m&igrave;nh&quot;, cậu ngẫm ngĩ. Trong l&uacute;c ấy &ocirc;ng gi&agrave; lần giở s&aacute;ch, kh&ocirc;ng c&oacute; vẻ g&igrave; muốn trả lại cả. Cậu chăn cừu nh&igrave;n bộ quần &aacute;o kh&aacute;c thường &ocirc;ng gi&agrave; mặc tr&ecirc;n người. Tr&ocirc;ng &ocirc;ng như một người Arập, điều kh&ocirc;ng c&oacute; g&igrave; lạ ở v&ugrave;ng n&agrave;y. Ch&acirc;u Phi chỉ c&aacute;ch Tarifa c&oacute; v&agrave;i giờ đường th&ocirc;i; chỉ cần d&ugrave;ng thuyền nhỏ vượt eo biển (Ch&acirc;u Phi c&aacute;ch T&acirc;y Ban Nha bởi eo biển Gibraltar) hẹp l&agrave; tới. Người Arập thường đến th&agrave;nh phố n&agrave;y mua sắm v&agrave; ng&agrave;y n&agrave;o cũng nhiều lần l&acirc;m r&acirc;m đọc những b&agrave;i kinh lạ tai của họ. &quot;&Ocirc;ng từ đ&acirc;u đến?&quot; cậu hỏi. &quot;Từ nhiều nơi.&quot; &quot;Kh&ocirc;ng ai c&oacute; thể đến c&ugrave;ng l&uacute;c từ nhiều nơi kh&aacute;c nhau được&quot;, cậu n&oacute;i. &quot;Ch&aacute;u l&agrave; kẻ chăn cừu v&agrave; biết nhiều nơi nhưng đến th&igrave; chỉ từ một th&agrave;nh phố th&ocirc;i; n&oacute; ở gần một nơi th&agrave;nh cổ. Ch&aacute;u sinh ra ở đấy.&quot; &quot;Thế th&igrave; c&oacute; thể n&oacute;i sinh qu&aacute;n ta ở Salem.&quot; Cậu chăn cừu kh&ocirc;ng biết Salem ở đ&acirc;u, nhưng kh&ocirc;ng hỏi tiếp để khỏi bị xem l&agrave; dối n&aacute;t. Cậu nh&igrave;n những người lăng xăng đi lại tr&ecirc;n b&atilde;i chợ một l&uacute;c l&acirc;u, người n&agrave;o cũng c&oacute; vẻ rất bận rộn. &quot;Salem giờ ra sao?&quot;, cậu hỏi, mong t&igrave;m được ch&uacute;t manh mối. &quot;Vẫn thường&quot;. Chẳng lần ra được ch&uacute;t g&igrave;. Cậu chỉ biết Salem kh&ocirc;ng ở trong v&ugrave;ng Andalusia, bởi nếu c&oacute; th&igrave; cậu đ&atilde; nghe n&oacute;i tới rồi. &quot;Thế &ocirc;ng l&agrave;m g&igrave; ở Salem?&quot; cậu hỏi tới. &quot;Ta l&agrave;m g&igrave; ư?&quot; &Ocirc;ng gi&agrave; bật cười ha hả. &quot;Ta l&agrave; vua xứ Salem!&quot; &#39;Người ta cứ hay n&oacute;i những chuyện lạ l&ugrave;ng&#39;, cậu nghĩ thầm. &quot;Đ&ocirc;i khi chơi với lũ cừu c&ograve;n th&iacute;ch hơn v&igrave; ch&uacute;ng c&acirc;m, chỉ biết t&igrave;m thức ăn v&agrave; nước uống. S&aacute;ch cũng gi&uacute;p ta đỡ buồn bằng c&aacute;ch kể ta nghe những chuyện hay ho mỗi khi ta muốn nghe. Nhưng khi tr&ograve; chuyện với người th&igrave; c&oacute; thể họ sẽ n&oacute;i những điều qu&aacute;i lạ khiến ta chịu kh&ocirc;ng biết phải tiếp tục n&oacute;i g&igrave;.&quot; &quot;T&ecirc;n ta l&agrave; Melchisedek&quot;, &ocirc;ng gi&agrave; n&oacute;i. &quot;Cậu c&oacute; bao nhi&ecirc;u cừu?&quot; &quot;Tạm đủ&quot;, cậu nghi ngại đ&aacute;p. &Ocirc;ng n&agrave;y muốn biết qu&aacute; nhiều về cậu. &quot;Thế th&igrave; phiền lắm đấy v&igrave; ta sẽ kh&ocirc;ng gi&uacute;p cậu được g&igrave; nếu cậu cho rằng c&oacute; đủ cừu&quot;. Đến đ&acirc;y th&igrave; cậu bực m&igrave;nh thực sự. Cậu c&oacute; cầu được gi&uacute;p đỡ g&igrave; đ&acirc;u; &ocirc;ng gi&agrave; xin cậu rượu vang, bắt chuyện v&agrave; mượn s&aacute;ch đấy chứ. &quot;&Ocirc;ng cho ch&aacute;u xin lại quyển s&aacute;ch. Ch&aacute;i phải trở lại chỗ bầy cừu rồi dẫn ch&uacute;ng đi tiếp.&quot; &quot;Nếu cậu trả th&ugrave; lao cho ta một phần mười số cừu của cậu th&igrave; ta sẽ chỉ cậu c&aacute;ch đến được c&aacute;i kho t&agrave;ng ch&ocirc;n giấu b&iacute; mật đ&oacute;&quot;, &ocirc;ng gi&agrave; n&oacute;i. Đến đ&acirc;y cậu chợt nhớ lại giấc mơ v&agrave; bỗng thấy mọi sự như s&aacute;ng tỏ. Mụ gi&agrave; giải mộng tuy kh&ocirc;ng đ&ograve;i g&igrave; hết nhưng b&acirc;y giờ l&atilde;o gi&agrave; n&agrave;y, c&oacute; thể l&agrave; chồng mụ lắm, sẽ lấy của cậu đổi lại một lời chỉ dẫn v&ocirc; gi&aacute; trị về một kho t&agrave;ng kh&ocirc;ng hề c&oacute;. Chắc hẳn l&atilde;o cũng l&agrave; d&acirc;n Zigeuner th&ocirc;i. Cậu chưa kịp mở miệng th&igrave; &ocirc;ng gi&agrave; đ&atilde; c&uacute;i nhặt một c&acirc;y que rồi viết tr&ecirc;n mặt c&aacute;t. Khi c&uacute;i như thế, c&oacute; g&igrave; đ&oacute; l&oacute;e s&aacute;ng tr&ecirc;n ngực &ocirc;ng l&agrave;m cậu l&oacute;a mắt. Nhưng &ocirc;ng gi&agrave;, bằng một động t&aacute;c phải n&oacute;i l&agrave; qu&aacute; nhanh nhẹn ở v&agrave;o tuổi &ocirc;ng, vội k&eacute;o &aacute;o kho&aacute;c che l&ecirc;n. Khi mắt kh&ocirc;ng c&ograve;n l&oacute;a nữa, cậu đọc những g&igrave; &ocirc;ng gi&agrave; vừa viết. Tr&ecirc;n mặt c&aacute;t b&atilde;i chợ l&agrave; t&ecirc;n bố mẹ cậu. Cậu đọc về cuộc đời m&igrave;nh, những tr&ograve; chơi thửa nhỏ. Những đ&ecirc;m lạnh lẽo trong chủng viện; cậu đọc t&ecirc;n con g&aacute;i chủ tiệm vải m&agrave; ch&iacute;nh cậu c&ograve;n chưa biết. Cậu đọc những chuyện của m&igrave;nh m&agrave; cậu chưa từng thổ lộ c&ugrave;ng ai &ndash; như vụ cậu lấy trộm s&uacute;ng của bố để đi săn hươu, chuyện lần đầu ti&ecirc;n trong đời tự th&acirc;n t&igrave;m biết dục t&iacute;nh l&agrave; g&igrave;. &quot;Ta l&agrave; vua xứ Salem&quot;, &ocirc;ng gi&agrave; d&otilde;ng dạc n&oacute;i. &quot;Tại sao một vị vua lại đi tr&ograve; chuyện với một kẻ chăn cừu tầm thường nhỉ?&quot; cậu thắc mắc hỏi, c&oacute; hơi xấu hổ về xuất th&acirc;n của m&igrave;nh. &quot;C&oacute; nhiều l&iacute; do. Nhưng l&iacute; do ch&iacute;nh l&agrave; cậu đ&atilde; th&agrave;nh c&ocirc;ng trong sự đeo đuổi vận mệnh của m&igrave;nh.&quot; Cậu chăn cừu chẳng hiểu thế n&agrave;o l&agrave; vận mệnh. &quot;Vận mệnh ch&iacute;nh l&agrave; điều m&agrave; anh lu&ocirc;n lu&ocirc;n muốn đạt được. Khi c&ograve;n trẻ ai ai cũng biết vận mệnh của m&igrave;nh l&agrave; g&igrave;. Trong đoạn đời n&agrave;y mọi sự đều đơn giản v&agrave; người ta d&aacute;m mơ mộng đủ thứ về những điều họ muốn l&agrave;m trong đời. Nhưng rồi, theo thời gian, một sức mạnh thần b&iacute; sẽ t&igrave;m c&aacute;ch thuyết phục ta rằng con đường đời như ta mơ ước sẽ kh&ocirc;ng thể n&agrave;o thực hiện được đ&acirc;u.&quot; Cậu chăn cừu chẳng hiểu g&igrave; lắm những g&igrave; &ocirc;ng gi&agrave; n&oacute;i. Tuy thế, cậu vẫn muốn biết sức mạnh thần b&iacute; kia nghĩa l&agrave; g&igrave;. Rồi c&ocirc; g&aacute;i con chủ tiệm vải sẽ tr&ograve;n xoe mắt nghe cậu giảng cho m&agrave; xem. &quot;Đ&oacute; l&agrave; một lực thần b&iacute; xem ra c&oacute; vẻ kh&ocirc;ng tốt l&agrave;nh nhưng trong thực tế lại gi&uacute;p ch&uacute;ng ta đi hết con đường đời của m&igrave;nh. Lực thần b&iacute; n&agrave;y chuẩn bị cho tinh thần v&agrave; &yacute; ch&iacute; ta sẵn s&agrave;ng, bởi sự thật vĩ đại tr&ecirc;n h&agrave;nh tinh n&agrave;y ch&iacute;nh l&agrave;: bất kể anh l&agrave; ai, anh l&agrave;m g&igrave;, khi anh thật t&acirc;m mong muốn điều g&igrave; th&igrave; điều mong muốn đ&oacute; sẽ được h&igrave;nh th&agrave;nh trong c&otilde;i T&acirc;m linh vũ trụ (Weltenseele; Soul of the Universe). Đ&oacute; sẽ l&agrave; nhiệm vụ của anh tr&ecirc;n tr&aacute;i đất.&quot; &quot;Ngay cả khi chỉ l&agrave; mong muốn được đi đ&acirc;y đi đ&oacute; hay l&agrave; được lấy con g&aacute;i nh&agrave; bu&ocirc;n vải hay sao?&quot; &quot;Hoặc mơ ước t&igrave;m kho t&agrave;ng chẳng hạn. T&acirc;m linh vũ trụ được nu&ocirc;i dưỡng bởi hạnh ph&uacute;c của con người, bởi cả sự bất hạnh, tị hiềm v&agrave; ghen tu&ocirc;ng nữa. Nhiệm vụ duy nhất của mỗi ch&uacute;ng ta l&agrave; thực hiện con đường m&igrave;nh đ&atilde; chọn. Tất cả chỉ l&agrave; một. Rồi khi anh quyết ch&iacute; muốn điều g&igrave; th&igrave; to&agrave;n vũ trụ sẽ chung sức để anh đạt được điều ấy.&quot; Cả hai ngồi lặng thinh hồi l&acirc;u, quan s&aacute;t người qua kẻ lại tr&ecirc;n b&atilde;i chợ. Rồi &ocirc;ng gi&agrave; l&ecirc;n tiếng trước. &quot;Tại sao cậu chọn nghề chăn cừu?&quot; &quot;V&igrave; ch&aacute;u th&iacute;ch đi đ&acirc;y đi đ&oacute;.&quot; &Ocirc;ng gi&agrave; chỉ v&agrave;o một người b&aacute;n kem với chiếc xe k&eacute;o m&agrave;u đỏ hai b&aacute;nh đậu ở một g&oacute;c b&atilde;i chợ. &quot;Khi c&ograve;n nhỏ người b&aacute;n kem kia cũng muốn đi đ&acirc;y đi đ&oacute; lắm. Nhưng m&agrave; anh ta thấy n&ecirc;n mua một xe kem để kiếm tiền v&agrave; d&agrave;nh dụm đ&atilde;. Khi n&agrave;o đủ tiền anh ta sẽ sang ch&acirc;u Phi chơi một th&aacute;ng. Anh ta kh&ocirc;ng hề hiểu rằng người ta l&uacute;c n&agrave;o cũng c&oacute; thể thực hiện được ước mơ của m&igrave;nh.&quot; &quot;Lẽ ra anh ta n&ecirc;n l&agrave;m kẻ chăn cừu&quot;, cậu buột miệng n&oacute;i to. &quot;Anh ta quả cũng c&oacute; nghĩ thế đấy&quot;, &ocirc;ng gi&agrave; n&oacute;i. &quot;Nhưng m&agrave; nghề b&aacute;n kem được coi trọng hơn l&agrave; chăn cừu. Họ c&oacute; nh&agrave; cửa, c&ograve;n người chăn cừu phải ngủ ngo&agrave;i đồng trống. Thi&ecirc;n hạ th&iacute;ch gả con g&aacute;i cho người b&aacute;n kem hơn cho g&atilde; chăn cừu.&quot; Ch&agrave;ng trai thấy tim m&igrave;nh đau nh&oacute;i khi nghĩ đến con g&aacute;i chủ tiệm vải. Chắc hẳn trong th&agrave;nh phố của c&ocirc; cũng c&oacute; một người b&aacute;n kem. &quot;N&oacute;i cho c&ugrave;ng con người coi việc kẻ kh&aacute;c đ&aacute;nh gi&aacute; anh b&aacute;n kem hay cậu chăn cừu quan trọng hơn l&agrave; thực hiện điều th&acirc;m t&acirc;m m&igrave;nh muốn.&quot; N&oacute;i đến đ&acirc;y &ocirc;ng gi&agrave; lật s&aacute;ch, đọc qua loa. Ch&agrave;ng chăn cừu đợi một l&uacute;c rồi mới l&ecirc;n tiếng cắt ngang, giống như l&uacute;c đầu cậu bị cắt ngang. &quot;Tại sao &ocirc;ng n&oacute;i với ch&aacute;u về những điều n&agrave;y?&quot; &quot;V&igrave; ch&iacute;nh cậu đ&atilde; cố gắng đi theo tiếng gọi của th&acirc;m t&acirc;m v&agrave; giờ đ&acirc;y sắp bỏ cuộc.&quot; &quot;L&uacute;c n&agrave;o &ocirc;ng cũng xuất hiện v&agrave;o đ&uacute;ng gi&acirc;y ph&uacute;t quyết đinh sao?&quot; &quot;Kh&ocirc;ng phải l&uacute;c n&agrave;o cũng dưới h&igrave;nh d&aacute;ng n&agrave;y, nhưng ta lu&ocirc;n lu&ocirc;n xuất hiện, dưới một dạng n&agrave;o đ&oacute;. Đ&ocirc;i khi dưới dạng một giải ph&aacute;p tốt, một &yacute; kiến xuất thần. C&oacute; lần, v&agrave;o l&uacute;c quyết định, ta tạo ho&agrave;n cảnh thuận lợi hơn. V&acirc;n v&acirc;n. Nhưng phần lớn người ta kh&ocirc;ng nhận ra điều ấy.&quot; &Ocirc;ng gi&agrave; kể rằng tuần trước &ocirc;ng, dưới dạng một vi&ecirc;n đ&aacute;, đ&atilde; đến với một người t&igrave;m đ&aacute; qu&yacute;. Người đ&oacute; đ&atilde; bỏ tất cả để t&igrave;m ngọc lục bảo. &Ocirc;ng ta đ&atilde; l&agrave;m việc r&ograve;ng r&atilde; năm năm ở một con s&ocirc;ng v&agrave; đ&atilde; đập 999999 vi&ecirc;n đ&aacute; chỉ để t&igrave;m lấy một vi&ecirc;n ngọc lục bảo. Bấy giờ người t&igrave;m ngọc đ&atilde; định bỏ cuộc rồi, trong khi chỉ cần một h&ograve;n đ&aacute; nữa th&ocirc;i, một h&ograve;n đ&aacute; duy nhất nữa, l&agrave; sẽ t&igrave;m được ngọc. Ch&iacute;nh v&igrave; người n&agrave;y đ&atilde; tin theo tiếng gọi của th&acirc;m t&acirc;m n&ecirc;n &ocirc;ng gi&agrave; quyết định ra tay gi&uacute;p. &Ocirc;ng biến th&agrave;nh một vi&ecirc;n đ&aacute; lăn tới ch&acirc;n người nọ. Người n&agrave;y, với tất cả phẫn nộ v&agrave; tuyệt vọng của năm năm mất trắng, liệng vi&ecirc;n đ&aacute; ra thật xa. &Ocirc;ng ta n&eacute;m mạnh đến nỗi n&oacute; l&agrave;m vỡ toang một h&ograve;n đ&aacute; kh&aacute;c, l&agrave;m lộ ra vi&ecirc;n ngọc lục bảo đẹp nhất thế gian. &quot;Con người nhận thức rất sớm họ sống tr&ecirc;n đời để l&agrave;m g&igrave;&quot;, &ocirc;ng gi&agrave; n&oacute;i, giọng nhuốm vẻ chua ch&aacute;t. &quot;C&oacute; thể ch&iacute;nh v&igrave; thế m&agrave; họ lại bỏ cuộc sớm. Đ&agrave;nh th&ocirc;i.&quot; L&uacute;c ấy cậu chăn cừu mới nhắc &ocirc;ng rằng hai người bắt đầu bằng c&acirc;u chuyện kho t&agrave;ng bị ch&ocirc;n giấu. &quot;Kho t&agrave;ng được d&ograve;ng s&ocirc;ng đưa l&ecirc;n mặt nước rồi lại v&ugrave;i xuống đ&aacute;y&quot;, &ocirc;ng gi&agrave; n&oacute;i. &quot;Nếu cậu muốn biết g&igrave; về kho t&agrave;ng của m&igrave;nh th&igrave; phải chịu trả ta một phần mười số cừu của cậu&quot;. &quot;&Ocirc;ng kh&ocirc;ng th&iacute;ch nhận một phần mười kho t&agrave;ng hơn &agrave;?&quot; &Ocirc;ng gi&agrave; tỏ vẻ kh&ocirc;ng h&agrave;i l&ograve;ng. &quot;Nếu cậu hứa trả bằng c&aacute;i m&igrave;nh chưa sở hữu th&igrave; rồi sẽ c&oacute; l&uacute;c cậu mất đi &yacute; ch&iacute; đạt được n&oacute; đấy.&quot; Nghe thế cậu chăn cừu b&egrave;n th&uacute; thật đ&atilde; hứa trả một phần mười kho t&agrave;ng cho b&agrave; gi&agrave; Zigeuner rồi. &quot;Phải, người Zigeuner kh&ocirc;n lắm&quot;, &ocirc;ng gi&agrave; n&oacute;i. &quot;D&ugrave; sao th&igrave; cũng tốt v&igrave; cậu đ&atilde; học được rằng ở đời mọi thứ đều c&oacute; gi&aacute; của ch&uacute;ng cả. Đ&oacute; ch&iacute;nh l&agrave; điều m&agrave; ch&uacute;ng ta, những sứ thần đem &aacute;nh s&aacute;ng đến soi rọi (Lichtbote; Warrior of the Light), muốn truyền đạt.&quot; &Ocirc;ng gi&agrave; đưa trả cậu quyển s&aacute;ch. &quot;Ng&agrave;y mai giờ n&agrave;y cậu đem một phần mười số cừu đến cho ta. Ta sẽ chỉ cho cậu con đường t&igrave;m đến được kho t&agrave;ng. Tạm biệt.&quot; Rồi &ocirc;ng gi&agrave; bỏ đi, biến mất sau một g&oacute;c tường. Ch&agrave;ng trai cố đọc tiếp nhưng kh&ocirc;ng tập trung nổi nữa. Cậu thấp thỏm v&agrave; căng thẳng v&igrave; biết những g&igrave; &ocirc;ng gi&agrave; n&oacute;i l&agrave; đ&uacute;ng sự thật. Cậu lại xe kem mua một c&acirc;y kem rồi c&acirc;n nhắc xem c&oacute; n&ecirc;n kể cho người b&aacute;n kem những g&igrave; &ocirc;ng gi&agrave; vừa n&oacute;i kh&ocirc;ng. &quot;Đ&ocirc;i khi n&ecirc;n để y&ecirc;n cho mọi sự tiếp diễn l&agrave; kh&ocirc;n ngoan hơn&quot;, cậu nghĩ v&agrave; b&igrave;nh t&acirc;m lại. Nếu cậu n&oacute;i ra th&igrave; biết đ&acirc;u người b&aacute;n kem sẽ băn khoan suốt v&agrave;i ba ng&agrave;y liền, rằng c&oacute; n&ecirc;n tung h&ecirc; hết tất cả kh&ocirc;ng, m&agrave; anh ta lại đ&atilde; qu&aacute; quen với c&aacute;i xe kem rồi. Cậu thấy đừng n&ecirc;n l&agrave;m người b&aacute;n kem phải lo nghĩ nữa. Thế l&agrave; cậu đi lang thang thơ thẩn tr&ecirc;n đường phố, ra đến bến cảng. Ở đ&acirc;y c&oacute; một ng&ocirc;i nh&agrave; nhỏ với quầy b&aacute;n v&eacute; đi ch&acirc;u Phi. Ai Cập ở b&ecirc;n ch&acirc;u Phi m&agrave;! &quot;Cậu cần g&igrave;?&quot; người đ&agrave;n &ocirc;ng ngồi ở quầy hỏi. &quot;Mai hẵng hay&quot;, cậu đ&aacute;p rồi vội v&atilde; đi ra. Chỉ cần b&aacute;n đi một con cừu l&agrave; đủ tiền qua eo biển th&ocirc;i m&agrave;. &Yacute; nghĩ n&agrave;y khiến cậu băn khoăn. &quot;Lại một tay mơ mộng vẩn vơ&quot;, g&atilde; ngồi quầy n&oacute;i với đồng nghiệp trong l&uacute;c cậu đi ra. &quot;Hắn c&oacute;c c&oacute; tiền mua v&eacute; đ&acirc;u.&quot; L&uacute;c đứng ở quầy v&eacute;, cậu chăn cừu nhớ tới lũ cừu của m&igrave;nh v&agrave; chợt thấy cần phải về với ch&uacute;ng. Trong v&ograve;ng hai năm cậu đ&atilde; học được hết mọi việc của nghề chăn cừu; cậu x&eacute;n l&ocirc;ng được n&agrave;y, săn s&oacute;c c&aacute;c con c&oacute; mang n&agrave;y, bảo vệ ch&uacute;ng trước ch&oacute; s&oacute;i n&agrave;y. V&agrave; cậu cũng biết r&agrave;nh mọi đồng cỏ v&ugrave;ng Andalusia. Cậu biết cả gi&aacute; mua v&agrave; b&aacute;n của từng con một. Cậu chọn con đường v&ograve;ng vo xa nhất để về chuồng của người bạn. Th&agrave;nh phố n&agrave;y cũng c&oacute; ng&ocirc;i th&agrave;nh cổ, thế l&agrave; cậu quyết định leo hết bậc thang đ&aacute;, l&ecirc;n chỗ cao nhất để ra ngồi nơi c&ocirc;ng sự. C&oacute; ai đ&oacute; đ&atilde; giải th&iacute;ch cho cậu rằng người Mauren đ&atilde; qua eo biển n&agrave;y chiếm cứ gần hết T&acirc;y Ban Nha trong nhiều năm. Cậu căm gh&eacute;t người Mauren, v&igrave; ch&iacute;nh họ đ&atilde; đưa người Zigeuner đến đ&acirc;y. Từ tr&ecirc;n cao n&agrave;y cậu nh&igrave;n thấy gần hết th&agrave;nh phố, cả c&aacute;i b&atilde;i chợ nơi cậu tr&ograve; chuyện với &ocirc;ng gi&agrave;. &quot;C&aacute;i gi&acirc;y ph&uacute;t đ&atilde; xui khiến ta gặp gỡ l&atilde;o gi&agrave; kia thật l&agrave; đ&aacute;ng nguyền rủa&quot;, cậu ngẫm nghĩ trong tuyệt vọng. Cậu chỉ muốn t&igrave;m mụ gi&agrave; giải mộng th&ocirc;i. Thế m&agrave; cả mụ lẫn l&atilde;o gi&agrave; đều chẳng cần biết cho rằng cậu l&agrave; một g&atilde; chăn cừu. Hẳn họ l&agrave; những người rất c&ocirc; đơn, chẳng c&ograve;n tin g&igrave; v&agrave;o cuộc đời nữa n&ecirc;n kh&ocirc;ng hiểu rằng người chăn cừu gắn b&oacute; với lũ vật của m&igrave;nh như thế n&agrave;o. Cậu biết r&otilde; đặc điểm của từng con một: con n&agrave;o đi c&agrave; nhắc, con n&agrave;o hai th&aacute;ng nữa sẽ đẻ v&agrave; con n&agrave;o lười nhất hạng. Cậu cũng biết phải x&eacute;n l&ocirc;ng thế n&agrave;o, phải mổ thịt ch&uacute;ng ra sao. Nếu cậu bỏ ch&uacute;ng m&agrave; đi th&igrave; ch&uacute;ng sẽ khốn khổ th&ocirc;i. Một l&agrave;n gi&oacute; nhẹ thổi. Cậu biết loại gi&oacute; n&agrave;y; người ta gọi n&oacute; l&agrave; gi&oacute; Levante (Levante: v&ugrave;ng đất trải từ đ&ocirc;ng Địa Trung Hải tới s&ocirc;ng Euphrat, s&ocirc;ng Nil v&agrave; bờ biển v&ugrave;ng Tiểu &Aacute;), v&igrave; đ&aacute;m Mauren xưa đ&atilde; từ phương Đ&ocirc;ng theo gi&oacute; n&agrave;y m&agrave; tới đ&acirc;y. Trước khi đến Tarifa cậu kh&ocirc;ng hề biết rằng ch&acirc;u Phi gần đến thế. Nhưng cũng l&agrave; mối nguy lớn đấy, v&igrave; bất cứ l&uacute;c n&agrave;o người Mauren cũng c&oacute; thể lại tấn c&ocirc;ng sang được. Gi&oacute; thổi mạnh hơn. &quot;M&igrave;nh kẹt giữa một b&ecirc;n l&agrave; lũ cừu v&agrave; một b&ecirc;n l&agrave; c&aacute;i kho t&agrave;ng&quot;, cậu nghĩ. Cậu phải chọn giữa một b&ecirc;n l&agrave; những g&igrave; quen thuộc, gần gũi v&agrave; một b&ecirc;n l&agrave; c&aacute;i cậu muốn sở hữu. C&ograve;n c&ocirc; g&aacute;i nữa chứ, nhưng c&ocirc; kh&ocirc;ng cần được cậu lo cho bằng bầy cừu v&igrave; c&ocirc; kh&ocirc;ng lệ thuộc v&agrave;o cậu. C&oacute; thể c&ocirc; kh&ocirc;ng c&ograve;n cả nhớ cậu l&agrave; ai nữa. Cậu d&aacute;m chắc rằng nếu hai ng&agrave;y nữa cậu kh&ocirc;ng xuất hiện ở đ&oacute; th&igrave; c&ocirc; cũng chẳng biết. Với c&ocirc; th&igrave; một ng&agrave;y như mọi ng&agrave;y; v&agrave; khi ng&agrave;y n&agrave;o cũng như ng&agrave;y nấy th&igrave; con người cũng chẳng nhận biết được những việc hay ho xảy đến trong đời. &quot;M&igrave;nh đ&atilde; gi&atilde; từ cha mẹ v&agrave; ng&ocirc;i th&agrave;nh cổ của qu&ecirc; hương ra đi. Cha mẹ v&agrave; ng&ocirc;i th&agrave;nh cổ đ&atilde; quen với chuyện ấy rồi, giống như m&igrave;nh cũng đ&atilde; quen vậy. Thế th&igrave; lũ cừu cũng sẽ quen với sự vắng mặt của m&igrave;nh th&ocirc;i&quot;, cậu ngẫm nghĩ. Từ chỗ cao n&agrave;y cậu nh&igrave;n rất r&otilde; b&atilde;i chợ. Người b&aacute;n kem vẫn c&ograve;n đ&oacute;. Một đ&ocirc;i trai g&aacute;i c&ograve;n trẻ ngồi trao nhau nụ h&ocirc;n d&agrave;i tr&ecirc;n c&aacute;i ghế cậu v&agrave; &ocirc;ng gi&agrave; đ&atilde; ngồi. &quot;Ch&agrave;, người b&aacute;n kem&quot;, cậu buột miệng rồi kh&ocirc;ng n&oacute;i tiếp v&igrave; gi&oacute; Levante thổi thốc v&agrave;o mặt. Gi&oacute; n&agrave;y tuy mang theo người Mauren thật, nhưng đưa đến cả m&ugrave;i thơm của sa mạc v&agrave; hương thơm của c&aacute;c phụ nữ đeo khăn che mặt (phụ nữ nhiều nước theo đạo Hồi thường đeo khăn che k&iacute;n mặt, chỉ để hở hai mắt). N&oacute; đem theo cả m&ugrave;i mồ h&ocirc;i v&agrave; giấc mơ của những người đ&agrave;n &ocirc;ng một ng&agrave;y n&agrave;o đ&oacute; đ&atilde; l&ecirc;n đường v&agrave;o chốn v&ocirc; định để t&igrave;m v&agrave;ng, t&igrave;m sự phi&ecirc;u lưu, t&igrave;m cả Kim Tự Th&aacute;p nữa. Cậu chợt ganh tị với ngọn gi&oacute; tự do kia v&agrave; thấy rằng m&igrave;nh cũng c&oacute; thể được tự do kh&ocirc;ng k&eacute;m. Chẳng c&oacute; g&igrave; ngăn cản được cậu, trừ ch&iacute;nh cậu ra. Bầy cừu, c&ocirc; con g&aacute;i chủ tiệm vải, những c&aacute;nh đồng cỏ v&ugrave;ng Andalusia chỉ l&agrave; những chặng tr&ecirc;n con đường đời của cậu th&ocirc;i.<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\nCHƯƠNG 2:<br />\r\nH&ocirc;m sau, cậu chăn cừu dẫn theo s&aacute;u con cừu đến gặp &ocirc;ng gi&agrave;. - Ch&aacute;u ngạc nhi&ecirc;n lắm, - cậu n&oacute;i, - v&igrave; bạn ch&aacute;u chịu mua ngay số cừu kia. Anh ta bảo rằng đ&atilde; từ l&acirc;u mơ ước được th&agrave;nh người chăn cừu v&agrave; đ&acirc;y quả l&agrave; dấu hiệu tốt cho anh ấy. - Bao giờ cũng thế cả, - &ocirc;ng gi&agrave; đ&aacute;p. &ndash; C&aacute;i đ&oacute; gọi l&agrave; nguy&ecirc;n l&iacute; thuận lợi (Guenstige Prinzip; Principle of favorability). Lần đầu ti&ecirc;n khi ta liều chơi đỏ đen th&igrave; thường l&agrave; ta thắng. Th&aacute;nh nh&acirc;n đ&atilde;i kẻ kh&ugrave; khờ m&agrave;. (Tạm dịch &quot;Anfaengerglueck; beginner&#39;&#39;s luck&quot; cho dễ hiểu). - Nhưng tại sao mới được chứ? - Tại v&igrave; cuộc đời muốn rằng ta m&atilde;i đi theo con đường m&igrave;nh chọn. Rồi &ocirc;ng xem x&eacute;t lũ cừu, ph&aacute;t hiện một con bị qu&egrave;. Cậu cả quyết rằng thật chẳng đ&aacute;ng kể v&igrave; con cừu nọ th&ocirc;ng minh nhất v&agrave; cho nhiều l&ocirc;ng nhất. &quot;Thế kho t&agrave;ng ở đ&acirc;u hở &ocirc;ng?&quot; cậu hỏi. &quot;Ở Kim Tự Th&aacute;p b&ecirc;n Ai Cập&quot;. Cậu th&oacute;t cả người. B&agrave; gi&agrave; kia cũng n&oacute;i y như thế m&agrave; chẳng đ&ograve;i g&igrave; hết. &quot;Để đến được đ&oacute; th&igrave; cậu phải lần theo c&aacute;c dấu hiệu. Ch&uacute;a vạch đường để người ngườit theo. Chỉ cần cậu biết nhận ra Người d&ugrave;ng dấu hiệu g&igrave; để vạch đường cho cậu&quot;. Cậu chưa kịp n&oacute;i g&igrave; th&igrave; c&oacute; một con bướm bay lượn giữa cậu v&agrave; &ocirc;ng gi&agrave;, l&agrave;m cậu nhớ đến &ocirc;ng nội của cậu. Hồi cậu c&ograve;n nhỏ &ocirc;ng nội cậu bảo rằng bướm mang lại điềm l&agrave;nh, tương tự như dế, cỏ ba l&aacute; bốn c&aacute;nh v&agrave; m&oacute;ng ngựa (Cỏ ba l&aacute; m&agrave; lại bốn c&aacute;nh rất hiếm. M&oacute;ng ngựa được d&acirc;n nhiều nước ch&acirc;u &nbsp;u treo trong nh&agrave; hoặc gắn tr&ecirc;n xe, coi đ&oacute; l&agrave; biểu tượng cho may mắn). &quot;Đ&uacute;ng thế&quot;, &ocirc;ng gi&agrave; n&oacute;i v&igrave; đọc được cậu đang nghĩ g&igrave;. &quot;N&oacute; giống như &ocirc;ng nội cậu đ&atilde; dạy cậu vậy. Ch&uacute;ng cũng l&agrave; những dấu hiệu đấy.&quot; Rồi &ocirc;ng mở &aacute;o kho&aacute;c. Cậu nh&igrave;n m&agrave; th&aacute;n phục v&agrave; nhớ tới &aacute;nh rực rỡ đ&atilde; thấy h&ocirc;m qua: &ocirc;ng gi&agrave; đeo gi&aacute;p che ngực bằng v&agrave;ng r&ograve;ng, gắn đầy đ&aacute; qu&yacute; đủ m&agrave;u. Nhất định &ocirc;ng phải l&agrave; vua thật rồi. Chắc &ocirc;ng kho&aacute;c th&ecirc;m &aacute;o ngo&agrave;i l&agrave; để tr&aacute;nh bị cướp th&ocirc;i. &quot;Cậu h&atilde;y cầm lấy&quot;, &ocirc;ng gi&agrave; n&oacute;i v&agrave; rứt từ giữa &aacute;o gi&aacute;p bằng v&agrave;ng kia một vi&ecirc;n đ&aacute; trắng v&agrave; một vi&ecirc;n đen. &quot;Hai vi&ecirc;n đ&aacute; n&agrave;y t&ecirc;n l&agrave; Urim v&agrave; Thummim. Vi&ecirc;n đen nghĩa l&agrave; c&oacute;, vi&ecirc;n trắng nghĩa l&agrave; kh&ocirc;ng. Nếu cậu kh&ocirc;ng tự m&igrave;nh nhận ra được dấu hiệu th&igrave; hai vi&ecirc;n đ&aacute; n&agrave;y hữu &iacute;ch cho cậu đấy. Phải lu&ocirc;n lu&ocirc;n đặt c&acirc;u hỏi kh&aacute;ch quan. Song tốt nhất l&agrave; cậu tự quyết định lấy. Kho t&agrave;ng nằm ở Kim Tự Th&aacute;p, cậu đ&atilde; biết rồi. Nhưng nhờ c&oacute; ta cậu mới quyết định, n&ecirc;n cậu phải trả s&aacute;u con cừu.&quot; Cậu nh&eacute;t hai vi&ecirc;n đ&aacute; v&agrave;o bị. Trong tương lai cậu sẽ tự m&igrave;nh quyết định lấy mọi chuyện. &quot;Đừng bao giờ qu&ecirc;n rằng tất cả l&agrave; một tổng thể. Cũng đừng qu&ecirc;n ng&ocirc;n ngữ của dấu hiệu. Nhất l&agrave; đừng qu&ecirc;n đi cho hết con đường m&igrave;nh tự chọn. Trước khi chia tay ta muốn kể cho cậu nghe một c&acirc;u chuyện: Một ng&agrave;y nọ, một thương nh&acirc;n gửi con trai của m&igrave;nh đến một nh&agrave; th&ocirc;ng th&aacute;i bậc nhất kh&ocirc;ng ai bằng để nhờ &ocirc;ng dạy cho người con b&iacute; quyết đạt được hạnh ph&uacute;c. Anh con trai đi r&ograve;ng r&atilde; bốn mươi ng&agrave;y xuy&ecirc;n qua sa mạc mới đến được t&ograve;a l&acirc;u đ&agrave;i nguy nga ngự tr&ecirc;n n&uacute;i cao. Nh&agrave; th&ocirc;ng th&aacute;i anh muốn t&igrave;m đang ở đ&oacute;. Thay v&igrave; gặp một vị th&aacute;nh th&igrave; anh lại v&agrave;o một gian ph&ograve;ng đầy người sinh hoạt tất bật. Thương nh&acirc;n hết đến rồi đi, người kh&aacute;c t&uacute;m tụm ở c&aacute;c g&oacute;c ph&ograve;ng b&agrave;n t&aacute;n, một d&agrave;n nhạc chơi c&aacute;c ca kh&uacute;c lảnh l&oacute;t, lại c&oacute; cả một b&agrave;n tiệc đầy sơn h&agrave;o hải vị c&ugrave;ng đ&oacute;. Nh&agrave; th&ocirc;ng th&aacute;i chuyện v&atilde;n với từng người một v&agrave; anh ta phải chờ suốt hai tiếng mới đến lượt m&igrave;nh được tiếp. Nh&agrave; th&ocirc;ng th&aacute;i lắng nghe anh ta tr&igrave;nh b&agrave;y rồi đ&aacute;p rằng hiện &ocirc;ng kh&ocirc;ng rảnh để chỉ dạy anh b&iacute; quyết của hạnh ph&uacute;c. &Ocirc;ng bảo anh h&atilde;y đi xem khắp l&acirc;u đ&agrave;i rồi hai tiếng sau trở lại. &quot;Nhưng ta y&ecirc;u cầu anh l&agrave;m hộ một điều&quot;, nh&agrave; th&ocirc;ng th&aacute;i n&oacute;i rồi đưa cho anh một muỗng con c&oacute; hai giọt dầu. &quot;Trong l&uacute;c đi xem th&igrave; anh cầm theo muỗng n&agrave;y v&agrave; nhớ giữ đừng l&agrave;m s&aacute;nh dầu nh&eacute;.&quot; Anh ta l&ecirc;n lầu, xuống lầu mắt kh&ocirc;ng rời c&aacute;i muỗng. Sau hai giờ anh quay lại gặp nh&agrave; th&ocirc;ng th&aacute;i. &quot;Sao&quot;, &ocirc;ng hỏi, &quot;anh đ&atilde; thấy c&aacute;c tấm thảm Ba Tư qu&yacute; gi&aacute; trong ph&ograve;ng ăn của ta chưa? Cả c&aacute;i vườn tr&aacute;ng lệ m&agrave; người l&agrave;m vườn đ&atilde; phải khổ c&ocirc;ng mười năm x&acirc;y dựng? v&agrave; những cuộn giấy da tuyệt hảo trong thư viện của ta nữa?&quot; Anh ta ngượng ng&ugrave;ng th&uacute; nhận rằng chẳng hề để mắt đến g&igrave; kh&aacute;c v&igrave; cứ phải chăm chăm ng&oacute; nh&igrave;n muỗng dầu đ&atilde; được giao ph&oacute;. &quot;Thế th&igrave; anh h&atilde;y đi th&ecirc;m lần nữa v&agrave; ngắm cho kĩ những thứ tuyệt mĩ trong thế giới của ta&quot;, nh&agrave; th&ocirc;ng th&aacute;i n&oacute;i. &quot;Kh&ocirc;ng thể đặt tin tưởng v&agrave;o một người khi m&igrave;nh kh&ocirc;ng hề biết người ấy sống trong một ng&ocirc;i nh&agrave; như thế n&agrave;o.&quot; Y&ecirc;n dạ hơn, anh ta lại cầm muỗng đi một v&ograve;ng. Lần n&agrave;y anh chăm ch&uacute; xem x&eacute;t những vật qu&yacute; treo tr&ecirc;n tường v&agrave; tr&ecirc;n trần nh&agrave;. Anh ngắm khu vườn c&oacute; n&uacute;i v&acirc;y quanh, với đủ thứ hoa thơm cỏ lạ v&agrave; mỗi t&aacute;c phẩm nghệ thuật ho&agrave;n mỹ đều được để đ&uacute;ng chỗ th&iacute;ch hợp. Trở lại gặp nh&agrave; th&ocirc;ng th&aacute;i anh kể chi tiết tất cả những g&igrave; đ&atilde; nh&igrave;n thấy. - Thế c&ograve;n hai giọt dầu ta nhờ anh giữ đ&acirc;u rồi? &ndash; nh&agrave; th&ocirc;ng th&aacute;i hỏi. Nh&igrave;n c&aacute;i muỗng, anh ta hốt hoảng thấy m&igrave;nh đ&atilde; l&agrave;m s&aacute;nh mất rồi. - Đ&acirc;y l&agrave; điều duy nhất m&agrave; ta c&oacute; thể khuy&ecirc;n anh, - nh&agrave; th&ocirc;ng th&aacute;i nhất thế gian n&oacute;i. &quot;B&iacute; quyết của hạnh ph&uacute;c l&agrave; biết ngắm nh&igrave;n mọi thứ tuyệt mỹ tr&ecirc;n thế gian n&agrave;y m&agrave; kh&ocirc;ng hề qu&ecirc;n hai giọt dầu tr&ecirc;n muỗng.&quot;&quot; Cậu chăn cừu kh&ocirc;ng n&oacute;i g&igrave; cả. Cậu hiểu &yacute; nghĩa c&acirc;u chuyện &ocirc;ng vua vừa kể. Người chăn cừu th&iacute;ch đi, đi m&atilde;i nhưng kh&ocirc;ng bao giờ bỏ qu&ecirc;n bầy cừu của m&igrave;nh. &Ocirc;ng gi&agrave; th&acirc;n mật nh&igrave;n cậu rồi x&ograve;e cả hai b&agrave;n tay l&agrave;m v&agrave;i động t&aacute;c lạ l&ugrave;ng tr&ecirc;n đầu cậu. Sau đ&oacute; &ocirc;ng dẫn bầy cừu đi. Ph&iacute;a tr&ecirc;n cao th&agrave;nh phố Tarifa c&oacute; một ng&ocirc;i th&agrave;nh cổ do người Mauren x&acirc;y. Ngồi tr&ecirc;n tường th&agrave;nh c&oacute; thể tr&ocirc;ng thấy b&atilde;i chợ, người b&aacute;n kem v&agrave; một mẩu của ch&acirc;u Phi. Xế trưa h&ocirc;m ấy, Melchisedek, vua xứ Salem, ngồi tr&ecirc;n chỗ tường th&agrave;nh nh&ocirc; ra, hứng gi&oacute; Levante. Lũ cừu ở cạnh &ocirc;ng ngọ nguậy m&atilde;i v&igrave; lo sợ trước &ocirc;ng chủ mới v&agrave; những đổi thay vừa xảy ra. Bởi ch&uacute;ng vốn chỉ cần ăn v&agrave; uống th&ocirc;i m&agrave;. Melchisedek nh&igrave;n theo con t&agrave;u nhỏ chạy bằng hơi nước vừa rời bến. &Ocirc;ng sẽ kh&ocirc;ng bao giờ gặp lại cậu chăn cừu, cũng như &ocirc;ng đ&atilde; kh&ocirc;ng hề gặp lại Abraham (phải chăng Coelho muốn &aacute;m chỉ &quot;giao ước&quot; giữa Đức Ch&uacute;a Trời v&agrave; Abraham về tương lai d&acirc;n Do Th&aacute;i (Cựu Ước)?) sau khi đ&atilde; nhận một phần mười. Đ&oacute; l&agrave; phần việc của &ocirc;ng. Thần th&aacute;nh kh&ocirc;ng n&ecirc;n c&oacute; mơ ước g&igrave;, bởi họ kh&ocirc;ng t&ugrave;y thuộc định mệnh. Tuy thế &ocirc;ng vua xứ Salem vẫn thầm mong cậu chăn cừu sẽ th&agrave;nh c&ocirc;ng. &quot;Tiếc rằng rồi cậu ấy sẽ sớm qu&ecirc;n mất t&ecirc;n ta&quot;, &ocirc;ng thầm nghĩ. &quot;Lẽ ra ta phải nhắc đi nhắc lại t&ecirc;n ta thường hơn để sau n&agrave;y cậu ấy sẽ kể về Melchisedek, vua xứ Salem.&quot; Rồi &ocirc;ng nh&igrave;n trời, n&oacute;i với vẻ ăn năn: &quot;Đ&uacute;ng l&agrave; c&oacute; cao ngạo thật đấy, nhưng m&agrave; một &ocirc;ng vua gi&agrave; thỉnh thoảng cũng được ph&eacute;p tự h&agrave;o về m&igrave;nh đấy chứ nhỉ.&quot; &quot;Ch&acirc;u Phi thật lạ l&ugrave;ng,&quot; cậu chăn cừu thầm nghĩ. Cậu ngồi trong một qu&aacute;n nước giống như nhiều qu&aacute;n nước kh&aacute;c m&agrave; cậu thấy nhan nhản dọc c&aacute;c con đường hẹp của th&agrave;nh phố. Mấy người đ&agrave;n &ocirc;ng h&uacute;t chung một c&aacute;i ống điếu to x&ugrave;, chuyền từ người n&agrave;y sang người kh&aacute;c. Chỉ trong v&agrave;i giờ từ khi đến nơi cậu đ&atilde; được chứng kiến bao điều lạ: đ&agrave;n &ocirc;ng nắm tay nhau đi ngo&agrave;i đường phố, phụ nữ d&ugrave;ng khăn che mặt v&agrave; c&aacute;c thầy tu leo l&ecirc;n c&aacute;c th&aacute;p cao nghệu, &ecirc; a đọc kinh như h&aacute;t trong l&uacute;c mọi người quanh đấy quỳ lạy x&igrave; xụp, vập tr&aacute;n xuống nền đất. &quot;Đ&acirc;y l&agrave; tập tục của người ngoại đạo&quot;, cậu tự nhủ. Thửa nhỏ cậu vẫn hay ngắm nh&igrave;n trong ng&ocirc;i nh&agrave; thờ ở nguy&ecirc;n qu&aacute;n pho tượng th&aacute;nh Santiago de Compostela tay vung thanh kiếm to bản, ph&oacute;ng ngựa qua những h&igrave;nh người tr&ocirc;ng giống như người d&acirc;n ở đ&acirc;y. Cậu thấy bất an v&agrave; c&ocirc; độc qu&aacute; chừng. Những người ngoại đạo tr&ocirc;ng mới dễ sợ l&agrave;m sao. Nhất l&agrave; do vội v&atilde; l&ecirc;n đường, cậu qu&ecirc;n bẵng một điều - điều cỏn con duy nhất th&ocirc;i - khiến cậu c&oacute; thể c&ograve;n l&acirc;u mới đến được kho t&agrave;ng: ở đất nước n&agrave;y người ta n&oacute;i tiếng Arập. Khi chủ qu&aacute;n lại gần, cậu chỉ v&agrave;o một thứ thức uống tr&ecirc;n b&agrave;n b&ecirc;n cạnh. Đ&oacute; l&agrave; thứ tr&agrave; đắng ngắt. Uống vang th&uacute; hơn nhiều. Nhưng cậu thấy kh&ocirc;ng n&ecirc;n bận t&acirc;m v&igrave; chuyện ấy, m&agrave; chỉ n&ecirc;n nghĩ tới kho t&agrave;ng th&ocirc;i, bằng c&aacute;ch n&agrave;o v&agrave; đi đường n&agrave;o đến đấy. Cậu b&aacute;n bầy cừu được khối tiền v&agrave; cậu biết rằng tiền như c&oacute; ma thuật. C&oacute; tiền th&igrave; kh&ocirc;ng đơn độc bao giờ. Kh&ocirc;ng l&acirc;u đ&acirc;u, c&oacute; khi chỉ v&agrave;i ng&agrave;y nữa th&ocirc;i, cậu sẽ c&oacute; mặt ở Kim Tự Th&aacute;p cho m&agrave; xem. Một &ocirc;ng gi&agrave; đầy v&agrave;ng tr&ecirc;n ngực cần g&igrave; phải lừa cậu lấy s&aacute;u con cừu chứ. &Ocirc;ng gi&agrave; từng n&oacute;i về dấu hiệu. L&uacute;c ngồi tr&ecirc;n t&agrave;u qua eo biển cậu đ&atilde; ngẫm nghĩ về thứ dấu hiệu n&agrave;y. Phải, cậu biết &yacute; &ocirc;ng n&oacute;i về c&aacute;i g&igrave; chứ: hồi c&ograve;n rong ruổi tr&ecirc;n c&aacute;c c&aacute;nh đồng cỏ Andalusia cậu vẫn tập th&oacute;i quen nh&igrave;n đất, nh&igrave;n trời t&igrave;m dấu hiệu xem n&ecirc;n đi hướng n&agrave;o. Cậu nghiệm ra rằng c&oacute; một giống chim b&aacute;o hiệu c&oacute; rắn ở gần đấy v&agrave; một loại bụi c&acirc;y cho biết quanh đấy c&oacute; nước. Tất cả l&agrave; do lũ cừu dạy cho cậu đấy. &quot;Khi Ch&uacute;a đ&atilde; dẫn dắt bầy cừu đi đ&uacute;ng đường th&igrave; Người cũng sẽ hướng dẫn con người y như vậy&quot;, cậu ngẫm nghĩ v&agrave; y&ecirc;n dạ, thấy tr&agrave; như bớt đắng hơn. &quot;Anh l&agrave; ai?&quot; cậu nghe c&oacute; người hỏi ngay b&ecirc;n cạnh bằng tiếng T&acirc;y Ban Nha. Cậu nhẹ cả người. Cậu vừa nghĩ đến dấu hiệu, thế l&agrave; c&oacute; người xuất hiện ngay. &quot;Do đ&acirc;u m&agrave; anh biết tiếng T&acirc;y Ban Nha?&quot; cậu hỏi. Anh ch&agrave;ng kia c&ograve;n trẻ, mặc &acirc;u phục, tuy m&agrave;u da giống người bản xứ hơn. Khổ người v&agrave; tuổi anh ta trạc như cậu. &quot;Ở đ&acirc;y hầu như ai cũng n&oacute;i được tiếng T&acirc;y Ban Nha cả. Đ&acirc;y tới đ&oacute; chỉ mất ng&oacute;t ng&eacute;t hai tiếng th&ocirc;i m&agrave;.&quot; &quot;Anh ngồi xuống đ&acirc;y v&agrave; gọi thức uống, t&ocirc;i trả tiền&quot;, cậu bảo anh ta. &quot;Gọi cho t&ocirc;i một ly vang. T&ocirc;i gh&eacute;t thứ tr&agrave; n&agrave;y qu&aacute;&quot;. &quot;Ở đ&acirc;y kh&ocirc;ng c&oacute; vang đ&acirc;u&quot;, anh kia đ&aacute;p. &quot;Đạo Hồi kh&ocirc;ng cho ph&eacute;p uống rượu.&quot; Cậu b&egrave;n kể rằng m&igrave;nh phải tới Kim Tự Th&aacute;p. Su&yacute;t t&iacute; nữa th&igrave; cậu buột miệng kể lu&ocirc;n về kho t&agrave;ng, nhưng kịp th&ocirc;i v&igrave; nếu cậu kể ra th&igrave; nhất định anh ch&agrave;ng Arập kia sẽ đ&ograve;i chia ch&aacute;c mới chịu dẫn cậu đi. Cậu nhớ đến lời cảnh b&aacute;o của &ocirc;ng gi&agrave; rằng kh&ocirc;ng n&ecirc;n hứa trả ai c&aacute;i g&igrave; m&igrave;nh chưa thật c&oacute;. &quot;Nếu được th&igrave; t&ocirc;i y&ecirc;u cầu anh đi c&ugrave;ng t&ocirc;i đến đ&oacute;. Tất nhi&ecirc;n t&ocirc;i trả tiền dẫn đạo&quot;. &quot;Anh đ&atilde; mường tượng sẽ đi đến đ&oacute; như thế n&agrave;o chưa?&quot;. Cậu để &yacute; thấy chủ qu&aacute;n đứng gần đấy dỏng tai nghe lỏm. Sự c&oacute; mặt của &ocirc;ng l&agrave;m cậu bực m&igrave;nh. Nhưng đ&atilde; t&igrave;m được người dẫn đường rồi th&igrave; cậu kh&ocirc;ng bỏ lỡ dịp được. &quot;Anh phải băng qua sa mạc Sahara; muốn thế anh phải c&oacute; nhiều tiền. T&ocirc;i muốn biết anh c&oacute; đủ tiền kh&ocirc;ng đ&atilde;.&quot; Cậu thấy c&acirc;u hỏi thật lạ l&ugrave;ng. Nhưng cậu tin tưởng ở lời &ocirc;ng gi&agrave;, khi &ocirc;ng quả quyết rằng nếu ta quyết t&acirc;m th&igrave; cả vũ trụ sẽ c&ugrave;ng chung sức. Thế l&agrave; cậu moi bị lấy tiền cho anh ch&agrave;ng nọ xem. Chủ qu&aacute;n cũng x&iacute;ch lại nh&igrave;n. Rồi hai người lớn tiếng với nhau bằng tiếng Arập. &quot;M&igrave;nh đi th&ocirc;i&quot;, anh ch&agrave;ng kia n&oacute;i. &quot;Hắn kh&ocirc;ng muốn bọn m&igrave;nh ngồi đ&acirc;y&quot;. Cậu chăn cừu như tr&uacute;t được g&aacute;nh nặng, đứng dậy trả tiền; nhưng chủ qu&aacute;n t&uacute;m tay cậu, cao giọng n&oacute;i một tr&agrave;ng d&agrave;i đầy vẻ giận dữ. Cậu tuy sức trai nhưng ở tr&ecirc;n đất lạ th&agrave;nh thử đ&agrave;nh phải để người bạn mới gỡ hộ thế b&iacute;, đẩy chủ qu&aacute;n sang một b&ecirc;n, k&eacute;o cậu ra đường. &quot;Hắn muốn lấy tiền của anh đ&oacute;&quot;, ch&agrave;ng kia n&oacute;i. &quot;Th&agrave;nh phố Tanger (Tanger: th&agrave;nh phố cảng ở Bắc Marốc) n&agrave;y kh&ocirc;ng như những nơi kh&aacute;c của ch&acirc;u Phi đ&acirc;u. Đ&acirc;y l&agrave; một phố cảng m&agrave; cảng n&agrave;o th&igrave; cũng đầy trộm cắp cả.&quot; Anh bạn n&agrave;y thật đ&aacute;ng tin qu&aacute; đi chứ. Chẳng g&igrave; anh ta cũng đ&atilde; cứu cậu tho&aacute;t cảnh kh&oacute; xử vừa rồi. Cậu đem tiền ra đếm. &quot;Ng&agrave;y mai bọn m&igrave;nh đ&atilde; c&oacute; thể đến Kim Tự Th&aacute;p rồi&quot;, anh ch&agrave;ng kia n&oacute;i rồi cầm tiền. &quot;Nhưng t&ocirc;i phải t&igrave;m mua hai con lạc đ&agrave; đ&atilde;&quot;. Thế l&agrave; cả hai đi v&ograve;ng v&egrave;o qua những ng&otilde; ng&aacute;ch của Tanger. Đ&acirc;u đ&acirc;u cũng thấy b&agrave;y h&agrave;ng qu&aacute;n. Cuối c&ugrave;ngt họ đến một b&atilde;i chợ to. Cả ngh&igrave;n người đang mặc cả, mua, b&aacute;n. Rau quả xếp b&ecirc;n cạnh gươm dao. Thảm b&agrave;y b&ecirc;n đủ loại ống điếu thuốc l&agrave;o. Cậu kh&ocirc;ng rời mắt khỏi người bạn mới. Anh ta cầm trọn số tiền cậu c&oacute;. Cậu muốn đ&ograve;i lại qu&aacute;, nhưng sợ rằng như thế sẽ bất lịch sự. Dẫu sao cậu chưa biết g&igrave; về phong tục của đất nước lạ n&agrave;y. &quot;M&igrave;nh chỉ cần coi chừng hắn th&ocirc;i&quot;, cậu tự trấn an; hơn nữa cậu to khỏe hơn anh ch&agrave;ng kia m&agrave;. Chợt cậu nh&igrave;n thấy giữa đống rau quả một thanh kiếm tuyệt đẹp. Bao kiếm bằng bạc, chu&ocirc;i kiếm m&agrave;u đen cẩn đ&aacute; đủ m&agrave;u. Cậu nhất quyết khi từ Ai Cập về sẽ mua thanh kiếm n&agrave;y. &quot;Anh hỏi hộ người b&aacute;n h&agrave;ng xem thanh kiếm gi&aacute; bao nhi&ecirc;u&quot;, cậu định n&oacute;i với anh ch&agrave;ng kia như thế th&igrave; mới hỡi &ocirc;i! Trong l&uacute;c ngắm nh&igrave;n thanh kiếm cậu đ&atilde; để g&atilde; kia lọt khỏi tầm mắt, d&ugrave; chỉ c&oacute; hai gi&acirc;y th&ocirc;i. Tim cậu th&oacute;t lại. Cậu sợ phải quay đầu t&igrave;m v&igrave; trực gi&aacute;c cho cậu biết điều g&igrave; đ&atilde; xảy ra. Cậu đăm đăm nh&igrave;n thanh kiếm th&ecirc;m một l&uacute;c nữa đến l&uacute;c đủ can đảm nh&igrave;n quanh. Quanh cậu l&agrave; chợ với biết bao kẻ bu&ocirc;n, người b&aacute;n lớn tiếng rao h&agrave;ng, mặc cả; thảm b&agrave;y cạnh tr&aacute;i phỉ, rau sống chen giữa m&acirc;m bạc, đ&agrave;n &ocirc;ng nắm tay nhau, đ&agrave;n b&agrave; che mặt, m&ugrave;i thơm ng&agrave;o ngạt của c&aacute;c m&oacute;n ăn lạ. C&ograve;n người bạn kia đ&atilde; biệt tăm, chẳng để lại ch&uacute;t dấu vết g&igrave;. Mới đầu cậu c&ograve;n cố tin rằng hai người sơ &yacute; m&agrave; lạc nhau n&ecirc;n đứng n&aacute;n lại đ&oacute; một l&uacute;c, chờ g&atilde; nọ. Một l&aacute;t sau c&oacute; người leo l&ecirc;n t&ograve;a th&aacute;p kia ng&acirc;n nga đọc kinh. Mọi người quỳ vập tr&aacute;n xuống đất, ng&acirc;n nga theo. Sau đ&oacute; họ - cần mẫn như đ&agrave;n kiến - gỡ quầy h&agrave;ng, rời khỏi chợ. Mặt trời dần lặn. Cậu nh&igrave;n theo cho đến khi mặt trời xuống khuất sau những ng&ocirc;i nh&agrave; m&agrave;u trắng dựng quanh chợ. Cậu hồi tưởng rằng mới đ&oacute;, khi mặt trời mọc, m&igrave;nh c&ograve;n ở một ch&acirc;u lục kh&aacute;c, c&ograve;n l&agrave; một g&atilde; chăn cừu, chủ một bầy cừu s&aacute;u mươi con v&agrave; c&oacute; hẹn với một c&ocirc; g&aacute;i. S&aacute;ng nay, tr&ecirc;n c&aacute;nh đồng cỏ, cậu c&ograve;n điểm qua một lần suốt c&aacute;c chặng đường của cuộc đời m&igrave;nh. Thế m&agrave; giờ đ&acirc;y, khi mặt trời lặn, cậu đứng tr&ecirc;n một đất nước xa lạ, kh&ocirc;ng hiểu nổi cả tiếng người ta n&oacute;i. Cậu kh&ocirc;ng c&ograve;n l&agrave; g&atilde; chăn cừu nữa; trắng tay; ngay cả v&agrave;i đồng lẻ để quay về, lần nữa l&agrave;m lại cuộc đời cũng kh&ocirc;ng c&oacute; nốt. &quot;Mọi sự diễn ra chỉ từ l&uacute;c mặt trời mọc đến khi mặt trời lặn th&ocirc;i&quot;, cậu qu&aacute; nản ch&iacute;. Cậu thấy thương th&acirc;n v&igrave; đ&ocirc;i khi cuộc đời thay đổi hẳn chỉ trong khoảnh khắc, nhanh đến nỗi kh&ocirc;ng hiểu chuyện g&igrave; đ&atilde; xảy đến với m&igrave;nh. Thật ra cậu rất xấu hổ khi phải kh&oacute;c. Kh&ocirc;ng bao giờ cậu kh&oacute;c trước bầy cừu cả. Nhưng giờ đ&acirc;y cậu đứng giữa chợ kh&ocirc;ng một b&oacute;ng người, qu&ecirc; nh&agrave; xa t&iacute;t tắp. Cậu kh&oacute;c. Kh&oacute;c v&igrave; Ch&uacute;a thật bất c&ocirc;ng n&ecirc;n mới bắt một người vững tin v&agrave;o giấc mơ của m&igrave;nh phải chịu số phận n&agrave;y. &quot;Khi c&ograve;n chăn cừu th&igrave; m&igrave;nh rất hạnh ph&uacute;c v&agrave; đem lại niềm vui cho mọi người chung quanh. Người ta mong m&igrave;nh đến v&agrave; tiếp đ&oacute;n &acirc;n cần. B&acirc;y giờ m&igrave;nh buồn nản v&agrave; bất hạnh. Phải l&agrave;m sao đ&acirc;y? M&igrave;nh sẽ giận v&agrave; ho&agrave;i nghi hết mọi người chỉ v&igrave; c&oacute; một kẻ đ&atilde; lừa gạt m&igrave;nh. M&igrave;nh sẽ th&ugrave; ghết tất cả những kẻ t&igrave;m được kho t&agrave;ng v&igrave; m&igrave;nh kh&ocirc;ng t&igrave;m được kho t&agrave;ng của m&igrave;nh. V&agrave; rồi đ&acirc;y m&igrave;nh sẽ &ocirc;m rịt lấy ch&uacute;t của cải c&oacute; được, v&igrave; m&igrave;nh qu&aacute; h&egrave;n mọn kh&ocirc;ng thể chinh phục thế giới.&quot; Cậu mở bị, xem thử c&ograve;n những g&igrave;. Biết đ&acirc;u khi ăn tr&ecirc;n t&agrave;u c&ograve;n s&oacute;t lại ch&uacute;t b&aacute;nh m&igrave;. Nhưng cậu chỉ t&igrave;m thấy quyển s&aacute;ch d&agrave;y, c&aacute;i &aacute;o kho&aacute;c v&agrave; hai vi&ecirc;n đ&aacute; &ocirc;ng gi&agrave; cho. Nh&igrave;n hai vi&ecirc;n đ&aacute; cậu thấy nhẹ cả người. Cậu đ&atilde; đổi s&aacute;u con cừu lấy hai vi&ecirc;n đ&aacute; qu&yacute; từ c&aacute;i &aacute;o gi&aacute;p bằng v&agrave;ng. Cậu c&oacute; thể b&aacute;n lấy tiền mua v&eacute; về nước. &quot;Từ nay m&igrave;nh phải kh&ocirc;n hơn&quot;, cậu tự nhủ rồi nhặt hai vi&ecirc;n đ&aacute; ra khỏi bị để đ&uacute;t t&uacute;i quần. Đ&acirc;y l&agrave; một phố cảng v&agrave; g&atilde; kia n&oacute;i đ&uacute;ng ở một điểm: cảng l&uacute;c n&agrave;o cũng đầy kẻ cắp. B&acirc;y giờ th&igrave; cậu chợt hiểu v&igrave; sao người chủ qu&aacute;n nổi giận: &ocirc;ng chỉ muốn n&oacute;i cho cậu hiểu rằng đừng n&ecirc;n tin g&atilde; kia. &quot;M&igrave;nh như mọi người th&ocirc;i: nh&igrave;n thế giới v&agrave; thấy theo &yacute; m&igrave;nh muốn chứ kh&ocirc;ng phải như trong thực tế.&quot; Cậu ngắm ngh&iacute;a hai vi&ecirc;n đ&aacute;, gượng nhẹ sờ, tay cảm nhiệt độ v&agrave; mặt l&aacute;ng của ch&uacute;ng. Ch&uacute;ng l&agrave; tất cả gia t&agrave;i của cậu. Chỉ cần sờ ch&uacute;ng l&agrave; cậu thấy l&ograve;ng thanh thản dần. Ch&uacute;ng gợi nhớ đến &ocirc;ng gi&agrave;. &quot;Chỉ cần cậu tha thiết muốn th&igrave; cả vũ trụ sẽ t&aacute;c động để cậu đạt được&quot;, &ocirc;ng gi&agrave; đ&atilde; n&oacute;i thế. Cậu suy nghĩ thật lung tung để hiểu &yacute; &ocirc;ng.</span></span></p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(7, 'Đắc nhân tâm', 'Văn học nước ngoài', 'Dale Breckenridge Carnegie', 1936, 'dac-nhan-tam.png', 'Đang cập nhật', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nội dung của cuốn s&aacute;ch gi&uacute;p bạn c&oacute; c&aacute;i nh&igrave;n kh&aacute;c hơn về kh&aacute;i niệm Đắc Nh&acirc;n T&acirc;m. N&oacute; kh&ocirc;ng c&ograve;n l&agrave; nghệ thuật thu phục l&ograve;ng người v&agrave; l&agrave;m cho tất cả mọi người xung quanh y&ecirc;u mến m&igrave;nh. M&agrave; Đắc Nh&acirc;n T&acirc;m ở đ&acirc;y sẽ c&oacute; &yacute; nghĩa đ&oacute; cần được thụ đắc bằng sự hiểu r&otilde; bản th&acirc;n, th&agrave;nh thật với ch&iacute;nh m&igrave;nh. Cần phải hiểu biết v&agrave; quan t&acirc;m đến những mọi người xung quanh. Chỉ như vậy bạn mới c&oacute; thể nh&igrave;n ra v&agrave; khơi gợi được những tiềm năng ẩn khuất ở nơi họ. Từ đ&oacute;, gi&uacute;p họ ph&aacute;t triển l&ecirc;n một tầm cao mới. Đ&acirc;y một trong những nghệ thuật cao nhất về con người. Đ&oacute; mang &yacute; nghĩa s&acirc;u sắc nhất v&agrave; được đ&uacute;c kết từ những nguy&ecirc;n tắc v&agrave;ng của t&aacute;c giả Dale Carnegie. C&ugrave;ng theo d&otilde;i nội dung của cuốn s&aacute;ch Đắc Nh&acirc;n T&acirc;m ngay dưới đ&acirc;y để hiểu hơn về cuốn s&aacute;ch n&agrave;y nh&eacute;!</span></span></p>\r\n', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#ff0000\"><strong>CHƯƠNG I</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">QUYỂN S&Aacute;CH CỦA MỌI THỜI ĐẠI Khi xuất bản lần đầu ti&ecirc;n v&agrave;o năm 1936, t&aacute;c phẩm &quot;How to Win Friends and Influence People&quot; của Dale Carnegie chỉ in c&oacute; năm ngh&igrave;n bản. Cả Dale Carnegie v&agrave; nh&agrave; xuất bản Simon &amp; Schuster đều kh&ocirc;ng ngờ rằng ngay khi ph&aacute;t h&agrave;nh, quyển s&aacute;ch đ&atilde; trở th&agrave;nh một hiện tượng đ&aacute;ng kinh ngạc trong ng&agrave;nh xuất bản Hoa Kỳ. Simon &amp; Schuster phải t&aacute;i bản li&ecirc;n tục mới kịp đ&aacute;p ứng đủ nhu cầu của bạn đọc. Trong suốt nhiều thập kỷ tiếp theo v&agrave; cho đến tận b&acirc;y giờ, t&aacute;c phẩm n&agrave;y vẫn chiếm vị tr&iacute; số một trong danh mục s&aacute;ch b&aacute;n chạy nhất v&agrave; trở th&agrave;nh một sự kiện c&oacute; một kh&ocirc;ng hai trong lịch sử ng&agrave;nh xuất bản thế giới v&agrave; được đ&aacute;nh gi&aacute; l&agrave; một quyển s&aacute;ch c&oacute; tầm ảnh hưởng nhất mọi thời đại. Dale Carnegie đ&atilde; từng n&oacute;i kiếm được một triệu đ&ocirc;-la c&ograve;n dễ d&agrave;ng hơn l&agrave; s&aacute;ng tạo ra một cụm từ tiếng Anh th&ocirc;ng dụng. &quot;How to win friends and influence people&quot; đ&atilde; trở th&agrave;nh một cụm 9 từ nổi tiếng được sử dụng nhiều đến mức ch&iacute;nh người tạo ra n&oacute; cũng kh&ocirc;ng ngờ đến. Cụm từ n&agrave;y v&agrave; những gi&aacute; trị trong t&aacute;c phẩm của Dale Carnegie đ&atilde; được đề cập, tr&iacute;ch dẫn, b&agrave;n luận, ứng dụng trong v&ocirc; v&agrave;n t&igrave;nh huống kh&aacute;c nhau của cuộc sống. T&aacute;c phẩm đ&atilde; được dịch sang hơn 45 ng&ocirc;n ngữ tr&ecirc;n thế giới. V&agrave;, thật diệu kỳ, quyển s&aacute;ch chưa bao giờ bị lạc hậu bởi thời gian. Mỗi thế hệ đều cảm nhận, kh&aacute;m ph&aacute; từ quyển s&aacute;ch c&aacute;ch tư duy, c&aacute;ch ứng xử v&agrave; c&aacute;ch sống mới mẻ, th&uacute; vị v&agrave; rất hữu &iacute;ch cho cuộc sống của ch&iacute;nh m&igrave;nh. Một vấn đề rất đ&aacute;ng được n&ecirc;u l&ecirc;n: Tại sao phải đ&aacute;nh gi&aacute; lại một t&aacute;c phẩm m&agrave; sự th&agrave;nh c&ocirc;ng v&agrave; nổi tiếng của n&oacute; đ&atilde; được minh chứng qua thực tiễn của mọi thời đại? Tại sao phải hiện đại h&oacute;a một t&aacute;c phẩm đ&atilde; được xem l&agrave; kinh điển, l&agrave; một biểu tượng văn h&oacute;a của nh&acirc;n loại? Để trả lời c&acirc;u hỏi tr&ecirc;n, trước hết ch&uacute;ng ta cần hiểu rằng Dale Carnegie đ&atilde; d&agrave;nh t&acirc;m huyết cả cuộc đời m&igrave;nh để viết n&ecirc;n t&aacute;c phẩm n&agrave;y. &quot;How to Win Friends and Influence People&quot; đ&atilde; được d&ugrave;ng l&agrave;m t&agrave;i liệu ch&iacute;nh cho c&aacute;c b&agrave;i giảng v&agrave; c&aacute;c cuộc n&oacute;i chuyện về &quot;Thuật đối nh&acirc;n xử thế v&agrave; giao tiếp hiệu quả&quot; của Dale Carnegie v&agrave; những thế hệ diễn giả sau n&agrave;y. Cho đến trước khi qua đời v&agrave;o năm 1955, &ocirc;ng vẫn kh&ocirc;ng ngừng bổ sung v&agrave; cập nhật những kinh nghiệm sống của m&igrave;nh v&agrave;o t&aacute;c phẩm n&agrave;y trong mỗi lần t&aacute;i bản để quyển s&aacute;ch lu&ocirc;n ph&ugrave; hợp với tư duy của thời đại mới. Dường như kh&ocirc;ng ai nhạy cảm bằng Dale Carnegie trong cảm nhận về xu hướng ph&aacute;t triển của c&aacute;c mối quan hệ v&agrave; cuộc sống nội t&acirc;m con người. Nếu như Dale Carnegie c&ograve;n sống, chắc 10 HOW TO WIN FRIENDS &amp; INFLUENCE PEOPLE chắn &ocirc;ng sẽ vẫn tiếp tục bổ sung, cập nhật để t&aacute;c phẩm t&acirc;m huyết của m&igrave;nh lu&ocirc;n đồng h&agrave;nh, sống m&atilde;i với thời gian. T&ecirc;n tuổi của nhiều nh&acirc;n vật nổi tiếng từ trước những năm 1930 từng được đề cập, tr&iacute;ch dẫn trong c&aacute;c lần xuất bản trước của quyển s&aacute;ch n&agrave;y kh&ocirc;ng c&ograve;n quen thuộc với bạn đọc ng&agrave;y nay nữa. Một số dẫn chứng v&agrave; v&iacute; dụ minh họa đ&atilde; lạc hậu với hiện tại. Do đ&oacute;, trong lần xuất bản mới nhất n&agrave;y, ch&uacute;ng t&ocirc;i cập nhật v&agrave; bổ sung những v&iacute; dụ, dẫn chứng để l&agrave;m nổi bật hơn những &yacute; tưởng tinh tế, tầm nh&igrave;n v&agrave; những kinh nghiệm c&oacute; gi&aacute; trị vĩnh hằng của Carnegie. Thật vậy, Dale Carnegie đ&atilde; viết như &ocirc;ng đ&atilde; nghĩ, đ&atilde; n&oacute;i v&agrave; đ&atilde; sống bằng cả tr&aacute;i tim m&igrave;nh, một tr&aacute;i tim ch&acirc;n th&agrave;nh, gi&agrave;u l&ograve;ng trắc ẩn v&agrave; lu&ocirc;n tr&agrave;n đầy nhiệt huyết. Từng ng&agrave;y, từng giờ, h&agrave;ng triệu người tr&ecirc;n khắp thế giới vẫn đang đọc t&aacute;c phẩm bất hủ n&agrave;y của Carnegie. Quyển s&aacute;ch đ&atilde; gi&uacute;p thay đổi cuộc đời của rất nhiều người tốt đẹp hơn. Ch&uacute;ng t&ocirc;i tr&acirc;n trọng giới thiệu với bạn đọc ấn bản cập nhật mới n&agrave;y v&agrave; tin tưởng n&oacute; sẽ đem lại cho bạn sự th&agrave;nh c&ocirc;ng v&agrave; hạnh ph&uacute;c trong cuộc sống. DOROTHY CARNEGIE</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#ff0000\"><strong>CHƯƠNG II</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">LỜI T&Aacute;C GIẢ DALE CARNEGIE &quot;ĐẮC NH&Acirc;N T&Acirc;M&quot; RA ĐỜI NHƯ THẾ N&Agrave;O? Trong 35 năm đầu thế kỷ hai mươi, c&aacute;c nh&agrave; xuất bản ở Mỹ đ&atilde; in tr&ecirc;n 200.000 đầu s&aacute;ch kh&aacute;c nhau. Hầu hết ch&uacute;ng đều nh&agrave;m ch&aacute;n v&agrave; thất bại về doanh thu. Gi&aacute;m đốc một trong những nh&agrave; xuất bản lớn nhất tr&ecirc;n thế giới cho t&ocirc;i biết rằng d&ugrave; nh&agrave; xuất bản của &ocirc;ng đ&atilde; c&oacute; bảy mươi lăm năm kinh nghiệm song vẫn thất bại 7 tr&ecirc;n 8 đầu s&aacute;ch đ&atilde; in trong năm. Như vậy, tại sao t&ocirc;i vẫn quyết t&acirc;m viết cuốn s&aacute;ch n&agrave;y v&agrave; sau khi ho&agrave;n tất, t&ocirc;i c&ograve;n t&acirc;m đắc đọc đi đọc lại rất nhiều lần? Từ năm 1912, t&ocirc;i giảng dạy nghệ thuật kinh doanh ở New York. Ban đầu, với kinh nghiệm thực tế, t&ocirc;i dạy học vi&ecirc;n c&aacute;ch 13 diễn thuyết trước c&ocirc;ng ch&uacute;ng. Sau đ&oacute;, t&ocirc;i nhận thấy c&aacute;c học vi&ecirc;n cần được hướng dẫn c&aacute;ch diễn đạt hiệu quả, đặc biệt l&agrave; nghệ thuật ứng xử với con người, trong kinh doanh cũng như giao tiếp x&atilde; hội h&agrave;ng ng&agrave;y. T&ocirc;i nghĩ rằng, ch&iacute;nh bản th&acirc;n t&ocirc;i cũng cần tự nh&igrave;n lại m&igrave;nh. Suy nghiệm về thời gian đ&atilde; qua, t&ocirc;i tự nhận thấy bản th&acirc;n đ&atilde; c&oacute; rất nhiều lần xử sự thiếu tế nhị. Nếu c&aacute;ch đ&acirc;y 20 năm m&agrave; được đọc một quyển s&aacute;ch dạy c&aacute;ch ứng xử tốt th&igrave; c&oacute; lẽ t&ocirc;i đ&atilde; kh&aacute;c đi rất nhiều. Cư xử sao cho đẹp l&ograve;ng người nhưng kh&ocirc;ng thiệt phần m&igrave;nh l&agrave; cả một nghệ thuật. Điều đ&oacute; đ&ograve;i hỏi sự nhạy cảm, tinh tế trong giao tiếp h&agrave;ng ng&agrave;y, đặc biệt l&agrave; trong quan hệ kinh doanh. Điều n&agrave;y đ&uacute;ng với tất cả mọi người thuộc mọi tầng lớp, giai cấp trong x&atilde; hội. Theo một nghi&ecirc;n cứu được tiến h&agrave;nh dưới sự bảo trợ của Viện Carnegie th&igrave; ngay cả trong lĩnh vực kỹ thuật c&ocirc;ng nghệ, th&agrave;nh c&ocirc;ng về t&agrave;i ch&iacute;nh của một người thường chỉ c&oacute; 15% l&agrave; do khả năng chuy&ecirc;n m&ocirc;n, 85% c&ograve;n lại t&ugrave;y thuộc v&agrave;o nh&acirc;n c&aacute;ch, sự kh&eacute;o l&eacute;o, tế nhị trong giao tiếp v&agrave; khả năng quản l&yacute;, l&atilde;nh đạo. Tr&ecirc;n 1.500 kỹ sư đ&atilde; đến với c&aacute;c lớp học do t&ocirc;i giảng dạy tại C&acirc;u lạc bộ c&aacute;c kỹ sư Philadelphia v&agrave; Viện Kỹ thuật điện Hoa Kỳ. L&yacute; do họ đến với lớp học n&agrave;y l&agrave; v&igrave; họ nhận ra rằng những người được trả lương cao nhất trong ng&agrave;nh cơ kh&iacute; kh&ocirc;ng phải l&agrave; những người th&ocirc;ng thạo nhất về m&aacute;y m&oacute;c m&agrave; ch&iacute;nh l&agrave; những người c&oacute; tr&igrave;nh độ kỹ thuật kết hợp với khả năng giao tiếp th&ocirc;ng minh v&agrave; tinh tế, c&oacute; khả năng l&atilde;nh đạo, biết khơi dậy l&ograve;ng nhiệt t&igrave;nh v&agrave; biết động vi&ecirc;n mọi người một c&aacute;ch tốt nhất. Rockefeller cũng từng n&oacute;i rằng: &quot;Nếu c&oacute; thể mua được khả năng đối nh&acirc;n xử thế th&igrave; t&ocirc;i sẵn s&agrave;ng dốc hết hầu bao của m&igrave;nh v&igrave; đ&acirc;y ch&iacute;nh l&agrave; điều quan trọng hơn bất cứ điều g&igrave; kh&aacute;c tr&ecirc;n đời&quot;. C&aacute;ch đ&acirc;y kh&aacute; l&acirc;u, trường Đại học Chicago v&agrave; c&aacute;c trường của Hội C&ocirc;ng gi&aacute;o Mỹ đ&atilde; tiến h&agrave;nh một cuộc thăm d&ograve; trong hai năm với 156 c&acirc;u hỏi để t&igrave;m hiểu xem những người trưởng th&agrave;nh muốn học hỏi điều g&igrave; nhiều nhất. Trong danh s&aacute;ch đ&oacute; c&oacute; những c&acirc;u như: C&ocirc;ng việc v&agrave; nghề nghiệp của bạn l&agrave; g&igrave;? Mối quan t&acirc;m của bạn l&agrave; g&igrave;? Thời giờ rảnh bạn l&agrave;m g&igrave;? Thu nhập của bạn ra sao? Những sở th&iacute;ch, ước mơ của bạn? Những vấn đề kh&oacute; khăn của bạn trong cuộc sống? Ngo&agrave;i c&ocirc;ng việc, học tập, bạn quan t&acirc;m đến điều g&igrave; nhất?... Kết quả cuộc thăm d&ograve; cho thấy mọi người quan t&acirc;m nhiều nhất đến sức khỏe, tiếp theo đ&oacute; l&agrave; c&aacute;ch ứng xử sao cho họ được người kh&aacute;c qu&yacute; trọng, tin tưởng v&agrave; nghe theo. Với mong muốn huấn luyện, đ&agrave;o tạo những học vi&ecirc;n ở Meriden một kh&oacute;a học về nghệ thuật giao tiếp ứng xử, những giảng vi&ecirc;n đ&atilde; cố gắng t&igrave;m kiếm một v&agrave;i quyển s&aacute;ch để c&oacute; thể l&agrave;m t&agrave;i liệu giảng dạy nhưng thật ngạc nhi&ecirc;n l&agrave; kh&ocirc;ng hề c&oacute; một quyển s&aacute;ch n&agrave;o được viết về đề t&agrave;i n&agrave;y cả. Kh&ocirc;ng thể t&igrave;m ở đ&acirc;u ra một cuốn s&aacute;ch để l&agrave;m gi&aacute;o tr&igrave;nh, thế l&agrave; t&ocirc;i bắt tay v&agrave;o viết một quyển s&aacute;ch như vậy. Trước khi viết, t&ocirc;i đ&atilde; bỏ nhiều thời gian để lục t&igrave;m v&agrave; tham khảo rất kỹ những vấn đề m&igrave;nh đang quan t&acirc;m tr&ecirc;n c&aacute;c s&aacute;ch b&aacute;o, tạp ch&iacute;, những c&acirc;u chuyện v&agrave; hồ sơ ghi ch&eacute;p c&aacute;c vụ kiện tụng trong gia đ&igrave;nh v&agrave; x&atilde; hội, c&aacute;c t&aacute;c phẩm kinh điển của những triết gia v&agrave; những nh&agrave; t&acirc;m l&yacute; học nổi tiếng tr&ecirc;n thế giới... Thậm ch&iacute; t&ocirc;i c&ograve;n thu&ecirc; cả một nh&agrave; nghi&ecirc;n cứu chuy&ecirc;n nghiệp d&agrave;nh ra hơn một năm rưỡi trời để t&igrave;m đọc tất cả những g&igrave; c&oacute; trong c&aacute;c thư viện m&agrave; t&ocirc;i đ&atilde; bỏ s&oacute;t, đ&agrave;o bới trong những b&agrave;i học về t&acirc;m l&yacute;, r&agrave; so&aacute;t h&agrave;ng trăm b&agrave;i b&aacute;o, t&igrave;m kiếm kh&ocirc;ng biết bao nhi&ecirc;u l&agrave; tiểu sử với nỗ lực t&igrave;m hiểu c&aacute;ch m&agrave; c&aacute;c nh&agrave; l&atilde;nh đạo kiệt xuất của mọi thời đại đ&atilde; đối xử với những người xung quanh họ như thế n&agrave;o. B&ecirc;n cạnh đ&oacute;, ch&uacute;ng t&ocirc;i cũng đọc về gương th&agrave;nh c&ocirc;ng của nhiều nh&acirc;n vật xuất ch&uacute;ng như Julius Caesar(1), Thomas Edison(2) ... T&ocirc;i c&ograve;n nhớ, chỉ ri&ecirc;ng Theodore Roosevelt, ch&uacute;ng t&ocirc;i đ&atilde; t&igrave;m đọc đến hơn 100 b&agrave;i tư liệu về cuộc đời &ocirc;ng. C&aacute; nh&acirc;n t&ocirc;i đ&atilde; trực tiếp phỏng vấn rất nhiều người th&agrave;nh c&ocirc;ng, trong đ&oacute; c&oacute; những nh&acirc;n vật nổi tiếng thế giới thuộc nhiều lĩnh vực: nh&agrave; ph&aacute;t minh vĩ đại Marconi, Edison&hellip; nh&agrave; l&atilde;nh đạo ch&iacute;nh trị xuất ch&uacute;ng như Franklin D. Roosevelt, James Farley, nh&agrave; kinh doanh t&agrave;i năng Owen D. Young, ng&ocirc;i sao m&agrave;n bạc Clark Gable, Mary Pickford, nh&agrave; th&aacute;m hiểm Martin Johnson... để cố t&igrave;m ra những b&iacute; quyết th&agrave;nh c&ocirc;ng m&agrave; họ đ&atilde; ứng dụng trong c&aacute;ch đối nh&acirc;n xử thế. Từ tất cả những t&agrave;i liệu thu thập được, t&ocirc;i đ&atilde; chuẩn bị một 16 HOW TO WIN FRIENDS &amp; INFLUENCE PEOPLE (1) Julius Caesar (100 &ndash; 44 Tr.CN): Ho&agrave;ng đế La M&atilde; cổ đại. (2) Thomas Edison (1847 &ndash; 1931): Nh&agrave; ph&aacute;t minh vĩ đại người Mỹ. b&agrave;i viết ngắn c&oacute; t&ecirc;n l&agrave; &quot;Đắc nh&acirc;n t&acirc;m &ndash; Nghệ thuật đối nh&acirc;n xử thế v&agrave; thu phục l&ograve;ng người&quot;. B&agrave;i viết n&agrave;y chỉ &quot;ngắn&quot; ở giai đoạn đầu nhưng kh&ocirc;ng l&acirc;u sau đ&oacute; được ph&aacute;t triển th&agrave;nh một b&agrave;i giảng k&eacute;o d&agrave;i trong v&ograve;ng một tiếng rưỡi đồng hồ. Khi kết th&uacute;c b&agrave;i giảng, t&ocirc;i đề nghị c&aacute;c học vi&ecirc;n thể nghiệm những kinh nghiệm, những nguy&ecirc;n tắc m&agrave; họ đ&atilde; học v&agrave;o trong c&ocirc;ng việc v&agrave; cuộc sống của họ v&agrave; khi quay trở lại lớp trong buổi học sau, mỗi học vi&ecirc;n sẽ tr&igrave;nh b&agrave;y những trải nghiệm v&agrave; kết quả m&agrave; họ đạt được. T&ocirc;i đ&atilde; thật sự bất ngờ, th&uacute; vị v&agrave; đồng cảm với những c&acirc;u chuyện x&uacute;c động v&agrave; những điều bổ &iacute;ch m&agrave; c&aacute;c học vi&ecirc;n kể lại. Đ&acirc;y kh&ocirc;ng phải l&agrave; một quyển s&aacute;ch b&igrave;nh thường nằm lặng y&ecirc;n tr&ecirc;n gi&aacute;. N&oacute; trưởng th&agrave;nh theo thời gian như sự ph&aacute;t triển của một đứa trẻ. N&oacute; &quot;lớn l&ecirc;n v&agrave; ph&aacute;t triển&quot; vượt ra khỏi những kinh nghiệm của h&agrave;ng triệu người qua c&aacute;c thời đại đầy biến động của rất nhiều nền văn h&oacute;a kh&aacute;c nhau. L&uacute;c khởi đầu c&aacute;ch đ&acirc;y nhiều năm, ch&uacute;ng t&ocirc;i đ&atilde; in những nguy&ecirc;n tắc ứng xử đ&oacute; tr&ecirc;n một tấm thẻ bằng k&iacute;ch cỡ tấm bưu thiếp. Một năm sau, ch&uacute;ng t&ocirc;i phải in tr&ecirc;n một bưu thiếp lớn hơn, sau đ&oacute; l&agrave; một tờ giấy khổ lớn&hellip; Dần dần những nguy&ecirc;n tắc đ&oacute; cứ tăng l&ecirc;n v&agrave; được in th&agrave;nh một loạt những quyển sổ tay nhỏ. Sau 15 năm nghi&ecirc;n cứu v&agrave; trải nghiệm, quyển s&aacute;ch n&agrave;y ch&iacute;nh thức được ho&agrave;n thiện v&agrave; ra đời. Những điều được tr&igrave;nh b&agrave;y trong quyển s&aacute;ch n&agrave;y kh&ocirc;ng chỉ đơn thuần l&agrave; những l&yacute; thuyết hay những l&yacute; luận cảm t&iacute;nh, chủ quan. Những kinh nghiệm n&agrave;y thật sự c&oacute; một ph&eacute;p lạ v&agrave; từng l&agrave;m thay đổi cuộc đời của rất nhiều người đ&atilde; ứng dụng n&oacute;. Trong lớp học của t&ocirc;i c&oacute; một vị gi&aacute;m đốc quản l&yacute; 314 nh&acirc;n vi&ecirc;n. Trước đ&acirc;y, &ocirc;ng thường xuy&ecirc;n &eacute;p buộc, chỉ tr&iacute;ch v&agrave; tr&aacute;ch mắng nh&acirc;n vi&ecirc;n của m&igrave;nh. Hầu như chẳng bao giờ &ocirc;ng c&oacute; được th&aacute;i độ dịu d&agrave;ng, những lời động vi&ecirc;n, khuyến kh&iacute;ch nh&acirc;n vi&ecirc;n. Nhưng ngay sau khi nghi&ecirc;n cứu những nguy&ecirc;n tắc được đề cập trong quyển s&aacute;ch, &ocirc;ng đ&atilde; thay đổi ho&agrave;n to&agrave;n triết l&yacute; sống của m&igrave;nh. Mọi nh&acirc;n vi&ecirc;n trong c&ocirc;ng ty &ocirc;ng như được thổi một luồng sinh kh&iacute; mới, l&agrave;m việc với một sự trung th&agrave;nh cao độ, l&ograve;ng nhiệt t&igrave;nh hăng h&aacute;i v&agrave; một tinh thần đồng đội mạnh mẽ &ndash; điều m&agrave; trước đ&acirc;y chưa hề c&oacute;. Điều tuyệt vời nhất l&agrave; &ocirc;ng đ&atilde; biến 314 con người m&agrave; &ocirc;ng từng xem như những kẻ chống đối trở th&agrave;nh 314 người bạn thật sự của m&igrave;nh. &Ocirc;ng tự h&agrave;o kể lại: &quot;Trước đ&acirc;y mỗi lần đến c&ocirc;ng ty, t&ocirc;i chẳng hề được ai ch&agrave;o đ&oacute;n. C&aacute;c nh&acirc;n vi&ecirc;n thường nh&igrave;n về ph&iacute;a kh&aacute;c khi thấy t&ocirc;i đến gần. Nhưng b&acirc;y giờ tất cả thực sự đều l&agrave; bạn t&ocirc;i, ngay cả người bảo vệ g&aacute;c cổng cũng ch&agrave;o t&ocirc;i một c&aacute;ch rất th&acirc;n mật&quot;. Người quản l&yacute; n&agrave;y đ&atilde; đạt được nhiều th&agrave;nh c&ocirc;ng hơn, nhiều niềm vui hơn &ndash; v&agrave; quan trọng hơn cả l&agrave; &ocirc;ng đ&atilde; t&igrave;m thấy được hạnh ph&uacute;c trong c&ocirc;ng việc v&agrave; cuộc sống. Cũng nhờ ứng dụng hiệu quả những nguy&ecirc;n tắc trong quyển s&aacute;ch m&agrave; rất nhiều nh&acirc;n vi&ecirc;n kinh doanh đ&atilde; tăng đ&aacute;ng kể doanh số của m&igrave;nh. Nhiều người ph&aacute;t triển th&ecirc;m được những kh&aacute;ch h&agrave;ng mới &ndash; những kh&aacute;ch h&agrave;ng m&agrave; trước đ&oacute; họ 18 HOW TO WIN FRIENDS &amp; INFLUENCE PEOPLE ho&agrave;n to&agrave;n v&ocirc; vọng trong việc x&acirc;y dựng mối quan hệ. C&aacute;c l&atilde;nh đạo cấp cao th&igrave; được thăng chức, tăng lương. Những buổi học về nghệ thuật ứng xử kh&ocirc;ng những đ&atilde; thay đổi được nhiều người bảo thủ, c&oacute; định kiến hay chống đối, những người thiếu h&ograve;a đồng tho&aacute;t khỏi nguy cơ bị sa thải m&agrave; c&ograve;n gi&uacute;p họ nhận được l&ograve;ng tin v&agrave; được giao ph&oacute; những trọng tr&aacute;ch cao hơn. Những c&aacute;ch ứng xử n&agrave;y kh&ocirc;ng những gi&uacute;p h&agrave;n gắn lại những rạn nứt trong c&aacute;c mối quan hệ x&atilde; hội, c&ocirc;ng việc m&agrave; c&ograve;n tạo n&ecirc;n những t&igrave;nh cảm tốt đẹp, l&agrave;m thăng hoa hạnh ph&uacute;c gia đ&igrave;nh. Hầu hết c&aacute;c học vi&ecirc;n đều rất kinh ngạc về những kết quả tựa như ph&eacute;p m&agrave;u m&agrave; họ đ&atilde; đạt được. Nhiều học vi&ecirc;n đ&atilde; điện thoại đến nh&agrave; t&ocirc;i cả v&agrave;o ng&agrave;y chủ nhật v&igrave; kh&ocirc;ng thể chờ đến 48 tiếng đồng hồ nữa để chia sẻ những trải nghiệm m&agrave; họ vừa kh&aacute;m ph&aacute; được. Một học vi&ecirc;n qu&aacute; x&uacute;c động trước một b&agrave;i tham luận đến mức ngồi lại thảo luận với c&aacute;c học vi&ecirc;n kh&aacute;c đến tận 3 giờ s&aacute;ng mới ra về. Anh ta qu&aacute; phấn kh&iacute;ch v&agrave; hồi hộp trước những nh&igrave;n nhận về lỗi lầm của bản th&acirc;n v&agrave; trước những cảm nhận cuộc sống c&oacute; th&ecirc;m nhiều &yacute; nghĩa, thế giới đầy những điều mới mẻ, đầy những cảm hứng đang chờ đợi m&igrave;nh đến nỗi anh thao thức, kh&ocirc;ng thể ngủ được trong nhiều đ&ecirc;m liền. Gi&aacute;o sư nổi tiếng William James của trường Đại học Harvard n&oacute;i rằng: &quot;So với tất cả những g&igrave; ch&uacute;ng ta đ&atilde; h&agrave;nh xử, ch&uacute;ng ta chỉ mới &quot;tỉnh ngộ&quot; c&oacute; nửa phần m&agrave; th&ocirc;i. Ch&uacute;ng ta mới chỉ sử dụng một phần nhỏ những khả năng thể chất v&agrave; tinh thần vốn c&oacute;. Con người thường thỏa hiệp, bằng l&ograve;ng với ch&iacute;nh m&igrave;nh v&agrave; sống trong c&aacute;c giới hạn tự đặt ra m&agrave; ch&uacute;ng c&aacute;ch rất xa so với khả năng thật sự của m&igrave;nh. Rất nhiều khả năng lớn lao đ&atilde; kh&ocirc;ng được d&ugrave;ng đến!&quot; V&agrave;, mục đ&iacute;ch duy nhất của quyển s&aacute;ch n&agrave;y l&agrave; gi&uacute;p c&aacute;c bạn ph&aacute;t hiện, ph&aacute;t triển v&agrave; tận dụng những tiềm năng đang c&ograve;n ngủ y&ecirc;n, chưa được khai th&aacute;c trong ch&iacute;nh con người bạn! Theo tiến sĩ John G. Hibben, nguy&ecirc;n Hiệu trưởng Đại học Princeton: &quot;Thước đo sự gi&aacute;o dục của một con người ch&iacute;nh l&agrave; khả năng ứng xử của anh ta trước những t&igrave;nh huống của cuộc sống&quot;. Nếu như bạn đọc xong ba chương đầu của quyển s&aacute;ch m&agrave; vẫn chưa cảm thấy m&igrave;nh tiếp nhận được những kiến thức v&agrave; kinh nghiệm tốt hơn để ứng dụng v&agrave;o những t&igrave;nh huống thực tế cuộc sống của bạn th&igrave; t&ocirc;i xem đ&oacute; l&agrave; một thất bại ho&agrave;n to&agrave;n. V&agrave;, như vậy, quyển s&aacute;ch n&agrave;y kh&ocirc;ng c&ograve;n cần thiết cho bạn nữa. Bởi v&igrave; &quot;mục đ&iacute;ch tối thượng của gi&aacute;o dục&quot;, như Herbert Spencer n&oacute;i, &quot;kh&ocirc;ng phải l&agrave; kiến thức - m&agrave; l&agrave; h&agrave;nh động&quot;. V&agrave; - đ&acirc;y ch&iacute;nh l&agrave; một quyển s&aacute;ch của h&agrave;nh động! DALE CARNEGIE 1936</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#ff0000\"><strong>CHƯƠNG 3:</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">ĐỂ QUYỂN S&Aacute;CH N&Agrave;Y MANG ĐẾN KẾT QUẢ TỐT NHẤT&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">1. Một y&ecirc;u cầu rất quan trọng để bạn đạt được hiệu quả cao nhất từ quyển s&aacute;ch n&agrave;y l&agrave;: Một khao kh&aacute;t m&atilde;nh liệt, một quyết t&acirc;m mạnh mẽ muốn thay đổi, muốn cải thiện khả năng đối nh&acirc;n xử thế v&agrave; ho&agrave;n thiện ch&iacute;nh m&igrave;nh. Để c&oacute; được một kh&aacute;t khao m&atilde;nh liệt như vậy, h&atilde;y nghĩ rằng việc hiểu biết v&agrave; vận dụng th&agrave;nh c&ocirc;ng những nguy&ecirc;n tắc n&agrave;y sẽ gi&uacute;p bạn c&oacute; một cuộc sống gi&agrave;u c&oacute; về tinh thần lẫn vật chất, c&oacute; được th&agrave;nh c&ocirc;ng v&agrave; hạnh ph&uacute;c hơn. Bạn cũng c&oacute; thể tự lặp đi lặp lại rằng: &quot;Uy t&iacute;n, gi&aacute; trị v&agrave; hạnh ph&uacute;c của t&ocirc;i phần lớn t&ugrave;y thuộc v&agrave;o khả năng ứng xử của t&ocirc;i với mọi người xung quanh&quot;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">&nbsp;2. Bạn n&ecirc;n đọc nhanh to&agrave;n bộ quyển s&aacute;ch để c&oacute; được c&aacute;i nh&igrave;n tổng qu&aacute;t về nội dung ch&iacute;nh. Sau đ&oacute;, bạn đọc lại thật kỹ từng chương. Về l&acirc;u d&agrave;i, điều n&agrave;y sẽ tiết kiệmđược thời gian, 21 gi&uacute;p bạn c&oacute; được sự thay đổi lớn trong nội t&acirc;m v&agrave; thu được những kết quả mỹ m&atilde;n</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">. 3. Trong khi đọc, bạn n&ecirc;n dừng lại suy ngẫm về điều vừa đọc, tự hỏi xem bằng c&aacute;ch n&agrave;o v&agrave; khi n&agrave;o th&igrave; c&oacute; thể ứng dụng từng gợi &yacute; thiết thực đ&oacute;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">&nbsp;4. H&atilde;y chuẩn bị sẵn b&uacute;t m&agrave;u, b&uacute;t ch&igrave;, b&uacute;t mực, thước hay một vật g&igrave; c&oacute; thể d&ugrave;ng để đ&aacute;nh dấu. Khi bạn t&igrave;m thấy một &yacute; tưởng m&agrave; bạn nghĩ l&agrave; c&oacute; thể ứng dụng ngay được, h&atilde;y t&ocirc; m&agrave;u, gạch dưới hay đ&aacute;nh dấu theo c&aacute;ch ri&ecirc;ng của bạn, hoặc d&aacute;n một mảnh giấy nhỏ l&agrave;m dấu. Điều n&agrave;y sẽ gi&uacute;p cho việc đọc s&aacute;ch trở n&ecirc;n th&uacute; vị hơn. Ngo&agrave;i ra, khi cần đọc lại, bạn sẽ nhanh ch&oacute;ng t&igrave;m thấy những điểm cần quan t&acirc;m một c&aacute;ch dễ d&agrave;ng hơn.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">5. T&ocirc;i c&oacute; quen biết một gi&aacute;m đốc h&agrave;nh ch&iacute;nh của một c&ocirc;ng ty bảo hiểm. Tất cả c&aacute;c hợp đồng bảo hiểm ph&aacute;t h&agrave;nh h&agrave;ng th&aacute;ng b&agrave; đọc qua đều giống hệt nhau. Thế nhưng cứ hết th&aacute;ng n&agrave;y sang th&aacute;ng kh&aacute;c, suốt mười lăm năm đương chức, b&agrave; ấy đ&atilde; đọc kh&ocirc;ng s&oacute;t một hợp đồng n&agrave;o. Tại sao thế? Bởi v&igrave; kinh nghiệm đ&atilde; dạy cho b&agrave; biết rằng, đ&acirc;y l&agrave; c&aacute;ch tốt nhất để b&agrave; lu&ocirc;n nhớ được r&otilde; r&agrave;ng những điều khoản trong đầu. T&ocirc;i đ&atilde; từng mất gần hai năm r&ograve;ng r&atilde; để viết một quyển s&aacute;ch về c&aacute;ch diễn thuyết trước c&ocirc;ng ch&uacute;ng nhưng thỉnh thoảng t&ocirc;i vẫn phải đọc lại để nhớ những g&igrave; ch&iacute;nh t&ocirc;i đ&atilde; viết trong quyển s&aacute;ch của m&igrave;nh. V&igrave; vậy, nếu muốn c&oacute; được lợi &iacute;ch thực sự l&acirc;u d&agrave;i từ quyển s&aacute;ch n&agrave;y, t&ocirc;i đề nghị bạn n&ecirc;n đọc đi đọc lại nhiều lần. Sau khi HOW TO WIN FRIENDS &amp; INFLUENCE PEOPLE đọc thật kỹ lưỡng, mỗi th&aacute;ng bạn n&ecirc;n d&agrave;nh một v&agrave;i giờ để xem lại quyển s&aacute;ch v&agrave; đặt n&oacute; ở vị tr&iacute; dễ nh&igrave;n thấy nhất tr&ecirc;n b&agrave;n l&agrave;m việc của m&igrave;nh. H&atilde;y lu&ocirc;n nhớ rằng việc ứng dụng những nguy&ecirc;n tắc đ&atilde; học chỉ c&oacute; thể trở th&agrave;nh th&oacute;i quen một khi ch&uacute;ng ta c&oacute; một kế hoạch thực h&agrave;nh, ứng dụng, c&oacute; ghi nhận v&agrave; điều chỉnh thật s&aacute;t sao v&agrave; li&ecirc;n tục 6. Học một &yacute; tưởng, c&aacute;ch thức mới l&agrave; một qu&aacute; tr&igrave;nh t&iacute;ch cực v&agrave; chủ động. Ch&uacute;ng ta cần phải học bằng thực h&agrave;nh. Cho n&ecirc;n, nếu muốn l&agrave;m chủ c&aacute;c nguy&ecirc;n tắc trong quyển s&aacute;ch n&agrave;y th&igrave; bạn cần phải thực h&agrave;nh. Bạn h&atilde;y tận dụng mọi cơ hội để ứng dụng những điều đ&atilde; học được. Nếu kh&ocirc;ng l&agrave;m thế bạn sẽ nhanh ch&oacute;ng qu&ecirc;n đi. Chỉ những kiến thức được ứng dụng mới được khắc ghi thật sự v&agrave;o trong tr&iacute; nhớ. Tuy nhi&ecirc;n, t&ocirc;i biết kh&ocirc;ng phải l&uacute;c n&agrave;o cũng c&oacute; thể dễ d&agrave;ng ứng dụng những nguy&ecirc;n tắc n&agrave;y. T&ocirc;i biết r&otilde; điều đ&oacute; bởi v&igrave; ch&iacute;nh t&ocirc;i vẫn thường thấy kh&oacute; ứng dụng những điều m&agrave; t&ocirc;i đ&atilde; biết v&agrave;o cuộc sống h&agrave;ng ng&agrave;y như t&ocirc;i đ&atilde; từng dự định. V&iacute; dụ, khi bạn kh&ocirc;ng h&agrave;i l&ograve;ng với ai th&igrave; bạn thường c&oacute; khuynh hướng ph&ecirc; ph&aacute;n v&agrave; chỉ tr&iacute;ch hơn l&agrave; t&igrave;m c&aacute;ch hiểu ho&agrave;n cảnh v&agrave; quan điểm của người ấy. Thường th&igrave; t&igrave;m thấy sai lầm dễ d&agrave;ng hơn t&igrave;m thấy điều đ&aacute;ng để khen ngợi v&agrave; n&oacute;i về điều bạn muốn dễ d&agrave;ng hơn l&agrave; n&oacute;i về điều người kh&aacute;c muốn, v.v. Cho n&ecirc;n, khi đọc quyển s&aacute;ch n&agrave;y, ngo&agrave;i việc t&igrave;m kiếm những &yacute; tưởng, th&ocirc;ng tin mới, bạn c&ograve;n cần t&igrave;m c&aacute;ch tạo n&ecirc;n những th&oacute;i quen mới, thực hiện một c&aacute;ch sống, c&aacute;ch ứng xử v&agrave; suy nghĩ mới. Điều n&agrave;y đ&ograve;i hỏi thời gian v&agrave; phải c&oacute; sự quyết t&acirc;m, ki&ecirc;n tr&igrave; tập luyện h&agrave;ng ng&agrave;y. Như vậy, nếu bạn thường xuy&ecirc;n đọc đi đọc lại quyển s&aacute;ch n&agrave;y v&agrave; xem đ&oacute; như một cẩm nang về c&aacute;ch cải thiện những mối quan hệ với con người v&agrave; x&atilde; hội, quyển s&aacute;ch sẽ rất hữu &iacute;ch cho bạn. Mỗi l&uacute;c bạn đối diện với một vấn đề phức tạp - như mắc một lỗi lầm trong ứng xử, bị một đồng nghiệp hay một người bạn hiểu sai, khi cần đối xử với một đứa b&eacute; ngỗ nghịch, h&ograve;a giải những bất đồng trong đời sống gia đ&igrave;nh hay phải l&agrave;m h&agrave;i l&ograve;ng một kh&aacute;ch h&agrave;ng đang bực bội - th&igrave; bạn n&ecirc;n ứng dụng linh động những nguy&ecirc;n tắc đ&oacute;. T&ocirc;i nghĩ rằng chắc chắn ch&uacute;ng sẽ đem lại những kết quả kỳ diệu cho bạn.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">&nbsp;7. Bạn c&oacute; thể đề nghị một người bạn hay người th&acirc;n quan s&aacute;t m&igrave;nh v&agrave; c&oacute; thể tặng người đ&oacute; một m&oacute;n qu&agrave; nhỏ mỗi lần người ấy bắt gặp bạn vi phạm một nguy&ecirc;n tắc ứng xử n&agrave;o đ&oacute;. H&atilde;y biến việc thực h&agrave;nh những nguy&ecirc;n tắc n&agrave;y th&agrave;nh một tr&ograve; chơi sinh động, th&uacute; vị.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">8. C&oacute; lần, chủ tịch một ng&acirc;n h&agrave;ng nổi tiếng ở Wall Street n&oacute;i về hệ thống c&acirc;u hỏi tự kiểm tra rất hiệu quả m&agrave; &ocirc;ng d&ugrave;ng để tự điều chỉnh c&aacute;ch ứng xử của m&igrave;nh. Mặc d&ugrave; tr&igrave;nh độ học vấn kh&ocirc;ng cao nhưng &ocirc;ng đ&atilde; trở th&agrave;nh một trong những nh&agrave; t&agrave;i ch&iacute;nh uy t&iacute;n nhất ở Mỹ. &Ocirc;ng th&uacute; nhận rằng, hầu hết mọi th&agrave;nh c&ocirc;ng m&agrave; &ocirc;ng c&oacute; được đều l&agrave; nhờ thường xuy&ecirc;n ứng dụng phương ph&aacute;p đ&oacute;. Dưới đ&acirc;y l&agrave; nguy&ecirc;n văn lời t&acirc;m sự của &ocirc;ng: &quot;Đ&atilde; bao năm qua t&ocirc;i giữ một quyển sổ ghi ch&eacute;p mọi cuộc hẹn m&agrave; t&ocirc;i phải thực hiện trong tuần. Gia đ&igrave;nh t&ocirc;i kh&ocirc;ng bao giờ c&oacute; kế hoạch n&agrave;o cho t&ocirc;i v&agrave;o buổi tối thứ bảy v&igrave; họ biết t&ocirc;i lu&ocirc;n d&agrave;nh thời 24 HOW TO WIN FRIENDS &amp; INFLUENCE PEOPLE gian n&agrave;y cho cho qu&aacute; tr&igrave;nh &quot;khai s&aacute;ng&quot;, tự kiểm điểm v&agrave; nh&igrave;n nhận lại bản th&acirc;n m&igrave;nh. Sau khi ăn, t&ocirc;i mở quyển lịch hẹn v&agrave; suy nghĩ về mọi cuộc phỏng vấn, thảo luận v&agrave; gặp gỡ đ&atilde; diễn ra trong tuần v&agrave; tự hỏi m&igrave;nh: &quot;T&ocirc;i đ&atilde; phạm những sai lầm g&igrave;?&quot; &quot;T&ocirc;i l&agrave;m được điều g&igrave; đ&uacute;ng v&agrave; t&ocirc;i c&oacute; thể ho&agrave;n thiện hơn việc đ&oacute; bằng c&aacute;ch n&agrave;o?&quot; &quot; T&ocirc;i học được những b&agrave;i học g&igrave; từ kinh nghiệm n&agrave;y?&quot; T&ocirc;i lu&ocirc;n cảm thấy rất kh&oacute; chịu trong những lần tự kiểm h&agrave;ng tuần đ&oacute; v&agrave; thường rất ngạc nhi&ecirc;n trước những lỗi lầm hay sơ xuất ngu xuẩn của m&igrave;nh. Tuy nhi&ecirc;n, theo ng&agrave;y th&aacute;ng, những lầm lỗi n&agrave;y đ&atilde; dần dần &iacute;t hẳn đi. Đ&ocirc;i khi, t&ocirc;i cũng c&oacute; dịp tự khen thưởng m&igrave;nh khi kh&ocirc;ng nhận thấy lỗi lầm n&agrave;o. C&aacute;ch tự ph&acirc;n t&iacute;ch v&agrave; ki&ecirc;n định tự gi&aacute;o dục m&igrave;nh li&ecirc;n tục từ năm n&agrave;y sang năm kh&aacute;c đ&atilde; gi&uacute;p &iacute;ch cho t&ocirc;i nhiều hơn bất cứ điều g&igrave; kh&aacute;c. N&oacute; kh&ocirc;ng chỉ gi&uacute;p t&ocirc;i n&acirc;ng cao khả năng ra quyết định hiệu quả m&agrave; c&ograve;n gi&uacute;p t&ocirc;i rất nhiều trong mọi mối giao tiếp. T&ocirc;i kh&ocirc;ng thể c&oacute; lời khuy&ecirc;n n&agrave;o tốt hơn thế&quot;. Bạn cũng c&oacute; thể d&ugrave;ng một hệ thống c&acirc;u hỏi tương tự để kiểm tra c&aacute;ch ứng dụng những nguy&ecirc;n tắc được đề cập trong quyển s&aacute;ch n&agrave;y. L&agrave;m như vậy bạn sẽ c&oacute; hai điều lợi. Thứ nhất, bạn sẽ thấy m&igrave;nh tham dự v&agrave;o một qu&aacute; tr&igrave;nh tự r&egrave;n luyện, tự học hỏi, tuy vất vả nhưng rất c&oacute; &yacute; nghĩa v&agrave; c&oacute; gi&aacute; trị l&acirc;u d&agrave;i với bản th&acirc;n. Thứ hai, bạn sẽ thấy khả năng ứng xử của m&igrave;nh với mọi người sẽ ng&agrave;y c&agrave;ng ho&agrave;n thiện.&nbsp;&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">9. Bạn c&oacute; thể d&ugrave;ng một cuốn sổ nhỏ để ghi lại diễn tiến những hiệu quả m&agrave; bạn đ&atilde; ứng dụng từ quyển s&aacute;ch n&agrave;y. Bạn h&atilde;y ghi thật kỹ, cụ thể t&ecirc;n người, thời điểm v&agrave; kết quả. L&agrave;m như thế sẽ th&ocirc;i th&uacute;c bạn cố gắng hơn nữa, những ghi ch&uacute; n&agrave;y sẽ trở th&agrave;nh những kỷ niệm rất th&uacute; vị khi bạn ngẫu nhi&ecirc;n đọc lại n&oacute; trong một buổi tối thảnh thơi n&agrave;o đ&oacute; ở nhiều năm sau&hellip; T&oacute;m lại, để đạt được hiệu quả tốt nhất từ quyển s&aacute;ch n&agrave;y, bạn n&ecirc;n: Ph&aacute;t triển niềm khao kh&aacute;t s&acirc;u sắc v&agrave; mạnh mẽ l&agrave; sẽ l&agrave;m chủ cho bằng được những nguy&ecirc;n tắc kỳ diệu về c&aacute;ch ứng xử trong mối quan hệ giữa người với người. Đọc nhanh để nắm tổng qu&aacute;t v&agrave; sau đ&oacute; đọc từng chương thật kỹ trước khi đọc chương tiếp theo. Khi đọc, bạn n&ecirc;n thường xuy&ecirc;n dừng lại để chi&ecirc;m nghiệm v&agrave; tự hỏi xem l&agrave;m thế n&agrave;o để c&oacute; thể ứng dụng từng nguy&ecirc;n tắc v&agrave;o cuộc sống h&agrave;ng ng&agrave;y của m&igrave;nh. Gạch dưới mỗi &yacute; tưởng hay v&agrave; quan trọng. Đọc lại quyển s&aacute;ch n&agrave;y v&agrave;o thời gian rảnh mỗi th&aacute;ng. Tận dụng mọi cơ hội để ứng dụng những nguy&ecirc;n tắc n&agrave;y. H&atilde;y d&ugrave;ng quyển s&aacute;ch như một cẩm nang gi&uacute;p bạn giải quyết những vấn đề h&agrave;ng ng&agrave;y. Tạo n&ecirc;n một tr&ograve; chơi th&uacute; vị cho việc thực tập của m&igrave;nh bằng c&aacute;ch tặng một người th&acirc;n n&agrave;o đ&oacute; một m&oacute;n qu&agrave; nhỏ 26 HOW TO WIN FRIENDS &amp; INFLUENCE PEOPLE mỗi khi người ấy bắt gặp bạn vi phạm một trong những nguy&ecirc;n tắc. Mỗi tuần h&atilde;y kiểm tra lại những tiến bộ m&agrave; bạn đạt được. Tự hỏi xem m&igrave;nh đ&atilde; phạm những sai lầm g&igrave;, đạt được những tiến bộ g&igrave;, r&uacute;t ra được những b&agrave;i học g&igrave; cho tương lai. Ghi lại thời gian v&agrave; c&aacute;ch thức cụ thể m&agrave; bạn đ&atilde; ứng dụng những nguy&ecirc;n tắc n&agrave;y v&agrave;o cuốn sổ nhỏ ĐẮC NH&Acirc;N T&Acirc;M</span>.</span></span></p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(8, 'Chí Phèo ', 'Văn học Việt Nam', 'Nam Cao', 1941, 'chi-pheo.jpg', 'Đã hoàn', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Truyện xoay quanh nh&acirc;n vật Ch&iacute; Ph&egrave;o - đứa trẻ bị bỏ hoang trong một l&ograve; gạch cũ v&agrave; được nhặt về nu&ocirc;i. Lớn l&ecirc;n hắn đi ở hết nh&agrave; n&agrave;y nh&agrave; kh&aacute;c. Năm 20 tuổi, hắn l&agrave;m canh điền cho nh&agrave; B&aacute; Kiến, bị B&aacute; Kiến vu oan v&agrave; bắt bỏ t&ugrave;. Hắn ở t&ugrave; bảy t&aacute;m năm rồi trở về với bộ dạng kh&aacute;c hẳn ng&agrave;y xưa. Hắn l&uacute;c n&agrave;o cũng say v&agrave; cứ say l&agrave; hắn lại đến nh&agrave; B&aacute; Kiến để chửi bới, rạch mặt ăn vạ. B&aacute; Kiến đ&atilde; biến Ch&iacute; Ph&egrave;o th&agrave;nh kẻ tay sai chuy&ecirc;n đ&acirc;m thu&ecirc; ch&eacute;m mướn cho l&atilde;o. Ch&iacute; Ph&egrave;o trở th&agrave;nh con quỷ dữ của l&agrave;ng Vũ Đại, ai cũng khiếp sợ. Cuộc đời hắn kh&ocirc;ng l&uacute;c n&agrave;o tỉnh. Nhưng v&agrave;o một đ&ecirc;m trăng, hắn gặp Thị Nở, họ ăn nằm với nhau. Nửa đ&ecirc;m hắn đau bụng, n&ocirc;n mửa v&agrave; s&aacute;ng h&ocirc;m sau, Thị Nở nấu cho hắn một b&aacute;t ch&aacute;o h&agrave;nh. Cũng từ đ&oacute; hắn khao kh&aacute;t trở về cuộc sống lương thiện v&agrave; muốn được sống c&ugrave;ng Thị Nở. Nhưng một lần nữa hắn bị đạp xuống vực v&igrave; b&agrave; c&ocirc; của Thị kh&ocirc;ng đồng &yacute;. Ch&iacute; Ph&egrave;o tuyệt vọng, lại uống v&agrave; cầm dao đến nh&agrave; B&aacute; Kiến đ&ograve;i lương thiện. Hắn đ&acirc;m chết B&aacute; Kiến rồi tự tử. Thị Nở nghe tin hắn chết nh&igrave;n xuống bụng v&agrave; nghĩ đến c&aacute;i l&ograve; gạch.</span></span></span></span></p>\r\n', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:red\">CHƯƠNG 1:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#333333\">Giới thiệu: Ch&iacute; Ph&egrave;o l&agrave; đứa con hoang bị bỏ rơi trong một c&aacute;i l&ograve; gạch cũ. Một người đi thả ống lươn nhặt được, đem về cho một b&agrave; go&aacute; m&ugrave;. B&agrave; go&aacute; m&ugrave; lại cho b&aacute;c ph&oacute; cối kh&ocirc;ng con. Khi b&aacute;c ph&oacute; cối mất, Ch&iacute; Ph&egrave;o l&agrave;m canh điền cho nh&agrave; B&aacute; Kiến. B&agrave; vợ thứ ba của B&aacute; Kiến cứ bắt Ch&iacute; Ph&egrave;o l&ecirc;n b&oacute;p ch&acirc;n, đấm lưng. B&aacute; Ki&ecirc;n ghen b&oacute;ng gi&oacute; n&ecirc;n đẩy Ch&iacute; Ph&egrave;o đi t&ugrave;.</span></span><br />\r\n<br />\r\n<span style=\"color:#333333\"><span style=\"background-color:white\">Ra t&ugrave;, Ch&iacute; Ph&egrave;o trở về l&agrave;ng Vũ Đại với diện mạo v&agrave; h&agrave;nh động của một con &ldquo;quỷ dữ&rdquo;. Ch&iacute; Ph&egrave;o triền mi&ecirc;n trong cơn say rồi đi đ&acirc;m ch&eacute;m thu&ecirc;. Cuối c&ugrave;ng, hắn trở th&agrave;nh tay sai của B&aacute; Kiến. Một h&ocirc;m, Ch&iacute; Ph&egrave;o gặp Thị Nở, một người đ&agrave;n b&agrave; vừa xấu x&iacute; vừa dở hơi. Hai người ăn nằm với nhau. Ch&iacute; Ph&egrave;o kh&aacute;t khao c&oacute; cuộc sống lương thiện c&ugrave;ng Thị Nở. B&agrave; c&ocirc; Thị Nở kh&ocirc;ng đồng &yacute;, Ch&iacute; Ph&egrave;o đau đớn, tuyệt vọng.</span><br />\r\n<br />\r\n<span style=\"background-color:white\">Ch&iacute; ph&egrave;o cầm dao đến nh&agrave; B&aacute; Kiến đ&ograve;i l&agrave;m người lương thiện rồi lấy dao đ&acirc;m chết B&aacute; Kiến v&agrave; tự s&aacute;t. Mọi người trong l&agrave;ng đến xem. Thị Nở cũng đến xem. Thị Nở nh&igrave;n xuống, bụng v&agrave; nghĩ tới c&aacute;i l&ograve; gạch cũ bỏ kh&ocirc;ng.</span></span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:red\">CHƯƠNG 2:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn vừa đi vừa chửi. Bao giờ cũng thế, cứ rượu xong l&agrave; hắn chửi. Bắt đầu chửi trời, c&oacute; hề g&igrave;? Trời c&oacute; của ri&ecirc;ng nh&agrave; n&agrave;o? Rồi hắn chửi đời. Thế cũng chẳng sao: Đời l&agrave; tất cả nhưng cũng chẳng l&agrave; ai. Tức m&igrave;nh hắn chửi ngay tất cả l&agrave;ng Vũ Đại. Nhưng cả l&agrave;ng Vũ Đại ai cũng nhủ: &ldquo;Chắc n&oacute; trừ m&igrave;nh ra!&rdquo;. Kh&ocirc;ng ai l&ecirc;n tiếng cả. Tức thật! Ồ thế n&agrave;y th&igrave; tức thật! Tức chết đi được mất! Đ&atilde; thế, hắn phải chửi cha đứa n&agrave;o kh&ocirc;ng chửi nhau với hắn. Nhưng cũng kh&ocirc;ng ai ra điều. Mẹ kiếp! Thế th&igrave; c&oacute; ph&iacute; rượu kh&ocirc;ng? Thế th&igrave; c&oacute; khổ hắn kh&ocirc;ng? Kh&ocirc;ng biết đứa chết mẹ n&agrave;o đẻ ra th&acirc;n hắn cho hắn khổ đến n&ocirc;ng nỗi n&agrave;y! A ha! Phải đấy hắn cứ thế m&agrave; chửi, hắn chửi đứa chết mẹ n&agrave;o đẻ ra th&acirc;n hắn, đẻ ra c&aacute;i thằng Ch&iacute; Ph&egrave;o? M&agrave; c&oacute; trời biết! Hắn kh&ocirc;ng biết, cả l&agrave;ng Vũ Đại cũng kh&ocirc;ng ai biết.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Một anh đi thả ống lươn, một buổi s&aacute;ng tinh sương đ&atilde; thấy hắn trần truồng v&agrave; x&aacute;m ngắt trong c&aacute;i v&aacute;y đụp để b&ecirc;n một l&ograve; gạch bỏ kh&ocirc;ng, anh ta rước lấy v&agrave; đem cho một người đ&agrave;n b&agrave; g&oacute;a m&ugrave;. Người đ&agrave;n b&agrave; g&oacute;a m&ugrave; n&agrave;y b&aacute;n hắn cho một b&aacute;c ph&oacute; cối kh&ocirc;ng con v&agrave; khi b&aacute;c ph&oacute; cối n&agrave;y chết th&igrave; hắn bơ vơ, hết đi ở cho nh&agrave; n&agrave;y lại đi ở cho nh&agrave; nọ. Năm hai mươi tuổi, hắn l&agrave;m canh điền cho &ocirc;ng l&yacute; Kiến, b&acirc;y giờ cụ b&aacute; Kiến, ăn ti&ecirc;n chỉ l&agrave;ng. H&igrave;nh như, c&oacute; mấy lần b&agrave; ba nh&agrave; &ocirc;ng l&yacute;, trẻ lắm m&agrave; lại hay ốm lửng, bắt hắn b&oacute;p ch&acirc;n, hay xoa bụng, đấm lưng g&igrave; đấy. Người ta bảo &ocirc;ng l&yacute; ra đ&igrave;nh th&igrave; h&aacute;ch dịch, cả l&agrave;ng phải sợ, m&agrave; về nh&agrave; phải sợ c&aacute;i b&agrave; ba c&ograve;n trẻ n&agrave;y. Người b&agrave; ấy phốp ph&aacute;p, m&aacute; b&agrave; ấy h&acirc;y h&acirc;y, m&agrave; &ocirc;ng l&yacute; th&igrave; hay đau lưng lắm; người c&oacute; bệnh đau lưng th&igrave; hay sợ vợ m&agrave; ch&uacute;a đời l&agrave; khoẻ ghen. C&oacute; người bảo &ocirc;ng l&yacute; ghen anh canh điền khoẻ mạnh m&agrave; sợ b&agrave; ba kh&ocirc;ng d&aacute;m n&oacute;i. C&oacute; người th&igrave; bảo anh canh điền ấy được b&agrave; ba quyền thu quyền bổ trong nh&agrave; tin cẩn n&ecirc;n lấy trộm tiền trộm th&oacute;c nhiều. Mỗi người n&oacute;i một c&aacute;ch. Chẳng biết đ&acirc;u m&agrave; lần. Chỉ biết một h&ocirc;m Ch&iacute; bị giải huyện rồi nghe đ&acirc;u phải đi t&ugrave;. Kh&ocirc;ng biết t&ugrave; mấy năm, nhưng hắn đi biệt tăm bảy, t&aacute;m năm, rồi một h&ocirc;m, hắn lại l&ugrave; l&ugrave; ở đ&acirc;u lần về. Hắn về lần n&agrave;y tr&ocirc;ng kh&aacute;c hẳn, mới đầu chẳng ai biết hắn l&agrave; ai. Tr&ocirc;ng đặc như thằng sắng c&aacute;! C&aacute;i đầu th&igrave; trọc lốc, c&aacute;i răng cạo trắng hớn, c&aacute;i mặt th&igrave; đen m&agrave; rất cơng cơng, hai mắt gườm gườm tr&ocirc;ng gớm chết! Hắn mặc quần &aacute;o n&aacute;i đen với c&aacute;i &aacute;o t&acirc;y v&agrave;ng. C&aacute;i ngực phanh đầy những n&eacute;t chạm trổ rồng, phượng với một &ocirc;ng tướng cầm ch&ugrave;y, cả hai c&aacute;nh tay cũng thế. Tr&ocirc;ng gớm chết!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn về h&ocirc;m trước, h&ocirc;m sau đ&atilde; thấy ngồi ở chợ uống rượu với thịt ch&oacute; suốt từ trưa đến xế chiều. Rồi say khướt, hắn x&aacute;ch một c&aacute;i vỏ chai đến cổng nh&agrave; b&aacute; Kiến, gọi tận t&ecirc;n tục ra m&agrave; chửi. Cụ b&aacute; kh&ocirc;ng c&oacute; nh&agrave;. Thấy điệu bộ hung hăng của hắn, b&agrave; cả đ&ugrave;n b&agrave; hai, b&agrave; hai th&uacute;c b&agrave; ba, b&agrave; ba gọi b&agrave; tư, nhưng kết cục chẳng b&agrave; n&agrave;o d&aacute;m ra n&oacute;i với hắn một v&agrave;i lời phải chăng. Mắc c&aacute;i phải c&aacute;i thằng liều lĩnh qu&aacute;, n&oacute; lại say rượu, tay n&oacute; lại lăm lăm cầm một c&aacute;i vỏ chai, m&agrave; nh&agrave; l&uacute;c ấy to&agrave;n đ&agrave;n b&agrave; cả... Th&ocirc;i th&igrave; cứ đ&oacute;ng c&aacute;i cổng cho thật chặt, rồi mặc th&acirc;y cha n&oacute;, n&oacute; chửi th&igrave; tai liền miệng đấy, chửi th&igrave; lại nghe! Th&agrave;nh thử chỉ c&oacute; ba con ch&oacute; dữ với một thằng say rượu!... Thật l&agrave; ầm ỹ! H&agrave;ng x&oacute;m phải một bữa điếc tai, nhưng c&oacute; lẽ trong bụng th&igrave; họ hả: xưa nay họ mới chỉ được nghe b&agrave; cả, b&agrave; hai, b&agrave; ba, b&agrave; tư nh&agrave; cụ b&aacute; chửi người ta, b&acirc;y giờ họ mới thấy người ta chửi lại cả nh&agrave; cụ b&aacute;. M&agrave; chửi mới sướng miệng l&agrave;m sao! Mới ngoa ngắt l&agrave;m sao. Họ bảo nhau: Phen n&agrave;y cha con thằng b&aacute; Kiến đố c&ograve;n d&aacute;m v&aacute;c mặt đi đ&acirc;u nữa! Mồ mả tổ ti&ecirc;n đến lộn l&ecirc;n mất. Cũng c&oacute; người hiền l&agrave;nh hơn bảo: &ldquo;Ph&uacute;c đời nh&agrave; n&oacute;, chắc &ocirc;ng l&yacute; kh&ocirc;ng c&oacute; nh&agrave;...&rdquo;. &Ocirc;ng l&yacute; đ&acirc;y l&agrave; &ocirc;ng l&yacute; Cường, con giai cụ B&aacute; nổi tiếng l&agrave; h&aacute;ch dịch, coi người như rơm r&aacute;c. Phải &ocirc;ng l&yacute; Cường thử c&oacute; nh&agrave; xem n&agrave;o! Quả nhi&ecirc;n họ n&oacute;i c&oacute; sai đ&acirc;u! Đấy, c&oacute; tiếng người sang sảng qu&aacute;t: &ldquo;M&agrave;y muốn l&ocirc;i th&ocirc;i g&igrave;?... C&aacute;i thằng kh&ocirc;ng cha kh&ocirc;ng mẹ n&agrave;y! M&agrave;y muốn l&ocirc;i th&ocirc;i g&igrave;?...&rdquo; Đ&atilde; bảo m&agrave;! C&aacute;i tiếng qu&aacute;t th&aacute;o kia l&agrave; tiếng l&yacute; Cường. L&yacute; Cường đ&atilde; về! L&yacute; Cường đ&atilde; về! Phải biết... A ha! Một c&aacute;i t&aacute;t rất k&ecirc;u. &Ocirc;i! C&aacute;i g&igrave; thế n&agrave;y? Tiếng đấm tiếng đ&aacute; nhau b&igrave;nh bịch. Th&ocirc;i, cứ gọi l&agrave; tan xương! Bỗng &ldquo;choang&rdquo; một c&aacute;i, th&ocirc;i phải rồi, hẳn đập c&aacute;i chai v&agrave;o cột cổng... ồ hắn k&ecirc;u! Hắn vừa chửi vừa k&ecirc;u l&agrave;ng như bị người ta cắt họng. Ồ hắn k&ecirc;u!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Ối l&agrave;ng nước ơi! Cứu t&ocirc;i với... Ối l&agrave;ng nước &ocirc;i! Bố con thằng b&aacute; Kiến n&oacute; đ&acirc;m chết t&ocirc;i! Thằng l&yacute; Cường n&oacute; đ&acirc;m chết t&ocirc;i rồi, l&agrave;ng nước &ocirc;i!...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">V&agrave; họ thấy Ch&iacute; Ph&egrave;o lăn lộn dưới đất, vừa k&ecirc;u vừa lấy mảnh chai c&agrave;o v&agrave;o mặt. M&aacute;u ra loe lo&eacute;t tr&ocirc;ng gớm qu&aacute;! Mấy con ch&oacute; x&ocirc;ng v&agrave;o quanh hắn, sủa rất hăng. L&yacute; Cường hơi t&aacute;i mặt, đứng nh&igrave;n m&agrave; cười nhạt, cười khinh bỉ. Hừ! Ngỡ l&agrave; g&igrave;, chẳng ho&aacute; ra nằm ăn vạ! Th&igrave; ra hắn định đến đ&acirc;y nằm vạ!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Người ta tu&ocirc;n đến xem. Mấy c&aacute;i ng&otilde; tối xung quanh đ&ugrave;n ra biết bao nhi&ecirc;u l&agrave; người! Thật ồn &agrave;o như chợ. B&agrave; cả, b&agrave; hai, b&agrave; ba, b&agrave; tư nh&agrave; cụ b&aacute; cũng vững dạ v&igrave; c&oacute; anh l&yacute;, cũng xưng xỉa ra chửi g&oacute;p. Thật ra, c&aacute;c b&agrave; muốn xem Ch&iacute; Ph&egrave;o ra l&agrave;m sao? Kh&ocirc;ng kh&eacute;o n&oacute; c&oacute; &yacute; gieo vạ cho cụ &ocirc;ng phen n&agrave;y...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Nhưng k&igrave;a cụ &ocirc;ng đ&atilde; về. Cụ cất tiếng rất sang hỏi: &ldquo;C&aacute;i g&igrave; m&agrave; đ&ocirc;ng như thế n&agrave;y?&rdquo;. Chỗ n&agrave;y &ldquo;lạy cụ&rdquo;, chỗ kia &ldquo;lạy cụ&rdquo;, người ta k&iacute;nh cẩn gi&atilde;n ra, v&agrave; Ch&iacute; Ph&egrave;o bỗng nằm d&agrave;i, kh&ocirc;ng nh&uacute;c nh&iacute;ch r&ecirc;n khe khẽ như gần chết.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Tho&aacute;ng nh&igrave;n qua, đ&atilde; hiểu cơ sự rồi. L&agrave;m l&yacute; trưởng rồi ch&aacute;nh tổng, b&acirc;y giờ lại đến lượt con cụ l&agrave;m l&yacute; trưởng, những việc như thế n&agrave;y cụ kh&ocirc;ng lạ g&igrave;. Cụ h&atilde;y qu&aacute;t mấy b&agrave; vợ đang xưng xỉa chực t&acirc;ng c&ocirc;ng với chồng:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- C&aacute;c b&agrave; đi v&agrave;o nh&agrave;: đ&agrave;n b&agrave; chỉ l&ocirc;i th&ocirc;i, biết g&igrave;?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Rồi quay lại bọn người l&agrave;ng, cụ dịu giọng hơn một ch&uacute;t:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Cả c&aacute;c &ocirc;ng, c&aacute;c b&agrave; nữa, về đi th&ocirc;i chứ! C&oacute; g&igrave; m&agrave; x&uacute;m lại như thế n&agrave;y?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Kh&ocirc;ng ai n&oacute;i g&igrave;, người ta dần dần tản đi. V&igrave; nể cụ b&aacute; cũng c&oacute;, nhưng v&igrave; nghĩ đến sự y&ecirc;n ổn của m&igrave;nh cũng c&oacute;: người nh&agrave; qu&ecirc; vốn gh&eacute;t l&ocirc;i th&ocirc;i. Ai dại g&igrave; đứng ỳ ra đấy, c&oacute; l&agrave;m sao họ triệu m&igrave;nh đi l&agrave;m chứng. Sau c&ograve;n trơ lại Ch&iacute; Ph&egrave;o v&agrave; cha con cụ b&aacute;. B&acirc;y giờ cụ mới lại gần hắn, khẽ lay v&agrave; gọi:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Anh Ch&iacute; ơi! Sao anh lại l&agrave;m ra thế ?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Ch&iacute; Ph&egrave;o lim dim mắt, r&ecirc;n l&ecirc;n:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Tao chỉ liều chết với bố con nh&agrave; m&agrave;y đấy th&ocirc;i. Nhưng tao m&agrave; chết th&igrave; c&oacute; thằng sạt nghiệp, m&agrave; c&ograve;n rũ t&ugrave; chưa biết chừng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Cụ b&aacute; cười nhạt, nhưng tiếng cười gi&ograve;n gi&atilde; lắm: người ta bảo cụ hơn người cũng chỉ bởi c&aacute;i cười.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- C&aacute;i anh n&agrave;y n&oacute;i mới hay! Ai l&agrave;m g&igrave; m&agrave; anh phải chết? Đời người chứ c&oacute; phải con ngo&eacute; đ&acirc;u? Lại say rồi phải kh&ocirc;ng?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Rồi đổi giọng, cụ th&acirc;n mật hỏi:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Về bao giờ thế? Sao kh&ocirc;ng v&agrave;o t&ocirc;i chơi? Đi v&agrave;o nh&agrave; uống nước.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thấy Ch&iacute; Ph&egrave;o kh&ocirc;ng nh&uacute;c nh&iacute;ch, cụ tiếp lu&ocirc;n:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- N&agrave;o đứng l&ecirc;n đi. Cứ v&agrave;o đ&acirc;y uống nước đ&atilde;. C&oacute; c&aacute;i g&igrave; ta n&oacute;i chuyện tử tế với nhau, cần g&igrave; m&agrave; phải l&agrave;m thanh động l&ecirc;n như thế, người ngo&agrave;i biết, mang tiếng cả.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Rồi vừa xốc Ch&iacute; Ph&egrave;o, cụ vừa ph&agrave;n n&agrave;n:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Khổ qu&aacute;! Gi&aacute; c&oacute; t&ocirc;i ở nh&agrave; th&igrave; đ&acirc;u đến nỗi. Ta n&oacute;i chuyện với nhau, thế n&agrave;o cũng xong. Người lớn cả, chỉ một c&acirc;u chuyện với nhau l&agrave; đủ. Chỉ tại thằng l&yacute; Cường n&oacute;ng t&iacute;nh kh&ocirc;ng biết nghĩ trước nghĩ sau. Ai, chứ anh với n&oacute; c&ograve;n c&oacute; họ kia đấy.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Ch&iacute; Ph&egrave;o chẳng biết họ h&agrave;ng ra l&agrave;m sao, nhưng cũng thấy l&ograve;ng ngu&ocirc;i ngu&ocirc;i. Hắn cố l&agrave;m ra vẻ nặng nề, ngồi l&ecirc;n. Cụ b&aacute; biết rằng m&igrave;nh đ&atilde; thắng, đưa mắt nh&aacute;y con một c&aacute;i, cụ qu&aacute;t:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- L&yacute; Cường đ&acirc;u! Tội m&agrave;y đ&aacute;ng chết. Kh&ocirc;ng bảo người ta đun nước, mau l&ecirc;n!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Cụ dắt Ch&iacute; Ph&egrave;o đứng dậy, giục th&ecirc;m v&agrave;i tiếng nữa, v&agrave; Ch&iacute; Ph&egrave;o chịu đi: hắn chỉ cố khập khiễng c&aacute;i ch&acirc;n như bị qu&egrave;. L&agrave; v&igrave; l&uacute;c ấy trong người hắn rượu đ&atilde; hơi nhạt rồi, kh&ocirc;ng c&ograve;n k&ecirc;u g&agrave;o chửi bới, hắn thấy h&igrave;nh như kh&ocirc;ng c&ograve;n hăng h&aacute;i nữa. Sự ngọt ng&agrave;o l&agrave;m mềm nhũn, vả lại những người đứng xem về cả rồi, hắn thấy h&igrave;nh như trơ trọi. C&aacute;i sợ cố hữu trong l&ograve;ng thức dậy, c&aacute;i sợ xa x&ocirc;i thuở ng&agrave;y xưa, hắn thấy quả l&agrave; t&aacute;o bạo. Kh&ocirc;ng t&aacute;o bạo m&agrave; d&aacute;m g&acirc;y sự với cha con b&aacute; Kiến, bốn đời l&agrave;m tổng l&yacute;. V&agrave; nghĩ thế, hắn thấy hắn cũng oai. Hắn l&agrave;m c&aacute;i &ocirc;ng g&igrave; ở l&agrave;ng n&agrave;y? Kh&ocirc;ng v&acirc;y c&aacute;nh, kh&ocirc;ng họ h&agrave;ng th&acirc;n th&iacute;ch; anh em kh&ocirc;ng c&oacute;, đến bố mẹ cũng kh&ocirc;ng... Ờ thế m&agrave; cũng d&aacute;m độc lực chọi nhau với l&yacute; trưởng, ch&aacute;nh tổng, b&aacute; hộ ti&ecirc;n chỉ l&agrave;ng Vũ Đại, Ch&aacute;nh Hội đồng kỳ h&agrave;o, huyện h&agrave;o, Bắc kỳ nh&acirc;n d&acirc;n đại biểu, kh&eacute;t tiếng đến cả trong h&agrave;ng huyện. Thử hỏi đ&atilde; c&oacute; mặt n&agrave;o trong c&aacute;i l&agrave;ng hơn hai ngh&igrave;n suất đinh n&agrave;y l&agrave;m được thế? Kể l&agrave;m rồi c&oacute; chết cũng l&agrave; cam t&acirc;m. Vậy m&agrave; kh&ocirc;ng: c&aacute;i cụ b&aacute; th&eacute;t ra lửa ấy lại xử nhũn, mời hắn v&agrave;o nh&agrave; xơi nước. Th&ocirc;i cũng hả, đ&atilde; xử nhũn th&igrave; hắn v&agrave;o. Nhưng bỗng hắn lại hơi ngần ngại; biết đ&acirc;u c&aacute;i l&atilde;o c&aacute;o gi&agrave; n&agrave;y n&oacute; lại chả lừa hắn v&agrave;o nh&agrave; rồi l&ocirc;i th&ocirc;i? Ồ m&agrave; thật c&oacute; thể như thế lắm! N&agrave;y n&oacute; h&atilde;y l&ocirc;i ngay mấy c&aacute;i m&acirc;m c&aacute;i nồi hay đồ v&agrave;ng đồ bạc ra kho&aacute;c v&agrave;o cổ hắn, rồi cho vợ ra k&ecirc;u l&agrave;ng l&ecirc;n, rồi cột cổ hắn v&agrave;o, chần cho một trận om xương, rồi vu cho l&agrave; ăn cướp th&igrave; sao? C&aacute;i thằng b&aacute; Kiến n&agrave;y, gi&agrave; đời đục kho&eacute;t, c&ograve;n đớn c&aacute;i nước g&igrave; m&agrave; chịu l&eacute;p như trấu thế? Th&ocirc;i dại g&igrave; m&agrave; v&agrave;o miệng cọp, hắn cứ đứng đ&acirc;y n&agrave;y, cứ lăn ra đ&acirc;y n&agrave;y, lại k&ecirc;u to&aacute;ng l&ecirc;n xem n&agrave;o. Nhưng nghĩ ngợi một t&iacute;, hắn c&oacute; lăn ra k&ecirc;u nữa, liệu c&oacute; c&ograve;n ai ra? Vả lại b&acirc;y giờ rượu nhạt rồi, nếu lại phải rạch mặt mấy nh&aacute;t nữa th&igrave; cũng đau. Th&ocirc;i cứ v&agrave;o! V&agrave;o th&igrave; v&agrave;o, cần qu&aacute;i g&igrave;. Muốn đập đầu th&igrave; v&agrave;o ngay nh&agrave; n&oacute; m&agrave; đập đầu c&ograve;n hơn ở ngo&agrave;i. C&ugrave;ng lắm, n&oacute; c&oacute; giở quẻ, hắn cũng chỉ đến đi ở t&ugrave;. Ở t&ugrave; th&igrave; hắn coi l&agrave; thường. Th&ocirc;i cứ v&agrave;o.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">V&agrave;o rồi, hắn mới biết những c&aacute;i hắn sợ l&agrave; h&atilde;o cả. B&aacute; Kiến quả c&oacute; &yacute; muốn d&agrave;n xếp c&ugrave;ng hắn thật. Kh&ocirc;ng phải cụ đớn, ch&iacute;nh thật cụ kh&ocirc;n r&oacute;c đời, thứ nhất sợ kẻ anh h&ugrave;ng, thứ hai sợ kẻ cố c&ugrave;ng liều th&acirc;n. Ch&iacute; Ph&egrave;o kh&ocirc;ng l&agrave; anh h&ugrave;ng, nhưng n&oacute; l&agrave; c&aacute;i thằng liều lĩnh. Liều lĩnh th&igrave; ai th&egrave;m chấp! Thế n&agrave;o l&agrave; mềm nắn rắn bu&ocirc;ng? C&aacute;i nghề l&agrave;m việc quan, nếu nhất nhất c&aacute;i g&igrave; cũng đ&egrave; đầu ấn cổ th&igrave; lại b&aacute;n nh&agrave; đi cho sớm. Cụ vẫn bảo l&yacute; Cường như thế đấy. Vũ dũng như hắn m&agrave; l&agrave;m l&yacute; trưởng l&agrave; nhờ c&oacute; cụ. Cụ m&agrave; chết đi rồi, &ldquo;ch&uacute;ng n&oacute;&rdquo; lại kh&ocirc;ng cho ăn b&ugrave;n.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Tiếng vậy, l&agrave;m tổng l&yacute; kh&ocirc;ng phải việc dễ. Ở c&aacute;i l&agrave;ng n&agrave;y, d&acirc;n qu&aacute; hai ngh&igrave;n, xa phủ xa tỉnh, kể ăn th&igrave; cũng dễ ăn nhưng kh&ocirc;ng phải hễ m&agrave; l&agrave;m l&yacute; trưởng th&igrave; cứ việc ngồi m&agrave; kho&eacute;t. Hồi năm nọ, một thầy địa l&yacute; qua đ&acirc;y c&oacute; bảo đất l&agrave;ng n&agrave;y v&agrave;o c&aacute;i thế &ldquo;quần ngư tranh thực&rdquo;, v&igrave; thế m&agrave; bọn đ&agrave;n anh chỉ l&agrave; một đ&agrave;n c&aacute; tranh mồi. Mồi th&igrave; ngon đấy, nhưng m&agrave; năm b&egrave; bảy mối, b&egrave; n&agrave;o cũng muốn ăn. Ngo&agrave;i mặt tử tế với nhau, nhưng thật ra trong bụng l&uacute;c n&agrave;o cũng muốn cho nhau lụn bại để cưỡi l&ecirc;n đầu l&ecirc;n cổ. Ngay thằng Ch&iacute; Ph&egrave;o n&agrave;y đến đ&acirc;y sinh sự biết đ&acirc;u lại kh&ocirc;ng c&oacute; thằng n&agrave;o ẩy đến? Nếu cụ kh&ocirc;ng chịu nhịn, l&agrave;m cho to chuyện c&oacute; khi tốn tiền. C&aacute;i nghề quan b&aacute;m thằng c&oacute; t&oacute;c ai b&aacute;m thằng trọc đầu? Bỏ t&ugrave; n&oacute; th&igrave; dễ rồi; nhưng bỏ t&ugrave; n&oacute; cũng c&oacute; ng&agrave;y n&oacute; được ra, liệu l&uacute;c ấy n&oacute; c&oacute; để m&igrave;nh y&ecirc;n kh&ocirc;ng chứ? Cụ phải c&aacute;i vụ thằng năm Thọ, m&atilde;i đến giờ vẫn chưa qu&ecirc;n.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Năm Thọ vốn l&agrave; một thằng đầu b&ograve; đầu bướu. Hồi ấy, b&aacute; Kiến mới ra l&agrave;m l&yacute; trưởng, n&oacute; h&igrave;nh như k&igrave;nh nhau với l&atilde;o ra mặt; l&yacute; Kiến muốn trị nhưng chưa c&oacute; dịp. Được &iacute;t l&acirc;u, hắn can dự v&agrave;o một vụ cướp bị bắt giam; l&yacute; Kiến ngấm ngầm vận động cho v&agrave;o t&ugrave;. Vẫn tưởng một người vai vế như năm Thọ m&agrave; thất cơ lỡ vận đến nỗi tội t&ugrave; l&agrave;m g&igrave; c&ograve;n d&aacute;m v&aacute;c mặt mo về l&agrave;ng? L&yacute; Kiến mừng thầm rằng đ&atilde; nhổ được c&acirc;y đinh trước mắt. N&agrave;o ngờ một buổi tối l&yacute; Kiến đang ngồi một m&igrave;nh soạn giấy m&aacute; th&igrave; năm Thọ v&aacute;c dao xộc v&agrave;o. N&oacute; đứng chặn lấy cửa v&agrave; bảo: nếu k&ecirc;u l&ecirc;n một tiếng th&igrave; đ&acirc;m chết liền. Th&igrave; ra n&oacute; vượt ngục v&agrave; về đ&acirc;y nhờ &ocirc;ng l&yacute; một c&aacute;i thẻ mang t&ecirc;n một người lương thiện v&agrave; một trăm đồng bạc để trốn đi. N&oacute; lại bảo: nghe n&oacute; th&igrave; n&oacute; đi biệt, m&agrave; kh&ocirc;ng nghe th&igrave; n&oacute; đ&acirc;m chết, rồi muốn ra sao th&igrave; ra; c&ograve;n muốn sống với vợ con th&igrave; nghe n&oacute;.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Lẽ tất nhi&ecirc;n l&agrave; l&yacute; Kiến nghe, năm Thọ đi phen ấy l&agrave; mất tăm, cũng kh&ocirc;ng bao giờ về nữa thật. Nhưng th&oacute;i đời, tre gi&agrave; măng mọc, c&oacute; bao giờ hết những thằng du c&ocirc;n? Năm Thọ vừa đi, lại c&oacute; binh Chức ở đ&acirc;u lần về. M&agrave; thằng n&agrave;y l&uacute;c n&oacute; c&ograve;n ở nh&agrave;, n&agrave;o n&oacute; c&oacute; ngạo ngược g&igrave; cho cam! Người ta đ&atilde; phải gọi hắn l&agrave; cục đất. Ai bảo l&agrave;m sao th&igrave; ư hữ l&agrave;m vậy, mới qu&aacute;t một tiếng th&igrave; đ&atilde; đ&aacute;i cả ra quần, thuế bổ một đồng th&igrave; đ&oacute;ng qu&aacute; hai, đến nỗi c&oacute; con vợ hay hay mắt, bị người ta ghẹo cũng chỉ im im rồi về nh&agrave; h&agrave;nh vợ chứ chẳng d&aacute;m ho he g&igrave;, thế đấy: c&aacute;i nghề đời hiền qu&aacute; cũng ho&aacute; ngu, ở đ&acirc;u chứ ở đất n&agrave;y đ&atilde; ngu, đ&atilde; nhịn th&igrave; ch&uacute;ng n&oacute; ấn cho đến kh&ocirc;ng c&ograve;n ng&oacute;c đầu l&ecirc;n được. Hắn l&agrave;m th&igrave; l&agrave;m cật lực m&agrave; quanh năm vẫn ngh&egrave;o rớt mồng tơi; chỉ v&igrave; một miếng cũng kh&ocirc;ng giữ được m&agrave; ăn; đứa n&agrave;o n&oacute; vớ n&oacute; cũng xoay, m&agrave; đứa n&agrave;o xoay th&igrave; cũng chịu. Sau c&ugrave;ng bực qu&aacute;, hắn ra đi l&iacute;nh. Lại c&agrave;ng th&ecirc;m tội! Kh&ocirc;ng bực c&ograve;n được vợ, tuy rằng thỉnh thoảng c&oacute; bị sấy sớt ra ngo&agrave;i, những vẫn l&agrave; vợ m&igrave;nh. Bực th&igrave; ho&aacute; ra mất vợ. Bởi v&igrave; chị vợ ở nh&agrave; c&ograve;n trẻ, mới hai con, c&aacute;i mắt sắc như dao lại hồng hồng đ&ocirc;i m&aacute;, bỗng nhi&ecirc;n sinh ra vắng chồng, của ngon trờ trờ ngay trước mắt, ai m&agrave; chịu được?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Nh&agrave; chị Binh ở gần đường. &Ocirc;ng ph&oacute; đi đ&aacute;nh bạc ban đ&ecirc;m về cũng tạt v&agrave;o; anh trương tuần đi tuần cũng tạt v&agrave;o; anh h&agrave;ng x&oacute;m cũng m&ograve; sang, thậm ch&iacute; đến c&aacute;i thằng hương Điền, đầu hai thứ t&oacute;c, gi&agrave; đời l&agrave;m đầy tớ cho c&aacute;c &ocirc;ng l&yacute; trưởng, cũng mon men v&agrave;o gạ gẫm. Vợ binh Chức đ&atilde; nghiễm nhi&ecirc;n th&agrave;nh một con nh&agrave; thổ kh&ocirc;ng phải trả tiền để bọn l&yacute; dịch trong l&agrave;ng chuy&ecirc;n đổi. Ch&iacute;nh ngay l&yacute; Kiến, tuy hồi ấy đ&atilde; c&oacute; đến ba vợ, cũng kh&ocirc;ng nỡ bỏ ho&agrave;i c&aacute;i của kh&ocirc;ng dưng được trời cho; v&agrave; kh&ocirc;ng bỏ ho&agrave;i, &ocirc;ng c&ograve;n được lợi. Mỗi lần chị Binh đi lĩnh lương hay lĩnh măng đa của chồng, phải mượn &ocirc;ng l&yacute; đi nhận thực. Kh&ocirc;ng &ocirc;ng l&yacute; n&agrave;o v&aacute;c của nh&agrave; đi ăn m&agrave; nhận thực cho người ta, điều ấy l&agrave; cố nhi&ecirc;n. Nhưng với l&yacute; Kiến th&igrave; kh&ocirc;ng những cơm rượu tiền t&uacute;i, c&ograve;n phải cho ngồi xe chung v&agrave; c&ograve;n ở lại tỉnh nữa.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thế l&agrave; mấy đồng bạc lương đi đời; mấy đứa con của chị ng&agrave;y mai chỉ được mấy c&aacute;i kẹo đạn m&uacute;t, hay hậu hĩnh ra th&igrave; được mấy cặp b&aacute;nh giầy gi&ograve; ăn. Th&agrave;nh thử c&ocirc;ng lao anh Binh, r&uacute;t lại chỉ cho chị Binh mỗi th&aacute;ng một lần hưởng những cuộc vui với &ocirc;ng l&yacute; nh&agrave;.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Chẳng hiểu anh ta cũng biết thế m&agrave; ch&aacute;n cảnh nh&agrave; hay sao m&agrave; m&atilde;n hạn ba năm cũng kh&ocirc;ng thấy trở về. Rồi &iacute;t l&acirc;u sau, c&oacute; tr&aacute;t về l&agrave;ng tr&oacute;c n&atilde; v&agrave; &aacute;p giải t&ecirc;n Trần Văn Chức. L&yacute; Kiến khai t&ecirc;n ấy thuộc hạng d&acirc;n lưu t&aacute;n kh&ocirc;ng về l&agrave;ng. Nhưng khai h&ocirc;m trước th&igrave; h&ocirc;m sau hắn về. L&yacute; Kiến sai đầy tớ đem tr&aacute;t đến nh&agrave; đ&ograve;i hắn. Hắn đến ngay, nhưng lại dẫn theo cả vợ v&agrave; hai con. Kh&ocirc;ng đợi &ocirc;ng l&yacute; n&oacute;i một c&acirc;u, hắn r&uacute;t con dao chọc tiết lợn ra, nhăm nhăm cầm ở tay m&agrave; bảo rằng: &ldquo;Chẳng n&oacute;i giấu g&igrave; &ocirc;ng, t&ocirc;i can &aacute;n giết người. Nếu &ocirc;ng kh&ocirc;ng thương, m&agrave; bắt giải th&igrave; vợ con t&ocirc;i chết đ&oacute;i. Th&ocirc;i th&igrave; đằng n&agrave;o ch&uacute;ng n&oacute; cũng chết, t&ocirc;i đ&acirc;m ch&uacute;ng n&oacute; chết ở đ&acirc;y rồi &ocirc;ng bắt đi ở t&ugrave; lu&ocirc;n thể&rdquo;. Mắt hắn đỏ ngầu; lưỡi dao hắn hoa l&ecirc;n loang lo&aacute;ng, chỉ tr&ocirc;ng cũng lạnh g&aacute;y. Hắn c&oacute; thể giết người được lắm, m&agrave; kh&ocirc;ng chỉ giết c&oacute; vợ v&agrave; con th&ocirc;i, khi hắn c&oacute; gan đ&acirc;m chết vợ con th&igrave; hắn c&oacute; ki&ecirc;ng g&igrave; c&aacute;i cổ của người kh&aacute;c nữa? L&yacute; Kiến nghĩ ngợi một l&uacute;c rồi bảo cứ về rồi &ocirc;ng liệu. &Ocirc;ng liệu nghĩa l&agrave; &ocirc;ng che đậy c&aacute;i &aacute;n của hắn cho kh&ocirc;ng ai biết v&agrave; mỗi lần c&oacute; tr&aacute;t về nhắc, &ocirc;ng lại khai rằng: vẫn chưa c&oacute; t&ecirc;n Chức về. Thế l&agrave; hắn nghiễm nhi&ecirc;n sống ở ngay ch&iacute;nh giữa qu&ecirc; hương hắn. V&agrave; b&acirc;y giờ người ta thấy vợ hắn rất ch&iacute;nh chuy&ecirc;n m&agrave; lại trung th&agrave;nh, chị chăm chỉ l&agrave;m ăn để nu&ocirc;i hắn. Những &ocirc;ng trưởng, &ocirc;ng ph&oacute; tự nhi&ecirc;n nghĩ bụng rằng: người ta c&oacute; chồng rồi m&agrave; c&ograve;n ch&agrave;ng m&agrave;ng th&igrave; phải tội; ai cũng sinh ra tử tế cả chỉ trừ anh Binh, bởi v&igrave; Chức b&acirc;y giờ lại rất mực ngang ngược. Hắn ăn vườn đấy, nhưng chẳng nộp thuế cho ai. Th&uacute;c hắn th&igrave; hắn chửi, cắm vườn hắn th&igrave; hắn ch&eacute;m, sinh chuyện với hắn th&igrave; ch&iacute;nh l&yacute; trưởng l&agrave;ng c&oacute; lỗi bởi v&igrave; cố &yacute; ẩn lậu hắn l&agrave; một t&ecirc;n can phạm. Ấy thế m&agrave; hắn cũng chưa vừa l&ograve;ng đ&acirc;u. Một h&ocirc;m, kh&ocirc;ng hiểu nghĩ ngợi thế n&agrave;o, hắn v&aacute;c dao đến bảo thẳng v&agrave;o mặt l&yacute; Kiến rằng:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Hồi t&ocirc;i c&ograve;n tại ngũ, t&ocirc;i gửi về nh&agrave; c&oacute; trăm. Kh&ocirc;ng biết vợ t&ocirc;i n&oacute; ti&ecirc;u pha g&igrave; hay cho trai m&agrave; kh&ocirc;ng c&ograve;n một đồng n&agrave;o cả. T&ocirc;i hỏi n&oacute; th&igrave; n&oacute; bảo: ở nh&agrave; đ&agrave;n b&agrave; con g&aacute;i một m&igrave;nh, kh&ocirc;ng d&aacute;m giữ tiền, được đồng n&agrave;o mang gửi &ocirc;ng l&yacute; cả. T&ocirc;i sợ n&oacute; bịa ra n&ecirc;n đ&atilde; tr&oacute;i sẵn n&oacute; ở nh&agrave;. B&acirc;y giờ t&ocirc;i đến thưa với &ocirc;ng, t&iacute;nh to&aacute;n xem được bao nhi&ecirc;u cho đem về nu&ocirc;i ch&aacute;u. Thiếu một đồng th&igrave; t&ocirc;i kh&ocirc;ng để y&ecirc;n cho ch&uacute;ng n&oacute;.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">L&yacute; Kiến hiểu rằng: &ldquo;ch&uacute;ng n&oacute;&rdquo; đ&acirc;y c&oacute; thể gồm cả &ocirc;ng. &Ocirc;ng cười nhạt bảo rằng:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Thế n&agrave;y n&agrave;y, anh Binh ạ: chị ấy gửi t&ocirc;i th&igrave; quả l&agrave; kh&ocirc;ng c&oacute;...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn trợn mắt l&ecirc;n qu&aacute;t:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Thế th&igrave; thằng n&agrave;o ăn đi?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">L&yacute; Kiến vội n&oacute;i lấp ngay:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Thế nhưng m&agrave; anh c&oacute; thiếu tiền th&igrave; cứ bảo t&ocirc;i một tiếng. Chị ấy tr&oacute;t ti&ecirc;u đi rồi th&igrave; c&oacute; giết cũng chẳng ra. L&ocirc;i th&ocirc;i l&agrave;m g&igrave; sinh tội.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">&Ocirc;ng mở tr&aacute;p ra quăng hắn 5 đồng bạc. Hắn cầm lấy, &ldquo;lạy &ocirc;ng&rdquo; tử tế, rồi x&aacute;ch dao ra về. Từ h&ocirc;m ấy hắn th&agrave;nh tử tế với l&yacute; Kiến, nhận l&agrave; chỗ đầy tớ ch&acirc;n tay, nhưng l&yacute; Kiến thỉnh thoảng vẫn phải cho hắn tiền. Cho m&atilde;i đến năm ngo&aacute;i đ&acirc;y, hắn chết...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Th&igrave; năm nay lại nảy ra Ch&iacute; Ph&egrave;o, một thằng hiền l&agrave;nh như đất - tội nghiệp cho hắn, c&oacute; lần l&yacute; Kiến thấy hắn vừa b&oacute;p đ&ugrave;i cho b&agrave; ba, vừa run run! Bỗng nhi&ecirc;n v&ugrave;ng dậy giở to&agrave;n những giọng uống m&aacute;u người kh&ocirc;ng tanh. Th&igrave; ra gi&agrave; n&eacute;o đứt d&acirc;y. Cụ ti&ecirc;n chỉ l&agrave;ng Vũ &ETH;ại nhận ra rằng: đ&egrave; n&eacute;n con em đến nỗi n&oacute; kh&ocirc;ng chịu được phải bỏ l&agrave;ng đi l&agrave; dại. Mười thằng đ&atilde; đi ra th&igrave; ch&iacute;n thằng trở về với c&aacute;i vẻ hung đồ, c&aacute;i t&iacute;nh ương ngạnh học được từ phương xa. Một người kh&ocirc;n ngoan chỉ b&oacute;p đến nửa chừng. H&atilde;y ngấm ngầm đẩy người ta xuống s&ocirc;ng, nhưng rồi dắt n&oacute; l&ecirc;n để n&oacute; đền ơn. H&atilde;y đập b&agrave;n đập ghế đ&ograve;i cho được năm đồng, nhưng được rồi th&igrave; lại vất trả lại năm h&agrave;o &ldquo;v&igrave; thương anh t&uacute;ng qu&aacute;&rdquo;! V&agrave; cũng phải t&ugrave;y mặt nữa: những thằng c&oacute; m&aacute;u mặt, vợ đẹp, con đ&agrave;n ch&iacute;nh l&agrave; những thằng dễ b&oacute;p; tr&aacute;i lại những thằng tứ cố v&ocirc; th&acirc;n, giết ch&uacute;ng th&igrave; dễ, nhưng giết được, chỉ c&ograve;n c&oacute; xương; m&agrave; g&acirc;y với ch&uacute;ng l&agrave; mở một dịp tốt để cho c&aacute;c phe nghịch xoay lại m&igrave;nh. L&agrave;ng n&agrave;o cũng c&oacute; nhiều c&aacute;nh, mỗi c&aacute;nh kết b&egrave; kết đảng chung quanh một người: c&aacute;nh cụ b&aacute; Kiến, c&aacute;nh &ocirc;ng đội Tảo, c&aacute;nh &ocirc;ng Tư &ETH;ạm, c&aacute;nh &ocirc;ng B&aacute;t T&ugrave;ng... Bằng ấy c&aacute;nh du lại với nhau để b&oacute;c lột con em, nhưng ngấm ngầm chia rẽ, nh&egrave; từng chỗ hở để m&agrave; trị nhau. Cụ lại nhận ra rằng: ở c&aacute;i đất nh&agrave; qu&ecirc;, bọn d&acirc;n hiền l&agrave;nh chỉ &egrave; cổ l&agrave;m nu&ocirc;i bọn h&agrave;o l&yacute;, nhưng ch&iacute;nh bọn h&agrave;o l&yacute;, nhiều khi lại phải ngậm miệng cung cấp cho những thằng c&ugrave;ng hơn cả d&acirc;n c&ugrave;ng liều lĩnh, l&uacute;c n&agrave;o cũng c&oacute; thể cầm dao đ&acirc;m người hay đ&acirc;m m&igrave;nh.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Nhưng cụ kh&ocirc;ng phải l&agrave; một người ưa than thở. Than thở chẳng &iacute;ch g&igrave; cho ai, c&aacute;i bọn d&acirc;n đinh suốt đời bị đ&egrave; n&eacute;n sở dĩ bị đ&egrave; n&eacute;n suốt đời chỉ v&igrave; khi bị đ&egrave; n&eacute;n ch&uacute;ng chỉ biết than thở chứ kh&ocirc;ng biết l&agrave;m g&igrave; kh&aacute;c. Cụ b&aacute; Kiến kh&ocirc;ng cần than thở: trị kh&ocirc;ng lợi th&igrave; cụ d&ugrave;ng. Cụ nghĩ bụng cũng phải c&oacute; những thằng đầu b&ograve; chứ? Kh&ocirc;ng c&oacute; những thằng đầu b&ograve; th&igrave; lấy ai m&agrave; trị những thằng đầu b&ograve;? Thế lực của cụ sở dĩ lấn &aacute;t được những v&acirc;y c&aacute;nh kh&aacute;c, một phần lớn cũng bởi cụ biết mềm biết cứng, biết thu dụng những thằng bạt mạng kh&ocirc;ng sợ chết v&agrave; kh&ocirc;ng sợ đi t&ugrave;. Những thằng ấy ch&iacute;nh l&agrave; những thằng được việc. Khi cần đến, chỉ cho n&oacute; dăm h&agrave;o uống rượu, l&agrave; c&oacute; thể sai n&oacute; t&aacute;c hại bất cứ anh n&agrave;o kh&ocirc;ng nghe m&igrave;nh. Gặp người bướng bỉnh, đanh th&eacute;p th&igrave; n&oacute; lừa đốt nh&agrave; hay cho mấy l&aacute;t dao; gặp người non mặt, th&igrave; n&oacute; quăng v&agrave;o chai rượu lậu, hay g&acirc;y sự rồi lăn ra k&ecirc;u l&agrave;ng. C&oacute; ch&uacute;ng n&oacute; sinh chuyện th&igrave; mới c&oacute; dịp m&agrave; ăn, nếu kh&ocirc;ng th&igrave; giữa đ&aacute;m d&acirc;n hiền l&agrave;nh v&agrave; y&ecirc;n phận n&agrave;y, kh&eacute;o lắm chỉ b&oacute;p nặn được v&agrave;o vụ thuế. Thuế một năm c&oacute; một lần nếu chỉ tr&ocirc;ng v&agrave;o đấy th&igrave; b&aacute;n cha đi cũng kh&ocirc;ng đủ để b&ugrave; v&agrave;o chỗ ba, bốn ngh&igrave;n bạc chạy chọt để tranh triện đồng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">V&igrave; thế, đ&ecirc;m h&ocirc;m ấy, ở nh&agrave; b&aacute; Kiến ra về, Ch&iacute; Ph&egrave;o v&ocirc; c&ugrave;ng hả h&ecirc;! B&aacute; Kiến đ&atilde; kh&ocirc;ng vu vạ g&igrave; cho hắn, lại c&ograve;n giết g&agrave; mua rượu cho hắn uống, xong lại đ&atilde;i th&ecirc;m đồng bạc để về uống thuốc. &ETH;ồng bạc, l&agrave;m g&igrave; đến thế? Hắn loạng choạng vừa đi vừa cười; hắn chẳng cần đến ba xu. L&uacute;c ngồi t&ugrave; hắn c&oacute; học m&oacute;t được mấy b&agrave;i thuốc giấu: chỉ v&agrave;i nắm l&aacute;, l&agrave; mặt hắn lại đ&acirc;u v&agrave;o đấy ngay. C&ograve;n đồng bạc lại để đi uống rượu...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn uống được c&oacute; ba h&ocirc;m, h&ocirc;m thứ tư th&igrave; hắn trợn mắt l&ecirc;n, bảo con mẹ h&agrave;ng rượu rằng:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- H&ocirc;m nay &ocirc;ng kh&ocirc;ng c&oacute; tiền; nh&agrave; m&agrave;y b&aacute;n chịu cho &ocirc;ng một chai. Tối &ocirc;ng mang tiền đến trả.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Mụ b&aacute;n h&agrave;ng rượu hơi ngần ngừ. Thế l&agrave; hắn r&uacute;t bao di&ecirc;m đ&aacute;nh c&aacute;i x&ograve;e, ch&acirc;m di&ecirc;m l&ecirc;n m&aacute;i nh&agrave; mụ. Mụ hoảng hốt k&ecirc;u la om s&ograve;m vội dập tắt ngọn lửa vừa mới ch&aacute;y. Rồi kh&oacute;c kh&oacute;c mếu mếu, mụ đưa chai rượu. Hắn hầm hầm, chĩa v&agrave;o mặt mụ bảo rằng:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- C&aacute;i giống nh&agrave; m&agrave;y kh&ocirc;ng ưa nhẹ! &Ocirc;ng mua chứ &ocirc;ng c&oacute; xin nh&agrave; m&agrave;y đ&acirc;u! M&agrave;y tưởng &ocirc;ng quỵt hở? M&agrave;y thử hỏi cả l&agrave;ng xem &ocirc;ng c&oacute; quỵt của đứa n&agrave;o bao giờ kh&ocirc;ng? &Ocirc;ng kh&ocirc;ng thiếu tiền! &Ocirc;ng c&ograve;n gửi đằng cụ b&aacute;, chiều n&agrave;y &ocirc;ng đi lấy về &ocirc;ng trả.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Mụ vừa k&eacute;o &aacute;o l&ecirc;n quẹt nước mũi, vừa bảo:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Ch&uacute;ng ch&aacute;u kh&ocirc;ng d&aacute;m chắc l&eacute;p nhưng quả l&agrave; &iacute;t vốn.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn qu&aacute;t l&ecirc;n:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- &Iacute;t vốn th&igrave; tối nay &ocirc;ng trả. Nh&agrave; m&agrave;y đ&atilde; chết ngay b&acirc;y giờ hay sao?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Rồi hắn x&aacute;ch chai ra về. Hắn về c&aacute;i miếu con ở bờ s&ocirc;ng, v&igrave; vốn từ trước đến nay kh&ocirc;ng c&oacute; nh&agrave;. L&uacute;c đi đường, hắn đ&atilde; vặn được ở nh&agrave; n&agrave;o đ&oacute; bốn quả chuối xanh, v&agrave; bốc của c&ocirc; h&agrave;ng x&eacute;n một d&uacute;m con muối trắng. B&acirc;y giờ hắn uống rượu với chuối xanh chấm muối trắng v&agrave; thấy rằng cũng ngon. Hắn uống rượu với c&aacute;i g&igrave; cũng ngon.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Uống xong hắn ch&ugrave;i miệng, rồi ngật ng&agrave; ngật ngưỡng đến nh&agrave; b&aacute; Kiến. Gặp ai hắn cũng bảo: hắn đến nh&agrave; cụ b&aacute; Kiến đ&ograve;i nợ đ&acirc;y! Mới tr&ocirc;ng thấy hắn v&agrave;o đến s&acirc;n, b&aacute; Kiến đ&atilde; biết hắn đến sinh sự rồi. C&aacute;i mắt th&igrave; ngầu l&ecirc;n, hai ch&acirc;n th&igrave; đi lảo đảo, c&aacute;i m&ocirc;i bầm lại m&agrave; run bần bật. Cũng may, hắn kh&ocirc;ng cầm vỏ chai, b&aacute; Kiến cũng d&otilde;ng dạc hỏi:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Anh Ch&iacute; đi đ&acirc;u đấy?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn ch&agrave;o to:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Lạy cụ ạ. Bẩm cụ... Con đến cửa cụ để k&ecirc;u cụ một việc ạ.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Giọng hắn l&egrave; nh&egrave; v&agrave; tiếng đ&atilde; gần như m&eacute;o m&oacute;. Nhưng bộ điệu th&igrave; lại như hiền l&agrave;nh; hắn vừa g&atilde;i đầu g&atilde;i tai, vừa lải nhải:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Bẩm cụ, từ ng&agrave;y cụ bắt đi ở t&ugrave;, con lại sinh ra th&iacute;ch đi ở t&ugrave;, bẩm c&oacute; thế, con c&oacute; n&oacute;i gian th&igrave; trời tru đất diệt, bẩm quả l&agrave; đi t&ugrave; sướng qu&aacute;. &ETH;i ở t&ugrave; c&ograve;n c&oacute; cơm để m&agrave; ăn, b&acirc;y giờ về l&agrave;ng về nước, một thước đất cắm d&ugrave;i kh&ocirc;ng c&oacute;, chả l&agrave;m g&igrave; n&ecirc;n ăn. Bẩm cụ, con lại đến k&ecirc;u cụ, cụ lại cho con đi t&ugrave;...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Cụ b&aacute; qu&aacute;t, bắt đầu bao giờ cụ cũng qu&aacute;t để thử d&acirc;y thần kinh của người.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Anh n&agrave;y lại say khướt rồi!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn x&ocirc;ng lại gần, đảo ngược mắt, giơ tay l&ecirc;n nửa chừng:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Bẩm kh&ocirc;ng ạ, bẩm thật l&agrave; kh&ocirc;ng say. Con đến xin cụ cho con đi t&ugrave; m&agrave; nếu kh&ocirc;ng được th&igrave;... th&igrave;... thưa cụ...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn m&oacute;c đủ mọi t&uacute;i, để t&igrave;m một c&aacute;i g&igrave;, hắn giơ ra: đ&oacute; l&agrave; một con dao nhỏ, nhưng rất sắc. Hắn nghiến răng n&oacute;i tiếp:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- V&acirc;ng, bẩm cụ kh&ocirc;ng được th&igrave; con phải đ&acirc;m chết dăm ba thằng, rồi cụ bắt con giải huyện.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Rồi hắn c&uacute;i xuống, tần mần gọt cạnh c&aacute;i b&agrave;n lim. Cụ b&aacute; cười khanh kh&aacute;ch. Cụ vẫn tự phụ hơn đời c&aacute;i cười T&agrave;o Th&aacute;o ấy. Cụ đứng l&ecirc;n vỗ vai hắn m&agrave; bảo rằng:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Anh bứa lắm. Nhưng n&agrave;y, anh Ch&iacute; ạ, anh muốn đ&acirc;m người cũng kh&ocirc;ng kh&oacute; g&igrave;. &ETH;ội Tảo n&oacute; c&ograve;n nợ t&ocirc;i năm mươi đồng đấy, anh chịu kh&oacute; đến đ&ograve;i cho t&ocirc;i, đ&ograve;i được tự nhi&ecirc;n c&oacute; vườn.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">&ETH;ội Tảo l&agrave; một tay vai vế trong l&agrave;ng. V&acirc;y c&aacute;nh &ocirc;ng ta mạnh, vẫn k&igrave;nh nhau với nh&agrave; cụ b&aacute; m&agrave; cụ b&aacute; thường phải chịu bởi hắn l&agrave; cựu binh, lương hưu tr&iacute; nhiều, quen thuộc nhiều, lại ăn n&oacute;i giỏi. Hắn vay cụ b&aacute; năm mươi đồng đ&atilde; từ l&acirc;u, b&acirc;y giờ đột nhi&ecirc;n trở mặt vỗ tuột, lấy cớ rằng số tiền ấy t&iacute;nh v&agrave;o m&oacute;n tiền ch&egrave; l&yacute; Cường ra l&agrave;m l&yacute; trưởng chưa tạ hắn. Cụ b&aacute; tức như chọc họng, nhưng chưa biết l&agrave;m thế n&agrave;o, bởi v&igrave; thằng binh Chức, đầy tớ ch&acirc;n tay của cụ, khả dĩ đương đầu với hắn được, chết năm ngo&aacute;i rồi. B&acirc;y giờ cụ mới lại gặp được Ch&iacute; Ph&egrave;o, c&oacute; thể thay cho binh Chức. Cụ thử n&oacute;i kh&iacute;ch xem sao. Nếu n&oacute; trị được đội Tảo th&igrave; tốt lắm. Nếu n&oacute; bị đội Tảo trị th&igrave; cụ cũng chẳng thiệt g&igrave;, đằng n&agrave;o cũng c&oacute; lợi cho cụ cả.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Ch&iacute; Ph&egrave;o nhận ngay! Hắn tức khắc đến nh&agrave; đội Tảo, v&agrave; cất tiếng chửi ngay từ đầu ng&otilde;. Gi&aacute; gặp phải h&ocirc;m kh&aacute;c, th&igrave; c&oacute; &aacute;n mạng lắm: đội Tảo cũng c&oacute; thể đ&acirc;m ch&eacute;m được, chưa bao giờ chịu h&agrave;ng trước cuộc giao tranh. Nhưng ph&uacute;c đời cho hắn, hay l&agrave; cho Ch&iacute; Ph&egrave;o, h&ocirc;m ấy hắn ốm liệt giường, kh&ocirc;ng sao nhắc m&igrave;nh dậy được, c&oacute; lẽ hắn cũng kh&ocirc;ng biết Ch&iacute; Ph&egrave;o chửi hắn. Vợ hắn, thấy Ch&iacute; Ph&egrave;o thở ra m&ugrave;i rượu, v&agrave; biết r&otilde; đầu đu&ocirc;i m&oacute;n nợ, lấy năm mươi đồng giấu chồng đưa cho người nh&agrave; đi theo Ch&iacute; Ph&egrave;o. Đ&agrave;n b&agrave; vốn chuộng ho&agrave; b&igrave;nh; họ muốn y&ecirc;n chuyện th&igrave; th&ocirc;i, gai ngạnh l&agrave;m g&igrave; cho sinh sự. Vả lại, b&agrave; đội cũng nghĩ rằng: chồng m&igrave;nh đang ốm... chồng m&igrave;nh c&oacute; nợ người ta hẳn hoi... V&agrave; năm chục đồng bạc đối với nh&agrave; m&igrave;nh l&agrave; mấy, l&ocirc;i th&ocirc;i lại chả tốn đến ba lần năm chục đồng!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">V&igrave; thế, Ch&iacute; Ph&egrave;o mới được v&ecirc;nh v&ecirc;nh ra về; hắn thấy hắn oai th&ecirc;m bậc nữa. Hắn tự đắc: &ldquo;anh h&ugrave;ng l&agrave;ng n&agrave;y c&oacute;c c&oacute; thằng n&agrave;o bằng ta!&rdquo;. Cụ b&aacute; thấy m&igrave;nh thắng b&ecirc;n địch m&agrave; kh&ocirc;ng cần đến hội đồng l&agrave;m bi&ecirc;n bản xem chừng th&iacute;ch ch&iacute;. Cụ đưa lu&ocirc;n cho anh đầy tớ ch&acirc;n tay mới lu&ocirc;n năm đồng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Anh Ch&iacute; ạ, cả năm chục đồng n&agrave;y phần anh. Nhưng nếu anh lấy cả th&igrave; chỉ ba h&ocirc;m l&agrave; tan hết. Vậy anh cầm lấy chỗ n&agrave;y uống rượu c&ograve;n để t&ocirc;i b&aacute;n cho anh mảnh vườn; kh&ocirc;ng c&oacute; vườn đất th&igrave; l&agrave;m ăn g&igrave;?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Ch&iacute; Ph&egrave;o &ldquo;v&acirc;ng dạ&rdquo; ra về. Mấy h&ocirc;m sau, cụ b&aacute; bảo l&yacute; Cường cho hắn năm s&agrave;o vườn ở b&atilde;i s&ocirc;ng cắm thuế của một người l&agrave;ng h&ocirc;m nọ. Ch&iacute; Ph&egrave;o bỗng th&agrave;nh ra c&oacute; nh&agrave;. Hồi ấy hắn mới đ&acirc;u hăm bảy hay hăm t&aacute;m...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">B&acirc;y giờ th&igrave; hắn đ&atilde; th&agrave;nh người kh&ocirc;ng tuổi rồi. Ba mươi t&aacute;m hay ba mươi ch&iacute;n? Bốn mươi hay ngo&agrave;i bốn mươi? C&aacute;i mặt hắn kh&ocirc;ng trẻ cũng kh&ocirc;ng gi&agrave;; n&oacute; kh&ocirc;ng c&ograve;n phải l&agrave; mặt người; n&oacute; l&agrave; mặt của con vật lạ, nh&igrave;n mặt những con vật c&oacute; bao giờ biết tuổi? C&aacute;i mặt hắn v&agrave;ng v&agrave;ng m&agrave; lại muốn xạm m&agrave;u gio; n&oacute; vằn dọc vằn ngang, kh&ocirc;ng thứ tự, biết bao nhi&ecirc;u l&agrave; sẹo. Vết những mảnh chai của bao nhi&ecirc;u lần ăn vạ k&ecirc;u l&agrave;ng, bao nhi&ecirc;u lần, hắn nhớ l&agrave;m sao nổi? Bao nhi&ecirc;u việc ức hiếp, ph&aacute; ph&aacute;ch, đ&acirc;m ch&eacute;m, mưu hại, người ta giao cho hắn l&agrave;m! Những việc ấy ch&iacute;nh l&agrave; cuộc đời của hắn; cuộc đời m&agrave; hắn cũng chả biết đ&atilde; d&agrave;i bao nhi&ecirc;u năm rồi. Bởi v&igrave; ngay đến c&aacute;i thẻ c&oacute; bi&ecirc;n tuổi hắn cũng kh&ocirc;ng c&oacute;, trong sổ l&agrave;ng người ta vẫn khai hắn l&agrave; d&acirc;n lưu t&aacute;n, l&acirc;u năm kh&ocirc;ng về l&agrave;ng. Hắn nhớ mang m&aacute;ng rằng c&oacute; lần hắn hai mươi tuổi, rồi hắn đi ở t&ugrave;, rồi h&igrave;nh như hắn hăm nhăm kh&ocirc;ng biết c&oacute; đ&uacute;ng kh&ocirc;ng? Bởi từ đấy th&igrave; đối với hắn kh&ocirc;ng c&ograve;n ng&agrave;y th&aacute;ng nữa. Bởi v&igrave; từ đấy hắn bao giờ cũng say. Nhưng cơn say của hắn tr&agrave;n cơn n&agrave;y qua cơn kh&aacute;c, th&agrave;nh một cơn d&agrave;i, m&ecirc;nh m&ocirc;ng, hắn ăn trong l&uacute;c say, ngủ trong l&uacute;c say, thức dậy vẫn c&ograve;n say, đập đầu rạch mặt chửi bới, dọa nạt trong l&uacute;c say, uống rượu trong l&uacute;c say, để rồi say nữa, say v&ocirc; tận. Chưa bao giờ hắn tỉnh v&agrave; c&oacute; lẽ hắn chưa bao giờ tỉnh t&aacute;o, để nhớ rằng c&oacute; hắn ở đời. C&oacute; lẽ hắn cũng kh&ocirc;ng biết rằng hắn l&agrave; con quỷ dữ của l&agrave;ng Vũ &ETH;ại, để t&aacute;c qu&aacute;i cho bao nhi&ecirc;u d&acirc;n l&agrave;ng. Hắn biết đ&acirc;u hắn đ&atilde; ph&aacute; bao nhi&ecirc;u cơ nghiệp, đập n&aacute;t bao nhi&ecirc;u cảnh y&ecirc;n vui, đạp đổ bao nhi&ecirc;u hạnh ph&uacute;c, l&agrave;m chảy m&aacute;u v&agrave; nước mắt của bao nhi&ecirc;u người lương thiện. Hắn đ&acirc;u biết v&igrave; hắn l&agrave;m tất cả những việc ấy trong khi người hắn say; hắn say th&igrave; hắn l&agrave;m bất cứ c&aacute;i g&igrave; người ta sai hắn l&agrave;m. Tất cả d&acirc;n l&agrave;ng đều sợ hắn v&agrave; tr&aacute;nh mặt hắn mỗi khi hắn qua.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">V&igrave; thế cho n&ecirc;n hắn chửi hay l&agrave; chẳng v&igrave; c&aacute;i g&igrave; hắn cũng chửi, cứ rượu xong l&agrave; hắn chửi. Hắn chửi như những người say rượu h&aacute;t. Gi&aacute; hắn biết h&aacute;t th&igrave; c&oacute; lẽ hắn kh&ocirc;ng cần chửi. Khổ cho hắn v&agrave; khổ cho người hắn lại kh&ocirc;ng biết h&aacute;t. Th&igrave; hắn chửi, cũng như chiều nay hắn chửi...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn chửi trời v&agrave; đời. Hắn chửi cả l&agrave;ng Vũ &ETH;ại. Hắn chửi tất cả những đứa n&agrave;o kh&ocirc;ng chửi nhau với hắn. Nhưng cũng mặc, ai m&agrave; ho&agrave;i hơi, tức m&igrave;nh hắn chửi đứa n&agrave;o đẻ ra ch&iacute;nh hắn, lại c&agrave;ng kh&ocirc;ng ai cần! V&agrave; hắn lấy thế l&agrave;m ức lắm; bởi v&igrave; người ta kh&ocirc;ng thể chửi nhau một m&igrave;nh; chửi nhau một m&igrave;nh th&igrave; c&ograve;n văn vẻ g&igrave;! Thế l&agrave; chắc chắn đ&atilde; c&oacute; một cớ để tức tối, một cớ rất ch&iacute;nh đ&aacute;ng để hắn c&oacute; thể h&ugrave;ng hổ đi b&aacute;o th&ugrave;. Phải, hắn phải b&aacute;o th&ugrave;, b&aacute;o th&ugrave; v&agrave;o bất cứ ai. Hắn phải v&agrave;o nh&agrave; n&agrave;o mới được, bất cứ nh&agrave; n&agrave;o. Hắn sẽ rẽ v&agrave;o bất cứ ng&otilde; n&agrave;o hắn gặp để đập ph&aacute; đốt nh&agrave; hay lăn ra k&ecirc;u l&agrave;ng nước. Phải đấy, hắn sẽ rẽ v&agrave;o bất cứ ng&otilde; n&agrave;o hắn gặp... &Aacute;, đ&acirc;u kia rồi mau mau...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Nhưng m&agrave; mặt trăng l&ecirc;n, mặt trăng rằm v&agrave;nh vạnh. V&agrave; &aacute;nh trăng chảy tr&ecirc;n đường trắng tinh. Ồ, c&aacute;i g&igrave; đ&acirc;y, đen v&agrave; m&eacute;o m&oacute; tr&ecirc;n đường trăng nhễ nhại? N&oacute; xệch xạc về b&ecirc;n tr&aacute;i, thu gọn v&agrave;o rồi lại d&agrave;i loang ra, x&eacute; r&aacute;ch v&agrave;i chỗ.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">N&oacute; cứ quần quật dưới ch&acirc;n Ch&iacute; Ph&egrave;o. Ch&iacute; Ph&egrave;o đứng lại v&agrave; nh&igrave;n n&oacute; v&agrave; hắn bỗng nghi&ecirc;ng ngả cười. Hắn cười ngặt nghẽo, cười rũ rượi. Gi&aacute; hắn cứ chửi lại c&ograve;n dễ nghe! C&aacute;i vật xệch xạc tr&ecirc;n đường ch&iacute;nh l&agrave; b&oacute;ng hắn. Thế l&agrave; hắn cười, v&agrave; hắn qu&ecirc;n b&aacute;o th&ugrave;: hắn đi qua c&aacute;i ng&otilde; đầu ti&ecirc;n kia rồi. B&acirc;y giờ th&igrave; đến ng&otilde; nh&agrave; tự L&atilde;ng, một anh thầy c&uacute;ng c&oacute; một bộ r&acirc;u lờ phờ. Ch&iacute; ph&egrave;o bỗng nảy ra một &yacute;: tạt v&agrave;o đ&acirc;y v&agrave; đập c&aacute;i b&agrave;n chầu văn của l&atilde;o tự nửa m&ugrave;a n&agrave;y ra. Bởi v&igrave; l&atilde;o tự n&agrave;y vừa l&agrave;m thầy c&uacute;ng lại vừa l&agrave;m nghề hoạn lợn. C&aacute;i đ&agrave;n của l&atilde;o lừng phừng nghe c&ograve;n chối tai hơn l&agrave; lợn k&ecirc;u. Nhưng l&uacute;c v&agrave;o th&igrave; l&atilde;o tự lại đang uống rượu; l&atilde;o uống rượu ngay ở s&acirc;n, vừa uống vừa vuốt r&acirc;u, vừa rung rung c&aacute;i đầu. Ch&iacute; Ph&egrave;o đứng lại nh&igrave;n, thấy l&atilde;o cũng hay hay. Hắn thấy tất cả những người uống rượu đều hay hay. Rồi đột nhi&ecirc;n hắn kh&aacute;t, trời ơi sao m&agrave; kh&aacute;t! Kh&aacute;t đến ch&aacute;y cổ họng... Kh&ocirc;ng do dự, hắn lại b&ecirc;n l&atilde;o tự, nhắc lấy chai rượu ngửa cổ dốc v&agrave;o mồm tu. L&atilde;o tự duỗi c&aacute;i cổ g&agrave; vặt l&ocirc;ng ra, trố mắt l&ecirc;n, nhưng kh&ocirc;ng n&oacute;i g&igrave;. Lưỡi l&atilde;o r&iacute;u lại rồi, c&ograve;n n&oacute;i l&agrave;m sao được? L&atilde;o đ&atilde; uống hết hai phần chai. C&ograve;n một phần th&igrave; Ch&iacute; Ph&egrave;o tu nốt. Hắn tu c&oacute; một hơi, rồi kh&agrave; một c&aacute;i, ch&eacute;p c&aacute;i miệng như c&ograve;n th&egrave;m. Rồi hắn nắm lấy mấy c&aacute;i r&acirc;u lờ phờ của l&atilde;o tự, n&acirc;ng soi l&ecirc;n trăng m&agrave; cười. L&atilde;o tự cũng cười. Hai thằng say rượu ngả v&agrave;o nhau m&agrave; cười, như một đ&ocirc;i tri kỷ cuồng. Rồi tự L&atilde;ng v&agrave;o nh&agrave; x&aacute;ch hai chai rượu nữa; l&atilde;o c&ograve;n đ&uacute;ng hai chai nữa, l&atilde;o mời Ch&iacute; Ph&egrave;o uống nữa, uống thật say, kh&ocirc;ng cần g&igrave;. Cứ việc uống, đừng c&oacute; lo ngại g&igrave; đấy! Vợ l&atilde;o chết đến bảy t&aacute;m năm nay rồi, con g&aacute;i l&atilde;o chửa hoang bỏ l&atilde;o đi, l&atilde;o chỉ c&oacute; một m&igrave;nh, kh&ocirc;ng c&ograve;n vợ con n&agrave;o m&egrave; nheo cả, l&atilde;o muốn uống đến bao giờ th&igrave; uống. Cứ uống! Cứ uống, cứ uống đi &ocirc;ng bạn lạc đường từ cung trăng xuống ạ! Uống thật tợn, uống đến đ&aacute;i ra rượu th&igrave; mới th&iacute;ch. Nhịn uống để l&agrave;m g&igrave;? C&oacute; gi&agrave;u c&oacute; sang, c&oacute; l&agrave;m n&ecirc;n &ocirc;ng cả b&agrave; lớn nữa, chết cũng kh&ocirc;ng ai gọi l&agrave; &ldquo;cụ lớn mả&rdquo;! L&atilde;o sống c&oacute; đến hơn năm mươi rồi m&agrave; chưa thấy một cụ lớn mả n&agrave;o sống s&oacute;t! Chỉ c&oacute; c&aacute;i mả, c&aacute;i mả đất. Ai chết cũng th&agrave;nh c&aacute;i mả, say sưa chết cũng th&agrave;nh c&aacute;i mả, lo g&igrave;? Cứ say.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Chưa bao giờ Ch&iacute; Ph&egrave;o được thỏa th&ecirc; đến thế! Hắn lấy l&agrave;m lạ sao m&atilde;i đến h&ocirc;m nay mới ngồi uống rượu với thằng cha tự n&agrave;y. Ch&uacute;ng uống với nhau rất l&agrave; nhiều. V&agrave; rất l&agrave; nhiều. Người ta tưởng như cả l&agrave;ng Vũ &ETH;ại phải nhịn uống để đủ rượu cho ch&uacute;ng uống.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">&ETH;ến l&uacute;c hết cả hai chai th&igrave; tự L&atilde;ng đ&atilde; b&ograve; ra s&acirc;n. L&atilde;o b&ograve; như cua v&agrave; hỏi Ch&iacute; Ph&egrave;o rằng: người ta đứng l&ecirc;n bằng c&aacute;i g&igrave;? Ch&iacute; Ph&egrave;o vần ngửa l&atilde;o ra, vuốt c&aacute;i r&acirc;u lờ phờ của l&atilde;o mấy c&aacute;i, rồi để mặc l&atilde;o thế, hắn lảo đảo ra về.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn vừa đi vừa phanh ngực ra m&agrave; g&atilde;i. Hắn g&atilde;i ngực rồi g&atilde;i l&ecirc;n cổ, g&atilde;i mang tai v&agrave; g&atilde;i l&ecirc;n cả đầu. C&oacute; l&uacute;c hắn phải đứng lại giữa đường m&agrave; g&atilde;i, ghệch ch&acirc;n l&ecirc;n m&agrave; g&atilde;i, hắn bứt rứt qu&aacute;, ngứa ng&aacute;y qu&aacute;, v&agrave; chợt nghĩ ngay đến c&aacute;i bờ s&ocirc;ng gần nh&agrave;. Bởi v&igrave; c&aacute;i vườn của hắn ở gần một con s&ocirc;ng con, nước lặng v&agrave; trong, khắp b&atilde;i trồng to&agrave;n d&acirc;u, gi&oacute; đưa đẩy những th&acirc;n mềm oặt ẹo, cuộn theo nhau th&agrave;nh l&agrave;n. Duy c&oacute; vườn nh&agrave; hắn trồng to&agrave;n chuối, ở một g&oacute;c vườn c&oacute; t&uacute;p lều con. Những đ&ecirc;m trăng như đ&ecirc;m nay, c&aacute;i vườn phẳng ngổn ngang những b&oacute;ng chuối đen như những c&aacute;i &aacute;o nhuộm vắt tung tr&ecirc;n b&atilde;i. V&agrave; những t&agrave;u chuối nằm ngửa, ưỡn cong cong l&ecirc;n hứng lấy trăng xanh rời rợi như l&agrave; ướt nước, thỉnh thoảng bị gi&oacute; lay lại giẫy l&ecirc;n đ&agrave;nh đạch như l&agrave; hứng t&igrave;nh.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Ch&iacute; Ph&egrave;o vừa t&ograve; m&ograve; nh&igrave;n những t&agrave;u l&aacute; chuối vừa đi xuống vườn. Nhưng hắn kh&ocirc;ng v&agrave;o c&aacute;i t&uacute;p lều &uacute;p x&uacute;p m&agrave; ra thẳng bờ s&ocirc;ng. Hắn định sẽ nhảy xuống tắm cho khỏi ngứa ng&aacute;y rồi lăn ngay ra vườn m&agrave; ngủ. Tội g&igrave; chui v&agrave;o lều, bức đến kh&ocirc;ng c&ograve;n thở được. Một thằng như hắn, đập đầu kh&ocirc;ng chết, huống hồ l&agrave; gi&oacute; sương... &ETH;ến bờ s&ocirc;ng hắn dừng lại, v&igrave; h&igrave;nh như c&oacute; người. C&oacute; người thật, v&agrave; hắn ng&acirc;y ra nh&igrave;n.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn nh&igrave;n giữa hai lọ nước v&agrave; tựa lưng v&agrave;o gốc chuối, một người đ&agrave;n b&agrave; ngồi t&ecirc;nh h&ecirc;nh. Ch&iacute;nh l&agrave; người đ&agrave;n b&agrave;, hắn biết vậy l&agrave; nhờ m&aacute;i t&oacute;c d&agrave;i bu&ocirc;ng x&otilde;a xuống vai trần v&agrave; ngực... Hai tay trần của mụ bu&ocirc;ng xu&ocirc;i, c&aacute;i mồm của mụ h&aacute; hốc l&ecirc;n trăng m&agrave; ngủ, hay l&agrave; chết. Đ&ocirc;i ch&acirc;n th&igrave; duỗi thẳng ra trước mặt, c&aacute;i v&aacute;y đen xộc xệch... B&ecirc;n kia, c&oacute; lẽ v&igrave; mụ giẫy c&aacute;i yếm xẹo xọ để trật ra c&aacute;i sườn n&acirc;y n&acirc;y. Tất cả những c&aacute;i ấy phơi ra trăng, rười rượi những trăng l&agrave;m trắng những c&aacute;i đ&oacute; c&oacute; lẽ ban ng&agrave;y kh&ocirc;ng trắng; trăng l&agrave;m đẹp l&ecirc;n. Ch&iacute; Ph&egrave;o tự nhi&ecirc;n thấy ứ đầy miệng bao nhi&ecirc;u l&agrave; nước d&atilde;i, m&agrave; cổ th&igrave; lại kh&ocirc;, hắn nuốt ừng ực, hắn thấy c&aacute;i g&igrave; rộn rạo ran khắp người. Bỗng nhi&ecirc;n hắn run run. Ồ tại sao như thế được? &ETH;&aacute;ng nhẽ ch&iacute;nh người đ&agrave;n b&agrave; khốn nạn kia phải run mới phải, c&aacute;i người đ&agrave;n b&agrave; dại dột đ&atilde; nằm ềnh ệch m&agrave; ngủ ngay gần nh&agrave; hắn n&agrave;y.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Nhưng người đ&agrave;n b&agrave; ấy lại ch&iacute;nh l&agrave; thị Nở, một người ngẩn ngơ như những người đần trong cổ t&iacute;ch v&agrave; xấu ma ch&ecirc; quỷ hờn. C&aacute;i mặt của thị đ&iacute;ch thực l&agrave; một sự mỉa mai của h&oacute;a c&ocirc;ng: n&oacute; ngắn đến nỗi người ta c&oacute; thể tưởng bề ngang lớn hơn bề d&agrave;i, thế m&agrave; hai m&aacute; n&oacute; lại h&oacute;p v&agrave;o mới thật l&agrave; tai hại, nếu m&aacute; n&oacute; phinh ph&iacute;nh th&igrave; mặt thị lại c&ograve;n được hao hao như mặt lợn, l&agrave; thứ mặt vốn nhiều hơn người ta tưởng, tr&ecirc;n cổ người. C&aacute;i mũi th&igrave; vừa ngắn, vừa to, vừa đỏ, vừa sần s&ugrave;i như vỏ cam s&agrave;nh, b&agrave;nh bạnh muốn chen lẫn nhau với những c&aacute;i m&ocirc;i cũng cố to cho kh&ocirc;ng thua c&aacute;i mũi: c&oacute; lẽ v&igrave; cố qu&aacute; cho n&ecirc;n ch&uacute;ng nứt nở như rạn ra. &ETH;&atilde; thế thị lại ăn trầu thuốc, hai m&ocirc;i d&agrave;y được bồi cho d&agrave;y th&ecirc;m, cũng may quyết trầu s&aacute;nh lại, che được c&aacute;i m&agrave;u thịt tr&acirc;u x&aacute;m ngo&aacute;ch. &ETH;&atilde; thế những c&aacute;i răng rất to lại ch&igrave;a ra: &yacute; hẳn ch&uacute;ng nghĩ sự c&acirc;n đối chữa được một v&agrave;i phần cho sự xấu. &ETH;&atilde; thế thị lại dở hơi; đ&oacute; l&agrave; một &acirc;n huệ đặc biệt của Thượng đế ch&iacute; c&ocirc;ng; nếu s&aacute;ng suốt th&igrave; người đ&agrave;n b&agrave; ấy sẽ khổ sở ngay từ khi mua c&aacute;i gương thứ nhất. V&agrave; thị lại ngh&egrave;o nếu tr&aacute;i lại, &iacute;t nhất đ&atilde; c&oacute; một đ&agrave;n &ocirc;ng khổ sở. V&agrave; thị lại l&agrave; d&ograve;ng giống của một nh&agrave; c&oacute; mả hủi: c&aacute;i n&agrave;y khiến kh&ocirc;ng một ch&agrave;ng trai n&agrave;o phải ph&acirc;n v&acirc;n. Người ta tr&aacute;nh thị như tr&aacute;nh một con vật rất tởm. Ngo&agrave;i ba mươi tuổi, thị vẫn chưa c&oacute; chồng. Ở c&aacute;i l&agrave;ng Vũ &ETH;ại n&agrave;y người ta kết bạn từ khi l&ecirc;n t&aacute;m, v&agrave; c&oacute; khi c&oacute; con từ l&uacute;c mười lăm; kh&ocirc;ng ai đợi đến năm hai mươi đẻ đứa con thứ nhất. Cứ nh&igrave;n t&igrave;nh h&igrave;nh ấy th&igrave; ta n&oacute;i qu&aacute;ch: thị Nở kh&ocirc;ng c&oacute; chồng. M&agrave; thị cũng kh&ocirc;ng c&ograve;n ai th&acirc;n th&iacute;ch, trừ một người c&ocirc; đ&atilde; c&oacute; thể gọi được l&agrave; gi&agrave;, v&agrave; đ&atilde; kh&ocirc;ng chồng như thị. Số trời định thế, để kh&ocirc;ng ai phải trơ trọi tr&ecirc;n đời n&agrave;y. Người c&ocirc; l&agrave;m thu&ecirc; cho người đ&agrave;n b&agrave; bu&ocirc;n chuối v&agrave; trầu kh&ocirc;ng xếp t&agrave;u đi Hải Ph&ograve;ng, c&oacute; khi đi tận H&ograve;n Gai, Cẩm Phả. C&ograve;n thị sống bằng những nghề lặt vặt ở l&agrave;ng. Hai c&ocirc; ch&aacute;u sống trong một c&aacute;i nh&agrave; tre c&aacute;ch vườn của Ch&iacute; Ph&egrave;o bởi một con đ&ecirc;; hắn ở ngo&agrave;i b&atilde;i, hai người ở trong x&oacute;m. C&oacute; lẽ ch&iacute;nh v&igrave; thế m&agrave; thị Nở kh&ocirc;ng sợ c&aacute;i thằng m&agrave; cả l&agrave;ng sợ. Gần gũi l&acirc;u cũng sinh quen, m&agrave; quen th&igrave; &iacute;t khi c&ograve;n sợ. Những người tr&ocirc;ng coi vườn b&aacute;ch th&uacute; thường bảo rằng hổ b&aacute;o hiền y như m&egrave;o. Vả lại c&oacute; l&yacute; n&agrave;o để thị sợ hắn đ&acirc;u? Người ta kh&ocirc;ng ai sợ kẻ kh&aacute;c phạm đến c&aacute;i xấu, c&aacute;i ngh&egrave;o, c&aacute;i ngẩn ngơ của m&igrave;nh, m&agrave; thị lại chỉ c&oacute; ba c&aacute;i ấy... Một phần nữa cũng bởi Ch&iacute; Ph&egrave;o &iacute;t khi ở nh&agrave;, m&agrave; ở nh&agrave; th&igrave; hắn lại hiền l&agrave;nh, ai c&oacute; thể &aacute;c trong khi ngủ? Hắn chỉ về nh&agrave; để ngủ.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Ng&agrave;y n&agrave;o thị Nở cũng phải qua vườn nh&agrave; hắn hai ba lần, l&agrave; v&igrave; qua vườn nh&agrave; hắn c&oacute; một lối nhỏ đi ra s&ocirc;ng; trước kia, cả x&oacute;m vẫn d&ugrave;ng c&aacute;i ng&otilde; ấy để ra s&ocirc;ng tắm giặt hay k&iacute;n nước. Nhưng từ khi hắn đến người ta th&ocirc;i dần, t&igrave;m một lối kh&aacute;c đi xa hơn. Trừ thị Nở: th&igrave; đ&atilde; bảo thị l&agrave; người dở hơi, thị kh&ocirc;ng th&iacute;ch l&agrave;m như kẻ kh&aacute;c. Qu&aacute; tin ở người, qu&aacute; tin ở m&igrave;nh liều lĩnh, bướng bỉnh hay c&oacute; lẽ chỉ kh&ocirc;ng chịu rời th&oacute;i quen. Nhưng h&atilde;y biết thị cứ đi ng&otilde; ấy v&agrave; vẫn chẳng l&agrave;m sao cả. Thế rồi quen đi. C&oacute; lần trong l&uacute;c Ch&iacute; Ph&egrave;o ngủ, thị v&agrave;o cả nh&agrave; hắn để rọi nhờ lửa nữa, c&oacute; lần thị xin của hắn một &iacute;t rượu về để b&oacute;p ch&acirc;n; hắn mải ngủ c&agrave;u nh&agrave;u bảo thị rằng: ở x&oacute; nh&agrave; ấy, muốn r&oacute;t bao nhi&ecirc;u th&igrave; r&oacute;t, để y&ecirc;n cho hắn ngủ. V&agrave; lắm l&uacute;c thị ngạc nhi&ecirc;n: sao người ta gh&ecirc; hắn thế?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Chiều h&ocirc;m ấy thị Nở cũng ra s&ocirc;ng k&iacute;n nước như mọi chiều. Nhưng chiều h&ocirc;m ấy, trăng lại s&aacute;ng hơn mọi chiều, trăng tỏa tr&ecirc;n s&ocirc;ng v&agrave; s&ocirc;ng gợn biết bao nhi&ecirc;u gợn v&agrave;ng. Những v&agrave;ng ấy rung rinh mới tr&ocirc;ng th&igrave; đẹp, nhưng tr&ocirc;ng l&acirc;u mỏi mắt. Gi&oacute; lại m&aacute;t như quạt hầu. Thị Nở thấy muốn ng&aacute;p m&agrave; m&iacute; mắt th&igrave; nặng dần, toan d&iacute;p lại. Thị vốn c&oacute; một tật kh&ocirc;ng sao chữa được: c&oacute; l&uacute;c đột nhi&ecirc;n muốn ngủ, bất cứ ở đ&acirc;u hay đang l&agrave;m g&igrave;. B&agrave; c&ocirc; thị bảo thị l&agrave; một người v&ocirc; t&acirc;m. Ng&aacute;p một c&aacute;i, thị nghĩ bụng: khoan h&atilde;y k&iacute;n nước, h&atilde;y để lọ xuống ngồi nghỉ đ&acirc;y. Bởi v&igrave; thị đ&atilde; luật quật đập đất từ trưa tới giờ. M&agrave; mấy khi được một nơi m&aacute;t thế, m&aacute;t rợn da rợn thịt, sung sướng qu&aacute;! M&aacute;t y như quạt hầu. Thị cởi &aacute;o ra ngồi tựa v&agrave;o gốc chuối, d&aacute;ng ngồi kh&ocirc;ng k&iacute;n đ&aacute;o, nhưng kh&ocirc;ng bao giờ thị biết thế n&agrave;o l&agrave; lả lơi. Con người v&ocirc; t&acirc;m, kh&ocirc;ng hay nghĩ xa x&ocirc;i m&agrave;. Vả lại ở đ&acirc;y chẳng c&oacute; ai. Ch&iacute; Ph&egrave;o đ&atilde; về đ&acirc;u, m&agrave; hắn c&oacute; về th&igrave; cũng say khướt đ&atilde; ngủ từ nửa đường v&agrave; tới nh&agrave; tức khắc ch&uacute;i đầu v&agrave;o ngủ nốt. Hắn ra l&agrave;m g&igrave; đ&acirc;y, cho c&oacute; ra nữa th&igrave; đ&atilde; sao? Thị kh&ocirc;ng thể sợ hắn c&oacute; thể phạm đến thị bởi lẽ rất giản dị l&agrave; thị chưa thấy ai phạm đến thị bao giờ. Thật ra th&igrave; thị cũng chẳng nghĩ ngợi nhiều đến thế. Trong &oacute;c thị đ&atilde; c&oacute; một b&oacute;ng đen lan rồi. Thị kh&ocirc;ng ngồi th&igrave; kh&ocirc;ng chịu được.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Ngồi một l&uacute;c thị thấy rằng: nếu cứ ngồi m&atilde;i th&igrave; ngủ mất. Nhưng thị đ&atilde; ngủ đến hai phần rồi. V&agrave; thị nghĩ: th&igrave; ngủ, ngủ th&igrave; đ&atilde; l&agrave;m sao! Về nh&agrave; th&igrave; cũng chỉ ngủ, ngủ ngay đ&acirc;y cũng vậy. B&agrave; c&ocirc; đi theo h&agrave;ng &iacute;t ra cũng dăm h&ocirc;m mới về. Thị cứ ngồi đ&acirc;y cho m&aacute;t. V&agrave; thị ngủ. Thị ngủ ngon l&agrave;nh v&agrave; say sưa.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Ch&iacute; Ph&egrave;o vẫn say say nh&igrave;n v&agrave; run run. Bỗng nhi&ecirc;n hắn r&oacute;n r&eacute;n lại gần thị Nở: lần đầu ti&ecirc;n hắn r&oacute;n r&eacute;n, từ khi về l&agrave;ng. Thoạt ti&ecirc;n, hắn x&aacute;ch c&aacute;i lọ để ra xa, rồi hắn lẳng lặng ngồi xuống b&ecirc;n sườn thị.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">V&agrave; thị Nở giật m&igrave;nh. Thị Nở mới kịp giật m&igrave;nh th&igrave; thằng đ&agrave;n &ocirc;ng đ&atilde; b&aacute;m v&iacute;u lấy thị.... Thị v&ugrave;ng vẫy đẩy ra, thị mở mắt, thị tỉnh hẳn, thị nhận ra Ch&iacute; Ph&egrave;o. Thị vừa thở, vừa vật nhau với hắn vừa hổn hển: &ldquo;&Ocirc; hay... Bu&ocirc;ng ra... T&ocirc;i k&ecirc;u... T&ocirc;i k&ecirc;u l&agrave;ng... Bu&ocirc;ng ra. T&ocirc;i k&ecirc;u l&agrave;ng l&ecirc;n b&acirc;y giờ!&rdquo; Thằng đ&agrave;n &ocirc;ng ph&igrave; cười. Sao thị lại k&ecirc;u l&agrave;ng nhỉ? Hắn vẫn tưởng chỉ c&oacute; hắn mới k&ecirc;u l&agrave;ng th&ocirc;i; người ta lại k&ecirc;u tranh của hắn, bỗng nhi&ecirc;n hắn la l&ecirc;n k&ecirc;u l&agrave;ng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn k&ecirc;u như một kẻ bị đ&acirc;m, vừa k&ecirc;u vừa dằn người đ&agrave;n b&agrave; xuống. Thị Nở trố mắt ng&acirc;y ra nh&igrave;n. Thị Nở kinh ngạc: sao hắn lại k&ecirc;u l&agrave;ng nhỉ? M&agrave; hắn vẫn chưa chịu th&ocirc;i k&ecirc;u l&agrave;ng. Cũng may người quanh đ&acirc;y kh&ocirc;ng c&oacute; ai lạ g&igrave; tiếng hắn, m&agrave; khi hắn k&ecirc;u l&agrave;ng th&igrave; kh&ocirc;ng ai cần động dạng: họ lạu bạu chửi rồi lại ngủ; hắn k&ecirc;u l&agrave;ng cũng như người kh&aacute;c buồn, h&aacute;t ngao. Đ&aacute;p lại hắn chỉ c&oacute; lũ ch&oacute; cắn xao l&ecirc;n trong x&oacute;m.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thị Nở bỗng nhi&ecirc;n bật cười. Thị Nở vừa rủa vừa đập tay l&ecirc;n lưng hắn. Nhưng đ&oacute; l&agrave; c&aacute;i đập y&ecirc;u, bởi v&igrave; đập xong, c&aacute;i tay ấy lại gi&uacute;i lưng hắn xuống. V&agrave; ch&uacute;ng cười với nhau...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">B&acirc;y giờ th&igrave; ch&uacute;ng ngủ b&ecirc;n nhau... Đứa b&eacute; b&uacute; no th&igrave; ngủ. Người ta ngủ say sau khi l&agrave;m việc y&ecirc;u. Ch&uacute;ng ngủ như chưa bao giờ được ngủ... Trăng vẫn thức, vẫn trong trẻo... Trăng rắc bụi tr&ecirc;n s&ocirc;ng, v&agrave; s&ocirc;ng gợn biết bao nhi&ecirc;u v&agrave;ng. Nhưng đến gần s&aacute;ng, bỗng nhi&ecirc;n Ch&iacute; Ph&egrave;o chống một tay xuống đất, ngồi l&ecirc;n một nửa. Hắn thấy n&ocirc;n nao, ch&acirc;n tay bủn rủn, như đến hai ba ng&agrave;y nhịn đ&oacute;i. Thế m&agrave; bụng lại phinh ph&iacute;nh đầy, h&igrave;nh như bụng hơi đau. C&ograve;n h&igrave;nh như g&igrave; nữa, th&ocirc;i đ&uacute;ng đau bụng rồi. Đau thật, đau mỗi l&uacute;c một dữ! N&oacute; cứ cuồn cuộn l&ecirc;n. &Agrave; m&agrave; trời l&agrave;nh lạnh. Hễ c&oacute; gi&oacute; th&igrave; kinh kinh. Mỗi lần gi&oacute;, hắn rươn rướn người. Hắn muốn đứng l&ecirc;n. Sao đầu nặng qu&aacute; m&agrave; ch&acirc;n th&igrave; bẩy rẩy. Mắt hắn hoa l&ecirc;n. Bụng quặn lại, đau g&ograve; ngườI. Hắn oẹ. Hắn oẹ ba bốn c&aacute;i. Oẹ m&atilde;i. Gi&aacute; mửa ra được th&igrave; dễ chịu. Hắn cho một ng&oacute;n tay v&agrave;o m&oacute;c họng. Hắn oẹ ra một c&aacute;i to hơn, ruột h&igrave;nh như lộn l&ecirc;n. Nhưng cũng chỉ nhổ ra to&agrave;n nước d&atilde;i. Hắn nghỉ một t&iacute; rồi lại cho tay v&agrave;o mồm. Lần n&agrave;y th&igrave; mửa được. Trời ơi! Mửa thốc mửa th&aacute;o, mửa ồng ộc, mửa đến cả ruột. Đến nỗi người đ&agrave;n b&agrave; phải dậy. Thị ngồi nhỏm dậy v&agrave; ngơ ng&aacute;c nh&igrave;n. C&aacute;i &oacute;c nặng nề ấy phải l&acirc;u mới nhớ ra v&agrave; l&acirc;u mới hiểu.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">B&acirc;y giờ th&igrave; Ch&iacute; Ph&egrave;o đ&atilde; mửa xong. Hắn mệt qu&aacute;, lại vật người ra đất. Hắn đờ hai con mắt khẽ r&ecirc;n: hắn chỉ c&ograve;n đủ sức để r&ecirc;n khe khẽ. Từ đống mửa bay lại một m&ugrave;i g&igrave; thoảng như m&ugrave;i rượu, hắn bỗng nhi&ecirc;n r&ugrave;ng m&igrave;nh.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thị Nở lại. Đặt một tay l&ecirc;n ngực hắn (thị suy nghĩ đến b&acirc;y giờ mới xong), thị hỏi hắn:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Vừa thổ hả?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Mắt hắn đảo l&ecirc;n nh&igrave;n thị, nh&igrave;n một tho&aacute;ng rồi lại đờ ra ngay.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- &ETH;i v&agrave;o nh&agrave; nh&eacute;?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn l&agrave;m như gật đầu. Nhưng c&aacute;i đầu kh&ocirc;ng động đậy, chỉ c&oacute; c&aacute;i m&iacute; mắt l&agrave; nh&iacute;ch th&ocirc;i.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Th&igrave; đứng l&ecirc;n.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Nhưng hắn đứng l&ecirc;n l&agrave;m sao được. Thị qu&agrave;ng tay v&agrave;o n&aacute;ch hắn, đỡ cho hắn gượng ngồi. Rồi thị k&eacute;o hắn đứng l&ecirc;n. Hắn đu v&agrave;o cổ thị, hai người lảo đảo đi về lều.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Kh&ocirc;ng c&oacute; giường, chỉ c&oacute; một c&aacute;i ch&otilde;ng tre. Thị để hắn nằm l&ecirc;n v&agrave; đi nhặt nhạnh tất cả những manh chiếu r&aacute;ch đắp l&ecirc;n cho hắn. Hắn hết r&ecirc;n. H&igrave;nh như hắn ngủ. Thị cũng lim dim chực ngủ. Nhưng trong nh&agrave; nhiều muỗi qu&aacute;. Muỗi nhắc cho thị c&aacute;i &aacute;o qu&ecirc;n ngo&agrave;i vườn. Thị ra vườn. Đ&ocirc;i lọ nhắc cho thị việc đi k&iacute;n nước, thị mải mốt mặc &aacute;o, k&iacute;n nước, rồi x&aacute;ch đ&ocirc;i lọ nước đi về nh&agrave;.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Trăng chưa lặn, kh&ocirc;ng chừng trời c&ograve;n khuya. Thị l&ecirc;n giường định ngủ. Nhưng nhớ lại việc lạ l&ugrave;ng tối qua. Thị cười. Thị thấy kh&ocirc;ng buồn ngủ, v&agrave; thị cứ lăn ra lăn v&agrave;o.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Khi Ch&iacute; Ph&egrave;o mở mắt th&igrave; trời đ&atilde; s&aacute;ng l&acirc;u. Mặt trời chắc đ&atilde; cao, v&agrave; nắng b&ecirc;n ngo&agrave;i chắc l&agrave; rực rỡ. Cứ nghe chim r&iacute;u r&iacute;t b&ecirc;n ngo&agrave;i l&agrave; đủ biết. Nhưng trong c&aacute;i lều ẩm thấp vẫn chỉ hơi tờ mờ. Ở đ&acirc;y người ta thấy chiều l&uacute;c xế trưa v&agrave; gặp đ&ecirc;m th&igrave; b&ecirc;n ngo&agrave;i trời vẫn s&aacute;ng. Chưa bao giờ Ch&iacute; Ph&egrave;o nhận thấy thế bởi chưa bao giờ hết say.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Nhưng b&acirc;y giờ th&igrave; hắn tỉnh. Hắn b&acirc;ng khu&acirc;ng như tỉnh dậy, hắn thấy miệng đắng, l&ograve;ng mơ hồ buồn. Người th&igrave; bủn rủn, ch&acirc;n tay kh&ocirc;ng buồn nhấc, hay l&agrave; đ&oacute;i rượu, hắn hơi r&ugrave;ng m&igrave;nh. Ruột gan lại n&ocirc;n nao l&ecirc;n một t&iacute;. Hắn sợ rượu cũng như những người ốm sợ cơm. Tiếng chim h&oacute;t ngo&agrave;i kia vui vẻ qu&aacute;! C&oacute; tiếng cười n&oacute;i của những người đi chợ. Anh thuyền ch&agrave;i g&otilde; m&aacute;i ch&egrave;o đuổi c&aacute;.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Những tiếng quen thuộc ấy h&ocirc;m n&agrave;o chả c&oacute;. Nhưng h&ocirc;m nay hắn mới nghe thấy... Chao &ocirc;i l&agrave; buồn!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Vải h&ocirc;m nay b&aacute;n mấy?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- K&eacute;m ba xu d&igrave; ạ.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Thế th&igrave; c&ograve;n ăn thua g&igrave;!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Thật thế đấy. Nhưng chẳng lẽ rằng lại chơi.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Ch&iacute; Ph&egrave;o đo&aacute;n một người đ&agrave;n b&agrave; hỏi một người đ&agrave;n b&agrave; kh&aacute;c đi b&aacute;n vải ở Nam &ETH;ịnh về. Hắn n&ocirc;n nao buồn, l&agrave; v&igrave; mẩu chuyện ấy nhắc cho hắn một c&aacute;i g&igrave; rất xa x&ocirc;i. H&igrave;nh như c&oacute; một thời hắn đ&atilde; ao ước c&oacute; một gia đ&igrave;nh nho nhỏ. Chồng cuốc mướn c&agrave;y thu&ecirc;, vợ dệt vải, ch&uacute;ng lại bỏ một con lợn nu&ocirc;i để l&agrave;m vốn liếng. Kh&aacute; giả th&igrave; mua dăm ba s&agrave;o ruộng l&agrave;m.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Tỉnh dậy hắn thấy gi&agrave; m&agrave; vẫn c&ograve;n c&ocirc; độc. Buồn thay cho đời! C&oacute; l&yacute; n&agrave;o như thế được? Hắn đ&atilde; gi&agrave; rồi hay sao? Ngo&agrave;i bốn mươi tuổi đầu... Dẫu sao, đ&oacute; kh&ocirc;ng phải tuổi m&agrave; người ta mới bắt đầu sửa soạn. Hắn đ&atilde; tới c&aacute;i dốc b&ecirc;n kia của đời. Ở những người như hắn, chịu đựng biết bao nhi&ecirc;u l&agrave; chất độc, đầy đọa cực nhọc m&agrave; chưa bao giờ ốm, một trận ốm c&oacute; thể gọi l&agrave; dấu hiệu b&aacute;o rằng cơ thể đ&atilde; hư hỏng nhiều. N&oacute; l&agrave; một cơn mưa gi&oacute; cuối thu cho biết trời gi&oacute; r&eacute;t, nay m&ugrave;a đ&ocirc;ng đ&atilde; đến. Ch&iacute; Ph&egrave;o h&igrave;nh như đ&atilde; tr&ocirc;ng thấy trước tuổi gi&agrave; của hắn, đ&oacute;i r&eacute;t v&agrave; ốm đau, v&agrave; c&ocirc; độc, c&aacute;i n&agrave;y c&ograve;n đ&aacute;ng sợ hơn đ&oacute;i r&eacute;t v&agrave; ốm đau.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Cũng may Thị Nở v&agrave;o. Nếu thị kh&ocirc;ng v&agrave;o, cứ để hắn vẩn vơ m&atilde;i, th&igrave; đến kh&oacute;c được mất. Thị v&agrave;o cắp một c&aacute;i rổ, trong c&oacute; một nồi g&igrave; đậy vung. Đ&oacute; l&agrave; một nồi ch&aacute;o h&agrave;nh c&ograve;n n&oacute;ng nguy&ecirc;n. L&agrave; v&igrave; l&uacute;c c&ograve;n đ&ecirc;m, thị trằn trọc một l&aacute;t, thị bỗng nhi&ecirc;n nghĩ rằng: c&aacute;i thằng liều lĩnh ấy kể ra th&igrave; đ&aacute;ng thương, c&ograve;n g&igrave; đ&aacute;ng thương bằng đau ốm m&agrave; nằm c&ograve;ng queo một m&igrave;nh. Gi&aacute; thử đ&ecirc;m qua kh&ocirc;ng c&oacute; thị th&igrave; hắn chết. Thị ki&ecirc;u ngạo v&igrave; đ&atilde; cứu sống một người. Thị thấy như y&ecirc;u hắn: đ&oacute; l&agrave; một c&aacute;i l&ograve;ng y&ecirc;u của một người l&agrave;m ơn. Nhưng cũng c&oacute; cả l&ograve;ng y&ecirc;u của một người chịu ơn. Một người như thị Nở c&agrave;ng kh&ocirc;ng thể qu&ecirc;n được. Cho n&ecirc;n thị nghĩ: m&igrave;nh bỏ hắn l&uacute;c n&agrave;y th&igrave; cũng bạc. Dẫu sao cũng đ&atilde; ăn nằm với nhau! Ăn nằm với nhau như &ldquo;vợ chồng&rdquo;. Tiếng &ldquo;vợ chồng&rdquo;, thấy ngường ngượng m&agrave; thinh th&iacute;ch. Đ&oacute; vẫn l&agrave; điều mong muốn &acirc;m thầm của con người khốn nạn ấy chăng. Hay sự kho&aacute;i lạc của x&aacute;c thịt đ&atilde; l&agrave;m nổi dậy những t&iacute;nh t&igrave;nh m&agrave; thị chưa bao giờ biết?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Chỉ biết rằng thị muốn gặp Ch&iacute; Ph&egrave;o, gặp hắn m&agrave; nhắc lại chuyện đ&ecirc;m qua th&igrave; chắc buồn cười lắm. Gớm! Sao lại c&oacute; thứ người đ&acirc;u m&agrave; l&igrave; qu&aacute; thế? Người ta ngồi đấy m&agrave; d&aacute;m x&aacute;n lăn ngay v&agrave;o, n&oacute; chả ngang ngược m&agrave; người ta k&ecirc;u b&eacute;, n&oacute; lại l&agrave;m to hơn. M&agrave; kể th&igrave; cũng ng&ugrave; ngờ. C&aacute;i thằng trời đ&aacute;nh kh&ocirc;ng chết ấy, n&oacute; c&ograve;n sợ ai m&agrave; h&ograve;ng k&ecirc;u. Nhưng m&agrave; đ&aacute;ng kiếp. Thổ trận ấy thật l&agrave; phải biết. Cứ gọi l&agrave; h&ocirc;m nay nhọc đừ. Phải cho hắn ăn t&iacute; g&igrave; mới được. Đang ốm thế th&igrave; chỉ ăn ch&aacute;o h&agrave;nh. Ra được mồ h&ocirc;i th&igrave; l&agrave; nhẹ nh&otilde;m người ngay đ&oacute; m&agrave;... Thế l&agrave; vừa s&aacute;ng th&igrave; đ&atilde; chạy đi t&igrave;m gạo. H&agrave;nh th&igrave; nh&agrave; thị may lại c&ograve;n.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thị nấu bỏ v&agrave;o c&aacute;i rổ, mang ra cho Ch&iacute; Ph&egrave;o.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thằng n&agrave;y rất ngạc nhi&ecirc;n. Hết ngạc nhi&ecirc;n th&igrave; hắn thấy mắt m&igrave;nh h&igrave;nh như ươn ướt. Bởi v&igrave; lần n&agrave;y l&agrave; lần thứ nhất hắn được một người đ&agrave;n b&agrave; cho. Xưa nay, n&agrave;o hắn c&oacute; thấy ai tự nhi&ecirc;n cho c&aacute;i g&igrave;. Hắn vẫn phải doạ nạt hay giật cướp. Hắn phải l&agrave;m cho người ta sợ. Hắn nh&igrave;n b&aacute;t ch&aacute;o bốc kh&oacute;i m&agrave; b&acirc;ng khu&acirc;ng. Thị Nở th&igrave; chỉ nh&igrave;n trộm hắn, rồi lại toe to&eacute;t cười. Tr&ocirc;ng thị thế m&agrave; c&oacute; duy&ecirc;n. T&igrave;nh y&ecirc;u l&agrave;m cho c&oacute; duy&ecirc;n. Hắn thấy vừa vui lại vừa buồn. V&agrave; một c&aacute;i g&igrave; nữa, giống như l&agrave; ăn năn. Cũng c&oacute; thể như thế lắm. Người ta hay ăn năn hối hận về tội &aacute;c khi kh&ocirc;ng đủ sức để &aacute;c nữa. Thị Nở giục hắn ăn n&oacute;ng. Hắn cầm lấy b&aacute;t ch&aacute;o đưa l&ecirc;n mồm. Trời ơi ch&aacute;o mới thơm l&agrave;m sao! Chỉ kh&oacute;i x&ocirc;ng v&agrave;o mũi cũng đủ l&agrave;m người nhẹ nh&otilde;m. Hắn h&uacute;p một h&uacute;p v&agrave; nhận ra rằng: những người suốt đời kh&ocirc;ng ăn ch&aacute;o h&agrave;nh kh&ocirc;ng biết rằng ch&aacute;o rất ngon. Nhưng sao lại m&atilde;i đến b&acirc;y giờ hắn mới nếm vị m&ugrave;i ch&aacute;o?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn tự hỏi rồi lại tự trả lời: v&igrave; c&oacute; ai nấu cho ăn đ&acirc;u? M&agrave; c&ograve;n ai nấu cho m&agrave; ăn nữa! &ETH;ời hắn chưa bao giờ được săn s&oacute;c bởi một tay &ldquo;đ&agrave;n b&agrave;&rdquo;. Hắn nhớ đến &ldquo;b&agrave; ta&rdquo;, c&aacute;i con quỷ c&aacute;i hay bắt hắn b&oacute;p ch&acirc;n m&agrave; lại k&ecirc;u b&oacute;p l&ecirc;n tr&ecirc;n, tr&ecirc;n nữa, n&oacute; chỉ nghĩ đến sao cho thỏa n&oacute; chứ c&oacute; y&ecirc;u hắn đ&acirc;u. Hồi ấy hắn hai mươi. Hai mươi tuổi, người ta kh&ocirc;ng l&agrave; đ&aacute;, nhưng cũng kh&ocirc;ng to&agrave;n l&agrave; x&aacute;c thịt. Người ta kh&ocirc;ng th&iacute;ch c&aacute;i g&igrave; người ta khinh. Vả lại bị một con đ&agrave;n b&agrave; gọi đến nh&agrave; m&agrave; b&oacute;p ch&acirc;n! Hắn thấy nhục hơn l&agrave; th&iacute;ch, huống hồi lại sợ. Quả thật, từ khi biết rằng con vợ chủ sai hắn l&agrave;m một việc kh&ocirc;ng ch&iacute;nh đ&aacute;ng, hắn vừa l&agrave;m vừa run. Kh&ocirc;ng l&agrave;m th&igrave; kh&ocirc;ng được: mọi việc trong nh&agrave;, quyền đ&agrave;n b&agrave;. Chứ hắn, hắn c&oacute; l&ograve;ng n&agrave;o đ&acirc;u! &ETH;ến nỗi người đ&agrave;n b&agrave; ph&aacute;t c&aacute;u. B&agrave; thấy xa x&ocirc;i kh&ocirc;ng được, phải l&agrave;m đến nơi. B&agrave; bảo hắn rằng: &ldquo;M&agrave;y thực th&agrave; qu&aacute;! Con trai g&igrave; hai mươi tuổi m&agrave; đ&atilde; như &ocirc;ng gi&agrave;&rdquo;. Hắn vẫn giả vờ kh&ocirc;ng hiểu. B&agrave; lẳng lơ bảo: &ldquo;Chả nhẽ tao gọi m&agrave;y v&agrave;o chỉ để b&oacute;p ch&acirc;n thế n&agrave;y th&ocirc;i ư?...&rdquo; v&agrave; thấy hắn d&ugrave;ng dằng, b&agrave; mắng xơi xơi v&agrave;o mặt. Hắn chỉ thấy nhục chứ y&ecirc;u đương g&igrave;. Kh&ocirc;ng, hắn chưa được một người đ&agrave;n b&agrave; n&agrave;o y&ecirc;u cả. V&igrave; thế m&agrave; b&aacute;t ch&aacute;o h&agrave;nh của thị Nở l&agrave;m hắn suy nghĩ nhiều. Hắn c&oacute; thể t&igrave;m bạn được, sao lại chỉ g&acirc;y kẻ th&ugrave;?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">B&aacute;t ch&aacute;o h&uacute;p xong rồi, thị Nở đỡ lấy b&aacute;t ch&aacute;o v&agrave; m&uacute;c th&ecirc;m b&aacute;t nữa. Hắn thấy m&igrave;nh v&atilde; bao nhi&ecirc;u mồ h&ocirc;i. Mồ h&ocirc;i chảy ra tr&ecirc;n đầu, tr&ecirc;n mặt, những giọt to như giọt nước. Hắn đưa tay &aacute;o quệt ngang một c&aacute;i, quệt mũi cười rồi lại ăn. Hắn c&agrave;ng ăn, mồ h&ocirc;i lại c&agrave;ng nhiều. Thị Nở nh&igrave;n hắn, lắc đầu thương hại. Hắn thấy l&ograve;ng th&agrave;nh trẻ con. Hắn muốn l&agrave;m nũng với thị như với mẹ. &Ocirc;i sao m&agrave; hắn hiền, ai d&aacute;m bảo đ&oacute; l&agrave; thằng Ch&iacute; Ph&egrave;o vẫn đập đầu, rạch mặt m&agrave; đ&acirc;m ch&eacute;m người? &ETH;&oacute; l&agrave; c&aacute;i bản t&iacute;nh của hắn, ng&agrave;y thường bị lấp đi. Hay trận ốm thay đổi hẳn về sinh l&yacute;, cũng thay đổi cả t&acirc;m l&yacute; nữa? Những người yếu đuối vẫn hay hiền l&agrave;nh. Muốn &aacute;c, phải l&agrave; kẻ mạnh. Hắn đ&acirc;u c&ograve;n mạnh nữa. V&agrave; c&oacute; l&uacute;c hắn ngẫm m&igrave;nh m&agrave; lo. Xưa nay hắn chỉ sống bằng giật cướp v&agrave; dọa nạt. Nếu kh&ocirc;ng c&ograve;n sức m&agrave; giật cướp, dọa nạt nữa th&igrave; sao? &ETH;&atilde; đ&agrave;nh, hắn chỉ mạnh v&igrave; liều. Nhưng hắn mơ hồ thấy rằng sẽ c&oacute; một l&uacute;c m&agrave; người ta kh&ocirc;ng thể liều lĩnh được nữa. Bấy giờ mới nguy! Trời ơi! Hắn th&egrave;m lương thiện, hắn muốn l&agrave;m h&ograve;a với mọi người biết bao! Thị Nở sẽ mở đường cho hắn. Thị c&oacute; thể sống y&ecirc;n ổn với hắn th&igrave; sao người kh&aacute;c lại kh&ocirc;ng thể được. Họ sẽ nhận lại hắn v&agrave;o c&aacute;i x&atilde; hội bằng phẳng, th&acirc;n thiện của những người lương thiện... Hắn băn khoăn nh&igrave;n thị Nở, như thăm d&ograve;. Thị vẫn im lặng, cười tin cẩn, hắn thấy tự nhi&ecirc;n nhẹ người. Hắn bảo thị:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Gi&aacute; cứ thế n&agrave;y m&atilde;i th&igrave; th&iacute;ch nhỉ?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thị kh&ocirc;ng đ&aacute;p, nhưng c&aacute;i mũi đỏ của thị như c&agrave;ng bạnh ra. Hắn thấy thế cũng kh&ocirc;ng c&oacute; g&igrave; l&agrave; xấu. Bằng một c&aacute;i giọng n&oacute;i v&agrave; một vẻ mặt rất phong t&igrave;nh theo &yacute; hắn, hắn bảo thị:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Hay m&igrave;nh sang đ&acirc;y ở với tớ một nh&agrave; cho vui.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thị lườm hắn. Một người thật xấu khi y&ecirc;u cũng lườm. Hắn th&iacute;ch ch&iacute; khanh kh&aacute;ch cười. L&uacute;c tỉnh t&aacute;o, hắn cười nghe thật hiền. Thị Nở lấy l&agrave;m bằng l&ograve;ng lắm. Bấy giờ mấy b&aacute;t ch&aacute;o &yacute; chừng đ&atilde; ngấm. Hắn thấy l&ograve;ng rất vui. Hắn bẹo thị Nở một c&aacute;i l&agrave;m thị nẩy hẳn người l&ecirc;n. V&agrave; hắn cười, hắn lại bảo:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- &ETH;ằng ấy c&ograve;n nhớ g&igrave; h&ocirc;m qua kh&ocirc;ng?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thị ph&aacute;t khẽ hắn một c&aacute;i, l&agrave;m vẻ kh&ocirc;ng ưa đ&ugrave;a. Sao m&agrave; e lệ thế. Xấu m&agrave; e lệ th&igrave; cũng đ&aacute;ng y&ecirc;u. Hắn cười ngất, v&agrave; muốn l&agrave;m thị thẹn th&ugrave;ng hơn nữa, hắn v&eacute;o thị một c&aacute;i thật đau v&agrave;o đ&ugrave;i. Lần n&agrave;y th&igrave; kh&ocirc;ng những thị nẩy người. Thị k&ecirc;u l&ecirc;n choe cho&eacute;. Thị nắm cổ hắn m&agrave; gi&uacute;i xuống. Ch&uacute;ng tỏ t&igrave;nh với nhau, kh&ocirc;ng cần đến những c&aacute;i h&ocirc;n. Ai lại h&ocirc;n, khi c&oacute; những c&aacute;i m&ocirc;i nứt nẻ như bờ ruộng v&agrave;o kỳ đại hạn v&agrave; c&aacute;i mặt rạch ngang dọc như mặt thớt. Vả lại, c&oacute; những c&aacute;ch &acirc;u yếm b&igrave;nh d&acirc;n hơn, ch&uacute;ng cấu v&eacute;o hoặc ph&aacute;t nhau... thiết thực biết mấy...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Ch&uacute;ng sẽ l&agrave;m th&agrave;nh một cặp rất xứng đ&ocirc;i. Ch&uacute;ng cũng nhận thấy thế, v&agrave; nhất định l&agrave; lấy nhau. Như thế năm ng&agrave;y chẵn, thị ở nh&agrave; hắn cả ng&agrave;y lẫn đ&ecirc;m, trừ những l&uacute;c đi kiếm tiền. Hắn kh&ocirc;ng c&ograve;n kinh rượu nhưng cố uống cho thật &iacute;t. &ETH;ể cho khỏi tốn tiền, nhưng nhất l&agrave; để tỉnh t&aacute;o để y&ecirc;u nhau. &ETH;&agrave;n b&agrave; kh&ocirc;ng men như rượu nhưng cũng l&agrave;m người say. V&agrave; hắn say thị lắm. Nhưng thị lại l&agrave; người dở hơi. &ETH;ến h&ocirc;m thứ s&aacute;u th&igrave; thị bỗng nhớ rằng thị c&oacute; một người c&ocirc; ở đời. Người c&ocirc; ấy nội ng&agrave;y nay sẽ về. Thị nghĩ bụng: h&atilde;y đừng y&ecirc;u để hỏi c&ocirc; thị đ&atilde;.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thấy thị hỏi, b&agrave; gi&agrave; kia bật cười. B&agrave; tưởng ch&aacute;u b&agrave; n&oacute;i đ&ugrave;a. Nhưng chợt nhớ rằng ch&aacute;u b&agrave; vốn dở hơi. B&agrave; bỗng trở n&ecirc;n hoảng hốt. B&agrave; nhục cho &ocirc;ng cha nh&agrave; b&agrave;. Cũng c&oacute; lẽ b&agrave; tủi th&acirc;n b&agrave;. B&agrave; nghĩ đến c&aacute;i đời dằng dặc của b&agrave;, kh&ocirc;ng c&oacute; chồng. B&agrave; thấy chua x&oacute;t lắm. B&agrave; uất ức, uất ức với ai kh&ocirc;ng biết. Nhưng rồi đổ c&aacute;i uất ức ngay l&ecirc;n ch&aacute;u b&agrave;! Người đ&agrave;n b&agrave; đức hạnh ấy thấy ch&aacute;u b&agrave; sao m&agrave; đĩ thế! Thật đốn mạt. Ngo&agrave;i ba mươi tuổi, m&agrave; chưa tr&oacute;t đời. Ngo&agrave;i ba mươi... ai lại c&ograve;n đi lấy chồng. Ai đời lại c&ograve;n đi lấy chồng! Ừ! M&agrave; c&oacute; lấy th&igrave; lấy ai chứ?... &ETH;&agrave;n &ocirc;ng chết hết cả rồi sao, m&agrave; lại đ&acirc;m đầu đi lấy một thằng kh&ocirc;ng cha. Ai lại đi lấy thằng chỉ c&oacute; một nghề l&agrave; đi rạch mặt ra ăn vạ. Trời ơi! Nhục nh&atilde; ơi l&agrave; nhục nh&atilde;! Hỡi &ocirc;ng cha nh&agrave; b&agrave;! B&agrave; g&agrave;o l&ecirc;n như con mẹ dại. B&agrave; xỉa x&oacute;i v&agrave;o mặt c&aacute;i con ch&aacute;u ba mươi tuổi m&agrave; chưa tr&oacute;t đời. B&agrave; bảo phắt n&oacute;:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- &ETH;&atilde; nhịn được đến bằng n&agrave;y tuổi th&igrave; nhịn hẳn; ai lại đi lấy thằng Ch&iacute; Ph&egrave;o!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thị nghe thấy thế m&agrave; lộn ruột. Nhưng thị biết c&atilde;i b&agrave; l&agrave;m sao? Con người ấy c&oacute; quyền n&oacute;i thế, bởi con người ấy năm mươi tuổi rồi c&ograve;n ai lấy chồng. Thị biết c&atilde;i l&agrave;m sao. M&agrave; kh&ocirc;ng c&atilde;i được th&igrave; giận dữ nổi l&ecirc;n đ&ugrave;ng đ&ugrave;ng. Thị tức lắm! Thị tức lắm! Thị cần đổ c&aacute;i tức ấy l&ecirc;n một người. Thị lon ton chạy sang nh&agrave; nh&acirc;n ng&atilde;i. Thị thấy hắn đang uống rượu, v&agrave; vừa uống vừa lầm bầm chửi thị về nh&agrave; l&acirc;u. Hắn kh&ocirc;ng quen đợi; bởi phải đợi, hắn lại l&ocirc;i rượu m&agrave; uống cho đỡ buồn. Uống v&agrave;o th&igrave; phải chửi, quen mồm rồi! Nhưng thị l&agrave;m g&igrave; m&agrave; hắn chửi? M&agrave; hắn c&oacute; quyền g&igrave; chửi thị? Ồ, thị đi&ecirc;n l&ecirc;n mất! Thị dẫm ch&acirc;n xuống đất, rồi lại nhảy cẫng l&ecirc;n như thượng đồng. Hắn th&uacute; vị qu&aacute;, lắc lư c&aacute;i đầu cười. Lại c&ograve;n cười! N&oacute; nhạo thị, trời ơi! Thị đi&ecirc;n l&ecirc;n mất, trời ơi l&agrave; trời! Thị chống hai tay v&agrave;o h&aacute;ng, v&ecirc;nh v&ecirc;nh c&aacute;i mặt, v&agrave; tớn c&aacute;i m&ocirc;i vĩ đại l&ecirc;n, tr&uacute;t v&agrave;o mặt hắn tất cả những lời b&agrave; c&ocirc;. Hắn nghĩ ngợi một t&iacute; rồi h&igrave;nh như hiểu. Hắn bỗng nhi&ecirc;n ngẩn người. Tho&aacute;ng một c&aacute;i, hắn lại như h&iacute;t h&iacute;t thấy c&aacute;i hơi ch&aacute;o h&agrave;nh. Hắn cứ ngồi ngẩn mặt kh&ocirc;ng n&oacute;i g&igrave;. Thị tr&uacute;t giận xong rồi. C&aacute;i mũi đỏ dị xuống rồi lại bạnh ra. Thị hả h&ecirc; lắm lắm. Thị ngoay ngo&aacute;y c&aacute;i m&ocirc;ng đ&iacute;t ra về. Hắn sửng sốt đứng l&ecirc;n gọi lại. Ai m&agrave; th&egrave;m lại! C&ograve;n muốn l&ocirc;i th&ocirc;i c&aacute;i g&igrave;? Hắn đuổi theo thị, nắm lấy tay thị. Thị gạt ra, lại gi&uacute;i th&ecirc;m cho một c&aacute;i. Hắn lăn kho&egrave;o xuống s&acirc;n. &ETH;&atilde; lăn ra th&igrave; hắn phải k&ecirc;u: bao giờ chả thế. Hắn nhặt một h&ograve;n gạch toan đập đầu. Nhưng h&igrave;nh như hắn chưa thật say. V&igrave; hắn nghĩ đập đầu ở đ&acirc;y chỉ thiệt; đập đầu ở đ&acirc;y để m&agrave; ăn vạ ai? Hắn phải tự đến c&aacute;i nh&agrave; con đĩ Nở kia. &ETH;ến để đ&acirc;m chết cả nh&agrave; n&oacute;, đ&acirc;m chết c&aacute;i con khọm gi&agrave; nh&agrave; n&oacute;. Nếu kh&ocirc;ng đ&acirc;m được, l&uacute;c ấy h&atilde;y đập đầu k&ecirc;u l&agrave;ng. Muốn đập đầu, phải uống thật say. Kh&ocirc;ng c&oacute; rượu, lấy g&igrave; l&agrave;m cho m&aacute;u n&oacute; chảy? Phải uống th&ecirc;m chai nữa. V&agrave; hắn uống. Nhưng tức qu&aacute;, c&agrave;ng uống c&agrave;ng tỉnh ra. Tỉnh ra, chao ơi, buồn! Hơi rượu kh&ocirc;ng sặc sụa. Hắn cứ thoang thoảng thấy hơi ch&aacute;o h&agrave;nh. Hắn &ocirc;m mặt kh&oacute;c rưng rức. Rồi lại uống. Hắn ra đi với một con dao ở thắt lưng. Hắn lảm nhảm: &ldquo;Tao phải đ&acirc;m chết n&oacute;!&rdquo;. Nhưng hắn lại cứ thẳng đường m&agrave; đi. C&aacute;i g&igrave; đ&atilde; l&agrave;m hắn qu&ecirc;n rẽ v&agrave;o nh&agrave; thị Nở? Những thằng đi&ecirc;n v&agrave; những thằng say rượu kh&ocirc;ng bao giờ l&agrave;m những c&aacute;i m&agrave; l&uacute;c ra đi ch&uacute;ng định l&agrave;m.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Trời nắng lắm, n&ecirc;n đường vắng. Hắn cứ đi, cứ chửi, cứ dọa giết &ldquo;n&oacute;&rdquo;, v&agrave; cứ đi. B&acirc;y giờ đến ng&otilde; nh&agrave; cụ B&aacute;. Hắn x&ocirc;ng x&ocirc;ng đi v&agrave;o. Cả nh&agrave; đi l&agrave;m đồng vắng, chỉ c&oacute; m&igrave;nh cụ B&aacute; đang nằm nghỉ trưa. Nghe tiếng hắn, cụ thấy sao bực m&igrave;nh qu&aacute;! Ch&iacute;nh thật th&igrave; cụ cũng đang bực m&igrave;nh. Bởi v&igrave; cụ thấy đầu hơi nhức. Cụ đang muốn c&oacute; một b&agrave;n tay man m&aacute;t xoa c&aacute;i đầu. Cũng c&oacute; lẽ, cụ chỉ muốn b&agrave; Tư đừng đi l&acirc;u qu&aacute; thế. Đi l&acirc;u thế, kh&ocirc;ng biết rằng đi đ&acirc;u? Sao b&agrave; ấy c&ograve;n trẻ qu&aacute;! Gần bốn mươi rồi m&agrave; tr&ocirc;ng c&ograve;n ph&acirc;y ph&acirc;y. C&ograve;n ph&acirc;y ph&acirc;y qu&aacute; đi nữa! Cụ năm nay đ&atilde; ngo&agrave;i s&aacute;u mươi. Gi&agrave; yếu qu&aacute;, nghĩ m&agrave; chua x&oacute;t. Gi&agrave; thế th&igrave; b&agrave; ấy chỉ cũng gi&agrave; đi cho xong. B&agrave; ấy lại cứ trẻ, cứ ph&acirc;y ph&acirc;y, cứ đẹp như mới ngo&agrave;i hai mươi tuổi, m&agrave; sao đa t&igrave;nh. Nh&igrave;n th&igrave; th&iacute;ch nhưng tưng tức lạ. Kh&aacute;c g&igrave; nhai miếng thịt b&ograve; lựt sựt khi rụng gần hết răng. Mắt b&agrave;, miệng b&agrave; c&oacute; duy&ecirc;n, nhưng tr&ocirc;ng đĩ lắm. Hơi một t&iacute; l&agrave; cười toe to&eacute;t, t&iacute;t cả mắt lại, c&aacute;i m&aacute; th&igrave; h&acirc;y h&acirc;y. M&agrave; thấy gh&eacute;t những thằng trai trẻ, gi&aacute; l&agrave;m con b&agrave; kh&ocirc;ng đ&aacute;ng, m&agrave; thấy đ&acirc;u cũng đ&ugrave;a. Ch&uacute;ng đ&ugrave;a nhạt như nước ốc, chỉ được c&aacute;i th&ocirc; tục, m&agrave; gặp ai đ&acirc;u cũng cười! Chẳng nghĩ đến địa vị cả, người đ&acirc;u m&agrave; v&ocirc; t&acirc;m! Tức lạ! Cụ chỉ muốn cho tất cả những thằng trai trẻ đi t&ugrave;... Những l&uacute;c như thế, th&igrave; một người dẫu kh&ocirc;n ngoan cũng kh&ocirc;ng b&igrave;nh tĩnh được. Nhất l&agrave; khi tr&ocirc;ng thấy một thằng chỉ đến v&ograve;i tiền uống rượu như Ch&iacute; Ph&egrave;o. Tuy vậy, cụ cũng m&oacute;c sẵn năm h&agrave;o. Th&agrave; m&oacute;c sẵn để tống n&oacute; đi cho ch&oacute;ng. Nhưng m&oacute;c rồi, cụ cũng phải qu&aacute;t một c&acirc;u cho nhẹ người:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Ch&iacute; Ph&egrave;o đấy hở ? L&egrave; b&egrave; vừa th&ocirc;i chứ, t&ocirc;i kh&ocirc;ng phải l&agrave; c&aacute;i kho.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Rồi n&eacute;m bẹt năm h&agrave;o xuống đất, cụ bảo hắn:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Cầm lấy m&agrave; c&uacute;t đi cho rảnh. Rồi l&agrave;m m&agrave; ăn chứ cứ b&aacute;o người ta m&atilde;i &agrave;?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn trợn mắt chỉ tay v&agrave;o mặt cụ:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Tao kh&ocirc;ng đến đ&acirc;y xin năm h&agrave;o.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thấy hắn toan l&agrave;m dữ cụ đ&agrave;nh dịu giọng:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Th&ocirc;i, cầm lấy vậy, t&ocirc;i kh&ocirc;ng c&ograve;n hơn.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn v&ecirc;nh c&aacute;i mặt l&ecirc;n, rất ki&ecirc;u ngạo:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Tao đ&atilde; bảo l&agrave; tao kh&ocirc;ng đ&ograve;i tiền.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Giỏi! H&ocirc;m nay mới thấy anh kh&ocirc;ng đ&ograve;i tiền. Thế anh cần g&igrave;?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn d&otilde;ng dạc:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Tao muốn l&agrave;m người lương thiện!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">B&aacute; Kiến cười ha hả:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Ồ tưởng g&igrave;! T&ocirc;i chỉ cần anh lương thiện cho thi&ecirc;n hạ nhờ.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn lắc đầu:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Kh&ocirc;ng được! Ai cho tao lương thiện? L&agrave;m thế n&agrave;o cho mất được những vết mảnh chai tr&ecirc;n mặt n&agrave;y? Tao kh&ocirc;ng thể l&agrave; người lương thiện nữa. Biết kh&ocirc;ng? Chỉ c&oacute; một c&aacute;ch... biết kh&ocirc;ng! Chỉ c&oacute; một c&aacute;ch l&agrave;... c&aacute;i n&agrave;y biết kh&ocirc;ng?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Hắn r&uacute;t dao ra x&ocirc;ng v&agrave;o. B&aacute; Kiến ngồi nhỏm dậy, Ch&iacute; Ph&egrave;o đ&atilde; văng dao tới rồi. B&aacute; Kiến chỉ kịp k&ecirc;u một tiếng. Ch&iacute; Ph&egrave;o vừa ch&eacute;m t&uacute;i bụi vừa k&ecirc;u l&agrave;ng thật to. Hắn k&ecirc;u l&agrave;ng, kh&ocirc;ng bao giờ người ta vội đến. Bởi thế khi người ta đến th&igrave; hắn cũng đang giẫy đ&agrave;nh đạch ở giữa bao nhi&ecirc;u l&agrave; m&aacute;u tươi. Mắt hắn trợn ngược. Mồm hắn ng&aacute;p ng&aacute;p, muốn n&oacute;i, nhưng kh&ocirc;ng ra tiếng. Ở cổ hắn, thỉnh thoảng m&aacute;u vẫn c&ograve;n ứ ra.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Cả l&agrave;ng Vũ &ETH;ại nhao l&ecirc;n. Họ b&agrave;n t&aacute;n rất nhiều về vụ &aacute;n kh&ocirc;ng ngờ ấy. C&oacute; nhiều kẻ mừng thầm. Kh&ocirc;ng thiếu kẻ mừng ra mặt. C&oacute; người n&oacute;i xa x&ocirc;i: &ldquo;Trời c&oacute; mắt đấy, anh em ạ!&rdquo;. Người kh&aacute;c th&igrave; n&oacute;i toạc ra: &ldquo;Thằng n&agrave;o chứ hai thằng ấy chết th&igrave; kh&ocirc;ng ai tiếc! R&otilde; thật bọn ch&uacute;ng n&oacute; giết nhau, n&agrave;o c&oacute; phải cần đến tay người kh&aacute;c đ&acirc;u&rdquo;. Mừng nhất l&agrave; bọn h&agrave;o l&yacute; ở trong l&agrave;ng. Họ tu&ocirc;n đến hỏi thăm, nhưng ch&iacute;nh l&agrave; để nh&igrave;n l&yacute; Cường bằng những con mắt thỏa m&atilde;n v&agrave; khi&ecirc;u kh&iacute;ch. &ETH;ội Tảo, kh&ocirc;ng cần k&iacute;n đ&aacute;o, n&oacute;i toang toang ngay ngo&agrave;i chợ, trước mặt bao người: &ldquo;Thằng bố chết, thằng con lớp n&agrave;y kh&ocirc;ng khỏi người ta cho ăn b&ugrave;n&rdquo;. Ai chả hiểu &ldquo;người ta&rdquo; đ&oacute; l&agrave; ch&iacute;nh &ocirc;ng. Bọn đ&agrave;n em th&igrave; b&agrave;n nhỏ: &ldquo;Thằng mọt gi&agrave; ấy chết, anh m&igrave;nh n&ecirc;n ăn mừng&rdquo;. Những người biết điều th&igrave; hay ngờ vực, họ ch&eacute;p miệng n&oacute;i: &ldquo;Tre gi&agrave; măng mọc, thằng ấy chết, c&ograve;n thằng kh&aacute;c, ch&uacute;ng m&igrave;nh cũng chẳng lợi t&iacute; g&igrave; đ&acirc;u...&rdquo;.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">B&agrave; c&ocirc; thị Nở chỉ v&agrave;o tận mặt ch&aacute;u m&agrave; đay nghiến:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Ph&uacute;c đời nh&agrave; m&agrave;y, con nh&eacute;. Chả &ocirc;m lấy &ocirc;ng Ch&iacute; Ph&egrave;o.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Thị cười v&agrave; n&oacute;i lảng:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- H&ocirc;m qua l&agrave;m bi&ecirc;n bản, l&yacute; Cường nghe đ&acirc;u tốn gần một trăm. Thiệt người lại tốn của.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Nhưng thị lại nghĩ thầm:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- Sao c&oacute; l&uacute;c n&oacute; hiền như đất.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">V&agrave; nhớ lại những l&uacute;c ăn nằm với hắn, thị nh&igrave;n trộm b&agrave; c&ocirc;, rồi nh&igrave;n nhanh xuống bụng:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">- N&oacute;i dại, nếu m&igrave;nh chửa, b&acirc;y giờ hắn chết rồi, th&igrave; l&agrave;m ăn thế n&agrave;o?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">&ETH;ột nhi&ecirc;n thị thấy tho&aacute;ng hiện ra một c&aacute;i l&ograve; gạch cũ bỏ kh&ocirc;ng, xa nh&agrave; cửa v&agrave; vắng người qua lại...</span></span></span></span></p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(9, 'Việt Bắc', 'Thơ', 'Tố Hữu', 1954, 'viet-bac.png', 'Đã hoàn', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Việt Bắc&nbsp;l&agrave; kh&uacute;c h&aacute;t &acirc;n t&igrave;nh chung của những người c&aacute;ch mạng v&agrave; của những người kh&aacute;ng chiến, của cả d&acirc;n tộc qua tiếng l&ograve;ng của nh&agrave; thơ. B&ecirc;n cạnh đ&oacute;, b&agrave;i thơ c&ograve;n cất l&ecirc;n được &acirc;m hưởng m&agrave; anh h&ugrave;ng ca vang dội v&agrave; đưa ta về với một thời k&igrave; lịch sử h&agrave;o h&ugrave;ng v&agrave; trọng đại của đất nước.</span></span></p>\r\n', '<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:red\">CHƯƠNG 1:</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:#fcfcfc\"><span style=\"color:black\">M&igrave;nh về m&igrave;nh c&oacute; nhớ ta?<br />\r\nMười lăm năm ấy thiết tha mặn nồng.<br />\r\nM&igrave;nh về m&igrave;nh c&oacute; nhớ kh&ocirc;ng<br />\r\nNh&igrave;n c&acirc;y nhớ n&uacute;i, nh&igrave;n s&ocirc;ng nhớ nguồn?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Tiếng ai tha thiết b&ecirc;n cồn</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">B&acirc;ng khu&acirc;n trong dạ, bồn chồn bước đi</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&Aacute;o ch&agrave;m đưa buổi ph&acirc;n ly</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Cầm tay nhau biết n&oacute;i g&igrave; h&ocirc;m nay&hellip;</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">M&igrave;nh đi c&oacute; nhớ những ng&agrave;y</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Mưa nguồn suối lũ, những m&acirc;y c&ugrave;ng m&ugrave;</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">M&igrave;nh về, c&oacute; nhớ chiến khu</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Miếng cơm chấm muối, mối th&ugrave; nặng vai</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">M&igrave;nh về rừng n&uacute;i nhớ ai</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Tr&aacute;m b&ugrave;i để rụng, măng mai để gi&agrave;</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">M&igrave;nh đi, c&oacute; nhớ những nh&agrave;</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Hắt hiu lau x&aacute;m, đậm đ&agrave; l&ograve;ng son</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">M&igrave;nh về c&oacute; nhớ n&uacute;i non</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Nhớ khi kh&aacute;n Nhật, thuở c&ograve;n Việt Minh</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">M&igrave;nh đi, m&igrave;nh c&oacute; nhớ m&igrave;nh</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">T&acirc;n Tr&agrave;o, Hồng Th&aacute;i, m&aacute;i đ&igrave;nh c&acirc;y đa?</span></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:red\">CHƯƠNG 2:</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Ta với m&igrave;nh, m&igrave;nh với ta</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">L&ograve;ng ta sau trước mặn m&agrave; đinh đinh</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">M&igrave;nh đi, m&igrave;nh lại nhớ m&igrave;nh</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Nguồn bao nhi&ecirc;u nước nghĩa t&igrave;nh bấy nhi&ecirc;u&hellip;</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Nhớ g&igrave; như nhớ người y&ecirc;u</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Trăng l&ecirc;n đầu n&uacute;i, nắng chiều lưng nương</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Nhớ từng bản kh&oacute;i c&ugrave;ng sương</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Sớm khuya bếp lửa người thương đi về</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Nhớ từng rừng nứa bờ tre</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Ng&ograve;i Thia s&ocirc;ng Đ&aacute;y, suối L&ecirc; vơi đầy</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Ta đi, ta nhớ những ng&agrave;y</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">M&igrave;nh đ&acirc;y ta đ&oacute; đắng cay ngọt b&ugrave;i&hellip;</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Thương nhau chia củ sắn l&ugrave;i</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">B&aacute;t cơm sẻ nữa, chăn xui đắp c&ugrave;ng</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Nhớ người mẹ nắng ch&aacute;y lưng</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Địu con l&ecirc;n g&atilde;y bẻ từng bắp ng&ocirc;</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Nhớ sao lớp học I tờ</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Đồng khuya đuốc s&aacute;ng những giờ li&ecirc;n hoan</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Nhớ sao ng&agrave;y nắng cơ quan</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Gian nan đời vẫn ca vang n&uacute;i đ&egrave;o.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Nhớ sao tiếng m&otilde; rừng chiều</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">&nbsp;Ch&agrave;y đ&ecirc;m nện cối đều đều suối xa&hellip;</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Ta về, m&igrave;nh c&oacute; nhớ ta</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Ta về ta nhớ những hoa c&ugrave;ng người</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Rừng xanh hoa chuối đỏ tươi</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Đ&egrave;o cao &aacute;nh nắng dao g&agrave;i thắt lưng.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Ng&agrave;y xu&acirc;n mơ nở trắng rừng</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Nhớ người đan n&oacute;n chuốt từng sợi giang</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Ve k&ecirc;u rừng ph&aacute;ch đổ v&agrave;ng</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Nhớ c&ocirc; em g&aacute;i h&aacute;i măn một m&igrave;nh</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Rừng thu trăng gọi h&ograve;a b&igrave;nh</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Nhớ ai tiếng h&aacute;t &acirc;n t&igrave;nh thủy chung.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Nhớ khi giặt đến giặt l&ugrave;ng</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Rừng c&acirc;y n&uacute;i đ&aacute; ta c&ugrave;ng đ&aacute;nh t&acirc;y</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">N&uacute;i giăng th&agrave;nh lũy sắt d&agrave;y</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Rừng che bộ đội rừng v&acirc;y qu&acirc;n th&ugrave;</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">M&ecirc;nh m&ocirc;ng bốn mặt sương m&ugrave;</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Đất trời ta cả chiến khu một l&ograve;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Ai về ai c&oacute; nhớ kh&ocirc;ng?</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Ta về ta nhớ Phủ Th&ocirc;ng, đ&egrave;o Gi&agrave;ng</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Nhớ s&ocirc;ng L&ocirc;, nhớ phố R&agrave;ng</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Nhớ từ Cao-Lạng, nhớ sang Nhị H&agrave;&hellip;</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Những đường Việt Bắt của ta</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Đ&ecirc;m đ&ecirc;m rầm rập như l&agrave; đất rung</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Qu&acirc;n đi điệp điệp tr&ugrave;ng tr&ugrave;ng</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">&Aacute;nh sao đầu s&uacute;ng bạn c&ugrave;ng mũ nan</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">D&acirc;n c&ocirc;ng đỏ đuốc từng đo&agrave;n</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Bước ch&acirc;n ta n&aacute;t, mu&ocirc;n t&agrave;n lửa bay.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Ngh&igrave;n đ&ecirc;m thăm thẳm sương d&agrave;y</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Đ&egrave;n pha bật s&aacute;ng như ng&agrave;y mai l&ecirc;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Tin vui chiến thắng trăm miền</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">H&ograve;a B&igrave;nh, T&acirc;y Bắc, Điện bi&ecirc;n vui về</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Vui từ Đồng Th&aacute;p, An Kh&ecirc;</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Vui l&ecirc;n Việt Bắt, đ&egrave;o Đe, n&uacute;i Hồng.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Ai về ai c&oacute; nhớ kh&ocirc;ng?</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Ngọn cờ đỏ thắm gi&oacute; lồng cửa hang.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Nắng trưa rực rỡ sao v&agrave;ng</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Trung ương, ch&iacute;nh phủ luận b&agrave;n việc c&ocirc;ng</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Điều qu&acirc;n chiến dịch Thu Đ&ocirc;ng</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">N&ocirc;ng th&ocirc;n ph&aacute;t động, giao th&ocirc;ng mở đường</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Giữ đ&ecirc;, ph&ograve;ng hạn, thu lương</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Gửi dao miền ngược, th&ecirc;m đường c&aacute;c khu&hellip;</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Ở đ&acirc;u u &aacute;m qu&acirc;n th&ugrave;</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Nh&igrave;n l&ecirc;n Việt Bắc: cụ Hồ s&aacute;ng soi</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Ở đ&acirc;u đai đớn giống n&ograve;i </span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Tr&ocirc;ng về Việt Bắc m&agrave; nu&ocirc;i ch&iacute; bền.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Mười lăm năm ấy ai qu&ecirc;n</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Qu&ecirc; hương c&aacute;ch mạng dựng l&ecirc;n cộng h&ograve;a</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">M&igrave;nh về m&igrave;nh c&oacute; nhớ ta</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">M&aacute;i đ&igrave;nh Hồng Th&aacute;i c&acirc;y đa T&acirc;n Tr&agrave;o.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">[&hellip;]</span></span></span></p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(10, 'Hoàng tử bé', 'Văn học nước ngoài', 'Antoine de Saint Exupéry', 1943, 'hoang-tu-be.jpg', 'Đã hoàn', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>Ho&agrave;ng tử b&eacute;</strong>&nbsp;l&agrave; c&acirc;u chuyện kể về một phi c&ocirc;ng phải hạ c&aacute;nh khẩn cấp trong sa mạc. Anh gặp một cậu b&eacute;, người h&oacute;a ra l&agrave; một ho&agrave;ng tử từ h&agrave;nh tinh kh&aacute;c đến. Ho&agrave;ng tử kể về những cuộc phi&ecirc;u lưu của em tr&ecirc;n Tr&aacute;i Đất v&agrave; về b&ocirc;ng hồng qu&iacute; gi&aacute; tr&ecirc;n h&agrave;nh tinh của em. Em thất vọng khi ph&aacute;t hiện ra hoa hồng l&agrave; lo&agrave;i b&igrave;nh thường như bao lo&agrave;i kh&aacute;c tr&ecirc;n Tr&aacute;i Đất. Một con c&aacute;o sa mạc khuy&ecirc;n em n&ecirc;n y&ecirc;u thương ch&iacute;nh b&ocirc;ng hồng của em v&agrave; h&atilde;y t&igrave;m kiếm trong đ&oacute; &yacute; nghĩa của cuộc đời m&igrave;nh. Nhận ra điều ấy, ho&agrave;ng tử quay trở về h&agrave;nh tinh của em.</span></span></p>\r\n', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>I</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Hồi l&ecirc;n s&aacute;u, c&oacute; lần t&ocirc;i đ&atilde; nh&igrave;n thấy một bức tranh tuyệt đẹp trong một cuốn s&aacute;ch n&oacute;i về Rừng hoang nhan đề &quot;Những chuyện c&oacute; thật&quot;. N&oacute; vẽ một con trăn đang nuốt một con th&uacute;. Đ&acirc;y l&agrave; bản sao của bức tranh đ&oacute;. Người ta n&oacute;i trong s&aacute;ch: &quot;Con trăn nuốt chửng cả con mồi m&agrave; kh&ocirc;ng nhai. Sau đ&oacute; n&oacute; kh&ocirc;ng thể nh&uacute;c nh&iacute;ch được nữa v&agrave; n&oacute; nằm ngủ s&aacute;u th&aacute;ng liền trong khi chờ ti&ecirc;u ho&aacute;.&quot; Từ đ&oacute; t&ocirc;i hay nghĩ đến c&aacute;c cuộc phi&ecirc;u lưu trong rừng rậm, v&agrave; đến lượt t&ocirc;i, với một c&acirc;y b&uacute;t ch&igrave; m&agrave;u, t&ocirc;i đ&atilde; vẽ được bức ph&aacute;c thảo đầu ti&ecirc;n. Bức ph&aacute;c thảo đầu tiền của t&ocirc;i. N&oacute; như thế n&agrave;y: T&ocirc;i đem khoe kiệt t&aacute;c của m&igrave;nh với những người lớn v&agrave; hỏi họ rằng n&oacute; c&oacute; l&agrave;m họ kinh h&atilde;i kh&ocirc;ng. Họ trả lời: &quot;Sao lại phải sợ một c&aacute;i mũ chứ?&quot; Bức vẽ của t&ocirc;i kh&ocirc;ng vẽ một c&aacute;i mũ. N&oacute; vẽ một con trăn đang nằm chờ ti&ecirc;u ho&aacute; một con voi. Thế l&agrave; t&ocirc;i phải vẽ ph&iacute;a trong của con trăn, để cho người lớn c&oacute; thể hiểu. Người lớn l&uacute;c n&agrave;o cũng cần phải c&oacute; giải th&iacute;ch. Bức ph&aacute;c thảo thứ hai của t&ocirc;i n&oacute; như thế n&agrave;y:&nbsp;Những người lớn b&egrave;n khuy&ecirc;n t&ocirc;i n&ecirc;n g&aacute;c sang một b&ecirc;n c&aacute;c bức vẽ trăn k&iacute;n v&agrave; trăn mở kia v&agrave; n&ecirc;n ch&uacute; t&acirc;m học địa l&yacute;, sử k&yacute;, t&iacute;nh to&aacute;n v&agrave; văn phạm. T&ocirc;i đ&atilde; bỏ dở như vậy đ&oacute;, v&agrave;o năm l&ecirc;n s&aacute;u, một sự nghiệp hội hoạ tuyệt vời. T&ocirc;i bị thất vọng v&igrave; sự thất bại của bản thảo số một v&agrave; số hai. Những người lớn chẳng bao giờ tự họ hiểu được c&aacute;i g&igrave; cả, v&agrave; thật l&agrave; mệt cho trẻ con l&uacute;c n&agrave;o cũng phải giải th&iacute;ch cho họ. Vậy l&agrave; t&ocirc;i phải chọn nghề kh&aacute;c, v&agrave; t&ocirc;i học l&aacute;i m&aacute;y bay. T&ocirc;i đ&atilde; bay khắp thế giới mỗi nơi một t&iacute;. V&agrave; m&ocirc;n địa l&yacute;, đ&uacute;ng như vậy, đ&atilde; gi&uacute;p t&ocirc;i rất nhiều. T&ocirc;i biết l&agrave;m thế n&agrave;o để chỉ nh&igrave;n qua một c&aacute;i l&agrave; ph&acirc;n biệt được ngay Trung Quốc với Arizona. C&aacute;i đ&oacute; thật l&agrave; &iacute;ch lợi nếu như người ta bay lạc đường trong đ&ecirc;m tối. T&ocirc;i cũng đ&atilde; gặp trong đời t&ocirc;i cả đống những con người nghi&ecirc;m chỉnh. T&ocirc;i đ&atilde; sống nhiều với những người lớn. T&ocirc;i đ&atilde; nh&igrave;n thấy họ rất l&agrave; gần. Nhưng c&aacute;i đ&oacute; chẳng l&agrave;m thay đổi &yacute; kiến của t&ocirc;i bao nhi&ecirc;u. Mỗi l&uacute;c gặp một người lớn c&oacute; vẻ s&aacute;ng sủa một t&iacute;, t&ocirc;i lại thử &ocirc;ng ta bằng bức ph&aacute;c thảo số một m&agrave; t&ocirc;i lu&ocirc;n mang theo. Nhưng lu&ocirc;n lu&ocirc;n &ocirc;ng ta trả lời: &quot;Đấy l&agrave; một c&aacute;i mũ&quot;. Thế l&agrave; t&ocirc;i chẳng th&egrave;m n&oacute;i với &ocirc;ng ta về trăn rắn, rừng hoang hay c&aacute;c v&igrave; sao nữa. T&ocirc;i tự hạ m&igrave;nh xuống ngang tầm &ocirc;ng ta. T&ocirc;i n&oacute;i về chơi b&agrave;i, chơi g&ocirc;n, ch&iacute;nh trị v&agrave; cravate. V&agrave; con người lớn kia cảm thấy h&agrave;i l&ograve;ng v&ocirc; c&ugrave;ng khi được quen một con người biết điều như vậy.<br />\r\n<br />\r\n<span style=\"color:#e74c3c\"><strong>II</strong></span><br />\r\n<br />\r\nT&ocirc;i đ&atilde; sống c&ocirc; đơn như vậy đ&oacute;, chẳng c&oacute; ai để chuyện tr&ograve; thực sự, cho đến khi m&aacute;y bay của t&ocirc;i bị hỏng giữa sa mạc Sahara, c&aacute;ch đ&acirc;y s&aacute;u năm. C&oacute; c&aacute;i g&igrave; đ&oacute; trong động cơ của t&ocirc;i bị g&atilde;y. V&agrave; t&ocirc;i chỉ đi một m&igrave;nh kh&ocirc;ng c&oacute; h&agrave;nh kh&aacute;ch cũng chẳng c&oacute; thợ m&aacute;y, t&ocirc;i phải một m&igrave;nh bắt đầu cuộc sửa chữa kh&oacute; khăn. Đấy l&agrave; vấn đề sống chết với t&ocirc;i. T&ocirc;i chỉ c&oacute; đủ nước để uống trong nhiều nhất l&agrave; t&aacute;m ng&agrave;y. Đ&ecirc;m đầu ti&ecirc;n vậy l&agrave; t&ocirc;i phải ngủ tr&ecirc;n c&aacute;t ở c&aacute;ch xa nơi người ở h&agrave;ng ng&agrave;n dặm. L&uacute;c đ&oacute; t&ocirc;i c&ograve;n c&ocirc; độc hơn cả một kẻ đắm t&agrave;u tr&ecirc;n chiếc b&egrave; l&ecirc;n đ&ecirc;nh giữa biển. Bạn chắc sẽ tưởng tượng ra nỗi kinh ngạc của t&ocirc;i, v&agrave;o l&uacute;c mờ s&aacute;ng, khi một giọng n&oacute;i nhỏ nhẹ ngộ nghĩnh đ&aacute;nh thức t&ocirc;i dậy. C&aacute;i giọng ấy n&oacute;i:<br />\r\n&ndash; Nếu &ocirc;ng vui l&ograve;ng... xin vẽ hộ t&ocirc;i một con cừu!<br />\r\n&ndash; C&aacute;i g&igrave;?<br />\r\n&ndash; Xin vẽ hộ t&ocirc;i một con cừu...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i nhảy dựng l&ecirc;n như l&agrave; bị s&eacute;t đ&aacute;nh. T&ocirc;i dụi mắt thật kỹ. T&ocirc;i đ&atilde; nh&igrave;n thật kỹ. V&agrave; t&ocirc;i thấy một cậu b&eacute; thật kh&aacute;c thường đang nh&igrave;n t&ocirc;i với vẻ nghi&ecirc;m trọng. Đ&acirc;y l&agrave; bức ch&acirc;n dung đẹp nhất m&agrave; về sau t&ocirc;i vẽ được về cậu b&eacute; ấy. Nhưng bức vẽ của t&ocirc;i chắc l&agrave; k&eacute;m đẹp hơn người mẫu nhiều. Kh&ocirc;ng phải lỗi tại t&ocirc;i. T&ocirc;i đ&atilde; bị người lớn l&agrave;m cho nản l&ograve;ng trong sự nghiệp hội hoạ từ hồi s&aacute;u tuổi, v&agrave; t&ocirc;i c&oacute; bao giờ học vẽ c&aacute;i g&igrave; ngo&agrave;i những con trăn kh&eacute;p k&iacute;n v&agrave; những con trăn mở bụng đ&acirc;u.&nbsp;Vậy l&agrave; t&ocirc;i nh&igrave;n c&aacute;i sự hiển hiện đ&oacute; với hai con mắt tr&ograve;n xoe v&igrave; kinh ngạc. Xin nhớ l&agrave; l&uacute;c ấy t&ocirc;i đang ở c&aacute;ch mọi chỗ c&oacute; người h&agrave;ng ng&agrave;n dặm. Thế m&agrave; cậu b&eacute; của t&ocirc;i tr&ocirc;ng chẳng giống như bị lạc đường, chẳng hề mệt lả, chẳng hề đ&oacute;i, chẳng hề kh&aacute;t nước hay sợ h&atilde;i g&igrave; cả. Cậu ta chẳng c&oacute; vẻ g&igrave; của một cậu b&eacute; lạc giữa sa mạc, c&aacute;ch nơi c&oacute; người cả ng&agrave;n dặm. Tới khi mở được miệng, t&ocirc;i hỏi em:<br />\r\n&ndash; Nhưng... em l&agrave;m c&aacute;i g&igrave; ở đ&acirc;y?<br />\r\nThế l&agrave; em lặp lại c&acirc;u n&oacute;i l&uacute;c n&atilde;y, thật nhẹ nh&agrave;ng, như l&agrave; một điều rất quan trọng:<br />\r\n&ndash; Nếu &ocirc;ng vui l&ograve;ng... xin vẽ hộ t&ocirc;i một con cừu...<br />\r\nKhi m&agrave; sự b&iacute; ẩn qu&aacute; lớn, người ta kh&ocirc;ng d&aacute;m kh&ocirc;ng v&acirc;ng lời. D&ugrave; điều n&agrave;y thật l&agrave; v&ocirc; l&yacute; khi t&ocirc;i đang ở c&aacute;ch xa nơi người ở cả ng&agrave;n dặm v&agrave; đang bị nguy đến t&iacute;nh mạng, t&ocirc;i r&uacute;t trong t&uacute;i ra một c&acirc;y b&uacute;t v&agrave; một mảnh giấy. Nhưng t&ocirc;i sực nhớ rằng m&igrave;nh đ&atilde; chỉ học địa l&yacute;, sử k&yacute;, t&iacute;nh to&aacute;n v&agrave; văn phạm, v&agrave; t&ocirc;i n&oacute;i với cậu b&eacute; (c&oacute; vẻ hi kh&oacute; chịu) rằng t&ocirc;i kh&ocirc;ng biết vẽ. Em trả lời t&ocirc;i:<br />\r\n&ndash; Kh&ocirc;ng sao đ&acirc;u. Xin vẽ hộ t&ocirc;i một con cừu.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Bởi v&igrave; t&ocirc;i chưa bao giờ vẽ một con cừu cả n&ecirc;n t&ocirc;i vẽ lại cho em một trong hai bức tranh m&agrave; t&ocirc;i c&oacute; thể vẽ. Đ&oacute; l&agrave; h&igrave;nh con trăn k&iacute;n. V&agrave; t&ocirc;i sững sờ khi nghe cậu b&eacute; trả lời:<br />\r\n&ndash; Kh&ocirc;ng! Kh&ocirc;ng! T&ocirc;i kh&ocirc;ng muốn một con voi trong bụng một con trăn đ&acirc;u. Con trăn nguy hiểm lắm, c&ograve;n con voi th&igrave; qu&aacute; kềnh c&agrave;ng. Chỗ t&ocirc;i b&eacute; lắm. T&ocirc;i cần một con cừu. H&atilde;y vẽ cho t&ocirc;i một con cừu!<br />\r\nThế l&agrave; t&ocirc;i vẽ.&nbsp;<br />\r\nEm nh&igrave;n chăm ch&uacute;, rồi n&oacute;i:<br />\r\n&ndash; Kh&ocirc;ng! Con n&agrave;y ốm qu&aacute;. H&atilde;y vẽ con kh&aacute;c đi.<br />\r\nT&ocirc;i vẽ.&nbsp;<br />\r\nCậu b&eacute; của t&ocirc;i cười nhẹ, giọng khoan dung:<br />\r\n&ndash; &Ocirc;ng thấy đấy... đ&acirc;y kh&ocirc;ng phi con cừu, đ&acirc;y l&agrave; d&ecirc;. N&oacute; c&oacute; sừng...<br />\r\nT&ocirc;i lại vẽ lại lần nữa. Nhưng bức n&agrave;y cũng bị từ chối như c&aacute;c bức trước.<br />\r\n&ndash; Con n&agrave;y gi&agrave; qu&aacute;. T&ocirc;i muốn một con cừu sống thật l&acirc;u.<br />\r\nBấy giờ t&ocirc;i hết ki&ecirc;n nhẫn, bởi sốt ruột muốn bắt đầu th&aacute;o m&aacute;y, t&ocirc;i vẽ nguệch ngoạc bức vẽ n&agrave;y đ&acirc;y.<br />\r\nV&agrave; t&ocirc;i n&oacute;i bừa:<br />\r\n&ndash; Đ&acirc;y l&agrave; c&aacute;i th&ugrave;ng. Con cừu ch&uacute; muốn n&oacute; ở trong ấy đấy.<br />\r\nNhưng t&ocirc;i hết sức ngạc nhi&ecirc;n khi thấy mặt vị quan to&agrave; nhỏ của t&ocirc;i s&aacute;ng r&otilde; l&ecirc;n:<br />\r\n&ndash; Đ&uacute;ng l&agrave; c&aacute;i m&agrave; t&ocirc;i muốn đấy! &Ocirc;ng nghĩ c&oacute; cần nhiều cỏ cho con cừu n&agrave;y kh&ocirc;ng ạ?<br />\r\n&ndash; Sao vậy?<br />\r\n&ndash; V&igrave; chỗ t&ocirc;i b&eacute; lắm...<br />\r\n&ndash; Chắc l&agrave; đủ. T&ocirc;i vẽ cho em một con cừu b&eacute; x&iacute;u ấy m&agrave;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Em c&uacute;i đầu xuống bức vẽ:<br />\r\n&ndash; Kh&ocirc;ng nhỏ lắm đ&acirc;u... K&igrave;a! N&oacute; đ&atilde; ngủ rồi...<br />\r\nV&agrave; như thế đấy, t&ocirc;i đ&atilde; l&agrave;m quen với &ocirc;ng ho&agrave;ng b&eacute; nhỏ.<br />\r\n<br />\r\n<span style=\"color:#e74c3c\"><strong>III</strong></span><br />\r\n<br />\r\nPhải rất l&acirc;u t&ocirc;i mới biết em từ đ&acirc;u đến. &Ocirc;ng ho&agrave;ng b&eacute; nhỏ, vốn rất hay đặt c&acirc;u hỏi cho t&ocirc;i, lại dường như kh&ocirc;ng bao giờ ch&uacute; &yacute; nghe t&ocirc;i hỏi. Chỉ v&igrave; những tiếng ngẫu nhi&ecirc;n buột ra dần dần n&oacute;i cho t&ocirc;i hiểu mọi sự. V&iacute; dụ như, khi lần đầu em thấy chiếc m&aacute;y bay của t&ocirc;i (t&ocirc;i sẽ kh&ocirc;ng vẽ chiếc m&aacute;y bay đ&acirc;u, bức vẽ đ&oacute; qu&aacute; phức tạp đối với t&ocirc;i) em hỏi t&ocirc;i:<br />\r\n&ndash; C&aacute;i thứ n&agrave;y l&agrave; c&aacute;i g&igrave;?<br />\r\n&ndash; Kh&ocirc;ng phải c&aacute;i thứ. N&oacute; bay được đấy. N&oacute; l&agrave; một chiếc m&aacute;y bay. Đ&oacute; l&agrave; chiếc m&aacute;y bay của t&ocirc;i.<br />\r\nT&ocirc;i tự h&agrave;o n&oacute;i cho em biết l&agrave; t&ocirc;i bay. Em liền k&ecirc;u l&ecirc;n:<br />\r\n&ndash; Sao? &Ocirc;ng từ tr&ecirc;n trời rơi xuống ư?<br />\r\n&ndash; Phải, t&ocirc;i n&oacute;i ki&ecirc;m tốn.<br />\r\n&ndash; &aacute;i ch&agrave;! C&aacute;i n&agrave;y ngộ đấy...<br />\r\nV&agrave; &ocirc;ng ho&agrave;ng b&eacute; nhỏ bật l&ecirc;n một tr&agrave;ng cười khanh kh&aacute;ch rất kh&aacute;u nhưng l&agrave;m t&ocirc;i c&aacute;u lắm. T&ocirc;i muốn ai cũng phải đ&aacute;nh gi&aacute; một c&aacute;ch nghi&ecirc;m t&uacute;c mọi tai nạn của t&ocirc;i. Sau đ&oacute; em n&oacute;i th&ecirc;m:<br />\r\n&ndash; Thế l&agrave; &ocirc;ng cũng từ tr&ecirc;n trời rơi xuống! &Ocirc;ng ở h&agrave;nh tinh n&agrave;o?<br />\r\nTức th&igrave; t&ocirc;i lo&eacute; l&ecirc;n một tia s&aacute;ng n&agrave;o đ&oacute;, về sự c&oacute; mặt b&iacute; ẩn của em, v&agrave; đột nhi&ecirc;n t&ocirc;i hỏi:<br />\r\n&ndash; Thế ra em ở một h&agrave;nh tinh kh&aacute;c tới?<br />\r\nNhưng em kh&ocirc;ng trả lời t&ocirc;i. Em khẽ lắc đầu nh&igrave;n chiếc m&aacute;y bay của t&ocirc;i:<br />\r\n&ndash; Nhưng m&agrave; ngồi tr&ecirc;n c&aacute;i đ&oacute; th&igrave; &ocirc;ng cũng chẳng thể tới từ xa lắm đ&acirc;u...<br />\r\nRồi em đắm m&igrave;nh trong giấc mơ m&agrave;ng thật d&agrave;i. Sau đ&oacute;, m&oacute;c t&uacute;i lấy con cừu t&ocirc;i vừa vẽ, em lại m&ecirc; mải ngắm c&aacute;i của b&aacute;u đ&oacute;.<br />\r\nC&aacute;c bạn h&atilde;y tưởng tượng t&ocirc;i bị k&iacute;ch động như thế n&agrave;o bởi lời thổ lộ nửa vời về &quot;c&aacute;c h&agrave;nh tinh kh&aacute;c&quot; ấy. V&igrave; vậy, t&ocirc;i cố t&igrave;m hiểu r&otilde; hơn:<br />\r\n&ndash; Cậu b&eacute; ơi, em từ đ&acirc;u đến? &quot;Chỗ em&quot; l&agrave; đ&acirc;u thế? Em muốn mang con cừu của ta về đ&acirc;u?<br />\r\nSau một l&aacute;t im lặng trầm ng&acirc;m, em trả lời t&ocirc;i:<br />\r\n&ndash; C&oacute; c&aacute;i tốt l&agrave; với c&aacute;i th&ugrave;ng &ocirc;ng cho t&ocirc;i, ban đ&ecirc;m con cừu c&oacute; thể d&ugrave;ng n&oacute; l&agrave;m nh&agrave; ở.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ndash; Phải đấy. V&agrave; nếu em ngoan, ta sẽ c&ograve;n cho em một sợi d&acirc;y để buộc n&oacute; lại ban ng&agrave;y. V&agrave; một c&aacute;i cọc nữa.<br />\r\nLời đề nghị đ&oacute; c&oacute; lẽ l&agrave;m cho &ocirc;ng ho&agrave;ng b&eacute; nhỏ kh&ocirc;ng vừa l&ograve;ng:<br />\r\n&ndash; Buộc n&oacute; lại? Nghĩ g&igrave; m&agrave; lạ thế!<br />\r\n&ndash; Nhưng kh&ocirc;ng buộc n&oacute; lại, n&oacute; sẽ đi lung tung, n&oacute; sẽ đi lạc...<br />\r\nCậu bạn của t&ocirc;i lại bật cười khanh kh&aacute;ch:<br />\r\n&ndash; N&oacute; đi đằng n&agrave;o được chứ?<br />\r\n&ndash; Bất cứ đ&acirc;u. Cứ đi thẳng tới trước mặt...<br />\r\nBấy giờ &ocirc;ng ho&agrave;ng nhỏ nhận x&eacute;t một c&aacute;ch nặng nề:<br />\r\n&ndash; Kh&ocirc;ng hề g&igrave;. Chỗ t&ocirc;i b&eacute; lắm!<br />\r\nRồi, c&oacute; lẽ với một ch&uacute;t ngậm ng&ugrave;i, em n&oacute;i th&ecirc;m:<br />\r\n&ndash; Thẳng tới trước mặt người ta chẳng đi được bao xa đ&acirc;u...<br />\r\n<br />\r\n<span style=\"color:#e74c3c\"><strong>IV</strong></span><br />\r\n<br />\r\nVậy đấy, t&ocirc;i biết được một điều nữa thật quan trọng: Rằng c&aacute;i h&agrave;nh tinh qu&ecirc; hương của cậu em chỉ lớn hơn c&aacute;i nh&agrave; một ch&uacute;t!&nbsp; Điều ấy chẳng l&agrave;m t&ocirc;i ngạc nhi&ecirc;n nhiều lắm. T&ocirc;i vẫn biết ngo&agrave;i những h&agrave;nh tinh lớn như Tr&aacute;i Đất, Sao Kim, Sao Hoả, Sao Thuỷ, m&agrave; người ta đặt t&ecirc;n cho, c&oacute; h&agrave;ng trăm ng&ocirc;i sao kh&aacute;c m&agrave; đ&ocirc;i khi b&eacute; đến nỗi người ta chỉ thấy trong k&iacute;nh viễn vọng một c&aacute;ch kh&oacute; khăn. Khi một nh&agrave; thi&ecirc;n văn kh&aacute;m ph&aacute; ra một trong bọn ch&uacute;ng, nh&agrave; thi&ecirc;n văn ấy cho n&oacute; một con số. V&iacute; dụ &ocirc;ng gọi n&oacute; l&agrave; &quot;tiểu h&agrave;nh tinh 3251&quot;. T&ocirc;i c&oacute; những l&yacute; do ch&iacute;nh đ&aacute;ng để coi rằng c&aacute;i h&agrave;nh tinh từ đ&oacute; &ocirc;ng ho&agrave;ng nhỏ đến đ&acirc;y l&agrave; tiểu h&agrave;nh tinh B612. Tiểu h&agrave;nh tinh đ&oacute; chỉ được tr&ocirc;ng thấy c&oacute; một lần trong k&iacute;nh viễn vọng năm 1909, bởi một nh&agrave; thi&ecirc;n văn Thổ Nhĩ Kỳ.&nbsp;L&uacute;c đ&oacute; &ocirc;ng n&agrave;y mở một buổi thuyết tr&igrave;nh lớn về ph&aacute;t hiện của m&igrave;nh tại một Hội nghị Quốc tế về Thi&ecirc;n văn. Nhưng do bộ quần &aacute;o của &ocirc;ng ta, chẳng ai tin điều &ocirc;ng ta n&oacute;i. Người lớn l&agrave; thế đấy. May mắn cho tiểu tinh cầu B612, một nh&agrave; độc t&agrave;i Thổ Nhĩ Kỳ buộc d&acirc;n Thổ phi mặc &acirc;u phục, ai kh&ocirc;ng tu&acirc;n theo sẽ bị tội chết. Nh&agrave; thi&ecirc;n văn tr&igrave;nh b&agrave;y lại vấn đề năm 1920, trong bộ quần &aacute;o rất lịch sự. V&agrave; lần n&agrave;y, tất cả mọi người đồng &yacute; với &ocirc;ng ta.&nbsp;<br />\r\n<br />\r\nNếu t&ocirc;i kể với c&aacute;c bạn tỉ mỉ về tiểu tinh cầu B612, v&agrave; nếu t&ocirc;i tiết lộ với c&aacute;c bạn số hiệu của n&oacute;, ấy l&agrave; tại c&aacute;c người lớn. Những người lớn rất th&iacute;ch chữ số. Khi bạn n&oacute;i chuyện với họ về một người bạn mới, kh&ocirc;ng bao giờ họ hỏi bạn về c&aacute;i cốt yếu đ&acirc;u. Họ kh&ocirc;ng bao giờ hỏi: &quot;Giọng n&oacute;i hắn ta thế n&agrave;o? Hắn th&iacute;ch chơi tr&ograve; g&igrave;? Hắn c&oacute; sưu tầm bươm bướm kh&ocirc;ng?&quot; Họ chỉ hỏi bạn: &quot;Hắn ta bao nhi&ecirc;u tuổi? Hắn ta c&oacute; mấy anh em? Hắn ta c&acirc;n nặng bao nhi&ecirc;u?&quot; Thế đấy. Sau đ&oacute;, họ cho vậy l&agrave; họ hiểu hắn ta rồi. Nếu bạn n&oacute;i với những người lớn: &quot;T&ocirc;i c&oacute; thấy một c&aacute;i nh&agrave; gạch mầu hồng với hoa phong lữ tr&ecirc;n cửa sổ, v&agrave; chim bồ c&acirc;u tr&ecirc;n m&aacute;i...&quot; họ chẳng l&agrave;m thế n&agrave;o m&agrave; h&igrave;nh dung nổi c&aacute;i nh&agrave; ấy như thế n&agrave;o đ&acirc;u. Phải n&oacute;i với họ: &quot;T&ocirc;i đ&atilde; thấy một c&aacute;i nh&agrave; 10 vạn franc&quot;. Họ sẽ k&ecirc;u ngay: &quot;&Ocirc;i thật xinh đẹp l&agrave;m sao.&quot;<br />\r\n<br />\r\nNhư vậy đ&oacute;, nếu c&aacute;c bạn bảo họ: &quot;&Ocirc;ng ho&agrave;ng b&eacute; nhỏ l&agrave; c&oacute; thật chứ, chứng cứ l&agrave; cậu ta rất đẹp, cậu ta cười v&agrave; cậu ta th&iacute;ch c&oacute; một con cừu. Khi người ta th&iacute;ch c&oacute; một con cừu, thế l&agrave; c&oacute; người ấy chứ!&quot;, họ sẽ nh&uacute;n vai v&agrave; cho bạn l&agrave; trẻ con! Nhưng nếu bạn hỏi họ: &quot;C&aacute;i h&agrave;nh tinh từ đ&oacute; cậu ấy đi đến đ&acirc;y l&agrave; tiểu tinh cầu B612&quot;, thế l&agrave; họ nghe ra ngay, v&agrave; th&ocirc;i kh&ocirc;ng ph&aacute; quấy bạn với c&aacute;c c&acirc;u hỏi của họ nữa. Họ l&agrave; thế. Kh&ocirc;ng n&ecirc;n giận họ. Trẻ con phải hết sức rộng lượng đối với người lớn. Nhưng chắc chắn rằng đối với bọn ta l&agrave; những người hiểu đời, ch&uacute;ng ta c&oacute;c cần những con số! T&ocirc;i đ&atilde; rất th&iacute;ch bắt đầu kể c&acirc;u chuyện n&agrave;y như kiểu một c&acirc;u chuyện thần ti&ecirc;n. T&ocirc;i đ&atilde; rất th&iacute;ch n&oacute;i thế n&agrave;y: &quot;Xưa c&oacute; một lần, một &ocirc;ng ho&agrave;ng b&eacute; nhỏ ở tr&ecirc;n một tinh cầu chỉ lớn hơn cậu ấy c&oacute; một t&iacute;, cậu ấy thấy cần c&oacute; một người bạn th&acirc;n...&quot;. Đối với những ai hiểu đời, kể như vậy c&oacute; vẻ thật hơn nhiều. Bởi v&igrave; t&ocirc;i kh&ocirc;ng muốn người ta đọc cuốn s&aacute;ch của t&ocirc;i một c&aacute;ch hời hợt. Khi kể lại c&aacute;c kỷ niệm n&agrave;y, t&ocirc;i buồn tủi biết bao. S&aacute;u năm đ&atilde; qua, từ khi cậu bạn t&ocirc;i đi mất với con cừu của em. Nếu t&ocirc;i cố gắng tả lại em ở đ&acirc;y, ch&iacute;nh l&agrave; để t&ocirc;i kh&ocirc;ng qu&ecirc;n em. Thật l&agrave; buồn nếu ta qu&ecirc;n một người bạn. C&oacute; phải ai cũng c&oacute; được một người bạn th&acirc;n đ&acirc;u. V&agrave; c&oacute; lẽ t&ocirc;i sắp trở n&ecirc;n những người lớn, chỉ c&ograve;n th&iacute;ch c&aacute;c chữ số. Lại ch&iacute;nh cũng v&igrave; thế nữa m&agrave; t&ocirc;i đ&atilde; mua một hộp m&agrave;u nước v&agrave; b&uacute;t ch&igrave; m&agrave;u. Trở lại vẽ vời thật l&agrave; kh&oacute;, v&agrave;o tuổi t&ocirc;i b&acirc;y giờ, khi m&agrave; người ta chưa hề vẽ g&igrave; ngo&agrave;i con trăn kh&eacute;p k&iacute;n với con trăn mở từ hồi l&ecirc;n s&aacute;u. T&ocirc;i sẽ cố thử, hẳn thế, l&agrave;m những bức ch&acirc;n dung c&agrave;ng giống c&agrave;ng hay. Nhưng t&ocirc;i kh&ocirc;ng th&agrave;nh c&ocirc;ng ch&uacute;t n&agrave;o. Một bức trước vứt đi, bức sau c&ograve;n tệ hơn nữa. T&ocirc;i lại cũng c&oacute; sai lầm về tầm v&oacute;c. Chỗ n&agrave;y th&igrave; &ocirc;ng ho&agrave;ng nhỏ lớn qu&aacute;. Chỗ kia em b&eacute; qu&aacute;. T&ocirc;i cứ lần m&ograve; như thế n&agrave;y rồi như thế kh&aacute;c, khi được khi kh&ocirc;ng. Cuối c&ugrave;ng t&ocirc;i c&ograve;n nhầm ở những n&eacute;t quan trọng hơn nữa. Nhưng n&agrave;y, c&aacute;c bạn phải tha lỗi cho t&ocirc;i. Bạn t&ocirc;i kh&ocirc;ng hề giảng giải g&igrave; cho t&ocirc;i. C&oacute; lẽ em cho l&agrave; t&ocirc;i cũng như em. Nhưng t&ocirc;i, buồn thay, t&ocirc;i kh&ocirc;ng biết c&aacute;ch nh&igrave;n thấy con cừu xuy&ecirc;n qua c&aacute;i th&ugrave;ng. C&oacute; lẽ t&ocirc;i hơi giống những người lớn rồi. T&ocirc;i đ&atilde; gi&agrave; rồi. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>V</strong></span><br />\r\n<br />\r\nMỗi ng&agrave;y t&ocirc;i lại biết th&ecirc;m một điều g&igrave; đ&oacute; về h&agrave;nh tinh, về l&uacute;c ra đi, về cuộc du h&agrave;nh của cậu. C&aacute;i ấy đến nhẹ nh&agrave;ng bằng những suy đo&aacute;n ngẫu nhi&ecirc;n. Cũng như vậy đ&oacute; m&agrave; v&agrave;o ng&agrave;y thứ ba, t&ocirc;i được biết về tấm bi kịch của những c&acirc;y baobab. Lần n&agrave;y cũng l&agrave; nhờ con cừu, &ocirc;ng ho&agrave;ng nhỏ hỏi t&ocirc;i, như vừa mới nghĩ ra điều g&igrave; nghi&ecirc;m trọng lắm:<br />\r\n&ndash; C&oacute; thật đ&uacute;ng l&agrave; con cừu ăn những bụi c&acirc;y con kh&ocirc;ng?<br />\r\n&ndash; Phải, đ&uacute;ng đấy.<br />\r\n&ndash; ồ, tốt qu&aacute;!<br />\r\nT&ocirc;i kh&ocirc;ng hiểu sao chuyện những con cừu ăn những bụi c&acirc;y con lại quan trọng như thế. Nhưng &ocirc;ng ho&agrave;ng nhỏ n&oacute;i th&ecirc;m:<br />\r\n&ndash; Cho n&ecirc;n ch&uacute;ng ăn cả những c&acirc;y baobab chứ?<br />\r\nT&ocirc;i bảo rằng c&acirc;y baobab kh&ocirc;ng phải l&agrave; thứ bụi c&acirc;y nhỏ, m&agrave; l&agrave; những c&acirc;y to như cả c&aacute;i nh&agrave; thờ, v&agrave; cậu em c&oacute; mang theo cả một đ&agrave;n voi, th&igrave; cả đ&agrave;n voi ấy cũng chẳng l&agrave;m lung lay nổi một c&acirc;y baobab. &Yacute;&nbsp;nghĩ về đ&agrave;n voi l&agrave;m cho &ocirc;ng ho&agrave;ng nhỏ bật cười:<br />\r\n&ndash; Phải chồng con n&agrave;y l&ecirc;n con kia...<br />\r\nNhưng cậu em nhận x&eacute;t với vẻ triết l&yacute;:<br />\r\n&ndash; Bọn baobab, trước khi lớn, cũng bắt đầu b&eacute; tẹo chứ!<br />\r\n&ndash; Đ&uacute;ng l&agrave; như thế. Nhưng sao em lại cứ muốn cho con cừu của em ăn những c&acirc;y baobab nhỏ thế?<br />\r\nEm trả lời: &quot;&Ocirc;i ch&agrave;!&quot; như đ&oacute; l&agrave; một chuyện tất nhi&ecirc;n. V&agrave; t&ocirc;i phải bắt tr&iacute; th&ocirc;ng minh của t&ocirc;i l&agrave;m việc dữ dội mới tự hiểu được vấn đề ấy. Nguy&ecirc;n l&agrave;, tr&ecirc;n h&agrave;nh tinh của &ocirc;ng ho&agrave;ng nhỏ, cũng như tr&ecirc;n mọi h&agrave;nh tinh kh&aacute;c, đều c&oacute; những loại cỏ tốt v&agrave; những loại cỏ xấu. Do đ&oacute;, c&oacute; hạt tốt của cỏ tốt v&agrave; hạt xấu của cỏ xấu. Nhưng kh&ocirc;ng thể nh&igrave;n thấy hạt. Ch&uacute;ng ngủ trong b&iacute; mật cho đến khi một c&aacute;i hạt n&agrave;o trong bọn ch&uacute;ng nổi hứng muốn thức dậy... N&oacute; vươn vai, rụt r&egrave; nh&uacute; l&ecirc;n mặt trời một c&aacute;i nh&aacute;nh con hiền l&agrave;nh tuyệt xinh. Nếu l&agrave; nh&aacute;nh dưa hay một nh&aacute;nh hồng, ta c&oacute; thể muốn để cho n&oacute; mọc thế n&agrave;o tuỳ &yacute;. Nhưng nếu l&agrave; một c&acirc;y xấu, ngay khi nhận ra l&agrave; phải nhổ ngay. M&agrave; tr&ecirc;n h&agrave;nh tinh của &ocirc;ng ho&agrave;ng nhỏ th&igrave; c&oacute; những hạt giống kinh khủng... ấy l&agrave; những hạt baobab. Ch&uacute;ng nhiễm đầy cả tinh cầu. M&agrave; một c&acirc;y baobab, nếu như ta ch&uacute; &yacute; muộn m&agrave;ng qu&aacute;, ta c&oacute; thể chẳng bao giờ dẫy n&oacute; ra được nữa, baobab sẽ mọc cao v&agrave; rễ n&oacute; chằng chịt khắp tinh cầu. N&oacute; cho rễ của n&oacute; x&oacute;i đục h&agrave;nh tinh. V&agrave; nếu h&agrave;nh tinh m&agrave; qu&aacute; b&eacute;, m&agrave; nếu c&acirc;y baobab m&agrave; nhiều qu&aacute;, c&oacute; thể l&agrave;m vỡ tung cả h&agrave;nh tinh.&nbsp;&quot;Đ&acirc;y l&agrave; một vấn đề kỷ luật, &ocirc;ng ho&agrave;ng nhỏ về sau n&oacute;i với t&ocirc;i. Khi ta l&agrave;m vệ sinh cho ta buổi s&aacute;ng rồi, ta phải l&agrave;m kỹ vệ sinh cho h&agrave;nh tinh. Phải đều đặn lo nhổ bọn baobab ngay l&uacute;c ta vừa ph&acirc;n biệt được ch&uacute;ng với c&acirc;y hoa hồng. Đ&oacute; l&agrave; một việc l&agrave;m ch&aacute;n lắm, nhưng m&agrave; thật dễ.&quot;&nbsp;Đến một ng&agrave;y, cậu em khuy&ecirc;n t&ocirc;i cố sức vẽ được một bức vẽ đẹp, để cho c&aacute;c cậu b&eacute; ở qu&ecirc; hương t&ocirc;i ghi nhớ chuyện ấy. &quot;Một ng&agrave;y kia, nếu c&aacute;c bạn ấy l&ecirc;n đường, cậu em n&oacute;i với t&ocirc;i, chuyện ấy sẽ c&oacute; &iacute;ch cho họ. Đ&ocirc;i khi ho&atilde;n lại một việc cũng chẳng hại g&igrave;. Nhưng nếu l&agrave; những c&acirc;y baobab, th&igrave; bao giờ cũng tai hoạ đấy. T&ocirc;i c&oacute; biết một tinh cầu, tr&ecirc;n ấy c&oacute; một g&atilde; lười. Hắn bỏ mặc ba c&aacute;i c&acirc;y con...&quot;&nbsp;&nbsp;Thế l&agrave;, theo sự chỉ dẫn của &ocirc;ng ho&agrave;ng nhỏ, t&ocirc;i đ&atilde; vẽ tinh cầu đ&oacute;. T&ocirc;i tuyệt kh&ocirc;ng th&iacute;ch l&ecirc;n mặt dạy đời. Nhưng c&aacute;i hoạ baobab c&ograve;n &iacute;t người biết qu&aacute;, m&agrave; những nguy hiểm m&agrave; một cậu b&eacute; một mai lạc v&agrave;o một tiểu tinh cầu sẽ gặp phải th&igrave; rất l&agrave; đ&aacute;ng kể, cho n&ecirc;n, chỉ một lần th&ocirc;i, t&ocirc;i l&agrave;m kh&aacute;c với nguy&ecirc;n tắc của t&ocirc;i. T&ocirc;i xin n&oacute;i: &quot;Hỡi c&aacute;c em! H&atilde;y coi chừng bọn baobab!&quot; ấy ch&iacute;nh l&agrave; để b&aacute;o trước cho c&aacute;c bạn t&ocirc;i về một nguy cơ m&agrave; c&aacute;c bạn cũng như t&ocirc;i vẫn gần kề b&ecirc;n n&oacute; m&agrave; kh&ocirc;ng hay biết, n&ecirc;n t&ocirc;i ra sức thật nhiều để vẽ bức vẽ đ&oacute;! B&agrave;i học m&agrave; t&ocirc;i đưa ra rất xứng c&ocirc;ng. C&oacute; lẽ bạn sẽ tự hỏi: Tại sao trong cuốn s&aacute;ch n&agrave;y kh&ocirc;ng c&oacute; bức vẽ n&agrave;o to lớn bằng bức vẽ những c&acirc;y baobab? C&acirc;u trả lời đơn giản th&ocirc;i: t&ocirc;i c&oacute; thử vẽ nhưng kh&ocirc;ng th&agrave;nh. Khi vẽ c&acirc;y baobab, t&ocirc;i bị th&ocirc;i th&uacute;c bởi một t&igrave;nh cảm cấp b&aacute;ch</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>VI</strong></span><br />\r\n<br />\r\n&Ocirc;i! &Ocirc;ng ho&agrave;ng b&eacute; nhỏ ơi, dần d&agrave;, như vậy đ&oacute;, t&ocirc;i hiểu ra cuộc đời nhỏ nhoi buồn b&atilde; của em. Bao l&acirc;u nay em chỉ nhờ sự &ecirc;m đềm của ho&agrave;ng h&ocirc;n để m&agrave; khu&acirc;y khoả. T&ocirc;i biết được n&eacute;t mới đ&oacute; v&agrave;o buổi s&aacute;ng ng&agrave;y thứ tư, khi em bảo t&ocirc;i:<br />\r\n&ndash; T&ocirc;i rất th&iacute;ch cảnh mặt trời lặn. Ta đi xem mặt trời lặn đi.<br />\r\n&ndash; Nhưng phải đợi chứ...<br />\r\n&ndash; Đợi c&aacute;i g&igrave;?<br />\r\n&ndash; Đợi l&uacute;c mặt trời đ&oacute; lặn.<br />\r\nThoạt nhi&ecirc;n em c&oacute; vẻ kinh ngạc, v&agrave; rồi em tự cười m&igrave;nh. V&agrave; em bảo t&ocirc;i:<br />\r\n&ndash; T&ocirc;i cứ tưởng c&ograve;n ở nh&agrave;!!<br />\r\nThế đấy. Khi ở nước Mỹ l&agrave; buổi trưa th&igrave; ai cũng biết l&agrave; mặt trời đang lặn ở nước Ph&aacute;p. Nếu chỉ cần đi một ph&uacute;t l&agrave; đến được nước Ph&aacute;p l&agrave; xem được cảnh mặt trời lặn. Kh&ocirc;ng may nước Ph&aacute;p ở qu&aacute; xa. Nhưng, tr&ecirc;n c&aacute;i h&agrave;nh tinh b&eacute; đến thế của em, em chỉ cần dịch ghế v&agrave;i bước. Khi n&agrave;o em th&iacute;ch l&agrave; em nh&igrave;n thấy mặt trời lặn...<br />\r\n&ndash; C&oacute; một ng&agrave;y, t&ocirc;i nh&igrave;n mặt trời lặn bốn mươi ba lần!<br />\r\nMột chốc sau đ&oacute; em n&oacute;i th&ecirc;m:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ndash; &Ocirc;ng biết đấy... khi người ta buồn qu&aacute;, người ta th&iacute;ch cảnh mặt trời lặn...<br />\r\n&ndash; Thế c&aacute;i ng&agrave;y bốn mươi ba lần mặt trời lặn ấy, c&oacute; phải em buồn qu&aacute; kh&ocirc;ng?<br />\r\nNhưng &ocirc;ng ho&agrave;ng b&eacute; nhỏ kh&ocirc;ng trả lời.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>VII</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ng&agrave;y thứ năm, vẫn l&agrave; nhờ ở con cừu, t&ocirc;i biết được c&aacute;i b&iacute; mật ấy trong cuộc đời &ocirc;ng ho&agrave;ng b&eacute; nhỏ. Bất ngờ em hỏi t&ocirc;i, kh&ocirc;ng cần m&agrave;o đầu, y như đ&oacute; l&agrave; kết quả của một vấn đề được &acirc;m thầm suy nghĩ từ l&acirc;u rồi:<br />\r\n&ndash; Một con cừu, nếu n&oacute; ăn c&acirc;y non, tất n&oacute; cũng ăn hoa chứ?<br />\r\n&ndash; Cừu th&igrave; gặp c&aacute;i g&igrave; n&oacute; ăn c&aacute;i ấy.<br />\r\n&ndash; Cả những b&ocirc;ng hoa c&oacute; gai ư?&ndash; Phải. Cả những b&ocirc;ng hoa c&oacute; gai.<br />\r\n&ndash; Thế th&igrave; gai d&ugrave;ng để l&agrave;m g&igrave; n&agrave;o?<br />\r\nT&ocirc;i kh&ocirc;ng biết. T&ocirc;i bấy giờ đang bận th&aacute;o một đinh ốc vặn qu&aacute; chặt trong động cơ của t&ocirc;i. T&ocirc;i đ&atilde; rất lo lắng v&igrave; thấy c&aacute;i m&aacute;y c&oacute; vẻ hỏng nặng lắm, m&agrave; nước uống th&igrave; cạn dần l&agrave;m cho t&ocirc;i sợ xảy ra điều tệ hại nhất.<br />\r\n&ndash; Những c&aacute;i gai, ch&uacute;ng d&ugrave;ng để l&agrave;m g&igrave;?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&Ocirc;ng ho&agrave;ng nhỏ kh&ocirc;ng bao giờ chịu bỏ một c&acirc;u hỏi, một khi đ&atilde; n&oacute;i ra. T&ocirc;i th&igrave; đang bực qu&aacute; với c&aacute;i đinh ốc, v&agrave; t&ocirc;i trả lời bừa:<br />\r\n&ndash; Gai, n&oacute; chẳng d&ugrave;ng được v&agrave;o việc g&igrave; sất. N&oacute; chỉ l&agrave; c&aacute;i t&iacute;nh độc &aacute;c của hoa th&ocirc;i!<br />\r\n&ndash; ồ!<br />\r\nNhưng sau một l&aacute;t im lặng em k&ecirc;u l&ecirc;n một c&aacute;ch giận dỗi:<br />\r\n&ndash; T&ocirc;i kh&ocirc;ng tin! Lo&agrave;i hoa yếu đuối lắm. Ch&uacute;ng ng&acirc;y thơ lắm. Ch&uacute;ng cố tự l&agrave;m cho ch&uacute;ng được vững t&acirc;m. Ch&uacute;ng cho l&agrave; với những c&aacute;i gai, ch&uacute;ng đ&atilde; gh&ecirc; gớm lắm...<br />\r\nT&ocirc;i kh&ocirc;ng trả lời g&igrave; cả. L&uacute;c ấy, t&ocirc;i tự nhủ: &quot;C&aacute;i đinh ốc n&agrave;y m&agrave; c&ograve;n ngoan cố, &ocirc;ng sẽ &agrave;y một b&uacute;a văng xương.&quot;<br />\r\n&Ocirc;ng ho&agrave;ng nhỏ lại l&agrave;m sao l&atilde;ng những suy nghĩ của t&ocirc;i:<br />\r\n&ndash; C&ograve;n &ocirc;ng, &ocirc;ng tưởng rằng hoa...<br />\r\n&ndash; Kh&ocirc;ng! Kh&ocirc;ng! T&ocirc;i c&oacute; tưởng g&igrave; đ&acirc;u! T&ocirc;i trả lời đại thế th&ocirc;i. T&ocirc;i đang bận những việc hệ trọng!<br />\r\nCậu nh&igrave;n t&ocirc;i ngạc nhi&ecirc;n:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ndash; Những việc hệ trọng!<br />\r\nCậu em nh&igrave;n thấy t&ocirc;i tay cầm b&uacute;a, c&aacute;c ng&oacute;n đen thui v&igrave; dầu m&aacute;y, c&uacute;i xuống một vật m&agrave; em xem ra thật l&agrave; xấu x&iacute;.<br />\r\n&ndash; &Ocirc;ng n&oacute;i như c&aacute;c người lớn ấy!<br />\r\nC&acirc;u ấy l&agrave;m t&ocirc;i hơi xấu hổ. Nhưng kh&ocirc;ng thương x&oacute;t, em n&oacute;i th&ecirc;m:<br />\r\n&ndash; &Ocirc;ng lẫn lộn hết... &ocirc;ng x&aacute;o trộn hết<br />\r\nCậu em thực sự bực tức. M&aacute;i t&oacute;c của em v&agrave;ng xo&atilde; tung ra trước gi&oacute;:<br />\r\n&ndash; T&ocirc;i biết c&oacute; một tinh cầu, tr&ecirc;n đ&oacute; c&oacute; một &ocirc;ng mặt mũi đỏ gay. &Ocirc;ng ta kh&ocirc;ng hề ngửi một b&ocirc;ng hoa. Kh&ocirc;ng hề ngắm một v&igrave; sao. Kh&ocirc;ng hề y&ecirc;u một người n&agrave;o. &ocirc;ng ta chẳng bao giờ l&agrave;m c&aacute;i g&igrave; kh&aacute;c những b&agrave;i t&iacute;nh cộng. V&agrave; suốt ng&agrave;y &ocirc;ng ta cứ lặp đi lặp lại như &ocirc;ng: &quot;T&ocirc;i l&agrave; một người đ&uacute;ng đắn! T&ocirc;i l&agrave; một người đ&uacute;ng đắn!&quot;, v&agrave; c&aacute;i đ&oacute; l&agrave;m &ocirc;ng ta v&ecirc;nh vang hợm hĩnh. Nhưng &ocirc;ng ta đ&acirc;u c&oacute; phải l&agrave; người, &ocirc;ng ta l&agrave; một c&aacute;i nấm!<br />\r\n&ndash; Một c&aacute;i g&igrave;?<br />\r\n&ndash; Một c&aacute;i nấm!<br />\r\n&Ocirc;ng ho&agrave;ng b&eacute; nhỏ l&uacute;c n&agrave;y t&aacute;i xanh v&igrave; giận.<br />\r\n&ndash; Đ&atilde; h&agrave;ng triệu năm nay, hoa l&agrave;m ra gai. H&agrave;ng triệu năm nay, cừu vẫn cứ ăn hoa. Vậy m&agrave; t&igrave;m hiểu xem v&igrave; sao hoa lại cứ khổ sở l&agrave;m ra những c&aacute;i gai v&ocirc; t&iacute;ch sự ấy, lại l&agrave; chuyện kh&ocirc;ng đ&uacute;ng đắn hay sao? Chiến tranh giữa cừu v&agrave; hoa l&agrave; chuyện kh&ocirc;ng quan trọng hay sao? Kh&ocirc;ng đ&uacute;ng đắn hơn, kh&ocirc;ng quan trọng hơn những b&agrave;i t&iacute;nh cộng của một c&aacute;i &ocirc;ng to tướng mặt mũi đỏ gay hay sao? V&agrave; nếu như t&ocirc;i, t&ocirc;i biết một c&aacute;i hoa duy nhất tr&ecirc;n đời kh&ocirc;ng c&oacute; ở đ&acirc;u ngo&agrave;i tinh cầu của t&ocirc;i, thế m&agrave; một con cừu nhỏ c&oacute; thể huỷ hoại n&oacute; bằng c&aacute;ch t&aacute;p một c&aacute;i m&agrave; th&ocirc;i, v&agrave;o một buổi s&aacute;ng n&agrave;o đ&oacute;, m&agrave; kh&ocirc;ng hề biết l&agrave; m&igrave;nh vừa l&agrave;m c&aacute;i g&igrave;, chuyện đ&oacute; kh&ocirc;ng quan trọng hay sao!<br />\r\nEm đỏ mặt rồi n&oacute;i tiếp:<br />\r\n&ndash; Khi một người y&ecirc;u một đo&aacute; hoa duy nhất trong h&agrave;ng triệu triệu ng&ocirc;i sao, th&igrave; chỉ nh&igrave;n những ng&ocirc;i sao l&agrave; đủ l&agrave;m cho anh ta hạnh ph&uacute;c. Anh ta nghĩ: &quot;Đo&aacute; hoa của m&igrave;nh ở đ&acirc;u đ&oacute; tr&ecirc;n kia...&quot; Nhưng nếu con cừu m&agrave; ăn đo&aacute; hoa đi, th&igrave; anh ta sẽ thấy như l&agrave; tất cả c&aacute;c ng&ocirc;i sao tự nhi&ecirc;n tắt lịm! V&agrave; chuyện đ&oacute; kh&ocirc;ng quan trọng hay sao!<br />\r\nEm nghẹn ng&agrave;o kh&ocirc;ng n&oacute;i được nữa. Thốt nhi&ecirc;n em bật l&ecirc;n nức nở. Đ&ecirc;m đ&atilde; bu&ocirc;ng xuống. T&ocirc;i đ&atilde; bỏ c&aacute;c đồ nghề xuống. T&ocirc;i c&oacute;c cần c&aacute;i b&uacute;a của t&ocirc;i, c&aacute;i đinh ốc của t&ocirc;i, c&oacute;c cần c&aacute;i kh&aacute;t, c&oacute;c cần c&aacute;i chết. Tr&ecirc;n một ng&ocirc;i sao, tr&ecirc;n một h&agrave;nh tinh, h&agrave;nh tinh của t&ocirc;i, tr&aacute;i đất, c&oacute; một &ocirc;ng ho&agrave;ng b&eacute; nhỏ cần được an ủi! T&ocirc;i &ocirc;m em trong v&ograve;ng tay. T&ocirc;i ru em. T&ocirc;i n&oacute;i với em: &quot;Đo&aacute; hoa m&agrave; em y&ecirc;u kh&ocirc;ng bị nguy đ&acirc;u... T&ocirc;i sẽ vẽ một c&aacute;i rọ m&otilde;m, cho con cừu của em... T&ocirc;i...&quot; T&ocirc;i kh&ocirc;ng biết n&oacute;i với em thế n&agrave;o nữa. T&ocirc;i cảm thấy m&igrave;nh rất vụng về. T&ocirc;i kh&ocirc;ng biết l&agrave;m sao với tới được em, đi đ&acirc;u để gặp được em... Thật huyền b&iacute; l&agrave;m sao, c&aacute;i xứ sở của nước mắt. VIII<br />\r\nT&ocirc;i đ&atilde; t&igrave;m hiểu rất nhanh để hiểu hơn về đo&aacute; hoa n&agrave;y. Tr&ecirc;n tinh cầu của &ocirc;ng ho&agrave;ng nhỏ, vẫn thường c&oacute; những b&ocirc;ng hoa rất đơn giản, điểm trang chỉ bằng một v&ograve;ng c&aacute;nh hoa, v&agrave; chẳng chiếm bao nhi&ecirc;u chỗ, v&agrave; chẳng l&agrave;m phiền ai. Một buổi s&aacute;ng, ch&uacute;ng hiện ra trong cỏ rồi lại h&eacute;o t&agrave;n v&agrave;o chiều tối. Nhưng c&aacute;i c&acirc;y hoa ấy, một ng&agrave;y kia, đ&atilde; nảy mầm từ một c&aacute;i hạt kh&ocirc;ng biết từ đ&acirc;u tới v&agrave; &ocirc;ng ho&agrave;ng nhỏ đ&atilde; theo d&otilde;i c&aacute;i mầm con kh&ocirc;ng giống với c&aacute;c mầm con n&agrave;o kh&aacute;c. N&oacute; c&oacute; thể l&agrave; một loại baobab mới. Nhưng c&aacute;i c&acirc;y nhỏ liền th&ocirc;i kh&ocirc;ng lớn l&ecirc;n nữa, v&agrave; bắt đầu sửa soạn để ra hoa. &Ocirc;ng ho&agrave;ng nhỏ chứng kiến sự xuất hiện một c&aacute;i nụ lớn, cảm thấy r&otilde; r&agrave;ng từ c&aacute;i nụ n&agrave;y sẽ lộ ra một điều kỳ diệu, nhưng đo&aacute; hoa vẫn cứ điểm trang ho&agrave;i, nấp k&iacute;n trong căn buồng xanh của n&oacute;. Hoa chưa chọn kỹ mầu sắc của m&igrave;nh. Hoa chậm r&atilde;i trang phục, sửa lại ngay ngắn từng c&aacute;nh hoa. Hoa kh&ocirc;ng muốn hiện ra nh&agrave;u n&aacute;t như c&aacute;i mồng g&agrave;. Hoa chỉ muốn lộ ra trong sắc hương rực rỡ. &Ocirc;i chao! Hoa thật l&agrave; điệu! Cuộc trang điểm huyền b&iacute; của n&agrave;ng k&eacute;o d&agrave;i từ ng&agrave;y n&agrave;y sang ng&agrave;y nọ. V&agrave; rồi tới một sớm mai, đ&uacute;ng giờ mặt trời mọc, n&agrave;ng hiện ra.&nbsp;<br />\r\nV&agrave; n&agrave;ng, vốn đ&atilde; c&ocirc;ng phu đến như thế, b&acirc;y giờ vừa ng&aacute;p vừa n&oacute;i:<br />\r\n&ndash; &Ocirc;i! Em chỉ vừa thức dậy... Em xin lỗi anh... T&oacute;c tai em c&ograve;n rũ rượi thế n&agrave;y...<br />\r\nThế l&agrave; &ocirc;ng ho&agrave;ng nhỏ kh&ocirc;ng n&eacute;n nổi sự ngưỡng mộ:<br />\r\n&ndash; N&agrave;ng thật l&agrave; đẹp!&ndash; Thật vậy sao, &ndash; hoa trả lời một c&aacute;ch nhẹ nh&agrave;ng &ndash; V&agrave; em lại c&ugrave;ng sinh ra với vầng dương...<br />\r\n&Ocirc;ng ho&agrave;ng nhỏ biết ngay c&ocirc; n&agrave;ng kh&ocirc;ng lấy g&igrave; l&agrave;m ki&ecirc;m tốn lắm, nhưng n&agrave;ng thật l&agrave; dễ thương!<br />\r\n&ndash; Đ&atilde; đến giờ điểm t&acirc;m, c&oacute; phải anh kh&ocirc;ng, &ndash; n&agrave;ng n&oacute;i th&ecirc;m ngay sau đ&oacute;, &ndash; xin anh h&atilde;y nhớ đến em...<br />\r\nThế l&agrave; &ocirc;ng ho&agrave;ng nhỏ, đầy xấu hổ, đi t&igrave;m một chiếc th&ugrave;ng tưới đầy nước m&aacute;t v&agrave; tưới cho b&ocirc;ng hoa.&nbsp;<br />\r\nNhư vậy đấy, c&ocirc; n&agrave;ng đ&atilde; nhanh ch&oacute;ng l&agrave;m cho cậu em lo nghĩ với t&iacute;nh ki&ecirc;u kỳ hơi u &aacute;m của c&ocirc;. Chẳng hạn một h&ocirc;m, c&ocirc; đ&atilde; n&oacute;i với &ocirc;ng ho&agrave;ng nhỏ về bốn c&aacute;i gai của m&igrave;nh: &ndash; Ch&uacute;ng c&oacute; thể tới đ&acirc;y, bọn hổ ấy, với m&oacute;ng vuốt của ch&uacute;ng.<br />\r\n&ndash; Tr&ecirc;n tinh cầu của anh kh&ocirc;ng c&oacute; hổ, &ocirc;ng ho&agrave;ng nhỏ nhận x&eacute;t. Vả lại, hổ đ&acirc;u c&oacute; ăn cỏ.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ndash; Em đ&acirc;u phải l&agrave; cỏ, b&ocirc;ng hoa n&oacute;i nhẹ nh&agrave;ng.<br />\r\n&ndash; Xin lỗi...<br />\r\n&ndash; Em chẳng sợ g&igrave; hổ, nhưng em khiếp gi&oacute; luồn. Anh c&oacute; một tấm chắn gi&oacute; n&agrave;o kh&ocirc;ng?<br />\r\n&quot;Khiếp gi&oacute; luồn... đối với một c&acirc;y nhỏ thế l&agrave; kh&ocirc;ng hay, &ocirc;ng ho&agrave;ng nhỏ nhận x&eacute;t. C&ocirc; n&agrave;ng n&agrave;y thật phức tạp...&quot;<br />\r\n&ndash; Chiều tối anh h&atilde;y đặt em trong bầu k&iacute;nh nh&eacute;. Chỗ anh r&eacute;t lắm. Thiếu tiện nghi qu&aacute;. ở chỗ em...<br />\r\nNhưng c&ocirc; n&agrave;ng im bặt. C&ocirc; đến đ&acirc;y l&uacute;c h&atilde;y c&ograve;n l&agrave; hạt. C&ocirc; chẳng thể hiểu được g&igrave; về c&aacute;c thế giới kh&aacute;c. Ngượng v&igrave; tr&oacute;t để lộ l&agrave; m&igrave;nh bốc ph&eacute;t một c&aacute;ch ng&acirc;y ng&ocirc; qu&aacute; như thế, c&ocirc; h&uacute;ng ho&aacute;ng ho hai ba lượt để dồn &ocirc;ng ho&agrave;ng nhỏ v&agrave;o l&uacute;ng t&uacute;ng:<br />\r\n&ndash; C&aacute;i chắn gi&oacute;, anh c&oacute; kh&ocirc;ng?...&ndash; Anh đ&atilde; định đi lấy th&igrave; em bắt đầu n&oacute;i!<br />\r\nThế l&agrave; c&ocirc; n&agrave;ng cố ho mạnh hơn nữa để bắt buộc cậu phải nhận lỗi.&nbsp;Vậy m&agrave; &ocirc;ng ho&agrave;ng nhỏ, bởi v&igrave; &yacute; tốt của t&igrave;nh y&ecirc;u, vẫn đối xử với c&ocirc; rất tốt. Ch&agrave;ng đ&atilde; nghĩ ngợi nghi&ecirc;m trang về những lời n&oacute;i vớ vẩn của n&agrave;ng, v&agrave; trở n&ecirc;n khổ sở lắm.&nbsp;&quot;Đ&aacute;ng lẽ t&ocirc;i kh&ocirc;ng n&ecirc;n nghe, một h&ocirc;m cậu em th&uacute; nhận với t&ocirc;i, kh&ocirc;ng bao giờ n&ecirc;n nghe lo&agrave;i hoa cả. Chỉ n&ecirc;n nh&igrave;n ch&uacute;ng v&agrave; thở hương thơm của ch&uacute;ng th&ocirc;i. C&aacute;i hoa của t&ocirc;i l&agrave;m thơm ng&aacute;t tinh cầu của t&ocirc;i, nhưng t&ocirc;i lại kh&ocirc;ng biết vui l&ograve;ng. C&acirc;u chuyện m&oacute;ng vuốt ấy đ&aacute;ng lẽ l&agrave;m cho t&ocirc;i cảm động th&igrave; t&ocirc;i lại bực...&quot;<br />\r\nCậu c&ograve;n th&uacute; nhận với t&ocirc;i:&nbsp;&quot;Ng&agrave;y ấy, t&ocirc;i chẳng biết c&aacute;ch hiểu. Đ&aacute;ng lẽ t&ocirc;i phải x&eacute;t đo&aacute;n n&agrave;ng tr&ecirc;n việc l&agrave;m chứ kh&ocirc;ng phải bằng lời n&oacute;i. N&agrave;ng toả thơm t&ocirc;i, l&agrave;m cho t&ocirc;i s&aacute;ng rực l&ecirc;n. Đ&aacute;ng lẽ t&ocirc;i kh&ocirc;ng bao giờ n&ecirc;n bỏ đi cả. Đ&aacute;ng lẽ t&ocirc;i phải thấy được c&aacute;i dịu hiền của n&agrave;ng đằng sau mọi đ&ograve;i hỏi đ&aacute;ng thương ấy. Lo&agrave;i hoa thường hay m&acirc;u thuẫn! Nhưng bấy giờ t&ocirc;i c&ograve;n qu&aacute; trẻ để m&agrave; biết y&ecirc;u n&agrave;ng.&quot;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>IX</strong></span><br />\r\n<br />\r\nT&ocirc;i đo&aacute;n rằng em nhờ v&agrave;o một chuyến thi&ecirc;n di của lo&agrave;i chim hoang để m&agrave; tho&aacute;t đi. Buổi s&aacute;ng h&ocirc;m ra đi, em đ&atilde; dọn dẹp tinh cầu thật ngăn nắp. Em nạo v&eacute;t kỹ c&agrave;ng c&aacute;c quả n&uacute;i lửa đang hoạt động của em. Em c&oacute; hai quả n&uacute;i lửa đang hoạt động. V&agrave; ch&uacute;ng rất thuận tiện cho việc nấu ăn buổi s&aacute;ng. Em cũng c&oacute; một quả n&uacute;i lửa đ&atilde; tắt... Nhưng, như lời em n&oacute;i: &quot;Biết đ&acirc;u đấy!&quot; n&ecirc;n em cũng nạo v&eacute;t cả quả n&uacute;i lửa đ&atilde; tắt nữa. Được nạo v&eacute;t kỹ, c&aacute;c quả n&uacute;i lửa sẽ ch&aacute;y đỏ v&agrave; đều, kh&ocirc;ng c&oacute; phun tr&agrave;o. C&aacute;c trận phun tr&agrave;o của n&uacute;i lửa cũng giống như lửa trong l&ograve; sưởi. Tất nhi&ecirc;n l&agrave; tr&ecirc;n tr&aacute;i đất của ch&uacute;ng ta, ch&uacute;ng ta thật nhỏ b&eacute; qu&aacute;, kh&ocirc;ng nạo v&eacute;t được c&aacute;c quả n&uacute;i lửa của m&igrave;nh. Cho n&ecirc;n ch&uacute;ng g&acirc;y cho ta nhiều điều phiền phức.&nbsp;&Ocirc;ng ho&agrave;ng nhỏ cũng nhổ, với một ch&uacute;t ngậm ng&ugrave;i, những c&aacute;i mầm vừa nh&uacute; của bọn baobab. Em đ&atilde; nghĩ rằng m&igrave;nh sẽ kh&ocirc;ng bao giờ về nữa. Nhưng mọi việc quen thuộc n&agrave;y buổi s&aacute;ng h&ocirc;m nay sao đối với em thật v&ocirc; c&ugrave;ng &ecirc;m đềm. V&agrave;, khi em tưới hoa lần cuối, v&agrave; sửa soạn đậy n&agrave;ng trong lồng k&iacute;nh, em cảm thấy muốn kh&oacute;c.<br />\r\n&ndash; Vĩnh biệt, em n&oacute;i với hoa.<br />\r\nNhưng n&agrave;ng kh&ocirc;ng đ&aacute;p.<br />\r\n&ndash; Vĩnh biệt, em nhắc lại.<br />\r\nHoa ho l&ecirc;n. Nhưng kh&ocirc;ng phải v&igrave; vi&ecirc;m họng.<br />\r\n&ndash; Em đ&atilde; khờ dại lắm, sau c&ugrave;ng c&ocirc; n&oacute;i. Anh tha lỗi cho em. H&atilde;y cố gắng m&agrave; sống hạnh ph&uacute;c.<br />\r\n&Ocirc;ng ho&agrave;ng nhỏ ngạc nhi&ecirc;n v&igrave; kh&ocirc;ng thấy n&agrave;ng tr&aacute;ch m&oacute;c. Em đứng sững, tay cầm nguy&ecirc;n c&aacute;i bầu k&iacute;nh. Em kh&ocirc;ng hiểu được vẻ dịu d&agrave;ng b&igrave;nh tĩnh ấy của n&agrave;ng.<br />\r\n&ndash; V&acirc;ng, em y&ecirc;u anh, hoa n&oacute;i với em. Anh kh&ocirc;ng biết g&igrave; cả, ấy l&agrave; lỗi tại em. Điều ấy kh&ocirc;ng quan trọng. Nhưng anh, anh cũng khờ dại như em. H&atilde;y cố gắng m&agrave; sống hạnh ph&uacute;c... Anh bỏ c&aacute;i bầu k&iacute;nh đ&oacute; xuống đi. Em kh&ocirc;ng cần đ&acirc;u...<br />\r\n&ndash; Thế gi&oacute;...<br />\r\n&ndash; Em kh&ocirc;ng hay bị cảm nhiều thế đ&acirc;u... Gi&oacute; m&aacute;t ban đ&ecirc;m tốt cho em lắm. Em l&agrave; một c&aacute;i hoa m&agrave;.<br />\r\n&ndash; Nhưng bọn th&uacute;...<br />\r\n&ndash; Chắc em phải chịu đựng v&agrave;i ba con s&acirc;u nếu em muốn biết bươm bướm l&agrave; thế n&agrave;o. H&igrave;nh như lũ bướm ấy thật l&agrave; đẹp. Nếu kh&ocirc;ng ai sẽ viếng thăm em? Anh sẽ đi xa. C&ograve;n bọn th&uacute; dữ, em kh&ocirc;ng sợ. Em c&oacute; m&oacute;ng vuốt của em.<br />\r\nN&agrave;ng ng&acirc;y thơ ch&igrave;a ra bốn c&aacute;i gai của m&igrave;nh. Rồi n&agrave;ng n&oacute;i th&ecirc;m:<br />\r\n&ndash; Đừng chần chờ như vậy nữa, kh&oacute; chịu lắm. Anh đ&atilde; quyết ra đi m&agrave;. H&atilde;y đi đi anh.<br />\r\nBởi v&igrave; n&agrave;ng kh&ocirc;ng muốn em nh&igrave;n thấy n&agrave;ng kh&oacute;c. Đ&oacute; l&agrave; một đo&aacute; hoa v&ocirc; c&ugrave;ng ki&ecirc;u h&atilde;nh...<br />\r\n<br />\r\n<span style=\"color:#e74c3c\"><strong>X</strong></span><br />\r\n<br />\r\nCậu em đ&atilde; đi qua v&ugrave;ng c&oacute; c&aacute;c tiểu tinh cầu 325, 326, 327, 328, 329 v&agrave; 330. Em bắt đầu đi thăm c&aacute;c tiểu tinh cầu ấy để kiếm việc v&agrave; để học hỏi.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tiểu tinh cầu thứ nhất c&oacute; một &ocirc;ng vua ở. Nh&agrave; vua mặc &aacute;o đỏ t&iacute;a đ&iacute;nh l&ocirc;ng th&uacute;, ngự tr&ecirc;n một c&aacute;i ngai v&agrave;ng thật giản dị nhưng đồng thời cũng thật uy nghi.<br />\r\n&ndash; &agrave;! Đ&acirc;y l&agrave; một thần d&acirc;n! Nh&agrave; vua k&ecirc;u l&ecirc;n khi thấy &ocirc;ng ho&agrave;ng nhỏ.<br />\r\nV&agrave; &ocirc;ng ho&agrave;ng b&eacute; tự hỏi:<br />\r\n&ndash; L&agrave;m sao &ocirc;ng ta nhận được ra m&igrave;nh khi m&agrave; chưa gặp m&igrave;nh bao giờ nhỉ!<br />\r\nEm kh&ocirc;ng biết rằng, đối với c&aacute;c bậc đế vương, thế giới đơn giản lắm. Tất cả mọi người đều l&agrave; thần d&acirc;n.<br />\r\n&ndash; Ngươi h&atilde;y đến gần cho ta nh&igrave;n ngươi được kỹ, nh&agrave; vua n&oacute;i với em, ng&agrave;i rất kho&aacute;i được l&agrave;m vua với một người n&agrave;o đ&oacute;.<br />\r\n&Ocirc;ng ho&agrave;ng nhỏ đưa mắt t&igrave;m chỗ ngồi, nhưng cả tinh cầu đ&atilde; bị c&aacute;i &aacute;o cho&agrave;ng l&ocirc;ng th&uacute; tuyệt đẹp cho&aacute;n hết chỗ. Em đ&agrave;nh phải đứng, v&agrave;, v&igrave; nhọc qu&aacute;, em ng&aacute;p một c&aacute;i.<br />\r\n&ndash; Đứng trước một bậc đế vương m&agrave; ng&aacute;p l&agrave; v&ocirc; lễ, vua ph&aacute;n. Ta cấm ngươi ng&aacute;p.<br />\r\n&ndash; Chỉ v&igrave; t&ocirc;i kh&ocirc;ng giữ được ạ! &Ocirc;ng ho&agrave;ng nhỏ rất ngượng đ&aacute;p lại. T&ocirc;i từ xa đến đ&acirc;y m&agrave; chưa được ngủ...&ndash; Thế th&igrave;, vua ph&aacute;n, ta ra lệnh cho ngươi ng&aacute;p. Bao nhi&ecirc;u năm nay, ta chưa được thấy ai ng&aacute;p. Những c&aacute;i ng&aacute;p đối với ta l&agrave; c&aacute;i lạ đấy. Ngươi ng&aacute;p nữa đi. Đ&oacute; l&agrave; lệnh ta.<br />\r\n&ndash; C&aacute;i n&agrave;y kh&oacute; qu&aacute;... t&ocirc;i kh&ocirc;ng ng&aacute;p được nữa... &Ocirc;ng ho&agrave;ng nhỏ n&oacute;i, mặt đỏ bừng.<br />\r\n&ndash; Hừm! Hừm! Vua đ&aacute;p. Thế th&igrave; ta ra lệnh cho ngươi khi th&igrave; ng&aacute;p khi th&igrave;...<br />\r\nNh&agrave; vua hơi l&uacute;ng t&uacute;ng v&agrave; c&oacute; vẻ phật &yacute;.&nbsp;V&igrave; vua ch&uacute; trọng nhất l&agrave; uy quyền của m&igrave;nh phải được tu&acirc;n theo. Ng&agrave;i kh&ocirc;ng tha thứ được sự tr&aacute;i lệnh. Đ&oacute; l&agrave; một nh&agrave; vua chuy&ecirc;n chế. Nhưng, v&igrave; ng&agrave;i rất tốt bụng, n&ecirc;n chỉ ra những c&aacute;i lệnh hợp l&yacute; th&ocirc;i.&nbsp;Ng&agrave;i thường ph&aacute;n: &quot;Nếu ta m&agrave; ra lệnh ột v&otilde; tướng phải biến th&agrave;nh chim biển, v&agrave; v&otilde; tướng ấy chẳng tu&acirc;n lệnh ta, th&igrave; ấy kh&ocirc;ng phải l&agrave; lỗi của v&otilde; tướng đ&oacute;. ấy l&agrave; lỗi của ta.&quot;<br />\r\n&ndash; T&ocirc;i ngồi được kh&ocirc;ng ạ? &Ocirc;ng ho&agrave;ng nhỏ rụt r&egrave; hỏi.<br />\r\n&ndash; Ta lệnh cho ngươi ngồi, vua đ&aacute;p lại, vừa uy nghi k&eacute;o một vạt &aacute;o cho&agrave;ng l&ocirc;ng th&uacute; l&ecirc;n.<br />\r\nNhưng &ocirc;ng ho&agrave;ng b&eacute; nhỏ lấy l&agrave;m kinh ngạc. C&aacute;i h&agrave;nh tinh thật l&agrave; b&eacute;. Đức vua c&oacute; thể trị v&igrave; tr&ecirc;n c&aacute;i g&igrave; ở đ&acirc;y?<br />\r\n&ndash; T&acirc;u bệ hạ, em n&oacute;i, c&uacute;i xin bệ hạ cho t&ocirc;i được hỏi...<br />\r\n&ndash; Ta ra lệnh cho ngươi hỏi, vua vội v&agrave;ng n&oacute;i.<br />\r\n&ndash; T&acirc;u bệ hạ, ng&agrave;i trị v&igrave; tr&ecirc;n c&aacute;i g&igrave;?<br />\r\n&ndash; Tr&ecirc;n tất cả. Nh&agrave; vua đ&aacute;p, hết sức giản dị.<br />\r\n&ndash; Tr&ecirc;n tất cả?<br />\r\nĐức vua ph&aacute;c một cử chỉ dứt kho&aacute;t chỉ c&aacute;i h&agrave;nh tinh của m&igrave;nh, c&aacute;c h&agrave;nh tinh kh&aacute;c v&agrave; c&aacute;c ng&ocirc;i sao.<br />\r\n&ndash; Tr&ecirc;n tất cả những c&aacute;i đ&oacute;? &Ocirc;ng ho&agrave;ng nhỏ hỏi.<br />\r\n&ndash; Tr&ecirc;n tất cả những c&aacute;i đ&oacute;... đức vua trả lời.<br />\r\nBởi v&igrave; đ&oacute; kh&ocirc;ng phải l&agrave; một vị vua chuy&ecirc;n chế m&agrave; c&ograve;n l&agrave; một vị vua to&agrave;n năng.<br />\r\n&ndash; C&aacute;c ng&ocirc;i sao c&oacute; tu&acirc;n lệnh bệ hạ kh&ocirc;ng?&ndash; Chắc chắn rồi, vua trả lời em. Ch&uacute;ng lập tức tu&acirc;n lệnh. Trẫm kh&ocirc;ng dung thứ sự tr&aacute;i lời.<br />\r\nMột quyền lực như thế l&agrave;m cho &ocirc;ng ho&agrave;ng nhỏ th&iacute;ch m&ecirc;. Nếu em m&agrave; c&oacute; quyền lực ấy, em c&oacute; thể mặc sức m&agrave; ngắm, kh&ocirc;ng phải l&agrave; bốn mươi bốn, m&agrave; đến bảy mươi hai, đến một trăm, đến cả hai trăm cảnh mặt trời lặn trong một ng&agrave;y, m&agrave; chẳng phải x&ecirc; dịch ghế ngồi. Rồi em cảm thấy hơi buồn v&igrave; chợt nghĩ đễn c&aacute;i tinh cầu nhỏ bị bỏ rơi của m&igrave;nh, em đ&aacute;nh bạo xin nh&agrave; vua một &acirc;n huệ:<br />\r\n&ndash; T&ocirc;i muốn được xem cảnh mặt trời lặn... xin bệ hạ h&atilde;y l&agrave;m t&ocirc;i vui l&ograve;ng, h&atilde;y ra lệnh ặt trời lặn...<br />\r\n&ndash; Nếu ta ra lệnh ột v&otilde; tướng bay từ đo&aacute; hoa n&agrave;y sang đo&aacute; hoa kia như một con bướm, hay lệnh cho &ocirc;ng ta viết một vở bi kịch, hay biến th&agrave;nh chim biển, v&agrave; nếu vị v&otilde; tướng ấy kh&ocirc;ng tu&acirc;n lệnh, th&igrave; lỗi ấy ở &ocirc;ng ta hay ở ta?&ndash; ởbệ hạ, &ocirc;ng ho&agrave;ng nhỏ cả quyết.<br />\r\n&ndash; Đ&uacute;ng. Phải ỗi người l&agrave;m việc người đ&oacute; c&oacute; thể l&agrave;m, đức vua nhắc lại. Quyền lực trước hết phải dựa tr&ecirc;n lẽ phải. Nếu nh&agrave; ngươi ra lệnh cho thần d&acirc;n của m&igrave;nh nhảy xuống bể, họ sẽ l&agrave;m c&aacute;ch mạng. Ta c&oacute; quyền buộc tu&acirc;n lệnh ta v&igrave; mọi lệnh ta đều hợp l&yacute;.<br />\r\n&ndash; Thế cảnh mặt trời lặn của t&ocirc;i th&igrave; sao? &Ocirc;ng ho&agrave;ng nhỏ vốn đ&atilde; đặt ra c&acirc;u hỏi th&igrave; kh&ocirc;ng bao giờ qu&ecirc;n, hỏi lại.<br />\r\n&ndash; Cảnh mặt trời lặn của ngươi, ngươi sẽ c&oacute;. Ta muốn thế. Nhưng trong khoa học cai trị của ta, ta phải chờ đến l&uacute;c đủ mọi điều kiện.<br />\r\n&ndash; Đến bao giờ th&igrave; đủ? &Ocirc;ng ho&agrave;ng nhỏ hỏi.<br />\r\n&ndash; ừm! ừm! Nh&agrave; vua n&oacute;i, thoạt ti&ecirc;n tra một cuốn lịch to tướng, ừm! ừm! V&agrave;o l&uacute;c... khoảng... khoảng... V&agrave;o l&uacute;c chiều nay, khoảng bảy giờ bốn mươi ph&uacute;t! Nh&agrave; ngươi sẽ thấy được lệnh ta được tu&acirc;n thủ.<br />\r\n&Ocirc;ng ho&agrave;ng nhỏ ng&aacute;p. Em tiếc cho cảnh mặt trời lặn hụt của em. V&agrave; rồi em cũng hơi thấy ch&aacute;n:<br />\r\n&ndash; T&ocirc;i chẳng c&oacute; g&igrave; l&agrave;m ở đ&acirc;y nữa, em n&oacute;i với nh&agrave; vua, t&ocirc;i sẽ đi.&ndash; Ngươi chớ đi, nh&agrave; vua vừa mới ki&ecirc;u h&atilde;nh xiết bao v&igrave; c&oacute; được một thần d&acirc;n, đ&aacute;p lời &ocirc;ng ho&agrave;ng nhỏ. Chớ đi, ta phong ngươi l&agrave;m thượng thư!<br />\r\n&ndash; Thượng thư bộ g&igrave;?<br />\r\n&ndash; Bộ... bộ tư ph&aacute;p!<br />\r\n&ndash; Nhưng c&oacute; ai để x&eacute;t xử đ&acirc;u ạ!<br />\r\n&ndash; Chưa thể biết, vua n&oacute;i với &ocirc;ng ho&agrave;ng nhỏ. Ta chưa đi tuần tra khắp vương quốc của ta. Ta gi&agrave; qu&aacute; rồi, kh&ocirc;ng c&oacute; đủ chỗ để một cỗ xa gi&aacute; cho ta, m&agrave; đi bộ th&igrave; ta nhọc lắm.<br />\r\n&ndash; ồ! Nhưng t&ocirc;i đ&atilde; nh&igrave;n thấy, &ocirc;ng ho&agrave;ng nhỏ nghi&ecirc;ng m&igrave;nh nh&igrave;n sang ph&iacute;a b&ecirc;n kia của h&agrave;nh tinh. B&ecirc;n kia cũng chẳng c&oacute; ai đ&acirc;u ạ...<br />\r\n&ndash; Thế th&igrave; ngươi h&atilde;y tự x&eacute;t xử lấy m&igrave;nh đi, đức vua đ&aacute;p lại em. Đ&oacute; l&agrave; điều kh&oacute; nhất. X&eacute;t m&igrave;nh kh&oacute; hơn x&eacute;t người nhiều. Nếu ngươi x&eacute;t được m&igrave;nh đ&uacute;ng đắn, th&igrave; ngươi l&agrave; một bậc hiền lương ch&acirc;n ch&iacute;nh.<br />\r\n&ndash; T&ocirc;i, &ocirc;ng ho&agrave;ng nhỏ đ&aacute;p, t&ocirc;i c&oacute; thể tự x&eacute;t m&igrave;nh bất cứ ở đ&acirc;u. T&ocirc;i kh&ocirc;ng nhất thiết phải ở đ&acirc;y.<br />\r\n&ndash; Hừm! Hừm! Vua n&oacute;i, h&igrave;nh như tr&ecirc;n h&agrave;nh tinh của ta, đ&acirc;u đ&oacute; c&oacute; một con chuột. Đ&ecirc;m ta nghe n&oacute; k&ecirc;u. Nh&agrave; ngươi c&oacute; thể x&eacute;t xử con chuột gi&agrave; ấy. Thỉnh thoảng ngươi gh&eacute;p n&oacute; v&agrave;o tội tử h&igrave;nh. Như thế, cuộc đời của n&oacute; sẽ tuỳ ở luật ph&aacute;p của nh&agrave; ngươi. Nhưng mỗi lần buộc tội xong, th&igrave; ngươi lại n&ecirc;n &acirc;n x&aacute; để d&agrave;nh dụm chuột. Chỉ c&oacute; mỗi một con ấy th&ocirc;i.<br />\r\n&ndash; T&ocirc;i, &ocirc;ng ho&agrave;ng nhỏ đ&aacute;p, t&ocirc;i kh&ocirc;ng th&iacute;ch buộc tội tử h&igrave;nh, v&agrave; t&ocirc;i chắc rằng t&ocirc;i sắp sửa ra đi.<br />\r\n&ndash; Kh&ocirc;ng, vua n&oacute;i.<br />\r\nNhưng &ocirc;ng ho&agrave;ng nhỏ, đ&atilde; chuẩn bị xong, kh&ocirc;ng muốn l&agrave;m phiền l&ograve;ng vị vua gi&agrave;:<br />\r\n&ndash; Nếu bệ hạ muốn được tu&acirc;n theo một c&aacute;ch đ&uacute;ng đắn, th&igrave; phải cho t&ocirc;i một c&aacute;i lệnh hợp l&yacute;. V&iacute; dụ như phải ra lệnh cho t&ocirc;i ra đi tức khắc. H&igrave;nh như mọi điều kiện đều thuận lợi...<br />\r\nNh&agrave; vua chẳng biết trả lời sao. Thoạt đầu &ocirc;ng ho&agrave;ng nhỏ hơi do dự, rồi thở d&agrave;i, em bước đi.<br />\r\n&ndash; Ta phong cho ngươi l&agrave;m đại sứ, nh&agrave; vua vội v&agrave;ng k&ecirc;u l&ecirc;n.<br />\r\nTr&ocirc;ng ng&agrave;i c&oacute; vẻ uy nghi lẫm liệt.&nbsp;Những người lớn thật rất kỳ quặc, cậu ho&agrave;ng tử thầm nhủ với ch&iacute;nh m&igrave;nh trong suốt cuộc h&agrave;nh tr&igrave;nh.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>XI</strong></span><br />\r\n<br />\r\nTr&ecirc;n tinh cầu thứ hai cư tr&uacute; một g&atilde; kho&aacute;c l&aacute;c.<br />\r\n&ndash; &aacute;! &agrave;! K&igrave;a một g&atilde; ngưỡng mộ đến thăm ta! G&atilde; kho&aacute;c l&aacute;c k&ecirc;u l&ecirc;n khi nh&igrave;n thấy &ocirc;ng ho&agrave;ng nhỏ từ xa. Bởi v&igrave;, đối với kẻ kho&aacute;c l&aacute;c, những người c&ograve;n lại đều l&agrave; kẻ ngưỡng mộ m&igrave;nh.<br />\r\n&ndash; Ch&agrave;o anh, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i. Anh c&oacute; c&aacute;i mũ ngộ qu&aacute;.<br />\r\n&ndash; C&aacute;i đ&oacute; l&agrave; để m&agrave; ch&agrave;o đấy, g&atilde; kho&aacute;c l&aacute;c trả lời. Để ch&agrave;o khi người ta hoan h&ocirc; t&ocirc;i. Kh&ocirc;ng may l&agrave; chẳng ai qua đ&acirc;y bao giờ cả.<br />\r\n&ndash; Thế &agrave;? &Ocirc;ng ho&agrave;ng nhỏ n&oacute;i, kh&ocirc;ng hiểu g&igrave; cả.<br />\r\n&ndash; H&atilde;y vỗ tay n&agrave;y v&agrave;o tay kia đi, g&atilde; kho&aacute;c l&aacute;c liền khuy&ecirc;n.<br />\r\n&Ocirc;ng ho&agrave;ng nhỏ vỗ tay n&agrave;y v&agrave;o tay kia. G&atilde; kho&aacute;c l&aacute;c ngả mũ ch&agrave;o một c&aacute;ch khi&ecirc;m tốn.<br />\r\n&ndash; Coi bộ vui hơn đi thăm nh&agrave; vua, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i thầm. V&agrave; em tiếp tục vỗ tay n&agrave;y v&agrave;o tay kia. G&atilde; kho&aacute;c l&aacute;c lại ngả mũ ch&agrave;o.<br />\r\nSau năm ph&uacute;t tập dượt, &ocirc;ng ho&agrave;ng nhỏ thấy ch&aacute;n v&igrave; sự đơn điệu của c&aacute;i tr&ograve; chơi ấy:<br />\r\n&ndash; Thế, muốn cho chiếc mũ n&oacute; rơi tuột xuống, em hỏi, th&igrave; phải l&agrave;m sao?<br />\r\nNhưng g&atilde; kho&aacute;c l&aacute;c kh&ocirc;ng nghe thấy. Những người kho&aacute;c l&aacute;c chẳng nghe thấy g&igrave; ngo&agrave;i những c&acirc;u ca ngợi.<br />\r\n&ndash; Ch&uacute; em kh&acirc;m phục ta nhiều thật chứ? G&atilde; hỏi &ocirc;ng ho&agrave;ng nhỏ.&ndash; Kh&acirc;m phục nghĩa l&agrave; thế n&agrave;o?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ndash; Kh&acirc;m phục nghĩa l&agrave; thừa nhận ta l&agrave; người đẹp nhất, ăn mặc sang nhất, gi&agrave;u c&oacute; nhất v&agrave; th&ocirc;ng minh nhất tr&ecirc;n h&agrave;nh tinh.<br />\r\n&ndash; Nhưng anh chỉ c&oacute; một m&igrave;nh tr&ecirc;n h&agrave;nh tinh của anh chứ mấy?<br />\r\n&ndash; H&atilde;y chiều ta đi m&agrave;. Dẫu sao cứ h&atilde;y cứ kh&acirc;m phục ta!<br />\r\n&ndash; T&ocirc;i kh&acirc;m phục anh, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i, hơi nh&uacute;n vai, nhưng sao điều đ&oacute; lại l&agrave;m anh quan t&acirc;m đến thế nhỉ?<br />\r\nV&agrave; &ocirc;ng ho&agrave;ng nhỏ ra đi.&nbsp;Những người lớn chắc chắn l&agrave; kỳ quặc rồi, em chỉ n&oacute;i đơn giản c&oacute; thế trong suốt cuộc h&agrave;nh tr&igrave;nh. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>XII</strong></span><br />\r\n<br />\r\nTinh cầu tiếp theo c&oacute; một bợm nhậu cư tr&uacute;. Cuộc viếng thăm lần n&agrave;y hết sức ngắn nhưng lại l&agrave;m cho &ocirc;ng ho&agrave;ng nhỏ mi&ecirc;n man buồn: &ndash; Anh l&agrave;m g&igrave; đấy? &Ocirc;ng ho&agrave;ng nhỏ n&oacute;i với tay bợm nhậu đang ngồi im lặng trước một bộ sưu tập chai kh&ocirc;ng v&agrave; một bộ sưu tập chai đầy.<br />\r\n&ndash; Ta nhậu, bợm nhậu trả lời, vẻ thiểu n&atilde;o.&ndash; Tại sao anh nhậu? &Ocirc;ng ho&agrave;ng nhỏ hỏi anh ta.&ndash; Để qu&ecirc;n, bợm nhậu trả lời.<br />\r\n&ndash; Để qu&ecirc;n c&aacute;i g&igrave;? &Ocirc;ng ho&agrave;ng nhỏ hỏi trong l&uacute;c bắt đầu cảm thấy &aacute;i ngại cho hắn.<br />\r\n&ndash; Để qu&ecirc;n nỗi xấu hổ của ta, bợm nhậu c&uacute;i đầu th&uacute; nhận.<br />\r\n&ndash; Xấu hổ v&igrave; c&aacute;i g&igrave;? &Ocirc;ng ho&agrave;ng nhỏ hỏi, đ&atilde; muốn gi&uacute;p đỡ hắn.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ndash; Xấu hổ v&igrave; c&aacute;i nhậu! Bợm nhậu kết th&uacute;c v&agrave; nhất quyết lặng im.<br />\r\nV&agrave; &ocirc;ng ho&agrave;ng th&igrave; ra đi, sửng sốt.Những người lớn nhất định l&agrave; rất kỳ quặc, cậu tự n&oacute;i thầm trong suốt cuộc h&agrave;nh tr&igrave;nh. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>XIII</strong></span><br />\r\n<br />\r\nTinh cầu thứ tư l&agrave; của một nh&agrave; doanh nghiệp. &Ocirc;ng n&agrave;y bận rộn đến nỗi kh&ocirc;ng ngẩng được đầu l&ecirc;n khi &ocirc;ng ho&agrave;ng nhỏ tới.<br />\r\n&ndash; Ch&agrave;o &ocirc;ng, em n&oacute;i với hắn. Điếu thuốc l&aacute; của &ocirc;ng tắt rồi.<br />\r\n&ndash; Ba với hai l&agrave; năm. Năm với bảy l&agrave; mười hai. Mười hai với ba l&agrave; mười lăm. Ch&agrave;o ch&uacute;. Mười lăm với bảy l&agrave; hăm hai. Hăm hai với s&aacute;u l&agrave; hăm t&aacute;m. Chẳng c&oacute; thời giờ m&agrave; ch&acirc;m lại. Hăm s&aacute;u với năm ba mươi mốt. Xong! Tất cả l&agrave; năm trăm linh một triệu s&aacute;u trăm năm mươi hai ngh&igrave;n bảy trăm ba mươi mốt.<br />\r\n&ndash; Năm trăm triệu c&aacute;i g&igrave; vậy?<br />\r\n&ndash; Hử? Thế ch&uacute; vẫn c&ograve;n đấy &agrave;?. Năm trăm linh một triệu... ta cũng chẳng biết l&agrave; c&aacute;i g&igrave; nữa. Ta c&oacute; qu&aacute; nhiều c&ocirc;ng việc! Ta rất đứng đắn, ta, ta kh&ocirc;ng th&iacute;ch những tr&ograve; nhảm nh&iacute;! Hai với năm l&agrave; bảy...<br />\r\n&ndash; Năm trăm linh một triệu c&aacute;i g&igrave;? &Ocirc;ng ho&agrave;ng nhỏ lặp lại, suốt đời em, kh&ocirc;ng bao giờ bỏ ra một c&acirc;u hỏi một khi đ&atilde; n&ecirc;u n&oacute; ra.<br />\r\nNh&agrave; doanh nghiệp ngẩng đầu l&ecirc;n:<br />\r\n&ndash; Năm mươi bốn năm ta ở tr&ecirc;n c&aacute;i h&agrave;nh tinh n&agrave;y, ta chỉ bị quấy rầy c&oacute; ba lần. Lần đầu c&aacute;ch đ&acirc;y hai mươi hai năm do một con bọ hung chỉ c&oacute; trời biết từ đ&acirc;u rơi xuống. N&oacute; g&acirc;y n&ecirc;n một tiếng động khủng khiếp v&agrave; l&agrave;m cho ta cộng sai bốn chỗ trong một b&agrave;i t&iacute;nh cộng. Lần thứ hai, l&agrave; c&aacute;ch đ&acirc;y mười một năm, do một cơn cảm c&uacute;m. Ta kh&ocirc;ng c&oacute; tập thể dục. Ta kh&ocirc;ng c&oacute; th&igrave; giờ chơi bời. Ta, ta l&agrave; một người đứng đắn! Lần thứ ba... l&agrave; lần n&agrave;y! Khi ta đang t&iacute;nh tới năm trăm linh một triệu...<br />\r\n&ndash; Triệu c&aacute;i g&igrave;?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nh&agrave; doanh nghiệp biết kh&ocirc;ng hy vọng g&igrave; được y&ecirc;n th&acirc;n:<br />\r\n&ndash; Triệu những vật nho nhỏ đ&ocirc;i khi nh&igrave;n thấy tr&ecirc;n trời ấy.<br />\r\n&ndash; Những con ruồi?&ndash; Kh&ocirc;ng phải, những vật nhỏ lấp l&aacute;nh ấy.<br />\r\n&ndash; Những con ong?<br />\r\n&ndash; Kh&ocirc;ng m&agrave;... Những vật nho nhỏ v&agrave;ng &oacute;ng vẫn l&agrave;m cho bọn người v&ocirc; t&iacute;ch sự ch&uacute;ng n&oacute; mơ m&agrave;ng ấy. Ta, ta l&agrave; một người đ&uacute;ng đắn! Ta kh&ocirc;ng c&oacute; th&igrave; giờ đ&acirc;u m&agrave; mơ m&agrave;ng.<br />\r\n&ndash; &agrave;! Những ng&ocirc;i sao.<br />\r\n&ndash; Đ&uacute;ng rồi đấy. Những ng&ocirc;i sao.<br />\r\n&ndash; Thế &ocirc;ng l&agrave;m g&igrave; với năm trăm triệu ng&ocirc;i sao?<br />\r\n&ndash; Năm trăm linh một triệu s&aacute;u trăm năm mươi hai ngh&igrave;n bảy trăm ba mươi mốt. Ta, ta l&agrave; một người đứng đắn, ta rất ch&iacute;nh x&aacute;c!<br />\r\n&ndash; Thế &ocirc;ng l&agrave;m g&igrave; với những ng&ocirc;i sao?&ndash; Ta l&agrave;m g&igrave; &agrave;?<br />\r\n&ndash; V&acirc;ng.<br />\r\n&ndash; Chẳng l&agrave;m g&igrave; sất. Ta chiếm hữu ch&uacute;ng.<br />\r\n&ndash; &Ocirc;ng chiếm hữu những ng&ocirc;i sao?<br />\r\n&ndash; Phải.<br />\r\n&ndash; Nhưng t&ocirc;i biết một &ocirc;ng vua cũng...<br />\r\n&ndash; C&aacute;c &ocirc;ng vua kh&ocirc;ng chiếm hữu g&igrave; cả. Họ chỉ &quot;trị v&igrave;&quot; ở tr&ecirc;n thế th&ocirc;i. Rất kh&aacute;c nhau.<br />\r\n&ndash; Thế việc sở hữu những ng&ocirc;i sao gi&uacute;p g&igrave; cho &ocirc;ng?<br />\r\n&ndash; N&oacute; gi&uacute;p ta gi&agrave;u.<br />\r\n&ndash; Gi&agrave;u gi&uacute;p &ocirc;ng được g&igrave;?<br />\r\n&ndash; Mua những ng&ocirc;i sao kh&aacute;c, nếu c&oacute; người t&igrave;m thấy nữa.<br />\r\nC&aacute;i &ocirc;ng n&agrave;y, &ocirc;ng ho&agrave;ng nhỏ nghĩ thầm, &ocirc;ng ta l&yacute; sự hơi giống tay bợm nhậu của m&igrave;nh.&nbsp;Tuy thế em vẫn hỏi th&ecirc;m:<br />\r\n&ndash; L&agrave;m thế n&agrave;o người ta sở hữu những ng&ocirc;i sao?<br />\r\n&ndash; Ch&uacute;ng của ai n&agrave;o? Nh&agrave; doanh nghiệp vặn lại, sửng cồ.<br />\r\n&ndash; T&ocirc;i kh&ocirc;ng biết. Kh&ocirc;ng của ai cả.<br />\r\n&ndash; Thế th&igrave; ch&uacute;ng l&agrave; của ta, bởi ta nghĩ tới trước nhất.<br />\r\n&ndash; Chỉ thế l&agrave; đủ ư?<br />\r\n&ndash; Chắc rồi. Khi ch&uacute; t&igrave;m thấy một vi&ecirc;n kim cương kh&ocirc;ng l&agrave; của ai cả, th&igrave; n&oacute; l&agrave; của ch&uacute;. Khi ch&uacute; t&igrave;m thấy một h&ograve;n đảo kh&ocirc;ng phải l&agrave; của ai cả, th&igrave; h&ograve;n đảo ấy l&agrave; của ch&uacute;. Khi ch&uacute; l&agrave; người đầu ti&ecirc;n c&oacute; một s&aacute;ng kiến, ch&uacute; đăng k&yacute; t&aacute;c quyền cho s&aacute;ng kiến đ&oacute;, n&oacute; l&agrave; của ch&uacute;. V&agrave; ta c&oacute; được những ng&ocirc;i sao kia, v&igrave; chưa hề c&oacute; ai trước ta nghĩ đến việc chiếm hữu ch&uacute;ng.<br />\r\n&ndash; Điều n&agrave;y th&igrave; đ&uacute;ng, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i. &Ocirc;ng d&ugrave;ng ch&uacute;ng để l&agrave;m g&igrave;?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ndash; Ta quản l&yacute; ch&uacute;ng. Ta đếm đi rồi ta đếm lại ch&uacute;ng, nh&agrave; doanh nghiệp n&oacute;i. Kh&oacute; đấy. Nhưng ta l&agrave; một người đứng đắn!<br />\r\n&Ocirc;ng ho&agrave;ng nhỏ vẫn chưa bằng l&ograve;ng:<br />\r\n&ndash; T&ocirc;i đấy ư, nếu t&ocirc;i c&oacute; một chiếc khăn qu&agrave;ng, t&ocirc;i qu&agrave;ng n&oacute; v&agrave;o cổ v&agrave; mang n&oacute; đi. T&ocirc;i, nếu t&ocirc;i c&oacute; một b&ocirc;ng hoa, t&ocirc;i c&oacute; thể h&aacute;i b&ocirc;ng hoa đ&oacute; v&agrave; mang n&oacute; đi. C&ograve;n &ocirc;ng đ&acirc;u c&oacute; thể h&aacute;i c&aacute;c ng&ocirc;i sao!<br />\r\n&ndash; Kh&ocirc;ng, nhưng ta c&oacute; thể bỏ ch&uacute;ng v&agrave;o ng&acirc;n h&agrave;ng.&ndash; Nghĩa l&agrave; thế n&agrave;o?<br />\r\n&ndash; Nghĩa l&agrave; ta viết tr&ecirc;n một tờ giấy con số ng&ocirc;i sao của ta. Sau đ&oacute; ta kho&aacute; chặt mẩu giấy ấy trong một c&aacute;i tủ.<br />\r\n&ndash; Chỉ thế th&ocirc;i &agrave;?<br />\r\n&ndash; Thế đủ rồi.<br />\r\nBuồn cười thật, &ocirc;ng ho&agrave;ng nhỏ nghĩ thầm. C&oacute; vẻ n&ecirc;n thơ đấy. Nhưng m&agrave; kh&ocirc;ng đứng đắn lắm đ&acirc;u.&nbsp;&Ocirc;ng ho&agrave;ng nhỏ nghĩ về việc đứng đắn rất kh&aacute;c với những người lớn.<br />\r\n&ndash; T&ocirc;i, em lại n&oacute;i, t&ocirc;i c&oacute; một b&ocirc;ng hoa m&agrave; h&ocirc;m n&agrave;o cũng tưới. T&ocirc;i c&oacute; ba quả n&uacute;i lửa m&agrave; tuần n&agrave;o t&ocirc;i cũng nạo v&eacute;t. T&ocirc;i cũng nạo v&eacute;t cả quả n&uacute;i lửa đ&atilde; tắt. Biết đ&acirc;u đấy! C&aacute;i đ&oacute; gi&uacute;p &iacute;ch cho c&aacute;c quả n&uacute;i lửa, v&agrave; gi&uacute;p &iacute;ch cho b&ocirc;ng hoa của t&ocirc;i, n&ecirc;n t&ocirc;i c&oacute; ch&uacute;ng. Nhưng &ocirc;ng chẳng gi&uacute;p &iacute;ch g&igrave; cho những ng&ocirc;i sao cả...<br />\r\nNh&agrave; doanh nghiệp mở miệng nhưng chẳng biết trả lời ra sao cả, v&agrave; &ocirc;ng ho&agrave;ng nhỏ ra đi.&nbsp;Những người lớn nhất định l&agrave; những người ho&agrave;n to&agrave;n kỳ qu&aacute;i. Em nghĩ thầm một c&aacute;ch đơn giản như vậy trong suốt cuộc h&agrave;nh tr&igrave;nh. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>XIV</strong></span><br />\r\n<br />\r\nH&agrave;nh tinh thứ năm rất lạ. N&oacute; l&agrave; c&aacute;i b&eacute; nhất trong số c&aacute;c h&agrave;nh tinh. ởđấy chỉ đủ chỗ ột c&acirc;y cột đ&egrave;n v&agrave; một người thắp đ&egrave;n. &Ocirc;ng ho&agrave;ng nhỏ nghĩ m&atilde;i nhưng kh&ocirc;ng thể n&agrave;o hiểu được tại sao, đ&acirc;u đ&oacute; trong bầu trời, tr&ecirc;n một h&agrave;nh tinh kh&ocirc;ng nh&agrave; cửa, kh&ocirc;ng d&acirc;n cư, lại c&oacute; một c&acirc;y cột đ&egrave;n v&agrave; một người thắp đ&egrave;n để l&agrave;m chi. Tuy vậy em tự bảo:<br />\r\n&ndash; Rất c&oacute; thể người n&agrave;y l&agrave; v&ocirc; l&yacute;. Tuy thế &ocirc;ng ta c&ograve;n &iacute;t v&ocirc; l&yacute; hơn &ocirc;ng vua, &iacute;t v&ocirc; l&yacute; hơn kẻ kho&aacute;c l&aacute;c, &iacute;t v&ocirc; l&yacute; hơn nh&agrave; doanh nghiệp, &iacute;t v&ocirc; l&yacute; hơn tay bợm nhậu. &iacute;t ra c&ocirc;ng việc &ocirc;ng ta l&agrave;m c&ograve;n c&oacute; một &yacute; nghĩa. Khi &ocirc;ng ta thắp c&acirc;y đ&egrave;n của m&igrave;nh, như l&agrave; &ocirc;ng ta sinh th&ecirc;m một ng&ocirc;i sao hay một b&ocirc;ng hoa. Khi &ocirc;ng ta tắt c&acirc;y đ&egrave;n, &ocirc;ng ta ru b&ocirc;ng hoa hay ng&ocirc;i sao ấy ngủ. ấy l&agrave; một c&ocirc;ng việc rất đẹp chứ. Việc ấy mới thật sự l&agrave; c&oacute; &iacute;ch l&agrave; v&igrave; n&oacute; đẹp.&nbsp;Khi đến gần tinh cầu, em k&iacute;nh trọng ch&agrave;o người thắp đ&egrave;n:<br />\r\n&ndash; Ch&uacute;c một ng&agrave;y tốt l&agrave;nh. Tại sao &ocirc;ng tắt đ&egrave;n đi?<br />\r\n&ndash; Đ&oacute; l&agrave; điều lệnh, người thắp đ&egrave;n trả lời. Ch&uacute;c một ng&agrave;y tốt l&agrave;nh.<br />\r\n&ndash; Điều lệnh g&igrave; vậy?<br />\r\n&ndash; L&agrave; tắt đ&egrave;n đi. Ch&uacute;c một buổi tối tốt l&agrave;nh.<br />\r\nV&agrave; &ocirc;ng ta lại thắp đ&egrave;n l&ecirc;n.<br />\r\n&ndash; Nhưng tại sao &ocirc;ng lại vừa thắp đ&egrave;n l&ecirc;n?<br />\r\n&ndash; Điều lệnh m&agrave;, người thắp đ&egrave;n trả lời.<br />\r\n&ndash; T&ocirc;i kh&ocirc;ng hiểu, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i.<br />\r\n&ndash; Chẳng c&oacute; g&igrave; m&agrave; hiểu cả, người thắp đ&egrave;n n&oacute;i. Điều lệnh l&agrave; điều lệnh. Ch&uacute;c một ng&agrave;y tốt l&agrave;nh.<br />\r\nR&ocirc;i &ocirc;ng ta tắt c&acirc;y đ&egrave;n.&nbsp;V&agrave; &ocirc;ng ta thấm mồ h&ocirc;i tr&ecirc;n tr&aacute;n bằng một chiếc m&ugrave;i soa ca r&ocirc; đỏ.<br />\r\n&ndash; Nghề của ta l&agrave;m đ&acirc;y thật kinh khủng. Xưa kia th&igrave; n&oacute; c&ograve;n c&oacute; l&yacute;. S&aacute;ng ta tắt, tối ta thắp. Phần c&ograve;n lại ta nghỉ, phần đ&ecirc;m c&ograve;n lại ta ngủ...<br />\r\n&ndash; Nhưng sau đ&oacute; điều lệnh thay đổi sao?<br />\r\n&ndash; Điều lệnh kh&ocirc;ng thay đổi, người thắp đ&egrave;n n&oacute;i. Bi kịch l&agrave; ở chỗ đ&oacute;! Cứ mỗi năm h&agrave;nh tinh n&agrave;y lại quay nhanh hơn, thế m&agrave; điều lệnh kh&ocirc;ng thay đổi!<br />\r\n&ndash; Thế th&igrave; sao? &Ocirc;ng ho&agrave;ng nhỏ hỏi.<br />\r\n&ndash; V&igrave; hiện nay n&oacute; quay mỗi ph&uacute;t một v&ograve;ng, ta kh&ocirc;ng c&ograve;n lấy một gi&acirc;y để nghỉ ngơi. Mỗi ph&uacute;t ta phải thắp đ&egrave;n v&agrave; phải tắt đ&egrave;n một lần!<br />\r\n&ndash; Lạ thực đấy! ởđ&acirc;y ng&agrave;y d&agrave;i chỉ c&oacute; một ph&uacute;t th&ocirc;i!<br />\r\n&ndash; Chẳng c&oacute; g&igrave; lạ cả, người thắp đ&egrave;n n&oacute;i. Ta chuyện tr&ograve; với nhau thế m&agrave; đ&atilde; một th&aacute;ng rồi đấy.<br />\r\n&ndash; Một th&aacute;ng kia ư?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ndash; Phải, ba mươi ph&uacute;t. Ba mươi ng&agrave;y. Ch&uacute;c một đ&ecirc;m tốt l&agrave;nh.<br />\r\nRồi &ocirc;ng lại thắp ngọn đ&egrave;n.&nbsp;&Ocirc;ng ho&agrave;ng nhỏ nh&igrave;n &ocirc;ng ta v&agrave; thấy y&ecirc;u mến người thắp đ&egrave;n trung th&agrave;nh đến thế với điều lệnh. Cậu em nhớ tới những cảnh mặt trời lặn m&agrave; xưa kia ch&iacute;nh em phải đi kiếm để xem, bằng c&aacute;ch dịch ghế ngồi. Em muốn gi&uacute;p &ocirc;ng bạn th&acirc;n:<br />\r\n&ndash; N&agrave;y &ocirc;ng... t&ocirc;i biết c&aacute;ch cho &ocirc;ng nghỉ ngơi khi n&agrave;o &ocirc;ng muốn đấy...&ndash; Ta lu&ocirc;n lu&ocirc;n muốn, người thắp đ&egrave;n n&oacute;i.<br />\r\nBởi v&igrave; người ta c&oacute; thể, c&ugrave;ng một l&uacute;c, vừa trung th&agrave;nh lại vừa lười biếng.&nbsp;&Ocirc;ng ho&agrave;ng nhỏ hỏi tiếp:<br />\r\n&ndash; C&aacute;i h&agrave;nh tinh của &ocirc;ng nhỏ đến nỗi &ocirc;ng c&oacute; thể bước sải ba bước l&agrave; đi gi&aacute;p một v&ograve;ng. &Ocirc;ng chỉ c&oacute; việc đi chầm chậm để l&uacute;c n&agrave;o cũng ở dưới &aacute;nh mặt trời cả. Khi n&agrave;o &ocirc;ng muốn nghỉ, &ocirc;ng cứ bước đi... thế l&agrave; &ocirc;ng muốn ng&agrave;y d&agrave;i bao nhi&ecirc;u th&igrave; n&oacute; d&agrave;i bấy nhi&ecirc;u.<br />\r\n&ndash; C&aacute;i ấy chẳng được việc g&igrave; cho ta, người thắp đ&egrave;n n&oacute;i. C&aacute;i m&agrave; ta th&iacute;ch nhất tr&ecirc;n đời, l&agrave; ngủ.<br />\r\n&ndash; Thế th&igrave; thật l&agrave; kh&ocirc;ng may, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i.<br />\r\n&ndash; Thật l&agrave; kh&ocirc;ng may, người thắp đ&egrave;n n&oacute;i. Ch&uacute;c một ng&agrave;y tốt l&agrave;nh.<br />\r\nRồi &ocirc;ng ta tắt đ&egrave;n.&nbsp;C&aacute;i &ocirc;ng n&agrave;y, &ocirc;ng ho&agrave;ng nhỏ nghĩ thầm trong l&uacute;c tiếp tục cuộc h&agrave;nh tr&igrave;nh. &Ocirc;ng ta sẽ bị tất cả những người kia khinh thường, &ocirc;ng vua, l&atilde;o kho&aacute;c l&aacute;c, tay bợm nhậu, nh&agrave; doanh nghiệp. Trong khi đ&oacute; chỉ c&oacute; &ocirc;ng ta l&agrave; kh&ocirc;ng thấy buồn cười. C&oacute; lẽ bởi v&igrave; &ocirc;ng lo toan ột c&aacute;i g&igrave; kh&aacute;c chứ kh&ocirc;ng phải l&agrave; bản th&acirc;n &ocirc;ng ta.Em thở d&agrave;i tiếc rẻ v&agrave; nghĩ tiếp:<br />\r\n&ndash; &Ocirc;ng ta l&agrave; người duy nhất đ&aacute;ng &igrave;nh kết bạn. Nhưng tinh cầu của &ocirc;ng ấy b&eacute; qu&aacute;. Kh&ocirc;ng c&oacute; chỗ cho hai người...<br />\r\nĐiều m&agrave; &ocirc;ng ho&agrave;ng nhỏ kh&ocirc;ng thể th&uacute; nhận với m&igrave;nh, ấy l&agrave; em tiếc c&aacute;i h&agrave;nh tinh thi&ecirc;n ph&uacute;c n&agrave;y l&agrave; v&igrave;, trước hết, l&agrave; n&oacute; c&oacute; một ngh&igrave;n bốn trăm bốn mươi lần mặt trời lặn trong hai mươi bốn tiếng đồng hồ!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>XV</strong></span><br />\r\n<br />\r\nTinh cầu thứ s&aacute;u l&agrave; tinh cầu mười lần rộng hơn. ở tr&ecirc;n đ&oacute; c&oacute; một t&ocirc;n &ocirc;ng gi&agrave; đang viết những cuốn s&aacute;ch d&agrave;y cộp.<br />\r\n&ndash; K&igrave;a! Kia l&agrave; một nh&agrave; th&aacute;m hiểm! &Ocirc;ng ta k&ecirc;u l&ecirc;n thế khi nh&igrave;n thấy &ocirc;ng ho&agrave;ng nhỏ.<br />\r\n&Ocirc;ng ho&agrave;ng nhỏ ngồi l&ecirc;n b&agrave;n v&agrave; thở một l&uacute;c. Em đ&atilde; đi một qu&atilde;ng đường kh&aacute; xa.<br />\r\n&ndash; Ch&uacute; từ đ&acirc;u đến? T&ocirc;n &ocirc;ng gi&agrave; hỏi.<br />\r\n&ndash; Cuốn s&aacute;ch bự n&agrave;y l&agrave; cuốn s&aacute;ch g&igrave; vậy? &Ocirc;ng ho&agrave;ng nhỏ hỏi. &Ocirc;ng l&agrave;m g&igrave; ở đ&acirc;y?<br />\r\n&ndash; Ta l&agrave; nh&agrave; địa l&yacute;, t&ocirc;n &ocirc;ng gi&agrave; n&oacute;i.&ndash; Nh&agrave; địa l&yacute; l&agrave; người như thế n&agrave;o?<br />\r\n&ndash; Đ&oacute; l&agrave; một nh&agrave; b&aacute;c học biết r&otilde; đ&acirc;u l&agrave; s&ocirc;ng biển, n&uacute;i non, th&agrave;nh phố v&agrave; sa mạc.<br />\r\n&ndash; Thế th&igrave; th&iacute;ch thật, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i. B&acirc;y giờ ta mới gặp được một nghề ra nghề.<br />\r\nRồi em đưa mắt nh&igrave;n c&aacute;i h&agrave;nh tinh của nh&agrave; địa l&yacute;, xung quanh em. Em chưa hề nh&igrave;n thấy một h&agrave;nh tinh uy nghi như vậy.<br />\r\n&ndash; Tinh cầu của &ocirc;ng đẹp qu&aacute;. C&oacute; đại dương kh&ocirc;ng &ocirc;ng?<br />\r\n&ndash; Ta kh&ocirc;ng biết, nh&agrave; địa l&yacute; n&oacute;i.<br />\r\n&ndash; A! (&ocirc;ng ho&agrave;ng nhỏ thất vọng). C&ograve;n n&uacute;i non?<br />\r\n&ndash; Ta kh&ocirc;ng biết, nh&agrave; địa l&yacute; n&oacute;i.<br />\r\n&ndash; V&agrave; c&aacute;c th&agrave;nh phố, v&agrave; c&aacute;c con s&ocirc;ng, v&agrave; c&aacute;c sa mạc?<br />\r\n&ndash; Ta cũng kh&ocirc;ng biết nốt, nh&agrave; địa l&yacute; n&oacute;i.<br />\r\n&ndash; &Ocirc;ng l&agrave; nh&agrave; địa l&yacute; cơ m&agrave;!<br />\r\n&ndash; Đ&uacute;ng, nh&agrave; địa l&yacute; n&oacute;i. Nhưng ta c&oacute; l&agrave; nh&agrave; th&aacute;m hiểm đ&acirc;u. Ta ho&agrave;n to&agrave;n thiếu c&aacute;c nh&agrave; th&aacute;m hiểm. Nh&agrave; địa l&yacute; kh&ocirc;ng phải l&agrave; người đi đến c&aacute;c th&agrave;nh phố, s&ocirc;ng biển, n&uacute;i non, đại dương v&agrave; sa mạc. Nh&agrave; địa l&yacute; rất quan trọng kh&ocirc;ng thể đi lung tung. &Ocirc;ng ta kh&ocirc;ng rời b&agrave;n giấy của m&igrave;nh. Nhưng &ocirc;ng ta tiếp c&aacute;c nh&agrave; th&aacute;m hiểm. &Ocirc;ng ta phỏng vấn họ, v&agrave; &ocirc;ng ta ghi ch&eacute;p lại những hồi ức của họ. V&agrave; nếu hồi ức của một trong c&aacute;c nh&agrave; th&aacute;m hiểm ấy m&agrave; đ&aacute;ng ch&uacute; &yacute; th&igrave; nh&agrave; địa l&yacute; sẽ cho điều tra về tư c&aacute;ch của nh&agrave; th&aacute;m hiểm ấy.<br />\r\n&ndash; Tại sao vậy?<br />\r\n&ndash; Bởi v&igrave; nh&agrave; th&aacute;m hiểm ấy m&agrave; n&oacute;i dối sẽ g&acirc;y n&ecirc;n những sai lầm tai hại trong c&aacute;c s&aacute;ch địa l&yacute;. V&agrave; cả nh&agrave; th&aacute;m hiểm n&agrave;o uống rượu nhiều qu&aacute; nữa.<br />\r\n&ndash; Tại sao vậy? &Ocirc;ng ho&agrave;ng nhỏ hỏi.&ndash; V&igrave; c&aacute;c bợm nhậu thấy một th&agrave;nh hai. Thế l&agrave; nh&agrave; địa l&yacute; sẽ ghi hai d&atilde;y n&uacute;i ở một nơi chỉ c&oacute; một d&atilde;y.<br />\r\n&ndash; T&ocirc;i c&oacute; biết một người nếu m&agrave; l&agrave;m nh&agrave; th&aacute;m hiểm th&igrave; sẽ tệ hại đấy, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i.<br />\r\n&ndash; C&oacute; thể như thế. Vậy n&ecirc;n khi tư c&aacute;ch của nh&agrave; th&aacute;m hiểm được chứng minh l&agrave; tốt rồi, th&igrave; người ta mới điều tra về ph&aacute;t hiện của anh ta.<br />\r\n&ndash; Người ta đi xem hay sao?<br />\r\n&ndash; Kh&ocirc;ng, như thế th&igrave; phức tạp qu&aacute;. Nhưng người ta đ&ograve;i hỏi nh&agrave; th&aacute;m hiểm cung cấp bằng chứng. V&iacute; dụ như t&igrave;m thấy một h&ograve;n n&uacute;i lớn, người ta buộc anh ta phải mang về những tảng đ&aacute; bự.<br />\r\nNh&agrave; địa l&yacute; bỗng nhi&ecirc;n x&uacute;c động:<br />\r\n&ndash; Nhưng m&agrave; ch&uacute;, ch&uacute; đến từ xa. Ch&uacute; l&agrave; một nh&agrave; th&aacute;m hiểm! Ch&uacute; h&atilde;y tả cho ta c&aacute;i h&agrave;nh tinh của ch&uacute; đi!<br />\r\nRồi nh&agrave; địa l&yacute;, sau khi đ&atilde; mở cuốn sổ to, liền gọt b&uacute;t ch&igrave;. Thoạt ti&ecirc;n người ta ghi bằng b&uacute;t ch&igrave; c&aacute;c chuyện kể của nh&agrave; th&aacute;m hiểm. Đợi cho nh&agrave; th&aacute;m hiểm cung cấp bằng chứng rồi, người ta mới ghi lại bằng b&uacute;t mực.<br />\r\n&ndash; Thế n&agrave;o? Nh&agrave; địa l&yacute; hỏi.<br />\r\n&ndash; ồ! Chỗ t&ocirc;i ấy ư, &ocirc;ng ho&agrave;ng n&oacute;i, chẳng c&oacute; g&igrave; đ&aacute;ng ch&uacute; &yacute; lắm, n&oacute; b&eacute; t&iacute; tẹo m&agrave;. T&ocirc;i c&oacute; ba quả n&uacute;i lửa. Hai quả hoạt động, một quả tắt. Nhưng biết đ&acirc;u đấy.<br />\r\n&ndash;Biết đ&acirc;u, nh&agrave; địa l&yacute; n&oacute;i.<br />\r\n&ndash; T&ocirc;i cũng c&oacute; một b&ocirc;ng hoa.<br />\r\n&ndash; Ch&uacute;ng t&ocirc;i kh&ocirc;ng ghi nhận b&ocirc;ng hoa, nh&agrave; địa l&yacute; n&oacute;i.<br />\r\n&ndash; Sao thế? N&oacute; l&agrave; thứ đẹp nhất kia m&agrave;!<br />\r\n&ndash; Bởi hoa vốn ph&ugrave; du.<br />\r\n&ndash; Thế n&agrave;o gọi l&agrave; &quot;ph&ugrave; du&quot;?<br />\r\n&ndash; C&aacute;c s&aacute;ch địa l&yacute;, nh&agrave; địa l&yacute; n&oacute;i, l&agrave; những cuốn s&aacute;ch ch&iacute;nh x&aacute;c nhất. Chẳng bao giờ lỗi thời cả. Chẳng mấy khi một quả n&uacute;i lại chuyển chỗ. Chẳng mấy khi một đại dương lại cạn nước. Ch&uacute;ng ta viết n&ecirc;n những điều vĩnh cửu.<br />\r\n&ndash; Song những n&uacute;i lửa đ&atilde; tắt c&oacute; thể thức dậy được, &ocirc;ng ho&agrave;ng nhỏ ngắt lời. Thế n&agrave;o gọi l&agrave; &quot;ph&ugrave; du&quot;?<br />\r\n&ndash; N&uacute;i lửa tắt hay hoạt động với bọn ta cũng thế th&ocirc;i, nh&agrave; địa l&yacute; n&oacute;i. C&aacute;i ta kể tới, đ&oacute; l&agrave; quả n&uacute;i. N&oacute; kh&ocirc;ng thay đổi.<br />\r\n&ndash; Nhưng thế n&agrave;o gọi l&agrave; &quot;ph&ugrave; du&quot;? &Ocirc;ng ho&agrave;ng nhỏ lặp lại, suốt đời, em đ&atilde; kh&ocirc;ng chịu bỏ qua một c&acirc;u hỏi, một khi đ&atilde; n&ecirc;u n&oacute; ra.<br />\r\n&ndash; Gọi như thế c&oacute; nghĩa l&agrave; &quot;đứng trước hiểm hoạ bị ti&ecirc;u diệt&quot;.<br />\r\n&ndash; C&aacute;i hoa của t&ocirc;i đang đứng trước hiểm hoạ bị ti&ecirc;u diệt ư?<br />\r\n&ndash; Chắc rồi.<br />\r\nĐo&aacute; hoa của t&ocirc;i vốn ph&ugrave; du, &ocirc;ng ho&agrave;ng nhỏ tự nhủ, v&agrave; n&agrave;ng chỉ c&oacute; bốn c&aacute;i gai để chống trọi ở đời! Thế m&agrave; ta để n&agrave;ng một m&igrave;nh ở qu&ecirc; nh&agrave;!&nbsp;ấy l&agrave; cơn nuối tiếc đầu ti&ecirc;n của em. Nhưng em lấy lại can đảm:<br />\r\n&ndash; B&acirc;y giờ &ocirc;ng khuy&ecirc;n t&ocirc;i n&ecirc;n đi thăm đ&acirc;u? Em hỏi.<br />\r\n&ndash; H&agrave;nh tinh tr&aacute;i đất, nh&agrave; địa l&yacute; trả lời. H&agrave;nh tinh ấy nổi tiếng lắm...<br />\r\nThế l&agrave; &ocirc;ng ho&agrave;ng nhỏ ra đi, l&ograve;ng mơ đến đo&aacute; hoa của m&igrave;nh.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>XVI</strong></span><br />\r\n<br />\r\nH&agrave;nh tinh thứ bảy vậy l&agrave; tr&aacute;i đất.&nbsp;Tr&aacute;i đất kh&ocirc;ng phải l&agrave; một h&agrave;nh tinh xo&agrave;ng! Người ta đ&atilde; t&iacute;nh c&oacute; tới một trăm mười một &ocirc;ng vua (trong đ&oacute;, hiển nhi&ecirc;n l&agrave; kh&ocirc;ng qu&ecirc;n t&iacute;nh cả c&aacute;c &ocirc;ng vua đen), bảy ngh&igrave;n nh&agrave; địa l&yacute;, ch&iacute;n trăm ngh&igrave;n nh&agrave; doanh nghiệp, bảy triệu rưỡi bợm nhậu, ba trăm mười một triệu kẻ kho&aacute;c l&aacute;c, nghĩa l&agrave; v&agrave;o qu&atilde;ng hai tỉ người lớn.&nbsp;Để c&aacute;c bạn c&oacute; ch&uacute;t &yacute; niệm về k&iacute;ch thước tr&aacute;i đất, t&ocirc;i phải n&oacute;i với c&aacute;c bạn rằng trước khi t&igrave;m ra được điện, tr&ecirc;n cả s&aacute;u ch&acirc;u lục người ta d&ugrave;ng cả một đạo qu&acirc;n thực sự s&aacute;u trăm bốn mươi hai ngh&igrave;n năm trăm mười một người đốt đ&egrave;n.&nbsp;Nh&igrave;n xa xa một ch&uacute;t cảnh đốt đ&egrave;n đ&oacute; thật tuyệt diệu. C&aacute;c chuyển động của đạo qu&acirc;n n&agrave;y qui định như trong một vở kịch ba-l&ecirc;. Trước ti&ecirc;n l&agrave; phi&ecirc;n những người đốt đ&egrave;n nước T&acirc;n T&acirc;y Lan v&agrave; nước &uacute;c. Sau khi đốt đ&egrave;n xong, họ đi ngủ. Bấy giờ đến lượt những người đốt đ&egrave;n Trung Quốc v&agrave; T&acirc;y B&aacute; Lợi &aacute;. Xong cả họ nữa, cũng biến mất trong c&aacute;c h&agrave;nh lang. Lại đến lượt những người đốt đ&egrave;n nước Nga v&agrave; nước ấn Độ. Rồi đến những người của ch&acirc;u Phi v&agrave; ch&acirc;u &Acirc;u. Rồi đến những người Nam Mỹ. Rồi đến những người Bắc Mỹ. V&agrave; chẳng bao giờ họ v&agrave;o ra s&acirc;n khấu nhầm lẫn cả. Thật l&agrave; vĩ đại.&nbsp;Chỉ c&oacute; &ocirc;ng đốt đ&egrave;n của c&acirc;y đ&egrave;n độc nhất ở Bắc Cực v&agrave; &ocirc;ng đốt đ&egrave;n của c&acirc;y đ&egrave;n độc nhất ở Nam Cực l&agrave; sống cuộc đời lề mề ăn kh&ocirc;ng ngồi rỗi: hai &ocirc;ng ấy chỉ l&agrave;m việc một năm c&oacute; hai lần. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>XVII</strong></span><br />\r\n<br />\r\nKhi người ta muốn ra vẻ th&ocirc;ng minh, thường th&igrave; điều n&agrave;y sẽ dẫn đến việc người ta n&oacute;i d&oacute;c đ&ocirc;i ch&uacute;t. T&ocirc;i kh&ocirc;ng thật th&agrave; lắm đ&acirc;u khi kể với c&aacute;c bạn về những người đốt đ&egrave;n. D&aacute;m c&oacute; thể t&ocirc;i đ&atilde; g&acirc;y một &yacute; niệm sai lầm về h&agrave;nh tinh của ch&uacute;ng ta đối với những ai chưa biết n&oacute;. Lo&agrave;i người chiếm rất &iacute;t chỗ tr&ecirc;n mặt đất. Nếu hai tỷ con người đang sống tr&ecirc;n mặt đất n&agrave;y m&agrave; đứng kh&aacute; s&aacute;t v&agrave;o nhau, như trong một cuộc meeting, th&igrave; họ sẽ đứng gọn trong một quảng trường chỉ rộng hơn hai dặm, d&agrave;i hai mươi dặm. C&oacute; thể dồn hết nh&acirc;n loại l&ecirc;n một h&ograve;n đảo b&eacute; nhất trong Th&aacute;i B&igrave;nh Dương.&nbsp;Những người lớn, chắc chắn rồi, sẽ chẳng tin bạn đ&acirc;u. Họ nghĩ l&agrave; họ chiếm nhiều chỗ lắm. Họ thấy họ quan trọng như c&aacute;c c&acirc;y baobab. Thế n&ecirc;n c&aacute;c bạn phải khuy&ecirc;n họ l&agrave;m c&aacute;c con t&iacute;nh đi. Họ t&ocirc;n thờ những con số: việc ấy l&agrave;m họ th&iacute;ch th&uacute;. Nhưng đừng c&oacute; ph&iacute; nhiều th&igrave; giờ của bạn v&agrave;o h&igrave;nh phạt đ&oacute;. Chẳng &iacute;ch g&igrave;. Bạn tin v&agrave;o t&ocirc;i m&agrave;.&nbsp;&Ocirc;ng ho&agrave;ng nhỏ, một khi đ&aacute;p xuống mặt đất, rất kinh ngạc v&igrave; chẳng tr&ocirc;ng thấy người n&agrave;o. Em đ&atilde; bắt đầu sợ m&igrave;nh nhầm h&agrave;nh tinh, th&igrave; một c&aacute;i v&ograve;ng mầu &aacute;nh trăng động đậy trong c&aacute;t.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ndash; Xin ch&agrave;o, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i b&acirc;ng quơ.<br />\r\n&ndash; Xin ch&agrave;o, con rắn n&oacute;i.<br />\r\n&ndash; T&ocirc;i rơi xuống h&agrave;nh tinh n&agrave;o vậy? &Ocirc;ng ho&agrave;ng nhỏ hỏi.<br />\r\n&ndash; Tr&ecirc;n tr&aacute;i đất, ở ch&acirc;u Phi, con rắn trả lời.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ndash; A! Thế tr&ecirc;n tr&aacute;i đất kh&ocirc;ng c&oacute; người n&agrave;o ư?&ndash; Đ&acirc;y l&agrave; sa mạc. Chẳng c&oacute; ai tr&ecirc;n sa mạc đ&acirc;u. Tr&aacute;i đất rộng lắm, rắn n&oacute;i.<br />\r\n&Ocirc;ng ho&agrave;ng nhỏ ngồi tr&ecirc;n một tảng đ&aacute; v&agrave; ngước mắt l&ecirc;n trời:<br />\r\n&ndash; C&oacute; những ng&ocirc;i sao được thắp s&aacute;ng l&agrave; để mỗi người một ng&agrave;y kia trở về ng&ocirc;i sao của m&igrave;nh? H&atilde;y nh&igrave;n xem tinh cầu của t&ocirc;i. N&oacute; ở ngay b&ecirc;n tr&ecirc;n ch&uacute;ng ta... Nhưng sao m&agrave; xa thế!<br />\r\n&ndash; N&oacute; đẹp đấy, rắn n&oacute;i. Cậu l&agrave;m g&igrave; tới đ&acirc;y?<br />\r\n&ndash; T&ocirc;i c&oacute; chuyện rắc rối với một b&ocirc;ng hoa, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i.<br />\r\n&ndash; &agrave;! rắn n&oacute;i.<br />\r\nV&agrave; cả hai lặng thinh. &ndash; Lo&agrave;i người ở đ&acirc;u? Sau c&ugrave;ng &ocirc;ng ho&agrave;ng nhỏ tiếp tục. Trong sa mạc thấy hơi bơ vơ...<br />\r\n&ndash; Ngay giữa lo&agrave;i người, cũng bơ vơ, rắn n&oacute;i.<br />\r\n&Ocirc;ng ho&agrave;ng nhỏ nh&igrave;n rắn rất l&acirc;u:<br />\r\n&ndash; Anh l&agrave; một con vật buồn cười thật, em n&oacute;i, mảnh khảnh cứ như một ng&oacute;n tay...<br />\r\n&ndash; Nhưng ta mạnh hơn ng&oacute;n tay của một &ocirc;ng vua, rắn n&oacute;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&Ocirc;ng ho&agrave;ng nhỏ mỉm cười:<br />\r\n&ndash; Anh kh&ocirc;ng mạnh lắm đ&acirc;u... đến ch&acirc;n anh cũng chẳng c&oacute;... anh muốn đi xa cũng kh&ocirc;ng được...<br />\r\n&ndash; Ta c&oacute; thể chở cậu đi xa hơn cả một chiếc t&agrave;u, rắn n&oacute;i.<br />\r\nRắn cuốn quanh mắt c&aacute; của &ocirc;ng ho&agrave;ng nhỏ, tr&ocirc;ng giống một chiếc v&ograve;ng v&agrave;ng:<br />\r\n&ndash; Kẻ n&agrave;o bị ta chạm tới, ta sẽ trả hắn về với đất l&agrave; nơi hắn sinh ra. Nhưng cậu th&igrave; trong sạch v&agrave; cậu từ một ng&ocirc;i sao tới...<br />\r\n&Ocirc;ng ho&agrave;ng nhỏ kh&ocirc;ng n&oacute;i g&igrave;.<br />\r\n&ndash; Anh l&agrave; một con vật buồn cười thật, em n&oacute;i.<br />\r\n&ndash; Cậu l&agrave;m cho ta thương hại, cậu thật yếu đuối, tr&ecirc;n c&aacute;i tr&aacute;i đất bằng đ&aacute; hoa cương n&agrave;y. Ta c&oacute; thể gi&uacute;p đỡ cậu một ng&agrave;y n&agrave;o đ&oacute; nếu cậu nhớ tinh cầu của cậu. Ta c&oacute; thể...<br />\r\n&ndash; ồ! T&ocirc;i hiểu rồi, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i. Nhưng tại sao anh l&uacute;c n&agrave;o cũng n&oacute;i b&iacute; hiểm thế?<br />\r\n&ndash; Mọi điều b&iacute; hiểm, ta đều giải hết, rắn n&oacute;i.<br />\r\nV&agrave; họ lặng thinh. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>XVIII</strong></span><br />\r\n<br />\r\n&Ocirc;ng ho&agrave;ng nhỏ đi qua sa mạc v&agrave; chỉ gặp c&oacute; một b&ocirc;ng hoa. Một b&ocirc;ng hoa chỉ ba c&aacute;nh, một b&ocirc;ng hoa chẳng l&agrave; g&igrave; cả...&nbsp;<br />\r\n&ndash; Ch&uacute;c một ng&agrave;y tốt l&agrave;nh, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ndash; Ch&uacute;c một ng&agrave;y tốt l&agrave;nh, b&ocirc;ng hoa n&oacute;i.<br />\r\n&ndash; Lo&agrave;i người ở đ&acirc;u? &Ocirc;ng ho&agrave;ng nhỏ lễ ph&eacute;p hỏi.<br />\r\n&ndash; Người hả? C&oacute; lo&agrave;i ấy đấy. Khoảng s&aacute;u hay bảy mống. Em c&oacute; nh&igrave;n thấy họ mấy năm trước kia. Nhưng kh&ocirc;ng ai biết t&igrave;m họ ở đ&acirc;u. Gi&oacute; thổi họ đi. Họ chẳng c&oacute; rễ, c&aacute;i đ&oacute; phiền cho họ lắm.<br />\r\n&ndash; Từ biệt, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i.&ndash; Từ biệt, đo&aacute; hoa n&oacute;i. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>XIX</strong></span><br />\r\n<br />\r\n&Ocirc;ng ho&agrave;ng nhỏ tr&egrave;o l&ecirc;n một quả n&uacute;i cao. Trước đ&acirc;y những n&uacute;i non duy nhất m&agrave; em biết chỉ l&agrave; ba quả n&uacute;i lửa cao ngang đầu gối em. V&agrave; em đ&atilde; d&ugrave;ng quả n&uacute;i lửa đ&atilde; tắt l&agrave;m ghế ngồi. &quot; Từ tr&ecirc;n một quả n&uacute;i cao như quả n&uacute;i n&agrave;y, em n&oacute;i thầm, m&igrave;nh sẽ đưa mắt l&agrave; thấy ngay h&agrave;nh tinh v&agrave; tất cả mọi người... &quot;. Nhưng em chỉ đ&atilde; thấy c&oacute; những mũi nhọn của c&aacute;c tảng đ&aacute; nhọn hoắt.&nbsp;<br />\r\n&ndash; Ch&uacute;c một ng&agrave;y tốt l&agrave;nh, em n&oacute;i b&acirc;ng quơ.<br />\r\n&ndash; Ch&uacute;c một ng&agrave;y tốt l&agrave;nh! Ch&uacute;c một ng&agrave;y tốt l&agrave;nh! Ch&uacute;c một ng&agrave;y tốt l&agrave;nh! tiếng vang đ&aacute;p lại.<br />\r\n&ndash; C&aacute;c anh l&agrave; ai? &Ocirc;ng ho&agrave;ng nhỏ hỏi.<br />\r\n&ndash; C&aacute;c anh l&agrave; ai! C&aacute;c anh l&agrave; ai! C&aacute;c anh l&agrave; ai! tiếng vang đ&aacute;p lại.<br />\r\n&ndash; H&atilde;y l&agrave;m bạn với t&ocirc;i, t&ocirc;i c&ocirc; đơn lắm, em n&oacute;i.<br />\r\n&ndash; T&ocirc;i c&ocirc; đơn lắm! T&ocirc;i c&ocirc; đơn lắm! tiếng vang đ&aacute;p lại.<br />\r\n&quot;C&aacute;i h&agrave;nh tinh n&agrave;y buồn cười thật! L&uacute;c đ&oacute; em nghĩ thầm. N&oacute; kh&ocirc; khốc, v&agrave; nhọn hoắt v&agrave; mặn ch&aacute;t. V&agrave; những con người th&igrave; thiếu hẳn &oacute;c tưởng tượng. Họ chỉ lặp lại những g&igrave; họ đ&atilde; nghe.... ở chỗ t&ocirc;i c&oacute; một b&ocirc;ng hoa: N&agrave;ng bao giờ cũng n&oacute;i trước!&quot;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>XX</strong></span><br />\r\n<br />\r\nNhưng &ocirc;ng ho&agrave;ng nhỏ, sau khi đi rất l&acirc;u qua c&aacute;t, đ&aacute; v&agrave; tuyết, cuối c&ugrave;ng cũng t&igrave;m thấy một con đường. V&agrave; mọi con đường đều đưa đến chỗ c&oacute; người ở.<br />\r\n&ndash; Ch&uacute;c một ng&agrave;y tốt l&agrave;nh, em n&oacute;i.<br />\r\nấy l&agrave; một khu vườn đầy hoa hồng<br />\r\n&ndash; Ch&uacute;c một ng&agrave;y tốt l&agrave;nh, c&aacute;c hoa hồng trả lời.<br />\r\n&Ocirc;ng ho&agrave;ng nhỏ nh&igrave;n c&aacute;c hoa hồng. Tất cả bọn họ đều giống b&ocirc;ng hoa của em.<br />\r\n&ndash; C&aacute;c n&agrave;ng l&agrave; ai? Em hỏi họ, sững sờ kinh ngạc.<br />\r\n&ndash; Ch&uacute;ng em l&agrave; những đo&aacute; hoa hồng, c&aacute;c hoa hồng n&oacute;i.<br />\r\n&ndash; A! &Ocirc;ng ho&agrave;ng nhỏ k&ecirc;u l&ecirc;n.<br />\r\nV&agrave; em cảm thấy rất đau khổ. Đo&aacute; hoa của em đ&atilde; đ&atilde; kể với em rằng n&agrave;ng l&agrave; duy nhất trong giống n&ograve;i của n&agrave;ng khắp trong vũ trụ. V&agrave; ở đ&acirc;y c&oacute; đến năm ngh&igrave;n đo&aacute; giống như nhau, chỉ mới trong mỗi một khu vườn.&nbsp;&quot;N&agrave;ng sẽ ngượng lắm đ&acirc;y, em nghĩ thầm, nếu thấy cảnh n&agrave;y. N&agrave;ng sẽ ho rũ rượi v&agrave; giả chết cho đỡ ngượng. C&ograve;n ta sẽ phải l&agrave;m ra vẻ an ủi vỗ về n&agrave;ng, nếu kh&ocirc;ng, để l&agrave;m ch&iacute;nh ta cũng phải xấu hổ, n&agrave;ng d&aacute;m chết thật mất&quot;.&nbsp;Rồi em lại tự bảo: &quot;Ta tưởng ta đ&atilde; gi&agrave;u lắm với một đo&aacute; hoa duy nhất. C&aacute;i đ&oacute; cộng với ba quả n&uacute;i lửa cao ngang đầu gối cuả ta, m&agrave; một quả, c&oacute; lẽ, đ&atilde; tắt m&atilde;i m&atilde;i, c&aacute;i đ&oacute; chẳng l&agrave;m cho ta th&agrave;nh một &ocirc;ng ho&agrave;ng lớn lắm đ&acirc;u&quot;. V&agrave;, nằm d&agrave;i trong cỏ, em kh&oacute;c.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>XXI</strong></span><br />\r\n<br />\r\nCh&iacute;nh v&agrave;o l&uacute;c đ&oacute; con c&aacute;o xuất hiện.<br />\r\n&ndash; Ch&uacute;c một ng&agrave;y tốt l&agrave;nh, con c&aacute;o n&oacute;i.<br />\r\n&ndash; Ch&uacute;c một ng&agrave;y tốt l&agrave;nh, &ocirc;ng ho&agrave;ng nhỏ đ&aacute;p lại lịch sự, em quay lại nhưng chẳng thấy g&igrave; cả.<br />\r\n&ndash; Tớ đ&acirc;y n&agrave;y, giọng n&oacute;i cất l&ecirc;n, ở dưới c&acirc;y t&aacute;o<br />\r\n&ndash; Cậu l&agrave; ai thế? &Ocirc;ng ho&agrave;ng nhỏ n&oacute;i, cậu thật l&agrave; xinh.<br />\r\n&ndash; Tớ l&agrave; một con c&aacute;o, con c&aacute;o n&oacute;i.<br />\r\n&ndash; Đến chơi với m&igrave;nh đi, &ocirc;ng ho&agrave;ng nhỏ rủ c&aacute;o. M&igrave;nh buồn qu&aacute;..<br />\r\n&ndash; Tớ kh&ocirc;ng chơi với cậu được. Tớ chưa được cảm ho&aacute;.<br />\r\n&ndash; &agrave;! Xin lỗi, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i.<br />\r\nNhưng sau khi suy nghĩ, em hỏi th&ecirc;m<br />\r\n&ndash; Thế n&agrave;o gọi l&agrave; &quot;cảm ho&aacute;&quot;?<br />\r\n&ndash; Cậu kh&ocirc;ng phải d&acirc;n ở đ&acirc;y. Cậu đi t&igrave;m c&aacute;i g&igrave;?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ndash; Tớ đi t&igrave;m những con người, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i. Thế n&agrave;o gọi l&agrave; &quot;cảm ho&aacute;&quot;?<br />\r\n&ndash; Lo&agrave;i người, c&aacute;o n&oacute;i, họ c&oacute; s&uacute;ng v&agrave; họ. Phiền lắm. Họ cũng nu&ocirc;i g&agrave; nữa. Đ&oacute; l&agrave; c&aacute;i duy nhất hấp dẫn ở họ. Cậu đi kiếm g&agrave; phải kh&ocirc;ng?<br />\r\n&ndash; Kh&ocirc;ng! &Ocirc;ng ho&agrave;ng nhỏ n&oacute;i, m&igrave;nh kiếm bạn. Thế n&agrave;o gọi l&agrave; &quot;cảm ho&aacute;&quot;?<br />\r\n&ndash; ấy l&agrave; một điều bị l&atilde;ng qu&ecirc;n qu&aacute; rồi, c&aacute;o n&oacute;i. N&oacute; c&oacute; nghĩa l&agrave; &quot; tạo l&ecirc;n những li&ecirc;n hệ..&quot;<br />\r\n&ndash; Tạo l&ecirc;n những li&ecirc;n hệ?<br />\r\n&ndash; Đ&uacute;ng như vậy, con c&aacute;o n&oacute;i. Đối với tớ hiện giờ cậu chỉ l&agrave; một cậu b&eacute; ho&agrave;n to&agrave;n giống trăm ngh&igrave;n cậu b&eacute; kh&aacute;c. V&agrave; tớ chẳng cần g&igrave; ở cậu. V&agrave; cậu cũng chẳng cần g&igrave; ở tớ. Tớ đối đối với cậu chỉ l&agrave; một con c&aacute;o giống như trăm ngh&igrave;n con c&aacute;o. Nhưng, nếu cậu cảm ho&aacute; tớ, cậu sẽ l&agrave; duy nhất tr&ecirc;n đời...<br />\r\n&ndash; Tớ bắt đầu hiểu, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i. C&oacute; một b&ocirc;ng hoa...tớ nghĩ l&agrave; n&agrave;ng cảm ho&aacute; tớ...<br />\r\n&ndash; C&oacute; thể lắm, c&aacute;o n&oacute;i. C&oacute; thể gặp mọi sự tr&ecirc;n tr&aacute;i đất n&agrave;y...<br />\r\n&ndash; ồ! Kh&ocirc;ng phải tr&ecirc;n tr&aacute;i tr&aacute;i đất n&agrave;y đ&acirc;u, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i.<br />\r\nCon c&aacute;o c&oacute; vẻ ch&uacute; &yacute;:<br />\r\n&ndash; Tr&ecirc;n một h&agrave;nh tinh kh&aacute;c &agrave;?<br />\r\n&ndash; Phải.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ndash; Tr&ecirc;n h&agrave;nh tinh ấy, c&oacute; thợ săn kh&ocirc;ng?<br />\r\n&ndash; Kh&ocirc;ng.<br />\r\n&ndash; Thế th&igrave; hay đấy. Thế c&ograve;n g&agrave;?<br />\r\n&ndash; Kh&ocirc;ng.<br />\r\n&ndash; Chả c&oacute; điều g&igrave; l&agrave; ho&agrave;n hảo cả! C&aacute;o thở d&agrave;i.<br />\r\nNhưng rồi c&aacute;o trở lại với &yacute; nghĩ của n&oacute;:<br />\r\n&ndash; Đời tớ tẻ nhạt. Tớ săn g&agrave;, người săn tớ. Tất cả lo&agrave;i g&agrave; đều giống nhau, v&agrave; tất cả lo&agrave;i người đều giống nhau. V&igrave; thế, tớ hơi ch&aacute;n. Nhưng nếu cậu cảm tớ, đời tớ sẽ rực nắng. Tớ sẽ nhận ra một bước ch&acirc;n kh&aacute;c hẳn mọi bước ch&acirc;n kh&aacute;c. C&aacute;c bước ch&acirc;n kh&aacute;c sẽ l&agrave;m cho tớ chui ngay xuống đất. Nhưng bước ch&acirc;n của cậu lại sẽ gọi tớ từ hang chạy ra, như l&agrave; một điệu nhạc. V&agrave; cậu h&atilde;y nh&igrave;n kia! Cậu thấy kh&ocirc;ng, ở kia, những đồng l&uacute;a m&igrave; ấy? Tớ kh&ocirc;ng ăn b&aacute;nh m&igrave;. L&uacute;a m&igrave; đối với tớ l&agrave; v&ocirc; dụng. C&aacute;c c&aacute;nh đồng l&uacute;a m&igrave; đối với tớ chẳng c&oacute; g&igrave; kh&ecirc;u gợi. C&aacute;i đ&oacute; buồn lắm. Nhưng cậu c&oacute; m&aacute;i t&oacute;c mầu v&agrave;ng kim. Thế th&igrave; sẽ rất tuyệt một khi cậu cảm ho&aacute; tớ! L&uacute;a m&igrave;, vốn m&agrave;u v&agrave;ng kim, sẽ gợi cho tớ kỷ niệm về cậu. V&agrave; tớ sẽ y&ecirc;u tiếng gi&oacute; reo trong l&uacute;a m&igrave;...<br />\r\nC&aacute;o im lặng v&agrave; nh&igrave;n &ocirc;ng ho&agrave;ng nhỏ một l&uacute;c l&acirc;u:<br />\r\n&ndash; Nếu cậu vui l&ograve;ng, h&atilde;y cảm ho&aacute; tớ đi, c&aacute;o n&oacute;i!<br />\r\n&ndash; Tớ cũng muốn thế lắm, &ocirc;ng ho&agrave;ng nhỏ trả lời, nhưng tớ kh&ocirc;ng c&oacute; nhiều th&igrave; giờ. Tớ cần t&igrave;m kiếm nhiều bạn v&agrave; t&igrave;m hiểu bao nhi&ecirc;u sự vật.<br />\r\n&ndash; Người ta chỉ hiểu được những vật người ta đ&atilde; cảm ho&aacute;, c&aacute;o n&oacute;i. Lo&agrave;i người b&acirc;y giờ kh&ocirc;ng c&ograve;n đủ th&igrave; giờ hiểu c&aacute;i g&igrave; hết. Họ mua những vật l&agrave;m sẵn ở c&aacute;c nh&agrave; bu&ocirc;n. Nhưng kh&ocirc;ng ở đ&acirc;u c&oacute; nh&agrave; bu&ocirc;n bạn, con người kh&ocirc;ng c&oacute; bạn nữa. Nếu cậu muốn c&oacute; một người bạn, h&atilde;y cảm ho&aacute; tớ!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ndash; Phải l&agrave;m sao? &ocirc;ng ho&agrave;ng nhỏ hỏi.<br />\r\n&ndash; Phải thật ki&ecirc;n nhẫn, c&aacute;o trả lời. Ban đầu cậu h&atilde;y ngồi hơi xa tớ một t&iacute;, như thế, ở trong cỏ. Tớ dưa mắt liếc nh&igrave;n cậu, v&agrave; cậu chẳng n&oacute;i g&igrave; cả. Ng&ocirc;n ngữ l&agrave; nguồn gốc của ngộ nhận. Nhưng mỗi ng&agrave;y, cậu c&oacute; thể ngồi gần một t&iacute;...<br />\r\nNg&agrave;y h&ocirc;m sau, &ocirc;ng ho&agrave;ng nhỏ trở lại.<br />\r\n&ndash; Tốt hơn l&agrave; n&ecirc;n đến đ&uacute;ng giờ như h&ocirc;m trước, c&aacute;o n&oacute;i. Nếu cậu đến, chẳng hạn như l&uacute;c bốn giờ chiều, th&igrave; từ ba giờ, m&igrave;nh đ&atilde; cảm thấy hạnh ph&uacute;c. Thời khắc c&agrave;ng tr&ocirc;i, m&igrave;nh lại c&agrave;ng hạnh ph&uacute;c. Đến bốn giờ th&igrave; m&igrave;nh ph&aacute;t cuồng l&ecirc;n v&agrave; lo lắng; v&agrave; m&igrave;nh sẽ hiểu c&aacute;i gi&aacute; của hạnh ph&uacute;c! Nhưng nếu cậu đến bất cứ l&uacute;c n&agrave;o, m&igrave;nh kh&ocirc;ng biết l&uacute;c n&agrave;o th&igrave; n&ecirc;n trang phục cho c&otilde;i l&ograve;ng m&igrave;nh... Phải c&oacute; nghi thức chứ.<br />\r\n&ndash; Nghi thức l&agrave; c&aacute;i g&igrave;? &ocirc;ng ho&agrave;ng nhỏ hỏi.<br />\r\n&ndash; Đ&oacute; cũng l&agrave; c&aacute;i bị qu&ecirc;n l&acirc;u qu&aacute; rồi, c&aacute;o n&oacute;i. Đ&oacute; cũng l&agrave; c&aacute;i g&igrave; l&agrave;m ột ng&agrave;y trở n&ecirc;n kh&aacute;c những ng&agrave;y kh&aacute;c, một giờ trở n&ecirc;n kh&aacute;c những giờ kh&aacute;c. C&oacute; một nghi thức, chẳng hạn của bọn thợ săn của tớ. Mỗi thứ năm, họ khi&ecirc;u vũ với c&aacute;c c&ocirc; g&aacute;i trong l&agrave;ng. Thế th&igrave; thứ năm l&agrave; một ng&agrave;y kỳ diệu! H&ocirc;m ấy tớ c&oacute; thể rong chơi đến tận vườn nho. Nếu bọn thợ săn m&agrave; khi&ecirc;u vũ bất cứ ng&agrave;y n&agrave;o, th&igrave; ng&agrave;y n&agrave;o cũng như ng&agrave;y n&agrave;o, tớ sẽ chẳng c&oacute; ng&agrave;y n&agrave;o được nghỉ nữa.<br />\r\nThế l&agrave; &ocirc;ng ho&agrave;ng nhỏ cảm ho&aacute; con c&aacute;o. V&agrave; giờ ra đi đ&atilde; đến:<br />\r\n&ndash; A! C&aacute;o n&oacute;i, tớ sắp kh&oacute;c l&ecirc;n đ&acirc;y.<br />\r\n&ndash; Đ&oacute; l&agrave; lỗi tại cậu, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i. M&igrave;nh tuyệt chẳng muốn cậu khổ, cậu lại cứ muốn m&igrave;nh cảm ho&aacute; cậu.<br />\r\n&ndash; Đ&uacute;ng thế, c&aacute;o n&oacute;i.<br />\r\n&ndash; Nhưng cậu sẽ kh&oacute;c! &ocirc;ng ho&agrave;ng nhỏ n&oacute;i.<br />\r\n&ndash; Đ&uacute;ng thế, c&aacute;o n&oacute;i.<br />\r\n&ndash; Thế th&igrave; cậu chẳng được g&igrave; cả!<br />\r\n&ndash; Được chứ, c&aacute;o n&oacute;i, l&agrave; do c&aacute;i m&agrave;u v&agrave;ng của l&uacute;a m&igrave; ấy.<br />\r\nRồi n&oacute; n&oacute;i th&ecirc;m:<br />\r\n&ndash; H&atilde;y trở lại thăm những đo&aacute; hồng đi. Cậu sẽ thấy đo&aacute; hoa của cậu l&agrave; duy nhất tr&ecirc;n đời. Rồi cậu h&atilde;y lại đ&acirc;y từ biệt tớ, tớ sẽ l&agrave;m qu&agrave; cho cậu một điều b&iacute; mật.<br />\r\n&Ocirc;ng ho&agrave;ng nhỏ đi thăm lại những đo&aacute; hoa hồng.<br />\r\n&ndash; C&aacute;c c&ocirc; chẳng giống ch&uacute;t n&agrave;o với đo&aacute; hồng của t&ocirc;i, c&aacute;c c&ocirc; chưa l&agrave; g&igrave; cả, em bảo c&aacute;c b&ocirc;ng hồng. Chưa ai cảm ho&aacute; c&aacute;c c&ocirc;, c&aacute;c c&ocirc; cũng chưa cảm ho&aacute; ai. C&aacute;c c&ocirc; giống như con c&aacute;o của t&ocirc;i trước kia. N&oacute; chỉ l&agrave; một con c&aacute;o giống như trăm ngh&igrave;n con c&aacute;o. Song t&ocirc;i đ&atilde; l&agrave;m cho n&oacute; trở th&agrave;nh bạn t&ocirc;i, v&agrave; b&acirc;y giờ n&oacute; trở n&ecirc;n duy nhất tr&ecirc;n đời.<br />\r\nV&agrave; c&aacute;c b&ocirc;ng hồng hết sức l&uacute;ng t&uacute;ng.<br />\r\n&ndash; C&aacute;c c&ocirc; đẹp, nhưng c&aacute;c c&ocirc; trống rỗng, em n&oacute;i với họ. Người ta kh&ocirc;ng thể chết v&igrave; c&aacute;c c&ocirc; được. Phải, đo&aacute; hồng của t&ocirc;i, một người qua đường tầm thường tưởng l&agrave; n&agrave;ng giống c&aacute;c c&ocirc;. Nhưng đối với t&ocirc;i th&igrave; n&agrave;ng quan trọng hơn tất cả c&aacute;c c&ocirc;, bởi v&igrave; ch&iacute;nh l&agrave; n&agrave;ng m&agrave; tay t&ocirc;i đ&atilde; tưới. Bởi v&igrave; ch&iacute;nh l&agrave; n&agrave;ng m&agrave; t&ocirc;i đ&atilde; đặt ch&iacute;nh dưới bầu k&iacute;nh. Bởi v&igrave; ch&iacute;nh l&agrave; n&agrave;ng m&agrave; t&ocirc;i đ&atilde; che bằng tấm b&igrave;nh phong. Bởi v&igrave; l&agrave; n&agrave;ng m&agrave; t&ocirc;i đ&atilde; bắt những con s&acirc;u (trừ hai ba con d&agrave;nh để th&agrave;nh bướm). Bởi v&igrave; ch&iacute;nh l&agrave; n&agrave;ng m&agrave; t&ocirc;i đ&atilde; ngồi nghe than thở, hay t&aacute;n hươu t&aacute;n vượn, hay đ&ocirc;i khi cả lặng im nữa. Bởi v&igrave; đ&oacute; l&agrave; đo&aacute; hồng của t&ocirc;i.<br />\r\nRồi em trở lại chỗ con c&aacute;o:<br />\r\n&ndash; Từ biệt, em n&oacute;i.<br />\r\n&ndash; Từ biệt, c&aacute;o n&oacute;i. Đ&acirc;y l&agrave; c&aacute;i b&iacute; mật của tớ. N&oacute; đơn giản th&ocirc;i: người ta chỉ nh&igrave;n thấy thật r&otilde; r&agrave;ng bằng tr&aacute;i tim. C&aacute;i cốt yếu th&igrave; con mắt kh&ocirc;ng nh&igrave;n thấy.<br />\r\n&ndash; C&aacute;i cốt yếu th&igrave; con mắt kh&ocirc;ng nh&igrave;n thấy, &ocirc;ng ho&agrave;ng nhỏ lặp lại, để m&agrave; ghi nhớ.<br />\r\n&ndash; Ch&iacute;nh thời giờ cậu đ&atilde; mất, cho đo&aacute; hồng của cậu l&agrave;m cho đo&aacute; hồng của cậu trở n&ecirc;n quan trọng đến thế.<br />\r\n&ndash; Ch&iacute;nh thời giờ t&ocirc;i đ&atilde; mất cho b&ocirc;ng hồng của t&ocirc;i... &ocirc;ng ho&agrave;ng nhỏ n&oacute;i, để m&agrave; ghi nhớ.<br />\r\n&ndash; Lo&agrave;i người đ&atilde; qu&ecirc;n mất ch&acirc;n l&yacute; n&agrave;y, c&aacute;o n&oacute;i. Nhưng cậu kh&ocirc;ng được qu&ecirc;n. Cậu trở n&ecirc;n m&atilde;i m&atilde;i c&oacute; tr&aacute;ch nhiệm về những g&igrave; cậu đ&atilde; cảm ho&aacute;. Cậu c&oacute; tr&aacute;ch nhiệm đối với hoa hồng của cậu.<br />\r\n&ndash; T&ocirc;i c&oacute; tr&aacute;ch nhiệm với hoa hồng của t&ocirc;i... &ocirc;ng ho&agrave;ng nhỏ lặp lại để m&agrave; ghi nhớ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>XXII</strong></span> </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ndash; Xin ch&agrave;o, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i.<br />\r\n&ndash; Xin ch&agrave;o, người bẻ ghi n&oacute;i.<br />\r\n&ndash; &Ocirc;ng l&agrave;m g&igrave; ở đ&acirc;y? &Ocirc;ng ho&agrave;ng nhỏ hỏi.<br />\r\n&ndash; Ta xếp h&agrave;nh kh&aacute;ch, th&agrave;nh từng g&oacute;i ngh&igrave;n người một, người bẻ ghi n&oacute;i. Ta hướng dẫn những chuyến t&agrave;u trở ch&uacute;ng đi, l&uacute;c về b&ecirc;n phải, l&uacute;c về b&ecirc;n tr&aacute;i.<br />\r\nRồi một con t&agrave;u nhanh s&aacute;ng choang, gầm như sấm, l&agrave;m căn ph&ograve;ng g&aacute;c ghi rung l&ecirc;n.<br />\r\n&ndash; Họ vội qu&aacute;, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i. Họ t&igrave;m c&aacute;i g&igrave; vậy?<br />\r\n&ndash; Ch&iacute;nh người l&aacute;i t&agrave;u cũng kh&ocirc;ng biết, người g&aacute;c ghi n&oacute;i..<br />\r\nV&agrave; lại gầm l&ecirc;n, từ hướng ngược lại, một chuyến t&agrave;u nhanh thứ hai s&aacute;ng choang.&ndash; Họ đ&atilde; trở về rồi ư? &ocirc;ng ho&agrave;ng nhỏ hỏi...<br />\r\n&ndash; Kh&ocirc;ng phải những người khi n&atilde;y. Họ đổi chỗ đấy.<br />\r\n&ndash; Họ kh&ocirc;ng bằng l&ograve;ng chỗ của họ?<br />\r\n&ndash; Người ta kh&ocirc;ng bao giờ bằng l&ograve;ng chỗ của m&igrave;nh cả! Người bẻ ghi n&oacute;i.<br />\r\nV&agrave; lại tiếng ầm ầm của chiếc t&agrave;u nhanh thứ ba s&aacute;ng choang.<br />\r\n&ndash; Họ đuổi theo những h&agrave;nh kh&aacute;ch l&uacute;c n&atilde;y phải kh&ocirc;ng? &ocirc;ng ho&agrave;ng nhỏ hỏi.<br />\r\n&ndash; Họ chẳng đuổi theo c&aacute;i g&igrave; hết, người bẻ ghi n&oacute;i. Họ ngủ gật hoặc ngồi ng&aacute;p vặt trong đ&oacute;. Chỉ c&oacute; những đứa trẻ l&agrave; d&aacute;n mũi v&agrave;o cửa k&iacute;nh th&ocirc;i.<br />\r\n&ndash; Chỉ c&oacute; những đứa trẻ l&agrave; biết m&igrave;nh t&igrave;m c&aacute;i g&igrave;, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i, ch&uacute;ng mất th&igrave; giờ v&igrave; một con b&uacute;p b&ecirc; bằng giẻ r&aacute;ch, v&agrave; con b&uacute;p b&ecirc; ấy trở n&ecirc;n quan trọng lắm, ai lấy đi của ch&uacute;ng, ch&uacute;ng sẽ kh&oacute;c...<br />\r\n&ndash; Ch&uacute;ng thật may mắn, người bẻ ghi n&oacute;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>XXIII</strong></span> </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ndash; Xin ch&agrave;o, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i.<br />\r\n&ndash; Xin ch&agrave;o, người l&aacute;i bu&ocirc;n n&oacute;i.<br />\r\nấy l&agrave; một người b&aacute;n loại thuốc c&oacute; thể l&agrave;m cho đỡ kh&aacute;t. Mỗi lần uống một vi&ecirc;n, v&agrave; người ta sẽ thấy kh&ocirc;ng cần phải uống nước nữa.<br />\r\n&ndash; Tại sao &ocirc;ng b&aacute;n thứ đ&oacute;? &ocirc;ng ho&agrave;ng nhỏ hỏi.<br />\r\n&ndash; Đ&acirc;y l&agrave; một sự tiết kiệm lớn về thời giờ, người l&aacute;i bu&ocirc;n n&oacute;i. C&aacute;c nh&agrave; chuy&ecirc;n m&ocirc;n đ&atilde; c&oacute; t&iacute;nh to&aacute;n. Mỗi tuần lễ ta sẽ tiết kiệm được năm mươi ba ph&uacute;t.<br />\r\n&ndash; Thế người ta d&ugrave;ng năm mươi ba ph&uacute;t ấy để l&agrave;m g&igrave;?<br />\r\n&ndash; Muốn l&agrave;m g&igrave; th&igrave; l&agrave;m...<br />\r\n&quot;Ta, &ocirc;ng ho&agrave;ng nhỏ nghĩ thầm, nếu ta c&oacute; năm mươi ba ph&uacute;t để l&agrave;m g&igrave; th&igrave; l&agrave;m, ta sẽ bước thật nhẹ nh&agrave;ng đến một c&aacute;i nguồn nước...&quot;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>XXIV</strong></span><br />\r\n<br />\r\nL&uacute;c đ&oacute; l&agrave; ng&agrave;y thứ t&aacute;m từ khi t&ocirc;i bị hỏng m&aacute;y trong sa mạc, v&agrave; t&ocirc;i vừa nghe c&acirc;u chuyện người l&aacute;i bu&ocirc;n vừa uống giọt nước cuối c&ugrave;ng trong số nước dự trữ.<br />\r\n&ndash; A! T&ocirc;i n&oacute;i với &ocirc;ng ho&agrave;ng nhỏ, c&aacute;c kỷ niệm của em thật l&agrave; đẹp, nhưng anh vẫn chưa chữa được m&aacute;y bay của anh, anh chẳng c&ograve;n g&igrave; để uống nữa, v&agrave; anh cũng sẽ hạnh ph&uacute;c lắm đấy, nếu b&acirc;y giờ anh cũng c&oacute; thể bước thật nhẹ nh&agrave;ng đến một c&aacute;i nguồn nước!<br />\r\n&ndash; Bạn c&aacute;o của t&ocirc;i ơi, em n&oacute;i...<br />\r\n&ndash; Ch&uacute; em b&eacute; bỏng ơi, b&acirc;y giờ c&ograve;n chồn c&aacute;o g&igrave; nữa!<br />\r\n&ndash; Sao thế?<br />\r\n&ndash; Bởi v&igrave; ta sắp chết kh&aacute;t...<br />\r\nEm kh&ocirc;ng hiểu l&yacute; lẽ của t&ocirc;i, em trả lời t&ocirc;i:<br />\r\n&ndash; C&oacute; được một người bạn l&agrave; tuyệt chứ, d&ugrave; cho ta sắp chết đi nữa. T&ocirc;i, t&ocirc;i rất h&agrave;i l&ograve;ng được c&oacute; bạn c&aacute;o của t&ocirc;i...<br />\r\nEm kh&ocirc;ng lường được hiểm nguy, t&ocirc;i nghĩ thầm. Em kh&ocirc;ng hề đ&oacute;i hay kh&aacute;t. Một ch&uacute;t mặt trời cũng đủ cho em rồi.&nbsp;Nhưng em nh&igrave;n t&ocirc;i v&agrave; trả lời điều t&ocirc;i vừa nghĩ:<br />\r\n&ndash; T&ocirc;i cũng đang kh&aacute;t... ta đi t&igrave;m một c&aacute;i giếng đi...<br />\r\nT&ocirc;i ph&aacute;c một cử chỉ mệt mỏi: thật l&agrave; phi l&yacute; khi đi t&igrave;m một c&aacute;i giếng, một c&aacute;ch h&uacute; hoạ, ở trong sa mạc m&ecirc;nh m&ocirc;ng. Tuy thế, ch&uacute;ng t&ocirc;i vẫn bước đi.&nbsp;Khi ch&uacute;ng t&ocirc;i bước đi, im lặng, giờ n&agrave;y qua giờ kh&aacute;c, m&agrave;n đ&ecirc;m bu&ocirc;ng xuống v&agrave; c&aacute;c v&igrave; sao bắt đầu toả s&aacute;ng. T&ocirc;i nh&igrave;n mọi vật như trong cơn m&ecirc;, đầu n&oacute;ng bừng v&igrave; kh&aacute;t. Những lời &ocirc;ng ho&agrave;ng b&eacute; nhỏ n&oacute;i nhảy m&uacute;a trong tr&iacute; nhớ t&ocirc;i.<br />\r\n&ndash; Em, em cũng kh&aacute;t hay sao? T&ocirc;i hỏi em.<br />\r\nNhưng em kh&ocirc;ng trả lời c&acirc;u t&ocirc;i hỏi. Em chỉ n&oacute;i giản dị:<br />\r\n&ndash; Nước cũng c&oacute; thể tốt l&agrave;nh cho tr&aacute;i tim...<br />\r\nT&ocirc;i kh&ocirc;ng hiểu c&acirc;u em đ&aacute;p nhưng t&ocirc;i im lặng... t&ocirc;i biết l&agrave; kh&ocirc;ng n&ecirc;n hỏi em.&nbsp;Em thấy mệt. Em ngồi xuống. T&ocirc;i ngồi xuống cạnh em. V&agrave; sau một l&aacute;t im lặng, em lại n&oacute;i:<br />\r\n&ndash; C&aacute;c ng&ocirc;i sao đẹp, l&agrave; do ở đ&oacute; c&oacute; một b&ocirc;ng hoa m&agrave; người ta kh&ocirc;ng nh&igrave;n thấy...<br />\r\nT&ocirc;i đ&aacute;p lại &quot;Đ&uacute;ng thế&quot; v&agrave; t&ocirc;i nh&igrave;n, kh&ocirc;ng n&oacute;i, những gợn s&oacute;ng của c&aacute;t dưới &aacute;nh trăng.<br />\r\n&ndash; Sa mạc thật đẹp, em n&oacute;i th&ecirc;m...<br />\r\nĐiều n&agrave;y đ&uacute;ng. Bao giờ t&ocirc;i cũng y&ecirc;u sa mạc. Ta ngồi tr&ecirc;n một đụn c&aacute;t. Ta kh&ocirc;ng tr&ocirc;ng thấy g&igrave; hết. Ta kh&ocirc;ng nghe thấy g&igrave; hết. Nhưng c&oacute; một c&aacute;i g&igrave; đ&oacute; toả s&aacute;ng trong lặng lẽ...<br />\r\n&ndash; C&aacute;i đ&atilde; t&ocirc; điểm cho sa mạc, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i, l&agrave; n&oacute; ẩn giấu một c&aacute;i giếng ở nơi n&agrave;o đ&oacute;...<br />\r\nT&ocirc;i kinh ngạc v&igrave; bỗng nhi&ecirc;n hiểu ra c&aacute;i &aacute;nh s&aacute;ng huyền b&iacute; ấycủa c&aacute;t. Khi c&ograve;n l&agrave; một cậu b&eacute; t&ocirc;i ở trong một ng&ocirc;i nh&agrave; cổ, v&agrave; c&oacute; một truyền thuyết l&agrave; ở ng&ocirc;i nh&agrave; cổ n&agrave;y c&oacute; ch&ocirc;n một kho b&aacute;u. Tất nhi&ecirc;n, chưa ai t&igrave;m ra kho b&aacute;u đ&oacute;, c&oacute; lẽ cũng chưa ai thử đi t&igrave;m. Nhưng n&oacute; đ&atilde; l&agrave;m cho ng&ocirc;i nh&agrave; trở n&ecirc;n th&agrave;nh ti&ecirc;n. C&aacute;i nh&agrave; của t&ocirc;i c&oacute; giấu trong đ&aacute;y tr&aacute;i tim của n&oacute; một điều b&iacute; mật...<br />\r\n&ndash; Phải, t&ocirc;i n&oacute;i với &ocirc;ng ho&agrave;ng nhỏ. D&ugrave; l&agrave; ng&ocirc;i nh&agrave;, ng&ocirc;i sao hay sa mạc, c&aacute;i l&agrave;m ch&uacute;ng ta trở n&ecirc;n đẹp th&igrave; kh&ocirc;ng thể nh&igrave;n thấy!<br />\r\n&ndash; T&ocirc;i rất h&agrave;i l&ograve;ng, em n&oacute;i, v&igrave; &ocirc;ng cũng đồng &yacute; với bạn c&aacute;o của t&ocirc;i.<br />\r\nKhi &ocirc;ng ho&agrave;ng nhỏ b&eacute; thiu thiu ngủ, t&ocirc;i bế em l&ecirc;n v&ograve;ng tay v&agrave; lại l&ecirc;n đường. L&ograve;ng t&ocirc;i x&uacute;c động. T&ocirc;i c&oacute; cảm gi&aacute;c như đang giữ một kho b&aacute;u mong manh. T&ocirc;i c&oacute; cảm gi&aacute;c như tr&ecirc;n Tr&aacute;i Đất n&agrave;y kh&ocirc;ng c&oacute; g&igrave; mong manh hơn. T&ocirc;i nh&igrave;n, dưới &aacute;nh s&aacute;ng trăng, vầng tr&aacute;n xanh xao ấy, đ&ocirc;i mắt nhắm nghiền, những lọn t&oacute;c run rẩy trước gi&oacute;, v&agrave; t&ocirc;i nghĩ thầm: c&aacute;i m&agrave; ta thấy đ&acirc;y chỉ l&agrave; một c&aacute;i vỏ. C&aacute;i quan trọng nhất th&igrave; kh&ocirc;ng nh&igrave;n thấy được...&nbsp;Khi đ&ocirc;i m&ocirc;i h&eacute; mở của em tho&aacute;ng một nụ cười, t&ocirc;i lại tự nhủ: c&aacute;i l&agrave;m cho ta x&uacute;c động mạnh đến thế về &ocirc;ng ho&agrave;ng b&eacute; nhỏ đang ngủ n&agrave;y, đ&oacute; l&agrave; l&ograve;ng chung thuỷ của em đối với một đo&aacute; hoa hồng, ấy l&agrave; h&igrave;nh ảnh một đo&aacute; hồng rực s&aacute;ng nơi em như một ngọn đ&egrave;n, cả trong khi em ngủ... V&agrave; t&ocirc;i c&agrave;ng thấy em c&ograve;n mong manh hơn. Ta phải hết sức che chở cho những ngọn đ&egrave;n: một ngọn gi&oacute; c&oacute; thể thổi tắt được...&nbsp;V&agrave;, cứ bước đi như vậy, t&ocirc;i t&igrave;m ra c&aacute;i giếng v&agrave;o l&uacute;c rạng đ&ocirc;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>XXV</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ndash; Con người, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i, họ chui v&agrave;o c&aacute;c chuyến t&agrave;u nhanh, nhưng họ chẳng biết m&igrave;nh t&igrave;m kiếm thứ g&igrave;. Thế m&agrave; họ cứ cuống qu&iacute;t l&ecirc;n v&agrave; quay cuồng...<br />\r\nV&agrave; em n&oacute;i th&ecirc;m:<br />\r\n&ndash; N&agrave;o c&oacute; &iacute;ch g&igrave;...<br />\r\nC&aacute;i giếng m&agrave; ch&uacute;ng t&ocirc;i đến, n&oacute; kh&ocirc;ng giống c&aacute;c giếng ở sa mạc Sahara. C&aacute;c giếng Sahara chỉ đơn giản l&agrave; những c&aacute;i lỗ đ&agrave;o trong c&aacute;t. C&ograve;n c&aacute;i n&agrave;y giống như giếng l&agrave;ng. Nhưng ở đấy chẳng c&oacute; ng&ocirc;i l&agrave;ng n&agrave;o, v&agrave; t&ocirc;i tưởng m&igrave;nh đang mơ.<br />\r\n&ndash; Lạ thật, t&ocirc;i n&oacute;i với &ocirc;ng ho&agrave;ng nhỏ, mọi c&aacute;i đều c&oacute; sẵn: c&aacute;i r&ograve;ng rọc, c&aacute;i gầu v&agrave; sợi d&acirc;y...<br />\r\nEm cười, sờ sợi d&acirc;y, lăn c&aacute;i r&ograve;ng rọc. V&agrave; c&aacute;i r&ograve;ng rọc liền r&ecirc;n một tiếng, giống như tiếng r&ecirc;n của c&aacute;i chong ch&oacute;ng chỉ hướng gi&oacute; sau một hồi gi&oacute; ngủ y&ecirc;n l&acirc;u qu&aacute;.<br />\r\n&ndash; &Ocirc;ng nghe thấy kh&ocirc;ng, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i, ta đ&aacute;nh thức c&aacute;i giếng n&agrave;y v&agrave; n&oacute; h&aacute;t...<br />\r\nT&ocirc;i kh&ocirc;ng muốn em gắng sức:<br />\r\n&ndash; Để anh, t&ocirc;i n&oacute;i, n&oacute; nặng qu&aacute; đối với em.<br />\r\nChậm r&atilde;i, t&ocirc;i k&eacute;o g&agrave;u l&ecirc;n th&agrave;nh giếng. T&ocirc;i đặt n&oacute; chắc chắn tr&ecirc;n đ&oacute;. Trong tai t&ocirc;i vẫn c&ograve;n văng vẳng tiếng h&aacute;t của c&aacute;i r&ograve;ng rọc, v&agrave; trong nước vẫn c&ograve;n run rẩy, t&ocirc;i thấy mặt trời run l&ecirc;n.<br />\r\n&ndash; T&ocirc;i kh&aacute;t thứ nước n&agrave;y đ&acirc;y, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i. Cho t&ocirc;i uống đi...<br />\r\nV&agrave; t&ocirc;i hiểu ra em t&igrave;m kiếm c&aacute;i g&igrave;.<br />\r\nT&ocirc;i nhắc chiếc gầu l&ecirc;n kề m&ocirc;i em. Em uống, hai mắt nhắm nghiền. &Ecirc;m đềm như một ng&agrave;y hội. Thứ nước n&agrave;y c&ograve;n l&agrave; một c&aacute;i g&igrave; kh&aacute;c hơn mọt thứ nước uống. N&oacute; sinh ra từ cuộc đi bộ dưới trời sao, từ tiếng h&aacute;t của c&aacute;i r&ograve;ng rọc, từ sự gắng sức của c&aacute;nh tay t&ocirc;i. N&oacute; tốt l&agrave;nh cho tim ta, như một m&oacute;n qu&agrave; tặng. Ng&agrave;y t&ocirc;i c&ograve;n l&agrave; một cậu b&eacute;, &aacute;nh s&aacute;ng của c&acirc;y Noel, tiếng nhạc của buổi lễ nửa đ&ecirc;m, những nụ cười dịu d&agrave;ng cũng đ&atilde; l&agrave;m n&ecirc;n sự rực rỡ của m&oacute;n qu&agrave; Noel m&agrave; t&ocirc;i được nhận.<br />\r\n&ndash; Lo&agrave;i người nơi &ocirc;ng, &ocirc;ng ho&agrave;ng nhỏ n&oacute;i, trồng năm ngh&igrave;n hoa hồng cũng trong một khu vườn...thế m&agrave; họ chẳng t&igrave;m thấy c&aacute;i họ t&igrave;m...<br />\r\n&ndash; Họ chẳng t&igrave;m thấy đ&acirc;u, t&ocirc;i n&oacute;i...<br />\r\n&ndash; Trong khi c&aacute;i họ muốn t&igrave;m lại c&oacute; thể gặp trong chỉ một đo&aacute; hồng hoặc chỉ một &iacute;t nước...<br />\r\n&ndash; Đ&uacute;ng thế, t&ocirc;i n&oacute;i.<br />\r\nV&agrave; &ocirc;ng ho&agrave;ng nhỏ n&oacute;i th&ecirc;m:<br />\r\n&ndash; Con mắt vốn m&ugrave; lo&agrave;. Phải t&igrave;m kiếm với tr&aacute;i tim.<br />\r\nT&ocirc;i đ&atilde; uống. T&ocirc;i đ&atilde; nghỉ ngơi thoả thu&ecirc;. V&agrave;o l&uacute;c tinh mơ, c&aacute;t một m&agrave;u mật ong. T&ocirc;i thấy sung sướng cả v&igrave; c&aacute;i m&agrave;u mật ong ấy nữa. Tại sao t&ocirc;i cứ phải khổ sở...<br />\r\n&ndash; &Ocirc;ng phải giữ lời hứa, &ocirc;ng ho&agrave;ng nhỏ b&acirc;y giờ lại ngồi xuống b&ecirc;n t&ocirc;i, nhẹ nh&agrave;ng n&oacute;i.<br />\r\n&ndash; Lời hứa n&agrave;o?&ndash; &Ocirc;ng biết đấy... Một c&aacute;i rọ m&otilde;m cho con cừu của t&ocirc;i... T&ocirc;i chịu tr&aacute;ch nhiệm về c&aacute;i hoa ấy!<br />\r\nT&ocirc;i r&uacute;t từ trong t&uacute;i &aacute;o ra c&aacute;c bản vẽ ph&aacute;c của t&ocirc;i. &Ocirc;ng ho&agrave;ng nhỏ tr&ocirc;ng thấy c&aacute;c bức vẽ b&egrave;n cười bảo:<br />\r\n&ndash; C&aacute;c c&acirc;y baobap của &ocirc;ng, tr&ocirc;ng giống như c&aacute;c c&acirc;y cải hoa ấy...<br />\r\n&ndash; ồ!<br />\r\nT&ocirc;i vốn tự h&agrave;o biết bao với bức vẽ c&aacute;c c&acirc;y bao bap!<br />\r\n&ndash; Con c&aacute;o của &ocirc;ng... hai tai n&oacute;...tr&ocirc;ng giống như hai c&aacute;i sừng...v&agrave; lại d&agrave;i qu&aacute;!<br />\r\nV&agrave; em lại cười nữa.<br />\r\n&ndash; Cậu kh&ocirc;ng c&ocirc;ng bằng, cậu nhỏ ơi, t&ocirc;i vốn chỉ biết vẽ trăn k&iacute;n với chăn mở th&ocirc;i m&agrave;!<br />\r\n&ndash; ồ! Được cả th&ocirc;i, em n&oacute;i, trẻ con ch&uacute;ng hiểu hết.<br />\r\nThế l&agrave; t&ocirc;i d&ugrave;ng b&uacute;t ch&igrave; ph&aacute;c một c&aacute;i rọ m&otilde;m. V&agrave; l&ograve;ng t&ocirc;i se lại khi đưa c&aacute;i rọ m&otilde;m đ&oacute; cho em:<br />\r\n&ndash; Em c&oacute; những sự định m&agrave; t&ocirc;i kh&ocirc;ng được r&otilde;...<br />\r\nNhưng em chẳng trả lời t&ocirc;i. Em bảo t&ocirc;i:<br />\r\n&ndash; &Ocirc;ng biết kh&ocirc;ng, ng&agrave;y t&ocirc;i rơi xuống Tr&aacute;i Đất... mai đ&atilde; l&agrave; tr&ograve;n một năm...<br />\r\nRồi, sau một l&aacute;t im lặng, em lại n&oacute;i:<br />\r\n&ndash; T&ocirc;i rơi xuống ngay gần đ&acirc;y...<br />\r\nV&agrave; em đỏ mặt.&nbsp;V&agrave; một lần nữa, chẳng hiểu tại sao, t&ocirc;i cảm thấy buồn b&atilde; một c&aacute;ch kỳ lạ. Khi ấy t&ocirc;i bỗng buột miệng hỏi:<br />\r\n&ndash; Thế ra kh&ocirc;ng phải l&agrave; ngẫu nhi&ecirc;n m&agrave;, buổi s&aacute;ng ấy, t&ocirc;i gặp em, c&aacute;ch đ&acirc;y t&aacute;m ng&agrave;y, em lang thang một m&igrave;nh một ngh&igrave;n dặm c&aacute;ch mọi v&ugrave;ng c&oacute; người ở! Em trở lại nơi em rơi xuống hay sao?<br />\r\n&Ocirc;ng ho&agrave;ng nhỏ lại đỏ mặt.&nbsp;V&agrave; t&ocirc;i n&oacute;i th&ecirc;m, ngập ngừng:<br />\r\n&ndash; L&agrave; v&igrave;, c&oacute; lẽ, v&igrave; ng&agrave;y kỷ niệm chăng?<br />\r\n&Ocirc;ng ho&agrave;ng lại đỏ mặt lần nữa. Em kh&ocirc;ng bao giờ trả lời c&aacute;c c&acirc;u hỏi, nhưng khi người ta đỏ mặt, như thế c&oacute; nghĩa l&agrave; &quot;phải&quot;, phải kh&ocirc;ng?<br />\r\n&ndash; A, t&ocirc;i n&oacute;i, t&ocirc;i sợ...<br />\r\nNhưng em trả lời t&ocirc;i:<br />\r\n&ndash; B&acirc;y giờ &ocirc;ng phải l&agrave;m việc th&ocirc;i. &Ocirc;ng phải trở về chỗ c&aacute;i m&aacute;y của &ocirc;ng. T&ocirc;i đợi &ocirc;ng ở đ&acirc;y. Chiều mai &ocirc;ng h&atilde;y đến...<br />\r\nSong t&ocirc;i kh&ocirc;ng y&ecirc;n t&acirc;m. T&ocirc;i nhớ chuyện con c&aacute;o. C&oacute; nguy cơ l&agrave; ta c&oacute; thể kh&oacute;c một t&iacute; nếu ta lỡ để cho ai cảm ho&aacute; m&igrave;nh...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>XXVI</strong></span><br />\r\n<br />\r\nCạnh c&aacute;i giếng, c&oacute; một bức tường đổ n&aacute;t. Khi ở chỗ l&agrave;m việc trở lại v&agrave;o chiều h&ocirc;m sau, từ xa t&ocirc;i đ&atilde; thấy &ocirc;ng ho&agrave;ng nhỏ của t&ocirc;i ngồi tr&ecirc;n đ&oacute;, hai ch&acirc;n lơ lửng. V&agrave; t&ocirc;i nghe em n&oacute;i:<br />\r\n&ndash; Thế anh kh&ocirc;ng nhớ sao? Kh&ocirc;ng phải l&agrave; chỗ n&agrave;y đ&acirc;u!<br />\r\nMột giọng n&oacute;i kh&aacute;c c&oacute; lẽ trả lời em, v&igrave; em lại đ&aacute;p:<br />\r\n&ndash; Phải! Phải! Ng&agrave;y th&igrave; đ&uacute;ng, nhưng kh&ocirc;ng phải chỗ n&agrave;y...<br />\r\nT&ocirc;i bước tiếp đến chỗ bức th&agrave;nh. T&ocirc;i vẫn kh&ocirc;ng nh&igrave;n thấy, cũng chẳng nghe thấy ai. Thế nhưng, &ocirc;ng ho&agrave;ng nhỏ lại đ&aacute;p nữa:<br />\r\n&ndash;... Đ&uacute;ng thế. Anh cứ xem nơi n&agrave;o bắt đầu c&oacute; dấu ch&acirc;n của t&ocirc;i ở tr&ecirc;n c&aacute;t. Anh chỉ việc đợi t&ocirc;i ở đấy. T&ocirc;i sẽ đến đ&ecirc;m nay.<br />\r\nT&ocirc;i chỉ c&aacute;ch bức tường c&oacute; hai mươi m&eacute;t v&agrave; t&ocirc;i vẫn chẳng nh&igrave;n thấy g&igrave;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&Ocirc;ng ho&agrave;ng nhỏ lại n&oacute;i, sau một l&aacute;t im lặng:<br />\r\n&ndash; Anh c&oacute; nọc tốt đấy chứ? Anh chắc l&agrave; kh&ocirc;ng l&agrave;m t&ocirc;i đau l&acirc;u chứ?<br />\r\nT&ocirc;i dừng lại, tr&aacute;i tim se thắt, nhưng t&ocirc;i vẫn kh&ocirc;ng hiểu.<br />\r\n&ndash; Th&ocirc;i b&acirc;y giờ h&atilde;y đi đi, em n&oacute;i. T&ocirc;i muốn xuống.<br />\r\nThế l&agrave; ch&iacute;nh t&ocirc;i nh&igrave;n xuống ch&acirc;n tường, v&agrave; t&ocirc;i nhảy dựng l&ecirc;n. N&oacute; ở đ&oacute;, n&oacute; ngẩng đầu l&ecirc;n ph&iacute;a &ocirc;ng ho&agrave;ng nhỏ, một giống rắn v&agrave;ng c&oacute; thể diệt ta trong ba mươi gi&acirc;y. Vừa lục t&uacute;i t&igrave;m khẩu s&uacute;ng ngắn, t&ocirc;i vừa chạy tới, nhưng, nghe tiếng t&ocirc;i, con rắn liền chuồn trong c&aacute;t, như thể một d&ograve;ng nước chảy đi, v&agrave; chẳng cần phải vội v&agrave;ng, n&oacute; luồn l&aacute;ch giữa c&aacute;c tảng đ&aacute; với một tiếng động nh&egrave; nhẹ như tiếng va chạm của kim kh&iacute;.&nbsp;T&ocirc;i đến b&ecirc;n bức tường vừa đ&uacute;ng l&uacute;c để đ&oacute;n trong v&ograve;ng tay t&ocirc;i &ocirc;ng ho&agrave;ng b&eacute; nhỏ, trắng bệch như tuyết.<br />\r\n&ndash; Chuyện qu&aacute;i quỉ g&igrave; thế! B&acirc;y giờ em lại đi tr&ograve; chuyện với rắn!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i th&aacute;o chiếc khăn v&agrave;ng kim bao giờ cũng kho&aacute;c ở cổ em. T&ocirc;i dấp nước v&agrave;o ch&aacute;n em v&agrave; cho em uống. V&agrave; b&acirc;y giờ, t&ocirc;i kh&ocirc;ng d&aacute;m hỏi em điều g&igrave; nữa. Em nh&igrave;n t&ocirc;i nghi&ecirc;m nghị v&agrave; đưa hai tay &ocirc;m cổ t&ocirc;i. T&ocirc;i nghe tiếng tim em đập như tiếng đập của một con chim sắp chết, khi bị tr&uacute;ng đạn s&uacute;ng săn. Em bảo t&ocirc;i:<br />\r\n&ndash; T&ocirc;i rất mừng, v&igrave; biết &ocirc;ng đ&atilde; t&igrave;m ra m&aacute;y bay của &ocirc;ng thiếu c&aacute;i g&igrave;. &Ocirc;ng sắp trở về nh&agrave; &ocirc;ng...<br />\r\n&ndash; Sao em biết?<br />\r\nĐ&uacute;ng l&agrave; l&uacute;c ấy t&ocirc;i vừa định b&aacute;o cho em biết, tưởng đ&atilde; tuyệt vọng rồi, bỗng dưng t&ocirc;i lại sửa được m&aacute;y.&nbsp;Em chẳng trả lời c&acirc;u hỏi của t&ocirc;i, nhưng lại n&oacute;i th&ecirc;m:<br />\r\n&ndash; T&ocirc;i cũng như &ocirc;ng, h&ocirc;m nay t&ocirc;i về nh&agrave; t&ocirc;i...<br />\r\nRồi, giọng ngầm ng&ugrave;i:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ndash; Xa hơn nhiều lắm... kh&oacute; hơn nhiều lắm...<br />\r\nT&ocirc;i c&oacute; cảm gi&aacute;c vừa xảy ra một việc g&igrave; dị thường. T&ocirc;i siết chặt em trong v&ograve;ng tay t&ocirc;i như một em b&eacute;, nhưng khi ấy t&ocirc;i vẫn c&oacute; cảm gi&aacute;c em đang tuột thẳng xuống một vực thẳm n&agrave;o đ&oacute; d&ugrave; t&ocirc;i cố n&iacute;u giữ lại...&nbsp;&aacute;nh mắt em nghi&ecirc;m nghị, mất h&uacute;t v&agrave;o một nơi rất xa:<br />\r\n&ndash; T&ocirc;i c&oacute; con cừu của &ocirc;ng. V&agrave; t&ocirc;i c&oacute; c&aacute;i th&ugrave;ng cho con cừu. V&agrave; t&ocirc;i c&oacute; c&aacute;i rọ m&otilde;m...<br />\r\nV&agrave; em mỉm cười buồn buồn.&nbsp;T&ocirc;i đợi rất l&acirc;u. T&ocirc;i thấy em dần d&agrave; ấm lại:<br />\r\n&ndash; Cậu em ạ, em đ&atilde; sợ h&atilde;i...<br />\r\nEm c&oacute; sợ h&atilde;i, đ&uacute;ng rồi! Nhưng em cười dịu d&agrave;ng:<br />\r\n&ndash; Chiều nay t&ocirc;i sẽ c&ograve;n sợ nhiều hơn...<br />\r\nMột lần nữa, t&ocirc;i lại cảm thấy lạnh buốt người v&igrave; một c&aacute;i g&igrave; đ&oacute; kh&ocirc;ng thể cứu v&atilde;n được nữa. V&agrave; t&ocirc;i hiểu rằng t&ocirc;i kh&ocirc;ng thể chịu nổi &yacute; nghĩ rồi đ&acirc;y kh&ocirc;ng bao giờ c&ograve;n nghe thấy tiếng cười n&agrave;y nữa. Tiếng cười ấy đối với t&ocirc;i như một nguồn nước trong sa mạc.<br />\r\n&ndash; Cậu em ạ, t&ocirc;i c&ograve;n muốn được nghe em cười...<br />\r\nNhưng em bảo t&ocirc;i:<br />\r\n&ndash; Đ&ecirc;m nay, vừa đ&uacute;ng một năm. Ng&ocirc;i sao của t&ocirc;i sẽ lại ở đ&uacute;ng b&ecirc;n tr&ecirc;n chỗ t&ocirc;i rơi xuống năm ngo&aacute;i...<br />\r\n&ndash; Cậu em ạ, c&oacute; phải l&agrave; một cơn &aacute;c mộng kh&ocirc;ng, chuyện rắn, cuộc hẹn gặp với ng&ocirc;i sao n&agrave;y...<br />\r\nNhưng em kh&ocirc;ng trả lời c&acirc;u t&ocirc;i hỏi. Em bảo t&ocirc;i:<br />\r\n&ndash; C&aacute;i g&igrave; m&agrave; quan trọng, c&aacute;i đ&oacute; kh&ocirc;ng nh&igrave;n thấy được...<br />\r\n&ndash; Đ&uacute;ng thế...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ndash; Giống như b&ocirc;ng hoa của t&ocirc;i. Khi &ocirc;ng y&ecirc;u một b&ocirc;ng hoa ở tr&ecirc;n một ng&ocirc;i sao, thật l&agrave; &ecirc;m đềm, ban đ&ecirc;m, khi &ocirc;ng nh&igrave;n trời. Tất cả c&aacute;c ng&ocirc;i sao đều nở hoa.<br />\r\n&ndash; Đ&uacute;ng thế...<br />\r\n&ndash; Giống như nước vậy. Nước m&agrave; &ocirc;ng cho t&ocirc;i uống như l&agrave; &acirc;m nhạc, l&agrave; v&igrave; c&aacute;i r&ograve;ng rọc v&agrave; sợi d&acirc;y... &ocirc;ng nhớ chứ... thật ngon l&agrave;nh.<br />\r\n&ndash; Đ&uacute;ng thế...<br />\r\n&ndash; Ban đ&ecirc;m, &ocirc;ng sẽ nh&igrave;n sao. Chỗ t&ocirc;i b&eacute; qu&aacute; kh&ocirc;ng thể chỉ cho &ocirc;ng đ&acirc;u l&agrave; ng&ocirc;i sao của t&ocirc;i được. Thế lại c&agrave;ng hay. Ng&ocirc;i sao của t&ocirc;i, đối với &ocirc;ng sẽ l&agrave; một trong c&aacute;c ng&ocirc;i sao kia... Như thế, tất cả c&aacute;c ng&ocirc;i sao, &ocirc;ng đều sẽ th&iacute;ch nh&igrave;n. Tất cả ch&uacute;ng sẽ l&agrave; bạn của &ocirc;ng. V&agrave; rồi t&ocirc;i sẽ biếu &ocirc;ng một m&oacute;n qu&agrave;...<br />\r\nEm lại cười th&ecirc;m nữa.<br />\r\n&ndash; A! Em ơi, em ơi, t&ocirc;i th&iacute;ch nghe em cười như thế n&agrave;y!<br />\r\n&ndash; Đ&uacute;ng đ&oacute; l&agrave; m&oacute;n qu&agrave; t&ocirc;i biếu &ocirc;ng... giống như l&agrave; nước vậy...<br />\r\n&ndash; Em muốn n&oacute;i sao?<br />\r\n&ndash; Mọi người c&oacute; những ng&ocirc;i sao kh&ocirc;ng của ai giống ai. Đối với một số ưa th&iacute;ch đi xa, c&aacute;c ng&ocirc;i sao l&agrave; những kẻ dẫn đường. Đối với số kh&aacute;c, ch&uacute;ng chỉ l&agrave; những đốm s&aacute;ng nhỏ. Đối với những nh&agrave; b&aacute;c học, ch&uacute;ng l&agrave; những b&agrave;i to&aacute;n. Đối với nh&agrave; doanh nghiệp của t&ocirc;i, ch&uacute;ng l&agrave; v&agrave;ng. Nhưng tất cả loại ng&ocirc;i sao ấy đều c&acirc;m lặng. &Ocirc;ng, &ocirc;ng sẽ c&oacute; những ng&ocirc;i sao trước đ&acirc;y kh&ocirc;ng ai c&oacute;...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ndash; Em muốn n&oacute;i sao?<br />\r\n&ndash; Khi &ocirc;ng nh&igrave;n trời, ban đ&ecirc;m, bởi v&igrave; ở một trong những ng&ocirc;i sao đ&oacute; c&oacute; t&ocirc;i ở, bởi v&igrave; t&ocirc;i cười tr&ecirc;n một trong những ng&ocirc;i sao đ&oacute;, cho n&ecirc;n đối với &ocirc;ng tưởng chừng như tất cả c&aacute;c ng&ocirc;i sao đều cười. &Ocirc;ng, &ocirc;ng sẽ c&oacute; được những ng&ocirc;i sao biết cười.<br />\r\nRồi em lại cười nữa.<br />\r\n&ndash; V&agrave; khi &ocirc;ng đ&atilde; ngu&ocirc;i rồi (người ta bao giờ cũng ngu&ocirc;i đi), &ocirc;ng sẽ bằng l&ograve;ng đ&atilde; từng được quen biết t&ocirc;i. &Ocirc;ng m&atilde;i m&atilde;i l&agrave; bạn t&ocirc;i. Rồi đ&ocirc;i khi &ocirc;ng mở của sổ nh&agrave; &ocirc;ng, như thế đấy, để cho vui th&ocirc;i... Bạn hữu của &ocirc;ng sẽ lấy l&agrave;m lạ khi thấy &ocirc;ng vừa nh&igrave;n trời vừa cười. &Ocirc;ng sẽ bảo họ: &quot;Phải, c&aacute;c ng&ocirc;i sao, l&uacute;c n&agrave;o ch&uacute;ng cũng l&agrave;m &igrave;nh cười!&quot; V&agrave; họ sẽ tưởng &ocirc;ng đi&ecirc;n. Ho&aacute; ra t&ocirc;i chơi khăm &ocirc;ng một vố...<br />\r\nRồi em lại cười nữa.<br />\r\n&ndash; Xem như t&ocirc;i đ&atilde; tặng &ocirc;ng, thay v&igrave; những ng&ocirc;i sao, h&agrave;ng đống những c&aacute;i chu&ocirc;ng nhỏ biết cười...<br />\r\nRồi em lại cười nữa. V&agrave; em trở lại nghi&ecirc;m nghị:<br />\r\n&ndash; Đ&ecirc;m nay... &ocirc;ng biết đấy... đừng đến l&agrave;m g&igrave;.<br />\r\n&ndash; T&ocirc;i sẽ kh&ocirc;ng rời em đ&acirc;u.<br />\r\n&ndash; T&ocirc;i sẽ c&oacute; vẻ như đau... T&ocirc;i sẽ c&oacute; vẻ sắp chết. Thế th&ocirc;i m&agrave;! &Ocirc;ng đừng đến chứng kiến cảnh đ&oacute;, kh&ocirc;ng đ&aacute;ng g&igrave; đ&acirc;u...<br />\r\n&ndash; T&ocirc;i sẽ kh&ocirc;ng rời em.<br />\r\nNhưng em c&oacute; vẻ lo lắng.<br />\r\n&ndash; T&ocirc;i n&oacute;i với &ocirc;ng... l&agrave; tại v&igrave; con rắn đ&acirc;y. Chớ để rắn n&oacute; cắn &ocirc;ng. Lo&agrave;i rắn, ch&uacute;ng &aacute;c lắm. Ch&uacute;ng c&oacute; thể cắn để m&agrave; chơi...<br />\r\n&ndash; T&ocirc;i sẽ kh&ocirc;ng rời em.<br />\r\nNhưng c&oacute; c&aacute;i g&igrave; đ&oacute; l&agrave;m em y&ecirc;n t&acirc;m:<br />\r\n&ndash; Nhưng đ&uacute;ng l&agrave; n&oacute; sẽ kh&ocirc;ng c&ograve;n nọc cho lần cắn thứ hai...&nbsp;<br />\r\nĐ&ecirc;m ấy, t&ocirc;i kh&ocirc;ng tr&ocirc;ng thấy em bỏ đi. Em đ&atilde; trốn đi kh&ocirc;ng một tiếng động. Khi t&ocirc;i đuổi kịp em, em bước quả quyết thật nhanh. Em chỉ n&oacute;i với t&ocirc;i:<br />\r\n&ndash; A! &Ocirc;ng lại đến đ&acirc;y rồi...<br />\r\nRồi em nắm tay t&ocirc;i. Nhưng em lại dằn vặt nữa:<br />\r\n&ndash; &Ocirc;ng kh&ocirc;ng đ&uacute;ng. &Ocirc;ng sẽ khổ cho xem. T&ocirc;i sẽ c&oacute; vẻ chết nhưng kh&ocirc;ng phải l&agrave; thật đ&acirc;u...<br />\r\nT&ocirc;i, t&ocirc;i im lặng.<br />\r\n&ndash; &Ocirc;ng biết đấy. Đường xa lắm! T&ocirc;i kh&ocirc;ng mang nổi c&aacute;i th&acirc;n x&aacute;c n&agrave;y. Nặng lắm.<br />\r\nT&ocirc;i, t&ocirc;i im lặng.<br />\r\n&ndash; Nhưng n&oacute; cũng sẽ chỉ như c&aacute;i vỏ gi&agrave; bỏ lại. Những c&aacute;i vỏ gi&agrave; th&igrave; c&oacute; g&igrave; đ&aacute;ng buồn...<br />\r\nT&ocirc;i, t&ocirc;i im lặng.<br />\r\nEm c&oacute; hơi nản. Nhưng em gắng th&ecirc;m một lần nữa:<br />\r\n&ndash; Sẽ hay lắm chứ, &ocirc;ng biết đấy. T&ocirc;i cũng sẽ nh&igrave;n c&aacute;c ng&ocirc;i sao. C&aacute;c ng&ocirc;i sao sẽ l&agrave; những c&aacute;i giếng với một c&aacute;i r&ograve;ng rọc han rỉ. Tất cả mọi ng&ocirc;i sao sẽ r&oacute;t cho t&ocirc;i uống...<br />\r\nT&ocirc;i, t&ocirc;i im lặng.<br />\r\n&ndash; Sẽ thật buồn cười! &Ocirc;ng sẽ c&oacute; năm trăm triệu chiếc chu&ocirc;ng, c&ograve;n t&ocirc;i, năm trăm triệu nguồn nước...<br />\r\nV&agrave; em cũng im lu&ocirc;n, bởi v&igrave; em kh&oacute;c...<br />\r\n&ndash; Đ&acirc;y rồi. &Ocirc;ng để t&ocirc;i bước một m&igrave;nh th&ocirc;i.<br />\r\nV&agrave; em ngồi xuống v&igrave; sợ.&nbsp;<br />\r\nEm lại n&oacute;i nữa:<br />\r\n&ndash; &Ocirc;ng biết đấy... đo&aacute; hoa của t&ocirc;i... t&ocirc;i phải c&oacute; tr&aacute;ch nhiệm! N&agrave;ng thật l&agrave; yếu đuối! N&agrave;ng thật ng&acirc;y thơ! N&agrave;ng chỉ c&oacute; bốn c&aacute;i gai chẳng thấm v&agrave;o đ&acirc;u để m&agrave; chống trọi tr&ecirc;n đời...<br />\r\nT&ocirc;i ngồi xuống v&igrave; kh&ocirc;ng thể giữ m&igrave;nh đứng được nữa. Em n&oacute;i:<br />\r\n&ndash; Kia rồi... chỉ c&oacute; thế th&ocirc;i...<br />\r\nEm lưỡng lự một ch&uacute;t, rồi đứng l&ecirc;n. Em bước một bước. C&ograve;n t&ocirc;i, t&ocirc;i kh&ocirc;ng thể n&agrave;o cử động được.<br />\r\nChỉ c&oacute; một lo&aacute;ng chớp m&agrave;u v&agrave;ng gần b&ecirc;n mắt c&aacute; ch&acirc;n em. Em đứng một l&uacute;c kh&ocirc;ng động đậy. Em kh&ocirc;ng k&ecirc;u. Em ng&atilde; xuống nhẹ nh&agrave;ng như một c&aacute;i c&acirc;y. Cả một tiếng động cũng kh&ocirc;ng c&oacute;, l&agrave; bởi v&igrave; c&aacute;t. </span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>XXVII</strong></span><br />\r\n<br />\r\nV&agrave; giờ đ&acirc;y, đ&uacute;ng vậy, s&aacute;u năm đ&atilde; tr&ocirc;i qua... T&ocirc;i chưa bao giờ kể lại c&acirc;u chuyện n&agrave;y. Những bạn b&egrave; gặp lại t&ocirc;i rất lấy l&agrave;m h&agrave;i l&ograve;ng thấy t&ocirc;i c&ograve;n sống. T&ocirc;i đ&atilde; rất buồn, nhưng t&ocirc;i n&oacute;i với họ: Đ&oacute; l&agrave; v&igrave; mệt...&nbsp;B&acirc;y giờ t&ocirc;i đ&atilde; ngu&ocirc;i ngu&ocirc;i. Nghĩa l&agrave;... kh&ocirc;ng hẳn l&agrave; như thế. Nhưng t&ocirc;i biết em đ&atilde; trở về tinh cầu của m&igrave;nh, v&igrave; s&aacute;ng sớm h&ocirc;m sau, t&ocirc;i đ&atilde; kh&ocirc;ng t&igrave;m thấy th&acirc;n x&aacute;c em. C&aacute;i th&acirc;n x&aacute;c chẳng nặng nề g&igrave; mấy... V&agrave; ban đ&ecirc;m, t&ocirc;i th&iacute;ch lắng nghe c&aacute;c ng&ocirc;i sao. Ch&uacute;ng như năm trăm triệu c&aacute;i chu&ocirc;ng nhỏ...&nbsp;Nhưng c&oacute; xảy ra một điều g&igrave; đ&oacute; kh&ocirc;ng b&igrave;nh thường. Chiếc rọ m&otilde;m t&ocirc;i đ&atilde; vẽ cho &ocirc;ng ho&agrave;ng nhỏ, t&ocirc;i đ&atilde; qu&ecirc;n vẽ th&ecirc;m c&aacute;i đai da. Em sẽ kh&ocirc;ng thể n&agrave;o buộc n&oacute; cho con cừu được.&nbsp;Thế l&agrave; t&ocirc;i tự hỏi: &quot; Chuyện g&igrave; đ&atilde; xảy ra tr&ecirc;n tinh cầu ấy? C&oacute; lẽ con cừu đ&atilde; ăn mất đo&aacute; hoa chăng...&quot;&nbsp;Khi th&igrave; t&ocirc;i tự nhủ: &quot;Chắc l&agrave; kh&ocirc;ng! &Ocirc;ng ho&agrave;ng nhỏ đ&ecirc;m n&agrave;o cũng đậy hoa trong c&aacute;i bầu k&iacute;nh, v&agrave; em cũng tr&ocirc;ng chừng con cừu cẩn thận...&quot; Thế l&agrave; t&ocirc;i vui sướng. V&agrave; tất cả c&aacute;c ng&ocirc;i sao mỉm cười dịu d&agrave;ng.&nbsp;Khi th&igrave; t&ocirc;i tự nhủ: &quot;Thế n&agrave;o chẳng c&oacute; l&uacute;c người ta lơ đ&atilde;ng, v&agrave; thế l&agrave; đủ! Em đ&atilde; qu&ecirc;n mất, một đ&ecirc;m n&agrave;o đ&oacute;, c&aacute;i bầu k&iacute;nh, hoặc con cừu lẻn ra kh&ocirc;ng một tiếng động trong đ&ecirc;m...&quot; Thế l&agrave; những c&aacute;i chu&ocirc;ng nhỏ đều biến th&agrave;nh nước mắt!...&nbsp;Đ&oacute; l&agrave; một b&iacute; ẩn lớn lao. Với bạn, l&agrave; người cũng y&ecirc;u &ocirc;ng ho&agrave;ng b&eacute; nhỏ, cũng như với t&ocirc;i, cả vũ trụ n&agrave;y sẽ chẳng c&ograve;n như cũ nếu như ở đ&acirc;u đ&oacute;, kh&ocirc;ng biết ở đ&acirc;u, một con cừu m&agrave; ta kh&ocirc;ng biết, đ&atilde; c&oacute; hay l&agrave; kh&ocirc;ng ăn một đo&aacute; hoa hồng...&nbsp;H&atilde;y nh&igrave;n l&ecirc;n trời. H&atilde;y tự hỏi: con cừu đ&atilde; c&oacute; ăn hay kh&ocirc;ng ăn đo&aacute; hoa? V&agrave; bạn sẽ thấy tất cả đều thay đổi...&nbsp;V&agrave; kh&ocirc;ng một người lớn n&agrave;o lại hiểu được chuyện ấy quan trọng đến nhường n&agrave;o!&nbsp;Cảnh ấy đối với t&ocirc;i, l&agrave; cảnh đẹp nhất v&agrave; buồn nhất tr&ecirc;n thế gian. N&oacute; cũng l&agrave; c&aacute;i cảnh ở trang trước, nhưng t&ocirc;i đ&atilde; vẽ một lần nữa để bạn nh&igrave;n r&otilde; hơn. Ch&iacute;nh tại nơi đ&acirc;y &ocirc;ng ho&agrave;ng b&eacute; nhỏ đ&atilde; xuất hiện tr&ecirc;n Tr&aacute;i Đất, rồi lại biến đi.&nbsp;H&atilde;y nh&igrave;n chăm ch&uacute; cảnh n&agrave;y cho đến khi bạn chắc rằng bạn c&oacute; thể nhận ra n&oacute;, nếu ng&agrave;y kia bạn du h&agrave;nh sang ch&acirc;u Phi, trong sa mạc. V&agrave; nếu bạn t&igrave;nh cờ đi ngang qua đ&oacute;, t&ocirc;i xin bạn, xin bạn đừng vội, h&atilde;y n&aacute;n lại một ch&uacute;t ngay dưới ng&ocirc;i sao! Nếu bấy giờ c&oacute; một đứa b&eacute; đến b&ecirc;n bạn, nếu em cười, nếu em c&oacute; m&aacute;i t&oacute;c v&agrave;ng &oacute;ng, nếu em kh&ocirc;ng trả lời khi người ta hỏi, bạn sẽ đo&aacute;n ra đấy l&agrave; ai. Bấy giờ bạn h&atilde;y thương t&ocirc;i! Đừng để t&ocirc;i buồn qu&aacute; thế n&agrave;y: H&atilde;y viết thư cho t&ocirc;i b&aacute;o rằng em đ&atilde; trở lại...<br />\r\n<br />\r\n<strong>HẾT</strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(11, 'Người đua diều', 'Văn học nước ngoài', 'Khaled Hosseini', 2003, 'nguoi-dua-dieu.jpg', 'Đang cập nhật', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Người đua diều&nbsp;từng<em>&nbsp;</em>l&agrave; từng đoạn k&yacute; ức của Amir về cuộc đời của m&igrave;nh, từ khi cậu l&agrave; một cậu b&eacute; mười một tuổi cho đến khi cậu trở th&agrave;nh một người đ&agrave;n &ocirc;ng trung ni&ecirc;n trưởng th&agrave;nh. Trong k&yacute; ức của m&igrave;nh, Amir kh&ocirc;ng ngừng nhớ về qu&ecirc; hương Afghanistan, về người bạn thời thơ ấu, v&agrave; về những tội lỗi kh&ocirc;ng thể tha thứ. Cậu lu&ocirc;n cố gắng qu&ecirc;n đi tất cả, nhưng chứng bệnh đau đầu kinh ni&ecirc;n như l&agrave; mối d&acirc;y gắn kết cậu với qu&aacute; khứ, v&agrave; n&oacute; vẫn l&agrave;m cậu kh&ocirc;ng ngừng day dứt mỗi khi nhớ về.Mọi chuyện tưởng chừng đ&atilde; y&ecirc;n ổn với Amir khi cậu chuyển đến định cư ở Mỹ với Baba, bắt đầu đi học, trở th&agrave;nh nh&agrave; văn, lập gia đ&igrave;nh v&agrave; coi California như qu&ecirc; hương cho đến một ng&agrave;y cậu nhận được cuộc gọi từ ch&uacute; Rahim Khan từ Pakistan. Cuộc gọi ấy đưa ch&uacute;ng ta đến với Afghanistan tươi đẹp ở những năm 1970, với Amir v&agrave; Hassan, với những lỗi lầm kh&ocirc;ng được chuộc, v&agrave; với c&aacute;ch Amir đối diện với qu&aacute; khứ của m&igrave;nh.</span></span></p>\r\n', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><span style=\"color:#e74c3c\">CHƯƠNG I&nbsp;</span></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i đ&atilde; trở th&agrave;nh t&ocirc;i như ng&agrave;y h&ocirc;m nay từ tuổi mười hai, v&agrave;o một ng&agrave;y lạnh lẽo, ảm đạm m&ugrave;a đ&ocirc;ng năm 1975. T&ocirc;i nhớ ch&iacute;nh x&aacute;c l&uacute;c đ&oacute; đang khom người sau bức tường đất sụp đổ, nh&ograve;m trộm v&agrave;o lối nhỏ gần khe suối đ&oacute;ng băng. Thời đ&oacute; l&acirc;u lắm rồi, nhưng t&ocirc;i biết những g&igrave; người ta n&oacute;i về qu&aacute; khứ, m&agrave; t&ocirc;i đ&atilde; học được, về việc anh c&oacute; thể ch&ocirc;n v&ugrave;i n&oacute; đi, l&agrave; một điều sai lầm. Bởi v&igrave; qu&aacute; khứ c&ograve;n đang thay đổi. B&acirc;y giờ nh&igrave;n lại, t&ocirc;i nhận ra t&ocirc;i vẫn đang nh&ograve;m v&agrave;o c&aacute;i lối nhỏ hoang vắng đ&oacute; suốt hai mươi s&aacute;u năm qua.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">M&ugrave;a h&egrave; vừa rồi, một h&ocirc;m ch&uacute; Rahim Khan gọi cho t&ocirc;i từ Pakistan. Ch&uacute; y&ecirc;u cầu t&ocirc;i tới gặp ch&uacute;. Đứng trong bếp, với m&aacute;y nghe b&ecirc;n tai, t&ocirc;i hiểu đ&oacute; kh&ocirc;ng phải l&agrave; ch&uacute; Rahim Khan tr&ecirc;n m&aacute;y. Đ&oacute; l&agrave; qu&aacute; khứ tội lỗi kh&ocirc;ng được chuộc của t&ocirc;i. Sau khi treo m&aacute;y, t&ocirc;i đi dạo bộ dọc theo Hồ Spreckels ở ph&iacute;a bắc c&ocirc;ng vi&ecirc;n Cổng V&agrave;ng. Mặt trời buổi chiều sớm lấp l&aacute;nh nước hồ, tr&ecirc;n đ&oacute; h&agrave;ng chục nhưng chiếc thuyền con đang giong buồm theo l&agrave;n gi&oacute; nhẹ kh&ocirc;. Rồi t&ocirc;i liếc nh&igrave;n l&ecirc;n, thấy một đ&ocirc;i diều m&agrave;u đỏ với những chiếc đu&ocirc;i đ&agrave;i m&agrave;u xanh lơ bay v&uacute;t l&ecirc;n trời. Đ&ocirc;i diều nh&agrave;o lộn tầng kh&ocirc;ng, b&ecirc;n tr&ecirc;n những h&agrave;ng c&acirc;y đầu ph&iacute;a T&acirc;y c&ocirc;ng vi&ecirc;n, bay qua những cối xay gi&oacute;, bồng bềnh b&ecirc;n nhau như một cặp mắt đang nh&igrave;n xuống San Francisco, th&agrave;nh phố giờ đ&acirc;y t&ocirc;i gọi l&agrave; qu&ecirc; hương. V&agrave; bất chợt giọng n&oacute;i của Hassan th&igrave; th&agrave;o trong đầu t&ocirc;i: v&igrave; cậu, cả ng&agrave;n lần rồi. Hassan người đua diều m&ocirc;i hẻ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i ngồi tr&ecirc;n băng ghế c&ocirc;ng vi&ecirc;n cạnh một c&acirc;y liễu rủ. T&ocirc;i nghĩ tới điều g&igrave; đ&oacute; Rahim Khan đ&atilde; n&oacute;i trước khi c&uacute;p m&aacute;y, gần như chợt nghĩ th&ecirc;m: lu&ocirc;n c&oacute; một con đường để tốt l&agrave;nh trở lại. T&ocirc;i ngước nh&igrave;n cặp diều sinh đ&ocirc;i. T&ocirc;i nghĩ về Hassan. Nghĩ về Baba. Ali. Kabul. T&ocirc;i nghĩ về cuộc đời t&ocirc;i đ&atilde; từng sống, cho tới khi m&ugrave;a đ&ocirc;ng năm 1975 ập đến, l&agrave;m thay đổi tất cả. V&agrave; khiến t&ocirc;i trở th&agrave;nh t&ocirc;i như ng&agrave;y h&ocirc;m nay.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>CHƯƠNG II</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Đ&ocirc;i khi ngồi tr&ecirc;n những ngọn c&acirc;y đ&oacute;, t&ocirc;i xui Hassan lấy s&uacute;ng cao su bắn quả &oacute;c ch&oacute; v&agrave;o con ch&oacute; chăn cừu giống Đức chột mắt của &ocirc;ng h&agrave;ng x&oacute;m. Hassan chẳng bao giờ muốn vậy, nhưng nếu t&ocirc;i y&ecirc;u cầu, thực sự y&ecirc;u cầu, cậu sẽ kh&ocirc;ng từ chối. Hassan chẳng bao giờ từ chối t&ocirc;i bất cứ điều g&igrave;. V&agrave; cậu bắn s&uacute;ng cao su ch&iacute;nh x&aacute;c chết người. Cha của Hassan, &ocirc;ng Ali, thường bắt gặp ch&uacute;ng t&ocirc;i v&agrave; ph&aacute;t đi&ecirc;n l&ecirc;n, đi&ecirc;n theo c&aacute;ch người hiền hậu như Ali c&oacute; thể l&agrave;m được. &Ocirc;ng thường bật ng&oacute;n tay, vẫy ch&uacute;ng t&ocirc;i tụt xuống. &Ocirc;ng giật lấy mảnh gương v&agrave; bảo ch&uacute;ng t&ocirc;i b&agrave; nội của Hassan đ&atilde; dạy &ocirc;ng rằng quỷ dữ cũng chiếu những mảnh gương, chiếu để l&agrave;m rối tr&iacute; c&aacute;c t&iacute;n đồ Hồi gi&aacute;o trong l&uacute;c cầu nguyện. &Ocirc;ng lu&ocirc;n quắc mắt nh&igrave;n con trai m&igrave;nh n&oacute;i th&ecirc;m &quot;v&agrave; quỷ cũng cười h&ocirc; hố trong khi l&agrave;m như vậy.&quot;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&quot;V&acirc;ng, thưa cha&quot;, Hassan thường lẩm bẩm, vừa nh&igrave;n xuống dưới ch&acirc;n. Nhưng kh&ocirc;ng bao giờ cậu đổ cho t&ocirc;i. Kh&ocirc;ng bao giờ n&oacute;i rằng mảnh gương cũng như việc bắn quả &oacute;c ch&oacute; v&agrave;o con ch&oacute; của &ocirc;ng l&aacute;ng giềng lu&ocirc;n lu&ocirc;n l&agrave; tr&ograve; nghịch ngợm của t&ocirc;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Những c&acirc;y bạch dương xếp th&agrave;nh h&agrave;ng dọc con đường xe chạy l&aacute;t gạch đỏ dẫn tới đ&ocirc;i cổng sắt uốn. Đ&ocirc;i cổng kế tiếp mở v&agrave;o phần mở rộng của đường xe v&agrave;o tư dinh của cha t&ocirc;i. Ng&ocirc;i nh&agrave; toạ lạc ph&iacute;a b&ecirc;n tr&aacute;i con đường gạch, s&acirc;n sau ở cuối đường.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Mọi người đều đồng &yacute; l&agrave; cha t&ocirc;i, tức Baba của t&ocirc;i, đ&atilde; x&acirc;y một ng&ocirc;i nh&agrave; đẹp nhất quận Wazir Akar Khan, v&ugrave;ng phụ cận mới v&agrave; gi&agrave;u c&oacute; ở phần Bắc Kabul. V&agrave;i người c&ograve;n nghĩ đ&oacute; l&agrave; ng&ocirc;i nh&agrave; xinh đẹp nhất trong to&agrave;n bộ Kabul. Một lối v&agrave;o rộng, hai b&ecirc;n l&agrave; những kh&oacute;m hồng dẫn tới một to&agrave; nh&agrave; trải d&agrave;i với s&agrave;n nh&agrave; bằng đ&aacute; cẩm thạch v&agrave; những cửa sổ rộng. Những vi&ecirc;n gạch khảm khắc cầu kỳ, m&agrave; Baba tự tay lựa chọn ở Isfahan, được l&aacute;t cho s&agrave;n của bốn ph&ograve;ng tắm. Những tấm thảm th&ecirc;u sợi v&agrave;ng, Baba mua ở Calcutta, chăng dọc c&aacute;c bức tường. Một ch&ugrave;m đ&egrave;n phal&ecirc; treo dưới v&ograve;m trần.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tr&ecirc;n g&aacute;c l&agrave; ph&ograve;ng ngủ của t&ocirc;i, ph&ograve;ng ngủ của Baba, ph&ograve;ng đọc của &ocirc;ng, c&ograve;n được coi như &quot;ph&ograve;ng h&uacute;t&quot;, nơi kh&ocirc;ng ngừng toả ra m&ugrave;i thuốc l&aacute; v&agrave; m&ugrave;i quế. Baba v&agrave; bạn &ocirc;ng ngả người tr&ecirc;n chiếc ghế đệm da m&agrave;u đen ở đ&oacute;, sau khi Ali đ&atilde; phục vụ cơm tối. Họ nhồi tẩu thuốc - ri&ecirc;ng Baba lu&ocirc;n gọi đ&oacute; l&agrave; &quot;vỗ b&eacute;o tẩu thuốc&quot; - v&agrave; tranh luận về ba đề t&agrave;i ưa th&iacute;ch: ch&iacute;nh trị, kinh doanh v&agrave; b&oacute;ng đ&aacute;. Đ&ocirc;i khi t&ocirc;i hỏi Baba liệu t&ocirc;i c&oacute; thề ngồi c&ugrave;ng họ được kh&ocirc;ng, nhưng Baba thường đ&uacute;ng chặn trước cửa v&agrave; n&oacute;i &quot;L&agrave;m việc của con đi, nhanh l&ecirc;n n&agrave;o. Giờ l&agrave; l&uacute;c người lớn n&oacute;i chuyện. Sao con kh&ocirc;ng đọc một cuốn s&aacute;ch n&agrave;o đ&oacute; của con?&quot; &Ocirc;ng thường đ&oacute;ng cửa lại, để mặc t&ocirc;i ngạc nhi&ecirc;n kh&ocirc;ng hiểu tại sao &ocirc;ng cứ &quot;l&uacute;c người lớn n&oacute;i chuyện&quot; m&atilde;i thế. T&ocirc;i thường &ocirc;m đầu gối ngồi ngay b&ecirc;n cửa. Đ&ocirc;i khi t&ocirc;i ngồi đ&oacute; một tiếng đồng hồ, c&oacute; khi hai tiếng, lắng nghe họ cười, họ chuyện gẫu với nhau.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ph&ograve;ng kh&aacute;ch ở tầng dưới c&oacute; một bức tường cong với những chiếc tủ đ&oacute;ng theo phong c&aacute;ch truyền thống. Trong ph&ograve;ng treo nhưng bức h&igrave;nh gia đ&igrave;nh. Một tấm ảnh giấy lụa cũ của &ocirc;ng nội t&ocirc;i v&agrave; quốc vương Nadir Shah chụp năm 1931, hai năm trước khi quốc vương bị &aacute;m s&aacute;t. Hai người đang đứng b&ecirc;n ngắm một con nai chết, ch&acirc;n đi ủng cao tận đầu gối, s&uacute;ng kho&aacute;c qua vai. Kia l&agrave; một bức h&igrave;nh đ&ecirc;m cưới cha mẹ t&ocirc;i, Baba sang trọng trong bộ lễ phục đen v&agrave; mẹ t&ocirc;i v&aacute;y trắng với nụ cười của một nữ quận ch&uacute;a trẻ. Đ&acirc;y l&agrave; Baba v&agrave; người bạn qu&yacute; nhất, bạn kinh doanh của &ocirc;ng, ch&uacute; Rahim Khan, đứng trước nh&agrave; của ch&uacute;ng t&ocirc;i, chẳng ai mỉm cười. T&ocirc;i l&agrave; đứa b&eacute; trong bức ảnh, Baba đang bế t&ocirc;i, tr&ocirc;ng c&oacute; vẻ mệt mỏi ra nghi&ecirc;m khắc. T&ocirc;i ở trong tay &ocirc;ng, nhưng những ng&oacute;n tay t&ocirc;i cong l&ecirc;n ngoắc lấy ng&oacute;n &uacute;t của ch&uacute; Rahim Khan.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Bức tường cong dẫn v&agrave;o ph&ograve;ng ăn, giữa ph&ograve;ng l&agrave; một chiếc b&agrave;n bằng gỗ gụ c&oacute; thể phục vụ thoải m&aacute;i ba mươi thực kh&aacute;ch - v&agrave; hợp với sở th&iacute;ch của cha t&ocirc;i, những bữa tiệc cực kỳ tốn k&eacute;m hầu như tuần n&agrave;o cũng c&oacute;. Ở đầu kia của ph&ograve;ng ăn l&agrave; một l&ograve; sưởi cao lớn bằng đ&aacute; cẩm thạch, lu&ocirc;n lu&ocirc;n ch&aacute;y s&aacute;ng ngọn lửa m&agrave;u da cam trong suốt m&ugrave;a đ&ocirc;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Một cửa k&iacute;nh trượt rộng lớn mở ra c&aacute;i hi&ecirc;n b&aacute;n nguyệt bao qu&aacute;t non một h&eacute;cta đất ph&iacute;a sau v&agrave; những h&agrave;ng c&acirc;y anh đ&agrave;o. Baba v&agrave; Ali đ&atilde; trồng một vườn rau nhỏ dọc theo d&atilde;y tường Ph&iacute;a Đ&ocirc;ng, gồm c&agrave; chua, h&uacute;ng bạc h&agrave;, ớt v&agrave; cả một h&agrave;ng ng&ocirc; chẳng bao giờ thu hoạch m&agrave; Hassan v&agrave; t&ocirc;i quen gọi l&agrave; &quot;Bức tường Ng&ocirc; ốm&quot;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Đầu ph&iacute;a Nam của khu vườn, dưới b&oacute;ng c&acirc;y sơn tr&agrave; l&agrave; nh&agrave; của những người gi&uacute;p việc, một t&uacute;p lều nhỏ tường đất khi&ecirc;m tốn, nơi Hassan sống c&ugrave;ng với bố m&igrave;nh.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ch&iacute;nh ở đ&oacute;, trong t&uacute;p lều nhỏ ấy, Hassan được sinh ra v&agrave;o m&ugrave;a đ&ocirc;ng năm 1964, đ&uacute;ng một năm sau khi mẹ t&ocirc;i mất l&uacute;c sinh t&ocirc;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Mười t&aacute;m năm sống trong ng&ocirc;i nh&agrave; n&agrave;y, t&ocirc;i chỉ bước v&agrave;o l&atilde;nh địa của Hassan v&agrave; Ali một v&agrave;i lần. Khi mặt trời xuống thấp sau những d&atilde;y đồi v&agrave; ch&uacute;ng t&ocirc;i đ&atilde; chơi đ&ugrave;a hết cả ng&agrave;y, Hassan v&agrave; t&ocirc;i ai về đường nấy. T&ocirc;i đi qua những kh&oacute;m hoa hồng về to&agrave; lầu của Baba, Hassan về t&uacute;p lều đất nơi cậu đ&atilde; được sinh ra, nơi cậu sống đời m&igrave;nh. T&ocirc;i vẫn c&ograve;n nhớ nơi đ&oacute; vắng vẻ, sạch sẽ v&agrave; leo l&eacute;t s&aacute;ng nhờ hai c&acirc;y đ&egrave;n dầu. C&oacute; hai tấm đệm ở hai ph&iacute;a đối diện căn lều, ở giữa l&agrave; một tấm thảm Herati nhỏ đ&atilde; cũ, xung quanh m&ograve;n xơ, một chiếc ghế đẩu ba ch&acirc;n v&agrave; chiếc b&agrave;n gỗ ở g&oacute;c nh&agrave; đề Hassan vẽ tranh. Tường xung quanh trần trụi, ngoại trừ một tấm thảm duy nhất đ&iacute;nh c&aacute;c hạt cườm th&agrave;nh mấy từ Allah-u-akbar 1, Baba đ&atilde; mua n&oacute; cho &ocirc;ng Ali trong một chuyến đi Mashad.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ch&iacute;nh trong t&uacute;p lều đ&oacute; m&agrave; mẹ của Hassan, b&agrave; Sanaubar đ&atilde; sinh ra cậu v&agrave;o một ng&agrave;y đ&ocirc;ng gi&aacute; năm 1964. Trong khi mẹ t&ocirc;i băng huyết chết khi sinh t&ocirc;i, Hassan lại mất mẹ l&uacute;c sinh ra chưa được một tuần. Mất mẹ, th&igrave; hầu hết người Afghan đều coi l&agrave; c&ograve;n tệ hại hơn mẹ chết nhiều: B&agrave; ấy bỏ đi theo một bộ tộc vũ c&ocirc;ng v&agrave; du ca.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Hassan chẳng bao giờ n&oacute;i về mẹ, như thể b&agrave; ấy chưa bao giờ tồn tại. T&ocirc;i lu&ocirc;n lu&ocirc;n thắc mắc kh&ocirc;ng hiểu liệu cậu c&oacute; mơ thấy mẹ m&igrave;nh kh&ocirc;ng, b&agrave; ấy tr&ocirc;ng như thế n&agrave;o v&agrave; đang ở đ&acirc;u. T&ocirc;i kh&ocirc;ng hiểu liệu cậu c&oacute; mong gặp mẹ kh&ocirc;ng. Cậu c&oacute; đau khổ về mẹ như t&ocirc;i đau khổ v&igrave; kh&ocirc;ng bao giờ được gặp mẹ? Một h&ocirc;m, ch&uacute;ng t&ocirc;i đang cuốc bộ từ ng&ocirc;i nh&agrave; của cha t&ocirc;i đến rạp chiếu phim Zainab để xem bộ phim mới của Iran. Ch&uacute;ng t&ocirc;i đ&atilde; di tắt qua trại l&iacute;nh gần trường trung học Istiqlal - Baba đ&atilde; cấm ch&uacute;ng t&ocirc;i đi lối tắt ấy, nhưng l&uacute;c n&agrave;y &ocirc;ng c&ugrave;ng ch&uacute; Rahim Khan đang ở Pakistan. Ch&uacute;ng t&ocirc;i nhảy qua h&agrave;ng r&agrave;o v&acirc;y quanh doanh trại, băng qua khe nước nhỏ v&agrave; đột nhập v&agrave;o một b&atilde;i đất trống bẩn thỉu c&oacute; những chiếc xe tăng cổ lỗ bỏ hoang phủ đầy bụi. Đ&aacute;m l&iacute;nh tụ tập dưới b&oacute;ng một chiếc xe tăng vừa h&uacute;t thuốc l&aacute; vừa đ&aacute;nh b&agrave;i. Một g&atilde; tr&ocirc;ng thấy ch&uacute;ng t&ocirc;i, hu&yacute;ch khuỷu tay v&agrave;o g&atilde; ngồi b&ecirc;n, gọi Hassan.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- &Ecirc; thằng nhỏ, - hắn n&oacute;i. - Tao biết m&agrave;y đấy.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Trước đ&oacute;, ch&uacute;ng t&ocirc;i đ&atilde; bao giờ gặp hắn đ&acirc;u. Hắn l&agrave; một g&atilde; đ&agrave;n &ocirc;ng to b&egrave;, thấp b&eacute;o, đầu cạo trọc, r&acirc;u đen lởm chởm tr&ecirc;n mặt. C&aacute;i c&aacute;ch hắn nhăn nhở cười với ch&uacute;ng t&ocirc;i, liếc nh&igrave;n d&acirc;m dật khiến t&ocirc;i hoảng sợ. &quot;Kệ, cứ đi,&quot; t&ocirc;i l&uacute;ng b&uacute;ng bảo Hassan.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- M&agrave;y! Thằng Hazara! 2 H&atilde;y nh&igrave;n tao khi tao n&oacute;i với m&agrave;y, - t&ecirc;n l&iacute;nh sủa. Hắn đưa điếu thuốc đang h&uacute;t cho thằng ngồi b&ecirc;n, một tay ngoắc ng&oacute;n c&aacute;i v&agrave;o ng&oacute;n trỏ th&agrave;nh một v&ograve;ng tr&ograve;n. Ng&oacute;n giữa tay b&ecirc;n kia thọc v&agrave;o v&ograve;ng tr&ograve;n đ&oacute;. Thọc v&agrave;o thọc ra. Thọc v&agrave;o, thọc ra, - tao biết mẹ m&agrave;y đấy, m&agrave;y c&oacute; hiểu kh&ocirc;ng? Tao biết mẹ m&agrave;y h&agrave;ng thật. Tao chơi mẹ m&agrave;y từ ph&iacute;a sau, b&ecirc;n khe nước, chỗ kia k&igrave;a.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Bọn l&iacute;nh cười rộ. Một t&ecirc;n trong bọn k&ecirc;u r&eacute; l&ecirc;n. T&ocirc;i bảo Hassan cứ đi, kệ cứ đi.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- C&aacute;i lều 3 xinh xinh nhỏ x&iacute;u của mẹ m&agrave;y mới m&ugrave;i mẫn l&agrave;m sao!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ecirc;n l&iacute;nh vừa n&oacute;i vừa nhăn nhở cười, bắt tay những t&ecirc;n kh&aacute;c.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">L&aacute;t sau, trong b&oacute;ng tối, sau khi bộ phim bắt đầu chiếu, t&ocirc;i nghe tiếng Hassan ngồi b&ecirc;n t&ocirc;i đang sụt sịt. Nước mắt chảy r&ograve;ng r&ograve;ng tr&ecirc;n đ&ocirc;i m&aacute;. T&ocirc;i ngồi x&iacute;ch lại, dang c&aacute;nh tay &ocirc;m lấy người cậu, k&eacute;o cậu s&aacute;t v&agrave;o t&ocirc;i. Cậu ngả đầu l&ecirc;n vai t&ocirc;i. &quot;Hắn nhầm cậu với ai đấy,&quot; t&ocirc;i th&igrave; th&agrave;o. &quot;Hắn nhầm cậu với ai m&agrave;!&quot;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i nghe n&oacute;i chẳng một ai thực sự ngạc nhi&ecirc;n khi Sanaubar bỏ nh&agrave; đi. Mọi người đều phải nhướn m&agrave;y khi Ali, một người đ&agrave;n &ocirc;ng l&agrave;u l&agrave;u kinh Koran, cưới Sanaubar, người đ&agrave;n b&agrave; mười ch&iacute;n, tr&ocirc;ng trẻ hơn tuổi, một người đ&agrave;n b&agrave; đẹp nhưng tai tiếng, bu&ocirc;ng thả, sống bất chấp nhưng điều &ocirc; nhục. Cũng giống như Ali, b&agrave; ta l&agrave; một t&iacute;n đồ Hồi gi&aacute;o Shi&#39;a v&agrave; thuộc chủng tộc Hazara. B&agrave; cũng l&agrave; em họ, con c&ocirc; con cậu của &ocirc;ng v&agrave; điều đ&oacute; dẫn đến sự chọn lựa tự nhi&ecirc;n cho h&ocirc;n phối. Nhưng ngo&agrave;i sự tương đồng ấy, Ali v&agrave; Sanaubar hầu như chẳng c&oacute; điểm giống nhau n&agrave;o, &iacute;t nhất l&agrave; mọi vẻ bề ngo&agrave;i tương ứng. Trong khi đ&ocirc;i mắt Sanaubar long lanh xanh biếc, bộ mặt ranh ma, nghe đồn đ&atilde; dụ dỗ kh&ocirc;ng biết bao nhi&ecirc;u đ&agrave;n &ocirc;ng rơi v&agrave;o tội lỗi, th&igrave; Ali lại bị liệt bẩm sinh cơ mặt dưới, nguy&ecirc;n nh&acirc;n khiến &ocirc;ng kh&ocirc;ng thể mỉm cười, khiến &ocirc;ng m&atilde;i m&atilde;i c&oacute; bộ mặt nghi&ecirc;m khắc. Nếu thấy được bộ mặt như đ&aacute; tạc của Ali sung sướng hoặc buồn rầu th&igrave; đ&oacute; l&agrave; một điều kỳ lạ, bởi v&igrave; chỉ c&oacute; đ&ocirc;i mắt xếch m&agrave;u n&acirc;u của &ocirc;ng lo&eacute; l&ecirc;n thay cho nụ cười v&agrave; tu&ocirc;n ra nỗi sầu. Người ta bảo đ&ocirc;i mắt l&agrave; cửa sổ của t&acirc;m hồn. Chẳng bao giờ c&oacute; trường hợp n&agrave;o đ&uacute;ng hơn thế với Ali, con người chỉ c&oacute; thể thổ lộ t&acirc;m can m&igrave;nh qua đ&ocirc;i mắt.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i nghe đồn rằng những bước đi kh&ecirc;u gợi v&agrave; c&aacute;i h&ocirc;ng đung đưa của Sanaubar đ&atilde; khiến bao người đ&agrave;n &ocirc;ng phải mơ đến cảnh phản bội vợ con. Nhưng bệnh vi&ecirc;m tuỷ x&aacute;m g&acirc;y bại liệt đ&atilde; để lại cho Ali ch&acirc;n phải vặn vẹo, teo cơ, lớp da v&agrave;ng sạm b&ecirc;n dưới chỉ l&agrave; một lớp cơ mỏng bọc xương. T&ocirc;i nhớ một h&ocirc;m, năm t&ocirc;i t&aacute;m tuổi, Ali đem t&ocirc;i ra phố chợ 4mua một &iacute;t b&aacute;nh naan 5. T&ocirc;i bước ph&iacute;a sau &ocirc;ng, bụm miệng, cố bắt chước bước đi của &ocirc;ng. T&ocirc;i quan s&aacute;t &ocirc;ng ngo&aacute;y chiếc ch&acirc;n khẳng khiu theo một v&ograve;ng cung qu&eacute;t đất. T&ocirc;i quan s&aacute;t to&agrave;n th&acirc;n &ocirc;ng nghi&ecirc;ng một c&aacute;ch kh&oacute; khăn về b&ecirc;n phải mỗi lần &ocirc;ng đặt ch&acirc;n đ&oacute; xuống. Mỗi bước đi như vậy m&agrave; &ocirc;ng kh&ocirc;ng bị lật nh&agrave;o dường như đ&atilde; l&agrave; một điều thần kỳ nho nhỏ. Khi cố bắt chước &ocirc;ng, t&ocirc;i su&yacute;t bị ng&atilde; xuống r&atilde;nh nước. Khiến t&ocirc;i kh&uacute;c kh&iacute;ch cười. Ali quay lại, bắt gặp t&ocirc;i nhại theo &ocirc;ng. &Ocirc;ng kh&ocirc;ng n&oacute;i g&igrave;. Kh&ocirc;ng l&uacute;c ấy. Kh&ocirc;ng bao giờ hết. &Ocirc;ng chỉ tiếp tục bước đi.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Bộ mặt của Ali v&agrave; bước đi của &ocirc;ng l&agrave;m đ&aacute;m trẻ nhỏ trong v&ugrave;ng hoảng sợ. Nhưng điều phiền nhiễu thực sự l&agrave; những đứa lớn hơn. Ch&uacute;ng b&aacute;m theo &ocirc;ng tr&ecirc;n đường phố, v&agrave; nhại theo &ocirc;ng mỗi khi &ocirc;ng c&agrave; nhắc đi qua. C&oacute; đứa c&ograve;n gọi &ocirc;ng l&agrave; Bababu hay Ngo&aacute;o ộp. &quot;&Ecirc;, Bababu, h&ocirc;m nay ăn thịt ai rồi.&quot; Ch&uacute;ng đồng thanh sủa l&ecirc;n v&agrave; cười ph&aacute;. &quot;Đồ Batabu Mũi Tẹt, mi ăn thịt ai rồi?&quot;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ch&uacute;ng gọi &ocirc;ng l&agrave; &quot;đồ mũi tẹt&quot; bởi v&igrave; Ali v&agrave; Hassan mang đặc trưng chủng tộc Hazara gốc M&ocirc;ng Cổ. Đ&atilde; bao năm rồi, tất cả những g&igrave; t&ocirc;i biết về người Hazara l&agrave; họ l&agrave; hậu duệ của người M&ocirc;ng Cổ thời Đế chế Mogul v&agrave; tr&ocirc;ng giống như những người Trung Hoa b&eacute; nhỏ. S&aacute;ch gi&aacute;o khoa trong nh&agrave; trường chỉ nhắc đến tổ ti&ecirc;n họ một c&aacute;ch qua loa. Thế rồi một h&ocirc;m, t&ocirc;i ở trong ph&ograve;ng đọc của Baba, xem qua những s&aacute;ch linh tinh của &ocirc;ng, t&ocirc;i t&igrave;m thấy một trong những cuốn cổ sử của mẹ t&ocirc;i. Cuốn s&aacute;ch do một người Iran c&oacute; t&ecirc;n l&agrave; Khorami viết. T&ocirc;i thổi bụi đi, l&eacute;n đem n&oacute; về giường t&ocirc;i đ&ecirc;m đ&oacute;, v&agrave; sửng sốt thấy cả một chương về lịch sử của người Hazara. Cả một chương ri&ecirc;ng về d&acirc;n tộc của Hassan! Trong chương ấy, t&ocirc;i đọc thấy d&acirc;n tộc t&ocirc;i, tộc Pashtun đ&atilde; ngược đ&atilde;i v&agrave; h&agrave; hiếp người Hazara. Chương s&aacute;ch n&oacute;i người Hazara đ&atilde; gắng sức v&ugrave;ng l&ecirc;n chống lại người Pashtun v&agrave;o thế kỷ mười ch&iacute;n, nhưng người Pashtun đ&atilde; &quot;nghiền n&aacute;t họ bằng bạo lực kh&ocirc;ng thể tả xiết bằng lời.&quot; Cuốn s&aacute;ch n&oacute;i d&acirc;n ch&uacute;ng t&ocirc;i đ&atilde; giết người Hazara, đuổi họ khỏi đất đai của họ, đốt nh&agrave; của họ, b&aacute;n vợ con họ. Cuốn s&aacute;ch n&oacute;i, một phần l&yacute; do khiến người Pashtun &aacute;p bức người Hazara v&igrave; người Pashtun l&agrave; những t&iacute;n đồ hồi gi&aacute;o Sunni, trong khi đ&oacute; người Hazara lại l&agrave; Hồi gi&aacute;o Shi&#39;a. Cuốn s&aacute;ch viết nhiều điều m&agrave; t&ocirc;i kh&ocirc;ng hiểu, những điều m&agrave; c&aacute;c thầy gi&aacute;o t&ocirc;i kh&ocirc;ng n&oacute;i đến. Những điều m&agrave; cả Baba cũng chẳng bao giờ đề cập. N&oacute; cũng n&oacute;i một số điều t&ocirc;i đ&atilde; biết, như mọi người gọi người Hazara l&agrave; bọn ăn thịt chuột, mũi tẹt, lũ lừa ưa nặng. T&ocirc;i đ&atilde; từng nghe thấy mấy thằng nh&atilde;i ở v&ugrave;ng l&acirc;n cận h&eacute;t l&ecirc;n những biệt hiệu ấy với Hassan.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tuần sau đ&oacute;, sau giờ học, t&ocirc;i giơ cuốn s&aacute;ch cho thầy gi&aacute;o t&ocirc;i v&agrave; chỉ v&agrave;o chương n&oacute;i về người Hazara. &Ocirc;ng xem lướt qua v&agrave;i trang, cười khẩy v&agrave; trả lại t&ocirc;i cuốn s&aacute;ch. &quot;Đ&oacute; l&agrave; điều duy nhất d&acirc;n Shi&#39;a l&agrave;m đ&uacute;ng,&quot; &ocirc;ng n&oacute;i tiếp, vừa thu dọn s&aacute;ch vở, &quot;l&agrave;m như những kẻ tuẫn đạo.&quot; &Ocirc;ng nhăn mũi khi n&oacute;i từ Shi&#39;a, như thể n&oacute; l&agrave; một loại bệnh n&agrave;o đ&oacute;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nhưng mặc dầu kế thừa c&ugrave;ng d&ograve;ng m&aacute;u gia đ&igrave;nh v&agrave; chủng tộc với &ocirc;ng, Sanaubar vẫn v&agrave;o h&ugrave;a với lũ trẻ trong v&ugrave;ng nhục mạ Ali. T&ocirc;i được nghe n&oacute;i b&agrave; ta chẳng cần giấu giếm g&igrave; việc b&agrave; ta khinh thị vẻ ngo&agrave;i của &ocirc;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thế n&agrave;y m&agrave; l&agrave; chồng ư? - b&agrave; ta thường cười nhạo. - T&ocirc;i thấy mấy con lừa gi&agrave; l&agrave;m chồng c&ograve;n th&iacute;ch hợp hơn.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Rốt cuộc, phần lớn mọi người đều ngờ cuộc h&ocirc;n nh&acirc;n n&agrave;y vốn l&agrave; một sự d&agrave;n xếp giữa Ali v&agrave; cậu m&igrave;nh, cha của Sanaubar. Họ n&oacute;i Ali đ&atilde; cưới c&ocirc; em họ để gi&uacute;p phục hồi đ&ocirc;i ch&uacute;t danh dự cho t&ecirc;n tuổi bị &ocirc; uế của cậu, cho d&ugrave; Ali, mồ c&ocirc;i từ hồi năm tuổi, đ&acirc;u c&oacute; ch&uacute;t của cải vật chất hay khoản thừa kế n&agrave;o.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ali kh&ocirc;ng bao giờ trả đũa bất kỳ kẻ n&agrave;o h&agrave;nh hạ &ocirc;ng, t&ocirc;i cho rằng một phần v&igrave; &ocirc;ng kh&ocirc;ng bao giờ c&oacute; thể đuổi kịp họ bằng c&aacute;i ch&acirc;n xoắn vặn k&eacute;o l&ecirc; ph&iacute;a sau. Nhưng chủ yếu bởi Ali đ&atilde; miễn nhiễm trước những lời lăng nhục của đ&aacute;m người c&ocirc;ng k&iacute;ch &ocirc;ng. &Ocirc;ng đ&atilde; t&igrave;m thấy niềm vui, thấy thứ thuốc giải độc của m&igrave;nh, l&uacute;c Sanaubar sinh hạ Hassan. Việc đ&oacute; vốn kh&aacute; đơn giản. Kh&ocirc;ng c&oacute; nh&acirc;n vi&ecirc;n sản khoa, kh&ocirc;ng c&oacute; người g&acirc;y m&ecirc;, kh&ocirc;ng c&oacute; những thiết bị kiểm tra th&iacute;ch hợp. Đ&uacute;ng l&agrave; Sanaubar chỉ nằm ngửa ra tr&ecirc;n một tấm đệm trần bẩn thỉu, với Ali v&agrave; một b&agrave; đỡ gi&uacute;p việc sinh nở. Thật ra, Sanaubar cũng chẳng cần gi&uacute;p lắm, bởi v&igrave;, ngay trong khi ra đời, Hassan đ&atilde; giống hệt như bản chất của m&igrave;nh rồi. Cậu kh&ocirc;ng thể l&agrave;m tổn thương bất kỳ ai. Cằn nhằn v&agrave;i c&acirc;u, đẩy mấy c&aacute;i, thế l&agrave; t&ograve;i ra Hassan. T&ograve;i ra v&agrave; cười.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Theo như lời thổ lộ với đ&agrave;y tớ nh&agrave; một người h&agrave;ng x&oacute;m của c&aacute;i b&agrave; đỡ hay bu&ocirc;n chuy&ecirc;n, người đ&atilde; kể lại với bất kỳ ai muốn nghe, th&igrave; Sanaubar liếc mắt nh&igrave;n đứa trẻ trong c&aacute;nh tay Ali, thấy c&aacute;i m&ocirc;i hở, đ&atilde; xổ ra một chuỗi cười cay đắng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Đ&oacute;. - Sanaubar n&oacute;i. - B&acirc;y giờ th&igrave; &ocirc;ng đ&atilde; c&oacute; đứa con ngu xuẩn của ch&iacute;nh &ocirc;ng để n&oacute; cười hộ cho &ocirc;ng rồi đ&oacute;! - B&agrave; ta đ&atilde; kh&ocirc;ng chịu ngay cả bế Hassan, v&agrave; đ&uacute;ng năm ng&agrave;y sau th&igrave; bỏ đi.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Baba thu&ecirc; ch&iacute;nh b&agrave; v&uacute; đ&atilde; từng cho t&ocirc;i b&uacute; để cho Hassan b&uacute;. Ali kể cho ch&uacute;ng t&ocirc;i, b&agrave; v&uacute; l&agrave; một người đ&agrave;n b&agrave; Hazara mắt xanh qu&ecirc; ở Bamiyan, th&agrave;nh phố của những bức tượng Phật khổng lồ. &quot;B&agrave; ấy c&oacute; giọng h&aacute;t ru mới ngọt ng&agrave;o l&agrave;m sao chứ!&quot; &Ocirc;ng thường n&oacute;i với ch&uacute;ng t&ocirc;i như thế.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">B&agrave; ấy h&aacute;t thế n&agrave;o, Hassan v&agrave; t&ocirc;i lu&ocirc;n hỏi, dẫu hai đứa đ&atilde; biết thừa - thật ra v&igrave; Ali đ&atilde; kể lại cho ch&uacute;ng t&ocirc;i kh&ocirc;ng biết bao lần. Ch&uacute;ng t&ocirc;i chỉ muốn nghe Ali h&aacute;t.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&Ocirc;ng hắng giọng v&agrave; bắt đầu:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tr&ecirc;n một ngọn n&uacute;i cao t&ocirc;i đứng,<br />\r\n&Ocirc;i Ali, Sư tử của Thượng đế, Vua của Con người,<br />\r\nH&atilde;y mang lại niềm vui cho những tr&aacute;i tim<br />\r\nsầu n&atilde;o của ch&uacute;ng t&ocirc;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">H&aacute;t xong, &ocirc;ng thường nhắc nhở ch&uacute;ng t&ocirc;i rằng c&oacute; một t&igrave;nh anh em giữa những con người được b&uacute; c&ugrave;ng một bầu v&uacute; mẹ, t&igrave;nh m&aacute;u mủ m&agrave; ngay cả thời gian cũng kh&ocirc;ng thể ph&aacute; vỡ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Hassan v&agrave; t&ocirc;i b&uacute; c&ugrave;ng một bầu v&uacute;. Ch&uacute;ng t&ocirc;i chập chững đi những bước đầu ti&ecirc;n tr&ecirc;n c&ugrave;ng một b&atilde;i cỏ trong c&ugrave;ng một chiếc s&acirc;n. V&agrave; dưới c&ugrave;ng một m&aacute;i nh&agrave;, ch&uacute;ng t&ocirc;i bập bẹ những tiếng n&oacute;i đầu ti&ecirc;n:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Của t&ocirc;i l&agrave; Baba.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Của cậu ấy l&agrave; Amir. T&ecirc;n t&ocirc;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">B&acirc;y giờ nh&igrave;n lại, t&ocirc;i nghĩ khởi nguồn của những g&igrave; đ&atilde; xảy ra trong m&ugrave;a đ&ocirc;ng năm 1975 - v&agrave; tất cả những g&igrave; tiếp theo sau đ&oacute; - đ&atilde; nằm sẵn trong những tiếng bập bẹ đầu ti&ecirc;n ấy.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>CHƯƠNG III</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tương truyền cha t&ocirc;i, một bận, bằng hai tay kh&ocirc;ng đ&atilde; vật nhau với một con gấu đen ở Baluchistan. Nếu chuyện kể về một ai kh&aacute;c, hẳn n&oacute; sẽ bị gạt bỏ coi như chuyện Laaf 1, rằng người Afghan c&oacute; khuynh hướng thổi phồng mọi chuyện - buồn thay, hầu như đ&oacute; lại l&agrave; nỗi đau của đất nước. Nếu ai đ&oacute; khoe khoang con m&igrave;nh l&agrave; b&aacute;c sĩ, may ra đứa trẻ từng c&oacute; lần vượt qua một cuộc trắc khảo sinh học ở trường trung học. Nhưng lại chẳng một ai mảy may nghi ngờ t&iacute;nh ch&iacute;nh x&aacute;c của bất kỳ chuyện n&agrave;o về Baba. Va nếu họ nghi ngờ, được th&ocirc;i, Baba đ&atilde; c&oacute; ba vết sẹo s&oacute;ng h&agrave;ng xuống th&agrave;nh một đường x&ugrave; x&igrave; ở lưng &ocirc;ng. T&ocirc;i đ&atilde; bao lần tưởng tượng ra những cuộc đấu vật của Baba, cả mơ thấy nữa. V&agrave; trong những giấc mơ ấy, t&ocirc;i chẳng bao giờ ph&acirc;n biệt r&otilde; Baba v&agrave; con gấu.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ch&iacute;nh ch&uacute; Rahim Khan l&agrave; người đầu ti&ecirc;n gọi Baba bằng c&aacute;i t&ecirc;n sau n&agrave;y trở th&agrave;nh biệt danh nổi tiếng của &ocirc;ng, Toophan agha, hoặc &quot;Ng&agrave;i B&atilde;o tố&quot;. Đ&oacute; l&agrave; c&aacute;i biệt danh kh&aacute; th&iacute;ch hợp. Cha t&ocirc;i l&agrave; một sức mạnh từ trong bản chất, một mẫu người Pashtun cao to sừng sững với bộ r&acirc;u rậm, mớ t&oacute;c quăn m&agrave;u n&acirc;u cắt ngắn ph&oacute;ng t&uacute;ng kh&ocirc;ng theo trật tự n&agrave;o, như ch&iacute;nh bản th&acirc;n &ocirc;ng, đ&ocirc;i b&agrave;n tay tưởng c&oacute; thể nhổ bật rễ cả một c&acirc;y liễu, v&agrave; c&aacute;i nh&igrave;n trừng trừng giận dữ như muốn &quot;bắt con quỷ quỳ xuống xin tha tội,&quot; như ch&uacute; Rahim vẫn thường n&oacute;i. Tại những bữa tiệc, khi c&aacute;i th&acirc;n h&igrave;nh một m&eacute;t t&aacute;m mươi lăm của &ocirc;ng th&igrave;nh thịch bước v&agrave;o ph&ograve;ng ăn, mọi con mắt d&otilde;i theo &ocirc;ng như hoa hướng dương hướng về mặt trời.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Kh&ocirc;ng thể n&agrave;o m&agrave; lờ &ocirc;ng đi được, kể cả khi &ocirc;ng ngủ. T&ocirc;i thường phải n&uacute;t đầy b&ocirc;ng v&agrave;o hai tai, k&eacute;o chăn tr&ugrave;m k&iacute;n đầu m&agrave; vẫn nghe thấy tiếng Baba ng&aacute;y - to đến nỗi giống như tiếng gầm của động cơ xe tải - xuy&ecirc;n cả qua tường. Từ ph&ograve;ng ngủ của t&ocirc;i phải qua đại sảnh mới đến ph&ograve;ng ngủ của cha t&ocirc;i. Mẹ t&ocirc;i l&agrave;m thế n&agrave;o để ngủ được c&ugrave;ng ph&ograve;ng với &ocirc;ng quả l&agrave; một điều b&iacute; ẩn đối với t&ocirc;i. C&oacute; cả một danh s&aacute;ch những chuyện t&ocirc;i muốn hỏi mẹ, nếu l&uacute;c n&agrave;o đ&oacute; t&ocirc;i gặp được mẹ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">V&agrave;o khoảng mấy năm cuối của thập kỷ s&aacute;u mươi, l&uacute;c t&ocirc;i chừng năm s&aacute;u tuổi, Baba quyết định x&acirc;y một trại mồ c&ocirc;i. T&ocirc;i được nghe chuyện qua ch&uacute; Rahim Khan. Ch&uacute; kể cho t&ocirc;i, Baba đ&atilde; tự m&igrave;nh vẽ những bản thiết kế, mặc d&ugrave; thật ra &ocirc;ng chẳng c&oacute; một ch&uacute;t kinh nghiệm n&agrave;o về kiến tr&uacute;c cả. Những người ho&agrave;i nghi giục &ocirc;ng ngừng ngay tr&ograve; đi&ecirc;n rồ v&agrave; phải thu&ecirc; một kiến tr&uacute;c sư. Tất nhi&ecirc;n Baba kh&ocirc;ng chịu, ai nấy đều lắc đầu ph&aacute;t ng&aacute;n về những cung c&aacute;ch ương bướng của &ocirc;ng. Thế rồi Baba th&agrave;nh c&ocirc;ng v&agrave; mọi người lại lắc đầu cho&aacute;ng v&aacute;ng trước những kiểu c&aacute;ch chiến thắng của &ocirc;ng. Baba trả tiền t&uacute;i của m&igrave;nh cho việc x&acirc;y dựng trại mồ c&ocirc;i hai tầng n&agrave;y, ngay ngo&agrave;i dải đất hẹp của Fadeh Maywand ở ph&iacute;a Nam s&ocirc;ng Kabul. Ch&uacute; Rahim bảo t&ocirc;i ri&ecirc;ng Baba t&agrave;i trợ ho&agrave;n to&agrave;n cho dự &aacute;n, trả c&ocirc;ng cho c&aacute;c kỹ sư, thợ điện, thợ nước v&agrave; tất cả nh&acirc;n c&ocirc;ng, kh&ocirc;ng đếm xỉa đến đ&aacute;m giới chức ở th&agrave;nh phố, loại &quot;ria m&eacute;p cần phải b&ocirc;i dầu.&quot;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Phải mất ba năm để x&acirc;y trại mồ c&ocirc;i. Đến l&uacute;c đ&oacute; t&ocirc;i đ&atilde; t&aacute;m tuổi. T&ocirc;i nhớ h&ocirc;m trước ng&agrave;y kh&aacute;nh th&agrave;nh trại mồ c&ocirc;i, Baba mang t&ocirc;i đến hồ Ghargha, c&aacute;ch Kabul v&agrave;i dặm về ph&iacute;a Bắc. &Ocirc;ng bảo t&ocirc;i t&igrave;m Hassan nữa để c&ugrave;ng đi, nhưng t&ocirc;i n&oacute;i dối &ocirc;ng rằng Hassan bị ti&ecirc;u chảy. T&ocirc;i muốn Baba ho&agrave;n to&agrave;n l&agrave; của m&igrave;nh. Th&ecirc;m nữa, một bận tại hồ Ghargha, Hassan v&agrave; t&ocirc;i n&eacute;m thia lia đ&aacute;, Hassan lia đ&aacute; nảy được t&aacute;m lần. T&ocirc;i nhiều nhất chỉ được năm. Baba ở đ&oacute; theo d&otilde;i, v&agrave; &ocirc;ng vỗ nhẹ v&agrave;o lưng Hassan. Rồi c&ograve;n đưa c&aacute;nh tay qu&agrave;ng vai cậu ấy nữa.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ch&uacute;ng t&ocirc;i ngồi v&agrave;o b&agrave;n ăn picnic b&ecirc;n hồ, chỉ c&oacute; mỗi Baba v&agrave; t&ocirc;i ăn trứng luộc với b&aacute;nh nh&acirc;n thịt Kofia - một loại b&aacute;nh naan cuốn thịt vi&ecirc;n v&agrave; dưa g&oacute;p. Nước hồ xanh biếc v&agrave; &aacute;nh nắng lấp l&aacute;nh tr&ecirc;n mặt nước s&aacute;ng như gương. V&agrave;o những ng&agrave;y thứ S&aacute;u bờ hồ nhộn nhịp c&aacute;c gia đ&igrave;nh đang mong c&oacute; một ng&agrave;y phơi nắng. Nhưng h&ocirc;m ấy l&agrave; giữa tuần, chỉ c&oacute; mỗi Baba v&agrave; t&ocirc;i, ch&uacute;ng t&ocirc;i v&agrave; hai người kh&aacute;ch du lịch t&oacute;c d&agrave;i, r&acirc;u rậm - &quot;kiểu hippy&quot;, t&ocirc;i nghe thấy họ được gọi thế. Họ ngồi tr&ecirc;n bến, ch&acirc;n khuấy nước, cần c&acirc;u trong tay. T&ocirc;i hỏi Baba, tại sao họ lại để t&oacute;c d&agrave;i, nhưng Baba chỉ c&agrave;u nh&agrave;u kh&ocirc;ng trả lời. &Ocirc;ng đang chuẩn bị b&agrave;i diễn văn cho ng&agrave;y h&ocirc;m sau, đang lật mở một mớ rối tung những trang viết tay, lấy b&uacute;t ch&igrave; ghi ch&uacute; chỗ n&agrave;y chỗ kh&aacute;c. T&ocirc;i cắn quả trứng v&agrave; hỏi Baba c&oacute; đ&uacute;ng như một đứa bạn c&ugrave;ng trường bảo t&ocirc;i, rằng nếu ăn phải một miếng vỏ trứng th&igrave; thế n&agrave;o cũng đ&aacute;i ra n&oacute;. Baba lại c&agrave;u nh&agrave;u.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i lại cắn một miếng b&aacute;nh cuộn. Một trong hai người kh&aacute;ch du lịch t&oacute;c v&agrave;ng cười v&agrave; ph&aacute;t v&agrave;o lưng người kia. Xa xa b&ecirc;n kia hồ, một chiếc xe tải &igrave; ạch chạy quanh g&oacute;c lượn của đồi. &Aacute;nh nắng nhấp nh&aacute;y trong chiếc gương b&ecirc;n cửa xe.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Con nghĩ con bị Saratan 2, - t&ocirc;i n&oacute;i. Đầy bụng. Baba ngẩng đầu l&ecirc;n khỏi nhưng trang giấy đang sột soạt trong gi&oacute; nhẹ. Bảo t&ocirc;i c&oacute; thể tự m&igrave;nh lấy soda uống. Tất cả những g&igrave; t&ocirc;i phải l&agrave;m l&agrave; t&igrave;m trong th&ugrave;ng xe.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">H&ocirc;m sau, b&ecirc;n ngo&agrave;i trại mồ c&ocirc;i, hết cả ghế ngồi cho mọi người. Số đ&ocirc;ng phải đứng để xem lễ khai trương. Đ&oacute; l&agrave; một ng&agrave;y gi&ocirc;ng gi&oacute;, v&agrave; t&ocirc;i ngồi ph&iacute;a sau Baba, tr&ecirc;n một diễn đ&agrave;i nhỏ ngay ph&iacute;a ngo&agrave;i cửa ch&iacute;nh của c&ocirc;ng tr&igrave;nh mới. Baba mặc một bộ lễ phục m&agrave;u xanh chai v&agrave; đội mũ l&ocirc;ng cừu. Giữa chừng b&agrave;i diễn văn, gi&oacute; thổi lật mũ của &ocirc;ng v&agrave; mọi người cười rộ. &Ocirc;ng ra hiệu cho t&ocirc;i cầm mũ cho &ocirc;ng, t&ocirc;i lấy thế l&agrave;m sung sướng bởi v&igrave; như vậy mọi người sẽ biết &ocirc;ng l&agrave; cha t&ocirc;i, Baba của t&ocirc;i. &Ocirc;ng quay lại micr&ocirc; v&agrave; n&oacute;i, &ocirc;ng mong to&agrave; nh&agrave; sẽ vững chắc hơn chiếc mũ của &ocirc;ng, v&agrave; tất cả lại cười. Khi Baba kết th&uacute;c b&agrave;i diễn văn, mọi người đứng cả l&ecirc;n hoan h&ocirc;. Họ vỗ tay một hồi l&acirc;u. Sau đ&oacute; mọi người bắt tay &ocirc;ng, v&agrave;i người trong bọn họ xoa đầu t&ocirc;i v&agrave; cũng bắt tay t&ocirc;i nữa. T&ocirc;i tự h&agrave;o về Baba, về ch&uacute;ng t&ocirc;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nhưng bất kể những th&agrave;nh tựu của Baba, người ta vẫn lu&ocirc;n nghi ngờ &ocirc;ng. Họ bảo việc &ocirc;ng theo đuổi kinh doanh l&agrave; kh&ocirc;ng th&iacute;ch hợp với d&ograve;ng m&aacute;u của m&igrave;nh. V&agrave; &ocirc;ng n&ecirc;n nghi&ecirc;n cứu luật như cha của &ocirc;ng. V&igrave; thế Baba đ&atilde; chứng tỏ cho họ biết tất cả đều sai lầm bằng việc kh&ocirc;ng chỉ theo đuổi chuyện kinh doanh của ri&ecirc;ng &ocirc;ng m&agrave; c&ograve;n trở th&agrave;nh một trong những thương nh&acirc;n gi&agrave;u c&oacute; nhất ở Kabul. Baba v&agrave; ch&uacute; Rahim Khan g&acirc;y dựng một h&atilde;ng kinh doanh thảm xuất khẩu th&agrave;nh c&ocirc;ng mỹ m&atilde;n, hai hiệu t&acirc;n dược, v&agrave; một nh&agrave; h&agrave;ng ăn.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Khi mọi người n&oacute;i kh&aacute;y Baba sẽ kh&ocirc;ng lấy được vợ ra hồn - v&igrave; rốt cuộc &ocirc;ng kh&ocirc;ng thuộc d&ograve;ng m&aacute;u ho&agrave;ng tộc - &ocirc;ng lại cưới mẹ t&ocirc;i, Sofia Akrami, một phụ nữ học vấn cao được mọi người coi như một trong nhưng phu nh&acirc;n t&ocirc;n k&iacute;nh nhất, đẹp nhất v&agrave; đức hạnh nhất. V&agrave; kh&ocirc;ng chỉ dạy văn học cổ điển Karsi tại trường đại học, b&agrave; c&ograve;n l&agrave; hậu duệ của một gia đ&igrave;nh ho&agrave;ng tộc, sự kiện m&agrave; cha t&ocirc;i thường bỡn cợt n&eacute;m trả v&agrave;o những bộ mặt ho&agrave;i nghi bằng c&aacute;ch gọi &quot;nữ quận ch&uacute;a của t&ocirc;i&quot;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">C&ugrave;ng với t&ocirc;i, hiển nhi&ecirc;n l&agrave; một ngoại lệ, cha t&ocirc;i x&acirc;y đắp thế giới quanh &ocirc;ng theo sở th&iacute;ch của &ocirc;ng. Cố nhi&ecirc;n, Baba nh&igrave;n thế giới chỉ c&oacute; trắng v&agrave; đen. &Ocirc;ng lại phải tự m&igrave;nh định đoạt c&aacute;i g&igrave; trắng c&aacute;i g&igrave; đen. Bạn kh&ocirc;ng thể y&ecirc;u một con người sống theo lối đ&oacute; m&agrave; kh&ocirc;ng cảm thấy sợ h&atilde;i. C&oacute; thể c&ograve;n hơi gh&eacute;t nữa.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Khi t&ocirc;i học lớp năm, c&oacute; một gi&aacute;o sư dạy ch&uacute;ng t&ocirc;i về đạo Hồi. T&ecirc;n &ocirc;ng l&agrave; Fatiullah Khan, một người thấp b&eacute;o c&oacute; bộ mặt đầy sẹo trứng c&aacute;, giọng kh&agrave;n kh&agrave;n. &Ocirc;ng giảng cho ch&uacute;ng t&ocirc;i những lợi &iacute;ch của thuế cứu tế v&agrave; bổn phận h&agrave;nh hương đến th&aacute;nh địa Mecca. &Ocirc;ng dạy ch&uacute;ng t&ocirc;i những sự v&ocirc; c&ugrave;ng phức tạp của năm bổn kinh cầu nguyện Namaz 3, v&agrave; bắt ch&uacute;ng t&ocirc;i phải thuộc l&ograve;ng những vần thơ của kinh Koran - dẫu &ocirc;ng kh&ocirc;ng bao giờ dịch những từ ra cho ch&uacute;ng t&ocirc;i, &ocirc;ng vẫn nhấn mạnh, đ&ocirc;i khi với sự trợ gi&uacute;p của một c&agrave;nh liễu đ&atilde; b&oacute;c vỏ, rằng phải ph&aacute;t &acirc;m được tiếng Ẳ rập cho thật đ&uacute;ng th&igrave; Thượng đế mới nghe r&otilde; được ch&uacute;ng t&ocirc;i. Một h&ocirc;m &ocirc;ng bảo ch&uacute;ng t&ocirc;i rằng đạo Hồi coi uống rượu l&agrave; một tội gh&ecirc; gớm. Những kẻ n&agrave;o uống rượu sẽ phải trả lời về tội của họ v&agrave;o ng&agrave;y Qiyamat, Ng&agrave;y Ph&aacute;n xử. Thời đ&oacute;, uống rượu hết sức phổ biến ở Kabul. Chẳng một ai c&ocirc;ng khai chỉ tr&iacute;ch anh về điều đ&oacute;, nhưng những người Afghan phải uống th&igrave; cứ uống l&eacute;n uống l&uacute;t, chẳng nể nang g&igrave; cả. Mọi người mua rượu Scotch như mua &quot;thuốc chữa bệnh&quot; trong t&uacute;i giấy n&acirc;u từ những &quot;cửa h&agrave;ng thuốc&quot; đặc biệt. Họ thường ra về với chiếc t&uacute;i giấu cho thật k&iacute;n, đ&ocirc;i khi thu h&uacute;t những c&aacute;i nh&igrave;n vừa l&eacute;n l&uacute;t vừa kh&ocirc;ng bằng l&ograve;ng của những người đ&atilde; biết tai tiếng của cửa h&agrave;ng về chuyện l&agrave;m ăn như thế.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ch&uacute;ng t&ocirc;i đang ở tr&ecirc;n g&aacute;c, trong ph&ograve;ng đọc của Baba, cũng l&agrave; ph&ograve;ng h&uacute;t thuốc, khi t&ocirc;i kể cho &ocirc;ng những g&igrave; gi&aacute;o sĩ Fatiullah Khan đ&atilde; dạy ch&uacute;ng t&ocirc;i ở lớp. Baba tự r&oacute;t cho m&igrave;nh một ly whisky từ quầy rượu x&acirc;y ở g&oacute;c ph&ograve;ng. &Ocirc;ng nghe, gật đầu, nhấp một nhắp rượu. Rồi &ocirc;ng ngồi v&agrave;o chiếc ghế sofa đệm da, đặt ly rượu xuống, bế t&ocirc;i l&ecirc;n đ&ugrave;i &ocirc;ng. T&ocirc;i cảm thấy như đang ngồi tr&ecirc;n hai th&acirc;n c&acirc;y. &Ocirc;ng h&iacute;t một hơi d&agrave;i v&agrave; thở ra đằng mũi, kh&ocirc;ng kh&iacute; r&iacute;t l&ecirc;n qua ria m&eacute;p &ocirc;ng tựa như kh&ocirc;ng dứt. Trong nỗi sợ điếng người, t&ocirc;i ph&acirc;n v&acirc;n kh&ocirc;ng biết c&oacute; muốn &ocirc;m gh&igrave; lấy &ocirc;ng hay tụt ngay ra khỏi đ&ugrave;i &ocirc;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bố thấy con bị hoang mang về những g&igrave; con đang học ở trường, theo lối gi&aacute;o dục hiện thời, - &ocirc;ng n&oacute;i bằng một giọng nặng trịch.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Nhưng nếu những g&igrave; &ocirc;ng ta n&oacute;i đều đ&uacute;ng, ho&aacute; ra Baba l&agrave; kẻ mắc tội?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Hừm! - Baba nghiến một miếng nước đ&aacute; giữa hai h&agrave;m răng. - Con c&oacute; biết bố con nghĩ thế n&agrave;o về tội lỗi kh&ocirc;ng?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- C&oacute; ạ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Vậy bố n&oacute;i cho con nghe, - Baba tiếp - nhưng trước hết phải hiểu điều n&agrave;y, v&agrave; hiện ngay b&acirc;y giờ, Amir ạ. Con sẽ chẳng học được điều g&igrave; ra hồn từ những l&atilde;o r&acirc;u d&agrave;i ngu xuẩn ấy.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- &Yacute; bố n&oacute;i gi&aacute;o sĩ Fatiullah Khan?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Baba ra hiệu bằng ly rượu. Nước đ&aacute; k&ecirc;u lanh canh.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bố muốn n&oacute;i tất cả bọn họ. Đ&aacute;i v&agrave;o r&acirc;u tất cả c&aacute;i lũ khỉ l&ecirc;n mặt đạo đức ấy.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i bật cười kh&uacute;c kh&iacute;ch. H&igrave;nh ảnh Baba đ&aacute;i l&ecirc;n r&acirc;u của bất kỳ con khỉ n&agrave;o, l&ecirc;n mặt đạo đức hoặc kh&ocirc;ng phải thế, thật qu&aacute; đ&atilde;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Họ chẳng l&agrave;m được tr&ograve; g&igrave; ngo&agrave;i việc lần tr&agrave;ng hạt v&agrave; đọc một cuốn s&aacute;ch viết theo một lối m&agrave; ch&iacute;nh họ cũng kh&ocirc;ng hiểu, - &ocirc;ng nhấp một nhắp nữa. - Cầu Thượng đế ph&ugrave; hộ cho ch&uacute;ng ta để đừng bao giờ Afghanistan rơi v&agrave;o tay họ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Nhưng gi&aacute;o sĩ Fatiullah Khan c&oacute; vẻ tốt m&agrave;, - t&ocirc;i cố n&oacute;i xen v&agrave;o giữa l&uacute;c đang bật l&ecirc;n cười r&uacute;c r&iacute;ch.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Th&agrave;nh C&aacute;t Tư H&atilde;n cũng c&oacute; vẻ như vậy, - Baba n&oacute;i. - Nhưng th&ocirc;i xếp lại chuyện ấy. Con hỏi bố về tội lỗi v&agrave; bố muốn n&oacute;i cho con nghe. Con đang nghe đấy chứ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- V&acirc;ng ạ, - t&ocirc;i n&oacute;i, m&iacute;m m&ocirc;i lại. Nhưng một tiếng khụt khịt vẫn cứ bật ra khỏi mũi t&ocirc;i khiến t&ocirc;i lại kh&uacute;c kh&iacute;ch cười.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bố muốn n&oacute;i với con như một người đ&agrave;n &ocirc;ng n&oacute;i với một người đ&agrave;n &ocirc;ng. Con c&oacute; thể xử sự như thế được một lần kh&ocirc;ng?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- V&acirc;ng, thưa Baba jan 4, - t&ocirc;i ấp &uacute;ng ngạc nhi&ecirc;n, kh&ocirc;ng phải lần đầu chỉ bằng mấy lời m&agrave; Baba lại c&oacute; thể khiến t&ocirc;i thấy nh&oacute;i l&ecirc;n trong l&ograve;ng. Ch&uacute;ng t&ocirc;i đ&atilde; c&oacute; được những ph&uacute;t gi&acirc;y tốt đẹp ngắn ngủi - chẳng mấy khi Baba n&oacute;i với t&ocirc;i m&agrave; chỉ c&oacute; mỗi m&igrave;nh t&ocirc;i ngồi tr&ecirc;n đ&ugrave;i &ocirc;ng như thế - v&agrave; t&ocirc;i đ&uacute;ng l&agrave; một thằng ngốc mới để ph&iacute; ho&agrave;i điều đ&oacute;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Tốt lắm, - Baba n&oacute;i nhưng mắt &ocirc;ng lạ l&ugrave;ng. - B&acirc;y giờ, kh&ocirc;ng cần biết &ocirc;ng thầy gi&aacute;o sĩ ấy dạy g&igrave;, chỉ biết c&oacute; duy nhất một tội, một tội th&ocirc;i. Đ&oacute; l&agrave; tội ăn cắp. Mọi tội kh&aacute;c đều l&agrave; biến th&aacute;i của tội ăn cắp. Con c&oacute; hiểu kh&ocirc;ng?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Kh&ocirc;ng, thưa Baba jan, - T&ocirc;i n&oacute;i, trong l&ograve;ng thất vọng những mong m&igrave;nh hiểu. T&ocirc;i đ&acirc;u muốn l&agrave;m &ocirc;ng thất vọng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Baba thốt ra một tiếng thở d&agrave;i sốt ruột. Như thế cũng l&agrave;m t&ocirc;i day dứt, bởi &ocirc;ng kh&ocirc;ng phải con người n&oacute;ng nảy. T&ocirc;i vẫn nhớ tất cả những lần tối rồi m&agrave; &ocirc;ng vẫn chưa về nh&agrave;, tất cả những lần t&ocirc;i ăn tối một m&igrave;nh. T&ocirc;i thường hỏi &ocirc;ng Ali rằng Baba đ&acirc;u, khi n&agrave;o th&igrave; cha t&ocirc;i trở về nh&agrave;, mặc dầu t&ocirc;i thừa biết &ocirc;ng ở c&ocirc;ng trường x&acirc;y dựng để tr&ocirc;ng nom chỗ n&agrave;y, gi&aacute;m s&aacute;t chỗ kia. Như thế kh&ocirc;ng cần ki&ecirc;n nhẫn sao? T&ocirc;i đ&acirc;m ra căm gh&eacute;t tất cả những đứa trẻ m&agrave; &ocirc;ng đang x&acirc;y trại mồ c&ocirc;i cho. Đ&ocirc;i khi t&ocirc;i mong ch&uacute;ng chết đi theo cha mẹ ch&uacute;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Khi con giết một người, con ăn cắp một cuộc đời, - Baba n&oacute;i. - Con ăn cắp quyền l&agrave;m vợ của một người đ&agrave;n b&agrave;, cướp cha của lũ trẻ. Khi con n&oacute;i dối l&agrave; con ăn cắp quyền của ai đ&oacute; được biết sự thật. Khi con lừa bịp, con ăn cắp quyền được ngay thẳng. Con c&oacute; hiểu kh&ocirc;ng?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i hiểu. Khi Baba s&aacute;u tuổi, giữa đ&ecirc;m khuya một t&ecirc;n trộm đột nhập v&agrave;o nh&agrave; &ocirc;ng t&ocirc;i. &Ocirc;ng t&ocirc;i, vị thẩm ph&aacute;n đ&aacute;ng k&iacute;nh, đương đầu với hắn, nhưng t&ecirc;n trộm đ&acirc;m &ocirc;ng t&ocirc;i v&agrave;o họng giết &ocirc;ng chết ngay, cướp mất cha của Baba. D&acirc;n thị x&atilde; bắt được t&ecirc;n s&aacute;t nh&acirc;n ngay trước buổi trưa h&ocirc;m sau. Ho&aacute; ra l&agrave; mộ l&ecirc;n du thủ du thực từ v&ugrave;ng Kunduz. Người ta treo cổ hắn l&ecirc;n c&agrave;nh một c&acirc;y sồi, trước lễ cầu nguyện ban chiều hai tiếng. Ch&iacute;nh ch&uacute; Rahim Khan chứ kh&ocirc;ng phải Baba kể cho t&ocirc;i chuyện đ&oacute;. T&ocirc;i lu&ocirc;n được biết mọi điều về Baba từ những người kh&aacute;c.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Kh&ocirc;ng c&oacute; h&agrave;nh động n&agrave;o xấu xa hơn trộm cắp, Amir ạ, - Baba n&oacute;i. - Một người chiếm đoạt những g&igrave; kh&ocirc;ng phải của m&igrave;nh, d&ugrave; đ&oacute; l&agrave; một cuộc đời hay một ổ b&aacute;nh naan... Ta nhổ v&agrave;o loại người ấy. V&agrave; Thượng đế ph&ugrave; hộ cho hắn để đừng bao giờ hắn gặp phải ta. Con c&oacute; hiểu kh&ocirc;ng?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i thấy c&aacute;i &yacute; của Baba chỉ tr&iacute;ch kịch liệt t&ecirc;n trộm cắp vừa hay ho vừa l&agrave;m t&ocirc;i sợ ph&aacute;t khiếp:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- V&acirc;ng, thưa Baba.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Nếu như c&oacute; một vị Thượng đế ở đ&acirc;u đ&oacute;, th&igrave; ta chỉ mong &ocirc;ng ấy c&oacute; nhiều việc quan trọng hơn l&agrave; để &yacute; đến ta uống rượu Scotch v&agrave; ăn thịt lợn. N&agrave;o, b&acirc;y giờ tụt xuống. N&oacute;i chuyện m&atilde;i về tội lỗi l&agrave;m ta lại kh&aacute;t rồi.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i nh&igrave;n &ocirc;ng r&oacute;t đầy ly rượu v&agrave; ngạc nhi&ecirc;n, kh&ocirc;ng hiểu c&ograve;n l&acirc;u lắm kh&ocirc;ng, cho tới khi ch&uacute;ng t&ocirc;i lại chuyện tr&ograve; như vừa rồi. Bởi v&igrave; sự thật l&agrave;, t&ocirc;i lu&ocirc;n cảm thấy như Baba hơi gh&eacute;t t&ocirc;i. V&agrave; tại sao lại kh&ocirc;ng nhỉ? Rốt cuộc, chẳng phải t&ocirc;i đ&atilde; giết chết người vợ y&ecirc;u dấu của &ocirc;ng, b&agrave; quận ch&uacute;a xinh đẹp của &ocirc;ng hay sao? Điều tối thiểu m&agrave; t&ocirc;i c&oacute; thể l&agrave;m, l&agrave; phải cư xử sao cho đ&uacute;ng ph&eacute;p để trở n&ecirc;n giống &ocirc;ng hơn một ch&uacute;t. Nhưng t&ocirc;i đ&atilde; kh&ocirc;ng trở th&agrave;nh giống &ocirc;ng. Kh&ocirc;ng ch&uacute;t n&agrave;o.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&deg; &deg; &deg;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ở trường, ch&uacute;ng t&ocirc;i thường chơi tr&ograve; chơi Sherjangi, hay &quot;Đấu thơ&quot;. Thầy gi&aacute;o dạy m&ocirc;n văn học Farsi điều khiển cuộc chơi kh&aacute;i như sau: Anh đọc l&ecirc;n một c&acirc;u từ một b&agrave;i thơ v&agrave; đối thủ của anh c&oacute; s&aacute;u mươi gi&acirc;y để đ&aacute;p lại bằng một c&acirc;u thơ kh&aacute;c khởi đầu bằng chữ cuối của c&acirc;u kia. Mọi người ở lớp đều muốn t&ocirc;i trong đội chơi của họ, bởi v&igrave; v&agrave;o l&uacute;c mười một tuổi, t&ocirc;i đ&atilde; c&oacute; thể đọc h&agrave;ng t&aacute; c&aacute;c c&acirc;u thơ từ Khayy&aacute;m, H&atilde;fez hoặc Masnawi nổi tiếng của Rumi 5. Một bận, t&ocirc;i chấp cả lớp v&agrave; t&ocirc;i thắng. Rất khuya đ&ecirc;m đ&oacute;, t&ocirc;i kể lại chuyện ấy với Baba, nhưng &ocirc;ng chỉ gật đầu lẩm bẩm &quot;Tốt&quot;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">V&igrave; thế m&agrave; t&ocirc;i thường t&igrave;m c&aacute;ch tho&aacute;t khỏi sự xa c&aacute;ch của cha, nhờ v&agrave;o đống s&aacute;ch của người mẹ đ&atilde; khuất của t&ocirc;i. Tất nhi&ecirc;n, cả Hassan nữa. T&ocirc;i đọc mọi thứ của Rumi, H&atilde;fez, Saadi, Victor Hugo, Jules Verne, Mark Twain, Ian Fleming. Khi đọc hết đống s&aacute;ch của mẹ - kh&ocirc;ng kể những cuốn s&aacute;ch lịch sử buồn ch&aacute;n, t&ocirc;i chẳng bao giờ để &yacute; tới loại ấy, chỉ đọc những tiểu thuyết, những sử thi, - t&ocirc;i bắt đầu ti&ecirc;u tiền bố cho v&agrave;o việc mua s&aacute;ch. Mỗi tuần t&ocirc;i mua một cuốn ở hiệu s&aacute;ch gần C&ocirc;ng vi&ecirc;n Điện ảnh, v&agrave; cất giữ trong một th&ugrave;ng c&aacute;c t&ocirc;ng, khi đ&atilde; hết chỗ để tr&ecirc;n kệ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Đương nhi&ecirc;n, cưới một nh&agrave; thơ l&agrave; một chuyện, l&agrave;m cha một đứa con trai th&iacute;ch v&ugrave;i đầu v&agrave;o s&aacute;ch thơ hơn đi săn... ồ, đ&oacute; lại l&agrave; điều m&agrave; Baba kh&ocirc;ng nhắm tới, t&ocirc;i cho l&agrave; như vậy. Đ&agrave;n &ocirc;ng đ&iacute;ch thực kh&ocirc;ng đọc thơ - v&agrave; Thượng đế cấm họ kh&ocirc;ng bao giờ được l&agrave;m thơ! Những người đ&agrave;n &ocirc;ng đ&iacute;ch thực - những ch&agrave;ng trai đ&iacute;ch thực nữa - chơi b&oacute;ng đ&aacute; như Baba đ&atilde; từng chơi hồi c&ograve;n trẻ. Giờ đ&acirc;y, b&oacute;ng đ&aacute; vẫn l&agrave; điều g&igrave; đ&oacute; khiến &ocirc;ng say m&ecirc;. Năm 1970, Baba cho ngừng việc x&acirc;y dựng trại mồ c&ocirc;i, v&agrave; bay đến Tehran một th&aacute;ng để xem b&oacute;ng đ&aacute; thế giới tr&ecirc;n v&ocirc; tuyến truyền h&igrave;nh, v&igrave; thời đ&oacute; Afghanistan chưa c&oacute; truyền h&igrave;nh. &Ocirc;ng ghi t&ecirc;n t&ocirc;i v&agrave;o những đội b&oacute;ng đ&aacute; để khuấy l&ecirc;n c&ugrave;ng sự đam m&ecirc; trong t&ocirc;i. Nhưng t&ocirc;i l&agrave; một g&aacute;nh nặng sai lầm tệ hại khổ sở cho đội b&oacute;ng của t&ocirc;i, lu&ocirc;n l&agrave;m vướng ch&acirc;n một đường chuyền th&iacute;ch hợp hoặc v&ocirc; t&igrave;nh đứng chặn một đường b&oacute;ng tho&aacute;ng. T&ocirc;i l&ecirc; đ&ocirc;i ch&acirc;n khẳng khiu về ph&iacute;a s&acirc;n đối phương, h&eacute;t l&ecirc;n gọi những đường b&oacute;ng chẳng bao giờ đến được với m&igrave;nh. V&agrave; t&ocirc;i c&agrave;ng cố, hai tay giơ tr&ecirc;n đầu vẫy vẫy, cuồng nhiệt h&eacute;t to &quot;T&ocirc;i đ&oacute;n đ&acirc;y! T&ocirc;i đ&oacute;n đ&acirc;y!&quot; th&igrave; lại c&agrave;ng bị phớt lờ. Nhưng Baba n&agrave;o c&oacute; chịu bu&ocirc;ng tha. Khi sự thể đ&atilde; trở n&ecirc;n qu&aacute; r&otilde; r&agrave;ng, t&ocirc;i kh&ocirc;ng kế thừa được những t&agrave;i năng lực sĩ của &ocirc;ng, &ocirc;ng lại t&igrave;m c&aacute;ch biến t&ocirc;i th&agrave;nh một kh&aacute;n giả say m&ecirc; b&oacute;ng đ&aacute;. Chắc chắn t&ocirc;i c&oacute; thể xoay xở ư? T&ocirc;i vờ vịt quan t&acirc;m đến chuyện đ&oacute; c&agrave;ng l&acirc;u c&agrave;ng tốt. T&ocirc;i reo l&ecirc;n c&ugrave;ng &ocirc;ng khi đội Kabul ghi b&agrave;n v&agrave;o đội Kandahar v&agrave; h&eacute;t l&ecirc;n lăng mạ trọng t&agrave;i thổi phạt đền đội b&oacute;ng của ch&uacute;ng t&ocirc;i. Nhưng Baba đo&aacute;n được t&ocirc;i thiếu niềm say m&ecirc; thực sự v&agrave; &ocirc;ng đ&agrave;nh đầu h&agrave;ng trước sự thể hiển nhi&ecirc;n l&agrave; con trai &ocirc;ng chẳng bao giờ trở th&agrave;nh cầu thủ cũng như một fan b&oacute;ng đ&aacute;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i c&ograve;n nhớ một lần Baba mang t&ocirc;i tới xem cuộc tỉ th&iacute; Buzkashi h&agrave;ng năm tổ chức v&agrave;o đầu xu&acirc;n, ng&agrave;y Tết năm mới.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Buzkashi l&agrave;, v&agrave; m&atilde;i l&agrave;, niềm đam m&ecirc; của cả nước Afghanistan. Một Chapandaz, kỵ m&atilde; cực kỳ điệu nghệ, thường được những người h&acirc;m mộ gi&agrave;u c&oacute; bảo trợ, phải chộp được x&aacute;c con d&ecirc; hoặc con th&uacute; đ&atilde; chết từ giữa một đ&aacute;m hỗn loạn, phi hết nước đại mang theo con mồi chạy quanh đấu trường, rồi n&eacute;m n&oacute; v&agrave;o đường tr&ograve;n thắng điểm, trong khi ấy đội của Chapandaz kh&aacute;c săn đuổi, v&agrave; l&agrave;m đủ mọi điều tuỳ sức của họ - đ&aacute;, t&uacute;m, vụt, thụi để chộp lại con mồi từ anh ta. H&ocirc;m đ&oacute;, người xem g&agrave;o l&ecirc;n phấn kh&iacute;ch khi người kỵ sĩ tr&ecirc;n đấu trường gầm nhưng tiếng th&eacute;t chiến trận, chen đẩy để cướp con mồi trong đ&aacute;m bụi m&ugrave; mịt. Mặt đất rung l&ecirc;n tiếng v&oacute; ngựa rầm rập. T&ocirc;i v&agrave; cha t&ocirc;i ngồi xem ở ph&iacute;a kh&aacute;n đ&agrave;i tr&ecirc;n khi c&aacute;c kỵ sĩ phi hết nước đại rầm rập chạy qua, vừa reo vừa g&agrave;o th&eacute;t, ngựa s&ugrave;i bọt m&eacute;p. Bỗng Baba chỉ về ph&iacute;a ai đ&oacute;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Amir n&agrave;y, con c&oacute; tr&ocirc;ng thấy người ngồi tr&ecirc;n đ&oacute; c&ugrave;ng với những người kh&aacute;c v&acirc;y quanh &ocirc;ng ta kh&ocirc;ng?T&ocirc;i c&oacute; thấy.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Đ&oacute; l&agrave; &ocirc;ng Henry Kissinger 6 đấy.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Ồ! - T&ocirc;i n&oacute;i. T&ocirc;i đ&acirc;u c&oacute; biết &ocirc;ng Kissinger l&agrave; ai, v&agrave; đ&atilde; định hỏi cha. Nhưng ngay l&uacute;c ấy, t&ocirc;i khiếp h&atilde;i chứng kiến một trong những Chapandaz ng&atilde; khỏi y&ecirc;n v&agrave; bị gi&agrave;y x&eacute;o dưới nhiều m&oacute;ng ngựa. Th&acirc;n thể người ấy bị n&eacute;m tung l&ecirc;n, v&agrave; quăng v&agrave;o đ&aacute;m đ&ocirc;ng hỗn loạn như một con b&uacute;p b&ecirc; nhồi vải vụn, cuối c&ugrave;ng lăn l&ocirc;ng lốc rồi dừng lại, khi đ&aacute;m hỗn loạn đ&atilde; rời đi. Anh ta giật l&ecirc;n một c&aacute;i rồi nằm bất động, đ&ocirc;i ch&acirc;n c&ograve;ng lại th&agrave;nh những g&oacute;c bất b&igrave;nh thường, một vũng m&aacute;u ướt đẫm tr&ecirc;n c&aacute;t.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i bắt đầu kh&oacute;c.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i kh&oacute;c suốt tr&ecirc;n đường về nh&agrave;. T&ocirc;i c&ograve;n nhớ hai tay Baba siết chặt v&ocirc; lăng như thế n&agrave;o. Siết chặt v&agrave; nới lỏng. C&aacute;i ch&iacute;nh l&agrave;, t&ocirc;i kh&ocirc;ng bao giờ qu&ecirc;n những cố gắng phi thường của Baba h&ograve;ng che giấu c&aacute;i nh&igrave;n kinh tởm tr&ecirc;n khu&ocirc;n mặt &ocirc;ng khi &ocirc;ng im l&igrave;m l&aacute;i xe.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Khuya đ&ecirc;m đ&oacute;, t&ocirc;i đang đi ngang qua ph&ograve;ng đọc của cha t&ocirc;i th&igrave; tho&aacute;ng nghe thấy &ocirc;ng đang n&oacute;i chuyện với ch&uacute; Rahim Khan. T&ocirc;i &aacute;p tai v&agrave;o cửa đ&oacute;ng k&iacute;n.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">-...may l&agrave; n&oacute; khoẻ mạnh, - ch&uacute; Rahim Khan đang n&oacute;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- T&ocirc;i biết, t&ocirc;i biết. Nhưng n&oacute; cứ lu&ocirc;n v&ugrave;i đầu v&agrave;o mấy cuốn s&aacute;ch đ&oacute; hoặc lang thang quanh nh&agrave; như lạc trong mộng mị.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Th&igrave; sao?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- T&ocirc;i kh&ocirc;ng th&iacute;ch thế, - Baba n&oacute;i oang oang, ch&aacute;n nản, gần như tức giận.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ch&uacute; Rahim Khan cười.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Trẻ con đ&acirc;u phải những quyền s&aacute;ch tập t&ocirc; m&agrave;u. Anh kh&ocirc;ng phải b&ocirc;i đầy ch&uacute;ng bằng những m&agrave;u anh ưa th&iacute;ch.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- T&ocirc;i n&oacute;i để &ocirc;ng biết, - Baba n&oacute;i. - T&ocirc;i kh&ocirc;ng th&iacute;ch như thế ch&uacute;t n&agrave;o cả, m&agrave; c&oacute; phải t&ocirc;i sống với đứa trẻ bất kỳ n&agrave;o đ&oacute; đ&acirc;u cơ chứ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Anh biết kh&ocirc;ng, đ&ocirc;i khi anh l&agrave; một người tự coi m&igrave;nh l&agrave; trung t&acirc;m bậc nhất đấy. - Ch&uacute; Rahim Khan n&oacute;i. Ch&uacute; l&agrave; người duy nhất m&agrave; t&ocirc;i biết c&oacute; thể ph&acirc;n đ&ocirc;i bằng việc n&oacute;i những điều như thế với Baba.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thế th&igrave; chẳng c&ograve;n điều g&igrave; phải b&agrave;n cả.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Kh&ocirc;ng ư?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Kh&ocirc;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Rồi sao?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i nghe thấy tiếng da đệm ghế Baba kin k&iacute;t, như thể &ocirc;ng đang x&ecirc; dịch tr&ecirc;n đ&oacute;. T&ocirc;i nhắm mắt lại, &eacute;p tai chặt hơn nữa v&agrave;o c&aacute;nh cửa, nửa muốn nghe, nửa kh&ocirc;ng muốn. - &quot;Đ&ocirc;i khi từ cửa số n&agrave;y t&ocirc;i nh&igrave;n ra ngo&agrave;i v&agrave; thấy n&oacute; đang chơi tr&ecirc;n đường phố c&ugrave;ng lũ con trai h&agrave;ng x&oacute;m. T&ocirc;i thấy ch&uacute;ng tr&ecirc;u chọc n&oacute;, cướp đồ chơi của n&oacute;, ẩy v&agrave;o chỗ n&agrave;y, đ&aacute;nh v&agrave;o chỗ kia. M&agrave;, &ocirc;ng biết kh&ocirc;ng, n&oacute; kh&ocirc;ng bao giờ đ&aacute;nh trả. Kh&ocirc;ng bao giờ. N&oacute; chỉ... c&uacute;i đầu v&agrave;...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Vậy n&oacute; kh&ocirc;ng ưa bạo lực, - ch&uacute; Rahim n&oacute;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- &Yacute; t&ocirc;i kh&ocirc;ng phải như thế, Rahim, &ocirc;ng biết m&agrave;, - Baba phản b&aacute;c. - C&oacute; một c&aacute;i g&igrave; đ&oacute; thiếu hụt trong thằng b&eacute; n&agrave;y.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Phải, một t&iacute;nh c&aacute;ch tiểu nh&acirc;n.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Tự vệ kh&ocirc;ng c&oacute; g&igrave; giống với tiểu nh&acirc;n. &Ocirc;ng c&oacute; biết điều g&igrave; lu&ocirc;n xảy ra khi những đứa trẻ h&agrave;ng x&oacute;m tr&ecirc;u chọc n&oacute; kh&ocirc;ng? Hassan gi&uacute;p sức v&agrave; cản ch&uacute;ng lại. T&ocirc;i đ&atilde; tận mắt chứng kiến cảnh đ&oacute;. Rồi khi hai đứa về nh&agrave;, t&ocirc;i n&oacute;i với n&oacute;: &quot;L&agrave;m thế n&agrave;o m&agrave; Hassan lại c&oacute; vết xước tr&ecirc;n mặt thế kia?&quot; N&oacute; n&oacute;i: &quot;Cậu ấy ng&atilde;&quot;. T&ocirc;i bảo cho &ocirc;ng hay, Rahim ạ, c&oacute; một c&aacute;i g&igrave; đ&oacute; thiếu hụt ở thằng b&eacute; đ&oacute;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Anh chỉ cần để n&oacute; tự t&igrave;m con đường của m&igrave;nh, - ch&uacute; Rahim Khan n&oacute;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- V&agrave; rồi n&oacute; sẽ đi tới đ&acirc;u, - Baba n&oacute;i. - Một thằng con trai kh&ocirc;ng tự bảo vệ m&igrave;nh sẽ trở th&agrave;nh một người đ&agrave;n &ocirc;ng kh&ocirc;ng thể bảo vệ bất cứ điều g&igrave;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Anh vẫn cứ hay qu&aacute; đơn giản ho&aacute; mọi điều.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- T&ocirc;i kh&ocirc;ng nghĩ thế.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Anh tức giận bởi v&igrave; anh sợ n&oacute; sẽ kh&ocirc;ng bao giờ quản l&yacute; được c&ocirc;ng việc kinh doanh cho anh.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- N&agrave;o, xem b&acirc;y giờ ai đang qu&aacute; đơn giản ho&aacute; mọi chuyện đ&acirc;y, - Baba n&oacute;i. - Xem n&agrave;o, t&ocirc;i biết c&oacute; một sự quyến luyến giữa anh v&agrave; n&oacute;. T&ocirc;i sung sướng về điều đ&oacute;. Ghen tị nhưng sung sướng. T&ocirc;i muốn n&oacute;i thế. N&oacute; cần một ai đ&oacute;... hiểu n&oacute;, bởi v&igrave; Thượng đế biết, t&ocirc;i kh&ocirc;ng hiểu nổi n&oacute;. Nhưng c&oacute; điều g&igrave; đ&oacute; Amir l&agrave;m t&ocirc;i bứt rứt, bứt rứt như thế n&agrave;o t&ocirc;i kh&ocirc;ng diễn tả được. N&oacute; giống như... - T&ocirc;i c&oacute; thể h&igrave;nh dung ra &ocirc;ng loay hoay t&igrave;m một từ thật đ&uacute;ng. &Ocirc;ng hạ giọng xuống, nhưng dẫu sao t&ocirc;i cũng vẫn nghe thấy. - Nếu như t&ocirc;i kh&ocirc;ng tận mắt tr&ocirc;ng thấy b&aacute;c sĩ l&ocirc;i n&oacute; ra từ trong bụng vợ t&ocirc;i, t&ocirc;i chẳng giờ tin n&oacute; l&agrave; con trai t&ocirc;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">S&aacute;ng h&ocirc;m sau, l&uacute;c chuẩn bị bữa s&aacute;ng cho t&ocirc;i Hassan hỏi c&oacute; điều g&igrave; l&agrave;m t&ocirc;i kh&oacute; chịu vậy. T&ocirc;i nổi c&aacute;u với cậu ấy, bảo h&atilde;y nghĩ đến c&ocirc;ng việc của m&igrave;nh.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ch&uacute; Rahim đ&atilde; nhầm về chuyện t&iacute;nh c&aacute;ch tiểu nh&acirc;n.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>CHƯƠNG IV</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Năm 1933, năm Baba ch&agrave;o đời, cũng l&agrave; năm quốc vương Zahir Shah bắt đầu năm thứ bốn mươi trị v&igrave; Afghanistan, hai anh em, hai g&atilde; trai trẻ trong một gia đ&igrave;nh gi&agrave;u c&oacute; v&agrave; danh gi&aacute; ở Kabul ngồi sau tay l&aacute;i chiếc xe Ford của cha m&igrave;nh. Ng&agrave; ng&agrave; say thuốc l&aacute; tẩm thuốc phiện v&agrave; chếnh cho&aacute;ng rượu vang Ph&aacute;p, họ đ&atilde; c&aacute;n chết đ&ocirc;i vợ chồng người Hazara tr&ecirc;n con đường tới Paghman. Cảnh s&aacute;t đem hai g&atilde; trai c&oacute; vẻ ăn năn hối lỗi v&agrave; đứa con trai mồ c&ocirc;i năm tuổi của đ&ocirc;i vợ chồng đ&atilde; chết đến trước &ocirc;ng t&ocirc;i, l&uacute;c đ&oacute; l&agrave; một vị quan to&agrave; uy danh v&agrave; l&agrave; một con người uy t&iacute;n mẫu mực. Sau khi nghe hai g&atilde; trai tường tr&igrave;nh v&agrave; người cha xin khoan hồng, &ocirc;ng t&ocirc;i quyết định anh em nh&agrave; kia phải lập tức đến Kandahar v&agrave; ghi t&ecirc;n v&agrave;o qu&acirc;n đội trong một năm - mặc cho gia đ&igrave;nh họ đ&atilde; xoay xở mọi c&aacute;ch để họ được miễn trừ bản &aacute;n. Người cha của họ cũng tranh biện, nhưng cũng kh&ocirc;ng quyết liệt lắm, v&agrave; cuối c&ugrave;ng mọi người đều đồng &yacute; l&agrave; việc trừng phạt c&oacute; lẽ hơi cay nghiệt nhưng thoả đ&aacute;ng. C&ograve;n với đứa trẻ mồ c&ocirc;i, &ocirc;ng t&ocirc;i nhận nu&ocirc;i trong gia đ&igrave;nh m&igrave;nh v&agrave; ra lệnh cho những đ&agrave;y tớ kh&aacute;c k&egrave;m cặp nhưng phải đối xử tốt với đứa b&eacute;. Đứa b&eacute; đ&oacute; l&agrave; Ali.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ali v&agrave; Baba lớn l&ecirc;n c&ugrave;ng nhau như đ&ocirc;i bạn để chỏm - &iacute;t nhất cũng cho đến khi bệnh vi&ecirc;m tuỷ x&aacute;m l&agrave;m bại liệt ch&acirc;n của Ali - hệt như t&ocirc;i v&agrave; Hassan c&ugrave;ng lớn l&ecirc;n ở thế hệ sau. Baba lu&ocirc;n kể cho ch&uacute;ng t&ocirc;i nghe về những tr&ograve; tinh nghịch của &ocirc;ng v&agrave; Ali, v&agrave; Ali thường lắc đầu bảo: &quot;Nhưng, Agha sahib 1, kể cho ch&uacute;ng t&ocirc;i nghe ai l&agrave; người nghĩ ra tr&ograve; tinh nghịch v&agrave; ai l&agrave; người thực hiện đ&aacute;ng thương chứ?&quot; Baba thường chỉ cười v&agrave; dang c&aacute;nh tay &ocirc;m lấy Ali.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nhưng kh&ocirc;ng c&oacute; một chuyện n&agrave;o &ocirc;ng kể m&agrave; trong đ&oacute; Baba lại xem Ali như bạn của m&igrave;nh.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Điều lạ l&ugrave;ng l&agrave;, t&ocirc;i cũng kh&ocirc;ng bao giờ nghĩ t&ocirc;i v&agrave; Hassan l&agrave; đ&ocirc;i bạn. Dẫu sao, cũng kh&ocirc;ng theo nghĩa th&ocirc;ng thường. Bất kể việc ch&uacute;ng t&ocirc;i dạy nhau đi xe đạp bu&ocirc;ng hai tay, hoặc thiết kế một c&aacute;i m&aacute;y t&iacute;nh tại nh&agrave; c&oacute; đủ chức năng từ một hộp c&aacute;c t&ocirc;ng. Bất kể ch&uacute;ng t&ocirc;i c&ugrave;ng nhau thả diều suốt m&ugrave;a đ&ocirc;ng, v&agrave; đua diều. Bất kể đối với t&ocirc;i, bộ mặt của người Afghanistan ch&iacute;nh l&agrave; bộ mặt của thằng b&eacute; c&oacute; d&aacute;ng người thon thả, với c&aacute;i đầu cạo trọc, đ&ocirc;i tai mọc thấp, thằng b&eacute; với bộ mặt b&uacute;p b&ecirc; T&agrave;u vĩnh viễn ngời s&aacute;ng nụ cười m&ocirc;i hẻ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Bất chấp mọi điều đ&oacute;. Bởi v&igrave; lịch sử kh&ocirc;ng dễ để m&agrave; vượt qua. T&ocirc;n gi&aacute;o cũng vậy. Rốt cuộc rồi vẫn cứ l&agrave; người Pashtun, c&ograve;n cậu ấy l&agrave; người Hazara. T&ocirc;i l&agrave; người Sunmi v&agrave; cậu ấy l&agrave; người Shi&#39;a, v&agrave; kh&ocirc;ng g&igrave; c&oacute; thể thay đổi điều đ&oacute;. Kh&ocirc;ng g&igrave; hết.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nhưng ch&uacute;ng t&ocirc;i l&agrave; những đứa trẻ đ&atilde; từng c&ugrave;ng tập b&ograve; với nhau, v&agrave; cũng kh&ocirc;ng c&oacute; lịch sử n&agrave;o, vấn đề chủng tộc n&agrave;o, x&atilde; hội n&agrave;o, t&ocirc;n gi&aacute;o n&agrave;o thay đổi được điều đ&oacute;. T&ocirc;i đ&atilde; trải qua mười hai năm đầu của đời t&ocirc;i chơi đ&ugrave;a c&ugrave;ng Hassan. Đ&ocirc;i khi, cả thời thơ ấu của t&ocirc;i tựa như một ng&agrave;y h&egrave; d&agrave;i lười biếng c&ugrave;ng với Hassan, đuổi nhau giữa nhưng rặng c&acirc;y trong s&acirc;n nh&agrave; cha t&ocirc;i, chơi tr&ograve; trốn t&igrave;m, c&ocirc;ng an bắt kẻ trộm, cao bồi v&agrave; người da đỏ, h&agrave;nh hạ c&ocirc;n tr&ugrave;ng - với th&agrave;nh c&ocirc;ng rực rỡ kh&ocirc;ng chối c&atilde;i được l&agrave; c&aacute;i lần ch&uacute;ng t&ocirc;i r&uacute;t v&ograve;i của con ong rồi buộc quanh sinh vật đ&aacute;ng thương một c&aacute;i d&acirc;y, để mỗi lần n&oacute; bay l&ecirc;n lại bị giật mạnh lại.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ch&uacute;ng t&ocirc;i đuổi theo người Kochi, những bộ lạc du mục ngang qua Kabul, tr&ecirc;n đường tới c&aacute;c d&atilde;y n&uacute;i ph&iacute;a Bắc. Ch&uacute;ng t&ocirc;i thường nghe tiếng những ng&ocirc;i nh&agrave; lưu động tiến gần v&ugrave;ng l&acirc;n cận ch&uacute;ng t&ocirc;i, tiếng đ&agrave;n cừu của họ k&ecirc;u &egrave; ẹ, tiếng đ&agrave;n d&ecirc; k&ecirc;u be be, nhạc rung quanh cổ lũ lạc đ&agrave;. Ch&uacute;ng t&ocirc;i thường chạy ra khỏi nh&agrave; để xem đo&agrave;n nh&agrave; lưu động c&oacute;t k&eacute;t lăn qua đường phố của ch&uacute;ng t&ocirc;i, những người đ&agrave;n &ocirc;ng đầy bụi bặm, mặt d&atilde;i dầu nắng gi&oacute; v&agrave; những người đ&agrave;n b&agrave; cho&agrave;ng khăn d&agrave;i sặc sỡ, đeo những chuỗi hạt cườm v&agrave; v&ograve;ng bạc ở cổ tay v&agrave; khuỷu tay. Ch&uacute;ng t&ocirc;i n&eacute;m đ&aacute; cuội v&agrave;o những con d&ecirc; của họ. Ch&uacute;ng t&ocirc;i phun nước v&agrave;o những con la của họ. T&ocirc;i thường bắt Hassan ngồi tr&ecirc;n Bức tường Ng&ocirc; ốm lấy s&uacute;ng cao su bắn sỏi v&agrave;o đ&iacute;t lũ lạc đ&agrave;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ch&uacute;ng t&ocirc;i c&ugrave;ng nhau đi xem bộ phim cao bồi viễn T&acirc;y đầu ti&ecirc;n, Rio Bravo, với diễn vi&ecirc;n John Wayne thủ vai, ở C&ocirc;ng vi&ecirc;n Điện ảnh, vượt qua đường phố c&oacute; hiệu s&aacute;ch ưa th&iacute;ch của t&ocirc;i. T&ocirc;i nhớ m&igrave;nh đ&atilde; từng n&agrave;i xin Baba mang ch&uacute;ng t&ocirc;i tới Iran, để ch&uacute;ng t&ocirc;i c&oacute; thể gặp John Wayne. Baba ph&aacute; l&ecirc;n cười một chuỗi d&agrave;i tưởng vỡ họng - một &acirc;m thanh kh&ocirc;ng phải kh&ocirc;ng giống động cơ xe tải đang rồ m&aacute;y - rồi khi đ&atilde; c&oacute; thể n&oacute;i lại được, &ocirc;ng giải th&iacute;ch cho ch&uacute;ng t&ocirc;i kh&aacute;i niệm về lồng tiếng. Hassan v&agrave; t&ocirc;i sửng sốt. Sững sờ. John Wayne kh&ocirc;ng thực sự n&oacute;i tiếng Farsi v&agrave; &ocirc;ng ta kh&ocirc;ng phải người Iran. &Ocirc;ng ta l&agrave; người Mỹ, hệt như những người đ&agrave;n &ocirc;ng v&agrave; đ&agrave;n b&agrave; t&oacute;c d&agrave;i th&acirc;n thiện ch&uacute;ng t&ocirc;i lu&ocirc;n thấy vơ vẩn ở Kabul, mặc những chiếc v&aacute;y m&agrave;u sặc sỡ v&agrave; xơ gấu. Ch&uacute;ng t&ocirc;i xem Rio Bravo ba lần, nhưng ch&uacute;ng t&ocirc;i xem bộ phim cao bồi ưa th&iacute;ch của m&igrave;nh, phim Bảy anh ch&agrave;ng cừ kh&ocirc;i tới mười ba bận. Mỗi lần xem, l&uacute;c kết th&uacute;c ch&uacute;ng t&ocirc;i đều kh&oacute;c khi thấy những đứa trẻ Mexico ch&ocirc;n Charles Bronson - ho&aacute; ra cũng kh&ocirc;ng phải l&agrave; người Iran.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ch&uacute;ng t&ocirc;i tản bộ trong những phố chợ nhỏ sặc m&ugrave;i r&ecirc;u mốc của quận Sha-e-Nau ở Kabul, hoặc trong khu đ&ocirc; thị mới ph&iacute;a T&acirc;y quận Wazir Akbar Khan. Ch&uacute;ng t&ocirc;i tr&ograve; chuyện về bất cứ phim n&agrave;o vừa xem v&agrave; dạo bộ giữa những đ&aacute;m người hối hả của khu phố chợ. Ch&uacute;ng t&ocirc;i len lỏi giữa những thương gia v&agrave; những kẻ ăn m&agrave;y, lang thang qua c&aacute;c ng&otilde; hẹp vướng v&iacute;u nhiều d&atilde;y lều qu&aacute;n nhỏ chặt n&iacute;ch h&agrave;ng. Baba cho mỗi đứa ch&uacute;ng t&ocirc;i mỗi tuần mười đồng Afghani 2 v&agrave; ch&uacute;ng t&ocirc;i ti&ecirc;u v&agrave;o khoản Coca-Cola ấm v&agrave; kem nước hoa hồng b&ecirc;n tr&ecirc;n c&oacute; rắc quả hồ trăn nghiền nhỏ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Trong năm học, ch&uacute;ng t&ocirc;i c&oacute; lệ quen h&agrave;ng ng&agrave;y. V&agrave;o l&uacute;c t&ocirc;i lề mề ra khỏi giường v&agrave; l&ecirc; đến buồng tắm th&igrave; Hassan đ&atilde; tắm rửa rồi, cả cầu nguyện kinh Namaz buổi s&aacute;ng với &ocirc;ng Ali v&agrave; sửa soạn bữa s&aacute;ng cho t&ocirc;i: tr&agrave; đen n&oacute;ng, với ba thỏi đường, một l&aacute;t b&aacute;nh naannướng b&ecirc;n tr&ecirc;n quệt m&oacute;n mứt dầm anh đ&agrave;o chua m&agrave; t&ocirc;i ưa th&iacute;ch, tất cả đều được đặt ngay ngắn tr&ecirc;n b&agrave;n ăn. Trong khi t&ocirc;i ăn v&agrave; ph&agrave;n n&agrave;n về b&agrave;i tập về nh&agrave;, Hassan dọn dẹp giường ngủ, đ&aacute;nh gi&agrave;y cho t&ocirc;i, l&agrave; &aacute;o ngo&agrave;i mặc h&ocirc;m đ&oacute; v&agrave; xếp s&aacute;ch b&uacute;t cho t&ocirc;i. T&ocirc;i thường nghe thấy cậu h&aacute;t một m&igrave;nh ở ngo&agrave;i tiền sảnh l&uacute;c l&agrave; quần &aacute;o, h&aacute;t nhưng b&agrave;i ca cổ Hazara bằng giọng mũi. Rồi, Baba v&agrave; t&ocirc;i rời khỏi nh&agrave; trong chiếc xe Ford Mustang đen - chiếc xe m&agrave; đ&acirc;u đ&acirc;u mọi người cũng nh&igrave;n mộ c&aacute;ch th&egrave;m thuồng, bởi v&igrave; n&oacute; c&ugrave;ng loại xe m&agrave; Steve McQueen đ&atilde; l&aacute;i trong phim Bullitt, bộ phim được chiếu trong một rạp chiếu phim tới s&aacute;u th&aacute;ng liền. Hassan ở nh&agrave; gi&uacute;p &ocirc;ng Ali những c&ocirc;ng việc trong ng&agrave;y: giặt bằng tay quần &aacute;o bẩn rồi đem phơi ngo&agrave;i s&acirc;n, lau qu&eacute;t s&agrave;n nh&agrave;, ra phố chợ mua b&aacute;nh naan sống, nấu m&oacute;n sốt thịt cho bữa chiều, tưới cỏ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Đi học về, t&ocirc;i v&agrave; Hassan lại gặp nhau, vơ lấy một cuốn s&aacute;ch, tung tăng phi l&ecirc;n ngọn đồi h&igrave;nh b&aacute;t &uacute;p toạ lạc đ&uacute;ng ph&iacute;a Bắc dinh cơ của cha t&ocirc;i ở quận Wazir Akbar Khan. Nơi đ&oacute; c&oacute; một nghĩa địa hoang phế, với những d&atilde;y bia mộ bằng đ&aacute; kh&ocirc;ng ghi t&ecirc;n v&agrave; những bụi c&acirc;y ngăn hai b&ecirc;n lối đi. Nhiều m&ugrave;a mưa v&agrave; tuyết rơi đ&atilde; l&agrave;m chiếc cổng sắt han gỉ v&agrave; khiến những d&atilde;y tường đ&aacute; trắng của nghĩa địa xi&ecirc;u đổ. C&oacute; một c&acirc;y lựu gần lối v&agrave;o nghĩa địa. Một ng&agrave;y m&ugrave;a h&egrave;, t&ocirc;i đ&atilde; d&ugrave;ng con dao bếp của &ocirc;ng Ali khắc t&ecirc;n ch&uacute;ng t&ocirc;i t&ecirc;n th&acirc;n c&acirc;y: &quot;Amir v&agrave; Hassan, hai Sultan 3 của Kabul&quot;. Những chữ đ&oacute; ch&iacute;nh thức tỏ r&otilde;: c&acirc;y lựu l&agrave; của ch&uacute;ng t&ocirc;i. Đi học về, Hassan v&agrave; t&ocirc;i tr&egrave;o l&ecirc;n c&acirc;y v&agrave; h&aacute;i những quả lựu đỏ tươi. Ăn lựu v&agrave; ch&ugrave;i tay xuống cỏ xong, t&ocirc;i thường đọc s&aacute;ch cho Hassan nghe.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ngồi xếp bằng tr&ograve;n, &aacute;nh nắng v&agrave; b&oacute;ng l&aacute; c&acirc;y lựu vờn m&uacute;a tr&ecirc;n mặt, Hassan lơ đ&atilde;ng nhổ những t&uacute;m cỏ khỏi mặt đất, trong khi t&ocirc;i đọc những truyện ngắn m&agrave; cậu kh&ocirc;ng tự đọc được. Thực tế Hassan lớn l&ecirc;n cũng m&ugrave; chữ như &ocirc;ng Ali v&agrave; phần lớn những người Hazara vốn đ&atilde; được định phận từ gi&acirc;y ph&uacute;t sinh ra, thậm ch&iacute; c&oacute; lẽ từ l&uacute;c bắt đầu được thai ngh&eacute;n bất đắc dĩ trong bụng của Sanaubar. Nhưng rốt cuộc người hầu biết chữ để l&agrave;m g&igrave; cơ chứ? Nhưng dẫu m&ugrave; chữ, hoặc biết đ&acirc;u ch&iacute;nh v&igrave; vậy, Hassan bị sự huyền b&iacute; của ng&ocirc;n từ thu h&uacute;t, bị quyến rũ bởi một thế giới b&iacute; mật cấm đo&aacute;n với m&igrave;nh. T&ocirc;i đọc cho cậu ấy những b&agrave;i thơ, truyện ngắn, đ&ocirc;i khi những c&acirc;u đố mẹo - dẫu t&ocirc;i thường dừng ngay khi thấy cậu giải hay hơnt&ocirc;i qu&aacute; nhiều. Thế l&agrave; t&ocirc;i đọc cho cậu những chuyện kh&ocirc;ng cần tranh c&atilde;i, như những chuyện rủi ro của gi&aacute;o sĩ Hồi gi&aacute;o Nasruddin ngớ ngẩn v&agrave; con lừa của &ocirc;ng ta. Ch&uacute;ng t&ocirc;i ngồi h&agrave;ng giờ dưới gốc c&acirc;y đ&oacute;, ngồi cho đến khi mặt trời t&agrave; &uacute;a ở ph&iacute;a T&acirc;y, vậy m&agrave; Hassan vẫn cố n&agrave;i, n&oacute;i hai đứa c&ograve;n đủ &aacute;nh s&aacute;ng để đọc th&ecirc;m một truyện nữa, một chương nữa.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Phần đọc t&ocirc;i th&uacute; vị l&agrave; khi ch&uacute;ng t&ocirc;i t&igrave;nh cờ gặp một từ m&agrave; cậu ấy kh&ocirc;ng biết. T&ocirc;i thường tr&ecirc;u cậu, phơi b&agrave;y sự dốt n&aacute;t của cậu ra. C&oacute; lần t&ocirc;i đang đọc cho cậu nghe một truyện về tu sĩ hồi gi&aacute;o Nasruddin, Hassan ngắt lời t&ocirc;i:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Từ ấy nghĩa thế n&agrave;o?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Từ n&agrave;o?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Đần độn.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Cậu kh&ocirc;ng biết nghĩa l&agrave; g&igrave; &agrave;? - T&ocirc;i nhăn nhở hỏi.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Kh&ocirc;ng, Amir agha.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Nhưng n&oacute; chỉ l&agrave; một từ th&ocirc;ng thường th&ocirc;i m&agrave;!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- T&ocirc;i vẫn kh&ocirc;ng hiểu.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nếu Hassan c&oacute; cảm thấy t&ocirc;i chơi xỏ cậu, th&igrave; khu&ocirc;n mặt tươi cười của cậu cũng kh&ocirc;ng biểu lộ ra.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Ồ cả trường t&ocirc;i, ai chả biết n&oacute; nghĩa l&agrave; g&igrave;, - T&ocirc;i n&oacute;i. - Để xem n&agrave;o, &quot;Đần độn&quot; c&oacute; nghĩa l&agrave; tinh nhanh, th&ocirc;ng minh. T&ocirc;i đặt một c&acirc;u cho cậu c&oacute; sử dụng chữ đ&oacute; nh&eacute;: N&oacute;i về từ vựng, Hassan l&agrave; một kẻ đần độn.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- &Agrave;, &agrave;, - Hassan gật gật đầu.Sau đ&oacute;, t&ocirc;i lu&ocirc;n cảm thấy m&igrave;nh c&oacute; tội. V&igrave; vậy t&ocirc;i thường t&igrave;m c&aacute;ch x&iacute; xo&aacute; bằng c&aacute;ch cho cậu ấy một chiếc &aacute;o sơ mi cũ, hoặc một đồ chơi hỏng. T&ocirc;i thường tự nhủ thế l&agrave; đủ sửa chữa cho tr&ograve; đ&ugrave;a v&ocirc; hại rồi.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Cuốn s&aacute;ch m&agrave; Hassan ưa th&iacute;ch nhất l&agrave; cuốn Shahnamah, thi&ecirc;n sử thi thế kỷ thứ mười kể về những anh h&ugrave;ng Ba Tư cổ xưa. Cậu th&iacute;ch tất cả c&aacute;c chương, c&aacute;c Shah 4 đời trước Feridoun, Zal v&agrave; Rudabeh. Nhưng truyện ưa th&iacute;ch nhất của cậu cũng như của t&ocirc;i, l&agrave; &quot;Rostam v&agrave; Sohrab&quot;, c&acirc;u chuyện về chiến binh vĩ đại Rostam v&agrave; con ngựa nhanh như gi&oacute; Rakhsh. Tr&ecirc;n chiến trường, Rostam ch&eacute;m tử thương Sohrab, kẻ b&aacute;o ứng can đảm của m&igrave;nh, chỉ để nhận ra Sohrab ch&iacute;nh l&agrave; đứa con trai từ l&acirc;u thất lạc. Gi&agrave;y v&ograve; đau đớn, Rostam nghe những lời trăng trối của con trai m&igrave;nh:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nếu &ocirc;ng quả thật l&agrave; cha ta, chẳng phải thanh kiếm trong tay &ocirc;ng đ&atilde; vấy m&aacute;u tươi của con trai &ocirc;ng rồi ư. V&agrave; &ocirc;ng l&agrave;m thế l&agrave; do sự ngoan cố của &ocirc;ng. Bởi ta đ&atilde; cố l&agrave;m cho &ocirc;ng biết y&ecirc;u thương, ta van xin đ&iacute;ch danh &ocirc;ng, v&igrave; ta nghĩ nh&igrave;n thấy trong &ocirc;ng những dấu hiệu nhắc nhớ điều g&igrave; đ&oacute; của mẹ ta. Nhưng ta cầu cứu tr&aacute;i tim &ocirc;ng v&ocirc; &iacute;ch, v&agrave; b&acirc;y giờ cơ hội t&aacute;i hợp đ&atilde; qua rồi...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Đọc lại đi, t&ocirc;i xin Amir agha đấy, - Hassan thường n&oacute;i vậy. Đ&ocirc;i khi nước mắt ứa l&ecirc;n trong đ&ocirc;i mắt của Hassan khi t&ocirc;i đọc c&aacute;i đoạn ấy, v&agrave; t&ocirc;i lu&ocirc;n kh&ocirc;ng hiểu cậu kh&oacute;c v&igrave; ai, cho &ocirc;ng Rostam dằn vặt đớn đau x&eacute; toang &aacute;o quần v&agrave; phủ tro tr&ecirc;n đầu m&igrave;nh, hay Sohrab đang hấp hối, từ l&acirc;u vẫn mong đợi t&igrave;nh thương y&ecirc;u của cha m&igrave;nh. Ri&ecirc;ng t&ocirc;i, t&ocirc;i kh&ocirc;ng thể thấy được tấn bi kịch trong số phận của Rostam. Rốt cuộc, phải chăng tất cả những người cha trong t&acirc;m can s&acirc;u k&iacute;n của m&igrave;nh đều ấp ủ một ước muốn giết chết con trai họ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Một h&ocirc;m, v&agrave;o th&aacute;ng Bảy năm 1973, t&ocirc;i chơi một tr&ograve; bịp nhỏ kh&aacute;c với Hassan. Đang đọc cho cậu ấy nghe, bất chợt t&ocirc;i bỏ c&acirc;u chuyện đi. T&ocirc;i vờ như vẫn đọc cuốn s&aacute;ch đ&oacute;, cũng lật trang đều đặn, nhưng t&ocirc;i đ&atilde; bỏ rơi ho&agrave;n to&agrave;n văn bản, l&agrave;m chủ c&acirc;u chuyện, v&agrave; phịa ra theo &yacute; t&ocirc;i. Hassan đương nhi&ecirc;n kh&ocirc;ng nhận ra điều đ&oacute;. Với cậu, những từ tr&ecirc;n trang giấy l&agrave; sự cạnh tranh gay gắt của bao nhi&ecirc;u mật m&atilde; kh&ocirc;ng thể giải nổi, b&iacute; hiểm. Từ ngữ l&agrave; những cổng v&agrave;o b&iacute; mật v&agrave; t&ocirc;i nắm giữ ch&igrave;a kho&aacute;. Sau đ&oacute;, t&ocirc;i hỏi cậu xem c&oacute; th&iacute;ch truyện đ&oacute; kh&ocirc;ng, một tiếng kh&uacute;c kh&iacute;ch d&acirc;ng l&ecirc;n trong họng t&ocirc;i, khi Hassan vỗ tay hoan h&ocirc;. T&ocirc;i hỏi:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Cậu đang l&agrave;m c&aacute;i g&igrave; đấy?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Đấy l&agrave; c&acirc;u chuyện hay nhất cậu đọc cho t&ocirc;i l&acirc;u nay đấy, - Hassan vẫn vừa vỗ tay vừa n&oacute;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i cười:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thật kh&ocirc;ng?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thật.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thế th&igrave; hấp dẫn ư? - t&ocirc;i lẩm bẩm. T&ocirc;i cũng định n&oacute;i như thế. Điều n&agrave;y l&agrave;... ho&agrave;n to&agrave;n kh&ocirc;ng mong đợi. - Cậu tin chắc thế kh&ocirc;ng, Hassan?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Hassan vẫn đang vỗ tay:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thật tuyệt vời, Amir agha. Mai cậu lại đọc nữa cho t&ocirc;i chứ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Hấp dẫn, - t&ocirc;i nhắc lại, hơi kh&oacute; thở, cảm thấy như một người vừa kh&aacute;m ph&aacute; ra kho t&agrave;ng ch&ocirc;n giấu trong s&acirc;n sau nh&agrave; m&igrave;nh. Bước xuống ch&acirc;n đồi, những &yacute; nghĩ c&uacute; phụt ra trong đầu t&ocirc;i như ph&aacute;o hoa trong ng&agrave;y hội Chaman 5.Truyện hay nhất cậu đọc cho t&ocirc;i l&acirc;u nay đấy, cậu ấy đ&atilde; n&oacute;i thế. T&ocirc;i đ&atilde; đọc cho cậu kh&aacute; nhiều truyện ngắn. Hassan h&igrave;nh như đang hỏi t&ocirc;i điều g&igrave; đ&oacute;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- G&igrave; cơ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Hấp dẫn, thế nghĩa l&agrave; g&igrave;?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i cười, gh&igrave; chặt lấy cậu ấy v&agrave; đặt một c&aacute;i h&ocirc;n l&ecirc;n m&aacute;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thế l&agrave; thế n&agrave;o? - Hassan giật m&igrave;nh, đỏ mặt n&oacute;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i th&acirc;n mật ẩy cậu một c&aacute;i. Rồi mỉm cười.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Hassan, cậu l&agrave; một &ocirc;ng ho&agrave;ng. Cậu l&agrave; một &ocirc;ng ho&agrave;ng v&agrave; t&ocirc;i y&ecirc;u cậu.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ngay tối h&ocirc;m đ&oacute;, t&ocirc;i viết c&aacute;i truyện ngắn đầu ti&ecirc;n của t&ocirc;i. Mất ba mươi ph&uacute;t. Đ&oacute; l&agrave; một c&acirc;u chuyện nhỏ u tối về một người đ&agrave;n &ocirc;ng t&igrave;m thấy chiếc cốc thần v&agrave; biết được rằng nếu kh&oacute;c v&agrave;o chiếc cốc ấy nước mắt của &ocirc;ng sẽ biến th&agrave;nh ngọc trai. Nhưng mặc đầu lu&ocirc;n sống trong cảnh ngh&egrave;o khổ, &ocirc;ng vẫn l&agrave; người hạnh ph&uacute;c v&agrave; hiếm khi rơi một giọt nước mắt. V&igrave; vậy, &ocirc;ng t&igrave;m mọi c&aacute;ch l&agrave;m cho m&igrave;nh buồn, để nước mắt c&oacute; thể l&agrave;m cho &ocirc;ng trở n&ecirc;n gi&agrave;u c&oacute;. Ngọc trai chất đống cao đến đ&acirc;u, l&ograve;ng tham của &ocirc;ng ta tăng l&ecirc;n đến đấy. Truyện kết th&uacute;c kết th&uacute;c khi người đ&agrave;n &ocirc;ng ngồi tr&ecirc;n một n&uacute;i ngọc, con dao cầm trong tay, đang kh&oacute;c một c&aacute;ch v&ocirc; vọng v&agrave;o trong chiếc cốc, tay &ocirc;m x&aacute;c người vợ y&ecirc;u dấu bị giết.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tối đ&oacute;, t&ocirc;i leo l&ecirc;n thang g&aacute;c v&agrave; bước v&agrave;o ph&ograve;ng h&uacute;t thuốc của Baba, trong tay t&ocirc;i l&agrave; hai tờ giấy tr&ecirc;n đ&oacute; t&ocirc;i viết nguệch ngoạc c&acirc;u chuyện ấy. Baba v&agrave; ch&uacute; Rahim Khan đang h&uacute;t tẩu thuốc v&agrave; nhấm nh&aacute;p rượu brandy khi t&ocirc;i bước v&agrave;o.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- C&aacute;i g&igrave; thế, Amir? - Baba vừa hỏi vừa ngả người tr&ecirc;n ghế sofa, đan hai tay gối ph&iacute;a sau đầu. Kh&oacute;i xanh cuộn xo&aacute;y quanh mặt &ocirc;ng. C&aacute;i nh&igrave;n trừng trừng của &ocirc;ng l&agrave;m cho họng t&ocirc;i kh&ocirc; lại. T&ocirc;i hắng giọng n&oacute;i t&ocirc;i vừa viết xong một truyện ngắn.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Baba gật đầu v&agrave; nhếch m&eacute;p cười, chẳng ngụ &yacute; g&igrave; hơn l&agrave; giả vờ quan t&acirc;m. &quot;Được. tốt lắm, phải kh&ocirc;ng n&agrave;o &ocirc;ng n&agrave;o?&quot; &ocirc;ng n&oacute;i vậy. Rồi chăng th&ecirc;m g&igrave; nữa. &Ocirc;ng chỉ nh&igrave;n t&ocirc;i qua l&agrave;n kh&oacute;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">C&oacute; lẽ t&ocirc;i đứng đ&oacute; chưa đầy một ph&uacute;t, nhưng đến ng&agrave;y h&ocirc;m nay, n&oacute; l&agrave; c&aacute;i ph&uacute;t d&agrave;i nhất của đời t&ocirc;i. Những gi&acirc;y đồng hồ nặng nhọc tr&ocirc;i đi, gi&acirc;y n&agrave;y c&aacute;ch gi&acirc;y sau một thời gian vĩnh cửu. Kh&ocirc;ng kh&iacute; trở n&ecirc;n nặng nề, ẩm thấp, gần như đặc lại. T&ocirc;i như đang thở ra những vi&ecirc;n gạch. Baba tiếp tục nh&igrave;n t&ocirc;i trừng trừng v&agrave; kh&ocirc;ng bảo t&ocirc;i đọc truyện.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Như mọi khi, Rahim Khan lu&ocirc;n lu&ocirc;n l&agrave; người giải cứu t&ocirc;i. Ch&uacute; ch&igrave;a b&agrave;n tay ra v&agrave; ưu &aacute;i t&ocirc;i bằng một nụ cười kh&ocirc;ng ch&uacute;t vờ vĩnh:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Amir jan, ch&uacute; rất muốn đọc truyện đ&oacute;, cho ch&uacute; đọc được kh&ocirc;ng?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Baba kh&oacute; c&oacute; khi n&agrave;o lại d&ugrave;ng c&aacute;i từ Jan tỏ vẻ th&acirc;n thiết đ&oacute; khi &ocirc;ng n&oacute;i với t&ocirc;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Baba nh&uacute;n vai đứng dậy. &Ocirc;ng c&oacute; vẻ nhẹ m&igrave;nh như thể ch&iacute;nh &ocirc;ng cũng được ch&uacute; Rahim Khan cứu.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Phải đấy, đưa cho Kak 6 Rahim. Bố l&ecirc;n g&aacute;c chuẩn bị đ&acirc;y.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">N&oacute;i xong, rồi &ocirc;ng rời khỏi căn ph&ograve;ng. Hầu như ng&agrave;y n&agrave;o t&ocirc;i cũng t&ocirc;n s&ugrave;ng Baba với một cảm x&uacute;c m&atilde;nh liệt gần tới mức t&ocirc;n gi&aacute;o. Nhưng ngay l&uacute;c đ&oacute;, t&ocirc;i mong c&oacute; thề cắt tĩnh mạch t&ocirc;i ra để tr&uacute;t hết d&ograve;ng m&aacute;u đ&aacute;ng nguyền rủa của &ocirc;ng khỏi cơ thể t&ocirc;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Một giờ sau, khi trời chiều đ&atilde; tối mờ mờ, hai người bọn họ rời nh&agrave; đi trong chiếc xe hơi của cha t&ocirc;i. L&uacute;c ra khỏi nh&agrave;, ch&uacute; Rahim Khan ngồi xổm xuống trước mặt t&ocirc;i v&agrave; trao trả c&aacute;i truyện ngắn của t&ocirc;i c&ugrave;ng một mảnh giấy gập k&iacute;n. Mặt ch&uacute; lo&eacute; s&aacute;ng một nụ cười v&agrave; ch&uacute; nh&aacute;y mắt bảo t&ocirc;i: &quot;Cho ch&aacute;u đấy. Sau h&atilde;y đọc.&quot; Rồi ch&uacute; dừng lại v&agrave; th&ecirc;m một từ duy nhất, sau n&agrave;y đ&atilde; cổ vũ t&ocirc;i theo đuổi việc viết văn, hơn bất kỳ lời ch&uacute;c mừng n&agrave;o m&agrave; bất kỳ chủ bi&ecirc;n n&agrave;o từng d&agrave;nh cho t&ocirc;i. Từ đ&oacute; l&agrave; Hoan h&ocirc;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Khi hai người đi rồi, t&ocirc;i ngồi xuống giường m&igrave;nh v&agrave; ước g&igrave; ch&uacute; Rahim Khan l&agrave; cha t&ocirc;i. Rồi t&ocirc;i nghĩ về Baba v&agrave; bộ ngực lớn vĩ đại của &ocirc;ng, cảm thấy tuyệt vời biết bao khi &ocirc;ng &ocirc;m t&ocirc;i v&agrave;o ngực, người &ocirc;ng sặc m&ugrave;i rượu cất th&ocirc; v&agrave;o buổi s&aacute;ng ra sao, v&agrave; r&acirc;u &ocirc;ng giụi v&agrave;o mặt t&ocirc;i như thế n&agrave;o. T&ocirc;i bỗng cảm thấy v&ocirc; c&ugrave;ng đau đớn v&igrave; c&aacute;i tội lỗi bất ngờ, đến mức phải lao ngay v&agrave;o buồng tắm v&agrave; n&ocirc;n v&agrave;o c&aacute;i chậu.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Khuya đ&ecirc;m đ&oacute;, nằm co tr&ecirc;n giường, t&ocirc;i đọc đi đọc lại bức thư ngắn của ch&uacute; Rahim Khan.Nội dung như sau:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Amir jan!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ch&uacute; th&iacute;ch truyện ngắn của ch&aacute;u lắm. Mashallah 7, Thượng đế đ&atilde; ph&uacute; cho ch&aacute;u một t&agrave;i năng đặc biệt. Bổn phận của ch&uacute; b&acirc;y giờ l&agrave; phải m&agrave;i giũa t&agrave;i năng đ&oacute;, bởi v&igrave; một con người để ph&iacute; t&agrave;i năng Thượng đế ban cho m&igrave;nh, l&agrave; một con lừa. Ch&aacute;u đ&atilde; viết c&acirc;u chuyện của ch&aacute;u bằng một thứ ngữ ph&aacute;p s&aacute;ng sủa v&agrave; một phong c&aacute;ch th&uacute; vị. Nhưng ấn tượng s&acirc;u sắc nhất về truyện ngắn ch&aacute;u viết l&agrave; n&oacute; c&oacute; t&iacute;nh ch&acirc;m biếm. Ch&aacute;u c&oacute; thể c&ograve;n chưa biết từ đ&oacute; nghĩa l&agrave; g&igrave;. Nhưng rồi một ng&agrave;y n&agrave;o đ&oacute; ch&aacute;u sẽ biết. N&oacute; l&agrave; c&aacute;i g&igrave; đ&oacute; m&agrave; một số nh&agrave; văn muốn vươn tới nhưng kh&ocirc;ng bao giờ đạt được. Ch&aacute;u đ&atilde; thực hiện được điều đ&oacute; bằng truyện ngắn đ&acirc;u ti&ecirc;n.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Cửa nh&agrave; ch&uacute; lu&ocirc;n lu&ocirc;n v&agrave; m&atilde;i sẽ rộng mở với ch&aacute;u, Amir jan ạ. Ch&uacute; sẽ đọc bất kỳ truyện ngắn n&agrave;o ch&aacute;u cần chia sẻ với ch&uacute;. Hoan h&ocirc;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Bạn của ch&aacute;u,<br />\r\nRahim</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Phấn chấn bởi bức thư của ch&uacute; Rahim Khan, t&ocirc;i vồ lấy c&aacute;i truyện ngắn v&agrave; chạy vội xuống cầu thang ra tiền sảnh, nơi Hassan v&agrave; &ocirc;ng Ali đang ngủ tr&ecirc;n tấm đệm. Đ&oacute; l&agrave; lần duy nhất họ ngủ trong ng&ocirc;i nh&agrave;, khi Baba đi vắng v&agrave; &ocirc;ng Ali phải tr&ocirc;ng nom t&ocirc;i. T&ocirc;i lay Hassan thức dậy v&agrave; hỏi c&oacute; muốn nghe một truyện kh&ocirc;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Cậu ấy dụi đ&ocirc;i mắt d&iacute;p lại v&igrave; ng&aacute;i ngủ v&agrave; duỗi người ra:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- B&acirc;y giờ ư? Mấy giờ rồi?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Cần g&igrave; mấy giờ. Truyện ngắn n&agrave;y rất đặc biệt. M&igrave;nh tự viết ra đấy, - t&ocirc;i th&igrave; th&agrave;o, mong kh&ocirc;ng l&agrave;m &ocirc;ng Ali thức giấc.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Mặt Hassan s&aacute;ng ngời l&ecirc;n.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thế th&igrave; t&ocirc;i phải nghe rồi, - cậu n&oacute;i v&agrave; tung chăn ra.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i đọc truyện đ&oacute; cho Hassan ở ph&ograve;ng kh&aacute;ch, b&ecirc;n l&ograve; sưởi bằng đ&aacute; cẩm thạch. Lần n&agrave;y th&igrave; kh&ocirc;ng c&oacute; chuyện đọc giả vờ nữa; đ&oacute; l&agrave; truyện của t&ocirc;i. Hassan l&agrave; một th&iacute;nh giả ho&agrave;n hảo về nhiều mặt, ho&agrave;n to&agrave;n ch&igrave;m đắm v&agrave;o c&acirc;u chuyện, n&eacute;t mặt thay đổi theo giọng điệu đổi thay trong c&acirc;u chuyện. Khi t&ocirc;i đọc đến c&acirc;u cuối Hassan vỗ tay kh&ocirc;ng th&agrave;nh tiếng, mặt rạng rỡ hẳn l&ecirc;n:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Mashallah Amir agha! Hoan h&ocirc;!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Cậu th&iacute;ch chứ? - T&ocirc;i hỏi, v&agrave; lần thứ hai cảm thấy hương vị ngọt ng&agrave;o biết mấy - của một lời khen.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Một ng&agrave;y n&agrave;o đ&oacute;, Inshallah 8, cậu sẽ l&agrave; một nh&agrave; văn lớn, - Hassan n&oacute;i, - v&agrave; mọi người tr&ecirc;n to&agrave;n thế giới sẽ đọc những truyện cậu viết.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Hassan, cậu bốc rồi, - t&ocirc;i n&oacute;i, thấy y&ecirc;u Hassan v&igrave; thế.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nhưng cậu khăng khăng:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Kh&ocirc;ng, cậu sẽ vĩ đại v&agrave; nổi tiếng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Rồi Hassan dừng lại, như thể đang định n&oacute;i th&ecirc;m điều g&igrave; đ&oacute;. Cậu ta đắn đo t&igrave;m lời lẽ, rồi hắng giọng, bẽn lẽn hỏi:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Nhưng cậu sẽ cho ph&eacute;p t&ocirc;i hỏi một c&acirc;u về c&aacute;i truyện đ&oacute; chứ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Tất nhi&ecirc;n.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Được... - Cậu bắt đầu, rồi tịt mất.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- N&oacute;i đi, Hassan, - t&ocirc;i mỉm cười bảo, dẫu bất chợt c&aacute;i t&ecirc;n nh&agrave; văn dễ nao n&uacute;ng trong t&ocirc;i kh&ocirc;ng tin lắm c&oacute; muốn nghe kh&ocirc;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Được, nếu cho ph&eacute;p t&ocirc;i hỏi, tại sao người ấy lại phải giết vợ m&igrave;nh? Tại sao &ocirc;ng ta cứ phải bao giờ cũng buồn mới rơi nước mắt? &Ocirc;ng ta kh&ocirc;ng thể chỉ ngửi một củ h&agrave;nh ư?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i sững sờ. C&aacute;i chi tiết cụ thể đ&oacute;, qu&aacute; r&otilde; r&agrave;ng ho&agrave;n to&agrave;n ngớ ngẩn, thế m&agrave; t&ocirc;i lại kh&ocirc;ng nghĩ ra. T&ocirc;i mấp m&aacute;y m&ocirc;i kh&ocirc;ng th&agrave;nh tiếng. C&oacute; vẻ như cũng trong đ&ecirc;m đ&oacute;, t&ocirc;i đ&atilde; học được về một trong những mục ti&ecirc;u viết, ấy l&agrave; ch&acirc;m biếm. T&ocirc;i cũng được biết tới một trong những c&aacute;i bẫy của n&oacute;: Kẽ hở của T&igrave;nh tiết. Được Hassan dạy cho, thật đ&aacute;ng kinh ngạc. Hassan - kẻ kh&ocirc;ng thể đọc nổi v&agrave; chẳng bao giờ viết nổi một từ suốt cuộc đời m&igrave;nh. Một giọng n&oacute;i lạnh l&ugrave;ng v&agrave; đen tối th&igrave; th&agrave;o trong t&ocirc;i, Thằng Hazara m&ugrave; chữ ấy, n&oacute; th&igrave; biết c&aacute;i g&igrave; n&agrave;o? N&oacute; sẽ chẳng l&agrave; g&igrave; ngo&agrave;i việc l&agrave; thằng bếp. Sao n&oacute; lại d&aacute;m ph&ecirc; b&igrave;nh cậu?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Được lắm!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i bắt đầu, nhưng chẳng bao giờ n&oacute;i hết được c&acirc;u ấy. Bởi v&igrave; Afghanistan đ&atilde; bất ngờ biến đổi m&atilde;i m&atilde;i.</span></span></p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(12, 'Không gia đình', 'Văn học nước ngoài', 'Hector Malot', 1878, 'khong-gia-dinh.png', 'Đang cập nhật', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Kh&ocirc;ng gia đ&igrave;nh&nbsp;kể chuyện một em b&eacute; kh&ocirc;ng cha mẹ, kh&ocirc;ng họ h&agrave;ng th&acirc;n th&iacute;ch, đi theo một đo&agrave;n xiếc ch&oacute;, khỉ, rồi cầm đầu đo&agrave;n ấy đi lưu lạc khắp nước Ph&aacute;p, sau đ&oacute; bị t&ugrave; ở Anh, cuối c&ugrave;ng t&igrave;m thấy mẹ v&agrave; em. Em b&eacute; R&ecirc;mi ấy đ&atilde; lớn l&ecirc;n trong gian khổ. Em đ&atilde; chung đụng với mọi hạng người, sống khắp mọi nơi, &quot;nơi th&igrave; lừa đảo, nơi th&igrave; x&oacute;t thương&quot;. Em đ&atilde; lao động m&agrave; sống, l&uacute;c đầu dưới quyền điều khiển của một &ocirc;ng gi&agrave; từng trải v&agrave; đạo đức, cụ Vitali, về sau th&igrave; tự lập v&agrave; kh&ocirc;ng những lo cho m&igrave;nh, c&ograve;n bảo đảm việc biểu diễn v&agrave; sinh sống cho cả một g&aacute;nh h&aacute;t rong. Đ&atilde; c&oacute; khi em v&agrave; cả đo&agrave;n lang thang mấy h&ocirc;m liền kh&ocirc;ng c&oacute; ch&uacute;t g&igrave; trong bụng. Đ&atilde; c&oacute; khi em su&yacute;t chết r&eacute;t. Đ&atilde; c&oacute; khi em bị lụt ngầm ch&ocirc;n trong giếng mỏ mười mấy ng&agrave;y đ&ecirc;m. Đ&atilde; c&oacute; khi em mắc oan bị giải ra trước t&ograve;a v&agrave; bị ở t&ugrave;.&nbsp;V&agrave; cũng c&oacute; khi em được nu&ocirc;i nấng đ&agrave;ng ho&agrave;ng, no ấm. Nhưng d&ugrave; ở đ&acirc;u, trong cảnh ngộ n&agrave;o, em vẫn noi theo nếp r&egrave;n dạy của &ocirc;ng gi&agrave; Vitali giữ phẩm chất l&agrave;m người, nghĩa l&agrave; ngay thẳng, gan dạ, tự trọng, thương người, ham lao động, kh&ocirc;ng ngửa tay xin xỏ, kh&ocirc;ng dối tr&aacute;, gian giảo, nhớ ơn nghĩa, lu&ocirc;n lu&ocirc;n muốn l&agrave;m người c&oacute; &iacute;ch...</span></span></p>\r\n', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>CHƯƠNG I</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i l&agrave; một đứa trẻ người ta nhặt được.&nbsp;Nhưng cho tới l&uacute;c t&aacute;m tuổi t&ocirc;i vẫn tưởng rằng t&ocirc;i c&oacute; một người mẹ bởi v&igrave; mỗi l&uacute;c t&ocirc;i kh&oacute;c lại c&oacute; một người đ&agrave;n b&agrave; nhẹ nh&agrave;ng &ocirc;m siết t&ocirc;i trong hai c&aacute;nh tay v&agrave; ru t&ocirc;i khiến nước mắt t&ocirc;i ngừng chảy.&nbsp;Khi t&ocirc;i đi ngủ kh&ocirc;ng bao giờ b&agrave; kh&ocirc;ng đến &ocirc;m h&ocirc;n t&ocirc;i v&agrave; khi gi&oacute; th&aacute;ng mười hai l&agrave;m tuyết d&aacute;n chặt v&agrave;o c&aacute;c tấm k&iacute;nh cửa sổ trắng x&oacute;a b&agrave; nắm lấy hai b&agrave;n ch&acirc;n t&ocirc;i v&agrave; cứ ngồi sưởi ấm ch&acirc;n t&ocirc;i trong hai b&agrave;n tay b&agrave;, vừa h&aacute;t cho t&ocirc;i nghe một b&agrave;i h&aacute;t m&agrave; đến nay t&ocirc;i c&ograve;n nhớ l&otilde;m b&otilde;m v&agrave;i c&acirc;u. Khi t&ocirc;i c&atilde;i nhau với một đứa bạn b&agrave; lại bảo t&ocirc;i kể cho b&agrave; nghe những nỗi buồn của t&ocirc;i để t&igrave;m lời an ủi hoặc thừa nhận t&ocirc;i c&oacute; l&yacute;.&nbsp;Bằng v&agrave;o những c&aacute;i đ&oacute; v&agrave; nhiều c&aacute;i kh&aacute;c nữa như c&aacute;ch b&agrave; n&oacute;i với t&ocirc;i, nh&igrave;n t&ocirc;i, vuốt ve t&ocirc;i, mắng t&ocirc;i một c&aacute;ch tr&igrave;u mến, t&ocirc;i tin rằng b&agrave; l&agrave; mẹ t&ocirc;i.&nbsp;V&agrave; đ&acirc;y l&agrave; v&igrave; sao t&ocirc;i biết b&agrave; kh&ocirc;ng phải mẹ t&ocirc;i.&nbsp;L&agrave;ng t&ocirc;i, n&oacute;i cho đ&uacute;ng hơn, l&agrave;ng nơi t&ocirc;i được nu&ocirc;i dạy, gọi l&agrave; Chavanon, một trong những l&agrave;ng ngh&egrave;o nhất ở miền Trung nước Ph&aacute;p.&nbsp;Đất rất bạc m&agrave;u, muốn gặt h&aacute;i tốt phải b&oacute;n ph&acirc;n hoặc cho th&ecirc;m chất cải tạo đất m&agrave; ở trong nước kh&ocirc;ng c&oacute;. V&igrave; thế người ta chỉ gặp (hoặc &iacute;t ra l&agrave; ở thời kỳ t&ocirc;i n&oacute;i đến) rất &iacute;t c&aacute;nh đồng c&agrave;y cấy trong khi tr&ocirc;ng thấy nhiều v&ugrave;ng m&ecirc;nh m&ocirc;ng mọc to&agrave;n cỏ thạch thảo v&agrave; c&acirc;y đậu kim. Hết v&ugrave;ng đất to&agrave;n bụi c&acirc;y lại đến v&ugrave;ng đất tru&ocirc;ng.&nbsp;ở đ&uacute;ng v&agrave;o một nếp gấp của v&ugrave;ng đất đ&oacute;, tr&ecirc;n bờ một d&ograve;ng suối l&agrave; nh&agrave; t&ocirc;i, nơi t&ocirc;i sống những năm đầu ti&ecirc;n của cuộc đời. Cho đến l&uacute;c t&aacute;m tuổi t&ocirc;i kh&ocirc;ng bao giờ tr&ocirc;ng thấy đ&agrave;n &ocirc;ng ở trong nh&agrave; n&agrave;y, tuy thế mẹ t&ocirc;i kh&ocirc;ng g&oacute;a chồng, chồng b&agrave; l&agrave; thợ đẽo đ&aacute; chưa trở lại qu&ecirc; hương lần n&agrave;o kể từ khi t&ocirc;i đến tuổi hiểu biết được những g&igrave; xảy ra quanh m&igrave;nh. Chỉ thỉnh thoảng &ocirc;ng mới gửi bạn b&egrave; về l&agrave;ng v&agrave;i mẩu tin.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- M&aacute; Barberin n&agrave;y, &ocirc;ng nh&agrave; b&agrave; khỏe, &ocirc;ng ấy nhờ t&ocirc;i bảo b&agrave; l&agrave; c&ocirc;ng việc vẫn chạy tốt v&agrave; chuyển tiền cho b&agrave; đ&acirc;y n&agrave;y..Chỉ c&oacute; thế.<br />\r\n<br />\r\n&Ocirc;ng Barberin ở Paris l&acirc;u thế ta đừng tưởng v&igrave; &ocirc;ng kh&ocirc;ng th&acirc;n t&igrave;nh với vợ &ocirc;ng m&agrave; &ocirc;ng ở Paris do c&ocirc;ng việc đ&ograve;i hỏi. Khi n&agrave;o gi&agrave; &ocirc;ng sẽ về ở với b&agrave; vợ gi&agrave; của &ocirc;ng, v&agrave; với số tiền ky c&oacute;p được họ sẽ tr&aacute;nh được ngh&egrave;o khổ.&nbsp;Một buổi chiều th&aacute;ng mười một, một người đ&agrave;n &ocirc;ng dừng lại trước h&agrave;ng r&agrave;o nh&agrave; ch&uacute;ng t&ocirc;i v&agrave; hỏi t&ocirc;i c&oacute; phải đ&acirc;y l&agrave; nh&agrave; m&aacute; Barberin kh&ocirc;ng.&nbsp;T&ocirc;i mời &ocirc;ng ta v&agrave;o.&nbsp;&Ocirc;ng đẩy r&agrave;o v&agrave; chậm bước về ph&iacute;a nh&agrave; t&ocirc;i.&nbsp;T&ocirc;i chưa nh&igrave;n thấy ai lấm b&ugrave;n b&ecirc; bết đến thế. H&agrave;ng mảng b&ugrave;n phủ từ ch&acirc;n l&ecirc;n đến đầu &ocirc;ng khiến người ta hiểu ngay &ocirc;ng đ&atilde; đi tr&ecirc;n những con đường rất xấu trong thời gian kh&aacute; d&agrave;i.<br />\r\n<br />\r\nNghe tiếng ch&uacute;ng t&ocirc;i m&aacute; Barberin chạy ra.<br />\r\n- T&ocirc;i mang tin từ Paris về đ&acirc;y.<br />\r\n- A! Trời ơi! - M&aacute; Barberin k&ecirc;u l&ecirc;n. - Tai vạ đến với J&eacute;r&ocirc;me rồi!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Phải đấy, sự thực l&agrave; &ocirc;ng nh&agrave; ta bị thương, chắc sẽ qu&egrave; mất th&ocirc;i. Hiện giờ &ocirc;ng ấy đang nằm bệnh viện. T&ocirc;i nằm cạnh giường &ocirc;ng ấy n&ecirc;n khi ra viện &ocirc;ng ấy nhờ t&ocirc;i qua nh&agrave; nhắn gi&ugrave;m. T&ocirc;i kh&ocirc;ng ở lại được đ&acirc;u v&igrave; c&ograve;n ba dặm nữa phải đi.<br />\r\n<br />\r\nM&aacute; Barberin muốn biết kỹ hơn b&egrave;n mời &ocirc;ng ta ở lại ăn tối. M&aacute; bảo đường xấu v&agrave; nghe n&oacute;i trong rừng c&oacute; ch&oacute; s&oacute;i, s&aacute;ng mai h&atilde;y đi.<br />\r\n<br />\r\n&Ocirc;ng ngồi xuống trong g&oacute;c l&ograve; sưởi vừa ăn vừa kể cho ch&uacute;ng t&ocirc;i nghe tai nạn đ&atilde; xảy ra thế n&agrave;o: &ocirc;ng Barberin bị gi&agrave;n gi&aacute;o đổ đ&egrave; bẹp nửa người v&agrave; v&igrave; kh&ocirc;ng chứng minh được tại sao &ocirc;ng cần phải đứng ở nơi xảy ra tai nạn n&ecirc;n nh&agrave; thầu kh&ocirc;ng chịu bồi thường một đồng n&agrave;o.<br />\r\n<br />\r\n- Con người tội nghiệp ấy kh&ocirc;ng gặp may rồi, &ocirc;ng ta n&oacute;i, bọn l&aacute;u c&aacute; th&igrave; t&igrave;m ngay được c&aacute;ch kiếm lời nhưng &ocirc;ng nh&agrave; b&agrave; th&igrave; kh&ocirc;ng được c&aacute;i g&igrave; hết. Tuy nhi&ecirc;n t&ocirc;i khuy&ecirc;n &ocirc;ng ấy kiện tay thầu kho&aacute;n.<br />\r\n<br />\r\nM&aacute; Barberin định đi Paris.&nbsp;S&aacute;ng h&ocirc;m sau ch&uacute;ng t&ocirc;i xuống l&agrave;ng hỏi &yacute; kiến mục sư. &ocirc;ng mục sư viết thư cho cha tuy&ecirc;n &uacute;y ở bệnh viện &ocirc;ng Barberin nằm v&agrave; v&agrave;i h&ocirc;m sau nhận được trả lời n&oacute;i rằng m&aacute; Barberin kh&ocirc;ng cần l&ecirc;n Paris chỉ cần gửi một m&oacute;n tiền l&ecirc;n cho chồng th&ocirc;i để &ocirc;ng đi kiện nh&agrave; thầu.&nbsp;Ng&agrave;y lại ng&agrave;y, tuần lại tuần cứ thế tr&ocirc;i qua, nhiều thư gửi về, thư n&agrave;o cũng y&ecirc;u cầu gửi tiền th&ecirc;m, l&aacute; thư cuối c&ugrave;ng n&oacute;i rằng nếu kh&ocirc;ng c&oacute; tiền th&igrave; b&aacute;n con b&ograve; Roussette đi..Chỉ những người đ&atilde; sống ở th&ocirc;n qu&ecirc; mới hiểu được cảnh khốn quẫn đau thương trong ba chữ &quot;B&aacute;n con b&ograve;&quot;. Thực tế d&ugrave; ngh&egrave;o t&uacute;ng đến mấy, gia đ&igrave;nh đ&ocirc;ng đến mấy người n&ocirc;ng d&acirc;n vẫn vững t&acirc;m kh&ocirc;ng lo đ&oacute;i nếu nh&agrave; c&oacute; một con b&ograve; c&aacute;i.&nbsp;M&aacute; Barberin v&agrave; t&ocirc;i đ&atilde; nhờ con b&ograve; c&aacute;i của ch&uacute;ng t&ocirc;i m&agrave; sống no đủ, cho tới tận l&uacute;c ấy t&ocirc;i hầu như c&oacute; bao giờ ăn thịt đ&acirc;u.&nbsp;Với lại ch&uacute;ng t&ocirc;i y&ecirc;u con b&ograve; lắm ấy thế m&agrave; nay phải xa n&oacute; rồi.<br />\r\n<br />\r\nMột &ocirc;ng l&aacute;i đến nh&agrave; ch&uacute;ng t&ocirc;i, sau khi xem đi xem lại Roussette v&agrave; nhắc đi nhắc lại h&agrave;ng trăm lần l&agrave; con b&ograve; n&agrave;y kh&ocirc;ng th&iacute;ch hợp với &ocirc;ng, kh&ocirc;ng b&aacute;n lại được cho ai, kh&ocirc;ng c&oacute; sữa, cuối c&ugrave;ng mới n&oacute;i l&agrave; bằng l&ograve;ng mua nhưng chỉ l&agrave; v&igrave; l&ograve;ng tốt muốn gi&uacute;p m&aacute; Barberin m&agrave; th&ocirc;i.&nbsp;Roussette h&igrave;nh như am hiểu nhất định kh&ocirc;ng chịu ra khỏi chuồng.<br />\r\n- Ra ph&iacute;a sau n&oacute; đuổi n&oacute; ra.&nbsp; - Người l&aacute;i n&oacute;i v&agrave; đưa t&ocirc;i c&aacute;i roi.<br />\r\n- L&agrave;m thế kh&ocirc;ng được.- M&aacute; Barberin bảo.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">M&aacute; cầm lấy sợi d&acirc;y dắt, nhẹ nh&agrave;ng bảo con b&ograve;:<br />\r\n- Ra n&agrave;o, ra n&agrave;o, c&ocirc; g&aacute;i đẹp.<br />\r\nV&agrave; Roussette kh&ocirc;ng chống lại nữa, ra tới đường c&aacute;i người l&aacute;i buộc n&oacute; đằng sau chiếc xe.&nbsp;Thế l&agrave; hết sữa, hết bơ. Buổi s&aacute;ng một mẩu b&aacute;nh, buổi chiều khoai t&acirc;y ăn với muối.&nbsp;Sau đ&oacute; kh&ocirc;ng bao l&acirc;u đến ng&agrave;y thứ ba b&eacute;o; năm ngo&aacute;i v&agrave;o ng&agrave;y n&agrave;y m&aacute; Barberin đ&atilde; l&agrave;m cho t&ocirc;i một bữa tiệc c&oacute; b&aacute;nh x&egrave;o, b&aacute;nh tẩm bột r&aacute;n, t&ocirc;i ăn nhiều đến nỗi m&aacute; rất sung sướng.&nbsp;Nhưng l&uacute;c ấy ch&uacute;ng t&ocirc;i c&ograve;n Roussette n&ecirc;n c&oacute; sữa để tẩm bột v&agrave; c&oacute; bơ cho v&agrave;o chảo.&nbsp;Tuy nhi&ecirc;n m&aacute; Barberin đ&atilde; l&agrave;m t&ocirc;i ngạc nhi&ecirc;n; m&aacute; xin h&agrave;ng x&oacute;m nh&agrave; n&agrave;y một ch&eacute;n sữa, nh&agrave; kia một miếng bơ, thế l&agrave; đến trưa về t&ocirc;i thấy m&aacute; đang đổ bột v&agrave;o một chiếc chảo bằng đất.<br />\r\n- ạ n&agrave;y c&oacute; cả bột cơ ạ. - T&ocirc;i vừa n&oacute;i vừa bước lại gần.<br />\r\n- Đ&uacute;ng đấy b&eacute; R&eacute;mi của m&aacute; ạ. Thế người ta l&agrave;m g&igrave; với bột n&agrave;o? - M&aacute; Barberin nh&igrave;n t&ocirc;i hỏi.<br />\r\n- L&agrave;m b&aacute;nh.<br />\r\n- G&igrave; nữa?<br />\r\n- Quấy bột.<br />\r\n- G&igrave; nữa n&agrave;o?<br />\r\n- &ocirc;i trời... con cũng kh&ocirc;ng biết nữa..- Con biết h&ocirc;m nay l&agrave; ng&agrave;y thứ ba b&eacute;o chứ, ng&agrave;y của b&aacute;nh x&egrave;o v&agrave; b&aacute;nh r&aacute;n m&agrave;...<br />\r\n- &ocirc;i, m&aacute; Barberin!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- M&aacute; đ&atilde; thu xếp sao cho ng&agrave;y thứ ba b&eacute;o đối với con kh&ocirc;ng đến nỗi tệ qu&aacute;. Nh&igrave;n v&agrave;o th&ugrave;ng xem n&agrave;o?<br />\r\nT&ocirc;i hăng h&aacute;i nhấc nắp th&ugrave;ng l&ecirc;n, thấy ở trong c&oacute; sữa, bơ v&agrave; ba quả t&aacute;o.<br />\r\n- Đưa trứng cho m&aacute;. - M&aacute; n&oacute;i. - Trong khi m&aacute; đập trứng con gọt t&aacute;o nh&eacute;.<br />\r\nKhi bột đ&atilde; nh&agrave;o xong m&aacute; Barberin đặt chiếc liễn tr&ecirc;n tro n&oacute;ng, thế l&agrave; chỉ c&ograve;n đợi đến chiều nữa th&ocirc;i v&igrave; v&agrave;o bữa tối ch&uacute;ng t&ocirc;i mới ăn b&aacute;nh x&egrave;o v&agrave; b&aacute;nh r&aacute;n.&nbsp;Cuối c&ugrave;ng nến được thắp l&ecirc;n.<br />\r\n- Nh&oacute;m lửa đi con. - M&aacute; Barberin bảo t&ocirc;i.<br />\r\nKh&ocirc;ng cần phải nhắc t&ocirc;i đến lần thứ hai.&nbsp;Chẳng mấy chốc ngọn lửa đ&ugrave;ng đ&ugrave;ng bốc l&ecirc;n ống kh&oacute;i, &aacute;nh s&aacute;ng chập chờn của n&oacute; tỏa khắp gian ph&ograve;ng.&nbsp;M&aacute; Barberin nhấc chiếc chảo r&aacute;n treo tr&ecirc;n tường xuống để n&oacute; l&ecirc;n tr&ecirc;n ngọn lửa.<br />\r\n- Đưa m&aacute; bơ n&agrave;o.<br />\r\nM&aacute; cắt &iacute;t bơ bằng đầu con dao bỏ v&agrave;o chảo.<br />\r\n&aacute;i ch&agrave;! Thơm thật l&agrave; thơm!<br />\r\nTuy nhi&ecirc;n d&ugrave; tập trung ch&uacute; &yacute; đến mấy đi nữa t&ocirc;i vẫn nghe như c&oacute; bước ch&acirc;n ngo&agrave;i s&acirc;n.<br />\r\nAi c&oacute; thể đến v&agrave;o giờ n&agrave;y nhỉ?<br />\r\n- Một chiếc gậy chạm v&agrave;o ngưỡng cửa, cửa mở tung ra.<br />\r\nMột người đ&agrave;n &ocirc;ng bước v&agrave;o, nhờ &aacute;nh s&aacute;ng ngọn lửa t&ocirc;i thấy &ocirc;ng ta mặc một chiếc &aacute;o bờ-lu trắng tay chống một chiếc gậy to.<br />\r\n- ở đ&acirc;y đang l&agrave;m tiệc đấy &agrave;? Cứ y&ecirc;n! - &ocirc;ng ta n&oacute;i bằng một giọng th&ocirc; lỗ.<br />\r\n- A! Trời ơi! - M&aacute; Barberin k&ecirc;u l&ecirc;n, đặt mạnh chiếc chảo xuống đất.<br />\r\nRồi cầm lấy c&aacute;nh tay t&ocirc;i b&agrave; đẩy t&ocirc;i về ph&iacute;a người đ&agrave;n &ocirc;ng đang dừng lại tr&ecirc;n ngưỡng cửa:<br />\r\n- Đ&acirc;y l&agrave; cha con.<br />\r\nT&ocirc;i lại gần để h&ocirc;n &ocirc;ng nhưng &ocirc;ng lấy đầu chiếc gậy ngăn t&ocirc;i lại.<br />\r\n- Thằng n&agrave;y l&agrave; thằng n&agrave;o đ&acirc;y?<br />\r\n&Ocirc;ng bước mấy bước về ph&iacute;a t&ocirc;i, chiếc gậy vẫn giơ l&ecirc;n l&agrave;m t&ocirc;i l&ugrave;i lại.<br />\r\n- &agrave; ra c&aacute;c người l&agrave;m tiệc ng&agrave;y thứ ba b&eacute;o.<br />\r\n- &ocirc;ng ta n&oacute;i. - S&uacute;p g&igrave; đấy?.- Chẳng c&oacute; s&uacute;p g&igrave; cả. Ch&uacute;ng t&ocirc;i c&oacute; chờ &ocirc;ng đ&acirc;u.<br />\r\n&Ocirc;ng ta nh&igrave;n quanh:<br />\r\n- Bơ n&agrave;y, h&agrave;nh n&agrave;y. - &ocirc;ng n&oacute;i. - Bốn năm củ h&agrave;nh với miếng bơ l&agrave; c&oacute; m&oacute;n s&uacute;p ngon rồi c&ograve;n g&igrave;. Ta b&oacute;c h&agrave;nh đi.<br />\r\nM&aacute; Barberin vội l&agrave;m theo y&ecirc;u cầu của chồng trong khi &ocirc;ng ta ngồi v&agrave;o chiếc ghế d&agrave;i ở g&oacute;c l&ograve; sưởi.&nbsp;T&ocirc;i kh&ocirc;ng d&aacute;m rời nơi chiếc gậy đ&atilde; đưa t&ocirc;i tới. Dựa v&agrave;o b&agrave;n, t&ocirc;i nh&igrave;n kỹ người kh&aacute;ch mới tới n&agrave;y.&nbsp;Đ&oacute; l&agrave; một người đ&agrave;n &ocirc;ng độ năm mươi tuổi mặt th&ocirc; n&eacute;t đanh lại, đầu ngoẹo sang vai phải sau chấn thương vừa rồi.&nbsp;<br />\r\n- B&agrave; định nấu s&uacute;p cho ch&uacute;ng t&ocirc;i với mẩu bơ n&agrave;y chứ g&igrave;?<br />\r\nTự m&igrave;nh bưng chiếc đĩa tr&ecirc;n c&oacute; miếng bơ &ocirc;ng ta đổ tọt miếng bơ v&agrave;o trong chảo.&nbsp;Hết bơ, c&ograve;n g&igrave; l&agrave; b&aacute;nh x&egrave;o nữa cơ chứ.&nbsp;Gi&aacute; như v&agrave;o l&uacute;c kh&aacute;c hẳn t&ocirc;i phải x&oacute;t xa với tai họa n&agrave;y lắm nhưng l&uacute;c n&agrave;y &yacute; nghĩ người đ&agrave;n &ocirc;ng ấy l&agrave; cha t&ocirc;i chiếm cứ cả t&acirc;m hồn t&ocirc;i.&nbsp;T&ocirc;i chưa bao giờ tự hỏi m&igrave;nh một c&aacute;ch cụ thể thế n&agrave;o l&agrave; một người cha, nh&igrave;n con người bỗng nhi&ecirc;n từ tr&ecirc;n trời rơi xuống n&agrave;y t&ocirc;i cảm thấy sợ h&atilde;i.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- M&agrave;y đừng đứng im thế, &ocirc;ng ta n&oacute;i, đi m&agrave; dọn đĩa l&ecirc;n b&agrave;n đi chứ.<br />\r\nT&ocirc;i vội v&acirc;ng lời. S&uacute;p đ&atilde; nấu xong. M&aacute; Barberin m&uacute;c s&uacute;p ra.&nbsp;Đến l&uacute;c đ&oacute; &ocirc;ng ta mới ra b&agrave;n ngồi v&agrave; bắt đầu ăn, chỉ thỉnh thoảng mới dừng lại nh&igrave;n t&ocirc;i.&nbsp;T&ocirc;i hoang mang qu&aacute; ăn kh&ocirc;ng nổi.<br />\r\n- B&igrave;nh thường n&oacute; c&oacute; ăn &iacute;t như thế n&agrave;y kh&ocirc;ng?<br />\r\n- &agrave;, vẫn thế đấy.<br />\r\n- Kệ x&aacute;c n&oacute;! Gi&aacute; như n&oacute; kh&ocirc;ng ăn th&igrave; c&agrave;ng tốt!<br />\r\nDĩ nhi&ecirc;n t&ocirc;i kh&ocirc;ng n&oacute;i g&igrave;, m&aacute; Barberin cũng vậy, m&aacute; cứ đi lại quanh b&agrave;n chăm ch&uacute; phục vụ chồng.<br />\r\n- M&agrave;y kh&ocirc;ng đ&oacute;i &agrave;? - &ocirc;ng ta hỏi t&ocirc;i.<br />\r\n- Kh&ocirc;ng ạ.<br />\r\n- Thế th&igrave; đi ngủ đi, v&agrave; ngủ ngay đi.<br />\r\nGiống như trong đa số nh&agrave; n&ocirc;ng d&acirc;n, căn bếp của ch&uacute;ng t&ocirc;i cũng đồng thời l&agrave; ph&ograve;ng ngủ.&nbsp;T&ocirc;i vội v&agrave;ng thay quần &aacute;o đi ngủ. Nhưng ngủ được hay kh&ocirc;ng lại l&agrave; một chuyện kh&aacute;c. T&ocirc;i kh&ocirc;ng buồn ngủ!.Người đ&agrave;n &ocirc;ng ấy l&agrave; cha t&ocirc;i! Thế th&igrave; tại sao &ocirc;ng đối với t&ocirc;i nghiệt ng&atilde; như thế?&nbsp;D&aacute;n mũi v&agrave;o tường t&ocirc;i cố xua đuổi những &yacute; nghĩ tr&ecirc;n m&agrave; kh&ocirc;ng được.&nbsp;Sau một l&uacute;c t&ocirc;i nghe c&oacute; người đến gần giường t&ocirc;i.&nbsp;Nghe tiếng bước ch&acirc;n chậm chạp, k&eacute;o l&ecirc; v&agrave; nặng nề t&ocirc;i nhận ra ngay kh&ocirc;ng phải m&aacute; Barberin.&nbsp;Một hơi thở n&oacute;ng hổi lướt tr&ecirc;n t&oacute;c t&ocirc;i.<br />\r\n- M&agrave;y ngủ chưa? - Một giọng ngh&egrave;n nghẹn hỏi t&ocirc;i.<br />\r\nT&ocirc;i cẩn thận kh&ocirc;ng trả lời.<br />\r\n- N&oacute; ngủ rồi, m&aacute; Barberin n&oacute;i, n&oacute; c&oacute; th&oacute;i quen nằm xuống l&agrave; ngủ, &ocirc;ng c&oacute; thể n&oacute;i được.<br />\r\nC&oacute; lẽ t&ocirc;i phải n&oacute;i l&agrave; t&ocirc;i chưa ngủ, nhưng t&ocirc;i kh&ocirc;ng d&aacute;m.<br />\r\n- Vụ kiện của &ocirc;ng đến đ&acirc;u rồi? - M&aacute; Barberin hỏi.<br />\r\n- Thua rồi! C&aacute;c quan t&ograve;a đều bảo rằng lỗi ở t&ocirc;i.<br />\r\nN&oacute;i đến đ&acirc;y &ocirc;ng đấm một c&aacute;i xuống b&agrave;n.<br />\r\n- Kiện thua, &ocirc;ng n&oacute;i tiếp, tiền mất, qu&egrave; quặt, đ&oacute;i ngh&egrave;o. Như thế chưa đủ, về đến đ&acirc;y lại c&ograve;n thấy một đứa trẻ con nữa. B&agrave; h&atilde;y giải th&iacute;ch cho t&ocirc;i v&igrave; sao kh&ocirc;ng l&agrave;m theo lời t&ocirc;i bảo?<br />\r\n- V&igrave; t&ocirc;i kh&ocirc;ng thể l&agrave;m được. Người ta kh&ocirc;ng thể bỏ một đứa trẻ nu&ocirc;i bằng ch&iacute;nh sữa m&igrave;nh.<br />\r\n- C&oacute; phải con b&agrave; đ&acirc;u.<br />\r\n- Cuối c&ugrave;ng t&ocirc;i cũng muốn l&agrave;m theo lời &ocirc;ng bảo đấy nhưng đ&uacute;ng l&uacute;c ấy th&igrave; n&oacute; ốm, kh&ocirc;ng phải l&uacute;c đem n&oacute; đến trại trẻ v&ocirc; thừa nhận được.<br />\r\n- Thế n&oacute; khỏi khi n&agrave;o?<br />\r\n- Sau trận ốm đ&oacute; lại đến trận kh&aacute;c, n&oacute; ho gh&ecirc; lắm, thằng b&eacute; tội nghiệp, ho đến l&agrave;m n&aacute;t l&ograve;ng người ta ra được. Chẳng phải v&igrave; ho m&agrave; thằng Nicolas của ch&uacute;ng ta đ&atilde; chết đấy ư? T&ocirc;i tưởng như đem n&oacute; l&ecirc;n tỉnh n&oacute; cũng sẽ chết như thế.<br />\r\n- Nhưng sau đ&oacute;?<br />\r\n- Thời gian cứ dần tr&ocirc;i.<br />\r\n- N&oacute; l&ecirc;n mấy rồi?<br />\r\n- L&ecirc;n t&aacute;m.<br />\r\n- Th&igrave; n&oacute; sẽ đến nơi n&oacute; phải đến v&agrave;o l&uacute;c l&ecirc;n t&aacute;m.<br />\r\n- A! J&eacute;r&ocirc;me, &ocirc;ng kh&ocirc;ng định l&agrave;m điều đ&oacute; đấy chứ?<br />\r\n- Ai ngăn cản được t&ocirc;i n&agrave;o?.C&oacute; một l&uacute;c im lặng v&agrave; t&ocirc;i thở được, x&uacute;c động l&agrave;m t&ocirc;i nghẹn ng&agrave;o đến tắc thở.<br />\r\nChẳng mấy chốc m&aacute; Barberin n&oacute;i tiếp:<br />\r\n- Paris đ&atilde; l&agrave;m &ocirc;ng thay đổi rồi!<br />\r\n- C&oacute; lẽ thế. Nhưng c&oacute; điều chắc chắn l&agrave; n&oacute; đ&atilde; l&agrave;m t&ocirc;i qu&egrave;. L&agrave;m sao kiếm sống b&acirc;y giờ? Ta đ&acirc;u c&ograve;n tiền nữa. Chẳng lẽ trong khi m&igrave;nh kh&ocirc;ng c&oacute; g&igrave; ăn lại c&ograve;n phải nu&ocirc;i th&ecirc;m một đứa trẻ kh&ocirc;ng phải con m&igrave;nh?<br />\r\n- N&oacute; l&agrave; con t&ocirc;i.<br />\r\n- Chẳng phải con b&agrave; cũng như kh&ocirc;ng phải con t&ocirc;i vậy. N&oacute; kh&ocirc;ng phải một đứa trẻ con nh&agrave; n&ocirc;ng. T&ocirc;i đ&atilde; nh&igrave;n n&oacute; trong bữa tối: n&oacute; mong manh lắm.<br />\r\n- Đ&oacute; l&agrave; một đứa trẻ trung hậu. Sau n&agrave;y n&oacute; sẽ l&agrave;m việc cho ch&uacute;ng ta.<br />\r\n- Nhưng trong khi chờ đợi ta phải l&agrave;m việc để nu&ocirc;i n&oacute;.<br />\r\n- Nếu cha mẹ n&oacute; đ&ograve;i th&igrave; sao?<br />\r\n- Nếu thế hẳn họ đ&atilde; đi t&igrave;m n&oacute;. C&oacute; lẽ họ đ&atilde; chết.<br />\r\n- Nhưng họ c&ograve;n sống th&igrave; sao? Một ng&agrave;y kia họ đến hỏi ta th&igrave; sao?<br />\r\n- Ta đưa họ đến trại trẻ chứ sao? Th&ocirc;i t&ocirc;i đến ch&agrave;o Francois đ&acirc;y.<br />\r\nCửa ra v&agrave;o mở ra rồi đ&oacute;ng lại. &ocirc;ng ta đi mất.&nbsp;Thế l&agrave; t&ocirc;i nhỏm dậy, gọi m&aacute; Barberin.&nbsp;M&aacute; chạy đến ch&acirc;n giường t&ocirc;i.<br />\r\n- M&aacute; kh&ocirc;ng để con đi đến trại trẻ v&ocirc; thừa nhận chứ?<br />\r\n- Kh&ocirc;ng, b&eacute; R&eacute;mi của m&aacute; ạ.<br />\r\nV&agrave; m&aacute; &ocirc;m h&ocirc;n t&ocirc;i, gh&igrave; chặt lấy t&ocirc;i trong hai c&aacute;nh tay.&nbsp;Sự &acirc;u yếm của m&aacute; l&agrave;m t&ocirc;i can đảm l&ecirc;n.<br />\r\n- Thế ra con đ&atilde; nghe thấy hết những điều J&eacute;r&ocirc;me n&oacute;i rồi ư?<br />\r\n- V&acirc;ng, m&aacute; kh&ocirc;ng phải m&aacute; con, &ocirc;ng ấy kh&ocirc;ng phải cha con.<br />\r\nT&ocirc;i kh&ocirc;ng n&oacute;i hai c&acirc;u tr&ecirc;n bằng c&ugrave;ng một giọng, bởi v&igrave; nếu như t&ocirc;i khổ t&acirc;m biết m&aacute; kh&ocirc;ng phải mẹ t&ocirc;i, t&ocirc;i lại mừng v&igrave; biết &ocirc;ng ta kh&ocirc;ng phải cha t&ocirc;i.<br />\r\n- C&oacute; lẽ m&aacute; n&ecirc;n cho con biết sự thật th&igrave; hơn.<br />\r\n- M&aacute; Barberin n&oacute;i. Kh&ocirc;ng ai biết mẹ con l&agrave; ai cả. B&agrave; c&ograve;n sống hay kh&ocirc;ng cũng kh&ocirc;ng ai biết.<br />\r\nMột buổi s&aacute;ng ở Paris tr&ecirc;n đường đi l&agrave;m J&eacute;r&ocirc;me.qua đại lộ Breteuil v&agrave; nghe thấy tiếng trẻ con kh&oacute;c. Tiếng kh&oacute;c như vẳng ra từ một khe cửa vườn. L&uacute;c đ&oacute; v&agrave;o th&aacute;ng hai. &ocirc;ng lại gần v&agrave; thấy một em b&eacute; nằm tr&ecirc;n ngưỡng cửa. J&eacute;r&ocirc;me rất l&uacute;ng t&uacute;ng b&egrave;n bế b&eacute; đến sở cảnh s&aacute;t. ở sở cảnh s&aacute;t người ta mở t&atilde; l&oacute;t b&eacute; ra trước lửa sưởi. Đ&oacute; l&agrave; một em b&eacute; khoảng năm s&aacute;u th&aacute;ng, t&atilde; l&oacute;t chứng tỏ em l&agrave; con nh&agrave; gi&agrave;u c&oacute;. &ocirc;ng cảnh s&aacute;t giải th&iacute;ch c&oacute; lẽ b&eacute; bị người ta ăn cắp rồi bỏ đấy. &ocirc;ng cũng n&oacute;i sẽ gửi b&eacute; đến trại trẻ nhặt được nếu kh&ocirc;ng ai nhận tr&ocirc;ng nom b&eacute;. Cha mẹ em b&eacute; thế n&agrave;o cũng đi t&igrave;m, c&oacute; người tr&ocirc;ng nom b&eacute; th&igrave; họ sẽ trọng thưởng. J&eacute;r&ocirc;me b&egrave;n n&oacute;i muốn nhận tr&ocirc;ng nom b&eacute;, họ trao b&eacute; cho &ocirc;ng. M&aacute; cũng c&oacute; một đứa con trai bằng tuổi ấy. Thế l&agrave; m&aacute; trở th&agrave;nh mẹ con. Con m&aacute; chết, m&aacute; c&agrave;ng gắn b&oacute; với con hơn. M&aacute; qu&ecirc;n hẳn con kh&ocirc;ng phải con ruột của m&aacute;. Kh&ocirc;ng may J&eacute;r&ocirc;me kh&ocirc;ng qu&ecirc;n. Ba th&aacute;ng sau thấy cha mẹ con kh&ocirc;ng t&igrave;m con, &ocirc;ng đ&atilde; muốn đem con v&agrave;o trại trẻ.<br />\r\n- &ocirc;i! Đừng v&agrave;o trại trẻ! - T&ocirc;i k&ecirc;u l&ecirc;n.<br />\r\n- Con sẽ kh&ocirc;ng đi trại trẻ. J&eacute;r&ocirc;me kh&ocirc;ng phải người &aacute;c, chỉ tại buồn phiền ngh&egrave;o kh&oacute; l&agrave;m &ocirc;ng ta đ&acirc;m ra như thế m&agrave; th&ocirc;i. Ch&uacute;ng ta sẽ l&agrave;m việc, cả con cũng sẽ l&agrave;m việc.<br />\r\n- V&acirc;ng, m&aacute; muốn g&igrave; con l&agrave;m nấy. Miễn l&agrave; đừng v&agrave;o trại trẻ.<br />\r\nSau khi &ocirc;m h&ocirc;n t&ocirc;i m&aacute; quay mặt t&ocirc;i v&agrave;o tường. T&ocirc;i muốn ngủ nhưng v&igrave; qu&aacute; xao động n&ecirc;n kh&ocirc;ng sao ngủ được.&nbsp;T&ocirc;i kh&ocirc;ng muốn đi trại trẻ... T&ocirc;i sợ &ocirc;ng Barberin... Cuối c&ugrave;ng th&igrave; t&ocirc;i cũng ngủ được v&agrave; ngủ suốt đ&ecirc;m. S&aacute;ng h&ocirc;m sau động t&aacute;c đầu ti&ecirc;n của t&ocirc;i l&agrave; nh&igrave;n quanh để biết chắc chắn l&agrave; họ kh&ocirc;ng đem t&ocirc;i đi.&nbsp;Suốt buổi s&aacute;ng Barberin kh&ocirc;ng n&oacute;i g&igrave; với t&ocirc;i cả v&agrave; t&ocirc;i bắt đầu tin rằng &ocirc;ng ta đ&atilde; qu&ecirc;n kế hoạch của m&igrave;nh.&nbsp;Đến giữa trưa th&igrave; &ocirc;ng ta bảo t&ocirc;i đội mũ đi theo &ocirc;ng.&nbsp;T&ocirc;i sợ qu&aacute; đưa mắt về ph&iacute;a m&aacute; Barberin. M&aacute; trộm lấy tay ra hiệu bảo t&ocirc;i cứ y&ecirc;n t&acirc;m.&nbsp;Thế l&agrave; t&ocirc;i l&ecirc;n đường.&nbsp;Từ nh&agrave; t&ocirc;i đến l&agrave;ng kh&aacute; xa, đi bộ phải mất h&agrave;ng tiếng đồng hồ. Suốt trong tiếng đồng hồ đ&oacute; Barberin kh&ocirc;ng hề n&oacute;i với t&ocirc;i một lần n&agrave;o.&nbsp;&Ocirc;ng ta đi trước, khập kh&agrave; khập khiễng, thỉnh thoảng lại quay lại nh&igrave;n xem t&ocirc;i c&oacute; đi theo kh&ocirc;ng..ạng ta đưa t&ocirc;i đi đ&acirc;u? C&acirc;u hỏi n&agrave;y l&agrave;m t&ocirc;i lo lắng, t&ocirc;i nghĩ đến bỏ trốn.&nbsp;Với mục đ&iacute;ch đ&oacute; t&ocirc;i cố chần chừ lại sau.&nbsp;T&ocirc;i định khi n&agrave;o ở xa &ocirc;ng ta t&ocirc;i sẽ nhảy xuống hố, &ocirc;ng ta sẽ chịu kh&ocirc;ng t&oacute;m được t&ocirc;i.&nbsp;Đầu ti&ecirc;n &ocirc;ng ta bằng l&ograve;ng bảo t&ocirc;i đi đằng sau, nhưng chẳng mấy chốc &ocirc;ng đo&aacute;n ra &yacute; định của t&ocirc;i n&ecirc;n nắm lấy cổ tay t&ocirc;i.&nbsp;Cứ thế ch&uacute;ng t&ocirc;i đi v&agrave;o l&agrave;ng.&nbsp;Khi đi qua qu&aacute;n c&agrave;-ph&ecirc;, c&oacute; người gọi Barberin v&agrave; mời &ocirc;ng ta v&agrave;o. Barberin nắm tai t&ocirc;i, đẩy t&ocirc;i đi trước, v&agrave; khi ch&uacute;ng t&ocirc;i đ&atilde; v&agrave;o trong qu&aacute;n, &ocirc;ng đ&oacute;ng cửa lại. Trong khi &ocirc;ng ngồi ở một chiếc b&agrave;n với &ocirc;ng chủ qu&aacute;n c&agrave; ph&ecirc;, t&ocirc;i tới ngồi b&ecirc;n l&ograve; sưởi.&nbsp;ở g&oacute;c đối diện nơi t&ocirc;i ngồi c&oacute; một cụ gi&agrave; cao lớn r&acirc;u bạc trắng, mặc một bộ quần &aacute;o rất kỳ quặc. B&ecirc;n cạnh cụ c&oacute; ba con ch&oacute;: một con ch&oacute; b&ocirc;ng trắng, một con ch&oacute; b&aacute;c-be đen v&agrave; một con ch&oacute; c&aacute;i con nom vừa ranh ma vừa dịu d&agrave;ng.&nbsp;Trong khi t&ocirc;i ngắm cụ gi&agrave;, Barberin th&igrave; thầm chuyện tr&ograve; với &ocirc;ng chủ qu&aacute;n. &ocirc;ng ta bảo &ocirc;ng chủ qu&aacute;n l&agrave; ch&uacute;ng t&ocirc;i từ l&agrave;ng l&ecirc;n t&igrave;m &ocirc;ng x&atilde; trưởng để nhờ &ocirc;ng n&agrave;y y&ecirc;u cầu trại trẻ trả cho &ocirc;ng ta tiền trợ cấp nu&ocirc;i t&ocirc;i.&nbsp;Đ&oacute; l&agrave; điều m&aacute; Barberin y&ecirc;u cầu được &ocirc;ng chồng đ&acirc;y...<br />\r\nCụ gi&agrave; cũng nghe tuy l&agrave;m ra vẻ kh&ocirc;ng nghe thấy g&igrave;. Bỗng nhi&ecirc;n cụ chỉ tay về ph&iacute;a t&ocirc;i v&agrave; hỏi Barberin:<br />\r\n- Thằng b&eacute; n&agrave;y l&agrave;m &ocirc;ng vướng v&iacute;u ư? V&agrave; &ocirc;ng tưởng rằng ch&iacute;nh quyền sẽ trả cho &ocirc;ng tiền trợ cấp ư?<br />\r\n- Chứ c&ograve;n g&igrave; nữa! N&oacute; kh&ocirc;ng c&oacute; cha mẹ, t&ocirc;i nu&ocirc;i n&oacute;, vậy phải c&oacute; người chi tiền cho n&oacute; chứ?<br />\r\n- T&ocirc;i kh&ocirc;ng bảo l&agrave; kh&ocirc;ng nhưng t&ocirc;i tin l&agrave; &ocirc;ng kh&ocirc;ng đạt được điều &ocirc;ng y&ecirc;u cầu.<br />\r\n- Thế th&igrave; n&oacute; sẽ v&agrave;o trại trẻ, chẳng c&oacute; luật n&agrave;o bắt n&oacute; phải ở trong nh&agrave; t&ocirc;i cả.<br />\r\n- Ng&agrave;y xưa &ocirc;ng đ&atilde; bằng l&ograve;ng nhận n&oacute; về, c&oacute; nghĩa đ&atilde; cam kết giữ n&oacute;.<br />\r\n- T&ocirc;i kh&ocirc;ng giữ n&oacute; nữa.<br />\r\n- C&oacute; lẽ c&oacute; thể c&oacute; c&aacute;ch giũ n&oacute; ra được, m&agrave; biết đ&acirc;u c&ograve;n kiếm ch&aacute;c được ch&uacute;t g&igrave; nữa đấy &ocirc;ng ạ. - Cụ gi&agrave; n&oacute;i sau một l&aacute;t suy nghĩ.<br />\r\nRời chiếc ghế dựa của m&igrave;nh cụ tới ngồi trước mặt Barberin..- Để t&ocirc;i nhận n&oacute;, cụ n&oacute;i, t&ocirc;i thu&ecirc; n&oacute;. Mỗi năm t&ocirc;i trả &ocirc;ng hai mươi phrăng. Gi&aacute; cao đấy v&agrave; t&ocirc;i trả trước.<br />\r\n- Nếu t&ocirc;i giữ n&oacute;, trại trẻ c&oacute; thể trả t&ocirc;i mỗi th&aacute;ng hơn mười phrăng.<br />\r\n- Thế nếu trại trẻ đ&aacute;ng lẽ để &ocirc;ng giữ lại đem n&oacute; cho người kh&aacute;c th&igrave; sao, thế l&agrave; &ocirc;ng chẳng được g&igrave; hết, trong khi với t&ocirc;i &ocirc;ng chỉ việc đưa tay ra.<br />\r\nCụ gi&agrave; lục t&uacute;i lấy ra một t&uacute;i tiền bằng da lấy ra bốn đồng bạc b&agrave;y l&ecirc;n b&agrave;n v&agrave; l&agrave;m ch&uacute;ng k&ecirc;u lanh canh.<br />\r\n- Cụ thử nghĩ xem, Barberin k&ecirc;u l&ecirc;n, một ng&agrave;y n&agrave;o đ&oacute; đứa b&eacute; tất phải c&oacute; cha mẹ! L&uacute;c đ&oacute; ai nu&ocirc;i n&oacute; hẳn sẽ c&oacute; lời. Nếu kh&ocirc;ng t&iacute;nh đến chuyện đ&oacute; h&agrave; tất t&ocirc;i đ&atilde; nhận tr&ocirc;ng nom n&oacute;.<br />\r\nC&acirc;u n&oacute;i của Barberin l&agrave;m t&ocirc;i gh&eacute;t &ocirc;ng ta th&ecirc;m ch&uacute;t nữa.<br />\r\n- Th&igrave; ch&iacute;nh v&igrave; &ocirc;ng kh&ocirc;ng tr&ocirc;ng mong g&igrave; ở cha mẹ n&oacute;, cụ gi&agrave; n&oacute;i, n&ecirc;n &ocirc;ng mới đuổi n&oacute;.<br />\r\nCụ ranh m&atilde;nh nh&igrave;n Barberin.<br />\r\n- Đứa b&eacute; n&agrave;y sẽ đi c&ugrave;ng với t&ocirc;i, cụ n&oacute;i, t&ocirc;i gi&agrave; rồi, sau một ng&agrave;y mệt nhọc, thời tiết xấu, t&ocirc;i thường c&oacute; những &yacute; nghĩ u &aacute;m, n&oacute; sẽ l&agrave;m t&ocirc;i vui l&ecirc;n. N&oacute; sẽ tham gia g&aacute;nh h&aacute;t của xi-nho Vitalis.<br />\r\n- G&aacute;nh h&aacute;t của cụ ở đ&acirc;u?<br />\r\n- Xi-nho Vitalis l&agrave; t&ocirc;i, c&ograve;n g&aacute;nh h&aacute;t, đ&acirc;y.<br />\r\nN&oacute;i rồi cụ mở &aacute;o măng-t&ocirc; lấy ra một con vật kỳ lạ đang &aacute;p người v&agrave;o ngực cụ.&nbsp;T&ocirc;i kh&ocirc;ng biết gọi con vật kỳ qu&aacute;i n&agrave;y l&agrave; g&igrave; v&igrave; đ&acirc;y l&agrave; lần đầu ti&ecirc;n t&ocirc;i nh&igrave;n thấy n&oacute;.<br />\r\n- A! Con khỉ xấu x&iacute;! - Barberin k&ecirc;u.<br />\r\nTiếng đ&oacute; l&agrave;m t&ocirc;i hết ngạc nhi&ecirc;n bởi v&igrave; tuy chưa nh&igrave;n thấy con khỉ bao giờ &iacute;t nhất t&ocirc;i cũng đ&atilde; nghe n&oacute;i tới.<br />\r\n- Đ&acirc;y l&agrave; ng&agrave;i Joli - Coeur. Bạn ơi h&atilde;y ch&agrave;o đi n&agrave;o.<br />\r\nJoli - Coeur đưa b&agrave;n tay kh&eacute;p chặt l&ecirc;n m&ocirc;i v&agrave; gửi ch&uacute;ng t&ocirc;i một c&aacute;i h&ocirc;n.<br />\r\n- B&acirc;y giờ, - vừa n&oacute;i cụ Vitalis vừa đưa tay về ph&iacute;a con ch&oacute; b&ocirc;ng trắng, - &ocirc;ng Capi đ&acirc;y sẽ h&acirc;n hạnh giới thiệu c&aacute;c bạn &ocirc;ng với cử tọa k&iacute;nh mến.<br />\r\nNghe lệnh, con ch&oacute; b&ocirc;ng đứng ngay dậy, bằng hai ch&acirc;n sau, khoanh hai ch&acirc;n trước lại trước ngực, c&uacute;i ch&agrave;o chủ m&igrave;nh thật thấp. Hai con ch&oacute; kia, nh&igrave;n chăm chăm v&agrave;o Capi cũng lập tức đứng l&ecirc;n, mỗi con giơ một ch&acirc;n ra trước tựa như đưa tay cho người ta bắt, bước s&aacute;u bước ra trước rồi l&ugrave;i ba bước ra sau, c&uacute;i ch&agrave;o.<br />\r\n- Capi, cho ta biết mấy giờ rồi.<br />\r\nCapi kh&ocirc;ng khoanh tay nữa, lại gần chủ vạch &aacute;o măng-t&ocirc; lục trong t&uacute;i &aacute;o gi-l&ecirc; l&ocirc;i ra một c&aacute;i đồng hồ quả qu&yacute;t, nh&igrave;n v&agrave;o mặt đồng hồ rồi sủa l&ecirc;n hai tiếng to sau đ&oacute; đến ba tiếng nhỏ hơn. Quả l&uacute;c đ&oacute; l&agrave; hai giờ ba khắc.<br />\r\n- Tốt. B&acirc;y giờ đề nghị con mời n&agrave;ng Dolce ra nhảy cho ch&uacute;ng ta xem n&agrave;o.<br />\r\nCapi lập tức lục t&uacute;i &aacute;o v&eacute;t của chủ l&ocirc;i ra một đoạn d&acirc;y thừng. N&oacute; ra hiệu cho Zerbino, con ch&oacute; b&aacute;c-be nhỏ, thế l&agrave; con n&agrave;y ra đứng ngay trước mặt Capi. Capi b&egrave;n n&eacute;m cho n&oacute; một đầu d&acirc;y v&agrave; hai con bắt đầu quay d&acirc;y. Dolce lao v&agrave;o v&ograve;ng quay vừa nhảy vừa nh&igrave;n chủ.&nbsp;R&otilde; r&agrave;ng l&agrave; học tr&ograve; cụ Vitalis thật nực cười v&agrave; cứ đi chơi suốt như thế hẳn phải th&uacute; vị, nhưng nếu đi theo họ t&ocirc;i phải xa m&aacute; Barberin.&nbsp;<br />\r\n- B&acirc;y giờ, cụ Vitalis n&oacute;i tiếp, trở lại c&ocirc;ng việc của ch&uacute;ng ta. T&ocirc;i trả &ocirc;ng ba mươi phrăng.<br />\r\n- Bốn mươi.<br />\r\nBắt đầu b&agrave;n bạc. Cụ Vitalis ngắt lời:<br />\r\n- Thằng b&eacute; ngồi đ&acirc;y chắc ch&aacute;n lắm, cho n&oacute; đi ra ngo&agrave;i s&acirc;n chơi đi.<br />\r\nĐồng thời cụ l&agrave;m hiệu cho Barberin.<br />\r\n- Phải đấy, &ocirc;ng n&agrave;y n&oacute;i, ra s&acirc;n chơi đi bao giờ tao gọi h&atilde;y v&agrave;o.<br />\r\nT&ocirc;i đi ra s&acirc;n, ngồi tr&ecirc;n một tảng đ&aacute; suy nghĩ. Số phận t&ocirc;i đang được quyết định đ&acirc;y.&nbsp;Lạnh v&agrave; lo lắng l&agrave;m t&ocirc;i run l&ecirc;n cầm cập. Cuộc b&agrave;n bạc k&eacute;o d&agrave;i kh&aacute; l&acirc;u. Cuối c&ugrave;ng t&ocirc;i tr&ocirc;ng thấy mỗi một m&igrave;nh Barberin. &ocirc;ng ta ra t&igrave;m t&ocirc;i để giao cho cụ Vitalis chắc?<br />\r\n- N&agrave;o, đi về nh&agrave; n&agrave;o. - &ocirc;ng ta n&oacute;i.<br />\r\nVề nh&agrave; ư? Kh&ocirc;ng phải xa m&aacute; Barberin ư?<br />\r\nT&ocirc;i muốn hỏi nhưng kh&ocirc;ng d&aacute;m.<br />\r\n- Mười ph&uacute;t trước khi tới nh&agrave;, Barberin dừng lại:<br />\r\n- M&agrave;y m&agrave; kể lại một lời n&agrave;o m&agrave;y nghe thấy, &ocirc;ng ta vừa n&oacute;i vừa v&eacute;o mạnh tai t&ocirc;i, th&igrave; m&agrave;y sẽ phải trả gi&aacute; đắt đấy. Liệu hồn!.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>CHƯƠNG II</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">N&agrave;o! Thế &ocirc;ng x&atilde; trưởng bảo thế n&agrave;o n&agrave;o?<br />\r\n- M&aacute; Barberin hỏi khi ch&uacute;ng t&ocirc;i về tới nh&agrave;.<br />\r\n- Kh&ocirc;ng gặp. Mai ch&uacute;ng t&ocirc;i quay lại.<br />\r\nNhư thế l&agrave; Barberin đ&atilde; từ bỏ giao k&egrave;o mua b&aacute;n của l&atilde;o ta. Suốt dọc đường t&ocirc;i đ&atilde; nhiều lần tự hỏi đi về nh&agrave; như thế n&agrave;y liệu l&atilde;o c&oacute; giở tr&ograve; mưu mẹo g&igrave; kh&ocirc;ng, c&acirc;u n&oacute;i vừa rồi của l&atilde;o ph&aacute; tan mọi nghi ngờ.&nbsp;Nhưng s&aacute;ng h&ocirc;m sau, khi tỉnh dậy t&ocirc;i kh&ocirc;ng thấy m&aacute; Barberin đ&acirc;u cả.<br />\r\n- M&aacute; đ&acirc;u?<br />\r\n- M&aacute; ra l&agrave;ng, chiều mới về.<br />\r\nSự vắng mặt n&agrave;y l&agrave;m t&ocirc;i lo lắng. Barberin nh&igrave;n t&ocirc;i vẻ rất lạ.&nbsp;Bỗng nhi&ecirc;n t&ocirc;i thấy cụ Vitalis v&agrave; mấy con ch&oacute; đi v&agrave;o. &Agrave;&nbsp;ra thế! Cụ đến đ&oacute;n t&ocirc;i, Barberin bảo m&aacute; ra l&agrave;ng để m&aacute; kh&ocirc;ng bảo vệ được t&ocirc;i.<br />\r\n- &ocirc;i, xin cụ, cụ đừng mang ch&aacute;u đi!<br />\r\nV&agrave; t&ocirc;i &ograve;a l&ecirc;n kh&oacute;c nức nở.<br />\r\n- N&agrave;o n&agrave;o, cậu con trai của &ocirc;ng, cụ n&oacute;i với t&ocirc;i thật dịu d&agrave;ng, ở với &ocirc;ng ch&aacute;u kh&ocirc;ng khổ đ&acirc;u, lại được ở b&ecirc;n mấy đứa học tr&ograve; rất ngộ của &ocirc;ng, ch&aacute;u tiếc c&aacute;i g&igrave; cơ chứ?<br />\r\n- M&aacute; Barberin!<br />\r\n- Kiểu g&igrave; th&igrave; m&agrave;y cũng kh&ocirc;ng ở lại đ&acirc;y, Barberin vặn tai t&ocirc;i n&oacute;i, hoặc l&agrave; cụ đ&acirc;y hoặc l&agrave; trại trẻ, m&agrave;y chọn thứ n&agrave;o th&igrave; chọn.<br />\r\n- Kh&ocirc;ng, m&aacute; Barberin cơ!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thằng b&eacute; n&agrave;y c&oacute; t&igrave;nh, l&agrave; dấu hiệu tốt. Th&ocirc;i ta v&agrave;o việc!<br />\r\nN&oacute;i rồi cụ b&agrave;y l&ecirc;n b&agrave;n t&aacute;m đồng năm phrăng, Barberin vơ lu&ocirc;n v&agrave;o t&uacute;i l&atilde;o.<br />\r\n- Đưa g&oacute;i quần &aacute;o của n&oacute; cho n&oacute;, n&agrave;o l&ecirc;n đường. R&eacute;mi! Đi trước con Capi.<br />\r\nT&ocirc;i cảm thấy cụ Vitalis nắm lấy cổ tay t&ocirc;i.&nbsp;Barberin đi v&agrave;o nh&agrave;. Thế l&agrave; hết.&nbsp;L&ecirc;n dốc kh&aacute; d&agrave;i. Cụ Vitalis kh&ocirc;ng rời tay t&ocirc;i..- Cụ cho ch&aacute;u nghỉ một ch&uacute;t c&oacute; được kh&ocirc;ng ạ? - T&ocirc;i hỏi cụ.<br />\r\n- Sẵn s&agrave;ng.<br />\r\nLần đầu ti&ecirc;n cụ rời tay t&ocirc;i. Nhưng đồng thời t&ocirc;i thấy c&aacute;i nh&igrave;n của cụ hướng về con Capi l&agrave;m hiệu. Capi lập tức đến đứng đằng sau t&ocirc;i. N&oacute; l&agrave; tay cai ngục của t&ocirc;i: chỉ một cử động bỏ trốn n&oacute; sẽ vồ lấy ch&acirc;n t&ocirc;i ngay.&nbsp;T&ocirc;i đi tới ngồi tr&ecirc;n ụ đất, Capi đi theo v&agrave; với đ&ocirc;i mắt mờ lệ t&ocirc;i t&igrave;m ng&ocirc;i nh&agrave; của m&aacute; Barberin.&nbsp;Bỗng nhi&ecirc;n tr&ecirc;n con đường dốc l&ecirc;n l&agrave;ng t&ocirc;i nh&igrave;n thấy một chiếc khăn nhỏ m&agrave;u trắng. Xa qu&aacute; n&ecirc;n t&ocirc;i chỉ thấy được m&agrave;u trắng của chiếc khăn nhưng c&oacute; những l&uacute;c tr&aacute;i tim nh&igrave;n tinh hơn đ&ocirc;i mắt: t&ocirc;i nhận ra m&aacute; Barberin. Đ&uacute;ng m&aacute; rồi. M&aacute; bước từng bước d&agrave;i.&nbsp;Tới r&agrave;o nh&agrave; ch&uacute;ng t&ocirc;i m&aacute; đẩy ra, v&agrave;o s&acirc;n v&agrave; đi qua s&acirc;n kh&aacute; nhanh.&nbsp;T&ocirc;i lập tức đứng l&ecirc;n tr&ecirc;n ụ đất kh&ocirc;ng nghĩ tới con Capi đ&atilde; chồm đến gần t&ocirc;i.&nbsp;M&aacute; Barberin kh&ocirc;ng ở trong nh&agrave; l&acirc;u. M&aacute; đi ra ngay v&agrave; chạy trong s&acirc;n.&nbsp;M&aacute; t&igrave;m t&ocirc;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ocirc;i c&uacute;i xuống ph&iacute;a trước v&agrave; k&ecirc;u l&ecirc;n bằng tất cả sức lực của m&igrave;nh:<br />\r\n- M&aacute; ơi! M&aacute; ơi!<br />\r\nNhưng giọng t&ocirc;i ch&igrave;m nghỉm trong kh&ocirc;ng trung.<br />\r\n- Ch&aacute;u đi&ecirc;n &agrave;? - Cụ Vitalis hỏi.<br />\r\nT&ocirc;i kh&ocirc;ng trả lời chỉ d&aacute;n mắt v&agrave;o m&aacute; Barberin nhưng m&aacute; đ&acirc;u c&oacute; ngờ t&ocirc;i ở gần m&aacute; đến như thế n&ecirc;n kh&ocirc;ng nghĩ đến chuyện ngẩng đầu l&ecirc;n. M&aacute; đi qua s&acirc;n, ra lại ngo&agrave;i đường, quay nh&igrave;n tứ ph&iacute;a.&nbsp;T&ocirc;i c&agrave;ng g&agrave;o to hơn.&nbsp;L&uacute;c đ&oacute; cụ Vitalis đo&aacute;n ra sự thể b&egrave;n cũng leo l&ecirc;n ụ đất. Cụ nh&igrave;n thấy c&aacute;i khăn trắng.<br />\r\n- Tội nghiệp thằng b&eacute;! - Cụ th&igrave; thầm.<br />\r\n- &ocirc;i! Ch&aacute;u xin cụ, cho ch&aacute;u về. - Được sự th&ocirc;ng cảm của cụ động vi&ecirc;n, t&ocirc;i n&oacute;i.<br />\r\nNhưng cụ nắm lấy cổ tay t&ocirc;i đưa t&ocirc;i xuống đường.<br />\r\n- Ch&aacute;u đ&atilde; nghỉ rồi, th&ocirc;i l&ecirc;n đường, ch&agrave;ng trai của &ocirc;ng. - Cụ n&oacute;i.<br />\r\nT&ocirc;i muốn gỡ tay cụ ra nhưng cụ nắm chặt lắm.<br />\r\n- Capi, Zerbino! - Cụ gọi.<br />\r\nHai con ch&oacute; v&acirc;y lấy t&ocirc;i..Đ&agrave;nh phải theo cụ Vitalis th&ocirc;i.&nbsp;T&ocirc;i đ&atilde; c&oacute; bằng chứng để hiểu cụ kh&ocirc;ng phải người &aacute;c. Sau khi đi độ mười lăm ph&uacute;t cụ bỏ c&aacute;nh tay t&ocirc;i.&nbsp;<br />\r\n- B&acirc;y giờ ch&aacute;u đi cạnh &ocirc;ng, nhưng đừng qu&ecirc;n nếu ch&aacute;u muốn trốn th&igrave; Capi v&agrave; Zerbino sẽ theo kịp ch&aacute;u ngay.<br />\r\nT&ocirc;i bu&ocirc;ng một tiếng thở d&agrave;i:<br />\r\n- Ch&aacute;u buồn, &ocirc;ng hiểu. - Cụ Vitalis tiếp tục n&oacute;i. - Ch&aacute;u c&oacute; thể kh&oacute;c. Nhưng chỉ cần ch&aacute;u cố m&agrave; cảm thấy được l&agrave; &ocirc;ng mang ch&aacute;u đi kh&ocirc;ng phải để l&agrave;m ch&aacute;u khổ. Ch&aacute;u m&agrave; kh&ocirc;ng đi th&igrave; sao n&agrave;o? Sẽ v&agrave;o trại trẻ. Những người nu&ocirc;i ch&aacute;u kh&ocirc;ng phải cha mẹ ch&aacute;u. M&aacute; tốt với ch&aacute;u v&agrave; ch&aacute;u y&ecirc;u m&aacute;, ch&aacute;u khổ t&acirc;m phải xa m&aacute;, tất cả những c&aacute;i đ&oacute; l&agrave; tốt th&ocirc;i, nhưng b&agrave; ấy kh&ocirc;ng giữ nổi ch&aacute;u. Ch&agrave;ng trai của &ocirc;ng ạ, ch&aacute;u phải hiểu rằng cuộc đời thường l&agrave; một trận chiến m&agrave; trong đ&oacute; c&oacute; phải người ta muốn l&agrave;m g&igrave; l&agrave; l&agrave;m được đ&acirc;u.&nbsp;Những lời của cụ rất kh&ocirc;n ngoan nhưng l&uacute;c n&agrave;y một sự thực c&ograve;n k&ecirc;u to hơn bất kỳ lời n&oacute;i n&agrave;o: đ&oacute; l&agrave; sự chia ly.&nbsp;Từ nay t&ocirc;i kh&ocirc;ng bao giờ c&ograve;n nh&igrave;n thấy người đ&atilde; nu&ocirc;i t&ocirc;i lớn l&ecirc;n nữa v&agrave; &yacute; nghĩ n&agrave;y l&agrave;m t&ocirc;i nghẹn ng&agrave;o trong cổ họng.<br />\r\n- H&atilde;y suy nghĩ về những điều &ocirc;ng n&oacute;i với ch&aacute;u, thỉnh thoảng cụ Vitalis lại nhắc lại, ch&aacute;u kh&ocirc;ng phải khổ khi ở với &ocirc;ng đ&acirc;u.<br />\r\nCuối c&ugrave;ng th&igrave; cụ gi&agrave; cao lớn v&agrave; đẹp l&atilde;o n&agrave;y c&oacute; lẽ kh&ocirc;ng đến nỗi kinh khủng như l&uacute;c đầu t&ocirc;i tưởng.&nbsp;Đ&oacute; l&agrave; lần đầu ti&ecirc;n t&ocirc;i đi một mạch kh&ocirc;ng nghỉ như vậy. K&eacute;o l&ecirc; đ&ocirc;i ch&acirc;n, vất vả lắm t&ocirc;i mới theo được chủ t&ocirc;i. Tuy nhi&ecirc;n t&ocirc;i kh&ocirc;ng d&aacute;m xin cụ dừng ch&acirc;n.&nbsp;<br />\r\n- Đ&ocirc;i guốc của ch&aacute;u l&agrave;m ch&aacute;u mệt đấy. - Cụ bảo t&ocirc;i. - Đến Ussel &ocirc;ng sẽ mua gi&agrave;y cho ch&aacute;u.<br />\r\nC&acirc;u n&oacute;i n&agrave;y l&agrave;m t&ocirc;i th&ecirc;m can đảm. Thực tế đ&ocirc;i gi&agrave;y l&agrave; thứ t&ocirc;i hằng kh&aacute;t khao mong muốn.<br />\r\n- Ussel c&ograve;n xa kh&ocirc;ng ạ?<br />\r\n- &agrave; ra đ&oacute; l&agrave; tiếng n&oacute;i của l&ograve;ng ch&aacute;u, cụ Vitalis vừa n&oacute;i vừa cười, ch&aacute;u muốn c&oacute; gi&agrave;y đi chứ g&igrave;?<br />\r\nĐược, &ocirc;ng hứa, gi&agrave;y đinh. Một chiếc quần cụt bằng nhung, một &aacute;o v&eacute;t, một chiếc mũ nữa. &ocirc;ng hy vọng ch&uacute;ng l&agrave;m ch&aacute;u kh&ocirc; nước mắt v&agrave; sẽ gi&uacute;p cho đ&ocirc;i ch&acirc;n ch&aacute;u đi nốt s&aacute;u dặm đường c&ograve;n lại..Kh&ocirc;ng, &ocirc;ng chủ t&ocirc;i kh&ocirc;ng phải một con người t&agrave;n &aacute;c. Một người t&agrave;n &aacute;c l&agrave;m sao nhận ra đ&ocirc;i guốc l&agrave;m t&ocirc;i mệt được?&nbsp;Trời đầy m&acirc;y x&aacute;m xịt v&agrave; kh&ocirc;ng bao l&acirc;u bắt đầu mưa nhẹ hạt.&nbsp;Với tấm da cừu cụ Vitalis được bảo vệ kh&aacute; k&iacute;n lại c&ograve;n che chở được Joli - Coeur nữa, anh ch&agrave;ng n&agrave;y ngay từ giọt mưa đầu ti&ecirc;n đ&atilde; ẩn ngay v&agrave;o chỗ nấp. Nhưng mấy con ch&oacute; v&agrave; t&ocirc;i chẳng mấy chốc ướt thấm v&agrave;o da thịt, bọn ch&oacute; th&igrave; thỉnh thoảng c&ograve;n giũ được l&ocirc;ng chứ c&ograve;n t&ocirc;i, t&ocirc;i cứ phải đi dưới một sức nặng l&agrave;m t&ocirc;i gi&aacute; băng l&ecirc;n được.<br />\r\n- Ch&aacute;u c&oacute; dễ bị cảm lạnh kh&ocirc;ng? - Chủ t&ocirc;i hỏi.<br />\r\n- Ch&aacute;u kh&ocirc;ng biết, ch&aacute;u nhớ l&agrave; chưa bao giờ th&igrave; phải.<br />\r\n- Tốt. Quả ch&aacute;u tốt thật đấy. Nhưng &ocirc;ng kh&ocirc;ng muốn phơi ch&aacute;u ra mưa thế n&agrave;y, chẳng được &iacute;ch g&igrave;, h&ocirc;m nay ch&uacute;ng ta kh&ocirc;ng đi xa hơn nữa. C&oacute; l&agrave;ng đ&acirc;y rồi ta sẽ v&agrave;o ngủ ở đ&oacute; th&ocirc;i.<br />\r\nTrong l&agrave;ng kh&ocirc;ng c&oacute; c&aacute;i qu&aacute;n n&agrave;o v&agrave; kh&ocirc;ng ai muốn nhận một người ăn m&agrave;y k&eacute;o theo một đứa trẻ v&agrave; ba con ch&oacute; tất cả đều vấy b&ugrave;n be b&eacute;t l&agrave;m g&igrave;.&nbsp;Cuối c&ugrave;ng một người n&ocirc;ng d&acirc;n mở cửa cho ch&uacute;ng t&ocirc;i v&agrave;o một vựa th&oacute;c.&nbsp;Cụ Vitalis l&agrave; một người cẩn trọng. Trong c&aacute;i t&uacute;i cụ đeo ở vai c&oacute; một chiếc b&aacute;nh m&igrave; tr&ograve;n to tướng. Cụ đưa cho t&ocirc;i một miếng c&ograve;n cụ th&igrave; vừa ăn vừa chia b&aacute;nh th&agrave;nh những miếng nhỏ ph&acirc;n ph&aacute;t cho lũ học tr&ograve; phần d&agrave;nh cho ch&uacute;ng.&nbsp;Mệt đến gẫy người, hai ch&acirc;n bị trầy da v&igrave; đ&ocirc;i guốc, t&ocirc;i run l&ecirc;n v&igrave; lạnh trong bộ quần &aacute;o sũng nước.<br />\r\n- Răng ch&aacute;u va lập cập rồi k&igrave;a, cụ Vitalis n&oacute;i, ch&aacute;u c&oacute; r&eacute;t kh&ocirc;ng?<br />\r\n- Hơi r&eacute;t th&ocirc;i ạ.<br />\r\nT&ocirc;i nghe tiếng cụ mở c&aacute;i t&uacute;i:<br />\r\n- Đ&acirc;y l&agrave; c&aacute;i &aacute;o sơ-mi kh&ocirc; v&agrave; &aacute;o gi-l&ecirc;, ch&aacute;u mặc v&agrave;o đi, sau đ&oacute; r&uacute;c v&agrave;o cỏ kh&ocirc; ch&aacute;u sẽ ấm l&ecirc;n nhanh th&ocirc;i v&agrave; ngủ được.<br />\r\nTuy nhi&ecirc;n t&ocirc;i kh&ocirc;ng ấm l&ecirc;n nhanh như cụ Vitalis n&oacute;i, rất l&acirc;u sau t&ocirc;i cứ trở m&igrave;nh m&atilde;i, l&ograve;ng qu&aacute; đau buồn, qu&aacute; khốn khổ. L&uacute;c n&agrave;o cũng như thế n&agrave;y sao? Đi bộ kh&ocirc;ng ngừng dưới trời mưa, ngủ trong một vựa th&oacute;c, run l&ecirc;n v&igrave; lạnh, chỉ c&oacute; một mẩu b&aacute;nh m&igrave; cho bữa tối, kh&ocirc;ng ai thương x&oacute;t t&ocirc;i, kh&ocirc;ng ai y&ecirc;u t&ocirc;i. Đang nghĩ ngợi, l&ograve;ng nặng trĩu v&agrave; hai mắt đẫm nước mắt, t&ocirc;i cảm thấy một hơi thở &acirc;m ấm phả v&agrave;o mặt t&ocirc;i. Đưa tay ra t&ocirc;i sờ thấy l&agrave;n l&ocirc;ng mượt như len của con Capi. N&oacute; nhẹ nh&agrave;ng tới b&ecirc;n t&ocirc;i, nằm xuống chỗ cỏ kh&ocirc; b&ecirc;n cạnh t&ocirc;i v&agrave; tế nhị liếm v&agrave;o b&agrave;n tay t&ocirc;i.&nbsp;Cảm động qu&aacute; t&ocirc;i hơi nhỏm m&igrave;nh dậy h&ocirc;n v&agrave;o chiếc mũi lạnh ngắt của n&oacute;. N&oacute; k&ecirc;u l&ecirc;n một tiếng nghẹn ng&agrave;o, đặt ch&acirc;n n&oacute; v&agrave;o b&agrave;n tay t&ocirc;i rồi nằm im.&nbsp;Thế l&agrave; t&ocirc;i qu&ecirc;n mệt, qu&ecirc;n buồn, họng t&ocirc;i nghẹn lại, t&ocirc;i kh&ocirc;ng c&ograve;n chỉ c&oacute; một m&igrave;nh nữa:&nbsp;t&ocirc;i đ&atilde; c&oacute; một người bạn.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">H&ocirc;m sau ch&uacute;ng t&ocirc;i l&ecirc;n đường từ s&aacute;ng sớm.&nbsp;Đ&atilde; tạnh mưa, bầu trời xanh v&agrave; nhờ l&agrave;n gi&oacute; kh&ocirc; thổi suốt đ&ecirc;m, kh&ocirc;ng c&ograve;n b&ugrave;n lầy nữa. Lũ ch&oacute; nhảy nh&oacute;t quanh ch&uacute;ng t&ocirc;i. Thỉnh thoảng Capi lại đứng tr&ecirc;n hai ch&acirc;n sau hướng về t&ocirc;i sủa l&ecirc;n một tiếng m&agrave; t&ocirc;i hiểu rất r&otilde; &yacute; nghĩa.&nbsp;<br />\r\n- Can đảm l&ecirc;n! - N&oacute; bảo t&ocirc;i như vậy.<br />\r\nĐ&oacute; l&agrave; một con ch&oacute; rất th&ocirc;ng minh c&aacute;i g&igrave; cũng hiểu v&agrave; biết c&aacute;ch l&agrave;m cho người ta hiểu m&igrave;nh. Giữa n&oacute; v&agrave; t&ocirc;i kh&ocirc;ng cần lời: ngay từ ng&agrave;y đầu ti&ecirc;n ch&uacute;ng t&ocirc;i đ&atilde; hiểu nhau rồi.&nbsp;Chưa bao giờ ra khỏi l&agrave;ng n&ecirc;n t&ocirc;i rất sốt ruột muốn tr&ocirc;ng thấy một th&agrave;nh phố. Nhưng Ussel kh&ocirc;ng l&agrave;m t&ocirc;i l&oacute;a mắt. Những ng&ocirc;i nh&agrave; cổ c&oacute; th&aacute;p b&ecirc;n tr&ecirc;n khiến t&ocirc;i chỉ thấy lạnh nhạt thờ ơ.&nbsp;Đầu &oacute;c t&ocirc;i chỉ c&oacute; mỗi một &yacute; nghĩ: hiệu gi&agrave;y.&nbsp;Đ&acirc;u l&agrave; hiệu gi&agrave;y sẽ cung cấp cho t&ocirc;i đ&ocirc;i gi&agrave;y m&agrave; cụ Vitalis đ&atilde; hứa?&nbsp;Chẳng mấy chốc t&ocirc;i c&oacute; c&aacute;i hạnh ph&uacute;c xỏ ch&acirc;n v&agrave;o đ&ocirc;i gi&agrave;y đinh nặng gấp mười lần đ&ocirc;i guốc của t&ocirc;i. L&ograve;ng h&agrave;o hiệp của chủ t&ocirc;i kh&ocirc;ng dừng ở đ&oacute;; mua gi&agrave;y xong cụ mua cho t&ocirc;i một &aacute;o v&eacute;t nhung m&agrave;u xanh da trời, một quần len v&agrave; một mũ phớt. Tất cả những g&igrave; cụ hứa.&nbsp;R&otilde; r&agrave;ng cụ l&agrave; người tuyệt nhất trần đời rồi!&nbsp;Nhung đ&atilde; nhầu, len đ&atilde; sờn, c&aacute;i mũ th&igrave; kh&ocirc;ng ai đo&aacute;n được trước đ&acirc;y m&agrave;u g&igrave; nữa, nhưng cho&aacute;ng ngợp v&igrave; những huy ho&agrave;ng đ&oacute; t&ocirc;i chẳng cần biết đến những nhược điểm n&agrave;y l&agrave;m g&igrave;.&nbsp;T&ocirc;i chỉ mong ch&oacute;ng được mặc bộ c&aacute;nh mới, nhưng trước khi đưa cho t&ocirc;i cụ Vitalis c&ograve;n l&agrave;m ch&uacute;ng biến đổi đ&atilde;.&nbsp;Về đến qu&aacute;n cụ lấy k&eacute;o trong t&uacute;i ra cắt hai ống quần t&ocirc;i l&ecirc;n đến tận đầu gối. Khi t&ocirc;i nh&igrave;n cụ với đ&ocirc;i mắt sợ h&atilde;i:.- Như vậy để ch&aacute;u tr&ocirc;ng kh&ocirc;ng giống ai, cụ n&oacute;i. Ch&uacute;ng ta đang ở Ph&aacute;p, &ocirc;ng mặc cho ch&aacute;u như người &yacute;, nếu ta ở &yacute; &ocirc;ng lại mặc cho ch&aacute;u như người Ph&aacute;p. Ch&uacute;ng ta l&agrave; nghệ sĩ. Bề ngo&agrave;i của ch&uacute;ng ta đ&atilde; phải g&acirc;y t&ograve; m&ograve; rồi. Ch&aacute;u thử tưởng tượng xem nếu ngay chiều nay ta ra nơi c&ocirc;ng cộng m&agrave; ăn mặc như những nh&agrave; tư sản hay những n&ocirc;ng d&acirc;n l&agrave;m sao ta buộc mọi người phải nh&igrave;n ta v&agrave; dừng lại quanh ta? Kh&ocirc;ng được. H&atilde;y học lấy rằng ở đời n&agrave;y đ&ocirc;i khi sự xuất hiện th&ocirc;i cũng đ&atilde; l&agrave; một việc tối cần thiết rồi.&nbsp;ấy l&agrave; tại sao buổi s&aacute;ng l&agrave; người Ph&aacute;p buổi chiều t&ocirc;i đ&atilde; th&agrave;nh người &yacute;. Quần t&ocirc;i ngắn đến gối, cụ Vitalis lại buộc quanh tất t&ocirc;i những sợi d&acirc;y đỏ bắt ch&eacute;o nhau suốt cẳng ch&acirc;n. Tr&ecirc;n chiếc mũ phớt của t&ocirc;i cụ cũng buộc ch&eacute;o nhau nhiều d&acirc;y ruy-băng kh&aacute;c v&agrave; trang tr&iacute; th&ecirc;m một b&oacute; hoa bằng len.&nbsp;Thật th&agrave; m&agrave; n&oacute;i t&ocirc;i thấy m&igrave;nh tuyệt đẹp, chẳng thế m&agrave; anh bạn Capi của t&ocirc;i sau khi ngắm t&ocirc;i rất l&acirc;u phải ch&igrave;a ch&acirc;n ra cho t&ocirc;i với vẻ h&agrave;i l&ograve;ng.&nbsp;Trong khi t&ocirc;i xỏ quần &aacute;o mới, Joli - Coeur đứng trước mặt t&ocirc;i bắt chước điệu bộ t&ocirc;i lại c&ograve;n l&agrave;m qu&aacute; l&ecirc;n nữa. T&ocirc;i thắng xong bộ c&aacute;nh, ngắm vuốt xong xu&ocirc;i th&igrave; n&oacute; đặt hai tay l&ecirc;n h&aacute;ng, ngửa đầu ra sau bắt đầu ph&aacute; ra cười với những tiếng k&ecirc;u l&iacute; nh&iacute; giễu cợt. Đ&atilde; sống th&acirc;n t&igrave;nh với Joli -Coeur kh&aacute; l&acirc;u t&ocirc;i c&oacute; thể khẳng định n&oacute; rất hay cười. C&oacute; lẽ c&aacute;i cười của n&oacute; kh&ocirc;ng giống hệt như c&aacute;i cười của con người. Nhưng cuối c&ugrave;ng, mỗi khi c&oacute; một t&igrave;nh cảm n&agrave;o đ&oacute; l&agrave;m cho n&oacute; vui l&ecirc;n, người ta thấy hai m&eacute;p n&oacute; k&eacute;o ra sau, hai mi mắt he h&eacute;, hai h&agrave;m răng động đậy rất nhanh v&agrave; đ&ocirc;i mắt đen của n&oacute; hầu như bắn ra những tia lửa.<br />\r\n- B&acirc;y giờ ch&aacute;u sửa soạn &aacute;o quần đ&atilde; xong, cụ Vitalis bảo t&ocirc;i, ta bắt đầu l&agrave;m việc để đến mai ng&agrave;y phi&ecirc;n chợ c&oacute; thể ra mắt một buổi biểu diễn lớn trong đ&oacute; ch&aacute;u khởi sự bước v&agrave;o nghề.<br />\r\nT&ocirc;i hỏi cụ thế n&agrave;o l&agrave; bước v&agrave;o nghề, cụ giải th&iacute;ch c&oacute; nghĩa lần đầu xuất hiện trước c&ocirc;ng ch&uacute;ng diễn vở h&agrave;i kịch.<br />\r\n- &ocirc;ng sẽ phải tập cho ch&aacute;u vai m&agrave; &ocirc;ng d&agrave;nh cho ch&aacute;u.<br />\r\nĐ&ocirc;i mắt ngạc nhi&ecirc;n của t&ocirc;i n&oacute;i với cụ l&agrave; t&ocirc;i kh&ocirc;ng hiểu.<br />\r\n- &ocirc;ng muốn ch&aacute;u hiểu vai tr&ograve; ch&aacute;u phải l&agrave;m.<br />\r\n&Ocirc;ng mang ch&aacute;u theo l&agrave; để ch&aacute;u l&agrave;m việc. Việc của ch&aacute;u l&agrave; diễn h&agrave;i kịch với Joli - Coeur v&agrave; mấy ch&uacute; ch&oacute; của &ocirc;ng..- Nhưng ch&aacute;u c&oacute; biết g&igrave; đ&acirc;u! - T&ocirc;i sợ h&atilde;i k&ecirc;u l&ecirc;n.<br />\r\n- Ch&iacute;nh v&igrave; thế &ocirc;ng mới phải dạy ch&aacute;u. Kh&ocirc;ng phải tự nhi&ecirc;n m&agrave; Capi đi đứng duy&ecirc;n d&aacute;ng như thế tr&ecirc;n đ&ocirc;i ch&acirc;n sau cũng kh&ocirc;ng phải Dolce nhảy d&acirc;y l&agrave; &yacute; th&iacute;ch của n&oacute;. Capi đ&atilde; phải học đứng v&agrave; Dolce đ&atilde; phải học nhảy; ch&uacute;ng thậm ch&iacute; đ&atilde; phải l&agrave;m việc kh&aacute; l&acirc;u mới đạt được những t&agrave;i đ&oacute;. Cho n&ecirc;n ch&aacute;u cũng phải l&agrave;m việc để học những vai kh&aacute;c nhau m&agrave; ch&aacute;u sẽ diễn với ch&uacute;ng.&nbsp;Vở kịch, cụ Vitalis n&oacute;i tiếp, c&oacute; t&ecirc;n l&agrave; Người đầy tớ của ng&agrave;i Joli - Coeur hay Kẻ ngu hơn trong hai người lại l&agrave; kẻ m&agrave; người ta kh&ocirc;ng nghĩ tới.&nbsp;Đề t&agrave;i như thế n&agrave;y: Cho tới h&ocirc;m ấy ng&agrave;i Joli -Coeur vẫn c&oacute; một t&ecirc;n đầy tớ m&agrave; ng&agrave;i rất h&agrave;i l&ograve;ng, đ&oacute; l&agrave; Capi. Nhưng Capi đ&atilde; về gi&agrave;, ng&agrave;i Joli - Coeur muốn c&oacute; một đầy tớ mới. Capi chịu tr&aacute;ch nhiệm cung cấp t&ecirc;n đầy tớ mới. Nhưng đ&oacute; kh&ocirc;ng phải l&agrave; con ch&oacute; sẽ nối tiếp sự nghiệp của Capi m&agrave; l&agrave; một ch&agrave;ng trai n&ocirc;ng d&acirc;n t&ecirc;n l&agrave; R&eacute;mi.<br />\r\n- Khỉ l&agrave;m g&igrave; c&oacute; đầy tớ ạ.<br />\r\n- Trong h&agrave;i kịch th&igrave; c&oacute;. Ch&aacute;u đến v&agrave; ng&agrave;i Joli - Coeur thấy ch&aacute;u c&oacute; vẻ đần độn ng&acirc;y ng&ocirc;.<br />\r\n- Thế chẳng hay t&iacute; n&agrave;o.<br />\r\n- C&oacute; nghĩa l&yacute; g&igrave; đ&acirc;u nếu chỉ để cười? Vả chăng ch&aacute;u h&igrave;nh dung m&igrave;nh thực sự đến nh&agrave; một qu&yacute; &ocirc;ng để l&agrave;m đầy tớ v&agrave; giả dụ họ bảo ch&aacute;u dọn b&agrave;n ăn. Đ&acirc;y ch&iacute;nh l&agrave; c&aacute;i b&agrave;n trong vở tr&igrave;nh diễn của ch&uacute;ng ta. N&agrave;o ch&aacute;u bước l&ecirc;n v&agrave; dọn b&agrave;n ra.&nbsp;Tr&ecirc;n c&aacute;i b&agrave;n c&oacute; c&aacute;c đĩa, một c&aacute;i cốc, một con dao, một c&aacute;i dĩa v&agrave; khăn b&agrave;n m&agrave;u trắng.&nbsp;L&agrave;m sao b&agrave;y ra b&acirc;y giờ?&nbsp;T&ocirc;i tự hỏi m&igrave;nh c&acirc;u hỏi đ&oacute;, hai tay th&otilde;ng xuống, c&uacute;i người xuống trước, miệng h&aacute; ra chẳng biết bắt đầu từ chỗ n&agrave;o; chủ t&ocirc;i vỗ tay cười ph&aacute; l&ecirc;n.&nbsp;<br />\r\n- Hoan h&ocirc;! - Cụ n&oacute;i. - Tuyệt! L&agrave;m ra vẻ mặt như ch&aacute;u thật xuất sắc. Vẻ ng&acirc;y thơ của ch&aacute;u đ&aacute;ng phục đấy.<br />\r\n- Ch&aacute;u chẳng biết phải l&agrave;m g&igrave;.<br />\r\n- Ch&iacute;nh v&igrave; thế m&agrave; đ&acirc;m xuất sắc. V&agrave;i ng&agrave;y nữa ch&aacute;u sẽ biết tường tận ch&aacute;u phải l&agrave;m g&igrave;. Đến l&uacute;c đ&oacute; ch&aacute;u lại phải nhớ lại c&aacute;i l&uacute;ng t&uacute;ng m&agrave; l&uacute;c n&agrave;y đ&acirc;y ch&aacute;u đang cảm thấy v&agrave; giả vờ những thứ m&agrave; ch&aacute;u kh&ocirc;ng cảm thấy nữa. Nếu ch&aacute;u t&igrave;m lại được c&aacute;ch tạo ra vẻ mặt v&agrave; th&aacute;i độ như l&uacute;c n&agrave;y th&igrave; &ocirc;ng n&oacute;i trước l&agrave; ch&aacute;u sẽ th&agrave;nh c&ocirc;ng rực rỡ đấy. Nh&acirc;n vật m&agrave; ch&aacute;u phải thể hiện trong vở.h&agrave;i kịch l&agrave; như thế n&agrave;o? L&agrave; một ch&agrave;ng trai n&ocirc;ng th&ocirc;n chưa nh&igrave;n thấy g&igrave; v&agrave; kh&ocirc;ng biết g&igrave; cả; thậm ch&iacute; thấy m&igrave;nh vụng về ngu si hơn con khỉ; do đ&oacute; vở kịch c&oacute; t&ecirc;n Kẻ ngu hơn trong hai người lại l&agrave; kẻ m&agrave; người ta kh&ocirc;ng nghĩ tới. Ngu hơn Joli - Coeur, đ&oacute; l&agrave; vai diễn của ch&aacute;u.&nbsp;Vở Người đầy tớ của ng&agrave;i Joli - Coeur kh&ocirc;ng phải một h&agrave;i kịch lớn, biểu diễn chưa đầy hai mươi ph&uacute;t. Nhưng ch&uacute;ng t&ocirc;i tập đến gần ba tiếng đồng hồ, cụ Vitalis bắt ch&uacute;ng t&ocirc;i l&agrave;m đi l&agrave;m lại đến mười lần c&ugrave;ng một thứ, lũ ch&oacute; cũng như t&ocirc;i. Dĩ nhi&ecirc;n lũ ch&oacute; c&oacute; l&uacute;c qu&ecirc;n v&agrave;i phần trong vai của ch&uacute;ng.<br />\r\n<br />\r\nT&ocirc;i rất ngạc nhi&ecirc;n thấy chủ t&ocirc;i ki&ecirc;n nhẫn v&agrave; dịu d&agrave;ng biết nhường n&agrave;o. Cụ kh&ocirc;ng hề tức giận một lần n&agrave;o.<br />\r\n- N&agrave;o ta lại bắt đầu lại, mỗi lần y&ecirc;u cầu của cụ kh&ocirc;ng đạt được cụ lại n&oacute;i: chưa được, Capi; c&ograve;n ng&agrave;i Joli - Coeur, ng&agrave;i kh&ocirc;ng ch&uacute; &yacute; g&igrave; cả.<br />\r\nChỉ c&oacute; thế, nhưng thế cũng l&agrave; đủ.<br />\r\n- Được rồi, cụ bảo t&ocirc;i, tập đ&atilde; xong, ch&aacute;u c&oacute; nghĩ rằng m&igrave;nh sẽ quen đ&oacute;ng kịch kh&ocirc;ng?<br />\r\n- Ch&aacute;u chẳng biết nữa.<br />\r\n- Ch&aacute;u c&oacute; ch&aacute;n kh&ocirc;ng?<br />\r\n- Kh&ocirc;ng, ch&aacute;u th&iacute;ch l&agrave; kh&aacute;c.<br />\r\n- Thế th&igrave; tốt. Ch&aacute;u th&ocirc;ng minh, lại ch&uacute; &yacute;.<br />\r\nVừa ch&uacute; &yacute; vừa v&acirc;ng lời người ta sẽ đạt tới tất cả mọi thứ. Ch&aacute;u h&atilde;y xem lũ ch&oacute; v&agrave; so s&aacute;nh ch&uacute;ng với Joli - Coeur. Joli - Coeur c&oacute; lẽ hiếu động hơn, th&ocirc;ng minh hơn nhưng kh&ocirc;ng dễ bảo.&nbsp;Bảo n&oacute; c&aacute;i g&igrave; kh&ocirc;ng bao giờ n&oacute; vui l&ograve;ng l&agrave;m.&nbsp;Đ&oacute; l&agrave; do bản chất n&oacute; v&agrave; ch&iacute;nh v&igrave; vậy &ocirc;ng kh&ocirc;ng bao giờ giận n&oacute;. Khỉ kh&ocirc;ng giống ch&oacute;, kh&ocirc;ng c&oacute; &yacute; thức về bổn phận.&nbsp;T&ocirc;i mạnh dạn n&oacute;i với cụ rằng c&aacute;i l&agrave;m t&ocirc;i ngạc nhi&ecirc;n nhất trong buổi tập vừa qua l&agrave; cụ tỏ ra v&ocirc; c&ugrave;ng ki&ecirc;n nhẫn.&nbsp;Cụ mỉm cười:<br />\r\n- Người ta thấy ngay l&agrave;, cụ n&oacute;i, trước nay ch&aacute;u chỉ sống với những người n&ocirc;ng d&acirc;n t&agrave;n nhẫn với s&uacute;c vật.<br />\r\n- M&aacute; Barberin dịu d&agrave;ng với con b&ograve; của ch&uacute;ng ch&aacute;u lắm. - T&ocirc;i n&oacute;i.<br />\r\n- B&agrave; l&agrave;m thế l&agrave; đ&uacute;ng. B&agrave; hiểu r&otilde; điều m&agrave; người n&ocirc;ng d&acirc;n thường kh&ocirc;ng biết: dịu d&agrave;ng th&igrave; người ta được nhiều hơn. Ch&iacute;nh v&igrave; kh&ocirc;ng bao giờ tức giận c&aacute;c con vật của &ocirc;ng m&agrave; &ocirc;ng đ&atilde; đ&agrave;o tạo được ch&uacute;ng như ng&agrave;y nay. Nếu &ocirc;ng đ&aacute;nh ch&uacute;ng, ch&uacute;ng sẽ sợ, sợ h&atilde;i l&agrave;m t&ecirc; liệt tr&iacute; th&ocirc;ng.minh. Hơn nữa nếu để m&igrave;nh đi đến giận dữ &ocirc;ng sẽ mất l&ograve;ng tin ở ch&aacute;u. Mấy con ch&oacute; của &ocirc;ng đ&atilde; dạy cho &ocirc;ng nhiều b&agrave;i học như những b&agrave;i học &ocirc;ng dạy ch&uacute;ng. &ocirc;ng đ&atilde; l&agrave;m ph&aacute;t triển tr&iacute; th&ocirc;ng minh của ch&uacute;ng c&ograve;n ch&uacute;ng tạo t&iacute;nh c&aacute;ch cho &ocirc;ng.&nbsp;C&aacute;c bạn t&ocirc;i, lũ ch&oacute; v&agrave; con khỉ, c&oacute; thuận lợi hơn t&ocirc;i ở chỗ đ&atilde; quen xuất hiện trước c&ocirc;ng ch&uacute;ng th&agrave;nh ra ng&agrave;y h&ocirc;m sau ch&uacute;ng kh&ocirc;ng hề sợ h&atilde;i.&nbsp;Nhưng t&ocirc;i kh&ocirc;ng c&oacute; được c&aacute;i an t&acirc;m b&igrave;nh tĩnh của ch&uacute;ng. Nỗi x&uacute;c động của t&ocirc;i thật mạnh mẽ khi ch&uacute;ng t&ocirc;i rời qu&aacute;n đi đến nơi biểu diễn.&nbsp;Cụ Vitalis mở đầu cuộc diễu h&agrave;nh bằng một điệu van-xơ thổi tr&ecirc;n s&aacute;o. Theo sau l&agrave; Capi, tr&ecirc;n lưng Capi, Joli - Coeur trong bộ qu&acirc;n phục đại tướng Anh ung dung ngồi thoải m&aacute;i. Rồi c&aacute;ch xa ra một đoạn kha kh&aacute; l&agrave; Zerbino v&agrave; Dolce tiến tới. Cuối c&ugrave;ng t&ocirc;i kết th&uacute;c đo&agrave;n diễu h&agrave;nh.&nbsp;Nhưng c&aacute;i l&agrave;m người ta ch&uacute; &yacute; c&ograve;n nhiều hơn cả sự h&ugrave;ng tr&aacute;ng của cuộc diễu h&agrave;nh của ch&uacute;ng t&ocirc;i lại l&agrave; những tiếng s&aacute;o ch&oacute;i tai. Mọi người đổ ra cửa xem ch&uacute;ng t&ocirc;i diễu qua, tất cả c&aacute;c r&egrave;m cửa sổ đều được k&eacute;o l&ecirc;n nhanh ch&oacute;ng.&nbsp;V&agrave;i đứa trẻ đi theo ch&uacute;ng t&ocirc;i, nhiều n&ocirc;ng d&acirc;n sửng sốt nhập bọn với ch&uacute;ng, khi đến nơi biểu diễn ch&uacute;ng t&ocirc;i đ&atilde; c&oacute; cả một đ&aacute;m rước đằng sau m&igrave;nh rồi.&nbsp;Phần đầu buổi biểu diễn của ch&uacute;ng t&ocirc;i gồm một số tr&ograve; do ch&oacute; thực hiện. Cụ Vitalis bỏ ống s&aacute;o, thay v&agrave;o đ&oacute; một chiếc vĩ cầm, k&eacute;o đệm theo c&aacute;c tiết mục của ch&oacute; l&uacute;c th&igrave; điệu van-xơ l&uacute;c th&igrave; một điệu nhạc buồn r&eacute;o rắt.&nbsp;Tiết mục đầu ti&ecirc;n kết th&uacute;c, Capi ngậm một c&aacute;i b&aacute;t gỗ giữa hai h&agrave;m răng, đi tr&ecirc;n hai ch&acirc;n sau bắt đầu l&agrave;m một v&ograve;ng quanh &quot;cử tọa đ&aacute;ng k&iacute;nh&quot;. Khi n&agrave;o kh&ocirc;ng thấy xu rơi xuống n&oacute; lấy hai ch&acirc;n trước đặt l&ecirc;n người vị kh&aacute;n giả chai sạn, sủa hai ba c&aacute;i rồi đập nh&egrave; nhẹ v&agrave;o c&aacute;i t&uacute;i m&agrave; n&oacute; muốn mở. Thế l&agrave; trong c&ocirc;ng ch&uacute;ng xuất hiện những tiếng k&ecirc;u, những lời b&agrave;n t&aacute;n vui vẻ, những tiếng chế giễu.<br />\r\n- Con ch&oacute; b&ocirc;ng n&agrave;y th&ocirc;ng minh thật, n&oacute; biết những ai c&oacute; t&uacute;i đầy đấy nh&eacute;.<br />\r\nThế l&agrave; cuối c&ugrave;ng những đồng xu đ&agrave;nh được dốc ra.&nbsp;Đến lượt Joli - Coeur v&agrave; t&ocirc;i ra mắt.<br />\r\n- Thưa qu&yacute; b&agrave; qu&yacute; &ocirc;ng, cụ Vitalis n&oacute;i, ch&uacute;ng t&ocirc;i tiếp tục buổi biểu diễn bằng một h&agrave;i kịch dễ.thương c&oacute; t&ecirc;n: Người đầy tớ của ng&agrave;i Joli -Coeur hay Kẻ ngu hơn trong hai người lại l&agrave; kẻ kh&ocirc;ng ai nghĩ tới. Một người như t&ocirc;i kh&ocirc;ng hạ m&igrave;nh; để khen vở kịch v&agrave; diễn vi&ecirc;n của ch&iacute;nh m&igrave;nh, chỉ xin c&aacute;c vị h&atilde;y mở to mắt, h&atilde;y dỏng tai v&agrave; chuẩn bị tay để vỗ.&nbsp;Tuy nhi&ecirc;n để l&agrave;m cho diễn xuất của c&aacute;c diễn vi&ecirc;n dễ hiểu hơn, cụ Vitalis k&egrave;m theo mấy lời b&igrave;nh.&nbsp;Thế l&agrave;, với vẻ hiếu chiến ngầm, cụ tuy&ecirc;n bố &ocirc;ng Joli - Coeur, rồi đến Capi, đến t&ocirc;i, l&ecirc;n s&acirc;n khấu. Cụ b&igrave;nh luận t&oacute;m tắt từng cảnh một. Vở kịch được dựng l&ecirc;n nhằm thể hiện sự ngu si đần độn của t&ocirc;i trong tất cả mọi mặt: mỗi cảnh t&ocirc;i phải giở v&agrave;i h&agrave;nh động ngớ ngẩn trong khi Joli - Coeur ngược lại phải t&igrave;m cơ hội trổ hết th&ocirc;ng minh v&agrave; kh&eacute;o l&eacute;o. &aacute;i ch&agrave;! Duy&ecirc;n d&aacute;ng v&agrave; lịch sự của ng&agrave;i Joli - Coeur sao m&agrave; khiến kh&aacute;n giả m&ecirc; say đến thế! C&ograve;n sự vụng về l&uacute;ng t&uacute;ng của t&ocirc;i l&agrave;m họ cười ơi l&agrave; cười! Tiếng vỗ tay vang dội tứ ph&iacute;a, buổi biểu diễn kết th&uacute;c thắng lợi. Con khỉ mới th&ocirc;ng minh l&agrave;m sao! T&ecirc;n đầy tớ mới ngu ngốc l&agrave;m sao! Cụ Vitalis khen t&ocirc;i như vậy v&agrave; t&ocirc;i tự h&agrave;o lắm.&nbsp;Chắc chắn c&aacute;c kịch sĩ trong đo&agrave;n &ocirc;ng Vitalis t&agrave;i cao rồi - t&ocirc;i muốn n&oacute;i lũ ch&oacute; v&agrave; con khỉ -nhưng t&agrave;i n&agrave;y cũng chẳng nhiều vẻ lắm. Biểu diễn mấy buổi người ta đ&atilde; biết hết vở.&nbsp;Từ đ&oacute; đi đến kết luận l&agrave; kh&ocirc;ng ở l&acirc;u m&atilde;i một th&agrave;nh phố được.&nbsp;Sau ba ng&agrave;y đến Ussel lại phải l&ecirc;n đường.&nbsp;Đi đ&acirc;u b&acirc;y giờ nhỉ? T&ocirc;i đ&atilde; kh&aacute; mạnh dạn với chủ t&ocirc;i để hỏi cụ c&acirc;u hỏi n&agrave;y.<br />\r\n- Ch&aacute;u c&oacute; biết đất nước n&agrave;y chứ? - Cụ vừa nh&igrave;n t&ocirc;i vừa trả lời.<br />\r\n- Kh&ocirc;ng ạ.<br />\r\n- Thế tại sao ch&aacute;u lại hỏi &ocirc;ng l&agrave; ta đi đ&acirc;u?<br />\r\nNếu &ocirc;ng bảo ch&aacute;u l&agrave; ta đi Aurillac để tiếp tục đi Bordeaux rồi từ Bordeaux đi Pyr&eacute;n&eacute;es th&igrave; ch&aacute;u biết được c&aacute;i g&igrave;?<br />\r\n- Nhưng cụ biết xứ sở n&agrave;y chứ ạ?<br />\r\n- &ocirc;ng chưa bao giờ đến đ&acirc;y.<br />\r\n- Thế tại sao cụ lại biết l&agrave; m&igrave;nh đi đ&acirc;u?<br />\r\nCụ nh&igrave;n t&ocirc;i rất l&acirc;u.<br />\r\n- Ch&aacute;u kh&ocirc;ng biết đọc phải kh&ocirc;ng? - Cụ hỏi t&ocirc;i.<br />\r\n- Kh&ocirc;ng, nhưng ch&aacute;u đ&atilde; nh&igrave;n thấy người ta đọc. - T&ocirc;i tự h&agrave;o n&oacute;i..Quả thật người ta c&oacute; cho t&ocirc;i đi học nhưng chỉ c&oacute; một th&aacute;ng.<br />\r\n- Đọc c&oacute; kh&oacute; lắm kh&ocirc;ng ạ? - T&ocirc;i hỏi cụ Vitalis sau khi đi kh&aacute; l&acirc;u vừa đi vừa nghĩ ngợi.<br />\r\n- Kh&oacute; đối với kẻ n&agrave;o rắn đầu th&ocirc;i, lại c&agrave;ng kh&oacute; với những ai kh&ocirc;ng thiện ch&iacute;. Ch&aacute;u c&oacute; rắn đầu kh&ocirc;ng?<br />\r\n- Ch&aacute;u kh&ocirc;ng biết nhưng c&oacute; vẻ như nếu cụ dạy ch&aacute;u đọc th&igrave; ch&aacute;u kh&ocirc;ng ngại kh&oacute; đ&acirc;u.<br />\r\n- Được, ch&uacute;ng ta sẽ xem; ta c&ograve;n thời gian trước mặt m&agrave;.<br />\r\nT&ocirc;i kh&ocirc;ng biết học đọc kh&oacute; thế n&agrave;o nhưng t&ocirc;i h&igrave;nh dung ngay thấy m&igrave;nh mở một quyển s&aacute;ch v&agrave; biết được c&oacute; những g&igrave; trong đ&oacute;.&nbsp;H&ocirc;m sau trong khi đi đường t&ocirc;i thấy chủ t&ocirc;i c&uacute;i xuống nhặt tr&ecirc;n đường c&aacute;i một mảnh v&aacute;n phủ bụi đến một nửa.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Đ&acirc;y l&agrave; quyển s&aacute;ch, ch&aacute;u sẽ học đọc trong đ&oacute;. - Cụ bảo t&ocirc;i.<br />\r\nT&ocirc;i nh&igrave;n cụ xem c&oacute; phải cụ giễu t&ocirc;i kh&ocirc;ng.&nbsp;Rồi khi thấy cụ rất nghi&ecirc;m t&uacute;c, t&ocirc;i nh&igrave;n kỹ c&aacute;i vật m&agrave; cụ t&igrave;m được. Đ&oacute; l&agrave; một mảnh v&aacute;n bằng gỗ sồi, d&agrave;i bằng c&aacute;nh tay, rộng bằng hai b&agrave;n tay, rất nhẵn, b&ecirc;n tr&ecirc;n chẳng c&oacute; chữ c&oacute; h&igrave;nh g&igrave; cả.<br />\r\n- Cụ giễu ch&aacute;u đấy ạ?<br />\r\n- Đ&acirc;u c&oacute;, con trai ta, cứ chờ tới l&uacute;c ch&uacute;ng ta đến bụi c&acirc;y đằng kia đ&atilde;, ta nghỉ ở đấy v&agrave; ch&aacute;u sẽ thấy &ocirc;ng dạy ch&aacute;u đọc bằng c&aacute;i mảnh gỗ n&agrave;y như thế n&agrave;o.<br />\r\nCụ Vitalis lấy một con dao trong t&uacute;i ra, cắt từ mảnh v&aacute;n một lớp gỗ mỏng gọt nhẵn đi rất kỹ c&agrave;ng. Sau đ&oacute; cụ cắt n&oacute; th&agrave;nh từng miếng nhỏ đều nhau.<br />\r\n- Tr&ecirc;n mỗi miếng gỗ n&agrave;y, cụ n&oacute;i, &ocirc;ng sẽ lấy mũi dao kho&eacute;t một chữ c&aacute;i. Qua đ&oacute; ch&aacute;u học được h&igrave;nh d&aacute;ng c&aacute;c chữ v&agrave; khi đ&atilde; thuộc ch&aacute;u gh&eacute;p ch&uacute;ng lại th&agrave;nh c&aacute;c từ. Khi ch&aacute;u đ&atilde; tạo th&agrave;nh c&aacute;c từ m&agrave; &ocirc;ng bảo rồi, ch&aacute;u sẽ đọc được một quyển s&aacute;ch.<br />\r\nChẳng bao l&acirc;u c&aacute;c t&uacute;i &aacute;o t&ocirc;i đầy những miếng gỗ v&agrave; t&ocirc;i mau ch&oacute;ng biết mặt c&aacute;c chữ c&aacute;i, nhưng để biết đọc lại l&agrave; một chuyện kh&aacute;c. Sự việc kh&ocirc;ng nhanh được như thế thậm ch&iacute; c&oacute; l&uacute;c t&ocirc;i đ&atilde; hối tiếc muốn học đọc.&nbsp;Dầu sao phải n&oacute;i rằng, c&ocirc;ng bằng m&agrave; n&oacute;i, kh&ocirc;ng phải v&igrave; lười m&agrave; t&ocirc;i hối tiếc m&agrave; ch&iacute;nh l&agrave; do tự &aacute;i.&nbsp;Trong khi dạy t&ocirc;i chữ c&aacute;i cụ Vitalis c&oacute; &yacute; nghĩ dạy lu&ocirc;n cả con Capi. Ch&uacute;ng t&ocirc;i c&ugrave;ng học với nhau. Dĩ nhi&ecirc;n Capi kh&ocirc;ng thể đọc l&ecirc;n những.chữ c&aacute;i m&agrave; n&oacute; tr&ocirc;ng thấy v&igrave; n&oacute; kh&ocirc;ng biết n&oacute;i, nhưng khi những miếng gỗ được b&agrave;y tr&ecirc;n cỏ n&oacute; phải lấy ch&acirc;n tha ra những chữ m&agrave; cụ Vitalis đọc l&ecirc;n.&nbsp;L&uacute;c đầu tiến bộ của t&ocirc;i nhanh hơn n&oacute; nhưng nếu như t&ocirc;i c&oacute; tr&iacute; th&ocirc;ng minh nhạy b&eacute;n hơn th&igrave; n&oacute; lại c&oacute; tr&iacute; nhớ vững v&agrave;ng hơn. Thế l&agrave; cứ mỗi lần t&ocirc;i phạm lỗi chủ t&ocirc;i lại kh&ocirc;ng qu&ecirc;n bảo t&ocirc;i:<br />\r\n- Capi sẽ biết đọc trước R&eacute;mi cho m&agrave; xem, thật xấu hổ.<br />\r\nĐiều đ&oacute; k&iacute;ch th&iacute;ch t&ocirc;i đến mức t&ocirc;i dốc l&ograve;ng ra học m&agrave; trong khi con ch&oacute; dừng lại ở chỗ viết được t&ecirc;n n&oacute; t&ocirc;i tiến tới đọc được một quyển s&aacute;ch.<br />\r\n- B&acirc;y giờ ch&aacute;u đ&atilde; đọc được chữ, cụ Vitalis bảo t&ocirc;i, ch&aacute;u c&oacute; muốn đọc được nhạc kh&ocirc;ng?<br />\r\n- Ch&aacute;u c&oacute; h&aacute;t được như cụ kh&ocirc;ng ạ?<br />\r\nThỉnh thoảng cụ Vitalis h&aacute;t v&agrave; cụ đ&acirc;u c&oacute; ngờ nghe cụ h&aacute;t như một ng&agrave;y hội đối với t&ocirc;i.<br />\r\n- Ch&aacute;u muốn h&aacute;t ư? Nghe &ocirc;ng h&aacute;t ch&aacute;u thấy th&iacute;ch ư?<br />\r\n- Th&iacute;ch nhất đấy ạ. Mỗi khi cụ h&aacute;t ch&aacute;u chỉ muốn kh&oacute;c, khi cụ h&aacute;t một kh&uacute;c h&aacute;t &ecirc;m đềm hay buồn b&atilde;, kh&uacute;c h&aacute;t lại đưa ch&aacute;u về với m&aacute; Barberin, ch&aacute;u nghĩ đến m&aacute;, ch&aacute;u nh&igrave;n thấy m&aacute; trong nh&agrave;. ấy thế m&agrave; ch&aacute;u c&oacute; hiểu g&igrave; về những lời cụ h&aacute;t đ&acirc;u v&igrave; n&oacute; l&agrave; tiếng &yacute; m&agrave;.<br />\r\nVừa n&oacute;i vừa nh&igrave;n cụ, t&ocirc;i thấy đ&ocirc;i mắt cụ như đẫm nước mắt, t&ocirc;i b&egrave;n hỏi cụ n&oacute;i như vậy c&oacute; l&agrave;m cụ khổ t&acirc;m phiền n&atilde;o g&igrave; kh&ocirc;ng.<br />\r\n- Kh&ocirc;ng con ạ, cụ n&oacute;i với t&ocirc;i bằng một giọng cảm động, con l&agrave;m &ocirc;ng nhớ tới tuổi trẻ của &ocirc;ng, thời thanh ni&ecirc;n đẹp đẽ của &ocirc;ng. Cứ y&ecirc;n t&acirc;m, &ocirc;ng sẽ dạy con h&aacute;t. V&agrave; v&igrave; con l&agrave; người c&oacute; tấm l&ograve;ng, cả con nữa, con cũng sẽ l&agrave;m người ta kh&oacute;c v&agrave; vỗ tay.<br />\r\nThế l&agrave; ngay h&ocirc;m sau chủ t&ocirc;i dạy &acirc;m nhạc như đ&atilde; dạy t&ocirc;i học. Cụ bắt đầu gọt những miếng gỗ nhỏ rồi lấy dao khắc l&ecirc;n đ&oacute;. Chuẩn bị xong c&aacute;c b&agrave;i học, phải th&uacute; thật l&agrave; học nhạc kh&ocirc;ng phải k&eacute;m nhọc nhằn hơn học đọc. Cụ Vitalis, vốn ki&ecirc;n nhẫn với mấy con vật của cụ l&agrave; thế m&agrave; nhiều lần phải bực với t&ocirc;i.&nbsp;Cuối c&ugrave;ng những bước đầu đ&atilde; vượt qua v&agrave; t&ocirc;i h&agrave;i l&ograve;ng thấy m&igrave;nh đ&atilde; c&oacute; thể xướng &acirc;m một điệu nhạc m&agrave; cụ Vitalis viết tr&ecirc;n một tờ giấy.&nbsp;Ng&agrave;y h&ocirc;m ấy cụ t&aacute;t nhẹ v&agrave;o mỗi b&ecirc;n m&aacute; t&ocirc;i hai c&aacute;i t&aacute;t y&ecirc;u v&agrave; tuy&ecirc;n bố rằng nếu cứ tiếp tục như thế t&ocirc;i sẽ trở th&agrave;nh một ca sĩ lớn..Dĩ nhi&ecirc;n chuyện học h&agrave;nh n&oacute;i tr&ecirc;n kh&ocirc;ng phải chỉ l&agrave;m trong một ng&agrave;y, v&agrave; trong rất nhiều th&aacute;ng t&uacute;i quần t&uacute;i &aacute;o t&ocirc;i nh&eacute;t đầy c&aacute;c miếng gỗ.&nbsp;Hơn nữa học kh&ocirc;ng đều bởi v&igrave; chỉ những l&uacute;c rỗi cụ Vitalis mới dạy t&ocirc;i học.&nbsp;Mỗi ng&agrave;y ch&uacute;ng t&ocirc;i phải ho&agrave;n tất đoạn h&agrave;nh tr&igrave;nh, d&agrave;i ngắn t&ugrave;y theo x&oacute;m l&agrave;ng ở xa nhau hay kh&ocirc;ng; phải biểu diễn ở tất cả những nơi c&oacute; cơ may kiếm được ch&uacute;t thu nhập &iacute;t ỏi; phải tập vai cho mấy con ch&oacute; v&agrave; cho Joli - Coeur; chuẩn bị bữa ăn; chỉ sau tất cả những c&aacute;i đ&oacute; mới n&oacute;i đến chuyện học đọc hay học nhạc.&nbsp;Cuối c&ugrave;ng t&ocirc;i đ&atilde; học được một c&aacute;i g&igrave; đ&oacute; v&agrave; đồng thời học đi những qu&atilde;ng đường d&agrave;i. T&ocirc;i l&agrave; một đứa trẻ kh&aacute; gầy c&ograve;m ốm yếu khi sống với m&aacute; Barberin; nay b&ecirc;n cạnh cụ Vitalis, sống giữa kh&iacute; trời, với gian khổ, đ&ocirc;i ch&acirc;n v&agrave; hai c&aacute;nh tay t&ocirc;i khỏe l&ecirc;n, phổi t&ocirc;i nở ra, da t&ocirc;i săn sắn lại, t&ocirc;i c&oacute; thể chịu đựng được lạnh cũng như n&oacute;ng, nắng cũng như mưa, những thiếu thốn những mệt nhọc m&agrave; kh&ocirc;ng đau khổ ch&uacute;t n&agrave;o.&nbsp;Ch&uacute;ng t&ocirc;i đ&atilde; vượt qua một phần của miền Nam nước Ph&aacute;p. Ch&uacute;ng t&ocirc;i cứ thẳng tiến, gặp đ&acirc;u đi đấy v&agrave; cứ thấy một c&aacute;i l&agrave;ng n&agrave;o kh&ocirc;ng đến nỗi ngh&egrave;o khổ qu&aacute; ch&uacute;ng t&ocirc;i lại chuẩn bị tiến v&agrave;o một c&aacute;ch oai vệ. T&ocirc;i trang điểm cho lũ ch&oacute;, chải l&ocirc;ng cho Dolce, mặc quần &aacute;o cho Zerbino, d&aacute;n miếng d&aacute;n l&ecirc;n một b&ecirc;n mắt con Capi để n&oacute; c&oacute; thể đ&oacute;ng vai một &ocirc;ng gi&agrave; cảu nhảu c&agrave;u nh&agrave;u, t&ocirc;i buộc Joli - Coeur phải mặc quần &aacute;o đại tướng v&agrave;o.&nbsp;Cụ Vitalis cho t&ocirc;i được ho&agrave;n to&agrave;n tự do ph&oacute;ng t&uacute;ng:<br />\r\n- Kh&ocirc;ng ngờ ch&aacute;u lại được &ocirc;ng cho đi qua to&agrave;n bộ nước Ph&aacute;p ở c&aacute;i tuổi m&agrave; trẻ con chỉ đi học tiểu học hoặc trung học, ch&aacute;u h&atilde;y nh&igrave;n, h&atilde;y học đi. Thấy c&aacute;i g&igrave; kh&ocirc;ng hiểu hoặc muốn hỏi điều g&igrave;, cứ hỏi &ocirc;ng. C&oacute; lẽ kh&ocirc;ng phải l&uacute;c n&agrave;o &ocirc;ng cũng trả lời được bởi v&igrave; &ocirc;ng kh&ocirc;ng cho rằng m&igrave;nh hiểu biết tất cả nhưng biết đ&acirc;u &ocirc;ng c&oacute; thể thỏa m&atilde;n được t&ograve; m&ograve; của ch&aacute;u th&igrave; sao? Kh&ocirc;ng phải xưa nay &ocirc;ng vẫn l&agrave;m gi&aacute;m đốc một g&aacute;nh h&aacute;t s&uacute;c vật th&ocirc;ng th&aacute;i, &ocirc;ng cũng đ&atilde; học được những điều ngo&agrave;i những c&aacute;i bổ &iacute;ch cho &ocirc;ng l&uacute;c n&agrave;y để giới thiệu Capi v&agrave; Joli - Coeur với &quot;cử tọa đ&aacute;ng k&iacute;nh&quot;. Nhưng ta sẽ n&oacute;i với nhau chuyện đ&oacute; sau. Ch&aacute;u chỉ cần biết l&agrave; một người l&agrave;m tr&ograve; ch&oacute; cũng c&oacute; một vị tr&iacute; đ&aacute;ng kể trong thi&ecirc;n hạ đấy chứ. Kh&ocirc;ng phải thường đ&acirc;u..Sau khi rời d&atilde;y n&uacute;i ở Auvergne ch&uacute;ng t&ocirc;i tới v&ugrave;ng cao nguy&ecirc;n đ&aacute; v&ocirc;i ở Quercy.&nbsp;Giữa b&igrave;nh nguy&ecirc;n n&agrave;y c&oacute; một l&agrave;ng lớn t&ecirc;n l&agrave; Labastide - Murat, ch&uacute;ng t&ocirc;i qua đ&ecirc;m ở đ&oacute; trong vựa th&oacute;c một c&aacute;i qu&aacute;n.<br />\r\n- Ch&iacute;nh ở đ&acirc;y, cụ Vitalis bảo t&ocirc;i buổi tối trước khi đi ngủ, cũng c&oacute; thể ngay tại c&aacute;i qu&aacute;n n&agrave;y, đ&atilde; sinh ra một con người bắt đầu đời m&igrave;nh l&agrave;m một thằng b&eacute; qu&eacute;t chuồng ngựa. T&ecirc;n &ocirc;ng ta l&agrave; Murat. &ocirc;ng đ&atilde; trở th&agrave;nh một anh h&ugrave;ng v&agrave; người ta đ&atilde; lấy t&ecirc;n &ocirc;ng đặt cho l&agrave;ng n&agrave;y. &ocirc;ng biết &ocirc;ng ta v&agrave; đ&atilde; từng chuyện tr&ograve; thường xuy&ecirc;n với &ocirc;ng ta.<br />\r\n- Khi &ocirc;ng ta c&ograve;n l&agrave; thằng b&eacute; qu&eacute;t chuồng ngựa ấy ạ?<br />\r\n- Kh&ocirc;ng, cụ Vitalis cười n&oacute;i, khi &ocirc;ng ấy l&agrave;m vua cơ.<br />\r\n- Cụ đ&atilde; từng quen một &ocirc;ng vua?<br />\r\nHẳn giọng t&ocirc;i thốt l&ecirc;n phải buồn cười lắm bởi v&igrave; c&aacute;i cười của chủ t&ocirc;i lại cất l&ecirc;n v&agrave; k&eacute;o d&agrave;i rất l&acirc;u.<br />\r\n- &ocirc;ng sẽ kể ch&aacute;u nghe chuyện &ocirc;ng vua n&agrave;y.<br />\r\nThế l&agrave; trong nhiều giờ sau đ&oacute; ch&uacute;ng t&ocirc;i ngồi tr&ecirc;n chiếc ghế d&agrave;i, &ocirc;ng th&igrave; n&oacute;i, t&ocirc;i th&igrave; d&aacute;n mắt v&agrave;o khu&ocirc;n mặt &ocirc;ng đang được &aacute;nh trăng mờ nhạt chiếu s&aacute;ng.&nbsp;Chủ t&ocirc;i biết thật nhiều điều!.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>CHƯƠNG III</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Rời v&ugrave;ng đất kh&ocirc; cằn của những cao nguy&ecirc;n đ&aacute; v&ocirc;i v&agrave; v&ugrave;ng đất tru&ocirc;ng, t&ocirc;i nhớ lại l&agrave; ch&uacute;ng t&ocirc;i tới một thung lũng xanh tươi với một thu nhập kha kh&aacute;, đất đai gi&agrave;u c&oacute; của xứ sở l&agrave;m người d&acirc;n sung t&uacute;c, ch&uacute;ng t&ocirc;i biểu diễn nhiều buổi, những đồng xu cũng dễ d&agrave;ng rơi xuống chiếc b&aacute;t gỗ của Capi.&nbsp;Một lần, ch&uacute;ng t&ocirc;i ngủ trong một ng&ocirc;i l&agrave;ng kh&aacute; ngh&egrave;o khổ, trời vừa s&aacute;ng đ&atilde; lại ra đi. Ch&uacute;ng t&ocirc;i đi m&atilde;i tr&ecirc;n con đường c&aacute;i bụi m&ugrave; v&agrave; bỗng nhi&ecirc;n tầm nh&igrave;n của ch&uacute;ng t&ocirc;i, cho tới l&uacute;c đ&oacute; vẫn bị hạn chế trong một con đường bao quanh những ruộng nho, tự nhi&ecirc;n mở ra một kh&ocirc;ng gian m&ecirc;nh m&ocirc;ng. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bordeaux đấy. - Cụ Vitalis n&oacute;i.<br />\r\nĐối với một đứa trẻ như t&ocirc;i, từ trước đến nay mới chỉ nh&igrave;n thấy v&agrave;i th&agrave;nh phố nhỏ gặp tr&ecirc;n đường đi, đ&acirc;y đ&uacute;ng l&agrave; một quang cảnh thần ti&ecirc;n. Tr&ecirc;n s&ocirc;ng t&agrave;u b&egrave; qua lại tấp nập: một số t&agrave;u trở về từ những chuyến đi d&agrave;i ng&agrave;y tr&ecirc;n biển cả, một số kh&aacute;c từ cảng ra đi. Khi tới chiếc cầu nối Bastide với Bordeaux, cụ Vitalis kh&ocirc;ng c&ograve;n th&igrave; giờ để trả lời một phần trăm những c&acirc;u hỏi m&agrave; t&ocirc;i đặt ra cho cụ nữa!&nbsp;Từ Bordeaux ch&uacute;ng t&ocirc;i phải đi qua Pau.&nbsp;H&agrave;nh tr&igrave;nh khiến ch&uacute;ng t&ocirc;i phải đi qua b&atilde;i sa mạc lớn trải d&agrave;i tới tận d&atilde;y Pyr&eacute;n&eacute;es n&agrave;y c&oacute; t&ecirc;n gọi l&agrave; miền Lander. Kh&ocirc;ng c&ograve;n vườn nho, kh&ocirc;ng c&ograve;n đồng cỏ, kh&ocirc;ng c&ograve;n vườn c&acirc;y ăn tr&aacute;i. Hiếm lắm mới tr&ocirc;ng thấy nh&agrave; cửa. B&igrave;nh nguy&ecirc;n trải d&agrave;i trước mặt ch&uacute;ng t&ocirc;i đến v&ocirc; tận, đất m&agrave;u x&aacute;m.&nbsp;Suốt dọc đường chỉ thấy thạch thảo kh&ocirc; với c&acirc;y đậu kim cằn cỗi.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Ch&uacute;ng ta đang ở miền Lander, cụ Vitalis n&oacute;i, ta c&ograve;n phải đi từ hai mươi đến hai mươi nhăm dặm nữa giữa sa mạc n&agrave;y. Đ&ocirc;i ch&acirc;n ch&aacute;u phải can đảm l&ecirc;n.<br />\r\nNhưng kh&ocirc;ng phải chỉ l&agrave;m cho đ&ocirc;i ch&acirc;n can đảm l&ecirc;n m&agrave; c&ograve;n phải l&agrave;m cho c&aacute;i đầu v&agrave; tr&aacute;i tim cũng can đảm l&ecirc;n nữa bởi v&igrave; bước ch&acirc;n tr&ecirc;n con đường tưởng như kh&ocirc;ng bao giờ tận c&ugrave;ng n&agrave;y người ta lu&ocirc;n cảm thấy tr&agrave;n ngập một nỗi buồn kh&ocirc;ng sao chế ngự nổi..Kể từ hồi ấy t&ocirc;i đ&atilde; nhiều lần đi những chuyến đi biển v&agrave; cứ mỗi lần giữa đại dương kh&ocirc;ng một c&aacute;nh buồm t&ocirc;i lại t&igrave;m lại được trong t&ocirc;i c&aacute;i t&igrave;nh cảm buồn b&atilde; kh&ocirc;ng sao định nghĩa nổi m&agrave; t&ocirc;i đ&atilde; cảm thấy trong cảnh c&ocirc; đơn ấy.&nbsp;Pau để lại cho t&ocirc;i một kỷ niệm th&uacute; vị. Ch&uacute;ng t&ocirc;i ở đ&oacute; cả m&ugrave;a đ&ocirc;ng, ng&agrave;y n&agrave;o cũng đi ra phố v&agrave; những nơi c&ocirc;ng cộng. Ch&uacute;ng t&ocirc;i c&oacute; một c&ocirc;ng ch&uacute;ng trẻ con kh&ocirc;ng biết ch&aacute;n c&aacute;c tiết mục của ch&uacute;ng t&ocirc;i.&nbsp;Một buổi s&aacute;ng, ch&uacute;ng t&ocirc;i l&ecirc;n đường. Kh&ocirc;ng biết h&agrave;ng bao nhi&ecirc;u tuần ch&uacute;ng t&ocirc;i cứ thẳng trước mặt m&agrave; tiến bước. Rồi một tối kia ch&uacute;ng t&ocirc;i tới một th&agrave;nh phố lớn. Nh&agrave; cửa x&acirc;y bằng gạch đỏ, phố x&aacute; c&oacute; vỉa h&egrave; l&aacute;t bằng những h&ograve;n cuội nhọn. Ch&uacute;ng t&ocirc;i đ&atilde; đến Toulouse.&nbsp;H&ocirc;m đ&oacute; cũng như mọi khi việc đầu ti&ecirc;n ch&uacute;ng t&ocirc;i lo lắng l&agrave; t&igrave;m nơi thuận tiện để biểu diễn. Ch&uacute;ng t&ocirc;i t&igrave;m được một con đường nhưng một vi&ecirc;n cảnh s&aacute;t bảo ch&uacute;ng t&ocirc;i đi chỗ kh&aacute;c.&nbsp;Mặc d&ugrave; chủ t&ocirc;i chỉ l&agrave; một người l&agrave;m tr&ograve; th&uacute; ngh&egrave;o v&agrave; gi&agrave;, nhưng cụ c&oacute; tự trọng của m&igrave;nh, kh&ocirc;ng chịu đi. Vi&ecirc;n cảnh s&aacute;t quay lưng đi nhưng h&ocirc;m sau lại tới, bước qua d&acirc;y thừng qu&acirc;y th&agrave;nh rạp h&aacute;t của ch&uacute;ng t&ocirc;i v&agrave;o đ&uacute;ng giữa l&uacute;c biểu diễn. Sự can thiệp n&agrave;y g&acirc;y ra nhiều tiếng x&igrave; x&agrave;o.&nbsp;R&otilde; r&agrave;ng l&agrave; người ta ủng hộ cụ Vitalis. Họ chế giễu vi&ecirc;n cảnh s&aacute;t v&agrave; th&iacute;ch th&uacute; với bộ dạng nhăn nh&oacute; của Joli - Coeur.&nbsp;i&ecirc;n cảnh s&aacute;t kh&ocirc;ng c&oacute; vẻ một con người ki&ecirc;n nhẫn, đột ngột quay g&oacute;t.<br />\r\n- Nếu ng&agrave;y mai ch&oacute; của &ocirc;ng kh&ocirc;ng bị buộc m&otilde;m, t&ocirc;i sẽ đưa ra t&ograve;a.<br />\r\nNg&agrave;y h&ocirc;m sau t&ocirc;i tới chỗ mọi khi chăng d&acirc;y thừng. Chỉ vừa dạo l&ecirc;n v&agrave;i nhịp đ&agrave;n h&aacute;c-pơ mọi người đ&atilde; đổ x&ocirc; đến từ tứ ph&iacute;a.&nbsp;Vi&ecirc;n cảnh s&aacute;t cũng đến ngay sau đ&oacute;. Joli -Coeur tr&ocirc;ng thấy trước ti&ecirc;n b&egrave;n hai tay chống nạnh, đầu ngửa ra sau, đi đi lại lại quanh t&ocirc;i người cứng đờ, ngực ưỡn ra với một vẻ oai vệ rất l&agrave; lố bịch.&nbsp;C&ocirc;ng ch&uacute;ng cười ồ l&ecirc;n v&agrave; vỗ tay nhiều tr&agrave;ng d&agrave;i. Vi&ecirc;n cảnh s&aacute;t chưng hửng, n&eacute;m về ph&iacute;a t&ocirc;i những c&aacute;i nh&igrave;n giận dữ l&agrave;m cho c&ocirc;ng ch&uacute;ng c&agrave;ng la &oacute; th&ecirc;m.&nbsp;Bản th&acirc;n t&ocirc;i cũng muốn cười nhưng kh&ocirc;ng d&aacute;m. Vi&ecirc;n cảnh s&aacute;t cứ đi đi lại lại ngo&agrave;i d&acirc;y thừng. T&ocirc;i gọi Joli - Coeur nhưng n&oacute; kh&ocirc;ng chịu v&acirc;ng lời cứ tiếp tục đi như thế, cứ mỗi khi t&ocirc;i muốn t&oacute;m lấy n&oacute; n&oacute; lại chạy tho&aacute;t khỏi tay t&ocirc;i..Vi&ecirc;n cảnh s&aacute;t cho l&agrave; t&ocirc;i k&iacute;ch động con khỉ, phăng phăng bước v&agrave;o b&ecirc;n trong v&ograve;ng d&acirc;y thừng v&agrave; ngay lập tức gi&aacute;ng cho t&ocirc;i hai c&aacute;i t&aacute;t vẹo người.&nbsp;Khi t&ocirc;i đứng vững lại được, cụ Vitalis kh&ocirc;ng hiểu bằng c&aacute;ch n&agrave;o đ&atilde; đứng giữa t&ocirc;i v&agrave; vi&ecirc;n cảnh s&aacute;t, nắm lấy cổ tay vi&ecirc;n cảnh s&aacute;t.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- T&ocirc;i cấm &ocirc;ng kh&ocirc;ng được đ&aacute;nh thằng b&eacute; n&agrave;y.<br />\r\nVi&ecirc;n cảnh s&aacute;t muốn giằng tay ra nhưng cụ Vitalis cứ nắm chặt lấy cổ tay hắn khiến hắn giận đi&ecirc;n l&ecirc;n. Chủ t&ocirc;i tr&ocirc;ng cao thượng tuyệt vời. Vi&ecirc;n cảnh s&aacute;t t&uacute;m lấy cổ &aacute;o chủ t&ocirc;i v&agrave; dữ dằn đẩy cụ ra trước hắn. Cụ Vitalis tức qu&aacute; đứng thẳng người l&ecirc;n đ&aacute;nh mạnh v&agrave;o cổ tay vi&ecirc;n cảnh s&aacute;t để gỡ ra.<br />\r\n- &ocirc;ng muốn g&igrave; ch&uacute;ng t&ocirc;i n&agrave;o? - Cụ Vitalis hỏi.<br />\r\n- T&ocirc;i muốn bắt cụ.<br />\r\n- Kh&ocirc;ng cần thiết phải đ&aacute;nh thằng b&eacute;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Theo t&ocirc;i!<br />\r\nCụ Vitalis đ&atilde; lấy lại được b&igrave;nh tĩnh; cụ kh&ocirc;ng đ&aacute;p lời hắn, nhưng quay lại t&ocirc;i:<br />\r\n- Ch&aacute;u về qu&aacute;n. ở đấy với lũ ch&oacute;. &ocirc;ng sẽ tin cho ch&aacute;u sau.<br />\r\nCụ kh&ocirc;ng n&oacute;i được g&igrave; th&ecirc;m nữa v&igrave; vi&ecirc;n cảnh s&aacute;t đ&atilde; l&ocirc;i cụ đi.&nbsp;Thế l&agrave; tan cuộc biểu diễn.&nbsp;C&ocirc;ng ch&uacute;ng mau ch&oacute;ng tản đi; chỉ c&ograve;n v&agrave;i người ở lại b&agrave;n t&aacute;n về những g&igrave; vừa xảy ra.&nbsp;T&ocirc;i về qu&aacute;n rất lo lắng. Trong thời gian n&agrave;y t&ocirc;i l&agrave;m g&igrave;? Sống thế n&agrave;o? Sống bằng g&igrave;? Chủ t&ocirc;i c&oacute; th&oacute;i quen mang tiền tr&ecirc;n người chưa kịp đưa cho t&ocirc;i đồng n&agrave;o. T&ocirc;i chỉ c&oacute; v&agrave;i xu trong t&uacute;i.&nbsp;T&ocirc;i tr&ocirc;i qua hai ng&agrave;y như vậy trong lo lắng, kh&ocirc;ng d&aacute;m ra khỏi s&acirc;n qu&aacute;n trọ, chỉ tr&ocirc;ng nom Joli - Coeur v&agrave; lũ ch&oacute;.&nbsp;Cuối c&ugrave;ng đến ng&agrave;y thứ ba c&oacute; người mang đến cho t&ocirc;i một l&aacute; thư của cụ Vitalis. Trong thư chủ t&ocirc;i bảo họ cho cụ v&agrave;o t&ugrave; chờ thứ bảy sau đưa ra t&ograve;a tiểu h&igrave;nh về tội đ&atilde; chống lại vi&ecirc;n chức ch&iacute;nh quyền.&nbsp;T&ocirc;i đi nắm th&ecirc;m t&igrave;nh h&igrave;nh, người ta n&oacute;i t&ograve;a xử bắt đầu l&uacute;c mười giờ. Ch&iacute;n giờ s&aacute;ng thứ bảy t&ocirc;i l&agrave; người đầu ti&ecirc;n v&agrave;o ph&ograve;ng xử. Dần dần ph&ograve;ng đ&ocirc;ng l&ecirc;n. T&ocirc;i kh&ocirc;ng hiểu g&igrave; về t&ograve;a &aacute;n v&agrave; ph&aacute;p luật nhưng theo bản năng, t&ocirc;i h&atilde;i h&ugrave;ng kinh khủng. C&oacute; vẻ như ch&uacute;ng t&ocirc;i đang gặp nguy hiểm.&nbsp;T&ocirc;i ngồi thu m&igrave;nh đằng sau chiếc l&ograve; sưởi..Chủ t&ocirc;i kh&ocirc;ng được đem ra xử đầu ti&ecirc;n. Bắt đầu l&agrave; những người đ&atilde; ăn cắp, đ&atilde; đ&aacute;nh nhau, ai cũng n&oacute;i l&agrave; m&igrave;nh v&ocirc; tội nhưng tất thảy đều bị kết &aacute;n. Cuối c&ugrave;ng cụ Vitalis ra ngồi tr&ecirc;n ghế d&agrave;i m&agrave; những người trước cụ đ&atilde; ngồi, giữa hai tay hiến binh.&nbsp;Những g&igrave; l&uacute;c đầu người ta hỏi cụ v&agrave; cụ trả lời như thế n&agrave;o t&ocirc;i kh&ocirc;ng biết g&igrave; hết. T&ocirc;i qu&aacute; x&uacute;c động để nghe v&agrave; để hiểu. Hơn nữa t&ocirc;i kh&ocirc;ng nghĩ đến nghe. T&ocirc;i cứ nh&igrave;n chủ t&ocirc;i đứng thẳng, m&aacute;i t&oacute;c d&agrave;i bạc trắng hất ra sau trong th&aacute;i độ một con người vừa hổ thẹn vừa đau buồn. T&ocirc;i nh&igrave;n vị quan t&ograve;a thẩm vấn cụ. - Như vậy l&agrave; cụ thừa nhận đ&atilde; đ&aacute;nh nhiều lần vi&ecirc;n cảnh s&aacute;t bắt cụ?&nbsp;<br />\r\n- Kh&ocirc;ng, thưa ch&aacute;nh t&ograve;a, chỉ c&oacute; một lần th&ocirc;i ạ, t&ocirc;i gỡ ra khỏi c&aacute;i siết chặt của &ocirc;ng ta. Khi tới nơi ch&uacute;ng t&ocirc;i định biểu diễn t&ocirc;i thấy &ocirc;ng cảnh s&aacute;t t&aacute;t thằng b&eacute; đi c&ugrave;ng với t&ocirc;i.<br />\r\n- Thằng b&eacute; c&oacute; phải con cụ kh&ocirc;ng?<br />\r\n- Kh&ocirc;ng, thưa ch&aacute;nh t&ograve;a, nhưng t&ocirc;i y&ecirc;u n&oacute; như con trai t&ocirc;i. Khi thấy n&oacute; bị đ&aacute;nh, t&ocirc;i đ&atilde; để m&igrave;nh cả giận mất kh&ocirc;n, nắm chặt lấy b&agrave;n tay &ocirc;ng cảnh s&aacute;t ngăn &ocirc;ng ta tiếp tục đ&aacute;nh n&oacute; nữa.<br />\r\n- Ch&iacute;nh &ocirc;ng cũng đ&aacute;nh vi&ecirc;n cảnh s&aacute;t chứ g&igrave;?<br />\r\n- Khi &ocirc;ng n&agrave;y nắm v&agrave;o cổ tay t&ocirc;i t&ocirc;i qu&ecirc;n mất người lao v&agrave;o t&ocirc;i l&agrave; ai v&agrave; một động t&aacute;c bản năng, kh&ocirc;ng cố &yacute; đ&atilde; đưa t&ocirc;i đi qu&aacute; xa.<br />\r\n- ở tuổi &ocirc;ng kh&ocirc;ng thể để m&igrave;nh đi qu&aacute; xa được.<br />\r\n- Kh&ocirc;ng may l&agrave; kh&ocirc;ng phải người ta l&uacute;c n&agrave;o cũng l&agrave;m được những việc phải l&agrave;m.<br />\r\n- Ta sẽ nghe đến &ocirc;ng cảnh s&aacute;t.<br />\r\nTay n&agrave;y kể lại những chuyện đ&atilde; xảy ra, nhấn mạnh v&agrave;o c&aacute;ch người ta chế giễu con người hắn hơn l&agrave; c&uacute; bị đ&aacute;nh.&nbsp;Trong khi tay cảnh s&aacute;t khai với t&ograve;a cụ Vitalis đ&aacute;ng lẽ ch&uacute; &yacute; nghe th&igrave; lại nh&igrave;n quanh khắp gian ph&ograve;ng. T&ocirc;i hiểu cụ t&igrave;m t&ocirc;i. T&ocirc;i b&egrave;n rời chỗ nấp, len lỏi giữa những người t&ograve; m&ograve;, l&ecirc;n h&agrave;ng ghế đầu. Cụ nh&igrave;n thấy t&ocirc;i v&agrave; gương mặt buồn rầu của cụ s&aacute;ng hẳn l&ecirc;n, t&ocirc;i cảm thấy cụ sung sướng khi nh&igrave;n thấy t&ocirc;i, nước mắt tr&agrave;n mi t&ocirc;i d&ugrave; t&ocirc;i cố k&igrave;m lại.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Cụ chỉ n&oacute;i c&oacute; thế để bảo vệ m&igrave;nh th&ocirc;i ư?<br />\r\n- Cuối c&ugrave;ng chủ tọa phi&ecirc;n t&ograve;a hỏi.<br />\r\n- Phần t&ocirc;i t&ocirc;i chẳng c&oacute; g&igrave; để n&oacute;i th&ecirc;m cả, nhưng v&igrave; đứa b&eacute; m&agrave; t&ocirc;i thương y&ecirc;u thắm thiết sắp sửa tứ cố v&ocirc; th&acirc;n, v&igrave; n&oacute;, xin t&ograve;a rộng lượng.để ch&uacute;ng t&ocirc;i chỉ phải xa nhau trong thời gian ngắn nhất.<br />\r\nT&ocirc;i cứ tưởng người ta trả lại tự do cho chủ t&ocirc;i, cụ sẽ kh&ocirc;ng l&agrave;m sao hết.&nbsp;Một vi&ecirc;n thẩm ph&aacute;n kh&aacute;c ph&aacute;t biểu v&agrave;i ph&uacute;t, rồi đến chủ tọa phi&ecirc;n t&ograve;a, bằng giọng trang trọng, n&oacute;i rằng người c&oacute; t&ecirc;n Vitalis đ&atilde; lăng mạ v&agrave; c&oacute; những h&agrave;nh động l&agrave;m tổn thương đến nh&acirc;n vi&ecirc;n ch&iacute;nh quyền, bị kết &aacute;n hai th&aacute;ng t&ugrave; v&agrave; bồi thường một trăm phrăng.&nbsp;Qua h&agrave;ng nước mắt t&ocirc;i nh&igrave;n thấy c&aacute;nh cửa qua đ&oacute; cụ Vitalis đi v&agrave;o nay lại mở ra, đằng sau cụ l&agrave; một tay hiến binh, sau đ&oacute; cửa đ&oacute;ng lại.&nbsp;Hai th&aacute;ng xa c&aacute;ch! Đi đ&acirc;u b&acirc;y giờ ?&nbsp;Khi về tới qu&aacute;n trọ l&ograve;ng t&ocirc;i nặng trĩu, hai mắt hoe đỏ, t&ocirc;i thấy &ocirc;ng chủ qu&aacute;n đ&atilde; chờ t&ocirc;i ở cửa.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thế n&agrave;o, &ocirc;ng ta hỏi, chủ m&agrave;y thế n&agrave;o?<br />\r\n- Bị kết &aacute;n hai th&aacute;ng t&ugrave; v&agrave; bồi thường một trăm phrăng.<br />\r\n- Thế trong hai th&aacute;ng n&agrave;y m&agrave;y l&agrave;m g&igrave;?<br />\r\n- Thưa &ocirc;ng, ch&aacute;u kh&ocirc;ng biết.<br />\r\n- &agrave;! M&agrave;y kh&ocirc;ng biết? M&agrave;y c&oacute; tiền tự nu&ocirc;i th&acirc;n v&agrave; nu&ocirc;i lũ vật n&agrave;y kh&ocirc;ng? - Thưa &ocirc;ng kh&ocirc;ng ạ.<br />\r\n- Thế m&agrave;y tr&ocirc;ng v&agrave;o tao để c&oacute; chỗ ở chắc?<br />\r\n- ồ, kh&ocirc;ng ạ. Ch&aacute;u c&oacute; tr&ocirc;ng v&agrave;o ai đ&acirc;u.<br />\r\nLời t&ocirc;i n&oacute;i kh&ocirc;ng g&igrave; thực bằng.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Được đấy con ạ, m&agrave;y n&oacute;i đ&uacute;ng, chủ m&agrave;y đ&atilde; nợ tao qu&aacute; nhiều rồi, tao kh&ocirc;ng thể để m&agrave;y nợ th&ecirc;m nữa m&agrave; r&uacute;t cục kh&ocirc;ng chắc c&oacute; được trả hay kh&ocirc;ng. M&agrave;y phải đi th&ocirc;i.<br />\r\n- &ocirc;ng muốn ch&aacute;u đi đ&acirc;u ạ?<br />\r\n- C&oacute; phải việc của tao đ&acirc;u, tao chẳng phải bố m&agrave;y cũng kh&ocirc;ng phải chủ m&agrave;y, tao giữ m&agrave;y l&agrave;m g&igrave;? Tất nhi&ecirc;n m&agrave;y phải để lại đ&acirc;y c&aacute;i t&uacute;i của chủ m&agrave;y. Khi n&agrave;o chủ m&agrave;y ra t&ugrave; sẽ đến đ&acirc;y lấy t&uacute;i v&agrave; thanh to&aacute;n. C&ograve;n m&agrave;y, m&agrave;y l&agrave;m g&igrave; chẳng t&igrave;m được c&aacute;ch kiếm sống. Đi đến những suối nước n&oacute;ng ấy, kiếm tiền được đấy. M&agrave;y chỉ cần trở lại khi n&agrave;o chủ m&agrave;y ra.<br />\r\nT&ocirc;i cảm thấy c&oacute; chống lại cũng v&ocirc; &iacute;ch. T&ocirc;i v&agrave;o chuồng ngựa, th&aacute;o ch&oacute; v&agrave; Joli - Coeur, buộc chặt c&aacute;i t&uacute;i của m&igrave;nh, đeo d&acirc;y chiếc đ&agrave;n h&aacute;c-pơ l&ecirc;n vai, đi ra khỏi qu&aacute;n.&nbsp;Chủ qu&aacute;n đứng ở cửa.&nbsp;<br />\r\n- Nếu c&oacute; thư tao sẽ giữ cho m&agrave;y. - &ocirc;ng ta k&ecirc;u to bảo t&ocirc;i.<br />\r\nT&ocirc;i vội v&agrave;ng đi khỏi th&agrave;nh phố n&agrave;y. T&ocirc;i chỉ c&oacute; mười một xu. T&ocirc;i đ&atilde; trở th&agrave;nh chủ gia đ&igrave;nh, t&ocirc;i, một đứa b&eacute; kh&ocirc;ng gia đ&igrave;nh, v&agrave; t&ocirc;i &yacute; thức được tr&aacute;ch nhiệm của m&igrave;nh.&nbsp;Mười một xu của t&ocirc;i kh&ocirc;ng thể cho ch&uacute;ng t&ocirc;i ăn cả bữa s&aacute;ng lẫn bữa tối, ch&uacute;ng t&ocirc;i đ&agrave;nh d&ugrave;ng một bữa v&agrave;o giữa trưa. Qu&aacute;n trọ nơi ch&uacute;ng t&ocirc;i vừa bị đuổi ở ngoại &ocirc; Saint Michel tr&ecirc;n đường đi Montpellier, lẽ tự nhi&ecirc;n l&agrave; t&ocirc;i theo con đường ấy.&nbsp;Cuối c&ugrave;ng t&ocirc;i thấy đ&atilde; đủ xa Toulouse để kh&ocirc;ng c&ograve;n sợ nữa. T&ocirc;i v&agrave;o hiệu b&aacute;nh m&igrave; đầu ti&ecirc;n y&ecirc;u cầu b&aacute;n cho một pao rưỡi v&agrave; trả họ t&aacute;m xu. T&ocirc;i đi ra cắp chặt b&aacute;nh m&igrave; dưới c&aacute;nh tay. Tới c&aacute;i c&acirc;y đầu ti&ecirc;n gặp tr&ecirc;n đường, ch&uacute;ng t&ocirc;i y&ecirc;n vị, t&ocirc;i chia đều chiếc b&aacute;nh m&igrave; tr&ograve;n ra cho năm ch&uacute;ng t&ocirc;i.&nbsp;Mặc d&ugrave; bữa tiệc n&agrave;y kh&ocirc;ng phải l&agrave; một bữa tiệc sau đ&oacute; cần một b&agrave;i diễn văn, t&ocirc;i vẫn thấy đ&atilde; đến l&uacute;c cần n&oacute;i với c&aacute;c bạn t&ocirc;i một v&agrave;i c&acirc;u. C&oacute; lẽ Capi hiểu &yacute; định của t&ocirc;i v&igrave; n&oacute; cứ d&aacute;n chặt đ&ocirc;i mắt th&ocirc;ng minh v&agrave; &acirc;u yếm của n&oacute; v&agrave;o mắt t&ocirc;i.&nbsp;<br />\r\n- Phải, c&aacute;c bạn ạ, t&ocirc;i c&oacute; một tin xấu b&aacute;o cho c&aacute;c bạn đ&acirc;y. Chủ ch&uacute;ng ta phải xa ch&uacute;ng ta hai th&aacute;ng. Việc n&agrave;y trước nhất l&agrave;m cụ rất buồn, sau đến ch&uacute;ng ta cũng vậy. Cụ l&agrave; người nu&ocirc;i ch&uacute;ng ta sống, vắng cụ, ch&uacute;ng ta rơi v&agrave;o t&igrave;nh cảnh kinh khủng. Ch&uacute;ng ta kh&ocirc;ng c&oacute; tiền.<br />\r\nNghe thấy chữ tiền m&agrave; n&oacute; rất hiểu, Capi b&egrave;n đứng l&ecirc;n tr&ecirc;n hai ch&acirc;n sau đi một v&ograve;ng như vẫn đi quanh &quot;cử tọa đ&aacute;ng k&iacute;nh&quot; vậy.<br />\r\n- M&agrave;y muốn ch&uacute;ng ta biểu diễn chứ g&igrave;, t&ocirc;i n&oacute;i tiếp, quả đ&oacute; l&agrave; một lời khuy&ecirc;n tốt. Nhưng liệu ch&uacute;ng ta c&oacute; thu được g&igrave; kh&ocirc;ng? Tất cả l&agrave; ở đ&oacute;. Nếu ch&uacute;ng ta kh&ocirc;ng th&agrave;nh c&ocirc;ng, t&ocirc;i xin b&aacute;o trước với c&aacute;c bạn l&agrave; cả gia sản ch&uacute;ng ta chỉ c&oacute; ba xu. Phải thắt lưng buộc bụng. T&igrave;nh h&igrave;nh l&agrave; như vậy đấy, t&ocirc;i mong c&aacute;c bạn hiểu sự nghi&ecirc;m trọng của n&oacute;, đem hết th&ocirc;ng minh ra phục vụ x&atilde; hội ch&uacute;ng ta. T&ocirc;i đ&ograve;i hỏi c&aacute;c bạn v&acirc;ng lời v&agrave; can đảm. H&atilde;y tr&ocirc;ng cậy v&agrave;o t&ocirc;i, cũng như t&ocirc;i tr&ocirc;ng cậy ở c&aacute;c bạn.<br />\r\nNghỉ ngơi một l&aacute;t t&ocirc;i ra lệnh l&ecirc;n đường: phải kiếm một chỗ ngủ, kiếm bữa s&aacute;ng mai, muốn vậy ch&uacute;ng t&ocirc;i đ&agrave;nh ngủ ngo&agrave;i trời để tiết kiệm.&nbsp;Sau khoảng một giờ đi bộ ch&uacute;ng t&ocirc;i tới một ng&ocirc;i l&agrave;ng xem ra ph&ugrave; hợp với việc thực hiện mục đ&iacute;ch của t&ocirc;i. T&ocirc;i trang điểm c&aacute;c kịch sĩ của m&igrave;nh, đi th&agrave;nh h&agrave;ng ngũ chỉnh tề nhất c&oacute; thể, tiến v&agrave;o l&agrave;ng. Kh&ocirc;ng may thiếu ống s&aacute;o của cụ Vitalis, thiếu cả c&aacute;i oai vệ của cụ để l&agrave;m mọi người phải nh&igrave;n. T&ocirc;i cũng lại kh&ocirc;ng c&oacute; d&aacute;ng cao lớn v&agrave; c&aacute;i đầu diễn cảm của cụ, tr&ecirc;n mặt t&ocirc;i thể hiện nỗi lo lắng th&igrave; đ&uacute;ng hơn l&agrave; sự tự tin.&nbsp;Tới một b&atilde;i đất ở giữa c&oacute; một c&aacute;i m&aacute;y nước dưới b&oacute;ng mấy c&acirc;y ti&ecirc;u huyền, t&ocirc;i lấy đ&agrave;n h&aacute;c-pơ ra dạo một kh&uacute;c van-xơ. Nhạc rất vui, ng&oacute;n tay t&ocirc;i nhẹ nh&agrave;ng nhưng l&ograve;ng t&ocirc;i buồn b&atilde;, t&ocirc;i cảm thấy m&igrave;nh g&aacute;nh tr&ecirc;n hai vai một trọng lượng qu&aacute; nặng.&nbsp;T&ocirc;i bảo Zerbino v&agrave; Dolce ra nhảy, ch&uacute;ng v&acirc;ng lời ngay v&agrave; ra nhảy theo nhạc. Nhưng chẳng ai buồn tới xem ch&uacute;ng t&ocirc;i cả mặc dầu tr&ecirc;n ngưỡng c&aacute;c cửa ra v&agrave;o t&ocirc;i nh&igrave;n thấy những người phụ nữ ngồi đan l&aacute;t hoặc chuyện tr&ograve; với nhau.&nbsp;T&ocirc;i tiếp tục chơi đ&agrave;n, Zerbino v&agrave; Dolce tiếp tục nhảy. H&igrave;nh như c&oacute; người tiến lại gần ch&uacute;ng t&ocirc;i, nếu một người đến th&igrave; sẽ c&oacute; hai người, rồi mười, hai mươi người kh&aacute;c nữa.&nbsp;Nhưng tha hồ t&ocirc;i chơi, tha hồ Zerbino v&agrave; Dolce nhảy, mọi người vẫn cứ y&ecirc;n vị trong nh&agrave; thậm ch&iacute; chẳng th&egrave;m nh&igrave;n về ph&iacute;a ch&uacute;ng t&ocirc;i nữa.&nbsp;Thật tuyệt vọng!.C&oacute; lẽ những người n&agrave;y kh&ocirc;ng th&iacute;ch nhảy chăng? T&ocirc;i bắt đầu h&aacute;t kh&uacute;c can-z&ocirc;. T&ocirc;i thấy một người đ&agrave;n &ocirc;ng mặc &aacute;o v&eacute;t đội mũ phớt tiến đến gần ch&uacute;ng t&ocirc;i. T&ocirc;i h&aacute;t c&agrave;ng hăng h&aacute;i.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- N&agrave;y! - &ocirc;ng ta k&ecirc;u l&ecirc;n. - M&agrave;y l&agrave;m g&igrave; ở đ&acirc;y thế thằng nghịch ngợm n&agrave;y?<br />\r\n- &ocirc;ng thấy đấy, t&ocirc;i đang h&aacute;t.<br />\r\n- M&agrave;y c&oacute; giấy ph&eacute;p h&aacute;t ở x&atilde; tao kh&ocirc;ng?<br />\r\n- Kh&ocirc;ng ạ.<br />\r\n- Thế th&igrave; c&uacute;t nếu kh&ocirc;ng tao đưa ra t&ograve;a b&acirc;y giờ, thằng ăn m&agrave;y xấu xa n&agrave;y. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tao l&agrave; người g&aacute;c đồng qu&ecirc; đ&acirc;y!&nbsp;Người g&aacute;c đồng qu&ecirc;!&nbsp;T&ocirc;i lại đi tr&ecirc;n con đường m&agrave; t&ocirc;i đ&atilde; đến.&nbsp;T&ocirc;i c&oacute; đi xin ăn đ&acirc;u: T&ocirc;i h&aacute;t, t&ocirc;i nhảy, đ&oacute; l&agrave; c&aacute;ch l&agrave;m việc của t&ocirc;i, t&ocirc;i c&oacute; l&agrave;m g&igrave; xấu đ&acirc;u!&nbsp;<br />\r\nChỉ năm ph&uacute;t sau t&ocirc;i đ&atilde; ra khỏi c&aacute;i th&ocirc;n x&oacute;m k&eacute;m hiếu kh&aacute;ch n&agrave;y. Lũ ch&oacute; theo t&ocirc;i, đầu c&uacute;i xuống, hiểu l&agrave; ch&uacute;ng t&ocirc;i đ&atilde; gặp chuyện kh&ocirc;ng may.&nbsp;Ch&uacute;ng t&ocirc;i cứ theo con đường trắng trước mặt m&agrave; đi. Hết c&acirc;y số n&agrave;y sang c&acirc;y số kh&aacute;c, cho đến l&uacute;c những &aacute;nh hồng cuối c&ugrave;ng của mặt trời đang lặn đ&atilde; biến mất khỏi bầu trời, ch&uacute;ng t&ocirc;i vẫn chưa t&igrave;m thấy một chỗ n&agrave;o c&oacute; thể ngủ được.&nbsp;Ch&uacute;ng t&ocirc;i dừng ch&acirc;n b&ecirc;n một c&aacute;nh rừng, nghỉ ngơi ở đ&oacute;, Capi canh g&aacute;c.&nbsp;Ng&agrave;y du h&agrave;nh đầu ti&ecirc;n thế n&agrave;y thật l&agrave; xấu.&nbsp;Chuyến đi ng&agrave;y mai sẽ ra sao? T&ocirc;i đ&oacute;i v&agrave; kh&aacute;t, m&agrave; chỉ c&ograve;n c&oacute; ba xu. L&agrave;m sao nu&ocirc;i cả đo&agrave;n, nu&ocirc;i bản th&acirc;n t&ocirc;i, v&agrave; nếu ng&agrave;y mai v&agrave; cả những ng&agrave;y tiếp theo nữa kh&ocirc;ng t&igrave;m được c&aacute;ch biểu diễn? Chết đ&oacute;i cả hay sao?&nbsp;Vừa lật đi lật lại những c&acirc;u hỏi n&agrave;y t&ocirc;i vừa nh&igrave;n những v&igrave; sao lấp l&aacute;nh tr&ecirc;n đầu trong bầu trời tối thẫm. Ch&uacute;ng t&ocirc;i bơ vơ qu&aacute; v&agrave; bị bỏ rơi!&nbsp;T&ocirc;i cảm thấy mắt m&igrave;nh ngập lệ rồi bỗng nhi&ecirc;n t&ocirc;i &ograve;a kh&oacute;c. T&ocirc;i kh&oacute;c trong hai b&agrave;n tay, kh&oacute;c kh&ocirc;ng sao ngừng lại được v&agrave; bỗng t&ocirc;i cảm thấy một hơi thở &acirc;m ấm lướt qua t&oacute;c m&igrave;nh: đ&oacute; l&agrave; Capi.&nbsp;T&ocirc;i lấy hai c&aacute;nh tay &ocirc;m lấy cổ n&oacute; v&agrave; h&ocirc;n l&ecirc;n c&aacute;i m&otilde;m ẩm của Capi, n&oacute; b&egrave;n thốt l&ecirc;n hai ba tiếng r&ecirc;n nghẹn ng&agrave;o, c&oacute; lẽ n&oacute; cũng kh&oacute;c với t&ocirc;i.&nbsp;Khi t&ocirc;i tỉnh dậy, trời đ&atilde; s&aacute;ng r&otilde; v&agrave; Capi ngồi trước mặt t&ocirc;i, đang nh&igrave;n t&ocirc;i, chim h&oacute;t tr&ecirc;n c&agrave;nh l&aacute;, mặt trời đ&atilde; l&ecirc;n cao, chiếu xuống những tia nắng n&oacute;ng l&agrave;m ấm cả l&ograve;ng người..T&ocirc;i đ&atilde; quyết định: ti&ecirc;u nốt ba xu rồi sau đ&oacute; liệu sau.<br />\r\n<br />\r\nTới l&agrave;ng, chẳng cần hỏi thăm đ&acirc;u l&agrave; hiệu b&aacute;nh m&igrave;, c&aacute;i mũi ch&uacute;ng t&ocirc;i đ&atilde; đưa ch&uacute;ng t&ocirc;i tới đ&oacute;. Ba xu b&aacute;nh m&igrave; chỉ đủ cho ch&uacute;ng t&ocirc;i một bữa s&aacute;ng c&ograve;m c&otilde;i, nuốt qu&aacute; nhanh ch&oacute;ng.&nbsp;Sau đ&oacute; t&ocirc;i đi qua ng&ocirc;i l&agrave;ng t&igrave;m xem c&oacute; chỗ n&agrave;o thuận tiện cho biểu diễn kh&ocirc;ng.&nbsp;&yacute; định của t&ocirc;i l&agrave; nghi&ecirc;n cứu địa phương trước đ&atilde;, đến trưa mới quay về thử vận may. T&ocirc;i nhằm một nơi để ch&uacute;ng t&ocirc;i tạm thu m&igrave;nh trong chốc l&aacute;t ở v&ugrave;ng đồng qu&ecirc; b&ecirc;n cạnh, b&ecirc;n bờ một con s&ocirc;ng đ&agrave;o. Ch&aacute;n nản x&acirc;m chiếm l&ograve;ng ch&uacute;ng t&ocirc;i, nhu cầu ăn trở n&ecirc;n th&ocirc;i th&uacute;c. Phải tạo ra một c&ocirc;ng việc g&igrave; đ&oacute; cho n&oacute; bận bịu.&nbsp;T&ocirc;i nhớ lại cụ Vitalis bảo trong chiến tranh khi một trung đo&agrave;n h&agrave;nh qu&acirc;n l&acirc;u mệt mỏi, người ta chơi nhạc khiến binh l&iacute;nh qu&ecirc;n cả mệt.&nbsp;T&ocirc;i lấy đ&agrave;n h&aacute;c-pơ ra dạo một điệu nhảy rồi một kh&uacute;c van-xơ. L&uacute;c đầu c&aacute;c diễn vi&ecirc;n của t&ocirc;i xem chừng chưa sẵn s&agrave;ng lắm, r&otilde; r&agrave;ng một mẩu b&aacute;nh l&uacute;c n&agrave;y mới l&agrave;m n&ecirc;n chuyện. Nhưng dần dần &acirc;m nhạc ph&aacute;t huy hiệu quả: ch&uacute;ng t&ocirc;i qu&ecirc;n mẩu b&aacute;nh kh&ocirc;ng c&oacute; v&agrave; t&ocirc;i chỉ c&ograve;n nghĩ đến chơi nhạc, lũ ch&oacute; chỉ c&ograve;n nghĩ đến nhảy m&agrave; th&ocirc;i.&nbsp;Bỗng nhi&ecirc;n t&ocirc;i nghe thấy giọng trong trẻo của một đứa trẻ reo l&ecirc;n: Hoan h&ocirc;!<br />\r\n<br />\r\nMột chiếc t&agrave;u dừng lại tr&ecirc;n s&ocirc;ng đ&agrave;o, mũi quay về ph&iacute;a bờ t&ocirc;i đang đứng, hai con ngựa k&eacute;o n&oacute; dừng lại ở bờ b&ecirc;n kia. Đ&oacute; l&agrave; một chiếc t&agrave;u đặc biệt, t&ocirc;i chưa tr&ocirc;ng thấy c&aacute;i t&agrave;u n&agrave;o giống thế bao giờ: n&oacute; ngắn hơn nhiều so với c&aacute;c x&agrave;-lan thường phục vụ việc đi lại tr&ecirc;n s&ocirc;ng, tr&ecirc;n cầu t&agrave;u lại được x&acirc;y một nh&agrave; cầu lắp k&iacute;nh. Ph&iacute;a trước nh&agrave; cầu l&agrave; một c&aacute;i hi&ecirc;n c&oacute; b&oacute;ng m&aacute;t của những c&acirc;y leo che phủ. C&agrave;nh l&aacute; c&acirc;y m&oacute;c v&agrave;o chỗ n&agrave;y một t&iacute; chỗ kia một t&iacute; ở c&aacute;c vết cắt tr&ecirc;n m&aacute;i nh&agrave; rủ xuống từng ch&ugrave;m d&acirc;y leo xanh mướt. Dưới c&aacute;i hi&ecirc;n đ&oacute; t&ocirc;i tr&ocirc;ng thấy hai người: một phu nh&acirc;n c&ograve;n trẻ, vẻ qu&yacute; ph&aacute;i v&agrave; buồn buồn đang đứng v&agrave; một cậu b&eacute; trạc tuổi t&ocirc;i h&igrave;nh như đang nằm.&nbsp;C&oacute; lẽ đ&oacute; l&agrave; thằng b&eacute; đ&atilde; reo l&ecirc;n: Hoan h&ocirc;!&nbsp;Kh&ocirc;ng c&ograve;n ngạc nhi&ecirc;n nữa t&ocirc;i n&acirc;ng mũ cảm ơn cậu b&eacute; đ&atilde; vỗ tay t&ocirc;i B&agrave; phu nh&acirc;n hỏi với giọng người nước ngo&agrave;i:<br />\r\n- Ch&aacute;u chỉ v&igrave; th&iacute;ch m&agrave; chơi đ&agrave;n đấy chứ?<br />\r\n- Dạ để l&agrave;m cho c&aacute;c kịch sĩ của ch&aacute;u diễn tr&ograve;... với lại cũng để cho khu&acirc;y khỏa.<br />\r\nCậu b&eacute; ra hiệu v&agrave; b&agrave; ta c&uacute;i xuống..- Ch&aacute;u muốn tiếp tục chơi đ&agrave;n nữa kh&ocirc;ng?<br />\r\n- B&agrave; ta hỏi.<br />\r\n- Xin chơi một điệu nhảy, v&agrave; nếu cử tọa đ&aacute;ng k&iacute;nh muốn, t&ocirc;i sẽ giới thiệu nhiều tr&ograve; kh&aacute;c nữa giống như c&aacute;c tiết mục trong c&aacute;c rạp xiếc ở Paris.<br />\r\nĐ&oacute; l&agrave; một c&acirc;u n&oacute;i của chủ t&ocirc;i, t&ocirc;i cố gắng tu&ocirc;n ra như cụ một c&aacute;ch cao qu&yacute;. T&ocirc;i cầm lấy chiếc đ&agrave;n h&aacute;c-pơ v&agrave; bắt đầu chơi một điệu van-xơ, lập tức Capi lấy hai tay &ocirc;m lấy m&igrave;nh Dolce v&agrave; ch&uacute;ng bắt đầu nhảy theo nhạc. Sau đ&oacute; Joli -Coeur nhảy một điệu s&ocirc;-l&ocirc;. Cứ thế ch&uacute;ng t&ocirc;i tr&igrave;nh diễn tất cả mọi tiết mục kh&ocirc;ng biết mệt l&agrave; g&igrave;. C&aacute;c diễn vi&ecirc;n h&agrave;i kịch của t&ocirc;i đều hiểu rằng người ta sẽ trả c&ocirc;ng cho nỗi kh&oacute; nhọc của ch&uacute;ng bằng một bữa cơm, ch&uacute;ng l&agrave;m việc kh&ocirc;ng tiếc m&igrave;nh, cả t&ocirc;i cũng vậy. Vừa chơi đ&agrave;n vừa tr&ocirc;ng coi c&aacute;c diễn vi&ecirc;n, thỉnh thoảng t&ocirc;i lại nh&igrave;n cậu b&eacute;, c&oacute; điều kỳ lạ l&agrave; tuy c&oacute; vẻ rất th&iacute;ch những tr&ograve; biểu diễn của ch&uacute;ng t&ocirc;i, n&oacute; vẫn cứ nằm bất động, chỉ động đậy hai b&agrave;n tay để vỗ tay t&aacute;n thưởng ch&uacute;ng t&ocirc;i m&agrave; th&ocirc;i. N&oacute; liệt chăng?<br />\r\nNgọn gi&oacute; v&ocirc; t&igrave;nh đ&atilde; đưa chiếc t&agrave;u s&aacute;t v&agrave;o bờ nơi t&ocirc;i đứng, v&agrave; l&uacute;c n&agrave;y t&ocirc;i nh&igrave;n cậu b&eacute; r&otilde; như t&ocirc;i ở ngay tr&ecirc;n t&agrave;u vậy: t&oacute;c n&oacute; v&agrave;ng, mặt n&oacute; nhợt nhạt đến nỗi nh&igrave;n thấy cả những mạch m&aacute;u xanh dưới l&agrave;n da trong suốt.<br />\r\n- Trả tiền c&aacute;c ch&aacute;u diễn như thế n&agrave;o nhỉ? -B&agrave; phu nh&acirc;n hỏi.<br />\r\n- Tuỳ theo kh&aacute;n giả th&iacute;ch nhiều hay &iacute;t.<br />\r\n- Mẹ ơi, thế th&igrave; trả cho đắt v&agrave;o mẹ ạ. - Cậu b&eacute; n&oacute;i.<br />\r\nRồi n&oacute; n&oacute;i th&ecirc;m mấy lời bằng một thứ tiếng m&agrave; t&ocirc;i kh&ocirc;ng hiểu.<br />\r\n- Arthur muốn nh&igrave;n gần c&aacute;c diễn vi&ecirc;n xem sao. - B&agrave; ta bảo t&ocirc;i.<br />\r\nT&ocirc;i ra hiệu cho lũ ch&oacute;, ch&uacute;ng nhảy ph&oacute;c xuống t&agrave;u. Joli - Coeur nhảy th&igrave; dễ rồi nhưng t&ocirc;i kh&ocirc;ng bao giờ tin ở n&oacute;: một khi l&ecirc;n t&agrave;u n&oacute; c&oacute; thể giở những tr&ograve; hề biết đ&acirc;u kh&ocirc;ng hợp với thị hiếu của b&agrave; qu&yacute; ph&aacute;i th&igrave; sao?<br />\r\n- Con khỉ c&oacute; dữ kh&ocirc;ng? - B&agrave; ta hỏi.<br />\r\n- Thưa b&agrave; kh&ocirc;ng ạ, nhưng kh&ocirc;ng phải l&uacute;c n&agrave;o n&oacute; cũng v&acirc;ng lời.<br />\r\n- Thế th&igrave; ch&aacute;u h&atilde;y xuống t&agrave;u c&ugrave;ng với n&oacute;.<br />\r\nN&oacute;i xong b&agrave; ra hiệu cho một người đ&agrave;n &ocirc;ng đứng ở ph&iacute;a sau gần b&aacute;nh l&aacute;i v&agrave; lập tức người n&agrave;y đi ra ph&iacute;a trước, bắc một tấm v&aacute;n l&ecirc;n bờ l&agrave;m th&agrave;nh một c&aacute;i cầu cho ph&eacute;p t&ocirc;i l&ecirc;n t&agrave;u m&agrave;.kh&ocirc;ng phải nhảy liều nguy hiểm. Thế l&agrave; t&ocirc;i bước l&ecirc;n t&agrave;u một c&aacute;ch trang trọng, chiếc đ&agrave;n h&aacute;c-pơ tr&ecirc;n vai v&agrave; Joli - Coeur trong tay.<br />\r\n- Con khỉ! Con khỉ! - Arthur k&ecirc;u l&ecirc;n.<br />\r\nT&ocirc;i đến gần cậu b&eacute; v&agrave; trong khi n&oacute; nịnh nọt vuốt ve con khỉ t&ocirc;i tha hồ ngắm n&oacute;.&nbsp;Thật l&agrave; lạ! Quả l&agrave; n&oacute; bị buộc v&agrave;o một tấm v&aacute;n đ&uacute;ng như l&uacute;c đầu t&ocirc;i đ&atilde; nghĩ.&nbsp;<br />\r\n- Ch&aacute;u c&oacute; cha mẹ hay &iacute;t nhất l&agrave; một &ocirc;ng chủ chứ? - B&agrave; qu&yacute; ph&aacute;i hỏi.<br />\r\n- C&oacute; ạ, nhưng l&uacute;c n&agrave;y ch&aacute;u sống một m&igrave;nh, trong hai th&aacute;ng.<br />\r\n- &ocirc;i, ch&uacute; b&eacute; đ&aacute;ng thương! L&agrave;m sao phải sống một m&igrave;nh l&acirc;u đến thế, ở tuổi ch&aacute;u!<br />\r\n- Phải vậy ạ, thưa b&agrave;.<br />\r\n- &ocirc;ng chủ c&oacute; lẽ bắt ch&aacute;u sau hai th&aacute;ng phải đem về cho &ocirc;ng ta một m&oacute;n tiền chăng?<br />\r\n- Kh&ocirc;ng ạ, chỉ cốt đủ sống c&ugrave;ng g&aacute;nh h&aacute;t th&ocirc;i ạ.<br />\r\n- Cho tới h&ocirc;m nay ch&aacute;u đủ sống chứ?<br />\r\nT&ocirc;i do dự trước khi trả lời. T&ocirc;i chưa bao giờ gặp một người đ&agrave;n b&agrave; qu&yacute; ph&aacute;i l&agrave;m nảy sinh trong t&ocirc;i một t&igrave;nh cảm t&ocirc;n trọng như con người đang hỏi t&ocirc;i đ&acirc;y. B&agrave; hỏi t&ocirc;i với biết bao l&ograve;ng tốt, giọng b&agrave; sao m&agrave; dịu d&agrave;ng, c&aacute;i nh&igrave;n của b&agrave; sao m&agrave; nh&atilde; nhặn, thế l&agrave; t&ocirc;i quyết định n&oacute;i sự thực.&nbsp;T&ocirc;i kể cho b&agrave; nghe v&igrave; sao t&ocirc;i phải xa cụ Vitalis v&agrave; l&agrave;m sao m&agrave; từ khi dời Toulouse t&ocirc;i kh&ocirc;ng sao kiếm nổi một xu.&nbsp;Trong khi t&ocirc;i n&oacute;i chuyện Arthur chơi với lũ ch&oacute; tuy vậy n&oacute; vẫn lắng nghe.<br />\r\n- Thế th&igrave; bọn anh hẳn đang đ&oacute;i lắm nhỉ. -N&oacute; k&ecirc;u l&ecirc;n.<br />\r\nNghe thấy từ đ&oacute;i m&agrave; ch&uacute;ng hiểu rất r&otilde;, lũ ch&oacute; sủa ầm l&ecirc;n c&ograve;n Joli - Coeur th&igrave; lấy tay xoa bụng một c&aacute;ch cuồng nhiệt.<br />\r\n- &ocirc;i, mẹ ơi! - Arthur n&oacute;i.<br />\r\nB&agrave; qu&yacute; ph&aacute;i hiểu ngay lời gọi đ&oacute; l&agrave; nghĩa thế n&agrave;o, b&agrave; n&oacute;i v&agrave;i lời bằng tiếng nước ngo&agrave;i với một người đ&agrave;n b&agrave; vừa th&ograve; đầu ra khỏi c&aacute;nh cửa h&eacute; mở v&agrave; gần như ngay sau đ&oacute; b&agrave; ta bưng ra một c&aacute;i b&agrave;n tr&ecirc;n đ&atilde; dọn sẵn thức ăn.<br />\r\n- Ngồi xuống con. - B&agrave; qu&yacute; ph&aacute;i n&oacute;i.<br />\r\nT&ocirc;i kh&ocirc;ng để phải van n&agrave;i l&acirc;u hơn, đặt đ&agrave;n xuống, nhanh nhẹn ngồi ngay v&agrave;o b&agrave;n; lũ ch&oacute;.ngồi quanh t&ocirc;i theo thứ tự, Joli - Coeur th&igrave; ngồi tr&ecirc;n đầu gối t&ocirc;i.<br />\r\n- Lũ ch&oacute; của anh c&oacute; ăn b&aacute;nh m&igrave; kh&ocirc;ng? -Arthur hỏi.<br />\r\nLại c&ograve;n c&oacute; ăn b&aacute;nh m&igrave; hay kh&ocirc;ng? T&ocirc;i cho mỗi con ch&oacute; một mẩu b&aacute;nh m&igrave;, ch&uacute;ng nhai ngấu nghiến.<br />\r\n- C&ograve;n con khỉ th&igrave; sao? - Arthur lại hỏi.<br />\r\nNhưng chẳng cần phải hỏi han chăm lo đến n&oacute;: trong khi t&ocirc;i cho lũ ch&oacute; ăn n&oacute; đ&atilde; chộp ngay một miếng vỏ pa-t&ecirc; v&agrave; đang nghẹn ở dưới gầm b&agrave;n.&nbsp;Đến lượt t&ocirc;i, t&ocirc;i lấy một l&aacute;t b&aacute;nh m&igrave;, v&agrave; nếu như t&ocirc;i kh&ocirc;ng nghẹn như Joli - Coeur th&igrave; t&ocirc;i cũng ăn lấy ăn để như n&oacute;.&nbsp;- Tội nghiệp ch&uacute; b&eacute;. - B&agrave; qu&yacute; ph&aacute;i vừa n&oacute;i vừa r&oacute;t đầy cốc nước cho t&ocirc;i.<br />\r\n- Nếu bọn anh kh&ocirc;ng gặp ch&uacute;ng em th&igrave; sẽ ăn tối ở đ&acirc;u? - Arthur hỏi.<br />\r\n- Th&igrave; sẽ nhịn ăn tối.<br />\r\nArthur b&egrave;n quay về ph&iacute;a mẹ n&oacute;, hai mẹ con n&oacute;i với nhau bằng tiếng nước ngo&agrave;i, c&oacute; vẻ như n&oacute; y&ecirc;u cầu mẹ n&oacute; điều g&igrave; đ&oacute; c&ograve;n mẹ n&oacute; đưa ra v&agrave;i &yacute; kiến phản đối.&nbsp;Đột nhi&ecirc;n b&agrave; bảo t&ocirc;i:<br />\r\n- Ch&aacute;u c&oacute; đồng &yacute; ở lại đ&acirc;y c&ugrave;ng ch&uacute;ng t&ocirc;i kh&ocirc;ng?<br />\r\nT&ocirc;i nh&igrave;n b&agrave; kh&ocirc;ng trả lời v&igrave; c&acirc;u hỏi n&agrave;y bất chợt qu&aacute; đối với t&ocirc;i.<br />\r\n- Tr&ecirc;n t&agrave;u n&agrave;y ấy ạ?<br />\r\n- Phải. Con trai t&ocirc;i bị ốm, b&aacute;c sĩ y&ecirc;u cầu buộc n&oacute; v&agrave;o một tấm v&aacute;n như ch&aacute;u tr&ocirc;ng thấy đấy. Để n&oacute; khỏi buồn ch&aacute;n t&ocirc;i đưa n&oacute; đi chơi tr&ecirc;n chiếc t&agrave;u n&agrave;y. Ch&aacute;u ở lại đ&acirc;y với ch&uacute;ng t&ocirc;i.<br />\r\nCon khỉ v&agrave; lũ ch&oacute; của ch&aacute;u sẽ biểu diễn cho Arthur xem. C&ograve;n ch&aacute;u, nếu muốn, ch&aacute;u sẽ chơi đ&agrave;n h&aacute;c-pơ. Như vậy ch&aacute;u gi&uacute;p ch&uacute;ng t&ocirc;i, về ph&iacute;a ch&uacute;ng t&ocirc;i, ch&uacute;ng t&ocirc;i cũng c&oacute; &iacute;ch cho bọn ch&aacute;u. Mỗi ng&agrave;y ch&aacute;u đỡ phải lo t&igrave;m c&ocirc;ng ch&uacute;ng, việc n&agrave;y đối với một đứa trẻ ở tuổi ch&aacute;u kh&ocirc;ng phải dễ d&agrave;ng g&igrave;.&nbsp;Sẽ được sống tr&ecirc;n t&agrave;u, tr&ecirc;n mặt nước, hạnh ph&uacute;c n&agrave;o bằng! Đ&oacute; l&agrave; &yacute; nghĩ đầu ti&ecirc;n đập v&agrave;o đầu &oacute;c t&ocirc;i v&agrave; l&agrave;m cho n&oacute; s&aacute;ng l&oacute;e l&ecirc;n. Thật l&agrave; một giấc mơ!&nbsp;Chỉ cần suy nghĩ v&agrave;i gi&acirc;y l&agrave; t&ocirc;i đủ cảm thấy tất cả những g&igrave; khiến t&ocirc;i sung sướng qua đề xuất n&agrave;y v&agrave; người đưa &yacute; kiến đ&oacute; ra rộng lượng biết bao nhi&ecirc;u!.T&ocirc;i cầm lấy b&agrave;n tay b&agrave; qu&yacute; ph&aacute;i m&agrave; h&ocirc;n. B&agrave; c&oacute; vẻ cảm k&iacute;ch về sự thể hiện l&ograve;ng biết ơn n&agrave;y v&agrave; vuốt ve tr&aacute;n t&ocirc;i một c&aacute;ch &acirc;u yếm.&nbsp;V&igrave; người ta y&ecirc;u cầu t&ocirc;i chơi đ&agrave;n h&aacute;c-pơ, vậy th&igrave; l&agrave;m sao t&ocirc;i c&oacute; thể ơ thờ đối với việc l&agrave;m thỏa l&ograve;ng mong muốn của họ? T&ocirc;i cầm lấy c&acirc;y đ&agrave;n, đứng trước mũi t&agrave;u v&agrave; bắt đầu chơi nhạc.&nbsp;C&ugrave;ng l&uacute;c đ&oacute; b&agrave; qu&yacute; ph&aacute;i đưa một chiếc c&ograve;i bằng bạc l&ecirc;n miệng v&agrave; thổi l&ecirc;n một tiếng ch&oacute;e tai.&nbsp;T&ocirc;i lập tức ngừng đ&agrave;n. Arthur đo&aacute;n ra nỗi lo lắng của t&ocirc;i.&nbsp;<br />\r\n- Mẹ thổi c&ograve;i để cho hai con ngựa lại tiếp tục đi đấy.<br />\r\nQuả thật con t&agrave;u nay đ&atilde; xa bờ đang bắt đầu lướt tr&ecirc;n l&agrave;n nước lặng lẽ của con s&ocirc;ng đ&agrave;o do ngựa k&eacute;o đi. S&oacute;ng vỗ v&agrave;o th&acirc;n t&agrave;u, hai b&ecirc;n đường c&acirc;y cối chạy ngược lại ch&uacute;ng t&ocirc;i, mặt trời đang lặn chiếu những tia nắng xi&ecirc;n nghi&ecirc;ng l&agrave;m ch&uacute;ng s&aacute;ng l&ecirc;n.<br />\r\n- Anh chơi đ&agrave;n đi chứ? - Arthur bảo.<br />\r\nV&agrave; gật đầu l&agrave;m hiệu gọi mẹ lại gần, n&oacute; nắm lấy tay mẹ v&agrave; giữ b&agrave;n tay mẹ trong tay m&igrave;nh suốt thời gian t&ocirc;i chơi những bản nhạc m&agrave; chủ t&ocirc;i đ&atilde; dạy t&ocirc;i.</span></span></p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(13, 'Vội vàng', 'Thơ', 'Xuân Diệu', 1938, 'voi-vang.jpg', 'Đã hoàn', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&nbsp;B&agrave;i thơ l&agrave; lời giục gi&atilde; thế hệ trẻ sống vội v&agrave;ng, sống cuống qu&iacute;t để hưởng thụ hết những g&igrave; đẹp đẽ nhất của cuộc đời. Bởi tuổi trẻ của mỗi người chỉ đến một lần duy nhất v&agrave; thời gian th&igrave; kh&ocirc;ng đứng y&ecirc;n để đợi một ai cả. N&oacute; sẽ tr&ocirc;i đi v&agrave; người ta sẽ mất n&oacute; vĩnh viền.&nbsp;Đồng thời, b&agrave;i thơ đ&atilde; thế hiện l&ograve;ng ham sống m&atilde;nh liệt của c&aacute;i t&ocirc;i Xu&acirc;n Diệu rất hiện đại c&ugrave;ng với một quan niệm sống mới mẻ về thời gian, tuổi trẻ v&agrave; hạnh ph&uacute;c.&nbsp;C&aacute;i t&ocirc;i trong thơ Xu&acirc;n Diệu l&agrave; c&aacute;i t&ocirc;i ti&ecirc;u biểu của thời đại thơ Mới, cũng l&agrave; &nbsp;dấu mốc cho sự thắng thế ho&agrave;n to&agrave;n của thơ Mới tr&ecirc;n thi đ&agrave;n l&uacute;c bấy giờ:&nbsp;Đ&oacute; l&agrave; một &yacute; thức r&aacute;o riết về &yacute; thức c&aacute; nh&acirc;n, về đời sống c&aacute; thể, những khao kh&aacute;t n&agrave;y l&agrave; của một người, của một c&aacute;i t&ocirc;i, mạnh dạn bước ra để b&agrave;y tỏ chứ kh&ocirc;ng c&ograve;n cần phải n&uacute;p dưới b&oacute;ng của c&aacute;i ta nữa.</span></span></p>\r\n', '<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">T&ocirc;i muốn tắt nắng đi</span><br />\r\n<span style=\"color:black\">Cho m&agrave;u đừng nhạt mất;</span><br />\r\n<span style=\"color:black\">T&ocirc;i muốn buộc gi&oacute; lại</span><br />\r\n<span style=\"color:black\">Cho hương đừng bay đi.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Của ong bướm n&agrave;y đ&acirc;y tuần th&aacute;ng mật;</span><br />\r\n<span style=\"color:black\">N&agrave;y đ&acirc;y hoa của đồng nội xanh r&igrave;;</span><br />\r\n<span style=\"color:black\">N&agrave;y đ&acirc;y l&aacute; của c&agrave;nh tơ phơ phất;</span><br />\r\n<span style=\"color:black\">Của yến anh n&agrave;y đ&acirc;y kh&uacute;c t&igrave;nh si;</span><br />\r\n<span style=\"color:black\">V&agrave; n&agrave;y đ&acirc;y &aacute;nh s&aacute;ng chớp h&agrave;ng mi,</span><br />\r\n<span style=\"color:black\">Mỗi s&aacute;ng sớm, thần Vui hằng g&otilde; cửa;</span><br />\r\n<span style=\"color:black\">Th&aacute;ng gi&ecirc;ng ngon như một cặp m&ocirc;i gần;</span><br />\r\n<span style=\"color:black\">T&ocirc;i sung sướng. Nhưng vội v&agrave;ng một nửa:</span><br />\r\n<span style=\"color:black\">T&ocirc;i kh&ocirc;ng chờ nắng hạ mới ho&agrave;i xu&acirc;n.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Xu&acirc;n đang tới, nghĩa l&agrave; xu&acirc;n đang qua,</span><br />\r\n<span style=\"color:black\">Xu&acirc;n c&ograve;n non, nghĩa l&agrave; xu&acirc;n sẽ gi&agrave;,</span><br />\r\n<span style=\"color:black\">M&agrave; xu&acirc;n hết nghĩa l&agrave; t&ocirc;i cũng mất.</span><br />\r\n<span style=\"color:black\">L&ograve;ng t&ocirc;i rộng, nhưng lượng trời cứ chật,</span><br />\r\n<span style=\"color:black\">Kh&ocirc;ng cho d&agrave;i thời trẻ của nh&acirc;n gian;</span><br />\r\n<span style=\"color:black\">N&oacute;i l&agrave;m chi rằng xu&acirc;n vẫn tuần ho&agrave;n,</span><br />\r\n<span style=\"color:black\">Nếu tuổi trẻ chẳng hai lần thắm lại</span><br />\r\n<span style=\"color:black\">C&ograve;n trời đất, nhưng chẳng c&ograve;n t&ocirc;i m&atilde;i,</span><br />\r\n<span style=\"color:black\">N&ecirc;n b&acirc;ng khu&acirc;ng t&ocirc;i tiếc cả đất trời;</span><br />\r\n<span style=\"color:black\">M&ugrave;i th&aacute;ng năm đều rớm vị chia ph&ocirc;i,</span><br />\r\n<span style=\"color:black\">Khắp s&ocirc;ng, n&uacute;i vẫn than thầm tiễn biệt....</span><br />\r\n<span style=\"color:black\">Con gi&oacute; xinh th&igrave; th&agrave;o trong l&aacute; biếc,</span><br />\r\n<span style=\"color:black\">Phải chăng hờn v&igrave; nỗi phải bay đi ?</span><br />\r\n<span style=\"color:black\">Chim rộn r&agrave;ng bỗng đứt tiếng reo thi,</span><br />\r\n<span style=\"color:black\">Phải chăng sợ độ phai t&agrave;n sắp sửa ?</span><br />\r\n<span style=\"color:black\">Chẳng bao giờ, &ocirc;i! Chẳng bao giờ nữa&hellip;</span></span></span></span></p>\r\n\r\n<div style=\"border-bottom:solid black 1.0pt; padding:31.0pt 31.0pt 1.0pt 31.0pt\">\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"background-color:white\"><span style=\"color:black\">Mau đi th&ocirc;i! M&ugrave;a chưa ngả chiều h&ocirc;m,</span><br />\r\n<span style=\"color:black\">Ta muốn &ocirc;m</span><br />\r\n<span style=\"color:black\">Cả sự sống mới bắt đầu mơn mởn;</span><br />\r\n<span style=\"color:black\">Ta muốn riết m&acirc;y đưa v&agrave; gi&oacute; lượn,</span><br />\r\n<span style=\"color:black\">Ta muốn say c&aacute;nh bướm với t&igrave;nh y&ecirc;u,</span><br />\r\n<span style=\"color:black\">Ta muốn th&acirc;u trong một c&aacute;i h&ocirc;n nhiều</span><br />\r\n<span style=\"color:black\">V&agrave; non nước, v&agrave; c&acirc;y, v&agrave; cỏ rạng,</span><br />\r\n<span style=\"color:black\">Cho chếnh cho&aacute;ng m&ugrave;i thơm, cho đ&atilde; đầy &aacute;nh s&aacute;ng</span><br />\r\n<span style=\"color:black\">Cho no n&ecirc; thanh sắc của thời tươi;</span><br />\r\n<span style=\"color:black\">&nbsp;Hỡi xu&acirc;n hồng, ta muốn cắn v&agrave;o ngươi!</span></span></span></span></span></p>\r\n</div>\r\n'),
(14, 'Tây tiến', 'Thơ', 'Quang Dũng', 1948, 'tay-tien.jpg', 'Đã hoàn', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ph&aacute;c họa l&ecirc;n bức tranh thi&ecirc;n nhi&ecirc;n h&ugrave;ng vĩ, dữ dội v&agrave; mĩ lệ của v&ugrave;ng T&acirc;y Bắc.&nbsp;Ca ngợi vẻ đẹp đậm chất bi tr&aacute;ng nhưng cũng v&ocirc; c&ugrave;ng l&atilde;ng mạn của người l&iacute;nh T&acirc;y Tiến, họ lu&ocirc;n mang trong m&igrave;nh kh&iacute; ph&aacute;ch anh h&ugrave;ng, l&yacute; tưởng cao đẹp, một vẻ đẹp đậm chất người l&iacute;nh cụ Hồ.&nbsp;Thể hiện t&igrave;nh qu&acirc;n d&acirc;n gắn b&oacute; thắm thiết của người l&iacute;nh T&acirc;y Tiến với nh&acirc;n d&acirc;n qu&ecirc; hương T&acirc;y Bắc trong những năm th&aacute;ng kh&aacute;ng chiến chống thực d&acirc;n Ph&aacute;p.</span></span></p>\r\n', '<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">S&ocirc;ng M&atilde; xa rồi T&acirc;y Tiến ơi!</span><br />\r\n<span style=\"color:black\">Nhớ về rừng n&uacute;i, nhớ chơi vơi</span><br />\r\n<span style=\"color:black\">S&agrave;i Khao sương lấp đo&agrave;n qu&acirc;n mỏi</span><br />\r\n<span style=\"color:black\">Mường L&aacute;t hoa về trong đ&ecirc;m hơi</span><br />\r\n<span style=\"color:black\">Dốc l&ecirc;n kh&uacute;c khuỷu dốc thăm thẳm</span><br />\r\n<span style=\"color:black\">Heo h&uacute;t cồn m&acirc;y, s&uacute;ng ngửi trời</span><br />\r\n<span style=\"color:black\">Ng&agrave;n thước l&ecirc;n cao, ng&agrave;n thước xuống</span><br />\r\n<span style=\"color:black\">Nh&agrave; ai Pha Lu&ocirc;ng mưa xa khơi</span><br />\r\n<span style=\"color:black\">Anh bạn d&atilde;i dầu kh&ocirc;ng bước nữa</span><br />\r\n<span style=\"color:black\">Gục l&ecirc;n s&uacute;ng mũ bỏ qu&ecirc;n đời!</span><br />\r\n<span style=\"color:black\">Chiều chiều oai linh th&aacute;c gầm th&eacute;t</span><br />\r\n<span style=\"color:black\">Đ&ecirc;m đ&ecirc;m Mường Hịch cọp tr&ecirc;u người</span><br />\r\n<span style=\"color:black\">Nhớ &ocirc;i T&acirc;y Tiến cơm l&ecirc;n kh&oacute;i</span><br />\r\n<span style=\"color:black\">Mai Ch&acirc;u m&ugrave;a em thơm nếp x&ocirc;i</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">Doanh trại bừng l&ecirc;n hội đuốc hoa</span><br />\r\n<span style=\"color:black\">K&igrave;a em xi&ecirc;m &aacute;o tự bao giờ</span><br />\r\n<span style=\"color:black\">Kh&egrave;n l&ecirc;n man điệu n&agrave;ng e ấp</span><br />\r\n<span style=\"color:black\">Nhạc về Vi&ecirc;n Chăn x&acirc;y hồn thơ</span><br />\r\n<span style=\"color:black\">Người đi Ch&acirc;u Mộc chiều sương ấy</span><br />\r\n<span style=\"color:black\">C&oacute; thấy hồn lau nẻo bến bờ</span><br />\r\n<span style=\"color:black\">C&oacute; nhớ d&aacute;ng người tr&ecirc;n độc mộc</span><br />\r\n<span style=\"color:black\">Tr&ocirc;i d&ograve;ng nước lũ hoa đong đưa</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">T&acirc;y Tiến đo&agrave;n binh kh&ocirc;ng mọc t&oacute;c</span><br />\r\n<span style=\"color:black\">Qu&acirc;n xanh m&agrave;u l&aacute; dữ oai h&ugrave;m</span><br />\r\n<span style=\"color:black\">Mắt trừng gửi mộng qua bi&ecirc;n giới</span><br />\r\n<span style=\"color:black\">Đ&ecirc;m mơ H&agrave; Nội d&aacute;ng kiều thơm</span><br />\r\n<span style=\"color:black\">Rải r&aacute;c bi&ecirc;n cương mồ viễn xứ</span><br />\r\n<span style=\"color:black\">Chiến trường đi chẳng tiếc đời xanh</span><br />\r\n<span style=\"color:black\">&Aacute;o b&agrave;o thay chiếu, anh về đất</span><br />\r\n<span style=\"color:black\">S&ocirc;ng M&atilde; gầm l&ecirc;n kh&uacute;c độc h&agrave;nh</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:black\">T&acirc;y Tiến người đi kh&ocirc;ng hẹn ước</span><br />\r\n<span style=\"color:black\">Đường l&ecirc;n thăm thẳm một chia ph&ocirc;i</span><br />\r\n<span style=\"color:black\">Ai l&ecirc;n T&acirc;y Tiến m&ugrave;a xu&acirc;n ấy</span><br />\r\n<span style=\"color:black\">Hồn về Sầm Nứa chẳng về xu&ocirc;i.</span></span></span></span></p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(15, 'Tắt đèn', 'Văn học Việt Nam', 'Ngô Tất Tố', 1937, 'tat-den.png', 'Đã hoàn', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&aacute;c phẩm kể về nh&acirc;n vật ch&iacute;nh l&agrave;&nbsp;chị Dậu.&nbsp;Trước khi lấy chồng chị vốn c&oacute; t&ecirc;n l&agrave; L&ecirc; Thị Đ&agrave;o, một c&ocirc; g&aacute;i đẹp, giỏi giang, th&aacute;o v&aacute;t v&agrave;&nbsp;sinh ra trong gia đ&igrave;nh trung lưu.&nbsp;Vốn l&uacute;c đầu, gia cảnh anh chị Dậu c&oacute; dư giả, nhưng v&igrave; liền l&uacute;c mẹ v&agrave; em trai anh Dậu c&ugrave;ng qua đời, anh chị d&ugrave; đ&atilde; hết sức cần kiệm nhưng vẫn phải ti&ecirc;u qu&aacute; nhiều tiền cho hai đ&aacute;m ma. Chưa hết, sau khi đ&aacute;m ma cho em trai xong, anh Dậu bỗng mắc bệnh sốt r&eacute;t, kh&ocirc;ng l&agrave;m g&igrave; được, mọi vất vả dồn l&ecirc;n vai chị Dậu, khiến gia cảnh l&acirc;m v&agrave;o cảnh &#39;nhất nh&igrave; trong hạng c&ugrave;ng đinh trong l&agrave;ng.&nbsp;M&ugrave;a sưu đến, chị Dậu phải chạy vạy khắp nơi vay tiền để nộp cho chồng, nhưng kh&ocirc;ng kiếm đ&acirc;u ra. Anh Dậu d&ugrave; bị ốm nhưng vẫn bị bọn cai lệ c&ugrave;m kẹp l&ocirc;i ra giam ở đ&igrave;nh l&agrave;ng. Cuối c&ugrave;ng, bần c&ugrave;ng qu&aacute;, chị buộc l&ograve;ng phải dứt ruột b&aacute;n đi c&aacute;i T&iacute;, đứa con g&aacute;i đầu l&ograve;ng 7 tuổi ngoan ngo&atilde;n, hiếu thảo v&agrave; ổ ch&oacute; mới đẻ chưa kịp mở mắt cho vợ chồng l&atilde;o Nghị Quế b&ecirc;n th&ocirc;n Đo&agrave;i để lấy hai đồng nộp sưu. Nhưng do bị bọn cai trị ch&egrave;n &eacute;p, b&oacute;c lột chị van xin kh&ocirc;ng được, kh&ocirc;ng thể chịu được nữa, chị đ&atilde; ra tay đ&aacute;nh cả cai lệ v&agrave; t&ecirc;n người nh&agrave; l&yacute; trưởng.&nbsp;Phạm tội đ&aacute;nh người nh&agrave; nước, chị bị th&uacute;c giải l&ecirc;n quan. T&ecirc;n quan huyện lại l&agrave; t&ecirc;n d&acirc;m &ocirc;, định ra tay s&agrave;m sỡ chị. Chị b&egrave;n vứt tọt nắm bạc v&agrave;o mặt hắn rồi v&ugrave;ng chạy.&nbsp;T&aacute;c phẩm kết th&uacute;c bằng c&acirc;u &quot;Chị v&ugrave;ng chạy ra ngo&agrave;i giữa l&uacute;c trời tối đen mực, đen như c&aacute;i tiền đồ của chị vậy!&quot;</span></span></p>\r\n', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:red\">CHƯƠNG I</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Bắt đầu từ g&agrave; g&aacute;y một tiếng, tr&acirc;u b&ograve; lục-tục k&eacute;o thợ cầy đến đoạn đường ph&iacute;a trong điếm tuần.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Mọi ng&agrave;y, giờ ấy, những con-vật n&agrave;y cũng như những người cổ cầy, vai bừa kia, đ&atilde; lần-lượt đi m&ograve; ra ruộng l&agrave;m việc cho chủ. H&ocirc;m nay, v&igrave; cổng l&agrave;ng chưa mở, ch&uacute;ng phải chia qu&atilde;ng đứng rải-r&aacute;c ở hai vệ đường, giống như một lũ phu vờ chờ đ&oacute;n những &ocirc;ng quan lớn.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Dưới b&oacute;ng tối của rặng tre um-t&ugrave;m, tiếng tr&acirc;u thở h&igrave;-h&ograve;, tiếng b&ograve; đập đu&ocirc;i đen-đ&eacute;t, sen với tiếng người khạc kh&uacute;ng-khắng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Cảnh-tượng điếm tuần th&igrave;nh-l&igrave;nh hiện ra trong &aacute;nh lửa lập-l&ograve;e của chiếc mồi rơm bị thổi. Cạnh giẫy s&agrave;o, gi&aacute;o ngả nghi&ecirc;ng dựng ở gi&aacute;p tường, một lũ tuần-phu lố-nhố ngồi tr&ecirc;n lớp chiếu quằn-qu&egrave;o. C&oacute; người ph&igrave;-ph&ograve; thổi mồi. C&oacute; người ve-ve mồi thuốc v&agrave; ch&igrave;a tay chờ đ&oacute;n điếu đ&oacute;m. C&oacute; người h&aacute; miệng ng&aacute;p d&agrave;i. C&oacute; người đang hai tay dụi mắt. Cũng c&oacute; người gối đầu tr&ecirc;n c&aacute;i miệng hiệu sừng tr&acirc;u, ngảnh mặt v&agrave;o v&aacute;ch m&agrave; ng&aacute;y.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">C&aacute;i điếu c&agrave;y v&agrave; c&aacute;i đ&oacute;m lửa bị năm, s&aacute;u người chuyền tay, chiếu đi, chiếu lại độ v&agrave;i ba v&ograve;ng, &aacute;nh lửa lại tắt, trong điếm chỉ c&ograve;n tiếng n&oacute;i chuyện rầm-rầm.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">G&agrave; g&aacute;y giục. Trời s&aacute;ng mờ-mờ.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Tr&acirc;u b&ograve;, con đứng, con nằm, thi nhau quai hai h&agrave;m răng nhai trầu xu&ocirc;ng v&agrave; nhả ra những cục nước bọt to bằng c&aacute;i trứng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Thợ cầy khắp lượt d&ugrave;ng bắp cầy, vai cầy l&agrave;m ghế ngồi, c&ugrave;ng nhau b&agrave;n t&aacute;n băng-cua về chuyện sưu thuế.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Những con ch&egrave;o-bẻo chẽo-choẹt h&oacute;t tr&ecirc;n ngọn tre, như muốn họa lại kh&uacute;c ca r&eacute;o-rắt - m&agrave; người qu&ecirc; vẫn gọi l&agrave; kh&uacute;c &quot;v&aacute;y c&ocirc;, c&ocirc; cởi&quot; - của mấy con ch&agrave;o-m&agrave;o đậu tr&ecirc;n c&agrave;nh xoan, đon-đả ch&agrave;o vẻ tươi đẹp của cảnh trời buổi s&aacute;ng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Toang cổng vẫn đ&oacute;ng chặt. Tuần-phu lại lần lượt thổi mồi h&uacute;t thuốc l&agrave;o.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- &Ocirc;ng Trương ơi, &ocirc;ng l&agrave;m ơn mở cổng cho t&ocirc;i đ&aacute;nh tr&acirc;u ra đồng ạ. Mọi ng&agrave;y b&acirc;y giờ t&ocirc;i đ&atilde; cầy được ba s&agrave;o ruộng rồi. H&ocirc;m nay, b&acirc;y giờ c&ograve;n nhong-nh&oacute;ng ở đ&acirc;y... Phỏng chừng từ giờ đến trưa, cầy sao cho xong c&aacute;i ruộng mẫu hai? Th&ocirc;i &ocirc;ng l&agrave;m ph&uacute;c, l&agrave;m đức...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Sau tiếng năn-nỉ của anh ch&agrave;ng cục-mịch, vai v&aacute;c cầy, tay cầm thừng tr&acirc;u, trương-tuần quẳng c&aacute;i điếu cầy xuống đất, thở nốt kh&oacute;i thuốc trong miệng, rồi giương đ&ocirc;i mắt say thuốc lờ-đờ:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- Nay chẳng xong th&igrave; mai! &Ocirc;ng L&yacute; đ&atilde; bảo thuế c&ograve;n thiếu nhiều, kh&ocirc;ng cho một con tr&acirc;u, con b&ograve; n&agrave;o ra đồng hết thảy...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- Thưa &ocirc;ng, &ocirc;ng chủ t&ocirc;i n&oacute;ng t&iacute;nh lắm kia! &Ocirc;ng ấy đ&atilde; dặn buổi nay t&ocirc;i phải cầy cho xong ruộng ấy, nếu kh&ocirc;ng xong, đến trưa về ăn cơm, &ocirc;ng ấy mắng như t&aacute;t nước v&agrave; n&oacute;i như m&oacute;c cơm ra... Chớ t&ocirc;i c&oacute; muốn vất-vả v&agrave;o m&igrave;nh l&agrave;m g&igrave;? Vả lại, &ocirc;ng L&yacute; sai tuần đ&oacute;ng cổng, cốt để bắt tr&acirc;u, bắt b&ograve; của người thiếu thuế kia chứ! &Ocirc;ng chủ t&ocirc;i nộp thuế đủ từ h&ocirc;m qua rồi, xin &ocirc;ng mở cổng cho t&ocirc;i...!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Trương-tuần nhăn mặt:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- T&ocirc;i kh&ocirc;ng phải tộc-biểu, kh&ocirc;ng phải phần thu, biết điếc g&igrave; đ&acirc;u &ocirc;ng chủ anh nộp thuế rồi hay chưa nộp. B&acirc;y giờ mở cổng cho anh, chốc nữa &ocirc;ng L&yacute; &ocirc;ng ấy chửi cha t&ocirc;i l&ecirc;n, anh c&oacute;-nghe hộ t&ocirc;i kh&ocirc;ng?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Anh ch&agrave;ng cục-mịch lủi-thủi đ&aacute;nh tr&acirc;u gồng cầy l&ugrave;i xuống, để nhường khu đất trước điếm cho người kh&aacute;c v&aacute;c bừa v&agrave; đuổi tr&acirc;u l&ecirc;n.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- Người ta tr&acirc;u của nh&agrave;, &ocirc;ng chẳng cho ra th&igrave; chớ. &ETH;&acirc;y t&ocirc;i, tr&acirc;u thu&ecirc;... đồng hai một buổi đ&aacute;p, &ocirc;ng ạ. Xin &ocirc;ng lượng t&igrave;nh m&agrave; ngỏ cổng cho t&ocirc;i ra đồng kẻo trưa qu&aacute; mất rồi.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Người ấy n&oacute;i tuy thiết-tha, trương-tuần chỉ đ&aacute;p lại bằng c&aacute;i lắc đầu:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- Nếu trưa qu&aacute; th&igrave; bừa lu&ocirc;n đến chiều. Tr&acirc;u thu&ecirc; sợ g&igrave;!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- Thế được th&igrave; c&ograve;n g&igrave; n&oacute;i truyện g&igrave; nữa! Của một đống tiền, ai để cho m&igrave;nh ốp n&oacute; đến chiều? Chỉ đến non trưa, &ocirc;ng chủ c&oacute; tr&acirc;u cho thu&ecirc; đ&atilde; ra tận ruộng th&aacute;o vai tr&acirc;u m&agrave; dắt tr&acirc;u về, muốn bừa th&ecirc;m một nửa đường nữa cũng kh&ocirc;ng cho, &ocirc;ng ạ. Th&ocirc;i! M&ugrave;a l&agrave;m ăn, &ocirc;ng nghĩ lại...!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- T&ocirc;i chẳng nghĩ lại, nghĩ đi g&igrave; cả! Bố t&ocirc;i sống lại, bảo t&ocirc;i mở cổng n&agrave;y b&acirc;y giờ, t&ocirc;i cũng xin chịu đừng n&oacute;i anh...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Người ấy lại v&aacute;c bừa, đưa tr&acirc;u trở lại chỗ cũ với c&aacute;i sắc mặt th&igrave;u-thịu.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Mặt trời ngấp ngh&eacute; mặt lũy, muốn nh&ograve;m v&agrave;o điếm. Tuần-phi&ecirc;n lẻ-tẻ v&aacute;c s&agrave;o, cắp chiếu, đeo hiệu đi về. Trong điếm chỉ c&ograve;n trương-tuần duỗi gối kiểu gọng bừa, ngồi tr&ecirc;n chiếc chiếu điểm những tro mồi, b&atilde; điếu, đốc suất mấy t&ecirc;n đ&agrave;n em ở lại canh ng&agrave;y.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Xa xa nẻo trong đ&igrave;nh, một hồi m&otilde; c&aacute; thật d&agrave;i, tiếp lu&ocirc;n đến trống ngũ-li&ecirc;n nện đủ ba hồi ch&iacute;n tiếng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Như c&oacute; vẻ kinh ngạc về những thứ hiệu lệnh dữ dội, mấy con tr&acirc;u b&ograve; đanh nằm c&ograve;ng queo tr&ecirc;n đường, đồng thời l&oacute;p-ng&oacute;p đứng dậy.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- Mẹ cha ch&uacute;ng n&oacute;! H&ocirc;m nay vẫn chưa đ&oacute;ng thuế, ch&uacute;ng n&oacute; định để tội vạ cho ai? &ETH;ược! Cứ bướng đi, &ocirc;ng m&agrave; bắt hết tr&acirc;u b&ograve; b&aacute;n r&aacute;o!...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Tiếng chửi om-s&ograve;m như giục mấy chục cặp mắt ngơ-ngẩn của bọn cầy đều phải nghi&ecirc;ng về ph&iacute;a đ&igrave;nh. &Ocirc;ng l&yacute; n&aacute;ch cắp cuốn sổ, một tay cầm c&acirc;y gậy song, một tay xếch đ&ocirc;i ống quần m&oacute;ng lợn, vừa đi vừa ra ph&iacute;a điếm tuần vừa th&eacute;t mắng những người chậm thuế.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">&ETH;&atilde; năm h&ocirc;m nay, nghĩa l&agrave; sau khi b&agrave;i-bổ tr&igrave;nh phủ đ&atilde; giao về với một chữ &quot;y&quot;, L&yacute;-trưởng &ETH;&ocirc;ng-x&aacute; ng&agrave;y n&agrave;o cũng vất-vả về thuế.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">&ETH;ầu ti&ecirc;n hắn c&ograve;n cho m&otilde; đi rao. Rồi đến tộc-biểu, phần thu đi hỏi. Rồi đến đầy-tớ của hắn đưa đầy-tớ ch&aacute;nh-tổng đi th&uacute;c từng người. Trong năm ng&agrave;y nay, ng&agrave;y n&agrave;o cũng vậy, m&otilde; c&aacute;, trống th&uacute;c li&ecirc;n hồi, hiệu ốc, hiệu sừng thổi inh-ỏi. Suốt từ s&aacute;ng sớm cho tới tối mịt, trong l&agrave;ng l&uacute;c n&agrave;o cũng như đ&aacute;m đ&aacute;nh cướp. B&acirc;y giờ đ&atilde; gần đến ng&agrave;y đổ thuế, c&ocirc;ng việc c&agrave;ng gấp. H&ocirc;m qua hắn đ&atilde; l&ecirc;n phủ, xin với &ocirc;ng phủ ph&aacute;i cho một người cai lệ v&agrave; hai người l&iacute;nh cơ về l&agrave;ng để trừng-trị những kẻ bướng-bỉnh.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Nhờ c&oacute; c&aacute;i thần-thế ấy, hắn mới chửi rỡ, th&eacute;t mắng khắp l&agrave;ng cho oai.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Thợ cầy v&agrave; tuần-phu đều biết c&aacute;i h&aacute;ch- dịch của &ocirc;ng L&yacute;, ai nấy chỉ đ&aacute;p lại những c&acirc;u chửi ch&ugrave;m chửi lợp bằng sự n&iacute;n im.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Tr&acirc;u, b&ograve; nhiều con vẫy tai như muốn ch&agrave;o một người chức-việc chăm-chỉ phận-sự trong khi người ấy qua trước mặt ch&uacute;ng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Bước l&ecirc;n s&agrave;n điếm, L&yacute;-trưởng quăng tạch cuốn sổ xuống s&agrave;n, giơ tay chỉ v&agrave;o mặt mấy t&ecirc;n đ&agrave;n em:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- Hiệu kh&ocirc;ng thổi, để l&agrave;m sỏ bố ch&uacute;ng bay &agrave;?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Một hồi c&ograve;i tu-tu đồng thời nổi l&ecirc;n, hiệu sừng sen với hiệu ốc theo đ&uacute;ng hiệu ốc cố theo đ&uacute;ng mệnh của &quot;nhất l&yacute; chi trưởng&quot;.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">&ETH;ập hai b&agrave;n ch&acirc;n v&agrave;o nhau, rũ cho sạch bụi, rồi co ch&acirc;n l&ecirc;n ngồi v&agrave;o chiếu, L&yacute;-trưởng vớ lu&ocirc;n lấy chiếu điếu cầy v&agrave; sai tuần-phủ lấy đ&oacute;m, thổi lửa.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Mấy anh thợ c&agrave;y của những điền-chủ đ&atilde; nộp đủ thuế, bạo-dạn tiến đến trước mặt &ocirc;ng L&yacute;:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- Thưa &ocirc;ng, trưa lắm rồi! Xin &ocirc;ng cho tuần mở cổng để ch&uacute;ng t&ocirc;i đ&aacute;nh tr&acirc;u đi cầy!...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- Thong thả! H&atilde;y đứng đấy! Cầy đ&atilde; n&oacute;ng bằng thuế của nh&agrave; nước &agrave;?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Vừa n&oacute;i, L&yacute;-trưởng vừa giặt mồi thuốc v&agrave;o điếu, h&uacute;t lu&ocirc;n một sạp ba điếu. Kh&oacute;i thuốc theo hai lỗ mũi tu&ocirc;n ra như hai ng&agrave; voi. L&yacute;-trưởng d&otilde;ng-dạc:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- Trương-tuần, anh bảo n&oacute; mở cổng ra. Nh&agrave; n&agrave;o đủ thuế th&igrave; cho tr&acirc;u b&ograve; ra đồng. C&ograve;n nh&agrave; n&agrave;o thiếu th&igrave; bắt tr&acirc;u b&ograve; điệu cả về đ&igrave;nh cho t&ocirc;i, để t&ocirc;i liệu cho ch&uacute;ng n&oacute;!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Sau một tiếng dạ của Trương-tuần, mấy t&ecirc;n đ&agrave;n em loay-hoay th&aacute;o n&ecirc;m, r&uacute;t then cổng. Rồi c&ugrave;ng đứng do&atilde;i ch&acirc;n ch&egrave;o, cố lấy hết sức của lực điền, xuống gạch v&igrave; cối cổng đ&atilde; m&ograve;n nhiều qu&aacute;. L&yacute; trưởng đứng l&ecirc;n mở sổ sướng t&ecirc;n những người đủ thuế cho Trương-tuần nghe. Ước chừng mươi con tr&acirc;u b&ograve; được đi với bọn thợ cầy c&ugrave;ng ra ngo&agrave;i cổng. C&ograve;n độ hai chục con nữa đều phải v&acirc;ng lệnh &ocirc;ng L&yacute;, theo g&oacute;t Trương-tuần v&agrave; tuần-phu lũ-lượt k&eacute;o v&agrave;i s&acirc;n đ&igrave;nh, chờ khi phải chịu tội thay cho chủ.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:red\">CHƯƠNG II</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">M&otilde; c&aacute; tr&ecirc;n cột đ&igrave;nh lạ h&aacute; miệng nhận những c&acirc;y d&ugrave;i giận dữ. Trống c&aacute;i dưới x&aacute; đ&igrave;nh lại lỳ mặt chịu những c&aacute;i nện phũ ph&agrave;ng. Vừa dứt hồi vang của những tiếng m&otilde;, tiếng trống bị những lũy tre ngăn cản phải lật trở lại, L&yacute; trưởng liệng d&ugrave;i trống xuống s&acirc;n đ&igrave;nh v&agrave; qu&aacute;t một c&aacute;ch a d&aacute;ng ra phết:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- Từ s&aacute;ng đến giờ vẫn chưa đem chiếu ra trải, th&igrave; cha n&oacute; ngồi v&agrave;o đ&acirc;u? &Agrave; c&aacute;i thằng Mới l&aacute;o thật! Xong thuế rồi &ocirc;ng tống cổ...</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Mẹ Mới ở ph&iacute;a sau đ&igrave;nh lếch thếch &ocirc;m mấy chiếc chiếu đặt l&ecirc;n s&agrave;n đ&igrave;nh:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- Bẩm &ocirc;ng h&ocirc;m qua &ocirc;ng dặn bố n&oacute; nh&agrave; con s&aacute;ng nay phải đi chợ sớm...</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- Trải ra! Rồi đi gọi c&aacute;c &ocirc;ng phần việc đến đ&acirc;y... Kệnh dệnh, kệnh dệnh! Bố người ta đấy m&agrave; h&ocirc;m n&agrave;o cũng bắt người ta phải mời... Chẳng qua việc l&agrave; việc l&agrave;ng, chứ việc ri&ecirc;ng nh&agrave; ai!.. Ng&agrave;y mai m&agrave; kh&ocirc;ng đủ thuế, th&igrave; &ocirc;ng khai hết ra cho, thử xem thằng n&agrave;o ngồi t&ugrave;?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">&ETH;ằng sau c&oacute; tiếng l&eacute;o x&eacute;o:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- L&agrave;m g&igrave; m&agrave; gắt gỏng dữ thế &ocirc;ng L&yacute;? B&agrave; ch&aacute;u bận đi mang cơm thợ cầy, t&ocirc;i phải nấu ch&iacute;n nồi c&aacute;m cho con lợn sề n&oacute; ăn. V&igrave; n&oacute; mới đẻ, sợ bỏ đ&oacute;i th&igrave; n&oacute; mất sữa, rồi con n&oacute; xấu. Bởi thế t&ocirc;i mới chậm ra một ch&uacute;t, chứ c&oacute; kệnh dệnh g&igrave; đ&acirc;u?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Những c&acirc;u ph&acirc;n trần tha thiết k&eacute;o d&agrave;i từ ngo&agrave;i đường v&agrave;o đến giọt đ&igrave;nh. Thủ quỹ nhanh nhảu bước l&ecirc;n thềm đ&igrave;nh với c&aacute;i tr&aacute;p sơn đen bệ vệ nấp ở sườn tay phải. L&yacute; trưởng vội v&agrave;ng n&oacute;i chữa:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- T&ocirc;i n&oacute;i l&agrave; n&oacute;i người kh&aacute;c kia! Chứ &ocirc;ng... nh&agrave; neo, lắm việc, t&ocirc;i c&oacute; tr&aacute;ch g&igrave; &ocirc;ng đ&acirc;u... K&igrave;a c&aacute;c &ocirc;ng ấy đ&atilde; ra cả k&igrave;a!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Chưởng bạ &ocirc;m ống sổ v&agrave; một tập sổ đi đầu một bọn độ hơn mười người: Thư k&yacute; lồng khăn xếp v&agrave;o c&aacute;nh tay, Ph&oacute; l&yacute; quần &aacute;o th&acirc;m tr&ecirc;n cổ. Ch&aacute;nh hội vắt &aacute;o the b&ecirc;n vai, năm s&aacute;u &ocirc;ng kỳ cựu v&agrave; tộc biểu lệt xệt k&eacute;o đ&ocirc;i guốc gỗ đ&atilde; m&ograve;n hết g&oacute;t. Cả bọn lật đật tiến từ x&oacute;m giữa v&agrave;o đ&igrave;nh. Ai nấy trụt guốc cầm tay, nhảy l&ecirc;n s&agrave;n đ&igrave;nh, đ&uacute;t guốc v&agrave;o dưới gậm chiếu rồi ngồi l&ecirc;n tr&ecirc;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Con vợ thằng Mới cung k&iacute;nh đệ len b&ecirc;n cạnh cột đ&igrave;nh một c&aacute;i điếu đ&agrave;n đựng trong một chiếc b&aacute;t gỗ đ&aacute;nh đai bằng lạt v&agrave; c&aacute;i mồi rơm d&agrave;i chừng một sải. Hai đứa con n&oacute; kỳ kệch khi&ecirc;ng l&ecirc;n chỗ đ&oacute; nồi nước ch&egrave; kh&ocirc; mới nấu, kh&oacute;i đương bốc l&ecirc;n nghi ng&uacute;t v&agrave; chồng b&aacute;t đ&agrave;n sơn bằng những c&aacute;u nước ch&egrave;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Ch&aacute;nh hội vừa khơi m&agrave;o n&oacute;i về chuyện thuế th&igrave; người nh&agrave; L&yacute; trưởng lễ mễ bưng một bộ khay đ&egrave;n thuốc phiện rước Ch&aacute;nh tổng từ nẻo x&oacute;m dưới đi l&ecirc;n. &Ocirc;ng cai lệ v&agrave; hai &ocirc;ng l&iacute;nh cơ hộ tống cụ Ch&aacute;nh v&agrave;o trước cửa đ&igrave;nh. trong đ&aacute;m đ&ocirc;ng mỗi người một c&acirc;u:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- Cụ đ&atilde; ra!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- Lạy cụ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- Xin mời cụ l&ecirc;n tr&ecirc;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Ch&aacute;nh tổng khoan thai bỏ giầy, bước qua một giẫy chiếu dưới, để lại mặt chiếu hai h&agrave;ng dấu ch&acirc;n đầy c&aacute;t bụi, rồi vắt vẻo ngồi l&ecirc;n chiếc chiếu tr&ecirc;n c&ugrave;ng. Bộ khay đ&egrave;n tự tay người nh&agrave; L&yacute; trưởng sang tay cai lệ, l&ecirc;n nằm l&agrave;m bạn với &quot;quan trong h&agrave;ng tổng&quot;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Ngay từ s&aacute;ng sớm, cụ Ch&aacute;nh đ&atilde; c&oacute; điểm t&acirc;m độ v&agrave;i chục điếu ở nh&agrave; L&yacute; trưởng, cho n&ecirc;n cụ rất tỉnh t&aacute;o. Nghi&ecirc;m nghị, cụ l&ecirc;n giọng kẻ cả:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- C&aacute;c &ocirc;ng chức việc l&agrave;ng n&agrave;y thật lười qu&aacute;. H&ocirc;m nay m&agrave; thuế c&ograve;n thiếu be thiếu b&eacute;t, chưa thu được một nửa b&agrave;i chỉ! T&ocirc;i hạn từ giờ đến tối phải thu cho đủ. Nếu kh&ocirc;ng đủ, s&aacute;ng mai t&ocirc;i phải tr&igrave;nh quan.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">L&yacute; trưởng đ&oacute;n:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- V&acirc;ng! Xin cụ th&uacute;c giục đi cho! Chứ mấy h&ocirc;m nay c&aacute;c &ocirc;ng ấy cứ ỳ ra đ&oacute;, chẳng &ocirc;ng n&agrave;o chịu ra miệng ra tiếng.. th&igrave; phỏng ai n&oacute; nộp cho?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Mọi người đều im lặng. Kh&ocirc;ng phải sợ c&acirc;u dọa nạt của Ch&aacute;nh tổng L&yacute; trưởng, họ c&ograve;n m&atilde;i nh&igrave;n ra cổng đ&igrave;nh.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Thằng Mới kĩu kịt tr&ecirc;n vai chiếc đ&ograve;n g&aacute;nh treo hai chiếc quang. Giằng trước l&agrave; một xanh vừa gan vừa phổi, vừa tiết để tr&ecirc;n rỗ l&ograve;ng. &ETH;ằng sau th&igrave; một th&uacute;ng thịt lợn.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Lững thững ở ngo&agrave;i tiến v&agrave;o, n&oacute; đặt quang g&aacute;nh xuống s&acirc;n đ&igrave;nh, v&agrave; mời &ocirc;ng L&yacute; ra xem.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- Bẩm &ocirc;ng, h&ocirc;m nay thịt đắt, tất cả hết s&aacute;u đồng rưỡi.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">&Ocirc;ng Ch&aacute;nh hội, &ocirc;ng thủ quỹ v&agrave; nhiều &ocirc;ng kh&aacute;c c&ugrave;ng đổ x&ocirc; ra. Ai nấy đều biểu m&ocirc;i ch&ecirc; đắt, Ch&aacute;nh tổng nằm trong giữa đ&igrave;nh n&oacute;i ra:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- &ETH;ắt với rẽ chẳng qua cũng của mấy đứa phụ canh, c&aacute;c &ocirc;ng c&oacute; mất g&igrave; đ&acirc;u! Th&ocirc;i để cho n&oacute; đem về l&agrave;m đi, trưa lắm rồi!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Thằng Mới mừng rỡ lại cất quang g&aacute;nh l&ecirc;n vai. L&yacute; trưởng dặn theo:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- Phải đ&aacute;nh mỗi m&acirc;m một b&aacute;t tiết canh, m&agrave;y nh&eacute;!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Rồi mọi người c&ugrave;ng l&ecirc;n s&agrave;n đ&igrave;nh, bắt đầu nh&uacute;ng tay v&agrave;o việc.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">L&yacute; trưởng quăng cuốn sổ điền ra chiếu v&agrave; bảo thư k&yacute;:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- &Ocirc;ng đọc cho c&aacute;c &ocirc;ng kia t&iacute;nh, xem rằng chủ n&agrave;o bao nhi&ecirc;u... rồi bi&ecirc;n ghi v&agrave;o mảnh giấy. T&iacute;nh những chủ chưa nộp đồng n&agrave;o ấy th&ocirc;i.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Thủ quỹ mở tr&aacute;p lấy ra c&aacute;i b&agrave;n t&iacute;nh, c&aacute;i b&uacute;t ch&igrave; v&agrave; một tờ giấy t&acirc;y, rồi đưa b&agrave;n t&iacute;nh cho &ocirc;ng L&yacute; cựu:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- &Ocirc;ng t&iacute;nh ta, t&ocirc;i t&iacute;nh t&acirc;y, rồi c&ugrave;ng kh&aacute;p lại, hễ hai đằng m&agrave; kh&ocirc;ng sai nhau l&agrave; đ&uacute;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Một người đ&agrave;n b&agrave; r&oacute;n r&eacute;n bước v&agrave;o s&agrave;n đ&igrave;nh với một chuỗi tiền chinh trong tay:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- Lạy cụ Ch&aacute;nh, lại c&aacute;c cụ ạ...</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">B&ecirc;n b&agrave;n đ&egrave;n, một cậu l&iacute;nh cơ ngẩng cổ l&ecirc;n qu&aacute;t:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- L&agrave;m g&igrave; m&agrave; đến h&ocirc;m nay mới đem nộp thuế? Lại c&ograve;n v&aacute;c những khải định ra đấy! Ai lấy cho?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">L&yacute; trưởng gạt đi:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- Người nh&agrave; t&ocirc;i đấy... c&aacute;c &ocirc;ng t&iacute;nh gi&ugrave;m, xem của b&agrave; ấy c&oacute; bao nhi&ecirc;u ruộng tất cả.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Thư k&yacute; giở sổ đọc:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- Nguyễn Thị Qu&iacute; điền dĩ hạ: nhất sở &ETH;ồng Bắc xứ bảy s&agrave;o ch&iacute;n thước, nhất sở &ETH;ồng C&aacute; xứ s&aacute;u s&agrave;o ba thước hai thốn...</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">L&yacute; cựu vừa gẫy con to&aacute;n l&aacute;ch t&aacute;ch vừa nhẩm:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- Nhất ngũ như ngũ, nhị ngũ nhất thập chi, tam ngũ nhất thập ngũ...</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Thủ quỹ chăm chỉ để hai con mắt v&agrave;o mảnh giấy t&acirc;y dưới ngọn b&uacute;t ch&igrave;. Một hồi sau, Thư k&yacute; đọc hết, L&yacute; cựu hỏi:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- C&oacute; phải ba mẫu bốn s&agrave;o năm thước ba thốn hay kh&ocirc;ng?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Thủ quỹ đ&aacute;p:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- Kh&ocirc;ng biết. B&acirc;y giờ t&ocirc;i mới t&iacute;nh. Cộng bằng t&iacute;nh t&acirc;y vẫn chậm hơn t&iacute;nh ta. &Ocirc;ng biết thế.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Thị Qu&iacute; n&oacute;i theo:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- V&acirc;ng, &ocirc;ng thủ t&iacute;nh kỹ cho. Ruộng nh&agrave; ch&aacute;u c&oacute; đ&acirc;u m&agrave; được ba mẫu!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Một hồi nữa, thủ quỹ h&igrave; hục với c&acirc;y b&uacute;t ch&igrave;, rồi ngẩng đầu l&ecirc;n mắng Thị Qu&iacute;:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- B&agrave; n&oacute;i lạ! Bốn mẫu hai ruộng cả thảy, sao d&aacute;m bảo kh&ocirc;ng được ba mẫu!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Thị Qu&iacute; thề sống, thề chết:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- Ch&aacute;u c&oacute; ăn gian th&igrave; trời kh&ocirc;ng chứng! Thật quả ruộng của nh&agrave; ch&aacute;u chỉ c&oacute; hai mẫu bẩy s&agrave;o...</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">L&yacute; cựu, Thủ quỹ c&ugrave;ng s&ocirc; lại dồn Thị Qu&iacute;. Người đ&agrave;n b&agrave; ấy nhất định kh&ocirc;ng chịu. C&aacute;i b&agrave;n t&iacute;nh v&agrave; c&acirc;y b&uacute;t ch&igrave; phải l&agrave;m việc th&ecirc;m nhiều lần nữa.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">Ch&aacute;nh tổng sốt ruột, giục L&yacute; trưởng:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- Ai t&iacute;nh cứ t&iacute;nh, ai đi đốc cứ đi đốc. Chứ x&uacute;m nhau ở cả đ&acirc;y, th&igrave; đến tết cũng kh&ocirc;ng thể thu xong thuế!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">L&yacute; trưởng lấy l&agrave;m phải, liền sai mấy t&ecirc;n gia nh&acirc;n đi đưa c&aacute;c &ocirc;ng lệ, l&iacute;nh cơ đầy tớ Ch&aacute;nh tổng v&agrave;o khắp những nh&agrave; đinh c&ugrave;ng v&agrave; dặn:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">- &ETH;ứa n&agrave;o chưa c&oacute; sưu hay chưa c&oacute; thuế th&igrave; cứ trối cổ n&oacute; lại v&agrave; điệu ra đ&acirc;y, để t&ocirc;i cho ch&uacute;ng một mẻ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">M&otilde; lại th&uacute;c, trống lại giục, t&ugrave; v&agrave; lại inh ỏi thổi l&ecirc;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:black\">&Aacute;nh nắng bứt rứt chiếu đến nửa s&acirc;n đ&igrave;nh. Mấy con tr&acirc;u, b&ograve; vẫn nằm g&ograve; lưng dưới những gốc gạo, gốc đa, hơi thở trong cổ họng k&eacute;o ra hồng hộc.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"background-color:white\"><span style=\"color:red\">CHƯƠNG III</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Nếp nh&agrave; tranh lủn củn nấp dưới rặng tre l&agrave; ng&agrave;, lặng lẽ &uacute;p lấy khu đất dề th&agrave;nh v&agrave; k&iacute;n đ&aacute;o n&aacute;u trong một con x&oacute;m cuối l&agrave;ng &ETH;&ocirc;ng x&aacute;, đứng xa ng&oacute; lại, c&oacute; thể lầm với nơi nhốt lợn hay chứa tro, đ&oacute; l&agrave; nh&agrave; của Nguyễn Văn Dậu.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Với tầm cao vừa xế mặt th&agrave;nh, giẫy m&aacute;i hi&ecirc;n cườm cượp nh&ograve;m ra ven th&agrave;nh, lu&ocirc;n lu&ocirc;n phạt kẻ ra v&agrave;o bằng những c&aacute;i cộc bươu đầu cho&aacute;ng &oacute;c, nếu họ qu&ecirc;n kh&ocirc;ng c&uacute;i đầu.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Với lớp r&ecirc;u xanh điểm những l&aacute; tre v&agrave;ng &uacute;a v&agrave; những cỏ gấu phất phơ, c&aacute;i s&acirc;n đất rộng bằng đường bừa, lỗ chỗ vết ch&acirc;n ch&oacute; sen vết ch&acirc;n người, vừa l&agrave;m đường đi, vừa l&agrave;m khu vực &quot;ho&atilde;n xung&quot;, ph&ograve;ng bị những l&uacute;c mưa r&agrave;o, đất ở ch&acirc;n th&agrave;nh khỏi lở v&agrave;o thềm v&agrave; cửa.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Bao nhi&ecirc;u bộ phận cần c&oacute; của một gia đ&igrave;nh, đều được thu cả v&agrave;o trong nh&agrave;.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Nh&agrave; cất theo lối kiến tr&uacute;c phổ th&ocirc;ng của c&aacute;c cụ, to&agrave;n thể gồm c&oacute; một ch&aacute;i v&agrave; hai gian.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Gian đầu l&agrave; buồng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Bức m&agrave;nh r&aacute;ch m&eacute;p lướt thướt rủ ngo&agrave;i cửa buồng, cũng như bức ph&ecirc;n nan nứa sừng sững chắn ngang l&ograve;ng nh&agrave;, đều hết sức giữ cho trong buồng c&oacute; vẻ b&iacute; mật. Tr&aacute;i lại, những khe hở ở cạnh cửa v&agrave; nhưng lỗ thủng ở ch&acirc;n ph&ecirc;n lại c&ugrave;ng ra &yacute; ph&ocirc; s&ograve;ng, như muốn khai rằng: ngo&agrave;i chiếc giường tre gẫy gi&aacute;t, k&ecirc; gi&aacute;p bức bụa, trong n&agrave;y c&oacute; một chum mẻ, vại h&agrave;n, chen nhau đứng b&ecirc;n cạnh đống đất hang chuột.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">&ETH;ối với buồng, ấy l&agrave; bếp đun, cảnh tượng lại c&agrave;ng bề bộn.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Dưới từng m&aacute;y ch&aacute;i thấp hẹp, những luồng &aacute;nh s&aacute;ng xanh t&iacute;a, th&ocirc;ng thống k&eacute;o thẳng từ n&oacute;c đến nền, cố hun cho kh&ocirc; những chỗ đ&ecirc;m qua mưa dột.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Cạnh b&oacute; củi giong ẩm ướt, đo&agrave;n vung sứt miệng hềnh hệch nằm ngửa trong những c&aacute;i rế tre, như muốn cười với lũ m&ecirc;u đất thư nh&agrave;n, l&ocirc;ng lốc lăn nghi&ecirc;ng lăn ngửa.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Tr&ecirc;n b&atilde;i tro trấu tanh b&agrave;nh, nguội lạnh, mấy h&ograve;n gạch vỡ chầu đầu v&agrave;o nhau, nghi&ecirc;ng m&igrave;nh nằm theo thế ch&acirc;n vạc, đương chờ đến l&uacute;c đội nồi.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Kề đ&oacute; đến b&agrave;n thờ.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Chiếc ngưỡng tre uốn lưng g&ugrave; g&ugrave; đỡ lấy một đố tre, d&otilde;ng dạc đứng ra h&igrave;nh chữ &quot;m&ocirc;n&quot; v&agrave; hộ vệ cho một mớ nan tre l&agrave;nh ph&agrave;nh long bựt, giường thờ giống như c&aacute;i chạn đựng b&aacute;t. lơ lửng g&aacute;c giữa khoảng cột của bếp v&agrave; buồng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Tờ giấy xanh lạnh l&ugrave;ng phủ ngo&agrave;i v&aacute;ch đất tỏ rằng nh&agrave; mới c&oacute; tang.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Nhưng mớ mạng nhện chằng chịt quấn tr&ecirc;n đ&aacute;m ch&acirc;n hường lơ thơ, lại l&agrave;m chứng cho một thời gian kh&aacute; l&acirc;u, chủ nh&acirc;n kh&ocirc;ng hề c&uacute;ng lễ. Rồi ở giữa nh&agrave;, chiếc chiếu r&aacute;ch thủng xộc xệch phủ tr&ecirc;n tấm phản long đinh.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Rồi ở dưới phản, mấy con ch&oacute; con đ&ograve;i ăn, ăng ẳng k&ecirc;u kh&ocirc;ng dứt tiếng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">V&agrave; ở ngo&agrave;i hi&ecirc;n, quang, g&aacute;nh, liềm, h&aacute;i sen, với &aacute;o yếm, &aacute;o, v&aacute;y, quần, l&ocirc;i th&ocirc;i rủ suốt mấy gian ruổi.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Trong tay bồng đứa con g&aacute;i hai tuổi, chị Dậu thơ thẩn ngồi tr&ecirc;n chiếc ch&otilde;ng long nan. C&aacute;i nhanh nhảu của đ&ocirc;i mắt sắc ngọt, c&aacute;i sinh sắn của cặp m&ocirc;i đỏ tươi, c&aacute;i mịn m&agrave;ng của nước da đen gi&ograve;n v&agrave; c&aacute;i nuột n&agrave; của người đ&agrave;n b&agrave; hai mươi bốn tuổi, vẫn kh&ocirc;ng đ&aacute;nh đổ những c&aacute;i lo phiền buồn b&atilde; trong đ&aacute;y tim. N&eacute;t mặt rầu rầu, chị im lặng nh&igrave;n sự ngoan ngo&atilde;n của hai đứa con nhỏ.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">C&aacute;i T&yacute; v&agrave; thằng Dần, đương hỳ hục bới đống rễ khoai, t&igrave;m những củ mập, củ gi&agrave;y, bỏ v&agrave;o trong rổ. Ch&uacute;ng n&oacute; mừng reo h&yacute; hửng khi thấy một củ nguy&ecirc;n l&agrave;nh, v&agrave; ch&uacute;ng n&oacute; c&atilde;i cọ lầu nhầu khi lục m&atilde;i chẳng được g&igrave; cả.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Mặt trời gần đến đỉnh đầu.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">&Aacute;nh nắng xuy&ecirc;n qua lũy tre chiếu xuống trước thềm khoang cửa.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">&ETH;ằng sau g&agrave; g&aacute;y te te.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">N&oacute;c bếp l&aacute;ng giềng ngọn kh&oacute;i bốc l&ecirc;n nghi ng&uacute;t.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Thằng Dần với bộ mặt th&igrave;u thịu, bỏ đống rễ khoai đứng dậy l&ugrave;ng b&ugrave;ng:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- Nhặt m&atilde;i từ s&aacute;ng đến giờ mới được ba mẫu khoai ranh! Con kh&ocirc;ng phải tội m&agrave; bới nữa. U đi mua gạo mau l&ecirc;n! H&agrave;ng x&oacute;m nấu cơm trưa rồi, nh&agrave; ta vẫn chưa ăn cơm s&aacute;ng! Ch&uacute;ng con đ&oacute;i qu&aacute;!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Ng&oacute; con một c&aacute;ch đau đớn, chị Dậu ngọt ng&agrave;o:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- Con h&atilde;y cố nhặt th&ecirc;m v&agrave;i chục mẫu nửa, rồi chị n&oacute; luộc cho ăn, chứ u l&agrave;m g&igrave; c&oacute; tiền đ&ocirc;ng gạo?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Thằng b&eacute; phụng phịu:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- H&ocirc;m qua v&agrave; h&ocirc;m kia u b&aacute;n hai g&aacute;nh khoai lang được năm h&agrave;o m&agrave;... đ&atilde; ti&ecirc;u g&igrave; đ&acirc;u!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Vừa n&oacute;i n&oacute; vừa tung tăng chạy đến cạnh mẹ, toan lần dải yếm của mẹ. C&aacute;i T&yacute; thỏ thẻ khuy&ecirc;n em:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- Tiền b&aacute;n khoai c&ograve;n phải để d&agrave;nh đ&oacute;ng sưu cho &ocirc;ng L&yacute; chứ? Dễ được đem m&agrave; mua gạo đấy hẳn? Em c&oacute; đ&oacute;i th&igrave; h&atilde;y ăn tạm củ khoai sống vậy!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">C&acirc;u n&oacute;i nghĩa l&yacute; của con b&eacute; bảy tuổi, h&igrave;nh như c&oacute; một sức mạnh thần b&iacute;, khiến cho chị Dậu hai h&agrave;ng nước mắt chạy quanh. Uể oải, chị bế c&aacute;i Tỉu l&ecirc;n sườn v&agrave; lừ thừ đi ra ngo&agrave;i cổng, để ng&oacute;ng xem chồng đ&atilde; về chưa.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Chồng chị, - anh Nguyễn Văn Dậu - tuy mới hai mươi s&aacute;u tuổi, nhưng đ&atilde; học nghề l&agrave;m ruộng đến mười bảy năm.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Với c&aacute;i sức khỏe mạnh, với c&aacute;i t&iacute;nh nhanh nhẩu, với c&aacute;i đức chịu thương chịu kh&oacute; trong nghề cầy thu&ecirc; cuốc mướn, những năm son rỗi, vợ chồng nh&agrave; anh cũng vẫn kiếm được thừa ăn. Mấy năn nay phần v&igrave; th&oacute;c cao, gạo k&eacute;m, phần th&igrave; con đẻ th&ecirc;m ra, vợ bận mọn lu&ocirc;n, sự ti&ecirc;u d&ugrave;ng của anh mới kh&ocirc;ng được rồi r&agrave;o như trước, vặt mũi chỉ đủ đ&uacute;t miệng m&agrave; th&ocirc;i.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Rồi th&aacute;ng t&aacute;m năm ngo&aacute;i, mẹ anh bị bệnh qua đời. Lại th&aacute;ng gi&ecirc;ng năm nay, thằng em trai của anh cũng v&igrave; phải gi&oacute; m&agrave; chết. Dầu anh hết sức tằn tiện, hai c&aacute;i ma ấy cũng cứ l&ocirc;i k&eacute;o đi mất của anh hai m&oacute;n l&agrave; l&agrave;ng hết đ&uacute;ng s&aacute;u đồng, v&agrave; hai cổ quan t&agrave;i hết gần t&aacute;m đồng. Nh&agrave; kh&ocirc;ng c&oacute;, anh phải quang co tần tảo cho c&oacute;. Hai c&aacute;i rớp ấy cũng đủ đưa anh l&ecirc;n đến bậc nh&igrave;, bậc nhất trong hạng c&ugrave;ng đinh rồi. Hơn nữa, th&aacute;ng ba đến giờ thần bệnh sốt r&eacute;t ở đ&acirc;u k&eacute;o đến, n&oacute; bắt anh cứ ph&atilde;i nghĩ việc nằm nh&agrave;.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Thế l&agrave; gần ba th&aacute;ng trời, những sự đ&oacute;ng g&oacute;p chi ti&ecirc;u của một gia đ&igrave;nh năm miệng ăn, hết thảy tr&ocirc;ng v&agrave;o hai b&agrave;n tay trắng của người đ&agrave;n b&agrave; con mọn.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Sự đ&oacute;i r&aacute;ch của con v&agrave; sự lầm than của vợ, đắp đổi tiến đến trước mắt, đ&atilde; l&agrave;m cho anh gan ruột nẫu n&agrave;. Th&ecirc;m mấy bữa nay, L&yacute; trưởng ng&agrave;y ng&agrave;y sai người giục thuế, anh c&agrave;ng luống cuống như con kiến b&ograve; trong chảo n&oacute;ng, kh&ocirc;ng biết lo liệu thế n&agrave;o. Sớm ng&agrave;y, cơn sốt đ&atilde; tan anh phải gắng gượng chống gậy ra đi, cũng định vay mượn lấy một v&agrave;i đồng, trả cho xong &quot;m&oacute;n nợ nh&agrave; nước&quot;. Nhưng đi th&igrave; đi, ch&iacute;nh anh cũng kh&ocirc;ng tự biết m&igrave;nh s&egrave; đi đến nh&agrave; a, v&agrave; chừng bao giờ th&igrave; về.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Mong m&atilde;i kh&ocirc;ng thấy b&oacute;ng chồng, chị Dậu lại bỗng c&aacute;i Tỉu trở v&agrave;o, ngồi phịch xuống ch&otilde;ng.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">C&aacute;i T&yacute; vẫn loay hoay với đống rễ khoai. Thằng Dần đương r&aacute;u r&aacute;u nhai mẫu khoai sống.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">B&ecirc;n nh&agrave; l&aacute;ng giềng c&oacute; tiếng m&acirc;m b&aacute;t lạch cạch. Mấy con n&iacute;t t&iacute;u t&iacute;t gọi nhau ăn cơm. M&ugrave;i canh rưa v&agrave; m&ugrave;i c&aacute; khi theo ngọn gi&oacute; nồm đưa sang ng&agrave;o ngạt.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Thằng b&eacute; h&aacute;u đ&oacute;i nuốt vội miếng khoai trong miệng, vớ tấm &aacute;o n&acirc;u toạc vạt kho&aacute;c lu&ocirc;n v&agrave;o m&igrave;nh, đon đả chạy lại vỗ v&agrave;o vai mẹ:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- Con sang chơi b&ecirc;n nh&agrave; &ocirc;ng B&aacute;c, u nh&eacute;!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">C&aacute;i T&yacute; chừng mắt:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- B&ecirc;n ấy sắp sửa ăn cơm ấy m&agrave;...! Sang l&agrave;m g&igrave;? Rồi b&agrave; B&aacute;c lại đuổi oai o&aacute;i như h&ocirc;m nọ ấy. Th&ocirc;i, đ&oacute;i th&igrave; chịu vậy, chứ em cứ nhặt cố lấy mấy củ nữa, rồi chị đứng l&ecirc;n bắc nồi.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Thằng Dần quăng tọt c&aacute;i &aacute;o v&agrave;o ch&otilde;ng, ngoảnh đ&iacute;t quay lại, tr&ecirc;n mặt đầy vẻ thất vọng:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- Khổ lắm, bao nhi&ecirc;u củ ngon củ l&agrave;nh, u đ&atilde; lựa hết đem b&aacute;n, ở đ&acirc;y chỉ c&ograve;n những rễ v&agrave; rễ, lấy đ&acirc;u ra khoai m&agrave; nhặt?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">C&aacute;i T&yacute; lại dịu n&eacute;t mặt:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- Chả b&aacute;n th&igrave; lấy tiền đ&acirc;u đ&oacute;ng sưu?... Em h&atilde;y chịu kh&oacute; nhặt đi với chị! H&atilde;y c&ograve;n v&ocirc; khối củ mẫm ra đấy.&nbsp;</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">- Nhưng m&agrave; em c&ograve;n đ&oacute;i v&agrave;ng cả mắt, kh&ocirc;ng nhặt được nữa...!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">N&oacute;i xong, thằng Dần bước đến b&ecirc;n cạnh rổ khoai, chọn lấy một củ lớn nhất, n&oacute; ch&ugrave;i v&agrave;o bụng cho sạch c&aacute;t bụi rồi cho v&agrave;o mồm cắn...</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#222222\">Chị Dậu tuy vẫn ngồi im kh&ocirc;ng n&oacute;i, nhưng ở kh&oacute;e mắt, nước mắt lại cứ chan chứa chảy ra. Chừng như kh&ocirc;ng muốn để cho c&aacute;c con thấy sự đau l&ograve;ng của m&igrave;nh, chị vờ ngảnh mặt nh&igrave;n v&agrave;o trong v&aacute;ch.</span></span></span></span></p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(16, 'Romeo and Juliet', 'Văn học nước ngoài', 'William Shakespeare', 1594, 'romeo-and-juliet.jpg', 'Đã hoàn', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Romeo v&agrave; Juliet dựa tr&ecirc;n một cốt truyện c&oacute; sẵn kể về một mối t&igrave;nh oan tr&aacute;i vốn l&agrave; c&acirc;u chuyện c&oacute; thật, từng xảy ra ở&nbsp;&Yacute;&nbsp;thời Trung Cổ. Hai d&ograve;ng họ&nbsp;nh&agrave; Montague&nbsp;v&agrave;&nbsp;nh&agrave; Capulet&nbsp;c&oacute; mối hận th&ugrave; l&acirc;u đời. Romeo, con trai họ Montague v&agrave; Juliet, con g&aacute;i họ Capulet đ&atilde; y&ecirc;u nhau say đắm ngay từ c&aacute;i nh&igrave;n đầu ti&ecirc;n tại buổi dạ tiệc tổ chức tại nh&agrave; Capulet.&nbsp;Đ&ocirc;i trai g&aacute;i n&agrave;y đ&atilde; đến nh&agrave; thờ nhờ tu sĩ&nbsp;Friar Laurence&nbsp;b&iacute; mật l&agrave;m lễ cưới.&nbsp;Anh họ của Juliet l&agrave;&nbsp;Tybalt&nbsp;đ&atilde; giết chết người bạn rất th&acirc;n của Romeo l&agrave;&nbsp;Mercutio. Để trả th&ugrave; cho bạn, Romeo đ&atilde; đ&acirc;m chết Tybalt.&nbsp;V&igrave; tội giết người n&ecirc;n Romeo bị trục xuất khỏi Verona v&agrave; bị đi đ&agrave;y biệt xứ.&nbsp;Juliet bị cha mẹ &eacute;p gả cho&nbsp;B&aacute; tước Paris. Juliet cầu cứu sự gi&uacute;p đỡ của tu sĩ Laurence. Tu sĩ cho n&agrave;ng uống một liều thuốc ngủ, uống v&agrave;o sẽ như người đ&atilde; chết, thuốc c&oacute; t&aacute;c dụng trong v&ograve;ng 42 tiếng. Tu sĩ sẽ b&aacute;o cho Romeo đến hầm mộ cứu n&agrave;ng trốn khỏi th&agrave;nh Verona.&nbsp;Đ&aacute;m cưới giữa Juliet v&agrave; Paris trở th&agrave;nh đ&aacute;m tang. X&aacute;c Juliet được đưa xuống hầm mộ. Tu sĩ chưa kịp b&aacute;o cho Romeo th&igrave; từ chỗ bị lưu đ&agrave;y nghe tin Juliet chết, Romeo đau đớn trốn về Verona. Tr&ecirc;n đường về ch&agrave;ng kịp mua một liều thuốc cực độc d&agrave;nh cho m&igrave;nh. Tại nghĩa địa, gặp Paris đến viếng Juliet, Romeo đ&acirc;m chết Paris rồi uống thuốc độc tự tử theo người m&igrave;nh y&ecirc;u. Romeo vừa gục xuống th&igrave; thuốc của Juliet hết hiệu nghiệm. N&agrave;ng tỉnh dậy v&agrave; nh&igrave;n thấy x&aacute;c Romeo b&ecirc;n cạnh đ&atilde; chết, Juliet r&uacute;t dao tự vẫn.&nbsp;C&aacute;i chết tang thương của đ&ocirc;i bạn trẻ đ&atilde; thức tỉnh hai d&ograve;ng họ. B&ecirc;n x&aacute;c hai người, hai d&ograve;ng họ đ&atilde; qu&ecirc;n mối th&ugrave; truyền kiếp v&agrave; bắt tay nhau đo&agrave;n tụ, nhưng c&acirc;u chuyện t&igrave;nh y&ecirc;u ấy vẫn m&atilde;i sẽ l&agrave; nỗi đau rất lớn trong l&ograve;ng những người biết đến họ.</span></span></p>\r\n', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">Những đường phố vắng lặng của thủ phủ Verone thanh b&igrave;nh v&agrave;o thế kỷ XIV nằm im l&igrave;m ng&aacute;i ngủ giữa những l&acirc;u đ&agrave;i cổ k&iacute;nh v&agrave; d&atilde;y tường th&agrave;nh cao ngất v&acirc;y k&iacute;n những c&ocirc;ng vi&ecirc;n rậm m&aacute;t. Đời sống nơi đ&oacute; tươi vui v&agrave; thoải m&aacute;i. Một vị l&atilde;nh ch&uacute;a d&ograve;ng d&otilde;i Della Scala, nh&acirc;n từ, đức độ, chăm lo cho sự phồn vinh của thủ phủ. Những gia đ&igrave;nh qu&yacute; tộc tập hợp th&acirc;n bằng quyến thuộc, bộ hạ t&ugrave;y t&ugrave;ng v&agrave; đ&aacute;m người hầu tận tụy, trung th&agrave;nh. Những mối bất h&ograve;a truyền kiếp m&agrave; nguy&ecirc;n nh&acirc;n đ&atilde; bị l&atilde;ng qu&ecirc;n theo thời gian, vẫn c&ograve;n chia rẽ hai d&ograve;ng họ Montaigu v&agrave; Capulet: hai vị trưởng tộc đ&atilde; trầm tĩnh v&agrave; kh&ocirc;n ngoan, bởi tuổi đời chồng chất, nhưng con ch&aacute;u họ, những g&atilde; trai trẻ s&ocirc;i nổi v&agrave; t&aacute;o bạo, kh&ocirc;ng kể bọn t&ocirc;i tớ, l&uacute;c n&agrave;o cũng sẵn s&agrave;ng đ&oacute;ng vai hảo hớn v&agrave; anh h&ugrave;ng rơm, thường khuấy động sự y&ecirc;n tĩnh của th&agrave;nh phố. Vị l&atilde;nh ch&uacute;a bực tức, truyền rằng, từ nay, bất cứ cuộc ẩu đả n&agrave;o cũng bị xử phạt tử h&igrave;nh.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">Ch&agrave;ng trai trẻ Rom&eacute;o lu&ocirc;n khiến cha mẹ m&igrave;nh lo &acirc;u. Nước mắt ch&agrave;ng chan h&ograve;a với sương sớm. Lời thở than của ch&agrave;ng khiến m&acirc;y trời u &aacute;m. Ch&agrave;ng xa l&aacute;nh bạn b&egrave;, anh Benvolio đứng đắn v&agrave; g&atilde; Meroutio n&ocirc;ng nổi, đ&atilde; lang thang trong rừng trước l&uacute;c b&igrave;nh minh. Bởi ch&agrave;ng si m&ecirc; n&agrave;ng Rosaline kiều diễm, đẹp m&agrave; hiểm &aacute;c, v&agrave; ch&agrave;ng chỉ c&ograve;n biết h&eacute;o m&ograve;n trước sự khinh bạc của n&agrave;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">Benvolio biết tin Rosaline v&agrave; nhiều bậc vương hầu, c&ugrave;ng phu nh&acirc;n sẽ đến dự yến tiệc tại nh&agrave; qu&yacute; tộc Capulet, m&agrave; tiểu thư Juliette vừa được g&atilde; Paris bảnh trai cầu h&ocirc;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">- Đến đ&oacute; đi, anh n&oacute;i với Rom&eacute;o, để m&agrave; so s&aacute;nh gương mặt của người m&agrave; cậu y&ecirc;u với v&agrave;i ba khu&ocirc;n mặt kh&aacute;c, m&agrave; tớ sẽ giới thiệu với cậu, để cậu phải thừa nhận rằng, con thi&ecirc;n nga của cậu chỉ l&agrave; một con quạ xấu x&iacute;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">- Ta sẽ đến, Rom&eacute;o hăm hở, nhưng chỉ để chi&ecirc;m ngưỡng dung nhan rạng rỡ của người m&agrave; ta y&ecirc;u th&ocirc;i.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">Rồi hai g&atilde; liều lĩnh, cải trang v&agrave; đeo mặt nạ, đến ngay nh&agrave; kẻ th&ugrave; th&acirc;m s&acirc;u của gia đ&igrave;nh, v&igrave; Rom&eacute;o thuộc d&ograve;ng d&otilde;i Montaigu, c&ograve;n l&acirc;u đ&agrave;i m&agrave; cậu ta sẽ xuất hiện l&agrave; của d&ograve;ng d&otilde;i Capulet.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">Trong l&uacute;c ấy, người hầu v&agrave; t&ocirc;i tớ hối hả, chộn rộn chuẩn bị yến tiệc. L&atilde;o Capulet ch&uacute;c mừng quan kh&aacute;ch, n&oacute;i đ&ugrave;a v&agrave; tr&ecirc;u ghẹo c&aacute;c phu nh&acirc;n, truyền những lệnh m&acirc;u thuẫn nhau cho bộ hạ v&agrave; nhắc lại những kỷ niệm thời trai trẻ. Nhạc nổi l&ecirc;n: cuộc khi&ecirc;u vũ bắt đầu. V&agrave; Rom&eacute;o, trong trang phục người h&agrave;nh hương đột nhi&ecirc;n khẽ hỏi người hầu ph&ograve;ng:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">C&ocirc; g&aacute;i n&agrave;o lộng lẫy thướt tha trong tay hai người h&agrave;o hoa kia thế? Trong đ&ecirc;m mờ ảo n&agrave;y, n&agrave;ng kh&aacute;c n&agrave;o vi&ecirc;n tr&acirc;n ch&acirc;u l&oacute;ng l&aacute;nh, trang điểm đ&ocirc;i tay g&atilde; Ethiopi. N&agrave;ng l&agrave;m mờ đi &aacute;nh s&aacute;ng những ngọn đuốc. Khi cuộc khi&ecirc;u vũ chấm dứt, ta sẽ cho b&agrave;n tay trần tục của ta c&aacute;i vinh hạnh chạm v&agrave;o tay n&agrave;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">Những lời n&oacute;i đ&oacute; đ&atilde; lọt v&agrave;o tai đứa ch&aacute;u đức &ocirc;ng Capulet, t&ecirc;n l&agrave; Tebaldo. G&atilde; đ&atilde; nhận ra giọng n&oacute;i của một người Montaigu, b&egrave;n h&ograve; h&eacute;t l&ecirc;n v&agrave; chạy t&igrave;m c&acirc;y trường kiếm, g&atilde; muốn trừng phạt một sự x&uacute;c phạm đối với gia đ&igrave;nh ch&uacute;, trong khi &ocirc;ng n&agrave;y, trầm tĩnh hơn, nghi&ecirc;m khắc buộc hắn n&ecirc;n nhẫn nhịn.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">- Mi muốn nghịch lời ta ư? Khốn nạn, mi chọn đ&uacute;ng l&uacute;c thật. Mi l&agrave; một kẻ ph&aacute;ch lối. H&atilde;y đứng y&ecirc;n đ&oacute;, nếu kh&ocirc;ng&hellip;A! Con g&agrave; chọi, mi muốn giương cựa ra đấy &agrave;! H&atilde;y mau vứt bỏ bộ mặt hung tợn đ&oacute; đi, thật l&agrave; chướng mắt trong buổi hội h&egrave; vui vẻ n&agrave;y.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">Tebaldo giận dữ, cằn nhằn bỏ đi, vừa thề rằng hắn sẽ chẳng dừng lại ở đấy đ&acirc;u. Ngay l&uacute;c đ&oacute;, cuộc khi&ecirc;u vũ chấm dứt.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">Nhờ h&oacute;a trang, Rom&eacute;o c&oacute; thể men đến gần c&ocirc; g&aacute;i m&agrave; m&igrave;nh ch&uacute; &yacute; v&agrave; bắt đầu t&aacute;n tỉnh bằng những lời lẽ cầu kỳ bay bướm:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">- Nếu b&agrave;n tay t&ocirc;i, ch&agrave;ng thở d&agrave;i, chẳng ch&uacute;t n&agrave;o xứng đ&aacute;ng, lại l&agrave;m cho tay c&ocirc; uế tạp, t&ocirc;i biết c&aacute;ch chuộc tội bất k&iacute;nh đ&oacute;: m&ocirc;i t&ocirc;i sẵn s&agrave;ng x&oacute;a n&oacute; bằng một c&aacute;i h&ocirc;n nồng n&agrave;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">- Người h&agrave;nh hương đ&aacute;ng mến ơi! &ndash;c&ocirc; Juliette tinh nghịch đ&aacute;p- sao lại n&oacute;i xấu b&agrave;n tay m&igrave;nh thế, n&oacute; cũng chỉ ước ao được sử dụng theo th&oacute;i quen b&igrave;nh thường th&ocirc;i; những bậc th&aacute;nh vẫn để tay m&igrave;nh chạm phải người h&agrave;nh hương v&agrave; một c&aacute;i bắt tay ch&iacute;nh l&agrave; c&aacute;i h&ocirc;n của những người s&ugrave;ng đạo.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">- Nhưng chẳng lẽ c&aacute;c vị th&aacute;nh v&agrave; cả những người s&ugrave;ng đạo đều kh&ocirc;ng c&oacute; m&ocirc;i sao? &ndash;Rom&eacute;o lại tiếp.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">- V&acirc;ng c&oacute; chứ, nhưng m&ocirc;i của họ d&ugrave;ng để cầu nguyện.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">- Nếu thế, th&aacute;nh nữ y&ecirc;u qu&iacute; ơi, h&atilde;y thỏa m&atilde;n nguyện cầu tr&ecirc;n m&ocirc;i t&ocirc;i, cho niềm tin đừng trở n&ecirc;n v&ocirc; vọng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">- C&aacute;c vị th&aacute;nh c&oacute; nh&uacute;c nh&iacute;ch g&igrave; đ&acirc;u, -Juliette, vốn th&ocirc;ng thạo c&aacute;i tr&ograve; chơi ứng đối d&iacute; dỏm n&agrave;y n&oacute;i- thế m&agrave; họ vẫn thỏa m&atilde;n những lời nguyện cầu, hướng về họ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">Cuộc đối thoại sẽ c&ograve;n tiếp diễn bất tận nếu b&agrave; v&uacute; gi&agrave; kh&ocirc;ng đến mời Juliette đến ngồi b&ecirc;n cạnh mẹ, phu nh&acirc;n Capulet.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">Rom&eacute;o b&egrave;n hỏi thăm một b&agrave;, đẩy đ&agrave;, phởn phơ v&agrave; ba hoa, v&agrave; kinh ho&agrave;ng biết rằng m&igrave;nh vừa tỏ t&igrave;nh với một thiếu nữ d&ograve;ng d&otilde;i Capulet. Để mặc cho Benvolio l&ocirc;i đi, ch&agrave;ng đến từ gi&atilde; chủ nh&acirc;n, đang đứng b&ecirc;n thềm tiễn kh&aacute;ch. Juliette mơ mộng v&agrave; t&ograve; m&ograve;, đ&atilde; quay lại, thỏ thẻ hỏi b&agrave; v&uacute; rất kh&eacute;o l&eacute;o:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">- V&uacute; ơi! N&oacute;i cho con biết, ch&agrave;ng qu&iacute; tộc kia l&agrave; ai thế?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">- Đ&oacute; l&agrave; con trai v&agrave; l&agrave; người thừa kế l&atilde;o Tiberio.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">- C&ograve;n anh ch&agrave;ng vừa bước qua cửa?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">- Đ&oacute; c&oacute; lẽ l&agrave; ch&agrave;ng trai trẻ Petruchio.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">- N&agrave;y, c&ograve;n anh ch&agrave;ng đi theo sau, đấy, c&aacute;i người kh&ocirc;ng hề khi&ecirc;u vũ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">- &Agrave;, v&uacute; kh&ocirc;ng biết.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">- V&uacute; hỏi t&ecirc;n ch&agrave;ng đi, -rồi c&ocirc; n&oacute;i th&ecirc;m, nhẹ như hơi thở, nếu ch&agrave;ng đ&atilde; c&oacute; vợ, con nhất quyết sẽ chẳng bao giờ lấy chồng!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">- T&ecirc;n ch&agrave;ng l&agrave; Rom&eacute;o, b&agrave; v&uacute; quay lại n&oacute;i, d&ograve;ng họ Montaigu, con trai độc nhất của kẻ đại th&ugrave; nh&agrave; ta.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">- Mối t&igrave;nh duy nhất của ta lại trao gửi cho kẻ duy nhất ta phải th&ugrave; gh&eacute;t! &Ocirc;i! Ta đ&atilde; gặp ch&agrave;ng qu&aacute; muộn m&agrave; chẳng hề quen! &Ocirc;i! Ta đ&atilde; quen ch&agrave;ng qu&aacute; muộn. &Ocirc;i! Mối t&igrave;nh kỳ lạ vừa chớm nở trong l&ograve;ng ta! Ta phải y&ecirc;u một kẻ th&ugrave; phải căm gh&eacute;t.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">- Con n&oacute;i g&igrave; thế? Con thầm th&igrave; g&igrave; thế? &ndash;b&agrave; v&uacute; thắc mắc.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#545353\">- Những c&acirc;u thơ con vừa mới học được của người vừa mới khi&ecirc;u vũ với con &ndash;n&agrave;ng quyết giữ k&iacute;n nỗi niềm ri&ecirc;ng của m&igrave;nh, v&igrave; c&ocirc; biết r&otilde; tật ba hoa của b&agrave; v&uacute;. Tiệc đ&atilde; t&agrave;n, họ rời khỏi ph&ograve;ng ch&iacute;nh.</span></span></span></p>\r\n'),
(17, 'Nhật ký trong tù', 'Thơ', 'Hồ Chí Minh', 1942, 'nhat-ky-trong-tu.jpg', 'Đang cập nhật', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nhật k&yacute; trong t&ugrave; l&agrave; một tập thơ nổi tiếng của vị l&atilde;nh tụ vĩ đại Hồ Ch&iacute; Minh. Th&aacute;ng 8 năm 1942, Người sang Trung Quốc để tranh thủ sự ủng hộ của anh em bạn b&egrave; quốc tế cho C&aacute;ch mạng Việt Nam. Sau 15 ng&agrave;y đi bộ, khi vừa tới thị trấn T&uacute;c Vinh tỉnh Quảng T&acirc;y th&igrave; bị ch&iacute;nh quyền Tưởng Giới Thạch bắt giam v&igrave; t&igrave;nh nghi l&agrave; gi&aacute;n điệp.Từ đ&oacute; Người bị cầm t&ugrave; trong gần 30 nh&agrave; lao thuộc 13 huyện tỉnh Quảng T&acirc;y. Đến th&aacute;ng 9/1943, Người được thả tự do. 14 th&aacute;ng trong t&ugrave; Người đ&atilde; s&aacute;ng t&aacute;c tập thơ Ngục trung nhật k&yacute; (Nhật k&yacute; trong t&ugrave;). Tập thơ gồm 133 b&agrave;i thơ bằng chữ H&aacute;n ghi lại chặng đường đấu tranh gian khổ nhưng rất đỗi lạc quan của người t&ugrave; Hồ Ch&iacute; Minh.&nbsp;</span></span></p>\r\n', '<h3 style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>NGƯỜI BẠN T&Ugrave; THỔI S&Aacute;O</strong></span></span></span></h3>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Bỗng nghe trong ngục s&aacute;o vi vu,<br />\r\nKh&uacute;c nhạc t&igrave;nh qu&ecirc; chuyển điệu sầu,<br />\r\nMu&ocirc;n dặm quan h&agrave;, kh&ocirc;n xiết nỗi<br />\r\nL&ecirc;n lầu, ai đ&oacute; ng&oacute;ng tr&ocirc;ng nhau</span></span></p>\r\n\r\n<hr />\r\n<h3 style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>C&Aacute;I C&Ugrave;M</strong></span></span></span></h3>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nghĩ việc tr&ecirc;n đời kỳ lạ thật<br />\r\nC&ugrave;m ch&acirc;n sau trước cũng tranh nhau<br />\r\nĐược c&ugrave;m ch&acirc;n mới y&ecirc;n bề ngủ<br />\r\nKh&ocirc;ng được c&ugrave;m ch&acirc;n biết ngủ đ&acirc;u</span></span></p>\r\n\r\n<hr />\r\n<h3 style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>TRUNG THU</strong></span></span></span></h3>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Trung thu v&agrave;nh vạch mảnh gương thu<br />\r\nS&aacute;ng khắp nh&acirc;n gian bạc một m&agrave;u<br />\r\nSum họp nh&agrave; ai ăn Tết đ&oacute;<br />\r\nChẳng qu&ecirc;n trong ngục kẻ ăn sầu</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Trung thu ta cũng Tết trong t&ugrave;<br />\r\nTrăng gi&oacute; đ&ecirc;m thu gợn vẻ sầu<br />\r\nChẳng được tự do m&agrave; thưởng nguyệt<br />\r\nL&ograve;ng theo vời vợi mảnh trăng thu</span></span></p>\r\n\r\n<hr />\r\n<h3 style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>CHIỀU TỐI</strong></span></span></span></h3>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Chim mỏi về rừng t&igrave;m chốn ngủ<br />\r\nCh&ograve;m m&acirc;y nh&egrave; nhẹ giữa tầng kh&ocirc;ng<br />\r\nC&ocirc; em x&oacute;m n&uacute;i xay ng&ocirc; tối<br />\r\nXay hết, l&ograve; than đ&atilde; rực hồng</span></span></p>\r\n\r\n<hr />\r\n<h3 style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>TỰ KHUY&Ecirc;N M&Igrave;NH</strong></span></span></span></h3>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">V&iacute; kh&ocirc;ng c&oacute; cảnh đ&ocirc;ng t&agrave;n<br />\r\nTh&igrave; đ&acirc;u c&oacute; cảnh huy ho&agrave;ng ng&agrave;y xu&acirc;n<br />\r\nNghĩ m&igrave;nh trong bước gian tu&acirc;n<br />\r\nTai ương r&egrave;n luyện tinh thần th&ecirc;m hăng.</span></span></p>\r\n\r\n<hr />\r\n<h3 style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>RỤNG MẤT MỘT CHIẾC RĂNG</strong></span></span></span></h3>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Cứng rắn như anh kh&aacute;c th&oacute;i thường<br />\r\nPhải đ&acirc;u mềm tựa lưỡi kh&ocirc;ng xương<br />\r\nNgọt b&ugrave;i cay đắng từng chia sẻ<br />\r\nNay phải xa nhau, kẻ một đường.</span></span></p>\r\n\r\n<hr />\r\n<h3 style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>BUỒN BỰC</strong></span></span></span></h3>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tr&aacute;ng sĩ đua nhau ra mặt trận<br />\r\nHo&agrave;n cầu lửa bốc rực trời xanh<br />\r\nTrong ngục người nh&agrave;n nh&agrave;n qu&aacute; đỗi<br />\r\nCh&iacute; cao m&agrave; chẳng đ&aacute;ng đồng chinh</span></span></p>\r\n\r\n<hr />\r\n<h3 style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><span style=\"color:#e74c3c\">HO&Agrave;NG H&Ocirc;N</span></strong></span></span></h3>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Gi&oacute; sắc tựa gươm m&agrave;i đ&aacute; n&uacute;i<br />\r\nR&eacute;t như d&ugrave;i nhọn ch&iacute;ch c&agrave;nh c&acirc;y<br />\r\nCh&ugrave;a xa chu&ocirc;ng giục người nhanh bước<br />\r\nTrẻ dắt tr&acirc;u về tiếng s&aacute;o bay</span></span></p>\r\n\r\n<hr />\r\n<h3 style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>KH&Ocirc;NG NGỦ ĐƯỢC</strong></span></span></span></h3>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Một canh...hai canh...lại ba canh<br />\r\nTrằn trọc băn khoăn giấc chẳng l&agrave;nh<br />\r\nCanh bốn, canh năm vừa chợp mắt<br />\r\nSao v&agrave;ng năm c&aacute;nh mộng hồn bay.</span></span></p>\r\n\r\n<hr />\r\n<h3 style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>NGHE TIẾNG CH&Agrave;Y GI&Atilde; GẠO</strong></span></span></span></h3>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Gạo đem v&agrave;o gi&atilde; bao đau đớn<br />\r\nGạo gi&atilde; xong rồi trắng tựa b&ocirc;ng<br />\r\nSống tr&ecirc;n đời người cũng vậy<br />\r\nGian nan r&egrave;n luyện mới th&agrave;nh c&ocirc;ng.</span></span></p>\r\n\r\n<hr />\r\n<h3 style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>BỐN TH&Aacute;NG RỒI</strong></span></span></span></h3>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ldquo;Một ng&agrave;y t&ugrave;, ngh&igrave;n thu ở ngo&agrave;i&rdquo;<br />\r\nLời n&oacute;i người xưa đ&acirc;u c&oacute; sai<br />\r\nSống kh&aacute;c lo&agrave;i người vừa bốn th&aacute;ng<br />\r\nTiều tụy c&ograve;n hơn mười năm trời</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Bởi v&igrave;:<br />\r\nBốn th&aacute;ng cơm kh&ocirc;ng no<br />\r\nBốn th&aacute;ng đ&ecirc;m thiếu ngủ<br />\r\nBốn th&aacute;ng &aacute;o kh&ocirc;ng thay<br />\r\nBốn th&aacute;ng kh&ocirc;ng giặt giũ</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Cho n&ecirc;n:<br />\r\nRăng rụng mất một chiếc<br />\r\nT&oacute;c bạc th&ecirc;m mấy phần<br />\r\nGầy đen như quỷ đ&oacute;i<br />\r\nGhẻ lở mọc đầy th&acirc;n</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">May m&agrave;:<br />\r\nKi&ecirc;n tr&igrave; v&agrave; nhẫn nại<br />\r\nKh&ocirc;ng chịu lui một ph&acirc;n<br />\r\nVật chất tuy đau khổ<br />\r\nKh&ocirc;ng nao n&uacute;ng tinh thần.</span></span></p>\r\n\r\n<hr />\r\n<h3 style=\"text-align:center\"><span style=\"color:#e74c3c; font-family:Tahoma, Geneva, sans-serif\"><span style=\"font-size:16px\"><strong>ĐI ĐƯỜNG</strong></span></span></h3>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&quot;Đi đường mới biết gian lao,</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">N&uacute;i cao rồi lại n&uacute;i cao trập tr&ugrave;ng;</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">N&uacute;i cao l&ecirc;n đến tận c&ugrave;ng,</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thu v&agrave;o tầm mắt mu&ocirc;n tr&ugrave;ng nước non&quot;.</span></span></p>\r\n\r\n<hr />\r\n<h3 style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>NỬA Đ&Ecirc;M</strong></span></span></span></h3>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ngủ th&igrave; ai cũng như lương thiện<br />\r\nTỉnh dậy ph&acirc;n ra kẻ dữ, hiền<br />\r\nHiền dữ phải đ&acirc;u l&agrave; t&iacute;nh sẵn<br />\r\nPhần nhiều do gi&aacute;o dục m&agrave; n&ecirc;n</span></span></p>\r\n\r\n<hr />\r\n<p style=\"text-align:center\"><br />\r\n<span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>DẠ B&Aacute;N</strong></span><br />\r\nThụy th&igrave; độ tượng thuần lương h&aacute;n<br />\r\nTỉnh hậu t&agrave;i ph&acirc;n thiện &aacute;c nh&acirc;n<br />\r\nThiện, &aacute;c nguy&ecirc;n lai v&ocirc; định t&iacute;nh<br />\r\nĐa do gi&aacute;o dục đ&iacute;ch nguy&ecirc;n nh&acirc;n.</span></span></p>\r\n\r\n<hr />\r\n<h3 style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>NẮNG SỚM</strong></span></span></span></h3>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tri&ecirc;u dương xuy&ecirc;n qu&aacute; lung to&agrave;n bộ,</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thi&ecirc;u tận u y&ecirc;n dữ &aacute;m mai;</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Sinh kh&iacute; đốn th&igrave; sung vũ trụ,</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Phạm nh&acirc;n c&aacute; c&aacute; tiếu nhan khai.</span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Bản dịch của Huệ Chi:</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nắng sớm xuy&ecirc;n qua nơi ngục thất,</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Đốt tan kh&oacute;i đặc với sương d&agrave;y;</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Đất trời ph&uacute;t chốc tr&agrave;n sinh kh&iacute;,</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">T&ugrave; phạm cười tươi nở mặt m&agrave;y.</span></span></p>\r\n\r\n<hr />\r\n<h3 style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>XẾ CHIỀU</strong></span></span></span></h3>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Cơm xong b&oacute;ng đ&atilde; xuống trầm trầm<br />\r\nVang tiếng đ&agrave;n ca rộn tiếng ng&acirc;n<br />\r\nNh&agrave; ngục Tĩnh t&acirc;y mờ mịt tối<br />\r\nBổng th&agrave;nh nhạc qu&aacute;n viện h&agrave;m l&acirc;m</span></span></p>\r\n\r\n<hr />\r\n<h3 style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>GIA QUYẾN NGƯỜI BỊ BẮT L&Iacute;NH</strong></span></span></span></h3>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Biền biệt anh đi kh&ocirc;ng trở lại<br />\r\nBuồng the, trơ trọi, thiếp &ocirc;m sầu<br />\r\nQuan tr&ecirc;n s&oacute;t nỗi em c&ocirc; quạnh<br />\r\nN&ecirc;n lại mời em tạm ở t&ugrave;.</span></span></p>\r\n'),
(18, 'Sóng', 'Thơ', 'Xuân Quỳnh', 1967, 'song.jpg', 'Đã hoàn', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Qua h&igrave;nh tượng s&oacute;ng, tr&ecirc;n cơ sở kh&aacute;m ph&aacute; sự tương đồng, h&ograve;a hợp giữa s&oacute;ng v&agrave; em, b&agrave;i thơ diễn tả t&igrave;nh y&ecirc;u của người phụ nữ thiết tha, nồng n&agrave;n, chung thủy, muốn vượt l&ecirc;n thử th&aacute;ch của thời gian v&agrave; sự hữu hạn của đời người. Từ đ&oacute; cho thấy t&igrave;nh y&ecirc;u l&agrave; một thứ t&igrave;nh cảm cao đẹp, một hạnh ph&uacute;c lớn lao của con người.</span></span></p>\r\n', '<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"background-color:#fcfcfc\"><span style=\"color:black\">Dữ dội v&agrave; dịu &ecirc;m<br />\r\nỒn &agrave;o v&agrave; lặng lẽ<br />\r\nS&ocirc;ng&nbsp;kh&ocirc;ng hiểu nổi m&igrave;nh<br />\r\nS&oacute;ng t&igrave;m ra tận bể<br />\r\n<br />\r\n&Ocirc;i con s&oacute;ng ng&agrave;y xưa<br />\r\nV&agrave; ng&agrave;y sau vẫn thế<br />\r\nNỗi kh&aacute;t vọng t&igrave;nh y&ecirc;u<br />\r\nBồi hồi trong ngực trẻ<br />\r\n<br />\r\nTrước mu&ocirc;n tr&ugrave;ng s&oacute;ng bể<br />\r\nEm nghĩ về anh, em<br />\r\nEm nghĩ về biển lớn<br />\r\nTừ nơi n&agrave;o s&oacute;ng l&ecirc;n?<br />\r\n<br />\r\nS&oacute;ng bắt đầu từ gi&oacute;<br />\r\nGi&oacute; bắt đầu từ đ&acirc;u?<br />\r\nEm cũng kh&ocirc;ng biết nữa<br />\r\nKhi n&agrave;o ta y&ecirc;u nhau<br />\r\n<br />\r\nCon s&oacute;ng dưới l&ograve;ng s&acirc;u<br />\r\nCon s&oacute;ng tr&ecirc;n mặt nước<br />\r\n&Ocirc;i con s&oacute;ng nhớ bờ<br />\r\nNg&agrave;y đ&ecirc;m kh&ocirc;ng ngủ được<br />\r\nL&ograve;ng em nhớ đến anh<br />\r\nCả trong mơ c&ograve;n thức<br />\r\n<br />\r\nDẫu xu&ocirc;i về phương bắc<br />\r\nDẫu ngược về phương nam<br />\r\nNơi n&agrave;o em cũng nghĩ<br />\r\nHướng về anh - một phương<br />\r\n<br />\r\nỞ ngo&agrave;i kia đại dương<br />\r\nTrăm ngh&igrave;n con s&oacute;ng đ&oacute;<br />\r\nCon n&agrave;o chẳng tới bờ<br />\r\nD&ugrave; mu&ocirc;n vời c&aacute;ch trở<br />\r\n<br />\r\nCuộc đời tuy d&agrave;i thế<br />\r\nNăm th&aacute;ng vẫn đi qua<br />\r\nNhư biển kia dẫu rộng<br />\r\nM&acirc;y vẫn bay về xa<br />\r\n<br />\r\nL&agrave;m sao được tan ra<br />\r\nTh&agrave;nh trăm con s&oacute;ng nhỏ<br />\r\nGiữa biển lớn t&igrave;nh y&ecirc;u<br />\r\nĐể ng&agrave;n năm c&ograve;n vỗ</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(19, 'Số đỏ', 'Văn học Việt Nam', 'Vũ Trọng Phụng', 1936, 'so-do.jpg', 'Đang cập nhật', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&nbsp;Số đỏ&nbsp;l&agrave; tiểu thuyết kể về cuộc đời của&nbsp;Xu&acirc;n T&oacute;c Đỏ. Xu&acirc;n l&agrave; dứa trẻ mồ c&ocirc;i, lang thang đầu đường x&oacute; chợ. Cậu phải tr&egrave;o me, tr&egrave;o sấu để kiếm sống. Ch&iacute;nh v&igrave; thế n&ecirc;n Xu&acirc;n sớm bị tha ho&aacute; v&agrave; bị cớm bắt giam. Sau khi may mắn được b&agrave; Ph&oacute; Đoan &ndash; một mụ me T&acirc;y d&acirc;m đ&atilde;ng cứu v&agrave; giới thiệu c&ocirc;ng việc cho Xu&acirc;n. Xu&acirc;n l&agrave;m nh&acirc;n vi&ecirc;n phụ cho cửa h&agrave;ng &Acirc;u Ho&aacute; chuy&ecirc;n cắt may quần &aacute;o cho vợ chồng Văn Minh. Xu&acirc;n T&oacute;c Đỏ nhận danh hiệu&nbsp;&ldquo; Sinh vi&ecirc;n trường thuốc&rdquo;, &ldquo;đốc tờ Xu&acirc;n&rdquo;.&nbsp;Hắn được gia nhập v&agrave;o giới thượng lưu, mở rộng quan hẹ với những người c&oacute; quyền thế. Xu&acirc;n được c&ocirc; Tuyết, em của Văn Minh, con g&aacute;i cụ cố Hồng y&ecirc;u say đắm.&nbsp;Xu&acirc;n c&agrave;ng ng&agrave;y c&agrave;ng được mọi người k&iacute;nh trọng. Hắn v&ocirc; t&igrave;nh g&acirc;y ra c&aacute;i chết của cụ cố tổ v&agrave; được cả gia đ&igrave;nh họ biết ơn. Gia đ&igrave;nh cụ cố Hồng chuẩn bị v&agrave; tổ chức đ&aacute;m tang rất long trọng v&agrave; nhộn nhịp. Vợ chồng Văn Minh rất y&ecirc;u qu&yacute; Xu&acirc;n v&agrave; c&ograve;n định gả c&ocirc; Tuyết cho v&igrave; c&ocirc; Tuyết vốn cũng l&agrave; người hư hỏng.&nbsp;Sau đ&aacute;m tang, Xu&acirc;n đăng k&iacute; tham gia giải quần vợt nh&acirc;n dịp vua Xi&ecirc;m sang Bắc Kinh. Bằng thủ đoạn của m&igrave;nh, Xu&acirc;n trờ th&agrave;nh người duy nhất đấu với qu&aacute;n qu&acirc;n của nước Xi&ecirc;m. Xu&acirc;n cố t&igrave;nh thua v&igrave; giữ mối quan hệ giao hảo giữa hai nước. Ch&iacute;nh v&igrave; thế hằn c&agrave;ng được tung h&ocirc; v&agrave; trở th&agrave;nh vĩ nh&acirc;n. Xu&acirc;n được tham gia v&agrave;o hiều hội v&agrave; trở th&agrave;nh con rể của cụ vố Hồng.</span></span></p>\r\n', '<p style=\"text-align:justify\"><span style=\"color:#e74c3c\"><strong><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">CHƯƠNG I: SỐ Đ&Agrave;O HOA CỦA XU&Acirc;N T&Oacute;C ĐỎ - L&Ograve;NG THƯƠNG CỦA NGƯỜI Đ&Agrave;N B&Agrave; PH&Oacute; ĐOAN</span></span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">L&uacute;c ấy v&agrave;o độ 3 giờ chiều, một ng&agrave;y thứ năm.&nbsp;Trong khu s&acirc;n quần m&agrave; b&ecirc;n ngo&agrave;i l&agrave; những h&agrave;ng ruối k&iacute;n m&iacute;t, chỉ c&oacute; một s&acirc;n hữu l&agrave; được hai người Ph&aacute;p d&ugrave;ng đến. Hai đứa trẻ nhỏ tuổi uể oải đi nhặt những quả b&oacute;ng để n&eacute;n cho hai người T&acirc;y. Mồ h&ocirc;i ướt đầm &aacute;o, hai người n&agrave;y cũng chơi uể oải như những nh&agrave; thể thao bất đắc dĩ kh&aacute;c.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- xanh ca!&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- xanh x&iacute;t!&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Những c&acirc;u h&ocirc; như vậy chen lẫn những tiếng bồm bộp của những quả ban bị đ&aacute;nh đi, như giữ nhịp cho kh&uacute;c &acirc;m nhạc của mấy vạn con ve sầu.&nbsp;Ngo&agrave;i đường ở vệ h&egrave;, một người b&aacute;n nước chanh, ngồi chồm chỗm tr&ecirc;n c&agrave;ng xe, đương n&oacute;i chuyện với một người bạn đồng nghiệp.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Qu&aacute;i, thứ năm g&igrave; m&agrave; vắng thế!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Chốc nữa họ mới lại chứ? B&acirc;y giờ mới hơn ba giờ. Từ h&ocirc;m nay trở đi, họ tập gấp, chắc ng&agrave;y n&agrave;o cũng phải luyện chứ chả cứ thứ năm thứ bảy hay chủ nhật...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thế &agrave;? Sao biết?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- M&ecirc; đi! Ba bốn th&aacute;ng nữa, đức vua ra đ&acirc;y, lại c&ograve;n g&igrave;! Chuyến n&agrave;y sẽ c&oacute; c&uacute;p oai gh&ecirc;... C&aacute;c anh c&aacute;c chị gọi l&agrave; tập mửa mật.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tr&ecirc;n h&egrave;, dưới b&oacute;ng c&acirc;y gạo, một &ocirc;ng thầy số đ&atilde; c&oacute; tuổi ngồi b&igrave;nh tĩnh nh&igrave;n c&aacute;i ch&aacute;p, nghi&ecirc;n mực, miếng son, ống b&uacute;t, với mấy l&aacute; số tử vi mẫu, thỉnh thoảng lại ng&aacute;p một c&aacute;i như một nh&agrave; triết học ch&acirc;n ch&iacute;nh. C&aacute;ch đấy mười bước, Xu&acirc;n T&oacute;c Đỏ ngồi tri kỷ với một chị h&agrave;ng m&iacute;a. Thương mại? Kh&ocirc;ng! Ấy l&agrave; một cuộc t&igrave;nh duy&ecirc;n, với, hơn nữa - theo lối gọi của những &ocirc;ng l&agrave;m b&aacute;o - một cuộc t&igrave;nh duy&ecirc;n của B&igrave;nh d&acirc;n.&nbsp;L&agrave; v&igrave; Xu&acirc;n T&oacute;c Đỏ cứ sấn sổ đưa tay toan cướp giật &aacute;i t&igrave;nh...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">-... Cứ ỡm ờ m&atilde;i!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Xin một tị! Xin một tị tỉ t&igrave; ti th&ocirc;i!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Khỉ lắm nữa!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Lẳng lơ th&igrave; cũng chẳng m&ograve;n...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thật đấy. Ch&iacute;nh chuy&ecirc;n cũng chẳng sơn son để thờ! Nhưng n&agrave;y! Duy&ecirc;n kia ai đợi m&agrave; chờ? T&igrave;nh kia ai tưởng m&agrave; tơ tưởng t&igrave;nh? H&agrave;ng đ&atilde; ế bỏ mẹ ra thế n&agrave;y n&agrave;y, mua chẳng mua gi&uacute;p lại chỉ được c&aacute;i bộ ếm...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Xu&acirc;n T&oacute;c đỏ đứng phăng l&ecirc;n, anh h&ugrave;ng m&agrave; n&oacute;i dỗi:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Đ&acirc;y kh&ocirc;ng cần!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Chị h&agrave;ng m&iacute;a lườm d&agrave;i một c&aacute;i, cong cớn:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Kh&ocirc;ng cần th&igrave; c&uacute;t v&agrave;o trong ấy c&oacute; được kh&ocirc;ng?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Xu&acirc;n T&oacute;c Đỏ lại cười h&iacute; h&iacute; như ngựa rồi ngồi xuống...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- N&oacute;i đ&ugrave;a đấy, chứ đ&acirc;y m&agrave; lại chả cần đấy th&igrave; đấy cần đếch g&igrave; đ&acirc;y? Th&ocirc;i đi, l&agrave;m bộ vừa vừa chứ... B&aacute;n một xu n&agrave;o.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Ừ! Ứ! Đưa tiền ngay ra đ&acirc;y xem!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">R&uacute;t ở t&uacute;i quần sau một c&aacute;i m&ugrave; soa, cởi một n&uacute;t buộc như một c&aacute;i tai lợn, Xu&acirc;n T&oacute;c Đỏ đập đồng h&agrave;o v&aacute;n xuống thềm gạch xi măng đ&aacute;nh keng một c&aacute;i rất oanh liệt.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Trong khi chị h&agrave;ng m&iacute;a cầm một tấm để r&oacute;c vỏ th&igrave; Xu&acirc;n lải nhải tự cổ động cho m&igrave;nh:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Năm h&agrave;o c&ograve;n hai đấy! Tối h&ocirc;m qua mất ba h&agrave;o. Thết bạn cẩn thận... Hai h&agrave;o v&eacute; đi tuần trong H&yacute; viện rồi lại b&aacute;t phở t&aacute;i năm. Chơi thế mới ch&aacute;nh chứ? C&ocirc;ng tử bột th&igrave; cũng ch&uacute;a đến thế l&agrave; c&ugrave;ng... Ấy ăn ti&ecirc;u rộng như thế mới chết! Đ&acirc;y bảo đấy về đ&acirc;y phải lo th&igrave; khỏi ăn chơi, th&igrave; đấy m&atilde;i chả nghe!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Chị h&agrave;ng m&iacute;a l&agrave;m thinh, Xu&acirc;n nhồm nho&agrave;m nhai m&iacute;a, lấy b&atilde; n&eacute;m v&agrave;o một c&aacute;i cột đ&egrave;n. Sau c&ugrave;ng, hắn ch&ugrave;i tay v&agrave;o quần, đứng l&ecirc;n vươn vai... Chị h&agrave;ng m&iacute;a đưa trả h&agrave;o ch&iacute;n tiền thừa th&igrave; hắn khoanh hai tay sau lưng kh&ocirc;ng nhận.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bỏ hộ v&agrave;o t&uacute;i quần... Thọc tay v&agrave;o!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Bực m&igrave;nh, chị h&agrave;ng để tiền dưới đất th&igrave; hắn c&uacute;i xuống nhặt lấy vậy.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Chả nưới mẹ g&igrave; cả! Than &ocirc;i c&aacute;i cảnh đ&ecirc;m thu tịch mịch c&agrave;ng l&agrave;m như gợi kh&aacute;ch đa sầu!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Vừa h&aacute;t mấy c&acirc;u cải lương Nam Kỳ, hắn vừa đỏng đảnh tiến đến chỗ &ocirc;ng thầy số. Hắn đứng nh&igrave;n &ocirc;ng gi&agrave; hồi l&acirc;u như một anh d&acirc;n qu&ecirc; lần đầu đứng trước c&aacute;i chuồng khỉ của b&agrave; B&eacute; T&yacute;, rồi n&oacute;i lớn:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Xem một quẻ đ&acirc;y!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&Ocirc;ng cụ tỉnh cơn ngủ gật tức khắc, l&ocirc;i ngay c&aacute;i b&uacute;t l&ocirc;ng g&agrave;i ở tai xuống nhanh nhẹn chẳng k&eacute;m những thầy cảnh s&aacute;t l&uacute;c bi&ecirc;n phạt.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Hai h&agrave;o! Hai h&agrave;o một l&aacute;, c&oacute; bằng l&ograve;ng th&igrave;....</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Một h&agrave;o đấy! B&oacute;i rẻ c&ograve;n hơn ngồi kh&ocirc;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Ừ, th&igrave; đặt tiền đi vậy...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Th&igrave; đặt! Đ&acirc;y kh&ocirc;ng cần!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Hắn ngồi xuống chiếu, để l&ecirc;n nắp ch&aacute;p h&agrave;o chỉ. &Ocirc;ng thầy lấy giấy bản, loay hoay m&agrave;i mực, nhổ v&agrave;i ba b&atilde;i nước bọt v&agrave;o nghi&ecirc;n mực, rồi cầm b&uacute;t hỏi:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Ng&agrave;y sinh, th&aacute;ng đẻ, n&oacute;i ra.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Hai mươi nhăm tuổi đấy, bố ạ! Th&aacute;ng mười, ng&agrave;y rằm, giờ g&agrave; l&ecirc;n chuồng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&Ocirc;ng thầy b&ograve; nho&agrave;i tr&ecirc;n chiếc chiếu, trước khi viết l&ecirc;n giấy c&ograve;n lẩm nhẩm đọc trong mồm v&agrave; bấm tr&ecirc;n ng&oacute;n tay. Xu&acirc;n T&oacute;c Đỏ khoanh tay quanh hai đầu gối, để cằm t&igrave; l&ecirc;n một cổ tay. &Ocirc;ng thầy vừa viết vừa n&oacute;i:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Tuần triệt đương đầu kiếp kh&ocirc;ng th&acirc;n mệnh....</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&Acirc;m dương tuần triệt tại tiền,</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Cha mẹ ắt hẳn chơi ti&ecirc;n thuở n&agrave;o.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Nếu n&oacute;i đ&uacute;ng giờ th&igrave; số n&agrave;y phải bồ c&ocirc;i sớm.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Đ&uacute;ng đấy! Đ&uacute;ng!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- L&uacute;c thiếu thời th&igrave; cậu vất vả lắm.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Kh&aacute;!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- &Agrave;, m&agrave; số n&agrave;y cũng kh&ocirc;ng xấu lắm đ&acirc;u.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Khốc hư t&yacute; ngọ cư quan</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tiếng tăm rậy khắp giang sơn một thời.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Sau n&agrave;y danh phận cũng to cơ đấy!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Được! Thế bao giờ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Từ năm nay trở đi đ&atilde; mở vận đấy.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Chưa thấy g&igrave; cả.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Cuối năm sẽ thấy.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Từ đầu năm đến giờ đ&atilde; ph&aacute;t những g&igrave;?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Đầu năm đến giờ chỉ ph&aacute;t sao đ&agrave;o hoa th&ocirc;i...!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thế l&agrave; thế n&agrave;o?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Nghĩa l&agrave; những chuyện giai g&aacute;i th&igrave; dễ ăn thua lắm.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Xu&acirc;n T&oacute;c Đỏ vỗ tay đ&ocirc;m đốp như những khi n&oacute; thấy bọn quần vợt đ&aacute;nh được một miếng hay. Rồi n&oacute; n&oacute;i ngậu sị.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Đ&uacute;ng gh&ecirc;! Đ&uacute;ng gh&ecirc;! H&ocirc;m qua, l&uacute;c tan h&aacute;t, đi qua ng&otilde; Sầm C&ocirc;ng th&igrave; c&oacute; ba bốn chị chạy &ugrave;a ra, v&acirc;y chung quanh, k&eacute;o tay, n&iacute;u &aacute;o, t&igrave;nh bỏ mẹ ra ấy! Xin chịu thầy.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Rồi quay lại doạ chị h&agrave;ng m&iacute;a:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Phải biết!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Rồi khẽ n&oacute;i với &ocirc;ng thầy:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Ngay như con b&eacute; b&aacute;n m&iacute;a n&agrave;y th&igrave; cũng &quot;nước n&ocirc;i&quot; đến nơi chứ c&oacute; kh&ocirc;ng đ&acirc;u! Cụ đo&aacute;n đ&aacute;ng đồng tiền lắm.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">L&uacute;c ấy c&oacute; một chiếc xe hơi h&ograve;m đầu nhọn, đu&ocirc;i nhọn, đỗ ngay trước cửa s&acirc;n quần. Cửa xe mở, một b&agrave; trạc ngoại tứ tuần m&agrave; y phục c&ograve;n trai lơ hơn của c&aacute;c thiếu nữ, mặt bự ra những son v&agrave; phấn, t&oacute;c đen lay l&aacute;y nhưng m&agrave; quăn quăn, cả người nặng &iacute;t ra cũng bẩy mươi c&acirc;n, nhưng c&aacute;i khăn v&agrave;nh gi&acirc;y đ&uacute;ng mốt hết sức th&igrave; lại nhỏ x&iacute;u v&agrave; ngắn ngủn c&oacute; một mẩu, một tay cầm một c&aacute;i d&ugrave; thật t&yacute; hon v&agrave; một c&aacute;i v&iacute; da khổng lồ, tay kia &ocirc;m một con ch&oacute; b&eacute; tr&ocirc;ng kỳ dị như một con kỳ l&acirc;n, bước xuống đất một c&aacute;ch nặng nề vất vả. Rồi đến một ch&agrave;ng thiếu ni&ecirc;n cao ngẳng, gầy đ&eacute;t, lộ hầu, hai mắt như ốc nhồi, t&oacute;c cũng uốn quăn. &Acirc;u phục lối du lịch, chui ở xe ra đưa tay cho một thiếu nữ mặc quần đ&ugrave;i trắng, t&oacute;c b&uacute;i, giầu cao su, tay cắp hai c&aacute;i vợt, chui ra sau c&ugrave;ng. Cả ba đủng đỉnh v&agrave;o s&acirc;n.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Mải nghe đo&aacute;n số, Xu&acirc;n T&oacute;c Đỏ kh&ocirc;ng tr&ocirc;ng thấy những người ấy. Hắn cứ việc cắt nghĩa m&atilde;i c&aacute;i số đ&agrave;o hoa v&agrave; hỏi dồn &ocirc;ng thầy:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Sau n&agrave;y c&oacute; gi&agrave;u kh&ocirc;ng? Hay chỉ c&oacute; danh gi&aacute; h&atilde;o?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Gi&agrave;u th&igrave; chả gi&agrave;u g&igrave; nhưng cũng phong lưu.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">C&acirc;u đo&aacute;n ấy khiến Xu&acirc;n ta nghĩ ngợi, mơ mộng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">L&uacute;c mới 9 tuổi, n&oacute; đ&atilde; phải đi ở nhờ nh&agrave; một người b&aacute;c họ, họ th&uacute;c b&aacute;. B&aacute;c n&oacute; nu&ocirc;i n&oacute; thay đầy tớ v&agrave; được cả họ khen l&agrave; nu&ocirc;i ch&aacute;y bồ c&ocirc;i. Nhưng một h&ocirc;m n&oacute; bị đ&aacute;nh một trận v&agrave; bị đuổi đi. B&aacute;c g&aacute;i n&oacute; tắm, n&oacute; đ&atilde; kho&eacute;t một chỗ ph&ecirc;n nứa để nh&igrave;n! Từ đấy, thằng Xu&acirc;n lấy đầu h&egrave; x&oacute; cửa l&agrave;m nh&agrave;, lấy sấu ở c&aacute;c phố, c&aacute; hồ Ho&agrave;n Kiếm l&agrave;m cơm. N&oacute; đ&atilde; b&aacute;n ph&aacute; xa, b&aacute;n nhật tr&igrave;nh, l&agrave;m chạy hiệu rạp h&aacute;t, b&aacute;n cao đan ho&agrave;n t&aacute;n tr&ecirc;n xe lửa, v&agrave; v&agrave;i ba nghề tiểu xảo kh&aacute;c nữa. &Aacute;nh nắng mặt trời l&agrave;m cho t&oacute;c n&oacute; đỏ như t&oacute;c T&acirc;y. Cảnh ngộ đ&oacute; tạo n&oacute; n&ecirc;n một đứa ho&agrave;n to&agrave;n v&ocirc; gi&aacute;o dục, tuy n&oacute; tinh qu&aacute;i lắm, thạo đời lắm. N&oacute; mới xin được ch&acirc;n nhặt b&oacute;ng trong s&acirc;n quần độ trong v&ograve;ng một năm nay th&ocirc;i. Nhưng n&oacute; đ&aacute;nh quần ch&oacute;ng hay lắm n&ecirc;n được hội vi&ecirc;n Ph&aacute;p v&agrave; Nam c&oacute; l&ograve;ng y&ecirc;u, được trọng đ&atilde;i một ch&uacute;t. Mộng tưởng của n&oacute; l&agrave; sẽ c&oacute; ng&agrave;y được oai như Chim, Giao&nbsp;nếu hạnh ph&uacute;c dắt đến cho n&oacute; một &ocirc;ng bầu. B&acirc;y giờ th&igrave; n&oacute; cam t&acirc;m y&ecirc;n phận l&agrave; một thằng nhặt b&oacute;ng. Tuy nhi&ecirc;n n&oacute; cũng mừng đ&atilde; t&igrave;m được nghề ấy, c&aacute;i nghề tuy h&egrave;n nhưng c&ograve;n c&oacute; thể hy vọng được ch&uacute;t danh thơm. B&aacute;n lạc, tr&egrave;o sấu, c&acirc;u c&aacute;, l&agrave;m l&iacute;nh chạy cờ hiệu, những nghiệp ấy chỉ dắt đến một chỗ tắc tị. Phong tr&agrave;o thể thao, phong tr&agrave;o b&igrave;nh d&acirc;n khiến n&oacute; lắm l&uacute;c tự ki&ecirc;u tự đắc lạ l&ugrave;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Cụ tr&ocirc;ng mặt t&ocirc;i mai sau c&oacute; phất được kh&ocirc;ng?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&Ocirc;ng thầy ngắm ngh&iacute;a c&aacute;i đầu t&oacute;c đỏ, c&aacute;i tr&aacute;n l&eacute;p, c&aacute;i quai h&agrave;m to, c&aacute;i nh&acirc;n trung d&agrave;i, hai c&aacute;i tai đầy đặn ấy, rồi gật g&ugrave;:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Kh&aacute; lắm! Hậu vận kh&aacute; lắm! Chỉ tiếc c&aacute;i t&oacute;c kh&ocirc;ng được đen.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Mẹ kiếp! Chứ xưa nay c&oacute; mũ bao giờ đ&acirc;u m&agrave; t&oacute;c chả đỏ!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Chợt từ s&acirc;n quần c&oacute; một đứa b&eacute; chay ra gọi rầm n&oacute; l&ecirc;n m&agrave; rằng:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- K&igrave;a anh Xu&acirc;n! Kh&ocirc;ng v&agrave;o đi? Tiểu thư đ&atilde; đến đấy! Kh&ocirc;ng c&oacute; người, anh kh&ocirc;ng v&agrave;o đi &agrave;?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Xu&acirc;n T&oacute;c Đỏ hỏi:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Tiểu thư &agrave;?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Phải! Con Văn Minh c&oacute; c&aacute;i thằng chồng ta đặt t&ecirc;n l&agrave; C&agrave; Kếu ấy m&agrave;. Cả con mẹ Ph&oacute; Đoan cũng đến xem, lại đ&ograve;i chơi nữa!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Xu&acirc;n T&oacute;c Đỏ đứng l&ecirc;n, dặn &ocirc;ng thầy:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Cứ viết đi, rồi chiều hay mai t&ocirc;i lấy, cụ nh&eacute;! Nhớ hộ l&agrave; tiền rồi. N&agrave;o! V&agrave;o ken cờ ban&nbsp;với mỹ nh&acirc;n cho n&oacute; hoa đ&agrave;o một thể!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Đi qua chị h&agrave;ng m&iacute;a, thằng Xu&acirc;n cười t&igrave;nh, nh&aacute;y một c&aacute;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Ơ voa&nbsp; nh&eacute;! Mai nh&eacute;!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Rồi n&oacute; chạy v&agrave;o s&acirc;n b&ecirc;n tả l&agrave; chỗ ba người đợi.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Lạy cụ lớn ạ! Lạy &ocirc;ng! Lạy b&agrave;!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Cặp vợ chồng Văn Minh khẽ c&uacute;i đầu đ&aacute;p, nhưng b&agrave; Ph&oacute; Đoan nguẩy một c&aacute;i. Thiếu phụ cười, đưa mắt cho chồng. Người n&agrave;y bảo Xu&acirc;n:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- B&agrave; t&ocirc;i kh&ocirc;ng th&iacute;ch kiểu c&aacute;ch thế.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">B&agrave; Ph&oacute; Đoan mắng lu&ocirc;n Xu&acirc;n T&oacute;c Đỏ:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Anh ngu lắm! Cụ g&igrave;? T&ocirc;i chỉ mới đ&aacute;ng tuổi l&agrave; mẹ anh th&ocirc;i. Cụ th&igrave; ra đẻ được ra mẹ anh nữa kia &agrave;? M&agrave; mẹ anh th&igrave;...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Lạy b&agrave; lớn ạ, ch&aacute;u lỡ lời, b&agrave; lớn tha cho.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Sau khi c&aacute;i cơn thịnh nộ của một me t&acirc;y ch&acirc;n ch&iacute;nh nguội đi rồi, Xu&acirc;n cầm c&aacute;i vợt chạy ra ph&iacute;a s&acirc;n kia. Bộp bộp, ban bay đi bay lại... Bộ đ&ugrave;i trắng n&otilde;n của c&ocirc; Văn Minh l&agrave;m cho Xu&acirc;n đ&aacute;nh lỗi mấy quả đầu, v&agrave; l&agrave;m cho c&ocirc; n&agrave;y c&oacute; c&aacute;i ảo tưởng l&agrave; m&igrave;nh chơi cừ hơn xưa.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Vẫn c&ograve;n hầm hầm, b&agrave; Ph&oacute; Đoan lầu nhầu:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- C&aacute;i d&acirc;n An Nam ngu thật!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thiếu ni&ecirc;n đ&aacute;p:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- D&igrave; chấp c&aacute;i hạng ấy l&agrave;m qu&aacute;i g&igrave;!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Dễ t&ocirc;i cũng phải tập thể thao mới được, kh&ocirc;ng c&oacute; chả mấy l&uacute;c m&agrave; gi&agrave;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Ồ! Nếu thế th&igrave; Văn Minh t&ocirc;i xin k&yacute; cả hai tay! Thật kh&ocirc;ng hở d&igrave;? D&igrave; th&iacute;ch tập thể thao? Một cuộc đắc thắng của thể thao! Một cuộc tiến bộ của nước Việt Nam! Sự cường thịnh của n&ograve;i giống!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&Ocirc;ng ấy n&oacute;i thế một c&aacute;ch sốt sắng chẳng k&eacute;m những người gầy g&ograve; ốm yếu kh&ocirc;ng hề tập thể thao bao giờ v&agrave; chỉ xui giục kẻ kh&aacute;c. Nguy&ecirc;n do &ocirc;ng ấy l&agrave; một du học sinh quay về tổ quốc m&agrave; kh&ocirc;ng c&oacute; một mảnh văn bằng n&agrave;o cả.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">H&igrave;nh như ở b&ecirc;n Ph&aacute;p, &ocirc;ng l&agrave; bạn th&acirc;n của những nh&agrave; ch&iacute;nh trị đ&atilde; từng l&agrave;m thứ trưởng, thượng thư, văn h&agrave;o, v&acirc;n v&acirc;n, những vị c&oacute; danh tiếng m&agrave; b&aacute;o ch&iacute; Việt Nam cũng nhắc nhỏm tới. Sở Li&ecirc;m Ph&oacute;ng Securit&eacute; đ&atilde; cắt hai vi&ecirc;n th&aacute;m tử đi d&ograve; &ocirc;ng. Sau ba th&aacute;ng r&ograve;ng r&atilde; hai vi&ecirc;n th&aacute;m tử chỉ thấy những h&agrave;nh tung b&iacute; mật của &ocirc;ng l&agrave;: h&uacute;t thuốc l&aacute; Camel. Về sau &ocirc;ng lấy vợ gi&agrave;u. C&oacute; vợ rồi, &ocirc;ng đặt l&agrave; Văn Minh. Sở Li&ecirc;n Ph&oacute;ng Securit&eacute; lại phải một phen lo sợ. D&ograve; m&atilde;i mới biết rằng t&ecirc;n của vợ &ocirc;ng l&agrave; Văn, của &ocirc;ng l&agrave; Minh th&igrave; &ocirc;ng đặt ngay l&agrave; Văn Minh, t&ecirc;n vợ ở tr&ecirc;n t&ecirc;n &ocirc;ng, t&ecirc;n &ocirc;ng đội dưới, cho n&oacute; c&oacute; vẻ nịnh đầm. Thế th&ocirc;i, chứ &ocirc;ng chẳng phản đối, cũng chẳng cải c&aacute;ch, quốc gia, quốc tế chi chi, cũng kh&ocirc;ng cả.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Từ độ được nhiều người gọi l&agrave; Văn Minh, &ocirc;ng ta thấy cần phải chủ trương cuộc &Acirc;u ho&aacute; th&igrave; c&aacute;i t&ecirc;n mới khỏi v&ocirc; nghĩa. Một c&aacute;i linh hồn khoẻ trong một x&aacute;c thịt kho&egrave;! Ph&aacute;t minh được ch&acirc;n l&yacute; ấy rồi, đi đ&acirc;u &ocirc;ng cũng hăng h&aacute;i cổ động cho thể thao. Vợ &ocirc;ng trước nhất, rồi đến người kh&aacute;c. &Ocirc;ng kh&ocirc;ng thể thao, thể dục cũng kh&ocirc;ng, v&igrave; kh&ocirc;ng c&oacute; th&igrave; giờ! C&aacute;i chương tr&igrave;nh &Acirc;u ho&aacute; của &ocirc;ng ta l&agrave;m cho &ocirc;ng ta l&uacute;c n&agrave;o cũng phải trầm tư mặc tưởng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">C&ograve;n lai lịch b&agrave; Ph&oacute; Đoan, th&igrave; kể nghe cũng hay hay. Hồi đương xu&acirc;n, b&agrave; bị một người l&iacute;nh T&acirc;y hiếp, l&uacute;c b&agrave; mới ở nh&agrave; qu&ecirc; ra tỉnh xem hội Đ&igrave;nh Chiến. Sau cuộc hiếp tr&aacute;p ph&eacute;p đến ngay cuộc hiếp đ&uacute;ng luật, nghĩa l&agrave; cuộc l&agrave;m ph&eacute;p cưới. Người l&iacute;nh ấy sau th&agrave;nh một &ocirc;ng Ph&oacute; Đoan. Ăn ở với nhau độ 10 năm, &ocirc;ng Ph&oacute; Đoan chết, chết trung th&agrave;nh với nh&agrave; nước, chết chung t&igrave;nh với vợ, chết như những người y&ecirc;u vợ qu&aacute; sức. Rồi b&agrave; lấy một &ocirc;ng ph&aacute;n trẻ được hai năm th&igrave; &ocirc;ng chồng nội ho&aacute; cũng lăn cổ ra chết. V&igrave; lẽ chưa ai thấy b&agrave; c&oacute; nh&acirc;n t&igrave;nh, n&ecirc;n những ngọn lưỡi rắn độc phao rằng những ngọn lửa t&igrave;nh do những kẻ chim b&agrave; kh&ocirc;ng được đ&atilde; kh&ecirc;u l&ecirc;n, b&agrave; bắt &ocirc;ng ph&aacute;n phải rập tất cả. B&agrave; ch&iacute;nh chuy&ecirc;n đến nỗi chồng b&agrave; kiệt sức, cạn sức, phải trốn xuống suối v&agrave;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Hai thiếu nữ Ph&aacute;p v&agrave; một thiếu ni&ecirc;n Nam v&agrave;o s&acirc;n.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Xu&acirc;n T&oacute;c Đỏ nhường vợt cho đ&aacute;m hội vi&ecirc;n.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Một c&ocirc; đầm đi v&agrave;o buồng thay quần &aacute;o th&igrave; Xu&acirc;n cũng biến mất.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">S&acirc;n quần ầm ĩ những tiếng ch&agrave;o hỏi cười n&oacute;i. Rồi những quả quần bay đi bay lại như đ&agrave;n dơi bắt muỗi tr&ecirc;n kh&ocirc;ng gian...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Một l&aacute;t sau, tự nhi&ecirc;n thấy một người Ph&aacute;p nắm t&oacute;c Xu&acirc;n, l&ocirc;i n&oacute; sềnh sệch ra s&acirc;n m&agrave; t&aacute;t, m&agrave; sỉ vả... Mọi người x&uacute;m lại hỏi. Th&igrave; ra n&oacute; bị bắt quả tang nh&igrave;n trộm một c&ocirc; đầm l&uacute;c c&ocirc; đang thay v&aacute;y để mặc quần đ&ugrave;i! Hội nhất định đuổi n&oacute;, v&agrave; kh&ocirc;ng trả lương th&aacute;ng ấy nữa.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">L&uacute;c đ&oacute; b&agrave; Ph&oacute; Đoan mới nhận ra rằng d&acirc;n Việt Nam kh&ocirc;ng những ngu m&agrave; c&ograve;n đ&aacute;ng thương. B&agrave; thở d&agrave;i, ngậm ng&ugrave;i bảo ch&aacute;u rể b&agrave;:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Trẻ trung ai chả c&oacute; khi dại dột? Tha thứ l&agrave; phải, chấp l&agrave;m g&igrave; thiếu ni&ecirc;n? R&otilde; khổ, r&otilde; tội nghiệp! Đuổi người ta như thế m&agrave; đ&agrave;nh l&ograve;ng!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#e74c3c\"><strong><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">CHƯƠNG II:&nbsp; QUAN PH&Ugrave; V&Agrave; TH&Aacute;I TUẾ&nbsp;THAN &Ocirc;I D&Acirc;N TA! VĂN MINH, HẠI CHƯA!&nbsp;CẨM V&Agrave; CẨM, CẨM PHẠT</span></span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Sau khi đi qua một c&aacute;i s&acirc;n vắng ngắt, người l&iacute;nh cảnh s&aacute;t dẫn Xu&acirc;n T&oacute;c Đỏ v&agrave; &ocirc;ng l&atilde;o thầy số đến một c&aacute;i buồng nhỏ vặn một v&ograve;ng kh&oacute;a, rồi mỉa mai ngọt ng&agrave;o bảo:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Mời hai ng&agrave;i v&agrave;o !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Trong ph&ograve;ng giam l&uacute;c ấy c&oacute; một l&atilde;o ăn m&agrave;y v&agrave; vợ con hắn, một thằng ma c&agrave; b&ocirc;ng v&agrave; một người đ&agrave;n b&agrave; b&aacute;n h&agrave;ng rong c&oacute; một g&aacute;nh b&uacute;n chả ế. Người n&agrave;y ngồi dựa tường, hai tay b&oacute; gối, giữa hai th&uacute;ng qu&agrave;, vẻ mặt đa tư lự, trong khi thằng ma c&agrave; b&ocirc;ng nằm d&agrave;i dưới gạch ng&aacute;y như k&eacute;o gỗ, v&agrave; ba người của c&aacute;i gia đ&igrave;nh h&agrave;nh khất th&igrave; bắt rận cho nhau một c&aacute;ch n&ecirc;n thơ. C&aacute;nh cửa bị đẩy đ&aacute;nh sập một c&aacute;i, tiếng ổ kh&oacute;a bị xoay l&aacute;ch t&aacute;ch, tiếng giầy vi&ecirc;n đội sếp mỗi l&uacute;c một xa... Một ngọn đ&egrave;n đỏ v&igrave; yếu, chiếu một thứ s&aacute;ng tối tăm cho gian ph&ograve;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">L&atilde;o thầy số để ch&aacute;p, chiếu, &ocirc; xuống đất rồi ngồi l&ecirc;n ch&aacute;p để thở hổn hển.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Xu&acirc;n T&oacute;c Đỏ th&igrave; c&ograve;n đứng lại, hai tay g&atilde;i sườn nh&igrave;n mọi người một c&aacute;ch trịnh thượng, nh&igrave;n ph&ograve;ng giam một c&aacute;ch thản nhi&ecirc;n. N&oacute; bĩu mồm n&oacute;i:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Nước mẹ g&igrave; ! B&oacute;p với chả b&oacute;p ! Ph&ograve;ng giam th&igrave; bằng c&aacute;i lỗ mũi ! R&otilde; chả biết xấu !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">L&atilde;o thầy số giương to hai con mắt:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- C&oacute; xấu c&aacute;i đếch &ocirc;ng đ&acirc;y n&agrave;y !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Xu&acirc;n T&oacute;c Đỏ ph&acirc;n vua ngay với mọi người:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- &Ecirc; ! &ecirc; ! R&otilde; thối chửa ! Người ta bảo m&igrave;nh đ&acirc;u n&agrave;o ! Người ta bảo nh&agrave; nước chứ !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">N&oacute; n&oacute;i vậy l&agrave; c&oacute; &yacute; khinh bỉ c&aacute;i nh&agrave; giam. Ty cảnh s&aacute;t n&agrave;y l&agrave; một ty s&eacute;p thuộc bộ thứ 18 của th&agrave;nh phố m&agrave; nh&agrave; nước mới đặt th&ecirc;m ra &iacute;t l&acirc;u nay th&ocirc;i. Tất cả nh&acirc;n vi&ecirc;n trong sở cẩm chỉ c&oacute; bẩy người: một &ocirc;ng Cẩm T&acirc;y, một &ocirc;ng th&ocirc;ng ng&ocirc;n ta, một &ocirc;ng quản cảnh s&aacute;t, v&agrave; bốn người l&iacute;nh. Khu vực m&agrave; sở cẩm c&oacute; nhiệm vụ tr&ocirc;ng coi gồm c&oacute; 16 phố, to&agrave;n l&agrave; phố T&acirc;y, c&oacute; phố d&agrave;i h&agrave;ng năm c&acirc;y số, phố n&agrave;o cũng c&oacute; vẻ th&aacute;i b&igrave;nh. Th&agrave;nh thử mỗi khi bắt phạt được một đ&aacute;m th&igrave; nh&acirc;n vi&ecirc;n sở cẩm sướng như tr&uacute;ng số độc đắc. Bốn thầy l&iacute;nh cảnh s&aacute;t phải thay tua nhau đạp xe khắp cả 16 phố như thế mới trong trong nửa năm đ&atilde; trở n&ecirc;n bốn cua rơ đại t&agrave;i. C&oacute; thầy đ&atilde; giật giải H&agrave; Nội - Hải Ph&ograve;ng, c&oacute; thầy được giải ba giải tư trong những cuộc đua H&agrave; Nội - Sơn T&acirc;y, H&agrave; Nội - Bắc Ninh, v&ograve;ng quanh H&agrave; Nội v.v... Trong 16 phố ấy chẳng may c&oacute; khi xảy ra một sự g&igrave; phạm v&agrave;o trật tự th&igrave; phần nhiều lại kh&ocirc;ng thấy b&oacute;ng v&iacute;a c&aacute;c thầy cảnh s&aacute;t đ&acirc;u cả ! H&agrave;ng rong, bồi, bếp, phu xe, ăn m&agrave;y, những người ấy chỉ sau khi thầy cảnh s&aacute;t đ&atilde; cắm đầu đạp xe khỏi phố th&igrave; mới đ&aacute;i đường, th&igrave; mới đ&aacute;nh nhau, chửi nhau... V&igrave; lẽ tất cả c&oacute; bốn thầy l&iacute;nh th&ocirc;i, n&ecirc;n l&uacute;c n&agrave;o ở sở cẩm cũng phải c&oacute; hai thầy, chỉ c&ograve;n lại hai thầy đi tuần ngo&agrave;i đường. Mười s&aacute;u phố cho hai người, việc tuần ph&ograve;ng th&agrave;nh ra gần như l&agrave; việc tập đua xe đạp!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Năm ấy v&igrave; kinh tế khủng hoảng, ng&acirc;n s&aacute;ch hao hụi, Đ&ocirc;ng Dương đại hội kinh tế v&agrave; t&agrave;i ch&iacute;nh chuẩn y bản dự luật buộc sở cảnh s&aacute;t phạt d&acirc;n th&agrave;nh phố 4 vạn đồng. Sở cảnh s&aacute;t trung ương chi cho Ty cảnh s&aacute;t chi nh&aacute;nh n&agrave;y phải phạt d&acirc;n l&agrave; 5 ngh&igrave;n, nghĩa l&agrave; một phần t&aacute;m số tiền tổng cộng vậy.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&Ocirc;ng Cẩm đ&atilde; v&ograve; b&ugrave; cả đầu, đ&atilde; rứt soăn cả r&acirc;u. An Nam năm nay bị cẩm phạt phần nhiều chỉ một &iacute;t bồi, bếp, phu xe, h&agrave;ng rong, c&ograve;n th&igrave; to&agrave;n người Ph&aacute;p cả, l&agrave;m thế n&agrave;o phạt cho đủ 5 ngh&igrave;n bạc ? &Ocirc;ng Cẩm, sau c&ugrave;ng, gọi nh&acirc;n vi&ecirc;n trong sở họp một phi&ecirc;n b&iacute; mật. Sau cuộc hội họp, ai nấy rất k&iacute;nh phục c&aacute;i phương kế nhiệm mầu. Người bắt đầu cho gia đ&igrave;nh dọn cả về khu 16 phố ấy. Thế rồi th&igrave;... trước nhất, ch&iacute;nh &ocirc;ng Cẩm T&acirc;y bị phạt về tội để ch&oacute; sổng ra đường, hay l&agrave; v&igrave; b&agrave; Cẩm qu&ecirc;n bảo bồi qu&eacute;t nh&agrave; cho đ&uacute;ng ph&eacute;p vệ sinh. Lần lượt đến người nh&agrave; &ocirc;ng Th&ocirc;ng ng&ocirc;n, &ocirc;ng quản, bốn thầy l&iacute;nh, người loong toong, người phu lục lộ tr&ocirc;ng coi c&aacute;i vườn hoa của sở cẩm. N&agrave;o l&agrave; tội đ&aacute;i đường, tội c&atilde;i nhau, tội đi xe đạp kh&ocirc;ng đ&egrave;n, tội để nh&agrave; cửa mất vệ sinh, v&acirc;n v&acirc;n... Th&agrave;nh thử nh&acirc;n vi&ecirc;n sở cẩm cứ phạt lẫn nhau văng t&ecirc; đi th&ocirc;i, phạt nhau như h&igrave;nh c&oacute; th&acirc;m th&ugrave; với nhau vậy.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Bữa ấy, &ocirc;ng Cẩm T&acirc;y đang ngồi đ&aacute;nh m&aacute;y chữ về một tờ bi&ecirc;n bản quan trọng th&igrave; c&oacute; một người l&iacute;nh cảnh s&aacute;t hấp tấp chạy v&agrave;o b&aacute;o một vụ trộm ở nh&agrave; một người T&acirc;y. Vụ trộm xẩy ra từ đ&ecirc;m trước, xong đến l&uacute;c ấy sự chủ mới biết. &Ocirc;ng Cẩm T&acirc;y ch&aacute;n nản n&oacute;i bằng tiếng Ph&aacute;p rằng:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Trộm th&igrave; lại phải giao ra to&agrave;, c&ograve;n phạt g&igrave; nữa ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Rồi &ocirc;ng quay v&agrave;o bảo vi&ecirc;n quản thay quyền &ocirc;ng trong l&uacute;c &ocirc;ng ra đi với người th&ocirc;ng ng&ocirc;n...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ngồi lại một m&igrave;nh ở ph&ograve;ng giấy, vi&ecirc;n quản ng&aacute;p như một nh&agrave; bu&ocirc;n gặp hồi kinh tế khủng hoảng. Một người đi qua, &ocirc;ng gọi lại ch&aacute;n nản m&agrave; rằng:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- N&agrave;y, thầy min đơ&nbsp;thầy c&oacute; buồn kh&ocirc;ng !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thầy n&agrave;y gật g&ugrave; như một nh&agrave; nho say rượu ch&aacute;n đời:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Buồn lắm ạ ! Buồn lắm, chỉ muốn chết qu&aacute;ch !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&Ocirc;ng quản than thở một c&aacute;ch rền rĩ:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Ch&uacute;ng ta bị phạt nhiều qu&aacute;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thầy min đơ nhắc lại, &acirc;u sầu:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thật vậy, ch&uacute;ng ta bị nhiều qu&aacute;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- C&aacute;i sổ dự to&aacute;n của c&ocirc;ng quỹ cần tiền...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- M&agrave; mười s&aacute;u phố của ta cần nhiều d&acirc;n An Nam ta.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thầy c&oacute; tiếc c&aacute;i thời buổi ng&agrave;y xưa, c&aacute;ch đ&acirc;y mười năm kh&ocirc;ng?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Tiếc lắm ! Mười năm trước đ&acirc;y, d&acirc;n ta c&ograve;n ngu.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Ng&agrave;y nay d&acirc;n ta văn minh mất rồi, r&otilde; thảm hại ! Thầy phải biết l&agrave; xưa kia, x&atilde; hội tinh những du c&ocirc;n v&agrave; nặc n&ocirc;, tinh những người bất lịch sự, chỗ n&agrave;o cũng ph&oacute;ng uế, cũng đ&aacute;nh nhau. Hồi ấy c&oacute; khi bốn người ngồi c&ugrave;ng một xe ! Họ chửi nhau h&agrave;ng nửa giờ, đ&aacute;nh nhau vỡ đầu, nh&agrave; cửa của họ th&igrave; r&aacute;c rưởi, nước cống, nước r&atilde;nh tung to&eacute;, ngập lụt... Ch&oacute; của họ cũng chạy ra ngo&agrave;i đường nh&ocirc;ng nh&ocirc;ng... Xe đi đ&egrave;o, hay kh&ocirc;ng đ&egrave;n l&agrave; nhan nhản. B&acirc;y giờ mọi sự đ&atilde; thay đổi cả. C&aacute;i thời tốt đẹp của c&aacute;c cụ nh&agrave; ta kh&ocirc;ng c&ograve;n nữa ! Thật l&agrave; tai hại ! Than &ocirc;i !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Cụ t&iacute;nh ! B&acirc;y giờ đến cả thằng phu xe cũng biết luật ! Chả bao giờ ch&uacute;ng qu&ecirc;n đ&egrave;n ! Chả mấy khi ch&uacute;ng đứng giữa đường ngh&ecirc;nh ngang ! Chả c&ograve;n mấy người r&eacute;o năm đời mười đời nhau ra như ng&agrave;y xưa nữa ! Bao nhi&ecirc;u nền nếp của x&atilde; hội n&agrave;y thế l&agrave; hết nhẵn nhụi ! Ngay cả đến trẻ con ch&uacute;ng n&oacute; cũng kh&ocirc;ng bậy bạ như xưa ! Đứa n&agrave;o cũng quần &aacute;o bảnh bao đứng đắn lắm, văn minh lắm, tr&egrave;o me tr&egrave;o sấu, đ&aacute; b&oacute;ng giữa đường, những c&aacute;i ấy l&agrave; th&ocirc;i cả.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Sinh ra ngay c&aacute;i b&aacute;o ch&iacute;, thật l&agrave; nhảm qu&aacute;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Ch&iacute;nh vậy. D&acirc;n ch&uacute;ng chỉ v&igrave; b&aacute;o ch&iacute; m&agrave; đ&acirc;m ra văn minh, kh&ocirc;ng c&ograve;n cẩm phạt nhiều như trước nữa.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- T&eacute; ra ch&uacute;ng ta, ch&uacute;ng ta lại th&agrave;nh ra cẩm phạt !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- &Ocirc; ! &ocirc; ! Thế th&igrave; thật l&agrave; một sự tr&aacute;i ngược kh&ocirc;ng thể tha thứ được ! Ch&uacute;ng ta l&agrave;... ch&uacute;ng ta l&agrave; l&iacute;nh cảnh s&aacute;t !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- T&ocirc;i, t&ocirc;i l&agrave; một nh&acirc;n vi&ecirc;n quản nữa kia thưa thầy ! Sapristi! Thật thế, nếu thỉnh thoảng ta kh&ocirc;ng dự v&agrave;o một cuộc đua xe đạp th&igrave; c&oacute; thể n&oacute;i l&agrave; cuộc đời như thế n&agrave;y l&agrave; từ từ hạ m&agrave;n mất rồi !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- M&agrave; b&aacute;o ch&iacute; thỉnh thoảng kh&ocirc;ng đăng t&ecirc;n, đăng ảnh ch&uacute;ng ta l&ecirc;n trang nhất th&igrave; thật l&agrave; kh&ocirc;ng thể n&agrave;o tha thứ được cho b&aacute;o ch&iacute; !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thế n&agrave;o ? Chủ nhật sau, thầy c&oacute; dự cuộc đua H&agrave; Nội - H&agrave; Đ&ocirc;ng kh&ocirc;ng?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- C&oacute; chớ ! Cụ t&iacute;nh mỗi ng&agrave;y bốn lượt đạp 16 phố m&agrave; cấm gặp sự g&igrave; đ&aacute;ng bi&ecirc;n phạt để m&agrave; nghỉ ch&acirc;n, như thế qu&aacute; tập tr&ecirc;n v&ograve;ng đua rồi c&ograve;n g&igrave; ! Chả dự cuộc cũng uổng !... Nhưng m&agrave;, cụ quản ạ, nếu d&acirc;n ta tiến bộ đ&oacute; kh&ocirc;ng phải l&agrave; một lẽ để ch&uacute;ng ta cứ nh&egrave; người nh&agrave; của nhau m&agrave; bi&ecirc;n phạt lẫn nhau !... C&aacute;i khoản 5 ngh&igrave;n đồng trong sổ dự to&aacute;n l&agrave; v&ocirc; nghĩa l&yacute;... t&ocirc;i... t&ocirc;i phản đối !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Vi&ecirc;n quản sợ h&atilde;i đứng l&ecirc;n, xua tay:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Chết ! chết ! Kh&eacute;o kh&ocirc;ng m&agrave; thầy lại bất tu&acirc;n thượng lệnh b&acirc;y giờ ! Thầy thử hỏi ngay b&agrave; đầm vợ &ocirc;ng Cẩm xem ! B&agrave; vui vẻ k&ecirc;u th&aacute;ng trước bị ng&oacute;t hai chục bạc, kh&ocirc;ng ph&agrave;n n&agrave;n g&igrave; cả.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thầy l&iacute;nh vẫn hậm hực:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Th&ocirc;i, thế th&igrave; t&ocirc;i chỉ c&ograve;n c&aacute;ch li dị vợ t&ocirc;i m&agrave; th&ocirc;i !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Chết nỗi ! Tại sao thế ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- T&ocirc;i đ&atilde; dặn n&oacute; thỉnh thoảng phải bảo trẻ nhỏ ra n&eacute;m sấu ngo&agrave;i phố, kh&ocirc;ng th&igrave; để nh&agrave; cửa cho r&otilde; bẩn thỉu, cống r&atilde;nh cho n&oacute; ngập lụt, cho thầy mintoa&nbsp;thỉnh thoảng bi&ecirc;n phạt, th&igrave; t&ocirc;i mới c&oacute; dịp phạt lại vợ thầy ấy, thế m&agrave; n&oacute; cứ để con t&ocirc;i ngoan như bụt, nh&agrave; cửa sạch như lau, như ch&ugrave;i ! Con khốn nạn, con &aacute;c phụ !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Trước một cảnh ngộ kh&oacute; xử, vi&ecirc;n quản kh&ocirc;ng d&aacute;m ph&ecirc; b&igrave;nh nữa. Muốn lảng chuyện, &ocirc;ng ta bảo người l&iacute;nh xuống nh&agrave; giam tạm, l&ocirc;i bọn người bị bắt l&ecirc;n lấy cung.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thầy minđơ mở cửa ph&ograve;ng giam giữa l&uacute;c Xu&acirc;n T&oacute;c Đỏ khẩu chiến với &ocirc;ng thầy số.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Ừ ! Gai ngạnh lắm th&igrave; chết ! Cho m&agrave; bị bắt ! T&ocirc;i chả cần, v&igrave; số t&ocirc;i th&aacute;ng n&agrave;y Quan Ph&ugrave; Th&aacute;i Tuế long đong, th&aacute;ng ng&agrave;y chờ đợi cửa c&ocirc;ng mỏi m&ograve;n ! Bị bắt thế n&agrave;y l&agrave; nhẹ, rồi cũng qua m&agrave; th&ocirc;i !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Đ&acirc;y kh&ocirc;ng cần ! Kh&ocirc;ng phải n&oacute;i ph&eacute;t, chứ từ thuở trời đất sinh ra l&agrave;m người, đ&acirc;y bị bắt về b&oacute;p &iacute;t ra cũng đ&atilde; l&agrave; bận thứ mười lăm.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Đ&aacute;nh người gi&agrave; cả l&agrave; h&agrave;nh hung, rồi th&igrave; t&ugrave; !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Kh&ocirc;ng nghe, Xu&acirc;n T&oacute;c Đỏ cứ n&oacute;i liến thoắng:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- M&agrave; trước kia bị bắt về b&oacute;p ch&iacute;nh cơ ! Một sở cẩm to, oai, tr&ocirc;ng rợn t&oacute;c g&aacute;y, c&oacute; bảy t&aacute;m &ocirc;ng Cẩm ria m&eacute;p to tướng, ngực đặc mền đay, &ocirc;ng n&agrave;o cũng đeo s&uacute;ng lục ! Lại c&oacute; h&agrave;ng trăm đội xếp d&ugrave;i khui sơn trắng, cầm những x&iacute;ch to tướng, m&agrave; dề b&oacute;&nbsp;th&igrave; cửa gi&oacute;ng sắt như chuồng hổ, tinh những muỗi với rệp, giam được h&agrave;ng v&agrave;i trăm người ! Chứ bị bắt v&agrave;o c&aacute;i b&oacute;p nhỏ n&agrave;y, ph&ograve;ng giam như c&aacute;i lỗ mũi giam được c&oacute; v&agrave;i người thế n&agrave;y th&igrave;... nước mẹ g&igrave; !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Người l&iacute;nh qu&aacute;t:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Ra cả ! Ra cả ! Ra lấy cung mau ! Im mồm, c&atilde;i nhau m&atilde;i g&igrave; !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Trừ thằng ma c&agrave; b&ocirc;ng vẫn nằm ng&aacute;y o o như sấm, cả bọn đứng l&ecirc;n cả. Người l&iacute;nh phải lấy ch&acirc;n lay n&oacute; th&igrave; n&oacute; n&oacute;i lảm nhảm:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Im để người ta ngủ n&agrave;o !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- C&oacute; dậy kh&ocirc;ng ? &Ocirc;ng lại x&aacute;ch cổ l&ecirc;n b&acirc;y giờ !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thằng ma c&agrave; b&ocirc;ng ngồi l&ecirc;n ngơ ng&aacute;c:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Dạ ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Đi ra !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">N&oacute; đứng l&ecirc;n lầu nhầu:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Đương ngủ ngo&agrave;i đường th&igrave; bắt v&agrave;o b&oacute;p, đương ngủ ở b&oacute;p th&igrave; gọi dậy bảo đi ra ! R&otilde; lắm chuyện.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Bọn người ấy ra khỏi ph&ograve;ng giam, qua s&acirc;n bước b&agrave;o ph&ograve;ng giấy. Vi&ecirc;n quản thấy Xu&acirc;n T&oacute;c Đỏ ăn mặc hơn cả (c&aacute;i quần T&acirc;y, đ&ocirc;i giầy cao su, c&aacute;i &aacute;o l&oacute;t cụt tay) th&igrave; hỏi n&oacute; trước ti&ecirc;n:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Anh n&agrave;y ! Tội g&igrave; ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&Ocirc;ng thầy số n&oacute; ngay:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm quan lớn, n&oacute; đ&aacute;nh con.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Xu&acirc;n T&oacute;c Đỏ c&atilde;i:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Kh&ocirc;ng phải đ&aacute;nh, t&ocirc;i chỉ định b&oacute;p cổ l&atilde;o !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- N&oacute; thụi con hai c&aacute;i đau qu&aacute; rồi mới b&oacute;p cổ...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Con chưa thụi, m&agrave; cũng chưa kịp b&oacute;p cổ th&igrave; l&atilde;o đ&atilde; k&ecirc;u nhặng l&ecirc;n !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Vi&ecirc;n quản đập b&agrave;n:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Im ! Im ngay ! Để bản chức hỏi đ&atilde;. Ai phải, ai tr&aacute;i ? Đầu đu&ocirc;i ra sao? Anh n&agrave;y, đ&aacute;nh người v&igrave; lẽ g&igrave; ? Khai ra ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm l&atilde;o ăn kh&ocirc;ng ăn hỏng, đ&aacute;nh lừa của con một h&agrave;o. L&atilde;o đo&aacute;n số sai cả, đ&ograve;i tiền lại kh&ocirc;ng trả ! Con muốn đ&egrave; l&atilde;o để lấy lại chứ kh&ocirc;ng c&oacute; &yacute; đ&aacute;nh.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- C&oacute; xem số kh&ocirc;ng ? C&oacute; nhận một h&agrave;o kh&ocirc;ng ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm quan lớn, l&aacute; số con đ&atilde; lấy rẻ c&oacute; một h&agrave;o, đo&aacute;n c&acirc;u n&agrave;o cũng tr&uacute;ng cả m&agrave; n&oacute; c&ograve;n muốn đ&ograve;i tiền lại.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Sai b&eacute;t cả, bẩm quan lớn ! L&atilde;o k&ecirc;u tương lai con kh&aacute; lắm, thế m&agrave; vừa đ&oacute;an xong th&igrave; con mất việc.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Vi&ecirc;n quản lừ mắt nh&igrave;n &ocirc;ng thầy số:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Đo&aacute;n thế m&agrave; đ&ograve;i lấy tiền !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm tương lai l&agrave; mai sau, chứ c&oacute; phải ngay l&uacute;c ấy đ&acirc;u ? Con nghi&ecirc;n cứu mười năm l&yacute; số, đo&aacute;n như Th&aacute;nh như Thần, c&oacute; khi n&agrave;o sai ! M&agrave; con được xem hầu quan lớn bữa n&agrave;o đ&acirc;u m&agrave; quan lớn đ&atilde; quở con đo&aacute;n sai !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Vi&ecirc;n quản lừ mắt nh&igrave;n Xu&acirc;n T&oacute;c Đỏ:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Người ta n&oacute;i phải đấy chứ ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thầy số n&oacute;i lu&ocirc;n:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Như tướng quan lớn đủ biết ! Cung quan lộc tốt, h&eacute;t ra lửa, mi trường qu&aacute; mục, nhiều anh em, lại c&oacute; thuỳ ch&acirc;u, hậu vận sẽ giầu lớn !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Vi&ecirc;n quản lườm Xu&acirc;n T&oacute;c Đỏ lần nữa:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Người ta đo&aacute;n hay đấy chứ ? A l&ecirc; ! Phạt anh ! Anh can tội đ&aacute;nh người, m&agrave; lại người gi&agrave; cả ! Phạt một đồng t&aacute;m ! Tha ngay cho &ocirc;ng thầy số n&agrave;y ra ! C&ograve;n người kia đưa thẻ xem.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ngo&agrave;i đường c&oacute; tiếng xe hơi đỗ. B&agrave; Ph&oacute; Đoan bước v&agrave;o, tủm tỉm ch&agrave;o hai người thay mặt nh&agrave; nước. Cả hai người n&agrave;y vồn v&atilde; như nh&agrave; bu&ocirc;n tiếp kh&aacute;ch sộp. L&agrave; v&igrave; b&agrave; Ph&oacute; Đoan hay để ch&oacute; sổng ra ngo&agrave;i đường nhi&ecirc;u hay bị phạt, kể trong cả 16 phố. Th&agrave;nh thử sở cẩm nhớ ơn b&agrave; chẳng k&eacute;m một hiệu bu&ocirc;n ế ẩm với một b&agrave; kh&aacute;ch quen. Vi&ecirc;n quản hỏi:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- B&agrave; muốn g&igrave;, ch&uacute;ng t&ocirc;i sẵn l&ograve;ng...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- &Agrave;, t&ocirc;i đến nộp phạt cho một người nh&agrave;. N&oacute; đ&acirc;y rồi, xin cụ tha cho.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Vi&ecirc;n quản xoa tay. Thầy l&iacute;nh minđơ n&oacute;i:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- V&acirc;ng, b&agrave; nộp tiền ngay cho.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bao nhi&ecirc;u thế ạ ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Một đồng t&aacute;m.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thầy minđơ v&agrave;o ngồi b&agrave;n giấy viết bi&ecirc;n lai. Xu&acirc;n T&oacute;c Đỏ ngẩn người ra, kh&ocirc;ng hiểu... N&oacute; k&iacute;nh cẩn hỏi:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm b&agrave; lớn, sao b&agrave; lớn lại thương con như thế ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- &Agrave;, rồi biết ! Ch&uacute;ng t&ocirc;i cần d&ugrave;ng đến anh. Anh cứ về nh&agrave; rồi sẽ hiểu. Anh c&oacute; việc l&agrave;m rồi !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">L&atilde;o thầy số ph&acirc;n bua:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Đấy nh&eacute; ? Bảo sai nữa đi !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Xu&acirc;n T&oacute;c Đỏ quay lại:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Đ&uacute;ng thật ! Cụ l&agrave; th&aacute;nh sống ! Con xin lỗi cụ vậy !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">B&agrave; Ph&oacute; Đoan hỏi:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- C&aacute;i g&igrave; ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm b&agrave;, thầy số hay lắm !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thế &agrave;? Thế th&igrave; cũng l&ecirc;n xe về xem cho t&ocirc;i, đi !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Giấy bi&ecirc;n lai đ&atilde; xong. B&agrave; Ph&oacute; Đoan trả tiền. &Ocirc;ng thầy quay v&agrave;o ph&ograve;ng giam lấy kh&iacute; cụ rồi ra, theo Xu&acirc;n v&agrave; b&agrave; Ph&oacute; l&ecirc;n xe. Vi&ecirc;n quản tiễn ra tận cổng n&oacute;i:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Xin cảm tạ ! Lần sau xin qu&yacute; kh&aacute;ch chiếu cố.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">N&oacute;i xong &ocirc;ng ta mới tưng hửng chợt nhớ ra đ&acirc;y l&agrave; sở cẩm, chứ kh&ocirc;ng phải l&agrave; hiệu b&aacute;nh T&acirc;y của b&agrave; quảnh ở nh&agrave;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#e74c3c\"><strong><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">CHƯƠNG III: CON GIỜI, CON PHẬT - QUỶ CỐC TỬ PHỤC SINH - MỘT C&Aacute;I GHI &Acirc;N</span></span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Sau mấy tiếng c&ograve;i un un dữ dội nghe như tiếng gầm của một thứ lợn rừng kỳ qu&aacute;i, chiếc xe chờ ba ph&uacute;t th&igrave; c&oacute; một t&ecirc;n gia nh&acirc;n chạy ra mở toang hai c&aacute;nh cửa sắt, đ&oacute;n xe từ từ v&agrave;o s&acirc;n. Những c&acirc;y liễu, c&acirc;y ph&ugrave; dung, c&acirc;y xương rồng l&agrave;o, cỏ t&oacute;c ti&ecirc;n, những đ&ocirc;n sứ, những luống hoa lạ, hiện mập mờ dưới &aacute;nh đ&egrave;n điện ở ngo&agrave;i phố chiếu v&agrave;o vườn của c&aacute;i nh&agrave; t&acirc;y đồ sộ kiểu biệt thự... ngần ấy thứ k&iacute;ch th&iacute;ch rất mạnh v&agrave;o t&acirc;m tr&iacute; của Xu&acirc;n. Lần đầu ! N&oacute; cảm thấy đời n&oacute; từ nay m&agrave; đi dễ thường đ&atilde; v&agrave;o một kỷ nguy&ecirc;n mới. C&ograve;n &ocirc;ng l&atilde;o thầy số th&igrave; cứ thỉnh thoảng lại lườn n&oacute; một c&aacute;ch đắc ch&iacute; v&agrave; trợ lực cho sự lườm ngu&yacute;t của &ocirc;ng bằng c&aacute;ch lấy khuỷu tay th&iacute;ch v&agrave;o mạng mỡ Xu&acirc;n T&oacute;c Đỏ đ&aacute;nh hự một c&aacute;i. Ngồi cạnh người t&agrave;i xế trầm ng&acirc;m, Xu&acirc;n kh&ocirc;ng d&aacute;m phản đối lại bằng c&aacute;ch g&igrave;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Xe đỗ trước mười hai thềm xi măng. Người t&agrave;i xế xuống mở cửa xe. B&agrave; Ph&oacute; Đoan d&ugrave; Nhật, v&iacute; da, v&agrave; ch&oacute; bước xuống. &Ocirc;ng l&atilde;o thầy số cũng &ocirc;m lấy ch&aacute;p, &ocirc; v&agrave; chiếu... Xu&acirc;n xuống sau c&ugrave;ng... th&igrave; chiếc xe quay v&agrave;o nh&agrave; chứa xe. Một người đ&agrave;n b&agrave; m&agrave; y phục tỏ ra l&agrave; gia nh&acirc;n vội chạy xuống đỡ những c&aacute;i đồ cồng kềnh cho b&agrave; chủ. B&agrave; n&agrave;y hỏi:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Cậu đ&acirc;u ? Cậu l&agrave;m g&igrave; ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm... cậu tắm.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Cậu tắm &agrave;? Thế cậu xơi cơm chưa?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Rồi kh&ocirc;ng đợi trả lời b&agrave; quay lại sau, bảo:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Chị Ba ! Sao chị lại để cậu ấy tắm ở đ&acirc;y thế n&agrave;y? Ai lại sềnh sềnh ra thế !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm... cậu v&ograve;i thế, kh&ocirc;ng chiều th&igrave; cậu lại kh&oacute;c !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Trong c&aacute;i chậu thau khổng lồ, một cậu b&eacute; to tướng b&eacute;o mũm mĩm, mặt tr&ocirc;ng ngẩn ngơ, gi&aacute; đứng l&ecirc;n th&igrave; &iacute;t ra cũng cao lớn hơn một thước t&acirc;y, ngồi vầy nước như một đứa trẻ l&ecirc;n ba. Chung quanh c&aacute;i chậu thau c&oacute; v&ocirc; số đồ chơi bầy la liệt... N&agrave;o l&agrave; con ch&oacute; b&ocirc;ng, con b&uacute;p b&ecirc;, c&aacute;i &ocirc; t&ocirc;, c&aacute;i tầu bay, c&aacute;i k&egrave;n... B&agrave; chủ vừa đặt con ch&oacute; xuống vừa nhanh nhẩu n&oacute;i:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- &Agrave; cậu tắm ! Cậu của me ngoan. Me đi vắng, ở nh&agrave; c&oacute; đứa n&agrave;o đ&aacute;nh cậu kh&ocirc;ng ? Loulou Hu&yacute;t! Hu&yacute;t...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">B&agrave; Ph&oacute; vừa hu&yacute;t hai tiếng c&ograve;i th&igrave; con ch&oacute; đ&atilde; giơ hai ch&acirc;n trước l&ecirc;n, run run hai ch&acirc;n sau, cố đứng, lưỡi l&egrave; d&agrave;i, ra &yacute; ch&agrave;o cậu b&eacute; ngồi tắm. Cậu n&agrave;y đang bần thần vầy nước bắn to&eacute; ra xung quanh chậu, thấy thế th&igrave; cau mặt, nguẩy đầu một c&aacute;i m&agrave; rằng:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Em ch&atilde; !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Th&ocirc;i thế me xin lỗi cậu vậy ! Me thơm cậu nh&eacute; !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Em ch&atilde; !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">B&agrave; Ph&oacute; đứng tần ngần hồi l&acirc;u, lại hỏi:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Th&ocirc;i thế cậu cứ tắm cho ngoan rồi v&agrave;o ăn cơm với me nh&eacute; ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Em ch&atilde; !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Ờ, thế th&igrave; th&ocirc;i vậy. Thế cậu y&ecirc;u me th&igrave; cậu thơm me đi n&agrave;o.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tức th&igrave; cậu b&eacute; đứng l&ecirc;n... Chao &ocirc;i! Cậu b&eacute; nhưng m&agrave; cậu đ&atilde; nhớn lắm. Trần truồng, nồng nỗng, cậu đứng l&ecirc;n cao tồng ngồng m&agrave; h&ocirc;n mẹ. Cảnh tượng ấy nếu kh&ocirc;ng c&oacute; c&aacute;i gi&aacute; trị qu&aacute;i gở, &iacute;t ra cũng hay ho chẳng k&eacute;m một tấm ảnh khi&ecirc;u d&acirc;m! Cậu con b&agrave; Ph&oacute;, theo lời bọn gia nh&acirc;n k&eacute;m gi&aacute;o dục, th&igrave; đ&atilde; &quot;đủ tư c&aacute;ch&quot; lắm rồi. Muốn xo&aacute; sự ngạc nhi&ecirc;n tr&ecirc;n mặt Xu&acirc;n v&agrave; &ocirc;ng thầy số, b&agrave; n&agrave;y quay lại hợm hĩnh ph&acirc;n bua:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Con giời con Phật đấy !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thầy số hiểu ngay đ&oacute; l&agrave; một &ocirc;ng con cầu tự, chỉ c&oacute; Xu&acirc;n l&agrave; h&atilde;y c&ograve;n ngẩn mặt ra. B&agrave; Ph&oacute; quay v&agrave;o nh&agrave;. Chưa chi lại thấy tiếng kh&oacute;c chu ch&eacute;o sướt mướt của cậu b&eacute;. B&agrave; qu&aacute;t:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Chị ba đ&acirc;u !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Cậu b&eacute; g&agrave;o th&eacute;t:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Cậu v&agrave;o m&iacute; cơ! Cậu v&agrave;o m&iacute; !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Chị ba ! Mau l&ecirc;n, lau cho cậu rồi c&otilde;ng cậu.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tr&ecirc;n lưng người đ&agrave;n b&agrave;, cậu b&eacute; khổng lồ ấy c&ograve;n ng&acirc;y ng&ocirc; r&uacute;n rẩy bắt chước người cưỡi ngựa, miệng k&ecirc;u: &quot;Nhong ! nhong ! nhong!&quot;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thấy chướng mắt qu&aacute; thể, Xu&acirc;n kh&ocirc;ng thể chịu được. N&oacute; lẩm bẩm trong cổ họng: &quot;Mẹ kiếp! chứ con với chả c&aacute;i !&quot; Đến cửa ph&ograve;ng kh&aacute;ch, b&agrave; Ph&oacute; chỉ tay:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- C&aacute;c người ngồi đ&acirc;y chờ t&ocirc;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Rồi b&agrave; v&agrave;o một buồng kh&aacute;c.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Trong khi chờ đợi, hai người n&agrave;y cứ thấy cậu b&eacute; nhớn tướng ấy, mặc &aacute;o nhưng kh&ocirc;ng mặc quần, ngẩn ngơ l&oacute; đầu v&agrave;o cười, rồi lại giấu mặt đi, rồi lại l&oacute; mặt v&agrave;o, rồi lại cười... C&aacute;i &aacute;o l&aacute; bằng lụa mỡ g&agrave;, trước ngực c&oacute; dấu ấn son đỏ to tướng, sau lưng cũng thế. Chung quanh cổ, một c&aacute;i v&ograve;ng v&agrave;ng đeo nặng chĩu, một chiếc kh&aacute;nh v&agrave;ng với một c&aacute;i gối quạ. Thấy tiếng chị ba n&oacute;i như van lơn:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Cậu Phước mặc quần v&agrave;o, đi.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Em ch&atilde; !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Mặc quần v&agrave;o, kh&ocirc;ng &ocirc;ng ấy cười cho, đi !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thế bắt &ocirc;ng ấy cho em chơi nh&eacute;?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Ừ, mặc quần đi th&igrave; t&ocirc;i bắt cho m&agrave; chơi!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Em ch&atilde;!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thầy số đ&aacute; ch&acirc;n Xu&acirc;n m&agrave; rằng:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Đ&iacute;ch con cầu tự thật!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Đến b&acirc;y giờ Xu&acirc;n mới hiểu ra, gật g&ugrave;:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- A, &agrave; !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- N&agrave;y, qu&aacute;i nhỉ ! Tr&ocirc;ng b&agrave; củ c&oacute; vẻ vợ T&acirc;y lắm !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Xu&acirc;n để tay l&ecirc;n mồm l&agrave;m một c&aacute;i suỵt rồi khẽ đ&aacute;p:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Ch&iacute;nh đấy.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&Ocirc;ng thầy số th&igrave; th&agrave;o:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thế sao lại c&oacute; con cầu tự An Nam ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Chưa kịp đ&aacute;p, Xu&acirc;n đ&atilde; nghe thấy giọng b&agrave; Ph&oacute;:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Cậu ơi ! Cậu y&ecirc;u qu&yacute; của me ơi, cậu mặc quần &aacute;o v&agrave;o, ch&oacute;ng ngoan...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Rồi b&agrave; v&agrave;o. Lần n&agrave;y th&igrave; b&agrave; đ&atilde; bỏ c&aacute;i &aacute;o d&agrave;i, c&aacute;i khăn v&agrave;nh d&acirc;y. C&aacute;i &aacute;o lụa mỏng d&iacute;nh b&ecirc;n trong lại kh&ocirc;ng c&oacute; c&oacute;oc-s&ecirc;, c&aacute;i quần lụa mỏng d&iacute;nh, l&agrave;m cho b&agrave; chẳng kh&aacute;c g&igrave; một t&iacute;n đồ của chủ nghĩa khoả th&acirc;n v&agrave; l&agrave;m cho thằng Xu&acirc;n cảm thấy như m&igrave;nh l&agrave; một đứa con nh&agrave; v&ocirc; gi&aacute;o dục. &Ocirc;ng thầy số đứng l&ecirc;n để thủ lễ một ch&uacute;t. B&agrave; Ph&oacute; hỏi:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thầy xem số hay xem tướng?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm... cả hai thứ ạ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Đằng n&agrave;o đ&uacute;ng ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Xem số th&igrave; đ&uacute;ng hơn.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Xem cho t&ocirc;i một quẻ đi !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm b&agrave; lớn cho biết ng&agrave;y sinh th&aacute;ng đẻ...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- &Agrave;, thế th&igrave; t&ocirc;i qu&ecirc;n, kh&ocirc;ng nhớ r&otilde;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm thế để xem tướng vậy, nhưng sợ kh&ocirc;ng được kỹ lắm bằng xem số.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Ừ! Được! Cứ việc...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm b&agrave; lớn tốt lắm, mười hai cung phi chỉ đ&aacute;ng ph&agrave;n n&agrave;n một cung. Bẩm ấy l&agrave; cung phối hợp, nghĩa l&agrave; cung chồng. G&ograve; m&aacute; hơi cao.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">B&agrave; Ph&oacute; cau m&agrave;y một l&uacute;c n&oacute;i c&oacute; &yacute; gắt:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Sao? Ph&agrave;n n&agrave;n c&aacute;i nỗi g&igrave;? &Ocirc;ng Đoan nh&agrave; t&ocirc;i xưa kia tử tế với t&ocirc;i lắm. M&agrave; &ocirc;ng Ph&aacute;n nh&agrave; t&ocirc;i cũng thế, cũng trung hậu lắm. L&uacute;c &ocirc;ng Ph&aacute;n nh&agrave; t&ocirc;i chết cũng c&ograve;n k&ecirc;u l&agrave; y&ecirc;u thương t&ocirc;i. Ở đời n&agrave;y, mấy ai đ&atilde; hai đời chồng được như thế?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm v&acirc;ng... Nhưng m&agrave; theo lối cổ th&igrave; thế l&agrave; lưỡng độ, m&agrave; đ&atilde; phải bước đến hai bước th&igrave; l&agrave; đ&aacute;ng ph&agrave;n n&agrave;n.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- &Agrave;, c&oacute; thế chứ ! Nhưng m&agrave; theo lối kim th&igrave; mấy đời chồng cũng được, miễn tử tế th&igrave; th&ocirc;i. Cụ đo&aacute;n đ&uacute;ng đấy !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- T&iacute;nh nết b&agrave; th&igrave; nh&acirc;n đức, hay thương người.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Cụ đo&aacute;n hay lắm !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Cung t&agrave;i bạch tốt, cung điền trạch c&agrave;ng tốt, m&agrave; cung mồ mả tổ ti&ecirc;n, th&igrave; bẩm... nhờ ph&uacute;c ấm... dễ thường được đất.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thế đường tử tức th&igrave; ra sao?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Cũng tốt lắm, nhưng m&agrave; hơi hiếm.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">B&agrave; Ph&oacute; lại ra &yacute; kh&ocirc;ng bằng l&ograve;ng m&agrave; rằng:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Sao lại hiếm? Chả g&igrave; t&ocirc;i cũng c&oacute; hai đứa con rồi. C&ocirc; Jannette nh&agrave; t&ocirc;i hiện đ&atilde; đi học, sắp thi t&uacute; t&agrave;i, m&agrave; cậu Phước đ&acirc;y th&igrave; hay ăn ch&oacute;ng nhớn, ba th&aacute;ng nữa th&igrave; đ&uacute;ng mười một tuổi. Hai đứa con sao lại l&agrave; hiếm?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm nhất nam viết hữu, thập nữ viết v&ocirc;... Bao nhi&ecirc;u con g&aacute;i th&igrave;, theo lời th&aacute;nh dạy, cũng chẳng đ&aacute;ng kể. C&ograve;n con giai m&agrave; một c&oacute; cậu th&ocirc;i, theo lời th&aacute;nh dạy, thế cũng l&agrave; hiếm.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- &Agrave;, ra c&oacute; một con giai th&ocirc;i th&igrave; cũng l&agrave; hiếm.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm b&agrave; lớn, liệu b&agrave; lớn c&oacute; c&ograve;n muốn đi bước nữa?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Th&ocirc;i! T&ocirc;i đ&atilde; nhất định... n&oacute;i c&oacute; vong hồn &ocirc;ng Đoan với &ocirc;ng Ph&aacute;n nh&agrave; t&ocirc;i chứng gi&aacute;m cho, t&ocirc;i đ&atilde; nhất định thủ tiết với hai &ocirc;ng! L&agrave; v&igrave; t&ocirc;i tuy c&ograve;n trẻ măng thật, nhưng cũng mang tiếng l&agrave; gi&agrave; rồi. Chỉ c&ograve;n n&ecirc;n ở vậy nu&ocirc;i con th&ocirc;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm cung tử tức do thế m&agrave; l&agrave; hiếm.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Cụ đo&aacute;n hay đấy. Thế số cậu Phước thế n&agrave;o ? Ấy c&aacute;i đức Phật ch&ugrave;a Hương cho cậu xuống với t&ocirc;i đ&atilde; hơn mười năm. T&ocirc;i chỉ lo...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm tốt nhất ! Số cậu l&agrave; hưởng thụ, suốt đời nh&agrave;n hạ m&agrave; hưởng cả ph&uacute; lẫn qu&yacute;, lẫn khang, lẫn ninh.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">B&agrave; Ph&oacute; c&uacute;i đầu một c&aacute;ch k&iacute;nh cẩn, khẽ n&oacute;i:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- T&ocirc;i, t&ocirc;i cứ sợ m&igrave;nh người trần mắt thịt, kh&ocirc;ng biết chiều &yacute; cậu th&igrave; cậu lại đ&ograve;i về.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Thầy số sốt sắng c&atilde;i:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Chả lo ! Tr&ocirc;ng qua tướng mạo đủ biết ! Tướng cậu thọ lắm, m&agrave; b&agrave; lớn nhờ c&oacute; cậu n&ecirc;n sự thịnh vượng lại c&agrave;ng bền vững.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Hay! Hay! Cụ đo&aacute;n số thế th&igrave; tuyệt nhất trần đời !</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm nếu b&agrave; lớn n&oacute;i r&otilde; ng&agrave;y sinh th&aacute;ng đẻ th&igrave; t&ocirc;i sẽ lấy l&aacute; số Tử Vi, th&igrave; c&oacute; thể biết những sự xẩy ra h&agrave;ng năm, h&agrave;ng th&aacute;ng, h&agrave;ng ng&agrave;y...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thế nữa kia ư?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm v&acirc;ng. Xem số th&igrave; kỹ hơn xem tướng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Th&ocirc;i, thế cụ về kẻo khuya, nay mai cụ lại chơi gi&uacute;p c&aacute;i việc ấy cho t&ocirc;i nh&eacute; ! Chị ba đ&acirc;u? Tiễn cụ một đồng bạc xe cho t&ocirc;i để cụ về! Thế n&agrave;o cụ cũng lại coi hộ cho t&ocirc;i đấy!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- V&acirc;ng, v&acirc;ng. Bẩm lạy b&agrave; lớn, nay mai ch&uacute;ng t&ocirc;i sẽ đ&uacute;ng hẹn.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&Ocirc;ng thầy cắp &ocirc;, chiếu, ch&aacute;p đi khỏi, th&igrave; b&agrave; Ph&oacute; hỏi Xu&acirc;n:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Anh n&agrave;y, anh c&oacute; biết t&ocirc;i đ&atilde; l&agrave;m g&igrave; cho anh kh&ocirc;ng?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Hốt hoảng l&uacute;ng t&uacute;ng mất v&agrave;i ph&uacute;t, Xu&acirc;n mới n&oacute;i:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Dạ, bẩm b&agrave; lớn, nếu kh&ocirc;ng c&oacute; b&agrave; lớn th&igrave; ch&uacute;ng t&ocirc;i phải giam tại b&oacute;p.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Ừ, anh cũng biết ơn đấy.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm, c&aacute;i ơn ấy th&igrave; chả đời n&agrave;o con qu&ecirc;n được.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Anh đừng xưng con với t&ocirc;i! T&ocirc;i l&agrave; người văn minh, kh&ocirc;ng ph&acirc;n biệt giai cấp, kh&ocirc;ng chia rẽ sang h&egrave;n...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm v&acirc;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thế anh c&ograve;n bố mẹ kh&ocirc;ng?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm, t&ocirc;i bồ c&ocirc;i cả bố lẫn mẹ từ sớm.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Tội nghiệp! Thế anh đ&atilde; c&oacute; vợ con g&igrave; chưa?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm chưa...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Tội nghiệp! Thế l&agrave; tốt lắm! B&acirc;y giờ thời buổi kh&oacute; khăn, cũng kh&ocirc;ng n&ecirc;n vợ con l&agrave;m g&igrave; vội. Thế anh c&oacute; biết t&ocirc;i gọi anh đến đ&acirc;y l&agrave;m g&igrave; kh&ocirc;ng?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm chưa ạ. Ch&uacute;ng t&ocirc;i chờ xem b&agrave; lớn ph&aacute;n bảo những g&igrave;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- T&ocirc;i th&igrave; vốn người nh&acirc;n đức, hay thương người. M&agrave; anh th&igrave; cũng đ&aacute;ng thương, đương l&agrave;m ăn m&agrave; bỗng mất việc thế ắt l&agrave; khổ. Sao anh dại thế? Vẫn biết v&agrave;o tuổi trẻ trung th&igrave; thường tinh nghịch như anh, nhưng m&agrave; phải xem người ta c&oacute; ưng thuận th&igrave; h&atilde;y... Thế chứ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Xu&acirc;n ngẩn ngơ m&agrave; rằng:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm, con c&oacute; hiểu g&igrave; đ&acirc;u. Tự nhi&ecirc;n người ta đ&aacute;nh con, người ta đuổi con, người ta &aacute;p chế...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Th&ocirc;i đi, anh đừng chối!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Con... t&ocirc;i c&oacute; chối c&atilde;i g&igrave; đ&acirc;u?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Thế anh bị đuổi v&igrave; lẽ g&igrave;?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- T&ocirc;i đang sửa soạn khăn b&ocirc;ng, th&ugrave;ng tắm cho c&aacute;c hội vi&ecirc;n, đương l&uacute;i h&uacute;i l&agrave;m lụng, th&igrave; &ocirc;ng T&acirc;y ấy v&agrave;o l&ocirc;i ra đ&aacute;nh mắng...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Chứ kh&ocirc;ng phải anh đương...?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Một c&aacute;ch ng&acirc;y thơ th&agrave;nh thực nhất đời, Xu&acirc;n đ&aacute;p:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- T&ocirc;i đương bịt một c&aacute;i lỗ ống ch&igrave; trong buồng tắm của hội qu&aacute;n!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Ồ! Ồ! Thế &ocirc;ng T&acirc;y &ocirc;ng ấy bảo anh c&oacute; phạm một tội, sao anh kh&ocirc;ng c&atilde;i?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Xu&acirc;n đỏ mặt hồi l&acirc;u rồi ấp &uacute;ng:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm... bẩm &ocirc;ng ấy buộc những tội g&igrave; th&igrave; n&agrave;o t&ocirc;i c&oacute; biết tiếng T&acirc;y đ&acirc;u?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">B&agrave; Ph&oacute; Đoan đứng ngẩn người ra, nuốt sự thất vọng đ&aacute;nh ực một c&aacute;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">B&agrave; nhớ lại mấy c&aacute;i t&ecirc;n, c&aacute;i lầm từ xưa kia... Từ khi bị hiếp, những cảm gi&aacute;c t&ecirc; m&ecirc; hiếm c&oacute; rất kh&oacute; tả, rất kỳ qu&aacute;i, cứ theo m&atilde;i b&agrave; như b&oacute;ng theo người, l&acirc;u dần, việc ấy th&agrave;nh một &aacute;m ảnh. B&agrave; vẫn ao ước được - bị hiếp nữa m&agrave; kh&ocirc;ng bao giờ c&aacute;i dịp hiếm c&oacute; ấy lại t&aacute;i hiện. Th&agrave;nh thử b&agrave; chỉ c&oacute; hiếp chồng chứ quả thật - n&oacute;i c&oacute; quỷ thần hai vai chứng gi&aacute;m - b&agrave; chẳng được - bị chồng hiếp cho lần n&agrave;o.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Sau khi đọc bộ Kim Anh lệ sử thấy n&oacute;i ở c&aacute;i ch&ugrave;a tỉnh ấy, sư m&ocirc; cứ vờ l&agrave; Phật để xuống b&aacute;n con cho những đ&agrave;n b&agrave; cầu tự, b&agrave; đ&atilde; tức khắc d&ograve; hỏi, rồi đi... B&agrave; đ&atilde; bị tẽn! L&uacute;c về nh&agrave; &ocirc;ng Ph&aacute;n ban con cho b&agrave;, chứ chẳng c&oacute; sư m&ocirc; qu&aacute;i n&agrave;o.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Đến lần n&agrave;y.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Trước c&aacute;i vẻ mặt thật th&agrave; của Xu&acirc;n, b&agrave; hơi buồn, nghĩ dễ thường n&oacute; bị đuổi oan... B&agrave; đ&atilde; hơi c&aacute;u... Nhưng một &yacute; nghĩ tho&aacute;ng chạy qua &oacute;c b&agrave;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Anh l&ecirc;n g&aacute;c n&agrave;y chờ t&ocirc;i tắm một l&aacute;t rồi t&ocirc;i sẽ n&oacute;i chuyện tại sao t&ocirc;i bảo anh về đ&acirc;y.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Xu&acirc;n nghe theo... Hai người l&ecirc;n thang. Đến một c&aacute;i ph&ograve;ng kh&aacute;ch nữa, b&agrave; Ph&oacute; bảo:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Anh ngồi đ&acirc;y, xem quyển anbom n&agrave;y m&agrave; chờ t&ocirc;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Rồi b&agrave; b&agrave;o buồng tắm, c&aacute;ch chỗ Xu&acirc;n ngồi chờ c&oacute; v&agrave;i bước ch&acirc;n. B&agrave; cởi quần &aacute;o, đội c&aacute;i mũ cao su bịt k&iacute;n t&oacute;c, vặn m&aacute;y nước... Từ c&aacute;i b&ocirc;ng hoa sen kẽm, nước tr&uacute;t xuống &agrave;o &agrave;o! B&agrave; Ph&oacute; thỉnh thoảng lại vỗ v&agrave;o bụng, v&agrave;o đ&ugrave;i b&igrave; bạch. Rồi b&agrave;, than &ocirc;i! tr&aacute;i ngược - b&agrave; nh&ograve;m qua lỗ kho&aacute; xem b&ecirc;n ngo&agrave;i động tĩnh ra sao... Th&igrave; ra, chăm ch&uacute; v&agrave;o quyển s&aacute;ch ảnh, thằng Xu&acirc;n cứ ngồi nguy&ecirc;n chỗ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Như thường!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tắm xong, b&agrave; ra, ph&aacute;n một c&aacute;ch uể oải:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Th&ocirc;i anh về! T&ocirc;i định đến mai th&igrave; sẽ mượn anh. Ng&agrave;y mai anh sẽ đến hiệu &Acirc;u ho&aacute; t&igrave;m c&ocirc; Văn Minh, th&igrave; t&ocirc;i sẽ n&oacute;i gi&uacute;p. Thế n&agrave;o th&igrave; rồi anh cũng sẽ khỏi thất nghiệp.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Bẩm...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">- Th&ocirc;i, anh kh&ocirc;ng phải l&agrave; người th&ocirc;ng minh! Anh về đi! Mai sẽ biết. Phải nhớ: hiệu &Acirc;u ho&aacute;, tiệm may phụ nữ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Xu&acirc;n T&oacute;c Đỏ ra đi, với mối hy vọng chan chứa trong l&ograve;ng, kh&ocirc;ng hiểu rằng m&igrave;nh đ&atilde; bị khinh bỉ như một nh&agrave; đạo đức ch&acirc;n ch&iacute;nh đ&atilde; bị khinh bỉ.</span></span></p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(20, 'Cánh đồng bất tận', 'Văn học Việt Nam', 'Nguyễn Ngọc Tư', 2005, 'canh-dong-bat-tan.jpg', 'Đã hoàn', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#242424\">&ldquo;C&aacute;nh đồng bất tận&rdquo; gồm hai truyện vừa l&agrave; &quot;C&aacute;nh đồng bất tận&quot; v&agrave; &quot;Đất&quot;, l&agrave; c&acirc;u chuyện về những mảnh đời mải mốt b&ecirc;n d&ograve;ng M&ecirc; K&ocirc;ng, đ&aacute;nh dấu sự thấu hiểu v&agrave; cảm th&ocirc;ng s&acirc;u sắc của Nguyễn Ngọc Tư với người n&ocirc;ng d&acirc;n (v&agrave; con người n&oacute;i chung). B&agrave;ng bạc khắp truyện l&agrave; t&igrave;nh y&ecirc;u thương, sự th&ugrave; hận, nỗi nhớ nhung, niềm &acirc;n hận, v&agrave; sự b&oacute;c trần những g&oacute;c khuất tăm tối trong t&acirc;m hồn khiến cho nh&acirc;n vật trong đ&oacute; trở n&ecirc;n &ldquo;đời&rdquo; hơn bao giờ hết.&nbsp;Truyện vừa &ldquo;C&aacute;nh đồng bất tận&rdquo;, được chuyển thể th&agrave;nh phim năm 2010 với t&ecirc;n gọi &ldquo;The Floating Lives&rdquo;, đ&atilde; được dịch v&agrave; xuất bản ở H&agrave;n Quốc, Trung Quốc, Ph&aacute;p, Đức, Thụy Điển, v&agrave; nhận được nhiều lời khen ngợi.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>Phần 1</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Con kinh nhỏ nằm vắt qua một c&aacute;nh đồng rộng. V&agrave; khi ch&uacute;ng t&ocirc;i&nbsp;quyết định dừng lại, m&ugrave;a hạn hung h&atilde;n dường như cũng gom hết&nbsp;nắng đổ xuống nơi n&agrave;y. Những c&acirc;y l&uacute;a chết non tr&ecirc;n đồng, th&acirc;n đ&atilde;&nbsp;kh&ocirc; cong như t&agrave;n nhang chưa rụng, nắm v&agrave;o b&agrave;n tay l&agrave; n&aacute;t vụn.&nbsp;Cha t&ocirc;i th&aacute;o c&aacute;i khung tre chắn dưới s&agrave;n ghe, bầy vịt l&uacute;c nh&uacute;c&nbsp;chen ra, cuống qu&yacute;t, nh&aacute;o nh&agrave;o quẫy ngụp xuống mặt nước v&aacute;ng&nbsp;ph&egrave;n. Một lớp ph&egrave;n mới, v&agrave;ng sẫm qu&aacute;nh lại tr&ecirc;n bộ l&ocirc;ng của&nbsp;những con vịt đ&oacute;i, nhớp nh&aacute;p b&aacute;m tr&ecirc;n vai Điền khi n&oacute; trầm m&igrave;nh&nbsp;bơi đi cặm cọc, giăng lưới r&agrave;o bầy vịt lại. T&ocirc;i bưng c&aacute;i c&agrave; r&agrave;ng l&ecirc;n&nbsp;bờ, nh&oacute;m củi.&nbsp;Rồi ngọn lửa hoi h&oacute;t thở dưới nồi cơm đ&atilde; l&ecirc;n tim, người đ&agrave;n b&agrave;&nbsp;vẫn c&ograve;n nằm tr&ecirc;n ghe. Ngay cả &yacute; định ngồi dậy cũng xao x&aacute;c tan&nbsp;mau dưới những tiếng r&ecirc;n d&agrave;i. M&ocirc;i chị sưng vểu ra, xanh dờn. V&agrave;&nbsp;tay, v&agrave; ch&acirc;n, v&agrave; dưới c&aacute;i &aacute;o m&agrave; t&ocirc;i đ&atilde; đắp cho l&agrave; một c&aacute;i &aacute;o kh&aacute;c đ&atilde;&nbsp;bị x&eacute; tả tơi phơi những mảng thịt người ta cấu nh&eacute;o t&iacute;m ngắt.&nbsp;V&agrave; những ch&acirc;n t&oacute;c tr&ecirc;n đầu chị cũng đang tụ m&aacute;u. Người ta đ&atilde; l&ograve;n&nbsp;tay, ngoay ch&uacute;ng để k&eacute;o chị l&ecirc; lết hết một qu&atilde;ng đường x&oacute;m,&nbsp;trước khi dừng ch&acirc;n một ch&uacute;t ở nh&agrave; m&aacute;y ch&agrave; gạo. Họ giằng n&eacute;m,&nbsp;họ quăng quật chị tr&ecirc;n c&aacute;i nền vương v&atilde;i trấu. Vai nữ ch&iacute;nh, một&nbsp;người đ&agrave;n b&agrave; xốc xếch đ&atilde; lạc giọng, đ&ocirc;i l&uacute;c lả đi v&igrave; ghen tu&ocirc;ng v&agrave;&nbsp;kiệt sức. Nhưng đ&aacute;m đ&ocirc;ng rạo rực chung quanh đ&atilde; vực tinh thần&nbsp;chị ta dậy, họ d&ugrave;ng ch&acirc;n đ&aacute; v&oacute;i v&agrave;o c&aacute;i th&acirc;n x&aacute;c tả tơi kia bằng vẻ&nbsp;hằn học, hả h&ecirc;, qu&ecirc;n phứt vụ l&uacute;a thất b&aacute;t ch&aacute;y kh&ocirc; tr&ecirc;n đồng,&nbsp;qu&ecirc;n nỗi lo đ&oacute;i no giữa m&ugrave;a gi&aacute;p hạt. Cuộc vui hẳn sẽ d&agrave;i, nếu như&nbsp;kh&ocirc;ng c&oacute; một &yacute; tưởng mới nảy ra trong cơn phấn kh&iacute;ch. Họ d&ugrave;ng&nbsp;dao phay chạt m&aacute;i t&oacute;c d&agrave;y kia, dục dặc, h&igrave; hục như phạt một nắm&nbsp;cỏ cứng v&agrave; kh&ocirc;. Khi đu&ocirc;i t&oacute;c dứt l&igrave;a, được tự do, chị v&ugrave;ng dậy, lao&nbsp;nhanh xuống ghe ch&uacute;ng t&ocirc;i như một tiếng th&eacute;t, lăn qua ch&acirc;n t&ocirc;i,&nbsp;đến chỗ cha, l&agrave;m đổ những bao trấu cha vừa mới xếp.&nbsp;Đ&aacute;m người ngơ ng&aacute;c mấy gi&acirc;y để chấp nhận việc con mồi bỏ chạy.&nbsp;T&ocirc;i mất mấy gi&acirc;y để h&aacute;o hức thấy m&igrave;nh nghĩa hiệp như Lục V&acirc;n&nbsp;Ti&ecirc;n, t&ocirc;i lồm cồm x&ocirc; ghe dạt khỏi bờ, sợ h&atilde;i v&agrave; sung sướng, t&ocirc;i&nbsp;cầm s&agrave;o chống thục mạng ra giữa s&ocirc;ng, mắt kh&ocirc;ng rời đ&aacute;m người&nbsp;đang tr&agrave;n ra m&eacute; bờ chực lao xuống, nhảy nh&oacute;t đi&ecirc;n cuồng. Rồi&nbsp;tiếng chửi rủa ch&oacute;i l&oacute;i ch&igrave;m đi, tiếng bầy vịt tao t&aacute;c k&ecirc;u dưới sạp&nbsp;ch&igrave;m đi, trong t&ocirc;i chỉ c&oacute; &acirc;m thanh của chiếc m&aacute;y Koler4 nổ khan,&nbsp;rung bần bật dưới tay Điền, khạc ra những đ&aacute;m kh&oacute;i kh&eacute;t lẹt, đen&nbsp;ng&ograve;m. Kh&oacute;i tr&ocirc;i về ph&iacute;a sau ch&uacute;ng t&ocirc;i, mờ nho&egrave; những b&oacute;ng người&nbsp;đang tuyệt vọng ng&oacute; theo, b&agrave;n tay n&agrave;o đ&oacute; cầm nắm t&oacute;c của chị vẫy&nbsp;l&ecirc;n phơ phất phơ phất ...&nbsp;Cha t&ocirc;i kh&ocirc;ng đ&oacute;ng vai tr&ograve; g&igrave; trong cuộc th&aacute;o chạy ấy, &ocirc;ng im lặng,&nbsp;khi đ&atilde; đi kh&aacute; xa, cha ra đằng mũi cầm s&agrave;o. T&ocirc;i b&ograve; v&agrave;o trong ghe,&nbsp;lấy &aacute;o đắp l&ecirc;n người chị, sao cho che được đ&ocirc;i v&uacute; r&aacute;ch bươm v&agrave;&nbsp;khoảng đ&ugrave;i rướm m&aacute;u. Chị cười mếu m&aacute;o, n&oacute;i cảm ơn bằng mắt&nbsp;v&agrave; thiếp đi.&nbsp;Suốt một qu&atilde;ng đường, chị kh&ocirc;ng hề thay đổi thế nằm, im l&igrave;m,&nbsp;lạnh ngắt như người chết. Trong ghe chỉ tr&ocirc;i m&ecirc;nh mang những&nbsp;tiếng r&ecirc;n khi d&agrave;i, khi ngắn, khi thiu thỉu buồn xo, l&uacute;c nghe như&nbsp;tiếng nấc nghẹn ng&agrave;o...&nbsp;Nhờ vậy m&agrave; ch&uacute;ng t&ocirc;i biết chị c&ograve;n sống, để đi với ch&uacute;ng t&ocirc;i gần&nbsp;hết con s&ocirc;ng B&igrave;m Bịp, đến c&aacute;nh đồng hoang vắng n&agrave;y. Điền hơi lo&nbsp;lắng khi nghe chị vẫn c&ograve;n r&ecirc;n rỉ, nghĩ l&agrave; chị đ&atilde; đ&oacute;i, n&oacute; hối t&ocirc;i nấu&nbsp;cơm mau. N&oacute; thấy tiếc v&igrave; tr&ecirc;n ghe chỉ c&ograve;n mấy con kh&ocirc; sặt mặn&nbsp;ch&aacute;t, &ldquo;tui nuốt c&ograve;n kh&ocirc;ng v&ocirc;, n&oacute;i chi...&rdquo;&nbsp;Nhưng chiều h&ocirc;m đ&oacute; v&agrave; cả ng&agrave;y sau, chị kh&ocirc;ng ăn. Chị từ chối cả&nbsp;uống nước, đợi đ&ocirc;i m&ocirc;i kh&ocirc; đ&atilde; bắt đầu nứt ra, chị mới chịu hớp&nbsp;một v&agrave;i ngụm &iacute;t ỏi, dường như chỉ đủ ướt m&ocirc;i. Đ&oacute;i v&agrave; kh&aacute;t, nhưng&nbsp;chị c&ograve;n sợ đau hơn. Người ta đ&atilde; đổ keo d&aacute;n sắt v&agrave;o cửa m&igrave;nh của&nbsp;chị...&nbsp;T&ocirc;i n&oacute;i lại với cha v&agrave; Điền trong bữa cơm. T&ocirc;i nghe hai người lặng&nbsp;đi, tiếng đũa tre khua v&agrave;o miệng ch&eacute;n ngưng bặt. Điền ng&oacute; t&ocirc;i v&agrave;&nbsp;t&ocirc;i th&igrave; đọc được sự gh&ecirc; sợ, kinh tởm cồn l&ecirc;n trong mắt cha. Điền&nbsp;chan nước v&agrave;o ch&eacute;n, lua vội v&agrave;ng rồi men theo con đường đất dọc&nbsp;m&eacute; kinh, n&oacute; đi v&agrave;o trong x&oacute;m. T&ocirc;i dặn v&oacute;i theo, gh&eacute; tiệm mua&nbsp;gi&ugrave;m ng&agrave;n rưỡi đường c&aacute;t.&nbsp;Chắc gi&oacute; đ&atilde; bạt mất lời t&ocirc;i, khi quay về, Điền kh&ocirc;ng mang theo g&igrave;,&nbsp;n&oacute; lẳng lặng x&ograve;e tay trước mặt t&ocirc;i, tay n&oacute; d&iacute;nh một lớp g&igrave; đ&oacute;, b&oacute;ng&nbsp;mượt, trong suốt, v&agrave; đang kh&ocirc; qu&aacute;nh lại, khiến những ng&oacute;n tay đơ&nbsp;ra như đ&aacute;. Điền bảo, &ldquo;Keo d&aacute;n sắt...&rdquo;. Dường như những người sản&nbsp;xuất ra loại keo n&agrave;y cũng kh&ocirc;ng ngờ n&oacute; nhiều c&ocirc;ng dụng đến thế.&nbsp;Hai chị em t&ocirc;i tỉ mẩn lột lớp keo ra, mảng da non tr&ecirc;n b&agrave;n tay n&oacute;&nbsp;bỏng đỏ nhừ, tươm m&aacute;u. Ch&uacute;ng t&ocirc;i c&ugrave;ng ng&oacute; về chỗ khoang ghe,&nbsp;nghe tiếng thở th&ecirc;nh thang c&ugrave;ng gi&oacute;...&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>Phần 2</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">C&aacute;nh đồng kh&ocirc;ng c&oacute; t&ecirc;n. Nhưng với t&ocirc;i v&agrave; Điền, chẳng c&oacute; nơi n&agrave;o&nbsp;l&agrave; v&ocirc; danh, ch&uacute;ng t&ocirc;i nhắc, ch&uacute;ng t&ocirc;i gọi t&ecirc;n bằng những kỷ niệm&nbsp;m&agrave; ch&uacute;ng t&ocirc;i c&oacute; tr&ecirc;n mỗi c&aacute;nh đồng. Chỗ chị em t&ocirc;i trồng c&acirc;y, chỗ&nbsp;Điền bị rắn cắn, chỗ t&ocirc;i c&oacute; kỳ kinh nguyệt đầu ti&ecirc;n... V&agrave; mai n&agrave;y&nbsp;khi tr&ocirc;i dạt đến một nơi n&agrave;o kh&aacute;c, nhắc đến c&aacute;nh đồng n&agrave;y với c&aacute;i&nbsp;t&ecirc;n của chị, chắc ch&uacute;ng t&ocirc;i sẽ xốn xang.&nbsp;S&aacute;ng sớm ng&agrave;y thứ ba, chị ngồi dậy được, ng&oacute; quanh, chị hỏi:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&nbsp;&ldquo;Trời đất ơi, chỗ n&agrave;o m&agrave; vắng tanh vậy n&egrave;&rdquo;. </span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">L&agrave;ng x&oacute;m xa xa đằng&nbsp;ph&iacute;a những rặng dừa xanh thẩm. Đồng đất trống trơn, tr&ecirc;n đ&ocirc;i bờ&nbsp;kinh chơ vơ những c&acirc;y g&ograve;n. Hai đứa nhỏ đầu mướt sương đang h&iacute;&nbsp;h&uacute;i trộn thức ăn cho vịt, nh&igrave;n chị ngạc nhi&ecirc;n đến ng&acirc;y ngất. Giọng&nbsp;n&oacute;i chị kh&ocirc;ng bị thương t&iacute;ch g&igrave; hết, trong vắt v&agrave; ngọt ng&agrave;o.&nbsp;Chị hỏi</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Tắm ở đ&acirc;u, mấy cưng ?&rdquo;. </span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i chỉ xuống kinh. Chị ng&oacute;&nbsp;những v&aacute;ng ph&egrave;n, ngao ng&aacute;n. Điền n&oacute;i, đằng kia c&oacute; một c&aacute;i ao.&nbsp;Đ&oacute; l&agrave; một hố bom cũ, b&igrave;nh b&aacute;t mọc quanh, rau muống chằng chịt&nbsp;phủ k&iacute;n mặt nước, cọng rau ốm nhằng, đỏ au. Chỗ n&agrave;y, h&ocirc;m qua,&nbsp;Điền đ&atilde; c&acirc;u được những con c&aacute; th&aacute;t l&aacute;t &uacute; mềm. Chị trầm m&igrave;nh&nbsp;dưới đ&oacute; rất l&acirc;u, chẳng kỳ cọ g&igrave;, chỉ để nước lạnh chườm dịu lại&nbsp;những chỗ đau. L&uacute;c chị l&ecirc;n bờ, t&ocirc;i thấy m&aacute;u lợn cợn theo những&nbsp;giọt nước nhỏ xuống giữa hai đ&ugrave;i, chắc chị đ&atilde; l&agrave;m g&igrave; với chỗ keo&nbsp;d&aacute;n &aacute;c nghiệt ấy. Rồi cũng l&acirc;u lắc, cũng c&agrave; nhắc từng bước ngắn&nbsp;như l&uacute;c đi, chị với t&ocirc;i quay lại bờ kinh. Điền h&iacute; hửng khi thấy chị&nbsp;chịu mặc chiếc &aacute;o sơ-mi thẫm ph&egrave;n v&agrave; c&aacute;i quần cụt quăn queo của&nbsp;n&oacute;.&nbsp;Chỉ c&oacute; cha lầm l&igrave; dọn cỏ quanh ch&ograve;i. Chỉ c&oacute; cha l&agrave; hờ hững với&nbsp;th&agrave;nh quả của hai chị em t&ocirc;i. Kh&ocirc;ng chấp th&aacute;i độ lạt lẽo của cha,&nbsp;chị ng&oacute; về ph&iacute;a người đ&agrave;n &ocirc;ng đang vồng lưng trong nắng sớm,&nbsp;chếnh cho&aacute;ng:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Ba mấy cưng đẹp trai dễ sợ...&rdquo;.&nbsp;&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">V&igrave; lẽ đ&oacute; ? C&oacute; phải v&igrave; cha m&agrave; chị ở lại với ch&uacute;ng t&ocirc;i, tr&ecirc;n một c&aacute;nh&nbsp;đồng vắng ngắt. Những vết thương đ&atilde; l&agrave;nh rất mau. Chị cười, bị&nbsp;đ&aacute;nh ho&agrave;i riết cũng quen. T&ocirc;i hỏi chị l&agrave;m g&igrave; để bị đ&aacute;nh. Chị cười,&nbsp;&ldquo;L&agrave;m đĩ&rdquo;. Rồi c&oacute; lẽ chị &aacute;y n&aacute;y v&igrave; qu&aacute; sỗ s&agrave;ng với ch&uacute;ng t&ocirc;i, chị v&ograve;&nbsp;đầu Điền &ldquo;chắc mấy cưng kh&ocirc;ng biết đ&acirc;u...&rdquo;.&nbsp;Điền nh&igrave;n t&ocirc;i cười. Ch&uacute;ng t&ocirc;i đ&atilde; gặp nhiều, rất nhiều người phụ&nbsp;nữ giống chị. Cứ mỗi m&ugrave;a gặt, họ lại dập d&igrave;u tr&ecirc;n đ&ecirc;, lượn lờ&nbsp;quanh lều của những thợ gặt, những người đ&agrave;n &ocirc;ng giữ l&uacute;a v&agrave; bọn&nbsp;nu&ocirc;i vịt chạy đồng. Họ cố l&agrave;m ra vẻ trẻ trung, tươi tắn nhưng mặt&nbsp;v&agrave; cổ đ&atilde; nh&atilde;o, nh&igrave;n kỹ ph&aacute;t ứa nước mắt. Đ&ecirc;m đến, sau c&aacute;c đụn&nbsp;l&uacute;a, họ thả tiếng cười ch&uacute;t ch&iacute;t, tiếng thở mơn man... l&ecirc;n trời, l&agrave;m&nbsp;nhiều người đ&agrave;n b&agrave; đang cắm c&uacute;i nấu cơm, cho con b&uacute; trong lều&nbsp;thắt l&ograve;ng lại. Tối n&agrave;o mua rượu cho cha, ch&uacute;ng t&ocirc;i cũng đi ngang&nbsp;qua những đ&ocirc;i người. Ch&uacute;ng t&ocirc;i nhận ra họ ngay, khi kh&ocirc;ng c&ograve;n&nbsp;mảnh vải n&agrave;o tr&ecirc;n người họ vẫn điềm nhi&ecirc;n cười kh&uacute;c kh&iacute;ch v&agrave;&nbsp;uốn &eacute;o th&acirc;n m&igrave;nh chứ kh&ocirc;ng trơ ra ngượng ngịu, cam chịu như&nbsp;những người phụ nữ qu&ecirc;. S&aacute;ng sau, họ xi&ecirc;u xi&ecirc;u biến mất, đem&nbsp;theo mớ tiền c&ocirc;ng &iacute;t ỏi suốt một ng&agrave;y l&agrave;m việc quần quật của đ&aacute;m&nbsp;đ&agrave;n &ocirc;ng.&nbsp;Chị, cũng giống như họ, chớm t&agrave;n tạ, đ&oacute;i r&atilde; ruột ở thị th&agrave;nh mới&nbsp;chạy xuống qu&ecirc;, cất c&aacute;i qu&aacute;n nhỏ, giả đ&ograve; bu&ocirc;n b&aacute;n b&aacute;nh kẹo lặt&nbsp;vặt, thực chất l&agrave; l&agrave;m nghề. Ở đ&oacute; đ&agrave;n &ocirc;ng dễ t&iacute;nh v&agrave; thiệt th&agrave;. Chị&nbsp;sống nhờ những m&oacute;n tiền họ cắm c&acirc;u đ&ecirc;m đ&ecirc;m, bằng tiền b&aacute;n l&uacute;a,&nbsp;dừa kh&ocirc; hay những buồng chuối ch&iacute;n. Cũng c&oacute; l&uacute;c thu hoạch bất&nbsp;ngờ, khi chị mồi ch&agrave;i một người đ&agrave;n &ocirc;ng v&agrave;o tr&ograve; chơi giường&nbsp;chiếu, suốt hai ng&agrave;y đ&ecirc;m, v&agrave; chị được một triệu hai. Đ&oacute; l&agrave; vốn vay&nbsp;xo&aacute; đ&oacute;i giảm ngh&egrave;o, khi về tới nh&agrave;, với t&aacute;m trăm ng&agrave;n c&ograve;n lại&nbsp;trong t&uacute;i, hẳn người đ&agrave;n &ocirc;ng ấy n&atilde;o nề biết bao nhi&ecirc;u, o&aacute;n chị biết&nbsp;bao nhi&ecirc;u khi thấy vợ con nheo nh&oacute;c bu quanh nồi khoai luộc&nbsp;trong nhập nhoạng nắng chiều.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- Ăn tr&ecirc;n mồ h&ocirc;i nước mắt của người ta n&ecirc;n l&acirc;u l&acirc;u bị đ&aacute;nh cũng&nbsp;đ&aacute;ng đời, hen mấy cưng ?&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Chị n&oacute;i, v&agrave; ng&atilde; nghi&ecirc;ng cười, dường như chị thấy m&igrave;nh trả gi&aacute; vậy&nbsp;cũng vừa. </span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;M&agrave; h&ecirc;n nghen, nhờ vậy m&agrave; gặp được mấy cưng, được ở&nbsp;chung vầy, vui thiệt vui...&rdquo;.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Cha kh&ocirc;ng vui, v&igrave; c&oacute; th&ecirc;m một miệng ăn. Bầy vịt cũng kh&ocirc;ng vui,&nbsp;ch&uacute;ng mổ v&agrave;o ch&acirc;n chị mỗi khi chị bước qua r&agrave;o,</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;b&agrave; ở đ&acirc;y l&agrave;m&nbsp;chi để phần ăn của tụi tui bị x&eacute;n bớt, trong m&aacute;ng độn to&agrave;n l&agrave; trấu&nbsp;ng&aacute;n thấy &ocirc;ng thấy cha, đ&atilde; vậy c&ograve;n bắt tụi tui đẻ trứng để nu&ocirc;i&nbsp;b&agrave;&rdquo;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Chị nhảy xổm ra, la oai o&aacute;i, sau lại cười (m&agrave; con mắt đung đưa&nbsp;ph&iacute;a cha) &ldquo;mai mốt mấy con vịt quỷ n&agrave;y sẽ kho&aacute;i chị, mấy hồi...&rdquo;.&nbsp;Nhưng t&ocirc;i v&agrave; Điền biết thể n&agrave;o chị cũng ra đi, trong mỏi m&ograve;n.&nbsp;Qu&atilde;ng thời gian chị b&ecirc;n ch&uacute;ng t&ocirc;i, v&igrave; thế cũng mong manh. Nhiều&nbsp;khi l&ugrave;a vịt ăn m&oacute;t một đỗi đồng, nghĩ chị bỏ đi, Điền thảng thốt&nbsp;chạy về.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">&nbsp;- Mấy cưng thương chị thiệt hả ? Tội chưa... !?? &rdquo;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Chị ngạc nhi&ecirc;n, thấy nước mắt r&agrave;n rụa tr&ecirc;n m&aacute; thằng Điền (m&agrave; chị&nbsp;kh&ocirc;ng biết l&agrave; n&oacute; bị bệnh chảy nước mắt sống từ hồi ch&iacute;n tuổi).&nbsp;Thật cảm động khi đời đ&aacute;nh ta tả tơi bầm dập, vậy m&agrave; hai đứa nhỏ&nbsp;n&agrave;y lại tr&igrave;u mến, quyến luyến lạ l&ugrave;ng. Th&ecirc;m một l&yacute; do nữa để chị ở&nbsp;lại với ch&uacute;ng t&ocirc;i, trong một m&ugrave;a hạn n&oacute;ng bỏng, bất thường.&nbsp;M&ugrave;a đến sớm. V&igrave; vậy m&agrave; nắng rất d&agrave;i. Mới rồi, ch&uacute;ng t&ocirc;i dừng&nbsp;ch&acirc;n ở một x&oacute;m nhỏ b&ecirc;n bờ s&ocirc;ng lớn m&ecirc;nh mang. Mỉa mai, người&nbsp;ở đ&acirc;y lại kh&ocirc;ng c&oacute; nước để d&ugrave;ng (như ch&uacute;ng t&ocirc;i đi tr&ecirc;n đất dằng&nbsp;dặc m&agrave; kh&ocirc;ng c&oacute; cục đất chọi chim). Người họ đầy ghẻ ch&oacute;c,&nbsp;những đứa trẻ g&atilde;i đến bật m&aacute;u. Họ đi mua nước ngọt bằng xuồng&nbsp;ch&egrave;o, n&iacute;n thở để nước khỏi s&aacute;nh ra ngo&agrave;i v&igrave; đường xa, nước mắc.&nbsp;Buổi chiều đi l&agrave;m mướn về, họ tụt xuống ao tắm t&aacute;p thứ nước&nbsp;chua l&eacute;t v&igrave; ph&egrave;n, rồi xối lại đ&uacute;ng hai g&agrave;u. Nước vo cơm d&ugrave;ng để&nbsp;rửa rau, rửa rau xong d&agrave;nh rửa c&aacute;. Con n&iacute;t ba tuổi đ&atilde; biết qu&yacute;&nbsp;nước, mắc lắm cũng r&aacute;ng chạy ra vườn đ&aacute;i v&ocirc; chậu ớt, chậu h&agrave;nh&nbsp;(b&aacute;o hại c&acirc;y rụi l&aacute;). Ở đ&oacute;, c&oacute; người con trai bảo, &ldquo;ước l&agrave;m sao&nbsp;trước l&uacute;c m&aacute; tui chết, b&agrave; được tắm một bữa đ&atilde; đời&rdquo;. C&acirc;u n&oacute;i n&agrave;y&nbsp;l&agrave;m t&ocirc;i thương anh ta qu&aacute; chừng. H&ocirc;m t&ocirc;i đi, anh tần ngần đứng&nbsp;b&ecirc;n h&egrave;, hỏi nhỏ</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;Kh&ocirc;ng biết em c&oacute; muốn ở lại với... m&aacute; tui?!&rdquo;. T&ocirc;i&nbsp;lắc đầu, hai g&agrave;u nước &iacute;t ỏi của m&aacute; anh, t&ocirc;i nỡ n&agrave;o sẻ nửa ?&nbsp;T&ocirc;i giục cha rời khỏi c&aacute;i x&oacute;m t&agrave;n tạ đ&oacute;. Những c&aacute;nh đồng ch&uacute;ng&nbsp;t&ocirc;i qua, l&uacute;a chết kh&ocirc; khi mới trổ b&ocirc;ng. Người ta kh&ocirc;ng thể trồng&nbsp;đậu, trồng dưa v&igrave; thiếu nước. Bầy con n&iacute;t giỡn nhoi tr&ecirc;n những&nbsp;con kinh kh&ocirc; trơ l&ograve;ng.&nbsp;Chỗ ch&uacute;ng t&ocirc;i cắm lều cầm vịt, nước đ&atilde; sắc lại thẫm một m&agrave;u v&agrave;ng&nbsp;u &aacute;m. Nhưng ch&uacute;ng t&ocirc;i chẳng c&oacute; chỗ n&agrave;o để đi nữa, từ bờ b&ecirc;n kia&nbsp;của s&ocirc;ng B&igrave;m Bịp l&agrave; v&ugrave;ng đệm cho những c&aacute;nh rừng tr&agrave;m lớn.&nbsp;M&ugrave;a n&agrave;y, người ta lấy nước từ tất cả c&aacute;c d&ograve;ng s&ocirc;ng nhỏ, c&aacute;c con&nbsp;kinh để bơm v&agrave;o rừng, chống ch&aacute;y. Ch&uacute;ng t&ocirc;i cũng kh&ocirc;ng thể&nbsp;ngược s&ocirc;ng B&igrave;m Bịp băng qua v&ugrave;ng Ki&ecirc;n H&agrave;, ở đ&oacute; họ kiểm dịch&nbsp;th&uacute; y rất gắt gao. V&agrave; dịch c&uacute;m gia cầm nghe đ&acirc;u vẫn c&ograve;n b&ugrave;ng ph&aacute;t&nbsp;khắp đồng bằng.&nbsp;Để bầy vịt khỏi bị ch&ocirc;n sống (điều đ&oacute; đồng nghĩa với việc đứt vốn&nbsp;cho m&ugrave;a sau), bọn t&ocirc;i quyết định vẫn cầm ch&uacute;ng ở đ&acirc;y. Ch&uacute;ng&nbsp;được nu&ocirc;i trong sự mỏi m&ograve;n. Ng&agrave;y ng&agrave;y, t&ocirc;i l&ugrave;a vịt đi ăn m&oacute;t&nbsp;những b&ocirc;ng l&uacute;a kh&ocirc; quắt queo tr&ecirc;n đồng, kh&ocirc;ng c&oacute; nước ch&uacute;ng b&igrave;&nbsp;bạch, chậm r&igrave; v&agrave; chẳng thể đi xa. Trứng thưa thớt, những quả&nbsp;trứng ch&uacute;ng đẻ ra cũng chai ngắt, d&agrave;i nhằng, nhẹ t&ecirc;nh, vỏ d&agrave;y sần&nbsp;sượng. Đ&ograve;i hỏi g&igrave; nữa ở những con vịt đ&atilde; gi&agrave;, đ&atilde; đẻ quần quật suốt&nbsp;ba m&ugrave;a trước, đ&atilde; v&ocirc; vọng v&igrave; ng&agrave;y c&agrave;ng kh&oacute; t&igrave;m l&uacute;a v&agrave; c&aacute;m trong&nbsp;m&aacute;ng thức ăn. Ngay cả nước để ch&uacute;ng tắm t&aacute;p cũng chua l&eacute;t v&igrave;&nbsp;ph&egrave;n.&nbsp;M&agrave;, m&ugrave;a mưa vẫn c&ograve;n xa lắm.&nbsp;Ng&agrave;y ng&agrave;y, Điền rủ chị đi giăng lưới, nhắp c&aacute; v&agrave; t&aacute;t những c&aacute;i&nbsp;mương cạn. C&aacute; ăn kh&ocirc;ng hết, chị đem v&ocirc; x&oacute;m b&aacute;n v&agrave; h&atilde;nh diện&nbsp;đưa cho cha v&agrave;i mươi ng&agrave;n thừa sau khi đ&atilde; sắm &iacute;t quần &aacute;o. Mắt chị&nbsp;nh&igrave;n cha đầy khi&ecirc;u kh&iacute;ch</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;Cuộc đeo đuổi vẫn c&ograve;n d&agrave;i, cưng &agrave;...&rdquo;.&nbsp;Chị đổ l&igrave;. Chị t&igrave;m mọi c&aacute;ch để s&agrave; v&agrave;o cha. Một bữa chị bảo Điền&nbsp;xuống ghe ngủ với t&ocirc;i, c&ograve;n chị sẽ l&ecirc;n ch&ograve;i. Đ&oacute; l&agrave; một đ&ecirc;m tối nhờ&nbsp;nhờ, tr&ocirc;i tr&ecirc;n trời một mảnh trăng mỏng leo l&eacute;t. Điền ngọ nguậy,&nbsp;xoay trở li&ecirc;n tục, n&oacute; k&ecirc;u kh&oacute; ngủ, đ&ograve;i nghe t&ocirc;i h&aacute;t, b&agrave;i g&igrave; cũng&nbsp;được. Nhưng Điền vẫn thao thức, dường như giọng h&aacute;t t&ocirc;i kh&ocirc;ng&nbsp;&aacute;t được tiếng sột soạt rạo rực trong c&aacute;i ch&ograve;i nhỏ tr&ecirc;n bờ. Điền ch&ecirc;&nbsp;ngủ ghe ch&ograve;ng ch&agrave;nh qu&aacute; chừng. T&ocirc;i biết l&ograve;ng n&oacute; đang chao.&nbsp;Điền c&oacute; những ng&agrave;y bối rối. N&oacute; hay hỏi t&ocirc;i, &ldquo;người ta thương mẹ&nbsp;ra l&agrave;m sao?&rdquo;. Mặt n&oacute; d&atilde;n ra, khi biết c&acirc;y kẹp t&oacute;c, tr&aacute;i dừa tươi hay&nbsp;con c&aacute; th&aacute;c l&aacute;c... m&agrave; n&oacute; d&agrave;nh cho chị cũng giống hệt như người ta&nbsp;vẫn thường cho mẹ. V&agrave; niềm nhớ l&uacute;c đi xa, nỗi khao kh&aacute;t được&nbsp;nằm gần, được dụi mũi m&igrave;nh v&agrave;o da thịt người đ&oacute;... cũng tự nhi&ecirc;n&nbsp;như &yacute; nghĩ b&igrave;nh thường nhất của những đứa con. Nhưng những&nbsp;nghi hoặc vẫn loay hoay trong mắt Điền, v&agrave; n&oacute; quyết định chịu&nbsp;đựng một m&igrave;nh, kh&aacute;m ph&aacute; một m&igrave;nh. Th&iacute; dụ như đ&ecirc;m nay, c&aacute;i g&igrave;&nbsp;khiến tim ta đau nh&oacute;i, c&aacute;i g&igrave; l&agrave;m cho ta cảm thấy giận dữ, nặng nề&nbsp;?&nbsp;Khi t&ocirc;i thức dậy, Điền đ&atilde; mệt mỏi thiếp đi, n&oacute; nằm co quắp, hai tay&nbsp;kẹp giữa đ&ugrave;i, mặt buồn như phủ một lớp sương gi&aacute;. Chị bước ra&nbsp;khỏi ch&ograve;i, khoan kho&aacute;i vươn vai. Sự ưng &yacute;, m&atilde;n nguyện lấp l&aacute;nh&nbsp;tr&ecirc;n kho&eacute; mắt. Khu&ocirc;n mặt chị tr&agrave;n ngập &aacute;nh s&aacute;nh, như chị vừa mở&nbsp;ra một c&aacute;nh cửa mặt trời. C&oacute; một con đường n&agrave;o đ&oacute; tr&atilde;i d&agrave;i trước&nbsp;chị. Chị cười, bảo :</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- Hồi h&ocirc;m sương nhiều gh&ecirc;, n&oacute; nhiểu l&ecirc;n mặt chị ho&agrave;i, nhột gần&nbsp;chết.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Rồi chị gi&agrave;nh nấu cơm. Chị xoắn tay &aacute;o l&ecirc;n h&igrave; hụi thổi lửa, đầu t&oacute;c&nbsp;xấp x&atilde;i d&iacute;nh đầy vảy c&aacute;. Tr&ocirc;ng chị như b&agrave; vợ tảo tần. H&igrave;nh ảnh ấy&nbsp;l&agrave;m t&ocirc;i ứa nước mắt, nhưng cha t&ocirc;i chỉ lạt lẽo nhếch cười. V&igrave; c&aacute;i&nbsp;cười đ&oacute; m&agrave; t&ocirc;i ứa nước mắt th&ecirc;m lần nữa.&nbsp;Cha đưa chị một &iacute;t tiền ngay trong bữa cơm, khi nh&agrave; đủ mặt. </span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;T&ocirc;i&nbsp;trả cho hồi h&ocirc;m...&rdquo;. Rồi cha điềm nhi&ecirc;n phủi đ&iacute;t đủng đỉnh đứng&nbsp;l&ecirc;n, sự khinh miệt v&agrave; đắc thắng no n&ecirc; trong mắt. Chị nh&eacute;t tiền v&agrave;o&nbsp;trong &aacute;o ngực, cười</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;Trời ơi, ba mấy cưng sộp qu&aacute; chừng&rdquo;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Điền v&agrave; t&ocirc;i rủ chị đi c&acirc;u (Ch&uacute;ng t&ocirc;i cho l&agrave; chị buồn, d&ugrave; chuyện đ&oacute;&nbsp;kh&aacute; tức cười, l&agrave;m điếm được trả tiền m&agrave; kh&ocirc;ng d&iacute;nh con c&aacute; n&agrave;o, chị n&oacute;i.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;Mắc cười qu&aacute;, tới mấy con c&aacute; quỷ&nbsp;n&agrave;y c&ograve;n ch&ecirc; chị&rdquo;. C&acirc;u n&oacute;i nghe tỉnh bơ, b&acirc;ng quơ m&agrave; tủi v&ocirc;&nbsp;phương. Điền lặng bắt con c&aacute; r&ocirc; lội xuống mương, lặn s&acirc;u, m&oacute;c c&aacute;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">v&ocirc; lưỡi c&acirc;u của chị. L&uacute;c n&oacute; trồi đầu l&ecirc;n th&igrave; đ&atilde; thấy chị cười.&nbsp;Trưa ấy ch&uacute;ng t&ocirc;i trầm nghịch dưới nước rất l&acirc;u. Chị cười n&ocirc;n khi&nbsp;thấy b&ugrave;n b&aacute;m dưới mũi t&ocirc;i x&aacute;m xanh như bộ r&acirc;u củ ấu. Tự dưng&nbsp;n&eacute;t mặt chị bỗng &acirc;u yếm lạ, như đang nựng nịu một đứa b&eacute; con v&agrave;&nbsp;thằng em trai mười bảy tuổi của t&ocirc;i đứng đực ra, chết lặng trong&nbsp;nỗi ngượng ng&ugrave;ng. Nước cồn c&agrave;o chỗ bụng n&oacute;, t&ocirc;i biết chị đang&nbsp;t&aacute;o tợn l&agrave;m g&igrave; đ&oacute; ph&iacute;a dưới. Rồi ph&aacute;t hiện ra một mất m&aacute;t lớn lao,&nbsp;chị thảng thốt k&ecirc;u l&ecirc;n :</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- Trời đất ơi, sao vầy n&egrave;, cưng ?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Chị to&agrave;n hỏi những c&acirc;u hỏi kh&oacute;. Chỉ nghe th&ocirc;i đ&atilde; đau, n&oacute;i chi trả&nbsp;lời. Th&iacute; dụ như c&oacute; lần chị hỏi &ldquo;M&aacute; mấy cưng đ&acirc;u ?&rdquo;, &ldquo;Nh&agrave; mấy cưng&nbsp;ở chỗ n&agrave;o ?&rdquo;, thằng Điền đổ quạu :</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- Biết chết liền ! buồn nỗi g&igrave;.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n'),
(21, 'Cà phê cùng Tony', 'Văn học Việt Nam', 'Tony', 2017, 'ca-phe-cung-tony.jpg', 'Đã hoàn', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\">C&agrave; ph&ecirc; c&ugrave;ng Tony l&agrave; tập hợp những mẩu truyện ngắn được viết rất sinh động. C&oacute; những chuyện l&agrave; thật, cũng c&oacute; những chuyện l&agrave; ảo nhưng dưới ng&ograve;i b&uacute;t của Tony th&igrave; những c&acirc;u chuyện hư cấu cũng hiện l&ecirc;n sống động như chuyện nh&agrave; h&agrave;ng x&oacute;m s&aacute;t v&aacute;ch. Đơn giản bởi C&agrave; ph&ecirc; c&ugrave;ng Tony viết về những chuyện h&agrave;ng ng&agrave;y vẫn xảy ra trong ch&iacute;nh x&atilde; hội n&agrave;y. Dượng c&ograve;n n&oacute;i rằng c&oacute; độc giả đ&ograve;i x&eacute; s&aacute;ch v&igrave; trời ơi s&aacute;ch n&oacute;i đ&uacute;ng qu&aacute;, n&oacute;i tr&uacute;ng ph&oacute;c, động đến l&ograve;ng tự &aacute;i s&acirc;u c&ugrave;ng n&ecirc;n chỉ muốn x&eacute; s&aacute;ch cho b&otilde; tức.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#e74c3c\"><strong>Tập 1:&nbsp;</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\"><span style=\"background-color:#ffffff\">Sau khi t&ocirc;́t nghi&ecirc;̣p, Tony có đi m&acirc;̀n một thời gian ở c&ocirc;ng ty của Nh&acirc;̣t Bản. Một h&ocirc;m, c&ocirc;ng ty nó tái cơ c&acirc;́u. Các t&acirc;̣p đoàn đa qu&ocirc;́c gia l&acirc;u l&acirc;u nó re-structure m&ocirc;̣t cái, dẹp bỏ một vài vị trí. Th&ecirc;́ là nó đ&ecirc;̀n cho m&acirc;́y tháng lương, r&ocirc;̀i cho l&ecirc;n đường.</span></span><br />\r\n<br />\r\n<span style=\"color:#212529\"><span style=\"background-color:#ffffff\">Nhớ bữa làm cu&ocirc;́i, c&acirc;̀m cục ti&ecirc;̀n lớn n&ecirc;n vui lắm, bèn đi mua cái tủ lạnh. Ước mơ của cả thời sinh vi&ecirc;n là trong phòng ngủ có cái tủ lạnh đ&ecirc;̉ bỏ trái c&acirc;y, bia bọt vào u&ocirc;́ng cho mát. Nhi&ecirc;u ti&ecirc;̀n còn lại đi chơi, từ bắc chí nam, đ&ocirc;̣ng Phong Nha, H&ocirc;̣i An, Mỹ Sơn, Lăng C&ocirc;&hellip;gì cũng đi cho h&ecirc;́t. Đúng một tháng sau thì sạch túi. Cái nằm suy nghĩ, giờ phải làm sao đ&ecirc;̉ ki&ecirc;́m ti&ecirc;̀n đ&acirc;y. Bèn nhớ đ&ecirc;́n ngh&ecirc;̀ vi&ecirc;́t báo từ lúc còn là sinh vi&ecirc;n. Ngày nào vi&ecirc;́t cũng cả bốn, năm bài đủ mọi đ&ecirc;̀ tài, li&ecirc;n h&ecirc;̣ các anh trong ban bi&ecirc;n t&acirc;̣p đ&ecirc;̉ gửi lại. Bài được đăng đ&ecirc;̀u đặn, tháng cũng được đ&ocirc;i ba tri&ecirc;̣u. Nhưng s&ocirc;́ng ch&acirc;̣t v&acirc;̣t quá, làm freelance như phi&ecirc;n dịch, dạy học&hellip; kh&ocirc;ng có thu nh&acirc;̣p &ocirc;̉n định. Gửi h&ocirc;̀ sơ xin vi&ecirc;̣c thì như rải truy&ecirc;̀n đơn, c&ocirc;ng ty nào th&acirc;́y bảng đi&ecirc;̉m đẹp ng&acirc;́t ng&acirc;y, bằng loại giỏi n&ecirc;n mời l&ecirc;n phỏng v&acirc;́n ngay. Nhưng phỏng v&acirc;́n xong đ&ecirc;̀u từ ch&ocirc;́i, nói mày lanh quá sợ làm kh&ocirc;ng b&ecirc;̀n. Hoặc đẹp trai quá đ&ocirc;̀ng nghi&ecirc;̣p sẽ ganh tụy. N&ecirc;n đẹp và giỏi, lại là rào cản lớn đ&ecirc;̉ xin vi&ecirc;̣c thành c&ocirc;ng...&nbsp;</span></span><br />\r\n<br />\r\n<span style=\"color:#212529\"><span style=\"background-color:#ffffff\">M&acirc;́t cả năm th&acirc;́t nghi&ecirc;̣p. Bà già dưới qu&ecirc; gọi l&ecirc;n, nói con cứ l&ocirc;ng b&ocirc;ng hoài v&acirc;̣y, tìm vi&ecirc;̣c làm đi. Cái bu&ocirc;̀n d&ecirc;̃ sợ. Đ&acirc;u bao giờ nghĩ mình như vầy mà th&acirc;́t nghi&ecirc;̣p đ&acirc;u. Xin miết m&agrave; kh&ocirc;ng th&agrave;nh c&ocirc;ng n&ecirc;n đ&acirc;m ra mất tự tin. C&aacute;i một bữa đi coi b&oacute;i. Chị xem b&oacute;i t&ecirc;n Nguyệt ở B&ugrave;i Viện n&oacute;i số em n&oacute; vậy, em xin việc kh&ocirc;ng được v&igrave; số em l&agrave;m chủ. M&igrave;nh về mở cờ trong bụng nhưng cũng chẳng tin b&oacute;i to&aacute;n bao giờ. R&ocirc;̀i m&ocirc;̣t l&acirc;̀n đi Cambodia làm phi&ecirc;n dịch cho một c&ocirc;ng ty x&acirc;y dựng x&acirc;y con đường n&ocirc;́i thủ đ&ocirc; Phnompenh đ&ecirc;́n cửa kh&acirc;̉u Poipet, th&acirc;́y lúa b&ecirc;n đó sao x&acirc;́u quá. Mới nh&acirc;̣n ra là b&ecirc;n mình do dùng ph&acirc;n bón n&ecirc;n t&ocirc;́t tươi v&acirc;̣y, bèn v&ecirc;̀ nước mở hãng ph&acirc;n Phượng Tím, ban đầu kh&ocirc;ng c&oacute; vốn th&igrave; chọn giải ph&aacute;p l&agrave;m trung gian, l&agrave;m c&ograve; kiếm tiền chứ biết thế n&agrave;o, m&igrave;nh c&oacute; ai gi&uacute;p đỡ về tinh thần hay tiền bạc g&igrave; đ&acirc;u.</span></span><br />\r\n<br />\r\n<span style=\"color:#212529\"><span style=\"background-color:#ffffff\">N&oacute;i l&agrave; quyết t&acirc;m l&agrave;m. Ngày mở hãng, chạy qua mấy người bạn th&acirc;n mượn tiền để mua sắm thiết bị ban đầu. Bạn b&egrave; ng&agrave;y thường nghe Tony n&oacute;i chuyện vui th&igrave; th&iacute;ch lắm, cười ha hả nhưng đến l&uacute;c cần tiền mượn th&igrave; th&ocirc;i l&yacute; do đủ cả để từ chối. Nhiều bạn n&oacute;i chơi th&igrave; chơi, đừng mượn tiền v&igrave; tui đầu tư mua đất hết rồi; một c&ocirc; cũng gi&agrave;u c&oacute; lắm nhưng sợ mất, giả bộ n&oacute;i chồng chị kh&ocirc;ng đồng &yacute;, cuối c&ugrave;ng chỉ c&oacute; c&ocirc; Song H&ograve;a cho mượn ba triệu v&agrave; c&ocirc; Troang x&ugrave; k&ecirc;u chạy qua cư x&aacute; Đ&ocirc; Th&agrave;nh lấy hai triệu đi. Cầm năm triệu trong tay, nh&igrave;n hai c&ocirc; bạn học cũ m&agrave; muốn rớt nước mắt. L&uacute;c kh&oacute; khăn mới biết l&ograve;ng người. Hồi xưa &ocirc;ng cậu Tony c&oacute; n&oacute;i, muốn đ&aacute;nh gi&aacute; một con người dễ ẹt, đụng đến vấn đề tiền bạc th&igrave; bản chất thế n&agrave;o sẽ l&ograve;i ra thế ấy. Tony vẫn theo d&otilde;i hai c&ocirc; bạn ấy, một c&ocirc; b&acirc;y giờ sang Myanmar l&agrave;m tr&ugrave;m b&ecirc;n đ&oacute; về truyền th&ocirc;ng, c&ocirc; c&ograve;n lại mở hệ thống cửa h&agrave;ng thời trang lừng lẫy. Người tốt th&igrave; bao giờ cũng được trời thương, người cho đi th&igrave; sẽ được nhận lại nhiều hơn gấp vạn lần.</span></span><br />\r\n<br />\r\n<span style=\"color:#212529\"><span style=\"background-color:#ffffff\">Cầm tiền tr&ecirc;n tay, Tony chạy thẳng ra Metro mua cái máy in fax copy scan bốn trong một. R&ugrave;i đi tìm ngu&ocirc;̀n hàng. Tới c&ocirc;ng ty ph&acirc;n bón nào cũng tươi cười anh của em, chụy của em&hellip; ai ai cũng say m&ecirc;. Nói Tony ơi em d&ecirc;̃ thương quá à. Tony nói thấy tui d&ecirc;̃ thương thì cho trả thi&ecirc;́u đi. Họ đ&ocirc;̀ng ý li&ecirc;̀n, nói em l&acirc;́y hàng v&ecirc;̀ bán đi, m&acirc;́y tháng sau trả lại ti&ecirc;̀n cũng được, toàn đ&ecirc;̉ giá v&ocirc;́n. Cái mình bắt đ&acirc;̀u l&ecirc;n k&ecirc;́ hoạch đi bán hàng.</span></span><br />\r\n<br />\r\n<span style=\"color:#212529\"><span style=\"background-color:#ffffff\">Kh&ocirc;̉ n&ocirc;̃i, khách toàn ở tỉnh hoặc xa chứ n&ocirc;̣i thành ai xài ph&acirc;n bón thu&ocirc;́c trừ s&acirc;u. Mà ti&ecirc;̀n đ&acirc;u đi xu&ocirc;́ng đó ti&ecirc;́p thị, bèn chọn ngoại thành Sài Gòn và m&acirc;́y tỉnh l&acirc;n c&acirc;̣n ti&ecirc;́p thị trước. Cứ ban ngày đi bán hàng, t&ocirc;́i v&ecirc;̀ làm gi&acirc;́y tờ hợp đ&ocirc;̀ng đ&ecirc;́n khuya. Cứ sáng sáng, Tony u&ocirc;́ng một ly cà ph&ecirc; to khủng khi&ecirc;́p đ&ecirc;̉ tỉnh ngủ, r&ocirc;̀i phóng chi&ecirc;́c Wave Alpha đỏ, treo lủng lẳng bịch m&acirc;̃u và m&acirc;́y tờ rơi, phóng xu&ocirc;́ng các huy&ecirc;̣n Bình Chánh, Hóc M&ocirc;n, Nhà Bè hay Bình Dương, Đ&ocirc;̀ng Nai đ&ecirc;̉ chào hàng. Thường thì hẹn m&acirc;́y đại lý cùng một tuy&ecirc;́n đường cho nó ti&ecirc;̣n, trưa thì tạt v&ocirc; ăn cơm ở quán l&ecirc;̀ đường. Những bu&ocirc;̉i trưa nắng nóng, bụi mịt mùng, Tony đen thui đen thít, dáng cao g&acirc;̀y v&acirc;̃n phóng xe vun vút. Lúc đó ước mơ có được chi&ecirc;́c xe bốn bánh, dù là xe tải cũng được. Cứ nhìn theo chi&ecirc;́c xe hơi chạy qua mà thèm, mà nhớ đ&ecirc;́n khi xưa mình là nh&acirc;n vi&ecirc;n t&acirc;̣p đoàn thương mại hàng đ&acirc;̀u của Nh&acirc;̣t, một bước l&ecirc;n xe hơi taxi, đi đ&acirc;u cũng sang trọng quý phái&hellip; giờ như &ocirc;ng xe &ocirc;m, thỉnh thoảng cũng tủi th&acirc;n. Nhưng k&ecirc;̣, miễn có ti&ecirc;̀n là được, mình lao đ&ocirc;̣ng bằng sức lực và trí tu&ecirc;̣ của mình mà, chứ có l&acirc;́y của ai. B&acirc;́t ch&acirc;́p cái nắng nhi&ecirc;̣t đới chói chang như đ&ocirc;̉ lửa, b&acirc;́t ch&acirc;́p những bu&ocirc;̉i trưa ba mươi s&aacute;u, ba mươi bảy độ và bụi bay mịt mùng, gương mặt anh &acirc;́y v&acirc;̃n cứ thanh tú. Và v&acirc;̃n ki&ecirc;u sa h&acirc;́t mặt l&ecirc;n trời như cái thời sinh vi&ecirc;n đi xe đạp. H&ocirc;̀i đó, tụi cùng lớp nói sao mày ở tỉnh l&ecirc;n, nhà nghèo th&acirc;́y mẹ, đen thui &ocirc;́m nhách x&acirc;́u bà c&ocirc;́, xe đạp thì cái bàn đạp rớt m&acirc;́t, chỉ còn hai cái thanh sắt láng bóng nhọt hoắt, mà cứ vừa đi vừa h&acirc;́t mặt l&ecirc;n trời. Tụi tao đi &lsquo;Đờ Rim&rsquo; hay 86 &ldquo;kim vàng giọt l&ecirc;̣&rdquo; mà c&ograve;n chưa dám nữa là.</span></span><br />\r\n<br />\r\n<span style=\"color:#212529\"><span style=\"background-color:#ffffff\">Hẻm bi&ecirc;́t vì sao từ nhỏ xíu, Tony tự mình cho là t&agrave;i giỏi hơn người n&ecirc;n tự tin hơi over một chút. M&ocirc;̃i l&acirc;̀n đi thi mà rớt héng, là nói chắc ban giám khảo hẻm đủ trình đ&ocirc;̣. Hay th&acirc;̀y c&ocirc; nào dạy gì mới, cũng nghi ngờ, v&ecirc;̀ phải l&acirc;̣t sách coi lại. Gi&acirc;́y khen học sinh giỏi là vứt m&acirc;́t, v&igrave; mình là hạc sinh xu&acirc;́t sắc chứ đ&acirc;u phải học sinh giỏi m&agrave; khen giỏi. N&ecirc;n tự g&acirc;y áp lực, nói sau này phải giàu, giỏi và đẹp mới hợp thức hóa được cái vi&ecirc;̣c chảnh đ&acirc;́y. N&ecirc;n sau này phải đi xe &ocirc; t&ocirc;, vì ng&ocirc;̀i trong xe, có h&acirc;́t cái mặt l&ecirc;n trời thì kh&ocirc;ng bị ăn nắng da đen thui đen thít. Ở Việt Nam, da trắng thì mới sang trọng&hellip;</span></span><br />\r\n<br />\r\n<strong><span style=\"color:#e74c3c\"><span style=\"background-color:#ffffff\">T&acirc;̣p 2: Đ&ocirc;̉ bệnh</span></span></strong><br />\r\n<br />\r\n<span style=\"color:#212529\"><span style=\"background-color:#ffffff\">Mặc dù là hạc giỏi, n&ecirc;́u chịu khó thi th&ocirc;́ thì chắc cũng ki&ecirc;́m được hạc b&ocirc;̉ng tàn ph&acirc;̀n. Nhưng lúc đó nghĩ sao th&ocirc;i ở nhà ki&ecirc;́m ti&ecirc;̀n, rùi vài năm sau đi du hạc cũng chẳng mu&ocirc;̣n, mặc dù r&acirc;́t thèm. Lớp của Tony, đi du hạc h&ecirc;́t m&acirc;́y chục bạn, v&ecirc;̀ k&ecirc;̉ nghe cu&ocirc;̣c s&ocirc;́ng sinh vi&ecirc;n xứ người mà thèm rỏ dãi. Đ&ecirc;m đ&ecirc;m cứ nằm mơ, th&acirc;́y mình đang nằm sõng xoài tr&ecirc;n bãi cỏ, hay tung cái mũ cử nh&acirc;n l&ecirc;n trời ngước l&ecirc;n chụp hình, gi&ocirc;́ng hình chụp trong m&acirc;́y catalogue quảng cáo. Nhưng sáng mơi tỉnh d&acirc;̣y, v&acirc;̃n cái máng lợn b&ecirc;n mình. Lại u&ocirc;́ng ly cà ph&ecirc; to đùng và t&acirc;́t tả đi bán ph&acirc;n bán thu&ocirc;́c...</span></span><br />\r\n<br />\r\n<span style=\"color:#212529\"><span style=\"background-color:#ffffff\">Mùa nóng thì chạy xe ngày m&acirc;́y chục c&acirc;y rát bỏng cả da. Cởi &aacute;o ra th&igrave; &frac12; c&aacute;nh tay đen thui. Mùa mưa thì th&ocirc;i, áo mưa áo gió bùng nhùng, nhưng chạy phải đua t&ocirc;́c đ&ocirc;̣ cho nhanh vì một ngày phải ti&ecirc;́p thị m&acirc;́y khách. N&ecirc;n giờ ở Đ&ocirc;ng Nam B&ocirc;̣, đường nào cũng rành, đường tắt thì giỏi hơn cả xe &ocirc;m. Có bữa mưa gió s&acirc;́m sét đùng đùng, chạy từ khu Sóng Th&acirc;̀n l&ecirc;n tr&ecirc;n huy&ecirc;̣n T&acirc;n Uy&ecirc;n, đường đ&acirc;́t đỏ l&acirc;̀y l&ocirc;̣i giữa rừng cao su, chỉ có m&ocirc;̃i mình mình và m&acirc;́y chi&ecirc;́c xe tải, nước mưa như roi qu&acirc;́t vào mặt. Nhưng bản th&acirc;n kh&ocirc;ng sợ ma, kh&ocirc;ng sợ cướp, chỉ sợ mưa gi&oacute; như vậy sẽ ướt m&acirc;́y bịch ph&acirc;n m&acirc;̃u, khách coi th&acirc;́y ch&ecirc; hẻm mua. Một ng&agrave;y ngồi xe m&aacute;y cả mấy tiếng, về đến nh&agrave; tay cầm ch&eacute;n cơm m&agrave; cứ rung rung theo chu kỳ h&uacute;t-n&eacute;n-nổ-xả của động cơ xe m&aacute;y. Ăn cơm kh&ocirc; kh&ocirc;ng nổi, n&ecirc;n phải nấu canh, cứ bỏ cơm v&ocirc; canh m&agrave; h&uacute;p, cho cơm n&oacute; tr&ocirc;i v&ocirc; miệng chứ sợ kh&ocirc;ng c&oacute; sức khỏe l&agrave;m việc.</span></span><br />\r\n<br />\r\n<span style=\"color:#212529\"><span style=\"background-color:#ffffff\">Được cái là đi ti&ecirc;́p thị khi mưa gió t&acirc;̀m tã v&acirc;̣y, ghé đại lý ai cũng đ&ocirc;̣ng lòng, nói ở lại chị n&acirc;́u cháo gà cho ăn, ở lại u&ocirc;́ng với anh chén rượu. Cái mình cám ơn vì phải v&ecirc;̀ sớm, t&ocirc;́i còn phải v&ecirc;̀ nhà làm hợp đ&ocirc;̀ng, xu&acirc;́t hóa đơn, coi ti&ecirc;̀n bạc thu chi th&ecirc;́ nào&hellip; vì chỉ có một mình mình làm, cả m&acirc;́y tháng sau mới tuy&ecirc;̉n được m&acirc;́y đứa sinh vi&ecirc;n làm th&ecirc;m v&ocirc; phụ. Cu&ocirc;́i tu&acirc;̀n, tụi bạn làm văn phòng nước ngoài ở qu&acirc;̣n một rủ đi bar hay nh&acirc;̣u, ng&ocirc;̀i chung một đám, th&ecirc;́ nào mình cũng n&ocirc;̉i b&acirc;̣t vì nét già nua, đen đúa và h&ocirc;́c hác, duy chỉ có nụ cười v&acirc;̃n vui vẻ và v&acirc;̃n chỉ huy mọi trò t&ecirc;́u táo trong lúc ăn nh&acirc;̣u, cho đời nó hưng ph&acirc;́n&hellip;</span></span></span></span></p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(22, 'Cuốn theo chiều gió', 'Văn học nước ngoài', 'Margaret Mitchell', 1936, 'cuon-theo-chieu-gio.jpg', 'Đã hoàn', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Lấy bối cảnh từ cuộc nội chiến v&ocirc; c&ugrave;ng khốc liệt giữa Bắc v&agrave; Nam Mỹ,&nbsp;Cuốn Theo Chiều Gi&oacute;&nbsp;với cốt truyện r&otilde; r&agrave;ng, logic, dễ hiểu, đ&atilde; khắc họa một c&aacute;ch t&agrave;i t&igrave;nh t&acirc;m trạng, t&iacute;nh c&aacute;ch v&agrave; th&acirc;n phận của nhiều lớp người trong chiến tranh v&agrave; thời hậu chiến. Nh&acirc;n vật ch&iacute;nh của tiểu thuyết l&agrave; c&ocirc; g&aacute;i Scarlett O&#39;hara c&ugrave;ng với Rhett Butler trở th&agrave;nh cặp nh&acirc;n vật điển h&igrave;nh, thuộc loại th&agrave;nh c&ocirc;ng nhất trong văn học Hoa Kỳ. Cuốn Theo Chiều Gi&oacute;&nbsp;c&oacute; sức hấp dẫn m&atilde;nh liệt giới trẻ Mỹ cũng như thanh ni&ecirc;n to&agrave;n thế giới v&igrave; đ&acirc;y l&agrave; cuốn tiểu thuyết t&igrave;nh y&ecirc;u đặc sắc. Lạ kỳ thay, trong chiến tranh v&agrave; những năm hậu chiến v&ocirc; c&ugrave;ng gian khổ, t&igrave;nh y&ecirc;u lại lu&ocirc;n lu&ocirc;n ch&oacute;i ngời, trở th&agrave;nh động lực gi&uacute;p cho con người vượt qua chết ch&oacute;c, đ&oacute;i khổ v&agrave; sự h&egrave;n hạ... Kh&ocirc;ng chỉ c&oacute; t&igrave;nh y&ecirc;u trai g&aacute;i,&nbsp;Cuốn Theo Chiều Gi&oacute;&nbsp;c&ograve;n l&agrave; b&agrave;i ca của t&igrave;nh y&ecirc;u qu&ecirc; hương đất nước, t&igrave;nh tương th&acirc;n tương &aacute;i. Ba năm sau khi tiểu thuyết&nbsp;Cuốn Theo Chiều Gi&oacute;&nbsp;ra đời, bộ phim c&ugrave;ng t&ecirc;n dựng theo t&aacute;c phẩm của Margaret Mitchell được c&ocirc;ng chiếu đ&atilde; trở th&agrave;nh sự kiện lớn, th&agrave;nh niềm tự h&agrave;o của điện ảnh Mỹ.</span></span></p>\r\n', '<p style=\"text-align:justify\"><span style=\"color:#e74c3c\"><strong><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">CHƯƠNG I</span></span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Scarlett O&quot; Hara kh&ocirc;ng đẹp, nhưng nam giới &iacute;t nhận ra điều đ&oacute; mỗi khi bị cuốn h&uacute;t bởi sự duy&ecirc;n d&aacute;ng của n&agrave;ng, như trường hợp hai anh em song sinh Tarleton . Khu&ocirc;n mặt n&agrave;ng l&agrave; một sự kết hợp h&agrave;i ho&agrave; giữa những đường n&eacute;t kiều diễm của mẹ, người qu&yacute; tộc miền duy&ecirc;n hải thuộc d&ograve;ng d&otilde;i Ph&aacute;p, v&agrave; những g&oacute;c cạnh th&ocirc; kệch của người cha &Aacute;i Nhĩ Lan, da dẻ hồng h&agrave;o. Tuy thế, đ&oacute; l&agrave; khu&ocirc;n mặt ưa nh&igrave;n với cằm thon, h&agrave;m nở rộng. Đ&ocirc;i mắt xanh biếc của n&agrave;ng được viền bởi những h&agrave;ng nở rộng. Đ&ocirc;i mắt xanh biếc của n&agrave;ng được viền bởi những h&agrave;ng mi d&agrave;i rậm uốn cong v&uacute;t . B&ecirc;n tr&ecirc;n đ&ocirc;i mắt đ&oacute; l&agrave; hai vệt m&agrave;y ch&ecirc;nh chếch vạch th&agrave;nh hai đường nghi&ecirc;ng đậm n&eacute;t tr&ecirc;n l&agrave;n da trắng trong của hoa mộc lan- m&agrave;u da m&agrave; phụ nữ miền Nam v&ocirc; c&ugrave;ng qu&yacute; trọng v&agrave; cẩn thận giữ g&igrave;n bằng những chiếc n&oacute;n rộng v&agrave;nh, mạng che mặt v&agrave; bao tay để chống lại &aacute;nh nắng gay gắt của xứ Georgia.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ngồi với Stuart v&agrave; Brent Tarleton dưới b&oacute;ng m&aacute;t của m&aacute;i hi&ecirc;n Tara, đồn điền của cha n&agrave;ng, giữa một chiều nắng th&aacute;ng Tư năm 1861, Scarlett l&agrave; hiện th&acirc;n của h&igrave;nh ảnh y&ecirc;u kiều Chiếc &aacute;o mới bằng sa- tanh c&oacute; điểm hoa xo&egrave; rộng mười một thước tr&ecirc;n những viền đai, xứng hợp ho&agrave;n to&agrave;n với đ&ocirc;i gi&agrave;y da b&ecirc; thuộc m&agrave;u xanh m&agrave; cha n&agrave;ng vừa mua từ Atlanta Chiếc &aacute;o l&agrave;m nổi bật tột c&ugrave;ng c&aacute;i v&ograve;ng eo bốn mươi hai ph&acirc;n rưỡi, v&ograve;ng eo thon mảnh nhứt của địa hạt xứ Georgia, v&agrave; chiếc yếm b&oacute; s&aacute;t vừa vặn để lộ bộ ngực tr&ograve;n trịa của một thiếu nữ 16 tuổi ? Tuy nhi&ecirc;n, dẫu t&agrave; &aacute;o đ&atilde; được kh&eacute;p k&iacute;n quanh, dẫu m&aacute;i t&oacute;c l&aacute;ng mịn đ&atilde; được b&uacute;i gọn đ&uacute;ng vẻ trang nghi&ecirc;m, v&agrave; dẫu hai b&agrave;n tay trắng nhỏ đ&atilde; được xếp tr&eacute;o tr&ecirc;n đ&ugrave;i, Scarlett cũng kh&ocirc;ng che giấu nổi con người thật sự của n&agrave;ng tr&ecirc;n khu&ocirc;n mặt cố giữ n&eacute;t đoan trang, đ&ocirc;i mắt m&agrave;u lam vẫn kh&ocirc;ng ngớt l&oacute;ng l&aacute;nh &aacute;nh nhiệt cuồng, bướng bỉnh v&agrave; kh&aacute;t sống, tương phản r&otilde; rệt với c&aacute;i vẻ bề ngo&agrave;i cố l&agrave;m ra thuỳ mị. Phong c&aacute;ch của n&agrave;ng l&agrave; kết tinh những lời khuy&ecirc;n dạy của mẹ v&agrave; kỷ luật nghi&ecirc;m khắc của mammy, nhưng đ&ocirc;i mắt vẫn l&agrave; mắt của ri&ecirc;ng n&agrave;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Mỗi người một b&ecirc;n Scarlett, hai anh em sinh đ&ocirc;i nh&agrave;n nh&atilde; ngồi tr&ecirc;n ghế, vừa n&oacute;i cười, vừa lơ đ&atilde;ng nh&igrave;n &aacute;nh nắng chiếu xuy&ecirc;n hai ly rượu ngọt bạc h&agrave; to tướng, v&agrave; hững hờ bắt tr&eacute;o đ&ocirc;i ch&acirc;n d&agrave;i mang gi&agrave;y ống cao tới gối, cuồn cuộn nhưng bắp thịt rắn chắc của người quen sống tr&ecirc;n lưng ngựa. C&ugrave;ng 19 tuổi, c&ugrave;ng cao 1m85, xương d&agrave;i thịt chắc, hai anh em c&ugrave;ng giống nhau như hai b&agrave;nh b&ocirc;ng vải, cũng những khu&ocirc;n mặt sạm nắng, cũng t&oacute;c đỏ hoe, cũng &aacute;nh mắt tươi vui ngạo nghễ, cũng mặc một loại &aacute;o m&agrave;u xanh v&agrave; quần kỵ m&atilde; m&agrave;u hột cải.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">B&ecirc;n ngo&agrave;i, nắng chiều chiếu nghi&ecirc;ng xuống khoảnh s&acirc;n, n&eacute;m những tia s&aacute;ng ch&oacute;i chang l&ecirc;n h&agrave;ng c&acirc;y dương đ&agrave;o với từng ch&ugrave;m hoa trắng tr&ecirc;n bối cảnh xanh um. Ngựa của 2 anh em song sinh được cột ngo&agrave;i m&atilde; lộ, 2 con cao lớn bộ l&ocirc;ng hung đỏ kh&ocirc;ng kh&aacute;c g&igrave; t&oacute;c chủ. Vơ vẩn quanh ch&acirc;n ngựa l&agrave; một lũ ch&oacute; mảnh khảnh, hung hăng, đang gầm gừ nhau, v&agrave; bất cứ l&uacute;c n&agrave;o cũng theo s&aacute;t Stuart với Brent. Xa hơn 1 ch&uacute;t, dường như để ra điều qu&yacute; ph&aacute;i, một con ch&oacute; l&ocirc;ng lốm đố đen nằm g&aacute;c m&otilde;m l&ecirc;n hai ch&acirc;n trước, nhẫn nại đợi chủ trở về d&ugrave;ng bữa tối.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Giữa đ&agrave;n ch&oacute;, đ&ocirc;i ngựa v&agrave; hai anh em song sinh nảy sinh 1 mối th&acirc;n t&igrave;nh c&ograve;n s&acirc;u đậm hơn cả mối t&igrave;nh của những kẻ lu&ocirc;n khắng kh&iacute;t b&ecirc;n nhau. Tất cả đều cuờng tr&aacute;ng, v&ocirc; tư, l&agrave;nh lặn duy&ecirc;n d&aacute;ng v&agrave; dũng cảm, hai g&atilde; con trai cũng hung hăng như ngựa họ, hung hăng v&agrave; nguy hiểm nhưng đồng thời cũng ngoan ngo&atilde;n với những ai kh&eacute;o xử.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Mặc dầu đuợc sinh trưởng giữa cuộc sống dễ d&atilde;i ở đồn điền, được săn s&oacute;c hầu hạ từ l&uacute;c b&eacute; nhưng cả 3 khu&ocirc;n mặt duới m&aacute;i hi&ecirc;n kh&ocirc;ng c&oacute; g&igrave; uể oải hay mềm yếu. Họ vẫn c&oacute; đủ nghị lực v&agrave; sự mẫn tiệp của d&acirc;n qu&ecirc; đ&atilde; từng quen sống ngo&agrave;i trời v&agrave; kh&ocirc;ng một mảy may bận tr&iacute; bởi những điều nhạt nhẽo trong s&aacute;ch vở. Cuộc sống ở hạt Clay ton, miền Bắc Georgia h&atilde;y c&ograve;n mới mẻ v&agrave; c&oacute; phần sống sượng so với tr&igrave;nh độ ở c&aacute;c hạt Augusta, Savannah v&agrave; Charleston. Người miền Nam kỳ cựu v&agrave; nghi&ecirc;m trang nhất thường nh&igrave;n d&acirc;n sống ở cao nguy&ecirc;n Georgia với con mắt khinh thường, nhưng ở đ&acirc;y tại miền Bắc Georgia, vấn đề thiếu s&oacute;t những điểm cầu kỳ trong nền gi&aacute;o dục cổ điển chẳng c&oacute; g&igrave; đ&aacute;ng cho l&agrave; xấu hổ, miễn l&agrave; người đ&agrave;n &ocirc;ng c&oacute; đủ khả năng để thực hiện những việc th&iacute;ch nghi. V&agrave; trồng b&ocirc;ng vải, cuỡi ngựa hay bắn giỏi, khi&ecirc;u vũ nhẹ nh&agrave;ng, biết hầu chuyện nữ giới một c&aacute;ch lịch thiệp v&agrave; biết uống ruợu như một kh&aacute;ch h&agrave;o hoa l&agrave; tất cả những g&igrave; đ&aacute;ng kể.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Về c&aacute;c phương diện đ&oacute;, hai anh em Tarleton tỏ ra xuất sắc, lại c&ograve;n vượt bực hơn với sự bất lực r&otilde; r&agrave;ng về chuyện kh&ocirc;ng thể th&acirc;u thập nổi bất cứ những g&igrave; chứa đựng trong s&aacute;ch vở. Gia đ&igrave;nh họ qu&aacute; gi&agrave;u, nu&ocirc;i nhiều ngựa v&agrave; nhiều n&ocirc; lệ hơn bất cứ ai trong to&agrave;n hạt, nhưng cả hai th&igrave; lại &iacute;t học hơn bất cứ người Cracker(người da trắng ngh&egrave;o khổ ở miền Nam Hoa Kỳ) n&agrave;o chung quanh.Đ&oacute; l&agrave; l&yacute; do ch&aacute;nh yếu đ&atilde; khiến cho Stuart v&agrave; Brent nh&agrave;n hạ giết th&igrave; giờ ở cổng đồn điền Tara v&agrave;o buổi chiều th&aacute;ng Tư n&agrave;y. Họ vừa bị trục xuất ra khỏi Đại học Georgia, trường đại học thứ tư trong v&ograve;ng 2 năm đ&atilde; đuổi họ ra khỏi cửa, v&agrave; anh em của họ l&agrave; Tom v&agrave; Boyd cũng bỏ học, c&ugrave;ng về, bởi v&igrave; họ kh&ocirc;ng thể tiếp tục ở lại nơi đ&atilde; từng thẳng tay xử tệ với hai anh em song sinh. Đối với Stuart v&agrave; Brent th&igrave; lần bị đuổi học sau c&ugrave;ng n&agrave;y chỉ l&agrave; một tr&ograve; đ&ugrave;a, v&agrave; Scarlett, kể từ khi rời khỏi nữ học x&aacute; Phayetteville, kh&ocirc;ng buồn lật qua một trang s&aacute;ch n&agrave;o nữa cả, cũng cho đ&oacute; l&agrave; một điều th&uacute; vị.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">N&agrave;ng n&oacute;i:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&minus; T&ocirc;i biết 2 anh bất cần chuyện đuổi học, cả anh Tom cũng vậy. Nhưng chẳng biết anh Boyd sẽ ra sao? Anh ấy hiếu học, nhưng hai anh lại l&agrave;m cho anh ấy lần lượt phải bỏ học ở Virginia, Alabama,</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">South Carolina, v&agrave; b&acirc;y giờ tới Georgia. Cứ c&aacute;i đ&agrave; n&agrave;y, chắc chẳng bao giờ anh ấy tốt nghiệp đuợc.Brent hững hờ:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&minus; Ồ, c&oacute; g&igrave; đ&acirc;u . Anh ấy c&oacute; thể học luật tại văn ph&ograve;ng thẩm ph&aacute;n Parmalee ở Phayetteville. Vả lại, chuyện cũng chẳng c&oacute; g&igrave; quan trọng. Đằng n&agrave;o th&igrave; bọn n&agrave;y cũng phải trở về trước khi hết học tr&igrave;nh.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&minus; Sao vậy ?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&minus; Chiến tranh chớ c&ograve;n sao nửa C&ocirc; b&eacute; khờ, chiến tranh sắp sửa b&ugrave;ng nổ bất cứ khi n&agrave;o. C&ocirc; tưởng c&oacute; đứa n&agrave;o trong bọn n&agrave;y sẽ chịu đi học trong khi chiến tranh diễn ra, phải kh&ocirc;ng?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Scarlett kh&oacute; chịu:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&minus; C&aacute;c anh dư biết l&agrave; kh&ocirc;ng c&oacute; chiến tranh m&agrave; chỉ n&oacute;i cho c&oacute; chuyện th&ocirc;i. Nếu c&oacute; th&igrave; tại sao anh Asley Quilkes v&agrave; ba anh ấy n&oacute;i với ba t&ocirc;i l&agrave; mấy &ocirc;ng đặc uỷ của m&igrave;nh tại Hoa Thịnh Đốn tuần tra đ&atilde; đi tới một thoả hiệp với &ocirc;ng Lincoln về ch&aacute;nh s&aacute;ch Li&ecirc;n Bang. Vả lại, bọn Yankee qu&aacute; sợ m&igrave;nh th&igrave; l&agrave;m sao d&aacute;m đ&aacute;nh. Nhứt định l&agrave; kh&ocirc;ng c&oacute; chiến tranh đ&acirc;u, t&ocirc;i ch&aacute;n nghe chuyện đ&oacute; lắm rồi.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Cả 2 anh em song sinh đều bất b&igrave;nh k&ecirc;u l&ecirc;n dường như họ đ&atilde; bị đ&aacute;nh lừa:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&minus; Kh&ocirc;ng c&oacute; chiến tranh!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">V&agrave; Stuart tiếp ngay:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&minus; C&oacute; chớ, cưng ơi, nhứt định sắp c&oacute; chiến tranh. C&oacute; lẽ bọn Yankee vẫn sợ m&igrave;nh, nhưng qua c&aacute;i lối Đại tướng Beauregard n&atilde; đại b&aacute;c đẩy bật ch&uacute;ng ra khỏi căn cứ Sumter, ch&uacute;ng bắt buộc sẽ phải đ&aacute;nh&nbsp;trả nếu kh&ocirc;ng muốn cả thế giới coi ch&uacute;ng l&agrave; những t&ecirc;n khiếp nhược. Tại sao, Li&ecirc;n bang...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Scarlett m&iacute;m m&ocirc;i, bực tức:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&minus; Nếu c&aacute;c anh n&oacute;i tới &quot;chiến tranh&quot; một lần nữa, t&ocirc;i sẽ v&agrave;o nh&agrave; v&agrave; đ&oacute;ng cửa lại ngay. Chưa bao giờ t&ocirc;i ch&aacute;n nản tiếng n&agrave;o hơn tiếng &quot;chiến tranh&quot; ngoại trừ tiếng &quot;ph&acirc;n ly&quot;. Hết s&aacute;ng tới trưa v&agrave; tối, ba kh&ocirc;ng ngớt n&oacute;i chuyện chiến tranh, v&agrave; bao nhi&ecirc;u kh&aacute;ch tới nh&agrave; cũng b&ocirc; b&ocirc; n&oacute;i về chuyện căn cứ Sumter, về quyền lợi của c&aacute;c Tiểu bang v&agrave; &ocirc;ng Abe Lincoln đến nổi t&ocirc;i ch&aacute;n ngấy muốn h&eacute;t l&ecirc;n. Rồi bọn con trai cũng n&oacute;i vậy v&agrave; nhắc chuyện bộ đội xưa cũ của họ. Cứ vậy m&agrave; m&ugrave;a xu&acirc;n n&agrave;y chẳng c&oacute; một hội h&egrave; n&agrave;o cho ra đ&aacute;m, bởi v&igrave; bọn con trai chẳng c&oacute; g&igrave; để n&oacute;i kh&aacute;c hơn. T&ocirc;i hết sức bằng l&ograve;ng Georgia đ&atilde; chờ đến qua lễ Gi&aacute;ng Sinh mới ly khai, nếu kh&ocirc;ng th&igrave; mấy cuộc vui dịp Gi&aacute;ng Sinh đều hỏng cả. Nếu c&aacute;c anh lặp lại lần nữa tiếng &quot;chiến tranh&quot; l&agrave; t&ocirc;i v&agrave;o nh&agrave; ngay.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Scarlett sẽ l&agrave;m đ&uacute;ng như đ&atilde; n&oacute;i v&igrave; rằng n&agrave;ng kh&ocirc;ng thể chấp nhận được bất cứ một c&acirc;u chuyện n&agrave;o m&agrave; trong đ&oacute; n&agrave;ng kh&ocirc;ng phải l&agrave; chủ đề. Tuy vậy, n&agrave;ng vẫn mỉm cười trong khi n&oacute;i, cố l&agrave;m cho đồng tiền ở m&aacute; l&uacute;m s&acirc;u th&ecirc;m, v&agrave; chớp nhanh h&agrave;ng mi đen rậm như c&aacute;nh bướm. Đ&uacute;ng như n&agrave;ng mong mỏi, cả hai đều thoả th&iacute;ch v&agrave; vội v&agrave;ng xin lỗi đ&atilde; l&agrave;m người đẹp mất vui. Họ kh&ocirc;ng hề buồn phiền ch&uacute;t n&agrave;o về việc n&agrave;ng kh&ocirc;ng th&iacute;ch th&uacute; nghe chuyện của m&igrave;nh. Chiến tranh l&agrave; việc của đ&agrave;n &ocirc;ng chớ kh&ocirc;ng phải của nữ giới, v&agrave; họ xem th&aacute;i độ của n&agrave;ng như một dấu hiệu hiển nhi&ecirc;n của nữ t&iacute;nh .</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Sau khi đưa hai người bạn trai ra khỏi c&acirc;u chuyện nh&agrave;m ch&aacute;n về chiến tranh, Scarlett k&eacute;o vấn đề trở lại hiện t&igrave;nh của họ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&minus; Hai anh bị đuổi học, b&aacute;c g&aacute;i c&oacute; n&oacute;i g&igrave; kh&ocirc;ng?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Hai anh em sanh đ&ocirc;i l&agrave;m ra l&uacute;ng t&uacute;ng, nhớ lại th&aacute;i độ của b&agrave; mẹ ba th&aacute;ng trước khi họ bị trục xuất khỏi Đại học Virginia.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Stuart đ&aacute;p:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&minus; Ơ, bả chưa kịp n&oacute;i g&igrave; cả. Tom v&agrave; tụi n&agrave;y rời nh&agrave; l&uacute;c s&aacute;ng sớm trước khi bả dậy, Tom th&igrave; đi qua chơi b&ecirc;n gia đ&igrave;nh Phontaine c&ograve;n tụi nầy đi thẳng lại đ&acirc;y.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&minus; Đ&ecirc;m qua, l&uacute;c mấy anh về tới, b&aacute;c cũng kh&ocirc;ng n&oacute;i g&igrave; sao?</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&minus; Đ&ecirc;m qua, tụi nầy gặp vận may. Ngay trước khi bọn nầy về con ngựa giống m&agrave; m&aacute; đặt mua ở Kentucky th&aacute;ng trước được đưa tới, v&agrave; cả nh&agrave; n&aacute;o động l&ecirc;n. Đ&uacute;ng l&agrave; con ngựa phi thường, Scarlett. C&ocirc;&nbsp;phải n&oacute;i với ba c&ocirc; qua xem cho biết... dọc đường n&oacute; cắn thằng n&agrave;i v&agrave; đạp nhầu hai thằng hắc n&ocirc; m&agrave; m&aacute; sai ra đ&oacute;n ở ga xe lửa Jonesboro. V&agrave; ngay trước khi bọn nầy về tới, n&oacute; đ&atilde; ph&aacute; tan hoang t&agrave;u ngựa v&agrave; su&yacute;t giết chết con Straquberry, con ngựa n&ograve;i gi&agrave; của m&aacute;. L&uacute;c tụi nầy về tới, m&aacute; đang ở ngo&agrave;i t&agrave;u ngựa với cả một bịch đường để nhử n&oacute; v&agrave; đ&atilde; th&agrave;nh c&ocirc;ng kh&ocirc;ng mấy kh&oacute;. Mấy đứa hắc n&ocirc; th&igrave; đeo cứng tr&ecirc;n x&agrave; t&agrave;u ngựa, mắt đứng tr&ograve;ng, bọn n&oacute; sợ dữ lắm, c&ograve;n m&aacute; th&igrave; nhỏ to b&ecirc;n tai con th&uacute; l&agrave;m như l&agrave; bạn cũ. Trong khi đ&oacute;, con ngựa đứng y&ecirc;n ăn đường của b&agrave; gi&agrave; đ&uacute;t cho. Chưa c&oacute; ai trị ngựa bằng m&aacute;. V&agrave; vừa thấy mặt tụi nầy, m&aacute; h&eacute;t lớn : &quot;Trời ơi! Bốn đứa m&agrave;y lại về nh&agrave; l&agrave;m g&igrave; nữa đ&acirc;y? Tụi b&acirc;y c&ograve;n độc hại hơn bịnh dịch ở b&ecirc;n Ai cập!&quot;. Vậy l&agrave; con ngựa bắt đầu khịt khịt v&agrave; chồm l&ecirc;n. B&agrave; gi&agrave; lại h&eacute;t : &quot;Đi hết ngay! Bộ tụi b&acirc;y kh&ocirc;ng thấy con ngựa cưng của tao bực m&igrave;nh đ&oacute; sao? S&aacute;ng mai tao sẽ hỏi tội cả lũ&quot;. Kh&ocirc;ng chần chờ g&igrave; nữa, cả bọn đi ngủ, v&agrave; s&aacute;ng nay, tụi nầy đi sớm trước khi b&agrave; gi&agrave; thức dậy, để anh Boyd ở lại xoay xở .</span></span></p>\r\n'),
(23, 'Tuổi trẻ đáng giá bao nhiêu?', 'Văn học Việt Nam', 'Rosie Nguyễn', 2018, 'tuoi-tre-dang-gia-bao-nhieu.jpg', 'Đã hoàn', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tuổi trẻ đ&aacute;ng gi&aacute; bao nhi&ecirc;u l&agrave; một cuốn s&aacute;ch self help kh&aacute; nổi tiếng đối với giới trẻ. Cuốn s&aacute;ch kh&aacute; nhẹ nh&agrave;ng, kh&ocirc;ng qu&aacute; cao si&ecirc;u v&agrave; do được viết bởi một t&aacute;c giả kh&aacute; trẻ, n&ecirc;n kh&ocirc;ng nặng nề gi&aacute;o điều.&nbsp;</span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tuổi trẻ đ&aacute;ng gi&aacute; bao nhi&ecirc;u&nbsp;kh&ocirc;ng mang t&iacute;nh chất dạy dỗ, kh&ocirc;ng chỉ tr&iacute;ch cực đoan m&agrave; chỉ đơn giản l&agrave; những t&acirc;m sự b&igrave;nh dị của một người trẻ tuổi. T&aacute;c giả Rosie Nguyễn c&oacute; lẽ lu&ocirc;n mong muốn mang đến cho bạn trẻ những tư tưởng t&iacute;ch cực nhất để mạnh mẽ bước ch&acirc;n v&agrave;o đời.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\"><strong>NẾU T&Ocirc;I C&Ograve;N HAI MƯƠI</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&quot;Những th&oacute;i quen tốt ta h&igrave;nh th&agrave;nh khi c&ograve;n trẻ kh&ocirc;ng tạo n&ecirc;n kh&aacute;c biệt nhỏ n&agrave;o, đ&uacute;ng hơn, ch&uacute;ng tạo ra tất cả kh&aacute;c biệt.&quot; (<strong>Aristotle</strong>)</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Kh&ocirc;ng biết bao nhi&ecirc;u lần t&ocirc;i nghe những người trẻ quanh m&igrave;nh than buồn, ch&aacute;n, bảo kh&ocirc;ng biết g&igrave; để l&agrave;m. V&agrave; rồi kh&ocirc;ng biết l&agrave;m g&igrave; n&ecirc;n ta giết thời giờ với những th&uacute; vui nhỏ nhặt, rong chơi cho qua ng&agrave;y đoạn th&aacute;ng, ngủ v&ugrave;i lười biếng hoặc ch&igrave;m đắm v&agrave;o y&ecirc;u đương.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Nhưng khi đ&atilde; đi qua gần hết thời đ&ocirc;i mươi, ngấp ngh&eacute; ở ngưỡng ba mươi, nh&igrave;n lại t&ocirc;i mới thấy tiếc nuối. Thấy b&acirc;y giờ cuộc sống c&oacute; qu&aacute; nhiều cơ hội, nhiều điều phải l&agrave;m, nhiều thứ để học, m&agrave; m&igrave;nh lại kh&ocirc;ng c&oacute; đủ thời gian cho ngần ấy thứ. Nghĩ nếu m&agrave; m&igrave;nh biết những điều n&agrave;y khi c&ograve;n đi học, khi m&igrave;nh c&ograve;n trẻ tuổi, chắc hẳn cuộc sống của m&igrave;nh sẽ kh&aacute;c, chắc m&igrave;nh sẽ bớt đi nhiều vật v&atilde; gian nan.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Ai c&oacute; trải qua rồi mới hiểu, tuổi trẻ ngắn ngủi biết bao nhi&ecirc;u. Thời gian một đi l&agrave; kh&ocirc;ng trở lại. Điều đ&aacute;ng qu&yacute; nhất m&agrave; tuổi trẻ c&oacute; được l&agrave; thời gian, nhưng rất nhiều người trẻ kh&ocirc;ng biết l&agrave;m g&igrave; c&oacute; &iacute;ch với thời gian của họ. Tr&ecirc;n thực tế, c&oacute; rất nhiều điều để l&agrave;m, khi người ta c&ograve;n trẻ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Nếu c&oacute; thể quay ngược qu&aacute; khứ, trở lại thời c&ograve;n mười t&aacute;m đ&ocirc;i mươi, t&ocirc;i sẽ d&agrave;nh thời gian để:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\"><strong>1. Đầu tư cho sức khỏe</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Chạy bộ, đi bơi, đ&aacute;nh b&oacute;ng b&agrave;n, tập yoga, học v&otilde;... Luyện tập thể dục thể thao kh&ocirc;ng nhất thiết phải l&agrave; mua một đ&ocirc;i gi&agrave;y h&agrave;ng hiệu gi&aacute; hai ba triệu, mua bộ đồ gi&aacute; mấy trăm ngh&igrave;n, hay đăng k&yacute; thẻ th&agrave;nh vi&ecirc;n trung t&acirc;m thể h&igrave;nh sang trọng ph&iacute; v&agrave;i chục triệu một năm. Gi&agrave;y Việt Nam chất lượng cao l&agrave; đủ để chạy, v&agrave;i chục ngh&igrave;n đồng l&agrave; c&oacute; c&aacute;i k&iacute;nh bơi. Chạy bộ trong c&ocirc;ng vi&ecirc;n kh&ocirc;ng tốn tiền, đi bơi th&igrave; chỉ mười mấy ngh&igrave;n đồng một v&eacute;, c&aacute;c lớp học v&otilde; ở trung t&acirc;m thể thao quận huyện khoảng một hai trăm ngh&igrave;n một th&aacute;ng, chịu kh&oacute; t&igrave;m kiếm th&igrave; quanh bạn sẽ c&oacute; kh&ocirc;ng &iacute;t c&acirc;u lạc bộ r&egrave;n luyện sức khỏe miễn ph&iacute;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Luyện tập thể lực trước hết l&agrave; đem lại một sức khỏe tốt, gi&uacute;p tinh thần hưng phấn, đ&agrave;o thải chất độc bị hấp thụ từ m&ocirc;i trường sống &ocirc; nhiễm v&agrave; thực phẩm độc hại. Một cơ thể l&agrave;nh mạnh l&agrave; điều kiện ti&ecirc;n quyết để c&oacute; một cuộc sống tốt. Th&ecirc;m v&agrave;o đ&oacute;, tương t&aacute;c trong những cộng đồng kh&aacute;c nhau gi&uacute;p ta c&oacute; nhiều mối quan hệ hơn, mở rộng tầm nh&igrave;n v&agrave; quan điểm về cuộc đời.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\"><strong>2. Đọc s&aacute;ch</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Phần lớn tri thức nh&acirc;n loại đều nằm trong s&aacute;ch. C&ograve;n b&aacute;o, tạp ch&iacute; thường chỉ chứa th&ocirc;ng tin, chưa kể b&aacute;o mạng v&agrave; mạng x&atilde; hội hiện nay đa phần l&agrave; tin r&aacute;c. Vậy m&agrave; ng&agrave;y nay người ta đọc b&aacute;o nhiều hơn đọc s&aacute;ch. Sự thật l&agrave; s&aacute;ch gi&uacute;p con người thay đổi cuộc đời. Những l&uacute;c tinh thần đi xuống, kh&ocirc;ng muốn l&agrave;m g&igrave;, tốt nhất l&agrave; đọc s&aacute;ch. V&igrave; s&aacute;ch kh&ocirc;ng những n&acirc;ng cao kiến thức m&agrave; c&ograve;n tạo động lực th&uacute;c đẩy ta h&agrave;nh động tốt hơn.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">S&aacute;ch cũng kh&ocirc;ng phải rẻ, nhất l&agrave; với v&iacute; tiền mỏng manh của người trẻ. Nếu kh&ocirc;ng c&oacute; điều kiện mua s&aacute;ch giấy th&igrave; l&ecirc;n mạng t&igrave;m c&aacute;c trang web đọc ebook miễn ph&iacute; như&nbsp;<strong>docsach24.com</strong>. Nếu đ&atilde; đọc ebook th&igrave; tốt nhất l&agrave; t&igrave;m ebook nguy&ecirc;n bản tiếng Anh, vừa c&oacute; kiến thức vừa tăng th&ecirc;m vốn từ vựng, một c&ocirc;ng đ&ocirc;i việc. Chỉ cần một tuần đọc một quyển s&aacute;ch. Một năm nh&igrave;n lại, thấy kiến thức của m&igrave;nh đ&atilde; d&agrave;y hơn v&agrave;i phần.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Điều c&ocirc;ng bằng nhất trong cuộc sống n&agrave;y l&agrave; ch&uacute;ng ta đều c&oacute; quyền lựa chọn v&agrave; quyết định con đường m&igrave;nh sẽ đi. Bạn sẽ tuyệt vọng hay ở tr&ecirc;n một con đường mới, bạn sẽ biến m&igrave;nh th&agrave;nh một kẻ thất bại thảm hại hay lột x&aacute;c th&agrave;nh c&ocirc;ng ch&uacute;a ki&ecirc;u h&atilde;nh? Bạn sẽ l&agrave; ai? Cuộc đời bạn sẽ như thế n&agrave;o? Chẳng phải v&igrave; người đ&agrave;n &ocirc;ng n&agrave;o cả? trước hết l&agrave; do bạn!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\"><strong>3. Học trực tuyến tr&ecirc;n mạng.</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">C&aacute;c trang web MOOC (viết tắt của từ Massive Open Online Course - Kh&oacute;a học trực tuyến đại tr&agrave;) cho ph&eacute;p người d&ugrave;ng theo học tr&ecirc;n mạng về nhiều đề t&agrave;i kh&aacute;c nhau, như nghệ thuật, lịch sử, thuật l&atilde;nh đạo, kinh tế kinh doanh, c&aacute;ch dạy tiếng Anh, thiết kế, lập tr&igrave;nh, thi&ecirc;n văn học, vũ trụ, v&acirc;n v&acirc;n. Một số trang MOOC ho&agrave;n to&agrave;n miễn ph&iacute; như:&nbsp;<em>www.coursera.org, www.edx.org, hay www.khanacademy.org</em>được tạo ra bởi những trường đại học danh tiếng của Mỹ, với gi&aacute;o tr&igrave;nh chuẩn v&agrave; được soạn giảng bởi c&aacute;c gi&aacute;o sư nổi tiếng. Người d&ugrave;ng chỉ cần đăng k&yacute; t&agrave;i khoản l&agrave; c&oacute; thể bắt đầu học. Họ c&oacute; thể thảo luận c&aacute;c thắc mắc của m&igrave;nh với bạn b&egrave; c&ugrave;ng kh&oacute;a, c&aacute;c gi&aacute;o sư v&agrave; trợ giảng trong diễn đ&agrave;n của lớp học. Họ cũng l&agrave;m b&agrave;i tập về nh&agrave;, được kiểm tra kiến thức sau mỗi tuần v&agrave; thi kết th&uacute;c m&ocirc;n để kiểm tra tr&igrave;nh độ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Tr&ecirc;n thế giới hiện nay, h&igrave;nh thức học trực tuyến qua MOOC đang ng&agrave;y c&agrave;ng thịnh h&agrave;nh. N&oacute; bổ sung, b&ugrave; đắp v&agrave; thay thế cho h&igrave;nh thức gi&aacute;o dục truyền thống. H&igrave;nh thức n&agrave;y được c&aacute;c nh&agrave; gi&aacute;o dục cho rằng sẽ trở th&agrave;nh loại h&igrave;nh gi&aacute;o dục của tương lai, v&igrave; ta kh&ocirc;ng phải tốn thời gian di chuyển, c&oacute; thể tự lựa chọn khung giờ học ph&ugrave; hợp cho m&igrave;nh, học đồng thời nhiều chương tr&igrave;nh kh&aacute;c nhau, hoặc bổ sung những kỹ năng h&aacute;i ra tiền m&agrave; kh&ocirc;ng được dạy ở c&aacute;c trường đại học. Với MOOC, người d&ugrave;ng từ khắp nơi tr&ecirc;n thế giới c&oacute; thể trau dồi kiến thức v&agrave; thoản m&atilde;n niềm khao kh&aacute;t học hỏi, bất kể họ đang ở độ tuổi, tr&igrave;nh độ v&agrave; địa điểm n&agrave;o.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Thay v&igrave; ngồi thương kh&oacute;c một mối t&igrave;nh đ&atilde; chết, đăng k&yacute; kh&oacute;a học&nbsp;<em>Apply to US Universities (Nộp đơn v&agrave;o c&aacute;c trường đại học Mỹ)</em>, rồi kiếm tấm v&eacute; đi du học Mỹ cho người y&ecirc;u cũ l&eacute; mắt, chẳng phải tốt hơn sao?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\"><strong>4. Cho d&acirc;n du lịch bụi ở nhờ.</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Người trẻ hiện nay ng&agrave;y c&agrave;ng nhiều người th&iacute;ch đi du lịch. Trong l&uacute;c chưa c&oacute; thời gian, hay chưa t&iacute;ch g&oacute;p đủ tiền để đi, sao bạn kh&ocirc;ng thử cho d&acirc;n du lịch bụi ở nhờ nh&agrave; m&igrave;nh? C&aacute;c trang web như&nbsp;<em>www. couchsurfing.com</em>&nbsp;hay&nbsp;<em>www.hospitalityclub.org</em>&nbsp;chuy&ecirc;n kết nối người đi du lịch bụi v&agrave; người bản xứ. Khi đi du lịch đến một v&ugrave;ng đất n&agrave;o đ&oacute;, ta c&oacute; thể xin ở nhờ nh&agrave; người d&acirc;n bản xứ ho&agrave;n to&agrave;n miễn ph&iacute;. Khi ở nh&agrave;, ta cũng c&oacute; thể b&agrave;y tỏ l&ograve;ng hiếu kh&aacute;ch bằng c&aacute;ch cho du kh&aacute;ch ở nhờ. D&acirc;n đi bụi chẳng cần g&igrave; sang trọng. Họ chỉ cần một chỗ vừa đủ để đặt lưng buổi tối, một chiếc ghế s&ocirc; pha, một phần giường c&ograve;n trống, hoặc đ&ocirc;i khi chỉ l&agrave; một s&agrave;n nh&agrave; sạch sẽ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Những trải nghiệm như vậy thường đem lại cho đ&ocirc;i b&ecirc;n những kỷ niệm kh&oacute; qu&ecirc;n. Với người cho ở nhờ, chẳng cần đi đ&acirc;u cũng sẽ c&oacute; những c&acirc;u chuyện lữ h&agrave;nh từ khắp nơi tr&ecirc;n thế giới ở ngay ngưỡng của nh&agrave; bạn. Những chia sẻ, kinh nghiệm của người đi du lịch cũng c&oacute; thể truyền cảm hứng v&agrave; chứa nhiều th&ocirc;ng tin hữu &iacute;ch cho chuyến đi sắp tới. Chỉ cần đăng k&yacute; t&agrave;i khoản, viết th&ocirc;ng tin c&aacute; nh&acirc;n, thế l&agrave; bạn đ&atilde; sẵn s&agrave;ng để kết nối với d&acirc;n lữ h&agrave;nh to&agrave;n cầu. Nếu kh&ocirc;ng thể cho ở nhờ, th&igrave; cũng c&oacute; thể gặp nhau tr&ograve; chuyện, dẫn kh&aacute;ch du lịch đi tham quan quanh v&ugrave;ng bạn ở, hoặc tham gia hoạt động của c&aacute;c nh&oacute;m th&agrave;nh vi&ecirc;n tại địa phương.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\"><strong>5. Đi du lịch bụi.</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Kh&ocirc;ng cần nhiều tiền v&agrave; thời gian, nhiều tỉnh gần S&agrave;i G&ograve;n c&oacute; thể đi về trong ng&agrave;y, chỉ cần đổ đầy b&igrave;nh xăng l&agrave; mặc sức chạy. Rời khỏi th&agrave;nh phố nghẹt bụi để t&igrave;m đồng l&uacute;a bao la, m&ugrave;i rơm rạ tr&ecirc;n c&aacute;nh đồng mới gặt, m&ugrave;i kh&oacute;i đốt đồng, m&ugrave;i hoa m&agrave;u tươi tốt, chim c&ograve; vỗ c&aacute;nh bay l&ecirc;n, đ&agrave;n bỏ nhởn nhơ gặm cỏ. Nếu c&ograve;n sinh vi&ecirc;n, những th&aacute;ng nghỉ h&egrave; c&oacute; thể g&oacute;i đồ xuy&ecirc;n Việt, hoặc đi một v&ograve;ng ba nước Đ&ocirc;ng Dương. Dư dả hơn, chỉ cần trong t&uacute;i mười mấy triệu đồng l&agrave; c&oacute; thể đi một v&ograve;ng mấy nước Đ&ocirc;ng Nam &Aacute; bằng đường bộ, từ Việt Nam đến Campuchia, Th&aacute;i Lan, Malaysia, xuống Singapore rồi quay trở về.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Du lịch bụi kh&ocirc;ng hề đắt đỏ như người ta tưởng. Trong quyển s&aacute;ch hướng dẫn du lịch bụi&nbsp;<em>Ta ba l&ocirc; tr&ecirc;n đất &Aacute;&nbsp;</em>của m&igrave;nh, t&ocirc;i đ&atilde; từng kể c&acirc;u chuyện người thật việc thật về c&ocirc; b&eacute; sinh vi&ecirc;n đ&atilde; đi du lịch bụi qua Campuchia v&agrave; Th&aacute;i Lan cả th&aacute;ng trời chỉ tốn c&oacute; hai triệu đồng, chủ yếu bằng c&aacute;ch đi nhờ xe (hitchhiking) v&agrave; ngủ nhờ nh&agrave; người d&acirc;n bản xứ. Trường hợp như thế trong giới du lịch bụi kh&ocirc;ng phải l&agrave; hiếm. Những chuyến đi đ&oacute; kh&ocirc;ng tốn nhiều chi ph&iacute; m&agrave; để lại những trải nghiệm thay đổi đời người.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Ch&igrave;a kh&oacute;a khi đi du lịch bụi kh&ocirc;ng phải trong t&uacute;i bạn c&oacute; bao nhi&ecirc;u tiền, m&agrave; l&agrave; trong l&ograve;ng bạn c&oacute; bao nhi&ecirc;u can đảm. Can đảm để bước ra khỏi v&ugrave;ng an to&agrave;n của m&igrave;nh, can đảm để l&agrave;m điều mới. Can đảm để trải nghiệm. C&oacute; nhiều c&aacute;ch để đi du lịch với &iacute;t tiền, thậm ch&iacute; kh&ocirc;ng c&oacute; tiền. C&oacute; thể đi bộ, đi xe đạp, đi nhờ xe. Ở nhờ nh&agrave; người d&acirc;n bản xứ qua&nbsp;<em>www.couchsurfing.com</em>&nbsp;như chia sẻ ở tr&ecirc;n, l&agrave;m việc t&igrave;nh nguyện để đổi lấy nơi ăn ngủ th&ocirc;ng qua&nbsp;<em>www.idealist.org</em>, hoặc&nbsp;<em>www.workaway.info</em>. Gi&uacute;p đỡ người kh&aacute;c v&agrave; đem lại gi&aacute; trị trong l&uacute;c m&igrave;nh đi, hiểu đời hiểu người hơn sau mỗi chuyến đi.&nbsp;</span></span></span></p>\r\n'),
(24, 'Ông già và biển cả', 'Văn học nước ngoài', ' Hemingway', 1952, 'ong-gia-va-bien-ca.jpg', 'Đang cập nhật', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Trong suốt t&aacute;m mươi tu ng&agrave;y r&ograve;ng r&atilde; tr&ecirc;n biển, &ocirc;ng l&atilde;o Santiago kh&ocirc;ng bắt được con c&aacute; n&agrave;o, người d&acirc;n l&agrave;ng ch&agrave;i c&agrave;ng c&oacute; cơ sở cho rằng &ocirc;ng l&atilde;o đ&atilde; &ldquo;đi đứt&rdquo; v&igrave; vận rủi, cậu b&eacute; Ma-n&ocirc;-lin cũng kh&ocirc;ng cho cậu b&eacute; đi c&acirc;u với &ocirc;ng l&atilde;o nữa. V&agrave;o ng&agrave;y thứ t&aacute;m mươi lăm, l&atilde;o quyết định ra khơi trước khi trời s&aacute;ng. Lần n&agrave;y l&atilde;o đi thật xa, đến tận v&ugrave;ng Giếng Lớn. Khoảng trưa, một con c&aacute; lớn cắn c&acirc;u, k&eacute;o thuyền về hướng t&acirc;y bắc. S&aacute;ng ng&agrave;y thứ hai, con c&aacute; nhảy l&ecirc;n. Đ&oacute; l&agrave; một con c&aacute; kiếm, lớn đến nỗi trước đ&acirc;y l&atilde;o chưa từng nh&igrave;n thấy. Con c&aacute; lại lặn xuống, k&eacute;o thuyền chạy về hướng đ&ocirc;ng. Sang đến ng&agrave;y thứ ba, con c&aacute; bắt đầu lượn v&ograve;ng. D&ugrave; đ&atilde; kiệt sức, l&atilde;o ki&ecirc;n tr&igrave; thu ngắn d&acirc;y c&acirc;u, rồi dốc to&agrave;n lực ph&oacute;ng lao đ&acirc;m chết được con c&aacute;, buộc n&oacute; v&agrave;o mạn thuyền dong về.&nbsp;Tr&ecirc;n đoạn đường về, đ&aacute;m mập đ&atilde; đ&aacute;nh hơi được con mồi m&agrave; lao tới tấn c&ocirc;ng, xẻ thịt con c&aacute; kiếm. Suốt cả ng&agrave;y h&ocirc;m ấy, &ocirc;ng l&atilde;o phải d&ugrave;ng sức lực t&agrave;n để chiến đấu với lũ c&aacute; mập, &ocirc;ng đ&atilde; vung ch&agrave;y, ph&oacute;ng lao v&agrave; d&ugrave;ng m&aacute;i ch&egrave;o để đ&aacute;nh chết nhiều con c&aacute; mập. Khi về đến bờ con c&aacute; kiếm chỉ c&ograve;n lại c&aacute;i đầu v&agrave; bộ xương trắng. Khi đưa được thuyền v&agrave;o cảng, &ocirc;ng l&atilde;o mệt mỏi đi về nh&agrave;, &ocirc;ng vật người ng&atilde; xuống giường, nhanh ch&oacute;ng ch&igrave;m v&agrave;o giấc ngủ, &ocirc;ng mơ về những con sư tử. Tuy nhi&ecirc;n, l&atilde;o cũng rất h&agrave;i l&ograve;ng với th&agrave;nh quả ng&agrave;y h&ocirc;m ấy của m&igrave;nh.</span></span></span></p>\r\n', '<p><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">L&atilde;o đ&atilde; gi&agrave;, một m&igrave;nh một thuyền c&acirc;u c&aacute; tr&ecirc;n d&ograve;ng Nhiệt lưu v&agrave; đ&atilde; t&aacute;m mươi tư ng&agrave;y qua l&atilde;o kh&ocirc;ng bắt được lấy một mống c&aacute; n&agrave;o. Bốn mươi ng&agrave;y đầu thằng b&eacute; đi với l&atilde;o. Nhưng sau bốn mươi ng&agrave;y kh&ocirc;ng c&acirc;u được c&aacute;, cha mẹ thằng b&eacute; bảo n&oacute; rằng rốt cuộc b&acirc;y giờ &ocirc;ng l&atilde;o đ&atilde; ho&agrave;n to&agrave;n salao, c&aacute;ch diễn đạt tệ nhất của vận rủi, rồi buộc n&oacute; đi theo thuyền kh&aacute;c v&agrave; ngay trong tuần lễ đầu ti&ecirc;n chiếc thuyền ấy đ&atilde; c&acirc;u được ba con c&aacute; lớn. Điều đ&oacute; khiến thằng b&eacute; buồn khi hằng ng&agrave;y thấy &ocirc;ng l&atilde;o trở về với chiếc thuyền kh&ocirc;ng, n&oacute; lu&ocirc;n xuống gi&uacute;p l&atilde;o mang khi th&igrave; cuộn d&acirc;y, c&aacute;i lao m&oacute;c săn c&aacute;, khi th&igrave; c&aacute;i s&agrave;o hay tấm buồm quấn quanh cột. Tấm buồm được v&aacute; bằng bao bột, cuộn lại tr&ocirc;ng như một l&aacute; cờ bại trận triền mi&ecirc;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&Ocirc;ng l&atilde;o gầy g&ograve;, giơ cả xương, g&aacute;y hằn s&acirc;u nhiều nếp nhăn. Những vệt n&aacute;m v&ocirc; hại tr&ecirc;n l&agrave;n da m&aacute; của l&atilde;o do bị ung thư bởi &aacute;nh mặt trời phản hồi tr&ecirc;n mặt biển nhiệt đới. Những vệt ấy k&eacute;o d&agrave;i xuống cả hai b&ecirc;n m&aacute;, tay l&atilde;o hằn những vết sẹo s&acirc;u bởi k&eacute;o những con c&aacute; lớn. Nhưng chẳng c&oacute; vết n&agrave;o trong số sẹo ấy c&ograve;n mới cả. Ch&uacute;ng cũ kỹ như mấy vệt x&oacute;i m&ograve;n tr&ecirc;n sa mạc kh&ocirc;ng c&aacute;. Mọi thứ tr&ecirc;n cơ thể l&atilde;o đều to&aacute;t l&ecirc;n vẻ gi&agrave; nua, trừ đ&ocirc;i mắt; ch&uacute;ng c&oacute; c&ugrave;ng m&agrave;u với nước biển, vui vẻ v&agrave; kh&ocirc;ng hề thất bại.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- &Ocirc;ng Santiago! - thằng b&eacute; n&oacute;i khi hai người leo l&ecirc;n bờ nơi con thuyền được k&eacute;o l&ecirc;n. - Ch&aacute;u c&oacute; thể lại đi c&ugrave;ng &ocirc;ng. Ch&uacute;ng ta đ&atilde; c&oacute; &iacute;t tiền rồi.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&Ocirc;ng l&atilde;o dạy thằng b&eacute; c&acirc;u c&aacute; v&agrave; n&oacute; qu&yacute; l&atilde;o.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Đừng! - l&atilde;o n&oacute;i.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Ch&aacute;u đang đi với con thuyền may mắn. H&atilde;y ở lại với họ!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Nhưng &ocirc;ng c&ograve;n nhớ c&oacute; hồi suốt t&aacute;m mươi bảy ng&agrave;y ta kh&ocirc;ng bắt được c&aacute; nhưng sau đ&oacute; trong ba tuần lễ ng&agrave;y n&agrave;o ta cũng vớ được c&aacute; lớn!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- &Ocirc;ng nhớ! - &ocirc;ng l&atilde;o n&oacute;i. - &Ocirc;ng biết ch&aacute;u đ&atilde; kh&ocirc;ng rời bỏ &ocirc;ng v&igrave; thiếu l&ograve;ng tin!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Tại cha ch&aacute;u bắt l&agrave;m như thế. Ch&aacute;u c&ograve;n nhỏ, ch&aacute;u phải nghe lời cha!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- &Ocirc;ng hiểu! - &ocirc;ng l&atilde;o n&oacute;i. - Đấy l&agrave; chuyện thường!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Cha ch&aacute;u chẳng tin đ&acirc;u!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Phải! - &ocirc;ng l&atilde;o n&oacute;i. - Nhưng ch&uacute;ng ta tin, đ&uacute;ng kh&ocirc;ng?!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- V&acirc;ng! - thằng b&eacute; đ&aacute;p. - Ch&aacute;u c&oacute; thể mời &ocirc;ng một l&aacute;</span><a href=\"https://docsach24.co/doc-sach/ong-gia-va-bien-ca/index_split_008.html#note_1\" style=\"text-decoration:none\"><span style=\"color:#077dca\"><u>1</u></span></a><span style=\"color:#212529\">&nbsp;bia ở kh&aacute;ch sạn Terrace trước khi ch&uacute;ng ta mang mấy thứ n&agrave;y về nh&agrave; chứ?!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Tại sao lại kh&ocirc;ng?! &ocirc;ng l&atilde;o n&oacute;i. - D&acirc;n ch&agrave;i với nhau cả m&agrave;!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Họ ngồi ở Terrace v&agrave; rất nhiều người đ&aacute;nh c&aacute; tr&ecirc;u &ocirc;ng l&atilde;o nhưng l&atilde;o kh&ocirc;ng giận. Một số kh&aacute;c, những ngư d&acirc;n lớn tuổi hơn, nh&igrave;n l&atilde;o v&agrave; cảm thấy buồn. Nhưng họ kh&ocirc;ng để lộ ra m&agrave; chỉ từ tốn n&oacute;i về d&ograve;ng chảy, độ s&acirc;u bu&ocirc;ng c&acirc;u, về thời tiết tốt, ổn định v&agrave; về những thứ họ đ&atilde; nh&igrave;n thấy. Mấy tay đ&aacute;nh c&aacute; th&agrave;nh c&ocirc;ng của ng&agrave;y ấy đ&atilde; trở về, xả thịt con c&aacute; kiếm của họ, sắp đầy l&ecirc;n hai tấm v&aacute;n, mỗi người khi&ecirc;ng một đầu đi xuy&ecirc;n đến nh&agrave; để c&aacute;, đợi chiếc xe tải ướp lạnh đưa đến chợ Havana. Những người bắt được c&aacute; mập th&igrave; đưa ch&uacute;ng đến xưởng c&aacute; mập ph&iacute;a b&ecirc;n kia vịnh; ch&uacute;ng được m&oacute;c treo l&ecirc;n bằng r&ograve;ng rọc, gan bị mổ lấy, vi bị cắt, da bị lột v&agrave; thịt th&igrave; được xẻ th&agrave;nh s&uacute;c đưa ướp muối. Khi gi&oacute; Đ&ocirc;ng thổi, m&ugrave;i tanh nồng từ xưởng c&aacute; mập b&ecirc;n kia cảng phả đến. Nhưng h&ocirc;m nay, m&ugrave;i c&aacute; chỉ thoang thoảng v&igrave; gi&oacute; thổi chếch sang hướng Bắc rồi lặng hẳn; tr&ecirc;n Terrace trời tỏa nắng dễ chịu.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- &Ocirc;ng Santiago! thằng b&eacute; gọi.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Ừ! - &ocirc;ng l&atilde;o đ&aacute;p. L&atilde;o đang giữ c&aacute;i l&aacute; v&agrave; hồi tưởng về nhiều năm trước.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Ch&aacute;u đi kiếm gi&uacute;p &ocirc;ng mấy con c&aacute; m&ograve;i cho ng&agrave;y mai nh&eacute;?!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Đừng. Đi chơi b&oacute;ng ch&agrave;y đi. &Ocirc;ng vẫn c&oacute; thể ch&egrave;o v&agrave; Rogelio sẽ quăng lưới!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Ch&aacute;u th&iacute;ch đi. Nếu ch&aacute;u kh&ocirc;ng được c&acirc;u c&aacute; với &ocirc;ng th&igrave; ch&aacute;u muốn gi&uacute;p &ocirc;ng việc g&igrave; đ&oacute;!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Ch&aacute;u đ&atilde; mua bia cho &ocirc;ng! &ocirc;ng l&atilde;o n&oacute;i. - Ch&aacute;u thực sự l&agrave; đ&agrave;n &ocirc;ng rồi!</span></span></span></p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(25, 'Cho tôi xin một vé đi tuổi thơ', 'Văn học Việt Nam', ' Nguyễn Nhật Ánh', 2008, 'cho-toi-xin-mot-ve-di-tuoi-tho.jpg', 'Đang cập nhật', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&ldquo;Cho T&ocirc;i Xin Một V&eacute; Đi Tuổi Thơ&rdquo; l&agrave; tập truyện d&agrave;i đ&atilde; v&ocirc; c&ugrave;ng quen thuộc với nhiều người của nh&agrave; văn nổi tiếng Nguyễn Nhật &Aacute;nh. Bởi t&iacute;nh nh&acirc;n văn s&acirc;u sắc trong n&oacute;, đ&acirc;y được coi l&agrave; một trong những s&aacute;ng t&aacute;c th&agrave;nh c&ocirc;ng nhất của &ocirc;ng v&agrave; gi&uacute;p &ocirc;ng gi&agrave;nh được Giải thưởng Văn học ASEAN năm 2010.&nbsp;Tr&ecirc;n mạch kể hấp dẫn của c&acirc;u chuyện, ngược d&ograve;ng thời gian, t&aacute;c giả đ&atilde; đưa người đọc trở lại những năm th&aacute;ng tuổi thơ vui vẻ với cốt truyện xoay quanh 4 bạn nhỏ nghịch ngợm, hồn nhi&ecirc;n cu M&ugrave;i, Hải c&ograve;, T&iacute; s&uacute;n v&agrave; con Tủn. Trong thế giới tuổi thơ tươi đẹp ấy của những đứa trẻ, ch&uacute;ng kh&ocirc;ng hề c&oacute; những lo toan, bộn bề về cuộc sống vật chất v&agrave; tinh thần m&agrave; chỉ hạnh ph&uacute;c đắm ch&igrave;m v&agrave;o những tr&ograve; chơi, những vui đ&ugrave;a của tuổi nhỏ. Trải d&agrave;i tr&ecirc;n từng trang viết l&agrave; những c&acirc;u chuyện h&agrave;i hước, d&iacute; dỏm, những tr&ograve; đ&ugrave;a vui vẻ khiến độc giả thực sự ước ao được quay lại những cảm x&uacute;c trong s&aacute;ng, qu&atilde;ng thời gian v&ocirc; tư như vậy một lần nữa.&nbsp;Đan xen v&agrave;o những t&igrave;nh huống của 4 đứa trẻ, t&aacute;c giả c&ograve;n th&ecirc;m v&agrave;o cuốn s&aacute;ch cả những suy nghĩ, những chi&ecirc;m nghiệm về cuộc sống hiện tại v&agrave; qu&aacute; khứ của con người khiến ch&uacute;ng ta phải dặn l&ograve;ng suy ngẫm ch&uacute;ng.&nbsp;Kh&ocirc;ng phải những trang s&aacute;ch n&oacute;i về l&yacute; lẽ hay những quan điểm nặng nhọc về cuộc sống nhưng &ldquo;Cho T&ocirc;i Một V&eacute; Đi Tuổi Thơ&rdquo; vẫn khiến cho độc giả cảm nhận được những ch&acirc;n l&yacute; nằm trong n&oacute;.&nbsp;Mỗi một lần đọc cuốn s&aacute;ch n&agrave;y, bạn như một lần được trở lại tuổi thơ đẹp đẽ của m&igrave;nh, từ đ&oacute;, thắp l&ecirc;n niềm tin v&agrave;o một cuộc sống tốt đẹp ở hiện tại v&agrave; tương lai.</span></span></p>\r\n', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#ff0000\"><strong>Chương 1: T&oacute;m lại l&agrave; hết một ng&agrave;y</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Năm đ&oacute; t&ocirc;i t&aacute;m tuổi.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Sau n&agrave;y, t&ocirc;i cũng nhiều lần thấy cuộc sống đ&aacute;ng ch&aacute;n khi thi trượt ở tuổi mười lăm, thất t&igrave;nh ở tuổi hăm bốn, thất nghiệp ở tuổi ba mươi ba v&agrave; gặt h&aacute;i mọi th&agrave;nh c&ocirc;ng ở tuổi bốn mươi.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Nhưng t&aacute;m tuổi c&oacute; c&aacute;i buồn ch&aacute;n của tuổi l&ecirc;n t&aacute;m.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ETH;&oacute; l&agrave; c&aacute;i ng&agrave;y kh&ocirc;ng hiểu sao t&ocirc;i lại c&oacute; &yacute; nghĩ rằng cuộc sống kh&ocirc;ng c&oacute; g&igrave; để m&agrave; chờ đợi nữa.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Rất nhiều năm về sau, t&ocirc;i được biết c&aacute;c triết gia v&agrave; c&aacute;c nh&agrave; thần học vẫn đang loay hoay đi t&igrave;m &yacute; nghĩa của cuộc sống v&agrave; tới Tết Ma Rốc họ cũng chưa chắc đ&atilde; t&igrave;m ra.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Nhưng năm t&ocirc;i t&aacute;m tuổi, t&ocirc;i đ&atilde; thấy cuộc sống chả c&oacute; g&igrave; mới mẻ để kh&aacute;m ph&aacute;.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Vẫn &aacute;nh mặt trời đ&oacute; chiếu rọi mỗi ng&agrave;y. Vẫn bức m&agrave;n đen đ&oacute; bu&ocirc;ng xuống mỗi đ&ecirc;m. Tr&ecirc;n m&aacute;i nh&agrave; v&agrave; tr&ecirc;n c&aacute;c c&agrave;nh l&aacute; sau vườn, gi&oacute; vẫn than thở giọng của gi&oacute;. Chim vẫn h&oacute;t giọng của chim. Dế ri ri giọng dế, g&agrave; quang qu&aacute;c giọng g&agrave;. N&oacute;i t&oacute;m lại, cuộc sống thật l&agrave; cũ kỹ.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Cuộc sống của t&ocirc;i c&ograve;n cũ kỹ hơn nữa. Mỗi đ&ecirc;m, trước khi đi ngủ, t&ocirc;i đ&atilde; biết tỏng ng&agrave;y mai những sự kiện g&igrave; sẽ diễn ra trong cuộc đời t&ocirc;i.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i kể ra nh&eacute;: S&aacute;ng, t&ocirc;i phải cố hết sức để thức dậy trong khi t&ocirc;i vẫn c&ograve;n muốn ngủ tiếp. Tất nhi&ecirc;n l&agrave; trước đ&oacute; t&ocirc;i vẫn giả vờ ngủ m&ecirc; mặc cho mẹ t&ocirc;i k&ecirc;u khản cả giọng rồi lay lay người t&ocirc;i, nhưng dĩ nhi&ecirc;n t&ocirc;i vẫn trơ ra như kh&uacute;c gỗ cho đến khi mẹ t&ocirc;i c&ugrave; v&agrave;o l&ograve;ng b&agrave;n ch&acirc;n t&ocirc;i.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Khi đặt ch&acirc;n xuống đất rồi, t&ocirc;i phải đi đ&aacute;nh răng rửa mặt, t&oacute;m lại l&agrave; l&agrave;m vệ sinh buổi s&aacute;ng trước khi bị ấn v&agrave;o b&agrave;n ăn để uể oải nhai ch&oacute;p ch&eacute;p một thứ g&igrave; đ&oacute; thường l&agrave; kh&ocirc;ng hợp khẩu vị. Mẹ t&ocirc;i lu&ocirc;n lu&ocirc;n quan t&acirc;m đến sức khỏe v&agrave; cụ thể h&oacute;a mối quan t&acirc;m của m&igrave;nh bằng c&aacute;ch bắt t&ocirc;i (v&agrave; cả nh&agrave;) ăn những m&oacute;n ăn c&oacute; nhiều chất dinh dưỡng trong khi t&ocirc;i chỉ kho&aacute;i xực những m&oacute;n m&agrave; b&agrave; cho rằng chẳng bổ b&eacute;o g&igrave;, như m&igrave; g&oacute;i chẳng hạn.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Quan t&acirc;m đến sức khỏe l&agrave; điều tốt, v&agrave; c&agrave;ng lớn tuổi mối quan t&acirc;m đ&oacute; c&agrave;ng tỏ ra đ&uacute;ng đắn. Chẳng ai d&aacute;m n&oacute;i quan t&acirc;m như vậy l&agrave; điều kh&ocirc;ng tốt. T&ocirc;i cũng thế th&ocirc;i. Khi t&ocirc;i trưởng th&agrave;nh, c&oacute; nh&agrave; b&aacute;o phỏng vấn t&ocirc;i, rằng giữa sức khỏe, t&igrave;nh y&ecirc;u v&agrave; tiền bạc, &ocirc;ng quan t&acirc;m điều g&igrave; nhất? L&uacute;c đầu t&ocirc;i n&oacute;i nhiều về t&igrave;nh y&ecirc;u, về sau t&ocirc;i n&oacute;i nhiều hơn về sức khỏe. T&ocirc;i phớt lờ tiền bạc, mặc d&ugrave; t&ocirc;i nhận thấy đ&oacute; l&agrave; một bất c&ocirc;ng: tiền bạc chưa bao giờ được con người ta thừa nhận l&agrave; mối quan t&acirc;m h&agrave;ng đầu d&ugrave; tiền bạc ng&agrave;y n&agrave;o cũng chạy đi mua qu&agrave; tặng cho t&igrave;nh y&ecirc;u v&agrave; thuốc men cho sức khỏe.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Nhưng th&ocirc;i, đ&oacute; l&agrave; chuyện của người lớn - chuyện sau n&agrave;y. C&ograve;n t&ocirc;i, l&uacute;c t&aacute;m tuổi, t&ocirc;i chỉ nhớ l&agrave; t&ocirc;i kh&ocirc;ng th&iacute;ch ăn những m&oacute;n bổ dưỡng. Nhưng tất nhi&ecirc;n l&agrave; t&ocirc;i vẫn buộc phải ăn, d&ugrave; l&agrave; ăn trong miễn cưỡng v&agrave; lười nh&aacute;c, v&agrave; đ&oacute; l&agrave; l&yacute; do mẹ t&ocirc;i lu&ocirc;n than thở về t&ocirc;i.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Ăn xong phần ăn buổi s&aacute;ng (chả sung sướng g&igrave;), t&ocirc;i vội v&agrave;ng truy l&ugrave;ng s&aacute;ch vở để nh&eacute;t v&agrave;o cặp, nhặt tr&ecirc;n đầu tivi một quyển, tr&ecirc;n đầu tủ lạnh một quyển kh&aacute;c v&agrave; moi từ dưới đống chăn gối một quyển kh&aacute;c nữa, dĩ nhi&ecirc;n bao giờ cũng thiếu một m&oacute;n g&igrave; đ&oacute;, rồi ba ch&acirc;n bốn cẳng chạy v&ugrave; ra khỏi nh&agrave;.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Trường gần nh&agrave; n&ecirc;n t&ocirc;i đi bộ, nhưng thực tế th&igrave; t&ocirc;i chưa bao giờ được thưởng thức th&uacute; đi bộ tới trường. T&ocirc;i to&agrave;n phải chạy. V&igrave; t&ocirc;i lu&ocirc;n lu&ocirc;n dậy trễ, lu&ocirc;n lu&ocirc;n l&agrave;m vệ sinh trễ, lu&ocirc;n lu&ocirc;n ăn s&aacute;ng trễ v&agrave; mất rất nhiều th&igrave; giờ để thu gom tập vở cho một buổi học. Về chuyện n&agrave;y, ba t&ocirc;i bảo: </span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- &quot;Con &agrave;, hồi bằng tuổi con, bao giờ ba cũng xếp gọn g&agrave;ng tập vở v&agrave;o cặp trước khi đi ngủ, như vậy s&aacute;ng h&ocirc;m sau chỉ việc &ocirc;m cặp ra khỏi nh&agrave;!&quot;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Nhưng hồi ba t&ocirc;i bằng tuổi t&ocirc;i th&igrave; t&ocirc;i đ&acirc;u c&oacute; mặt tr&ecirc;n c&otilde;i đời để kiểm tra những g&igrave; &ocirc;ng n&oacute;i, bởi khi t&ocirc;i bằng tuổi ba t&ocirc;i b&acirc;y giờ chắc chắn t&ocirc;i cũng sẽ lặp lại với con t&ocirc;i những điều &ocirc;ng n&oacute;i với t&ocirc;i - chuyện xếp tập vở trước khi đi ngủ v&agrave; h&agrave;ng đống những chuyện kh&aacute;c nữa, những chuyện m&agrave; t&ocirc;i kh&ocirc;ng hề l&agrave;m.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Ch&agrave;, với những chuyện như thế n&agrave;y, bạn đừng bao giờ đ&ograve;i hỏi phải chứng minh. &ETH;&ocirc;i khi v&igrave; một l&yacute; do n&agrave;o đ&oacute; m&agrave; ch&uacute;ng ta buộc phải bịa chuyện.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Ch&uacute;ng ta cứ lặp lại m&atilde;i c&acirc;u chuyện bịa đ&oacute; cho đến một ng&agrave;y ch&uacute;ng ta kh&ocirc;ng nhớ c&oacute; thật l&agrave; ch&uacute;ng ta đ&atilde; bịa n&oacute; ra hay kh&ocirc;ng, rồi sau đ&oacute; một thời gian nữa nếu cứ tiếp tục lặp lại c&acirc;u chuyện đ&oacute; nhiều lần th&igrave; ch&uacute;ng ta sẽ tin l&agrave; n&oacute; c&oacute; thật. Thậm ch&iacute; c&ograve;n hơn cả niềm tin th&ocirc;ng thường, đ&oacute; l&agrave; niềm tin v&ocirc; điều kiện, gần như l&agrave; sự x&aacute;c t&iacute;n. Như c&aacute;c nh&agrave; to&aacute;n học tin v&agrave;o định đề Euclide hay c&aacute;c t&iacute;n đồ Thi&ecirc;n Ch&uacute;a tin v&agrave;o sự sống lại của Jesus.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&Ocirc;i, nhưng đ&oacute; cũng lại l&agrave; những vấn đề của người lớn.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i kể tiếp c&acirc;u chuyện của t&ocirc;i hồi t&aacute;m tuổi.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Như vậy, ra khỏi nh&agrave; một l&aacute;t th&igrave; t&ocirc;i tới trường.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Trong lớp, t&ocirc;i lu&ocirc;n lu&ocirc;n ngồi ở b&agrave;n ch&oacute;t. Ngồi b&agrave;n ch&oacute;t th&igrave; tha hồ t&aacute;n gẫu, c&atilde;i cọ, cấu v&eacute;o hay giở đủ tr&ograve; nghịch ngợm m&agrave; kh&ocirc;ng sợ bị c&ocirc; gi&aacute;o ph&aacute;t hiện, nhưng điều hấp dẫn nhất ở vị tr&iacute; tối tăm đ&oacute; l&agrave; &iacute;t khi bị k&ecirc;u l&ecirc;n bảng trả b&agrave;i.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ETH;iều đ&oacute; c&oacute; quy luật của n&oacute;. Bạn nhớ lại đi, c&oacute; phải bạn c&oacute; rất nhiều bạn b&egrave;, y&ecirc;u qu&iacute; rất nhiều người nhưng kh&ocirc;ng phải l&uacute;c n&agrave;o bạn cũng nhớ tới họ.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Bộ nhớ ch&uacute;ng ta qu&aacute; nhỏ để chứa c&ugrave;ng l&uacute;c nhiều khu&ocirc;n mặt hay nhiều c&aacute;i t&ecirc;n, chỉ khi n&agrave;o nh&igrave;n thấy người đ&oacute; ngo&agrave;i phố hay bắt gặp c&aacute;i t&ecirc;n đ&oacute; trong một mẩu tin tr&ecirc;n b&aacute;o chẳng hạn th&igrave; ch&uacute;ng ta mới chợt nhớ ra v&agrave; cảm động thốt l&ecirc;n </span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- &quot;&Ocirc;i, đ&atilde; l&acirc;u lắm m&igrave;nh kh&ocirc;ng gặp n&oacute;. Năm ngo&aacute;i m&igrave;nh kẹt tiền, n&oacute; c&oacute; cho m&igrave;nh vay năm trăm ng&agrave;n!&quot;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">C&ocirc; gi&aacute;o của t&ocirc;i cũng vậy th&ocirc;i. L&agrave;m sao c&ocirc; c&oacute; thể nhớ tới t&ocirc;i v&agrave; k&ecirc;u t&ocirc;i l&ecirc;n bảng trả b&agrave;i khi m&agrave; c&ocirc; kh&ocirc;ng thể n&agrave;o nh&igrave;n thấy t&ocirc;i giữa một đống đầu cổ l&uacute;c nh&uacute;c che chắn trước mặt.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Ng&agrave;y n&agrave;o cũng như ng&agrave;y n&agrave;o, t&ocirc;i ngồi đ&oacute;, vừa x&igrave; xầm tr&ograve; chuyện vừa cựa quậy lung tung, v&agrave; mong ng&oacute;ng tiếng chu&ocirc;ng ra chơi đến chết được.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Trong những năm th&aacute;ng m&agrave; người ta gọi một c&aacute;ch văn hoa l&agrave; m&agrave;i đũng quần tr&ecirc;n ghế nh&agrave; trường (t&ocirc;i th&igrave; n&oacute;i thẳng l&agrave; bị giam cầm trong lớp học), t&ocirc;i chẳng th&iacute;ch được giờ n&agrave;o cả, từ giờ to&aacute;n, giờ tập viết đến giờ tập đọc, giờ ch&iacute;nh tả. T&ocirc;i chỉ th&iacute;ch mỗi giờ ra chơi.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Ra chơi c&oacute; lẽ l&agrave; điều tuyệt vời nhất m&agrave; người lớn c&oacute; thể nghĩ ra cho trẻ con. Ra chơi c&oacute; nghĩa l&agrave; những lời v&agrave;ng ngọc của thầy c&ocirc; tuột khỏi tr&iacute; nhớ nhanh như gi&oacute;, hết sức trơn tru. Ra chơi c&oacute; nghĩa l&agrave; được th&aacute;o cũi sổ lồng (tất nhi&ecirc;n sau đ&oacute; phải bấm bụng chui v&agrave;o lại), l&agrave; được tha hồ h&iacute;t thở kh&ocirc;ng kh&iacute; tự do.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Suốt những năm đi học, t&ocirc;i v&agrave; lũ bạn đ&atilde; sử dụng những khoảnh khắc tự do hiếm hoi đ&oacute; v&agrave;o việc đ&aacute; b&oacute;ng, bắn bi, nhưng thường xuy&ecirc;n nhất v&agrave; hăng h&aacute;i nhất l&agrave; những tr&ograve; rượt đuổi, đ&aacute;nh nhau hay vật nhau xuống đất cho đến khi kh&ocirc;ng đứa n&agrave;o c&ograve;n ra h&igrave;nh th&ugrave; một học sinh ngoan ngo&atilde;n nữa mới th&ocirc;i, tức l&agrave; l&uacute;c khuỷu tay đ&atilde; trầy xước, mắt đ&atilde; bầm t&iacute;m, ch&acirc;n đi c&agrave; nhắc v&agrave; &aacute;o quần th&igrave; tr&ocirc;ng c&ograve;n tệ hơn mớ giẻ lau nh&agrave;.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Tại sao t&ocirc;i kh&ocirc;ng kể giờ ra về v&agrave;o đ&acirc;y. V&igrave; ra về c&oacute; nghĩa l&agrave; rời khỏi một nh&agrave; giam n&agrave;y để đến một nh&agrave; giam kh&aacute;c, y như người ta chuyển trại cho c&aacute;c t&ugrave; nh&acirc;n, c&oacute; g&igrave; hay ho đ&acirc;u.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i kh&ocirc;ng n&oacute;i qu&aacute; l&ecirc;n đ&acirc;u, v&igrave; ng&agrave;y n&agrave;o ch&agrave;o đ&oacute;n t&ocirc;i ở đầu ng&otilde; cũng l&agrave; khu&ocirc;n mặt lo lắng của mẹ t&ocirc;i v&agrave; khu&ocirc;n mặt hầm hầm của ba t&ocirc;i.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Trời ơi, sao ng&agrave;y n&agrave;o cũng ra n&ocirc;ng nỗi thế n&agrave;y hả con?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ETH;ại kh&aacute;i mẹ t&ocirc;i n&oacute;i thế, giọng thảng thốt, vừa n&oacute;i vừa nắn n&oacute;t c&aacute;nh tay rướm m&aacute;u của t&ocirc;i như để xem n&oacute; sắp rụng khỏi người t&ocirc;i chưa.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Ba t&ocirc;i th&igrave; c&oacute; c&aacute;ch n&oacute;i kh&aacute;c, rất gần với c&aacute;ch rồng phun lửa:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- M&agrave;y lại đ&aacute;nh nhau rồi phải kh&ocirc;ng?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Con kh&ocirc;ng đ&aacute;nh nhau. Tụi bạn đ&aacute;nh con v&agrave; con đ&aacute;nh lại.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i n&oacute;i dối (mặc d&ugrave; n&oacute;i dối như thế c&ograve;n thật hơn l&agrave; n&oacute;i thật) v&agrave; khi ba t&ocirc;i tiến về ph&iacute;a t&ocirc;i với d&aacute;ng điệu của một cơn b&atilde;o cấp mười tiến v&agrave;o đất liền th&igrave; mẹ t&ocirc;i đ&atilde; kịp k&eacute;o t&ocirc;i ra xa:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- &Ocirc;ng ơi, con n&oacute; đ&atilde; n&aacute;t nhừ ra rồi!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Mẹ t&ocirc;i c&oacute; c&aacute;ch n&oacute;i cường điệu rất giống t&ocirc;i, t&ocirc;i vừa chạy theo b&agrave; vừa cười thầm về điều đ&oacute;.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Sau đ&oacute;, kh&ocirc;ng n&oacute;i th&igrave; ai cũng biết l&agrave; t&ocirc;i bị mẹ t&ocirc;i tống v&agrave;o nh&agrave; tắm. Khi t&ocirc;i đ&atilde; tinh tươm v&agrave; thơm phức như một ổ b&aacute;nh m&igrave; mới ra l&ograve; th&igrave; mẹ t&ocirc;i bắt đầu b&ocirc;i l&ecirc;n người t&ocirc;i đủ thứ thuốc xanh xanh đỏ đỏ khiến t&ocirc;i chẳng mấy chốc đ&atilde; rất giống một con tắc k&egrave; b&ocirc;ng.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Dĩ nhi&ecirc;n l&agrave; từ đ&oacute; cho tới bữa cơm, t&ocirc;i kh&ocirc;ng được ph&eacute;p bước ra khỏi nh&agrave; để tr&aacute;nh phải sa v&agrave;o những tr&ograve; đ&aacute;nh nhau kh&aacute;c hấp dẫn kh&ocirc;ng k&eacute;m với bọn nh&oacute;c trong x&oacute;m, những đối thủ thay thế hết sức xứng đ&aacute;ng cho tụi bạn ở trường.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Ăn trưa xong th&igrave; t&ocirc;i l&agrave;m g&igrave; v&agrave;o thời t&ocirc;i t&aacute;m tuổi?&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ETH;i ngủ trưa!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Tr&ecirc;n thế giới rộng lớn n&agrave;y, c&oacute; lẽ c&oacute; rất nhiều đứa nh&oacute;c trạc tuổi t&ocirc;i đều bị c&aacute;c bậc phụ huynh cột chặt v&agrave;o giấc ngủ trưa theo c&aacute;i c&aacute;ch người ta cột b&ograve; v&agrave;o cọc để ch&uacute;ng khỏi chạy lung tung m&agrave; hậu quả l&agrave; thế n&agrave;o h&agrave;ng x&oacute;m cũng k&eacute;o đến nh&agrave; chửi bới om s&ograve;m.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Chứ thực ra với một đứa b&eacute; t&aacute;m tuổi th&igrave; giấc ngủ trưa chẳng c&oacute; gi&aacute; trị g&igrave; về mặt sức khỏe. Khi t&ocirc;i lớn l&ecirc;n th&igrave; t&ocirc;i phải c&ocirc;ng nhận giấc ngủ trưa đối với một người lớn tuổi đ&uacute;ng l&agrave; qu&yacute; hơn v&agrave;ng. Lớn tuổi th&igrave; sức khỏe suy giảm. L&agrave;m việc nhiều th&igrave; đầu nhức, mắt mờ, lưng mỏi, tay run, giấc ngủ ban đ&ecirc;m vẫn chưa đủ liều để sửa chữa th&agrave;nh c&ocirc;ng những chỗ hỏng h&oacute;c của cơ thể. Buổi trưa phải chợp mắt th&ecirc;m một l&aacute;t th&igrave; buổi chiều mới đủ tỉnh t&aacute;o m&agrave; kh&ocirc;ng nện b&uacute;a v&agrave;o tay hay hụt ch&acirc;n khi bước xuống cầu thang.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Nhưng nếu bạn sống tr&ecirc;n đời mới c&oacute; t&aacute;m năm th&igrave; bạn kh&ocirc;ng c&oacute; l&yacute; do ch&iacute;nh đ&aacute;ng để coi trọng giấc ngủ trưa. Với những d&acirc;n tộc kh&ocirc;ng c&oacute; th&oacute;i quen ngủ trưa, như d&acirc;n Mỹ chẳng hạn, trẻ con c&agrave;ng kh&ocirc;ng t&igrave;m thấy ch&uacute;t x&iacute;u &yacute; nghĩa n&agrave;o trong việc phải leo l&ecirc;n giường sau giờ cơm trưa.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Hồi t&ocirc;i t&aacute;m tuổi dĩ nhi&ecirc;n t&ocirc;i kh&ocirc;ng c&oacute; được c&aacute;i nh&igrave;n th&ocirc;ng th&aacute;i như thế. Nhưng t&ocirc;i cũng lờ mờ nhận ra khi ba t&ocirc;i đi ngủ th&igrave; t&ocirc;i buộc phải đi ngủ, giống như một con cừu c&ograve;n thức th&igrave; người chăn cừu kh&ocirc;ng y&ecirc;n t&acirc;m chợp mắt vậy.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i nằm cựa quậy b&ecirc;n cạnh &ocirc;ng tr&ecirc;n chiếc đi-văng, thở d&agrave;i thườn thượt khi nghĩ đến những quả đấm m&agrave; lũ bạn nghịch ngợm đang vung l&ecirc;n ngo&agrave;i kia.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- &ETH;ừng cựa quậy! Cựa quậy ho&agrave;i th&igrave; sẽ kh&ocirc;ng ngủ được!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Ba t&ocirc;i n&oacute;i, v&agrave; t&ocirc;i vờ nghe lời &ocirc;ng. T&ocirc;i kh&ocirc;ng cựa quậy nhưng mắt vẫn mở thao l&aacute;o.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- &ETH;ừng mở mắt! Mở mắt ho&agrave;i th&igrave; sẽ kh&ocirc;ng ngủ được!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Ba t&ocirc;i lại n&oacute;i, &ocirc;ng vẫn nằm ngay ngắn n&ecirc;n t&ocirc;i nghĩ l&agrave; &ocirc;ng kh&ocirc;ng nh&igrave;n thấy t&ocirc;i mở mắt, &ocirc;ng chỉ đo&aacute;n thế th&ocirc;i. Chẳng may cho t&ocirc;i l&agrave; lần n&agrave;o &ocirc;ng cũng đo&aacute;n đ&uacute;ng.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i nhắm mắt lại, lim dim th&ocirc;i, mi mắt vẫn c&ograve;n hấp h&aacute;y, nhưng t&ocirc;i kh&ocirc;ng thể n&agrave;o bắt mi mắt t&ocirc;i đừng hấp h&aacute;y được.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Một l&aacute;t, ba t&ocirc;i hỏi:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Con ngủ rồi phải kh&ocirc;ng?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Dạ rồi.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i đ&aacute;p, ng&acirc;y ng&ocirc; v&agrave; ngoan ngo&atilde;n, rơi v&agrave;o bẫy của ba t&ocirc;i một c&aacute;ch dễ d&agrave;ng.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i nằm như vậy, thao thức một l&aacute;t, tủi th&acirc;n v&agrave; sầu muộn, rồi thiếp đi l&uacute;c n&agrave;o kh&ocirc;ng hay.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Khi t&ocirc;i thức dậy th&igrave; đường đời của t&ocirc;i đ&atilde; được vạch sẵn rồi. T&ocirc;i đi từ giường ngủ đến ph&ograve;ng tắm để rửa mặt rồi từ ph&ograve;ng tắm đi thẳng tới b&agrave;n học để l&agrave;m một c&ocirc;ng việc ch&aacute;n ngắt l&agrave; học b&agrave;i hoặc l&agrave;m b&agrave;i tập.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Thỉnh thoảng t&ocirc;i cũng được ph&eacute;p chạy ra đằng trước nh&agrave; chơi với lũ trẻ h&agrave;ng x&oacute;m nhưng trước &aacute;nh mắt gi&aacute;m s&aacute;t của mẹ t&ocirc;i (từ một vị tr&iacute; b&iacute; hiểm n&agrave;o đ&oacute; đằng sau c&aacute;c &ocirc; cửa m&agrave; m&atilde;i m&atilde;i t&ocirc;i kh&ocirc;ng kh&aacute;m ph&aacute; được), t&ocirc;i chỉ d&aacute;m chơi những tr&ograve; ẻo lả như nhảy l&ograve; c&ograve; hay bịt mắt bắt d&ecirc;, đại kh&aacute;i l&agrave; những tr&ograve; d&agrave;nh cho bọn con g&aacute;i hay kh&oacute;c nh&egrave;. (Về sau, tinh kh&ocirc;n hơn, t&ocirc;i đ&atilde; biết c&aacute;ch ỉ &ocirc;i để mẹ t&ocirc;i thả t&ocirc;i qua nh&agrave; h&agrave;ng x&oacute;m, nhờ đ&oacute; một thời gian d&agrave;i t&ocirc;i đ&atilde; c&oacute; cơ hội l&agrave;m những g&igrave; t&ocirc;i th&iacute;ch).</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Chơi một l&aacute;t, t&ocirc;i lại phải v&agrave;o ngồi &ecirc; a tụng b&agrave;i tiếp, c&agrave;ng tụng c&agrave;ng qu&ecirc;n, nhưng vẫn cứ tụng cho mẹ t&ocirc;i y&ecirc;n l&ograve;ng đi nấu cơm.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Từ gi&acirc;y ph&uacute;t n&agrave;y trở đi th&igrave; đời sống của t&ocirc;i tẻ nhạt v&ocirc; bờ bến.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i uể oải học b&agrave;i trong khi chờ cơm ch&iacute;n. Cơm ch&iacute;n rồi th&igrave; t&ocirc;i uể oải ăn cơm trong khi chờ tiếp tục học b&agrave;i.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Tivi tiveo hiếm khi t&ocirc;i m&oacute; tay v&agrave;o được, tr&ocirc;ng n&oacute; cứ như một thứ để trang tr&iacute;. Bao giờ cũng vậy, t&ocirc;i chỉ được rời khỏi b&agrave;n học khi n&agrave;o t&ocirc;i đ&atilde; thuộc tất cả b&agrave;i vở của ng&agrave;y h&ocirc;m sau.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Ba t&ocirc;i l&agrave; người trực tiếp kiểm tra điều đ&oacute;. Kh&aacute;c với mẹ t&ocirc;i, ba t&ocirc;i l&agrave; người ki&ecirc;n quyết đến mức t&ocirc;i c&oacute; cảm tưởng &ocirc;ng sẽ thăng tiến v&ugrave;n vụt nếu v&ocirc; ng&agrave;nh cảnh s&aacute;t, t&ograve;a &aacute;n hay thuế vụ. &Ocirc;ng kh&ocirc;ng bao giờ l&ugrave;i bước trước những giọt nước mắt của t&ocirc;i, d&ugrave; l&uacute;c đ&oacute; tr&ocirc;ng t&ocirc;i rất giống một kẻ sầu đời đến mức chỉ c&aacute;ch c&aacute;i chết c&oacute; một bước ch&acirc;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Con học b&agrave;i xong rồi ba. - Thường th&igrave; t&ocirc;i mở miệng trước.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Ba t&ocirc;i tiến lại v&agrave; nh&igrave;n t&ocirc;i bằng &aacute;nh mắt nghi ngờ:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Chắc kh&ocirc;ng con?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Dạ, chắc!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i mau mắn đ&aacute;p v&agrave; khi ba t&ocirc;i bắt đầu d&ograve; b&agrave;i th&igrave; t&ocirc;i lập tức phủ nhận sạch trơn sự quả quyết của m&igrave;nh bằng c&aacute;ch ngắc ngứ ngay ở chỗ m&agrave; t&ocirc;i nghĩ d&ugrave; c&oacute; va đầu phải gốc c&acirc;y t&ocirc;i cũng kh&ocirc;ng thể n&agrave;o qu&ecirc;n được.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Học lại lần nữa đi con!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Ba t&ocirc;i nh&uacute;n vai n&oacute;i v&agrave; quay đi với tờ b&aacute;o vẫn cầm chặt tr&ecirc;n tay, r&otilde; r&agrave;ng &ocirc;ng muốn gửi đến t&ocirc;i th&ocirc;ng điệp rằng &ocirc;ng sẵn s&agrave;ng chờ đợi t&ocirc;i cho d&ugrave; &ocirc;ng buộc phải đọc tới mẩu rao vặt cuối c&ugrave;ng khi kh&ocirc;ng c&ograve;n g&igrave; để m&agrave; đọc nữa.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Qua c&aacute;i c&aacute;ch &ocirc;ng vung vẩy tờ b&aacute;o tr&ecirc;n tay, t&ocirc;i e rằng ẩn &yacute; của &ocirc;ng c&ograve;n đi xa hơn: c&oacute; vẻ như nếu cần, &ocirc;ng sẽ bắt đầu đọc lại tờ b&aacute;o đến lần thứ hai v&agrave; hơn thế nữa. Nghĩ vậy, t&ocirc;i đ&agrave;nh v&ugrave;i đầu v&agrave;o những con chữ m&agrave; l&uacute;c n&agrave;y đối với t&ocirc;i đ&atilde; như những kẻ tử th&ugrave;, t&acirc;m trạng đ&oacute; c&agrave;ng khiến t&ocirc;i kh&oacute; m&agrave; ghi nhớ ch&uacute;ng v&ocirc; đầu &oacute;c.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Cho n&ecirc;n c&aacute;c bạn cũng c&oacute; thể đo&aacute;n ra khi t&ocirc;i đ&atilde; thuộc t&agrave;m tạm, nghĩa l&agrave; kh&ocirc;ng tr&ocirc;i chảy lắm th&igrave; cơ thể t&ocirc;i đ&atilde; bị giấc ngủ đ&aacute;nh gục một c&aacute;ch kh&ocirc;ng thương tiếc v&agrave; thường th&igrave; t&ocirc;i lết v&agrave;o giường bằng những bước ch&acirc;n xi&ecirc;u vẹo, nửa tỉnh nửa m&ecirc; trước &aacute;nh mắt x&oacute;t xa của mẹ t&ocirc;i.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Như vậy, t&oacute;m lại l&agrave; đ&atilde; hết một ng&agrave;y.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#ff0000\">Chương 2: Bố mẹ tuyệt vời</span></span></span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i chỉ cần kể một ng&agrave;y l&agrave; đ&atilde; đủ, kh&ocirc;ng cần phải kể th&ecirc;m những ng&agrave;y kh&aacute;c.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ETH;ơn giản l&agrave; ng&agrave;y n&agrave;o cũng giống như ng&agrave;y n&agrave;o. Một ng&agrave;y như mọi ng&agrave;y, như người ta vẫn n&oacute;i.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">V&agrave; v&igrave; thế cuộc sống đối với t&ocirc;i thật l&agrave; đơn điệu, nếu sự lặp đi lặp lại l&agrave; biểu hiện ch&iacute;nh x&aacute;c nhất v&agrave; r&otilde; rệt nhất của sự đơn điệu.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">M&atilde;i về sau n&agrave;y, t&ocirc;i mới kh&aacute;m ph&aacute; ra c&ograve;n c&oacute; c&aacute;ch nh&igrave;n kh&aacute;c về sự lặp đi lặp lại. Người ta gọi n&oacute; l&agrave; sự ổn định.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Một c&ocirc;ng việc c&oacute; thể sắp đặt trước, một sự nghiệp c&oacute; thể t&iacute;nh to&aacute;n trước, l&agrave; niềm ao ước của rất nhiều người, nhiều quốc gia.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Tất nhi&ecirc;n sẽ thật l&agrave; hay nếu ti&ecirc;n liệu được chỉ số tăng trưởng kinh tế của một đất nước nhưng nếu bạn cũng ti&ecirc;n liệu ch&iacute;nh x&aacute;c như thế về chỉ số tăng trưởng t&igrave;nh cảm của bản th&acirc;n th&igrave; điều đ&oacute; c&oacute; khi lại ch&aacute;n ngắt. Sẽ thật kỳ cục nếu như bạn tin chắc rằng một th&aacute;ng nữa bạn sẽ bắt đầu y&ecirc;u, ba th&aacute;ng sau bạn sẽ đang y&ecirc;u - &iacute;t th&ocirc;i, s&aacute;u th&aacute;ng sau bạn sẽ y&ecirc;u nhiều hơn...</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i từng thấy c&oacute; nhiều người trẻ tuổi l&ecirc;n kế hoạch cho cuộc đời m&igrave;nh: 22 tuổi tốt nghiệp đại học, 25 tuổi lập gia đ&igrave;nh, 27 tuổi mở c&ocirc;ng ty, 30 tuổi sinh con đầu l&ograve;ng, v&acirc;n v&acirc;n v&agrave; v&acirc;n v&acirc;n... Thật s&iacute;t sao! Nhưng một khi cuộc đời một con người được lập tr&igrave;nh chặt chẽ v&agrave; khoa học đến thế th&igrave; nếu tất cả đều v&agrave;o khu&ocirc;n như dự t&iacute;nh liệu bạn c&oacute; b&atilde;o h&ograve;a về cảm x&uacute;c hay kh&ocirc;ng?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Khi n&oacute;i về cảm x&uacute;c c&oacute; lẽ kh&ocirc;ng thể kh&ocirc;ng gắn n&oacute; với t&iacute;nh c&aacute;ch của từng người. Người lạc quan bảo rằng ổn định c&aacute;i điều m&agrave; người bi quan cho l&agrave; đơn điệu. Cuộc sống vợ chồng cũng thế th&ocirc;i, kẻ th&igrave; bảo &ecirc;m đềm, người th&igrave; cho v&ocirc; vị, biết l&agrave;m thế n&agrave;o! Quả thật, hai vợ chồng m&agrave; sống với nhau &ecirc;m đềm qu&aacute; kh&ocirc;ng kh&eacute;o lại giống sự &ecirc;m đềm giữa hai người h&agrave;ng x&oacute;m l&agrave;nh t&iacute;nh, v&agrave; người qu&aacute; kh&iacute;ch lại c&oacute; dịp b&ocirc; b&ocirc; l&ecirc;n rằng &ecirc;m đềm kh&ocirc;ng hề b&agrave; con g&igrave; với hạnh ph&uacute;c, biết n&oacute;i l&agrave;m sao!&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Nhưng &ocirc;i th&ocirc;i, t&ocirc;i lại n&oacute;i chuyện l&uacute;c t&ocirc;i đ&atilde; l&agrave; người lớn mất rồi. Lại n&oacute;i chuyện vợ chồng cấm kỵ v&ocirc; đ&acirc;y nữa!&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i sẽ quay lại chủ đề của cuốn s&aacute;ch n&agrave;y, quay lại ngay đ&acirc;y, tức l&agrave; n&oacute;i c&aacute;i chuyện t&ocirc;i hồi t&aacute;m tuổi.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Chuyện t&ocirc;i sắp kể ra đ&acirc;y, khổ thay, cũng lại li&ecirc;n quan đến chuyện vợ chồng. Nhưng b&ecirc;n cạnh c&aacute;i khổ cũng c&oacute; c&aacute;i may, đ&acirc;y chỉ l&agrave; tr&ograve; chơi vợ chồng th&ocirc;i - c&aacute;i tr&ograve; m&agrave; đứa trẻ n&agrave;o bằng tuổi t&ocirc;i cũng rất th&iacute;ch chơi mặc d&ugrave; khi lớn l&ecirc;n th&igrave; ch&uacute;ng rất d&egrave; chừng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i v&agrave; con T&iacute; s&uacute;n cạnh nh&agrave; t&ocirc;i l&agrave; một cặp.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i l&agrave; chồng, con T&iacute; s&uacute;n l&agrave; vợ.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Con T&iacute; s&uacute;n kh&ocirc;ng đẹp đẽ g&igrave;, người đen nhẻm, t&oacute;c xoăn t&iacute;t v&igrave; suốt ng&agrave;y chạy nhảy ngo&agrave;i nắng, đ&atilde; thế lại s&uacute;n răng.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Nhưng t&ocirc;i sẵn s&agrave;ng chấp nhận n&oacute; l&agrave;m vợ t&ocirc;i, chỉ v&igrave; n&oacute; th&iacute;ch t&ocirc;i, t&ocirc;i bảo g&igrave; n&oacute; cũng nghe răm rắp. Thật l&ograve;ng, t&ocirc;i th&iacute;ch con Tủn hơn, v&igrave; con Tủn xinh g&aacute;i nhất x&oacute;m, lại c&oacute; l&uacute;m đồng tiền. Nhưng t&ocirc;i kh&ocirc;ng cưới con Tủn bởi t&ocirc;i thấy n&oacute; cứ hay cặp k&egrave; với thằng Hải c&ograve;. Sau n&agrave;y t&ocirc;i biết đ&oacute; l&agrave; cảm gi&aacute;c ghen tu&ocirc;ng, tất nhi&ecirc;n l&agrave; ghen tu&ocirc;ng theo kiểu trẻ con, c&ograve;n l&uacute;c đ&oacute; t&ocirc;i chỉ cảm thấy kh&oacute; chịu th&ocirc;i.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">V&agrave; t&ocirc;i đ&ugrave;ng đ&ugrave;ng cưới con T&iacute; s&uacute;n, theo kiểu người lớn hay n&oacute;i: cưới người y&ecirc;u m&igrave;nh chứ kh&ocirc;ng cưới người m&igrave;nh y&ecirc;u, nhất l&agrave; khi người m&igrave;nh y&ecirc;u lại kh&ocirc;ng c&oacute; vẻ g&igrave; y&ecirc;u m&igrave;nh!&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i cưới con T&iacute; s&uacute;n chừng năm ph&uacute;t th&igrave; lập tức đẻ liền một l&uacute;c hai đứa con: thằng Hải c&ograve; v&agrave; con Tủn. Gh&eacute;t hai đứa n&oacute; th&igrave; bắt ch&uacute;ng l&agrave;m con vậy th&ocirc;i, chứ thằng Hải c&ograve; lớn hơn t&ocirc;i một tuổi</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Hải c&ograve; đ&acirc;u? - T&ocirc;i k&ecirc;u lớn.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Dạ, ba gọi con. - Hải c&ograve; lon ton chạy tới.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i ra oai:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- R&oacute;t cho ba miếng nước!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Thấy con Tủn che miệng cười kh&uacute;c kh&iacute;ch, Hải c&ograve; đ&acirc;m bướng:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Con đang học b&agrave;i.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Giờ n&agrave;y m&agrave; học b&agrave;i hả? - T&ocirc;i qu&aacute;t ầm - &ETH;ồ l&ecirc;u lổng!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Hải c&ograve; đưa tay ngo&aacute;y lỗ tai để nghe cho r&otilde;:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Học b&agrave;i l&agrave; l&ecirc;u lổng?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Chứ g&igrave; nữa! Kh&ocirc;ng học b&agrave;i l&agrave;m b&agrave;i g&igrave; hết! Con ngoan l&agrave; phải chạy nhảy, tr&egrave;o c&acirc;y, tắm s&ocirc;ng, đ&aacute;nh lộn!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Hải c&ograve; kh&ocirc;ng ngờ vớ được một &ocirc;ng bố đi&ecirc;n đi&ecirc;n như thế, cười to&eacute;t miệng:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Vậy con đi đ&aacute;nh lộn đ&acirc;y!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">N&oacute;i xong, n&oacute; co gi&ograve; chạy mất.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Nhưng t&ocirc;i kh&ocirc;ng giận n&oacute;. T&ocirc;i đang kho&aacute;i ch&iacute;. T&ocirc;i t&igrave;nh cờ ph&aacute;t hiện ra c&aacute;ch l&agrave;m cho cuộc sống bớt tẻ nhạt.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Tủn! - T&ocirc;i h&eacute;t.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Dạ. R&oacute;t nước hả ba?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i cười khảy:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- M&agrave;y đừng l&agrave;m ra vẻ ta đ&acirc;y th&ocirc;ng minh. Tao hết kh&aacute;t rồi.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i n&oacute;i như tr&uacute;t giận:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Tao l&agrave; tao ch&uacute;a gh&eacute;t mấy đứa con n&iacute;t th&ocirc;ng minh, tức l&agrave; mấy đứa học b&agrave;i nho&aacute;ng một c&aacute;i đ&atilde; thuộc vanh v&aacute;ch! Hừm, l&agrave;m như hay lắm!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Con Tủn kh&ocirc;ng biết t&ocirc;i muốn g&igrave;. Thấy t&ocirc;i qu&aacute;t s&ugrave;i bọt m&eacute;p, n&oacute; sợ run:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Dạ, con kh&ocirc;ng th&ocirc;ng minh. Con l&agrave; đứa ngu đần.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i hả h&ecirc;:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Vậy con mới đ&uacute;ng l&agrave; con ngoan của ba.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i m&oacute;c t&uacute;i lấy ra một c&acirc;y kẹo b&eacute; tẹo c&ograve;n s&oacute;t lại từ h&ocirc;m qua:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- &ETH;&acirc;y, ba thưởng cho con.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Con Tủn ngơ ng&aacute;c cầm lấy c&acirc;y kẹo, kh&ocirc;ng hiểu tại sao ngu m&agrave; được thưởng n&ecirc;n kh&ocirc;ng d&aacute;m ăn.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i đang t&iacute;nh bảo con Tủn &ldquo;Ăn đi con&rdquo; th&igrave; thằng Hải c&ograve; từ b&ecirc;n ngo&agrave;i xồng xộc chạy v&ocirc;, miệng thở hổn hển, l&agrave;m như vừa đ&aacute;nh nhau thật.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Con đi đ&aacute;nh lộn về đ&oacute; hả con? - T&ocirc;i &acirc;u yếm hỏi.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Dạ. - Hải c&ograve; phấn khởi - Con u&yacute;nh một l&uacute;c mười đứa lu&ocirc;n đ&oacute; ba!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Con thiệt l&agrave; ngoan. - T&ocirc;i khen, v&agrave; đưa mắt nh&igrave;n Hải c&ograve; từ đầu tới ch&acirc;n - Thế quần &aacute;o của con...</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Vẫn kh&ocirc;ng sao ba &agrave;. - Hải c&ograve; hớn hở khoe - Con đập nhau với tụi n&oacute; m&agrave; quần &aacute;o vẫn l&agrave;nh lặn, thẳng thớm...</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- &ETH;ồ khốn! - T&ocirc;i qu&aacute;t lớn, kh&ocirc;ng cho Hải c&ograve; n&oacute;i hết c&acirc;u - &ETH;&aacute;nh nhau m&agrave; kh&ocirc;ng r&aacute;ch &aacute;o, trầy ch&acirc;n, bầm mặt m&agrave; cũng gọi l&agrave; đ&aacute;nh nhau hả?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Sự giận dữ bất ngờ của t&ocirc;i l&agrave;m Hải c&ograve; nghệt mặt một l&uacute;c. N&oacute; chẳng biết phản ứng thế n&agrave;o ngo&agrave;i việc ấp a ấp &uacute;ng:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Dạ... dạ... ủa... ủa...</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Dạ dạ ủa ủa c&aacute;i g&igrave;! Con thiệt l&agrave; đứa hư hỏng! Con l&agrave;m ba xấu hổ đến chết mất th&ocirc;i!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Con T&iacute; s&uacute;n, vợ t&ocirc;i, bắt đầu cảm thấy hoang mang trước lối dạy con của t&ocirc;i:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- &Ocirc;ng &agrave;, con n&oacute; biết giữ g&igrave;n như thế l&agrave; tốt rồi.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- B&agrave; th&igrave; biết c&aacute;i g&igrave;! - T&ocirc;i nạt con T&iacute; s&uacute;n, nước miếng bay v&egrave;o v&egrave;o may m&agrave; kh&ocirc;ng tr&uacute;ng mặt n&oacute; - &ETH;&aacute;nh nhau chứ c&oacute; phải đi dự tiệc đ&acirc;u! &ETH;&aacute;nh nhau m&agrave; quần &aacute;o sạch sẽ thế kia th&igrave; c&oacute; nhục cho tổ ti&ecirc;n kh&ocirc;ng kia chứ!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i đấm ngực binh binh:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- &Ocirc;i, chẳng th&agrave; n&oacute; ch&eacute;m t&ocirc;i một dao cho rồi! Con ơi l&agrave; con! M&agrave;y ra đ&acirc;y m&agrave; giết ba m&agrave;y đi con!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Thấy t&ocirc;i tru tr&eacute;o gh&ecirc; qu&aacute;, con T&iacute; s&uacute;n n&iacute;n khe.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Trong khi thằng Hải c&ograve; cười h&iacute; h&iacute; th&igrave; con Tủn mặt đực ra như bị thằn lằn ị tr&uacute;ng mặt. N&oacute; kh&ocirc;ng biết l&agrave;m g&igrave; với c&acirc;y kẹo tr&ecirc;n tay, rằng n&ecirc;n nh&eacute;t v&agrave;o t&uacute;i &aacute;o hay bỏ v&agrave;o miệng. Tr&ocirc;ng mặt n&oacute; hết sức lo lắng, c&oacute; lẽ v&igrave; n&oacute; ho&agrave;n to&agrave;n kh&ocirc;ng biết được h&agrave;nh động n&agrave;o mới kh&ocirc;ng bị &ocirc;ng bố g&agrave;n dở kia liệt v&agrave;o loại &ldquo;hư hỏng&rdquo; hay tệ hơn, l&agrave; &ldquo;l&agrave;m nhục tổ ti&ecirc;n&rdquo;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Tụi bạn t&ocirc;i chỉ ngạc nhi&ecirc;n h&ocirc;m đầu ti&ecirc;n.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Rồi như bất cứ một đứa trẻ ch&acirc;n ch&iacute;nh n&agrave;o, tụi n&oacute; nhanh ch&oacute;ng cảm nhận được sự th&uacute; vị của tr&ograve; chơi tuyệt vời đ&oacute;.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">H&ocirc;m sau đến lượt thằng Hải c&ograve; v&agrave; con Tủn đ&oacute;ng vai ba mẹ. T&ocirc;i v&agrave; con T&iacute; s&uacute;n l&agrave;m con.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Tối h&ocirc;m trước Hải c&ograve; chắc thao thức suốt đ&ecirc;m, chờ trời s&aacute;ng. S&aacute;ng ra t&ocirc;i thấy mắt n&oacute; đỏ k&egrave;. Nếu h&ocirc;m đ&oacute; kh&ocirc;ng phải l&agrave; ng&agrave;y chủ nhật, c&oacute; lẽ Hải c&ograve; sẽ bị sự n&ocirc;n n&oacute;ng đốt th&agrave;nh than trước khi cả bọn đi học về.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Thằng cu M&ugrave;i đ&acirc;u? - Hải c&ograve; oang oang, giọng rất chi l&agrave; h&agrave;o hứng.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Cu M&ugrave;i l&agrave; t&ecirc;n ở nh&agrave; của t&ocirc;i. Ba mẹ t&ocirc;i gọi t&ocirc;i như thế c&oacute; lẽ do t&ocirc;i sinh năm M&ugrave;i.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Dạ. - T&ocirc;i ứng tiếng thưa.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Con đem tập vở ra đ&acirc;y cho ba xem n&agrave;o.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i l&ocirc;i cuốn tập nh&eacute;t trong lưng quần, hồi hộp đưa cho Hải c&ograve;, bụng cố đo&aacute;n xem n&oacute; định &ldquo;dạy dỗ&rdquo; t&ocirc;i như thế n&agrave;o.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Lật lật v&agrave;i trang, Hải c&ograve; h&eacute;t ầm:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Cu M&ugrave;i!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i lấm l&eacute;t nh&igrave;n n&oacute;:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Dạ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Hải c&ograve; đập tay xuống b&agrave;n một c&aacute;i rầm:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Con học h&agrave;nh c&aacute;ch sao m&agrave; tập vở trắng tinh như thế hả?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i chưa kịp đ&aacute;p, n&oacute; thẳng tay n&eacute;m cuốn tập qua cửa sổ, gầm gừ:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Học với chẳng h&agrave;nh! M&agrave;y giữ g&igrave;n tập vở sạch sẽ như thế n&agrave;y m&agrave;y kh&ocirc;ng sợ thầy c&ocirc; bảo ba mẹ m&agrave;y kh&ocirc;ng biết dạy con hả, thằng kia?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i bị mắng như t&aacute;t nước v&agrave;o mặt m&agrave; ruột nở từng kh&uacute;c. T&ocirc;i kh&ocirc;ng ngờ Hải c&ograve; l&agrave; một &ocirc;ng bố tuyệt vời đến thế.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i h&acirc;n hoan nhận lỗi:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Thưa ba, lần n&agrave;y con tr&oacute;t dại. Lần sau con kh&ocirc;ng d&aacute;m giữ g&igrave;n tập vở kỹ lưỡng như vậy nữa.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i n&oacute;i, v&agrave; đảo mắt nh&igrave;n quanh, thấy đằng g&oacute;c nh&agrave; con Tủn v&agrave; con T&iacute; s&uacute;n đưa tay bụm miệng cố n&eacute;n cười.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- C&aacute;i con nh&oacute;c s&uacute;n răng kia! Cười c&aacute;i g&igrave;! - Hải c&ograve; lừ mắt nh&igrave;n con T&iacute; s&uacute;n - M&agrave;y nấu cơm xong chưa m&agrave; đứng đ&oacute; nhe răng s&uacute;n ra cười hả?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Con T&iacute; s&uacute;n lễ ph&eacute;p:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Dạ, con đ&atilde; dọn cơm rồi. Mời ba mẹ v&agrave; anh Hai ăn cơm.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- M&agrave;y c&oacute; đi&ecirc;n kh&ocirc;ng vậy con! - Hải c&ograve; giơ hai tay l&ecirc;n trời - &ETH;ến giờ cơm l&agrave; ngồi v&ocirc; ăn, chỉ c&oacute; kẻ kh&ocirc;ng được gi&aacute;o dục đến nơi đến chốn mới l&agrave;m như vậy, hiểu chưa?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Dạ, chưa hiểu. - Con T&iacute; s&uacute;n thật th&agrave; - Chứ kẻ c&oacute; gi&aacute;o dục th&igrave; đến giờ cơm họ l&agrave;m g&igrave; hả ba?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Họ đi chơi chứ l&agrave;m g&igrave;. - Hải c&ograve; khoa tay như một diễn giả - Họ đi bơi, họ chơi bi-da, họ c&acirc;u c&aacute;, họ chơi rượt bắt hoặc đ&aacute;nh nhau, n&oacute;i chung họ c&oacute; thể l&agrave;m bất cứ chuyện g&igrave; để người kh&aacute;c phải đợi cơm, trừ c&aacute;i chuyện hết sức v&ocirc; văn h&oacute;a l&agrave; ngồi v&ocirc; b&agrave;n ăn.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Con Tủn tỉnh bơ đế v&ocirc;:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Ba con n&oacute;i đ&uacute;ng đ&oacute; con. Chỉ c&oacute; bọn hư hỏng mới ăn cơm đ&uacute;ng giờ th&ocirc;i!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">L&uacute;c đầu, t&ocirc;i tưởng chỉ c&oacute; m&igrave;nh t&ocirc;i kho&aacute;i c&aacute;i tr&ograve; đi&ecirc;n đi&ecirc;n n&agrave;y. H&oacute;a ra đứa n&agrave;o cũng kho&aacute;i. Trong bọn, con T&iacute; s&uacute;n l&agrave; đứa hiền l&agrave;nh v&agrave; chậm chạp nhất nhưng qua đến ng&agrave;y thứ ba, n&oacute; cũng kịp th&iacute;ch ứng với ho&agrave;n cảnh bằng c&aacute;ch chỉnh thằng Hải c&ograve; ra tr&ograve; khi tới lượt n&oacute; l&agrave;m mẹ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- 2 lần 4 l&agrave; mấy?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Dạ, l&agrave; 8.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Con T&iacute; s&uacute;n kh&ocirc;ng qu&aacute;t th&aacute;o om s&ograve;m như t&ocirc;i v&agrave; Hải c&ograve;, nhưng mặt n&oacute; tr&ocirc;ng thật thiểu n&atilde;o:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Sao lại l&agrave; 8 hả con? Thật uổng c&ocirc;ng mẹ cho con ăn học!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Hải c&ograve; chớp mắt:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Chứ l&agrave; mấy?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- L&agrave; mấy cũng được nhưng kh&ocirc;ng phải l&agrave; 8.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Mẹ ơi, theo bản cửu chương th&igrave; 2 lần 4 l&agrave; 8.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- M&agrave;y l&agrave; con vẹt hả con? Bản cửu chương bảo g&igrave; m&agrave;y nghe nấy l&agrave; sao? Thế m&agrave;y kh&ocirc;ng c&oacute; c&aacute;i đầu &agrave;?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Hải c&ograve; sờ tay l&ecirc;n đầu, hối hận:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Con đ&uacute;ng l&agrave; một đứa kh&ocirc;ng c&oacute; đầu &oacute;c. Lần sau con sẽ kh&ocirc;ng nghe theo bất cứ ai nữa, d&ugrave; đ&oacute; l&agrave; bản cửu chương hay thầy c&ocirc; gi&aacute;o. Con hứa với mẹ con sẽ tự suy nghĩ bằng c&aacute;i đầu của con.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">C&acirc;u n&oacute;i của Hải c&ograve; được coi như tuy&ecirc;n bố chung của cả bọn, kết th&uacute;c một thời kỳ tăm tối chỉ biết sống dựa v&agrave;o sự bảo ban của người kh&aacute;c. &Ocirc;i, cuộc sống kể từ l&uacute;c đ&oacute; mới thật đ&aacute;ng sống l&agrave;m sao!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Nhưng như người ta thường n&oacute;i &ldquo;niềm vui ngắn chẳng t&agrave;y gang&rdquo;: v&agrave;o c&aacute;i ng&agrave;y Hải c&ograve; mang bộ mặt ủ &ecirc; đến gặp t&ocirc;i, ch&uacute;ng t&ocirc;i chợt nhận ra cuộc sống vẫn x&aacute;m xịt như thể xưa nay một năm vẫn c&oacute; tới bốn m&ugrave;a đ&ocirc;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- M&agrave;y sao thế? Mới bị ăn đ&ograve;n &agrave;? - T&ocirc;i t&ograve; m&ograve; hỏi.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- Ừ. V&igrave; c&aacute;i tội d&aacute;m bảo chỉ c&oacute; đứa đần độn mới giữ g&igrave;n tập vở sạch sẽ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Con T&iacute; s&uacute;n xuất hiện với bộ mặt thảm sầu:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- C&ograve;n m&igrave;nh bị ba m&igrave;nh phạt v&igrave; khăng khăng 3 lần 5 kh&ocirc;ng phải l&agrave; 15.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Con Tủn g&oacute;p v&agrave;o hai h&agrave;ng nước mắt v&agrave; tiếng th&uacute;t th&iacute;t:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">- C&ograve;n m&igrave;nh th&igrave; mặc cho ba mẹ k&ecirc;u khản cả cổ, m&igrave;nh nhất định kh&ocirc;ng chạy về ăn trưa.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i lướt mắt nh&igrave;n ba đứa bạn, lặng lẽ thở d&agrave;i.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i tập t&agrave;nh l&agrave;m nh&agrave; c&aacute;ch mạng b&eacute; con, ch&aacute;n nản khi kh&ocirc;ng thay đổi được thế giới, đ&atilde; thế c&ograve;n l&agrave;m vạ l&acirc;y cho người kh&aacute;c.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Cho n&ecirc;n t&ocirc;i kh&ocirc;ng ủ &ecirc;, kh&ocirc;ng thảm sầu, kh&ocirc;ng th&uacute;t th&iacute;t v&agrave; rưng rưng hai h&agrave;ng nước mắt.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Nỗi đau của t&ocirc;i lặn v&agrave;o b&ecirc;n trong. N&oacute; s&acirc;u sắc hơn, &iacute;t nhất l&agrave; bằng nỗi đau của ba đứa bạn cộng lại.&nbsp;</span></span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">V&igrave; ng&agrave;y h&ocirc;m qua t&ocirc;i bị ăn đ&ograve;n v&igrave; phạm c&ugrave;ng l&uacute;c cả ba c&aacute;i tội tr&ecirc;n kia.</span></span></span></p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(26, 'Tam quốc diễn nghĩa', 'Văn học nước ngoài', 'La Quán Trung', 1959, 'tam-quoc-dien-nghia.jpg', 'Đã hoàn', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#3a3a3a\"><span style=\"background-color:#ffffff\">Truyện lấy bối cảnh v&agrave;o thời suy vong của nh&agrave; H&aacute;n khi m&agrave; những ho&agrave;ng đế cuối c&ugrave;ng của nh&agrave; H&aacute;n qu&aacute; tin d&ugrave;ng giới hoạn quan m&agrave; gạt bỏ những bề t&ocirc;i trung trực. Triều đ&igrave;nh ng&agrave;y c&agrave;ng b&ecirc; tha, hư n&aacute;t, khiến kinh tế suy sụp v&agrave; an ninh bất ổn. Đến đời H&aacute;n Linh Đế, năm 184, loạn giặc Khăn V&agrave;ng nổ ra do Trương Gi&aacute;c, một người đ&atilde; học được nhiều ma thuật v&agrave; b&ugrave;a ph&eacute;p chữa bệnh, cầm đầu. Sau đ&oacute; l&agrave; sự xuất hiện của ba anh em Lưu Bị, Quan Vũ v&agrave; Trương Phi, cả ba người đều muốn dẹp loạn y&ecirc;n d&acirc;n n&ecirc;n đ&atilde; kết nghĩa với nhau ở vườn đ&agrave;o.&nbsp;Truyện lấy bối cảnh v&agrave;o thời suy vong của nh&agrave; H&aacute;n khi m&agrave; những ho&agrave;ng đế cuối c&ugrave;ng của nh&agrave; H&aacute;n qu&aacute; tin d&ugrave;ng giới hoạn quan m&agrave; gạt bỏ những bề t&ocirc;i trung trực. Triều đ&igrave;nh ng&agrave;y c&agrave;ng b&ecirc; tha, hư n&aacute;t, khiến kinh tế suy sụp v&agrave; an ninh bất ổn. Đến đời H&aacute;n Linh Đế, năm 184, loạn giặc Khăn V&agrave;ng nổ ra do Trương Gi&aacute;c, một người đ&atilde; học được nhiều ma thuật v&agrave; b&ugrave;a ph&eacute;p chữa bệnh, cầm đầu. Sau đ&oacute; l&agrave; sự xuất hiện của ba anh em Lưu Bị, Quan Vũ v&agrave; Trương Phi, cả ba người đều muốn dẹp loạn y&ecirc;n d&acirc;n n&ecirc;n đ&atilde; kết nghĩa với nhau ở vườn đ&agrave;o.</span></span></span></span></p>\r\n', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><span style=\"color:#ff0000\">HỒI I: Tiệc vườn đ&agrave;o, anh h&ugrave;ng kết nghĩa; Ch&eacute;m Khăn V&agrave;ng, h&agrave;o kiệt lập c&ocirc;ng.</span></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Thế lớn trong thi&ecirc;n hạ, cứ tan l&acirc;u rồi lại hợp, hợp l&acirc;u rồi lại tan: Như cuối đời nh&agrave; Chu, bảy nước tranh gi&agrave;nh x&acirc;u x&eacute; nhau rồi sau lại hợp về nh&agrave; Tần. Đến khi nh&agrave; Tần mất, th&igrave; H&aacute;n Sở tranh h&ugrave;ng rồi sau thi&ecirc;n hạ lại hợp về tay nh&agrave; H&aacute;n. Nh&agrave; H&aacute;n, từ l&uacute;c vua Cao tổ (B&aacute;i C&ocirc;ng) ch&eacute;m rắn trắng khởi nghĩa, thống nhất được thi&ecirc;n hạ, sau vua Quang V&uuml; l&ecirc;n ng&ocirc;i, rồi truyền m&atilde;i đến vua Hiến Đế; l&uacute;c bấy giờ lại chia ra th&agrave;nh ba nước. Nguy&ecirc;n nh&acirc;n g&acirc;y ra biến loạn ấy l&agrave; do hai vua Ho&agrave;n đế, Linh đế. Vua Ho&agrave;n đế tin d&ugrave;ng l&uuml; hoạn quan, cấm cố những người hiền sĩ. Đến l&uacute;c vua Ho&agrave;n đế băng h&agrave;, vua Linh đế l&ecirc;n ng&ocirc;i nối nghiệp; được quan đại tướng qu&acirc;n Đậu V&uuml;, quan th&aacute;i ph&oacute; Trần Phồn gi&uacute;p đỡ. Khi ấy, trong triều c&oacute; bọn hoạn quan l&agrave; l&uuml; T&agrave;o Tiết lộng quyền. Đậu V&uuml;, Trần Phồn lập mưu định trừ bọn ấy đi, nhưng v&igrave; cơ mưu tiết lộ n&ecirc;n lại bị ch&uacute;ng n&oacute; giết mất. Từ đấy, bọn hoạn quan ng&agrave;y c&agrave;ng bạo ngược. Ng&agrave;y rằm th&aacute;ng tư năm Kiến ninh thứ hai (một trăm s&aacute;u mươi bảy dương lịch) vua ngự điện &Ocirc;n Đức. Tự nhi&ecirc;n c&oacute; cơn gi&oacute; to ầm ầm từ g&oacute;c điện nổi l&ecirc;n, rồi thấy một con rắn xanh lớn ở tr&ecirc;n x&agrave; nh&agrave; quăng xuống quằn quại tr&ecirc;n long &aacute;n. Vua kinh ho&agrave;ng ng&atilde; đ&ugrave;ng ra, c&aacute;c quan tả hữu vội cứu vực v&agrave;o cung; ở ngo&agrave;i văn v&otilde; c&uuml;ng sợ chạy cả. Được một l&aacute;t con rắn biến mất v&agrave; bỗng nhi&ecirc;n mưa to, sấm s&eacute;t ầm ầm; lại th&ecirc;m mưa đ&aacute; r&agrave;o r&agrave;o m&atilde;i đến nửa đ&ecirc;m mới tạnh, đổ nh&agrave; đổ cửa kh&ocirc;ng biết bao nhi&ecirc;u m&agrave; kể. Th&aacute;ng hai, năm Kiến ninh thứ tư (một trăm s&aacute;u mươi ch&iacute;n) tỉnh Lạc Dương c&oacute; động đất, nước bể d&acirc;ng l&ecirc;n ngập lưng trời, d&acirc;n cư ở ven bể bị s&oacute;ng lớn cuốn tr&ocirc;i đi mất cả. Năm Quang h&oacute;a thứ nhất (một trăm bảy mươi t&aacute;m), một con g&agrave; m&aacute;i tự dưng h&oacute;a ra g&agrave; trống. Mồng một th&aacute;ng s&aacute;u năm ấy, một luồng kh&iacute; đen d&agrave;i chừng hơn mươi trượng bay v&agrave;o trong điện &Ocirc;n Đức. Qua sang th&aacute;ng bảy, lại c&oacute; lắm điều gở lạ: Cầu vồng mọc ở giữa Ngọc đường; rặng n&uacute;i Ng&uuml; Nguy&ecirc;n bỗng dưng lở sụt xuống. Vua hạ chiếu, hỏi chư thần từ đ&acirc;u m&agrave; sinh ra những điềm qu&aacute;i gở ấy. C&oacute; quan nghị lang l&agrave; S&aacute;i Ung d&acirc;ng sớ l&ecirc;n, lời lẽ thống thiết, n&oacute;i rằng: &quot;Cầu vồng sa xuống, g&agrave; m&aacute;i h&oacute;a trống, ấy l&agrave; bởi quyền ch&iacute;nh trong nước ở tay đ&agrave;n b&agrave; v&agrave; ở tay hoạn quan&quot;. Vua xem sớ ngậm ng&ugrave;i thở d&agrave;i, đứng dậy thay &aacute;o. T&agrave;o Tiết khi ấy đứng hầu sau ngai nghe trộm thấy, trong l&ograve;ng căm giận, b&egrave;n m&aacute;ch bảo đồng bọn, b&agrave;n mưu kiếm cớ vu h&atilde;m S&agrave;i Ung, c&aacute;ch quan đuổi về qu&ecirc; qu&aacute;n. Về sau bọn hoạn quan l&agrave; Trương Nhượng, Triệu Trung, Phong Tư, Đoan Khu&ecirc;, T&agrave;o Tiết, Hầu L&atilde;m, Kiển Thạc, Tr&igrave;nh Kho&aacute;ng, Hạ Huy, Qu&aacute;ch Thắng, cả thẩy mười người gọi l&agrave; mười quan &quot;Thường thị&quot; b&egrave; đảng với nhau k&eacute;o c&aacute;nh l&agrave;m c&agrave;n. Nh&agrave; vua tin d&ugrave;ng t&ocirc;n trọng Trương Nhượng, gọi l&agrave; &quot;&Aacute; phụ&quot; (nghĩa l&agrave; vua coi như cha). Từ đấy ch&iacute;nh sự trong triều ng&agrave;y c&agrave;ng đổ n&aacute;t, l&ograve;ng người n&aacute;o loạn, giặc cướp nổi l&ecirc;n như ong. Khi ấy ở đất Cự Lộc, c&oacute; một nh&agrave; ba anh em: Anh cả l&agrave; Trương Gi&aacute;c, em hai l&agrave; Trương Bảo, em &uacute;t l&agrave; Trương Lương. Trương Gi&aacute;c vốn thi t&uacute; t&agrave;i trượt, nh&acirc;n thế bực m&igrave;nh v&agrave;o n&uacute;i h&aacute;i thuốc. Đi đường gặp một &ocirc;ng cụ gi&agrave; mặt tr&ograve;n mắt biếc, tay chống gậy l&ecirc;, gọi Trương Gi&aacute;c v&agrave;o trong một c&aacute;i động, trao cho ba quyển s&aacute;ch v&agrave; bảo rằng: Đ&acirc;y l&agrave; cuốn &quot;Th&aacute;i b&igrave;nh y&ecirc;u thuật&quot;, c&oacute; được cuốn n&agrave;y ngươi n&ecirc;n thay trời dạy người, để cứu lấy đời. Nếu sau n&agrave;y manh t&acirc;m t&agrave; gian ắt bị &aacute;c b&aacute;o. Trương Gi&aacute;c sụp xuống lạy, hỏi họ t&ecirc;n th&igrave; cụ gi&agrave; n&oacute;i: &quot;Ta l&agrave; Nam Hoa l&atilde;o ti&ecirc;n&quot;, n&oacute;i đoạn h&oacute;a ra một trận gi&oacute; biến mất. Trương Gi&aacute;c được ba quyển s&aacute;ch ấy đem về ng&agrave;y đ&ecirc;m học tập, kh&ocirc;ng bao l&acirc;u biết đủ c&aacute;c ph&eacute;p h&ocirc; gi&oacute; mưa, tự xưng l&agrave; Th&aacute;i b&igrave;nh đạo nh&acirc;n. Đến th&aacute;ng gi&ecirc;ng, năm Trung b&igrave;nh thứ nhất (một trăm t&aacute;m mươi bốn), c&oacute; bệnh &ocirc;n dịch. Trương Gi&aacute;c l&agrave;m ra nước ph&eacute;p chữa bệnh, cứu được nhiều người, tự xưng l&agrave; Đại hiền lương sư. Gi&aacute;c c&oacute; đồ đệ năm trăm người, đi dạo c&aacute;c nơi, ai c&uuml;ng biết ph&eacute;p thư ph&ugrave; niệm ch&uacute;. Về sau đồ đệ ng&agrave;y c&agrave;ng đ&ocirc;ng; Gi&aacute;c b&egrave;n chi học tr&ograve; ra ba mươi s&aacute;u phương, phương lớn hơn một vạn người, phương nhỏ s&aacute;u bảy ng&agrave;n người, phương n&agrave;o c&uuml;ng đặt một người l&agrave;m thủ lĩnh xưng l&agrave; tướng qu&acirc;n. Gi&aacute;c n&oacute;i phao l&ecirc;n rằng: &quot;Trời xanh đ&atilde; chết, trời v&agrave;ng n&ecirc;n dựng&quot;, &quot;Đến năm gi&aacute;p t&yacute;, thi&ecirc;n hạ th&aacute;i b&igrave;nh&quot;. Rồi sai người lấy đất th&oacute; trắng viết hai chữ &quot;Gi&aacute;p t&yacute;&quot; ở ngay giữa cửa. Nh&acirc;n d&acirc;n t&aacute;m ch&acirc;u Thanh, U, Từ, K&yacute;, Kinh, Dương, Duyện, Dư nh&agrave; n&agrave;o c&uuml;ng thờ mấy chữ hiệu: &quot;Đại hiền lương sư Trương Gi&aacute;c&quot;. Trương Gi&aacute;c lại sai đồng đảng l&agrave; M&atilde; Nguy&ecirc;n Nghĩa mật đem v&agrave;ng lụa v&agrave;o kinh kết giao với t&ecirc;n hoạn quan Phong Tư, đi l&agrave;m nội ứng, rồi b&agrave;n với hai em rằng: - Kh&ocirc;ng g&igrave; kh&oacute; bằng thu phục được l&ograve;ng d&acirc;n, nay l&ograve;ng d&acirc;n đ&atilde; quy thuận về ta, nếu kh&ocirc;ng thừa thế chiếm lấy thi&ecirc;n hạ th&igrave; thật l&agrave; đ&aacute;ng tiếc lắm. B&egrave;n một mặt sai may cờ v&agrave;ng hẹn ng&agrave;y khởi sự, một mặt sai đồ đệ l&agrave; Đường Ch&acirc;u đem thư b&aacute;o cho Phong Tư biết. Nhưng Đường Ch&acirc;u lại đi thẳng đến cửa cung tố c&aacute;o. Được tin, vua liền triệu quan đại tướng qu&acirc;n l&agrave; H&agrave; Tiến điều binh bắt ch&eacute;m M&atilde; Nguy&ecirc;n Nghĩa v&agrave; bắt cả bọn Phong Tư bỏ ngục. Trương Gi&aacute;c thấy việc đ&atilde; tiết lộ, vội v&agrave;ng cử binh khởi sự, tự xưng l&agrave; Thi&ecirc;n c&ocirc;ng tướng qu&acirc;n, Trương Lương xưng Nh&acirc;n c&ocirc;ng tướng qu&acirc;n n&oacute;i với mọi người rằng: &quot;Nay vận nh&agrave; H&aacute;n sắp hết, đại th&aacute;nh nh&acirc;n ra đời. C&aacute;c ngươi n&ecirc;n thuận mệnh trời, theo về ta để c&ugrave;ng vui hưởng th&aacute;i b&igrave;nh!&quot;. Nh&acirc;n d&acirc;n bốn phương đội khăn v&agrave;ng, đi theo Trương Gi&aacute;c c&oacute; tới bốn năm mươi vạn người. Thế giặc dữ dội, quan qu&acirc;n thua chạy như vịt. H&agrave; Tiến một mặt t&acirc;u vua, xin hỏa tốc xuống chiếu cho c&aacute;c nơi ph&ograve;ng giữ giết giặc lập c&ocirc;ng; một mặt sai ba quan trung lang tướng l&agrave; Lư Thực, Ho&agrave;ng Phủ Tung, Chu Tuấn đem qu&acirc;n tinh nhuệ chia ra l&agrave;m ba ngả dẹp giặc. Trong khi ấy, qu&acirc;n Trương Gi&aacute;c c&oacute; một to&aacute;n x&acirc;m phạm v&agrave;o bờ c&otilde;i U Ch&acirc;u. Quan th&aacute;i th&uacute; ch&acirc;u ấy l&agrave; Lưu Y&ecirc;n, người l&agrave;ng K&iacute;nh Lăng, đất Giang Hạ, d&ograve;ng d&otilde;i Lỗ Cung Vương nh&agrave; H&aacute;n, nghe tin qu&acirc;n giặc sắp đến, liền triệu quan hiệu &uacute;y Ch&acirc;u Tĩnh đến b&agrave;n luận. Tĩnh n&oacute;i: - Qu&acirc;n giặc nhiều, qu&acirc;n ta &iacute;t, &ocirc;ng n&ecirc;n tức khắc chi&ecirc;u mộ th&ecirc;m qu&acirc;n th&igrave; mới kịp ứng ph&oacute;. Lưu Y&ecirc;n cho l&agrave; phải, b&egrave;n sai treo bảng mộ qu&acirc;n. Khi bảng treo đến Tr&aacute;c quận th&igrave; c&oacute; ngay một vị anh h&ugrave;ng ra ứng mộ. Vị anh h&ugrave;ng ấy kh&ocirc;ng th&iacute;ch đọc s&aacute;ch mấy, t&iacute;nh &ocirc;n h&ograve;a, &iacute;t n&oacute;i, mừng giận kh&ocirc;ng hề lộ ra mặt, vốn c&oacute; ch&iacute; lớn, chỉ th&iacute;ch kết giao với những tay h&agrave;o kiệt trong thi&ecirc;n hạ. Kể d&aacute;ng người th&igrave; m&igrave;nh cao bảy thước rưỡi, hai tai chảy xuống gần vai, hai tay bu&ocirc;ng khỏi đầu gối, mắt tr&ocirc;ng thấy được tai, mặt đẹp như ngọc, m&ocirc;i đỏ như son, tức l&agrave; d&ograve;ng d&otilde;i Trung Sơn Tĩnh Vương Lưu Thắng, ch&aacute;u năm đời vua Cảnh đế nh&agrave; H&aacute;n, họ Lưu, t&ecirc;n Bị, tự l&agrave; Huyền Đức. Ng&agrave;y trước con Lưu Thắng l&agrave; Lưu Trinh, về đời vua H&aacute;n V&uuml;, được phong l&agrave;m Tr&aacute;c lộc Đ&igrave;nh hầu, sau v&igrave; tội g&oacute;p thiếu tiền c&uacute;ng tế bị mất chức v&igrave; vậy c&ograve;n s&oacute;t một ng&agrave;nh ở Tr&aacute;c quận. &Ocirc;ng của Huyền Đức l&agrave; Lưu H&ugrave;ng, cha l&agrave; Lưu Hoằng. Hoằng thi đỗ hiếu li&ecirc;m, đ&atilde; từng l&agrave;m quan, nhưng mất sớm. Huyền Đức mồ c&ocirc;i cha, thờ mẹ rất hiếu, nh&agrave; ngh&egrave;o, phải l&agrave;m nghề đ&oacute;ng d&eacute;p, dệt chiếu kiếm ăn. Nh&agrave; Huyền Đức ở th&ocirc;n L&acirc;u Tang, m&eacute; đ&ocirc;ng nam c&oacute; một c&acirc;y d&acirc;u rất lớn, cao hơn năm trượng, xa tr&ocirc;ng từng lớp tr&ecirc;n x&ograve;e ra như c&aacute;i t&aacute;n che cỗ xe. C&oacute; người thầy tướng đi qua tr&ocirc;ng thấy khen rằng: &quot;Nh&agrave; c&oacute; c&acirc;y d&acirc;u n&agrave;y tất sinh qu&yacute; tử&quot;. L&uacute;c Huyền Đức c&ograve;n thơ ấu, c&ugrave;ng trẻ con chơi dưới gốc d&acirc;u, thường vẫn n&oacute;i rằng: &quot;Ng&agrave;y sau ta l&agrave;m vua, c&uuml;ng ngự c&aacute;i xe c&oacute; t&aacute;n che như c&acirc;y d&acirc;u n&agrave;y&quot;. Người ch&uacute; l&agrave; Lưu Nguy&ecirc;n Khởi nghe n&oacute;i, lấy l&agrave;m lạ bảo rằng: &quot;Thằng b&eacute; n&agrave;y kh&ocirc;ng phải người thường&quot;. Nh&acirc;n thấy Huyền Đức nh&agrave; ngh&egrave;o, thường tư cấp cho thay đổi theo triều đại. Huyền Đức, năm mười t&aacute;m tuổi, mẹ cho đi học, thờ Trịnh Huyền v&agrave; Lưu Thực l&agrave;m thầy v&agrave; c&ugrave;ng với C&ocirc;ng T&ocirc;n Toản kết bạn học. L&uacute;c Lưu Y&ecirc;n treo bảng mộ qu&acirc;n, th&igrave; Huyền Đức hai mươi t&aacute;m tuổi. Bấy giờ Huyền Đức đọc bảng văn rồi thở d&agrave;i. C&oacute; một người đứng ph&iacute;a sau n&oacute;i lớn l&ecirc;n rằng: - Đại trượng phu như &ocirc;ng, kh&ocirc;ng ra gi&uacute;p nước, đứng thở d&agrave;i đ&oacute;, được việc chi? Huyền Đức ngoảnh lại nh&igrave;n: Người ấy m&igrave;nh cao t&aacute;m thước, đầu b&aacute;o, mắt tr&ograve;n, r&acirc;u h&ugrave;m, h&agrave;m &eacute;n, tiếng vang như sấm, d&aacute;ng như ngựa phi, Huyền Đức thấy dung mạo kh&aacute;c thường, liền hỏi họ t&ecirc;n. Người ấy n&oacute;i: - T&ocirc;i họ Trương t&ecirc;n Phi, tự l&agrave; Dực Đức, ở Tr&aacute;c quận đ&atilde; l&acirc;u đời. Gia tư c&oacute; trang trại ruộng vườn, lại c&oacute; l&ograve; mổ lợn v&agrave; ng&ocirc;i h&agrave;ng b&aacute;n rượu. T&ocirc;i chỉ th&iacute;ch kết giao với h&agrave;o kiệt trong thi&ecirc;n hạ. Vừa rồi thấy &ocirc;ng xem bảng văn rồi thở d&agrave;i, n&ecirc;n t&ocirc;i mới hỏi. Huyền Đức n&oacute;i: T&ocirc;i đ&acirc;y vốn d&ograve;ng d&otilde;i nh&agrave; H&aacute;n, họ Lưu t&ecirc;n Bị; nay thấy giặc Khăn V&agrave;ng nổi loạn, muốn ra dẹp giặc y&ecirc;n d&acirc;n, chỉ hiềm sức m&igrave;nh kh&ocirc;ng l&agrave;m nổi, n&ecirc;n mới thở d&agrave;i. Phi n&oacute;i: - Nh&agrave; t&ocirc;i gia tư c&uuml;ng kh&aacute;. &Yacute; t&ocirc;i muốn chi&ecirc;u mộ trai tr&aacute;ng trong l&agrave;ng, c&ugrave;ng &ocirc;ng mưu đồ việc lớn, &ocirc;ng t&iacute;nh sao? Huyền Đức mừng lắm. Hai người b&egrave;n rủ nhau v&agrave;o h&agrave;ng uống rượu. Đương đ&aacute;nh ch&eacute;n, thấy một người cao lớn lực lưỡng, đẩy một cỗ xe đến cửa, v&agrave;o h&agrave;ng ngồi phịch xuống gọi nh&agrave; h&agrave;ng: - Rượu mau l&ecirc;n! Để ta uống xong c&ograve;n v&agrave;o th&agrave;nh ứng mộ! Huyền Đức nh&igrave;n xem thấy người ấy m&igrave;nh cao ch&iacute;n thước, r&acirc;u d&agrave;i hai thước, mặt đỏ như gấc, m&ocirc;i như t&ocirc; son, mắt phượng, m&agrave;y tằm, oai phong lẫm liệt. Huyền Đức b&egrave;n mời c&ugrave;ng ngồi v&agrave; hỏi họ t&ecirc;n. Người ấy n&oacute;i: - T&ocirc;i họ Quan t&ecirc;n V&uuml;, tự l&agrave; Trương Sinh, sau đổi l&agrave; V&acirc;n Trường, người l&agrave;ng Giải Lương, tỉnh H&agrave; Đ&ocirc;ng. Nh&acirc;n thấy c&oacute; đứa thổ h&agrave;o ỷ thế hiếp người, t&ocirc;i b&egrave;n giết chết rồi đi l&agrave;m kẻ giang hồ đ&atilde; năm, s&aacute;u năm rồi. Nay nghe ở đ&acirc;y c&oacute; lệnh chi&ecirc;u binh ph&aacute; giặc n&ecirc;n t&ocirc;i đến ứngmộ. Huyền Đức c&uuml;ng đem ch&iacute; m&igrave;nh ra n&oacute;i, V&acirc;n Trường rất mừng. B&egrave;n c&ugrave;ng đến trại của Trương Phi b&agrave;n t&iacute;nh việc lớn. Phi n&oacute;i: - Sau trại t&ocirc;i c&oacute; một vườn đ&agrave;o đang nở hoa đẹp lắm, ng&agrave;y mai ta n&ecirc;n l&agrave;m lễ tế trời đất ở trong vườn, rồi ba ch&uacute;ng ta kết l&agrave;m anh em, c&ugrave;ng l&ograve;ng hợp sức, sau mới c&oacute; thể t&iacute;nh được việc lớn. Huyền Đức, V&acirc;n Trường đều n&oacute;i: - Như thế tốt lắm! Ng&agrave;y h&ocirc;m sau sửa soạn tr&acirc;u đen ngựa trắng v&agrave; c&aacute;c lễ vật ở trong vườn đ&agrave;o, ba người đốt hương lạy hai lạy thề rằng: - Ch&uacute;ng t&ocirc;i l&agrave; Lưu Bị, Quan V&uuml;, Trương Phi, dẫu rằng kh&aacute;c họ, song đ&atilde; kết l&agrave;m anh em, th&igrave; phải c&ugrave;ng l&ograve;ng hợp sức, cứu khốn ph&ugrave; nguy, tr&ecirc;n b&aacute;o đền nợ nước, dưới y&ecirc;n định mu&ocirc;n d&acirc;n. Ch&uacute;ng t&ocirc;i kh&ocirc;ng cần sinh c&ugrave;ng ng&agrave;y, c&ugrave;ng th&aacute;ng, c&ugrave;ng năm, chỉ muốn chết c&ugrave;ng năm, c&ugrave;ng ng&agrave;y, c&ugrave;ng th&aacute;ng. Ho&agrave;ng thi&ecirc;n hậu thổ soi x&eacute;t l&ograve;ng n&agrave;y. Nếu ai bội nghĩa qu&ecirc;n ơn th&igrave; trời người c&ugrave;ng giết. Thề xong t&ocirc;n Huyền Đức l&agrave;m anh cả, Quan V&uuml; thứ hai, Trương Phi em &uacute;t. Rồi mổ tr&acirc;u đặt tiệc, tụ họp d&uuml;ng sĩ trong l&agrave;ng được ba trăm người, c&uuml;ng đến vườn đ&agrave;o uống một bữa rượu thật say. H&ocirc;m sau ba người sửa soạn kh&iacute; giới, đương lo c&ograve;n thiếu ngựa cưỡi, th&igrave; thấy b&aacute;o c&oacute; hai người bu&ocirc;n dắt một đ&agrave;n ngựa đến trại. Huyền Đức n&oacute;i: - Thực l&agrave; trời gi&uacute;p ch&uacute;ng ta! N&oacute;i đoạn ba người c&ugrave;ng ra cửa trại đ&oacute;n. Nguy&ecirc;n hai người kh&aacute;ch ấy đều l&agrave; l&aacute;i bu&ocirc;n lớn ở Trung Sơn, một người t&ecirc;n Trương Thế B&igrave;nh, một người t&ecirc;n T&ocirc; Song, h&agrave;ng năm vẫn đem ngựa l&ecirc;n b&aacute;n miền Bắc, chỉ v&igrave; l&uacute;c ấy dọc đường c&oacute; nhiều giặc cướp n&ecirc;n quay trở về. Huyền Đức mời hai người v&agrave;o trại, l&agrave;m rượu khoản đ&atilde;i v&agrave; n&oacute;i r&otilde; &yacute; m&igrave;nh đang muốn dẹp giặc y&ecirc;n d&acirc;n. Hai người cả mừng, t&igrave;nh nguyện tặng năm mươi con ngựa tốt, năm trăm lạng v&agrave;ng bạc v&agrave; một ngh&igrave;n c&acirc;n sắt để l&agrave;m kh&iacute; giới. Huyền Đức cảm tạ hai người kh&aacute;ch v&agrave; tiễn l&ecirc;n đường, rồi sai gọi thơ đến&iuml; r&egrave;n hai thanh kiếm. V&acirc;n Trường đ&aacute;nh một thanh long đao nặng t&aacute;m mươi hai c&acirc;n, Trương Phi đ&aacute;nh một ngọn x&agrave; m&acirc;u d&agrave;i một trượng t&aacute;m thước. Mỗi người l&agrave;m một bộ &aacute;o gi&aacute;p, họp hương d&uuml;ng được hơn năm trăm người đem nhau đến gặp Ch&acirc;u Tĩnh. Ch&acirc;u Tĩnh đưa đi yết kiến quan th&aacute;i th&uacute; Lưu Y&ecirc;n. Ba người thi lễ xong, đều xưng họ t&ecirc;n. Huyền Đức kể r&otilde; t&ocirc;n ph&aacute;i. Lưu Y&ecirc;n cả mừng nhận Huyền Đức l&agrave; ch&aacute;u. V&agrave;i h&ocirc;m sau, c&oacute; tin b&aacute;o tướng giặc Khăn V&agrave;ng l&agrave; Tr&igrave;nh Viễn Ch&iacute; đem năm vạn qu&acirc;n đến đ&aacute;nh Tr&aacute;c Quận. Lưu Y&ecirc;n sai Ch&acirc;u Tĩnh dẫn ba anh em Huyền Đức đem năm trăm qu&acirc;n đi trước ph&aacute; giặc. Ba anh em Huyền Đức vui mừng hớn hở đi ngay. L&uacute;c đến dưới n&uacute;i Đại Hưng, thấy qu&acirc;n giặc đều x&otilde;a t&oacute;c ch&iacute;t khăn v&agrave;ng. Hai b&ecirc;n đối trận, Huyền Đức nhảy ngựa vụt ra, tả c&oacute; V&acirc;n Trường, hữu c&oacute; Dực Đức, giơ roi mắng lớn: - Qu&acirc;n giặc phản nước kia! Sao kh&ocirc;ng xuống ngựa h&agrave;ng ngay đi? Tr&igrave;nh Viễn Ch&iacute; cả giận, sai ph&oacute; tướng Đặng Mậu ra đ&aacute;nh. Trương Phi cầm m&acirc;u x&ocirc;ng ra đ&acirc;m tr&uacute;ng v&agrave;o bụng Đặng Mậu. Đặng Mậu ng&atilde; lăn xuống ngựa chết. Tr&igrave;nh Viễn Ch&iacute; thấy vậy, th&uacute;c ngựa m&uacute;a đao x&ocirc;ng thẳng v&agrave;o định ch&eacute;m Trương Phi. Tức th&igrave; V&acirc;n Trường m&uacute;a long đao tế ngựa ra đ&oacute;n địch. Tr&igrave;nh Viễn Ch&iacute; tr&ocirc;ng thấy hoảng sợ, chưa kịp trở tay đ&atilde; bị đao V&acirc;n Trường xả l&agrave;m hai đoạn. Đời sau c&oacute; thơ khen hai người rằng: Anh h&ugrave;ng xuất hiện buổi sơ đầu, Người thử long đao kẻ thử m&acirc;u, Mới bước ch&acirc;n ra uy đ&atilde; dữ, Tiếng tăm lừng lẫy cuộc ganh nhau. Qu&acirc;n giặc thấy Tr&igrave;nh Viễn Ch&iacute; bị ch&eacute;m, đều v&aacute;c ngược gi&aacute;o &ugrave; t&eacute; chạy, Huyền Đức th&uacute;c qu&acirc;n đuổi theo, giặc ra h&agrave;ng kh&ocirc;ng biết bao nhi&ecirc;u m&agrave; kể. Ba người đại thắng k&eacute;o qu&acirc;n trở về. Lưu Y&ecirc;n th&acirc;n ra đ&oacute;n tiếp, khao thưởng ba qu&acirc;n. H&ocirc;m sau Lưu Y&ecirc;n tiếp được tờ điệp của quan th&aacute;i th&uacute; Thanh Ch&acirc;u t&ecirc;n l&agrave; Cung Cảnh, b&aacute;o tin bị giặc Khăn V&agrave;ng bao v&acirc;y, th&agrave;nh sắp vỡ, kịp xin cho qu&acirc;n đến cứu. Lưu Y&ecirc;n b&agrave;n với Huyền Đức. Huyền Đức n&oacute;i: - Bị n&agrave;y t&igrave;nh nguyện đem qu&acirc;n đi cứu. Lưu Y&ecirc;n b&egrave;n sai Ch&acirc;u Tĩnh đem năm ngh&igrave;n qu&acirc;n c&ugrave;ng Huyền Đức, Quan, Trương k&eacute;o đến Thanh Ch&acirc;u. Giặc thấy c&oacute; qu&acirc;n đến cứu, chia qu&acirc;n đ&oacute;n đ&aacute;nh. Huyền Đức thấy qu&acirc;n m&igrave;nh &iacute;t, kh&oacute; đ&aacute;nh được, lui ba mươi dặm đ&oacute;ng trại, rồi bảo Quan, Trương rằng: - Giặc nhiều, ta &iacute;t, tất phải d&ugrave;ng kỳ binh mới c&oacute; thể thắng được. B&egrave;n sai V&acirc;n Trường dẫn một ngh&igrave;n qu&acirc;n phục b&ecirc;n tả n&uacute;i, Trương Phi dẫn một ngh&igrave;n qu&acirc;n phục b&ecirc;n hữu n&uacute;i, hẹn rằng nghe tiếng chi&ecirc;ng th&igrave; c&ugrave;ng k&eacute;o ra tiếp ứng. Ng&agrave;y h&ocirc;m sau Huyền Đức c&ugrave;ng Ch&acirc;u Tĩnh dẫn qu&acirc;n đ&aacute;nh trống h&ograve; reo thẳng tiến. Qu&acirc;n giặc vội v&atilde; k&eacute;o ra đ&oacute;n đ&aacute;nh, Huyền Đức lui binh ngay. Giặc thừa thế đuổi tr&agrave;n. Vừa qua sườn n&uacute;i, trong qu&acirc;n Huyền Đức khua chi&ecirc;ng vang l&ecirc;n. Hai đạo qu&acirc;n tả hữu x&ocirc; ra. Huyền Đức th&uacute;c qu&acirc;n quay lại, ba mặt gi&aacute;p đ&aacute;nh, qu&acirc;n giặc thua to, chạy đến dưới th&agrave;nh Thanh Ch&acirc;u, quan th&aacute;i th&uacute; Cung Cảnh c&uuml;ng đem d&acirc;n binh ra trợ chiến, qu&acirc;n giặc bị chết rất nhiều, giải được v&ograve;ng v&acirc;y. Đời sau c&oacute; thơ khen Huyền Đức rằng: Bầy mưu đặt mẹo kh&eacute;o ra c&ocirc;ng, Đ&ocirc;i hổ chung quy k&eacute;m một rồng. Buổi mới đ&atilde; n&ecirc;n c&ocirc;ng trạng lớn, Chia ba ch&acirc;n vạc đ&aacute;ng anh h&ugrave;ng. Cung Cảnh khao qu&acirc;n xong, Ch&acirc;u Tĩnh muốn về. Huyền Đức n&oacute;i: - Mới rồi nghe tin quan trung lang tướng Lư Thực c&ugrave;ng Trương Gi&aacute;c đ&aacute;nh nhau ở Quảng T&ocirc;n. Bị n&agrave;y trước kia c&oacute; học Lư tướng qu&acirc;n, nghĩa đạo thầy tr&ograve;, muốn sang gi&uacute;p sức. Ch&acirc;u Tĩnh dẫn qu&acirc;n về một m&igrave;nh, c&ograve;n Huyền Đức c&ugrave;ng Quan, Trương dẫn năm trăm qu&acirc;n bản bộ sang Quảng T&ocirc;n. Khi ba người đến dinh qu&acirc;n Lư Thực, v&agrave;o trướng thi lễ, b&agrave;y tỏ &yacute; kiến của m&igrave;nh, th&igrave; Lư Thực mừng lắm, lưu ở trướng tiền đợi khi d&ugrave;ng đến. Bấy giờ qu&acirc;n Trương Gi&aacute;c mười lăm vạn, qu&acirc;n Lư Thực năm vạn, đang chống nhau ở Quảng T&ocirc;n, chưa r&otilde; b&ecirc;n n&agrave;o thua được. Một h&ocirc;m Lư Thực bảo Huyền Đức rằng: - Trương Gi&aacute;c đ&atilde; bị ta v&acirc;y ở đ&acirc;y rồi. Duy hai em n&oacute; l&agrave; Trương Bảo, Trương Lương đang chống nhau với Ho&agrave;ng Phủ Tung v&agrave; Chu Tuấn ở Dĩnh Xuy&ecirc;n. &Ocirc;ng n&ecirc;n đem qu&acirc;n ngay bản bộ v&agrave; một ngh&igrave;n qu&acirc;n ta gi&uacute;p th&ecirc;m, đến thẳng Dĩnh Xuy&ecirc;n d&ograve; xem tin tức ra sao, rồi c&ugrave;ng nhau hẹn ng&agrave;y tiến đ&aacute;nh. Huyền Đức lĩnh mệnh, đem qu&acirc;n đi cả ng&agrave;y đ&ecirc;m đến Dĩnh Xuy&ecirc;n. L&uacute;c ấy Ho&agrave;ng Phủ Tung, Chu Tuấn đem qu&acirc;n đ&aacute;nh giặc, giặc bị thua lu&ocirc;n, phải lui về Trường X&atilde;, dựa theo bụi rậm đ&oacute;ng trại. Tung b&agrave;n với Tuấn rằng: - Qu&acirc;n giặc dựa v&agrave;o chỗ c&oacute; cỏ rậm đ&oacute;ng qu&acirc;n th&igrave; ta n&ecirc;n d&ugrave;ng mẹo h&oacute;a c&ocirc;ng. B&egrave;n sai qu&acirc;n sĩ mỗi người b&oacute; một b&oacute; cỏ, ngầm đi mai phục. Đ&ecirc;m ấy trời nổi cơn gi&oacute; lớn, v&agrave;o khoảng canh hai, qu&acirc;n phục k&eacute;o v&agrave;o ph&oacute;ng hỏa, Tung v&agrave; Tuấn đều dẫn qu&acirc;n đến đ&aacute;nh, kh&oacute;i lửa ngụt trời, qu&acirc;n giặc hoảng sợ, người kh&ocirc;ng kịp mặt gi&aacute;p, ngựa kh&ocirc;ng kịp thắng y&ecirc;n, x&ocirc; nhau m&agrave; chạy. Đ&ocirc;i b&ecirc;n đ&aacute;nh nhau đến s&aacute;ng, Trương Lương, Trương Bảo phải dẫn t&agrave;n qu&acirc;n cướp đường m&agrave; chạy. Bỗng thấy một to&aacute;n qu&acirc;n m&atilde; k&eacute;o to&agrave;n cờ đỏ x&ocirc;ng ra chặn đường. Một tướng đi đầu, m&igrave;nh cao bảy thước, mắt nhỏ r&acirc;u d&agrave;i. Vi&ecirc;n tướng ấy l&agrave; ai? Tức l&agrave; quan kỵ đ&ocirc; &uacute;y, người ở Ti&ecirc;n Quận nước B&aacute;i, họ T&agrave;o t&ecirc;n Th&aacute;o, tự xưng l&agrave; Mạnh Đức. Cha T&agrave;o Th&aacute;o l&agrave; T&agrave;o Tung, vốn xưa họ Hạ Hầu nhận l&agrave;m con nu&ocirc;i quan trung thường thị T&agrave;o Đằng, n&ecirc;n đổi theo họ T&agrave;o. Th&aacute;o tiểu tự l&agrave; A Man v&agrave; đặt một t&ecirc;n nữa l&agrave; C&aacute;t Lợi. L&uacute;c Th&aacute;o c&ograve;n trẻ, th&igrave; chỉ th&iacute;ch săn bắn, ham m&uacute;a h&aacute;t, nổi tiếng l&agrave; một tay cơ biến quyền mưu. Người ch&uacute; Th&aacute;o thấy Th&aacute;o chơn bời v&ocirc; độ, giận lắm, b&egrave;n m&aacute;ch với T&agrave;o Tung. Tung tr&aacute;ch mắng Th&aacute;o. Th&aacute;o nghĩ ngay một kế, l&uacute;c thấy ch&uacute; đến, giả tảng nằm quay ra đất, l&agrave;m như tr&uacute;ng phong. Ch&uacute; Th&aacute;o thấy vậy cả sợ, chạy đến bảo Tung, Tung vội lại xem, thấy Th&aacute;o kh&ocirc;ng c&oacute; bệnh chi cả, b&egrave;n hỏi: - Ch&uacute; m&agrave;y n&oacute;i m&agrave;y tr&uacute;ng phong, đ&atilde; khỏi rồi &agrave;? - Thưa cha, thuở b&eacute; đến giờ con c&oacute; bệnh ấy đ&acirc;u! Chẳng qua ch&uacute; con gh&eacute;t con, cho n&ecirc;n đặt điều ra thế. Tung tưởng thật. Từ đấy người ch&uacute; kể tội Th&aacute;o, Tung đều kh&ocirc;ng nghe nữa, nh&acirc;n thể Th&aacute;o c&agrave;ng được tự do ph&oacute;ng đ&atilde;ng hơn xưa. Bấy giờ c&oacute; người t&ecirc;n l&agrave; Kiều Huyền bảo Th&aacute;o rằng: - Thi&ecirc;n hạ sắp loạn, phi c&oacute; tay t&agrave;i giỏi hơn đời th&igrave; kh&ocirc;ng sao dẹp được loạn. L&agrave;m được như thế c&oacute; lẽ chỉ c&oacute; b&aacute;c! Người đất Nam Dương l&agrave; H&agrave; Ngung, một h&ocirc;m tr&ocirc;ng thấy Th&aacute;o c&uuml;ng t&aacute;n tụng rằng: - Nh&agrave; H&aacute;n sắp mất, y&ecirc;n được thi&ecirc;n hạ chắc chỉ c&oacute; người n&agrave;y! Người đất Nhữ Nam l&agrave; Hứa Thiệu c&oacute; tiếng l&agrave; giỏi biết người, Th&aacute;o th&acirc;n đến hỏi: - Như t&ocirc;i l&agrave; người thế n&agrave;o? Thiệu kh&ocirc;ng trả lời. Th&aacute;o hỏi lại lần nữa. Thiệu n&oacute;i: - Anh l&agrave; năng thần của đời trị v&agrave; gian h&ugrave;ng của đời loạn! Th&aacute;o nghe n&oacute;i cả mừng. Năm Th&aacute;o hai mươi tuổi thi đỗ hiếu li&ecirc;m, bổ l&agrave;m quan lang, sau lại thăng chức đ&ocirc; &uacute;y huyện Lạc Dương. L&uacute;c mới đến nhận chức, Th&aacute;o sai treo hơn mười c&aacute;i roi ng&uuml; sắc ở bốn cửa huyện, kh&ocirc;ng kể h&agrave;o qu&yacute;, hễ ai phạm ph&aacute;p đều kh&ocirc;ng tha. Ch&uacute; quan trung thường thị Kiển Thạc v&aacute;c dao đi đ&ecirc;m, Th&aacute;o đi tuần bắt được c&uuml;ng đem nọc đ&aacute;nh ngay. Bởi thế trong ngo&agrave;i kinh sợ, kh&ocirc;ng ai d&aacute;m l&agrave;m tr&aacute;i ph&eacute;p. Sau Th&aacute;o được thăng chức lệnh do&atilde;n Đốn kỷ. Nh&acirc;n giặc Khăn V&agrave;ng nổi loạn, Th&aacute;o lại được thăng l&agrave;m quan kỵ đ&ocirc; &uacute;y, đem năm ngh&igrave;n qu&acirc;n kỵ m&atilde; v&agrave; bộ binh đến Dĩnh Xuy&ecirc;n gi&uacute;p đ&aacute;nh. T&igrave;nh cờ dọc đường, gặp Trương Lương, Trương Bảo thua chạy, Th&aacute;o đ&oacute;n đ&aacute;nh một trận kịch liệt, ch&eacute;m giết hơn một vạn người, cướp được cờ, trống, ngựa, kh&iacute; giới rất nhiều. Lương, Bảo cố chết mới chạy tho&aacute;t. Th&aacute;o v&agrave;o hội kiến Ho&agrave;ng Phủ Tung, Chu Tuấn rồi lại dẫn qu&acirc;n đi đuổi Trương Lương, Trương Bảo ngay. Nhắc lại Huyền Đức dẫn Quan, Trương đi gần đến Dĩnh Xuy&ecirc;n, nghe tiếng reo h&ograve; đ&aacute;nh nhau, lại tr&ocirc;ng thấy lửa s&aacute;ng rực trời, vội th&uacute;c qu&acirc;n k&eacute;o đến, tới nơi giặc đ&atilde; chạy tan hết rồi. Huyền Đức v&agrave;o yết kiến Ho&agrave;ng Phủ Tung. Chu Tuấn lại n&oacute;i &yacute; kiến của Lư Thực. Tung n&oacute;i: - Nay Trương Lương, Trương Bảo thế c&ugrave;ng lực kiệt tất chạy đến Quảng T&ocirc;n nương nhờ Trương Gi&aacute;c. &Ocirc;ng n&ecirc;n đi gấp đường về gi&uacute;p ngay Lư Thực. Huyền Đức nghe lời, dẫn qu&acirc;n trở lại. Khi mới đi được nửa đường, gặp một to&aacute;n người ngựa, &aacute;p giải một xe t&ugrave;, tr&ecirc;n xe c&oacute; một c&aacute;i c&uuml;i nhốt một người t&ugrave;, tế ra ch&iacute;nh l&agrave; Lư Thực. Huyền Đức giật m&igrave;nh kinh h&atilde;i, vội xuống ngựa chạy đến hỏi thăm. Thực n&oacute;i: - Ta v&acirc;y đ&aacute;nh Trương Gi&aacute;c, sắp sửa ph&aacute;p tan, chỉ v&igrave; Gi&aacute;c d&ugrave;ng y&ecirc;u thuật, n&ecirc;n c&ograve;n nh&ugrave;ng nhằng chưa ph&aacute; hẳn được. Triều đ&igrave;nh sai vi&ecirc;n hoạn quan t&ecirc;n l&agrave; Tả Phong đến d&ograve; x&eacute;t qu&acirc;n t&igrave;nh. Phong đ&ograve;i ăn của đ&uacute;t m&agrave; kh&ocirc;ng được, v&igrave; lương qu&acirc;n ta c&ograve;n thiếu, tiền đ&acirc;u m&agrave; cung đốn họ, bởi thế Tả Phong căm giận, về triều t&acirc;u man cho ta ru r&uacute; ở trong l&uuml;y cao kh&ocirc;ng chịu đ&aacute;nh giặc, l&agrave;m cho l&ograve;ng qu&acirc;n ch&aacute;n nản. Triều đ&igrave;nh nổi giận, sai quan trung lang tướng Đổng Tr&aacute;c đến cầm qu&acirc;n thay ta, v&agrave; bắt ta về kinh hỏi tội. Trương Phi nghe n&oacute;i, nổi giận đ&ugrave;ng đ&ugrave;ng, toan giết hết to&aacute;n qu&acirc;n &aacute;p giải để cứu Lư Thực. Huyền Đức vội ngăn lại bảo rằng: - Kh&ocirc;ng n&ecirc;n, triều đ&igrave;nh đ&atilde; c&oacute; c&ocirc;ng luận, ch&uacute; kh&ocirc;ng được xử sự một c&aacute;ch n&oacute;ng nảy như thế. Qu&acirc;n sĩ lại giải Lư Thực đi. Quan C&ocirc;ng n&oacute;i: - Nay Lư trung lang đ&atilde; bị bắt, người kh&aacute;c thay quyền, ch&uacute;ng ta đến đấy c&uuml;ng v&ocirc; &iacute;ch, chi bằng h&atilde;y về Tr&aacute;c quận. Huyền Đức lấy l&agrave;m phải, b&egrave;n dẫn qu&acirc;n về ph&iacute;a bắc. Đi được hai ng&agrave;y, bỗng nghe sau n&uacute;i c&oacute; tiếng đ&aacute;nh nhau h&ograve; h&eacute;t. Huyền Đức c&ugrave;ng Quan, Trương cưỡi ngựa l&ecirc;n đồi cao tr&ocirc;ng xem, thấy qu&acirc;n H&aacute;n bị thua đương chạy, giặc khăn v&agrave;ng đ&ocirc;ng như kiến cỏ đương đuổi theo sau, tr&ecirc;n l&aacute; cờ viết bốn chữ &quot;Thi&ecirc;n c&ocirc;ng tướng qu&acirc;n&quot; rất lớn. Huyền Đức n&oacute;i: - Trương Gi&aacute;c đ&acirc;y rồi, đ&aacute;nh ngay đi! Ba người đều phi ngựa dẫn qu&acirc;n ra đ&aacute;nh. L&uacute;c ấy Trương Gi&aacute;c đương thừa thắng đuổi Đổng Tr&aacute;c, bỗng gặp ba người đem qu&acirc;n chẹn đ&aacute;nh, qu&acirc;n Gi&aacute;c hoảng loạn, phải thua chạy đến ngo&agrave;i năm mươi dặm. Ba người cứu được Đổng Tr&aacute;c về trại. Tr&aacute;c hỏi ba người hiện l&agrave;m quan g&igrave;? Huyền Đức n&oacute;i: &quot;Ch&acirc;n trắng&quot; Tr&aacute;c khinh thường, kh&ocirc;ng th&egrave;m đ&aacute;p tạ lại. Huyền Đức bỏ đi. Trương Phi cả giận n&oacute;i rằng: - Thằng cha n&agrave;y l&aacute;o qu&aacute;! Ch&uacute;ng ta lăn lộn v&agrave;o đất chết để cứu n&oacute; ra, n&oacute; kh&ocirc;ng ơn th&igrave; chớ, lại c&ograve;n l&agrave;m ph&aacute;ch khinh người đến thế, nếu kh&ocirc;ng giết n&oacute;, sao hả được giận n&agrave;y? B&egrave;n cầm dao v&agrave;o trướng định giết Đổng Tr&aacute;c. Đ&oacute; ch&iacute;nh l&agrave;: Nh&acirc;n t&igrave;nh thế th&aacute;i vẫn xưa nay, Ai biết anh h&ugrave;ng l&uacute;c trắng tay, Nếu được người người như Dực Đức, Tr&ecirc;n đời hẳn hết kẻ kh&ocirc;ng hay! Muốn biết t&iacute;nh mạng Đổng Tr&aacute;c thế n&agrave;o, xem hồi sau sẽ r&otilde;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><span style=\"color:#ff0000\">HỒI II: Trương Dực Đức giận đ&aacute;nh đốc bưu; H&agrave; Quốc Cửu mưu giết quan hoạn&nbsp;</span></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Tr&aacute;c t&ecirc;n chữ l&agrave; Trọng Dĩnh, qu&ecirc; ở huyện L&acirc;m Thao, quận L&uuml;ng T&acirc;y, l&agrave;m quan th&aacute;i th&uacute; ở H&agrave; Đ&ocirc;ng, xưa nay vốn t&iacute;nh ki&ecirc;u ngạo. L&uacute;c ấy v&igrave; khinh Huyền Đức n&ecirc;n Trương Phi nổi n&oacute;ng muốn v&agrave;o giết ngay. Huyền Đức, Quan C&ocirc;ng vội ngăn m&agrave; rằng: - Kh&ocirc;ng n&ecirc;n, hắn l&agrave; quan triều đ&igrave;nh, em chớ n&ecirc;n tự tiện giết hắn! Phi n&oacute;i: - Nếu kh&ocirc;ng giết n&oacute;, m&agrave; lại ở đ&acirc;y l&agrave;m đầy tớ cho n&oacute; sai khiến, th&igrave; t&ocirc;i kh&ocirc;ng thể chịuđược! Nếu hai anh muốn ở đ&acirc;y th&igrave; t&ocirc;i xin đi ngay nơi kh&aacute;c. Huyền Đức n&oacute;i: - Ba anh em ta kết nghĩa c&ugrave;ng sống chết, sao nỡ l&igrave;a nhau? Th&ocirc;i c&ugrave;ng đi nơi kh&aacute;c l&agrave; hơn cả. Phi n&oacute;i: - C&oacute; thế th&igrave; c&aacute;i tức n&agrave;y mới hơi hả. Ngay đ&ecirc;m ấy ba người dẫn qu&acirc;n đến với Chu Tuấn. Tuấn khoản đ&atilde;i rất hậu. C&ugrave;ng nhau họp qu&acirc;n, tiến đ&aacute;nh Trương Bảo. Bấy giờ T&agrave;o Th&aacute;o đương theo Ho&agrave;ng Phủ Tung đ&aacute;nh Trương Lương, hai b&ecirc;n đ&aacute;nh nhau một trận to ở Kh&uacute;c Dương. B&ecirc;n n&agrave;y Chu Tuấn tiến đ&aacute;nh Trương Bảo, Bảo dẫn t&aacute;m ch&iacute;n vạn qu&acirc;n đ&oacute;ng ở m&eacute; sau n&uacute;i. Tuấn sai Huyền Đức dẫn đội ti&ecirc;n phong ra đối địch với giặc. Trương Bảo sai ph&oacute; tướng Cao Thăng phi ngựa ra th&aacute;ch đ&aacute;nh, Huyền Đức sai Trương Phi cự với Cao Thăng. Phi ph&oacute;ng ngựa cầm m&acirc;u c&ugrave;ng Thăng giao chiến, chưa được v&agrave;i hiệp đ&atilde; đ&acirc;m Thăng ng&atilde; ngựa. Huyền Đức th&uacute;c qu&acirc;n x&ocirc;ng l&ecirc;n, Trương Bảo ngồi tr&ecirc;n ngựa x&otilde;a t&oacute;c m&uacute;a gươm, giở y&ecirc;u thuật, ph&uacute;t chốc gi&oacute; ầm ầm, một luồng kh&iacute; đen tự tr&ecirc;n kh&ocirc;ng tỏa xuống, trong luồng kh&iacute; đen c&oacute; v&ocirc; số người ngựa x&ocirc;ng ra, qu&acirc;n Huyền Đức sợ h&atilde;i rối loạn, Huyền Đức vội v&agrave;ng thu qu&acirc;n về, c&ugrave;ng Chu Tuấn b&agrave;n mưu định kế. Tuấn n&oacute;i: - N&oacute; d&ugrave;ng y&ecirc;u thuật th&igrave; ta ph&aacute; c&uuml;ng dễ. Ng&agrave;y mai n&ecirc;n sai qu&acirc;n chứa sẵn m&aacute;u lợn m&aacute;u ch&oacute;, m&aacute;u d&ecirc; phục ở tr&ecirc;n n&uacute;i, đợi qu&acirc;n giặc k&eacute;o đến, đứng tr&ecirc;n vẩy xuống, tự khắc giải được ph&eacute;p y&ecirc;u. Huyền Đức tu&acirc;n lệnh, sai Quan C&ocirc;ng, Trương Phi mỗi người dẫn một ngh&igrave;n qu&acirc;n đem sẵn m&aacute;u ch&oacute;, m&aacute;u lợn, m&aacute;u d&ecirc; v&agrave; đồ uế vật, phục tr&ecirc;n đỉnh n&uacute;i. H&ocirc;m sau Trương Bảo lại k&eacute;o cờ gi&oacute;ng trống đem qu&acirc;n đến th&aacute;ch đ&aacute;nh. Huyền Đức tự ra ngh&ecirc;nh địch. Trương Bảo lại d&ugrave;ng ph&eacute;p y&ecirc;u, ph&uacute;t chốc gi&oacute;, sấm nổi l&ecirc;n, c&aacute;t đ&aacute; tung trời, trong luồng kh&iacute; đen k&eacute;o ra v&ocirc; số người ngựa. Huyền Đức quay ngựa chạy. Trương Bảo th&uacute;c qu&acirc;n đuổi theo, khi v&agrave;o đến gần n&uacute;i, qu&acirc;n mai phục của Quan, Trương nổ một tiếng trống lệnh, uế vật vung ra, tức th&igrave; những người ngựa bằng giấy đều tự tr&ecirc;n kh&ocirc;ng rơi xuống, sấm gi&oacute; y&ecirc;n lặng, c&aacute;t đ&aacute; kh&ocirc;ng bay nữa. Trương Bảo thấy ph&eacute;p y&ecirc;u đ&atilde; bị ph&aacute; vội v&atilde; lui qu&acirc;n song tả c&oacute; Quan C&ocirc;ng, hữu c&oacute; Trương Phi, hai b&ecirc;n đổ ra, sau lưng c&oacute; Huyền Đức, Chu Tuấn k&eacute;o đến, qu&acirc;n giặc bị thua to. Huyền Đức tr&ocirc;ng thấy hiệu cờ &quot;Địa C&ocirc;ng tướng qu&acirc;n&quot; phi ngựa đuổi theo. Trương Bảo cuống cuồng chạy trốn. Huyền Đức bắn ngay một ph&aacute;t t&ecirc;n tr&uacute;ng c&aacute;nh tay tr&aacute;i, Trương Bảo đeo t&ecirc;n cố chết m&agrave; chạy v&agrave;o Dương Th&agrave;nh, đ&oacute;ng chặt cửa, kh&ocirc;ng d&aacute;m ra nữa. Chu Tuấn đem qu&acirc;n v&acirc;y th&agrave;nh, một mặt sai người đi d&ograve; tin tức Ho&agrave;ng Phủ Tung. Th&aacute;m tử về b&aacute;o: Ho&agrave;ng Phủ Tung đ&aacute;nh trận n&agrave;o thắng trận ấy. Triều đ&igrave;nh thấy Đổng Tr&aacute;c thua lu&ocirc;n, hạ lệnh cho Tung thay Tr&aacute;c. L&uacute;c Tung đến nơi th&igrave; Trương Gi&aacute;c đ&atilde; chết rồi. Trương Lương thống xuất cả qu&acirc;n ấy c&ugrave;ng qu&acirc;n Tung chống cự, bị Tung đ&aacute;nh thắng lu&ocirc;n bảy trận ch&eacute;m được Trương Lương ở Kh&uacute;c Dương. Trương Gi&aacute;c chết rồi c&uuml;ng bị quật mả, cắt lấy thủ cấp đem về b&ecirc;u ở kinh sư, c&ograve;n qu&acirc;n giặc ra h&agrave;ng hết cả. Ho&agrave;ng Phủ Tung c&oacute; c&ocirc;ng, triều đ&igrave;nh gia phong l&agrave;m sa kỵ tướng qu&acirc;n, lĩnh chức mục K&yacute; Ch&acirc;u. Ho&agrave;ng Phủ Tung d&acirc;ng biểu t&acirc;u Lư Thực c&oacute; c&ocirc;ng kh&ocirc;ng tội, triều đ&igrave;nh lại cho Lư Thực giữ nguy&ecirc;n chức c&uuml;. T&agrave;o Th&aacute;o c&uuml;ng c&oacute; c&ocirc;ng, được thăng Tế nam tướng sửa soạn đi nhậmchức. Chu Tuấn nghe n&oacute;i, hạ lệnh th&uacute;c qu&acirc;n hết sức đ&aacute;nh lấy Dương Th&agrave;nh. Thế giặc b&acirc;y giờ rất nguy khốn. Một t&ecirc;n tướng giặc l&agrave; Nghi&ecirc;m Ch&aacute;nh đ&acirc;m chết Trương Bảo, cắt lấy thủ cấp đầu h&agrave;ng. Chu Tuấn đem qu&acirc;n b&igrave;nh lu&ocirc;n được mấy quận, rồi d&acirc;ng biểu t&acirc;u b&agrave;y việc thắngtrận. Bấy giờ c&ograve;n ba t&ecirc;n dư đảng giặc Khăn V&agrave;ng l&agrave; Triệu Hoằng, H&agrave;n Trung v&agrave; T&ocirc;n Trọng, tụ tập được mấy vạn đi đến đ&acirc;u c&uuml;ng cướp của đốt nh&agrave;, n&oacute;i l&agrave; b&aacute;o th&ugrave; cho Trương Gi&aacute;c. Triều đ&igrave;nh gi&aacute;ng chỉ cho Chu Tuấn đem qu&acirc;n vừa thắng trận đi đ&aacute;nh. Tuấn vội v&agrave;ng dẫn qu&acirc;n tiến ngay. L&uacute;c ấy giặc đương chiếm giữ Uyển Th&agrave;nh, Tuấn đem qu&acirc;n đến đ&aacute;nh. Triệu Hoằng sai H&agrave;n Trung ra đối địch. Tuấn ph&aacute;i Huyền Đức, Quan, Trương đ&aacute;nh g&oacute;c th&agrave;nh t&acirc;y nam, H&agrave;n Trung sợ g&oacute;c t&acirc;y nam thất thủ, đem hết qu&acirc;n tinh nhuệ ra chống cự. Chu Tuấn đem hai ngh&igrave;n qu&acirc;n thiết kỵ đến đ&aacute;nh g&oacute;c đ&ocirc;ng bắc, giặc sợ th&agrave;nh h&atilde;m, vội bỏ g&oacute;c t&acirc;y nam. Huyền Đức đem qu&acirc;n đ&aacute;nh mạnh đằng sau, qu&acirc;n giặc thua to, phải chạy v&agrave;o th&agrave;nh. Chu Tuấn chia qu&acirc;n v&acirc;y k&iacute;n bốn mặt, trong th&agrave;nh lương cạn. H&agrave;n Trung sai người ra xin h&agrave;ng. Tuấn kh&ocirc;ng cho, Huyền Đức n&oacute;i: - Xưa vua Cao Tổ lấy được thi&ecirc;n hạ, c&uuml;ng hay chi&ecirc;u kẻ đầu h&agrave;ng, dung kẻ quy thuận, nay H&agrave;n Trung đ&atilde; h&agrave;ng thuận, sao &ocirc;ng kh&ocirc;ng cho? Tuấn n&oacute;i: - C&aacute;i đ&oacute; mỗi l&uacute;c mỗi kh&aacute;c, kh&ocirc;ng thể c&acirc;u nệ được. Xưa v&agrave;o đời Tần, Sở, thi&ecirc;n hạ rối loạn, d&acirc;n kh&ocirc;ng biết ai l&agrave; chủ, cho n&ecirc;n chi&ecirc;u kẻ đầu h&agrave;ng, thưởng kẻ quy phục để khuyến kh&iacute;ch kẻ kh&aacute;c về với m&igrave;nh. Nay bốn bể đ&atilde; về một mối, chỉ c&oacute; giặc Khăn V&agrave;ng phản nghịch, nếu cho ph&eacute;p n&oacute; h&agrave;ng, th&igrave; kh&ocirc;ng sao khuy&ecirc;n được người lương thiện. Bọn giặc l&uacute;c đắc &yacute; th&igrave; tha hồ giết người cướp của, l&uacute;c bị thua lại ra đầu h&agrave;ng. Nếu nhận cho ch&uacute;ng đầu h&agrave;ng, tức l&agrave; nu&ocirc;i c&aacute;i mầm phản nghịch cho ch&uacute;ng n&oacute;, kh&ocirc;ng phải l&agrave; việc hay vậy. Huyền Đức n&oacute;i: - Ng&agrave;i dạy c&uuml;ng phải, nhưng b&acirc;y giờ bốn mặt th&agrave;nh v&acirc;y k&iacute;n như bờ r&agrave;o sắt, giặc xin h&agrave;ng kh&ocirc;ng được, tất nhi&ecirc;n phải cố chết m&agrave; đ&aacute;nh. Ngh&igrave;n người một bụng c&ograve;n kh&oacute; đương nổi, nữa l&agrave; trong th&agrave;nh c&ograve;n những mấy vạn người liều mạng. Chi bằng bỏ trống hai mặt đ&ocirc;ng, nam; chỉ đ&aacute;nh hai mặt t&acirc;y, bắc. Giặc thấy c&oacute; đường th&aacute;o, tất bỏ th&agrave;nh m&agrave; chạy, kh&ocirc;ng c&ograve;n bụng n&agrave;o ham đ&aacute;nh, ta c&oacute; thể bắt sống được ch&uacute;ng. Tuấn lấy l&agrave;m phải, lập tức hạ lệnh r&uacute;t qu&acirc;n hai mặt đ&ocirc;ng nam, dồn lại đ&aacute;nh v&agrave;o mặt t&acirc;y, mặt bắc. Quả nhi&ecirc;n H&agrave;n Trung dẫn qu&acirc;n bỏ th&agrave;nh chạy. Tuấn c&ugrave;ng Huyền Đức, Quan, Trương th&uacute;c qu&acirc;n đuổi đ&aacute;nh, bắt chết H&agrave;n Trung, qu&acirc;n giặc đều tan vỡ chạy trốn. Trong khi đang đuổi đ&aacute;nh x&ocirc; x&aacute;t, gặp ngay Triệu Hoằng. T&ocirc;n Trọng dẫn qu&acirc;n đến, c&ugrave;ng Tuấn đ&aacute;nh nhau. Tuấn thấy qu&acirc;n Hoằng thế mạnh, đem qu&acirc;n tạm lui Hoằng thừa kế lại cướp được Uyển Th&agrave;nh. Tuấn đ&oacute;ng trại c&aacute;ch th&agrave;nh mười dặm. Chu Tuấn đang sắp sửa đ&aacute;nh th&agrave;nh, bỗng thấy một to&aacute;n ngựa từ ph&iacute;a đ&ocirc;ng dẫn đến; một vi&ecirc;n tướng đi đầu mặt to, tr&aacute;n rộng, m&igrave;nh hổ, lưng gấu. Tướng ấy họ T&ocirc;n t&ecirc;n Ki&ecirc;n, t&ecirc;n chữ l&agrave; Văn Đ&agrave;i, d&ograve;ng d&otilde;i T&ocirc;n V&uuml; ng&agrave;y xưa, qu&ecirc; ở huyện Ph&uacute; Xu&acirc;n thuộc Ng&ocirc; Quận. T&ocirc;n Ki&ecirc;n năm mười bảy tuổi, một h&ocirc;m &ocirc;ng c&ugrave;ng cha đi thuyền đến s&ocirc;ng Tiền Đường, thấy một bọn giặc bể hơn mười đứa vừa cướp được tiền của kh&aacute;ch bu&ocirc;n, đang chia nhau tr&ecirc;n bờ. Ki&ecirc;n n&oacute;i với cha rằng: &quot;Con xin l&ecirc;n bắt l&uuml; giặc n&agrave;y&quot;. B&egrave;n cầm dao nhảy vọt l&ecirc;n bờ, vừa m&uacute;a đao vừa th&eacute;t, chỉ đ&ocirc;ng chỉ t&acirc;y như c&aacute;ch ra hiệu gọi người. Giặc tưởng quan qu&acirc;n đến bỏ hết của cải chạy trốn. Ki&ecirc;n đuổi giết được một đứa, bởi thế nổi tiếng ở mấy quận huyện, được tiến cử l&agrave;m chức hiệu &uacute;y. Sau qu&acirc;n Cối K&ecirc; c&oacute; đưa y&ecirc;u tặc l&agrave; Hứa Xương l&agrave;m phản, tự xưng l&agrave; Dương Minh Ho&agrave;ng đế, tụ họp đến mấy vạn qu&acirc;n. Ki&ecirc;n c&ugrave;ng quan tư m&atilde; ấy chi&ecirc;u mộ d&uuml;ng sĩ được hơn ngh&igrave;n người họp với mấy qu&acirc;n ch&acirc;u quận đ&aacute;nh tan giặc ấy, ch&eacute;m được Hứa Xương v&agrave; con l&agrave; Hứa Thiều. Quan thứ sử Tang M&acirc;m d&acirc;ng biểu t&acirc;u c&ocirc;ng cho Ki&ecirc;n, triều đ&igrave;nh bổ Ki&ecirc;n l&agrave;m quan thừa ở Di&ecirc;m Độc, sau lại đổi l&agrave;m thừa ở Vu Thai, l&agrave;m thừa ởHạ Phi. Nay thấy giặc Khăn V&agrave;ng nổi loạn. Ki&ecirc;n tụ tập thiếu ni&ecirc;n trong l&agrave;ng c&ugrave;ng bọn kh&aacute;ch bu&ocirc;n v&agrave; tinh binh ở Ho&agrave;i Tứ cả thảy được một ngh&igrave;n năm trăm người, dẫn đến tiếp ứng. Chu Tuấn cả mừng, liền sai Ki&ecirc;n đ&aacute;nh cửa nam, Huyền Đức đ&aacute;nh cửa bắc, Tuấn tự đ&aacute;nh cửa t&acirc;y, để cửa đ&ocirc;ng cho giặc chạy. T&ocirc;n Ki&ecirc;n đi trước nhảy l&ecirc;n th&agrave;nh, ch&eacute;m lu&ocirc;n hơn hai mươi t&ecirc;n giặc, qu&acirc;n giặc sợ h&atilde;i bỏ chạy. Triệu Hoằng cầm gi&aacute;o phi ngựa ra địch với T&ocirc;n Ki&ecirc;n, Ki&ecirc;n b&egrave;n tự tr&ecirc;n mặt th&agrave;nh nhảy xuống, cướp gi&aacute;o của Hoằng đ&acirc;m Hoằng ng&atilde; ngựa, rồi lại nhảy l&ecirc;n ngựa Hoằng x&ocirc;ng v&agrave;o giết giặc. T&ocirc;n Trọng dẫn qu&acirc;n lẻn ra cửa bắc, gặp ngay Huyền Đức, kh&ocirc;ng c&ograve;n bụng n&agrave;o đối địch, chỉ trực chạy tho&aacute;t th&acirc;n. Huyền Đức bắn một ph&aacute;t t&ecirc;n. Trọng tự tr&ecirc;n ngựa lăn xuống. L&uacute;c ấy đại qu&acirc;n Chu Tuấn tự sau dồn đến, ch&eacute;m được v&agrave;i vạn đầu giặc. Giặc đầu h&agrave;ng kh&ocirc;ng biết bao nhi&ecirc;u m&agrave; kể. Một dải Nam Dương hơn mười quận đều y&ecirc;n. Tuấn k&eacute;o qu&acirc;n về kinh, được phong l&agrave;m sa kỵ tướng qu&acirc;n, lĩnh chức H&agrave; Nam lệnh do&atilde;n. Tuấn d&acirc;ng biểu t&acirc;u c&ocirc;ng T&ocirc;n Ki&ecirc;n v&agrave; Lưu Bị. V&igrave; Ki&ecirc;n chạy chọt, n&ecirc;n được bổ l&agrave;m tư m&atilde; đi nhậm chức ngay. C&ograve;n Lưu Bị chờ đợi m&atilde;i vẫn kh&ocirc;ng được bổ dụng. Ba người buồn b&atilde; kh&ocirc;ng vui, một h&ocirc;m đi chơi giong đường phố gặp quan lang trung Trương Qu&acirc;n. Huyền Đức đến ch&agrave;o, nh&acirc;n kể lu&ocirc;n c&ocirc;ng m&igrave;nh đ&aacute;nh giặc cho Qu&acirc;n nghe. Qu&acirc;n lấy l&agrave;m kinh ngạc, b&egrave;n v&agrave;o triều bệ kiến v&agrave; t&acirc;u rằng: - Trước đ&acirc;y giặc Khăn V&agrave;ng phản nghịch, căn do c&uuml;ng bởi bọn hoạn quan mười người b&aacute;n quan bu&ocirc;n tước, phi người th&acirc;n kh&ocirc;ng d&ugrave;ng, phi kẻ th&ugrave; kh&ocirc;ng giết, cho n&ecirc;n thi&ecirc;n hạ rối loạn. Xin bệ hạ ch&eacute;m ngay mười t&ecirc;n n&agrave;y, b&ecirc;u đầu ở Nam Giao, rồi sai sứ giả đi bố c&aacute;o thi&ecirc;n hạ, ai c&oacute; c&ocirc;ng th&igrave; trọng thưởng ngay. Như thế th&igrave; bốn bể tự khắc b&igrave;nh y&ecirc;n. Mười t&ecirc;n hoạn quan vội t&acirc;u vua rằng: - Trương Qu&acirc;n đặt điều t&acirc;u bậy, đ&aacute;ng tội khi qu&acirc;n. Vua sai v&otilde; sĩ đuổi Trương Qu&acirc;n ra. Mười t&ecirc;n hoạn quan b&agrave;n với nhau rằng: &quot;Chắc hẳn c&oacute; kẻ n&agrave;o c&oacute; c&ocirc;ng đ&aacute;nh giặc Khăn V&agrave;ng chưa bổ dụng n&ecirc;n sinh ra o&aacute;n hận. Ta h&atilde;y bảo nha m&ocirc;n ghi t&ecirc;n một số người, cất nhắc cho họ một ch&uacute;t, rồi sau sẽ liệu&quot;. Bởi vậy Huyền Đức được bổ l&agrave;m quan &uacute;y huyện An Hỷ, phủ Trung Sơn, Ch&acirc;u Định v&agrave; ph&aacute;i đi nhậm chức ngay. Huyền Đức giải t&aacute;n qu&acirc;n sĩ cho về l&agrave;ng, chỉ đem theo hơn hai mươi người th&acirc;n t&iacute;n c&ugrave;ng Quan, Trương đến huyện An Hỷ, l&agrave;m việc quan suốt một th&aacute;ng, chẳng lấy lễ của d&acirc;n một ch&uacute;t g&igrave;, n&ecirc;n ai nấy đều cảm phục. Sau khi nhậm chức, c&ugrave;ng Quan, Trương ăn một m&acirc;m, nằm một chiếu; khi Huyền Đức ngồi chỗ đ&ocirc;ng người, th&igrave; Quan, Trương đứng hầu hai b&ecirc;n, cả ng&agrave;y kh&ocirc;ng biết mỏi. Huyền Đức đến huyện chưa được bốn th&aacute;ng, bỗng triều đ&igrave;nh xuống chiếu: &quot;Những người n&agrave;o c&oacute; c&ocirc;ng đ&aacute;nh giặc m&agrave; l&agrave;m trưởng lại, th&igrave; đều bị thải hồi&quot;. Huyền Đức nghĩ m&igrave;nh c&oacute; lẽ c&uuml;ng ở trong số bị thải ấy, c&ograve;n đang nghi hoặc, bỗng thấy b&aacute;o c&oacute; đốc bưu đến huyện. Huyền Đức vội đi đ&oacute;n tiến. L&uacute;c gặp vi&ecirc;n đốc bưu, Huyền Đức v&aacute;i ch&agrave;o một c&aacute;ch cung k&iacute;nh, vi&ecirc;n đốc bưu ngồi tr&ecirc;n m&igrave;nh ngựa, chỉ vẫy đầu roi đ&aacute;p lại. Quan, Trương thấy vậy, tức giận v&ocirc; c&ugrave;ng. Khi đến nh&agrave; kh&aacute;ch vi&ecirc;n đốc bưu ngoảnh mặt hướng nam ngồi cao ngất ngưởng. Huyền Đức đứng hầu ở dưới thềm. L&uacute;c l&acirc;u vi&ecirc;n đốc bưu mới cất tiếng hỏi: - Thầy huyện Lưu xuất th&acirc;n từ ch&acirc;n g&igrave;? Huyền Đức đ&aacute;p: - Bị n&agrave;y d&ograve;ng d&otilde;i Trung sơn Tĩnh vương, khởi th&acirc;n từ Tr&aacute;c Quận, ch&eacute;m giết giặc Khăn V&agrave;ng, lớn nhỏ hơn ba mươi trận đ&aacute;nh, c&oacute; ch&uacute;t c&ocirc;ng lao, n&ecirc;n được bổ chứcn&agrave;y. Vi&ecirc;n đốc bưu th&eacute;t mắng: - Mi giả mạo ho&agrave;ng th&acirc;n, b&aacute;o c&agrave;n c&ocirc;ng trạng, hiện nay triều đ&igrave;nh xuống chiếu, ch&iacute;nh để thải bớt những bọn tham quan &ocirc; lại như mi đ&oacute;. Huyền Đức v&acirc;ng dạ lu&ocirc;n mấy tiếng, lui về huyện nha, c&ugrave;ng vi&ecirc;n đề lại b&agrave;n t&iacute;nh. Đề lại n&oacute;i: - L&atilde;o đốc bưu l&agrave;m dữ như vậy, chẳng qua chỉ chực đ&ograve;i của đ&uacute;t đấy th&ocirc;i. Huyền Đức n&oacute;i: - Ta kh&ocirc;ng tơ h&agrave;o của d&acirc;n một t&yacute; g&igrave;, lấy đ&acirc;u m&agrave; cung đốn hắn? Ng&agrave;y h&ocirc;m vi&ecirc;n đốc bưu đ&ograve;i đề lại đến trước, bắt &eacute;p phải khai man l&agrave; quan huyện hại d&acirc;n. Huyền Đức mấy lần k&eacute;o đến để k&ecirc;u van, đều bị qu&acirc;n canh cửa kh&ocirc;ng cho v&agrave;o. L&uacute;c ấy Trương Phi vừa uống mấy ch&eacute;n rượu giải buồn, cưỡi ngựa đi chơi qua nh&agrave; kh&aacute;ch, thấy năm, s&aacute;u mươi &ocirc;ng gi&agrave; đang kh&oacute;c than ở trước cửa. Phi hỏi cớ sao th&igrave; c&aacute;c l&atilde;o đều n&oacute;i: - Vi&ecirc;n đốc bưu cố &eacute;p đề lại khai man để hại &ocirc;ng Lưu. Ch&uacute;ng t&ocirc;i biết tin, đến đ&acirc;y k&ecirc;u gi&uacute;p, nhưng kh&ocirc;ng cho v&agrave;o, lại sai qu&acirc;n g&aacute;c cửa đ&aacute;nh đuổi ch&uacute;ng t&ocirc;i. Trương Phi cả giận, giương mắt tr&ograve;n xoe, hai h&agrave;m răng nghiến ken k&eacute;t, nhảy ngay xuống ngựa, chạy sấn v&agrave;o qu&acirc;n địch, những qu&acirc;n canh cửa kh&ocirc;ng t&agrave;i n&agrave;o cản lại được. Phi chạy thẳng v&agrave;o hậu đường, thấy Vi&ecirc;n đốc bưu đang ngồi chễm chệ tr&ecirc;n sảnh, đề lại bị tr&oacute;i ở dưới đất. Phi th&eacute;t lớn l&ecirc;n rằng: - Thằng mọt d&acirc;n kia! C&oacute; biết ta l&agrave; ai kh&ocirc;ng? Vi&ecirc;n đốc bưu chưa kịp n&oacute;i c&acirc;u g&igrave; cả, đ&atilde; bị Trương Phi t&uacute;m t&oacute;c l&ocirc;i tuột ra ngo&agrave;i nh&agrave; kh&aacute;ch, k&eacute;o thẳng về trước huyện, tr&oacute;i v&agrave;o t&agrave;u ngựa, rồi bẻ c&agrave;nh liễu đ&aacute;nh v&agrave;o hai đ&ugrave;i vi&ecirc;n đốc bưu, đ&aacute;nh g&atilde;y lu&ocirc;n đến hơn mười c&agrave;nh liễu. Huyền Đức đang l&uacute;c ngồi buồn bỗng nghe ngo&agrave;i cửa huyện c&oacute; tiếng x&ocirc;n xao liền hỏi, tả hữu n&oacute;i rằng: - Trương tướng qu&acirc;n đang tr&oacute;i đ&aacute;nh một người n&agrave;o ở cửa huyện. Huyền Đức vội chạy ra xem, tưởng Phi tr&oacute;i đ&aacute;nh ai, t&eacute; ra l&agrave; quan thanh tra! Huyền Đức kinh ngạc, hỏi đầu đu&ocirc;i. Phi n&oacute;i: - C&aacute;i thằng hại nước mọt d&acirc;n n&agrave;y, chẳng đ&aacute;nh cho chết c&ograve;n đợi đến bao giờ! Vi&ecirc;n đốc bưu k&ecirc;u: - &Ocirc;ng Huyền Đức ơi! Cứu t&ocirc;i với! Huyền Đức vốn người nh&acirc;n từ, trong l&ograve;ng kh&ocirc;ng nỡ liền bảo Trương Phi kh&ocirc;ng được đ&aacute;nh nữa. Quan C&ocirc;ng c&uuml;ng chạy lại n&oacute;i rằng: - Huynh trưởng l&agrave;m n&ecirc;n biết bao c&ocirc;ng lớn, chỉ mới được bổ chức huyện &uacute;y nhỏ mọn n&agrave;y. Nay lại c&ograve;n bị thằng đốc bưu n&oacute; sỉ nhục. T&ocirc;i nghĩ c&aacute;i bụi ch&ocirc;ng gai kh&ocirc;ng phải l&agrave; nơi chim loan chim phượng đậu. Bất nhược ta giết qu&aacute;ch thằng đốc bưu n&agrave;y đi, rồi bỏ quan về l&agrave;ng, mưu t&iacute;nh việc lớn c&ograve;n hơn. Huyền Đức b&egrave;n đem c&aacute;i ấn treo v&agrave;o cổ vi&ecirc;n đốc bưu m&agrave; mắng rằng: - Cứ c&aacute;i tội m&agrave;y hại d&acirc;n, đ&aacute;ng n&ecirc;n giết chết mới phải, nhưng nay h&atilde;y tạm tha cho m&agrave;y. Ấn đ&acirc;y, tao trả ch&uacute;ng m&agrave;y. Từ nay ch&uacute;ng tao kh&ocirc;ng ở đ&acirc;y nữa! Vi&ecirc;n đốc bưu được sống s&oacute;t, về n&oacute;i với quan th&aacute;i th&uacute; Định Ch&acirc;u, quan th&aacute;i th&uacute; tư giấy đi c&aacute;c nơi, sai người n&atilde; bắt anh em Huyền Đức. Huyền Đức c&ugrave;ng Quan, Trương sang Đại Ch&acirc;u ở với Lưu Kh&ocirc;i. Kh&ocirc;i thấy Huyền Đức l&agrave; người t&ocirc;n thất nh&agrave; H&aacute;n, b&egrave;n giấu ở trong nh&agrave;, kh&ocirc;ng cho ai biết. N&oacute;i về mười hoạn quan, trong tay đ&atilde; nắm được quyền to, b&egrave;n b&agrave;n t&iacute;nh với nhau hễ ai kh&ocirc;ng theo, ch&uacute;ng đều giết đi cả. Triệu Trung, Trương Nhượng sai người đến đ&ograve;i c&aacute;c tướng c&oacute; c&ocirc;ng ph&aacute; giặc Khăn V&agrave;ng ng&agrave;y trước phải lễ v&agrave;ng bạc mới được l&agrave;m quan, bằng kh&ocirc;ng th&igrave; t&acirc;u vua bắt b&atilde;i chức. V&igrave; lẽ ấy m&agrave; Ho&agrave;ng Phủ Tung, Chu Tuấn kh&ocirc;ng chịu đ&uacute;t l&oacute;t, đều bị b&atilde;i cả. Vua lại phong Triệu Trung l&agrave;m sa kỵ tướng qu&acirc;n, bọn Trương Nhượng mười ba người đều phong tước hầu, triều ch&iacute;nh mỗi ng&agrave;y một suy đồi, nh&acirc;n d&acirc;n c&uuml;ng ta o&aacute;n. Bởi thế ở Trường Sa c&oacute; Khu Tinh nổi loạn, ở Ngư Đương c&oacute; Trương Thuần, Trương Cử l&agrave; phản. Cử tự xưng l&agrave; thi&ecirc;n tử, Thuần xưng l&agrave; đại tướng qu&acirc;n. Những tờ biểu c&aacute;o cấp gửi về triều đ&igrave;nh như bướm bay, bọn hoạn quan đều giấu cả, kh&ocirc;ng t&acirc;u vua biết. Một h&ocirc;m vua đang c&ugrave;ng mười vi&ecirc;n hoạn quan uống rượu ở vườn hoa sau cung, bỗng thấy quan gi&aacute;n nghị đại phu Lưu Đ&agrave;o đi tắt đến trước mặt vua m&agrave; kh&oacute;c. Vua hỏi v&igrave; cớ g&igrave;. Đ&agrave;o n&oacute;i: - Thi&ecirc;n hạ nguy ngập đến nơi rồi, m&agrave; bệ hạ c&ograve;n cứu vui chơi say tỉnh với bọn hoạn quan như thế ru? Vua n&oacute;i: - Nh&agrave; nước đương y&ecirc;n ổn, c&oacute; việc g&igrave; m&agrave; nguy ngập? - T&acirc;u bệ hạ! Hiện nay giặc cướp nổi l&ecirc;n tứ tung x&acirc;m chiếm khắp c&aacute;c ch&acirc;u quận, c&aacute;i vạ đều bởi mười t&ecirc;n hoạn quan b&aacute;n quan hại d&acirc;n, lừa dối qu&acirc;n thượng m&agrave; ra cả. Cho n&ecirc;n những người ch&iacute;nh nh&acirc;n qu&acirc;n tử bỏ đi hết cả, c&aacute;i nguy đ&atilde; ở ngay trước mắt rồi c&ograve;n g&igrave;? Mười vi&ecirc;n hoạn quan đều phủ phục trước mặt vua t&acirc;u rằng: - Mu&ocirc;n t&acirc;u th&aacute;nh thượng, quan đại thần đ&atilde; kh&ocirc;ng c&oacute; lượng bao dung, ch&uacute;ng t&ocirc;i biết m&igrave;nh chẳng tho&aacute;t. C&uacute;i xin th&aacute;nh thượng cho ch&uacute;ng t&ocirc;i được to&agrave;n t&iacute;nh mạng trở về qu&ecirc; qu&aacute;n, t&igrave;nh nguyện đem hết gia sản gi&uacute;p đỡ việc qu&acirc;n. N&oacute;i đoạn đều kh&oacute;c nức nở. Vua cả giận mắng Đ&agrave;o rằng: - Nh&agrave; ngươi c&uuml;ng c&oacute; người hầu hạ, sao kh&ocirc;ng cho trẫm c&oacute; người hầu hạ? Tức th&igrave; sai v&otilde; sĩ l&ocirc;i Lưu Đ&agrave;o ra ch&eacute;m. Lưu Đ&agrave;o vừa đi vừa k&ecirc;u lớn rằng: - Trời ơi! Đ&agrave;o n&agrave;y chết kh&ocirc;ng đ&aacute;ng tiếc, chỉ tiếc c&aacute;i cơ nghiệp nh&agrave; H&aacute;n hơn bốn trăm năm nay bỗng ti&ecirc;u diệt trong khoảnh khắc. V&otilde; sĩ đem Lưu Đ&agrave;o ra, sắp h&agrave;nh h&igrave;nh th&igrave; c&oacute; một quan đại thần n&oacute;i to l&ecirc;n rằng: - Khoan, kh&ocirc;ng được hạ thủ vội, đợi ta v&agrave;o can vua đ&atilde;. Mọi người nh&igrave;n xem ai, th&igrave; l&agrave; quan tư đồ TrầnĐam. Đam đi tắt v&agrave;o cung t&acirc;u rằng: - T&acirc;u bệ hạ, chẳng hay Lưu gi&aacute;n nghị can tội g&igrave; m&agrave; bị giết? Vua ph&aacute;n: - N&oacute; d&aacute;m gi&egrave;m pha cận thần của trẫm, lại x&uacute;c phạm cả trẫm nữa. Đam t&acirc;u: - Hiện thi&ecirc;n hạ ai c&uuml;ng muốn nuốt sống ăn tươi mười t&ecirc;n hoạn quan. Vậy m&agrave; bệ hạ kinh n&oacute; như cha mẹ. Ch&uacute;ng kh&ocirc;ng c&oacute; một ch&uacute;t c&ocirc;ng n&agrave;o m&agrave; được phong đến tước hầu. Huống chi l&uuml; Phong Tư kết li&ecirc;n với giặc Khăn V&agrave;ng, toan l&agrave;m tay trong cho ch&uacute;ng n&oacute;. Nay nếu bệ hạ kh&ocirc;ng tỉnh ngộ, x&atilde; tắc đến đổ mất! Vua ph&aacute;n: - Phong Tư l&agrave;m loạn, việc c&ograve;n mập mờ chưa r&otilde;, c&ograve;n trong bọn hoạn quan mười người, h&aacute; kh&ocirc;ng c&oacute; một hai người trung nghĩa hay sao? Trần Đam đập đầu xuống đất, cố can. Vua nổi giận, sai l&ocirc;i Đam ra, bắt bỏ ngục c&ugrave;ng với Lưu Đ&agrave;o. Ngay đ&ecirc;m h&ocirc;m ấy bọn hoạn quan v&agrave;o ngục giết cả hai người rồi giả l&agrave;m chiếu chỉ nh&agrave; vua cử T&ocirc;n Ki&ecirc;n l&agrave;m th&aacute;i th&uacute; Trường Sa v&agrave; đem qu&acirc;n đi đ&aacute;nh Khu Tinh. Chưa đầy năm mươi ng&agrave;y c&oacute; tin b&aacute;o thắng trận, khu Giang Hạ đều b&igrave;nh định cả. Vua chiếu xuống phong Ki&ecirc;n l&agrave;m &Ocirc; tr&igrave;nh hầu, phong Lưu Ngu l&agrave;m quan mục U Ch&acirc;u, đem qu&acirc;n đến Ngư Dương đ&aacute;nh Trương Thuần, Trương Cử. Ở Đại Ch&acirc;u Lưu, Kh&ocirc;i được tin b&egrave;n viết thư tiến dẫn Huyền Đức. Nga mừng lắm, cử Huyền Đức l&agrave;m quan đ&ocirc; &uacute;y, đem qu&acirc;n đến tận hang ổ giặc đ&aacute;nh mấy trậnlớn, giặc bị thua lu&ocirc;n. Trương Thuận t&iacute;nh vốn hung hăng, kh&ocirc;ng được l&ograve;ng qu&acirc;n bị t&ecirc;n thủ hạ chặt đầu đem nộp rồi dẫn qu&acirc;n ra h&agrave;ng. Trương Cử biết thế m&igrave;nh kh&ocirc;ng sao địch nổi, c&uuml;ng tự thắt cổ chết. Thế l&agrave; cả c&otilde;i Ngư Dương đều b&igrave;nh định. Lưu Ngu d&acirc;ng biểu t&acirc;u c&ocirc;ng lớn của Lưu Bị. Triều đ&igrave;nh tha tội đ&aacute;nh vi&ecirc;n đốc bưu v&agrave; bổ l&agrave;m quan thừa ở Hạ Mật, sau lại nhắc l&ecirc;n l&agrave;m quan &uacute;y ở Cao Đường. C&ocirc;ng T&ocirc;n Toản lại d&acirc;ng biểu t&acirc;u c&ocirc;ng đ&aacute;nh giặc trước kia của Huyền Đức v&agrave; tiến cử l&agrave;m quan tư m&atilde;, lĩnh chức huyện lệnh B&igrave;nh Nguy&ecirc;n. Huyền Đức ở B&igrave;nh Nguy&ecirc;n nhờ c&oacute; lương tiền v&agrave; qu&acirc;n m&atilde;, n&ecirc;n kh&ocirc;i phục lại được cảnh phồn vinh ng&agrave;y trước. Lưu Ngu c&uuml;ng c&oacute; c&ocirc;ng dẹp giặc, n&ecirc;n được thăng l&agrave;m quan th&aacute;i &uacute;y. Th&aacute;ng tư, m&ugrave;a hạ năm Trung b&igrave;nh thứ s&aacute;u (một trăm t&aacute;m mươi ch&iacute;n) vua Linh Đế bệnh nặng, triệu quan đại tướng qu&acirc;n H&agrave; Tiến v&agrave;o cung b&agrave;n t&iacute;nh mọi việc quan trọng về sau. Nguy&ecirc;n H&agrave; Tiến vốn xuất th&acirc;n con nh&agrave; h&agrave;ng thịt, v&igrave; c&oacute; em g&aacute;i lấy vua, sinh được ho&agrave;ng tử t&ecirc;n l&agrave; Biện được lập l&agrave;m Ho&agrave;ng hậu, n&ecirc;n Tiến nhờ đ&oacute; được quyền cao chức trọng. Vua lại y&ecirc;u mến một mỹ nh&acirc;n nữa họ Vương, sinh được ho&agrave;ng tử t&ecirc;n l&agrave; Hiệp. H&agrave; hậu ghen gh&eacute;t, bỏ thuốc độc giết Vương mỹ nh&acirc;n, n&ecirc;n ho&agrave;ng tử Hiệp phải nu&ocirc;i ở trong cung Đổng Th&aacute;i Hậu. Đổng Th&aacute;i Hậu l&agrave; mẹ vua Linh Đế, nguy&ecirc;n l&agrave; vợ Giải độc đ&igrave;nh hầu Lưu Thường. Bởi khi trước vua Ho&agrave;n Đế kh&ocirc;ng c&oacute; con trai, phải đ&oacute;n con trai Giải độc đ&igrave;nh hầu l&ecirc;n l&agrave;m vua, tức l&agrave; vua Linh Đế. Linh Đế l&ecirc;n ng&ocirc;i; b&egrave;n đ&oacute;n mẹ v&agrave;o phụng dưỡng ở trong cung v&agrave; t&ocirc;n l&agrave;m Th&aacute;i Hậu. Đổng Th&aacute;i Hậu thường khuy&ecirc;n vua lập ho&agrave;ng tử Hiệp l&agrave;m th&aacute;i tử để nối ng&ocirc;i vua về sau. Vua c&uuml;ng y&ecirc;u Hiệp hơn, n&ecirc;n c&oacute; &yacute; muốn lập Hiệp. L&uacute;c ấy bệnh vua đ&atilde; nguy, trung thường thị l&agrave; Kiển Thạc t&acirc;u rằng: - T&acirc;u bệ hạ! Việc n&agrave;y quan hệ rất lớn, nếu muốn lập ho&agrave;ng tử Hiệp, trước hết xin giết H&agrave; Tiến mới khỏi lo ngại về sau. Vua lấy l&agrave;m phải, gi&aacute;ng chỉ vời Tiến v&agrave;o cung. Tiến vừa đi đến cửa cung, gặp quan tư m&atilde; Phan Ẩn bảo Tiến rằng: - Đừng v&agrave;o cung, Kiển Thạc n&oacute; định mưu giết &ocirc;ng đấy! Tiến cả sợ, vội về nh&agrave;, triệu c&aacute;c quan đại thần đến b&agrave;n định, muốn giết hết cả bọn hoạn quan. Một người đứng l&ecirc;n n&oacute;i: - Thế lực của bọn hoạn quan, g&acirc;y ra tự đời vua Xung, vua Chất, ng&agrave;y nay lan rộng khắp cả trong triều, giết hết thế n&agrave;o được. Nếu cơ mưu kh&ocirc;ng k&iacute;n, chết đến cả họ ngay, vậy xin nghĩ cho kỹ. Tiến nh&igrave;n xem ai, th&igrave; l&agrave; quan điển qu&acirc;n hiệu &uacute;y T&agrave;o Th&aacute;o. Tiến mắng Th&aacute;o rằng: - Trẻ con biết đ&acirc;u việc lớn của triều đ&igrave;nh! Đang l&uacute;c Tiến c&ograve;n d&ugrave;ng dằng chưa quyết. Phan An chạy đến b&aacute;o rằng: - Ho&agrave;ng đế đ&atilde; băng h&agrave; rồi, hiện Kiển Thạc đang b&agrave;n với mười t&ecirc;n hoạn quan định một mặt giấu k&iacute;n kh&ocirc;ng ph&aacute;t tang, một mặt giả l&agrave;m chiếu chỉ, triệu H&agrave; Quốc Cữu v&agrave;o cung giết đi, rồi lập Ho&agrave;ng tử Hiệp l&ecirc;n ng&ocirc;i. N&oacute;i chưa dứt lời, c&oacute; sứ giả đến triệu Tiến v&agrave;o cung, T&agrave;o Th&aacute;o n&oacute;i: - Việc cốt yếu b&acirc;y giờ l&agrave; phải lập vua trước đ&atilde;, rồi sau h&atilde;y n&oacute;i đến việc trừ giặc. Tiến n&oacute;i: - C&oacute; ai d&aacute;m c&ugrave;ng ta v&agrave;o cung lập vua mới v&agrave; đ&aacute;nh giặckh&ocirc;ng? Một người đứng phắt dậy n&oacute;i rằng: - T&ocirc;i xin đem năm ngh&igrave;n tinh binh, ch&eacute;m kh&oacute;a cửa cung, v&agrave;o lập vua mới, giết hết bọn hoạn quan, qu&eacute;t sạch trong triều để y&ecirc;n thi&ecirc;n hạ. Tiến nh&igrave;n xem, th&igrave; người ấy l&agrave; con quan tư đồ Vi&ecirc;n Ph&ugrave;ng, ch&aacute;u Vi&ecirc;n Ngỗi t&ecirc;n l&agrave; Thiệu, tự l&agrave; Bản Sơ biện l&agrave;m quan tư lệ hiệu &uacute;y. Tiến cả mừng, b&egrave;n điểm năm ngh&igrave;n qu&acirc;n ngự l&acirc;m giao cho Vi&ecirc;n Thiệu. Thiệu nai nịt đem qu&acirc;n đi trước. H&agrave; Tiến dẫn bọn H&agrave; Ngung, Tu&acirc;n Du, Trịnh Th&aacute;i, hơn ba mươi quan đại thần đi sau, c&ugrave;ng v&agrave;o trong cung, đến trước linh cữu vua Linh Đế, lập th&aacute;i tử Biện l&ecirc;n l&agrave;m vua. C&aacute;c quan tung h&ocirc; xong đ&acirc;u đấy, Vi&ecirc;n Thiệu b&egrave;n đi v&agrave;o cung bắt Kiển Thạc, Thạc kinh hoảng chạy nấp v&agrave;o dưới bụi c&acirc;y trong vườn ngự uyển, bị trung thường thị l&agrave; Qu&aacute;ch Thắng giết chết. Qu&acirc;n cấm binh do Thạc quản lĩnh đều ra h&agrave;ng hết cả. Vi&ecirc;n Thiệu n&oacute;i với H&agrave; Tiến rằng: - Bọn th&aacute;i gi&aacute;m trước kết b&egrave; với nhau, b&acirc;y giờ n&ecirc;n thừa thế giết cả đi. Bọn Trương Nhượng biết tin, sợ h&atilde;i hết hồn, vội chạy v&agrave;o cung van lạy H&agrave; Hậu rằng: - B&agrave;y mưu hại quốc cữu trước đ&acirc;y chỉ c&oacute; một m&igrave;nh Kiển Thạc thực quả kh&ocirc;ng d&iacute;nh d&aacute;ng g&igrave; đến ch&uacute;ng t&ocirc;i. Nay quốc cữu nghe lời Vi&ecirc;n Thiệu, muốn giết hết cả ch&uacute;ng t&ocirc;i, thật l&agrave; oan qu&aacute;, xin mẫu hậu rủ l&ograve;ng thương cứu cho. H&agrave; Hậu n&oacute;i: - C&aacute;c ngươi đừng lo, ta sẽ bảo hộ cho. B&egrave;n gi&aacute;ng chỉ triệu H&agrave; Tiến v&agrave;o cung, khẽ bảo rằng: - Anh em ta h&agrave;n vi từ thuở nhỏ, nếu kh&ocirc;ng c&oacute; bọn Trương Nhượng, sao c&oacute; ph&uacute; qu&yacute; ng&agrave;y nay? Nay thằng Kiển Thạc bất nh&acirc;n đ&atilde; bị giết rồi, sao anh c&ograve;n tin lời người ta n&oacute;i m&agrave; toan giết cả bọn hoạn quan? H&agrave; Tiến nghe đoạn, ra bảo c&aacute;c quan rằng: - Kiển Thạc b&agrave;y mưu hại ta đem giết cả họ n&oacute; đi. C&ograve;n những người kh&aacute;c đừng n&ecirc;n giết hại! Vi&ecirc;n Thiệu n&oacute;i: - Nhổ cỏ kh&ocirc;ng nhổ hết rễ, rồi mang vạ v&agrave;o th&acirc;n! Tiến n&oacute;i: - &Yacute; ta đ&atilde; quyết, người đừng nhiều lời nữa! C&aacute;c quan đều lui về cả. H&ocirc;m sau Th&aacute;i Hậu cho H&agrave; Tiến tham x&eacute;t c&ocirc;ng việc c&aacute;c bộ thượng thư, c&ograve;n c&aacute;c người kh&aacute; đều được thăng chức. Đổng Th&aacute;i Hậu nghe biết chuyện, b&egrave;n cho vời bọn Trương Nhượng v&agrave;o cung ph&aacute;n rằng: - Con em thằng H&agrave; Tiến, trước kia v&igrave; c&oacute; ta cất nhắc cho n&ecirc;n mới được sung sướng. Ng&agrave;y nay con n&oacute; l&ecirc;n ng&ocirc;i Ho&agrave;ng Đế, c&aacute;c quan trong ngo&agrave;i đều l&agrave; v&acirc;y c&aacute;nh n&oacute; cả, uy thế n&oacute; to lắm, ta biết t&iacute;nh sao b&acirc;y giờ? Trương Nhượng n&oacute;i: - T&acirc;u mẫu hậu, việc đ&oacute; c&uuml;ng dễ. Xin mẫu hậu cứ ra ngự triều đường, rủ m&agrave;nh m&agrave;nh coi x&eacute;t việc ch&iacute;nh, phong ho&agrave;ng tử Hiệp l&ecirc;n tước vương, gia phong quốc cữu Đổng Trọng l&ecirc;n chức lớn coi giữ binh quyền v&agrave; trọng dụng bọn hạ thần th&igrave; việc lớn c&oacute; thể mưu t&iacute;nh xongđược. Đổng Th&aacute;i Hậu cả mừng, s&aacute;ng h&ocirc;m sau l&acirc;m triều gi&aacute;ng chỉ phong ho&agrave;ng tử Hiệp l&agrave;m Trần lưu vương, Đổng Trọng l&agrave;m phi&ecirc;u kỵ tướng qu&acirc;n. Bọn Trương Nhượng c&uuml;ng đều được tham dự triều ch&iacute;nh. H&agrave; Th&aacute;i Hậu thấy Đổng Th&aacute;i Hậu chuy&ecirc;n quyền, b&egrave;n sửa một tiệc yến ở trong cung, mời Đổng Th&aacute;i Hậu đến dự. Giữa tiệc, H&agrave; Th&aacute;i Hậu đứng dậy, n&acirc;ng ch&eacute;n rượu v&aacute;i hai v&aacute;i m&agrave; thưa rằng: - Ch&uacute;ng ta đều l&agrave; đ&agrave;n b&agrave;, kh&ocirc;ng n&ecirc;n tham dự triều ch&iacute;nh. Xưa b&agrave; L&atilde; hậu chỉ v&igrave; tham giữ trọng quyền, đến nỗi ngh&igrave;n người trong họ đều bị giết. Nay ch&uacute;ng ta chỉ n&ecirc;n ở y&ecirc;n trong cung cấm, việc triều ch&iacute;nh đ&atilde; c&oacute; c&aacute;c nguy&ecirc;n l&atilde;o đại thần b&agrave;n t&iacute;nh với nhau. Thế mới l&agrave; hạnh ph&uacute;c cho nh&agrave; nước, c&uacute;i xin Th&aacute;i Hậu soi x&eacute;t. Đổng Th&aacute;i Hậu nổi giận mắng rằng: - M&agrave;y đ&atilde; đem l&ograve;ng ghen gh&eacute;t, đ&aacute;nh thuốc độc giết Vương mỹ nh&acirc;n. Nay m&agrave;y cậy c&oacute; con l&agrave;m vua, cậy thế lực anh m&agrave;y l&agrave; thằng H&agrave; Tiến, d&aacute;m n&oacute;i hỗn với tao &agrave;? Tao sai quan phi&ecirc;u kỵ (trỏ v&agrave;o Đổng Trọng) chặt cổ anh m&agrave;y dễ như trở b&agrave;n tay cho m&agrave; xem! H&agrave; hậu c&uuml;ng tức giận c&atilde;i lại rằng: - Ta đem lời khuy&ecirc;n can, m&agrave; người lại trở mặt giận &agrave;? Đổng Th&aacute;i Hậu c&agrave;ng tức, lại nhiếc m&oacute;c H&agrave; hậu: - C&aacute;i đồ b&aacute;n thịt nh&agrave; m&agrave;y, c&ograve;n biết c&aacute;i g&igrave;! Hai người c&atilde;i nhau m&atilde;i, sau c&oacute; bọn Trương Nhượng khuy&ecirc;n can, người n&agrave;o mới về cung người nấy. Đ&ecirc;m h&ocirc;m ấy H&agrave; hậu triệu H&agrave; Tiến v&agrave;o cung, kể lại việc đ&atilde; xảy ra. Tiến về mời c&aacute;c quan tam c&ocirc;ng đến b&agrave;n bạc. Buổi chầu s&aacute;ng h&ocirc;m sau, Tiến xui c&aacute;c đ&igrave;nh thần t&acirc;u rằng: - Đổng Th&aacute;i Hậu nguy&ecirc;n l&agrave; một vị Phi&ecirc;n phi, kh&ocirc;ng n&ecirc;n ở l&acirc;u trong cung cấm. Xin rời ngay ra an tr&iacute; ở H&agrave; Giang, hạn lập tức phải đi ngay! H&agrave; Tiến một mặt sai người đưa Đổng Th&aacute;i Hậu đi, một mặt ph&aacute;i qu&acirc;n cấm binh đến v&acirc;y nh&agrave; phi&ecirc;u kỵ tướng qu&acirc;n Đổng Trọng đ&ograve;i lấy ấn thụ. Đổng Trọng biết việc chẳng l&agrave;nh, liền tự vẫn ở hậu đường. L&uacute;c người nh&agrave; cất tiếng kh&oacute;c, qu&acirc;n sĩ mới kh&ocirc;ng v&acirc;y nữa trở về. Bọn Trương Nhượng, Đo&agrave;n Khu&ecirc; thấy phe Đổng Th&aacute;i Hậu thất bại, b&egrave;n đem v&agrave;ng ngọc, ch&acirc;u b&aacute;u đ&uacute;t l&oacute;t em trai H&agrave; Tiến l&agrave; H&agrave; Mi&ecirc;u v&agrave; mẹ Tiến l&agrave; V&uuml; Dương Qu&acirc;n, nhờ v&agrave;o n&oacute;i kh&eacute;o với H&agrave; Th&aacute;i Hậu che chở cho. Bởi vậy mười t&ecirc;n hoạn quan lại được tin d&ugrave;ng. Th&aacute;ng s&aacute;u năm ấy, H&agrave; Tiến ngầm sai người đến H&agrave; Giang đ&aacute;nh thuốc độc giết Đổng Th&aacute;i Hậu, đem linh cữu về kinh, t&aacute;ng ở Văn Lăng. Tiến c&aacute;o bệnh kh&ocirc;ng đi đưa đ&aacute;m. Một h&ocirc;m quan tư lệ hiệu &uacute;y Vi&ecirc;n Thiệu v&agrave;o n&oacute;i với Tiến rằng: - Bọn Trương Nhượng, Đo&agrave;n Khu&ecirc; đang đi n&oacute;i phao l&ecirc;n rằng &ocirc;ng đ&aacute;nh thuốc độc giết Đổng Th&aacute;i Hậu, để mưu việc lớn. Nếu b&acirc;y giờ &ocirc;ng kh&ocirc;ng giết ngay bọn ch&uacute;ng n&oacute;, tất c&oacute; vạ lớnvề sau. Xưa Đậu V&uuml; muốn nhờ bọn hoạn quan trong cung, v&igrave; mưu m&ocirc; kh&ocirc;ng k&iacute;n lại bị ch&uacute;ng giết. Hiện giờ anh em v&acirc;y c&aacute;nh &ocirc;ng, đều l&agrave; những tay anh tuấn, vả lại quyền ở trong tay, thật l&agrave; cơ hội trời cho, kh&ocirc;ng n&ecirc;n bỏ lỡ. Tiến n&oacute;i: - Thong thả để sau sẽ b&agrave;n t&iacute;nh. Mấy đứa tả hữu nghe lỏm được chuyện ấy, liền đi b&aacute;o với Trương Nhượng. Bọn Trương Nhượng lại đem rất nhiều của b&aacute;u lễ đ&uacute;t H&agrave; Mi&ecirc;u. Mi&ecirc;u v&agrave;o t&acirc;u H&agrave; hậu rằng: - Đại tướng qu&acirc;n ph&ograve; t&aacute; vua mới, kh&ocirc;ng l&agrave;m điều nh&acirc;n từ, chỉ chăm ch&eacute;m giết. Nay tự nhi&ecirc;n v&ocirc; cớ lại toan giết cả mười hoạn quan, thật tự m&igrave;nh g&acirc;y ra mầmloạn. H&agrave; hậu cho l&agrave; phải. Một chốc, H&agrave; Tiến v&agrave;o t&acirc;u xin giết bọn hoạn quan. Hậu n&oacute;i: - Bọn hoạn quan coi s&oacute;c việc trong cung cấm, ph&eacute;p c&uuml; nh&agrave; H&aacute;n từ xưa vẫn thế. Cớ sao ti&ecirc;n đế vừa mới chầu trời m&agrave; người chỉ muốn giết những bầy t&ocirc;i c&uuml;. Thế kh&ocirc;ng phải l&agrave; t&ocirc;n trọng sự thờ c&uacute;ng đối với ti&ecirc;n đế! Tiến vốn l&agrave; một người nh&ugrave; nhờ kh&ocirc;ng quyết đo&aacute;n, nghe H&agrave; Th&aacute;i Hậu n&oacute;i như vậy, v&acirc;ng dạ lu&ocirc;n miệng rồi lui ra. Vi&ecirc;n Thiệu đứng đ&oacute;n ngo&agrave;i cửa, hỏi rằng: - Việc lớn thế n&agrave;o? Tiến n&oacute;i: - Th&aacute;i Hậu kh&ocirc;ng nghe th&igrave; l&agrave;m thế n&agrave;o? Thiệu n&oacute;i: - N&ecirc;n triệu những người anh h&ugrave;ng c&aacute;c nơi đem qu&acirc;n về kinh giết hết bọn hoạn quan n&agrave;y đi. Đến l&uacute;c việc đ&atilde; cấp b&aacute;ch th&igrave; Th&aacute;i Hậu muốn chẳng nghe c&uuml;ng chẳng được. Tiến n&oacute;i: - Kế ấy diệu lắm. B&egrave;n truyền hịch đi c&aacute;c trấn, triệu c&aacute;c tướng lĩnh đem qu&acirc;n về kinh đ&ocirc;. Quan chủ bạ Trần L&acirc;m can rằng: - Việc ấy kh&ocirc;ng n&ecirc;n. Tục ngữ c&oacute; c&acirc;u: &quot;Bưng mắt bắt chim&quot;, ấy l&agrave; m&igrave;nh tự dối m&igrave;nh. Việc nhỏ mọn c&uuml;ng kh&ocirc;ng thể tự dối m&agrave; l&agrave;m xong, huống chi l&agrave; việc lớn nước nh&agrave;? Nay tướng qu&acirc;n dựa uy vua, cầm quyền lớn, như rồng bay hổ nhảy, muốn l&agrave;m thế n&agrave;o c&uuml;ng được. Việc giết bọn hoạn quan thật dễ kh&ocirc;ng kh&aacute;c quạt l&ograve; than đốt mấy sợi t&oacute;c. L&agrave;m việc một c&aacute;ch quyền biến, quyết đo&aacute;n ngay, ph&aacute;t động nhanh như sấm s&eacute;t tức l&agrave; thuận đạo trời v&agrave; l&ograve;ngngười. Nay nếu triệu c&aacute;c quan ngoại trấn, mỗi người một bụng, biết ai thế n&agrave;o? C&oacute; kh&aacute;c đưa chu&ocirc;i dao cho người cầm m&agrave; m&igrave;nh cầm đằng lưỡi kh&ocirc;ng? Như thế kh&ocirc;ng những việc kh&ocirc;ng th&agrave;nh m&agrave; lại sinh biến loạn nữa. Tiến cười n&oacute;i: - Đ&oacute; l&agrave; kiến thức của hạng người h&egrave;n nh&aacute;t! Một người đứng l&ecirc;n vỗ tay cười lớn m&agrave; rằng: - Việc ấy dễ như trở b&agrave;n tay, h&agrave; tất phải b&agrave;n cho lắm! Mọi người nh&igrave;n xem ai, người ấy ch&iacute;nh l&agrave; T&agrave;o Th&aacute;o. Đ&oacute; ch&iacute;nh l&agrave;: Muốn giết tiểu nh&acirc;n b&ecirc;n cạnh ch&uacute;a N&ecirc;n nghe mưu sĩ ở trong triều. Muốn biết T&agrave;o Th&aacute;o n&oacute;i thế n&agrave;o, xem hồi sau sẽ r&otilde;.</span></span></span></p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(27, 'Tuổi thơ dữ dội', 'Văn học Việt Nam', 'Phùng Quán', 1988, 'tuoi-tho-du-doi.jpg', 'Đang cập nhật', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tuổi Thơ Dữ Dội kể lại ᴄ&acirc;u ᴄhuуện ᴄủa Đội Thiếu ni&ecirc;n Trinh ѕ&aacute;t trung đo&agrave;n Trần Cao V&acirc;n. Kh&aacute;ᴄ ᴠới những ᴄ&acirc;u ᴄhuуện ᴄhiến tranh thường thấу, ѕ&aacute;ᴄh kh&ocirc;ng tập trung mi&ecirc;u tả những anh l&iacute;nh ᴄụ Hồ oai phong lẫm liệt, những mối t&igrave;nh ᴄhiến ѕĩ đi ᴄ&ugrave;ng năm th&aacute;ng, m&agrave; nh&acirc;n ᴠật ᴄh&iacute;nh ᴄủa Tuổi Thơ Dữ Dội l&agrave; những ᴄh&uacute; b&eacute; tham gia Vệ Quốᴄ Đo&agrave;n từ năm 12, 13 tuổi.</span></span></p>\r\n', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Một tuần trước ng&agrave;y Huế nổ s&uacute;ng kh&aacute;ng chiến, d&acirc;n trong th&agrave;nh phố được lệnh tản cư hết về c&aacute;c v&ugrave;ng qu&ecirc;. Nhưng qua mấy ng&agrave;y đầu đ&aacute;nh nhau, tin tức thắng lợi từ Mặt trận đưa về dồn dập. Tin thắng lợi một đồn mười, mười đồn trăm, n&ecirc;n đồng b&agrave;o chắc mẩm tụi T&acirc;y sắp đầu h&agrave;ng đến nơi rồi. Do đ&oacute;, kh&ocirc;ng ai bảo ai, mon men k&eacute;o dần về ph&iacute;a th&agrave;nh phố. Họ n&oacute;ng l&ograve;ng sốt ruột được mau mau trở về dọn dẹp lại nh&agrave; cửa, phố x&aacute;, trở về với c&ocirc;ng việc l&agrave;m ăn thường ng&agrave;y.</span><br />\r\n<br />\r\n<span style=\"color:#000000\">Cuối c&ugrave;ng họ tụ tập lại đ&ocirc;ng đ&uacute;c ph&iacute;a b&ecirc;n n&agrave;y cầu Bao Vinh. B&ecirc;n kia cầu thuộc khu vực mặt trận A, ai muốn sang phải c&oacute; giấy ph&eacute;p của Ban chỉ huy Mặt trận cấp.</span><br />\r\n<br />\r\n<span style=\"color:#000000\">Như đ&atilde; th&agrave;nh lệ, tờ mờ s&aacute;ng l&agrave; đồng b&agrave;o t&uacute;a hết ra đường, k&eacute;o đến đứng chen ch&uacute;c ở đầu cầu, chờ nghe tin tức thắng lợi của Mặt trận đ&ecirc;m qua. Họ chen lấn, &ugrave;n &ugrave;n mỗi l&uacute;c một s&aacute;t đến chỗ bậc đ&aacute; bước l&ecirc;n cầu. Nếu kh&ocirc;ng c&oacute; anh Vệ Quốc Qu&acirc;n cầm ngang khẩu s&uacute;ng trường d&agrave;i như c&acirc;y s&agrave;o chăn vịt đứng g&aacute;c ngay trước mặt, chắc cả khối người chen ch&uacute;c n&agrave;y đ&atilde; tr&agrave;n qua b&ecirc;n kia cầu như nước vỡ bờ.</span><br />\r\n<br />\r\n<span style=\"color:#000000\">&ETH;ồng b&agrave;o nhao nhao hỏi anh l&iacute;nh g&aacute;c:</span><br />\r\n<br />\r\n<span style=\"color:#000000\">-&ETH;&ecirc;m qua qu&acirc;n ta mần ăn c&oacute; kh&aacute; kh&ocirc;ng anh?</span><br />\r\n<br />\r\n<span style=\"color:#000000\">-Nện tụi T&acirc;y ở vị tr&iacute; m&ocirc; m&agrave; s&uacute;ng, lựu đạn nổ như rang bắp cả đ&ecirc;m rứa?</span><br />\r\n<br />\r\n<span style=\"color:#000000\">-Liệu tụi hắn sắp sửa &ldquo;h&ocirc;-l&ecirc;-manh&rdquo; chưa, cho b&agrave; con về dọn dẹp nh&agrave; cửa, phố x&aacute;?</span><br />\r\n<br />\r\n<span style=\"color:#000000\">Bị b&agrave; con hỏi tối tăm mặt mũi, anh l&iacute;nh g&aacute;c chỉ c&ograve;n biết nhăn nh&oacute; đề nghị:</span><br />\r\n<br />\r\n<span style=\"color:#000000\">-Xin b&agrave; con đứng x&ecirc; x&ecirc; ra cho t&ocirc;i một ch&uacute;t! &ETH;ứng s&aacute;t v&ocirc; tui ri c&ograve;n biết g&aacute;c x&aacute;ch l&agrave;m răng?</span><br />\r\n<br />\r\n<span style=\"color:#000000\">S&aacute;ng h&ocirc;m đ&oacute;, từ trong đ&aacute;m đ&ocirc;ng chen ch&uacute;c ồn &agrave;o ấy, c&oacute; thằng b&eacute; lợi dụng đ&uacute;ng l&uacute;c anh l&iacute;nh g&aacute;c c&ograve;n mải nhăn nh&oacute; đề nghị với đồng b&agrave;o, luồn ra sau lưng anh v&agrave; chạy t&oacute;t sang b&ecirc;n kia cầu. Anh chưa kịp qu&aacute;t gọi lại, n&oacute; đ&atilde; mất v&agrave;o d&atilde;y phố k&ecirc; đầu cầu b&ecirc;n kia.</span><br />\r\n<br />\r\n<span style=\"color:#000000\">Kh&ocirc;ng thể bỏ trống chỗ g&aacute;c để đuổii theo, anh chỉ c&ograve;n biết dậm ch&acirc;n dậm cẳng, l&agrave;u b&agrave;u bực tức:</span><br />\r\n<br />\r\n<span style=\"color:#000000\">-Con c&aacute;i nh&agrave; ai m&agrave; nghịch hơn quỉ!</span><br />\r\n<br />\r\n<span style=\"color:#000000\">C&oacute; mấy đứa b&aacute;n kẹo gừng, đậu phộng rang cũng lấp l&otilde;m định bắt chước ch&uacute; b&eacute; vừa rồi, nhưng anh l&iacute;nh g&aacute;c đ&atilde; nhanh tay t&oacute;m cổ &aacute;o l&ocirc;i lại hết.&nbsp;</span><span style=\"color:#000000\">Một thằng b&aacute;n kẹo gừng, mắt l&aacute;c thi&ecirc;n l&aacute;c địa, trề m&ocirc;i n&oacute;i với đứa bạn vừa bị t&oacute;m cổ l&ocirc;i lại:</span><br />\r\n<br />\r\n<span style=\"color:#000000\">-Tụi bay kh&ocirc;ng bắt chước nổi thằng ni m&ocirc;! Hắn lanh hơn chuột nhắt. Chơi trốn t&igrave;m, &ugrave; mọi, thả đỉa ba ba... với hắn chỉ c&oacute; thua. &ETH;ừng h&ograve;ng m&agrave; sờ được v&agrave;o ngừơi hắn.</span><br />\r\n<span style=\"color:#000000\">-Răng mi biết?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-Tao chơi m&atilde;i với hắn c&ograve;n lạ chi.</span><br />\r\n<br />\r\n<span style=\"color:#000000\">-Hắn khoe từ h&ocirc;m nổ s&uacute;ng, hắn đ&atilde; t&oacute;t được qua khu vực Mặt trận ba, bốn lần.- Ch&uacute; b&eacute; b&aacute;n đậu phụng rang n&oacute;i chen v&agrave;o</span><br />\r\n<br />\r\n<span style=\"color:#000000\">Hắn lọt qua b&ecirc;n đ&oacute; l&agrave;m chi?</span><br />\r\n<br />\r\n<span style=\"color:#000000\">-Coi chơi!</span><br />\r\n<br />\r\n<span style=\"color:#000000\">-Hắn c&oacute; kể chuyện chi hay kh&ocirc;ng?</span><br />\r\n<br />\r\n<span style=\"color:#000000\">-Khối! Nhưng th&uacute; nhất l&agrave; s&aacute;ng m&ocirc; hắn cũng gặp một to&aacute;n Vệ Quốc &ETH;o&agrave;n m&agrave; chỉ nhỏ bằng tụi ta th&ocirc;i, nhiều đứa c&ograve;n nhỏ hon. To&aacute;n ni chạy dọc theo bờ s&ocirc;ng, tập qu&acirc;n sự một hai, tập n&eacute;m lựu đạn, bắn s&uacute;ng...vui gh&ecirc; lắm. Hắn đứng coi m&agrave; th&egrave;m rệu cả nuớc miếng!</span><br />\r\n<br />\r\n<span style=\"color:#000000\">-Vệ Quốc &ETH;o&agrave;n m&agrave; chỉ nhỏ bằng tụi ta th&igrave; đ&aacute;nh t&acirc;y răng được? Hay l&agrave; hắn n&oacute;i l&aacute;o?</span><br />\r\n<br />\r\n<span style=\"color:#000000\">-Răng kh&ocirc;ng đ&aacute;nh được? - Thằng b&aacute;n đậu phụng rang v&ecirc;nh mặt l&ecirc;n n&oacute;i. - Ch&aacute;nh phủ cứ thử lấy tau v&ocirc; Vệ Quốc &ETH;o&agrave;n coi, c&oacute; chết cha tụi T&acirc;y kh&ocirc;ng?</span><br />\r\n<br />\r\n<span style=\"color:#000000\">Thằng b&aacute;n kẹo gừng mắt l&aacute;c cười ngạo:</span><br />\r\n<br />\r\n<span style=\"color:#000000\">-Lấy mi v&ocirc; Vệ Quốc &ETH;o&agrave;n th&igrave; th&agrave; lấy tau c&ograve;n hơn! Tau chẳng cần s&uacute;ng đạn g&igrave; hết - N&oacute; r&uacute;t trong t&uacute;i quần ra một c&aacute;i s&uacute;ng cao su, k&eacute;o căng hai sợi chun. - C&oacute; c&aacute;i ni, tau nhắm tr&uacute;ng mắt tụi T&acirc;y, tụii V&ecirc;-gi&ecirc;* tau nẻ. Cha con hắn tha hồ m&agrave; m&ugrave;.</span><br />\r\n<br />\r\n<span style=\"color:#000000\">* *</span><br />\r\n<br />\r\n<span style=\"color:#000000\">To&aacute;n &ldquo;Vệ Quốc &ETH;o&agrave;n con n&iacute;t&rdquo; l&uacute;c n&agrave;y đang nối nhau th&agrave;nh h&agrave;ng một, rầm rập l&ecirc;n ph&iacute;a cầu &ETH;&ocirc;ng Ba cũ.</span><br />\r\n<br />\r\n<span style=\"color:#000000\">Chạy dẫn đầu l&agrave; anh Vệ Quỗc Qu&acirc;n chỉ huy. Anh trạc hai ba, hai bốn tuổi, d&aacute;ng người tầm thước, vai rộng ngực nở, t&oacute;c b&uacute;i kiểu m&oacute;ng lừa, cổ đeo lủng lẳng chiếc c&ograve;i mạ kền s&aacute;ng lo&aacute;ng. S&aacute;ng h&ocirc;m đ&oacute; trời lạnh cắt ruột, anh chỉ mặc độc chiếc may &ocirc; trắng v&agrave; quần đ&ugrave;i xanh ống b&oacute; s&aacute;t đ&ugrave;i</span><br />\r\n<br />\r\n<span style=\"color:#000000\">C&ograve;n to&aacute;n trẻ ăn mặc tạp nham. Qu&aacute; nửa mặc &aacute;o quần Vệ Quốc &ETH;o&agrave;n sửa ngắn lại. Nhiều đứa bơi trong những chiếc &aacute;o trấn thủ rộng nhu &aacute;o thụng tế. C&oacute; đến hơn chục đứa nai nịt &ldquo;xanh-tuya-r&ocirc;ng&rdquo; da, c&oacute; kho&aacute; đồng to tướng v&agrave; đeo cả bao đạn. C&oacute; trời biết được trong những c&aacute;i bao đạn sứt sẹo, m&eacute;o m&oacute; ấy, ch&uacute;ng n&iacute;ch nh&eacute;t những g&igrave;!</span><br />\r\n<br />\r\n<span style=\"color:#000000\">D&atilde;y phố ven đường cửa đ&oacute;ng then c&agrave;i k&iacute;n m&iacute;t. Đồng b&agrave;o khu vực n&agrave;y đ&atilde; tản cư hết từ đ&ecirc;m đầu kh&aacute;ng chiến. H&agrave;ng chục con ch&oacute; lạc mất chủ, họp th&agrave;nh đ&agrave;n chạy loăng quăng tr&ecirc;n đường, tranh nhau ngửi bới c&aacute;c đống r&aacute;c kiếm ăn. Con n&agrave;o con ấy hai h&ocirc;ng l&eacute;p kẹp, xương sườn nh&ocirc; cao như vừa nuốt h&agrave;ng chục chiếc cạp rổ v&agrave;o bụng.</span><br />\r\n<br />\r\n<span style=\"color:#000000\">Khi to&aacute;n trẻ rầm rập chạy qua, lũ ch&oacute; sợ h&atilde;i, t&aacute;n toạn. Ch&uacute;ng chuồn hết v&agrave;o c&aacute;c hẻm phố, ch&otilde; m&otilde;m ra sủa v&aacute;ng. Bọn trẻ dậm ch&acirc;n, vỗ đ&ugrave;i, doạ lại lũ ch&oacute;.</span><br />\r\n<br />\r\n<span style=\"color:#000000\">Con đường ồn &agrave;o hẳn l&ecirc;n trong chốc l&aacute;t.</span></span></span></p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(28, 'Cây cam ngọt ngào của tôi', 'Văn học nước ngoài', 'JOSÉ MAURO DE VASCONCELOS ', 1968, 'cay-cam-ngot-cua-toi.jpg', 'Đã hoàn', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">&ldquo;Vị chua ch&aacute;t của c&aacute;i ngh&egrave;o h&ograve;a trộn với vị ngọt ng&agrave;o khi kh&aacute;m ph&aacute; ra những điều khiến cuộc đời n&agrave;y đ&aacute;ng số một t&aacute;c phẩm kinh điển của Brazil.&rdquo;&ndash; Booklist</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#000000; font-family:Tahoma,Geneva,sans-serif; font-size:16px; text-align:justify\">&ldquo;Một c&aacute;ch nh&igrave;n cuộc sống gần như ho&agrave;n chỉnh từ con mắt trẻ thơ&hellip; c&oacute; sức mạnh sưởi ấm v&agrave; l&agrave;m tan n&aacute;t c&otilde;i l&ograve;ng, d&ugrave; người đọc ở lứa tuổi n&agrave;o.&rdquo;&ndash; The National</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">H&atilde;y l&agrave;m quen với Zez&eacute;, cậu b&eacute; tinh nghịch si&ecirc;u hạng đồng thời cũng đ&aacute;ng y&ecirc;u bậc nhất, với ước mơ lớn l&ecirc;n trở th&agrave;nh nh&agrave; thơ cổ thắt nơ bướm. Chẳng phải ai cũng c&ocirc;ng nhận khoản &ldquo;đ&aacute;ng y&ecirc;u&rdquo; kia đ&acirc;u nh&eacute;. Bởi v&igrave;, ở c&aacute;i x&oacute;m ngoại &ocirc; ngh&egrave;o ấy, nỗi khắc khổ bủa v&acirc;y đ&atilde; che mờ mắt người ta trước tr&aacute;i tim thiện lương c&ugrave;ng tr&iacute; tưởng tượng tuyệt vời của cậu b&eacute; con năm tuổi.&nbsp;C&oacute; hề g&igrave; đ&acirc;u bao nhi&ecirc;u l&agrave; hắt hủi, đ&aacute;nh mắng, v&igrave; Zez&eacute; đ&atilde; c&oacute; một người bạn đặc biệt để tr&uacute;t nỗi l&ograve;ng: c&acirc;y cam ngọt nơi vườn sau. V&agrave; cả một người bạn nữa, bằng xương bằng thịt, một ng&agrave;y kia xuất hiện, cho cậu b&eacute; nhạy cảm kh&ocirc;n sớm biết thế n&agrave;o l&agrave; tr&igrave;u mến, thế n&agrave;o l&agrave; nỗi đau, v&agrave; m&atilde;i m&atilde;i thay đổi cuộc đời cậu.&nbsp;</span><span style=\"color:#000000\">Mở đầu bằng những thanh &acirc;m trong s&aacute;ng v&agrave; kết th&uacute;c lắng lại trong những nốt trầm ho&agrave;i niệm, C&acirc;y cam ngọt của t&ocirc;i khiến ta nhận ra vẻ đẹp thực sự của cuộc sống đến từ những điều giản dị như b&ocirc;ng hoa trắng của c&aacute;i c&acirc;y sau nh&agrave;, v&agrave; rằng cuộc đời thật khốn khổ nếu thiếu đi l&ograve;ng y&ecirc;u thương v&agrave; niềm trắc ẩn. Cuốn s&aacute;ch kinh điển n&agrave;y bởi thế kh&ocirc;ng ngừng khiến tr&aacute;i tim người đọc khắp thế giới thổn thức, kể từ khi ra mắt lần đầu năm 1968 tại Brazil.</span></span></span></p>\r\n', '<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#ff0000\">PHẦN 1:</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#ff0000\">Đ&Ocirc;I KHI, V&Agrave;O GI&Aacute;NG SINH, ĐỨA CON CỦA QUỶ RA ĐỜI</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><u><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Chương 1: KH&Aacute;M PH&Aacute; NHIỀU ĐIỀU</span></span></span></u></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Ch&uacute;ng t&ocirc;i đi xuống phố, tay trong tay, chẳng c&oacute; g&igrave; phải vội. Totoca đang dạy t&ocirc;i về cuộc sống. &agrave; điều đ&oacute; khiến t&ocirc;i thực sự hạnh ph&uacute;c, được anh trai nắm tay v&agrave; dạy nhiều điều. Nhưng l&agrave; dạy về những điều thuộc thế giới b&ecirc;n ngo&agrave;i. Bởi v&igrave; ở nh&agrave;, t&ocirc;i học hỏi bằng c&aacute;ch tự m&agrave;y m&ograve; kh&aacute;m ph&aacute; v&agrave; tự l&agrave;m, t&ocirc;i mắc lỗi nhiều v&agrave; v&igrave; mắc lỗi, t&ocirc;i thường bị ăn đ&ograve;n. Cho tới tận trước đ&acirc;y kh&ocirc;ng l&acirc;u, t&ocirc;i vẫn chưa bị ai đ&aacute;nh bao giờ. Nhưng rồi mọi người nghe được chuyện n&agrave;y, chuyện nọ v&agrave; bắt đầu n&oacute;i t&ocirc;i l&agrave; quỷ, l&agrave; qu&aacute;i vật, l&agrave; tiểu y&ecirc;u t&oacute;c hung. T&ocirc;i kh&ocirc;ng muốn biết về điều đ&oacute;. Nếu kh&ocirc;ng phải đang ở b&ecirc;n ngo&agrave;i th&igrave; t&ocirc;i đ&atilde; bắt đầu h&aacute;t. H&aacute;t th&uacute; vị lắm. Ngo&agrave;i ca h&aacute;t, Totoca c&ograve;n biết l&agrave;m điều kh&aacute;c nữa: anh c&oacute; thể hu&yacute;t s&aacute;o. Nhưng d&ugrave; cố gắng đến mức n&agrave;o t&ocirc;i cũng chẳng thể bắt chước anh hu&yacute;t s&aacute;o được, chẳng &acirc;m thanh n&agrave;o bật ra cả. Để an ủi t&ocirc;i, anh bảo chuyện đ&oacute; cũng b&igrave;nh thường th&ocirc;i, rằng t&ocirc;i chưa c&oacute; c&aacute;i miệng của người hu&yacute;t s&aacute;o. Nhưng v&igrave; kh&ocirc;ng thể h&aacute;t th&agrave;nh lời, t&ocirc;i h&aacute;t thầm ở trong l&ograve;ng. Thoạt đầu điều đ&oacute; thật kỳ cục, nhưng về sau t&ocirc;i cảm thấy h&aacute;t như thế thực sự th&uacute; vị. V&agrave; l&uacute;c n&agrave;y, t&ocirc;i đang nhớ lại một b&agrave;i h&aacute;t mẹ thường h&aacute;t khi t&ocirc;i c&ograve;n b&eacute; x&iacute;u. Mẹ đứng b&ecirc;n chậu giặt, mảnh khăn buộc quanh đầu để che nắng. Với chiếc tạp dề &ocirc;m quanh thắt lưng, mẹ đứng đ&oacute; hết giờ n&agrave;y đến giờ kh&aacute;c, dầm tay trong nước, khiến x&agrave; ph&ograve;ng nổi cơ man n&agrave;o l&agrave; bọt. Sau đ&oacute; mẹ vắt kiệt nước khỏi quần &aacute;o v&agrave; đem ra d&acirc;y, phơi l&ecirc;n, kẹp lại v&agrave; k&eacute;o chiếc d&acirc;y phơi l&ecirc;n cao. Mẹ l&agrave;m thế với tất cả quần &aacute;o. Mẹ giặt thu&ecirc; cho nh&agrave; b&aacute;c sĩ Faulhaber để kiếm th&ecirc;m ch&uacute;t &iacute;t gi&uacute;p trang trải chi ph&iacute; sinh hoạt. Mẹ cao, gầy, nhưng rất đẹp. Da mẹ r&aacute;m nắng, t&oacute;c thẳng v&agrave; đen. Khi mẹ kh&ocirc;ng cột t&oacute;c l&ecirc;n, t&oacute;c mẹ x&otilde;a đến tận thắt lưng. Nhưng tuyệt nhất l&agrave; khi mẹ h&aacute;t, v&agrave; t&ocirc;i hay quanh quẩn b&ecirc;n mẹ học lỏm. Ch&agrave;ng thủy thủ ơi, ch&agrave;ng thủy thủ ơi Ch&agrave;ng thủy thủ của nỗi buồn ơi V&igrave; ch&agrave;ng Ng&agrave;y mai em sẽ chết&hellip; Những con s&oacute;ng x&ocirc; bờ Quăng m&igrave;nh l&ecirc;n bờ c&aacute;t Ch&agrave;ng ra khơi rồi Ch&agrave;ng thủy thủ ơi&hellip; T&igrave;nh y&ecirc;u của ch&agrave;ng Chẳng được nổi một ng&agrave;y T&agrave;u nhổ neo rồi T&agrave;u ra khơi&hellip; Những con s&oacute;ng x&ocirc; bờ&hellip; B&agrave;i h&aacute;t đ&oacute; lu&ocirc;n khiến l&ograve;ng t&ocirc;i d&acirc;ng tr&agrave;o một nỗi buồn kh&oacute; hiểu. Totoca giật tay t&ocirc;i. T&ocirc;i bừng tỉnh.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;Sao thế Zez&eacute;?&rdquo;&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Kh&ocirc;ng sao ạ. Em đang h&aacute;t th&ocirc;i m&agrave;.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;H&aacute;t ư?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;V&acirc;ng.&rdquo;&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Vậy chắc anh điếc rồi.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Anh ấy kh&ocirc;ng biết con người ta c&oacute; thể h&aacute;t thầm ư? T&ocirc;i im lặng. Nếu anh kh&ocirc;ng biết th&igrave; t&ocirc;i cũng chẳng định dạy anh đ&acirc;u. Ch&uacute;ng t&ocirc;i đ&atilde; tới lề đường quốc lộ Rio-S&atilde;o Paulo. Tr&ecirc;n đường quốc lộ, c&oacute; đủ mọi loại xe. Xe tải, xe con, xe ngựa, xe đạp.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;N&agrave;y,Zez&eacute;, chuyện quan trọng đ&acirc;y n&agrave;y. Trước&nbsp; hết ch&uacute;ng ta phải quan s&aacute;t thật kỹ một chiều, sau đ&oacute; nh&igrave;n chiều kia. B&acirc;y giờ sang đường n&agrave;o.&rdquo; Ch&uacute;ng t&ocirc;i chạy sang đường.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Em c&oacute; sợ kh&ocirc;ng?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i sợ, nhưng t&ocirc;i lắc đầu.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Ch&uacute;ng m&igrave;nh c&ugrave;ng l&agrave;m lại n&agrave;o. Sau đ&oacute; anh muốn xem em đ&atilde; biết c&aacute;ch hay chưa.&rdquo; Ch&uacute;ng t&ocirc;i chạy trở lại ph&iacute;a b&ecirc;n kia đường.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;B&acirc;y giờ em sang đường đi. Đừng ngập ngừng, bởi v&igrave; em lớn rồi m&agrave;.&rdquo; Tim t&ocirc;i đập nhanh hơn.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;N&agrave;o. Sang đi.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i chạy sang đường, gần như n&iacute;n thở. T&ocirc;i đợi một ch&uacute;t v&agrave; anh ra hiệu cho t&ocirc;i quay trở về chỗ anh.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Lần đầu m&agrave; l&agrave;m được thế l&agrave; tốt đấy. Nhưng em đ&atilde; qu&ecirc;n một điều. Em phải nh&igrave;n cả hai ph&iacute;a xem c&oacute; xe đang chạy tới kh&ocirc;ng. Kh&ocirc;ng phải l&uacute;c n&agrave;o anh cũng c&oacute; mặt ở đ&acirc;y để ra hiệu cho em đ&acirc;u. Ch&uacute;ng ta sẽ thực h&agrave;nh th&ecirc;m tr&ecirc;n đường về nh&agrave; nh&eacute;. Nhưng giờ th&igrave; đi th&ocirc;i, v&igrave; anh muốn chỉ cho em một thứ.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Anh cầm tay t&ocirc;i v&agrave; ch&uacute;ng t&ocirc;i lại bắt đầu bước đi, thong thả. T&ocirc;i kh&ocirc;ng c&aacute;ch n&agrave;o th&ocirc;i nghĩ về một cuộc tr&ograve; chuyện c&aacute;ch đ&acirc;y &iacute;t l&acirc;u.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&quot;Totoca.&quot;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;G&igrave; cơ?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Anh đ&atilde; cảm nhận được tuổi ch&iacute;n chắn chưa?&rdquo;&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Thứ vớ vẩn g&igrave; vậy?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;B&aacute;c Edmundo n&oacute;i đấy. B&aacute;c n&oacute;i em kh&ocirc;n trước tuổi v&agrave; rằng chẳng bao l&acirc;u nữa em sẽ đến tuổi ch&iacute;n chắn. Nhưng em chẳng cảm thấy c&oacute; g&igrave; kh&aacute;c cả.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;B&aacute;c Edmundo bị ngớ ngẩn đấy. B&aacute;c ấy lu&ocirc;n nhồi những thứ vớ vẩn v&agrave;o đầu em.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;B&aacute;c ấy kh&ocirc;ng ngớ ngẩn đ&acirc;u. B&aacute;c ấy kh&ocirc;n ngoan đấy chứ. V&agrave; khi lớn l&ecirc;n em muốn trở n&ecirc;n h&ocirc;n ngoan, th&agrave;nh nh&agrave; thơ v&agrave; thắt nơ bướm. Một ng&agrave;y n&agrave;o đ&oacute; em sẽ được chụp ảnh ch&acirc;n dung thắt nơ hẳn hoi.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Tại sao lại thắt nơ?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Bởi v&igrave; anh kh&ocirc;ng thể trở th&agrave;nh nh&agrave; thơ nếu kh&ocirc;ng thắt nơ. Khi b&aacute;c Edmundo cho em xem ảnh c&aacute;c nh&agrave; thơ trong cuốn tạp ch&iacute; đ&oacute;, em thấy ai cũng thắt nơ cả.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Zez&eacute;, em phải th&ocirc;i đi, đừng tin mọi điều b&aacute;c ấy n&oacute;i với em nữa. B&aacute;c Edmundo hơi bi&ecirc;ng bi&ecirc;ng đấy. B&aacute;c ấy c&ograve;n hơi xạo nữa.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;B&aacute;c ấy l&agrave; đồ ch&oacute; đẻ ư?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Em đ&atilde; bị t&aacute;t lệch mặt v&igrave; n&oacute;i bậy qu&aacute; nhiều m&agrave; chưa chừa &agrave;! B&aacute;c Edmundo kh&ocirc;ng như vậy. Anh n&oacute;i bi&ecirc;ng biệng cơ m&agrave;. Nghĩa l&agrave; hơi h&acirc;m h&acirc;m.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Anh bảo b&aacute;c ấy l&agrave; kẻ n&oacute;i xạo m&agrave;.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Đ&oacute; l&agrave; hai chuyện ho&agrave;n to&agrave;n kh&aacute;c.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Kh&ocirc;ng, c&oacute; kh&aacute;c g&igrave; đ&acirc;u. H&ocirc;m nọ cha kể về &ocirc;ng Labonne với b&aacute;c Severino, bạn chơi b&agrave;i của cha&rdquo;, v&agrave; cha n&oacute;i&nbsp;&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">&nbsp;-&ldquo;L&atilde;o ch&oacute; đẻ ấy l&agrave; một kẻ n&oacute;i xạo trời đ&aacute;nh th&aacute;nh vật. V&agrave; chẳng ai t&aacute;t chả lệch mặt cả.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Người lớn n&oacute;i thế th&igrave; kh&ocirc;ng sao.&rdquo;&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Ch&uacute;ng t&ocirc;i kh&ocirc;ng n&oacute;i g&igrave; trong v&agrave;i ph&uacute;t.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;B&aacute;c Edmundo kh&ocirc;ng... M&agrave; bi&ecirc;ng bi&ecirc;ng c&oacute; nghĩa l&agrave; g&igrave;, anh Totoca?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Anh giơ ng&oacute;n tay chỉ l&ecirc;n đầu, quay quay ng&oacute;n tay vẽ th&agrave;nh những v&ograve;ng tr&ograve;n trong kh&ocirc;ng kh&iacute;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Kh&ocirc;ng, b&aacute;c ấy kh&ocirc;ng phải vậy. B&aacute;c ấy tốt lắm. B&aacute;c ấy dạy em nhiều điều, v&agrave; chỉ đ&aacute;nh em c&oacute; mỗi một lần, lại c&ograve;n chẳng đ&aacute;nh mạnh nữa.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Totoca giật m&igrave;nh.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;B&aacute;c ấy đ&aacute;nh em &agrave;? Khi n&agrave;o?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Khi em hư thật v&agrave; bị Gloria tống đến nh&agrave; b&agrave;. B&aacute;c ấy muốn đọc b&aacute;o nhưng kh&ocirc;ng t&igrave;m thấy k&iacute;nh. B&aacute;c t&igrave;m hết chỗ cao đến chỗ thấp v&agrave; nổi đi&ecirc;n thực sự. B&aacute;c hỏi b&agrave; k&iacute;nh của b&aacute;c ở đ&acirc;u nhưng b&agrave;&nbsp; kh&ocirc;ng biết. Hai người lục tung cả nh&agrave; l&ecirc;n để t&igrave;m. Thế rồi em n&oacute;i em biết k&iacute;nh của b&aacute;c ở đ&acirc;u v&agrave; nếu b&aacute;c cho &ldquo;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Mang k&iacute;nh ra đ&acirc;y th&igrave; b&aacute;c đưa tiền cho m&agrave;y.&rdquo;&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Em đến chỗ rương đựng quần &aacute;o lấy c&aacute;i k&iacute;nh ra. B&aacute;c n&oacute;i,&rdquo; Ra l&agrave; m&agrave;y, đồ ranh con!&rdquo; B&aacute;c ph&aacute;t v&agrave;o lưng em v&agrave; cất b&eacute;ng tiền đi.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Totoca cười ph&aacute; l&ecirc;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Em đến đ&oacute; để khỏi bị ăn đ&ograve;n ở nh&agrave; thế m&agrave; lại bị đ&aacute;nh ở đ&oacute;. Đi nhanh ch&acirc;n l&ecirc;n một ch&uacute;t, nếu kh&ocirc;ng ch&uacute;ng ta sẽ chẳng bao giờ đến nơi được đ&acirc;u.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i vẫn đang nghĩ về b&aacute;c Edmundo.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Totoca, trẻ con nghỉ hưu phải kh&ocirc;ng?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;G&igrave; cơ?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;B&aacute;c Edmundo chẳng l&agrave;m g&igrave; cả m&agrave; vẫn c&oacute; tiền. B&aacute;c kh&ocirc;ng l&agrave;m việc, nhưng T&ograve;a Thị ch&iacute;nh vẫn trả tiền cho b&aacute;c hằng th&aacute;ng.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Th&igrave; sao?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;&Agrave; th&igrave;, trẻ con chẳng l&agrave;m g&igrave;. Trẻ con ăn, ngủ v&agrave; được cha mẹ cho tiền.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Nghỉ hưu kh&aacute;c chứ, Zez&eacute;. Người nghỉ hưu đ&atilde; l&agrave;m việc trong một thời gian d&agrave;i rồi, t&oacute;c đ&atilde; ngả bạc, ch&acirc;n chậm mắt mờ như b&aacute;c Edmundo rồi. Nhưng đừng nghĩ về những thứ kh&oacute; nhằn ấy nữa. Nếu em muốn học nhiều điều từ b&aacute;c ấy th&igrave; tốt th&ocirc;i. Nhưng khi đi với anh th&igrave; kh&ocirc;ng nh&eacute;. H&atilde;y cư xử như những đứa con trai kh&aacute;c. Em thậm ch&iacute; c&oacute; thể chửi thề, nhưng đừng nh&eacute;t đầy đầu những thứ kh&oacute; nhằn. Nếu kh&ocirc;ng anh sẽ kh&ocirc;ng đi chơi với em nữa đ&acirc;u.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i xị mặt xuống v&agrave; kh&ocirc;ng muốn n&oacute;i chuyện nữa. T&ocirc;i cũng chẳng muốn h&aacute;t. Ch&uacute; chim nhỏ ca h&aacute;t b&ecirc;n trong t&ocirc;i đ&atilde; bay mất rồi. Ch&uacute;ng t&ocirc;i dừng lại v&agrave; Totoca chỉ tay về ph&iacute;a ng&ocirc;i nh&agrave;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;N&oacute; đấy. Th&iacute;ch kh&ocirc;ng?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Đ&oacute; l&agrave; một ng&ocirc;i nh&agrave; b&igrave;nh thường. M&agrave;u trắng với những &ocirc; cửa sổ m&agrave;u xanh dương. To&agrave;n bộ cửa nẻo đều đ&oacute;ng im ỉm v&agrave; im ắng như tờ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;C&oacute; ạ. Nhưng tại sao ch&uacute;ng ta phải chuyển đến đ&acirc;y chứ?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Thay đổi cũng tốt m&agrave;.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Ch&uacute;ng t&ocirc;i đứng tr&ocirc;ng qua h&agrave;ng r&agrave;o, nh&igrave;n c&acirc;y xo&agrave;i ph&iacute;a b&ecirc;n n&agrave;y v&agrave; c&acirc;y me ở</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">ph&iacute;a b&ecirc;n kia.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Em l&agrave; đứa hay chạy lăng xăng, nhưng em kh&ocirc;ng biết nh&agrave; m&igrave;nh đang c&oacute; chuyện g&igrave; đ&acirc;u. Cha thất nghiệp rồi, đ&uacute;ng kh&ocirc;ng? Đ&atilde; s&aacute;u th&aacute;ng kể từ khi cha choảng nhau với &ocirc;ng Scottfield v&agrave; bị đuổi việc. Em c&oacute; biết b&acirc;y giờ Lala đang l&agrave;m việc ở nh&agrave; m&aacute;y kh&ocirc;ng? C&ograve;n mẹ th&igrave; sắp đi l&agrave;m ở th&agrave;nh phố, trong xưởng English Mill đấy, biết kh&ocirc;ng? Giờ th&igrave; em biết rồi đấy, đồ ngốc ạ. Ở c&aacute;i nh&agrave; mới n&agrave;y, ta sẽ tiết kiệm được tiền thu&ecirc; nh&agrave;... C&aacute;i nh&agrave; kia th&igrave; cha nợ t&aacute;m th&aacute;ng tiền thu&ecirc; nh&agrave; rồi. Em&nbsp; c&ograve;n qu&aacute; nhỏ n&ecirc;n kh&ocirc;ng phải lo lắng về những chuyện buồn như thế. Nhưng anh sẽ phải gi&uacute;p thật lực, xắn tay v&agrave;o hỗ trợ việc nh&agrave;.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Anh đứng im lặng một l&aacute;t.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Totoca, họ sẽ mang con b&aacute;o đen v&agrave; hai con sư tử c&aacute;i tới đ&acirc;y chứ?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Dĩ nhi&ecirc;n. V&agrave; l&atilde;o gia nh&acirc;n n&agrave;y sẽ phải th&aacute;o dỡ c&aacute;i chuồng g&agrave;.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Anh nh&igrave;n t&ocirc;i bằng &aacute;nh mắt thương x&oacute;t, tr&igrave;u mến.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Anh ch&iacute;nh l&agrave; người sẽ dỡ vườn th&uacute; v&agrave; lắp gh&eacute;p n&oacute; lại ở đ&acirc;y.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i thở ph&agrave;o. Bởi v&igrave; nếu kh&ocirc;ng t&ocirc;i sẽ phải kiếm thứ g&igrave; đ&oacute; mới mẻ để chơi với em trai &uacute;t Luis của t&ocirc;i.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Em đ&atilde; thấy anh l&agrave; người bạn như thế n&agrave;o của em rồi, đ&uacute;ng kh&ocirc;ng Zez&eacute;? N&agrave;y, em sẽ chẳng mất m&aacute;t g&igrave; nếu n&oacute;i cho anh biết em đ&atilde; l&agrave;m chuyện đ&oacute; như thế n&agrave;o...&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Totoca, em thề l&agrave; em kh&ocirc;ng biết. Em thực sự kh&ocirc;ng biết.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Em n&oacute;i dối. Ai đ&oacute; đ&atilde; dạy em học.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Em chẳng học g&igrave; cả. Chẳng ai dạy em hết. Trừ phi quỷ sứ dạy em trong giấc ngủ. Jandira n&oacute;i quỷ sứ l&agrave; cha đỡ đầu của em.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Totoca bối rối. Anh thậm ch&iacute; c&ograve;n cốc đầu t&ocirc;i mấy c&aacute;i, cố bắt t&ocirc;i phải n&oacute;i cho anh biết. Nhưng t&ocirc;i kh&ocirc;ng biết m&igrave;nh đ&atilde; l&agrave;m được chuyện đ&oacute; như thế n&agrave;o.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Chẳng ai tự học c&aacute;i đ&oacute; được.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Nhưng anh kh&ocirc;ng biết phải n&oacute;i g&igrave; bởi v&igrave; chẳng ai thực sự nh&igrave;n thấy người n&agrave;o dạy t&ocirc;i học g&igrave; hết. Đ&oacute; l&agrave; một b&iacute; ẩn. T&ocirc;i nhớ lại chuyện xảy ra một tuần trước. N&oacute; đ&atilde; khiến cả nh&agrave; t&ocirc;i x&ocirc;n xao. Chuyện bắt đầu tại nh&agrave; b&agrave; t&ocirc;i, khi t&ocirc;i ngồi cạnh b&aacute;c Edmundo đang đọc b&aacute;o.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;B&aacute;c ơi.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;G&igrave; vậy, con trai?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">B&aacute;c k&eacute;o cặp k&iacute;nh xuống ch&oacute;p mũi, như c&aacute;i c&aacute;ch mọi người lớn thường l&agrave;m khi họ đ&atilde; gi&agrave;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Mấy tuổi b&aacute;c học đọc? Khoảng tầm s&aacute;u, bảy tuổi.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Trẻ năm tuổi c&oacute; thể học đọc kh&ocirc;ng b&aacute;c?&rdquo;&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;B&aacute;c nghĩ l&agrave; c&oacute;. Nhưng chẳng ai th&iacute;ch dạy ch&uacute;ng đ&acirc;u bởi v&igrave; ch&uacute;ng c&ograve;n qu&aacute; nhỏ.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;B&aacute;c học đọc như thế n&agrave;o ạ?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Như mọi người kh&aacute;c th&ocirc;i, với những người đọc mẫu. Đọc bờ a bao.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Ai cũng phải học đọc theo c&aacute;ch đ&oacute; ạ?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Theo b&aacute;c biết th&igrave; ai cũng học như thế cả.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Tất cả mọi người ư?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">B&aacute;c nh&igrave;n t&ocirc;i, ngạc nhi&ecirc;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;N&agrave;y,Zez&eacute;, đ&oacute; l&agrave; c&aacute;ch mọi người học đọc. B&acirc;y giờ h&atilde;y để y&ecirc;n cho b&aacute;c đọc nốt. Ra s&acirc;n sau m&agrave; t&igrave;m ổi đi.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">B&aacute;c đẩy k&iacute;nh l&ecirc;n v&agrave; cố tập trung đọc. Nhưng t&ocirc;i kh&ocirc;ng chịu đi.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Tiếc qu&aacute;!&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Nghe thấy tiếng cảm th&aacute;n ch&acirc;n thật ấy, b&aacute;c lại k&eacute;o k&iacute;nh xuống thấp.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Trời đất quỷ thần ơi. Ch&aacute;u định ngồi dai như đỉa đ&acirc;y đấy &agrave;?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Chỉ l&agrave; ch&aacute;u đ&atilde; đi cả một qu&atilde;ng đường xa như thế tới đ&acirc;y để n&oacute;i với b&aacute;c một điều.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Th&ocirc;i được rồi, vậy th&igrave; n&oacute;i đi.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Kh&ocirc;ng. Kh&ocirc;ng phải vậy. Trước hết ch&aacute;u cần biết ng&agrave;y lĩnh lương hưu tới đ&acirc;y của b&aacute;c l&agrave; ng&agrave;y n&agrave;o.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Ng&agrave;y kia, b&aacute;c n&oacute;i, vừa nh&igrave;n t&ocirc;i vẻ d&ograve; x&eacute;t vừa cười tủm tỉm.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Ng&agrave;y kia l&agrave; thứ mấy hả b&aacute;c?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Thứ S&aacute;u.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Ừm, thứ S&aacute;u b&aacute;c c&oacute; thể mang từ th&agrave;nh phố về cho ch&aacute;u một con Vua Bạc kh&ocirc;ng ạ?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;N&oacute;i từ từ xem n&agrave;o,Zez&eacute;. Vua Bạc l&agrave; g&igrave;?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;L&agrave; con ngựa trắng nhỏ ch&aacute;u nh&igrave;n thấy trong rạp chiếu b&oacute;ng. Chủ của n&oacute; l&agrave; Fred Thompson. N&oacute; l&agrave; ngựa đ&atilde; được thuần dưỡng rồi.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Ch&aacute;u muốn b&aacute;c mang về cho ch&aacute;u một con ngựa nhỏ k&eacute;o xe ư?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Kh&ocirc;ng ạ. Ch&aacute;u muốn con ngựa kiểu c&oacute; đầu gỗ v&agrave; bộ d&acirc;y cương cơ. Ngựa kiểu b&aacute;c gắn c&aacute;i đu&ocirc;i v&agrave;o rồi chạy v&ograve;ng quanh ấy ạ. Ch&aacute;u cần phải luyện tập v&igrave; sau n&agrave;y ch&aacute;u sẽ đ&oacute;ng phim.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">B&aacute;c cười ph&aacute; l&ecirc;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;B&aacute;c hiểu rồi. V&agrave; nếu b&aacute;c kiếm n&oacute; cho ch&aacute;u, th&igrave; b&aacute;c đượC g&igrave; n&agrave;o?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Ch&aacute;u sẽ l&agrave;m một việc g&igrave; đ&oacute; cho b&aacute;c ạ.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;Ch&aacute;u thơm b&aacute;c một c&aacute;i nh&eacute;?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;Ch&aacute;u kh&ocirc;ng th&iacute;ch thơm thiếc lắm đ&acirc;u ạ.&rdquo; &ldquo;Vậy th&igrave; &ocirc;m nh&eacute;?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i nh&igrave;n b&aacute;c Edmundo v&agrave; thật l&ograve;ng cảm thấy tội nghiệp cho b&aacute;c. Con chim nhỏ b&ecirc;n trong t&ocirc;i n&oacute;i một điều g&igrave; đ&oacute;. V&agrave; t&ocirc;i nhớ lại một chuyện t&ocirc;i đ&atilde; nghe người ta n&oacute;i rất nhiều lần, rằng b&aacute;c&nbsp; Edmundo sống ly th&acirc;n với vợ v&agrave; họ c&oacute; năm đứa con. Nhưng b&aacute;c sống một m&igrave;nh, đi lại chậm chạp lắm... C&oacute; lẽ b&aacute;c đi chậm v&igrave; nhớ c&aacute;c con chăng? Con b&aacute;c chẳng bao giờ đến thăm b&aacute;c. T&ocirc;i đi v&ograve;ng qua b&agrave;n v&agrave; &ocirc;m b&aacute;c thật chặt. T&ocirc;i cảm thấy đ&aacute;m t&oacute;c bạc của b&aacute;c cọ v&agrave;o tr&aacute;n m&igrave;nh. T&oacute;c b&aacute;c mềm thật đấy.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Đ&acirc;y kh&ocirc;ng phải v&igrave; con ngựa đ&acirc;u ạ. Ch&aacute;u sẽ l&agrave;m cho b&aacute;c một việc kh&aacute;c Cơ. Ch&aacute;u sẽ đọc.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;G&igrave; cơ,Zez&eacute;? Ch&aacute;u biết đọc ư? Ai dạy ch&aacute;u?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;Chẳng ai cả.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Ch&aacute;u n&oacute;i dối.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i đi ra, v&agrave; n&oacute;i vọng v&agrave;o từ ngo&agrave;i cửa</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Thứ S&aacute;u b&aacute;c h&atilde;y mang ngựa về cho ch&aacute;u v&agrave; b&aacute;c sẽ thấy ch&aacute;u c&oacute; biết đọc hay kh&ocirc;ng?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Sau đ&oacute;, khi đ&ecirc;m xuống v&agrave; chị Jandira đ&atilde; thắp đ&egrave;n dầu l&ecirc;n bởi v&igrave; c&ocirc;ng ty điện đ&atilde; cắt điện nh&agrave; ch&uacute;ng t&ocirc;i do chưa thanh to&aacute;n h&oacute;a đơn, t&ocirc;i đứng nh&oacute;n ch&acirc;n ngắm &ldquo;ng&ocirc;i sao&rdquo; ấy. Đ&oacute; l&agrave; bức tranh vẽ tr&ecirc;n một mảnh giấy, h&igrave;nh một ng&ocirc;i sao với lời nguyện cầu ở b&ecirc;n dưới mong ng&ocirc;i sao ph&ugrave; hộ cho nh&agrave; ch&uacute;ng t&ocirc;i.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Jandira, chị nhấc em l&ecirc;n được kh&ocirc;ng? Em sẽ đọc những chữ kia.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;N&oacute;i ph&eacute;t đủ rồi đấy,Zez&eacute;. Chị đang bận.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Cứ nhấc em l&ecirc;n một t&iacute; th&ocirc;i rồi em sẽ cho chị xem. &ldquo;N&agrave;y,Zez&eacute;, nếu định giở tr&ograve; g&igrave; th&igrave; m&agrave;y liệu hồn.&rdquo; Chị nhấc t&ocirc;i l&ecirc;n ph&iacute;a sau c&aacute;nh cửa.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;N&agrave;o, đọc đi. Chị đang muốn xem đ&acirc;y.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Vậy l&agrave; t&ocirc;i đọc, đọc thật. T&ocirc;i đọc c&acirc;u cầu nguyện, cầu Ch&uacute;a ph&ugrave; hộ cho gia đ&igrave;nh ch&uacute;ng t&ocirc;i v&agrave; bảo vệ ch&uacute;ng t&ocirc;i khỏi những linh hồn hiểm &aacute;c. Jandira đặt t&ocirc;i xuống. Miệng chị h&aacute; hốc.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Zez&eacute;, m&agrave;y đ&atilde; thuộc l&ograve;ng c&acirc;u đ&oacute; phải kh&ocirc;ng? M&agrave;y lừa chị phải kh&ocirc;ng?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Jandira, em thề. Em c&oacute; thể đọc mọi thứ.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Chẳng ai kh&ocirc;ng học m&agrave; lại biết đọc cả. L&agrave; b&aacute;c Edmundo dạy m&agrave;y phải kh&ocirc;ng? Hay b&agrave;?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Chẳng ai cả.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Chị chạy đi lấy một tờ b&aacute;o v&agrave; t&ocirc;i đọc kh&ocirc;ng sai chữ n&agrave;o. Chị k&ecirc;u r&eacute; l&ecirc;n v&agrave; gọi Gl&oacute;ria. Gloria trở n&ecirc;n lo lắng v&agrave; đi gọi Ala&iacute;de. Trong v&ograve;ng mười ph&uacute;t, h&agrave;ng x&oacute;m của ch&uacute;ng t&ocirc;i đ&atilde; x&uacute;m lại để xem chuyện lạ. Đ&oacute; ch&iacute;nh l&agrave; chuyện Totoca muốn t&ocirc;i n&oacute;i cho anh biết.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;B&aacute;c ấy đ&atilde; dạy em v&agrave; hứa cho em con ngựa nếu em học được.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Kh&ocirc;ng phải vậy.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Anh sẽ hỏi b&aacute;c ấy.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Anh đi m&agrave; hỏi. Em kh&ocirc;ng biết giải th&iacute;ch chuyện đ&oacute; như thế n&agrave;o, Totoca ạ. Nếu biết, em đ&atilde; n&oacute;i với anh.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Vậy th&igrave; đi th&ocirc;i. Rồi m&agrave;y sẽ thấy. Khi m&agrave;y cần g&igrave; đ&oacute;...&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Anh giận dữ chộp tay t&ocirc;i v&agrave; bắt đầu l&ocirc;i t&ocirc;i về nh&agrave;. Rồi anh nghĩ ra một c&aacute;ch để trả th&ugrave;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Đ&aacute;ng đời m&agrave;y chưa! M&agrave;y học qu&aacute; sớm, đồ ngốc ạ. Giờ th&igrave; m&agrave;y sẽ phải bắt đầu đến trường v&agrave;o th&aacute;ng Hai.&rdquo;&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Đ&oacute; l&agrave; &yacute; tưởng của Jandira. Nhờ thế nh&agrave; t&ocirc;i sẽ y&ecirc;n b&igrave;nh suốt cả buổi s&aacute;ng, c&ograve;n t&ocirc;i th&igrave; sẽ biết thế n&agrave;o l&agrave; lễ độ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Lại học c&aacute;ch sang đường đi. Đừng tưởng khi m&agrave;y đi học th&igrave; anh sẽ l&agrave; v&uacute; em của m&agrave;y, lần n&agrave;o cũng đưa m&agrave;y sang đường nh&eacute;. Nếu m&agrave;y th&ocirc;ng minh như vậy, th&igrave; m&agrave;y cũng c&oacute; thể học đượC việc n&agrave;y.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Đ&acirc;y, ngựa đ&acirc;y. Giờ th&igrave; đọc c&aacute;i n&agrave;y xem n&agrave;o.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">B&aacute;c mở tờ b&aacute;o ra v&agrave; chỉ cho t&ocirc;i một c&acirc;u trong một mẩu quảng c&aacute;o thuốc chữa bệnh.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Thuốc c&oacute; trong tất cả c&aacute;c hiệu thuốc v&agrave; c&aacute;c cửa h&agrave;ng dượC uy t&iacute;n,&rdquo; t&ocirc;i đọc.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">B&aacute;c Edmundo chạy ra gọi b&agrave; từ s&acirc;n sau v&agrave;o.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Mẹ ơi. N&oacute; thậm ch&iacute; đọc đ&uacute;ng cả từ hiệu thuốc n&agrave;y.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Cả hai người bắt đầu đố t&ocirc;i đọc những c&acirc;u kh&aacute;c v&agrave; t&ocirc;i đọc được hết. B&agrave; bắt đầu lẩm bẩm g&igrave; đ&oacute; t&ocirc;i nghe kh&ocirc;ng hiểu. B&aacute;c Edmundo đưa cho t&ocirc;i con ngựa v&agrave; t&ocirc;i lại &ocirc;m b&aacute;c. Sau đ&oacute; b&aacute;c n&acirc;ng cằm t&ocirc;i v&agrave; n&oacute;i bằng giọng ngập ngừng</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;Ch&aacute;u rồi sẽ tiến xa, khỉ con ạ. Kh&ocirc;ng phải ngẫu nhi&ecirc;n t&ecirc;n ch&aacute;u lại l&agrave; Jos&eacute;. Ch&aacute;u sẽ l&agrave; mặt trời v&agrave; c&aacute;c v&igrave; sao sẽ s&aacute;ng lấp l&aacute;nh quanh ch&aacute;u.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i kh&ocirc;ng hiểu b&aacute;c n&oacute;i g&igrave; v&agrave; tự hỏi liệu c&oacute; đ&uacute;ng l&agrave; b&aacute;c hơi bi&ecirc;ng biệng hay kh&ocirc;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Ch&aacute;u kh&ocirc;ng hiểu được chuyện đ&oacute; đ&acirc;u. Đ&oacute; l&agrave; c&acirc;u chuyện về Joseph1. Khi n&agrave;o ch&aacute;u lớn hơn một ch&uacute;t, b&aacute;c sẽ kể cho ch&aacute;u nghe.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i m&ecirc; mẩn c&aacute;c c&acirc;u chuyện. Chuyện c&agrave;ng kh&oacute; hiểu th&igrave; t&ocirc;i c&agrave;ng th&iacute;ch. T&ocirc;i vỗ vỗ con ngựa nhỏ của m&igrave;nh một l&uacute;c l&acirc;u rồi ngước l&ecirc;n nh&igrave;n b&aacute;c Edmundo, n&oacute;i</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;B&aacute;c c&oacute; nghĩ tuần tới ch&aacute;u sẽ lớn hơn một ch&uacute;t kh&ocirc;ng, b&aacute;c?&rdquo;</span></span></span></p>\r\n\r\n<p><u><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Chương 2:&nbsp;</span></span><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">MỘT C&Acirc;Y CAM NGỌT</span></span></u></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Trong gia đ&igrave;nh t&ocirc;i, mỗi đứa lớn đều phải trong một đứa em. Jandira tr&ocirc;ng Gloria v&agrave; một người em g&aacute;i kh&aacute;c - đ&atilde; bị đưa đến miền Bắc l&agrave;m con nu&ocirc;i để được nu&ocirc;i nấng cho tử tế. Totoca l&agrave; đứa em cưng của Jandira. Lala l&agrave; người tr&ocirc;ng nom t&ocirc;i, m&atilde;i cho đến dạo gần đ&acirc;y. Sau đ&oacute; th&igrave; chị hết th&iacute;ch t&ocirc;i. T&ocirc;i nghĩ chị đ&atilde; ch&aacute;n ph&aacute;t ớn t&ocirc;i rồi hoặc đ&atilde; y&ecirc;u m&ecirc; mệt bạn trai chị, một anh ch&agrave;ng bảnh bao diện quần thụng &aacute;o kho&aacute;c lửng giống như ca sĩ. C&aacute;i thời anh chị c&ograve;n thường đưa t&ocirc;i đi dạo (bạn trai chị gọi l&agrave; &ldquo;tản bộ&rdquo;) v&agrave;o c&aacute;c ng&agrave;y Chủ nhật, anh thường mua cho t&ocirc;i một &iacute;t kẹo thật ngon để t&ocirc;i kh&ocirc;ng kể cho ai biết chuyện của hai người. T&ocirc;i thậm ch&iacute; kh&ocirc;ng thể hỏi b&aacute;c Edmundo xem &ldquo;tản bộ&rdquo; c&oacute; nghĩa l&agrave; g&igrave;, nếu kh&ocirc;ng cả nh&agrave; sẽ ph&aacute;t hiện ra. Hai người anh chị nữa của t&ocirc;i bị chết yểu v&agrave; t&ocirc;i chỉ nghe n&oacute;i về họ m&agrave; th&ocirc;i. Mọi người kể anh chị l&agrave; hai em b&eacute; người da đỏ thuộc bộ tộc Apinaj&eacute;, da đen lắm, c&oacute; m&aacute;i t&oacute;c đen thẳng. Bởi vậy n&ecirc;n anh chị mới được đặt cho những c&aacute;i t&ecirc;n của người da đỏ. Chị t&ecirc;n l&agrave; Aracy, c&ograve;n anh l&agrave; Jurandyr. Sau đ&oacute; em trai Lu&iacute;s của t&ocirc;i ra đời. Gloria l&agrave; người tr&ocirc;ng nom n&oacute; nhiều nhất, sau đ&oacute; đến t&ocirc;i. Thằng b&eacute; thậm ch&iacute; kh&ocirc;ng cần ai phải tr&ocirc;ng, bởi v&igrave; tr&ecirc;n đời n&agrave;y chẳng c&oacute; b&eacute; trai n&agrave;o ngoan ngo&atilde;n, y&ecirc;n lặng v&agrave; dễ thương hơn n&oacute;. Ch&iacute;nh v&igrave; thế, khi n&oacute; n&oacute;i bằng giọng nhỏ nhẹ, s&otilde;i ơi l&agrave; s&otilde;i, đ&uacute;ng l&uacute;c t&ocirc;i đang bước ch&acirc;n ra đường, t&ocirc;i thay đổi &yacute; định ngay tức th&igrave;.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Zez&eacute; ơi, anh đưa em đi vườn th&uacute; nh&eacute;? H&ocirc;m nay c&oacute; vẻ trời sẽ kh&ocirc;ng mua đ&acirc;u, anh nhỉ?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Cưng qu&aacute; đi mất. N&oacute; n&oacute;i s&otilde;i qu&aacute;. Thằng b&eacute; đ&oacute; sau n&agrave;y kh&ocirc;ng phải của vừa đ&acirc;u; thể n&agrave;o n&oacute; cũng sẽ tiến xa cho xem. T&ocirc;i ngắm nh&igrave;n ng&agrave;y đẹp trời, nh&igrave;n bầu trời xanh thăm thẳm, chẳng nỡ l&ograve;ng n&agrave;o n&oacute;i dối. Bởi v&igrave; đ&ocirc;i khi, nếu đang kh&ocirc;ng vui, t&ocirc;i sẽ n&oacute;i</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;Lu&iacute;s, em đi&ecirc;n &agrave;. Nh&igrave;n xem, cơn d&ocirc;ng sắp k&eacute;o đến kia k&igrave;a!&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Lần n&agrave;y t&ocirc;i cầm b&agrave;n tay nhỏ b&eacute; của n&oacute; v&agrave; ch&uacute;ng t&ocirc;i bước ra s&acirc;n sau bắt đầu cuộc phi&ecirc;u lưu. S&acirc;n sau được chia th&agrave;nh ba khu vui chơi. Một l&agrave; vườn th&uacute;. Một khu l&agrave; ch&acirc;u &Acirc;u, kết th&uacute;c chỗ h&agrave;ng r&agrave;o nhỏ ngay ngắn của Julio. Tại sao lại l&agrave; ch&acirc;u &Acirc;u ư? Ngay cả ch&uacute; chim nhỏ trong t&ocirc;i cũng kh&ocirc;ng biết. Ch&uacute;ng t&ocirc;i chơi tr&ograve; đi c&aacute;p treo l&ecirc;n n&uacute;i Sugarloaf. Ch&uacute;ng t&ocirc;i mang c&aacute;i hộp đựng c&uacute;c &aacute;o ra v&agrave; x&acirc;u hết c&uacute;c lại bằng một sợi d&acirc;y. (B&aacute;c Edmundo gọi thứ d&acirc;y đ&oacute; l&agrave; thừng. T&ocirc;i cứ nghĩ &ldquo;thừng&rdquo; l&agrave; đứa con trai, nhưng b&aacute;c giải th&iacute;ch con trai l&agrave; &ldquo;thằng&rdquo; cơ). Sau đ&oacute; ch&uacute;ng t&ocirc;i buộc một đầu d&acirc;y v&agrave;o h&agrave;ng r&agrave;o v&agrave; một đầu kia v&agrave;o ng&oacute;n tay Luis. Ch&uacute;ng t&ocirc;i đẩy hết c&uacute;c l&ecirc;n t&iacute;t đầu d&acirc;y v&agrave; để ch&uacute;ng trượt từ từ, từng chiếc từng chiếc một. Mỗi toa c&aacute;p treo đều chở đầy những người ch&uacute;ng t&ocirc;i biết. C&oacute; một c&aacute;i đen s&igrave; chở Biriquinho. Chẳng c&oacute; g&igrave; l&agrave; lạ khi nghe thấy một giọng n&oacute;i vọng qua h&agrave;ng r&agrave;o,</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;Zez&eacute;, m&agrave;y đang ph&aacute; h&agrave;ng r&agrave;o của b&agrave; phải kh&ocirc;ng?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Kh&ocirc;ng đ&acirc;u, b&agrave; Dimerinda. B&agrave; cứ ra đ&acirc;y m&agrave; xem n&agrave;y.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Ồ, b&agrave; th&iacute;ch xem cảnh n&agrave;y đấy. Chơi ngoan với em trai m&agrave;y. Như thế chẳng hay hơn &agrave;?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">C&oacute; lẽ như thế hay hơn thật, nhưng khi &ldquo;cha đỡ đầu&rdquo; l&agrave; quỷ dữ của t&ocirc;i hu&yacute;ch t&ocirc;i một c&aacute;i, th&igrave; chẳng c&oacute; g&igrave; hay hơn chuyện giở tr&ograve; tinh qu&aacute;i...</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;B&agrave; sẽ cho ch&aacute;u một cuốn lịch l&agrave;m qu&agrave; Gi&aacute;ng sinh như năm ngo&aacute;i chứ ạ?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;M&agrave;y đ&atilde; l&agrave;m g&igrave; với cuốn lịch b&agrave; cho vậy hả?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;B&agrave; cứ v&agrave;o trong m&agrave; xem, b&agrave; Dimerinda. N&oacute; ở ngay ph&iacute;a tr&ecirc;n t&uacute;i b&aacute;nh m&igrave; đấy ạ.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">B&agrave; bật cười v&agrave; hứa sẽ sang xem. Chồng b&agrave; l&agrave;m việc tại cửa h&agrave;ng b&aacute;ch h&oacute;a tổng hợp của &ocirc;ng Chico Franco. Tr&ograve; chơi c&ograve;n lại l&agrave; Luciano. Ban đầu Luis thực sự sợ n&oacute;, cứ t&uacute;m lấy quần t&ocirc;i giật, đ&ograve;i đi khỏi chỗ đ&oacute;. Nhưng Luciano l&agrave; bạn t&ocirc;i. Mỗi khi nh&igrave;n thấy t&ocirc;i, n&oacute; đều r&iacute;t l&ecirc;n r&otilde; to. Gloria cũng kh&ocirc;ng th&iacute;ch n&oacute; v&agrave; bảo rằng lo&agrave;i dơi l&agrave; ma c&agrave; rồng h&uacute;t m&aacute;u trẻ con.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Kh&ocirc;ng phải vậy đ&acirc;u, Gl&oacute;. Luciano kh&ocirc;ng giống như vậy đ&acirc;u. N&oacute; l&agrave; bạn em m&agrave;. N&oacute; biết em.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Em v&agrave; c&aacute;i kiểu cuồng động vật của em, suốt ng&agrave;y n&oacute;i chuyện với con nọ, con kia...&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Kh&oacute; khăn lắm t&ocirc;i mới thuyết phục được Luis tin rằng Luciano kh&ocirc;ng phải một con vật. Đối với ch&uacute;ng t&ocirc;i, Luciano l&agrave; một chiếc m&aacute;y bay đang bay đến căn cứ kh&ocirc;ng qu&acirc;n Afonso.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Nh&igrave;n n&agrave;y, Luis.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">V&agrave; Luciano th&iacute;ch th&uacute; bay quanh ch&uacute;ng t&ocirc;i như thể n&oacute; hiểu ch&uacute;ng t&ocirc;i n&oacute;i g&igrave;. M&agrave; n&oacute; hiểu thật.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;N&oacute; l&agrave; một chiếc m&aacute;y bay. N&oacute; đang...&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i ngừng lại. T&ocirc;i phải bảo b&aacute;c Edmundo n&oacute;i cho t&ocirc;i nghe từ đ&oacute; lần nữa mới được. T&ocirc;i kh&ocirc;ng biết từ đ&oacute; l&agrave; &ldquo;nh&aacute;o lộn&rdquo;, &ldquo;nh&agrave;o lộn&rdquo; hay &ldquo;nhạo lộn&rdquo;. Một trong những từ đ&oacute;. Nhưng t&ocirc;i kh&ocirc;ng thể dạy em m&igrave;nh một từ kh&ocirc;ng ch&iacute;nh x&aacute;c. Nhưng b&acirc;y giờ n&oacute; muốn sang vườn th&uacute;. Ch&uacute;ng t&ocirc;i đến gần c&aacute;i chuồng g&agrave; cũ. Trong chuồng, hai con g&agrave; m&aacute;i l&ocirc;ng s&aacute;ng m&agrave;u đang mổ mổ xuống đất, c&ograve;n con g&agrave; m&aacute;i đen gi&agrave; khụ th&igrave; l&agrave;nh đến nỗi ch&uacute;ng t&ocirc;i c&ograve;n c&oacute; thể g&atilde;i đầu n&oacute;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Trước ti&ecirc;n ch&uacute;ng m&igrave;nh mua v&eacute; đ&atilde;. Nắm tay anh n&agrave;o, bởi v&igrave; trẻ con dễ bị lạc ở chỗ đ&ocirc;ng người như thế n&agrave;y lắm. Nh&igrave;n xem, ng&agrave;y Chủ nhật ở đ&acirc;y thật nhộn nhịp phải kh&ocirc;ng?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Luis nh&igrave;n quanh, thấy khắp nơi to&agrave;n người l&agrave; người, b&egrave;n nắm chặt tay t&ocirc;i. Tại quầy v&eacute;, t&ocirc;i ưỡn bụng v&agrave; hắng giọng, ra vẻ quan trọng. T&ocirc;i cho tay v&agrave;o t&uacute;i quần v&agrave; hỏi người phụ nữ ở quầy</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Miễn v&eacute; đến mấy tuổi nhỉ?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Năm.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Vậy th&igrave; cho một v&eacute; người lớn.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i bứt hai chiếc l&aacute; cam l&agrave;m v&eacute; v&agrave; ch&uacute;ng t&ocirc;i bước v&agrave;o.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Con trai, trước hết ch&uacute;ng ta sẽ đến xem những con chim đẹp như thế n&agrave;o nh&eacute;. Nh&igrave;n k&igrave;a, những con vẹt thường, vẹt đu&ocirc;i d&agrave;i, vẹt Nam Mỹ với đủ mọi m&agrave;u sắc k&igrave;a. Những con l&ocirc;ng sặc sỡ ở đằng kia l&agrave; vẹt Nam Mỹ đ&oacute;.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Mắt em t&ocirc;i lồi ra v&igrave; th&iacute;ch th&uacute;. Ch&uacute;ng t&ocirc;i đi loanh quanh, ngắm ngh&iacute;a mọi thứ. Ch&uacute;ng t&ocirc;i nh&igrave;n ng&oacute; nhiều đến nỗi t&ocirc;i c&ograve;n để &yacute; thấy Gloria v&agrave; Lal&aacute; ở đằng sau mọi thứ kh&aacute;c, đang ngồi tr&ecirc;n ghế băng b&oacute;c cam. Lal&aacute; đang nh&igrave;n t&ocirc;i... C&oacute; khi n&agrave;o c&aacute;c chị đ&atilde; ph&aacute;t hiện ra chăng? Nếu vậy thật th&igrave; chuyến thăm vườn th&uacute; đ&oacute; sẽ kết th&uacute;c k&egrave;m theo một c&uacute; ph&aacute;t ra tr&ograve; v&agrave;o m&ocirc;ng ai đ&oacute;. V&agrave; ai đ&oacute; ấy chỉ c&oacute; thể l&agrave; t&ocirc;i m&agrave; th&ocirc;i.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Tiếp theo l&agrave; g&igrave; vậy, Zez&eacute;, giờ ch&uacute;ng ta sẽ xem g&igrave; hả anh?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i lại hắng giọng v&agrave; lấy lại điệu bộ vừa rồi.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;Ra đằng kia xem khỉ n&agrave;o. B&aacute;c Edmundo gọi ch&uacute;ng l&agrave; linh trưởng.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Ch&uacute;ng t&ocirc;i mang mấy quả chuối theo v&agrave; n&eacute;m cho lũ khỉ. Ch&uacute;ng t&ocirc;i biết m&igrave;nh kh&ocirc;ng được ph&eacute;p l&agrave;m thế, nhưng những người tr&ocirc;ng coi vườn th&uacute; c&ograve;n đang bận t&uacute;i bụi bởi c&oacute; qu&aacute; đ&ocirc;ng kh&aacute;ch tham quan.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Đừng đến gần qu&aacute;, nếu kh&ocirc;ng ch&uacute;ng sẽ n&eacute;m vỏ chuối v&agrave;o người em đấy, nh&oacute;c ạ.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Em muốn xem sư tử.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Đi ngay v&agrave; lu&ocirc;n n&agrave;o.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i liếc nh&igrave;n sang chỗ c&oacute; hai &ldquo;linh trưởng&rdquo; kh&aacute;c đang ăn cam. Từ chuồng sư tử, t&ocirc;i c&oacute; thể nghe thấy hai con ấy đang n&oacute;i g&igrave;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Đ&acirc;y rồi.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i chỉ tay v&agrave;o hai con sư tử m&agrave;u v&agrave;ng, đ&iacute;ch thị l&agrave; sư tử ch&acirc;u Phi. Luis n&oacute;i n&oacute; muốn vỗ đầu con b&aacute;o đen một c&aacute;i.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Em đi&ecirc;n &agrave;, nh&oacute;c? B&aacute;o đen l&agrave; con vật đ&aacute;ng sợ nhất vườn th&uacute;. N&oacute; được đưa tới đ&acirc;y v&igrave; đ&atilde; cắn đứt c&aacute;nh tay của mười t&aacute;m người dạy th&uacute; rồi ăn hết đấy.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Luis c&oacute; vẻ sợ h&atilde;i, vội rụt tay lại.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;B&aacute;o đen đến từ một rạp xiếc hả anh?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;Đ&uacute;ng.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Rạp xiếc n&agrave;o vậy, Zez&eacute;? Thế m&agrave; trước đ&acirc;y anh chưa bao giờ n&oacute;i cho em biết.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i suy nghĩ lung lắm. T&ocirc;i c&oacute; biết ai c&oacute; t&ecirc;n ph&ugrave; hợp để đặt cho một rạp xiếc kh&ocirc;ng nhỉ?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;&Agrave;! N&oacute; đến từ rạp xiếc Rozemberg &rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Đ&oacute; kh&ocirc;ng phải tiệm b&aacute;nh ạ?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">C&agrave;ng ng&agrave;y c&agrave;ng kh&oacute; lừa thằng em t&ocirc;i. N&oacute; c&agrave;ng ng&agrave;y c&agrave;ng kh&ocirc;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Cũng c&oacute; tiệm b&aacute;nh t&ecirc;n như thế. Ngồi xuống ăn trưa th&ocirc;i. Ch&uacute;ng ta đ&atilde; đi bộ nhiều rồi.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Ch&uacute;ng t&ocirc;i ngồi xuống v&agrave; giả vờ ăn. Nhưng tại t&ocirc;i ngứa ng&aacute;y, cứ dỏng l&ecirc;n h&oacute;ng hai chị n&oacute;i&nbsp; chuyện.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Ch&uacute;ng m&igrave;nh n&ecirc;n học tập n&oacute;, Lal&aacute; ạ. Xem đấy, n&oacute; ki&ecirc;n nhẫn với thằng Lu&iacute;s biết bao.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Ừ, nhưng Luis c&oacute; l&agrave;m những chuyện m&agrave; n&oacute; vẫn l&agrave;m đ&acirc;u. Đ&oacute; l&agrave; những việc xấu xa chứ kh&ocirc;ng phải tr&ograve; tinh nghịch.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Vậy n&ecirc;n trong m&aacute;u n&oacute; mới c&oacute; quỷ, nhưng n&oacute; ngộ nghĩnh đấy chứ. Kh&ocirc;ng ai ở ngo&agrave;i phố c&oacute; thể tức giận với n&oacute;, d&ugrave; n&oacute; l&agrave;m g&igrave;...&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;N&oacute; m&agrave; đi qua chỗ chị th&igrave; sẽ kh&ocirc;ng tho&aacute;t khỏi cảnh bị phết roi đ&acirc;u. Một ng&agrave;y n&agrave;o đ&oacute; n&oacute; sẽ được dạy cho một b&agrave;i học.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i nh&igrave;n Gloria bằng &aacute;nh mắt thương x&oacute;t. Chị lu&ocirc;n đến giải cứu t&ocirc;i v&agrave; lần n&agrave;o t&ocirc;i cũng hứa với chị sẽ kh&ocirc;ng t&aacute;i phạm.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Để sau đi. B&acirc;y giờ th&igrave; đừng. Bọn ch&uacute;ng đang chơi y&ecirc;n m&agrave;.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Chị đ&atilde; biết hết rồi. Chị biết t&ocirc;i đ&atilde; lội qua mương sang s&acirc;n sau nh&agrave; b&agrave; Celina. T&ocirc;i m&ecirc; mẩn c&aacute;i d&acirc;y phơi quần &aacute;o đung đưa cả đống c&aacute;nh tay với ống ch&acirc;n trong gi&oacute;. L&uacute;c đ&oacute; quỷ dữ bảo t&ocirc;i rằng t&ocirc;i c&oacute; thể l&agrave;m cho tất cả những c&aacute;nh tay, ống ch&acirc;n kia lộn t&ugrave;ng ph&egrave;o c&ugrave;ng l&uacute;c. T&ocirc;i đồng &yacute; rằng cảnh đ&oacute; hẳn sẽ hết sức buồn cười. T&ocirc;i t&igrave;m thấy một mảnh thủy tinh sắc ở dưới mương, b&egrave;n tr&egrave;o l&ecirc;n c&acirc;y cam, ki&ecirc;n nhẫn cắt c&aacute;i d&acirc;y phơi. T&ocirc;i su&yacute;t ng&atilde; theo c&aacute;i d&acirc;y. C&oacute; tiếng k&ecirc;u thất thanh v&agrave; mọi người chạy t&uacute;a ra.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Gi&uacute;p t&ocirc;i với, d&acirc;y phơi bị đứt rồi.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Nhưng một giọng người kh&ocirc;ng biết ph&aacute;t ra từ đ&acirc;u tru tr&eacute;o.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">&nbsp;-&ldquo;Đ&oacute; l&agrave; thằng con nh&agrave; &ocirc;ng Paulo đấy, thằng ranh con ph&aacute; hoại đ&oacute;. T&ocirc;i nh&igrave;n thấy n&oacute; cầm mảnh thủy tinh leo l&ecirc;n c&acirc;y cam kia k&igrave;a.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Zez&eacute;?&quot;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;G&igrave; vậy, Luis?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Sao anh biết nhiều về vườn th&uacute; thế?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Anh đ&atilde; đến thăm v&ocirc; số vườn th&uacute; rồi m&agrave;.&rdquo; &rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i n&oacute;i xạo. Tất cả những g&igrave; t&ocirc;i biết đều l&agrave; do b&aacute;c Edmundo kể cho nghe. B&aacute;c thậm ch&iacute; đ&atilde; hứa một ng&agrave;y n&agrave;o đ&oacute; sẽ đưa t&ocirc;i đến vườn th&uacute;. Nhưng b&aacute;c đi qu&aacute; chậm n&ecirc;n khi hai b&aacute;c ch&aacute;u đến nơi th&igrave; chắc vườn th&uacute; chẳng c&ograve;n ở đ&oacute; nữa. Totoca đ&atilde; từng đến vườn th&uacute; một lần c&ugrave;ng với cha.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Vườn th&uacute;anh th&iacute;ch nhất l&agrave; RuaBar&otilde;odeDrummond, Vila Isabel. Em c&oacute; biết nam tước Drummond l&agrave; ai kh&ocirc;ng? Tất nhi&ecirc;n em chẳng biết đ&acirc;u. Em c&ograve;n b&eacute; qu&aacute; chưa biết những chuyện như thế n&agrave;y đ&acirc;u. Ng&agrave;i nam tước chắc chắn rất th&acirc;n với Ch&uacute;a. Bởi v&igrave; ch&iacute;nh ng&agrave;i đ&atilde; gi&uacute;p Ch&uacute;a ph&aacute;t minh ra tr&ograve; xổ số, c&aacute;i tr&ograve; m&agrave; họ b&aacute;n những tấm v&eacute; tại qu&aacute;n Đ&oacute;i Khổ v&agrave; tại vườn th&uacute; ấy. Khi n&agrave;o em lớn hơn...&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">&nbsp;C&aacute;c chị t&ocirc;i vẫn đang ở đ&oacute;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Khi em lớn hơn th&igrave; sao cơ?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Em hỏi nhiều qu&aacute; đấy, nh&oacute;c. Khi n&agrave;o em đủ lớn, anh sẽ dạy em về c&aacute;c con vật trong xổ số2 v&agrave; c&aacute;c số của ch&uacute;ng. Phải đến số hai mươi cơ. Từ số hai mươi đến số hai mươi lăm, anh biết l&agrave; c&oacute; một con b&ograve; c&aacute;i, một con b&ograve; đực, một con gấu,một con nai v&agrave; một con hổ. Anh kh&ocirc;ng biết ch&uacute;ng được xếp theo thứ tự như thế n&agrave;o, nhưng anh sẽ học để kh&ocirc;ng dạy nh&oacute;c sai.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Thằng b&eacute; đ&atilde; ph&aacute;t ch&aacute;n tr&ograve; chơi rồi.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Zez&eacute;, h&aacute;t b&agrave;i Ng&ocirc;i nh&agrave; nhỏ cho em nghe đi.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;Ch&uacute;ng ta đang ở vườn th&uacute;, đ&uacute;ng kh&ocirc;ng? Ở đ&acirc;y đang đ&ocirc;ng người qu&aacute;.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Kh&ocirc;ng. Ch&uacute;ng ta đ&atilde; ra khỏi vườn th&uacute; rồi m&agrave;.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;B&agrave;i đ&oacute; d&agrave;i lắm đấy. Anh chỉ h&aacute;t một đoạn em th&iacute;ch th&ocirc;i nh&eacute;.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i biết đ&oacute; l&agrave; đoạn về những con ve. T&ocirc;i h&iacute;t căng lồng ngực. T&ocirc;i sống trong một ng&ocirc;i nh&agrave; tr&ecirc;n đỉnh đồi Dưới ch&acirc;n nh&agrave; vườn c&acirc;y tr&aacute;i trải ra. Một nếp nh&agrave; nho nhỏ Đứng b&ecirc;n thềm t&ocirc;i c&oacute; thể nh&igrave;n ra tận biển xa. T&ocirc;i bỏ v&agrave;i đoạn. Trong đ&aacute;m c&acirc;y cọ lạ lũ ve sầu h&aacute;t th&aacute;nh ca. Mặt trời l&ecirc;n nhuộm những c&aacute;nh buồm v&agrave;ng ruộm Trong khu vườn, một ch&uacute; chim sơn ca. T&ocirc;i ngừng h&aacute;t. C&aacute;c chị vẫn đang ngồi đ&oacute; đợi t&ocirc;i. T&ocirc;i nảy ra một &yacute;: T&ocirc;i sẽ h&aacute;t cho đến khi đ&ecirc;m xuống. T&ocirc;i sẽ b&aacute;m trụ ở đ&acirc;y l&acirc;u hơn c&aacute;c chị. Chẳng may mắn được đến thế. T&ocirc;i h&aacute;t hết cả b&agrave;i đ&oacute;, h&aacute;t lại lần nữa, rồi h&aacute;t b&agrave;i &ldquo;V&igrave; t&igrave;nh y&ecirc;u tho&aacute;ng qua của em&rdquo;, thậm ch&iacute; cả b&agrave;i &ldquo;Ramona&rdquo;. Hai phi&ecirc;n bản kh&aacute;c nhau của b&agrave;i &ldquo;Ramona&rdquo; m&agrave; t&ocirc;i biết... M&agrave; c&aacute;c chị vẫn kh&ocirc;ng nh&uacute;c nh&iacute;ch. Đến l&uacute;c đ&oacute; th&igrave; t&ocirc;i cải rồi. Tốt hơn n&ecirc;n để chuyện n&agrave;y kết th&uacute;c th&ocirc;i. T&ocirc;i đi đến chỗ Lal&aacute;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;N&agrave;o, ch&igrave;a m&ocirc;ng ra đ&acirc;y.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i quay người lại, giơ m&ocirc;ng ra cho chị, cắn chặt rằng v&igrave; Lala đang cầm d&eacute;p tr&ecirc;n tay. Đ&oacute; l&agrave; &yacute; tưởng của mẹ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&quot;H&ocirc;m nay tất cả ch&uacute;ng ta sẽ đi xem nh&agrave;.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Totoca dẫn t&ocirc;i ra một chỗ v&agrave; th&igrave; thầm,</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;Nếu m&agrave;y n&oacute;i với bất cứ ai rằng ch&uacute;ng m&igrave;nh đ&atilde; đến đ&oacute; rồi, th&igrave; m&agrave;y sẽ l&atilde;nh đủ.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Nhưng t&ocirc;i chẳng hề c&oacute; &yacute; định ấy. Cả nh&oacute;m ch&uacute;ng t&ocirc;i đi ra đường. Gloria nắm tay t&ocirc;i v&agrave; được lệnh kh&ocirc;ng được rời mắt khỏi t&ocirc;i d&ugrave; chỉ một ph&uacute;t. C&ograve;n t&ocirc;i nắm tay Lu&iacute;s.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Khi n&agrave;o ch&uacute;ng ta chuyển đến đ&oacute; hả mẹ?&rdquo; Gloria hỏi.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Sau Gi&aacute;ng sinh hai h&ocirc;m, ch&uacute;ng ta sẽ phải g&oacute;i gh&eacute;m đồ đạc,&rdquo; mẹ n&oacute;i, giọng buồn buồn.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Mẹ c&oacute; vẻ rất mệt mỏi. T&ocirc;i thực l&ograve;ng thấy thương mẹ. Mẹ đ&atilde; l&agrave;m lụng cả đời. Mẹ l&agrave;m việc từ khi l&ecirc;n s&aacute;u, khi nh&agrave; m&aacute;y được x&acirc;y dựng. Người ta để mẹ ngồi l&ecirc;n một chiếc b&agrave;n v&agrave; mẹ phải l&agrave;m sạch v&agrave; lau kh&ocirc; c&aacute;c dụng cụ. Hồi đ&oacute;, mẹ c&ograve;n qu&aacute; b&eacute;, đến nỗi t&egrave; dầm cả ra v&igrave; kh&ocirc;ng thể tự xuống khỏi b&agrave;n được. Đ&oacute; l&agrave; l&yacute; do mẹ chưa bao giờ được đến trường, cũng như chưa bao giờ học đọc học viết. Khi mẹ kể cho t&ocirc;i nghe chuyện đ&oacute;, t&ocirc;i buồn đến mức đ&atilde; hứa rằng khi n&agrave;o th&agrave;nh nh&agrave; thơ v&agrave; trở n&ecirc;n th&ocirc;ng th&aacute;i, t&ocirc;i sẽ đọc thơ của t&ocirc;i cho mẹ nghe. Dấu hiệu Gi&aacute;ng sinh xuất hiện tr&ecirc;n khắp c&aacute;c cửa h&agrave;ng của hiệu. &Ocirc;ng gi&agrave; Noel đ&atilde; được vẽ l&ecirc;n tr&ecirc;n mọi &ocirc; cửa k&iacute;nh trưng b&agrave;y. Mọi người đều đ&atilde; mua thiệp, để tr&aacute;nh việc đến cận ng&agrave;y mới đổ x&ocirc; đi mua. T&ocirc;i c&oacute; một niềm hy vọng mong manh rằng lần n&agrave;y, Ch&uacute;a H&agrave;i Đồng sẽ được sinh ra trong tr&aacute;i tim t&ocirc;i. D&ugrave; g&igrave; đi nữa, c&oacute; lẽ t&ocirc;i cũng sẽ tiến bộ hơn ch&uacute;t &iacute;t khi đ&atilde; đến tuổi ch&iacute;n chắn.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;N&oacute; đ&acirc;y.&rdquo;&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Mọi người đều th&iacute;ch n&oacute;. Nh&agrave; mới nhỏ hơn nh&agrave; cũ một ch&uacute;t. Với sự gi&uacute;p sức của Totoca, mẹ th&aacute;o sợi d&acirc;y th&eacute;p buộc cổng v&agrave; ch&uacute;ng t&ocirc;i chạy &ugrave;a v&agrave;o. Gl&oacute;ria bu&ocirc;ng tay t&ocirc;i ra, qu&ecirc;n bẵng mất chị đ&atilde; sắp th&agrave;nh thiếu nữ. Chị chạy tới chỗ c&acirc;y xo&agrave;i v&agrave; cho&agrave;ng tay quanh th&acirc;n n&oacute;. đầu ti&ecirc;n.C&acirc;y xo&agrave;i l&agrave; của chị. Chị đến chỗ n&oacute; Totoca l&agrave;m như thế với c&acirc;y me. Chẳng c&ograve;n g&igrave; cho t&ocirc;i. T&ocirc;i nh&igrave;n Gl&oacute;ria, su&yacute;t kh&oacute;c.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;Thế c&ograve;n em th&igrave; sao, Gi&oacute;?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Chạy ra sau nh&agrave; xem sao. Chắc phải c&ograve;n những c&acirc;y kh&aacute;c nữa đấy, đồ ngốc.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i chạy đi, nhưng chỉ thấy cỏ mọc d&agrave;i v&agrave; cả đ&aacute;m những c&acirc;y cam gi&agrave; cỗi đầy gai. B&ecirc;n cạnh r&atilde;nh nước l&agrave; một c&acirc;y cam ngọt nhỏ. T&ocirc;i thất vọng qu&aacute;. C&aacute;c anh chị em của t&ocirc;i đ&atilde; chạy khắp nh&agrave; nhận ph&ograve;ng ngủ cả rồi. T&ocirc;i giật giật v&aacute;y của Gl&oacute;ria.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Chẳng c&oacute; g&igrave; kh&aacute;c cả.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Em chẳng biết quan s&aacute;t g&igrave; cả. Đợi một ph&uacute;t. Chị sẽ t&igrave;m một c&acirc;y cho em.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Ngay sau đ&oacute; chị ra vườn với t&ocirc;i. Chị chăm ch&uacute; nh&igrave;n những c&acirc;y cam.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Em kh&ocirc;ng th&iacute;ch c&acirc;y kia sao? C&acirc;y đ&oacute; đẹp m&agrave;.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">&nbsp;T&ocirc;i kh&ocirc;ng th&iacute;ch c&acirc;y n&agrave;y, cũng chẳng th&iacute;ch c&acirc;y kia, chẳng th&iacute;ch c&acirc;y n&agrave;o trong đ&aacute;m ấy. Ch&uacute;ng nhiều gai qu&aacute;.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Em th&iacute;ch c&acirc;y cam ngọt hơn đ&aacute;m c&acirc;y xấu x&iacute; n&agrave;y.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;Ở đ&acirc;u cơ?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i dẫn chị đi xem c&acirc;y cam.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;C&acirc;y cam nhỏ xinh qu&aacute; n&agrave;y! N&oacute; kh&ocirc;ng c&oacute; một c&aacute;i gai n&agrave;o. N&oacute; tr&ocirc;ng rất đặc trưng, đến mức nh&igrave;n từ xa ta cũng c&oacute; thể nhận ra n&oacute; l&agrave; một c&acirc;y cam ngọt. Nếu chị b&eacute; như em, chị cũng chẳng muốn c&acirc;y kh&aacute;c đ&acirc;u&rdquo; một c&acirc;y to co</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Nhưng em muốn&rdquo;&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">&nbsp;-&ldquo;Nghĩ kỹ m&agrave; xem, Zez&eacute;. C&acirc;y n&agrave;y vẫn c&ograve;n nhỏ. N&oacute; sẽ lớn l&ecirc;n th&agrave;nh một c&acirc;y to - em v&agrave; n&oacute; sẽ c&ugrave;ng lớn l&ecirc;n. Em v&agrave; n&oacute; sẽ hiểu nhau như anh em. Em c&oacute; thấy c&aacute;i c&agrave;nh kia kh&ocirc;ng? N&oacute; l&agrave; c&agrave;nh duy nhất, đ&uacute;ng vậy thật, nhưng n&oacute; hơi giống một con ngựa được sinh ra chỉ để cho em vậy.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i cảm thấy m&igrave;nh thực sự bị đối xử bất c&ocirc;ng. Chuyện n&agrave;y khiến t&ocirc;i nhớ đến c&aacute;i vỏ chai rượu Scotch C&oacute; h&igrave;nh c&aacute;c thi&ecirc;n thần m&agrave; ch&uacute;ng t&ocirc;i đ&atilde; c&oacute; lần nh&igrave;n thấy. Khi ấy, Lal&aacute; n&oacute;i,</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;Thi&ecirc;n thần n&agrave;y l&agrave; em.&rdquo;&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Gloria chọn một thi&ecirc;n thần v&agrave; Totoca cũng chọn một. Nhưng c&ograve;n t&ocirc;i th&igrave; sao? Đến cuối c&ugrave;ng, t&ocirc;i l&agrave; c&aacute;i đầu thấp b&eacute; ở ph&iacute;a sau tất cả những thi&ecirc;n thần kh&aacute;c, gần như kh&ocirc;ng c&oacute; c&aacute;nh. Thi&ecirc;n thần Scotland thứ tự thậm ch&iacute; c&ograve;n kh&ocirc;ng phải một thi&ecirc;n thần ho&agrave;n chỉnh... T&ocirc;i lu&ocirc;n l&agrave; kẻ cuối c&ugrave;ng. Mai sau lớn l&ecirc;n, t&ocirc;i sẽ cho họ thấy. T&ocirc;i sẽ mua nguy&ecirc;n cả c&aacute;nh rừng nhiệt đới Amazon, v&agrave; tất cả những c&aacute;i c&acirc;y cao chọc trời sẽ l&agrave; của t&ocirc;i. T&ocirc;i sẽ mua một cửa h&agrave;ng đầy những c&aacute;i chai in h&igrave;nh thi&ecirc;n thần v&agrave; kh&ocirc;ng ai lấy được một mảnh c&aacute;nh n&agrave;o ở đấy. Hờn dỗi, t&ocirc;i ngồi bệt xuống đất v&agrave; để nỗi tức giận của m&igrave;nh tựa v&agrave;o c&acirc;y cam nhỏ. Gl&oacute;ria mỉm cười bỏ đi.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Em sẽ kh&ocirc;ng tức giận l&acirc;u đ&acirc;u, Zez&eacute; ạ. Rồi em sẽ thấy chị n&oacute;i đ&uacute;ng th&ocirc;i.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i cầm một c&aacute;i que c&agrave;o c&agrave;o l&ecirc;n mặt đất v&agrave; vừa bắt đầu ngừng sụt sịt th&igrave; nghe thấy một giọng n&oacute;i cất l&ecirc;n từ đ&acirc;u đ&oacute;, gần tr&aacute;i tim m&igrave;nh.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Tớ nghĩ chị cậu n&oacute;i đ&uacute;ng đấy.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Mọi người lu&ocirc;n đ&uacute;ng. Chỉ c&oacute; tớ l&agrave; chẳng đ&uacute;ng bao giờ.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Kh&ocirc;ng phải vậy đ&acirc;u. Nếu ngắm kỹ tớ, cậu sẽ thấy.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Giật m&igrave;nh, t&ocirc;i lồm cồm nhổm dậy v&agrave; nh&igrave;n kỹ c&acirc;y cam nhỏ. Thật kỳ lạ, v&igrave; t&ocirc;i lu&ocirc;n n&oacute;i chuyện với mọi thứ, nhưng t&ocirc;i cứ nghĩ ch&iacute;nh ch&uacute; chim nhỏ trong t&ocirc;i đ&atilde; khiến cho mọi thứ tr&ograve; chuyện với t&ocirc;i.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Nhưng c&oacute; thật l&agrave; cậu biết n&oacute;i kh&ocirc;ng?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Cậu kh&ocirc;ng nghe thấy tớ n&oacute;i sao?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">V&agrave; n&oacute; bật cười kh&uacute;c kh&iacute;ch. T&ocirc;i su&yacute;t la l&ecirc;n v&agrave; &ugrave; t&eacute; chạy. Nhưng sự t&ograve; m&ograve; đ&atilde; giữ t&ocirc;i ở lại.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;L&agrave;m sao cậu c&oacute; thể n&oacute;i được?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;C&acirc;y cối tr&ograve; chuyện với vạn vật. N&oacute;i bằng l&aacute;, bằng c&agrave;nh, bằng gốc rễ. Cậu c&oacute; muốn thấy điều đ&oacute; kh&ocirc;ng? &Aacute;p tai v&agrave;o th&acirc;n của tớ đi, rồi cậu sẽ nghe thấy tiếng tim tớ.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i do dự một l&aacute;t, nhưng nh&igrave;n k&iacute;ch cỡ của c&acirc;y cam, nỗi sợ trong t&ocirc;i tan biến. T&ocirc;i &aacute;p tại v&agrave;o th&acirc;n c&acirc;y v&agrave; nghe thấy tiếng t&iacute;ch... t&iacute;ch...</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Thấy kh&ocirc;ng?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;H&atilde;y n&oacute;i với tớ xem n&agrave;o. Mọi người đều biết cậu c&oacute; thể n&oacute;i được &agrave;?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Kh&ocirc;ng. Chỉ m&igrave;nh cậu th&ocirc;i.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Thật kh&ocirc;ng?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Tớ thề đấy. Hồi xưa, một b&agrave; ti&ecirc;n bảo tớ rằng, khi một cậu b&eacute; giống như cậu l&agrave;m bạn với tớ, th&igrave; tớ sẽ n&oacute;i được v&agrave; sẽ rất vui.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Vậy cậu sẽ đợi chứ?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;G&igrave; cơ?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Đợi tớ chuyển tới đ&acirc;y. Phải mất hơn một tuần đấy. Cậu sẽ kh&ocirc;ng qu&ecirc;n c&aacute;ch n&oacute;i chuyện chứ?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Tớ kh&ocirc;ng bao giờ qu&ecirc;n đ&acirc;u. C&oacute; nghĩa l&agrave;, chỉ ri&ecirc;ng với cậu th&ocirc;i. Cậu c&oacute; muốn thử xem cưỡi tớ th&igrave; sẽ &ecirc;m như thế n&agrave;o kh&ocirc;ng?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Bằng c&aacute;ch n&agrave;o...?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Ngồi l&ecirc;n c&agrave;nh của tớ đi.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i l&agrave;m theo.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;N&agrave;o, lắc lư người v&agrave; nhắm mắt lại.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">T&ocirc;i l&agrave;m như c&acirc;y cam n&oacute;i.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Cậu nghĩ sao? Cậu đ&atilde; bao giờ c&oacute; con ngựa n&agrave;o oach hon chua?&quot;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Chưa từng. Tuyệt qu&aacute;. Tớ sẽ cho em tớ con ngựa Vua Bạc. Cậu sẽ thực sự th&iacute;ch thằng b&eacute; cho xem.&rdquo; T&ocirc;i tr&egrave;o xuống, thấy y&ecirc;u c&acirc;y cam nhỏ của m&igrave;nh.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;N&agrave;y, tớ định thế n&agrave;y nh&eacute;. Bất cứ khi n&agrave;o c&oacute; thể, thậm ch&iacute; trước khi ch&uacute;ng tớ chuyển tới đ&acirc;y, tớ sẽ đến tr&ograve; chuyện với cậu. B&acirc;y giờ tớ phải đi đ&acirc;y. Mọi người đ&atilde; ra Cổng chuẩn bị về rồi.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Nhưng bạn b&egrave; kh&ocirc;ng tạm biệt nhau kiểu ấy đ&acirc;u.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Suyt! Chị tớ đến đấy.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Gloria đến đ&uacute;ng l&uacute;c t&ocirc;i đang &ocirc;m c&aacute;i c&acirc;y.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Tạm biệt nh&eacute;, bạn của t&ocirc;i. Bạn l&agrave; sinh vật đẹp nhất tr&ecirc;n đời!&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Chẳng phải chị đ&atilde; n&oacute;i với em như thế rồi sao?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;Đ&uacute;ng vậy. B&acirc;y giờ nếu chị muốn đổi c&acirc;y xo&agrave;i hay c&acirc;y me lấy c&acirc;y của em th&igrave; em cũng kh&ocirc;ng đổi đ&acirc;u.&rdquo; Chị dịu d&agrave;ng vuốt t&oacute;c t&ocirc;i.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Zez&eacute;, Zez&eacute;...&quot;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Chị em t&ocirc;i nắm tay nhau rời khỏi đ&oacute;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Gl&oacute;, c&acirc;y xo&agrave;i của chị bị c&acirc;m th&igrave; phải, chị c&oacute; nghĩ vậy kh&ocirc;ng?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;C&ograve;n qu&aacute; sớm để n&oacute;i g&igrave;, nhưng c&oacute; vẻ như vậy thật.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">- &ldquo;Thế c&ograve;n c&acirc;y me của Totoca?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;N&oacute; hơi kỳ một ch&uacute;t, sao thế?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">-&ldquo;Em kh&ocirc;ng biết c&oacute; n&ecirc;n n&oacute;i cho chị biết hay kh&ocirc;ng. Nhưng một ng&agrave;y n&agrave;o đ&oacute; em sẽ kể cho chị nghe về một điều kỳ diệu, Gl&oacute; ạ.&rdquo;</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(29, 'Đất rừng phương nam', 'Văn học Việt Nam', 'Đoàn Giỏi', 1975, 'dat-rung-phuong-nam.jpg', 'Đã hoàn', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#333333\">C&acirc;u chuyện mượn h&igrave;nh ảnh một cậu b&eacute; bị lưu lạc trong thời kỳ kh&aacute;ng chiến chống Ph&aacute;p ở Nam bộ để giới thiệu Đất rừng Phương Nam. Nơi đ&oacute;, một v&ugrave;ng đất v&ocirc; c&ugrave;ng gi&agrave;u c&oacute;, h&agrave;o ph&oacute;ng v&agrave; h&ugrave;ng vĩ với những con người trung hậu, tr&iacute; dũng, một l&ograve;ng một dạ theo kh&aacute;ng chiến.&nbsp;Đất rừng phương Nam của Đo&agrave;n Giỏi như một x&atilde; hội của miền s&ocirc;ng nước T&acirc;y Nam bộ thu nhỏ. Bởi ở nơi đ&oacute;, người đọc đ&atilde; t&igrave;m thấy h&igrave;nh ảnh người d&acirc;n của v&ugrave;ng đất phương Nam từ s&ocirc;ng Tiền, s&ocirc;ng Hậu trải d&agrave;i đến Ki&ecirc;n Giang &ndash; Rạch Gi&aacute;, rồi xuống tận rừng U Minh, sau đ&oacute; dừng lại ở Năm Căn C&agrave; Mau. Bối cảnh trong Đất rừng phương Nam l&agrave; cả một đất trời thi&ecirc;n nhi&ecirc;n ưu đ&atilde;i, c&aacute;nh đồng b&aacute;t ng&aacute;t m&ecirc;nh m&ocirc;ng, s&oacute;ng nước r&igrave; rầm, rừng rậm bạt ng&agrave;n tr&ugrave; ph&uacute;, th&uacute; rừng hoang d&atilde; mu&ocirc;n lo&agrave;i&hellip;</span>&nbsp;<span style=\"color:#333333\">Chưa c&oacute; nh&agrave; văn n&agrave;o viết về thi&ecirc;n nhi&ecirc;n Nam Bộ được như Đo&agrave;n Giỏi. Nh&acirc;n vật ch&iacute;nh của &ocirc;ng l&agrave; thi&ecirc;n nhi&ecirc;n v&agrave; lo&agrave;i vật chứ kh&ocirc;ng phải con người. &Ocirc;ng l&agrave; người đầu ti&ecirc;n kể chuyện &ldquo;kinh dị&rdquo; về thi&ecirc;n nhi&ecirc;n Nam Bộ. Dưới ng&ograve;i b&uacute;t của &ocirc;ng, mọi chuyện đều trở n&ecirc;n lạ l&ugrave;ng.&nbsp;Nhận định&nbsp;&nbsp;&ldquo;C&agrave;ng về sau n&agrave;y, Đất rừng phương Nam c&agrave;ng tỏ ra c&oacute; vị tr&iacute; vững chắc trong số c&aacute;c quyển s&aacute;ch hay nhất viết về thiếu nhi. V&agrave; những người lớn đọc n&oacute; vẫn v&ocirc; c&ugrave;ng th&iacute;ch th&uacute;. Với một đời văn tr&ecirc;n bốn mươi năm, anh đ&atilde; kịp để lại cho đời những d&ograve;ng đẹp đẽ đậm sắc th&aacute;i v&agrave; đầy sinh th&uacute; về qu&ecirc; hương, đất nước, con người ở v&ugrave;ng đất Nam Bộ th&acirc;n y&ecirc;u của ch&uacute;ng ta.&rdquo;</span></span></span></p>\r\n', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">C&oacute; lẽ từ h&ocirc;m bước l&ecirc;n x&oacute;m chợ n&agrave;y, m&igrave;nh đ&atilde; bắt đầu cuộc sống lưu lạc rồi chăng Những đ&ecirc;m giật m&igrave;nh thức giấc nằm nghe tiếng gi&oacute; r&iacute;t th&ecirc; lương từ c&aacute;c c&aacute;nh đồng xa m&ocirc;ng quạnh v&agrave; lắng nghe tiếng nước chảy &agrave;o &agrave;o dưới ch&acirc;n cầu nước(Bậc thang bắc ra s&ocirc;ng để giặt giũ hay lấy nước) bắc ra con k&ecirc;nh thẳng tắp chạy d&agrave;i v&ocirc; tận trước ng&ocirc;i chợ n&agrave;y, t&ocirc;i vẫn thường vơ vẩn nghĩ như vậy</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i lạc tới x&oacute;m chợ n&agrave;y, t&iacute;nh ra đ&atilde; c&oacute; hơn nửa th&aacute;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Một buổi tối, đo&agrave;n thuyền vận tải qu&acirc;n lương của Uy ban kh&aacute;ng chiến Nam BỘ đỗ lại đ&acirc;y. Anh học sinh trường c&ocirc;le người trong ban tiếp tế đ&atilde; cho t&ocirc;i theo thuyền từ Hậu Giang đến đ&acirc;y bảo t&ocirc;i: &ldquo;Chỉ gh&eacute; lại độ nửa giờ cho c&aacute;c ch&uacute; l&ecirc;n chợ mua thức ăn. C&Oacute; muốn l&ecirc;n bờ kh&ocirc;ng?.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i lắc đầu. Anh b&egrave;n vỗ vai t&ocirc;i cười lớn: &ldquo;Vậy th&igrave; tốt qu&aacute;, ngồi l&ecirc;n mui ng&oacute; l&ecirc;n bờ chơi cũng được. Tr&ocirc;ng chừng đồ đạc em nh&eacute;!&rdquo; Anh lần theo c&acirc;y đ&ograve;n d&agrave;i bắc sang một chiếc thuyền kh&aacute;c, v&agrave; lẹ l&agrave;ng như con s&oacute;c, co người tr&egrave;o qua be mấy chiếc tam bản, mấy chiếc xuồng nhỏ của những người đi chợ đậu ken dọc theo bến rồi nhảy ph&oacute;c l&ecirc;n bờ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ndash; Đừng đi đ&acirc;u nh&eacute; Uống c&agrave; ph&ecirc; xong anh sẽ xuống cho ch&uacute; m&agrave;y c&aacute;i b&aacute;nh! &ndash; anh xốc khẩu s&uacute;ng m&ocirc;-de đeo b&ecirc;n h&ocirc;ng, vẫy vẫy t&ocirc;i v&agrave; rướn hai ch&acirc;n, cố n&oacute;i thật to để cho t&ocirc;i nghe thấy.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ndash; Rồi! Nghe rồi&hellip; Anh cứ đi đi! &ndash; t&ocirc;i bắc tay l&ecirc;n mồm đ&aacute;p lại.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i ngồi đong đưa hai ch&acirc;n tr&ecirc;n mui chiếc thuyền chở gạo ng&oacute;ng l&ecirc;n chợ. Đ&egrave;n măng-s&ocirc;ng trong c&aacute;c hiệu bu&ocirc;n, c&aacute;c tiệm ăn của người Hoa kiều thắp l&ecirc;n s&aacute;ng rực. Chỗ khoảng s&acirc;n rộng, những ngươi đ&agrave;n b&agrave; n&ocirc;ng d&acirc;n mặc quần &aacute;o b&agrave; ba đen, khăn rằn đen vắt ch&eacute;o qua vai ngồi xếp từng d&atilde;y d&agrave;i sau những chiếc cần x&eacute; to tướng, những chiếc th&uacute;ng đựng đầy ắp kh&ocirc;ng tr&ocirc;ng r&otilde; v&agrave; kh&ocirc;ng biết đ&oacute; l&agrave; thứ g&igrave; m&agrave; họ mang ra chợ b&aacute;n. Mỗi người b&aacute;n h&agrave;ng đều thắp một ngọn đ&egrave;n con Ơ&ucirc; chỗ m&igrave;nh ngồi. V&agrave; những người đi chợ, cũng c&oacute; nhiều ngươi cầm theo tr&ecirc;n tay một ngọn đ&egrave;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Đ&egrave;n người mua, đ&egrave;n người b&aacute;n cứ như sao sa.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Từ trong đ&aacute;m người ồn &agrave;o v&agrave; những &aacute;nh lửa đ&egrave;n lu&ocirc;n lu&ocirc;n cử động Ơ&ucirc; t&iacute;t chỗ cuối chợ, bỗng nổi l&ecirc;n bốn đầu ngọn đuốc ch&aacute;y đỏ rực c&ugrave;ng với tiếng trống, tiếng thanh la khua rung dồn dập.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ndash; H&aacute;t Sơn Đ&ocirc;ng, ch&uacute;ng m&agrave;y ơi?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ndash; Lại coi con b&eacute; nh&agrave;o v&ograve;ng lửa đi! Mau l&ecirc;n, ch&uacute;ng m&agrave;y ơi!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Bọn con n&iacute;t từ khắp c&aacute;c ng&otilde; ng&aacute;ch, từ trong c&aacute;c chỗ tối, từ dưới thuyền dưới xuồng ch&egrave;o l&ecirc;n, ba ch&acirc;n bốn cẳng reo h&ograve; chạy về chỗ đ&aacute;m h&aacute;t rong. Tự nhi&ecirc;n hai ch&acirc;n t&ocirc;i cũng ngứa ng&aacute;y muốn tu&ocirc;n theo. Đ&atilde; bắt đầu nghe tiếng loa của người Kh&aacute;ch m&atilde;i v&otilde; giới thiệu c&aacute;c m&ocirc;n tr&igrave;nh diễn, v&agrave; tiếng vỗ tay hoan h&ocirc; cua đ&aacute;m người xem v&acirc;y quanh.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">T&ocirc;i kiễng ch&acirc;n l&ecirc;n coi họ l&agrave;m g&igrave; đằng ấy. Xa qu&aacute;, chỉ thấy đầu ngươi l&ocirc; nh&ocirc; v&agrave; mỗi l&uacute;c lại nghe tiếng cười kho&aacute;i tr&aacute; dậy l&ecirc;n. l&acirc;u nay m&igrave;nh xem ch&aacute;n rồi. C&ograve;n lạ g&igrave; c&aacute;i thứ tr&ograve; ấy?&rdquo; T&ocirc;i tự nhủ thầm như vậy v&agrave; khinh khỉnh nh&igrave;n về đ&aacute;m người huy&ecirc;n n&aacute;o để n&eacute;n sự th&ocirc;i th&uacute;c cứ dồn trống trong ngực.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ndash; Thằng An c&ograve;n ngồi đấy kh&ocirc;ng? &ndash; b&aacute;c t&agrave;i c&ocirc;ng gi&agrave; cầm x&acirc;u thịt v&agrave; một mớ rau cải vừa bước l&ecirc;n đ&ograve;n d&agrave;i vừa hỏi, giọng ồm ồm.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Ch&aacute;u c&ograve;n đ&acirc;y, b&aacute;c ơi ! &ndash; t&ocirc;i vội đ&aacute;p, bụng hơi rạo rực.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ndash; N&egrave;, m&agrave; m&agrave;y muốn coi h&aacute;t Sơn Đ&ocirc;ng kh&ocirc;ng &ndash; b&aacute;c lại hỏi.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ndash; C&aacute;c anh bảo ch&aacute;u Ơ&ucirc; đ&acirc;y coi chừng thuyền.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">B&aacute;c t&agrave;i c&ocirc;ng vứt s&acirc;u thịt v&agrave;o c&aacute;i rổ, vặn lưng uốn xương sống nghe rắc rắc, hơi thở nặc nồng m&ugrave;i rượu:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&Ecirc; ! m&agrave;y đ&atilde; ăn thịt nai x&agrave;o cải rổ chưa? S&aacute;ng mai tao cho m&agrave;y n&iacute;ch một trận, phải biết nh&aacute;! &ndash; rồi b&aacute;c cười lớn:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Tao đ&atilde; đi khắp ngọn nguồn s&ocirc;ng lạch m&agrave; chưa thấy ai mập như con mẹ chủ qu&aacute;n n&agrave;y. N&Oacute; r&oacute;t lu&ocirc;n cho tao năm ly rượu tấm bồng mắt thỏ m&agrave; kh&ocirc;ng nhậu ra ngo&agrave;i một giọt chớ</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ndash; B&aacute;c c&oacute; l&ecirc;n chợ uống c&agrave; ph&ecirc; kh&ocirc;ng? &ndash; T&ocirc;i hỏi.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ndash; Tao m&agrave; c&agrave; ph&ecirc; c&agrave; ph&aacute;o g&igrave; mậy? ờ, m&agrave; c&aacute;c chả đang l&agrave;m c&agrave; ph&ecirc; trong c&aacute;i qu&aacute;n chỗ đ&aacute;m h&aacute;t Sơn Đ&ocirc;ng đ&oacute;?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Th&ocirc;i, c&oacute; tao Ơ&ugrave; đ&acirc;y rồi. Cho m&agrave;y l&ecirc;n chợ chơi một l&aacute;t. Nhớ đường trở xuống bến kh&ocirc;ng Đừng c&oacute; đi đ&acirc;u xa , coi chừng lạc</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Trời ơi, một thằng b&eacute; từ lọt l&ograve;ng mẹ đến giờ sống ở th&agrave;nh phố như t&ocirc;i m&agrave; c&oacute; thể lạc trong c&aacute;i chợ nhỏ xứ qu&ecirc; n&agrave;y? T&ocirc;i cười thầm trong tối, từ tr&ecirc;n mui gieo người nhảy độp xuống sạp thuyền.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ndash; Cười g&igrave; đ&oacute; mậy, An? &ndash; b&aacute;c chướng mắt hỏi t&ocirc;i.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ndash; Kh&ocirc;ng. Ch&aacute;u c&oacute; cười g&igrave; đ&acirc;u</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ndash; BỘ m&agrave;y tưởng tao say hả? &ecirc;, tao c&ograve;n thấy c&aacute;i b&ocirc;ng bần( một loại c&acirc;y mọc ven m&eacute; s&ocirc;ng c&ograve;n gọi l&agrave; thuỷ liễu ) tr&ocirc;i giữa k&ecirc;nh kia nh&aacute; ! . . . Ch&agrave;, nước giật r&ograve;ng rồi .</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Ch&aacute;u l&ecirc;n đ&acirc;y, b&aacute;c ạ!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Kh&ocirc;ng chờ nghe b&aacute;c ừ, tho&aacute;ng c&aacute;i, t&ocirc;i đ&atilde; đặt ch&acirc;n tới bờ. Chao &ocirc;i Chợ g&igrave; m&agrave; lạ l&ugrave;ng thế n&agrave;y? BỘ T&acirc;y sắp đ&aacute;nh tới nơi, n&ecirc;n người ta đem vườn b&aacute;ch th&uacute; ra ph&aacute;t m&atilde;i hay sao! Một con ba ba to gần bằng c&aacute;i nong, đặt lật ngửa, cứ ngọ nguậy bơi bơi bốn ch&acirc;n trước &ocirc;ng cụ gi&agrave; ngồi lim dim đ&ocirc;i mắt. Những con r&ugrave;a v&agrave;ng to gần bằng c&aacute;i t&ocirc;, đều tăm tắp như đổ Ơ&ucirc; c&ugrave;ng một khu&ocirc;n ra, nằm rụt cổ trong mấy chiếc giỏ cần x&eacute;. Đ&acirc;y l&agrave; một con nai người ta vừa xẻ thịt ra b&aacute;n, c&aacute;i thủ c&ograve;n nguy&ecirc;n chưa lột da b&agrave;y giữa đống thịt đỏ hỏn tr&ecirc;n một tấm l&aacute; chầm. Cua biển cũng c&oacute;, ếch cũng c&oacute; ngh&ecirc;u s&ograve; cũng c&oacute;. C&ograve;n c&aacute; t&ocirc;m th&igrave; nhiều lắm, đủ c&aacute;c loại t&ocirc;m, kh&ocirc;ng kể xiết. T&ocirc;i bước th&ecirc;m mấy bước, qua những đống tr&aacute;i kh&oacute;m ch&iacute;n v&agrave;ng tỏa thơm m&ugrave;i mật, thấy hai con tr&uacute;t nằm khoanh, vảy xếp lại như những đồng h&agrave;o lấp l&aacute;nh. C&Oacute; tiếng chim g&igrave; mổ nhau k&ecirc;u quang qu&aacute;c trong chiếc lồng kẽm chỗ tối tối: ngọn đ&egrave;n vừa bị c&aacute;nh chim đ&aacute;nh nhau quạt tắt, v&agrave; người chủ b&aacute;n chưa kịp thắp l&ecirc;n. Một ch&uacute; khỉ con cứ nhảy qua nhảy lại lia lịa, chờn vờn tr&egrave;o l&ecirc;n đống b&iacute; ng&ocirc;. Thấy t&ocirc;i đi qua, n&oacute; nhe răng khẹc khẹc, ng&oacute; t&ocirc;i rồi quay lại nh&ograve;m người chủ, dường như muốn bảo t&ocirc;i hỏi gi&ugrave;m tại sao c&aacute;i l&atilde;o rậm r&acirc;u kia kh&ocirc;ng thả mối d&acirc;y x&iacute;ch cổ ra để n&oacute; được tự do đi chơi như t&ocirc;i . . . Đằng chỗ đ&aacute;m h&aacute;t rong, một tr&agrave;ng ph&aacute;o nổi l&ecirc;n vang dậy. T&ocirc;i chẳng c&ograve;n t&acirc;m tr&iacute; đ&acirc;u m&agrave; nh&igrave;n ng&oacute; chung quanh nữa. T&ocirc;i lại ba ch&acirc;n bốn cẳng nhắm mắt nhắm mũi lao về ph&iacute;a tiếng ồn &agrave;o đang c&oacute; một sức cuốn h&uacute;t kh&ocirc;ng cưỡng được.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ldquo;M&igrave;nh chỉ coi một ch&uacute;t th&ocirc;i!&rdquo; T&ocirc;i nghĩ bụng v&agrave; đưa mắt nh&igrave;n v&agrave;o trong ng&ocirc;i qu&aacute;n c&agrave; ph&ecirc; nhỏ n&uacute;p dưới t&aacute;n c&acirc;y b&atilde; đậu ch&ocirc;m ch&ocirc;m những gai. C&aacute;i lưng g&ugrave; g&ugrave; anh học sinh trường c&ocirc;le lấp l&oacute; qua vai những người d&acirc;n qu&acirc;n tự vệ đội n&oacute;n b&agrave;ng rộng v&agrave;nh thả trễ sau g&aacute;y. T&ocirc;i muốn v&agrave;o b&aacute;o cho anh biết tới đ&atilde; l&ecirc;n đ&acirc;y, nhưng bụng ph&acirc;n v&acirc;n:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">,Lỡ ảnh bảo xuống thuyền th&igrave; sao?.. . ừ, m&agrave; m&igrave;nh chỉ cần coi qua một ch&uacute;t th&ocirc;i m&agrave;?&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Chen đ&acirc;u m&agrave; chen dữ vậy hả? &ndash; một thằng b&eacute; l&ugrave;n l&ugrave;n, b&eacute;o ục ịch, trợn mắt h&iacute;ch khuỷu tay v&agrave;o h&ocirc;ng t&ocirc;i.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ndash; Cho tao coi một ch&uacute;t m&agrave;!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Nghe t&ocirc;i n&oacute;i bằng một giọng nhường nhịn, thằng b&eacute; tức thời n&eacute;p người qua để cho t&ocirc;i l&aacute;ch v&agrave;o, rồi n&oacute; nhe răng cườl l&agrave;m như t&ocirc;i với n&oacute; đ&atilde; quen từ hồi n&agrave;o.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ndash; M&agrave;y mới tới &agrave;? Hồi n&atilde;y vừa diễn tr&ograve; hai con khỉ k&eacute;o xe m&ecirc; lắm? ừ, m&igrave;nh mới tới! &ndash; t&ocirc;i đ&aacute;p cho c&oacute; đ&aacute;p, kh&ocirc;ng c&ograve;n ch&uacute; &yacute; nghe n&oacute; kể những g&igrave; b&ecirc;n tai nữa.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Người kh&aacute;ch m&atilde;i v&otilde; mặc quần &aacute;o chẽn m&agrave;u đen đ&atilde; b&aacute;n qua một lượt thuốc cao, bắt đầu giới thiệu tiết mục &ldquo;Em b&eacute; mười tuổi nh&agrave;o qua v&ograve;ng lửa</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ndash; Tr&ograve; n&agrave;y đ&ecirc;m qua n&oacute; diễn rồi. Kh&ocirc;ng thể tả được m&agrave;!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ndash; thằng b&eacute; l&ugrave;n l&ugrave;n b&eacute;o ục ịch lại rỉ tai t&ocirc;i, giọng đầy tự h&agrave;o về c&aacute;i chuyện n&oacute; đ&atilde; được xem trước t&ocirc;i.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Một em b&eacute; vận bộ quần &aacute;o bằng xa tanh m&agrave;u đỏ, t&oacute;c tết quả đ&agrave;o, mang đ&ocirc;i h&agrave;i vải đen bước ra, c&uacute;i ch&agrave;o kh&aacute;n giả Chiếc v&ograve;ng sắt quấn giẻ tẩm dầu lồng giữa một khung gỗ h&igrave;nh chữ nhật, dựng đứng cỡ ngang tầm mắt t&ocirc;i, b&agrave;y giữa hiện trường. Dứt loạt vỗ tay chung quanh, người Kh&aacute;ch gi&agrave; cầm ngọn đuốc ch&acirc;m v&agrave;o chiếc v&ograve;ng quấn dẻ. Tức thời một vầng lửa h&igrave;nh trong như chiếc b&aacute;nh xe lửa của Na-tra(Một nh&acirc;n vật trong truyện &ldquo;phong thần&rdquo; trung quốc) phực ch&aacute;y v&ugrave; v&ugrave;. Em b&eacute; lại nghi&ecirc;ng đầu ch&agrave;o mọi người rồi d&uacute;n ch&acirc;n một c&aacute;i, chạy tới ba bước gieo m&igrave;nh như một con thoi bay qua v&ograve;ng lửa.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Mắt t&ocirc;i hoa l&ecirc;n, chỉ c&ograve;n thấy c&aacute;i b&oacute;ng đỏ lao qua lao lại xuy&ecirc;n ngang vầng lửa, v&agrave; hai tai t&ocirc;i &ugrave; đi v&igrave; tiếng reo h&ograve;, tiếng vỗ tay vang dậy-</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ndash; Đ&ecirc;m qua, n&oacute; nh&agrave;o s&aacute;u lần m&agrave; bữa nay n&oacute; tăng l&ecirc;n tới t&aacute;m lần! M&agrave;y thấy gh&ecirc; chưa? &ndash; thằng b&eacute; l&ugrave;n l&ugrave;n b&eacute;o ục ịch h&iacute;ch t&ocirc;i, v&ecirc;nh mặt n&oacute;i.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Trong l&uacute;c người Kh&aacute;ch cầm mấy chai rượu thuốc đi v&ograve;ng quanh ch&agrave;o h&agrave;ng th&igrave; em b&eacute; vừa đứng thở vừa đưa hai b&agrave;n tay nhỏ phủi phủi những sợi t&oacute;c ch&aacute;y trước tr&aacute;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">D&aacute;ng như đ&ecirc;m nay số thuốc b&aacute;n được &iacute;t ỏi qu&aacute;. Người Kh&aacute;ch mặt buồn buồn, đặt mấy chai rượu thuốc l&ecirc;n c&aacute;i rương lớn m&agrave;u đen, cố l&agrave;m ra vẻ tươi tỉnh:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ndash; &ocirc;ng b&agrave; c&ocirc; b&aacute;c xem xong tr&ograve; n&agrave;y, rồi hẵng mua gi&ugrave;m sau cũng dược. Hầy? Tả lồ l&ecirc;n(đ&aacute;nh thanh la l&ecirc;n)&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Tiếng trống, tiếng thanh la lại khua rung dồn dập.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&Ocirc;ng ta cầm một nắm dao con s&aacute;ng lo&aacute;ng tung l&ecirc;n bắt từng chiếc một, rồi lần lượt cắm tất cả mười hai con dao ấy v&agrave;o những khe hở đục trong c&aacute;i v&ograve;ng sắt c&ograve;n dang rừng rục lửa. Những mũi dao nhọn h&igrave;nh l&aacute; tr&uacute;c đ&agrave;o lấp l&aacute;nh &aacute;nh th&eacute;p trắng xanh tua tủa chĩa ngược v&agrave;o trong, c&agrave;ng l&agrave;m thu hẹp c&aacute;i khoảng tr&ograve;n ban n&atilde;y vừa đủ một th&acirc;n m&igrave;nh ngươi chui qua. Ngươi Kh&aacute;ch xiếc nh&igrave;n mấy người đ&aacute;nh trống đ&aacute;nh thanh la, ra hiệu ngừng tay. &ocirc;ng ta nhường mắt h&eacute;t l&ecirc;n một tiếng, r&ugrave;ng m&igrave;nh thu người lại v&agrave; lập tức d&uacute;n ch&acirc;n bay qua giữa nhũng mũi dao tua tủa. T&ocirc;i nhắm mắt lại. Trời ơi! Chỉ nh&iacute;ch ra một ph&acirc;n, bất cứ Ơ&ucirc; ph&iacute;a n&agrave;o th&igrave; những mũi dao sắc nhọn như gươm kia cũng đ&acirc;m x&oacute;c l&uacute;t v&agrave;o da thịt. &ocirc;ng ta lao đi lao lại ba lần như vậy m&agrave; tr&ocirc;ng sắc mặt v&agrave; hơi thở kh&ocirc;ng ch&uacute;t biến đổi.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">&ndash; Người lớn l&agrave;m kh&ocirc;ng hay. Em b&eacute; l&agrave;m mới hay? &ndash; người Kh&aacute;ch vung tay giới thiệu.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#212529\">Em b&eacute; g&aacute;i bước ra, nghi&ecirc;ng đầu ch&agrave;o mọi người. Hai b&agrave;n ch&acirc;n đi đ&ocirc;i h&agrave;i v&aacute;i đen nhỏ x&iacute;u khơi động giẫm l&ecirc;n giẫm xuống một chỗ tr&ecirc;n mặt đất lởm chởm những h&ograve;n cuội T&ocirc;i hồi hộp qua. Đ&ocirc;i m&ocirc;i em b&eacute; run run. R&Otilde; r&agrave;ng l&agrave; n&oacute; đang cố trấn tĩnh trước ph&uacute;t biểu diễn c&aacute;i tr&ograve; v&ocirc; v&ugrave;ng nguy hiểm n&agrave;y.</span></span></span></p>\r\n');
INSERT INTO `sach` (`idsach`, `tensach`, `loaisach`, `tacgia`, `namxb`, `img`, `tinhtrang`, `tomtat`, `noidung`) VALUES
(30, 'Hà Nội 36 phố phường', 'Văn học Việt Nam', 'Thạch Lam', 2018, 'ha-noi-36-pho-phuong.jpg', 'Đã hoàn', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tuy kh&ocirc;ng sinh ra ở H&agrave; Nội nhưng những truyện, t&ugrave;y b&uacute;t trong tập H&agrave; Nội 36 Phố Phường của&nbsp;Thạch Lam vẫn l&agrave;m say đắm l&ograve;ng người.&nbsp;H&agrave; Nội 36 Phố Phường l&agrave; tập s&aacute;ch viết chủ yếu về những m&oacute;n ăn v&agrave; sự gắn b&oacute; giữa ẩm thực với&nbsp;đời sống văn h&oacute;a x&atilde; hội của người H&agrave; Nội thời bấy giờ. Sau n&agrave;y, đ&atilde; c&oacute; nhiều nghi&ecirc;n cứu, tạp văn&nbsp;hay t&ugrave;y b&uacute;t viết về ẩm thực H&agrave; Nội nhưng H&agrave; Nội 36 Phố Phường vẫn c&oacute; chỗ đứng của ri&ecirc;ng&nbsp;m&igrave;nh tr&ecirc;n văn đ&agrave;n Việt Nam v&agrave; trong tr&aacute;i tim của những người y&ecirc;u H&agrave; Nội.&nbsp;Cuốn H&agrave; Nội 36 Phố Phường sẽ gi&uacute;p ta hiểu r&otilde; hơn về mảnh đất kinh k&igrave; th&ocirc;ng qua những n&eacute;t&nbsp;đẹp của thủ đ&ocirc; ng&agrave;n năm y&ecirc;u dấu. S&aacute;ch viết về những chuyện nhỏ v&agrave; gần gũi trong đời sống d&acirc;n&nbsp;sinh, thấp tho&aacute;ng trong đ&oacute; vẫn l&agrave; những số phận &eacute;o le hay t&igrave;nh y&ecirc;u trong s&aacute;ng của con người &ndash;&nbsp;một n&eacute;t đặc trưng trong những s&aacute;ng t&aacute;c của nh&agrave; văn trẻ.</span></span></p>\r\n', '<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><strong>Chương 1:&nbsp;H&agrave; Nội Ba Mươi S&aacute;u Phố Phường&nbsp;Thạch Lam&nbsp;Tựa</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">H&agrave; Nội &ndash; Thăng Long - chốn cố đ&ocirc; y&ecirc;u dấu của ch&uacute;ng ta đ&atilde; gần hai ngh&igrave;n&nbsp;năm soi b&oacute;ng tr&ecirc;n d&ograve;ng s&ocirc;ng Nhị. V&agrave; n&oacute; sẽ m&atilde;i m&atilde;i soi b&oacute;ng trong l&ograve;ng&nbsp;người Nam Việt, khi m&agrave; mỗi thời c&ograve;n c&oacute; những trang phong lưu mặc kh&aacute;ch&nbsp;đem ghi ch&eacute;p trong văn thơ, để truyền lại hậu thế c&aacute;i đời sống của n&oacute;, c&aacute;i&nbsp;lịch sử của n&oacute;.&nbsp;Lịch sử Thăng Long phải đ&acirc;u chỉ l&agrave; những lớp s&oacute;ng phế hưng dồn dập từ&nbsp;đời vua n&agrave;y sang đời vua kh&aacute;c, kế tiếp nhau m&agrave; x&acirc;y cung điện nguy nga ven&nbsp;hồ Tr&uacute;c Bạch, b&ecirc;n hồ Ho&agrave;n Kiếm? N&oacute; c&ograve;n l&agrave; cuộc sinh hoạt hằng ng&agrave;y của&nbsp;d&acirc;n th&agrave;nh thị, với tất cả những phong tục, tập qu&aacute;n, với tật cả những nh&acirc;n&nbsp;vật kỳ kh&ocirc;i, với tất cả những c&aacute;i vui, c&aacute;i buồn,c &aacute;i tức, c&aacute;i giận nho nhỏ sống&nbsp;trong x&oacute; tối, kh&ocirc;ng t&ecirc;n, kh&ocirc;ng tuổi, kh&ocirc;ng tiếng t&acirc;m lưu lại đời sau.&nbsp;M&agrave; những nh&agrave; bi&ecirc;n tập pho sử k&yacute; b&igrave;nh dị n&agrave;y thường cũng kh&ocirc;ng t&ecirc;n,&nbsp;kh&ocirc;ng tuổi, kh&ocirc;ng tiếng tăm lưu lại đời sau.&nbsp;D&ugrave; v&ocirc; danh hay hữu danh, đều được c&ocirc;ng ch&uacute;ng y&ecirc;u chuộng biết bao! V&agrave;&nbsp;những trang sử của họ c&oacute; khi kh&ocirc;ng cần đem in ra nhiều bản, chỉ truyền tụng&nbsp;từ miệng n&agrave;y sang miệng kh&aacute;c. thế m&agrave; đ&oacute; đều l&agrave; những t&aacute;c phẩm bất hủ, v&igrave;&nbsp;đ&atilde; th&agrave;nh ch&uacute;ng tadao, tục ngữ, hoặc những giai thoại trong d&acirc;n gian.&nbsp;Long Bi&ecirc;n, c&aacute;i t&ecirc;n thứ nhất của H&agrave; Nội. Long Bi&ecirc;n m&agrave; một thi sĩ đ&atilde; ch&uacute;ng&nbsp;ta tụng trong một b&agrave;i &ETH;ường luật:&nbsp;Long Bi&ecirc;n đ&atilde; đắp tự đời n&agrave;o?&nbsp;Chẳng thấp nhưng m&agrave; cũng chẳng cao.&nbsp;Chăm chăm ngoại th&agrave;nh x&acirc;y cũng đẹp,&nbsp;Cồn cồn d&ograve;ng nước chảy tu&ocirc;n v&agrave;o.&nbsp;Long Bi&ecirc;n đ&atilde; đắp tự đời Cao biền, nh&agrave; thơ Thăng Long cũng chẳng th&egrave;m&nbsp;biết. &ETH;ến cả hai chữ Cao Biền, thử hỏi trong d&acirc;n ch&uacute;ng, mấy ai c&ograve;n nhớ, nếu&nbsp;đ&oacute; kh&ocirc;ng l&agrave; một t&ecirc;n thầy địa l&yacute; cao tay m&agrave; mọi người khiếp sợ. M&ocirc;n ch&iacute;nh&nbsp;trị xa họ bao nhi&ecirc;u th&igrave; m&ocirc;n địa l&yacute; gần họ bấy nhiếu, gần với t&iacute;n ngưỡng v&agrave;&nbsp;tập qu&aacute;n của họ. Nếu Cao Biền chỉ l&agrave; một ch&iacute;nh kh&aacute;ch kh&ocirc;n kh&eacute;o, một nh&agrave;&nbsp;tổ chức c&oacute; đại t&agrave;i, th&igrave; y đ&atilde; chết từ l&acirc;u trong k&yacute; ức d&acirc;n H&agrave; Th&agrave;nh rồi. Phần d&atilde;&nbsp;sử hầu ho&agrave;n to&agrave;n chiếm lấy c&aacute;i đời lạ l&ugrave;ng của nh&agrave; đ&ocirc; hộ. M&agrave; phần d&atilde; sử ấy&nbsp;&ndash; nghĩa l&agrave; thi&ecirc;n sử k&yacute; phong ph&uacute; nhất, thật nhất v&igrave; sản thực, chất ph&aacute;c của&nbsp;d&acirc;n - phần d&atilde; sử ấy kh&ocirc;ng ra ngo&agrave;i m&ocirc;n địa l&yacute;. N&agrave;o chuyện &ldquo;Cao Biền dậy&nbsp;non&rdquo; n&agrave;o chuyện &ldquo;Cao Biền cưỡi diều đi t&igrave;m đất&rdquo;, n&agrave;o chuyện &ldquo;Thần T&ocirc;&nbsp;Lịch hiện l&ecirc;n m&aacute;ch đất cho Cao Biền&rdquo;. Bao nhi&ecirc;u chuyện kỳ dị biến đổi t&ugrave;y&nbsp;theo t&agrave;i tưởng tượng của từng người kể, m&agrave; trong đ&oacute;, m&ocirc;n ch&iacute;nh trị của Cao&nbsp;Biền kh&ocirc;ng dấu hết.&nbsp;Cao Biền xa ta qu&aacute; v&agrave; đ&atilde; hầu l&agrave; một nh&acirc;n vật hoang đường. &ETH;ến như những&nbsp;nh&acirc;n vật gần ch&uacute;ng ta hơn m&agrave; sử ta đ&atilde; ghi ch&eacute;p tường tận những hu&ocirc;n c&ocirc;ng&nbsp;v&otilde; lược, cũng kh&ocirc;ng c&ograve;n c&oacute; vang b&oacute;ng g&igrave; trong tr&iacute; nhớ d&acirc;n ch&uacute;ng H&agrave; Th&agrave;nh&nbsp;v&agrave; Nam Việt. Dễ mấy ai biết rằng tr&ecirc;n s&ocirc;gn Nhị kia, dưới gầm cầu &ldquo;Dốc&nbsp;gạch&rdquo;, h&agrave;ng trăm thuyền lớn của Chế Bồng Nga đ&atilde; chen ch&uacute;c đậu v&agrave; từ đ&oacute;&nbsp;h&agrave;ng ngh&igrave;n vạn qu&acirc;n Chi&ecirc;m Th&agrave;nh, cởi trần, da n&acirc;u v&agrave; b&oacute;ng như đồng mắt&nbsp;cua, đ&atilde; giương nỏ mạnh bắn những t&ecirc;n lửa l&ecirc;n c&aacute;c phố, c&aacute;c phường, đẩy d&acirc;n&nbsp;th&agrave;nh thị tranh nhau chạy loạn?&nbsp;Rồi tới nh&agrave; L&ecirc;, họ Mạc, họ Trịnh kế tiếp nhau x&acirc;y dựng l&acirc;u đ&agrave;i b&ecirc;n hồ T&acirc;y&nbsp;v&agrave; hồ Ho&agrave;n Kiếm. Th&igrave; nay cũng chỉ:&nbsp;&nbsp;Nền cũ l&acirc;u đ&agrave;i b&oacute;ng tịch dương.&nbsp;Trong l&ograve;ng người vẫn ghi nhớ những điều muốn ghi nhớ, những điều thuộc&nbsp;phạm vi mỹ thuật, v&agrave; t&igrave;nh cảm &ndash; L&ograve;ng người coi thường trận thủy chiến kinh&nbsp;thi&ecirc;n động địa. nhưng l&ograve;ng người đ&atilde; kh&ocirc;ng qu&ecirc;n c&aacute;i t&igrave;nh cảnh đ&aacute;ng thương&nbsp;của một n&agrave;ng c&ocirc;ng ch&uacute;a nh&agrave; Trần lọt v&agrave;o tay anh Ch&agrave;m đen đủi:&nbsp;C&ocirc;ng ch&uacute;a lấy thằng b&aacute;n than,&nbsp;N&oacute; đưa l&ecirc;n ng&agrave;n cũng phải l&ecirc;n theo.&nbsp;Người H&agrave; Th&agrave;nh đọc lại c&acirc;u ch&uacute;ng tadao cổ gần ngh&igrave;n năm, t&aacute;c phẩm của&nbsp;một văn nh&acirc;n Thăng Long đầy trắc ẩn c&ograve;n như đứng b&ecirc;n bờ s&ocirc;ng C&aacute;i m&agrave;&nbsp;nhỏ lệ tr&ocirc;gn theo n&agrave;ng Huyền Tr&acirc;n sừng sững tr&ecirc;n s&acirc;n l&aacute;i một chiếc thuyền&nbsp;bồng giương buồm, thuận gi&oacute; chạy ra biền để xu&ocirc;i Nam. H&igrave;nh c&ocirc;ng ch&uacute;a&nbsp;thon thon in bật l&ecirc;n nền trời sắc m&aacute;u. V&agrave; d&acirc;n Thăng Long chờ cho con&nbsp;thuyền lượn khuất sau cồn Ph&uacute;c X&aacute; mới chịu rời bước trở về nh&agrave; ngậm ng&ugrave;i&nbsp;than tiếc:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">&nbsp;&ldquo;Ngọc qu&iacute; ng&acirc;u vầy!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">&ldquo;Thương thay c&acirc;y quế giữa rừng,</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">&ETH;ể cho thằng M&aacute;n thường Mường n&oacute; leo.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">&ETH;ối với d&acirc;n Thăng Long, cũng như dod61i với d&acirc;n H&agrave; Th&agrave;nh ta ng&agrave;y nay,&nbsp;rất mập mờ về khoa địa dư: người mạn ngược hay người Ch&agrave;m, người M&ecirc;n,&nbsp;họ đều cho l&agrave; M&aacute;n l&agrave; Mường cả.&nbsp;V&agrave;o thời giữa hai triều Trần v&agrave; L&ecirc; d&acirc;n Thăng Long hẳn l&agrave; lao đao khổ sở&nbsp;hết chạy loạn Hồ đến chạy loạn Minh. Nhưng rồi người ta cũng chỉ ghi v&agrave;o&nbsp;nhật k&yacute;, v&agrave;o sử k&yacute; - sử k&yacute; đ&atilde; gợi những việc tức cười đau đớn:&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">- C&aacute;i nợ Liễu Thăng!</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">- V&agrave; c&aacute;i chết của Nguyễn Tr&atilde;i g&acirc;y n&ecirc;n &ndash; theo lời d&atilde; sử - do sự b&aacute;o o&aacute;n tiền&nbsp;kiếp của một c&ocirc; g&aacute;i T&acirc;y Hồ b&aacute;n chiếu, một nữ thi sĩ tinh nghịch cười đ&ugrave;a</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">như Xu&acirc;n Hương:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">Em ở T&acirc;y Hồ b&aacute;n chiếu gon,</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">Chồng c&ograve;n chưa c&oacute;, hỏi chi con?</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">D&acirc;n H&agrave; Th&agrave;nh ta từ xưa vẫn th&iacute;ch cười; cười buồn cũng như cười vui.&nbsp;Thỉnh thoảng lại ph&aacute; l&ecirc;n một tiếng cười bi ai chua ch&aacute;t hay tiếng cười lờm&nbsp;của Cống Quỳnh về thời hậu L&ecirc; dưới quyền hống h&aacute;ch của ch&uacute;a Trịnh. V&agrave;&nbsp;kh&ocirc;ng biết ai đ&atilde; thốt ra một c&acirc;u ca dao qu&aacute; quắt để mỉa mai &ocirc;ng quan thị m&agrave;&nbsp;ch&uacute;a Sầm giao cho c&aacute;i trọng tr&aacute;ch canh ph&ograve;ng một n&agrave;ng c&ocirc;ng ch&uacute;a y&ecirc;u?&nbsp;&ETH;&oacute; l&agrave; c&aacute;ch viết sử của ch&uacute;ng ta. Nếu n&oacute;i được rằng nh&agrave; sử đ&iacute;ch đ&aacute;ng l&agrave; biết&nbsp;ch&eacute;p những điều đ&aacute;ng ch&eacute;p v&agrave; b&ograve; những điều đ&aacute;ng bỏ, th&igrave; c&aacute;c sử gia Thăng&nbsp;Long thực đ&atilde; l&agrave;m tr&ograve;n phận sự, đ&atilde; ch&eacute;p những c&aacute;i ho&agrave;n to&agrave;n c&oacute; t&iacute;ch c&aacute;ch&nbsp;Thăng Long v&agrave; Nam Việt. C&ograve;n g&igrave; Nam Việt hơn Truyện Tấm C&aacute;m hiện nay&nbsp;vẫn lưu truyền ở cửa miệng, từ th&agrave;nh thị cho ch&iacute; th&ocirc;n qu&ecirc;. &ETH;&oacute; l&agrave; một truyện,&nbsp;một việc Thăng Long, đ&atilde; xảy ra tr&ecirc;n đất Thăng Long:&nbsp;Nhưng phải chờ h&agrave;ng chục năm nữa, mới thấy được những nh&agrave; bi&ecirc;n tập cho&nbsp;cuốn sử H&agrave; Th&agrave;nh c&oacute; t&ecirc;n c&oacute; tuổi để lại hậu thế. &ETH;&oacute; l&agrave; n&agrave;ng Xu&acirc;n Hương, rồi&nbsp;ch&agrave;ng T&uacute; Xuất, ch&agrave;ng Ba Giai.&nbsp;Ch&uacute;ng ta đi qua ch&ugrave;a Qu&aacute;n Sứ, ai m&agrave; giữ được kh&ocirc;ng thầm đọc b&agrave;i thơ&nbsp;tuyệt t&aacute;c của Xu&acirc;n Hương, tuy ch&ugrave;a Qu&aacute;n Sứ ng&agrave;y nay chẳng c&ograve;n nữa c&aacute;i&nbsp;cảnh vắng teo, c&aacute;i cảnh:&nbsp;S&aacute;ng banh kh&ocirc;ng kẻ khua t&agrave;ng m&iacute;t,&nbsp;Trưa trật n&agrave;o ai m&oacute;c kẽ r&ecirc;u.&nbsp;T&ocirc;i chắc kh&ocirc;ng nơi danh thắng H&agrave; Th&agrave;nh n&agrave;o lại kh&ocirc;ng c&oacute; vết t&iacute;ch c&aacute;c nh&agrave;&nbsp;văn y&ecirc;u Thăng Long ấy, lại kh&ocirc;ng được dự một phần v&agrave;o cuốn sổ nhật k&yacute;,&nbsp;v&agrave;o tập văn t&ugrave;y b&uacute;t của họ. C&oacute; lẽ chỉ v&igrave; kh&ocirc;ng người g&igrave;n giữ cho n&ecirc;n những&nbsp;&ocirc;ng văn qu&yacute; b&aacute;u đ&atilde; thất lạc mất cả, những &aacute;ng văn v&agrave; người đạo mạo đồng&nbsp;thời coi thường l&agrave; văn chương đ&ugrave;a bỡn hay nhỏ mọn, kh&ocirc;ng đ&aacute;ng kể, kh&ocirc;ng&nbsp;đ&aacute;ng lưu lại hậu htế...&nbsp;&ETH;ến thời ch&uacute;ng ta. &ETH;ến T&uacute; Mỡ v&agrave; Thạch Lam...&nbsp;Như Ba Giai, Xu&acirc;n Hương (c&ugrave;ng một họ Hồ với T&uacute; Mỡ), T&uacute; Mỡ đ&atilde; giễu&nbsp;c&aacute;c nh&acirc;n vật H&agrave; Th&agrave;nh v&agrave; khiến cho họ bất hủ. T&uacute; Mỡ y&ecirc;u H&agrave; Th&agrave;nh chẳng&nbsp;k&eacute;m bọn T&uacute; Xuất v&agrave; Xu&acirc;n Hương, trở th&agrave;nh tinh qu&iacute;, ranh m&atilde;nh, ngạo người&nbsp;y&ecirc;u H&agrave; Th&agrave;nh thấm th&iacute;a hơn, &acirc;m thầm hơn l&agrave; Thạch Lam.&nbsp;Cũng c&oacute; l&uacute;c Thạch Lam mỉm cười thương hại như Cống Quỳnh, như Xu&acirc;n&nbsp;Hương trước c&aacute;i dốt H&agrave; Th&agrave;nh, trước những c&aacute;i biển bằng chữ Ph&aacute;p rất lố&nbsp;lăng v&agrave; dốt n&aacute;t của c&aacute;c b&aacute;c pho cạo, của c&aacute;c &ocirc;ng h&agrave;ng vải. Thạch Lam lại&nbsp;cũng tức giận như Xu&acirc;n Hương khi thấy người ta v&ocirc; t&acirc;m hay hữu &yacute; l&agrave;m mất&nbsp;vẻ đẹp nơi th&agrave;nh phố xinh xắn của ch&uacute;ng ta.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">Xu&acirc;n Hương viết:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">Ai về nhắn nhủ phường lối t&ocirc;i,</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">Muốn sống đem v&ocirc;i qu&eacute;t trả đền.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">V&agrave; Thạch Lam thủ thỉ - bao giờ Thạch Lam cũng chỉ thủ thỉ, d&ugrave; tức giận&nbsp;đến đ&acirc;u:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">&ldquo;Những thanh sắt ở đ&oacute; (ch&ugrave;a Ngọc Sơn) cũng kh&aacute; l&acirc;u rồi th&igrave; phải, m&agrave; chưa&nbsp;thấy &ocirc;ng bạn n&agrave;o đ&oacute; l&agrave;m việc g&igrave; cả. Việc th&igrave; rất giản dị nghĩa l&agrave; bỏ những c&aacute;i&nbsp;đ&oacute; đi l&agrave; xong.&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">Nhưng Thạch Lam lưu t&acirc;m nhất đến cuốc sống h&agrave;ng ng&agrave;y của H&agrave; Th&agrave;nh&nbsp;đến những c&aacute;i thứ c&otilde;n con của d&acirc;n th&agrave;nh phố, c&ugrave;ng l&agrave; những nh&acirc;n vật b&eacute;&nbsp;nhỏ, đ&atilde; g&acirc;y cho th&agrave;nh phố c&aacute;i t&iacute;nh c&aacute;ch đặc biệt của n&oacute;.&nbsp;Ta h&atilde;y nghe Thạch Lam mở mục qu&agrave; H&agrave; Nội với một giọng y&ecirc;u đương v&agrave;&nbsp;tự phụ của một d&acirc;n H&agrave; Nội: &ldquo;Qu&agrave; H&agrave; Nội xưa nay vẫn c&oacute; tiếng l&agrave; ngon l&agrave;nh&nbsp;v&agrave; lịch sự. Ở c&aacute;c th&ocirc;n qu&ecirc;, ch&uacute;t &ldquo;qu&agrave; H&agrave; Nội&rdquo; l&agrave; của mong đợi, v&agrave; tỏ được&nbsp;l&ograve;ng qu&yacute; ho&aacute; của người cho...&rdquo;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">&ldquo;... Nếu ch&uacute;ng ta về c&aacute;c tỉnh nhỏ &iacute;t l&acirc;u, hay ở ngay Hải Ph&ograve;ng, Nam &ETH;ịnh&nbsp;nữa, ch&uacute;ng ta mới biết qu&agrave; H&agrave; Nội c&oacute; vị ngon l&agrave; chừng n&agrave;o! Cũng l&agrave; thứ b&uacute;n&nbsp;chả chẳng hạn, cũng rau ấy, b&uacute;n ấy thế sao m&agrave; b&uacute;n chả của H&agrave; Nội ngon v&agrave;&nbsp;đậm thế, ngon từ c&aacute;i m&ugrave;i thơm, từ c&aacute;i nước chấm ngon đi&rdquo;.&nbsp;Ta như nghe thấy tiếng nhai v&agrave; tiếng xu&yacute;t xoa của người ăn b&uacute;n chả. Ngon&nbsp;thật! V&agrave; c&aacute;i ngon, c&aacute;i đặc sắc của H&agrave; Nội ấy đ&aacute;ng được người ta n&oacute;i đến v&agrave;&nbsp;ch&eacute;p lại như người ta đ&atilde; n&oacute;i, đ&atilde; ch&eacute;p lịch sử b&agrave; Ba T&yacute;, b&agrave; Tư Hồng, hai nh&acirc;n&nbsp;vật H&agrave; Nội nhất của thời nay.&nbsp;V&agrave; Thạch lam đ&atilde; n&oacute;i đến, đ&atilde; ghi ch&eacute;p tường tận đầy đủ, tỉ mỉ với tất cả tấm&nbsp;l&ograve;ng y&ecirc;u của Thạch Lam. Thạch Lam y&ecirc;u cũng như Thạch Lam gh&eacute;t đều tha&nbsp;thiết tuy &acirc;m thầm.&nbsp;Thạch Lam thực l&agrave; một nghệ sĩ, một thi sĩ về khoa thẩm vi. Nghệ sĩ muốn tận hưởng. Thi sĩ t&igrave;m cảm hứng trong vi tế cũng như trong vĩ đại. Hiểu nổi&nbsp;Thạch Lam tất đ&atilde; phải biết như Thạch Lam rằng: &ldquo;Ăn qu&agrave; l&agrave; một nghệ thuật,&nbsp;ăn đ&uacute;ng c&aacute;i giờ ấy v&agrave; chọn người b&aacute;n ấy mới l&agrave; người s&agrave;nh ăn. Tất phải&nbsp;đứng ngắm ngh&iacute;a c&ocirc; b&aacute;n b&aacute;nh cuốn Thanh Tr&igrave;, d&aacute;ng điệu uyển chuyển g&aacute;nh&nbsp;h&agrave;ng đi v&agrave;o trong phố hay ngồi nhanh nhẹn b&oacute;c từng c&aacute;i b&aacute;nh mỏng như tờ&nbsp;giấy v&agrave; trong như lụa.&rdquo;&nbsp;Thạch Lam kh&ocirc;ng qu&ecirc;n một thứ qu&agrave; rong n&agrave;o, mỗi thứ Thạch Lam đều tả&nbsp;hết c&aacute;i hương vị, c&aacute;i h&igrave;nh thức đặc biệt của n&oacute;, hương vị v&agrave; h&igrave;nh thức truyền&nbsp;lại kh&ocirc;ng biết tự đời n&agrave;o. Nếu Thạch Lam th&iacute;ch l&agrave;m thơ th&igrave; hẳn đ&atilde; viết&nbsp;những b&agrave;i &ETH;ường luật hay tứ tuyệt để vịnh chiếc b&aacute;nh &ldquo;b&aacute;nh r&aacute;n n&oacute;ng một&nbsp;xu hai&rdquo; hay &ldquo;c&ocirc; h&agrave;ng cơm nắm lẳng lơ với hai quang th&uacute;ng bỏ ch&ugrave;ng tr&ocirc;ng&nbsp;cũng ngon mắt như qu&agrave; của c&ocirc; vậy&rdquo;. Y như những b&agrave;i thơ vịnh b&aacute;nh nhồi,&nbsp;vịnh b&aacute;nh tr&ocirc;i của Xu&acirc;n Hương. Biết đ&acirc;u Xu&acirc;n Hương cũng kh&ocirc;ng cảm&nbsp;hứng trong khi xơi qu&agrave; H&agrave; Nội m&agrave; đọc ra những b&agrave;i thơ lưu truyền kia!&nbsp;Thạch lam kh&ocirc;ng l&agrave;m văn vần như Xu&acirc;n Hương. Nhưng tứ thi phong ph&uacute;,&nbsp;m&agrave;u sắc s&aacute;ng tươi, n&eacute;t vế nhịp nh&agrave;ng, đ&atilde; khiến nhiều đoạn văn xu&ocirc;i của&nbsp;Thạch Lam trở n&ecirc;n những b&agrave;i thơ kh&aacute;u khỉnh. &ETH;&acirc;y ta h&atilde;y nghe Thạch Lam&nbsp;tả về qu&agrave; b&uacute;n ốc:</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">&ldquo;C&oacute; ai, buổi trưa vắng, hay l&uacute;c đ&ecirc;m khuya, đi qua nh&agrave; c&ocirc; đ&agrave;o, nh&agrave; c&aacute;c chị&nbsp;thanh l&acirc;u, thấy họ ăn c&aacute;i qu&agrave; ấy một c&aacute;ch chăm ch&uacute; v&agrave; tha thiết đến đ&acirc;u&nbsp;kh&ocirc;ng? Nước ốc chua l&agrave;m nhăn n&eacute;t mặt t&agrave;n phấn v&agrave; mệt lả: miếng ớt cay&nbsp;l&agrave;m xoa xu&yacute;t những cặp m&ocirc;i h&eacute;o hắt, v&agrave; khiến đ&ocirc;i khi nhỏ những giọt lệ thật&nbsp;th&agrave; hơn cả những giọt lệ t&igrave;nh.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">&ldquo;C&ocirc; h&agrave;ng ốc c&oacute; một c&aacute;i dụng cụ, một đầu l&agrave; b&uacute;a, một đầu l&agrave; d&ugrave;i nhọn. Một&nbsp;c&aacute;i g&otilde; nhẹ v&agrave; một c&aacute;i trở tay, l&agrave; con ốc nguy&ecirc;n cả ruột đ&atilde; gọn g&agrave;ng rơi m&igrave;nh&nbsp;v&agrave;o b&aacute;t nước chấm. C&ocirc; thoăn thoắt r&uacute;t ốc kh&ocirc;ng kịp. Tr&ocirc;ng thấy người ta ăn&nbsp;ngon l&agrave;nh, ch&iacute;nh c&ocirc; cũng sinh th&egrave;m: c&ocirc; th&uacute; thật với t&ocirc;i như thế.&rdquo;&nbsp;Biết bao ghệ thuật trong một bức tranh nhỏ mọn nhưng tinh tế. V&agrave; đến&nbsp;được nghệ thuật ấy kh&ocirc;ng phải chỉ c&oacute; t&agrave;i, m&agrave; c&ograve;n phải c&oacute; l&ograve;ng y&ecirc;u, y&ecirc;u&nbsp;th&agrave;nh thực, y&ecirc;u trong th&acirc;m t&acirc;m, y&ecirc;u những c&aacute;i b&eacute; bỏng của những đời b&eacute;&nbsp;bỏng, nghĩa l&agrave; y&ecirc;u H&agrave; Nội, v&igrave; những c&aacute;i b&eacute; bỏng ấy tức l&agrave; tất cả H&agrave; Nội.&nbsp;Thạch Lam vĩnh biệt H&agrave; Nội giữa một c&ocirc;ng cuộc m&agrave; anh đ&atilde; tỏ ra c&oacute; đủ t&agrave;i&nbsp;năng để theo đuổi tới đ&iacute;ch: c&ocirc;ng cuộc soạn một pho H&agrave; Nội du k&yacute;. Nếu anh&nbsp;c&ograve;n r&aacute;n ở lại với ch&uacute;ng ta, th&igrave; ng&ograve;i b&uacute;t linh động của anh tả c&aacute;i H&agrave; Nội hiện&nbsp;thời n&agrave;y, hẳn phải l&agrave;m ch&uacute;ng ta...</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><strong>Chương 2:&nbsp;H&agrave; Nội băm s&aacute;u phố phường</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">Người Ph&aacute;p c&oacute; Paris, người anh c&oacute; London, người T&agrave;u c&oacute; Thượng Hải...&nbsp;Trong c&aacute;c s&aacute;ch vỡ, c&aacute;c b&aacute;o ch&iacute;, họ n&oacute;i đến th&agrave;nh phố của họ một c&aacute;ch tha&nbsp;thiết, mến y&ecirc;u... Ta phải nghe người Ph&aacute;p n&oacute;i đến Paris, người ở Paris mới&nbsp;hiểu được sự y&ecirc;u qu&yacute; ấy đến bực n&agrave;o.&nbsp;Ch&uacute;ng ta cũng c&oacute; H&agrave; Nội, một th&agrave;nh phố rất nhiều vẻ đẹp, v&igrave; H&agrave; Nội đẹp&nbsp;thật (ch&uacute;ng ta chỉ c&ograve;n t&igrave;m những vẻ đẹp ấy ra), v&agrave; cũng v&igrave; ch&uacute;ng ta y&ecirc;u mến.&nbsp;Y&ecirc;u mến H&agrave; Nội với t&acirc;m hồn người H&agrave; Nội , cũng như người Parisien ch&iacute;nh&nbsp;hiệu y&ecirc;u mến Paris... Trong những cuộc phi&ecirc;u du - phiếm du ngo&agrave;i c&aacute;c phố&nbsp;H&agrave; Nội l&agrave; một c&aacute;i th&uacute; v&ocirc; song chỉ người H&agrave; Nội c&oacute; &ndash; ta n&ecirc;n ch&uacute; &yacute; đến&nbsp;những n&eacute;t thay đổi của th&agrave;nh phố , n&ecirc;n nhận x&eacute;t những vẻ đẹp cũng như vẻ&nbsp;xấu của phố phường, th&acirc;n mật với những th&uacute; vui chơi, hay những cảnh lầm&nbsp;than, với những người H&agrave; Nội cũng như ta. H&agrave; Nội c&oacute; một sức quyến rũ đối&nbsp;với c&aacute;c người ở nơi kh&aacute;c... Ở những hang c&ugrave;ng ng&otilde; hẻm của l&agrave;ng xa, hay&nbsp;những nương mật thẳm trong rừng n&uacute;i, ban chiều vẫn c&oacute; nhiều người ng&oacute;ng&nbsp;về một phương trời để cố tr&ocirc;gn c&aacute;i &aacute;nh s&aacute;ng mờ của H&agrave; Nội chiếu l&ecirc;n nền&nbsp;m&acirc;y. &ETH;ể cho những người mong ước kinh kỳ ấy, v&agrave; để cho những người ở&nbsp;H&agrave; Nội, ch&uacute;ng ta n&oacute;i đến tất cả những vẻ ri&ecirc;ng của H&agrave; Nội, khiến mọi sự đổi&nbsp;thay trong ba mươi s&aacute;u phố phường đều c&oacute; tiếng vang ra khắp mọi nơi.&nbsp; &nbsp;&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><strong>Chương 3:&nbsp;Những Biển H&agrave;ng</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">&ETH;&atilde; c&oacute; nhiều người nhận ra rằng phố H&agrave;ng &ETH;&agrave;o l&agrave; của... lo&agrave;i vật. Ở đấy, c&oacute;&nbsp;đủ để l&agrave;m một g&aacute;nh xiếc. Trước hết c&oacute; hiệu tr&acirc;u v&agrave;ng, hẳn thế. Ấy l&agrave; c&acirc;u&nbsp;chuyện huyền thoạicủa &ocirc;ng Khổng Minh Kh&ocirc;ng đ&atilde; được h&igrave;nh tượng ra bằng&nbsp;hai c&aacute;i biển. Rồi đến hiệu b&ograve; v&agrave;ng, c&aacute; ch&eacute;p v&agrave;ng (c&aacute; ch&eacute;p h&oacute;a long th&igrave; đ&uacute;ng&nbsp;hơn v&agrave; con c&aacute; n&agrave;y đ&atilde; tr&aacute;i tật chạy l&ecirc;n H&agrave;ng Ngang rồi), con lạc đ&agrave; kh&ocirc;ng&nbsp;biết đến đ&acirc;y để l&agrave;m g&igrave;?, con g&agrave; sống kim k&ecirc; hẳn th&ocirc;i, con hươu sao, con kỳ&nbsp;l&acirc;n, con phượng (lai ho&agrave;ng), con r&ugrave;a r&ugrave;a (kim quy), con r&ugrave;a r&ugrave;a n&agrave;y về n&uacute;i&nbsp;rồi, con vịt che &ocirc;, con voi (con n&agrave;y cũng về rừng), v&agrave; con t&ecirc; gi&aacute;c. C&aacute;c nh&agrave;&nbsp;h&agrave;ng c&ograve;n l&acirc;u mới d&ugrave;ng hết được t&ecirc;n c&aacute;c lo&agrave;i vật. V&agrave; ch&uacute;ng ta n&ecirc;n nhận rằng&nbsp;trong c&aacute;c con vật đ&atilde; d&ugrave;ng, kh&ocirc;ng c&oacute; con n&agrave;o dữ cả. Con t&ecirc; gi&aacute;c th&igrave; kể l&agrave; vật&nbsp;dữ, nhưng con t&ecirc; gi&aacute;c ở H&agrave;ng Đ&agrave;o th&igrave; l&agrave;nh lắm: n&oacute; kh&ocirc;ng cắn ai bao giờ.&nbsp;Kh&ocirc;ng c&oacute; hổ v&agrave;ng hay sư tử v&agrave;ng, chẳng hạn. V&igrave; những con vật tr&ecirc;n kia l&agrave;&nbsp;những con vật thần linh chăng, hay l&agrave; những con vật chỉ l&agrave;nh c&oacute; thể gợi l&ograve;ng&nbsp;tin của kh&aacute;ch mua? Con tr&acirc;u, con hươu, con b&ograve; ... Những con vật n&agrave;y c&oacute; l&agrave;m&nbsp;hại được ai bao giờ đ&acirc;u? V&agrave;o nh&agrave; con tr&acirc;u, con hươu mua vải, lụa, chắc&nbsp;kh&ocirc;ng bị hớ, chắc sẽ được nh&agrave; h&agrave;ng tiếp đ&atilde;i niềm nở v&agrave; tử tế (như c&aacute;c b&agrave;&nbsp;b&aacute;n h&agrave;ng Việt Nam biết tiếp khi kh&aacute;ch chỉ mặc cả m&agrave; kh&ocirc;ng mua, hay muốn&nbsp;mua m&agrave; trả rẻ), v&agrave; nếu họ c&oacute; bị dại như một con b&ograve; th&igrave; cũng được an ủi rằng&nbsp;&iacute;t ra cũng l&agrave; một con b&ograve; v&agrave;ng.&nbsp;T&ocirc;i chỉ kh&ocirc;ng hiểu tại sao bỗng dưng lại c&oacute; con lạc đ&agrave;. Con vật n&agrave;y h&igrave;nh&nbsp;như lạc lo&agrave;i v&agrave;o đ&aacute;m ấy, giữa những con vật m&agrave; n&oacute; kh&ocirc;ng quen bao giờ.&nbsp;Người phương T&acirc;y khinh ai thường gọi: c&aacute;i anh lạc đ&agrave; ấy ... Theo nghĩa đ&oacute;&nbsp;th&igrave; con lạc đ&agrave; &aacute;m chỉ nh&agrave; h&agrave;ng hay kh&aacute;ch mua h&agrave;ng?&nbsp;Ch&uacute;ng ta c&ograve;n phải hỏi tại sao con vật kh&aacute;c kh&ocirc;ng được d&ugrave;ng, v&agrave; tại sao địa&nbsp;phận ch&uacute;ng chỉ c&oacute; phố H&agrave;ng Đ&agrave;o th&ocirc;i. L&ecirc;n đến H&agrave;ng Ngang, xuống đến Bờ&nbsp;Hồ, l&agrave; đ&atilde; kh&ocirc;ng c&oacute; lo&agrave;i vật rồi (con c&aacute; h&oacute;a long l&ecirc;n H&agrave;ng Ngang l&agrave; tr&aacute;i với lẽ&nbsp;&nbsp;trời).&nbsp;C&oacute; một người kể chuyện với t&ocirc;i rằng đ&oacute; l&agrave; tại nh&agrave; h&agrave;ng ganh tị nhau.&nbsp;Nguy&ecirc;n hồi bấy giờ phố h&agrave;ng Đ&agrave;o c&ograve;n hẹp, nh&agrave; hai b&ecirc;n phố kh&ocirc;ng c&aacute;ch&nbsp;nhau xa mấy. C&oacute; hai &ocirc;ng chủ hiệu to, ngẫu nhi&ecirc;n một h&ocirc;m c&ugrave;ng c&oacute; một &yacute;, l&agrave;&nbsp;lấy con hươu l&agrave;m biểu hiệu. Hai con hươu c&ugrave;ng treo một l&uacute;c. C&oacute; nhiều sự&nbsp;nhầm lẫn xảy ra về sau, nhưng hai &ocirc;ng c&ugrave;ng ganh, kh&ocirc;ng &ocirc;ng n&agrave;o chịu đổi&nbsp;con kh&aacute;c. Như thế được một năm. Rồi bỗng nhi&ecirc;n, một &ocirc;ng lấy ngay con b&aacute;o&nbsp;l&agrave;m biểu hiện, v&agrave; phao ng&ocirc;n l&ecirc;n rằng chỉ &iacute;t l&acirc;u bữa l&agrave;b&aacute;o sẽ cắn hươu chết.&nbsp;&Ocirc;ng chủ hiệu kia tức kh&iacute; lấy biển hiệu con hổ v&agrave; phao ngược lại.&nbsp;&Ocirc;ng chủ hiệu n&agrave;y chẳng chịu k&eacute;m đổi ngay con b&aacute;o ra con sư tử. &Ocirc;ng kia&nbsp;cũng lập tức đổi ra con voi.&nbsp;Đến con voi th&igrave; &ocirc;ng n&agrave;y tức qu&aacute;: v&igrave; kh&ocirc;ng c&oacute; con n&agrave;o khỏe hơn con nữa.&nbsp;Voi đứng đầu trong giống vật rồi. Chỉ c&oacute; c&aacute;ch l&agrave;m con voi hơn. Thế rồi &ocirc;ng&nbsp;l&agrave;m con voi to hơn. &Ocirc;ng kia cũng chẳng chịu k&eacute;m, lại l&agrave;m con voi to hơn&nbsp;nữa. Hai con voi cứ thi nhau m&agrave; to m&atilde;i ra. Phố th&igrave; hẹp, cho n&ecirc;n một ng&agrave;y kia&nbsp;hai con voi đụng v&ograve;i nhau, lấp cả lối đi.&nbsp;Việc đến cửa quan. &Ocirc;ng quan ph&acirc;n xử, bắt voi hai b&ecirc;n đều b&eacute; lại, v&agrave; ra lệnh&nbsp;từ đấy chỉ được d&ugrave;ng những con vật hiền (như voi chẳng hạn) l&agrave;m biển h&agrave;ng&nbsp;m&agrave; th&ocirc;i. Những con vật dữ như b&aacute;o, hổ, gấu, m&egrave;o ... đều cấm tiệt.&nbsp;Ấy l&agrave; người ta kể cho t&ocirc;i nghe c&acirc;u chuyện như thế. Chuyện chả biết c&oacute; thật&nbsp;hay bịa, nhưng giảng tại sao người ta kh&ocirc;ng d&ugrave;ng c&aacute;c th&uacute; vật dữ th&igrave; c&oacute; (tuy&nbsp;vậy, con t&ecirc; gi&aacute;c?), c&ograve;n tại sao c&aacute;c vật chỉ c&oacute; phạm vi phố H&agrave;ng Đ&agrave;o, kh&ocirc;ng&nbsp;l&ecirc;n H&agrave;ng Ngang, kh&ocirc;ng xuống Bờ Hồ, th&igrave; tuyệt nhi&ecirc;n kh&ocirc;ng.&nbsp;</span></span></span></p>\r\n');

